[7944] = {title = "La Fortuna te espera...", objective = "Ve a las Cuevas de los Lamentos de Los Baldíos y busca tu fortuna.", description = "\"La Fortuna te espera en las Cuevas de los Lamentos.\"\n\nEl papelito de la Fortuna que te dio Sayge está caliente. Cuando lo aprietas, ves una imagen de una cueva al suroeste de El Cruce, en Los Baldíos. Te acercas más y te encuentras moviéndote a gran velocidad por unos enrevesados pasadizos hasta que llegas a un resplandeciente portal que lleva al mismísimo corazón de la cueva. Cuando lo cruzas, aparece de la nada un extraño cofre.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nArcón misterioso"},
[7945] = {title = "La Fortuna te espera...", objective = "Ve a la Roca Crines Pálidas de Mulgore y busca tu fortuna.", description = "\"La Fortuna te espera fuera de la Roca Crines Pálidas.\"\n\nEl papelito de la Fortuna que te dio Sayge está caliente. Cuando lo aprietas, ves una imagen de la Roca Crines Pálidas de Mulgore. Te acercas más y ves un tocón de árbol al lado de la entrada de la cueva.", progress = "Este tocón se parece al de tu visión.", completion = "Un registro rápido del tocón revela una caja fuerte. ¡Parece que has encontrado tu Fortuna!", rewards = "Recibirás:\n\nArcón misterioso"},
[7946] = {title = "Huevo de Jubjub", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nTiempo de reutilización de Jubling\n\nRecibirás:\n\nHuevo sin abrir de Jubling"},
[7961] = {title = "Waskily Wabbits!", objective = "Kill 5 rabbits and return to Jon LeCraft by the forge on Designer Island.", description = "These rabbits are breeding uncontrollably.  Something must be done!\n\nHelp us <nombre>, you're our only hope.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nPata de conejoCajón de conejos (pata de nieve)"},
[7962] = {title = "Pieles de conejo", objective = "Lleva 3 pieles de conejo Artisane a Jon L'Artesano.", description = "Quiero unas pieles de conejo Artisane. Ve a buscarme unas a Conejos Artisane.\n\n¡Es importante!Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nMarca de lo salvaje\n\nRecibirás:\n\nLangosta de 12 kilos"},
