[8194] = {title = "Aprendiz pescador", objective = "Aunque ya tenemos un ganador del concurso, aún quiero comprarte el pezrico a un precio razonable. ¿Qué dices?", description = nil, progress = "Aunque ya tenemos un ganador del concurso, aún quiero comprarte el pezrico a un precio razonable. ¿Qué dices?", completion = "¡A mí me viene bien, si a ti te viene bien!", rewards = "Recibirás:\n\n"},
[8195] = {title = "Las monedas Zulian, Razzashi y Hakkari", objective = "Necesito los Dechados de Poder conocidos como Monedas de las Tribus. Son la moneda que utilizan los distintos habitantes de Zul'Gurub y todas ellas están imbuidas con un sutil pero potente mojo.\n\nHay nueve tipos diferentes. Mis compatriotas buscan algunas de ellas para las distintas armaduras que ofrecen a los héroes Zandalar. Si tuvieras alguna de sobra te la cambiaré tres de ellas por una de nuestras marcas de honor. Puedes utilizar las marcas con Rin'wosho el Mercader, a cambio te ofrecerá objetos especiales.", description = nil, progress = "Necesito los Dechados de Poder conocidos como Monedas de las Tribus. Son la moneda que utilizan los distintos habitantes de Zul'Gurub y todas ellas están imbuidas con un sutil pero potente mojo.\n\nHay nueve tipos diferentes. Mis compatriotas buscan algunas de ellas para las distintas armaduras que ofrecen a los héroes Zandalar. Si tuvieras alguna de sobra te la cambiaré tres de ellas por una de nuestras marcas de honor. Puedes utilizar las marcas con Rin'wosho el Mercader, a cambio te ofrecerá objetos especiales.", completion = "Excelente, este juego de monedas es una buena adquisición para nuestra colección... la que utilizaremos para defender a nuestra tribu contra el auge del Dios de la Sangre. Has ganado esta muestra de honor, &lt;nombre&gt;, así como la gratitud de toda la tribu.", rewards = "Recibirás:\n\nMuestra de honor Zandalar"},
[8196] = {title = "Mangos de esencia", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nMango esencia"},
[8201] = {title = "Una colección de cabezas", objective = "Ata 5 cabezas de los canalizadores y regresa con ellas a Exzhal en la isla Yojamba.", description = "Es cierto. Hakkar vive ahora en Zul'Gurub, bañándose en el poder de nuestros dioses. Altos sacerdotes se encargan de canalizar las energías de nuestras divinidades más sagradas en el Cazador de Almas, que pronto tendrá más fuerza que cualquier ser del Azeroth. ¡Hay que detenerle!\n\nToma este Cordón sagrado y ensarta en él las cabezas de los altos sacerdotes que canalizan la energía de nuestros dioses.\n\nCuando lo hayas hecho, vuelve a verme.", progress = "¿Tienes la colección de cabezas, <nombre>? ¡Debemos impedir que Hakkar aumente su poder!", completion = "¡Conseguiste las cabezas! ¡Has puesto fin al expolio del poder de nuestros dioses y has debilitado a Hakkar!\n\nTus acciones son realmente heroicas, &lt;nombre&gt;. Que las cabezas de estos esclavos trols te sigan suplicando clemencia mucho tiempo después de que se haya enfriado su sangre.", rewards = "Podrás elegir una de estas recompensas:\n\nCinturón de Cabezas encogidasCinturón de Cabezas secasCinturón de Cabezas conservadasCinturón de las Pequeñas cabezas\n\nTambién recibirás:\n\nCinturón de Cabezas encogidasCinturón de Cabezas secasCinturón de Cabezas conservadasCinturón de las Pequeñas cabezas"},
