[8226] = {title = "Pez raro: pez demonio pálido", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nSombrero de pesca desgastado\n\nTambién recibirás:\n\n"},
[8227] = {title = "Cinta métrica de Nat", objective = "Devuelve la Cinta métrica de Nat a Nat Pagle en el Marjal Revolcafango.", description = "Entre los cebos y aparejos de pesca hay una cinta métrica de seda, enrollada. Unida a ella hay una etiqueta que dice:\n\n\"Nat Pagle, Marjal Revolcafango.\"", progress = "Hola, muchachito. ¿Has venido a pescar algo?", completion = "¡Vaya, encontraste mi cinta métrica favorita! ¡Me encanta esta cinta! Con ella he medido los peces más grandes que he pescado...\n\nPero... perdí esta cinta en Zul'Gurub... ¿has estado en Zul'Gurub? ¿Has visto al legendario Gahz'ranka?\n\n¿Quieres darle caza?", rewards = "Recibirás:\n\n"},
[8228] = {title = "¿Puedo tener un folleto de pesca?", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\n¡Torneo de pesca!"},
