[8385] = {title = "Esfuerzos concertados", objective = "Ah, <nombre>. Espero seguir contando con tu experiencia en los distintos frentes. Muchos de nuestros hombres y mujeres admiran a los veteranos tan experimentados como tú.", description = nil, progress = "Ah, <nombre>. Espero seguir contando con tu experiencia en los distintos frentes. Muchos de nuestros hombres y mujeres admiran a los veteranos tan experimentados como tú.", completion = "Estás a la altura de tu reputación, &lt;nombre&gt;. Sin gente como tú la Alianza no tendría ninguna oportunidad contra las salvajes fuerzas de la Horda.", rewards = "Te van a lanzar el siguiente hechizo:\n\nPuntos de honor +2388"},
[8386] = {title = "Lucha por Garganta Grito de Guerra", objective = "No olvides que la lucha en la Garganta Grito de Guerra pretende mucho más que la defensa de los árboles. Es por el mantenimiento de una frontera natural entre Vallefresno y Los Baldíos. ¿Traes alguna noticia del campo de batalla, <nombre>?", description = nil, progress = "No olvides que la lucha en la Garganta Grito de Guerra pretende mucho más que la defensa de los árboles. Es por el mantenimiento de una frontera natural entre Vallefresno y Los Baldíos. ¿Traes alguna noticia del campo de batalla, <nombre>?", completion = "Buen trabajo, &lt;nombre&gt;. Gracias a ti, las defensas naturales de Vallefresno estarán intactas un día más.", rewards = nil},
[8387] = {title = "Invasores del Valle de Alterac", objective = "¡La batalla por Alterac sigue ardiendo! ¡Debes volver al Valle de Alterac y expulsar una vez más a los invasores del territorio de Lobo Gélido, <nombre>!", description = nil, progress = "¡La batalla por Alterac sigue ardiendo! ¡Debes volver al Valle de Alterac y expulsar una vez más a los invasores del territorio de Lobo Gélido, <nombre>!", completion = "¡Buen trabajo, &lt;nombre&gt;! Deberías sentirte orgulloso. ¡Los invasores de la Alianza deben ser expulsados de Alterac!", rewards = "Te van a lanzar el siguiente hechizo:\n\nPuntos de honor +398"},
[8388] = {title = "Por Gran Honor", objective = "Muchos valientes guerreros te admiran, <nombre>. Sigue siendo un ejemplo para todos los que luchan contra la Alianza. ¿Qué noticias traes de tus viajes?", description = nil, progress = "Muchos valientes guerreros te admiran, <nombre>. Sigue siendo un ejemplo para todos los que luchan contra la Alianza. ¿Qué noticias traes de tus viajes?", completion = "Sigue honrando a la Horda, &lt;nombre&gt;. Es un placer comprobar que no te has debilitado.", rewards = "Te van a lanzar el siguiente hechizo:\n\nPuntos de honor +2388\n\nRecibirás:\n\n"},
[8389] = {title = "La batalla por Garganta Grito de Guerra", objective = "La batalla en la Garganta Grito de Guerra sigue ardiendo, <nombre>. ¡Ayuda a expulsar a las centinelas Ala de Plata de nuestras tierras!", description = nil, progress = "La batalla en la Garganta Grito de Guerra sigue ardiendo, <nombre>. ¡Ayuda a expulsar a las centinelas Ala de Plata de nuestras tierras!", completion = "¡Bien hecho, &lt;clase&gt;! ¡Has ayudado a asegurar a la Horda su control sobre la Garganta Grito de Guerra!", rewards = "Te van a lanzar el siguiente hechizo:\n\nPuntos de honor +50"},
[8390] = {title = "La conquista de la Cuenca de Arathi", objective = "¡La guerra contra la escoria de la Alianza exige recursos, <nombre>! No podemos permitir que nuestras reservas se agoten en medio de una batalla. ¡Sigue asegurándote de que la Cuenca de Arathi siga en nuestras manos!", description = nil, progress = "¡La guerra contra la escoria de la Alianza exige recursos, <nombre>! No podemos permitir que nuestras reservas se agoten en medio de una batalla. ¡Sigue asegurándote de que la Cuenca de Arathi siga en nuestras manos!", completion = "¡Un trabajo excelente, &lt;nombre&gt;! Mientras sigamos controlando la Cuenca de Arathi nuestros suministros estarán garantizados.", rewards = nil},
[8391] = {title = "Reclamo por la Cuenca de Arathi", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[8392] = {title = "Reclamo por la Cuenca de Arathi", objective = "La guerra contra la Horda está acabando rápidamente con nuestros recursos, <nombre>. ¡Es fundamental seguir controlando la Cuenca de Arathi para mantener la llegada de suministros! ¿Ya has prestado ayuda en Arathi?", description = nil, progress = "La guerra contra la Horda está acabando rápidamente con nuestros recursos, <nombre>. ¡Es fundamental seguir controlando la Cuenca de Arathi para mantener la llegada de suministros! ¿Ya has prestado ayuda en Arathi?", completion = "¡Excelente trabajo, &lt;clase&gt;! Tus esfuerzos ayudarán a proporcionar a la Alianza los recursos que necesita.", rewards = nil},
[8393] = {title = "Reclamo por la Cuenca de Arathi", objective = "Entrega 3 marcas de honor de la Cuenca de Arathi a un Brigadier general de la Alianza fuera de los campos de batalla.", description = "Ganar una guerra es algo más que ser más astuto y mejor guerrero que tu oponente. A menudo la victoria llega cuando produces más que tu enemigo.\n\nLo que tenemos en la Cuenca de Arathi es un considerable suministro de recursos a disposición del bando que primero los reclame.\n\nSi la Alianza se alza con la victoria, tendremos más espadas y picos para nuestras tropas. Si ganara la Horda, ¡esas espadas y picos pasarían a amenazarnos! Ha llegado el momento de ayudar a la Alianza en la Cuenca de Arathi, <nombre>.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nPuntos de honor +138\n\nRecibirás:\n\n"},
