[8407] = {title = "Lucha por Garganta Grito de Guerra", objective = "Don't forget the fight for Warsong Gulch is about more than just protecting trees.  It's about keeping a natural barrier between Ashenvale and the Barrens.  Do you bring any news from the battlefield, <nombre>?", description = nil, progress = "Don't forget the fight for Warsong Gulch is about more than just protecting trees.  It's about keeping a natural barrier between Ashenvale and the Barrens.  Do you bring any news from the battlefield, <nombre>?", completion = "Good work, &lt;nombre&gt;.  Thanks to you, our natural defenses in Ashenvale will remain intact for another day.", rewards = nil},
