[8409] = {title = "Los barriles dañados", objective = "Ve a ver de nuevo a la clamasombras Yanka, que está en el Festival del Hombre de Mimbre en los Claros de Tirisfal.", description = "Has conseguido meter los huevos podridos en los barriles. ¡Sin duda se arruinarán muchas partidas de cerveza en la Taberna Costasur!\n\nAhora debes ir a ver a la clamasombras Yanka al Festival del Hombre de Mimbre en los Claros de Tirisfal e informarle de tu éxito.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nGolosinas de calabaza de Halloween"},
[8410] = {title = "Maestría elemental", objective = "Recoge una muestra de aire, fuego, tierra y agua para Bath'rah el Cazavientos.", description = "Bath'rah el Cazavientos ha reclamado un chamán de tu sabiduría. Pide que lleves contigo una muestra de cada uno de los elementos para mostrar que estás <preparado/preparada>.\n\nPara encontrar a Bath'rah dirígete al río al este del Molino Tarren. Sigue el río hacia el Norte, hasta que des con las ruinas en las que vive Bath'rah.", progress = "¿Qué tienes para mí, <clase>?", completion = "Excelente, colega. Ahora nosotros hablar.", rewards = "Recibirás:\n\n"},
[8411] = {title = "Dominar los elementos", objective = "Lleva los elementos tierra, aire, fuego y agua a Bath'rah el Cazavientos.", description = "Me alegro de verte. Tráeme los elementos y luego hablamos.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[8412] = {title = "Tótem espiritual", objective = "Lleva lo necesario para el tótem del espíritu a Bath'rah el Cazavientos.", description = "Ya no les gusto a los espíritus elementales de este lugar.\n\nTú sabes de los tótems más que nadie y yo necesito un tótem de espíritu que me proteja. Ya tenemos los elementos y necesitamos algo más de Naturaleza. Más primigenio.\n\nEn las Tierras de la Peste del Oeste podrás encontrar osos para quitarles las garras y arañas para quitarles los ojos. Los pájaros carroñeros comen de todo, así que seguro que también tendrán algo de eso adentro.", progress = "Los espíritus saben lo que tramamos y han intentadod matarme. Espero que tengas las partes que te pedí.", completion = "¡Genial! Vamos a probar...", rewards = "Recibirás:\n\n"},
[8413] = {title = "Vudú", objective = "Lleva las plumas vudú a Bath'rah el Cazavientos.", description = "El tótem de espíritu no alcanza. Vamos a necesitar un poco de magia vudú.\n\nLos trols del Templo Sumergido tienen plumas mágicas muy poderosas. Debes ir allí y matar a Gasher, Mijan, Zolo, Hukku, Zul'lor y Loro. Entonces tendremos suficiente magia para un verdadero tótem de espíritu.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nPuños azuritaEspíritu de agua enamoradoBastón salvaje\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nPuños azuritaEspíritu de agua enamoradoBastón salvaje"},
[8414] = {title = "Expulsión de los demonios", objective = "Recoge 20 Piedras de la Plaga de esbirros y entrégaselas al Alto Sacerdote Thel'danis en las Tierras de la Peste del Oeste.", description = "Saludos, <clase>. Los cruzados como tú siempre son bienvenidos en esta tierra desolada.\n\nTu sagrada fuerza es necesaria para combatir al número infinito de no-muertos. Necesitamos que reúnas un gran número de piedras de la Plaga y que las entregues al sumo sacerdote Thel'danis. Podrás encontrarlo custodiando la Tumba de Uther en la Colina de las Penas, no lejos de aquí.\n\nSolo quien lleve una Comisión del Alba Argenta podrá encontrar piedras de la Plaga, así que habla con la oficial Purocorazón si aún no tienes ninguna.", progress = "What evil have you brought me?", completion = "There is risk in dispelling the evil from a scourgestone, for it attracts the attention of undead for countless miles.  But you are strong in the Light, &lt;nombre&gt;, and I do not fear for you...", rewards = "Recibirás:\n\n"},
[8415] = {title = "Alto del Orvallo", objective = "Habla con el comandante Ashlam Puñovalor en el Alto del Orvallo.", description = "El comandante Ashlam Puñovalor ha hecho un llamamiento en busca de un paladín de tu pureza y valor, <nombre>. Podrás encontrarle en el Alto del Orvallo, en la parte meridional de las Tierras de la Peste del Oeste. ¡Búscalo y contribuye a su causa en defensa de la Luz!", progress = nil, completion = "Very well, &lt;clase&gt;!  Let's see what you're made of.", rewards = "Recibirás:\n\n"},
[8416] = {title = "Piedras inertes de la Plaga", objective = "Lleva la Piedra inerte de la Plaga al comandante Ashlam Puñovalor en el Alto del Orvallo.", description = "Las piedras han purgado el mal, <nombre>.\n\nConozco los planes del comandante Ashlam y solo necesitará una piedra inerte. Llévasela y podremos purificar a la Plaga en el fuego de los justos.", progress = "Was Thal'danis able to cleanse the scourgestones?", completion = "You have done as I asked without question, &lt;nombre&gt;.\n\nTrust is achieved, it seems.  Perhaps I can reveal our plan in more detail to you now.", rewards = "Recibirás:\n\n"},
[8417] = {title = "Un espíritu afligido", objective = "Habla con el Héroe Caído de la Horda en la entrada de Las Tierras Devastadas.", description = "En Las Tierras Devastadas habita un espíritu preocupado que fue un día un gran héroe de la Horda. El honor de un guerrero no perece con su cuerpo, debería serte de ayuda hablar con él.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[8418] = {title = "La forja de la piedra del poderío", objective = "Lleva las plumas vudú a Ashlam Puñovalor.", description = "Ahora tenemos que combinar las Piedras de la Plaga inertes y crear un arma que destroce las filas de no-muertos. No es una tarea sencilla, <nombre>. Solo los trols del Templo Sumergido son portadores del tipo de extraña magia que puede convertir el mal en algo contrario a sí mismo.\n\nLos trols del Templo son conocidos como Gasher, Mijan, Zolo, Hukku, Zul'lor y Lor'ho. No sientas ningún remordimiento por sus corruptas almas... no están libres de mal.", progress = "Have you slain the wretched trolls?", completion = "This is excellent, &lt;nombre&gt;.  I would say I am surprised, but I knew without question that you wouldn't let us down.\n\nIt is my honor to bestow one of the Holy Mightstones to you.  Use it well.", rewards = "Podrás elegir una de estas recompensas:\n\nHoja forjada con luzOrbe santificadoSello caballeresco\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nPiedra sagrada del poderíoHoja forjada con luzSello caballerescoOrbe santificado"},
[8419] = {title = "Pedido de un diablillo", objective = "Lleva una pieza de tela de inferi a Diblis en Frondavil.", description = "<nombre>, un diablillo de Frondavil, llamado Diblis, ha pedido tu presencia. Es algo extraño, pidió que llevaras contiguo una pieza de tela de inferi como símbolo de tu buena fe. Teniendo en cuenta tu poder como brujo supongo que ya sabrás que los sátiros de Frondavil llevan consigo telas de inferi.\n\nEncontrarás a Diblis al norte de las Cascadas del Veneno, un lugar realmente encantador en esta época del año.", progress = "¿Tienes mi tela de inferi?", completion = "¡Eres un &lt;clase&gt; excelente!\n\n\n\nOh, me encanta el tacto de la tela, cómo quema la piel y cómo entreteje pensamientos malvados en la mente...", rewards = "Recibirás:\n\n"},
[8420] = {title = "Caliente y picante", objective = "Lleva un retal de tela de inferi a Diblis a Frondavil.", description = "Puede que suene extraño viniendo de un diablillo, pero quiero tener una mascota. Me encantaría tener una. ¡No es justo que Niby me tenga a mí y yo no tenga nada!\n\nNi siquiera tiene que ser una mascota viva. Solo necesito algo para empezar a hacer una, como una tela de inferi. Los sátiros de Frondavil a veces tienen tela de inferi. Solo tendrás que ir matando a los que encuentres hasta que consigas una. No está mal el plan, ¿verdad?Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[8421] = {title = "La mercancía equivocada", objective = "Lleva 10 maderas putrefactas y 4 esencias de veneno a Diblis a Frondavil.", description = "Ahora que tengo mi tela de inferi, puedo empezar a hacer mi mascota.\n\nPero va a necesitar unos ojos... Quizás puedas robar un poco de madera podrida de los Troncoferro, para que pueda tallarle unos bonitos ojos. Están al norte, en el Bosque de Troncoferro.\n\nY también necesitaré un poco de esencia de veneno para que tenga olorcillo a demonio. Los mocos corruptos tienen esa esencia. Suelen estar por aquí, cerquita de Niby y de mí.", progress = "¿Tienes las cosas?", completion = "Qué bien, qué bien.", rewards = "Recibirás:\n\n"},
[8422] = {title = "Plumas de trol", objective = "Reúne un total de 6 plumas vudú en el Templo Sumergido.", description = "Casi he acabado mi mascota. Ahora necesito algo para rellenarla.\n\nY solo pueden ser plumas vudú de los trols del Templo Sumergido. Se llaman Gasher, Mijar, Hukku, Zul'lorlo y Lor'ho. Mátalos. Mátalos a todos.\n\nNo será tarea fácil, <nombre>, pero haré que te merezca la pena correr el riesgo. Te daré una cosa de Niby... no creo que vaya a necesitarla de todas formas.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nCosechador de almasFragmento abisalTogas de Servidumbre\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nTogas de ServidumbreFragmento abisalCosechador de almas"},
[8423] = {title = "Parentesco guerrero", objective = "Mata a 7 jabinfernos de Las Tierras Devastadas y vuelve junto al Héroe Caído de la Horda.", description = "<El Héroe te mira detenidamente.>\n\nEres un <clase>, vaya si lo eres. Te encargaría una tarea honorable pero primero debo poner a prueba tu fuerza.\n\nDicen que Rexxar mide el filo de sus espadas matando a un jabinferno de un solo sablazo. Así que mediré la fuerza que tienes por cómo los matas.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[8424] = {title = "La guerra contra los Sombra Jurada", objective = "Acaba con los Sombra Jurada en las Tierras Devastadas y regresa junto al Héroe Caído de la Horda.", description = "La continuada presencia de los Sombra Jurada en Las Tierras Devastadas me entristece sobremanera. Sus oscuros rituales y hechizos causan agonía como nada que yo sufriera en vida.\n\nA través de ti puedo enviar mi mensaje de dolor. Adelante y transmíteles mi ira.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[8425] = {title = "Plumas Vudú", objective = "Llévale las plumas vudú de los trols del Templo Sumergido al Héroe Caído de la Horda.", description = "Tu honor me ha devuelto algo de paz. Es de justicia que te entregue algo a cambio.\n\nMis armas y armadura eran espléndidas en vida pero ahora ya no me sirven. Cumplirán mejor su función en tu posesión.\n\nVe al Templo Sumergido y destruye a los trols guardianes Gasher, Mijar, Zolo, Hukku, Zul'lor y Lor'ho. Llevan plumas mágicas que pueden recrear mi cuerpo físico lo suficiente para que te pueda dar tu premio.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nVisor de furiaFrasco de diamanteHombreras acero afilado\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nFrasco de diamanteHombreras acero afiladoVisor de furia"},
[8426] = {title = "La batalla por Garganta Grito de Guerra", objective = "Entrega 3 marcas de honor de Grito de Guerra a un Belisario de la Horda fuera de los campos de batalla.", description = "La batalla de la Garganta Grito de Guerra contra las centinelas Ala de Plata tiene una importancia vital. Con la excusa de estar protegiendo un bosque que no les pertenece, la Alianza intenta negar a la Horda una de sus principales fuentes de recursos madereros.\n\n¡No debemos permitirlo, <nombre>! ¡Vuelve a verme cuando puedas demostrar que has servido a la Horda dignamente!", progress = "¿Traes alguna noticia de la Garganta Grito de Guerra, <nombre>?", completion = "¡Excelente! ¡Has probado tu valía defendiendo nuestras operaciones en la Garganta Grito de Guerra! Que las sagas de tu honor recorran todas nuestras tierras.", rewards = "Te van a lanzar el siguiente hechizo:\n\nPuntos de honor +82\n\nRecibirás:\n\n"},
[8427] = {title = "La batalla por Garganta Grito de Guerra", objective = "Entrega 3 marcas de honor de Grito de Guerra a un Belisario de la Horda fuera de los campos de batalla.", description = "La batalla de la Garganta Grito de Guerra contra las centinelas Ala de Plata tiene una importancia vital. Con la excusa de estar protegiendo un bosque que no les pertenece, la Alianza intenta negar a la Horda una de sus principales fuentes de recursos madereros.\n\n¡No debemos permitirlo, <nombre>! ¡Vuelve a verme cuando puedas demostrar que has servido a la Horda dignamente!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
