[8440] = {title = "La conquista de la Cuenca de Arathi", objective = "¡La guerra contra la escoria de la Alianza exige recursos, <nombre>! No podemos permitir que nuestras reservas se agoten en medio de una batalla. ¡Sigue asegurándote de que la Cuenca de Arathi siga en nuestras manos!", description = nil, progress = "¡La guerra contra la escoria de la Alianza exige recursos, <nombre>! No podemos permitir que nuestras reservas se agoten en medio de una batalla. ¡Sigue asegurándote de que la Cuenca de Arathi siga en nuestras manos!", completion = "¡Un trabajo excelente, &lt;nombre&gt;! Mientras sigamos controlando la Cuenca de Arathi nuestros suministros estarán garantizados.", rewards = nil},
