[8572] = {title = "Equipamiento de Veterano", objective = "Lleva 7 insignias de combate Cenarion, 4 insignias de logística Cenarion y 4 insignias tácticas Cenarion a Vargus en el Fuerte Cenarion, en Silithus. También debes conseguir un nivel de reputación honrado con el Círculo Cenarion para poder completar esta misión.", description = "Las insignias que recibes por misiones de combate, logística y táctica para el Fuerte Cenarion, sirven para algo más que para presumir, <nombre>.\n\nTe aconsejo que las guardes bien. Como prueba de tu lealtad con nuestra organización, puedo ofrecerte equipamiento que reservamos para nuestros aliados más cercanos, a cambio de esas insignias.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nGracia de TierraSortija de Poderío de tierraJubón Poder terrenal\n\nTambién recibirás:\n\nGracia de TierraSortija de Poderío de tierraJubón Poder terrenal"},
[8573] = {title = "Equipamiento de Campeón", objective = "Lleva 15 insignias de combate Cenarion, 20 insignias de logística Cenarion, 20 insignias tácticas Cenarion y 1 Marca de Cenario a Vargus en el Fuerte Cenarion, en Silithus. También debes conseguir un nivel de reputación exaltado con el Círculo Cenarion para poder completar esta misión.", description = "Las insignias que recibes por misiones de combate, logística y táctica para el Fuerte Cenarion, sirven para algo más que para presumir, <nombre>.\n\nTe aconsejo que las guardes bien. Como prueba de tu lealtad con nuestra organización, puedo ofrecerte equipamiento que reservamos para nuestros aliados más cercanos, a cambio de esas insignias.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nPuño de CenarioCólera de CenarioTerremoto\n\nTambién recibirás:\n\nTerremotoPuño de CenarioCólera de Cenario"},
[8574] = {title = "Equipamiento de Fiel", objective = "Lleva 15 insignias de combate Cenarion, 20 insignias de logística Cenarion, 17 insignias tácticas Cenarion y 1 Marca de Rémulos a Vargus en el Fuerte Cenarion, en Silithus. También debes conseguir un nivel de reputación venerado con el Círculo Cenarion para poder completar esta misión.", description = "Las insignias que recibes por misiones de combate, logística y táctica para Astaorgullo Clamavientos, sirven para algo más que para presumir, <nombre>.\n\nTe aconsejo que las guardes bien. Como prueba de tu lealtad con nuestra organización, puedo ofrecerte equipamiento que reservamos para nuestros aliados más cercanos, a cambio de esas insignias.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nBrazales de roca profundaOrbe CalmaterraBrazales de Furia de rocaPoderío de Cenario\n\nTambién recibirás:\n\nBrazales de roca profundaOrbe CalmaterraBrazales de Furia de rocaPoderío de Cenario"},
[8575] = {title = "El libro de contabilidad mágico de Azuregos", objective = "Entrega el libro de contabilidad mágico de Azuregos a Narain Sabelotodo de Tanaris.", description = "No entiendes ni una palabra de este libro de contabilidad. ¡Ni siquiera sabes en qué idioma está escrito!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[8576] = {title = "La traducción del libro de contabilidad", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[8577] = {title = "Stewvul, ex mejor amigo", objective = "Narain Sabelotodo quiere que encuentres a su ex mejor amigo de toda la vida, Guisón, y que recuperes las gafas que le robó.", description = "Si quieres que te traduzca algo, necesitaré mis gafas de ver. Por desgracia, Stewvul, mi ex-mejor amigo de toda la vida me las robó hace un tiempo. Me rompió el corazón por cinco sitios y luego me dejó aquí para que me pudriera.\n\nCon la ayuda de mi bola de cristal, que SÍ es de fiar, he averiguado su paradero. Puede estar o en La Muralla de Cringrís de Argénteos o en lo que parece ser un portal en Terrallende que ahora está controlado por la Legión Ardiente. ¡Vas a tener que encontrarlo para recuperar mis gafas, <nombre>!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[8578] = {title = "¿Unas gafas? ¡Sin problemas!", objective = "Encuentra las gafas de Narain y llévaselas a Tanaris.", description = "<Oyes un suspiro de alivio que proviene de la caja.>\n\n¿Solo quieres sus gafas de ver? ¡Genial! Eso no será un problema... Solo dame un minuto para... ¡Eh! ¿Dónde dem...? ¡Maldita sea!\n\nMira, tengo una mala noticia y una peor. La mala noticia es que perdí las gafas. La peor es que las perdí cuando estaba curioseando por el Núcleo de Magma el mes pasado. Sí, el Núcleo de Magma... ¡Así que, supongo que ya no quieres nada más de mí! ¡Adiós!Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nPoción rejuvenecedora sublime"},
[8579] = {title = "Campeones mortales", objective = "Presentimos la presencia de nuestra estirpe esclavizada en el interior de Ahn'Qiraj; y ni un ejército de dragones bastarían para rescatarlos, <raza>.\n\nSi nos acercamos un poco más, el mismo hechizo que los retiene nos colocaría firmemente bajo el control de C'Thun.\n\nEl dios antiguo ha anticipado nuestra llegada. Toda esperanza es vana. ¿Qué diferencia podría suponer un simple mortal como tú?", description = nil, progress = "Presentimos la presencia de nuestra estirpe esclavizada en el interior de Ahn'Qiraj; y ni un ejército de dragones bastarían para rescatarlos, <raza>.\n\nSi nos acercamos un poco más, el mismo hechizo que los retiene nos colocaría firmemente bajo el control de C'Thun.\n\nEl dios antiguo ha anticipado nuestra llegada. Toda esperanza es vana. ¿Qué diferencia podría suponer un simple mortal como tú?", completion = "Veo que no eres un mortal cualquiera, &lt;nombre&gt;. Tus posibilidades contra el dios antiguo, aunque ínfimas, son mayores que las nuestras en este momento. A fin de cuentas, puede que espera a dragones en la puerta pero no está preparado para alguien como tú.\n\nIgual que Anacronos y Corzocelada derrotaron a los Qiraji hace mil años gracias a la alianza entre mortales y dragones, también deberíamos llegar a un pacto. Gánate la confianza del Vuelo de Bronce y te ganarás la nuestra. Solo entonces haremos de ti un Campeón de verdad.", rewards = "Te van a lanzar el siguiente hechizo:\n\nReputación - Jefe del templo de Ahn'Qiraj\n\nRecibirás:\n\n"},
[8580] = {title = "¡La Horda necesita flores de fuego!", objective = "Llévale 20 flores de fuego a montamurciélagos Pele'keiki en el Valle de los Espíritus de Orgrimmar.", description = "Eh, <clase>. ¡Que llamar a ti! Montón flor de fuego la Horda pensar necesidad para preparativos de guerra Ahn'Qiraj. ¿Poder servir en cosas majas esas florecillas? ¿Poder servir combustible para tanques de vapor de la Alianza? ¿Quién saber? No Pele'keiki!\n\nAunque Pele'keiki saber esto... él saber tú ayudarlo conseguir montón flores de fuego, ¿eh, colega?Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nSello de honor de la AlianzaSuministros de la campaña solidaria de Ahn'Qiraj"},
[8581] = {title = "¡La Horda necesita más flores de fuego!", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nSello de honor de la AlianzaSuministros de la campaña solidaria de Ahn'Qiraj"},
[8582] = {title = "¡La Horda necesita lotos cárdenos!", objective = "Llévale 20 lotos cárdenos al boticario Jezel en el Valle de los Espíritus de Orgrimmar.", description = "El loto cárdeno, <clase>, es un componente de muchas grandes pociones, elixires y aceites, algunos de los cuales nos serán de utilidad en Ahn'Qiraj. Tendremos que elaborar una ingente cantidad de tales pociones para los preparativos de la guerra. Y además experimentaré con nuevos usos para el loto cárdeno. Pero para empezar voy a necesitar grandes cantidades de esta flor.\n\nAquí es donde entras tú. Sal al campo y recoge tantos lotos cárdenos como puedas. ¿Es algo que estés <dispuesto/dispuesta> a hacer para nosotros?", progress = "¿Has vuelto ya con el loto cárdeno que te pedí, <clase>? Es vital que lo hagas en el menor tiempo posible no vaya a ser que todo lo que estoy intentando aquí se malogre.", completion = "Un trabajo digno de elogio, &lt;clase&gt;. Mediré estos ejemplares para que se usen apropiadamente y tomaré nota de tu contribución. Habrá que guardar debidamente el loto cárdeno hasta que esté listo. En cuanto a ti, si aún estás disponible, aún voy a necesitar más loto cárdeno. Así que pásate de nuevo pronto.", rewards = "Recibirás:\n\nSello de honor de la AlianzaSuministros de la campaña solidaria de Ahn'Qiraj"},
[8583] = {title = "¡La Horda necesita más lotos cárdenos!", objective = "De hecho, al parecer necesitamos aún más loto cárdeno, <clase>. Aunque mis nuevas investigaciones no hayan permitido todavía descubrir nuevas aplicaciones de utilidad para esta hierba, aún quedan metodologías ciertas y probadas que aprovechar.\n\nNecesito que vayas de nuevo al campo y recolectes al menos 20 ejemplares de loto cárdeno. Cuando los tengas, tráemelos aquí.", description = nil, progress = "De hecho, al parecer necesitamos aún más loto cárdeno, <clase>. Aunque mis nuevas investigaciones no hayan permitido todavía descubrir nuevas aplicaciones de utilidad para esta hierba, aún quedan metodologías ciertas y probadas que aprovechar.\n\nNecesito que vayas de nuevo al campo y recolectes al menos 20 ejemplares de loto cárdeno. Cuando los tengas, tráemelos aquí.", completion = "Un trabajo de primera y de lo más preciso, &lt;nombre&gt;. Tu diligencia es digna de elogio, al igual que tu celeridad. Me ocuparé personalmente de que no se desperdicia un ápice de este loto cárdeno.\n\nGracias otra vez y si encuentras más loto cárdeno, no dudes en traérmelo tan raudo como puedas.", rewards = "Recibirás:\n\nSello de honor de la AlianzaSuministros de la campaña solidaria de Ahn'Qiraj"},
[8584] = {title = "¡Nunca me preguntes por mi negocio!", objective = "Narain Sabelotodo de Tanaris quiere que hables con Dirge Hojágil de Gadgetzan.", description = "Mira, hay algo que tienes que saber sobre mí: ¡NUNCA JAMÁS me preguntes por mi negocio! Trabajo con cosas que sencillamente no entenderías. Como, por ejemplo: ¿sabes lo que es un bozalnano psicotrónico? ¡Pues claro que no! Por eso yo me encargo de la planificación y tú de los recaditos.\n\nComo ya te he dicho, si queremos que esto funcione, necesitaremos un pollo de 200 kilos.\n\nVe a Gadgetzan y habla con Dirge Hojágil. Él te dará pistas para que encuentres un espécimen adecuado.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[8585] = {title = "La Isla del Terror", objective = "Lleva el cadáver de Lakmaeran y 20 costillitas tiernas de quimerok a Dirge Hojágil a Tanaris.", description = "La leyenda cuenta que los chimaerok de la Isla del Terror de Feralas tienen los lomitos más tiernos y suculentos del mundo. Y resulta que estos chimaerok también tienen un papá que, con la presentación adecuada, puede parecer un pollo de 200 kilos... o no.\n\nVe a la Isla del Terror y mata a Lord Lakmaeran. Tráeme su cadáver fresquito. Eso debería dejar contento al gnomo. Pero para dejarme contento a mí, tendrás que traerme varias costillitas tiernas de chimaerok.\n\nSi lo haces, tendrás tu pollo de 200 kilos y un pequeño extra.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[8586] = {title = "Pinchos quimerok potentes de Dirge", objective = "Dirge Hojágil de Gadgetzan quiere que le lleves 20 unidades de combustible de cohete goblin y 20 de sal de roca.", description = "¡Mi receta está acabada! Lo que necesito ahora es que me traigas combustible de cohete y sal de roca. Yo me encargo de las especias y la carne que me trajiste completará el plato.\n\nCuando me traigas lo que te he pedido, el pollo estará listo para ser llevado a Narain.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nReceta: pinchos quimerok potentes de DirgePinchos quimerok potentes de Dirge"},
[8587] = {title = "Vuelve con Narain", objective = "Entrega el pollo de 200 kilos a Narain Sabelotodo de Tanaris.", description = "Soy un goblin de palabra, <chico/chica>. Aquí tienes tu pollo de 200 kilos.\n\nLo que no sé es para qué quiere un gnomo un pollo de 200 kilos...\n\n<Dirge rezonga algo ininteligible.>\n\nProbablemente para dormir en él... Gnomos cochinos...Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[8588] = {title = "¡La Horda necesita cuero pesado!", objective = "Llévale 10 piezas de cuero pesado al desollador Jamani en el Valle de los Espíritus de Orgrimmar.", description = "Ya que nos estamos preparando para la guerra, será mejor que consigamos primero todo lo que necesitamos, ¿no te parece, colega? Uno de los productos del material bélico que producimos es cuero pesado. Lo usamos para casi todo, desde refuerzos para armaduras y para clásicas lonas también.\n\nPero primero tenemos que conseguir más cantidad. Ese es tu trabajo, <clase>. Afila tu navaja y tráeme un paquete. Entonces seguiremos hablando.", progress = "No tengo tiempos para jueguecitos si solo has venido a dar discursos, <clase>. Hay demasiadas pilas de cuero pesado que curtir. Vuelve cuando tengas al menos 10 para mí.", completion = "Ah, así que ya tienes el cuero pesado que necesitaba. Afilada es tu espada, &lt;clase&gt;; ¡estas pieles son muy buenas! Ahora que hemos empezado con buen pie, me ocuparé de que se aproveche debidamente tu contribución. Y si encuentras más cuero pesado, tráemelo.", rewards = "Recibirás:\n\nSello de honor de la AlianzaSuministros de la campaña solidaria de Ahn'Qiraj"},
[8589] = {title = "¡La Horda necesita más cuero pesado!", objective = "Así es, <clase>, aún necesito más cuero pesado. Los pedidos de los generales y sus intendentes no se acaban nunca. ¡Y eso no es nada comparado con lo que los maestros de zepelín están pidiendo!\n\nTengo que completar mi cupo a paso ligero. <nombre>, tráeme más pilas de cuero pesado ¡tan pronto como puedas!", description = nil, progress = "Así es, <clase>, aún necesito más cuero pesado. Los pedidos de los generales y sus intendentes no se acaban nunca. ¡Y eso no es nada comparado con lo que los maestros de zepelín están pidiendo!\n\nTengo que completar mi cupo a paso ligero. <nombre>, tráeme más pilas de cuero pesado ¡tan pronto como puedas!", completion = "¡Así me gusta, colega! Muchísimas gracias por el cuero pesado. Cada granito de arena es importante. Voy a dejar estas pieles en una pila para que los demás peleteros puedan trabajarlas.\n\nGracias otra vez, &lt;nombre&gt;, y si te topas con más cuero pesado, ya sabes dónde encontrarme.", rewards = "Recibirás:\n\nSello de honor de la AlianzaSuministros de la campaña solidaria de Ahn'Qiraj"},
[8590] = {title = "¡La Horda necesita cuero pesado!", objective = "Llévale 10 piezas de cuero grueso al sargento Umala en el Valle de los Espíritus de Orgrimmar.", description = "¡Cómo me alegra que hayas decidido hablarme de eso, <clase>! Por lo visto, los preparativos de la Horda para la próxima guerra de Ahn'Qiraj están a toda máquina. Pero aún falta nuestra contribución.\n\nMe han pedido que reúna un gran número de pieles de cuero pesado para usarlas en todo tipo de material bélico; necesito que vayas a buscarlas y las recolectes. Te llevarás honores y la gratitud de la Horda si decides ayudarnos. ¿Lo harás?", progress = "Ah, <clase>, ¿tan pronto de vuelta con las 10 pieles de cuero pesado de las que hablamos?", completion = "Buen trabajo, &lt;clase&gt;. Estoy seguro de que todas estas pieles de cuero pesado servirán para reforzar los zepelines y confeccionar todo tipo de armas y armaduras. Cuentas con mi más sincero agradecimiento, así como con el de la Horda. Vuelve si encuentras más cuero pesado en tus viajes y que la suerte te acompañe.", rewards = "Recibirás:\n\nSello de honor de la AlianzaSuministros de la campaña solidaria de Ahn'Qiraj"},
[8591] = {title = "¡La Horda necesita más cuero pesado!", objective = "<clase>, como ves, necesito recolectar aún más cuero pesado. Una vez más te pido tu ayuda para esta tarea y prometo que si la completas, se te reconocerá tu dedicación.\n\n¡El tiempo es vital! ¡Vuelve aquí cuando tengas el cuero pesado para que pueda acabar nuestros preparativos para la guerra, heroína!", description = nil, progress = "<clase>, como ves, necesito recolectar aún más cuero pesado. Una vez más te pido tu ayuda para esta tarea y prometo que si la completas, se te reconocerá tu dedicación.\n\n¡El tiempo es vital! ¡Vuelve aquí cuando tengas el cuero pesado para que pueda acabar nuestros preparativos para la guerra, heroína!", completion = "¡Lo conseguiste, &lt;nombre&gt;, sabía que lo conseguirías! Una vez más, gracias en nombre de toda la Horda. Se toma buena nota de tu trabajo y se agradece. Y si encuentras más cuero pesado que te gustaría donar, tráemelo aquí.\n\n¡Lok'tar Ogar, &lt;clase&gt;!", rewards = "Recibirás:\n\nSello de honor de la AlianzaSuministros de la campaña solidaria de Ahn'Qiraj"},
[8592] = {title = "La tiara del Oráculo", objective = "Llévale el aro de Vek'lor, 2 ídolos del sabio, 5 escarabajos de plata y 5 escarabajos de hueso a Andorgos en Ahn'Qiraj. También debes alcanzar la reputación de amistoso ante el Linaje de Nozdormu para completar esta misión.", description = "Los Emperadores Gemelos... sus incesantes susurros se cobran lentamente un precio sobre mi sano juicio. Se mofan de mí, a sabiendas de que no puedo dar un paso más en sus dominios para salvar a mi hermano Arygos.\n\nTengo visiones de sus monstruosos rostros y su poderosa corona. Pero me vengaré... Transformaré esos símbolos de poder quiraji en unos más adecuados a nuestras necesidades.\n\nTráeme los componentes que necesito, <nombre>. Te daré un tocado de poder único.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nTiara del Oráculo"},
[8593] = {title = "Los pantalones del Oráculo", objective = "Llévale la piel intacta de Ouro, 2 ídolos de la vida, 5 escarabajos de oro y 5 escarabajos de terracota a Kandrostrasz en Ahn'Qiraj. También debes alcanzar la reputación de amistoso ante el Linaje de Nozdormu para completar esta misión.", description = "¿Deseas demostrar cuánto vales a los de mi especie, mortal? Vale, quizás podemos ayudarnos mutuamente.\n\nEl antiguo gusano de arena, Ouro, es un ser cuyo poder es legendario. Se rumorea que el propio antiguo Dios lo creó como una burla a la vida.\n\nSu piel no es natural... gruesa, casi impenetrable aunque flexible, lo que le permite desplazarse velozmente y con la gracia de criaturas mucho más pequeñas.\n\nTráeme una muestra de su piel y otros componentes menores y te fabricaré unas poderosas mallas.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nPantalones del Oráculo"},
[8594] = {title = "El manto del Oráculo", objective = "Llévale las ataduras de mando qiraji, 2 ídolos de renacimiento, 5 escarabajos de plata y 5 escarabajos de marfil a Andorgos en Ahn'Qiraj. También debes alcanzar la reputación neutral ante el Linaje de Nozdormu para completar esta misión.", description = "Se cuenta entre nosotros que el adorno de la armadura de los hombros que llevan las razas mortales se inspiró en un intento de emular las alas de un dragón.\n\nTráeme las esclavas que llevan los más importantes líderes qiraji y con ellas te forjaré unas hombreras ¡más temidas que las alas del mismísimo Nefarian!Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nManto del Oráculo"},
[8595] = {title = "Campeones mortales", objective = "Matar a un señor qiraji es un logro notable para un mortal, <nombre>. Sin embargo, son muchos y poderosos. Sigue derrotándolos y demuestra lo que vales como nuestro Campeón.", description = nil, progress = "Matar a un señor qiraji es un logro notable para un mortal, <nombre>. Sin embargo, son muchos y poderosos. Sigue derrotándolos y demuestra lo que vales como nuestro Campeón.", completion = "Sigues sirviéndonos bien, &lt;nombre&gt;. Nuestro pacto sigue en pie.", rewards = "Te van a lanzar el siguiente hechizo:\n\nReputación - Jefe del templo de Ahn'Qiraj"},
[8596] = {title = "Las botas del Oráculo", objective = "Llévale las ataduras de mando qiraji, 2 ídolos de muerte, 5 escarabajos de bronce y 5 escarabajos de oro a Kandrostrasz en Ahn'Qiraj. Esta misión requiere la reputación neutral con la facción Linaje de Nozdormu.", description = "La magia qiraji es poderosa y antigua. Los más poderosos habitantes de Ahn'Qiraj empuñan objetos de tal potencia, que dan que pensar hasta a un dragón.\n\nTráeme las esclavas de mando qiraji y otros componentes menores y crearé un par de poderosas botas para ti.\n\nTe servirán cuando te enfrentes al enemigo.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nBorceguíes del Oráculo"},
[8597] = {title = "Dracónico para torpes", objective = "Encuentra el libro de Narain Sabelotodo. Está enterrado en una isla de los Mares del Sur.", description = "¡Sabía que ese libro acabaría resultando útil un día! Menos mal que tuve la rapidez y la previsión de guardarlo en lugar seguro.\n\nNo te preocupes, ¡está en un sitio en el que nadie podría encontrarlo nunca! ¡Fíjate que necesité un girocóptero para llegar allí la primera vez Por desgracia, me estrellé en la playa en el camino de regreso. ¡Pero fue precisamente ese accidente lo que me dio mis súpergnomo-poderes psíquicos!\n\nBusca mi girocóptero estrellado por la Playa Finisterrae y nada hacia al sur desde allí. ¡Reconocerás el sitio en cuanto lo veas!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[8598] = {title = "ReScaTe", objective = "Lleva la carta del rescate a Narain Sabelotodo a Tanaris.", description = "<El texto de esta nota se compone de recortes de letras pegados en la superficie.>\n\nTenemos tu precioso libro, gnomo. Si quieres volver a verlo, tendrás que hacer lo que te pidamos.\n\n<Hay una detallada lista de instrucciones que ocupa 3 páginas.>\n\n¡Trae el dinero! ¡Y ven solo!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[8599] = {title = "Una canción de amor para Narain", objective = "Lleva la carta de amor de Meridith a Narain Sabelotodo a Tanaris.", description = "No recibo muchas visitas. De hecho, la última vez que tuve visita fue cuando rescaté a ese pobre gnomo de una máquina voladora que se estrelló.\n\n<Meridith mira al vacío, perdida en sus recuerdos.>\n\n¿Lo conoces? ¿Narain? ¿Narain Sabelotodo? ¡No sabes lo que significaría para mí que alguien le llevara un mensaje de mi parte! ¡Necesito decirle lo que siento! ¡Sé que es amor! ¡Sencillamente lo sé!\n\n¿No podrías llevar un mensaje a Narain? ¡Estaría en deuda contigo para siempre!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[8600] = {title = "¡La Horda necesita pieles de cuero basto!", objective = "Llévale 10 piezas de cuero basto al doctor Serratus en el Valle de los Espíritus de Orgrimmar.", description = "Considero que es más importante que la navaja se ajuste bien a tu palma que el estado del filo. Hasta la hoja más desgastada puede cortar si se aplica la presión debida, ¿no te parece, <clase>? Aunque tú y yo no tenemos tal problema ya que mantenemos siempre las hojas más afiladas, ¿cierto? \n\nBueno, da igual, tengo una tarea para ti. Como servicio a la Horda, y para contribuir a los preparativos de la guerra de Ahn'Qiraj, deseo que cuando vuelvas me traigas pieles de cuero basto. ¿Verdad que harás esto por nosotros?", progress = "¡No es posible que te lleve tanto tiempo desollar 10 pieles de cuero basto para mí! ¿Quizás necesites afilar tu cuchillo de desollar? ¿O acaso estoy confundido y las tienes ahí en tu bolsa?", completion = "Muy bien, parece que has hecho un buen trabajo, aunque algunas de estas pieles tienen los bordes algo irregulares. No importa, has completado la tarea y te has ganado una recompensa. Si te vuelves a encontrar con más cuero basto, tráemelo inmediatamente.", rewards = "Recibirás:\n\nSello de honor de la AlianzaSuministros de la campaña solidaria de Ahn'Qiraj"},
[8601] = {title = "¡La Horda necesita más pieles de cuero basto!", objective = "Los preparativos para la próxima guerra de Ahn'Qiraj están en curso, <clase>. Sin embargo, para garantizar que no nos quedamos rezagados, o al menos que no sea yo quien nos retrase, quiero que traigas otra pila de cuero basto. Si le concedes la prioridad a este asunto, te estaremos infinitamente agradecidos, <nombre>.", description = nil, progress = "Los preparativos para la próxima guerra de Ahn'Qiraj están en curso, <clase>. Sin embargo, para garantizar que no nos quedamos rezagados, o al menos que no sea yo quien nos retrase, quiero que traigas otra pila de cuero basto. Si le concedes la prioridad a este asunto, te estaremos infinitamente agradecidos, <nombre>.", completion = "Práctica, práctica, práctica. Con un cuchillo bien afilado y la habilidad para usarlo llegarás lejos en este mundo, &lt;nombre&gt;. Parece que los bordes de estas pieles están mejor que las últimas. ¿Compraste un cuchillo nuevo?\n\nSea como fuere, te agradecemos tus esfuerzos. He tomado buena nota de tu donación y se aprovechará bien, te lo aseguro. Si te acabas haciendo con más cuero basto, vuelve por aquí y habla conmigo ya que seguramente me hará falta más.", rewards = "Recibirás:\n\nSello de honor de la AlianzaSuministros de la campaña solidaria de Ahn'Qiraj"},
[8602] = {title = "Los espaldares de clamatormentas", objective = "Llévale las ataduras de dominio qiraji, 2 ídolos de la vida, 5 escarabajos de oro y 5 escarabajos de cristal a Andorgos en Ahn'Qiraj. También debes alcanzar la reputación neutral ante el Linaje de Nozdormu para completar esta misión.", description = "Se cuenta entre nosotros que el adorno de la armadura de los hombros que llevan las razas mortales se inspiró en un intento de emular las alas de un dragón.\n\nTráeme las esclavas que llevan los más importantes líderes qiraji y con ellas te forjaré unas hombreras ¡más temidas que las alas del mismísimo Nefarian!Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nEspaldares de Clamatormentas"},
[8603] = {title = "Las vestimentas del Oráculo", objective = "Llévale la coraza del dios antiguo, 2 ídolos de muerte, 5 escarabajos de hueso y 5 escarabajos de cristal a Vethsera en Ahn'Qiraj. También debes alcanzar la reputación de honrado ante el Linaje de Nozdormu para completar esta misión.", description = "El inmencionable antiguo Dios se despierta despacio de su sueño bajo Ahn'Qiraj. El terrible sino que aguarda a Azeroth podría llegar dentro de unos días o unos años; el tiempo nada significa para seres con su poder.\n\nCuando los astros sean favorables, desatará su ira contra todo ser viviente. Debes hallarlo antes de que ocurra y encontrar la forma de poner fin a su existencia.\n\nHazlo y tráeme un pedazo de sus restos. Te concederé una armadura como ningún mortal ha llevado… ¡una armadura hecha para quien ha acabado con un dios!Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nVestimentas del oráculo"},
[8604] = {title = "¡La Horda necesita vendas de lana!", objective = "Llévale 20 vendas de lana mágica al sanador Largocamino en el Valle de los Espíritus de Orgrimmar.", description = "Saludos, <clase>. Para los preparativos de la guerra de Ahn'Qiraj, estoy coordinando las tareas de la Horda para reunir vendas de lana. Por desgracia, cuando estén ahí fuera, nuestros soldados no pueden depender de la presencia de un sanador para mantenerse vivos. Créeme, he pasado por ahí. Por lo tanto, estamos confeccionando todos estos botiquines de primeros auxilios para ellos.\n\nSi tienes tiempo, necesito muchas vendas de lana. Tráemelas aquí y me ocuparé personalmente de que recibes tu merecida recompensa.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nSello de honor de la AlianzaSuministros de la campaña solidaria de Ahn'Qiraj"},
[8605] = {title = "¡La Horda necesita más vendas de lana!", objective = "¡Qué alegría volver a verte, <clase>! Seguimos trabajando en los preparativos para la guerra y aún me vendría bien cualquier contribución en recolectar vendas de lana. Cualquier ayuda será inmensamente agradecida. Solo espero que nuestros esfuerzos sean suficientes. He perdido demasiadas almas nobles en pasadas contiendas para no hacer todo lo que esté en mis manos para que no vuelva a ocurrir.", description = nil, progress = "¡Qué alegría volver a verte, <clase>! Seguimos trabajando en los preparativos para la guerra y aún me vendría bien cualquier contribución en recolectar vendas de lana. Cualquier ayuda será inmensamente agradecida. Solo espero que nuestros esfuerzos sean suficientes. He perdido demasiadas almas nobles en pasadas contiendas para no hacer todo lo que esté en mis manos para que no vuelva a ocurrir.", completion = "¡Como &lt;clase&gt;, mereces todos mis respetos, &lt;nombre&gt;! Me aseguraré de que esas vendas van a parar a las apropiadas. Cuentas con mi más sincero agradecimiento, así como con el de la Horda.\n\nVe en paz y vuelve si te encuentras con que te sobran vendas de lana. Toda contribución nos viene bien.", rewards = "Recibirás:\n\nSello de honor de la AlianzaSuministros de la campaña solidaria de Ahn'Qiraj"},
[8606] = {title = "Señuelo", objective = "Narain Sabelotodo de Tanaris quiere que viajes a Cuna del Invierno y coloques la bolsa de oro en el punto de entrega indicado por los secuestralibros.", description = "¡Lo tengo! Esto es lo que vamos a hacer: voy a llenar un saco de piedras y tú vas a hacer la entrega. Sé que la nota decía que tenía que hacerla yo, pero es lo que voy a hacer... más o menos.\n\n<Narain te mira de arriba a abajo.>\n\nSí, darás el pego. Coge el turbante, la túnica y el saco de piedras y ve al punto de entrega, en Cuna del Invierno.\n\nHe incluido todo lo que necesitas en este lote especial. Y cuando vengan a recoger la mercancía... ¡acaba con ellos! ¡Eso les enseñará a meterse con Narain!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[8607] = {title = "¡La Horda necesita vendas de paño mágico!", objective = "Llévale 20 vendas de paño mágico a Lady Callow en el Valle de los Espíritus de Orgrimmar.", description = "Espero que estés hablando conmigo porque vas a echar una mano con la recolección que me han confiado? ¡Bien! Por cualquier medio, y digo bien cualquiera, deseo que reúnas un buen número de vendas de paño mágico. Nuevas estaría bien pero no es absolutamente necesario. Tráemelas aquí, <clase>, y me ocuparé personalmente de que recibes tu merecida recompensa.", progress = "¡No me falles, me dejarías mal, <clase>! Vuelve lo antes posible con las vendas de paño mágico de las que hablamos.", completion = "Sorprendente, aunque aceptable. Quizás he subestimado tus facultades, &lt;clase&gt;. Quizás. Has completado una tarea muy sencilla y por ello recibirás una recompensa aún mayor. Pero que no se te suba a la cabeza. Si realmente quieres demostrar lo que vales, tendrás que volver a salir ahí fuera y traerme otro lote de vendas de paño mágico.", rewards = "Recibirás:\n\nSello de honor de la AlianzaSuministros de la campaña solidaria de Ahn'Qiraj"},
[8608] = {title = "¡La Horda necesita más vendas de paño mágico!", objective = "“¿De vuelta? Interesante. Como puedes ver, aún estoy reuniendo cosas para los “”preparativos”” de la guerra de Ahn'Qiraj. Lo cual significa que vuelvo a necesitarte para que salgas a buscar una pila de vendas de paño mágico. Espero con impaciencia tu regreso, <clase>.”", description = nil, progress = "“¿De vuelta? Interesante. Como puedes ver, aún estoy reuniendo cosas para los “”preparativos”” de la guerra de Ahn'Qiraj. Lo cual significa que vuelvo a necesitarte para que salgas a buscar una pila de vendas de paño mágico. Espero con impaciencia tu regreso, <clase>.”", completion = "¡Asombroso, &lt;clase&gt;, lo has vuelto a conseguir! Bueno, cierto es que suelen decir que tengo buen olfato para elegir a mis colaboradores. Muy bien, se anotará tu contribución y recibirás los beneficios sociales que tu trabajo merece. Te sugiero que vuelvas de ronda y recolectes otro lote de vendas de paño mágico si realmente quieres demostrar lo que vales.", rewards = "Recibirás:\n\nSello de honor de la AlianzaSuministros de la campaña solidaria de Ahn'Qiraj"},
[8609] = {title = "¡La Horda necesita vendas de paño rúnico!", objective = "Llévale 20 vendas de paño mágico a Pezuñarcilla Garrapetra en el Valle de los Espíritus de Orgrimmar.", description = "Al parecer la Horda y la Alianza se encuentran abocadas al enfrentamiento en la guerra de Ahn'Qiraj. Sorprendentemente, no es una guerra entre ellas, sino más bien contra los silítidos y la fuerza que los controla sea lo que sea. Otra cosa curiosa es que ambas partes están reuniendo los recursos necesario para librar una batalla con garantías de victoria.\n\nEstoy recogiendo vendas de paño rúnico para distribuirlas entre los soldados, pero no lo conseguiré sin ayuda, espero que la tuya. ¿Crees que puedes ayudarme con eso, <clase>?", progress = "Se avecina la guerra, me parece, <clase>. Te agradezco el tiempo y el esfuerzo que has dedicado a ayudarnos en nuestras tareas de recolección. ¿Has regresado porque ya tienes las vendas de paño rúnico de las que hablamos antes?", completion = "Eres un orgullo para la Horda, &lt;clase&gt;. Gracias por tus continuos esfuerzos, ¡de corazón! Tendré que envolver estas nuevas vendas y hacer un recuento para ver cuánto nos falta para el objetivo. Si te apetece, puedes recolectar más vendas de paño rúnico y traérmelas aquí.", rewards = "Recibirás:\n\nSello de honor de la AlianzaSuministros de la campaña solidaria de Ahn'Qiraj"},
[8610] = {title = "¡La Horda necesita más vendas de paño rúnico!", objective = "Cada vez más cercana está la hora de lanzarnos de lleno en la conflagración con Ahn'Qiraj, <clase>. Pero primero debemos unificar nuestras fuerzas y prepararnos para poder sobrevivir a la masacre de los insectos y sus maestros.\n\nSi has vuelto, seguro que querrás realizar otra contribución de vendas de paño rúnico para los preparativos de guerra, ¿no? Hazlo cuanto antes y vuelve aquí.", description = nil, progress = "Cada vez más cercana está la hora de lanzarnos de lleno en la conflagración con Ahn'Qiraj, <clase>. Pero primero debemos unificar nuestras fuerzas y prepararnos para poder sobrevivir a la masacre de los insectos y sus maestros.\n\nSi has vuelto, seguro que querrás realizar otra contribución de vendas de paño rúnico para los preparativos de guerra, ¿no? Hazlo cuanto antes y vuelve aquí.", completion = "Muestras un gran altruismo en tus actos, &lt;nombre&gt;. Y con ello probablemente se salven muchas vidas de quienes van a combatir en Ahn'Qiraj. Gracias y que sepas que cuentas con el agradecimiento de la Horda por tu donación. Si confeccionas o encuentras más vendas de paño rúnico, te ruego pienses en traérmelas aquí.", rewards = "Recibirás:\n\nSello de honor de la AlianzaSuministros de la campaña solidaria de Ahn'Qiraj"},
[8611] = {title = "¡La Horda necesita escalopines de lobo!", objective = "Llévale 20 escalopines de lobo al guardasangre Alquicrudo en el Valle de los Espíritus de Orgrimmar.", description = "No estoy aquí plantado solo para pillar un buen moreno, <clase>. Hay que preparar cosas para la guerra. Si vamos a aplastar a esos bichos en Ahn'Qiraj, tenemos que estar listos, ¡y eso también incluye la comida!\n\nMe tienen recolectando y salando escalopines de lobo para la guerra que se avecina. Pero me gustaría que te ocuparas de todo esto. Necesito que salgas a matar a unos cuantos lobos, los cocines en escalopines de lobo y me los traigas aquí. ¿Lo pillas?Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nSello de honor de la AlianzaSuministros de la campaña solidaria de Ahn'Qiraj"},
[8612] = {title = "¡La Horda necesita más escalopines de lobo!", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nSello de honor de la AlianzaSuministros de la campaña solidaria de Ahn'Qiraj"},
[8613] = {title = "¡La Horda necesita serviolas moteadas!", objective = "Llévale 20 serviolas moteadas al pescador Len'do en el Valle de los Espíritus de Orgrimmar.", description = "Buen día pa' pescar, ¿no creer, colega? Daguten, porque el menda necesesitar montón de pescao, eso pa’ece. La Horda, ellos pedir a menda encargarse de recolección serviola moteada pa’ ellos. Ellos decir necesidad pa’ preparar guerra con Ahn'Qiraj. Menda decir ellos menda hacer todo pa’ éxito. Así que menda necesitar ayuda. ¿<clase> ayudar menda?Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nSello de honor de la AlianzaSuministros de la campaña solidaria de Ahn'Qiraj"},
[8614] = {title = "¡La Horda necesita más serviolas moteadas!", objective = "Pues como puedes ver, aún hace falta mogollón d’esos pescaditos. Los combatientes de la próxima guerra d’Ahn'Qiraj necesitarán un montón de comida, así que m’hará falta toda la serviola moteada que se pueda conseguir. Las pescas, las cocinas y me las traes aquí, y la Horda y yo t'estaremos agradecíos pa' siempre.", description = nil, progress = "Pues como puedes ver, aún hace falta mogollón d’esos pescaditos. Los combatientes de la próxima guerra d’Ahn'Qiraj necesitarán un montón de comida, así que m’hará falta toda la serviola moteada que se pueda conseguir. Las pescas, las cocinas y me las traes aquí, y la Horda y yo t'estaremos agradecíos pa' siempre.", completion = "¡La pesca más guay, &lt;nombre&gt;! Lo que se dice un trabajo bueno y honesto. ¡Y sin espinas! Gracias por conseguir toda esa serviola moteada, colega. ¡Vuelve pronto si cocinas más!", rewards = "Recibirás:\n\nSello de honor de la AlianzaSuministros de la campaña solidaria de Ahn'Qiraj"},
[8615] = {title = "¡La Horda necesita salmón asado!", objective = "Llévale 20 salmones al horno al Jefe Garrafilo en el Valle de los Espíritus de Orgrimmar.", description = "Los preparativos para la guerra de Ahn'Qiraj es una colosal tarea, <clase>. Todos los materiales que se están recolectando aquí, ya sea metal para las armas o comida, son todos importantísimos. Porque, si llevas varios días sin comer, no tienes fuerzas para levantar el hacha cuando hace falta.\n\nDe ahí que reunamos tantas provisiones como podamos. Estoy recolectando salmón asado, un plato elegido porque alimenta y se conserva muy bien. ¿Puedes ayudarme con esta tarea, <clase>?Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nSello de honor de la AlianzaSuministros de la campaña solidaria de Ahn'Qiraj"},
[8616] = {title = "¡La Horda necesita más salmón asado!", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nSello de honor de la AlianzaSuministros de la campaña solidaria de Ahn'Qiraj"},
[8617] = {title = "¡La Horda necesita piedras nuclearias abrasadas!", objective = "Llévale 50 piedras nuclearias abrasadas al general Zog en Durotar.", description = "Ha llegado la hora de que contribuyas a los preparativos de la guerra de Ahn'Qiraj, <clase>. Se acabaron los idílicos tiempos en los que pasabas el rato ocioso. Ha llegado la hora de servirme a mí y a la Horda y te aseguro que no soy tan indulgente como el Jefe de Guerra.\n\nReunirás a tus fuerzas, una hermandad si la tienes, e invadirás el Núcleo de Magma. En su interior, te encontrarás con que sus moradores tienen piedras nuclearias abrasadas. Tráemelas en grandes cantidades.", progress = nil, completion = nil, rewards = nil},
[8618] = {title = "¡La Horda necesita más piedras nuclearias chamuscadas!", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[8619] = {title = "Alborhondo el Ancestro", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nAutoforzarse: reverencia\n\nRecibirás:\n\nMoneda de linaje"},
[8620] = {title = "La única receta", objective = "Recupera los 8 capítulos perdidos de Dracónico para torpes, únelos con la cubierta mágica y devuelve el libro completo Dracónico para torpes: Volumen II a Narain Sabelotodo de Tanaris.", description = "“Tenía que haberme dado cuenta. ¡Mi archienemigo, el doctor Weavil ha vuelto a las andadas! Y después de tu fracaso en la Cuna del Invierno, ¡el doctor Weavil ha destruido mi libro! ¿Cómo piensas salvar al mundo ahora, <héroe/heroína>?\n\n¡La nota que recibí decía que la única copia conocida de Dracónico para torpes: Volumen II fue dividida en 8 partes y arrojada al viento! Si consigues encontrar los capítulos perdidos, utiliza esta cubierta para reunirlos y vuelve a mí.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nTurbante de poderío psíquico gnómico"},
[8621] = {title = "Las botas de clamatormentas", objective = "Llévale las ataduras de dominio qiraji, 2 ídolos del sabio, 5 escarabajos de bronce y 5 escarabajos de terracota a Kandrostrasz en Ahn'Qiraj. Esta misión requiere la reputación neutral con la facción Linaje de Nozdormu.", description = "La magia Qiraji es fuerte y antigua. Los qiraji más poderosos poseen objetos de tal poder que dejarían tieso hasta a un dragón.\n\nTráeme las Ataduras de dominación qiraji y otros componentes menores y crearé un par de poderosas botas para ti\n\nTe serán muy útiles para enfrentarte al enemigo.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nGuardapiés de Clamatormentas"},
[8622] = {title = "El camisote de clamatormentas", objective = "Consigue el caparazón de un dios antiguo, 2 ídolos del sabio, 5 escarabajos de plata y 5 escarabajos de hueso a Vethsera, dentro de Ahn'Qiraj. También debes tener reputación de honorable con el Linaje de Nozdormu para completar esta misión.", description = "El inmencionable antiguo Dios se despierta despacio de su sueño bajo Ahn'Qiraj. El terrible sino que aguarda a Azeroth podría llegar dentro de unos días o unos años; el tiempo nada significa para seres con su poder.\n\nCuando los astros sean favorables, desatará su ira contra todo ser viviente. Debes hallarlo antes de que ocurra y encontrar la forma de poner fin a su existencia.\n\nHazlo y tráeme un pedazo de sus restos. Te concederé una armadura como ningún mortal ha llevado… ¡una armadura hecha para quien ha acabado con un dios!Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nCamisote de Clamatormentas"},
[8623] = {title = "La diadema de clamatormentas", objective = "Consigue la diadema de Vek'lor, 2 ídolos de renacimiento, 5 escarabajos de piedra y 5 escarabajos de cristal para ANdorgos en Ahn'Qiraj. También debes tener reputación Amistosa con el Linaje de Nozdormu para completar esta misión.", description = "Los Emperadores Gemelos... sus incesantes susurros se cobran lentamente un precio sobre mi sano juicio. Se mofan de mí, a sabiendas de que no puedo dar un paso más en sus dominios para salvar a mi hermano Arygos.\n\nTengo visiones de sus monstruosos rostros y su poderosa corona. Pero me vengaré... Transformaré esos símbolos de poder qiraji en unos más adecuados a nuestras necesidades.\n\nTráeme los componentes que necesito, <nombre>. Te daré un tocado de poder único.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nDiadema Clamatormentas"},
[8624] = {title = "Los leotardos de clamatormentas", objective = "Llévale la piel del gran gusano de arena, 2 ídolos de la disputa, 5 escarabajos de bronce y 5 escarabajos de marfil a Kandrostrasz en Ahn'Qiraj. También debes alcanzar la reputación de amistoso ante el Linaje de Nozdormu para completar esta misión.", description = "¿Deseas demostrar cuánto vales a los de mi especie, mortal? Vale, quizás podemos ayudarnos mutuamente.\n\nEl antiguo gusano de arena, Ouro, es un ser cuyo poder es legendario. Se rumorea que el propio antiguo Dios lo creó como una burla a la vida.\n\nSu piel no es natural... gruesa, casi impenetrable aunque flexible, lo que le permite desplazarse velozmente y con la gracia de criaturas mucho más pequeñas.\n\nTráeme una muestra de su piel y otros componentes menores y te fabricaré unas poderosas mallas.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nLeotardos de Clamatormentas"},
[8625] = {title = "Las hombreras Enigma", objective = "Consigue las ataduras de dominio qiraji, 2 ídolos de renacimiento, 5 escarabajos de plata y 5 escarabajos de marfil para Andorgos en Ahn'Qiraj. También debes tener reputación Neutral con el Linaje de Nozdormu para completar la misión.", description = "Se cuenta entre nosotros que el adorno de la armadura de los hombros que llevan las razas mortales se inspiró en un intento de emular las alas de un dragón.\n\nTráeme las esclavas que llevan los más importantes líderes qiraji y con ellas te forjaré unas hombreras ¡más temidas que las alas del mismísimo Nefarian!Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nHombreras enigma"},
[8626] = {title = "Las botas de artillero", objective = "Llévale las ataduras de mando qiraji, 2 ídolos de la vida, 5 escarabajos de piedra y 5 escarabajos de hueso a Kandrostrasz en Ahn'Qiraj. Esta misión requiere la reputación neutral con la facción Linaje de Nozdormu.", description = "La magia qiraji es poderosa y antigua. Los más poderosos habitantes de Ahn'Qiraj empuñan objetos de tal potencia, que dan que pensar hasta a un dragón.\n\nTráeme las esclavas de mando qiraji y otros componentes menores y crearé un par de poderosas botas para ti.\n\nTe servirán cuando te enfrentes al enemigo.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nGuardapiés de artillero"},
[8627] = {title = "El peto de vengador", objective = "Llévale el caparazón del dios antiguo, 2 ídolos del sabio, 5 escarabajos de plata y 5 escarabajos de hueso a Vethsera en Ahn'Qiraj. También debes alcanzar la reputación de honrado ante el Linaje de Nozdormu para completar esta misión.", description = "El inmencionable antiguo Dios se despierta despacio de su sueño bajo Ahn'Qiraj. El terrible sino que aguarda a Azeroth podría llegar dentro de unos días o unos años; el tiempo nada significa para seres con su poder.\n\nCuando los astros sean favorables, desatará su ira contra todo ser viviente. Debes hallarlo antes de que ocurra y encontrar la forma de poner fin a su existencia.\n\nHazlo y tráeme un pedazo de sus restos. Te concederé una armadura como ningún mortal ha llevado… ¡una armadura hecha para quien ha acabado con un dios!Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nPeto del Vengador"},
[8628] = {title = "La corona de vengador", objective = "Consigue la corona de Vek'lor, 2 ídolos de renacimiento, 5 escarabajos de piedra y 5 escarabajos de cristal para Andorgos en Ahn'Qiraj. También debes tener reputación Amistosa con el Linaje de Nozdormu para completar esta misión.", description = "Los Emperadores Gemelos... sus incesantes susurros se cobran lentamente un precio sobre mi sano juicio. Se mofan de mí, a sabiendas de que no puedo dar un paso más en sus dominios para salvar a mi hermano Arygos.\n\nTengo visiones de sus monstruosos rostros y su poderosa corona. Pero me vengaré... Transformaré esos símbolos de poder quiraji en unos más adecuados a nuestras necesidades.\n\nTráeme los componentes que necesito, <nombre>. Te daré un tocado de poder único.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nCorona del Vengador"},
