[8744] = {title = "Un regalo bien envuelto", objective = "La etiqueta de este regalo dice:\n\nPara <nombre>,\n\n¡Que pases unas dulces y felices fiestas del Gran Invierno!", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nObsequio envuelto con cuidado"},
[8745] = {title = "El tesoro del Atemporal", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nColmillo de KorialstraszPena de CantosombríoEspada rúnica de los rojosLegado de Cresta Cuervo\n\nTambién recibirás:\n\nLegado de Cresta CuervoEspada rúnica de los rojosPena de CantosombríoColmillo de Korialstrasz"},
[8746] = {title = "Metzen, el reno", objective = "Encuentra al reno Metzen. Usa las notas que te han dado como pistas para saber dónde está. \n\nCuando lo encuentres, échale un poco de polvo de reno; así se liberará de sus ataduras. \n\nDespués devuelve el saquito de polvo de reno a Kaymard Cobrellizco, que está en Orgrimmar.", description = "¡Qué desastre! ¡Han secuestrado al reno Metzen!\n\nEs uno de los ocho renos de Papá Invierno y propiedad de Pastos de Bosquehumeante. Hemos recibido dos cartas de dos grupos diferentes que se atribuyen el secuestro. ¡Y precisamente ahora, con la fiesta, estamos sin blanca! Por favor, ¡tráenos a Metzen!\n\nBusca pistas en las cartas. Cuando lo encuentres, rocíalo con este polvo de reno, así se liberará de sus ataduras.\n\n¡Date prisa, por favor!Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nAcebo en conserva"},
