[8767] = {title = "Un regalo un poco agitado", objective = "Parece que han agitado este regalo un par de veces. Su etiqueta dice:\n\nPara un <raza> <clase> muy especial.", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nObsequio ligeramente agitado"},
[8768] = {title = "Un regalo alegremente envuelto", objective = "El regalo está envuelto con motivos propios de estas fiestas y tiene tu nombre.\n\nEspera, ¿no acaba de moverse?", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nObsequio alegremente envuelto"},
[8769] = {title = "Un regalo que hace tic tac", objective = "Tus amigos de los Pastos de Bosquehumeante te desean unas felices fiestas del Gran Invierno.", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nObsequio que hace tic-tac"},
[8770] = {title = "Objetivo: los defensores de Colmen'Ashi", objective = "Mata 30 defensores Colmen'Ashi y preséntate ante la comandante Mar'alith en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas de combate I para poder completar la misión.", description = "Según progresa la guerra, la presencia de silítidos en la zona supone una amenaza constante para nuestras operaciones. Te han seleccionado para participar en un asalto a Colmen'Ashi donde tu objetivo principal consistirá en los defensores de Colmen'Ashi.\n\nInforma a la comandante Mar'alith cuando hayas terminado tu misión.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia de combate Cenarion"},
[8771] = {title = "Objetivo: los acecharenas de Colmen'Ashi", objective = "Mata 30 acecharenas Colmen'Ashi y preséntate ante la comandante Mar'alith en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas de combate II para poder completar la misión.", description = "Según progresa la guerra, la presencia de silítidos en la zona supone una amenaza constante para nuestras operaciones. Te han seleccionado para participar en un asalto a Colmen'Ashi donde tu objetivo principal consistirá en los acecharenas de Colmen'Ashi.\n\nInforma a la comandante Mar'alith cuando hayas terminado tu misión.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia de combate Cenarion"},
[8772] = {title = "Objetivo: los vigías de Colmen'Zora", objective = "Mata 30 vigías Colmen'Zora y preséntate ante la comandante Mar'alith en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas de combate VI para poder completar la misión.", description = "Se han pedido refuerzos para unirse al asalto a Colmen'Zora. Te han seleccionado para atacar a los vigías de Colmen'Zora. Informa a la comandante Mar’alith cuando hayas completado tu misión.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia de combate Cenarion"},
[8773] = {title = "Objetivo: los atracadores de Colmen'Zora", objective = "Mata 30 atracadores Colmen'Zora y preséntate ante la comandante Mar'alith en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas de combate IV para poder completar la misión.", description = "Se han pedido refuerzos para unirse al asalto a Colmen'Zora. Te han seleccionado para atacar a los atracadores de Colmen'Zora. Informa a la comandante Mar’alith cuando hayas completado tu misión.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia de combate Cenarion"},
[8774] = {title = "Objetivo: los emboscadores de Colmen'Regal", objective = "Mata 30 emboscadores Colmen'Regal y preséntate ante la comandante Mar'alith en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas de combate VIII para poder completar la misión.", description = "Según progresa la guerra, la presencia de silítidos en la zona supone una amenaza constante para nuestras operaciones. Te han seleccionado para participar en un asalto a Colmen'Regal donde tu objetivo principal consistirá en los emboscadores de Colmen'Regal.\n\nInforma a la comandante Mar'alith cuando hayas terminado tu misión.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia de combate Cenarion"},
[8775] = {title = "Objetivo: los escupefuego de Colmen'Regal", objective = "Mata 30 escupefuegos Colmen'Regal y preséntate ante la comandante Mar'alith en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas de combate IX para poder completar la misión.", description = "Según progresa la guerra, la presencia de silítidos en la zona supone una amenaza constante para nuestras operaciones. Te han seleccionado para participar en un asalto a Colmen'Regal donde tu objetivo principal consistirá en los escupefuegos aéreos de Colmen'Regal.\n\nInforma a la comandante Mar'alith cuando hayas terminado tu misión.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia de combate Cenarion"},
[8776] = {title = "Objetivo: los esclavizadores de Colmen'Regal", objective = "Mata 30 esclavizadores Colmen'Regal y preséntate ante la comandante Mar'alith en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas de combate X para poder completar la misión.", description = "Según progresa la guerra, la presencia de silítidos en la zona supone una amenaza constante para nuestras operaciones. Te han seleccionado para participar en un asalto a Colmen'Regal donde tu objetivo principal consistirá en los esclavizadores de Colmen'Regal.\n\nInforma a la comandante Mar'alith cuando hayas terminado tu misión.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia de combate Cenarion"},
[8777] = {title = "Objetivo: los hurgadores de Colmen'Regal", objective = "Mata 30 hurgadores Colmen'Regal y preséntate ante la comandante Mar'alith en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas de combate XI para poder completar la misión.", description = "Según progresa la guerra, la presencia de silítidos en la zona supone una amenaza constante para nuestras operaciones. Te han seleccionado para participar en un asalto a Colmen'Regal donde tu objetivo principal consistirá en los hurgadores de Colmen'Regal.\n\nInforma a la comandante Mar'alith cuando hayas terminado tu misión.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia de combate Cenarion"},
[8778] = {title = "La Brigada de Forjaz necesita explosivos", objective = "Lleva 6 aceites de inmolación, 5 unidades de combustible de cohete goblin y 10 unidades de pólvora densa al arcanista Bocafuente cerca de Colmen'Zora en Silithus. También debes llevar el Informe de tareas logísticas IV para poder completar la misión.", description = "La Brigada de Forjaz ha solicitado un extraño pedido de componentes... muy volátiles. Reúnelos y llévaselos al señor Bocafuente de la Brigada de Forjaz.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia logística CenarionSeguimiento del encargo de logística"},
[8779] = {title = "Materiales de visión", objective = "Lleva 1 fragmento grande brillante, 1 fragmento radiante grande y 1 esmeralda enorme a la Geóloga Trotuina en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas logísticas V para poder completar la misión.", description = "Los estudios de la Geóloga Trotuina han demostrado ser de gran valor para nuestras operaciones en Silithus. Recientemente, ha solicitado materiales de visión para utilizar en su investigación.\n\nPor favor, consigue esos materiales y llévaselos directamente a ella para que pueda continuar con su investigación.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia logística CenarionSeguimiento del encargo de logística"},
[8780] = {title = "Complementos para armadura para el campo de batalla", objective = "Lleva 8 complementos bastos para armadura y 8 complementos pesados para armadura a Janela Martillotenaz en la Avanzada de la Brigada de Forjaz, cerca de Colmen'Zora, en Silithus. También debes llevar el Informe de tareas logísticas VII para poder completar la misión.", description = "El suministro de armaduras de la Brigada de Forjaz en el campo de batalla, está empezando a escasear peligrosamente. Consigue un lote y llévaselo a Janela Martillotenaz en la Avanzada de la Brigada de Forjaz, justo fuera de Colmen'Zora.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia logística CenarionSeguimiento del encargo de logística"},
[8781] = {title = "Armas para el campo de batalla", objective = "Lleva 2 espadas de acero lunar a Janela Martillotenaz en la Avanzada de la Brigada de Forjaz, en las afueras de Colmen'Zora. También debes llevar el Informe de tareas logísticas VI para poder completar la misión.", description = "La Brigada de Forjaz ha sido un poderoso aliado en nuestra batalla en Silithus. Por desgracia, perdieron muchos suministros de camino hacia aquí. Consigue un lote de armas y llévaselo a Janela Martillotenaz, está cerca de Colmen'Zora.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia logística CenarionSeguimiento del encargo de logística"},
[8782] = {title = "Suministros para uniformes", objective = "Lleva 1 unidad de tela lunar, 2 madejas de paño rúnico y 1 seda de araña Tejehierro a Astaorgullo Clamavientos en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas logísticas VIII para poder completar la misión.", description = "La última entrada de voluntarios ha provocado escasez de uniformes. Consigue un lote de materiales de uniforme y llévaselo a Astaorgullo Clamavientos.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia logística CenarionEncargo de logística"},
[8783] = {title = "Materiales encantados", objective = "Lleva 2 lingotes de torio encantado y 2 cueros encantados a Vargus en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas logísticas IX para poder completar la misión.", description = "Vargus, el herrero del Fuerte Cenarion, ha solicitado materiales encantados para crear poderosas armas para nuestras tropas. Consíguelos y llévaselos directamente a Vargus para que las tropas del Fuerte Cenarion puedan seguir estando bien equipadas.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia logística CenarionEncargo de logística"},
[8784] = {title = "Los secretos de los Qiraji", objective = "Llévale el artefacto antiguo qiraji a los dragones ocultos cerca de la entrada del templo.", description = "¡Has descubierto un antiguo artefacto de Ahn'Qiraj! Esta reliquia quizás sea de utilidad para uno de los dragones que se encuentra cerca de la puerta del templo.", progress = "¡Estás brillante! Sé lo que eso significa...", completion = "Un hallazgo extraordinario, &lt;nombre&gt;. ¡Sencillamente maravilloso! Con esto seguro que lograremos penetrar en las perversas mentes de nuestro enemigo.", rewards = "Te van a lanzar el siguiente hechizo:\n\nEnjambre de Nozdormu +1000"},
[8785] = {title = "¡La Legión de Orgrimmar necesita mojo!", objective = "Lleva 6 unidades de mojo potente, 6 frascos de mojo grande y 8 aceites de inmolación a la sacerdotisa de las Sombras Shai cerca de Colmen'Regal, en Silithus. También debes llevar el Informe de tareas logísticas IV para poder completar la misión.", description = "La Legión de Orgrimmar ha solicitado varios ingredientes muy poco usuales. Dada su situación actual en Silithus, queremos entregarles los suministros tan pronto como podamos. Reúne los materiales y llévaselos a la trol llamada Shai.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia logística CenarionSeguimiento del encargo de logística"},
[8786] = {title = "Armas para el campo de batalla", objective = "Lleva 3 hachas macizas de hierro a Merok Zancolargo en el campamento de la Legión de Orgrimmar, en las afueras de Colmen'Regal. También debes llevar el Informe de tareas logísticas VI para poder completar la misión.", description = "La Legión de Orgrimmar ha solicitado unas armas adicionales que no hemos sido capaces de suministrar. Consigue las armas que nos han pedido y llévaselas a Merok Zancolargo, fuera de Colmen'Regal.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia logística CenarionSeguimiento del encargo de logística"},
[8787] = {title = "Complementos para armadura para el campo de batalla", objective = "Lleva 8 complementos bastos para armadura y 8 complementos pesados para armadura a Merok Zancolargo, cerca de Colmen'Regal. También debes llevar el Informe de tareas logísticas VII para poder completar la misión.", description = "Los valientes voluntarios de la Legión de Orgrimmar han venido a Silithus para ayudarnos en nuestra causa. De alguna forma, perdieron sus suministros de armaduras durante el viaje. Consigue un lote de armaduras y llévaselas a Merok Zancolargo al campamento de la Legión de Orgrimmar.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia logística CenarionSeguimiento del encargo de logística"},
[8788] = {title = "Un regalo un poco agitado", objective = "Parece que han agitado este regalo un par de veces. Su etiqueta dice:\n\nPara un <raza> <clase> muy especial.", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nObsequio ligeramente agitado"},
[8789] = {title = "Armamentos imperiales Qiraji", objective = "Arygos en el Templo de Ahn'Qiraj creará armamento imbuido de Elementium para ti si le llevas armamento imperial qiraji y 3 menas de Elementium.", description = "No hemos estado ociosos durante un milenio, <nombre>.\n\nHemos estado observando cómo estas bestias lo celebraban a solas cuando habían cumplido su propósito. Hemos estado escuchando cómo tramaban y confabulaban para destruir nuestro mundo. Y nos hemos enterado de sus propias traiciones internas.\n\nExisten puntos débiles y sabemos cómo usarlos a nuestro favor.\n\nEncuentra sus armamentos ceremoniales y tráemelos. Combinados con el elemento creado a partir de la volatilidad de su propio Dios, se convertirán en una fuerza de juicio.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nHacha de guerra Qiraji benditaMosquete Qiraji benditoPugio Qiraji benditoBaluarte Qiraji bendito\n\nTambién recibirás:\n\nHacha de guerra Qiraji benditaPugio Qiraji benditoBaluarte Qiraji benditoMosquete Qiraji bendito"},
[8790] = {title = "Regalos imperiales Qiraji", objective = "Merithra del Sueño en el Templo de Ahn'Qiraj creará armamento imbuido de Elementium para ti si le llevas regalos imperiales qiraji y 3 menas de Elementium.", description = "Según una teoría de Arygos el elemento nacido de conflictos entre los antiguos Dioses y sus esbirros también es extremadamente fatal para ellos si se dan las condiciones adecuadas. Me refiero al Eleméntium, mortal.\n\nEncuentra regalos imperiales qiraji y tráemelos junto con el elemento tóxico para infundir con ellos armas de gran poder.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nBastón de acólito Qiraji benditoBastón de augur Qiraji benditoMartillo de guerra Qiraji bendito\n\nTambién recibirás:\n\nMartillo de guerra Qiraji benditoBastón de acólito Qiraji benditoBastón de augur Qiraji bendito"},
[8791] = {title = "La caída de Osirio", objective = "Entrégale la cabeza de Osirio el Sinmarcas al comandante Mar'alith de Fuerte Cenarion en Silithus.", description = "Se acabó esa terrible amenaza. El cruel Osirio ha sido derrotado.\n\nLos vecinos de Kalimdor sentirán gran alivio cuando conozcan la noticia. Coge la cabeza de Osirio y llévasela al comandante Mar'alith de Fuerte Cenarion.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nTalismán del Mar de DunasAmuleto del Mar de DunasGargantilla del Mar de DunasColgante de las Arenas Movedizas\n\nTambién recibirás:\n\nTalismán del Mar de DunasGargantilla del Mar de DunasColgante de las Arenas MovedizasAmuleto del Mar de Dunas"},
[8792] = {title = "¡La Horda te necesita!", objective = "Habla con el Señor de la Guerra Gorchuk en el Valle de los Espíritus de Orgrimmar.", description = "Hola, ¡qué bien que hayas decidido escucharme! La Horda necesita toda la ayuda posible para preparar la guerra de Ahn'Qiraj ¡y eso significa que te necesitamos a ti también! En este mismo instante, los recolectores oficiales están reuniendo el material necesario para la guerra que se avecina, pero no lograremos nuestro objetivo sin tu ayuda, <nombre>!\n\nDeberías hablar con quien está al mando, el señor de la guerra Gorchuk. ¿Qué me dices, <clase>? ¿Nos ayudarás?Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[8793] = {title = "¡La Horda te necesita!", objective = "Habla con el Señor de la Guerra Gorchuk en el Valle de los Espíritus de Orgrimmar.", description = "Hola, ¡qué bien que hayas decidido escucharme! La Horda necesita toda la ayuda posible para preparar la guerra de Ahn'Qiraj ¡y eso significa que te necesitamos a ti también! En este mismo instante, los recolectores oficiales están reuniendo el material necesario para la guerra que se avecina, pero no lograremos nuestro objetivo sin tu ayuda, <nombre>!\n\nDeberías hablar con quien está al mando, el señor de la guerra Gorchuk. ¿Qué me dices, <clase>? ¿Nos ayudarás con estos preparativos vitales?Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[8794] = {title = "¡La Horda te necesita!", objective = "Habla con el Señor de la Guerra Gorchuk en el Valle de los Espíritus de Orgrimmar.", description = "Hola, ¡qué bien que hayas decidido escucharme! La Horda necesita toda la ayuda posible para preparar la guerra de Ahn'Qiraj ¡y eso significa que te necesitamos a ti también! En este mismo instante, los recolectores oficiales están reuniendo el material necesario para la guerra que se avecina, pero no lograremos nuestro objetivo sin tu ayuda, <nombre>!\n\nDeberías hablar con quien está al mando, el señor de la guerra Gorchuk. ¿Qué me dices, <clase>? ¿Nos ayudarás con estos preparativos vitales?", progress = nil, completion = "¡Throm'ka, &lt;clase&gt;! Me alegra ver a tantos integrantes de la Horda, como tú, &lt;nombre&gt;, ofreciéndose para dar su apoyo en los preparativos para la guerra de Ahn'Qiraj que se avecina. Nuestro esfuerzo común es garantía de una victoria sobre los silítidos y sus malignos amos ocultos en Ahn'Qiraj.\n\nAhora que estás aquí, procura hablar con los distintos recolectores y ofrecerles tu ayuda para reunir esos materiales en la medida de tus posibilidades.", rewards = "Recibirás:\n\n"},
[8795] = {title = "¡La Alianza te necesita!", objective = "Habla con el alguacil de campo Nevanur en La Sala Militar de Forjaz.", description = "Hola, ¡qué bien que hayas decidido escucharme! La Alianza necesita toda la ayuda posible para preparar la guerra de Ahn'Qiraj ¡y eso significa que te necesitamos a ti también! En este mismo instante, los recolectores oficiales están reuniendo el material necesario para la guerra que se avecina, pero no lograremos nuestro objetivo sin tu ayuda, <nombre>!\n\nDeberías hablar con quien está al mando, el alguacil de campo Avalancha ¿Qué me dices, <clase>? ¿Nos ayudarás con estos preparativos vitales?", progress = nil, completion = "¡Albricias, chico! Me alegra ver a tantos integrantes de la Alianza, como tú, &lt;nombre&gt;, ofreciéndose para dar su apoyo en los preparativos para la guerra de Ahn'Qiraj que se avecina. Nuestro esfuerzo común es garantía de una victoria sobre los silítidos y sus malignos amos ocultos en Ahn'Qiraj.\n\nAhora que estás aquí, procura hablar con los distintos recolectores y ofrecerles tu ayuda para reunir esos materiales en la medida de tus posibilidades.", rewards = "Recibirás:\n\n"},
[8796] = {title = "¡La Alianza te necesita!", objective = "Habla con el alguacil de campo Nevanur en La Sala Militar de Forjaz.", description = "Hola, ¡qué bien que hayas decidido escucharme! La Alianza necesita toda la ayuda posible para preparar la guerra de Ahn'Qiraj ¡y eso significa que te necesitamos a ti también! En este mismo instante, los recolectores oficiales están reuniendo el material necesario para la guerra que se avecina, pero no lograremos nuestro objetivo sin tu ayuda, <nombre>!\n\nDeberías hablar con quien está al mando, el alguacil de campo Avalancha. ¿Qué me dices, <clase>? ¿Nos ayudarás con estos preparativos vitales?", progress = nil, completion = "¡Albricias, chico! Me alegra ver a tantos integrantes de la Alianza, como tú, &lt;nombre&gt;, ofreciéndose para dar su apoyo en los preparativos para la guerra de Ahn'Qiraj que se avecina. Nuestro esfuerzo común es garantía de una victoria sobre los silítidos y sus malignos amos ocultos en Ahn'Qiraj.\n\nAhora que estás aquí, procura hablar con los distintos recolectores y ofrecerles tu ayuda para reunir esos materiales en la medida de tus posibilidades.", rewards = "Recibirás:\n\n"},
[8797] = {title = "¡La Alianza te necesita!", objective = "Habla con el alguacil de campo Nevanur en La Sala Militar de Forjaz.", description = "Hola, ¡qué bien que hayas decidido escucharme! La Alianza necesita toda la ayuda posible para preparar la guerra de Ahn'Qiraj ¡y eso significa que te necesitamos a ti también! En este mismo instante, los recolectores oficiales están reuniendo el material necesario para la guerra que se avecina, pero no lograremos nuestro objetivo sin tu ayuda, <nombre>!\n\nDeberías hablar con quien está al mando, el alguacil de campo Avalancha. ¿Qué me dices, <clase>? ¿Nos ayudarás con estos preparativos vitales?", progress = nil, completion = "¡Albricias, chico! Me alegra ver a tantos integrantes de la Alianza, como tú, &lt;nombre&gt;, ofreciéndose para dar su apoyo en los preparativos para la guerra de Ahn'Qiraj que se avecina. Nuestro esfuerzo común es garantía de una victoria sobre los silítidos y sus malignos amos ocultos en Ahn'Qiraj.\n\nAhora que estás aquí, procura hablar con los distintos recolectores y ofrecerles tu ayuda para reunir esos materiales en la medida de tus posibilidades.", rewards = "Recibirás:\n\n"},
[8798] = {title = "Un yeti propio", objective = "¡Vaya! ¡¿Sabes qué?! ¡Tú también eres ingeniero! Como me ayudaste a darles una lección a mis amigos, ¡voy a enseñarte a hacer tu propio yeti mecánico! ¡Ahora la diversión no va a parar nunca y podrás asustar a quien quieras! Siempre que puedas conseguir los componentes, claro.\n\n¿Qué dices, <nombre>? ¿Quieres aprender los secretos para hacer un yeti mecánico?", description = nil, progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nYeti mecánico sosegado"},
[8799] = {title = "El héroe del día", objective = "Lleva 1 porción de sal de roca y 5 monedas de oro al conservante de acebo; recibirás 5 acebos conservados.", description = "Oye, se supone que no tendría que decirte esto, pero como todo el mundo te admira y tal...\n\nEn esta época del año hay mucho acebo, pero no dura nada. Tenemos una máquina que lo conserva siempre fresco. Lo usamos para dar color durante las fiestas... ¡y para hacer negocio en verano!\n\nTe dejo usar el conservador si demuestras tu habilidad en la cocina. Necesitas sal de roca y unos cinco oros. Te prepararé una hornada toda para ti.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nAcebo en conserva"},
[8800] = {title = "Equipamiento de Cenarion", objective = "Habla con Vergus en el Fuerte Cenarion, en Silithus.", description = "Cuando vayas completando las misiones que te asignen en la guerra, vete a hablar con Vargus, el herrero. Muéstrale las insignias que hayas obtenido. Te proporcionará equipamiento afín a tu nivel de entrega a nuestra causa.", progress = nil, completion = "¿Kaldon te ha enviado? Muy bien, veamos lo que podemos hacer por ti.", rewards = nil},
[8801] = {title = "El legado de C'Thun", objective = "Llévale el ojo de C'Thun a Caelastrasz en el Templo de Ahn'Qiraj.", description = "La muralla de Ahn'Qiraj se estremece. Una fuerza del mal, más antigua que el mismo mundo, ha side destruida.\n\nAl mirar los restos de esa colosal abominación, casi se te congela el corazón. Incluso después de muerto, puedes sentir la herencia de la maldad de C'Thun a tu alrededor.\n\nHas logrado lo que parecía imposible.\n\nTe agarraste al rabillo del ojo del demonio y lo pusiste a buen recaudo en tu bolsa.\n\nLleva el ojo de C'Thun a los dragones de la cámara siguiente.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[8802] = {title = "El salvador de Kalimdor", objective = "Llévale el ojo de C'Thun a Anachronos en las Cavernas del Tiempo.", description = "Encamínate a las Cavernas del Tiempo, <señor/señora> <nombre>. Anacronos espera que regreses. Dale el ojo de C'Thun porque querrá, sin duda alguna, añadirlo a su colección magistral.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nAmuleto del Dios caídoCapa del Dios caídoAnillo del Dios caído\n\nTe van a lanzar el siguiente hechizo:\n\nEnjambre de Nozdormu +1000\n\nTambién recibirás:\n\nAnillo del Dios caídoCapa del Dios caídoAmuleto del Dios caído"},
[8803] = {title = "Un regalo de festividad", objective = "La nota de este regalo dice:\n\nPara <nombre>,\n\ncon la esperanza de que te ayude a repartir calor y felicidad por todo Azeroth.\n\nDe parte de Papá Invierno.", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nObsequio festivo"},
[8804] = {title = "Un kit de supervivencia en el desierto", objective = "Lleva 4 globos de agua, 4 antídotos potentes y 4 bolas de masa del desierto ahumadas a Calandrath en la taberna del Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas logísticas I para poder completar la misión.", description = "Con la llegada de reclutas frescos al Fuerte Cenarion llega también el problema de su supervivencia en el desierto. Las innumerables criaturas venenosas que viven en él y la deshidratación son serias amenazas para los voluntarios poco experimentados. Reúne los suministros necesarios para un equipo de supervivencia en el desierto y llévaselos a Calandrath al Fuerte Cenarion.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia logística CenarionEncargo de logística"},
[8805] = {title = "Botas para la guardia", objective = "Lleva 3 botas de mitril ornamentado a Vish Kozus, Capitán de la guardia del Fuerte Cenarion en Silithus. También debes llevar el Informe de tareas logísticas II para poder completar la misión.", description = "La división montada de la guardia del Fuerte Cenarion tiene escasez de botas de mitril ornamentadas. Consigue un cargamento y entrégaselo inmediatamente al capitán Vish Kozus en el Fuerte Cenarion.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia logística CenarionEncargo de logística"},
[8806] = {title = "Piedra de pulir para la guardia", objective = "Lleva 10 muelas densas, 10 muelas sólidas y 10 muelas pesadas a Vish Kozus, Capitán de la guardia del Fuerte Cenarion en Silithus. También debes llevar el Informe de tareas logísticas III para poder completar la misión.", description = "La guardia del Fuerte Cenarion tiene escasez de muelas. Consigue un cargamento y entrégaselo inmediatamente al capitán Vish Kozus en el Fuerte Cenarion.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia logística CenarionEncargo de logística"},
[8807] = {title = "Materiales de visión", objective = "Lleva 1 fragmento grande brillante, 1 fragmento radiante grande y 1 esmeralda enorme a la Geóloga Trotuina en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas logísticas V para poder completar la misión.", description = "Los estudios de la Geóloga Trotuina han demostrado ser de gran valor para nuestras operaciones en Silithus. Recientemente, ha solicitado materiales de visión para utilizar en su investigación.\n\nPor favor, consigue esos materiales y llévaselos directamente a ella para que pueda continuar con su investigación.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia logística CenarionSeguimiento del encargo de logística"},
[8808] = {title = "Suministros para uniformes", objective = "Lleva 1 unidad de tela lunar, 2 madejas de paño rúnico y 1 seda de araña Tejehierro a Astaorgullo Clamavientos en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas logísticas VIII para poder completar la misión.", description = "La última entrada de voluntarios ha provocado escasez de uniformes. Consigue un lote de materiales de uniforme y llévaselo a Astaorgullo Clamavientos.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia logística CenarionEncargo de logística"},
[8809] = {title = "Materiales encantados", objective = "Lleva 2 lingotes de torio encantado y 2 cueros encantados a Vargus en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas logísticas IX para poder completar la misión.", description = "Vargus, el herrero del Fuerte Cenarion, ha solicitado materiales encantados para crear poderosas armas para nuestras tropas. Consíguelos y llévaselos directamente a Vargus para que las tropas del Fuerte Cenarion puedan seguir estando bien equipadas.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia logística CenarionEncargo de logística"},
[8810] = {title = "Vendas para el campo de batalla", objective = "Lleva 30 vendas de paño rúnico pesado, 30 vendas de seda pesada y 30 vendas de paño mágico pesado a Astaorgullo Clamavientos en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas logísticas X para poder completar la misión.", description = "Debido a las víctimas que hemos tenido recientemente, nuestro suministro de vendajes en el campo de batalla empieza a escasear peligrosamente. Consigue un lote de suministros de primeros auxilios y llévaselo a Astaorgullo Clamavientos en el Fuerte Cenarion.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia logística CenarionSeguimiento del encargo de logística"},
[8811] = {title = "Un sello de honor", objective = "Si los aventureros solo tienen un sello de honor, se les entrega a cambio una pequeña cantidad de reputación con Darnassus.\n\nPor favor, no olvides que es mejor entregarme diez sellos al mismo tiempo, tus esfuerzos se verán mejor recompensados. Permitimos el intercambio de un solo sello como servicio para aquellos que no pueden completar un juego de diez.\n\nDicho esto, ya puedo ayudarte si sigues queriendo entregar un solo sello.", description = nil, progress = "Si los aventureros solo tienen un sello de honor, se les entrega a cambio una pequeña cantidad de reputación con Darnassus.\n\nPor favor, no olvides que es mejor entregarme diez sellos al mismo tiempo, tus esfuerzos se verán mejor recompensados. Permitimos el intercambio de un solo sello como servicio para aquellos que no pueden completar un juego de diez.\n\nDicho esto, ya puedo ayudarte si sigues queriendo entregar un solo sello.", completion = "Muy bien, tus hazañas han sido registradas y tus esfuerzos han sido debidamente reconocidos. Sigue haciéndolo así, &lt;clase&gt;.\n\nSi tienes más sellos que entregar puedo seguir ayudándote.", rewards = nil},
