[8856] = {title = "Un kit de supervivencia en el desierto", objective = "Lleva 4 globos de agua, 4 antídotos potentes y 4 salmones al horno a Calandrath, que está en la posada del Fuerte Cenarion, en Silithus.", description = "Con la llegada de reclutas frescos al Fuerte Cenarion llega también el problema de mantenerlos vivos en el desierto. Las innumerables criaturas venenosas que viven en él y la deshidratación son serias amenazas para los voluntarios poco experimentados. Reúne los suministros necesarios para un kit de supervivencia en el desierto y llévaselos a Calandrath al Fuerte Cenarion.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia logística CenarionSeguimiento del encargo de logística"},
[8857] = {title = "Los secretos de los Colosos: Ashi", objective = "Lleva la coraza del coloso de Ashi a Oglethorpe Obnoticus a Bahía del Botín.|n", description = "Busco gente osada con experiencia para llevar una muestra de la coraza del coloso de Ashi muerto a Oglethorpe a Bahía del Botín. No, no tienes que acercarte al cadáver, tengo un fragmento aquí. Pero date prisa, ¡la coraza no le servirá si está podrida!", progress = "¿Sí? ¿Qué llevas ahí?", completion = "¡Qué magnífica coraza! ¡Buen trabajo!", rewards = "Recibirás:\n\nBolsa de botín colosal"},
[8858] = {title = "Los secretos de los Colosos: Regal", objective = "Lleva la coraza del coloso de Regal al vigía Puñaceite a La Garganta de Fuego.", description = "Nada me gustaría más que estampar esta coraza de bicho en la cara de Puñaceite... Pero aquí estoy, recogiendo muestras para esa cucaracha hipertrofiada. Con lo grande que es el bicho, estaré aquí hasta que cante el gallo. \n\nLlévale la muestra a Puñaceite, <clase>, y date prisa: ¡paga muy bien! \n\nVe a Puesto del Thorium, en La Garganta de Fuego.", progress = "Mmm...", completion = "Magnífico; con esto se podría crear una armadura excelente. Ten, la recompensa prometida.", rewards = "Recibirás:\n\nBolsa de botín colosal"},
[8859] = {title = "Los secretos de los Colosos: Zora", objective = "Lleva la coraza del coloso de Zora a Lord Maxwell Neófitus a la Capilla de la Esperanza de la Luz, en las Tierras de la Peste del Este.|n", description = "Mis sirvientes por poco pierden la vida intentando recuperar esta muestra. ¡Debes darte prisa! Lord Maxwell Neófitus la necesita. ¡Opina que los secretos de este coloso nos ayudarán en la guerra contra la Plaga!\n\n¡¿Qué haces todavía por aquí?! ¡Ve a Esperanza de la Luz! Y recibirás una sabrosa recompensa por tus servicios...|n", progress = "Eso que llevas parece pesado.", completion = "¡Justo a tiempo! ¡Buen trabajo!", rewards = "Recibirás:\n\nBolsa de botín colosal"},
[8860] = {title = "¡Las fiestas de Año Nuevo!", objective = "Lleva la comida de Pastos de Bosquehumeante a la posadera Allison de Ventormenta.", description = "¡Ventormenta celebra un festival por año nuevo! ¡Habrá bebidas gratis todo el día y fuegos artificiales por la noche! Seguro que está muy bien, pero también hay fiesta en la Bahía del Botín y seguro que es mejor.\n\nEn todo caso, Allison, la posadera de Ventormenta, necesita comida de Pastos de Bosquehumeante para los juerguistas. ¿Puedes ir tú a llevarsela?\n\n¡Gracias, <nombre>! La posadera Allison está en la Rosa Áurea, en el Distrito de Mercaderes de Ventormenta.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[8861] = {title = "¡Las fiestas de Año Nuevo!", objective = "Lleva la comida de Pastos de Bosquehumeante a la posadera Pala de Cima del Trueno.", description = "¡Cima del Trueno va a hacer fiesta por año nuevo! Seguro que está muy bien, pero también hay fiesta en la Bahía del Botín y seguro que es mejor.\n\nEn todo caso, Pala, la posadera de Cima del Trueno, necesita comida de Pastos de Bosquehumeante para los juerguistas. ¿Puedes ir tú a llevarsela?\n\n¡Gracias, <nombre>! La posadera Pala está en la posada cerca del puente a Alto de los Cazadores.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[8862] = {title = "Vela de Elune", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nVela de ElunePaquete de fuegos artificiales del Festival Lunar"},
