[8864] = {title = "Vestidos para el Festival Lunar", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nVestido de fiesta verdeVestido de fiesta rosaVestido de fiesta morado\n\nTambién recibirás:\n\nPaquete de fuegos artificiales del Festival LunarVestido de fiesta verdeVestido de fiesta rosaVestido de fiesta morado"},
[8865] = {title = "Trajes para el Festival Lunar", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nTraje de pantalón de fiesta negroTraje de pantalón de fiesta azulTraje de pantalón de fiesta con cerceta\n\nTambién recibirás:\n\nPaquete de fuegos artificiales del Festival LunarTraje de pantalón de fiesta negroTraje de pantalón de fiesta con cercetaTraje de pantalón de fiesta azul"},
[8866] = {title = "Barbabronce el Ancestro", objective = "Me alegra saber que la gente de estas tierras sigue prestando homenaje a las antiguas razas. Te deseo todo lo mejor, <nombre>, y te ofrezco esta muestra...", description = nil, progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nAutoforzarse: reverencia\n\nRecibirás:\n\nMoneda de linaje"},
[8867] = {title = "Fuegos artificiales lunares", objective = "Lanza 8 fuegos artificiales lunares y 2 cohetes y acude a una vaticinadora del Festival Lunar de cualquier capital.", description = "Todos los años los druidas del Claro de la Luna celebran la gran victoria de nuestra ciudad sobre un antiguo mal.\n\nCelebramos la sabiduría de nuestros ancestros, participamos en magníficas fiestas y, por supuesto... ¡lanzamos fuegos artificiales!\n\nA cambio de una pequeña donación puedes adquirir cohetes de colores de nuestro vendedor. Prueba a usarlos en uno de los lanzacohetes que hay por aquí, <nombre>.", progress = "¿Encontraste los lanzacohetes, <nombre>?", completion = "Bien hecho, &lt;nombre&gt;.\n\nAhora que ya estás metido en el ambiente, ¿por qué no aceptas esta invitación al festival del Claro de la Luna?\n\nEs una celebración fantástica si tienes algo de tiempo. Hay comida, bebida, se cuentan cuentos y... claro, ¡hay más fuegos artificiales!\n\nAbre la invitación cuando estés dentro del círculo de La Gran Luz de Luna y serás transportado directamente al Claro de la Luna.", rewards = "Recibirás:\n\nInvitación al Festival Lunar"},
[8868] = {title = "La bendición de Elune", objective = "Invoca a Augurio y derrótalo para ganar la bendición de Elune. Ve a ver a Valadar Cantoestelar a Amparo de la Noche.", description = "La bestia Augurio fue un héroe de este mundo en otros tiempos. Era un lobo fuerte que luchó junto los héroes de la guerra de los Ancestros hasta que cayó víctima de una magia demoníaca. Se volvió contra sus aliados y arrasó Azeroth dejando una estela de terror y muerte a su paso.\n\nFue derrotado aquí, en Claro de la Luna, y descansa en el cieno del Lago Elune'ara. Pero cada año nuevo, durante el festival, se agita en su sueño eterno...\n\nRecluta un ejército, <nombre>, y junto a él invoca y derrota a la bestia. ¡Merecerás la bendición de Elune!Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nFarol de ElunePaquete de fuegos artificiales del Festival LunarPiedra sólida"},
[8869] = {title = "La dulce Serenidad", objective = "Viaja a Stratholme y mata al Forjamartillos carmesí. Recupera el mandil del Forjamartillos carmesí y vuelve a Lilith.", description = "En los confines de El Bastión Escarlata se encuentra la dulce Serenidad. El Forjamartillos Escarlata protegerá su secreto con su vida. Derrótalo y tráeme su mandil. Si lo haces, te ayudaré a convertirte en <un forjador/una forjadora> de martillos.\n\nPero ten clara una cosa: una vez elegido el camino del forjador de martillos, los caminos de forjador de espadas y de forjador de hachas te quedarán vedados.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nMaestro forjador de martillos\n\nRecibirás:\n\nDiseño: martillo de batalla encantado"},
[8870] = {title = "El Festival Lunar", objective = "Habla con la embajadora del Festival Lunar en la Sala Mística de Forjaz. También puedes hablar con la embajadora del Festival Lunar en otras capitales.", description = "¡Ha llegado el momento, <nombre>! El Círculo Cenarion invita a todos los habitantes de Azeroth a participar en nuestra celebración del Festival Lunar. Encontrarás a la embajadora del Festival Lunar en esta ciudad, en un lugar señalado por un círculo bendito de luz lunar. Te dará más datos sobre esta festividad.", progress = nil, completion = "Te doy la bienvenida, &lt;nombre&gt;. ¿Vienes a unirte a nuestras celebraciones?", rewards = nil},
[8871] = {title = "El Festival Lunar", objective = "Habla con el presagista del Festival Lunar en el Distrito del Parque, en Ventormenta. También puedes hablar con otros presagistas del Festival Lunar de otras capitales.", description = "¡Ha llegado ese momento del año, <nombre>! El Círculo Cenarion invita a todos los ciudadanos de Azeroth a participar en la celebración del Festival Lunar. Encontrarás a la embajadora del Festival Lunar en esta misma ciudad. Busca el lugar marcado por un círculo sagrado de luz lunar. Ella podrá decirte más cosas sobre el Festival.", progress = nil, completion = "Bienvenido, &lt;nombre&gt;. ¿Estás aquí para unirte a la celebración?", rewards = nil},
[8872] = {title = "El Festival Lunar", objective = "Habla con la presagista del Festival Lunar en el Enclave Cenarion de Darnassus. También puedes hablar con los presagistas del Festival Lunar en otras capitales.", description = "¡Ha llegado ese momento del año, <nombre>! El Círculo Cenarion invita a todos los ciudadanos de Azeroth a participar en la celebración del Festival Lunar. Encontrarás a la presagista del Festival Lunar en esta misma ciudad. Busca el lugar marcado por un círculo sagrado de luz lunar. Ella podrá decirte más cosas sobre el Festival.Cuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = nil},
[8873] = {title = "El Festival Lunar", objective = "Habla con la embajadora del Festival Lunar en el Valle de la Sabiduría, en Orgrimmar. También puedes hablar con la embajadora del Festival Lunar en otras capitales.", description = "¡Ha llegado ese momento del año, <nombre>! El Círculo Cenarion invita a todos los ciudadanos de Azeroth a participar en la celebración del Festival Lunar. Encontrarás a la embajadora del Festival Lunar en esta misma ciudad. Busca el lugar marcado por un círculo sagrado de luz lunar. Ella podrá decirte más cosas sobre el Festival.Cuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = nil},
[8874] = {title = "El Festival Lunar", objective = "Habla con la presagista del Festival Lunar en la entrada de Entrañas. También puedes hablar con otros presagistas del Festival Lunar de otras capitales.", description = "¡Ha llegado ese momento del año, <nombre>! El Círculo Cenarion invita a todos los ciudadanos de Azeroth a participar en la celebración del Festival Lunar. Encontrarás a la presagista del Festival Lunar en esta misma ciudad. Busca el lugar marcado por un círculo sagrado de luz lunar. Ella podrá decirte más cosas sobre el Festival.Cuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = nil},
[8875] = {title = "El Festival Lunar", objective = "Habla con la presagista del Festival Lunar en el Alto de los Ancestros de Cima del Trueno. También puedes hablar con otros presagistas del Festival Lunar de otras capitales.", description = "¡Ha llegado ese momento del año, <nombre>! El Círculo Cenarion invita a todos los ciudadanos de Azeroth a participar en la celebración del Festival Lunar. Encontrarás a la presagista del Festival Lunar en esta misma ciudad. Busca el lugar marcado por un círculo sagrado de luz lunar. Ella podrá decirte más cosas sobre el Festival.", progress = nil, completion = "Bienvenido, &lt;nombre&gt;. ¿Estás aquí para unirte a la celebración?", rewards = nil},
