[9063] = {title = "Torwa Abrecaminos", objective = "Habla con Torwa Abrecaminos en el Cráter de Un'Goro.", description = "Un cazador llamado Torwa Abrecaminos ha solicitado la ayuda de un druida. Dijo que solo nosotros tenemos las habilidades necesarias y el respeto por la naturaleza para ayudarlo.\n\nExiste un camino que conecta el Cráter de Un'Goro con el desierto de Tanaris. Encontrarás a Torwa justo al terminar el camino, al llegar al Cráter.", progress = nil, completion = "Has hecho un largo camino para venir a verme, &lt;clase&gt;, y has llegado justo a tiempo.", rewards = "Recibirás:\n\n"},
[9065] = {title = "The \"Chow\" Quest (123)aa", objective = "Kill Kobold Vermin, 2 of em.$Tpunk;!  Kill Kobold Vermin, 2 of em.Aprenderás:\n\n", description = "$Tpunk;!  Kill Kobold Vermin, 2 of em.Aprenderás:\n\n", progress = nil, completion = nil, rewards = "Aprenderás:\n\nCurar\n\nTe van a lanzar el siguiente hechizo:\n\nPollo otro\n\nTambién recibirás:\n\nAgua de manantial refrescantePoción de curación menor"},
[9068] = {title = "La túnica Fatalidad", objective = "Rimblat Fatalidad de la Capilla de la Esperanza de la Luz, en las Tierras de la Peste del Este, te hará una túnica Fatalidad si le llevas lo siguiente: 1 túnica profanada, 25 restos de malla, 4 barras de arcanita y 3 pieles bastas curtidas.", description = "¿Puedes hacerme el honor de devolver el armadura a los que cayeron defendiendo estas tierras?\n\nHa sido profanada y sin duda destinada a fines maléficos; pero puede renovarse con restos de armadura y utilizada de nuevo.\n\nTráeme los restos de los caídos e ingredientes de purificación y Fatalidad será toda tuya.\n\nBusca en Naxxramas.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nTúnica Rompeterra"},
