[9247] = {title = "La llamada de los guardianes", objective = "Llévale la carta del guardián de la palabra a la Capilla de la Esperanza de la Luz, en las Tierras de la Peste del Este.", description = "<nombre>,\n\nEn el pasado has servido a los intereses de Alba Argenta de modo muy satisfactorio. Una vez más lanzamos una llamada de auxilio.\n\n<nombre>, si deseas ayudarnos una vez más, preséntate al guardián de la palabra de la Capilla de la Esperanza de la Luz, en las Tierras de la Peste del Este.", progress = "Me alegro de verte, <nombre>.", completion = "Te debemos gratitud por el simple hecho de haber respondido, &lt;nombre&gt;. Esperamos que tu ejemplo anime a otros a unirse a nuestra lucha contra la Peste.", rewards = "Podrás elegir una de estas recompensas:\n\nAceite bendito de zahoríPiedra de afilar consagrada\n\nTambién recibirás:\n\nPiedra de afilar consagradaAceite bendito de zahorí"},
[9248] = {title = "Una humilde ofrenda", objective = "Aurel Hojaurea quiere que le lleves 1 Cetro Abisal al Fuerte Cenarion, en Silithus. También tienes que ganar honores en el Círculo Cenarion.", description = "Ishnu-alah, <raza>. Dame una prueba de la muerte de un Señor del Consejo Abisal y te recompensaré con un objeto que te ayudará a luchar contra los Qiraji.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nSortija de Cenario"},
[9249] = {title = "40 vales: esquema (mando de tonque de vapor)", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nEsquema: mando de tanque a vapor"},
[9250] = {title = "Armazón de Atiesh", objective = "Busca a alguien que te ayude a buscar las piezas que faltan y a exorcizar el bastón.", description = "Atiesh ya está casi completo. Solo falta la cabeza y la base del bastón. Y está el asunto de que el bastón es un conducto de la maldad de Sargeron.\n\nBusca a alguien que te ayude a buscar las piezas que faltan y a exorcizar el bastón.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
