[9319] = {title = "Una luz en la oscuridad", objective = "Toca las hogueras de Cumbre de Roca Negra, La Masacre, Scholomance y Stratholme y ve a ver al guardián de la llama.", description = "No somos los únicos en celebrar este día, <nombre>. La llama influye en todas las criaturas, todas las culturas. Se están encendiendo hogueras incluso en los lugares más oscuros.\n\nSi deseas verlas, desciende a las profundidades. Busca las hogueras que arden en Stratholme, en Scholomance, entre los ogros de La Masacre y en los salones de Cumbre de Roca Negra. Esperaré tu regreso.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nLlama presaFlores ardientes"},
[9320] = {title = "Poción sublime de maná", objective = "Además del resto de suministros, disponemos de pociones de maná superiores. Te daré una si me traes 8 runas necróticas.", description = nil, progress = "Además del resto de suministros, disponemos de pociones de maná superiores. Te daré una si me traes 8 runas necróticas.", completion = "Aquí tienes, &lt;nombre&gt;. Ten cuidado; no somos tantos que podamos permitirnos más bajas.", rewards = "Recibirás:\n\nPoción de maná sublime"},
[9321] = {title = "Poción sublime de curación", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nPoción de curación sublime"},
[9322] = {title = "Los fuegos salvajes de Kalimdor", objective = "Visita los campamentos del Solsticio de Azshara, Silithus, Cráter de Un'Goro y Cuna del Invierno y ve a ver al guardián de la llama.", description = "El Festival del Solsticio se celebra en todo Azeroth y las ricas tierras de Kalimdor no son una excepción. Parece que te gusta viajar; voy a darte un motivo para hacerlo.\n\nNo apreciarás realmente el solsticio de verano hasta que hayas visto las hogueras azules que arden por todas partes. Se han encendido hogueras en la helada Cuna del Invierno, al nordeste de Azshara, cerca de Sosiego del Valor en los eriales de Silithus y junto a la entrada del exuberante Un'Goro. Regresa cuando hayas completado tu viaje.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nBrazales de cenizaFlores ardientes"},
[9323] = {title = "Los fuegos salvajes de los Reinos del Este", objective = "Visita los campamentos del Festival del Solsticio de las Tierras Devastadas, las Tierras de la Peste del Este, las Tierras del Interior y la Garganta de Fuego y ve a ver al guardián de la llama.", description = "Esta celebración de la llama se extiende por toda la tierra, <clase>. No hay un solo lugar que no sienta el calor de esta fiesta y las hogueras más brillantes resplandecen en azul. Búscalas.\n\nEncontrarás una justo al norte de Cumbre de Roca Negra. Las demás están en las Tierras de la Peste del Este cerca de una ciudad decrépita, en el camino a los precipicios en las Tierras del Interior y en el camino a El Portal Oscuro en las Tierras Devastadas.\n\nVuelve cuando las hayas visto todas.", progress = nil, completion = "¡Te veo resplandeciente pese al polvo del camino y la fatiga! La llama nos une a todos; todo tu ser reluce con el poder de las hogueras que has tocado.\n\nPero imagino que también ansías una recompensa de tipo más material; aquí la tienes.", rewards = "Recibirás:\n\nManto del Festival del FuegoFlores ardientes"},
[9324] = {title = "Robar la llama de Orgrimmar", objective = "Devuelve la Llama de Orgrimmar al maestro del Conocimiento del festival.", description = "Esta llama, robada del fuego del hogar de Orgrimmar, te inunda de calor. No conoces este poder tan primitivo, pero puede que el maestro del Conocimiento tenga más información...Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nFlores ardientes"},
