[80153] = {title = "¡El Destellamatic 5200!", objective = "Inserta un objeto con mugre incrustada en el Destellamatic 5200 y asegúrate de tener 3 monedas de plata a mano.", description = "Este aparato destaca entre los gnomos que se mantienen firmes frente a la locura que acecha fuera de la sala. Tiene una plaquita que dice: \"Destellamatic 5200\" y:\n\n\"¿La suciedad arruina sus destellos? ¡El Destellamatic 5200 devuelve el brillo de forma brillante! Inserte el objeto que desea limpiar e introduzca tres monedas de plata en la ranura. Espere unos momentos, ¡y ya está! ¡Sus objetos de valor aparecerán limpios y resplandecientes!\".", progress = "Diría que el dispositivo necesita algún tipo de objeto con mugre incrustada para limpiarlo y tres monedas de plata para funcionar. Con esas dos cosas, el Destellamatic 5200 debería funcionar...", completion = "Insertas el objeto con mugre incrustada y 3 monedas de plata en el Destellamatic 5200. Se pone en marcha como si le fuera la vida en limpiar el objeto; hace unos ruidos tremendos y parece que hasta la carcasa de la máquina se queja...", rewards = nil},
[80155] = {title = "El Destellamatic en acción", objective = "El Destellamatic 5200 parece estar esperando un objeto con mugre incrustada que limpiar y 3 monedas de plata para activarse.", description = nil, progress = "El Destellamatic 5200 parece estar esperando un objeto con mugre incrustada que limpiar y 3 monedas de plata para activarse.", completion = "El Destellamatic 5200 se pone en marcha con sacudidas. En su interior, el agente limpiador gnómico está pulverizando la indefensa mugre incrustada en el objeto que hay dentro de la máquina.\n\nEl Destellamatic 5200 se para con un \"¡ding!\" familiar, y el receptáculo de objetos se abre para revelar una cajita muy bien envuelta.", rewards = "Recibirás:\n\nCaja envuelta de la Destellamatic"},
[80157] = {title = "¡El Destellamatic 5200!", objective = "El Destellamatic 5200 se para tan bruscamente como había arrancado. Se oye un simple \"ping\", y se abre la boca de la máquina. Dentro del Destellamatic 5200 hay una cajita con un lazo. Parece que la máquina envuelve los objetos después de limpiarlos. Incluso si la máquina no ha funcionado, al menos ahora el objeto está envuelto en una linda cajita.\n\nBueno, solo hay una forma de saber si el objeto está bien limpio...", description = nil, progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nCaja envuelta de la Destellamatic"},
[80158] = {title = "¡El Destellamatic 5200!", objective = "Inserta un objeto con mugre incrustada en el Destellamatic 5200 y asegúrate de tener 3 monedas de plata a mano.", description = "Este aparato destaca entre los gnomos que se mantienen firmes frente a la locura que acecha fuera de la sala. Tiene una plaquita que dice: \"Destellamatic 5200\" y:\n\n\"¿La suciedad arruina sus destellos? ¡El Destellamatic 5200 devuelve el brillo de forma brillante! Inserte el objeto que desea limpiar e introduzca tres monedas de plata en la ranura. Espere unos momentos, ¡y ya está! ¡Sus objetos de valor aparecerán limpios y resplandecientes!\".", progress = "Diría que el dispositivo necesita algún tipo de objeto con mugre incrustada para limpiarlo y tres monedas de plata para funcionar. Con esas dos cosas, el Destellamatic 5200 debería funcionar...", completion = "Insertas el objeto con mugre incrustada y 3 monedas de plata en el Destellamatic 5200. La máquina se pone en marcha como si le fuera la vida en limpiar la mugre del objeto; hace unos ruidos tremendos y parece que hasta la carcasa de la máquina se queja mientras da bandazos de un lado a otro...", rewards = nil},
[80160] = {title = "El Destellamatic en acción", objective = "El Destellamatic 5200 parece estar esperando un objeto con mugre incrustada que limpiar y 3 monedas de plata para activarse.", description = nil, progress = "El Destellamatic 5200 parece estar esperando un objeto con mugre incrustada que limpiar y 3 monedas de plata para activarse.", completion = "El Destellamatic 5200 se pone en marcha con sacudidas. En su interior, el agente limpiador gnómico está pulverizando la indefensa mugre incrustada en el objeto que hay dentro de la máquina.\n\nEl Destellamatic 5200 se para con un \"¡ding!\" familiar, y el receptáculo de objetos se abre para revelar una cajita muy bien envuelta.", rewards = "Recibirás:\n\nCaja envuelta de la Destellamatic"},
[80161] = {title = "¡El Destellamatic 5200!", objective = "El Destellamatic 5200 se para tan bruscamente como había arrancado. Se oye un simple \"ping\", y se abre la boca de la máquina. Dentro del Destellamatic 5200 hay una cajita con un lazo. Parece que la máquina envuelve los objetos después de limpiarlos. Incluso si la máquina no ha funcionado, al menos ahora el objeto está envuelto en una linda cajita.\n\nBueno, solo hay una forma de saber si el objeto está bien limpio...", description = nil, progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nCaja envuelta de la Destellamatic"},
[80169] = {title = "Lanzatracas", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nEsquema: lanzacohetesPaquete de fuegos artificiales del Festival Lunar"},
[80180] = {title = "La gran traición", objective = "Ve a Gnomeregan y mata al mekigeniero Termochufe. Ve a ver al Manitas Mayor Mekkatorque cuando hayas terminado.", description = "Yo confiaba en Termochufe, <raza>. Nunca pensé que nos traicionaría a todos los gnomos y a mí. ¿Y por qué? ¿Por el poder? ¿Por el dinero? Habría conseguido todo eso por sí mismo. Ahora nos han echado de nuestro hogar y ese chiflado es el que manda. Rey de pacotilla...\n\nVamos a recuperar Gnomeregan, <nombre>. No cejaremos hasta que la ciudad vuelva a nuestras manos. Si quieres ayudarnos, tengo un trabajito para ti. Mata al traidor. Acaba con el mekigeniero Termochufe.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nTogas civinad prístinasPeto de corredor ágilLeotardos triples reforzados\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nTogas civinad prístinasPeto de corredor ágilLeotardos triples reforzados"},
[80181] = {title = "Excavadoras gyroagujereamáticas", objective = "Lleva veinticuatro entrañas robomecánicas a Shoni en Ventormenta.", description = "Es terrible, <nombre>. No vamos a tener suficientes repuestos para llevar nuestra flota de excavadoras gyroagujereamáticas a Dun Morogh.\n\nLo que necesitamos son repuestos, <nombre>. ¿Y qué mejor lugar que Gnomeregan para conseguir repuestos para nuestros vehículos? He oído que robots y mecazancudos vagan libremente por ese lugar.\n\nNecesito que vayas a Gnomeregan y destruyas a esos robots. Tráeme sus entrañas robot. Con dos docenas tendré suficiente.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nHerramienta para desmontar de ShoniMitones llamativos\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nHerramienta para desmontar de ShoniMitones llamativos"},
[80182] = {title = "Necesitamos más material verdoso", objective = "Viaja hasta Gnomeregan y recupera los restos radiactivos de gran potencia. Pero ten cuidado, ya que es inestable y podría explotar en cualquier momento.\n\nOzzie también quiere que traigas la ampolla pesada de plomo una vez que hayas terminado el trabajo.", description = "Tu muestra no solo era insuficiente en cuanto a cantidad, además era impura.\n\nNecesitamos más material verdoso radiactivo. Es evidente que los troggs no han sido suficientemente irradiados. Tendrás que ir a Gnomeregan y traer restos radiactivos, directamente de la fuente. Estoy hablando de babosas, rondadores y horrores irradiados. Y ten cuidado: contener este tipo de restos es difícil. Es posible que tengas que hacer varios intentos hasta que hayas reunido una muestra adecuada.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[80307] = {title = "Un envío completo", objective = "¿Tienes algo para mí?", description = nil, progress = "¿Tienes algo para mí?", completion = "¡Parece que está todo! Gracias, &lt;nombre&gt;. Esos suministros son vitales para el frente.", rewards = "Te van a lanzar el siguiente hechizo:\n\nReputación - Facción mercantil\n\nRecibirás:\n\n"},
[80308] = {title = "Un envío completo", objective = "¿Tienes algo para mí?", description = nil, progress = "¿Tienes algo para mí?", completion = "¡Parece que está todo! Gracias, &lt;nombre&gt;. Esos suministros son vitales para el frente.", rewards = "Te van a lanzar el siguiente hechizo:\n\nReputación - Facción mercantil\n\nRecibirás:\n\n"},
[80309] = {title = "Un envío completo", objective = "¿Tienes algo para mí?", description = nil, progress = "¿Tienes algo para mí?", completion = "¡Parece que está todo! Gracias, &lt;nombre&gt;. Esos suministros son vitales para el frente.", rewards = "Te van a lanzar el siguiente hechizo:\n\nReputación - Facción mercantil\n\nRecibirás:\n\n"},
[80324] = {title = "El rey loco", objective = "Llévale las notas de ingeniería de Termochufe al Manitas Mayor Mekkatorque, que está en Ciudad Manitas, en Forjaz.", description = "Has encontrado un cuaderno ajado y sucio lleno de esquemas y notas garabateados. Basta con un simple vistazo para darse cuenta de que cualquiera de estas creaciones podría usarse para desatar una destrucción terrible si alguien fuese lo suficientemente insensato como para fabricarla.", progress = "<nombre>, ¿qué tienes ahí?", completion = "Gracias de corazón por recuperar estos esquemas. Miedo me da pensar lo que habría pasado con Forjaz si Termochufe hubiese usado estas creaciones contra nosotros.\n\n¡Esto hay que celebrarlo!", rewards = "Podrás elegir una de estas recompensas:\n\nColgante de vuelta a casaColgante de pistónTorque de oficial de paz de GnomereganDistintivo de justicia\n\nTambién recibirás:\n\nDistintivo de justiciaTorque de oficial de paz de GnomereganColgante de pistónColgante de vuelta a casa"},
[80325] = {title = "El rey loco", objective = "Llévale las notas de ingeniería de Termochufe a Nogg, que está en el Valle del Honor, en Orgrimmar.", description = "Has encontrado un cuaderno ajado y sucio lleno de esquemas y notas garabateados. Basta con un simple vistazo para darse cuenta de que cualquiera de estas creaciones podría usarse para desatar una destrucción terrible si alguien fuese lo suficientemente insensato como para fabricarla.", progress = "¿Qué me traes?", completion = "&lt;La mirada de Nogg se ilumina al leer las notas y los esquemas&gt;.\n\nVoy a reventar un montón de cosas con esta información.\n\nBuen trabajo, &lt;nombre&gt;. ¡Esto hay que celebrarlo!", rewards = "Podrás elegir una de estas recompensas:\n\nColgante de vuelta a casaColgante de pistónTorque de oficial de paz de GnomereganDistintivo de justicia\n\nTambién recibirás:\n\nDistintivo de justiciaTorque de oficial de paz de GnomereganColgante de pistónColgante de vuelta a casa"},
