[81570] = {title = "Jarl necesita una hoja", objective = "Lleva una espada ancha de acero lunar a Jarl en el Marjal Revolcafango.", description = "Tengo algo que cortar. No puedo decirte qué voy a cortar, pero... necesito una hoja. Una en concreto...\n\nHay quien la llama espada ancha de acero lunar. Yo prefiero llamarla hoja.\n\n¿Me ayudas? Tal vez puedas encontrarla en la ciudad, o quizás algún herrero te pueda hacer el favor.\n\nPero no te duermas en los laureles. Lo que quiero cortar no esperará para siempre...Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nAnillo Nudo de JuncoPantalones de artesano"},
[81682] = {title = "El antiguo campeón", objective = "Encuentra a Kajind en algún lugar de Azshara.", description = "Esta arena puede no parecer gran cosa, pero en su día causaba sensación. Luchadores profesionales, aventureros e, incluso, algunos integrantes del cártel se lanzaban al cuadrilátero en busca de dinero y gloria.\n\nEntre las apuestas y las concesiones, ¡me estaba forrando!\n\nAunque todo empezó a fastidiarse cuando se retiró el antiguo campeón, Kajind. Era un tipo bastante serio; un día partió a Azshara sin mediar palabra. Si lo encuentras, podríamos volver a poner en marcha la arena.Cuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = nil},
[81697] = {title = "Fuera colmillos", objective = "Quítale la espada de Kajind al draco azul que vaga por la región sureste de la Loma Desolada en Azshara.", description = "Solo sé luchar, <nombre>. Durante la Tercera Guerra, asesiné a incontables enemigos con mi espada. Cuando la guerra terminó, me gané la vida como luchador profesional. Fue en ese momento cuando llegué a la arena de Gadgetzan.\n\nFueron días gloriosos para mí, pero pronto me harté de ganar y viajé a Azshara en busca de un desafío mayor: un draco azul que vaga al sureste de aquí. ¡Ay de mí! La criatura por poco acaba conmigo. Escapé con vida, pero mi espada pasó a formar parte de su tesoro.\n\nSi la recuperas, volveré contigo a Gadgetzan.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[81716] = {title = "Recuperar el informe de campo de la incursión: Bosque del Ocaso", objective = "Mata demonios y lleva el informe de campo al capitán de campo Palandar.", description = "Gracias a Elune que has venido, <nombre>. Hemos sido asaltados por unos demonios que han atravesado la puerta del Sueño Esmeralda. He perdido toda comunicación con mi exploradora. Encuentra a la exploradora Kharla en la puerta del Sueño y tráeme su informe de campo. Y, ya que estás, acaba con varios de esos demonios. Cuando vuelvas, te encomendaré una misión más basándome en la información que traigas.", progress = "¿Traes el informe?", completion = "¡Gracias, &lt;nombre&gt;! De acuerdo con la información que has recuperado, tengo una misión nueva para ti. ¡Gracias por tu ayuda!", rewards = "Recibirás:\n\nInforme de misión: Bosque del Ocaso"},
[81727] = {title = "Recuperar el informe de campo de la incursión: Vallefresno", objective = "Mata demonios y lleva el informe de campo a la capitana de campo Hannalah.", description = "Gracias a Elune que has venido, <nombre>. Hemos sido asaltados por unos demonios que han atravesado la puerta del Sueño Esmeralda. He perdido toda comunicación con mi explorador. Encuentra al explorador Gemeron en la puerta del Sueño y tráeme su informe de campo. Y, ya que estás, acaba con varios de esos demonios. Cuando vuelvas, te encomendaré una misión más basándome en la información que traigas.", progress = "¿Traes el informe?", completion = "¡Gracias, &lt;nombre&gt;! De acuerdo con la información que has recuperado, tengo una misión nueva para ti. ¡Gracias por tu ayuda!", rewards = "Recibirás:\n\nInforme de misión: Vallefresno"},
[81728] = {title = "Recuperar el informe de campo de la incursión: Tierras del Interior", objective = "Mata demonios y lleva el informe de campo al capitán de campo Korlian.", description = "Gracias a Elune que has venido, <nombre>. Hemos sido asaltados por unos demonios que han atravesado la puerta del Sueño Esmeralda. He perdido toda comunicación con mi exploradora. Encuentra a la exploradora Fallia en la puerta del Sueño y tráeme su informe de campo. Y, ya que estás, acaba con varios de esos demonios. Cuando vuelvas, te encomendaré una misión más basándome en la información que traigas.", progress = "¿Traes el informe?", completion = "¡Gracias, &lt;nombre&gt;! De acuerdo con la información que has recuperado, tengo una misión nueva para ti. ¡Gracias por tu ayuda!", rewards = "Recibirás:\n\nInforme de misión: Tierras del Interior"},
[81729] = {title = "Recuperar el informe de campo de la incursión: Feralas", objective = "Mata demonios y lleva el informe de campo a la capitana de campo Arunnel.", description = "Gracias a Elune que has venido, <nombre>. Hemos sido asaltados por unos demonios que han atravesado la puerta del Sueño Esmeralda. He perdido toda comunicación con mi explorador. Encuentra al explorador Thandros en la puerta del Sueño y tráeme su informe de campo. Y, ya que estás, acaba con varios de esos demonios. Cuando vuelvas, te encomendaré una misión más basándome en la información que traigas.", progress = "¿Traes el informe?", completion = "¡Gracias, &lt;nombre&gt;! De acuerdo con la información que has recuperado, tengo una misión nueva para ti. ¡Gracias por tu ayuda!", rewards = "Recibirás:\n\nInforme de misión: Feralas"},
[81730] = {title = "Misión I del Bosque del Ocaso: derrota a los huargen", objective = "Derrota a 10 corredores de pesadilla y a 10 tejedores pesadilla en la incursión de la Pesadilla del Bosque del Ocaso, en El Vergel Pútrido, y vuelve con el capitán de campo Palandar.", description = "Mientras estos demonios nos tienen ocupados aquí, el Sueño Esmeralda se está convirtiendo en una pesadilla. Rápido, cruza el portal al Sueño Esmeralda que hay en la Arboleda del Crepúsculo y ve hasta la incursión de la Pesadilla de El Vergel Pútrido. Derrota a los huargen y regresa.", progress = nil, completion = "¡Bien hecho! Tus servicios serán bien recompensados. ¿Te interesa una misión más?", rewards = "Recibirás:\n\nInforme de misión: Bosque del Ocaso"},
[81731] = {title = "Misión II del Bosque del Ocaso: derrota a los ogros", objective = "Derrota a 3 ogros trastornados y a 3 tejefuegos trastornados en la incursión de la Pesadilla del Bosque del Ocaso, en el Túmulo de Vul'Gol, y vuelve con el capitán de campo Palandar.", description = "Mientras estos demonios nos tienen ocupados aquí, el Sueño Esmeralda se está convirtiendo en una pesadilla. Rápido, atraviesa el portal al Sueño Esmeralda que hay en la Arboleda del Crepúsculo y ve hasta la incursión de la Pesadilla del Túmulo de Vul'Gol. Derrota a los ogros y regresa.", progress = nil, completion = "¡Bien hecho! Tus servicios serán bien recompensados. ¿Te interesa una misión más?", rewards = "Recibirás:\n\nInforme de misión: Bosque del Ocaso"},
