[81738] = {title = "Misión IX del Bosque del Ocaso: recupera un huevo de dragón", objective = "Recupera un huevo de dragón verde sin eclosionar en la incursión de la Pesadilla del Bosque del Ocaso cerca de un gran tocón en La Hacienda Yorgen y vuelve a reunirte con el capitán de campo Palandar.", description = "Mientras estos demonios nos tienen ocupados aquí, el Sueño Esmeralda se está convirtiendo en una pesadilla. Rápido, atraviesa el portal al Sueño Esmeralda que hay en la Arboleda del Crepúsculo y ve hasta la incursión de la Pesadilla de La Hacienda Yorgen. Recupera un huevo de dragón verde sin eclosionar y tráemelo.", progress = "¿Tienes el huevo?", completion = "¡Bien hecho! Tus servicios serán bien recompensados. ¿Te interesa una misión más?", rewards = "Recibirás:\n\nInforme de misión: Bosque del Ocaso"},
[81739] = {title = "Misión X del Bosque del Ocaso: musgo de la Pesadilla", objective = "Reúne 10 trozos de musgo de la Pesadilla en las incursiones de la Pesadilla del Bosque del Ocaso y vuelve a reunirte con el capitán de campo Palandar.", description = "Mientras estos demonios nos tienen ocupados aquí, el Sueño Esmeralda se está convirtiendo en una pesadilla. Rápido, atraviesa el portal al Sueño Esmeralda que hay en la Arboleda del Crepúsculo y recolecta musgo de la Pesadilla del interior de las incursiones de la Pesadilla. Podemos aprovechar su magia para revertir el daño. Necesitarás la ayuda de un herbalista.", progress = "¿Tienes el musgo de la Pesadilla?", completion = "¡Bien hecho! Tus servicios serán bien recompensados. ¿Te interesa una misión más?", rewards = "Recibirás:\n\nInforme de misión: Bosque del Ocaso"},
[81740] = {title = "Misión XI del Bosque del Ocaso: mena de hierro frío", objective = "Reúne 10 menas de hierro frío en las incursiones de la Pesadilla del Bosque del Ocaso y vuelve a reunirte con el capitán de campo Palandar.", description = "Mientras estos demonios nos tienen ocupados aquí, el Sueño Esmeralda se está convirtiendo en una pesadilla. Rápido, atraviesa el portal al Sueño Esmeralda que hay en la Arboleda del Crepúsculo y reúne menas de hierro frío del interior de las incursiones de la Pesadilla. Podemos aprovechar su magia para revertir el daño. Necesitarás la ayuda de un minero.", progress = "¿Tienes las menas de hierro frío?", completion = "¡Bien hecho! Tus servicios serán bien recompensados. ¿Te interesa una misión más?", rewards = "Recibirás:\n\nInforme de misión: Bosque del Ocaso"},
[81741] = {title = "Misión XII del Bosque del Ocaso: escama de dragón tocada por el Sueño", objective = "Reúne 10 escamas de dragón tocadas por el Sueño en las incursiones de la Pesadilla del Bosque del Ocaso y vuelve a reunirte con el capitán de campo Palandar.", description = "Mientras estos demonios nos tienen ocupados aquí, el Sueño Esmeralda se está convirtiendo en una pesadilla. Rápido, atraviesa el portal al Sueño Esmeralda que hay en la Arboleda del Crepúsculo y reúne escamas de dragón tocadas por el Sueño de los dragonantes del interior de las incursiones de la Pesadilla. Podemos aprovechar su magia para revertir el daño. Necesitarás la ayuda de un desollador.", progress = "¿Tienes las escamas de dragón?", completion = "¡Bien hecho! Tus servicios serán bien recompensados. ¿Te interesa una misión más?", rewards = "Recibirás:\n\nInforme de misión: Bosque del Ocaso"},
[81742] = {title = "Misión XIII del Bosque del Ocaso: derrota a Ylanthrius", objective = "Derrota a Ylanthrius en la incursión de la Pesadilla del Bosque del Ocaso en La Hacienda Yorgen y vuelve a reunirte con el capitán de campo Palandar.", description = "Mientras estos demonios nos tienen ocupados aquí, el Sueño Esmeralda se está convirtiendo en una pesadilla. Rápido, atraviesa el portal al Sueño Esmeralda que hay en la Arboleda del Crepúsculo y ve hasta la incursión de la Pesadilla de La Hacienda Yorgen. Derrota al dragón Ylanthrius y regresa.", progress = nil, completion = "¡Bien hecho! Tus servicios serán bien recompensados. ¿Te interesa una misión más?", rewards = "Recibirás:\n\nInforme de misión: Bosque del Ocaso"},
