[81745] = {title = "Misión XVI del Bosque del Ocaso: rescata a Kroll Sombramontaña", objective = "Rescata al druida de la Zarpa durmiente Kroll Sombramontaña de la incursión de la Pesadilla del Bosque del Ocaso en la casa de invitados de El Vergel Pútrido y vuelve a reunirte con el capitán de campo Palandar.", description = "Mientras estos demonios nos tienen ocupados aquí, el Sueño Esmeralda se está convirtiendo en una pesadilla. Rápido, atraviesa el portal al Sueño Esmeralda que hay en la Arboleda del Crepúsculo y ve hasta la incursión de la Pesadilla de El Vergel Pútrido. Encuentra al druida de la Zarpa durmiente Kroll Sombramontaña y escóltalo hasta un lugar seguro. Después, regresa aquí.", progress = nil, completion = "¡Bien hecho! Tus servicios serán bien recompensados. ¿Te interesa una misión más?", rewards = "Recibirás:\n\nInforme de misión: Bosque del Ocaso"},
[81746] = {title = "Misión XVII del Bosque del Ocaso: rescata a Alara Sanarboleda", objective = "Rescata a Alara Sanarboleda de la incursión de la Pesadilla del Bosque del Ocaso en el establo de La Hacienda Yorgen y vuelve a reunirte con el capitán de campo Palandar.", description = "Mientras estos demonios nos tienen ocupados aquí, el Sueño Esmeralda se está convirtiendo en una pesadilla. Rápido, atraviesa el portal al Sueño Esmeralda que hay en la Arboleda del Crepúsculo y ve hasta la incursión de la Pesadilla de La Hacienda Yorgen. Encuentra a la druida de la Zarpa durmiente Alara Sanarboleda y escóltala hasta un lugar seguro. Después, regresa aquí.", progress = "¿Has rescatado a Alara?", completion = "¡Bien hecho! Tus servicios serán bien recompensados. ¿Te interesa una misión más?", rewards = "Recibirás:\n\nInforme de misión: Bosque del Ocaso"},
[81747] = {title = "Misión XVIII del Bosque del Ocaso: rescata a Elenora Caminaciénagas", objective = "Rescata a Elenora Caminaciénagas de la incursión de la Pesadilla del Bosque del Ocaso en las ruinas de una granja cercana al Túmulo de Vul'Gol y vuelve a reunirte con el capitán de campo Palandar.", description = "Mientras estos demonios nos tienen ocupados aquí, el Sueño Esmeralda se está convirtiendo en una pesadilla. Rápido, atraviesa el portal al Sueño Esmeralda que hay en la Arboleda del Crepúsculo y ve hasta la incursión de la Pesadilla del Túmulo de Vul'Gol. Encuentra a la druida de la Zarpa durmiente Elenora Caminaciénagas y escóltala hasta un lugar seguro. Después, regresa aquí.", progress = nil, completion = "¡Bien hecho! Tus servicios serán bien recompensados. ¿Te interesa una misión más?", rewards = "Recibirás:\n\nInforme de misión: Bosque del Ocaso"},
[81762] = {title = "Buenas noticias", objective = "Habla con Katherine la Pura en la Catedral de la Luz de Ventormenta.", description = "Saludos, <nombre>. Traigo buenas noticias. He podido contactar con mi amigo y pronto partiré a su encuentro. Además, ha aparecido otro orbe en Kalimdor. No puedo estar en dos sitios al mismo tiempo, así que, si quieres, me gustaría que hicieses el seguimiento de este nuevo orbe en mi lugar. Habla con Katherine. Ella te dará más indicaciones.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[81764] = {title = "El comerciante misterioso", objective = "Viaja a Theramore y encuentra al comerciante misterioso.", description = "Saludos, una vez más, <nombre>. Shaw y sus espías nos han pasado información sobre un misterioso comerciante que acaba de llegar a Kalimdor y pretende deshacerse de una colección de artefactos peligrosos. Hemos pensado que quizás tenga el orbe del eco. Se aloja en la taberna de Theramore en el Marjal Revolcafango. Date prisa, recupera el orbe y no hables con nadie del asunto. Te enviamos sin compañía para evitar llamar la atención de los enemigos.\n\nTen cuidado. Ve con la Luz.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nS03 - Flag Tracking Quest - Paladin 50 Quest - Aeonas Traveling (DNT\n\nRecibirás:\n\n"},
[81765] = {title = "Paladín de la Mano de Plata Elrick", objective = "Habla con Elrick para saber más.", description = "No conoces al paladín que tienes frente a ti. Habla con él para determinar qué sabe del comerciante y de la situación actual.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[81766] = {title = "La misiva sangrienta", objective = "Lleva la misiva a Katherine la Pura en la Catedral de la Luz de Ventormenta.", description = "Encuentras una misiva en el cuerpo del \"paladín\". Está cubierta de sangre y es difícil de leer, pero logras descifrar el mensaje impreso en el papiro. Deberías estudiarla y luego llevársela a Katherine para recibir más instrucciones.Aprenderás:\n\n", progress = nil, completion = nil, rewards = "Aprenderás:\n\nGrabar yelmo: Fanatismo\n\nTe van a lanzar el siguiente hechizo:\n\nFanatismo\n\nTambién recibirás:\n\n"},
