[83808] = {title = "En un aprieto", objective = "Recupera la espada del escudero Cuthbert en el campo que hay al sur y devuélvesela.", description = "¡Casi me matas del susto! ¡Te oí acercarte y pensé que la parca por fin había dado conmigo! Aun así, me alegro de verte. Te lo ruego, tengo que salir de aquí y encontrar a mi señor, sir Dalton. ¿Podrías recuperar mi arma? Me la arrancaron de las manos en el campo que hay fuera y me vi obligado a refugiarme en este granero.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[83934] = {title = "¡La pasta primero!", objective = "Lleva un real deslustrado de Minahonda a Pix Xizzix en Bahía del Botín.", description = "¡Hola! Pareces la clase de \"<aventurero habilidoso/aventurera habilidosa>\" que quizá pueda echarme una mano. Verás, mis hermanos y yo vinimos de las Islas Perdidas para poner en marcha un nuevo negocio, pero nos asaltaron unos piratas y nos robaron la inversión inicial. La gente que financia nuestra operación en casa no es lo que se dice comprensiva, así que te recompensaremos si eres capaz de encontrar el dinero y devolvérnoslo. No es oro normal, pero lo reconocerás cuando lo veas.Cuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = nil},
[84011] = {title = "Más lotos estelares", objective = "Recoge 10 lotos estelares en las incursiones de la Pesadilla de las Tierras del Interior y vuelve con el capitán de campo Korlian.", description = "Todavía necesitamos más lotos estelares para repeler las incursiones de la Pesadilla. Atraviesa el portal esmeralda en Seradane y tráeme unos pocos.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nTrozo de esmeralda"},
[84012] = {title = "Más menas de estrellargenta", objective = "Recoge 10 menas de estrellargenta en las incursiones de la Pesadilla de las Tierras del Interior y vuelve con el capitán de campo Korlian.", description = "Todavía necesitamos más menas de estrellargenta para repeler las incursiones de la Pesadilla. Atraviesa el portal esmeralda en Seradane y tráeme unas pocas.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nTrozo de esmeralda"},
[84013] = {title = "Más caracolas estelares", objective = "Recoge 10 caracolas estelares en las incursiones de la Pesadilla de las Tierras del Interior y vuelve con el capitán de campo Korlian.", description = "Todavía necesitamos más caracolas estelares para repeler las incursiones de la Pesadilla. Atraviesa el portal esmeralda en Seradane y tráeme unas pocas.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nTrozo de esmeralda"},
[84014] = {title = "Más raíces lunares", objective = "Recoge 10 raíces lunares en las incursiones de la Pesadilla de Feralas y vuelve con la capitana de campo Arunnel.", description = "Todavía necesitamos más raíces lunares para repeler las incursiones de la Pesadilla. Atraviesa el portal esmeralda en Rama Oniria y tráeme unas pocas.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nTrozo de esmeralda"},
[84015] = {title = "Más piedras lunares superiores", objective = "Recoge 10 piedras lunares superiores en las incursiones de la Pesadilla de Feralas y vuelve con la capitana de campo Arunnel.", description = "Todavía necesitamos más piedras lunares superiores para repeler las incursiones de la Pesadilla. Atraviesa el portal esmeralda en Rama Oniria y tráeme unas pocas.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nTrozo de esmeralda"},
[84016] = {title = "Más escamas de dragón lunar", objective = "Recoge 10 escamas de dragón lunar en las incursiones de la Pesadilla de Feralas y vuelve con la capitana de campo Arunnel.", description = "Todavía necesitamos más escamas de dragón lunar para repeler las incursiones de la Pesadilla. Atraviesa el portal esmeralda en Rama Oniria y tráeme unas pocas.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nTrozo de esmeralda"},
[84348] = {title = "Objetivo prioritario: Duque Tectonis", objective = "Hansel Manospesadas, en Puesto del Torio, quiere que destruyas al duque Tectonis. Puedes encontrarlo en un pequeño valle al oeste de la entrada de la Garganta de Fuego a la Montaña Roca Negra.", description = "Hay un elemental bastante molesto llamado Tectonis que maneja el cotarro cerca de la base de la Montaña Roca Negra, ¡y tiene que desaparecer! ¡Acaba con ese montón de rocas y vuelve a informarme cuando hayas terminado!\n\nEncontrarás al duque Tectonis en un valle al sudeste, cerca de la entrada de la Montaña Roca Negra.", progress = nil, completion = "¡Esto les pondrá las cosas difíciles a nuestros enemigos, &lt;nombre&gt;! ¡Hurra!", rewards = "Recibirás:\n\nAscuas de las Tierras de Fuego"},
[84349] = {title = "Objetivo prioritario: Duque Marcabrasador", objective = "El capitán de vigía Lolo Zancalarga quiere que destruyas al duque Marcabrasador. Lo encontrarás en la parte más profunda de las cuevas de la Fosa de la Escoria, directamente al sur de Puesto del Torio.", description = "Nuestros oteadores nos han informado de la presencia de un Señor del Fuego que se hace llamar duque Marcabrasador en lo más profundo de la Caldera, ¡y está trabajando duro para invocar más elementales de fuego de las Tierras de Fuego! Necesitamos que vayas allí y lo detengas antes de que la situación se descontrole.", progress = nil, completion = "¡Bien hecho, &lt;nombre&gt;! ¡Creo que necesito un trago después de tanto esfuerzo! ¡Ja!", rewards = "Recibirás:\n\nAscuas de las Tierras de Fuego"},
[84350] = {title = "Morder el polvo", objective = "Evonis Fumollín quiere que mates a 10 atracadores de obsidiana en la Garganta de Fuego. Puedes encontrarlos por toda la parte sur de la garganta y cerca de la entrada a la Montaña Roca Negra.", description = "¡Nos salen los elementales de tierra por las orejas! ¡Dales una buena tunda y mándalos de vuelta a las Tierras de Fuego!", progress = nil, completion = "¡Ja! ¡Así aprenderán!", rewards = "Recibirás:\n\nAscuas de las Tierras de Fuego"},
[84351] = {title = "Trabajo eficiente, trabajador sonriente", objective = "El capataz Scrange quiere que mates atracadores de obsidiana en la Garganta de Fuego y que le lleves sus núcleos de poder. Puedes encontrarlos por toda la parte sur de la Garganta de Fuego, y cerca de la entrada de la Montaña Roca Negra.", description = "Desde que comenzaron las erupciones, hemos estado luchando con uñas y dientes para mantener a raya a estos malditos elementales, y el esfuerzo ha pasado factura a nuestros artesanos. Resulta que mantener en condiciones óptimas el equipo de las tropas es mucho trabajo. Por suerte, los enanos somos los reyes de la eficiencia, no de deslomarnos, ¡y hemos descubierto que los núcleos de poder que hay dentro de las mareas de obsidiana al sudeste de Puesto del Torio funcionan de maravilla como piedras de afilar para pulir nuestras hojas fácilmente!\n\n¿Nos traerías algunos más?", progress = "¿Eh?", completion = "Con esto servirá, &lt;clase&gt;. ¡Gracias!", rewards = "Recibirás:\n\n"},
[84355] = {title = "Sofocar la clamallama", objective = "El capataz Scrange quiere que mates defensores clamallamas y elementalistas clamallamas en la Cresta Vigía al oeste de Puesto del Torio.", description = "Como si no tuviéramos bastantes cosas de las que ocuparnos por aquí, el enclave del Martillo Crepuscular al oeste de Puesto del Torio ha estado rebosante de actividad desde que la montaña entró en erupción. Por lo que parece, estos nuevos clamallamas son los que mueven los hilos ahora.\n\n¿Por qué no te acercas por allí y mandas al otro barrio a todos los que puedas?", progress = nil, completion = "¡Hasta nunca!", rewards = "Recibirás:\n\nAscuas de las Tierras de Fuego"},
[84356] = {title = "¡Cómo brilla!", objective = "El herrero maestro Burninate, en Puesto del Torio, quiere que reúnas 8 montones de piedras de llamas de esclavistas Hierro Negro en la Fosa de la Escoria. Puedes encontrar la entrada dentro de la Caldera, al sudeste de Puesto del Torio.", description = "Hace poco capturamos a un grupo de Hierro Negro que llevaban encima unas gemas encantadas especialmente interesantes. No parecen proceder de Azeroth, ¡y necesito estudiar algunas más! Tienen a sus esclavos excavando ahora mismo en la Caldera, y quiero que bajes ahí lo más rápido que puedas y me traigas algunas muestras más. Si lo haces, ¡te recompensaré con creces!", progress = "¿Qué tienes para mí?", completion = "¡Qué preciosidad! Estoy deseado ponerlas bajo mi monoscopio. Muchas gracias, &lt;clase&gt;.", rewards = "Recibirás:\n\nAscuas de las Tierras de Fuego"},
[84359] = {title = "Noches insomnes", objective = "Evonis Fumollín quiere que bebas el zumo onírico especialmente preparado y mates a 5 acechadores clamallamas cerca de Puesto del Torio, en la Garganta de Fuego.", description = "No basta con que estos bellacos nos ataquen por el día, ¡también tienen que hacerlo por la noche cuando dormimos! El nuevo Señor Crepuscular parece decantarse especialmente por los asuntos oníricos, ¡así que ha estado enviando asesinos para atacarnos en sueños! Necesito que te tomes un par de tragos de mi \"zumo onírico\", especialmente preparado a partir de productos de proximidad. Así podrás cruzar la línea que separa el mundo de la vigilia del de los sueños y detener a estos asaltantes clandestinos.", progress = nil, completion = "Bien hecho, &lt;nombre&gt;. ¡Así se lo pensarán dos veces antes de volver a colarse en Puesto del Torio!", rewards = "Recibirás:\n\nAscuas de las Tierras de Fuego"},
[84360] = {title = "Sofocar las llamas", objective = "El sobrestante Puñaceite quiere que mates a 15 invasores de las Tierras de Fuego en la Caldera, al sur de Puesto del Torio.", description = "Eh, tú, ¡te he reclutado para una misión! No paran de salir elementales de fuego de la Caldera. Necesito que vayas allí y cauterices la herida, no sé si me entiendes. ¡Manos a la obra, soldado!", progress = nil, completion = "Buen trabajo, &lt;nombre&gt;. Reconozco que tienes agallas.", rewards = "Recibirás:\n\nAscuas de las Tierras de Fuego"},
[84372] = {title = "Inmersión en lava", objective = "Lleva un trozo del mineral fundido resplandeciente a Hansel Manospesadas. Encontrarás la escoria fundida resplandeciente en las pozas de lava de la Fosa de la Escoria, en el fondo de la Caldera.", description = "Nuestros oteadores me cuentan que hay una enorme roca brillante que sobresale de la parte más profunda de las pozas de lava de la Caldera. Los elementales parecen reaccionar a este material, así que necesito que te cueles y me traigas un trozo de la roca. Ahí abajo hace un calor infernal, ¡así que te aconsejo que lleves contigo un buen suministro de pociones de protección contra el Fuego!", progress = "¿Qué tienes ahí?", completion = "Con suerte podremos usarlo para aprender más cosas sobre el enemigo al que nos enfrentamos.", rewards = "Recibirás:\n\nAscuas de las Tierras de Fuego"},
[84384] = {title = "Engaños demoníacos", objective = "Recoge 6 glándulas pineales de las lechubestias que habitan en Cuna del Invierno y entrégaselas a la emisaria diente oscuro en Frondavil.", description = "Loh loa me han hablado de un veneno muy poderoso que crece en Vallefrehno, en el lugar mancillado por la muerte del gran demonio Mannoroth. Alguien debe ehtar ehtrayendo poder de los rehtoh del señor del foso.\n\nDebe ehtar utilizando ilusioneh para ocultar sus ritualeh. Si deseah detenerlo, tendráh que protegerte de suh engañoh.\n\nMi tribu usa lah glándulah de búho para preparar un encantamiento que noh ayuda a dihcernir la verdad de lah mentirah. Recoge ehtah glándulah de los búhoh de Cuna del Invierno.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nResguardo de ilusión diente oscuro"},
}
