[8409] = {title = "惡臭的雞蛋", objective = "到提里斯法林地的稻草人節找黑暗召喚者雅恩卡覆命。", description = "腐爛的蛋已經被放進了桶裡。以後南海鎮旅館出產的酒肯定都是次品了！\n\n你現在應該回提里斯法林地向黑暗召喚者雅恩卡報告你的成功。您會收到：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n萬鬼節南瓜糖"},
[8410] = {title = "元素專精", objective = "為捕風者巴斯拉收集一份空氣樣本、一份火元素樣本、一份土元素樣本和一份水元素樣本。", description = "捕風者巴斯拉需要你這樣充滿智慧的薩滿。他要你帶上每種元素的樣本，證明你已準備就緒。\n\n你可以沿著塔倫米爾以東的河流往北走，巴斯拉就居住在沿途的一處廢墟中。您會收到：\n\n完成任務後，你將獲得：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n"},
[8411] = {title = "控制元素", objective = "將土元素、空氣元素、火元素和水元素交給捕風者巴斯拉。", description = "歡迎。把元素交給我，然後我們談談。您會收到：\n\n完成任務後，你將獲得：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n"},
[8412] = {title = "靈魂圖騰", objective = "將靈魂圖騰所需的材料交給捕風者巴斯拉。", description = "這地方的元素靈魂不再歡迎我了。\n\n你比任何人都懂圖騰，現在我需要靈魂圖騰來保護自己。我們已經有了元素，現在我們需要更多更原始、更接近自然的材料。\n\n你可以去西瘟疫之地獲取熊爪和蜘蛛的眼球，另外有時食腐的鳥類什麼都吃，所以它們身上可能也有那些東西。您會收到：\n\n完成任務後，你將獲得：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n"},
[8413] = {title = "巫毒羽毛", objective = "將巫毒羽毛交給捕風者巴斯拉。", description = "靈魂圖騰還不夠。我們還需要一些巫毒魔法來讓它發揮效力。\n\n沉沒的神廟中的食人妖身上有巫毒羽毛。去殺掉加什爾、米杉、祖羅、胡庫、祖羅爾和洛若爾。這樣我們就能擁有靈魂圖騰所需的魔法了。您能選擇下列奬勵中的一個：\n\n", progress = nil, completion = nil, rewards = "您能選擇下列奬勵中的一個：\n\n藍銅之拳被迷惑的水之魂荒野之杖\n\n下列法術會施放在您身上：\n\n經驗值爆發\n\n您還會收到：\n\n"},
[8414] = {title = "驅散邪惡", objective = "收集20個爪牙的天譴石並將它們交給西瘟疫之地的高階牧師賽爾鄧尼斯。", description = "你好，<class>。在這片荒無人煙的土地上，像你這樣的十字軍是受到歡迎的。\n\n我們需要借助你神聖的力量來消滅這裡無窮無盡的亡靈。更重要的是，我們需要你收集大量的天譴石，將它們交給高階牧師賽爾鄧尼斯。賽爾鄧尼斯就在距離此地不遠的悔恨嶺守護烏瑟之墓。\n\n只有佩戴銀色黎明委任徽章的人可以找到天譴石，如果你沒有徽章的話，就去跟軍官普爾哈特談談吧。您會收到：\n\n完成任務後，你將獲得：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n"},
[8415] = {title = "冰風崗哨", objective = "與冰風崗的指揮官阿什拉姆·瓦羅菲斯特談一談。", description = "指揮官阿什拉姆·瓦羅菲斯特需要你這樣純潔和勇敢的聖騎士，<name>。你可以在西瘟疫之地南方的冰風崗找到他。以聖光的名義，去協助他吧！您會收到：\n\n完成任務後，你將獲得：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n"},
[8416] = {title = "惰性天災石", objective = "將惰性天災石交給冰風崗的指揮官阿什拉姆·瓦羅菲斯特。", description = "天災石上的邪惡力量已經被清除了，<name>。\n\n我瞭解指揮官阿什拉姆的計劃，他還需要一塊惰性天災石。把惰性天災石帶給他，或許我們就能以正義之火消滅天譴軍團。您會收到：\n\n完成任務後，你將獲得：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n"},
[8417] = {title = "困擾的靈魂", objective = "到詛咒之地的入口去，與部落英雄的靈魂談一談。", description = "在詛咒之地中有個受到困擾的靈魂，他生前曾是部落的一位偉大的英雄。戰士的榮耀絕不會隨著肉體的毀滅而消逝，如果可能的話，我希望你能幫幫他。您會收到：\n\n完成任務後，你將獲得：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n"},
[8418] = {title = "鑄造力量之石", objective = "將巫毒羽毛帶給阿什拉姆·瓦羅菲斯特。", description = "現在我們必須把惰性天譴石組合起來，製造一件可以擊潰不死族的武器。這不是一件輕鬆的任務，<name>。只有沉沒的神廟裡的食人妖擁有那種罕見的魔法可以讓惡魔自行毀滅。\n\n在神廟裡，有幾個叫加什爾、米杉、祖羅、胡庫、祖羅爾和洛若爾的食人妖。不要為他們感到難過……他們是極端邪惡的存在。您能選擇下列奬勵中的一個：\n\n", progress = nil, completion = nil, rewards = "您能選擇下列奬勵中的一個：\n\n光鑄利刃聖化寶珠禮節徽記\n\n下列法術會施放在您身上：\n\n經驗值爆發\n\n您還會收到：\n\n神聖力量之石"},
[8419] = {title = "小鬼的要求", objective = "將1塊惡魔布交給費伍德森林的伊普斯。", description = "<name>，費伍德森林有個名叫伊普斯的小鬼要你去見他。這聽起來有點奇怪，因為他要你帶一塊惡魔布作為忠實的象徵。既然你已經是個強大的術士了，我想不用我說你也知道費伍德的薩特身上有時會帶著惡魔布吧？\n\n你可以在血毒瀑布以北找到伊普斯，我聽說這個季節血毒瀑布非常漂亮。您會收到：\n\n完成任務後，你將獲得：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n"},
[8420] = {title = "灼熱的疼痛", objective = "將1塊惡魔布交給費伍德森林的伊普斯。", description = "或許一個小鬼說這話有些奇怪，但是我希望擁有自己的寵物。尼比把我當作寵物，我卻沒有寵物，這不公平！\n\n事實上，我並不需要什麼活的寵物。如果你能給我一塊惡魔布的話，我就能製造一個寵物。這周圍的薩特身上經常帶著惡魔布，所以你可以去跟他們打打交道，你覺得如何？您會收到：\n\n完成任務後，你將獲得：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n"},
[8421] = {title = "奇怪的材料", objective = "將10塊腐爛的木頭和4個血毒精華交給費伍德森林的伊普斯。", description = "我已經有惡魔布啦，現在我可以開始製造*我的*寵物了。\n\n但是我還需要一些東西來製造寵物的眼睛。你能不能幫我從鐵樹上剝些腐爛的木頭來？這樣我才能雕刻出可愛的眼睛。我還需要血毒精華，讓我的寵物具有惡魔的氣息。被污染的軟泥怪具有這種精華，他們就在我們附近出沒。您會收到：\n\n完成任務後，你將獲得：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n"},
[8422] = {title = "巨魔的羽毛", objective = "到沉沒的神廟去，從食人妖們身上獲取6枝巫毒羽毛。", description = "我的寵物差不多要製作完成了。現在我需要一些填充物。\n\n我需要沉沒的神廟裡那些食人妖的巫毒羽毛。加什爾、米杉、祖羅、胡庫、祖羅爾和洛若爾會掉落巫毒羽毛。把他們全都殺了。\n\n這項任務可不簡單，<name>，我會送你一些尼比的東西——反正他似乎也用不著。您能選擇下列奬勵中的一個：\n\n", progress = nil, completion = nil, rewards = "您能選擇下列奬勵中的一個：\n\n靈魂收割者深淵碎片束縛長袍\n\n下列法術會施放在您身上：\n\n經驗值爆發\n\n您還會收到：\n\n"},
[8423] = {title = "戰士的血脈", objective = "在詛咒之地殺死7隻地獄野豬，然後回到部落英雄的靈魂那裡。", description = "<英雄注視著你。>\n\n毫無疑問，你是一位<class>。我要給你一項光榮的任務，但是首先，你必須證明自己的力量。\n\n據說雷克薩檢驗他的戰斧是否鋒利的標準，是以它能否一刀斬殺一頭地獄野豬。現在，我要讓你透過殺死它們來證明你擁有足夠的力量。您會收到：\n\n完成任務後，你將獲得：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n"},
[8424] = {title = "魔誓者之戰", objective = "殺死詛咒之地的魔誓祭司，然後回到部落英雄的靈魂那兒。", description = "我的存在本身就是個苦難，魔誓者更是為我帶來了無窮無盡的痛苦，他們的黑暗儀式讓我感到了比死亡還要可怕的苦楚。\n\n現在你可以幫我把痛苦帶給他們。去吧，成為我的憤怒的象徵。您會收到：\n\n完成任務後，你將獲得：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n"},
[8425] = {title = "巫毒羽毛", objective = "將你從沉沒的神廟的食人妖身上得到的巫毒羽毛交給部落英雄的靈魂。", description = "你為我帶來一絲平靜。我得給你點什麼作為回報。\n\n我的武器和護甲陪我度過了光輝的歲月，現在它們對我來說都沒什麼用處了。還是送給你吧。\n\n去沉沒的神廟，殺死食人妖守衛加什爾、米杉、祖羅、胡庫、祖羅爾和洛若爾。他們的巫毒羽毛能使我的肉身重現一段時間，足夠給你獎勵了。將巫毒羽毛帶給我吧。您能選擇下列奬勵中的一個：\n\n", progress = nil, completion = nil, rewards = "您能選擇下列奬勵中的一個：\n\n怒火面甲鑽石水瓶刺鋼護肩\n\n下列法術會施放在您身上：\n\n經驗值爆發\n\n您還會收到：\n\n"},
[8426] = {title = "峽谷之戰", objective = "將3枚戰歌榮譽獎章交給戰場外的部落戰爭使者。", description = "我們在戰歌峽谷對銀翼哨兵的戰役非常重要。聯盟以保護這片不屬於他們的森林為由，妄圖從部落手中奪取我們最大的伐木場。\n\n別讓他們的妄想成為事實，<name>！為了部落，證明你的價值！成功之後再回到我這兒來。下列法術會施放在您身上：\n\n", progress = nil, completion = nil, rewards = "下列法術會施放在您身上：\n\nHonor Points +82\n\n您會收到：\n\n"},
