[8770] = {title = "目標：亞什防禦者", objective = "殺死30隻亞什防禦者再到希利蘇斯的塞納里奧城堡向指揮官瑪爾利斯回報。你還必須將作戰任務簡報  I帶去才能完成這個任務。", description = "這一地區猖獗的異種蟲群對我們來說是個巨大的威脅。你被指派負責對亞什蟲巢發動攻擊，主要目標是亞什防禦者。\n\n在你的任務完成之後，向指揮官瑪爾利斯回報。您會收到：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n塞納里奧作戰徽章"},
[8771] = {title = "目標：亞什沙行者", objective = "殺死30隻亞什沙行者再到希利蘇斯向塞納里奧城堡的指揮官瑪爾利斯回報。你還必須將作戰任務簡報  II帶去才能完成這個任務。", description = "這一地區猖獗的異種蟲群對我們來說是個巨大的威脅。你被指派負責對亞什蟲巢發動攻擊，主要目標是亞什沙行者。\n\n在你的任務完成之後，向指揮官瑪爾利斯回報。您會收到：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n塞納里奧作戰徽章"},
[8772] = {title = "目標：佐拉守衛", objective = "殺死30隻佐拉守衛再到希利蘇斯的塞納里奧城堡向指揮官瑪爾利斯回報。你還必須將作戰任務簡報  VI帶去才能完成這個任務。", description = "我們需要援軍進攻佐拉蟲巢。你的主要目標是那些佐拉守衛，當你的任務完成之後就去向指揮官瑪爾利斯報告。您會收到：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n塞納里奧作戰徽章"},
[8773] = {title = "目標：佐拉掠奪者", objective = "殺死30隻佐拉掠奪者再到希利蘇斯的塞納里奧城堡向指揮官瑪爾利斯回報。你還必須將作戰任務簡報 IV帶去才能完成這個任務。", description = "我們需要援軍進攻佐拉蟲巢。你的主要目標是那些佐拉掠奪者，當你的任務完成之後就去向指揮官瑪爾利斯報告。您會收到：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n塞納里奧作戰徽章"},
