[8775] = {title = "目標：雷戈噴火者", objective = "殺死30隻雷戈噴火者再到希利蘇斯的塞納里奧城堡向指揮官瑪爾利斯回報。你還必須將作戰任務簡報  IX帶去才能完成這個任務。", description = "這一地區猖獗的異種蟲群對我們來說是個巨大的威脅。你被指派負責對雷戈蟲巢發動攻擊，主要目標是雷戈噴火者。\n\n在你的任務完成之後，向指揮官瑪爾利斯回報。您會收到：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n塞納里奧作戰徽章"},
[8776] = {title = "目標：雷戈制奴者", objective = "殺死30隻雷戈制奴者再到希利蘇斯的塞納里奧城堡向指揮官瑪爾利斯回報。你還必須將作戰任務簡報  X帶去才能完成這個任務。", description = "這一地區猖獗的異種蟲群對我們來說是個巨大的威脅。你被指派負責對雷戈蟲巢發動攻擊，主要目標是雷戈制奴者。\n\n在你的任務完成之後，向指揮官瑪爾利斯回報。您會收到：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n塞納里奧作戰徽章"},
[8777] = {title = "目標：雷戈掘洞者", objective = "殺死30隻雷戈掘洞者再到希利蘇斯的塞納里奧城堡向指揮官瑪爾利斯回報。你還必須將作戰任務簡報  XI帶去才能完成這個任務。", description = "這一地區猖獗的異種蟲群對我們來說是個巨大的威脅。你被指派負責對雷戈蟲巢發動攻擊，主要目標是雷戈掘洞者。\n\n在你的任務完成之後，向指揮官瑪爾利斯回報。您會收到：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n塞納里奧作戰徽章"},
[8778] = {title = "鐵爐堡軍旅需要炸藥！", objective = "把6瓶獻祭之油、5瓶哥布林火箭燃油和10份緻密炸藥粉交給希利蘇斯地區的佐拉蟲巢附近的祕術師諾斯賓。你還必須帶著後勤任務簡報 IV才能完成這個任務。", description = "鐵爐堡軍旅需要極大量…爆炸性…的零件。幫他們收集並帶給鐵爐堡軍旅的諾斯賓先生。您會收到：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n塞納里奧後勤徽章後勤指令 2"},
[8779] = {title = "占卜材料", objective = "將一個大塊魔光碎片，一個大塊強光碎片和一個巨型綠寶石帶到希利蘇斯給塞納里奧城堡的地理學家拉克斯班。你還必須將後勤任務簡報 V帶去才能完成這個任務。", description = "地理學家拉克斯班的研究對我們在希利蘇斯的運作有著極大的價值。她最近的研究需要占卜材料。\n\n請收集這些材料並直接送給她讓她繼續做研究。您會收到：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n塞納里奧後勤徽章後勤指令 2"},
[8780] = {title = "戰場上的護甲片", objective = "將8塊毛皮護甲片和8塊重型護甲片交給佐拉蟲巢附近的珍妮拉·鋼錘。你還必須將後勤任務簡報 VII帶去才能完成這個任務。", description = "鐵爐堡軍團的護甲片儲備量偏低，這很危險。去收集一些護甲片，把它們交給佐拉蟲巢附近的珍妮拉·鋼錘。您會收到：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n塞納里奧後勤徽章後勤指令 2"},
[8781] = {title = "戰場上的武器", objective = "將2把月鋼寬劍帶到佐拉蟲巢外的鐵爐堡軍旅前哨給珍妮拉·鋼錘。你還必須將後勤任務簡報 VI帶去才能完成這個任務。", description = "鐵爐堡軍旅對我們在希利蘇斯的戰鬥很有價值。不幸地，他們很多補給都在來這裡的路上弄丟了。取得一批武器並送給佐拉蟲巢附近的珍妮拉·鋼錘。您會收到：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n塞納里奧後勤徽章後勤指令 2"},
[8782] = {title = "制服供應", objective = "把1塊月布、2卷符文布卷和1份鐵網蛛絲交給喚風者普勞豪恩。你還必須將後勤任務簡報 VIII帶去才能完成這個任務。", description = "最近不斷加入的志願者導致我們制服短缺。去搜集一些制服的材料，然後把它們交給喚風者普勞豪恩。您會收到：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n塞納里奧後勤徽章後勤指令"},
[8783] = {title = "特殊的材料", objective = "帶2個附魔瑟銀錠和2塊魔化皮革到希利蘇斯給塞納里奧城堡的瓦古斯。你還必須將後勤任務簡報 IX帶去才能完成這個任務。", description = "塞納里奧城堡的鐵匠，瓦古斯，需要附魔材料為我們的軍隊製作強大的武器。取得這些東西並直接帶給瓦古斯，塞納里奧城堡的軍隊才能裝備齊全。您會收到：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n塞納里奧後勤徽章後勤指令"},
[8784] = {title = "其拉軍的秘密", objective = "將這個上古其拉神器帶給藏在神廟入口附近的巨龍。", description = "你發現了一個上古其拉神器！或許神廟入口附近的巨龍之一需要這個聖物。", progress = "你看起來容光煥發！我知道這代表什麼…", completion = "真是重大的發現，&lt;name&gt;。簡直是太驚人了！這絕對會讓我們對敵人古怪的心理多點了解。", rewards = "下列法術會施放在您身上：\n\nBrood of Nozdormu Factoin +1000"},
[8785] = {title = "奧格瑪軍團需要魔精油！", objective = "帶6瓶強力魔精油，6瓶大魔精和8瓶獻祭之油到希利蘇斯給雷戈蟲巢附近的暗影牧師珊雅。你還必須將後勤任務簡報 IV帶去才能完成這個任務。", description = "奧格瑪軍團需要幾種特別的材料。依他們在希利蘇斯的現況，我們希望能越快完成他們的要求越好。收集這些材料並帶給一個叫沙艾的食人妖。您會收到：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n塞納里奧後勤徽章後勤指令 2"},
[8786] = {title = "戰場上的武器", objective = "帶3把巨型鐵斧到雷戈蟲巢外面的奧格瑪軍團營地，交給梅洛克·遠途。你還必須將後勤任務簡報 VI帶去才能完成這個任務。", description = "奧格瑪軍團需要額外的武器，而我們現階段無法提供。取得他們要的武器並帶給雷戈蟲巢外面的梅洛克·遠途。您會收到：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n塞納里奧後勤徽章後勤指令 2"},
[8787] = {title = "戰場上的護甲片", objective = "帶8塊毛皮護甲片和8塊重型護甲片給雷戈蟲巢附近的梅洛克·遠途。你還必須將後勤任務簡報 VII帶去才能完成這個任務。", description = "奧格瑪軍團英勇的志願者已來到希利蘇斯幫助我們作戰。但是他們的護甲片補給在旅途中弄丟了。取得一批護甲片並送到奧格瑪軍團營地給梅洛克·遠途。您會收到：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n塞納里奧後勤徽章後勤指令 2"},
[8788] = {title = "輕輕搖晃過的禮物", objective = "這個禮物看起來好像已經被稍微搖晃過了幾次。上面的標籤寫著：\n\n給最特別的<race><class>。", description = nil, progress = nil, completion = nil, rewards = "您會收到：\n\n輕輕搖晃過的禮物"},
[8789] = {title = "其拉帝王武器", objective = "把其拉帝王武器和3塊元素礦石交給安其拉神廟的亞雷戈斯，他會幫你製作使用元素強化過的武器。", description = "一千年以來，我們並沒有無所事事地留在這裡，<name>。\n\n我們在這裡看著這些野獸耗盡資源時自相殘殺，我們傾聽了它們密謀毀滅我們世界的談話，我們還知道了它們內部的爭權奪利。\n\n它們確實有弱點，而且我們知道如何利用這些弱點。\n\n找到它們在葬禮儀祭上陪葬用的武器，把它和他們自己的神所創造的最強的元素融合在一起，我們就可以創造出終結一切的武器。您能選擇下列奬勵中的一個：\n\n", progress = nil, completion = nil, rewards = "您能選擇下列奬勵中的一個：\n\n神聖其拉戰斧神聖其拉火槍神聖其拉短劍神聖其拉壁壘"},
[8790] = {title = "其拉帝王徽記", objective = "如果你能將其拉帝王徽記和3個元素礦石帶給安其拉神廟夢境之龍麥琳瑟拉，她就能為你製作注入元素的武器。", description = "亞雷戈斯認為，在一定情況下，因上古之神和他們的僕從之間的衝突而產生的元素也會對他們本身造成致命的威脅。凡人，我指的是源質。\n\n將其拉帝王徽記和這種元素交給我，我就能為武器注入巨大的力量。您能選擇下列奬勵中的一個：\n\n", progress = nil, completion = nil, rewards = "您能選擇下列奬勵中的一個：\n\n神聖其拉侍僧法杖神聖其拉占卜法杖神聖其拉戰錘"},
[8791] = {title = "奧斯里安的墮落", objective = "將無疤者奧斯里安的頭顱帶到希利蘇斯給塞納里奧城堡的指揮官瑪爾利斯。", description = "來自其拉蟲群的威脅已經被平息。可怕的奧斯里安已被擊敗。\n\n卡林多的居民知道這樣的勝利都會如釋重負。將奧斯里安的頭顱帶給塞納里奧城堡的指揮官瑪爾利斯。您能選擇下列奬勵中的一個：\n\n", progress = nil, completion = nil, rewards = "您能選擇下列奬勵中的一個：\n\n流沙護符流沙咒符流沙頸飾流沙墜飾\n\n您還會收到：\n\n"},
[8792] = {title = "部落需要你的幫助！", objective = "和奧格瑪精神谷的督軍格爾庫克交談。", description = "嗨，我很高興你決定聽我說完。部落需要所有的幫助來準備安其拉之戰，這表示我們需要你！連我們現在講話的時候，都有官方的收集者在收集戰爭需要的材料，但是我們沒有你的幫助仍然無法達到目標，<name>！\n\n你應該去和負責的督軍格爾庫克談談。你看怎麼樣 <class>？你會幫我們做這重要的準備嗎？您會收到：\n\n完成任務後，你將獲得：\n\n", progress = nil, completion = nil, rewards = "您會收到：\n\n"},
