[407975] = {name = "狂野打击", additional_info = "瞬发\n[q]使20码内的小队成员凶悍地战斗。每次近战命中有20%的几率使攻击者以20%的额外攻击强度再进行一次攻击。对已受到风怒图腾影响的小队成员没有效果。\n（特效触发几率：20%，1.5秒 冷却）"},
[407977] = {name = "狂野打击", additional_info = "[q]在猎豹形态、熊形态或巨熊形态下，使100码内的小队或团队成员凶悍地战斗。每次近战命中有20%的几率使攻击者以20%的额外攻击强度再进行一次攻击。对已受到风怒图腾影响的目标没有效果。"},
[407988] = {name = "野蛮咆哮", additional_info = "25能量 100 码范围\n瞬发\n[q]终结技，使猎豹形态下的物理伤害提高30%。根据连击点数决定持续时间：\n 1点：14秒\n 2点：19秒\n 3点：24秒\n 4点：29秒\n 5点：34秒"},
[407989] = {name = "野蛮咆哮", additional_info = "瞬发\n需要 豹形态\n[q]终结技，使物理伤害提高30%。根据连击点数决定持续时间：\n 1点：14秒\n 2点：19秒\n 3点：24秒\n 4点：29秒\n 5点：34秒"},
[407993] = {name = "割碎", additional_info = "40能量 近战范围\n瞬发\n需要 豹形态\n[q]割碎目标躯体，造成270%的普通伤害，并使流血效果和撕碎对其造成的伤害提高30%，效果持续1分钟。奖励1个连击点数。所有影响爪击、槌击或由二者触发的效果都可对该技能生效。"},
[407995] = {name = "割碎", additional_info = "15怒气 近战范围\n瞬发 6秒 冷却\n需要 熊形态, 巨熊形态\n[q]割碎目标躯体，造成160%的普通伤害，并使流血效果和撕碎对其造成的伤害提高30%，效果持续1分钟。所有影响爪击、槌击或由二者触发的效果都可对该技能生效。\n\n 割碎（熊）击中目标时，高于( 60 * 5)的每一点防御技能还会使你的攻击强度提高4点，持续15秒。"},
