[400080] = {name = "紋刻護胸 - 致命毒釀", additional_info = "3秒 施法時間\n需要 胸, 胸\n[q]在你的護胸或長袍上紋刻致命毒釀符文：\n\n\n賦予你的毒藥數種改良：\n\n 當你對目標施加其他毒藥時還會施加致命毒藥。\n\n 若你的武器沒有施加毒藥，則會像施加了速效毒藥一般，有機率觸發速效毒藥。\n\n 現在致命毒藥和速效毒藥的傷害會被你的攻擊強度提高。\n\n 現在你的致命毒藥可以在受另一個盜賊的致命毒藥影響的目標身上觸發。"},
[400081] = {name = "紋刻束褲 - 正中眉心", additional_info = "3秒 施法時間\n需要 褲子\n[q]在你的束褲上紋刻正中眉心符文：\n\n\n遠程終結技，根據連擊點數決定造成的傷害，以攻擊強度來提升，並擊昏目標：\n\n 1點：[53 / 100 * ((5.741530 - 0.255683 * 60 + 0.032656 * 60 * 60) + (8.740728 - 0.415787 * 60 + 0.051973 * 60 * 60))]至[81 / 100 * ((5.741530 - 0.255683 * 60 + 0.032656 * 60 * 60) + (8.740728 - 0.415787 * 60 + 0.051973 * 60 * 60))]點傷害，擊昏1秒\n 2點：[53 / 100 * ((5.741530 - 0.255683 * 60 + 0.032656 * 60 * 60) + (8.740728 - 0.415787 * 60 + 0.051973 * 60 * 60) * 2)]至[81 / 100 * ((5.741530 - 0.255683 * 60 + 0.032656 * 60 * 60) + (8.740728 - 0.415787 * 60 + 0.051973 * 60 * 60) * 2)]點傷害，擊昏2秒\n 3點：[53 / 100 * ((5.741530 - 0.255683 * 60 + 0.032656 * 60 * 60) + (8.740728 - 0.415787 * 60 + 0.051973 * 60 * 60) * 3)]至[81 / 100 * ((5.741530 - 0.255683 * 60 + 0.032656 * 60 * 60) + (8.740728 - 0.415787 * 60 + 0.051973 * 60 * 60) * 3)]點傷害，擊昏3秒\n 4點：[53 / 100 * ((5.741530 - 0.255683 * 60 + 0.032656 * 60 * 60) + (8.740728 - 0.415787 * 60 + 0.051973 * 60 * 60) * 4)]至[81 / 100 * ((5.741530 - 0.255683 * 60 + 0.032656 * 60 * 60) + (8.740728 - 0.415787 * 60 + 0.051973 * 60 * 60) * 4)]點傷害，擊昏4秒\n 5點：[53 / 100 * ((5.741530 - 0.255683 * 60 + 0.032656 * 60 * 60) + (8.740728 - 0.415787 * 60 + 0.051973 * 60 * 60) * 5)]至[81 / 100 * ((5.741530 - 0.255683 * 60 + 0.032656 * 60 * 60) + (8.740728 - 0.415787 * 60 + 0.051973 * 60 * 60) * 5)]點傷害，擊昏5秒\n\n 與腎擊共用冷卻時間。"},
[400082] = {name = "紋刻護胸 - 不過是點皮肉傷", additional_info = "3秒 施法時間\n需要 胸, 胸\n[q]在你的護胸或長袍上紋刻不過是點皮肉傷符文：\n\n\n你在劍刃之舞生效期間所受到的物理傷害降低20%，並根據超過( 60 * 5)點的每12點防禦技能額外降低1%。此外，你的閃躲機率會乘以50%，你受到近戰致命一擊的機率降低6%，你造成的傷害降低20%，但你所有行動生成的威脅值會大幅提升，而且你的佯攻技能會取代為戲弄，使目標受到嘲諷並攻擊你。\n\n\n嘲諷目標，使其向你發起攻擊，但對已經攻擊你的目標無效。"},
[400093] = {name = "紋刻長靴 - 重拳連發", additional_info = "3秒 施法時間\n需要 鞋子\n[q]在你的靴子上紋刻重拳連發符文：\n\n\n每次你閃躲或招架，你的生命力便會提高5%，最多可疊加5次。"},
[400094] = {name = "紋刻手套 - 截肢", additional_info = "3秒 施法時間\n需要 手套"},
[400095] = {name = "紋刻護胸 - 拔槍快手", additional_info = "3秒 施法時間\n需要 胸, 胸"},
[400096] = {name = "紋刻腰帶 - 投擲手裏劍", additional_info = "3秒 施法時間\n需要 腰帶\n[q]在你的腰帶上紋刻投擲手裏劍符文：\n\n\n對你的敵人投擲手裏劍，造成等同於你攻擊強度50%的傷害，還會額外擊中最多4個鄰近目標。每個命中的目標獎勵1個連擊點數。"},
[400099] = {name = "紋刻束褲 - 劍刃之舞", additional_info = "3秒 施法時間\n需要 褲子\n[q]在你的束褲上紋刻劍刃之舞符文：\n\n\n終結技，使你的招架機率提高10%，並根據超過( 60 * 5)點的每點防禦技能賦予你4點攻擊強度。根據連擊點數決定持續時間：\n\n 1點：14秒\n 2點：18秒\n 3點：22秒\n 4點：26秒\n 5點：30秒"},
[400101] = {name = "紋刻腰帶 - 暗影閃現", additional_info = "3秒 施法時間\n需要 腰帶\n[q]在你的腰帶上紋刻暗影閃現符文：\n\n\n嘗試穿過暗影然後出現在敵人背後，並使移動速度提高70%，持續3秒。"},
[400102] = {name = "紋刻束褲 - 毒化", additional_info = "3秒 施法時間\n需要 褲子\n[q]在你的束褲上紋刻毒化符文：\n\n\n終結技，根據目標身上疊加的致命毒藥效果立即造成毒傷。使用毒化後，你在1秒再加上每個連擊點數可延長1秒的時間內，對目標發動速效毒藥的機率提高75%。每1點連擊點數能啟動1劑：\n 1劑：[((5.741530 - 0.255683 * 60 + 0.032656 * 60 * 60) * 80 / 100) * 1 + Attack power * 0.072]點傷害\n 2劑：[((5.741530 - 0.255683 * 60 + 0.032656 * 60 * 60) * 80 / 100) * 2 + Attack power * 0.144]點傷害\n 3劑：[((5.741530 - 0.255683 * 60 + 0.032656 * 60 * 60) * 80 / 100) * 3 + Attack power * 0.216]點傷害\n 4劑：[((5.741530 - 0.255683 * 60 + 0.032656 * 60 * 60) * 80 / 100) * 4 + Attack power * 0.288]點傷害\n 5劑：[((5.741530 - 0.255683 * 60 + 0.032656 * 60 * 60) * 80 / 100) * 5 + Attack power * 0.36]點傷害"},
[400104] = {name = "紋刻束褲 - 暗影閃現", additional_info = "3秒 施法時間\n需要 褲子\n[q]在你的束褲上紋刻暗影閃現符文：\n\n\n嘗試穿過暗影然後出現在敵人背後，並使移動速度提高70%，持續3秒。"},
[400105] = {name = "紋刻手套 - 暗影打擊", additional_info = "3秒 施法時間\n需要 手套\n[q]在你的手套上紋刻暗影打擊符文：\n\n\n傳送至目標後方打擊，對目標造成150% 武器傷害。必須潛行才能使用。獎勵1點連擊點數。"},
[400573] = {name = "秘法衝擊", additional_info = "瞬發 3分鐘 冷卻\n[q]以能量衝擊目標，對其造成秘法傷害。每次施放秘法衝擊時會使你所有的秘法法術傷害和治療效果提高15%，並且秘法衝擊的法力消耗提高175%。此效果最多可堆疊4次，持續6秒。"},
[400574] = {name = "秘法衝擊", additional_info = "7% 法力 值 30 碼範圍\n2.5秒 施法時間\n[q]以能量衝擊目標，對其造成[(13.828124 + 0.018012 * 60 + 0.044141 * 60 * 60) * 453 / 100]點至[(13.828124 + 0.018012 * 60 + 0.044141 * 60 * 60) * 527 / 100]點秘法傷害。每次施放秘法衝擊時會使你所有的秘法法術傷害提高15%，並且秘法衝擊的法力消耗提高175%。此效果最多可堆疊4次，持續6秒或直到你施放秘法衝擊以外的秘法傷害法術為止。"},
[400586] = {name = "秘法衝擊", additional_info = "瞬發 3分鐘 冷卻\n[q]以能量衝擊目標，對其造成秘法傷害。每次施放秘法衝擊時會使你所有其他的秘法法術傷害提高15%，並且秘法衝擊的法力消耗提高175%。此效果最多可堆疊4次，持續6秒。"},
[400588] = {name = "飛彈彈幕", additional_info = "[q]使你的秘法衝擊有40%的機率、而你的火球術和寒冰箭法術有20%的機率使你下一個秘法飛彈法術的引導時間縮短50%，法力消耗降低100%，且每0.5秒發射一發飛彈。\n（特效觸發機率：50%）"},
[400589] = {name = "飛彈彈幕", additional_info = "瞬發\n[q]使你的秘法衝擊、火球術和寒冰箭法術有一定的機率令下一個秘法飛彈的引導時間縮短50%，並且法力消耗降低100%，每0.5秒發射一發飛彈。"},
[400610] = {name = "秘法彈幕", additional_info = "8% 法力 值 30 碼範圍\n瞬發 3秒 冷卻\n[q]對敵方目標發射數發飛彈，造成[(13.828124 + 0.018012 * 60 + 0.044141 * 60 * 60) * 358 / 100]點至[(13.828124 + 0.018012 * 60 + 0.044141 * 60 * 60) * 438 / 100]點秘法傷害。此一法術還有25%機率觸發飛彈彈幕。"},
[400613] = {name = "活體爆彈", additional_info = "22% 法力 值 35 碼範圍\n瞬發"},
[400614] = {name = "活體爆彈", additional_info = "100 碼範圍\n瞬發\n[q]令目標化身為活體爆彈，直到主動取消內受到[(13.828124 + 0.018012 * 60 + 0.044141 * 60 * 60) * 85 / 100 * 4]點火焰傷害。在直到主動取消後或是法術被驅散後產生大爆炸，對10碼範圍內的所有敵人造成[(13.828124 + 0.018012 * 60 + 0.044141 * 60 * 60) * 171 / 100]點火焰傷害。"},
[400615] = {name = "過熱", additional_info = "[q]火焰衝擊都會造成致命一擊，並可在施放另一個法術時施放，且不會受到共用冷卻時間的影響，但其冷卻時間延長至15秒。"},
[400616] = {name = "火焰衝擊", additional_info = "115法力 20 碼範圍\n瞬發 15秒 冷卻\n[q]施放火焰衝擊敵人，對其造成 110 to 134點火焰傷害。"},
[400618] = {name = "火焰衝擊", additional_info = "40法力 20 碼範圍\n瞬發 15秒 冷卻\n[q]施放火焰衝擊敵人，對其造成 27 to 35點火焰傷害。"},
[400619] = {name = "火焰衝擊", additional_info = "75法力 20 碼範圍\n瞬發 15秒 冷卻\n[q]施放火焰衝擊敵人，對其造成 62 to 76點火焰傷害。"},
