[401556] = {name = "活化烈焰", additional_info = "11% 法力 值 35 碼範圍\n瞬發 30秒 冷卻\n[q]召喚一個會向目標移動的法火烈焰，並留下一條法火路徑。這個路徑會每秒會對鄰近敵人造成[(13.828124 + 0.018012 * 60 + 0.044141 * 60 * 60) * 100 / 100]點法火傷害，持續10秒。"},
