[415449] = {name = "腰部符文技能", additional_info = "瞬發\n[q]裝備一個刻有符文的腰部物品來啟用這個欄位中的一個技能。"},
[415450] = {name = "腳部符文技能", additional_info = "瞬發\n[q]裝備一個刻有符文的腳部物品來啟用這個欄位中的一個技能。"},
[415452] = {name = "強化揮擊", additional_info = "[q]獲得強化揮擊技能：\n\n\n你在獵豹形態下，揮擊技能會變成揮擊(獵豹形態)。而在熊形態下，你的揮擊技能最多會額外打擊7名敵人。\n\n 揮擊(獵豹形態)\n\n攻擊附近的敵人，對它們造成250%的武器傷害，並對你當前的目標生成1個連擊點數。"},
[415457] = {name = "飛舞刀刃", additional_info = "[q]獲得飛舞刀刃技能：\n\n\n立即將手持的兩把武器擲向8碼內的所有目標，匕首武器可造成75%的武器傷害，其他武器可造成50%的武器傷害。"},
[415459] = {name = "法術能量", additional_info = "[q]獲得法術能量技能：\n\n\n使你所有法術的致命一擊傷害加成提高50%。"},
[415460] = {name = "燃盡", additional_info = "[q]獲得燃盡技能：\n\n\n使你所有法術的法術致命一擊機率提高15%，但你的非週期性法術致命一擊現在會額外消耗1%的基礎法力。"},
[415467] = {name = "群體恢復", additional_info = "[q]獲得群體恢復技能：\n\n\n在3秒內治療目標玩家43.5碼範圍內的所有隊伍成員[(38.258376 + 0.904195 * 60 + 0.161311 * 60 * 60) * 55 / 100 * 3]點生命力，並對每個目標施加時空信標效果，持續15秒。\n\n 時空信標\n\n記下目標的時空位置。施法者造成的所有秘法傷害的70%會轉化成時光治療，並治療施法者當前的每個時空信標目標。對自身的治療效果降低50%，且當傷害是由傷害複數目標的秘法法術造成時，治療效果也會降低80%。"},
[415481] = {name = "苦痛受難", additional_info = "[q]獲得苦痛受難技能：\n\n\n心靈震爆、心靈鑽刺與精神鞭笞會使你目標身上的暗言術：痛、虛無瘟疫或吸血之觸其中之一重置至持續時間上限。剩餘持續時間最短的技能必定會優先被重置。"},
[415596] = {name = "精通盾牌", additional_info = "[q]獲得精通盾牌技能："},
[415598] = {name = "鬥士姿態", additional_info = "[q]獲得鬥士姿態技能：\n\n\n一個攻擊型的姿態，能使你在裝備盾牌時的傷害提高10%、格檔機率提高10%，但會使你的護甲值降低30%、產生的威脅值降低30%。此外，你的自動攻擊對並未以你為目標的敵人造成傷害時，獲得的怒氣提高50%。\n\n 採取鬥士姿態時若裝備盾牌，所有在其他姿態下才能使用的技能仍可以使用。"},
[415599] = {name = "一心狂怒", additional_info = "[q]獲得一心狂怒技能：\n\n\n使用雙武器時，你的移動速度提高10%，且每當你的近戰自動攻擊命中與前一次自動攻擊相同的目標時，獲得4%攻擊速度，最多可堆疊5次，持續10秒，或是直到你的自動攻擊命中不同的目標。"},
[415604] = {name = "無盡痛苦", additional_info = "[q]獲得無盡痛苦技能：\n\n\n吸取生命、吸取靈魂、暗影箭、暗影順劈斬、灼熱之痛、燒盡和蝕魂術會使你目標上的腐化持續時間刷新至最大持續時間。"},
[415605] = {name = "惡魔策略", additional_info = "[q]獲得惡魔策略技能：\n\n\n使你和你寵物的近戰和法術致命一擊機率提高10%。"},
[415703] = {name = "崇聖護盾", additional_info = "[q]獲得崇聖護盾技能：\n\n\n每當目標受到傷害時將獲得聖潔之盾，可吸收[36 * (38.258376 + 0.904195 * 60 + 0.161311 * 60 * 60) / 100]點傷害並且使聖騎士的聖光閃現造成極效治療效果的機率提高50%，最多可持續6秒。此外，使你的聖光閃現在治療擁有聖潔之盾的目標時，治療效果會在12秒內額外提高100%。此效果每6秒只會生效一次，持續1分鐘。此法術同一時間只能影響一個目標。"},
[415706] = {name = "戰爭藝術", additional_info = "[q]獲得戰爭藝術技能：\n\n\n你的近戰致命一擊會使驅邪術剩餘的冷卻時間縮短2秒，並使驅邪術的法力消耗降低80%。"},
[415709] = {name = "適者生存", additional_info = "[q]獲得適者生存技能：\n\n\n使你被近戰攻擊時造成致命一擊的機率降低6%，並使你受到的所有傷害降低10%。在熊形態或巨熊形態下受到的傷害會額外降低10%。"},
[415712] = {name = "能量激增", additional_info = "[q]獲得能量激增技能：\n\n\n 每次烈焰震擊造成傷害，便有5%機率重置熔岩爆發和閃電鏈的冷卻時間，並且會使你在10秒內施放的下一個熔岩爆發或閃電鏈變為瞬發法術。每次激流造成治療，便有5%機率使你在10秒內施放的下一個治療鍊變為瞬發法術。此外，你每5秒獲得相當於你智力15%的法力。"},
[415713] = {name = "心靈迅敏", additional_info = "[q]獲得心靈迅敏技能：\n\n\n以風暴打擊造成傷害會使你的攻擊強度提高相當於你智力的150%，並且使你的法術傷害提高相當於你總攻擊強度的35%，持續30秒。"},
[415714] = {name = "治癒之雨", additional_info = "[q]獲得治癒之雨技能：\n\n\n選擇目標玩家，並每秒為其15碼範圍內的所有目標玩家小隊成員治療[(29.888200 + 0.690312 * 60 + 0.136267 * 60 * 60) * 15 / 100]點生命力。"},
[415717] = {name = "靈貓迅閃", additional_info = "[q]獲得靈貓迅閃技能：\n\n\n使你的閃躲機率提高20%，寵物的閃躲機率提高9%。此外，側翼攻擊技能的冷卻時間縮短50%。"},
[415718] = {name = "蝮蛇守護", additional_info = "[q]獲得蝮蛇守護技能：\n\n\n獵人獲得蝮蛇守護，使其遠程和近戰自動攻擊可恢復法力，但造成的總傷害降低10%。此外，你每3秒可獲得法力上限10%的法力。獲得的法力值依武器攻速而定。獵人在同一時間內只能啟動一種守護。"},
[415719] = {name = "蓄勢待發", additional_info = "[q]獲得蓄勢待發技能：\n\n\n每當你放置一個陷阱，你下一次於20秒內使用的射擊(驅散射擊除外)技能不會消耗法力，且不會有冷卻時間。"},
[415720] = {name = "擊殺射擊", additional_info = "[q]獲得擊殺射擊技能：\n\n\n當你試著擊殺受傷的目標時會發射一次遠程攻擊，並造成100%武器傷害外加[587 / 100 * 100 / 100 * (2.976264 + 0.641066 * 60 + 0.022519 * 60 * 60)]點傷害。擊殺射擊沒有最小範圍。若對生命力低於20%的敵人使用擊殺射擊，該技能的冷卻時間便會重置。"},
[415723] = {name = "近戰專家", additional_info = "[q]獲得近戰專家技能：\n\n\n 猛禽一擊現在成為瞬發法術，且貓鼬撕咬和猛禽一擊每次攻擊有40%的機率重置貓鼬撕咬和猛禽一擊的冷卻時間。"},
[415725] = {name = "埋伏", additional_info = "[q]獲得埋伏技能：\n\n\n你的伏擊和背刺造成致命一擊時會使目標失去平衡，使他們的近戰攻擊間隔時間延長10%，並使其移動速度降低50%，持續8秒。"},
[415729] = {name = "啟蒙", additional_info = "[q]獲得啟蒙技能：\n\n\n當你的法力高於70%時，造成的傷害提高10%。在法力低於30%時施法，會保持10%的法力恢復速度。"},
[415734] = {name = "法霜箭", additional_info = "[q]獲得法霜箭技能：\n\n\n向目標射出一支法霜箭，造成[(13.828124 + 0.018012 * 60 + 0.044141 * 60 * 60) * 305 / 100 * ( 1 ) ]到[(13.828124 + 0.018012 * 60 + 0.044141 * 60 * 60) * 355 / 100 * ( 1 ) ]點法霜傷害並降低移動速度40%，持續9秒。此法術將會檢視目標的冰霜與秘法抗性，依照較低的一方造成傷害，並同時視為冰霜和秘法傷害。"},
[415737] = {name = "機緣回復", additional_info = "[q]獲得機緣回復技能：\n\n\n使用快速治療會使你下一次的次級治療術、治療術、強效治療術或治療禱言的施法時間縮短20%，持續20秒，並可最多疊加3次。"},
[415738] = {name = "心靈烙印", additional_info = "[q]獲得心靈烙印技能：\n\n\n在敵方目標的周圍引發暗影能量的大爆炸，對目標敵人和附近的其他敵人造成傷害，每1秒對所有位於目標10碼範圍內的所有敵人造成[70 / 100 * (9.456667 + 0.635108 * 60 + 0.039063 * 60 * 60)]點至[78 / 100 * (9.456667 + 0.635108 * 60 + 0.039063 * 60 * 60)]點暗影傷害，持續6秒。"},
[415739] = {name = "靈魂之力", additional_info = "[q]次級治療術、治療術、強效治療術與快速治療會使其治療目標身上的虛弱靈魂剩餘持續時間縮短2秒。此外，你真言術：盾的目標受到傷害時，就算該傷害會被吸收，目標也會獲得怒氣，且真言術：盾吸收的傷害也會如同治療效果般觸發正義之怒。"},
[415740] = {name = "靈魂之力", additional_info = "[q]獲得靈魂之力技能：\n\n\n次級治療術、治療術、強效治療術與快速治療會使其治療目標身上的虛弱靈魂剩餘持續時間縮短2秒。此外，你真言術：盾的目標受到傷害時，就算該傷害會被吸收，目標也會獲得怒氣，且真言術：盾吸收的傷害也會如同治療效果般觸發正義之怒。"},
[415743] = {name = "鮮血澎湃", additional_info = "[q]獲得鮮血澎湃技能：\n\n\n 英勇打擊、嗜血術和旋風斬有30%機率使你在15秒內施放的下一個猛擊變成瞬發法術、使用兩把近戰武器打擊，且不消耗怒氣。"},
[415744] = {name = "精準時機", additional_info = "[q]獲得精準時機技能：\n\n\n 猛擊現在是瞬發法術，但有6秒的冷卻時間。"},
[415745] = {name = "集中怒氣", additional_info = "[q]獲得集中怒氣技能：\n\n\n使你攻擊技能所消耗的怒氣減少3點。"},
[415749] = {name = "惡魔知識", additional_info = "[q]獲得惡魔知識技能：\n\n\n使你的法術傷害和治療效果提高相當於你目前寵物的總耐力加上智力的3%。"},
[415750] = {name = "燒盡", additional_info = "[q]獲得燒盡技能：\n\n\n灼燒敵人，造成[222 / 100 * (6.568597 + 0.672028 * 60 + 0.031721 * 60 * 60)]至[258 / 100 * (6.568597 + 0.672028 * 60 + 0.031721 * 60 * 60)]點傷害，並使你在接下來15秒內造成的所有火焰傷害提高40%。"},
[415751] = {name = "邪惡之舞", additional_info = "[q]獲得邪惡之舞技能：\n\n\n每次你對一名敵人造成致命一擊，你和你的惡魔寵物便會獲得等同於70%你法術致命一擊機率的閃躲機率，兩者還會恢復最大法力上限2%的法力。"},
[415755] = {name = "聖光守護", additional_info = "[q]獲得聖光守護技能：\n\n\n每次使用近戰武器擊中目標時，使你每3秒恢復5%的最大法力，持續15秒，但在此法力恢復期間內你的治療法術的治療量降低50%。"},
[415756] = {name = "精通光環", additional_info = "[q]獲得精通光環技能：\n\n\n使你的專注光環可使所有受影響的目標免疫沉默以及中斷的效果，並且使其他所有光環的效果提高100%。持續6秒。"},
[415759] = {name = "碎顱猛擊", additional_info = "[q]獲得碎顱猛擊技能：\n\n\n衝向13碼內的一名目標並重擊目標頭部，中斷其正在施放的法術，並使同系的所有法術在2秒內皆無法施放。與野性衝鋒共用冷卻時間。"},
[415760] = {name = "割裂", additional_info = "[q]獲得割裂技能：\n\n\n割裂敵方目標，在15秒內使他們出血造成[(9.183105 + 0.616405 * 60 + 0.028608 * 60 * 60) * 20 / 100 * 5]點傷害，且目標身上每有一層割裂疊加便會外加20%武器傷害，同時造成大量的威脅值。此效果可對同一目標疊加最多5次。"},
[415761] = {name = "生命種子", additional_info = "[q]獲得生命種子技能：\n\n\n當你的任何非週期性治療法術造成極效治療效果時，會在目標身上種下相當於該法術50%治療量的生命種子。當目標下一次受到傷害或是獲得非週期性治療時，生命種子會綻放，持續15秒。"},
[415762] = {name = "伊露恩之火", additional_info = "[q]獲得伊露恩之火技能：\n\n\n你的一些法術和技能會延長其目標身上的持續性傷害和治療效果的持續時間：\n\n 星火術會使月火術延長6秒。\n 憤怒會使日炎術延長3秒。\n 癒合會使回春術延長6秒。\n 撕碎會使撕扯延長2秒。\n\n 每個效果最多只能延長至初始的持續時間。"},
[415768] = {name = "燃燒", additional_info = "[q]獲得燃燒技能：\n\n\n你的烈焰震擊現在最多會打擊5個目標，其持續時間延長6秒，且造成的傷害提高100%。此外，當你裝備的主手武器帶有火舌武器效果時會提高你的法術傷害，其數值相當於你智力的150%。"},
[415813] = {name = "強效鬼魂之狼", additional_info = "[q]你的鬼魂之狼現在能在室內使用，並且在生效期間使你受到的所有傷害降低10%。"},
