[203745] = {name = "[q2]주문 기록: 얼음창", additional_info = "획득 시 귀속\n고유 아이템\n각인술\n(1) 필요\n[q2]장갑에 얼음창 룬을 새깁니다.\n\n\n적에게 [(13.828124 + 0.018012 * 60 + 0.044141 * 60 * 60) * 55 / 100]에서 [(13.828124 + 0.018012 * 60 + 0.044141 * 60 * 60) * 65 / 100]의 냉기 피해를 입힙니다. 얼어붙은 대상에게는 3배의 피해를 입힙니다."},
[203746] = {name = "[q2]주문 기록: 살아있는 불꽃", additional_info = "획득 시 귀속\n고유 아이템\n각인술\n(1) 필요\n[q2]바지에 살아있는 불꽃 룬을 새깁니다.\n\n\n대상 방향으로 이동하는 비전화염 불꽃을 소환합니다. 불꽃은 비전화염의 흔적을 남깁니다. 흔적은 주위의 적에게 1초마다 [(13.828124 + 0.018012 * 60 + 0.044141 * 60 * 60) * 100 / 100]의 비전화염 피해를 입힙니다. 20 sec 동안 지속됩니다."},
[203747] = {name = "[q2]주문 기록: 서리의 손가락", additional_info = "획득 시 귀속\n고유 아이템\n각인술\n(1) 필요\n[q2]가슴보호구 또는 로브에 서리의 손가락 룬을 새깁니다.\n\n\n빙결 효과를 일으키면 15%의 확률로 서리의 손가락 효과를 받아, 자신이 시전하는 다음 2회의 주문은 얼어붙은 대상을 공격하는 효과를 내도록 합니다. 15 sec 동안 지속됩니다."},
[203748] = {name = "[q2]주문 기록: 완전 연소", additional_info = "획득 시 귀속\n고유 아이템직업: 마법사\n[q2]가슴보호구에 완전 연소 룬을 새깁니다.\n\n\n모든 주문의 주문 극대화율이 15%만큼 증가하지만 비주기적인 주문이 극대화 효과를 발휘할 때 기본 마나의 1%를 추가로 소모합니다."},
