[205979] = {name = "[q2]측방 공격의 룬", additional_info = "획득 시 귀속\n고유 아이템직업: 사냥꾼\n[q2]바지에 측방 강타 룬을 새깁니다.\n\n\n시전자와 야수가 즉시 근접 공격력의 100%에 해당하는 피해를 입힙니다. 이후 10 sec 동안 살쾡이의 이빨과 랩터의 일격의 공격력이 10%만큼 증가합니다. 최대 3번까지 중첩됩니다. 랩터의 일격 사용 시 20%의 확률로 측방 강타의 재사용 대기시간이 초기화됩니다."},
[205995] = {name = "땅다람쥐 사향", additional_info = "직업: 사냥꾼\n[q2]사용 효과: 야수 길들이기 능력을 땅다람쥐에게도 사용할 수 있습니다. (애완동물 친구로 획득) (10 분 재사용 대기시간)"},
