[206469] = {name = "초원꽃", additional_info = "직업: 드루이드\n[q2]사용 효과: 초원꽃 3개를 결합하여 왕관을 만듭니다."},
[206954] = {name = "[q2]곰의 분노 우상", additional_info = "획득 시 귀속\n고유 아이템성물 우상\n직업: 드루이드\n요구 레벨 10\n[q2]장갑에 짓이기기 룬을 새깁니다.\n\n\n대상을 짓이겨 일반 공격력의 160%에 해당하는 피해를 입히고 1 min 동안 출혈 효과 및 칼날 발톱으로 받는 피해를 30%만큼 추가로 증가시킵니다. 이 능력은 할퀴기 및 후려치기와 관련된 모든 이로운 효과를 적용 및 발동시킵니다."},
[206963] = {name = "[q2]자연의 잠재력의 룬", additional_info = "획득 시 귀속\n고유 아이템직업: 드루이드\n[q2]가슴보호구 또는 로브에 살아있는 씨앗 룬을 새깁니다.\n\n\n치유 주문이 극대화 효과를 발휘하면 대상에게 치유량의 50%에 해당하는 생명력을 회복시키는 살아있는 씨앗을 심습니다. 살아있는 씨앗은 다음에 대상이 피해를 받거나 비주기적인 치유를 받으면 피어납니다. 15 sec 동안 지속됩니다."},
[206966] = {name = "사티로스풀 구근", additional_info = "획득 시 귀속\n고유 아이템"},
[206970] = {name = "[q2]생명의 룬", additional_info = "획득 시 귀속\n고유 아이템직업: 드루이드\n[q2]바지에 피어나는 생명 룬을 새깁니다.\n\n회복 및 피어나는 생명의 전역 재사용 대기시간이 0.5초만큼 감소하고 피어나는 생명 능력을 얻습니다.\n\n\n7 sec에 걸쳐 대상의 생명력을 [4 / 100 * 7 * (38.949830 + 0.606705 * 60 + 0.167780 * 60 * 60)]만큼 회복시킵니다. 피어나는 생명의 지속시간이 다하거나 무효화되면 대상은 그 즉시 [57 / 100 * (38.949830 + 0.606705 * 60 + 0.167780 * 60 * 60)]의 생명력을 회복하고 드루이드는 주문 시전에 소모한 자원의 절반을 회복합니다. 이 효과는 같은 대상에 대해 3번까지 중복될 수 있습니다."},
[206975] = {name = "유물 보관함 열쇠", additional_info = "고유 아이템"},
[206985] = {name = "[q2]우두머리의 메아리", additional_info = "획득 시 귀속\n고유 아이템직업: 주술사\n요구 레벨 3\n[q2]장화에 우두머리의 영혼 룬을 새깁니다.\n\n\nInfuses the target with the spirit of an alpha wolf, increasing all threat generated by the target by 45% for 30 min. Limit 1 target. If cast on a target other than self, the Shaman also gains Loyal Beta, increasing Attack Power by 20% for 30 min. A target cannot have both Spirit of the Alpha and Loyal Beta at the same time."},
[206989] = {name = "[q2]태양의 룬", additional_info = "획득 시 귀속\n고유 아이템직업: 드루이드\n[q2]장갑에 태양섬광 룬을 새깁니다.\n\n\n적을 불태워 [130 * (9.183105 + 0.616405 * 60 + 0.028608 * 60 * 60) / 100]에서 [152 * (9.183105 + 0.616405 * 60 + 0.028608 * 60 * 60) / 100]의 자연 피해를 입히고 12 sec에 걸쳐 [65 * 4 * (9.183105 + 0.616405 * 60 + 0.028608 * 60 * 60) / 100]의 추가 자연 피해를 입힙니다.\n\n 추가로 곰 변신 또는 표범 변신 상태일 때 태양 섬광 능력을 얻고, 달빛 섬광이 태양 섬광 (곰) 또는 태양섬광 (표범)으로 대체됩니다. 이 주문은 달빛 섬광과 관련된 모든 이로운 효과를 적용 및 발동시킵니다.\n\n 태양섬광 (곰)\n적을 불태워 [130 * (9.183105 + 0.616405 * 60 + 0.028608 * 60 * 60) / 100]에서 [152 * (9.183105 + 0.616405 * 60 + 0.028608 * 60 * 60) / 100]의 자연 피해를 입히고 12 sec에 걸쳐 [65 * 4 * (9.183105 + 0.616405 * 60 + 0.028608 * 60 * 60) / 100]의 추가 자연 피해를 입힙니다.\n\n 태양섬광 (표범)\n적을 불태워 [130 * (9.183105 + 0.616405 * 60 + 0.028608 * 60 * 60) / 100]에서 [152 * (9.183105 + 0.616405 * 60 + 0.028608 * 60 * 60) / 100]의 자연 피해를 입히고 12 sec에 걸쳐 [65 * 4 * (9.183105 + 0.616405 * 60 + 0.028608 * 60 * 60) / 100]의 추가 자연 피해를 입힙니다. 연계 점수 1점을 생성합니다."},
[206992] = {name = "[q2]두개골 강타의 룬", additional_info = "획득 시 귀속\n고유 아이템직업: 드루이드\n[q2]바지에 두개골 강타 룬을 새깁니다.\n\n\n13미터 내의 대상에게 돌진한 후 두개골을 강타하여 주문 시전을 방해하고, 2 sec 동안 같은 계열의 주문을 시전하지 못하게 합니다. 야성의 돌진과 재사용 대기시간을 공유합니다."},
