[208744] = {name = "[q2]어둠의 화살의 룬", additional_info = "획득 시 귀속\n고유 아이템직업: 흑마법사\n[q2]장갑에 연발 어둠의 화살 룬을 새깁니다.\n\n\n어둠의 화살이 10미터 내에 있는 최대 5명의 추가 적을 공격합니다. 공격력은 20%만큼 감소합니다."},
[208749] = {name = "나무옹이 은닉처 열쇠", additional_info = "고유 아이템"},
[208750] = {name = "[q2]정신 집중의 룬", additional_info = "획득 시 귀속\n고유 아이템직업: 흑마법사\n[q2]가슴보호구 또는 로브에 역술의 대가 룬을 새깁니다.\n\n\n생명력 흡수가 더는 정신을 집중하지 않습니다. 15 sec 동안 지속되며 15 sec의 재사용 대기시간이 적용됩니다. 또한 마나 소모량이 100%만큼 증가하며, 피해를 입힐 때마다 50%만큼 추가로 생명력을 회복합니다."},
[208753] = {name = "[q2]주문 기록: 재생", additional_info = "획득 시 귀속\n고유 아이템직업: 마법사\n[q2]가슴보호구 또는 로브에 재생 룬을 새깁니다.\n\n\n대상을 3 sec에 걸쳐 [(38.258376 + 0.904195 * 60 + 0.161311 * 60 * 60) * 42 / 100 * 3]만큼 치유하고 30초 동안 시간의 봉화를 부여합니다.\n\n 시간의 봉화\n\n대상의 시공간 위치를 기록합니다. 시전자가 입히는 모든 비전 피해의 80%가 시간의 치유로 전환되어 현재 시간의 봉화가 부여된 각 대상을 치유합니다. 대상이 자신일 경우 치유량이 50%만큼 감소합니다. 또한 여러 대상을 공격하는 비전 주문으로 피해를 입힐 경우에는 치유량이 80%만큼 감소합니다."},
[208754] = {name = "[q2]주문 기록: 생재", additional_info = "획득 시 귀속\n고유 아이템직업: 마법사\n[q2]사용 효과: 이해의 부적으로 주문 기록을 해석하여 새로운 각인술 주문을 배웁니다."},
[208758] = {name = "[q2]대지의 룬", additional_info = "획득 시 귀속\n고유 아이템직업: 주술사\n[q2]바지에 대지의 길 룬을 새깁니다.\n\n\n주무기에 대지의 무기가 활성화되어 있을 경우 위협 수준이 50%만큼, 생명력이 30%만큼 증가합니다. 받는 피해는 10%만큼, 근접 공격 치명타에 적중당할 확률도 6%만큼 감소합니다. 또한 대지 충격 시전 시 대상을 도발해 자신을 공격하게 하고 다른 충격 주문과 재사용 대기시간이 분리됩니다. 사정거리가 근접 공격 범위로 감소합니다."},
[208760] = {name = "공터 왕관", additional_info = "[q2]사용 효과: 평원의 영혼을 깨웁니다."},
[208764] = {name = "[q2]정신의 룬", additional_info = "고유 아이템직업: 사제\n[q2]허리띠에 정신의 쐐기 룬을 새깁니다.\n\n\n대상을 강타하여 [122 * (9.456667 + 0.635108 * 60 + 0.039063 * 60 * 60) / 100]에서 [142 * (9.456667 + 0.635108 * 60 + 0.039063 * 60 * 60) / 100]의 암흑냉기 피해를 입힙니다. 또한 다음 정신 분열의 치명타 및 극대화율이 30%만큼 증가합니다. 최대 3번까지 중첩됩니다."},
[208765] = {name = "[q2]도움의 손길", additional_info = "획득 시 귀속\n고유 아이템직업: 사제"},
[208766] = {name = "[q2]도움의 손길", additional_info = "획득 시 귀속\n고유 아이템직업: 사제\n[q2]<열려면 마우스 오른쪽 클릭>"},
[208767] = {name = "[q2]복원의 룬", additional_info = "고유 아이템직업: 사제\n[q2]바지에 회복의 기원 룬을 새깁니다.\n\n\n대상에 마력을 불어 넣어 다음에 피해를 입거나 치유 효과를 받을 시 [(38.258376 + 0.904195 * 60 + 0.161311 * 60 * 60) * 74 / 100]의 생명력을 회복하도록 합니다. 생명력이 회복될 때 20미터 반경 내의 파티원 혹은 공격대원에게로 회복의 기원이 전달됩니다. 최대 5번까지 이동하며 매 이동 시마다 효과는 30 sec 동안 지속됩니다. 이 주문은 동시에 한 대상에게만 걸 수 있습니다."},
[208768] = {name = "해적의 성냥갑", additional_info = "획득 시 귀속\n고유 아이템직업: 도적"},
[208771] = {name = "[q2]칼춤의 룬", additional_info = "획득 시 귀속\n고유 아이템직업: 도적\n[q2]바지에 칼춤 룬을 새깁니다.\n\n\n마무리 일격을 날려 무기 막기 확률을 10%만큼 증가시킵니다. 연계 점수당 다음 시간 동안 지속됩니다.\n\n 연계 점수 1점: 14초\n 연계 점수 2점: 18초\n 연계 점수 3점: 22초\n 연계 점수 4점: 26초\n 연계 점수 5점: 30초"},
[208772] = {name = "[q2]사브르 베기의 룬", additional_info = "획득 시 귀속\n고유 아이템직업: 도적\n[q2]장갑에 사브르 베기 룬을 새깁니다.\n\n\n흉포하게 적을 베어 대상에게 무기 공격력의 100%에 해당하는 피해를 입히고, 출혈을 일으켜 12 sec 동안 2초마다 (Attack power * 5 / 100)의 피해를 입힙니다. 최대 3번까지 중첩됩니다. 연계 점수 1점을 생성합니다.\n\n 사브르 베기는 사악한 일격으로 발동하거나 사악한 일격의 효과를 변경하는 모든 특성 및 효과가 적용됩니다.\n획득처 (드랍): 데피아즈단 정찰병\n드랍 확률: 4.76%"},
[208773] = {name = "낚시용 작살", additional_info = "획득 시 귀속\n직업: 전사 , 사냥꾼\n[q2]사용 효과: 대상에게 작살을 던집니다. 사정거리는 40미터입니다."},
[208777] = {name = "[q2]저격수의 룬", additional_info = "획득 시 귀속\n고유 아이템직업: 사냥꾼\n[q2]바지에 저격 훈련 룬을 새깁니다.\n\n\n지난 6초 동안 가만히 있었다면 사격 능력의 치명타 적중률이 10%만큼 증가합니다."},
[208778] = {name = "[q2]속전속결의 룬", additional_info = "획득 시 귀속\n고유 아이템직업: 전사\n[q2]장갑에 속전속결 룬을 새깁니다.\n\n\n즉시 양손 무기로 무모한 근접 공격을 날려 (Attack power * 10 / 100)에서 (Attack power * 20 / 100)의 물리 피해를 입힙니다. 이 능력은 영웅의 일격과 관련된 모든 이로운 효과를 적용 및 발동시킵니다."},
[208799] = {name = "[q2]주문 기록: 살아있는 폭탄", additional_info = "획득 시 귀속\n고유 아이템직업: 마법사\n[q2]장갑에 살아있는 폭탄 룬을 새깁니다.\n\n\n대상은 살아있는 폭탄이 되어 12 sec에 걸쳐 [(13.828124 + 0.018012 * 60 + 0.044141 * 60 * 60) * 85 / 100 * 4]의 화염 피해를 입습니다. 12 sec 후, 혹은 주문이 무효화되면 대상은 폭발하여 10미터 반경 내에 있는 모든 적에게 [(13.828124 + 0.018012 * 60 + 0.044141 * 60 * 60) * 171 / 100]의 화염 피해를 입힙니다."},
