[210500] = {name = "[q2]별의 룬", additional_info = "획득 시 귀속\n고유 아이템직업: 드루이드\n[q2]바지에 별빛쇄도 룬을 새깁니다.\n\n\n쇄도하는 항성의 마력을 발사해 [248 / 100 * (9.183105 + 0.616405 * 1 + 0.028608 * 1 * 1)]에서 [304 / 100 * (9.183105 + 0.616405 * 1 + 0.028608 * 1 * 1)]의 비전 피해를 입히고 15 sec 동안 다음 1회 동안 시전하는 별빛 화살의 공격력이 80%만큼 증가합니다.\n\n 별빛쇄도는 천벌 또는 별빛 화살에 이롭거나, 해당 주문이 발동시키는 특성 및 효과 대부분이 적용됩니다."},
[210534] = {name = "[q2]야생의 우상", additional_info = "획득 시 귀속\n고유 아이템성물 우상\n직업: 드루이드\n[q2]가슴보호구 또는 로브에 야생의 강타 룬을 새깁니다.\n\n\n표범 변신, 곰 변신 또는 광포한 곰 변신 상태일 때 20미터 내 파티원이 좀 더 매섭게 전투를 수행합니다. 근접 공격이 적중할 때마다 20%으 확률로 공격자가 재차 공격을 가해 전투력의 20%에 해당하는 추가 피해를 입힙니다. 파티원이 이미 질풍의 토템의 효과를 받는 경우 효과를 발휘하지 않습니다."},
