[210573] = {name = "[q2]불타는 분노의 룬", additional_info = "획득 시 귀속\n고유 아이템직업: 전사\n[q2]바지에 분노 잠식 룬을 새깁니다.\n\n\n격노하여 근접 공격력이 10%만큼 추가로 증가합니다. 12 sec 동안 혹은 80의 분노를 초과한 후 최대 12회 공격할 때까지 지속됩니다."},
[210589] = {name = "[q2]선조의 메아리", additional_info = "획득 시 귀속\n고유 아이템직업: 주술사\n요구 레벨 3\n[q2]바지에 고대의 인도 룬을 새깁니다.\n\n\n다음 10 sec 동안 입히는 피해의 25%가 치유량으로 전환되어 주위에 있는 최대 3명의 파티원을 치유합니다. 또한 치유량의 100%는 피해량으로 전환되어 가장 최근 화염 충격을 시전한 대상에게 피해를 입힙니다."},
[210596] = {name = "[q2]코브라의 일격의 룬", additional_info = "획득 시 귀속\n고유 아이템직업: 사냥꾼\n[q2]가슴보호구 또는 로브에 코브라의 일격 룬을 새깁니다.\n\n\n사격 능력이 치명타로 적중하면 다음 2회 동안 사용하는 야수의 특수 공격이 치명타로 적중합니다."},
[210653] = {name = "[q2]맹 고슈의 룬", additional_info = "획득 시 귀속\n고유 아이템직업: 도적\n[q2]허리띠에 맹고쉬 룬을 새깁니다.\n\n\n즉시 보조 무기로 대상을 공격해 일반 보조 무기 공격력에 해당하는 피해를 입히고 10 sec 동안 무기 막기 확률이 10%만큼 증가합니다. 연계 점수 3점을 생성합니다.\n\n 맹고쉬는 사악한 일격으로 발동하거나 사악한 일격의 효과를 변경하는 모든 특성 및 효과가 적용됩니다."},
[210654] = {name = "[q2]주문 기록: 시간 되돌리기", additional_info = "획득 시 귀속\n고유 아이템직업: 마법사\n[q2]장갑에 시간 되돌리기 룬을 새깁니다.\n\n\n시간의 봉화가 부여된 현재 대상이 즉시 지난 5초 동안 받는 모든 피해를 흡수합니다. 5초 전에 시간의 봉화가 부여되어 있지 않았던 대상에게는 효과를 발휘하지 않습니다."},
