[211385] = {name = "[q2]독사 확산의 룬", additional_info = "획득 시 귀속\n고유 아이템직업: 사냥꾼\n[q2]바지에 독사 확산 룬을 새깁니다.\n\n\n일제 사격에 적중당한 대상은 추가로 6 sec 동안 독사 쐐기에 걸립니다."},
[211386] = {name = "[q2]주문 기록: 비전 쇄도", additional_info = "획득 시 귀속\n고유 아이템직업: 마법사\n[q2]바지에 비전 쇄도 룬을 새깁니다.\n\n\n대상에게 쇄도하는 마력의 형태로 남은 마나를 모두 방출해 [(13.828124 + 0.018012 * 60 + 0.044141 * 60 * 60) * 226 / 100]에서 [(13.828124 + 0.018012 * 60 + 0.044141 * 60 * 60) * 264 / 100]의 비전 피해를 입힙니다. 남은 마나에 따라 공격력이 최대 300%만큼 증가합니다. 이후 일반 마나 회복 속도에 따라 마나가 회복되며 8 sec 동안 마나 회복 속도가 300%만큼 증가합니다."},
[211387] = {name = "[q2]유혹하는 빛의 룬", additional_info = "획득 시 귀속\n고유 아이템직업: 성기사\n[q2]장갑에 빛의 봉화 룬을 새깁니다.\n\n\n대상은 40미터 반경 내에 있는 모든 파티원과 공격대원에게 빛의 봉화가 됩니다. 해당 범위 내에 있는 파티원 또는 공격대원에게 시전하는 자신의 치유 주문은 회복시킨 생명력의 100%만큼 봉화가 된 대상의 생명력을 회복시킵니다. 동시에 오직 하나의 대상만 빛의 봉화가 될 수 있습니다. 1 min 동안 지속됩니다."},
[211390] = {name = "[q2]가벼운 상처의 룬", additional_info = "획득 시 귀속\n고유 아이템직업: 도적\n[q2]가슴보호구 또는 로브에 얕은 상처일 뿐 룬을 새깁니다.\n\n\n칼춤이 활성화된 동안 받는 물리 피해가 20%만큼 감소합니다. 추가로 근접 공격에 치명타로 적중당할 확률이 6%만큼 감소하고 모든 행동으로 생성되는 위협 수준이 크게 증가합니다. 교란 능력은 놀리기로 대체됩니다. 놀리기는 대상을 도발하여 자신을 공격하게 합니다.\n\n\n적을 도발하여 자신을 공격하도록 만듭니다. 이미 자신을 공격하고 있다면 아무런 효과를 발휘하지 못합니다."},
[211391] = {name = "[q2]치유의 비의 룬", additional_info = "획득 시 귀속\n고유 아이템직업: 주술사\n[q2]가슴보호구 또는 로브에 치유의 비 룬을 새깁니다.\n\n대상 플레이어 주위 15미터 내에 있는 대상 플레이어의 파티원 모두를 1초마다 [(29.888200 + 0.690312 * 60 + 0.136267 * 60 * 60) * 15 / 100]만큼 치유합니다."},
[211392] = {name = "[q2]영원한 고통의 룬", additional_info = "획득 시 귀속\n고유 아이템직업: 흑마법사\n[q2]바지에 영원한 고통 룬을 새깁니다.\n\n\n생명력 흡수, 영혼 흡수, 어둠의 화살, 어둠의 회전베기, 불타는 고통, 소각, 유령 출몰 시전 시 대상에게 걸린 부패의 지속시간이 최대 지속시간으로 갱신됩니다."},
