[211476] = {name = "[q2]불의 자취의 룬", additional_info = "획득 시 귀속\n고유 아이템직업: 흑마법사\n[q2]가슴보호구 또는 로브에 불꽃의 호수 룬을 새깁니다.\n\n\n불의 비가 지면에 불꽃의 호수를 남깁니다. 호수에 영향을 받는 적은 15 sec 동안 시전자와 시전자의 악마 소환수에게 받는 모든 화염 피해가 40%만큼 증가합니다."},
[211477] = {name = "[q2]소각의 룬", additional_info = "획득 시 귀속\n고유 아이템직업: 흑마법사\n[q2]바지에 소각 룬을 새깁니다.\n\n\n적을 불태워 [222 / 100 * (6.568597 + 0.672028 * 60 + 0.031721 * 60 * 60)]에서 [258 / 100 * (6.568597 + 0.672028 * 60 + 0.031721 * 60 * 60)]의 피해를 입히고 다음 15 sec 동안 모든 화염 공격력이 25%만큼 증가합니다."},
