[216745] = {name = "[q2]어둠의 마귀의 두루마리", additional_info = "획득 시 귀속\n고유 아이템직업: 사제\n[q2]어둠의 마귀를 창조하여 대상을 공격하도록 합니다. 시전자는 어둠의 마귀가 공격할 때마다 5%의 마나를 회복합니다. 15 sec 동안 지속됩니다."},
[216746] = {name = "[q2]지휘의 외침 안내서", additional_info = "획득 시 귀속\n고유 아이템직업: 전사\n[q2]전사의 외침으로 주위 30미터 반경에 있는 모든 파티원의 체력이 2만큼 증가됩니다. 2 min 동안 지속됩니다."},
[216747] = {name = "[q2]흑마법서: 영혼 수확", additional_info = "획득 시 귀속\n고유 아이템직업: 흑마법사\n[q2]영혼 흡수로 피해를 입힐 때마다 일정 확률로 추가 영혼의 조각을 얻습니다. 영혼의 조각을 얻지 못할 때마다 획득 확률이 증가합니다."},
[216748] = {name = "[q2]흑마법서: 소환의 차원문", additional_info = "획득 시 귀속\n고유 아이템직업: 흑마법사\n[q2]파티원 혹은 공격대원 2명이 대상 파티원 혹은 공격대원을 소환할 수 있는 소환의 문을 만드는 의식을 시작합니다. 의식을 수행하려면 흑마법사를 제외하고 파티원 혹은 공격대원 2명이 필요합니다. 의식에 참가하려는 플레이어는 비전투 상태여야 하며 소환의 문에 오른쪽 클릭을 한 뒤 의식이 완료될 때까지 절대로 움직이면 안됩니다."},
[216764] = {name = "[q2]깊은 야생의 전언", additional_info = "획득 시 귀속\n고유 아이템직업: 드루이드\n[q2]야생의 징표의 마나 소모량이 50%만큼 감소하고 지속시간은 50%만큼 증가합니다."},
[216767] = {name = "[q2]되살리기의 전언", additional_info = "획득 시 귀속\n고유 아이템직업: 드루이드\n[q2]죽은 플레이어를 되살립니다. 대상은 15%의 생명력과 마나를 지닌 채로 되살아납니다. 전투 중에는 사용할 수 없습니다."},
