local addonName, addonTable = ...

addonTable.questData['pt'] = {
[1] = {title = "A missão do rango", objective = "Mate Kobolds Daninhos, dois deles.$Tpunk;!  Kill Kobold Vermin, 2 of em. NEW TEST AGAINO seguinte feitiço será lançado em você:\n\n", description = "$Tpunk;!  Kill Kobold Vermin, 2 of em. NEW TEST AGAINO seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nGalinhar Outro\n\nVocê receberá:\n\n"},
[2] = {title = "Garra de Garraguda", objective = "Leve a Garra de Garraguda para Senani Coração Trovejante no Posto Machadada, Vale Gris.", description = "O poderoso hipogrifo Garraguda foi abatido, e a garra da fera serve como prova da sua vitória. Senani Coração Trovejante, do Posto Machadada, sem dúvida ficará interessada em ver esse troféu como prova do seu feito.", progress = "Sim, valoroso <class>, pressenti a sua chegada. Aposto que você traz notícias sobre a caçada, certo?", completion = "Impressionante, &lt;name&gt;... com certeza que não foi fácil conseguir a garra de Garraguda! A caçada do Vale Gris vai bem para você!\n\nDurante muito tempo, Garraguda aterrorizou os peões das serrarias próximas a seu caminho que tentavam viajar até aqui, no Posto Machadada. Assim que a notícia de que foi você quem deu cabo da fera começar a circular, muitas canções em homenagem à sua bravura serão ouvidas nas fogueiras e serrarias do Vale Gris!", rewards = "Você receberá:\n\n"},
[5] = {title = "A barriga roncante de Medrisco", objective = "Fale com o Chef Goroba.", description = "Eu estou preso, escondido nesta cidade fantasma há semanas, e não tenho comido nada além de larvas e ervas daninhas! Eu preciso de alguma comida decente, e estou disposto a pagar bem por isso.\n\nTraga-me um banquete e lhe pagarei generosamente.\n\nOuvi dizer que o Chef Goroba, da Taberna Corvo Escarlate, lá da Vila Sombria, faz uns bolinhos de caranguejo crepuscular deliciosos...", progress = nil, completion = "Você precisa de bolinhos de caranguejo, é? Bom, acho que posso preparar alguns para você...", rewards = "Você receberá:\n\n"},
[6] = {title = "Recompensa por Garrick Patatenra", objective = "Mate Garrick Patatenra e leve a cabeça dele para o Cabo Vilém na Abadia de Vila Norte.", description = "Garrick Patatenra — um criminoso que atormenta nossos fazendeiros e mercadores há semanas — foi visto em uma cabana perto dos vinhedos, que ficam a leste da Abadia e do outro lado da ponte. Traga-me a cabeça do patife e ganhe a recompensa por matá-lo!\n\nMas tenha cuidado, <name>. Garrick reuniu uma gangue de brutamontes. Não será fácil chegar até ele.", progress = "Você encontrou a cabana de Garrick? Estamos finalmente livres daquele patife?", completion = "Há... você pegou ele! Parabéns! Você prestou um excelente serviço a Elwynn e mereceu uma boa recompensa!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCalças AfuniladasTúnica em CamadasManto do Alferes\n\nVocê também receberá:\n\n"},
[7] = {title = "Limpeza no acampamento dos kobolds", objective = "Mate 10 Kobolds Daninhos, depois fale novamente com o Major Belmonte.", description = "Sua primeira tarefa é de purificação, <name>. Um clã de kobolds está infestando o bosque ao norte daqui. Vá até lá e derrote todos os malditos kobolds que encontrar. Se você reduzir os números deles, a gente tem mais chances de expulsar os kobolds da Vila Norte de uma vez por todas.", progress = nil, completion = "Muito bem, cidadão. Esses kobolds são uns ladrões covardes, mas, em grandes números, representam uma ameaça a todos nós. E os humanos de Ventobravo não precisam de mais uma ameaça.\n\nFico muito grato por você ter derrotado aquelas criaturas.", rewards = "Você receberá:\n\n"},
[8] = {title = "Palavra de ladino", objective = "Entregue a Carta Comum à Estalajadeira Geni nas Clareiras de Tirisfal.", description = "Ei, colega! Faz um favor para um jovem que tem combatido mais do que seu quinhão de aranhas e zumbis estúpidos?\n\nTenho aqui esta carta que precisa chegar a Montalvo... até uma estalajadeira chamada Geni qualquer coisa. O sobrenome dela não tem a menor importância.\n\nEnfim, é um lugar pequeno e aconchegante cheio de vítimas da peste tentando ganhar a vida. E seria um ótimo local para você descansar também se houver necessidade. Você deveria conferir... faça isso, que eu lhe pago bem.", progress = "Sim? Sim? O que é isso?\n\nOs Renegados... eles com certeza têm um apetite enorme. Sinta-se em casa... sim, descanse esses ossos cansados. Meu nome é Geni, se você precisar de alguma coisa.", completion = "Ah, uma carta? Que notícia maravilhosa! Tenho aguardado notícias da minha querida e velha mãe em Plangemortis. Ela é uma santa. Me pergunto como ela tem andado.\n\nO quê? Alegre demais? Não está acreditando nisso, não é?\n\nBem, some daqui então... não é da sua conta de quem é a carta. Anda! Some daqui!\n\nVolte quando você tiver dinheiro para gastar... desocupado.\n\nQuando menos esperar, você estará voltando para descansar após uma aventura de dias compridos ou alguma coisa idiota assim.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCogumelo da FlorestaÁgua Refrescante da Fonte\n\nVocê também receberá:\n\n"},
[9] = {title = "Campos mortais", objective = "O Fazendeiro Saldanha quer que você mate 20 Guarda-colheitas.", description = "Olha só o estado deste lugar! Estas terras já foram ocupadas por bons fazendeiros, mas os malditos ladrões botaram todos pra correr. Mas a mim, não! Acontece, porém, que alguns Guarda-colheitas tomaram posse dos campos.\n\nSe estiver dispost<o/a>, gostaria que você matasse vinte deles pra mim. Quando terminar, volte aqui que eu lhe darei uma boa recompensa. Depois que acabar com os que estiverem no meu campo, você também pode acabar com os que estiverem nos campos vizinhos.", progress = "Ah, que bom ver você de volta! Espero que você tenha conseguido livrar os campos desses Guarda-colheitas. Já matou vinte?", completion = "Excelente trabalho, meu amigo. Você fez por merecer a sua recompensa. Quem sabe agora Cerro Oeste volte a prosperar.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCalças do CeifadorVeste do Ceifador\n\nVocê também receberá:\n\n"},
[10] = {title = "Um sonho de voltar vivo pra cidade", objective = "Descubra qual foi a sina do Topógrafo Júnior Caranduru, e leve ele ou seus equipamentos de topografia para o Topógrafo Sênior Secamolha, em Geringontzan.", description = "Ah, que sacanagem tremenda! Eu mandei meu melhor assistente, o Topógrafo Júnior Caranduru, pro deserto a sudeste. Foi numa parte conhecida como Fenda Hazzali. Bom, o cara sumiu, e no último relatório mencionou aqueles insetos bizarros da área. Será que aquelas pragas já infestaram as duas regiões principais do deserto?\n\nNa moral, eu preciso que você vá à Fenda Hazzali procurar o Caranduru! Se não ele... pelo menos os equipamentos de topografia. Se bobear não sobrou mais nada do pobre coitado.", progress = "E aí, qual é a boa? Você tá com uma cara de quem foi atropelado por um javatusco... O Caranduru tá vivo?", completion = "Era um bom goblin, o jovem Caranduru. Tomara que tenha registrado alguma coisa no equipamento de topografia para não ter morrido em vão.\n\nTudo indica que esses insetos vão dar a maior trabalheira pra gente. Aquelas pragas são espertas demais pro meu gosto, e segundo as nossas pesquisas pode ser que estejam sendo controladas por terceiros! Se for o caso, não só Geringontzan, mas o deserto inteiro está em maus lençóis!", rewards = "Você receberá:\n\n"},
[11] = {title = "Caçando gnolls", objective = "Leve 8 Braçadeiras Pintadas dos Gnolls para o Delegado Ranieri no Quartel.", description = "Aquelas criaturas brutas e indecentes, os gnolls, tem sido vistas por aqui, nos limites da Floresta de Elwynn. Uma matilha grande, maior do que podemos enfrentar sozinhos, infestou o bosque ao sul daquela torre de guarda. Outro grupo infestou as áreas perto do Lago do Monumento, a leste daqui.\n\nO Exército de Ventobravo com certeza saberá recompensar quem ajudar a matar esses gnolls. Traga-me as braçadeiras pintadas que eles usam como prova.", progress = "Salve, <name>. Você andou matando os gnolls?", completion = "Vejo que você anda ocupado! Você tem nossa gratidão, &lt;name&gt;.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBroquel da MilíciaCalças do Pivete\n\nVocê também receberá:\n\n"},
[12] = {title = "A Milícia do Povo", objective = "Miguel Mantoforte quer que você mate 15 Coureadores Défias e 15 Contrabandistas Défias, e fale novamente com ele no Morro da Sentinela.", description = "A Milícia do Povo tem apenas um objetivo: defender as terras de Cerro Oeste e devolver a paz ao nosso entorno. Infelizmente, o preço da paz é muitas vezes pago com sangue.\n\nUm dos meus batedores informou que um bando de Coureadores Défias está causando problemas aqui perto. Eu tenho relatos de Coureadores Défias próximos da Mina Veiojango a noroeste, bem como na Fazenda dos Peçanha e na Plantação de Abóboras do Taturana. Se quiser se juntar a nós, mate 15 Coureadores Défias e 15 Contrabandistas Défias, e então venha falar comigo.", progress = nil, completion = "Muito bem, &lt;name&gt;. O meu batedor testemunhou seus atos de valentia. Você vem demonstrado seu valor até agora.", rewards = "Você receberá:\n\n"},
[13] = {title = "A Milícia do Povo", objective = "Miguel Mantoforte quer que você mate 15 Pilhadores Défias e 15 Gatunos Défias, e fale novamente com ele no Morro da Sentinela.", description = "Um bando de pilhadores Défias foi visto saqueando a Pedreira Costa Dourada, o Arroio da Lua e a Fazenda dos Aleixo. A Milícia do Povo não vai tolerar esse tipo de comportamento. Liquide-os imediatamente, <name>, e marque a presença da Luz no Cerro Oeste.\n\nA Pedreira Costa Dourada fica perto da costa, a oeste da torre. Para dar prosseguimento ao seu treinamento, quero que você mate 15 desses pilhadores Défias imundos e 15 gatunos Défias.", progress = nil, completion = "Seus atos de bravura até aqui têm comprovado seu valor para a Milícia do Povo.", rewards = "Você receberá:\n\n"},
[14] = {title = "A Milícia do Povo", objective = "Miguel Mantoforte quer que você mate 15 Bandoleiros Défias, 5 Tocaiantes Défias e 5 Rufiões Défias, e fale novamente com ele no Morro da Sentinela.", description = "Alguns Défias nos enganaram. Meu batedor mais confiável informou que esses Défias têm saqueado e pilhado do interior ao sul de Cerro Oeste. Nós achamos que eles estão se escondendo nos Obeliscos, planejando o próximo passo. Mate os malfeitores em nome da Milícia do Povo.", progress = nil, completion = "Quando deixei as terras infestadas de Lordaeron, me deparei com uma situação nefasta aqui na minha terra natal. Ainda há esperança para o Cerro Oeste. Como prova sua valentia em batalha, está claro para que você serve à nossa causa com honra. É com grande orgulho que eu nomeio você integrante da Milícia do Povo. Que a Luz ilumine você.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nGume da Milícia do PovoPunho da Milícia do PovoCentelha da Milícia do Povo\n\nVocê também receberá:\n\n"},
[15] = {title = "Investigue a Serra do Eco", objective = "Mate 10 Trabalhadores Kobolds e fale novamente com o Major Belmonte.", description = "<name>, meus batedores me disseram que a infestação de kobolds é maior do que pensávamos. Um grupo de trabalhadores kobolds se assentou perto da Mina da Serra do Eco, ao norte.\n\nVá até a mina e remova-os. Sabemos que há pelo menos 10 deles. Mate-os, veja se tem mais, depois venha falar comigo.", progress = nil, completion = "Eu não gosto da ideia desse monte de kobolds na nossa mina. Não tem como isso acabar bem. Aqui, aceite isto como pagamento. Quando estiver pronto, fale comigo novamente. Eu gostaria que você voltasse à mina mais uma vez...", rewards = "Você receberá:\n\n"},
[16] = {title = "Dê de beber a Geraldo", objective = "O trabalho na fazenda é pesado... estou sempre à procura de água fresca da fonte.\n\nSe você tiver água, estou disposto a fazer negócio.", description = nil, progress = "O trabalho na fazenda é pesado... estou sempre à procura de água fresca da fonte.\n\nSe você tiver água, estou disposto a fazer negócio.", completion = "Obrigado, &lt;name&gt;! E volte mais vezes se quiser negociar de novo!", rewards = "Você receberá:\n\nMaçã Vermelha Brilhante"},
[17] = {title = "Busca pelos reagentes de Uldaman", objective = "Leve 12 Campânulas de Fungo Magenta para Ghak Toquecura em Thelsamar.", description = "Ouvi falar de uma planta recém-descoberta chamada de campânula de fungo magenta. Elas crescem em aglomerados, no fundo do sítio de escavação de Uldaman — pode haver delas em outro lugar, mas é lá que eu tenho certeza de que você vai encontrá-las. Eu quero estudar seu uso potencial em alquimia, e é aí que você entra.\n\nEncontre os aglomerados de campânula de fungo magenta e me traga uma dúzia. Mas esteja ciente de que os aglomerados podem espalhar esporos venenosos se forem chacoalhados.\n\nFaça isso para mim, e vou preparar uma leva de um dos meus famosos elixires restauradores para você!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nPoção de Restauração"},
[18] = {title = "Irmandade dos Ladrões", objective = "Leve 12 Bandanas de Lona Vermelha para o Cabo Vilém diante da Abadia de Vila Norte.", description = "Nos últimos tempos, tem um grupo novo de ladrões na região de Vila Norte. Eles se intitulam a Irmandade Défias e foram vistos do outro lado do rio, a leste.\n\nNão sei o que estão aprontando, mas coisa boa é que não é! Traga para mim as bandanas que eles usam e eu te dou uma arma como recompensa.", progress = "Já conseguiu aquelas bandanas para mim?", completion = "Estou vendo que você me trouxe umas bandanas. O exército de Ventobravo agradece a sua ajuda.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nAdaga de MilíciaMartelo de MilíciaEspada Curta de MilíciaMartelo de Guerra de MilíciaCajado de Milícia\n\nVocê também receberá:\n\n"},
[19] = {title = "Tharil'zun", objective = "Leve a Cabeça do Tharil'zun ao Oficial Marris em Cristarrubra.", description = "Vanguardeiros Rocha Negra e Proscritos estão armando emboscadas daqui até a Bastilha Mirante de Pedra. O líder dos Vanguardeiros é um orc chamado Tharil'zun. Precisamos detê-lo. Tragam-me a cabeça de Tharil'zun!", progress = "A pressão dos orcs de Rocha Negra ainda está tensa. Você conseguiu nos livrar de Tharil'zun, pelo menos?", completion = "Tharil'zun era um orc vil e engenhoso. Muito bem, Tenho certeza de que não foi fácil derrotá-lo. Eis a sua recompensa, &lt;name&gt;.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBroquel Endurecido pelo FogoEsmaga-orc\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[20] = {title = "Ameaça dos Rochas Negras", objective = "Traga 10 Machados Gastos em Batalha para o Oficial Marris na Vila Plácida.", description = "Os orcs da Rocha Negra saíram das Estepes Ardentes e tomaram a Bastilha Mirante de Pedra, ao norte. A partir dali, saquearam as regiões ao norte da Vila Plácida e mantiveram os cidadãos dentro da cidade, cercados como gado.\n\nAjude a libertar a Vila Plácida dessa ameaça. Mate os orcs da Rocha Negra e me traga dez machados gastos em batalha como prova.\n\nApesar da ameaça vir do norte, recebemos relatos de mais Rochas Negras na parte sudeste de Cristarrubra. Se encontrar alguns lá, mate-os também.", progress = "Você andou matando orcs, <name>? Se sim, prove...", completion = "Agora temos menos orcs com que nos preocuparmos, obrigado.", rewards = "Você receberá:\n\n"},
[21] = {title = "Escaramuça na Serra do Eco", objective = "Mate 12 Operários Kobold, depois fale novamente com o Major Belmonte na Abadia de Vila Norte.", description = "Suas pesquisas provaram que a Mina da Serra do Eco precisa de um expurgo. Volte à mina e ajude a acabar com os kobolds lá.\n\nNão perca tempo, <name>. Quanto mais tempo os kobolds passarem na mina, mais estabelecidos eles estarão na Vila Norte.", progress = nil, completion = "Mais uma vez, você conquistou o meu respeito e a gratidão do exército de Ventobravo. Pode ser que ainda haja kobolds na mina, mas vou liderar novos ataques contra eles. Outras tarefas aguardam você.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCinto do ProvedorBotas do ProvedorLuvas do Provedor\n\nVocê também receberá:\n\n"},
[22] = {title = "Empadão de Fígado de Goretusco", objective = "Salma Saldanha precisa de 8 Fígados de Goretusco para fazer um Empadão de Fígado de Goretusco.", description = "Já descasquei as cebolas, triturei o alho e o alecrim, assei a crosta, piquei o endro, e o molho está cozinhando. Agora, eu só preciso de 8 fígados de goretusco para fazer o meu famoso empadão de carne!", progress = "Eu só preciso de 8 fígados de goretusco para o meu famoso empadão de carne!", completion = "Esses são perfeitos, &lt;name&gt;! Muito obrigada. O Fazendeiro Saldanha e eu vamos encher o bucho hoje à noite. Isso aqui é para você, pelo seu esforço. Você não achou que eu iria deixar um &lt;class&gt; como você ir embora com fome, achou?", rewards = "Você receberá:\n\nEmpadão de Fígado de GoretuscoReceita: Empadão de Fígado de Goretusco"},
[23] = {title = "Pata de Ursangous", objective = "Leve a Pata de Ursangous para Senani Coração Trovejante no Posto Machadada, Vale Gris.", description = "A pata do imponente urso Ursangous é o seu troféu desta parte da Caçada do Vale Gris. Na pata, há um pequeno emblema de quarto crescente de lua, cujo significado continua sendo um mistério.\n\nA pata deve ser levada a Senani Coração Trovejante, no Posto Machadada, para provar que você subjugou essa criatura poderosa e completou esta parte da caçada.", progress = "Ah, <name>! Eu estava pensando exatamente em você! Como está indo a sua caçada?", completion = "Ah, a pata do Ursangous! Sua habilidade é impressionante, &lt;class&gt;; dizem que ele matou muitas pessoas. A lua crescente em sua pata, sem dúvida, é um sinal de que ele foi treinado pelos elfos noturnos que espreitam no Vale Gris. O Ursangous é... era um dos ursos mais fortes que você poderia encontrar nestas partes. Superar uma criatura como essa é uma prova de vontade e determinação.\n\nSeus ancestrais ficariam orgulhosos!", rewards = "Você receberá:\n\n"},
[24] = {title = "Cabeça de Shadumbra", objective = "Leve a Cabeça de Shadumbra para Senani Coração Trovejante no Posto Machadada, Vale Gris.", description = "O outrora veloz Shadumbra agora permanece imóvel enquanto você declara vitória sobre ele. A cabeça do sabre-da-noite é um troféu apropriado para essa luta da Caçada do Vale Gris. Senani Coração Trovejante, no Posto Machadada, vai querer ver um troféu como esse para lhe dar crédito por esta parte da Caçada do Vale Gris.", progress = "Olá de novo, <name>! Veio me mostrar a prova da sua caçada?", completion = "Uma vitória para &lt;name&gt; na Caçada do Vale Gris! Muito bem — a cabeça do Shadumbra é um troféu apropriado a se conquistar numa luta tão impressionante, sem dúvida.\n\nA selvageria do Shadumbra era famosa em todo o posto aqui. Qualquer viagem para o oeste, especialmente a pé, tentando chegar à Praia de Zoram, era permeada de perigo por causa deste tigre. Graças a você, esse perigo diminuiu.", rewards = "Você receberá:\n\n"},
[25] = {title = "Paralisação nas Torres de Pedra", objective = "Liquide 12 Elementais da Água Contaminados no Lago Mistral, a leste do Túnel Garracava e a sudoeste de Posto Machadada.\n\nFaça o reconhecimento do gazebo do Lago Mistral, que fica de frente para o Entreposto da Aliança mais próximo.\n\nFale novamente com Mastok Wrilehiss em Posto Machadada, no Vale Gris.", description = "Se quer ajudar, então por que não faz alguma coisa sobre a situação para os lados do Túnel Garracava?\n\nOs transportes entre Vale Gris e Torres de Pedra estão parados porque o lago ganhou vida e está atacando qualquer coisa que se aproxime... ou seja, qualquer um no caminho. Ainda mais útil do que simplesmente atacar os elementais da água por lá seria alguém fazer o reconhecimento do gazebo do Lago Mistral... aquele de onde dá pra ver bem o Entreposto da Aliança.\n\nPront<o/a> para fazer alguma coisa de útil?", progress = nil, completion = "Sim, fiquei sabendo que alguém rasgou o lago como um redemoinho de poeira pelos Sertões. Pelo jeito, talvez dê para estabelecer uma área de observação ali para ficar de olho em nossos amigos da Aliança. Bem, isso se pudermos manter as coisas na surdina...\n\nParece que você acabou sendo bastante útil no final das contas. Os transportes entre o Vale Gris e as Torres de Pedra devem ficar bem mais tranquilos. Tome isso... você mereceu.", rewards = "Você receberá:\n\n"},
[26] = {title = "Uma lição a aprender", objective = "Fale com Dendrite Stellardor no vilarejo de Refúgio Noturno, na Clareira da Lua.", description = "<name>, tenho acompanhado seu progresso com grande alegria. Você está mesmo se tornando <um/uma> protet<or/ora> e tanto da natureza.\n\nAgora chegou a sua hora de aprender mais sobre um dos muitos aspectos animais nos quais, pela graça de Cenarius, nós podemos nos transformar – o aspecto que concede o comando e a maestria da água. Para isso, você precisa viajar para a cidade de Refúgio Noturno, que fica na Clareira da Lua, e falar com Dendrite Stellardor no Círculo Cenariano. Ele informará os seus próximos passos.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[27] = {title = "Uma lição a aprender", objective = "Fale com Dendrite Stellardor no vilarejo de Refúgio Noturno, na Clareira da Lua.", description = "<name>, tenho acompanhado seu progresso com grande alegria. Você está mesmo se tornando <um/uma> protet<or/ora> e tanto da natureza.\n\nAgora chegou a sua hora de aprender mais sobre um dos muitos aspectos animais nos quais, pela graça de Cenarius, nós podemos nos transformar – o aspecto que concede o comando e a maestria da água. Para isso, você precisa viajar para a cidade de Refúgio Noturno, que fica na Clareira da Lua, e falar com Dendrite Stellardor no Círculo Cenariano. Ele lhe informará dos seus próximos passos.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[28] = {title = "A prova do lago", objective = "Encontre um Adorno de Altar no Lago Eluna'ara e leve-o para o Santuário de Remulos, na parte noroeste da Clareira da Lua. Use o Adorno de Altar lá dentro.\n\nVocê terá que falar com Tajarri, lá no santuário mesmo, para concluir a prova.", description = "Na sua primeira prova, você mergulhará nas profundezas do Lago Eluna'ara, atrás de um Adorno de Altar. Ele se desmancha bem rápido, então trate de levá-lo correndo para o Santuário de Remulos. Use o adorno dentro do santuário, depois fale com Tajarri lá mesmo. Ela é uma das guardiãs mais respeitadas pela Clareira da Lua.\n\nVocê não terá muito tempo para concluir a tarefa e talvez esteja competindo pelos adornos com outros druidas. Destreza física e mental serão essenciais para triunfar.\n\nBoa sorte, <name>.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nAdorno de Altar"},
[29] = {title = "A prova do lago", objective = "Encontre um Adorno de Altar no Lago Eluna'ara e leve-o para o Santuário de Remulos, na parte noroeste da Clareira da Lua. Use o Adorno de Altar lá dentro.\n\nVocê terá que falar com Tajarri, lá no santuário mesmo, para concluir a prova.", description = "Na sua primeira prova, você mergulhará nas profundezas do Lago Eluna'ara, atrás de um Adorno de Altar. Ele se desmancha bem rápido, então trate de levá-lo correndo para o Santuário de Remulos. Use o adorno dentro do santuário, depois fale com Tajarri lá mesmo. Ela é uma das guardiãs mais respeitadas pela Clareira da Lua.\n\nVocê não terá muito tempo para concluir a tarefa e talvez esteja competindo pelos adornos com outros druidas. Destreza física e mental serão essenciais para triunfar.\n\nBoa sorte, <name>.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nAdorno de Altar"},
[30] = {title = "Missão do leão marinho", objective = "Encontre o Meio-pingente da Agilidade Aquática e o Meio-pingente da Resistência Aquática. Fale com os residentes da Clareira da Lua para obter pistas de onde esse itens podem estar.\n\nForme o Pingente do Leão Marinho a partir das duas metades. Você precisa estar perto do Santuário de Remulos para fazer isso.\n\nLeve o pingente reunido ao Dendrite Stellardor na vila de Refúgio Noturno, que fica na Clareira da Lua.", description = "Para a segunda prova, você vai precisar das duas metades que formam o Pingente do Leão Marinho. Uma delas extrai poder do aspecto da agilidade do leão marinho, que desliza pela água. A outra extrai poder do aspecto da inacreditável resistência do leão marinho. Os druidas se valem igualmente das duas características para encarnar o aspecto de sua forma aquática.\n\nFale com os moradores da Clareira da Lua para saber onde as partes podem estar, e traga ambas para cá, onde podemos reuni-las. Quando o pingente estiver formado, leve para Dendrite Stellardor.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nMeio-pingente da Agilidade AquáticaMeio-pingente da Resistência Aquática"},
[31] = {title = "Forma aquática", objective = "Volte ao Penhasco do Trovão e mostre a Turak Runa Totem o Pingente do Leão Marinho.", description = "Você completou as lições necessárias que todos os jovens druidas aprendem antes poderem adotar um aspecto aquático. Vá reencontrar seu treinador, Turak Runa Totem, no Penhasco do Trovão. Mostre-lhe o seu pingente e prove que você está <pronto/pronta> para aprender o que ele tem para lhe ensinar. Ele completará seu treinamento, permitindo que você entre em harmonia com a água.\n\nAdeus, jovem <druida/druidesa>. Nos encontraremos novamente.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nForma Aquática\n\nVocê receberá:\n\nCinturão de Aquarius"},
[32] = {title = "A ascensão dos silitídeos", objective = "Leve o relatório para Zilzibin Tamborleiro em Orgrimmar.", description = "Tá aqui o relatório. Quero que tu leve isso urgente pra alguém que manje pra valer desse tipo de coisa.\n\nConheço um estudioso troll, o Zilzibin Tamborleiro. Ele passou uma cara estudando formas de vida que só existem na ecologia de Azeroth. E, de quebra, ainda manja muito de história! Se tu quer alguém pra te ajudar a ver qualé dessa ameaça, e dar cabo dela, ele é o cara. Leva isso pra ele que a gente vai precisar de ajuda.\n\nZil mora numa casa no segundo andar do Bazar de Orgrimmar.", progress = "Olha, não sei quem tu é, mas pelo jeito que as coisas estão, tu já ouviu falar de euzinho aqui. Diga, tá precisando de alguma coisa? Ou trouxe alguma coisa pra mim?", completion = "Esse relatório... Já dei de cara com essas feras. Secamolha foi sábio de me avisar e mandar tu aqui.\n\nNão demora muito e essas criaturas vão devorar o deserto de Tanaris. Pode crer que elas são uma ameaça sem tamanho. Pode gravar esse nome, &lt;name&gt;: silitídeos. Esses insetos do relatório são silitídeos, peças importantes numa das maiores ameaças que tanto a Horda quanto a Aliança têm que encarar.\n\nQue dera eu tivesse sacado isso antes.", rewards = "Você receberá:\n\n"},
[33] = {title = "Só louco não come lobo", objective = "Leve 8 pedaços de Carne Dura de Lobo para Enzo Peleteiro nos arredores da Abadia de Vila Norte.", description = "Odeio aqueles lobos silvestres, mas não abro mão de um bife de lobo... Se me arranjar uma carne dura de lobo, trocarei por algo que você achará bem útil.\n\nCace os lobos silvestres e os lobinhos que andam nas áreas rurais da Vila Norte para coletar essa carne dura de lobo.", progress = "Ei, <name>. Já estou ficando com fome... conseguiu a carne dura de lobo?", completion = "Você andou ocupado! Mal posso esperar para pôr aquela carne de lobo na panela...\n\nTenho umas coisas aqui que podem lhe interessar. Escolha!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nSapatos Forrados de Pelame MaciosBraçadeiras de Pele de Lobo\n\nVocê também receberá:\n\n"},
[34] = {title = "O penetra", objective = "Martie Jainrose, de Vila Plácida, quer que você mate Ronquifuça. Traga a presa dele como prova.", description = "Meu jardim foi pisoteado e saqueado. E sei quem é o brutamontes que está fazendo isso. É aquele maldito javali que o Oficial Marris começou a chamar de Ronquifuça.\n\nVou ter que esperar a próxima estação pra poder plantar meus narcisos de novo... Tudo arruinado! Tudo arruinado por aquele javali maldito. Dê cabo dessa praga! Traga a presa dele pra mim e ficarei feliz em recompensar você. Ele passa o tempo fuçando nos campos a sudoeste de Vila Plácida, mas de vez em quando vem pra cá e faz esse estrago no meu jardim...", progress = "Ronquifuça continua roncando e fuçando ou você conseguiu libertar Vila Plácida dessa praga de uma vez por todas?", completion = "Finalmente aquela praga morreu! Obrigada, &lt;name&gt;, você me fez um grande favor. Meu jardim vai florir como nunca este ano!", rewards = "Você receberá:\n\nBuquê de Begônias Escarlate"},
[35] = {title = "Mais preocupações", objective = "O Delegado Durão quer que você fale com o Guarda Tomás.", description = "Se você está <preocupado/preocupada> quanto aos rumores sobre os murlocs, faça o seguinte: vá para a ponte leste de Elwynn e fale com o guarda Tomás. Ele está lá desde a semana passada e vai saber qual a situação do local.\n\nTraga-me o relatório dele.", progress = nil, completion = "Sim, os murlocs se estabeleceram perto dos córregos da parte leste de Elwynn. Não sabemos o que querem aqui, mas eles são agressivos e semi-inteligentes.", rewards = "Você receberá:\n\n"},
[36] = {title = "Ensopado de Cerro Oeste", objective = "Vera Taturana quer que você entregue a receita de Ensopado de Cerro Oeste dela a Salma Saldanha.", description = "Nunca achei que chegaria o dia em que eu sairia da fazenda. Mas os campos estão infestados de ladrões, e agora está perigoso demais para nós aqui. Assim que o Fazendeiro Taturana concertar a carroça, a gente pega a estrada.\n\nSerá que você não podia me fazer um favor? Deixa eu anotar aqui a minha receita do ensopado de Cerro Oeste. Você leva pra Salma Saldanha, da outra fazenda? A fazenda dos Saldanha fica logo depois da bifurcação na estrada.", progress = "Vou sentir muita falta da Vera Taturana. Acho que você não a viu no caminho de volta, não é?", completion = "A Vera sempre foi uma rapariga tão doce... Vamos sentir falta dela aqui em Cerro Oeste, mas, cá pra nós, o coração dela é citadino e Ventobravo vai ser muito boa pra ela. Mas chega de fofoca. Agora nós podemos fazer o ensopado de Cerro Oeste!", rewards = "Você receberá:\n\n"},
[37] = {title = "Encontre os guardas perdidos", objective = "O Guarda Tomás quer que você vá para o norte, seguindo o rio, e encontre os dois guardas perdidos, Rodolfo e Malaquias.", description = "Já faz dois dias que mandamos dois guardas, Rodolfo e Malaquias, para investigar ao longo do rio. Eles ainda não voltaram. Para completar meu relatório, preciso saber o que aconteceu com eles.\n\nVá para norte, seguindo o rio, e encontre os guardas... ou o que restou deles.", progress = nil, completion = "O cadáver foi despojado de seus pertences, mas perto dali você encontra um medalhão com uma inscrição gravada: \"Soldado Malaquias Rocha\".", rewards = "Você receberá:\n\n"},
[38] = {title = "Ensopado de Cerro Oeste", objective = "Salma Saldanha quer 3 nacos de Acém de Abutre, 3 Focinhos de Goretusco, 3 Olhos de Murloc e 3 Quiabos.", description = "Ajude-me a fazer um ensopado de Cerro Oeste! Retorne com os seguintes ingredientes: \n\n 3 nacos de Acém de Abutre\n 3 Focinhos de Goretusco \n 3 Olhos de Murloc \n 3 Quiabos", progress = "Retorne com os seguintes ingredientes: \n\n 3 nacos de Acém de Abutre\n 3 Focinhos de Goretusco \n 3 Olhos de Murloc \n 3 Quiabos", completion = "O quiabo vai dar uma engrossada no ensopado! Agora, nós colocamos o acém de abutre, alguns olhos de murloc e esses focinhos de goretusco deliciosos. E está pronto! Pela sua ajuda, &lt;name&gt;, eu quero que fique com a primeira porção de ensopado de Cerro Oeste do dia!", rewards = "Você receberá:\n\nEnsopado de Cerro OesteLuvas Térmicas da SalmaReceita: Ensopado de Cerro OesteFaca de Cozinha Afiada"},
[39] = {title = "O relatório de Tomás", objective = "Apresente-se ao Delegado Durão na Vila d'Ouro.", description = "Conte ao Delegado Durão sobre as mortes de Malaquias e Rodolfo. Conte também que os murlocs na área leste de Elwynn não podem ser contidos pelas tropas que temos lá.\n\nEu sei que não temos um grande contingente, mas com sorte Durão achará alguém.", progress = nil, completion = "Hmm, isso é problemático. Nossas defesas já foram dizimadas – com a perda de Malaquias e Rodolfo para aqueles murlocs, ficamos numa posição pior ainda.\n\nSe as coisas não melhorarem, antes do fim da semana a Vila d'Ouro virar uma balbúrdia!", rewards = "Você receberá:\n\n"},
[40] = {title = "Perigo anfíbio", objective = "Remy \"Duas Vezes\" quer que você fale com o Delegado Durão em Vila d'Ouro.", description = "<name>, há uma nova ameaça, nova ameaça, na Floresta de Elwynn! Os murlocs, os murlocs, estão subindo pelos córregos da parte leste da floresta, espantando os peixes e atacando a população, a população!\n\nEu avisei o delegado Durão, Durão, mas ele só se preocupa com gnolls e bandidos. Ele acha que os murlocs não são uma ameaça, uma ameaça.\n\nPor favor, por favor, <name>, fale com Durão e convença-o a mandar mais tropas para o leste! Para o leste!", progress = nil, completion = "Sim, eu falei com Remy. Eu o respeito como comerciante, mas todas as notícias de avistamento de murlocs são vagas, quando muito.\n\nEstamos cientes de sua preocupação, mas a menos que eu receba um relatório militar sobre a ameaça murloc, não posso me dar ao luxo de enviar mais tropas para o leste.", rewards = "Você receberá:\n\n"},
[45] = {title = "Descubra o destino de Rodolfo", objective = "Vasculhe o vilarejo murloc e tente encontrar Rodolfo ou vestígios de sua morte.", description = "Vasculhando a área, você encontra pegadas com marcas de membranas seguindo para leste ao longo da costa até o Lago do Monumento. Ao longe, na direção leste, dá para discernir os contornos de um vilarejo murloc.\n\nTalvez tenha sido lá que Rodolfo tenha encontrado seu fim...", progress = nil, completion = "Você encontra em volta do pescoço do cadáver um medalhão de metal com as palavras \"Soldado Raso Rodolfo Hartford\".", rewards = "Você receberá:\n\n"},
[46] = {title = "Barbatanas de molho", objective = "Leve 8 Barbatanas Rasgadas de Murloc para o Guarda Tomás na ponte leste de Elwynn.", description = "O Exército de Ventobravo está oferecendo recompensa por tocaieiros e colhedores murloc que forem encontrados em Elwynn. Mate-os e traga-me as barbatanas rasgadas deles, e o Exército de Ventobravo lhe dará uma generosa recompensa.\n\nOs murlocs construíram um vilarejo no Lago do Monumento, ao norte daqui, e outro ao sul, no ponto em que o córrego se bifurca.", progress = "Como está indo a caçada?", completion = "Conseguiu as barbatanas? Ótimo! O delegado Durão está um tanto apreensivo a respeito da situação no leste de Elwynn. Vai ser bom poder dizer pra ele que as coisas estão entrando nos eixos por lá.\n\nSuas ações ajudaram bastante nesse caso.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBaioneta LongaPorrete de Metal MaciçoEspada Muito Usada\n\nVocê também receberá:\n\n"},
[47] = {title = "Trocando Pó de Ouro", objective = "Traga 10 Pós de Ouro para Remy \"Duas Vezes\" em Vila d'Ouro. O Pó de Ouro é coletado dos Kobolds na Floresta de Elwynn.", description = "Os kobolds daqui às vezes carregam pó de ouro. Estou precisando de pó de ouro, pó de ouro... Me traga algum e eu lhe faço o melhor preço da cidade, o melhor preço da cidade!\n\nOs kobolds ficam na Mina Vailafundo, ao sul, e perto da Mina de Jaspe, a nordeste daqui, daqui.", progress = "Psst! Conseguiu o pó de ouro pra mim, pra mim?", completion = "Obrigado pelo pó, pelo pó, &lt;name&gt;. Aqui está seu dinheiro, dinheiro, e... aqui, uma lembrança dos meus associados, associados. Talvez você ache útil, útil.", rewards = "Você receberá:\n\nSaco de Bolas de Gude"},
[48] = {title = "Doce âmbar", objective = "Leve Água da Fonte Abençoada para Canabraba Cervaforte.", description = "Na terra ao sul, onde as vinhas se esgueiram e se retorcem\nFica um poço oculto onde águas profundas correm\nPuras como da Luz a Filha sagrada\nTraga-me agora a Água da Fonte Abençoada.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[49] = {title = "Doce âmbar", objective = "Canabraba Cervaforte quer um Saco de Cevada, um Saco de Centeio e um Saco de Milho.", description = "Âmbar é a cor do meu amor mais profundo\nEla gesta o sabor mais incrível do mundo\nPara que eu faça nascer a cerveja amada\nQuero sacos de centeio, milho e cevada.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[50] = {title = "Doce âmbar", objective = "Canabraba Cervaforte quer Veraprata.", description = "Mexe, mexe o doce mosto\nPra brassar ao nosso gosto\nDe metal forte será o destilador\nMas só a Veraprata agrega valor.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[51] = {title = "Doce âmbar", objective = "Leve um Galho de Plátano para Canabraba Cervaforte.", description = "Como o solo recém-arado à espera da semente\nÉ nosso líquido destilado, resta a filtragem somente.\nAgora precisamos de uma bela pilha de carvão.\nTrazer um galho de plátano é a sua nova missão.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[52] = {title = "Proteja a fronteira", objective = "Mate 8 Predadores e 5 Jovens Ursos da Floresta, e fale novamente com o Guarda Tomás na ponte leste de Elwynn.", description = "Saudações, <name>. Os animais selvagens ficam mais agressivos à medida que nos afastamos de Vila d'Ouro. A Madeireira Vale do Leste está sofrendo com os ataques constantes de lobos e ursos!\n\nUma ajuda aqui seria bem-vinda.", progress = nil, completion = "Muito obrigado pela ajuda, &lt;name&gt;. Tem alguma coisa na floresta deixando esses bichos afoitos...\n\nO que quer que seja, espero que continue por lá.", rewards = "Você receberá:\n\nPoção Inferior de Cura"},
[53] = {title = "Doce âmbar", objective = "Leve uma braçada de Carvalho Calcinado para Canabraba Cervaforte.", description = "Maná cristalino feito choro de menino\nO tempo anda, vai ficando cor de âmbar\nGuardado num barril calcinado de carvalho\nQuem bebe desse mel fica louco pra caramba.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nFrasco de Bota do Cervaforte"},
[54] = {title = "Apresente-se em Vila d'Ouro", objective = "Entregue os Documentos do Major Belmonte para o Delegado Durão em Vila d'Ouro.", description = "<name>, como você é <um/uma> <class> com comprovado interesse pela segurança de Vila Norte, queremos <designá-lo/designá-la> para a proteção dos arredores da Floresta de Elwynn.\n\nSe aceitar a incumbência, preciso que você entregue uma papelada que preparei para o delegado Durão em Vila d'Ouro, que fica na estrada a sul, além dos portões da fronteira.", progress = "Belmonte enviou notícias? A Vila Norte é um mero jardim se comparada à Floresta de Elwynn, então nem imagino qual seja o conteúdo desse relatório.\n\nDeixe-me ver essa papelada...", completion = "Bem, aqui diz que você recebeu a patente de subdelegado interino da Intendência de Polícia de Ventobravo. Meus parabéns.\n\nE boa sorte, pois cuidar da segurança de Elwynn não é moleza... Ainda mais com a maior parte do Exército fazendo não sei o que para não sei qual nobre!\n\nÉ difícil acompanhar a politicagem em tempos sombrios como estes...", rewards = "Você receberá:\n\nEscudo do Piqueiro"},
[55] = {title = "Morbídio Vil", objective = "Use a Desgraça de Morbídio em Morbídio Vil, mate Morbídio Vil, depois devolva a Desgraça de Morbídio a Noel, que está em seu acampamento.", description = "Morbídio Vil está escondido em seu covil, uma casa no topo da colina que fica a leste, acima do cemitério de Monte Corvo. O tempo dele está se esgotando...\n\nUse a Desgraça de Morbídio para tirar o poder das magias protetoras dele.\n\nAcabe com ele. Acabe com ele e nos salve de sua maldade. Seja o instrumento da minha vingança e <um herói/uma heroína> para a Floresta do Crepúsculo.", progress = "Morbídio Vil foi derrotado?", completion = "Morbídio Vil foi derrotado. Os cavalgantes negros ainda estão à solta, mas uma fração de tudo aquilo que eu mais espero já aconteceu.\n\nVocê conseguiu o impossível, e seus feitos vão viver para sempre nas lendas da Floresta do Crepúsculo.\n\nVá, &lt;name&gt;. Talvez, um dia, o destino nos coloque frente a frente mais uma vez.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nPantalonas da Vigília NoturnaManto do Mestre VigilanteCoifa Brilho Metálico\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\nTocha da Chama Sagrada"},
[56] = {title = "A Vigília Noturna", objective = "A Comandante Aldora Ebanez da Vila Sombria quer que você mate 8 Guerreiros Descarnados e 6 Magos Descarnados.", description = "Como Comandante da Vigília Noturna, é o meu dever proteger os cidadãos da Vila Sombria. Ventobravo retirou os guardas de seus postos aqui, e agora só contamos com os nossos próprios dispositivos.\n\nSe você acha que está à altura da tarefa, precisamos da sua ajuda! Auxilie a Vigília Noturna indo até os Jardins da Paz, que fica logo ao sul da Vila Sombria, e mate oito guerreiros descarnados e seis magos descarnados. A ameaça dos mortos-vivos precisa ser contida!", progress = nil, completion = "Maravilha, &lt;name&gt;. Você será recompensado pelos favores ao povo de Vila Sombria.", rewards = "Você receberá:\n\n"},
[57] = {title = "A Vigília Noturna", objective = "A Comandante Aldora Ebanez da Vila Sombria quer que você mate 15 Malévolos Descarnados e 15 Horrores Descarnados.", description = "<name>, estou recebendo relatos de uma infestação de mortos-vivos na floresta! Se você estiver à altura da tarefa, vá para o Monte Corvo e mate 15 daqueles vis malévolos descarnados e 15 horrores descarnados. Há também relatos de esqueletos logo ao sul de Vila Sombria, perto do Cemitério Jardins da Paz. Os mortos-vivos devem ser rechaçados da Floresta do Crepúsculo!", progress = nil, completion = "O povo da Vila Sombria lhe agradece, &lt;name&gt;. Você provou ser um grande aliado da Vigília Noturna.", rewards = "Você receberá:\n\n"},
[58] = {title = "A Vigília Noturna", objective = "A Comandante Aldora Ebanez da Vila Sombria quer que você mate 20 vetores da peste no mausoléu leste do Monte Corvo.", description = "A situação no Monte Corvo ainda é preocupante. A segurança da Vila Sombria está em suas mãos, <name>. Eu não tenho vigilantes suficientes para manter esta torre segura. Precisamos que você vá para o Monte Corvo e livre o mausoléu leste de 20 vetores da peste!", progress = nil, completion = "Em nome da Vigília Noturna e do povo de Vila Sombria, eu lhe agradeço, &lt;name&gt;, por sua bravura e dedicação. Que a Luz ilumine o seu caminho.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCartucheira da Vigília NoturnaAljava da Vigília NoturnaSaco de Aniagem da Vigília Noturna\n\nVocê também receberá:\n\n"},
[59] = {title = "Armadura de pano e couro", objective = "Dê o Vale-armadura de Ventobravo para Sara Albernaz.", description = "Como recompensa por sua bravura e astúcia, tenho um molde aqui para uma peça de armadura. Quero que você leve o molde para Sara Albernaz na Madeireira Vale do Leste. Apresente o molde e ela vai montar a armadura pra você. Quando receber a armadura, <name>, use-a para defender Elwynn.\n\nA Madeireira fica a leste, depois do posto do Guarda Tomás.", progress = "Fui contratada para fornecer armaduras de pano e couro para o Exército de Ventobravo.\n\nSe você tiver um molde, ficarei feliz de fazer uma armadura pra você.", completion = "Ah, obrigada pelo molde. Escolha seu modelo de armadura à vontade.\n\nE boa sorte, valente &lt;class&gt;. Que essa armadura lhe sirva bem.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nVeste Bem-costuradaCalças Remendadas\n\nVocê também receberá:\n\n"},
[60] = {title = "Velas dos kobolds", objective = "Leve 8 Velas Grandes para Durval Pilão em Vila d'Ouro.", description = "Olá, <meu bom senhor/minha cara dama>! Você pode me dar um segundo de sua atenção?\n\nMeu irmão e eu somos donos de uma botica em Ventobravo, e vim aqui coletar velas grandes, pois precisamos da cera. Pode me ajudar?\n\nVocê vai encontrar velas grandes com os kobolds. Ouvi boatos de que eles estariam infestando as minas de Elwynn... a Mina Vailafundo, ao sul daqui, e a Mina de Jaspe, a leste. Sugiro procurar velas por lá.", progress = "Você já coletou as velas?", completion = "Esteve ocupado caçando kobolds? Obrigado pelas velas, &lt;name&gt;. Aqui está sua recompensa...", rewards = "Você receberá:\n\nPalito de Cera Faiscante"},
[61] = {title = "Frete para Ventobravo", objective = "Leve o Carregamento do Guilherme para Morgado Pilão no Distrito Comercial de Ventobravo.", description = "Meu irmão Morgado está em Ventobravo esperando meu carregamento de velas. Eu mesmo não tenho tempo para fazer a viagem, mas, se você quiser fazer o frete, ele paga bem.\n\nJá embalei as velas. O Morgado fica na nossa loja, a Botica do Pilão, no Distrito Comercial.", progress = "Ah! Um carregamento do meu irmão? Esplêndido! A sorte está mesmo sorrindo para mim hoje!", completion = "Aqui está seu pagamento. E, enquanto estiver aqui, dê uma olhada! Certamente temos uma poção ou um berloque que você achará útil.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nFoguete ExplosivoÓleo de OlafElixir de Força de Leão\n\nVocê também receberá:\n\n"},
[62] = {title = "A Mina Vailafundo", objective = "Explore a Mina Vailafundo e depois fale novamente com o Delegado Durão em Vila d'Ouro.", description = "A mina de Vila Norte não é a única com problemas! Tenho relatórios aqui dizendo que a Mina Vailafundo, em Elwynn, também se tornou um antro de kobolds.\n\nExplore a mina, confirme esses relatórios e volte a falar comigo. A mina fica ao sul de Vila d'Ouro, entre as fazendas Campedra e Madruga.", progress = nil, completion = "Essas são más notícias. Daqui a pouco vão ser dragões... Vamos ter que aumentar a patrulha perto da mina. Obrigado, &lt;name&gt;. E espere um pouco aí... Acho que tenho outra tarefa pra você.", rewards = "Você receberá:\n\n"},
[63] = {title = "Clamor da água", objective = "Derrote a Manifestação da Água Corrompida e deposite as Braçadeiras da Manifestação Corrompida junto com as Gotas Restantes da Mais Pura Água no Braseiro da Fonteterna, que fica na Floresta de Pinhaprata.", description = "No litoral de Pinhaprata, a oeste do Sepulcro, você vai encontrar um altar que foi maculado pela peste. Os espíritos de lá não falam mais com o nosso povo, por isso cabe a você derrotar a manifestação e purificar a lagoa.\n\nPegue as braçadeiras dos elementais e as últimas gotas da poção de Salma e deposite no braseiro. O ritual deve bastar para evocar um espírito puro com que você possa se comunicar.\n\nTraga uma prova de que a tarefa foi cumprida e eu lhe recompensarei com o seu totem.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[64] = {title = "Herança esquecida", objective = "O Fazendeiro Taturana quer que você recupere o relógio de bolso dele no guarda-roupa da sede de sua fazenda de abóboras, a oeste.", description = "Foi horrível. A Vera me acordou quando ouviu uma balbúrdia nos campos, que estavam cheios de arruaceiros. Saímos correndo e acabei esquecendo meu relógio de bolso. O pai da Vera meu deu o relógio no nosso casamento, por isso eu fico doente só de pensar naqueles ladrões com meu presente. Deixei o relógio no guarda-roupa da sede. Procure os campos de abóbora a oeste, não dá para não ver. Se trouxer ele de volta para mim, eu ficarei muito grato.", progress = "Puxa, será que você achou meu relógio?", completion = "Meu relógio! Muito obrigado, gentil senhor.\n\nSomos pobres fazendeiros que perderam suas terras, mas aceite essa recompensa como prova de gratidão.", rewards = "Você receberá:\n\nPão FresquinhoLeite Gelado"},
[65] = {title = "A Irmandade Défias", objective = "Miguel Mantoforte quer que você converse com Wiley, em Vila Plácida.", description = "O bando de canalhas responsável por expulsar o povo de Cerro Oeste se autointitula A Irmandade Défias. Preciso que você se infiltre nesse clã de bandidos. Precisamos saber quem comanda o cartel e onde estão escondidos. Em Vila Plácida, na estalagem, tem um ladino chamado Wiley que me deve um favor.\n\nViaje até Vila Plácida, que fica nas Montanhas Cristarrubra, a leste de Elwynn, e tente obter o máximo de informações possível.", progress = nil, completion = "Foi Mantoforte quem mandou você? Bem, eu devo um favor a ele.", rewards = "Você receberá:\n\n"},
[66] = {title = "A lenda de Galvão", objective = "Encontre o Tabelião Azambuja na Prefeitura de Vila Sombria.", description = "Ontem à noite uma sensação horrível tomou conta do meu corpo. Eu senti que minha neta Alyssa corria grande perigo. Consultei as cartas e vi a morte na mesa.\n\nDepois de uma longa jornada por um transe sinistro, descobri uma pista a respeito desse mistério terrível. Um nome veio até mim, o nome Galvão.\n\nEncontre o Tabelião na Prefeitura e veja se consegue descobrir mais alguma coisa a respeito do sujeito. Eu temo por todos nós.", progress = nil, completion = "Ah, foi a Madame Eva quem mandou você...", rewards = "Você receberá:\n\n"},
[67] = {title = "A lenda de Galvão", objective = "Vá ao Liceu do Arroio da Lua e relate qualquer informação atualizada sobre Galvão ao Tabelião Azambuja.", description = "Galvão, é? Deixe-me ver o arquivo da cidade.\n\nGalvão... Galvão... vejamos. Ah, olha só! Tenho o registro de um sr. Galvão Brumanto. O último endereço conhecido foi o Liceu do Arroio da Lua. Nossa! Isso é que é desatualização!\n\nVocê me faz um favor, amig<o/a>? Se acontecer de você passar pelo Arroio da Lua, me conte se descobrir qualquer coisa sobre esse camarada. Eu gostaria de manter os registros em ordem.", progress = nil, completion = "O cofre velho se abre num rangido...", rewards = "Você receberá:\n\n"},
[68] = {title = "A lenda de Galvão", objective = "Leve a Carta Não Enviada e Empoeirada para o Tabelião Azambuja.", description = "Você encontra uma carta não enviada e empoeirada no velho baú.", progress = "Ah, eu lembro de você. Era você perguntando daquele tal Galvão. Chegou a encontrar o que você queria?", completion = "Interessante. Então o camarada passou mesmo um tempo no Arroio da Lua. É estranho que a carta nunca tenha sido entregue. A despeito disso, atualizarei o arquivo.", rewards = "Você receberá:\n\n"},
[69] = {title = "A lenda de Galvão", objective = "Vá à Estalagem do Leão Orgulhoso para ver se alguém ouviu falar de Galvão.", description = "Minha Luz! Devo ter deixado passar da primeira vez. No registro, logo abaixo do primeiro endereço de Galvão há outro listado, parcialmente raspado. Parece que ele estava indo para a Estalagem do Leão Orgulhoso, em Vila d'Ouro. Pode ser bom averiguar por lá, <name>.", progress = nil, completion = "Galvão? Parece familiar, com certeza.", rewards = "Você receberá:\n\n"},
[70] = {title = "A lenda de Galvão", objective = "Pegue a Carta Não Entregue de Galvão no baú entregue-a no Distrito do Canal, em Ventobravo.", description = "O nome Galvão não me é estranho. Agora eu lembro.\n\nMuitos anos atrás, em uma noite de tempestade, chegou um mensageiro em busca de um refúgio para passar a noite. Perto da meia-noite, o homem desceu correndo a escada, lívido de medo. Ainda de pijama, ele desapareceu no meio do temporal.\n\nNa pressa, ele esqueceu as cartas no baú lá em cima. Nunca voltou para buscar a correspondência. Ainda tem uma aqui, que aquele sujeito Galvão ia levar para o Distrito do Canal, em Ventobravo. Esteja à vontade para pegar.", progress = "O que temos aqui? Deixa eu dar uma olhada...", completion = "Você é muito cara-de-pau de trazer isso para cá.", rewards = "Você receberá:\n\n"},
[71] = {title = "Apresente-se a Tomás", objective = "Entregue os Medalhões de Rodolfo e Malaquias para o Guarda Tomás na ponte leste de Elwynn.", description = "Agora que você tem os dois medalhões, entregue-os para o guarda Tomás na ponte, para que ele saiba do destino que se abateu sobre seus guardas.", progress = "Olá, <name>. Você descobriu o que houve com Rodolfo e Malaquias?", completion = "Você confirmou meus temores, &lt;name&gt;. Os murlocs são uma ameaça que não podemos ignorar.", rewards = "Você receberá:\n\n"},
[72] = {title = "A lenda de Galvão", objective = "Procure alguma pista nos pertences guardados.", description = "Muito antes de mim, quem cuidava da propriedade era o meu pai. Ele que teve limpar toda a bagunça que ficou depois do massacre. Mas não importa.\n\nOs fundos dos Petracume acabaram. Agora os últimos pertences da família vão ser leiloados. Tudo culpa dos cobradores de impostos, aqueles abutres. Se tu tiver muito interessad<o/a> em saber mais, pues fique à vontade para olhar aqui no meio dessa tranqueira. Quem sabe o que tu pode encontrar?", progress = nil, completion = "Você abre a tampa do caixote devagar...", rewards = "Você receberá:\n\n"},
[73] = {title = "Sem recompensa", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[74] = {title = "A lenda de Galvão", objective = "A Página de Diário Rasgada que você descobriu lhe dá a próxima pista.", description = "Dentro do caixote você encontra vários objetos: heranças mofadas, um retrato de família, alguns troféus de caça e livros antigos. Quase no fundo, sob um vaso de cerâmica, você descobre uma página de diário rasgada.", progress = "O que você tem aí? Não consigo ver. Minha visão é muito ruim. Coloque nas minhas mãos.", completion = "Mal consigo identificar essas letras. Mas esse estilo de escrita me lembra de algo que vi antes de minha visão ficar tão ruim.", rewards = "Você receberá:\n\n"},
[75] = {title = "A lenda de Galvão", objective = "O Delegado Fadigas quer que você procure a Página de Diário Esmaecida no baú no andar de cima.", description = "Havia um maço de pergaminhos no baú no andar de cima quando me mudei para este lugar. Dei uma olhada neles quando cheguei, mas desisti quando meus olhos se enevoaram. \n\nFaça um favor a um homem velho e praticamente cego e procure no baú lá em cima qualquer coisa que possa ajudar você em sua missão de descobrir mais sobre esse tal Galvão. Tenho certeza de que há uma página de diário esmaecida que pode lhe interessar. Traga-a para mim, que ajudo você da maneira que eu puder.", progress = "Você encontrou aquela página que mencionei, <name>?", completion = "Você encontrou!", rewards = "Você receberá:\n\n"},
[76] = {title = "A Mina de Jaspe", objective = "Explore a Mina de Jaspe e, depois, fale com o Delegado Durão em Vila d'Ouro.", description = "Graças a você sabemos que a Mina Vailafundo está infestada de kobolds. Agora, precisamos de um batedor para investigar a Mina de Jaspe, que fica mais longe.\n\nExplore a Mina de Jaspe e confirme se há presença de kobolds. Para chegar à mina, vá para leste seguindo a estrada até que você chegue à Torre de Azora. Da torre, siga para o norte e você chegará à mina, ao pé das montanhas.", progress = nil, completion = "Kobolds na Mina de Jaspe? Praga! A situação piora a cada minuto!\n\nObrigado pelo relatório, &lt;name&gt;. Eu só queria que você me trouxesse boas notícias de vez em quando.", rewards = "Você receberá:\n\n"},
[77] = {title = "Pega a joça!", objective = "Recupere 10 garrafas de Suco Gami das Terras Agrestes dos caixotes localizados na base abandonada da Horda na Rocha Oculta.", description = "Eu tenho os materiais de embalagem originais para o suco gami que o Dran Droffers quer. Vou ajudá-<lo/la> a embalar o suco gami para o rude do pai do Malton... pela taxa de duas garrafas extras desse suco. Elas estão em caixotes encontrados na base abandonada da Horda perto da Rocha Oculta.\n\nSerá uma tarefa árdua, ou seja, com visgos e gosmas nojentas grudadas nas ruínas — dizem que eles são controlados por algum tipo de gosma-mestra. Enfim, pegue o suco gami e volte aqui para o embalarmos.", progress = "Suponho que você tenha vindo ao nosso acampamento para entregar as garrafas de suco gami, que são essenciais para a tarefa atual, correto?", completion = "Excelente execução, &lt;name&gt;. Fico satisfeito por você ter tomado o cuidado de não ferir a delicadeza do suco gami. Não há nada pior que o buquê de um suco gami que foi chacoalhado desnecessariamente.\n\nVai levar um instante para a magia fazer seu trabalho nessa carga preciosa. Na verdade, não tem magia alguma envolvida, eu só tenho algumas embalagens vazias para Suco Gami das Terras Agrestes. Nossa safadeza deve transformar essas garrafas em um tesouro alcoólico épico.\n\nSaúde! A uma excelente bebida!", rewards = "Você receberá:\n\n"},
[78] = {title = "A lenda de Galvão", objective = "Leve a Página do Diário Esmaecido para o Taberneiro Oseias.", description = "Conheço alguém que pode te ajudar. Nos tempos que eu comandava a Guarda de Ventobravo, a gente ia beber na Taberna Corvo Escarlate, na Vila Sombria. O estalajadeiro de lá, o Oseias, era especialista nas histórias da região. Mostre a página a ele, aposto que ele vai ter algo a dizer.", progress = "Foi o Delegado Fadigas que mandou você? Por que não disse logo?\n\nAh, o bom e velho Fadigas... pobre camarada, logo estará ceguinho. Enfim, vejamos o que você trouxe.", completion = "Pela Luz! Pode crer, reconheço essa caligrafia!", rewards = "Você receberá:\n\n"},
[79] = {title = "A lenda de Galvão", objective = "Leve a página enlameada para a Comandante Ebonlocke.", description = "Já faz tempo que eu acompanho a lenda desse tal de Galvão. Quando aqueles nobres foram assassinados alguns anos atrás, eu fui investigar junto com o Fadigas. Achei essas páginas enlameadas, mas nunca conseguimos confirmar se a caligrafia era daquele homem louco que mora na mata. As suas evidências provam que ele é culpado. Leve isto imediatamente para a Comandante Ebonlocke e conte a ela o que você descobriu.", progress = "Acho bom que essa sua pista seja boa, <class>. Vamos ver o que você tem e me conte a sua história, mas pela Luz, vê se não fica de enrolação. A defesa da Vila Sombria é a minha maior prioridade. Não tenho tempo a perder com pistas que não levam a nada.", completion = "Nossa, você se provou um detetive e tanto, &lt;class&gt;.", rewards = "Você receberá:\n\nPágina de Diário Manchada de Sangue"},
[80] = {title = "A lenda de Galvão", objective = "Mostre ao Tabelião Azambuja a Página do Diário Manchado de Sangue.", description = "Fiquei de olho nesse idiota do Galvão por algum tempo. Mas se esta página foi escrita pela mesma mão, isso prova a culpa dele sem sombra de dúvida.\n\nAo cuidar do Registro da Cidade por tanto tempo, o Tabelião Azambuja tornou-se um especialista em identificar caligrafia. Mostre a ele esta página e veja se a letra corresponde à da assinatura do registro.", progress = "Já de volta, <name>? O que você trouxe desta vez?", completion = "Sim! A letra nesta página corresponde mesmo à das assinaturas do registro.", rewards = "Você receberá:\n\n"},
[81] = {title = "Entrega de suco gami", objective = "Entregue o Barril para Dran Droffers em Orgrimmar.", description = "Tomei a precaução extra de guardar não só o suco embalado do Dran no barril, mas também as garrafas extras que ele está furtando. Embora eu não tenha Dran em alta conta, não há nada pior do que suco chacoalhado. Não tem de quê.\n\nAgora, entregue aquele barril para Dran Droffers no ferro-velho dele em Orgrimmar. Por favor, estenda meus mais calorosos cumprimentos a Malton. Diga-lhe que, caso um dia esteja a fim de uma boa bebida e companheiros de bebida ainda melhores, ficaremos honrados com a visita dele.", progress = "Se você não está aqui para comprar nada, e não está aqui para entregar nada, então está me fazendo perder tempo! É melhor você ter o suco gami!", completion = "Bem, vamos ver o que tem neste barril!\n\nSim, sim, parece que está tudo aqui. Aqui está sua taxa pela entrega, bom trabalho. Posso ganhar cinco vezes isso quando vendê-lo para a taberna! Har har!\n\nReservarei estas garrafas extras de Suco Gami das Terras Agrestes para uma ocasião especial. Acho que vou tomar um pouco com champanhe. Eu vou chamá-lo de... champami.", rewards = "Você receberá:\n\n"},
[82] = {title = "Investigação no Covil Nóxio", objective = "Leve cinco Pedaços de Inseto Centiparte do Covil Nóxio para o Alquimista Almofarrista em Geringontzan.", description = "Pois bem, esses relatórios sobre os insetos... o que sabemos é que eles estão drenando as poças de água que ficavam onde agora se encontra o Covil Nóxio. O que NÃO sabemos é, bem, todo o resto.\n\nPrimeiro, vamos descobrir quem é o nosso inimigo! Quero que tu entre no Covil Nóxio e tire algumas partes dessas criaturas. Pegue cinco partes do centiparte lá e leve para o Alquimista Almofarrista. Com ele no caso, vamos descobrir rapidinho o que essas coisas são!", progress = "Tu é um topógrafo júnior, <name>, não é? Rápido, temos uma situação terrível em mãos! Tu trouxe as partes de inseto que Secamolha disse que traria?!", completion = "Bom trabalho... vou analisar isso já! Mas posso te dizer logo de cara... essas coisas não se parecem nada com qualquer forma de inseto que eu já tenha visto. É quase como se esses bichos estivessem sendo controlados por uma força externa ou algo assim.\n\nTu precisa falar agora mesmo com Secamolha. Aparentemente ele precisa da sua ajuda com uma emergência o mais rápido possível! Não tenho certeza, mas acho que envolve mais daqueles insetos no deserto de Tanaris!", rewards = "Você receberá:\n\n"},
[83] = {title = "Além do linho vermelho", objective = "Leve 6 Bandanas de Linho Vermelhas para Sara Albernaz na Madeireira Vale do Leste.", description = "Os Défias de Vila Norte usam máscaras de lona, mas os Défias de Elwynn usam linho. Eu preciso desse material pra fabricar itens finos.\n\nMe traga umas bandanas vermelhas de linho e eu vou poder costurar alguma coisa pra você usar.\n\nOs Défias acampam em bandos por toda a Floresta de Elwynn.", progress = "Estou ficando sem linho, <name>. Já conseguiu alguma coisa pra mim?", completion = "Ah, são boas bandanas, mesmo um pouco rotas...\n\nAqui está!", rewards = "Você receberá:\n\nCamisa Vermelha de LinhoFaixa de Linho Vermelho"},
[84] = {title = "...com a vontade de comer", objective = "Leve a Torta de Barriga de Porco para Guinho Madruga nos Vinhedos dos Madruga.", description = "Aqui está. Quando você entregar a torta pro Guinho, diga a ele que espero que ele morra engasgado!", progress = "Ai ai... Tô morrendo de fome! Você trouxe a torta, <name>?", completion = "Mm, que cheirinho... Essa torta é a melhor! Minha memória já tá até voltando...", rewards = "Você receberá:\n\n"},
[85] = {title = "O colar perdido", objective = "Fale com Guinho Madruga.", description = "Perdi o meu colar... Aposto que foi aquele trombadinha do Guinho Madruga quem roubou! Ele está sempre se esgueirando feito um rato pelos Vinhedos dos Madruga, a leste daqui. \n\nRecupere meu colar, e você dará alento ao pobre coração de uma viúva...", progress = nil, completion = "Você perdeu o quê? Bom, eu não peguei colar nenhum porque não sou nenhum ladrão!\n\nEu até acho que sei quem foi... mas...&lt;sorrisinho malandro&gt;... quando eu estou com fome eu esqueço tudo...", rewards = "Você receberá:\n\n"},
[86] = {title = "Juntando a fome...", objective = "Leve 4 Nacos de Carne de Javali Macia para a \"Titia\" Berenice Campedra na Fazenda dos Campedra.", description = "Se eu comesse tipo uma torta bem servida, acho que lembrava rapidinho de quem foi que pegou o colar. Sabe, a dona Berenice lá da outra fazenda faz uma Torta de Barriga de Porco muito da gostosa...\n\nQuem sabe se você não der pra ela uns nacos de carne de javali maciazinha, desses que vivem rondando por aqui, ela não faz uma torta pra você...?", progress = "Não acho certo dar comida pro moleque que me roubou, pra começo de conversa, mas se é isso que eu preciso fazer pra recuperar o meu colar, que seja!\n\nVocê trouxe a carne de javali?", completion = "Apesar da carne não ser das melhores, é só cozinhar o suficiente e ela dá uma torta e tanto!", rewards = "Você receberá:\n\n"},
[87] = {title = "Dentadouro", objective = "Leve o Colar de Berenice para \"Titia\" Berenice Campedra na Fazenda dos Campedra, que fica na Floresta de Elwynn.", description = "Eu tava brincando perto da Mina Vailafundo e acho que deixei o, ahm, quer dizer, vi o colar da dona Berenice lá. Não me pergunte como ele chegou lá, não sei de nada!\n\nMas, enfim, eu vi um kobold grandão, com um dente de ouro, pegar o colar e correr pra dentro da mina. Encontre o kobold e você vai encontrar o colar, pode apostar!", progress = "Olá, <name>. Você encontrou meu colar?", completion = "Você encontrou! Obrigada, obrigada, querido!\n\nAqui, aceite isto. Era do meu marido, e ele dizia que dava sorte. Quem dera ele não tivesse esquecido de levar na última campanha... *sniff*", rewards = "Você receberá:\n\nLuvas com Selo Leonino"},
[88] = {title = "Princesa tem que morrer!", objective = "Mate Princesa, pegue a coleira dela e leve-a para Mama Campedra na Fazenda dos Campedra.", description = "Os Braga tem uma porca premiada, chamada Princesa. A bicha é GIGANTE, e ficou assim de tanto vir aqui e devorar minha horta!\n\nPor isso, eu quero a Princesa morta antes que ela volte aqui aos nossos campos. Mate a porca e traga a coleira dela como prova, que eu lhe darei alguma coisa como pagamento!\n\nA Princesa geralmente está na nossa plantação de abóboras, que fica a leste, além da fazenda dos Madruga. Acabe com ela antes que volte aqui!", progress = "Já achou ela? Conseguiu pegá-la?", completion = "Graças aos deuses! Aquela porca estava ficando tão grande que fiquei com medo de perder a safra inteira! Obrigada, &lt;name&gt;.\n\nAgora, pode escolher o que preferir.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas GastasBraçadeiras com Cravos de LatãoBotas do Fazendeiro\n\nVocê também receberá:\n\n"},
[89] = {title = "A ponte do Lago Plácido", objective = "Leve 5 Piques de Ferro e 5 Rebites de Ferro para o Encarregado Oslow, na Vila Plácida.", description = "Durante a última invasão órquica, tivemos que usar todo o nosso ferro para fazer projéteis, espadas e armaduras. Pedimos um novo carregamento de materiais para Ventobravo, mas um bando de gnolls Cristarrubra atacou a caravana e fugiu para as colinas que ficam atrás da Vila Plácida.\n\nAgora a gente ficou sem suprimentos para reconstruir essa ponte. Se você me trouxer 5 piques de ferro e 5 rebites de ferro, <name>, vou te dar uma senhora recompensa.\n\nAgora, ao trabalho!", progress = "Essa ponte não vai se construir sozinha! Agora, cadê os piques e rebites que te pedi?", completion = "Muito bem, &lt;name&gt;. Esses suprimentos vão ajudar e muito na reconstrução da ponte. Essa belezinha vai estar tinindo em dois tempos.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCalças de FerreiroLuvas de Operário da PonteLuvas de Montaria\n\nVocê também receberá:\n\n"},
[90] = {title = "Kebab Temperado de Lobo", objective = "Colete 10 Lombos de Lobo Magro e Tempero de Ventobravo. Depois, fale novamente com o Chef Goroba, na Vila Sombria.", description = "Kebab Temperado de Lobo é um dos pratos favoritos da casa! É claro que posso fazer pra você. Mas antes, vou precisar dos ingredientes. Traga para mim 10 Lombos de Lobo Magro e Tempero de Ventobravo. Tem tanto lobo aqui na floresta que você não vai ter a menor dificuldade de encontrar os lombos. Para o tempero, fale com Felicia Gump. Ela tem uma loja de flores em Ventobravo, no distrito do canal.", progress = "Já conseguiu arrumar 10 Lombos de Lobo Magro? E o tempero da Felícia, em Ventobravo?", completion = "Ótimo trabalho, &lt;name&gt;! Agora aproveite essa iguaria conhecida como Kebab Temperado de Lobo!", rewards = "Você receberá:\n\nKebab Temperado de LoboReceita: Kebab Temperado de Lobo"},
[91] = {title = "A lei de Salomão", objective = "Leve 10 Pingentes Umbrapele para o Meirinho Conacher na Prefeitura de Vila Plácida.", description = "Um inimigo de Ventobravo se instalou bem aos pés de Cristarrubra, na face leste das montanhas. O perverso vilão usa o poder Arcano para aterrorizar nossa cidade e nosso povo. É sob a alcunha de Morganth que ele conduz seus negócios maléficos.\n\nE agora, sob o domínio de Morganth, o clã Gnoll Umbrapele está fazendo o trabalho sujo dele. Conforme a Lei de Vila Plácida, a pena para ajudar um criminoso inimigo do Reino é a morte.\n\nEsses terríveis gnolls têm que morrer!\n\nMe traga 10 Pingentes Umbrapele e você será recompensad<o/a>.", progress = "Me entregue 10 Pingentes Umbrapele e você será recompensado.", completion = "A Corte de Vila Plácida reconhece o seu compromisso com a legalidade, &lt;name&gt;. Você fez valer as leis do Reino e nos ajudou a trazer justiça para essas terras.", rewards = "Você receberá:\n\n"},
[92] = {title = "Gulache de Cristarrubra", objective = "A Mestre-cuca Breanna de Vila Plácida quer 5 peças de carne dura de condor, 5 focinhos de grande goretusco e 5 porções de carne crocante de aranha.", description = "Nada me agradaria mais do que fazer um belo Gulache de Cristarrubra pra você, mas tem um probleminha. Durante a última revolta dos orcs, eu precisei alimentar uma brigada inteira das tropas de Ventobravo. Não sobrou nada na despensa. Se você me trouxer os ingredientes, preparo o prato com prazer.\n\nSão cinco peças de carne dura de condor, cinco focinhos de grande goretusco e cinco porções de carne crocante de aranha.", progress = "Ainda preciso de cinco peças de carne dura de condor, cinco focinhos de grande goretusco e cinco porções de carne crocante de aranha.", completion = "Bom trabalho, &lt;name&gt;. E que belos exemplares você conseguiu. Aqui está minha especialidade, o delicioso Gulache de Cristarrubra.", rewards = "Você receberá:\n\nGulache de CristarrubraReceita: Gulache de Cristarrubra"},
[93] = {title = "Bolinhos de Caranguejo Crepuscular", objective = "Colete 6 Pernas de Aranhas Pegajosas e leve para o Chef Goroba, na Vila Sombria.", description = "Vou te contar um segredinho — os bolinhos de caranguejo crepuscular são, na verdade, feitos de pernas de aranha! Eu sei que é um pouco nojento, mas os bolinhos ficam com um sabor pungente delicioso, além de darem um ótimo tira-gosto! Traga algumas pernas de aranha pegajosas que eu faço uns para você.\n\nOuvi dizer que as venaracnas dão muita carne. Os ninhos delas ficam a norte, no vale entre as montanhas e o rio.", progress = "Saudações, <name>. Já conseguiu aquelas Pernas de Aranhas Pegajosas?", completion = "Ah, que belos nacos de perna você tem aí! Falta só adicionar o meu tempero secreto (não pode olhar!) e botar para refogar em uma frigideira...\n\nOs bolinhos de caranguejo crepuscular são a minha especialidade, por isso não dou a receita para ninguém. Mas tome aqui a receita de outro prato tão bom quanto meus bolinhos.", rewards = "Você receberá:\n\nReceita: Bolo de Aranha Pegajoso"},
[94] = {title = "Olho vigilante", objective = "Ponha o Glifo de Azora na Estátua de Leão perto da Torre de Ilgalar em Cristarrubra.", description = "Morganth, meu rival, é um mago cruel com grande poder que vive na Torre de Ilgalar, em Cristarrubra. Eu criei uma maneira de espionar o que ele faz, o Olho de Azora, e agora posso observar de longe.\n\nMas para perscrutar a mente dele... eu vou precisar de ajuda.\n\nSe você estiver dispost<o/a>, leve meu Glifo de Azora até a Torre de Ilgalar. Ponha ele na Estátua de Leão ao lado da Torre.\n\nQuando você fizer isso, o Olho de Azora me permitirá ver os futuros planos de Morganth.", progress = "A Estátua do Leão está há décadas de guarda neste lugar, vigiando solitária a infame Torre de Ilgalar.\n\nNa base da estátua, você nota um pequeno engaste, onde talvez seja possível encaixar uma gema ou glifo...", completion = "Você põe o Glifo de Azora na pedra. Quando ele se fixa, você ouve a voz incorpórea de Teócrito:\n\nVocê tem minha gratidão, jovem &lt;name&gt;. Agora podemos nos comunicar por meio deste Glifo. Talvez surjam novas tarefas para você... depois que eu sondar a mente do meu rival.", rewards = "Você receberá:\n\n"},
[95] = {title = "A vingança de Noel", objective = "Vá até a Fazenda do Noel e descubra o que foi enterrado atrás do velho cepo de madeira.", description = "Enquanto eu cuidava de uma das fazendas a sudeste... os Cavalgantes Negros da Trilha do Vento Morto invadiram minha fazenda e massacraram minha família!\n\nQuando voltei, notei uma figura sombria à espreita perto do celeiro, enterrando alguma coisa. Ele conseguiu fugir e eu não fui atrás porque estava no rastro dos Cavalgantes Negros. Então, nunca soube o que ele escondia.\n\nSe puder descobrir o que essa figura sombria enterrou lá, serei grato. O local do esconderijo fica atrás do velho cepo de madeira, perto do celeiro.", progress = nil, completion = "Há um montinho de terra aqui, feito às pressas, como se alguém estivesse escondendo alguma coisa... e estivesse sem tempo.", rewards = "Você receberá:\n\n"},
[96] = {title = "Clamor da água", objective = "Leve o Estilhaço de Água para Islen Vidente da Água nos Sertões.", description = "Pega estilhaço. Pega estilhaço e dá vida.\n\nDá vida e compreende. Compreende água é vida, corrupção extingue vida.\n\nVocê protege e dá vida com poder da água.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nTotem de Torrente Curativa\n\nO seguinte feitiço será lançado em você:\n\nTotem de Torrente Curativa\n\nVocê também receberá:\n\nTotem da Água"},
[97] = {title = "A lenda de Galvão", objective = "Vá dar a notícia à Comandante Ebonlocke.", description = "Vá imediatamente até a Comandante Ebonlocke e avise que a caligrafia é mesmo do Galvão, <name>! As suspeitas dela estavam corretas!", progress = nil, completion = "Eu sabia! Bom trabalho, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[98] = {title = "A lenda de Galvão", objective = "Mate Galvão Brumanto e mostre o anel da família dele para Madame Eva.", description = "Galvão Brumanto sempre teve uma vida depravada. Matou inocentes com as próprias mãos. Sem dúvida é culpado de muitos crimes. Agora o lunático está ameaçando Vila Sombria, e só a Luz sabe que atos sórdidos ele está tramando. Vá à cabana dele, logo ao norte da cidade, <class>, e execute o Galvão de uma vez por todas.\n\nDepois que terminar, vá até a Madame Eva e mostre a ela o anel da família dele. Afinal, foi graças a uma premonição dela que fizemos essa terrível descoberta. Por causa dela, Vila Sombria está mais segura agora.", progress = "Pois não, <name>?", completion = "&lt;name&gt;, eu já sei: Galvão morreu. Senti uma onda de esperança correndo pela floresta maculada. De uma vez por todas, a Floresta do Crepúsculo está livre da sanha sanguinária dele. Minha alegria só não é maior porque não deixo de lembrar de todos os que foram vitimadas por aquele ser medonho. Ainda assim, &lt;class&gt;, a sua bravura e a sua astúcia devem ser celebradas.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCrescente dos Espíritos DesamparadosAnel dos Espíritos Desamparados\n\nVocê também receberá:\n\n"},
[99] = {title = "A loucura de Arugal", objective = "Leve 6 Grilhas de Lenhardente para Dalar Tessalba, no Sepulcro.", description = "Quanto mais aprendo sobre a magia de Arugal, maior o meu desprezo por aquele tolo desafortunado. Estou prestes a terminar minha pesquisa sobre o suposto remédio dele.\n\nMeu conhecimento estará completo quando descobrir qual é o encantamento que está causando um comportamento anômalo no Vilarejo Lenhardente. Durante o dia, os camponeses parecem ser humanos. Mas, assim que o sol se põe, os aldeões se transformam em Worgens Lunafúria.\n\nPreciso extrair energia das grilhas encantadas que Arugal usou neles. Traga-me seis Grilhas de Lenhardente encantadas, <name>.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nRachalenhaPilão para OssosVarinha de Cinzas\n\nO seguinte feitiço será lançado em você:\n\nIntelecto Arcano\n\nVocê também receberá:\n\n"},
[100] = {title = "O clamor da Água", objective = "Fale com a Manifestação Menor da Água na Floresta de Pinhaprata.", description = "Ao encerrar o encantamento, um clarão ilumina a cena. Não se ouve mais o barulho das gotas d'água caindo nas chamas, e a destruição dos espíritos corrompidos à sua volta domina todos os seus sentidos.", progress = nil, completion = nil, rewards = nil},
[101] = {title = "O totem do castigo", objective = "Leve 10 Presas de Carniçal, 10 Dedos Descarnados e 5 Ampolas de Peçonha de Aranha para a Madame Eva na Vila Sombria.", description = "Um mal místico se espalha pela floresta, a necessidade de se proteger é inegável, <name>. Os ventos sussurraram nos meus ouvidos que um grande perigo <o/a> aguarda no futuro próximo.\n\nSe quiser se proteger, nobre <class>, traga-me 10 presas de carniçal, 10 dedos descarnados e 5 ampolas de peçonha de aranha. Vou encantar um Totem do Castigo que vai retaliar todos que tentarem usar de violência contra você.", progress = "Traga-me 10 presas de carniçal, 10 dedos descarnados e 5 ampolas de peçonha de aranha. Vou encantar um Totem do Castigo que vai retaliar todos que tentarem usar de violência contra você.", completion = "Muito sábio da sua parte, &lt;name&gt;. O Totem do Castigo vai protegê-lo de tudo o que há de mal em Azeroth e além. Sempre que se sentir ameaçado, agarre-se ao totem e busque abrigo na aura mágica dele. Coragem, nobre &lt;class&gt;. Você precisará de muita coragem, pois eu prevejo um banho de sangue no seu futuro.", rewards = "Você receberá:\n\nTotem do Castigo"},
[102] = {title = "A patrulha do Cerro Oeste", objective = "Leve 8 Patas de Gnoll para o Capitão Danuvin no Morro da Sentinela.", description = "Ventobravo nos abandonou. Um vento rançoso sopra pelas planícies do Cerro Oeste. Aqui é a minha terra natal e eu não darei as costas a quem escolheu ficar. Nós, os fazendeiros de antigamente, vamos resistir.\n\nA sua tarefa, caso você a aceite, será patrulhar os campos do Cerro Oeste. Localize e mate esses gnolls malditos que, pelo que tudo indica, estão mancomunados com os bandidos das Minas Mortas. Traga-me oito patas de gnoll e eu <o/a> recompensarei pela coragem.", progress = "Você já recolheu oito patas daqueles gnolls traiçoeiros?", completion = "Bom trabalho, &lt;name&gt;. Com aventureiros valentes como você lutando ao lado da Milícia do Povo, o Cerro Oeste logo voltará à época das vacas gordas de outrora. Queremos lhe dar uma coisinha em reconhecimento ao seu empenho.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCinto da Milícia do PovoBraçadeiras da Milícia do Povo\n\nVocê também receberá:\n\n"},
[103] = {title = "A chama não pode apagar", objective = "Entregue 5 Frascos de Óleo para o Capitão Calvino no Farol de Cerro Oeste.", description = "A noite em que a família do faroleiro morreu foi terrível. Eu assisti, impotente, ao ataque liderado pelo Velho Olho-turvo. Mas o que está feito não se pode mudar. Agora, minha preocupação é com a vida dos marinheiros do Grande Oceano, cujos navios se aproximam dos perigosos rochedos da costa. Como não há vivalma para manter a chama do farol acesa, a responsabilidade recaiu sobre mim.\n\nAjude-me a manter a chama acesa trazendo cinco frascos de óleo dos monstros da colheita.", progress = "A chama não vai se manter por muito mais tempo sem óleo, <name>.", completion = "Bendito seja, bravo &lt;class&gt;. Os rochedos da costa de Cerro Oeste ficarão iluminados graças aos seus esforços. Muitas vidas serão poupadas enquanto a tocha estiver acesa.\n\nEu sofri uma morte insensata e desnecessária nestes rochedos, e meu compromisso no outro mundo é cuidar para que ninguém mais tenha esse destino.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nPoção Menor de ManaPoção Menor de Cura\n\nVocê também receberá:\n\nPergaminho do IntelectoPergaminho do Vigor"},
[104] = {title = "O mar não está para peixe", objective = "Leve uma escama do Velho Olho-turvo para o Capitão Calvino no Farol de Cerro Oeste.", description = "Quando eu perdi a vida nesses rochedos, não fazia ideia do que a vida após a morte reservava para mim. Naquela noite, o Farol encontrava-se apagado, pois o Velho Olho-turvo afugentou a família do faroleiro, porém eles retornaram posteriormente e reacenderam o facho de luz. O Velho Olho-turvo então coagiu os murlocs de mente mais fraca a invadir novamente o farol com ele. Desta feita, a família não teve tanta sorte e pereceram todos diante dos meus olhos.\n\nMate o Velho Olho-turvo caso você o veja pela praia e traga-me uma de suas escamas. Garanto-lhe que minha recompensa será generosa.", progress = "Você já deu fim a essa ameaça que responde pela alcunha de Velho Olho-turvo? Ele foi visto a vagar pelo litoral de Cerro Oeste.\n\nVolte aqui quando aquela criatura asquerosa estiver morta.", completion = "Então aquele bárbaro sórdido a quem chamam de Olho-turvo está morto. Muito bem, &lt;name&gt;. Pelas suas mãos uma vida se perdeu, mas muitas foram salvas. O Grande Oceano, por mais perigoso que seja, será um mar de rosas esta noite graças à sua façanha heroica.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nTocha do CalvinoBroquel dos MaresVarinha de Luz da Tocha\n\nVocê também receberá:\n\n"},
[105] = {title = "Adeus, Andorhal", objective = "Leve o Fragmento de Filactério de Araj para o Alto-executor Arcabuz no Baluarte, que fica nas Terras Pestilentas Ocidentais.", description = "Eis que chegou a hora de atacar Andorhal e expulsar o lich que a controla!\n\nAs ruínas da cidade são o domínio do lich — Araj, o Evocador. Seus protetores são inúmeros Flagelos que rodeiam os destroços do centro da cidade, além de uma comitiva formidável de guardiões que estão diretamente ligados a ele. Você vai precisar de muitos aliados para sequer ter um chance de derrotá-lo.\n\nDestrua Araj, <name>, e me traga um fragmento do filactério dele como prova!", progress = "Você tinha uma tarefa a cumprir, <name>. Trouxe o fragmento do filactério do lich para provar que fez o que tinha que fazer?", completion = "Você conseguiu! Araj... o lich, enfim, derrotado!\n\nHoje você prestou um tremendo serviço à Horda, &lt;name&gt;. Sua bravura e perseverança são características dos heróis de verdade. Por favor, aceite isto como um presente, para que todos saibam que a sua conquista fez história para a Horda!", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nMarca da Determinação"},
[106] = {title = "Jovens amantes", objective = "Entregue a Carta de Amor de Mabel para Tomasino Campedra.", description = "Ah, maldita sorte a minha! Meu coração pertence a Tomasino Campedra, mas nossas famílias se odeiam fervorosamente. Por isso não posso estar com ele, mesmo que meus olhos doam de vontade de ficar admirando aquele rosto lindo!\n\nPor favor, poderia entregar esta carta para ele, por mim? Ele costuma passar o tempo perto do rio a oeste daqui, depois da Fazenda dos Campedra.", progress = "Você trouxe o quê?? Mabel é a luz dessa minha vida insossa. Rápido, deixe-me ver essa carta!", completion = "Ah! Não consigo suportar nossa separação. Preciso vê-la!!", rewards = "Você receberá:\n\n"},
[107] = {title = "Bilhete para Durval", objective = "Leve o Bilhete da Vó Campedra para Durval Pilão.", description = "Aposto que Durval Pilão tem uma poção para unir jovens amantes!\n\nAqui, leve esse bilhete para ele. Ele está hospedado na Estalagem do Leão Orgulhoso em Vila d'Ouro.", progress = "Então você tem um bilhete da \"Vó\" Campedra, não é? Faz anos que não vejo Mildred! O que será que ela quer?", completion = "Meu coração se confrange com a sorte daqueles dois, Mabel e Tomasino... Eu me lembro de quando era um jovem apaixonado...\n\nDeve haver algo que possamos fazer para ajudá-los! Deixe-me pensar...", rewards = "Você receberá:\n\n"},
[108] = {title = "Recompensa misteriosa", objective = "Você receberá:\n\n", description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nRecompensa Desconhecida"},
[109] = {title = "Fale com Miguel Mantoforte", objective = "Fale com Miguel Mantoforte. Ele costuma ficar na torre de pedras do Morro da Sentinela, perto da estrada central do Cerro Oeste.", description = "Combate parece ter sido uma constante para você, <class>. Se ainda não o fez, você deveria falar com Miguel Mantoforte. Ele chefia a Milícia do Povo, que busca proteger as fazendas do Cerro Oeste. Aposto que sua ajuda seria bem-vinda. Ele costuma ficar na torre de pedras do Morro da Sentinela, perto da estrada central do Cerro Oeste.", progress = nil, completion = "Ah, meu amigo mandou você aqui? Que gentileza.\n\nBem, a monarquia de Ventobravo abandonou nossa causa. Agora, cabe à Milícia do Povo livrar esta terra da corrupção. Se nossa causa for de seu interesse, posso tirar proveito das suas habilidades de combate em prol da liberdade.", rewards = "Você receberá:\n\n"},
[110] = {title = "Análise de pedaços de inseto", objective = "Veja se o Alquimista Almofarrista em Geringontzan já terminou a análise dos pedaços de inseto.", description = "Vou tirar um tempo pra dar uma olhada no aparelho de inspeção do Caranduru... e também vou dar uma ajeitada nas coisas dele. Tô com uma pulga atrás da orelha me dizendo que essas pragas ainda vão causar mais perdas.\n\nVou te pedir pra falar com o Alquimista Almofarrista pra ver se ele já analisou os pedaços de inseto que mandamos. Se já tiver tudo pronto, traz pra mim.", progress = nil, completion = "Sim, estou com os resultados aqui... Eles são, no mínimo... perturbadores.\n\nNossa suspeita inicial era que tinha uma força externa mandando nesses bichos, e acreditamos que seja isso mesmo. Se for esse o caso, não só tem uma força muito poderosa nisso aí, mas ela também é maligna. Essa bicharada ficou destruidora. Nunca vi nada igual.", rewards = "Você receberá:\n\n"},
[111] = {title = "Fale com a vovó", objective = "Fale com a Vovó Campedra.", description = "Por favor, <name>, fale com minha avó. Se alguém pode pensar numa maneira de unir Mabel e eu, é ela.\n\nEla está na nossa casa, a leste daqui.", progress = nil, completion = "Enquanto nossas famílias estiverem brigando, Tomasino e Mabel não têm um futuro muito promissor juntos, mas... talvez possamos uni-los por algum tempo.\n\nHum... o que podemos fazer...?", rewards = "Você receberá:\n\n"},
[112] = {title = "Coletando alga", objective = "Leve 4 Folhas de Cristalga para Durval Pilão em Vila d'Ouro.", description = "Posso preparar um elixir de invisibilidade para Mabel. Assim ela poderá fugir dos Vinhedos dos Madruga e procurar Tomasino. Mas, para fazer o elixir, vou precisar de cristalgas.\n\nEmbora as algas cresçam no mar, às vezes os murlocs as recolhem. Veja se os murlocs do Lago de Cristal têm cristalgas. O Lago de Cristal fica a leste de Vila d'Ouro.", progress = "Conseguiu as cristalgas? Mabel deve estar louca pra ver o namorado...", completion = "Você conseguiu. Bom trabalho! Agora, espere um pouco enquanto preparo a poção...", rewards = "Você receberá:\n\n"},
[113] = {title = "Análise de pedaços de inseto", objective = "Leve o relatório para o Topógrafo Sênior Secamolha em Geringontzan. Certifique-se de que ele lhe dê uma cópia, conforme solicitado pelo Alquimista Almofarrista.", description = "O Topógrafo Sênior Secamolha precisa ver este relatório para ontem. Na verdade... todo mundo que sabe ler precisa ver isso. Estamos diante de uma grave ameaça não só a Tanaris e Geringontzan, mas a toda região sul de Kalimdor, caso nenhuma providência seja tomada. Diabos, talvez a toda Azeroth... esse negócio não dá nenhum indício de trégua.\n\nPeça ao Secamolha para fazer uma cópia do relatório e leve-a à sua facção. Todos precisam saber dessa ameaça!", progress = "E então, Topógrafo Júnior <name>, o velho Almofarrista já fez o relatório?", completion = "Obrigado por entregar o relatório, Topógrafo Júnior &lt;name&gt;. Aqui está o pagamento pelo serviço prestado à Companhia de Saneamento de Geringontzan.\n\nNão me alegra nem um pouco dar informações valiosas de mão beijada a quem pagaria uma nota preta por elas, mas já faz uns bons anos que eu conheço o Almofarrista. Se a recomendação dele é divulgar a informação, que ela seja divulgada. Marque um minutinho aí, que eu já faço a cópia.", rewards = "Você receberá:\n\n"},
[114] = {title = "A fuga", objective = "Leve o Elixir de Invisibilidade para Mabel Madruga.", description = "Leve esse elixir de invisibilidade para a jovem Mabel. Deve durar o suficiente para que ela se encontre com Tomasino.", progress = "Você entregou minha carta para Tomasino? O que ele disse??", completion = "Oh, minha nossa! Eu me sinto culpada por enganar minha família, mas meus sentimentos por Tomasino são fortes demais para ignorar.\n\nObrigada, &lt;name&gt;! Vou beber esse elixir assim que tiver uma chance e, então, irei encontrar meu amor!\n\nE quanto a você... por favor, aceite isto.", rewards = "Você receberá:\n\nPoção Menor de Cura"},
[115] = {title = "Magia Sombria", objective = "Leve 3 Orbes da Meia-noite para o Oficial Marris em Vila Plácida.", description = "Os orcs Rocha Negra convocaram lança-sombras para ajudar no ataque a Cristarrubra, e eles trouxeram consigo artefatos de poder tenebroso: os orbes da meia-noite. Esses orbes vem desferindo golpes pesados contra os defensores de Cristarrubra, temos que removê-los do conflito urgentemente.\n\nRecolha os orbes dos Lança-sombras Rocha Negra mortos e traga para mim. Eu vou tratar de descartá-los, o mundo será um lugar bem melhor sem eles!", progress = "Em que pé anda o conflito com os lança-sombras? Você encontrou os orbes da meia-noite?", completion = "Obrigado, &lt;name&gt;. Vou mandar destruí-los imediatamente.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[116] = {title = "Tempos de seca", objective = "O Taberneiro Daniel da Vila Plácida precisa de um barril de Cervaforte, um barril de vinho Merlot, uma garrafa de Pinga e um odre de Rum Doce.", description = "A coisa está feia para o meu lado, <name>. As garrafas estão quase secando. A entrega do próximo carregamento já está para lá de atrasada. A invasão órquica foi um inferno.\n\nVocê não teria como me ajudar?\n\nEu preciso que você busque um barril de Cervaforte do Canabraba Cervaforte, em Cerro Oeste; um tonel de Merlot em Ventobravo; uma garrafa de Pinga na Vila Sombria e um odre de Rum Doce em Vila d'Ouro. Traga-me tudo e eu lhe darei uma recompensa.", progress = "Eu quero que você busque um barril de Cervaforte do Canabraba Cervaforte, em Cerro Oeste; um barril de vinho Merlot em Ventobravo; uma garrafa de Pinga na Vila Sombria e um odre de Rum Doce em Vila d'Ouro. Traga-me tudo e eu lhe darei uma recompensa.", completion = "Bom trabalho, &lt;name&gt;! Eu conheço bem as estradas que você precisou percorrer para buscar toda essa bebida. Mas meus clientes ficarão satisfeitos! \n\nMinha esposa é uma grande costureira. Queira aceitar esse manto que ela fez como pagamento pelo serviço.", rewards = "Você receberá:\n\nManto Finamente TecidoAlgibeira Cheia de Moedas"},
[117] = {title = "Cervaforte", objective = "Leve 5 lúpulos para Canabraba Cervaforte fazer sua cerveja especial.", description = "Vamos apressar o passo, camarada, \nA receita não pode ficar errada\nCerveja precisa de lúpulo e trigo,\nPara não dar gostinho de milho.", progress = "Vamos apressar o passo, camarada, \nA receita não pode ficar errada\nCerveja precisa de lúpulo e trigo,\nPara não dar gostinho de milho.", completion = "Lúpulo, cevada, levedura e malte\nPra que na papila gustativa nada falte. \nQuem gosta mesmo, já se viu\nVai beber Cervaforte direto do barril!", rewards = "Você receberá:\n\nBarrilete de Cervaforte"},
[118] = {title = "O preço da ferradura", objective = "Leve o Bilhete de Vervo para o Ferreiro Argus em Vila d'Ouro.", description = "Eu tenho usado tanto ferro para manter os Guardas de Ventobravo equipados, que não sobrou nem para fazer ferradura para os cavalos do estábulo!\n\nLeve esse bilhete para o Ferreiro Argus em Vila d'Ouro. Eu expliquei aí meu problema e fiz um pedido de ferraduras.", progress = "Como é? O Vervo mandou você, foi? Passa o bilhete para cá. E DESEMBUCHA LOGO!", completion = "He, então o velho Vervo anda precisando de sapatos, é!?!", rewards = "Você receberá:\n\n"},
[119] = {title = "Fale novamente com Vervo", objective = "Fale novamente com Vervo Obom em Cristarrubra e entregue o Caixote de Ferraduras.", description = "Muito bem, eu vou mandar 50 ferraduras...\n\nAqui estão, encaixotadas e prontas para ir! Diga para o Vervo que foi um prazer fazer negócios, e não deixe ele esquecer de ler o bilhete que está no caixote!", progress = "Ótimo, você voltou! Conseguiu as ferraduras?", completion = "Obrigado, será de grande ajuda.\n\nO que é isso? O Argus mandou um bilhete dentro do caixote... O quê? Ele quer que EU pague? Afe!\n\nBem, &lt;name&gt;, obrigado pela ajuda.", rewards = "Você receberá:\n\n"},
[120] = {title = "Mensagem para Ventobravo", objective = "O Magistrado Salomão lhe entregou um relatório que deve ser entregue ao General Marcus Jonas em Ventobravo. O juiz quer que você fale novamente com ele assim que a entrega for feita.", description = "Estes são tempos difíceis, <class>. A cidade está sob cerco constante. Sem reforços, nós certamente seremos derrotados. A mensagem que agora confio a você é da maior importância. Leve este relatório para o General Marcus Jonas de Ventobravo imediatamente. Uma vez que tiver realizado a entrega, traga qualquer notícia que tiver para mim, seja boa ou ruim.\n\nAgora, se apresse!", progress = "O que você tem aí?", completion = "Descansar, &lt;class&gt;.\n\nO Magistrado Salomão é um nobre líder. As palavras dele têm muito peso para mim. Eu me aconselharei com o rei e esclarecei a situação para ele. Assegure o bom juiz de que ele tem o apoio do exército de Ventobravo. Os reforços serão enviados assim que Sua Majestade ordenar.", rewards = "Você receberá:\n\n"},
[121] = {title = "Mensagem para Ventobravo", objective = "Leve a resposta do General Marcus Jonas para o Magistrado Salomão em Vila Plácida.", description = "Enquanto espero uma resposta do rei, eu quero que você leve esta carta para o Magistrado Salomão.\n\n<Dispensado/Dispensada>, <class>!", progress = "O general mandou notícias? Os reforços estão a caminho?", completion = "Obrigado pela atenção, &lt;class&gt;. Pelos seus serviços à Vila Plácida e Ventobravo, eu lhe dou estas moedas.\n\nAgora, se me desculpar, eu acho esta correspondência um tanto misteriosa. Há algo de errado no nosso reino. Eu temo que este seja o início de um conflito, não o fim.", rewards = "Você receberá:\n\n"},
[122] = {title = "Escamas do ventre", objective = "Colete 6 Escamas de Ventre de Dragonete Negro e leve-as para Vervo Obom em Cristarrubra.", description = "Eu preciso de escamas do ventre de um filhote de dragonete negro para pagar as ferraduras que o Argus mandou de Vila d'Ouro. Se conseguir 6 delas, eu vou poder pagar o Argus... e guardar umas para fazer alguma coisa para você.\n\nOs filhotes de dragonete negro costumam voar lá pelo sul da Vila Plácida, mas gostam de perambular por aí. Você só precisa ficar de olho aberto e caçá-los quando os encontrar.", progress = "Eu sei que o mão de vaca do Argus vai mandar alguém para cobrar as escamas se eu não as entregar logo. Você já achou as escamas?", completion = "Obrigado! Isso vai quitar a minha dívida com o Argus...", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas de Dragonete NegroLuvas de Dragonete Negro\n\nVocê também receberá:\n\n"},
[123] = {title = "O Coletor", objective = "Fale com o Delegado Durão na Vila d'Ouro e lhe entregue a Escala do Coletor.", description = "O bilhete contém uma escala com os dias e horários em que uma pessoa – referida apenas como \"o Coletor\" – receberá um carregamento de ouro das minas da Floresta de Elwynn.\n\nPela escala, parece que o Coletor mora perto da Plantação de Abóboras dos Braga, na parte leste de Elwynn.\n\nIsso parece importante. Você deveria relatar isto ao Delegado Durão na Vila d'Ouro.", progress = "O quê?!? Nosso pessoal já não trabalha nas minas de Elwynn há meses!\n\nDeixe eu ver esse bilhete...", completion = "Hum... Eu já tinha ouvido falar desse \"Coletor\", mas não sei pra quem ele trabalha. Obrigado por trazer essa escala pra mim. Ela nos ajudará a resolver esse mistério.", rewards = "Você receberá:\n\n"},
[124] = {title = "Gnoll que ladra também morde", objective = "Mate 10 Brutamontes de Cristarrubra e 8 Místicos de Cristarrubra e fale novamente com Vervo Obom.", description = "Como se orcs atacando não fosse suficiente! Agora tem gnolls brutamontes e místicos vagando ao norte do meu estábulo, roubando os cavalos que se perdem...\n\nSe você puder nos livrar desses gnolls, os meus cavalos e eu vamos ficar muito agradecidos.", progress = nil, completion = "Obrigado pela ajuda, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[125] = {title = "As ferramentas perdidas", objective = "O Encarregado Oslow quer que você recupere sua caixa de ferramentas no fundo do Lago Plácido.", description = "Eu estou precisando de uma mãozinha, <name>. Com a cidade cercada, está sendo difícil conseguir suprimentos. As minhas ferramentas estavam vindo de carroça da Vila d'Ouro, mas a ponte foi destruída. Nós colocamos as ferramentas em um barco, mas os orcs acertaram ele com uma catapulta. Para o meu azar, a minha caixa de ferramentas foi parar no fundo do lago.\n\nRecupere a minha caixa de ferramentas, <name>, e eu lhe darei uma boa recompensa.", progress = "Conseguiu encontrar as minhas ferramentas?", completion = "Que resgate incrível, &lt;name&gt;! Achei que não fosse ver minhas ferramentas de novo.", rewards = "Você receberá:\n\nKit de Armadura Média"},
[126] = {title = "O gnoll de Vila Plácida", objective = "Leve a Pata de Berrante para Vervo Obom na Vila Plácida.", description = "<name>, os gnolls ainda estão por aí. Eu ouço o uivo quando eles caçam nas colinas acima da Vila Plácida. E um ladra mais alto do que o resto: Berrante, o líder.\n\nMate o Berrante e enxote o resto da matilha! Traga a pata dele como prova.\n\nMas tenha cuidado, esse gnoll não é o líder a toa. Ele não é um vira-lata qualquer...", progress = "O Berrante é uma criatura perigosa, <name>. Se não estiver pronto para ele, eu vou entender.", completion = "Mais uma vez, obrigado pela ajuda. O exército de Ventobravo está tão ocupado com os orcs que não sobram homens para proteger Cristarrubra de ameaças locais, como os gnolls.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nAnel de Vontade FérreaBotas Douradas de Milícia\n\nVocê também receberá:\n\n"},
[127] = {title = "O lago está para peixe", objective = "Leve 10 Peixes-lua Malhados para o Mestre de Doca Baren na Vila Plácida.", description = "O Lago Plácido é famoso pelo peixe-lua malhado. Eles estão sempre em demanda... e o meu estoque anda baixo!\n\nTraga 10 peixes e eu lhe darei algo em troca. Se não conseguir pescá-los... É capaz de você conseguir tomar os dos murlocs!", progress = "Você trouxe peixe-lua para mim, foi?", completion = "É um prazer fazer negócios com você, camarada!", rewards = "Você receberá:\n\nSopa de Barbatana de MurlocÓleo de Fígado de PeixeReceita: Sopa de Barbatana de Murloc"},
[128] = {title = "Recompensa pelos Rocha Negra", objective = "Mate 15 Campeões Rocha Negra e o Guarda Howe vai recompensar você na Vila Plácida.", description = "Precisamos destruir o Clã Rocha Negra. O inimigo invadiu nosso território. Essas montanhas estão sob direito soberano do Rei de Ventobravo. Nossas fontes informam que os líderes das unidades do Clã são denominados Campeões. Procure por eles no acampamento ao norte, a oeste da estrada para as Estepes Ardentes.\n\nO Magistrado Salomão quer que esses brutamontes pérfidos sejam eliminados. Mate 15 Campeões Rocha Negra e fale novamente comigo.", progress = nil, completion = "Vejo que você enfrentou o Clã Rocha Negra com valentia, &lt;class&gt;. Você prestou um grande serviço ao nosso distrito.", rewards = "Você receberá:\n\n"},
[129] = {title = "Almoço grátis", objective = "Leve o Almoço do Florestan para o Guarda Florestan. Ele patrulha a estrada que leva à Vila Sombria.", description = "Me faz um favor? Fiz um almoço para o Guarda Florestan, mas ele saiu na patrulha... Ele é um guarda de Ventobravo forte, alto, que sabe se cuidar, mas para uma cidadã como eu é muito arriscado sair.\n\nEntão, se puder entregar o almoço por mim, eu darei a você um almoço de graça! Florestan faz patrulhas num trecho da estrada que leva à Floresta do Crepúsculo.", progress = "Darcy mandou almoço, foi? Ela tem mesmo um coração de ouro. Bem... hora de papar!", completion = "Obrigado, um almoço vai bem... Proteger a Vila Plácida dos orcs e gnolls dá trabalho!", rewards = "Você receberá:\n\n"},
[130] = {title = "Visita à herborista", objective = "Falar com a Herborista de Cristarrubra, Martie Jainrose.", description = "Antes de falar outra vez com a Darcy, será que você podia entregar umas flores a ela por mim? Passe na loja da herborista da Vila Plácida, na ponta oeste da vila.\n\nMas quando pegar as flores... não diga à Martie, a herborista, nem de quem nem para quem são.", progress = nil, completion = "Procurando um buquê de flores? Você chegou na vila não faz muito... e já arranjou um chamego por aqui?\n\nSei que não é da minha conta, mas é bom saber que o amor está no ar... principalmente nesses dias perigosos.", rewards = "Você receberá:\n\n"},
[131] = {title = "Narcisos para entrega", objective = "Dê para Darcy o Buquê de Narcisos.", description = "Aqui está o buquê. Escolhi Narcisos. São minhas favoritas!", progress = "Olá outra vez, <name>. Florestan gostou do almoço?", completion = "Ah, que graça! Mal posso esperar para pôr num vaso!\n\nMas... essas são as flores favoritas da Martie. Florestan não pediu para você pegar esse buquê com aquela cobra invejosa, pediu? Você não contou a ela para quem era o buquê, disse? Se você fez isso, não me espantaria se estivessem envenenadas.\n\nAh, mas você não tem culpa. Obrigada, aqui está sua refeição.", rewards = "Você receberá:\n\nPeixe-lua Refogado"},
[132] = {title = "A Irmandade Défias", objective = "Leve o Bilhete do Wiley para Miguel Mantoforte no Cerro Oeste.", description = "O que vou revelar a você pode custar a minha vida. A gangue Défias está armando alguma coisa grande. A última coisa que fiquei sabendo é que estão em conluio com vários gnolls, kobolds e até goblins.\n\nLeve este bilhete para Mantoforte. Ele contém tudo que sei sobre isso.", progress = "É bom ver você outra vez, <name>. O que Wiley tem a dizer em defesa?", completion = "Puxa, devia ter deixado aquele patife apodrecer quando tive a chance, mas obter informações é crucial. Bom trabalho.", rewards = "Você receberá:\n\n"},
[133] = {title = "Boneco de carniçal", objective = "Colete 7 Costelas de Carniçal e leve-as para Abercrombie na cabana dele.", description = "Vivo tão longe da segurança da cidade, que é um milagre eu não ter virado comida de morto-vivo e zumbi! Na verdade, ontem mesmo eu vi um bando de matigossos batendo nas paredes da minha casa!\n\nEu quero fazer um boneco para funcionar como uma espécie de espantalho de carniçais. Para isso, eu preciso de algumas costelas de carniçal. Serve costela dos comedores de carne, dos mastigossos, dos devora-cérebros, dos apodrecidos e até dos vetores da peste.\n\nPegue as costelas para mim e lhe darei algumas moedas pelo favor.", progress = "Pensei ter ouvido um bando de carniçais correndo por trás casa. É melhor eu montar logo esse boneco! Trouxe as costelas de carniçal?", completion = "Ah, obrigado. Essas vão servir!", rewards = "Você receberá:\n\n"},
[134] = {title = "Ogros ladrões", objective = "Devolva o Caixote do Abercrombie para o Abercrombie.", description = "Há algumas semanas, fui colher plantas longe de casa e um bando de ogros me atacou! Eu fugi, mas tive que deixar para trás uma caixa com as minhas preciosas ferramentas e todas as plantas que colhi.\n\nDepois da perseguição, eles voltaram todos pimpões para o morro dos ogros no sul da Floresta do Crepúsculo. Tenho certeza que o caixote com as minhas ferramentas está lá.\n\nPor favor, <name>, traga o caixote de volta para mim. Ele me faz muita falta.", progress = "Você pegou o meu caixote? Se pegou, por favor... Me dê já aqui!", completion = "Graças ao Nec... Bem, graças ao SEU esforço, &lt;name&gt;! Você mais do que merece uma recompensa.\n\nRá! Noites felizes! Noites felizes pela frente!", rewards = "Você receberá:\n\n"},
[135] = {title = "A Irmandade Défias", objective = "Leve o Bilhete do Wiley para Mathias Shaw em Ventobravo.", description = "O que será que o Wiley quis dizer ao mencionar os Alvanéis. Talvez tenha sido um ato falho. Será que a gangue dos Défias teria se envolvido com os Alvanéis? Só uma pessoa poderia dizer: Mathias Shaw, líder da AVIN. Mostre a ele o Bilhete do Wiley e veja se ele pode ajudar com esse mistério. Se tiver dificuldade em encontrá-lo, procure no Quartel da Cidade Velha.", progress = "Qual é o seu assunto comigo? Sou um homem muito ocupado...", completion = "A questão pode ser mais complexa do que Mantoforte imagina.", rewards = "Você receberá:\n\n"},
[136] = {title = "O tesouro escondido do Capitão Albernaz", objective = "Encontre o baú do Capitão Albernaz e procure a próxima pista dentro dele.", description = "Se o campanha tá lendo isso, é porque o bom e velho Albernaz virou comida de peixe. Então o meu tesouro é seu, é só seguir os bizus. \n\nPrimeiro o campanha tem que achar o meu baú. Ele deve estar de agarra perto do naufrágio, na costa ocidental de Cerro Oeste, e já deve estar meio enterrado na areia. Tem um monte de navio a pique na costa, mas só uma âncora enferrujada. Encontre esse ferro e tá safo que vai achar o baú! O próximo bizu tá lá dentro.", progress = nil, completion = "O baú abre com um rangido. Não há nada lá dentro além de areia e água. Mas espere! Um pequeno caranguejo corre para fora com a pista na pata!", rewards = "Você receberá:\n\n"},
[138] = {title = "O tesouro escondido do Capitão Albernaz", objective = "Encontre o velho barril perto da chaminé em ruínas e procure a próxima pista dentro dele.\n\nItem fornecido:\n", description = "A pista do tesouro diz: \"Bom trabalho, campanha! Agora levante ferro e pegue pra leste, pra riba dos penhascos, pela estrada. Fica de olho nas ruínas da chaminé do lado da estrada. O outro bizu tá ali, dentro de um barril\".", progress = nil, completion = "Bom trabalho, caçador de tesouros!", rewards = "Você receberá:\n\n"},
[139] = {title = "O tesouro escondido do Capitão Albernaz", objective = "Procure a próxima pista dentro do garrafão vazio perto do moinho de vento.\n\nItem fornecido:\n", description = "Ao olhar dentro do barril, você encontra outro fragmento de pergaminho. Neste, lê-se: \"A partir do barril, olhe pra norte. Vai batendo perna em linha reta como o voo do corvo até encontrar um garrafão vazio perto do moinho de vento abandonado na falésia. Se o campanha fuçar dentro do garrafão, vai achar o que procura\".", progress = nil, completion = "Você está a caminho da sorte grande, caçador de tesouros!", rewards = "Você receberá:\n\n"},
[140] = {title = "O tesouro escondido do Capitão Albernaz", objective = "Encontre o baú do Capitão Albernaz e abra-o para receber sua recompensa.\n\nItem fornecido:\n", description = "Como previsto, no fundo do velho garrafão havia uma pista para o tesouro do Albernaz. O papel cheira a uísque e a tinta está meio borrada, mas ainda é possível entender parte da mensagem: \"Agora que o campanha encontrou meu velho garrafão de uísque, o tesouro tá perto! É só olhar pra oeste, a partir do garrafão, e andar para a praia. Chegando na água, deixa de frescura, arregace as calças e vá em frente! Nade em direção ao oeste até topar com a ilha do tesouro!\"", progress = nil, completion = "As dobradiças na tampa do baú estão um pouco enferrujadas, mas não emperradas. Você abre o baú com um pouco de esforço e encontra o butim.\n\nParabéns!", rewards = "Você receberá:\n\nBarra de PrataSaco de Butim do Capitão AlbernazCamisa do Capitão AlbernazFaixa do Capitão Albernaz"},
[141] = {title = "A Irmandade Défias", objective = "Leve o Relatório do Shaw para Miguel Mantoforte no Cerro Oeste.", description = "A Guilda dos Alvanéis era controlada por Edwin VanCleef. VanCleef foi responsável pela reconstrução de Ventobravo depois que orcs a dizimaram na Primeira Guerra. Pelo visto, o tratamento que o Rei dispensou a VanCleef e seus homens após o término da reconstrução não lhes agradou. Isso explica um pouco as coisas.\n\nElaborei um relatório mais detalhado para o seu mestre no Cerro Oeste. Leve-o para ele imediatamente!", progress = "O Mestre Shaw esclareceu a questão?", completion = "Edwin VanCleef... Conheço bem esse nome. É perturbador pensar que um homem tão empreendedor e talentoso poderia se tornar um vilão. Precisarei de mais provas antes de acreditar.", rewards = "Você receberá:\n\n"},
[142] = {title = "A Irmandade Défias", objective = "Rastreie o Mensageiro Défias no Cerro Oeste e leve sua mensagem para Mantoforte.", description = "Precisamos descobrir a localização do esconderijo Défias. <name>, meu batedor informa que um mensageiro Défias foi visto nas estradas entre o Arroio da Lua, a Pedreira Costa Dourada e a Mina Veiojango. Quero que você o capture. Se ele resistir, mate-o e traga a mensagem que ele carrega para mim.", progress = "<name>, conseguiu coletar alguma informação? Encontou o mensageiro?", completion = "Isso é prova incontestável de que VanCleef está no comando. Agora, falta é saber onde a gangue Défias se esconde.\n\nTivemos um golpe de sorte enquanto você esteve fora. Capturamos um ladrão tentando roubar a carroça de Saldanha. Ele prometeu nos levar ao esconderijo se pouparmos sua vida. Quero que você proteja o traidor para que ele possa apontar o esconderijo. Fale novamente comigo quando tiver descoberto a localização.", rewards = "Você receberá:\n\n"},
[143] = {title = "Mensagem para Cerro Oeste", objective = "O Magistrado Salomão quer que você leve a súplica dele a Miguel Mantoforte em Cerro Oeste.", description = "Alguma coisa estranha aconteceu com o Exército de Ventobravo. Eles já deveriam ter chegado em grande número a essa altura. Mas o tempo é um luxo que não temos. Eu não vou ficar aqui assistindo o povo de Vila Plácida dar a própria vida sem tentar conseguir mais ajuda. Eu soube de uma milícia do povo se formando em Cerro Oeste.\n\nLeve esta súplica ao líder deles, Miguel Mantoforte. Talvez os homens de Mantoforte possam nos dar apoio aqui até que o Rei mande os reforços.", progress = "Salve, <class>. O que você quer com a Milícia do Povo?", completion = "Bom mensageiro, você serviu bem a seu mestre.", rewards = "Você receberá:\n\n"},
[144] = {title = "Mensagem para Cerro Oeste", objective = "Fale novamente com o Magistrado Salomão e relate a resposta de Miguel Mantoforte.", description = "É doloroso para mim ler o bilhete do Magistrado Salomão. No entanto, está óbvio que ele não sabe da guerra aqui em Cerro Oeste, ou não esperaria ajuda da milícia. Se Ventobravo não tivesse nos abandonado também, não precisaríamos de da Milícia.\n\nLeve esta resposta a seu mestre na Cristarrubra, <class>. Diga a ele o quando me pesa o coração a perda de tantos homens bons.", progress = "Podemos esperar a vinda da Milícia do Povo em breve? E o Senhor Mantoforte? Boas novas?", completion = "Vou lhe dizer, isso não é nada bom. Eu não havia me dado conta de que a situação de Mantoforte não está muito diferente da nossa aqui em Vila Plácida. O que pode ser importante o suficiente para impedir o exército de Ventobravo de ajudar o povo? Bah. É melhor não especular em voz alta.\n\nAqui, mensageiro. Aceite essas moedas pelo serviço prestado ao distrito. Pode ser que eu o convoque novamente.", rewards = "Você receberá:\n\n"},
[145] = {title = "Mensagem para Vila Sombria", objective = "O Magistrado Salomão quer que você entregue uma carta ao senhor Ebanez na Vila Sombria.", description = "Preciso de sua ajuda novamente. Escrevi ao Lorde Ebanez solicitando que ele envie seus guardas, a Vigília Noturna, para ajudar na defesa da Vila Plácida. É essencial que essa mensagem chegue rápido a ele. Você precisa ser extremamente cautelos<o/a>. Ebanez é o prefeito da Vila Sombria, no coração da Floresta do Crepúsculo. Não se afaste da estrada.", progress = "Você parece ter vindo de longe, <class>. Que assunto o traz à Vila Sombria?", completion = "Obrigado por fazer essa viagem até aqui. Essa informação é realmente importante.", rewards = "Você receberá:\n\n"},
[146] = {title = "Mensagem para Vila Sombria", objective = "Fale novamente com o Magistrado Salomão e relate a resposta do senhor Ebanez.", description = "Se não conhecesse o Magistrado Salomão pessoalmente, diria que o homem pirou. Mandar a Vigília Noturna para Vila Plácida porque Ventobravo se recusa a ajudar? A Vigília Noturna foi formada porque Ventobravo nos abandonou quando essa magia nefasta se infiltrou nessas terras. Se eu enviasse a Vigília Noturna esta cidade seria tomada pelo mal antes mesmo que os guardas chegassem aos limites da floresta.\n\nLeve esta carta em resposta a seu mestre na Vila Plácida.", progress = "Ah, que bom que você voltou, mensageiro. Devo informar que a Vigília Noturna está vindo da Vila Sombria?", completion = "Pela Luz! O exército de Ventobravo se retirou de Cerro Oeste. E a Guarda não protege mais a Vila Sombria? Tem traição nesse meio. Como pode?\n\nAh. Esqueci de recompensar você pelo serviço, &lt;class&gt;. Aqui tem umas moedas. Agora, com sua licença. Tem alguma coisa errada em Ventobravo, e eu preciso descobrir o que é.", rewards = "Você receberá:\n\n"},
[147] = {title = "Perseguição implacável", objective = "Encontre e mate \"o Coletor\". Depois, entregue o Anel do Coletor para o Delegado Durão.", description = "Se o \"Coletor\" está pegando o ouro das nossas minas, então ele está roubando do reino! Faça o Coletor sentir o peso da justiça, além de me trazer o anel mencionado na escala de coleta que você me trouxe. Talvez possamos descobrir para quem o Coletor trabalha...\n\nA escala informa que o Coletor está escondido na Plantação de Abóboras dos Braga. Procure por ele lá.", progress = "Você encontrou o Coletor? Descobriu para quem ele estava trabalhando?", completion = "Você o encontrou? Muito bem, &lt;name&gt;. Ele não vai coletar mais nada das Minas de Elwynn!\n\nE o anel que você trouxe é bem interessante... é um anel de associação da velha Guilda dos Alvanéis de Ventobravo. Por que um ladrãozinho furreca teria um anel de uma guilda de artífices, e por que os Ladrões Défias estão \"coletando\" dinheiro das nossas minas?\n\nPerguntas difíceis. Espero que um dia tenhamos as respostas.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nLuvas Encadeadas de VentobravoMunhequeiras Elásticas\n\nVocê também receberá:\n\n"},
[148] = {title = "Suprimentos da Vila Sombria", objective = "Fale com Madame Eva.", description = "Ah, o que um pobre velho pode fazer? Preciso de suprimentos, mas a Vila Sombria fica tão longe e eu estou tão velho e fraco... Eu nunca sobreviveria à viagem.\n\nVocê poderia ir até lá e trazer para mim uma Linha de Cabelo de Fantasma? Madame Eva costuma ter dessas linhas em casa, na praça da Vila Sombria.", progress = nil, completion = "Ah, você precisa de Linha de Cabelo de Fantasma, não é? Lamento, estou com o estoque vazio. Mas posso fiar um novelo para você... Se me trouxer o cabelo de fantasma.", rewards = "Você receberá:\n\n"},
[149] = {title = "Linha de Cabelo de Fantasma", objective = "Leve o Pente Espectral para a Maria Cega.", description = "Há uma casa de fazenda abandonada, nas montanhas a sul da Vila Sombria, que é assombrada por um velho e melancólico fantasma chamado Maria Cega.\n\nPegue este pente espectral e peça à Maria Cega que penteie os cabelos. Em seguida, traga o pente para mim e catarei o cabelo de fantasma para fazer a linha que você quer.\n\nE cuidado, pois ultimamente criaturas sombrias têm espreitado a casa...", progress = "<Soluços> Ah, por favor, não olhe para mim! Eu arranquei meus olhos com minhas próprias mãos, mas ainda assim consigo sentir seu olhar!\n\nEu estou horrorosa! Horrorosa! Por favor, não olhe para mim!!!", completion = "O que é isso? Um pente? Ah, que adorável! E ele desliza como se meu cabelo não fosse a maçaroca que é.\n\nAh, se eu tivesse um espelho...", rewards = "Você receberá:\n\n"},
[150] = {title = "Caçadores de murlocs", objective = "Leve 8 Barbatanas de Murloc para o Mestre de Doca Baren em Vila Plácida.", description = "Os murlocs estão virando uma pedra no sapato. Grandes grupos de homens-peixe andam lançando âncora ao longo das margens do Lago Plácido e pescando como se não houvesse amanhã! Precisamos nos livrar deles antes que eles comam todos os peixes do lago!\n\nCace os murlocs! Traga-me oito barbatanas deles e talvez eu tenha alguma coisa para lhe dar...", progress = "Conseguiu as barbatanas? Depressa, precisamos expulsar aqueles murlocs do lago!", completion = "Muito bem, &lt;name&gt;. Espero que aqueles murlocs não tenham lhe dado muito trabalho.\n\nÉ estranho vê-los tão longe do mar. Será que eles estão aqui fugindo de alguma coisa?", rewards = "Você receberá:\n\nVara de Pescar Enânica"},
[151] = {title = "Pobre Velha Brancurinha", objective = "Vera Taturana quer que você leve 8 Punhados de Aveia para ela em Cerro Oeste.", description = "Pobre Velha Brancurinha! A bichinha está tão cansada depois de tanto que a fizemos trabalhar. Eu a alimentei antes de sairmos da fazenda, mas não estávamos esperando que a carroça quebrasse na nossa mão. Se você puder trazer alguns punhados de aveia dos campos para ela, eu agradeceria muito.\n\nAposto que você vai achar nas fazendas de Cerro Oeste, se conseguir evitar aquelas máquinas horríveis que dominaram tudo. Há várias fazendas sudoeste daqui.", progress = "A Velha Brancurinha já está nas últimas. Por acaso, você encontrou aveia para ela?", completion = "Muito obrigada, &lt;name&gt;! A pobre Velha Brancurinha vai ficar tão feliz!", rewards = "Você receberá:\n\nCobertor da Velha BrancurinhaComedouro da Velha Brancurinha"},
[152] = {title = "Há perigo na costa", objective = "Mate 7 Caçamarés, 7 Guerreiros, 7 Oráculos e 7 Correcostas e fale novamente om o Capitão Calvino no Farol de Cerro Oeste.", description = "Você provavelmente já reparou que há vários navios naufragados ao longo da costa. O Grande Oceano é deveras traiçoeiro. Precisamos manter a costa de Cerro Oeste livre, para que os marinheiros que chegarem à praia fiquem em segurança. Os murlocs dão problema demais.\n\nMate 7 Caçamarés, 7 Guerreiros, 7 Oráculos e 7 Correcostas, e eu farei com que você seja devidamente <recompensado/recompensada>.", progress = nil, completion = "Muito bem, &lt;name&gt;. Você é bom em combate. Graças a você, a Costa de Cerro Oeste é agora um lugar mais seguro.", rewards = "Você receberá:\n\n"},
[153] = {title = "Bandanas vermelhas de couro", objective = "Leve 15 Bandanas Vermelhas de Couro para o Batedor Galiaan no Morro da Sentinela.", description = "O Front dos Défias está sempre mudando. Já faz bastante tempo que eu acompanho os movimentos deles. Vale dizer que dei um jeito de usar as bandanas vermelhas de couro para rastrear vários membros da gangue.\n\nTraga 15 bandanas para mim que vou saber lhe recompensar bem.", progress = "Traga 15 bandanas vermelhas de couro para mim. Pago bem.", completion = "Muito bem, &lt;race&gt;. Aceite um destes itens como pagamento pelo seu trabalho.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nManto da Milícia do PovoGrevas da Milícia do PovoPerneiras da Milícia do Povo\n\nVocê também receberá:\n\n"},
[154] = {title = "Devolva o pente", objective = "Devolva o Pente de Fantasma para Madame Eva na Vila Sombria.", description = "NÃO!!!!!\n\nLeve o pente para bem longe de mim!\n\nEu sou um monstro, e nunca mais alguém vai me achar bonita de novo!", progress = "Minha intuição diz que você viu a Maria Cega. Trouxe meu pente?", completion = "Com esse cabelo de fantasma, dá para fiar um carretel inteiro de linha e ainda guardar uma parte. Tome aqui algumas moedas pelo cabelo extra.", rewards = "Você receberá:\n\n"},
[155] = {title = "A Irmandade Défias", objective = "Escolte o Traidor Défias ao esconderijo secreto da Irmandade Défias. Quando o Traidor Défias mostrar onde VanCleef e seus comparsas estão escondidos, retorne a Miguel Mantoforte com a informação.", description = "O que deu na cabeça do Mantoforte para mandar <um/uma> <race> esquelétic<o/a> como você para me proteger? Bom, se não tem tu, vai tu mesmo. \n\nSabe como é, né? Você me dá cobertura e eu te levo ao esconderijo do Défias. Mas fica perto de mim. A gangue do Défias quer minha cabeça numa bandeja. Se me virem com você, vão querer me matar.\n\nReúna uns amigos e me avise quando estiver pront<o/a>.", progress = nil, completion = "Muito bem, &lt;name&gt;! VanCleef não vai ter a menor chance agora que sabemos onde ele está escondido.", rewards = "Você receberá:\n\n"},
[156] = {title = "Coleta de flores do mal", objective = "Colete 8 Flores do Mal e entregue-as ao Taberneiro Oseias em Vila Sombria.", description = "Se você me trouxer um maço de flores do mal, eu faço uma partida de suco de zumbi.\n\nAs flores do mal crescem em lugares estranhos. Fiquei sabendo que dá para encontrar algumas dentro do crânio de horrores descarnados e malévolos descarnados... mas nunca fui lá conferir, é claro!\n\nO Cemitério de Monte Corvo e os Jardins da Paz estão cheinhos de descarnados. Você pode até dar cabo de alguns enquanto procura as flores do mal. A Vila Sombria agradece.", progress = "Oi, <name>. Já conseguiu colher as flores do mal?", completion = "Ótimo, você conseguiu as flores do mal. Vou contar para Aldora Ebanez da Vigília Noturna que você matou descarnados.\n\nZelar pela segurança da Vila Sombria é um duro fardo, para eles é ótimo saber que alguém está ajudando a deter a onda de demônios que assola a Floresta do Crepúsculo.", rewards = "Você receberá:\n\n"},
[157] = {title = "Entregue a linha", objective = "Leve a Linha de Cabelo de Fantasma para Abercrombie na cabana dele, que fica ao norte do Cemitério de Monte Corvo.", description = "Aqui está a linha, <name>. Ela tem mil e uma utilidades, desde prender espíritos do mal até dar vida a objetos inanimados costurados com ela.", progress = "Eu quero proteger a porta e as janelas da minha casa com a linha de cabelo de fantasma para afastar espíritos indesejáveis. Você a trouxe para mim?", completion = "Maravilha! Isto vai servir muito bem...\n\nTome, bom &lt;name&gt;, aceite isto como pagamento por seu feito honroso.", rewards = "Você receberá:\n\n"},
[158] = {title = "Suco de zumbi", objective = "Fale com o Taberneiro Oseias.", description = "Preciso de uma garrafa de uma certa bebida — suco de zumbi. O Taberneiro Oseias, da Taberna Corvo Escarlate de Vila Sombria, deve ter em estoque.\n\nE, por favor, corra, <name>. As noites estão frias, e eu preciso dessa bebida para botar fogo nas minhas veias de novo!", progress = nil, completion = "Então você quer suco de zumbi, hein? Hum... Essa coisa é forte. Não costumo receber pedidos disso.", rewards = "Você receberá:\n\n"},
[159] = {title = "Alguém pediu um suco?", objective = "Leve o Suco de Zumbi para o Abercrombie na cabana dele.", description = "Está aqui o suco de zumbi...\n\n...E tenha cuidado com isso. É forte o suficiente para levantar os mortos!", progress = "Preciso muito do suco de zumbi para me manter aquecido nessas longas e gélidas noites. Conseguiu trazer para mim?", completion = "Muito obrigado, &lt;name&gt;. Você aqueceu o coração de um velho homem com sua ingenuidade... Ooops, quero dizer, com sua bondade!\n\nTome, meu amigo. Aceite isto como sinal de gratidão.", rewards = "Você receberá:\n\n"},
[160] = {title = "Carta ao prefeito", objective = "Entregue a Carta para Malaquias Ebanez, Prefeito de Vila Sombria, a pedido de Abercrombie.", description = "Entregue este bilhete para Malaquias Ebanez, o prefeito de Vila Sombria.\n\nEu e ele somos velhos amigos... E quando ele souber o que você fez por mim... Ele vai ficar tão feliz quanto eu! Rá rá rá rá rá!", progress = "Abercrombie...? Não conheço ninguém com esse nome. Você disse que tem uma carta desse tal de Abercrombie?\n\nBem, então vamos ver isso aí...", completion = "&lt;Malaquias examina a carta.&gt;\n\nQue língua é essa? Parece muito antiga. Eu não consigo ler.", rewards = "Você receberá:\n\n"},
[161] = {title = "Uma ameaça sombria à espreita", objective = "O Engenheiro-chefe Vedaçude quer que você leve o pó com cheiro estranho para Ashlan Risadura, o perito em explosivos, em Dun Modr.", description = "Se minhas suspeitas tiverem razão de ser, isso é uma espécie de pó explosivo. É bom um perito em explosivos analisar, pra gente saber com o que estamos lidando. O Ashlan Risadura tá com o regimento que protege Dun Modr e a Ponte Thandol. Preciso que tu faça uma jornada urgente e perigosa, <name>.\n\nVai até o Portão de Algaz, segue a estrada que cruza o Pantanal e procura o Risadura em Dun Modr. Vou te dar um conselho: fica na estrada e não para por nada!", progress = "Tu disse alguma coisa, jovem? Não tô ouvindo nada, só esse zunido nos ouvidos. Me diz, o que tu tem aí?", completion = "Ah, o velho Vedaçude tem motivo mesmo pra ficar preocupado.", rewards = "Você receberá:\n\n"},
[162] = {title = "A ascensão dos silitídeos", objective = "Leve o Relatório de Análise de Insetos para Gracinha Forçânima em Darnassus.", description = "Peço a você que leve este relatório para alguém com conhecimentos práticos neste tipo de coisa.\n\nEu conheço uma erudita elfa noturna chamada Gracinha Forçânima. Ela passou muito tempo estudando formas de vida que só existem na ecologia de Azeroth. Além do mais, ela também é historiadora! Se você quer que alguém te ajude a descobrir o que exatamente é essa ameaça e, o mais importante, como acabar com ela, leve o relatório para Gracinha. A ajuda dela será bem-vinda.\n\nPelo que ouvi dizer, ela está vivendo no Templo da Lua, em Darnassus.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[163] = {title = "Monte Corvo", objective = "Descubra o que está assombrando o Monte Corvo.", description = "Tem alguma coisa errada no Monte Corvo, <name>. O Petrônio jura que alguém, ou alguma coisa, está assombrando o lugar. Ele viu sombras se mexendo duas noites seguidas, e antes disso, ele disse que viu uma janela com a luz acesa.\n\nSe você se preocupa mesmo com a segurança da Floresta do Crepúsculo, descubra que monstro está assombrando o Monte Corvo.", progress = nil, completion = "Não! Por favor, não me mata! Sou só eu... Medrisco! Eles tão pra todo lado... eu não consigo fugir! Monstro no Monte Corvo? N-n-não... não pode ser. Só o Medrisco. Medrisco não faz nada.", rewards = "Você receberá:\n\n"},
[164] = {title = "Entrega para Noel", objective = "Entregue os suprimentos a Noel.", description = "Você conhece o Noel? Aquele sim é um homem que trabalha em prol da Luz! Ele fica com os seguidores num acampamento na margem noroeste da Floresta do Crepúsculo.\n\nInfelizmente, por serem muito poucos, eles não podem mandar ninguém para buscar suprimentos na cidade. Você pode levar esses suprimentos para eles?", progress = "Você parece duro na queda, <class>. Veio lutar com a gente?", completion = "Esses suprimentos vão ser muito úteis. A ajuda da família Carevino é sempre muito bem-vinda. Tome, fique com isto para compensar o trabalho.", rewards = "Você receberá:\n\n"},
[165] = {title = "O eremita", objective = "Confira se o velho que vive na choupana perto do cemitério do Monte Corvo está bem.", description = "Um senhor vinha sempre à cidade comprar suprimentos, mas já faz algum tempo que eu não o vejo. Ele vive numa choupana de frente para o cemitério do Monte Corvo, se me lembro corretamente. Talvez fosse uma boa ideia você ir até lá ver se está tudo bem.", progress = nil, completion = "Elaine Carevino? Hehe, ah sim... claro, claro, os Carevino.\n\nNão, não tem nada errado, mas está um pouco difícil para mim ir à cidade agora. As estradas são perigosas e eu não estou ficando mais jovem. Será que você pode ajudar um velho fazendo alguns favores?", rewards = "Você receberá:\n\n"},
[166] = {title = "A Irmandade Défias", objective = "Mate Edwin VanCleef e leve a cabeça dele para Miguel Mantoforte.", description = "Falta apenas uma tarefa para você cumprir. Edwin VanCleef deve morrer. Me entristece condenar qualquer pessoa à morte, mas é para o bem maior do povo do Cerro Oeste que VanCleef seja tirado de circulação de uma vez por todas. Traga a cabeça do vilão para mim quando estiver feito.", progress = "Como vai a caçada a Edwin VanCleef?", completion = "Sua bravura é notável, &lt;name&gt;. A Milícia do Povo só tem a agradecer pelo serviço que você prestou à população do Cerro Oeste. Com VanCleef morto, tem início a derrocada da Irmandade Défias. Com sorte, em breve a paz voltará a grassar por estas belas terras.\n\nReceba meus cumprimentos, milorde!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCalções do Cerro OesteTúnica do Cerro OesteCajado do Cerro Oeste\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[167] = {title = "Meu irmão...", objective = "Leve a Insígnia da Liga dos Exploradores do Encarregado Urtigão para Wilder Urtigão em Ventobravo.", description = "A gente tava numa grande mina no Cerro Oeste, escondido debaixo de um celeiro no Arroio da Lua. Não sei de que buraco aqueles ladrõezinhos duma figa saíram. Pois então, como eu dizia, o túnel da mina desabou em cima da gente. Eu escapei, mas os outros... não faço ideia do que aconteceu com eles.\n\nDe repente tu te sai melhor nisso que eu. Se puder procurar meu irmão, eu agradeço muito. Ele tava com a Insígnia da Liga dos Exploradores. Se não conseguir achar ele, a insígnia já ia me dar uma paz de espírito.", progress = "Você encontrou algum sinal do meu irmão? Depois de tanto tempo ainda é possível ter alguma esperança?", completion = "Minha suspeitas estavam corretas. Meu pobre irmão... Obrigado por encerrar esse assunto de uma vez por todas, ainda que de maneira tão triste.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nRevanche do Minerador"},
[168] = {title = "Lembranças dos que se foram", objective = "Recupere 4 Carteiras do Sindicato dos Mineradores e leve-as para Wilder Urtigão em Ventobravo.", description = "Muitos dos meus amigos sucumbiram naquele dia horrível, quando a mina desabou. Se, durante tuas aventuras, tu porventura passar por aquele lado da mina, no Arroio da Lua, vê se acha alguma coisa pra identificar eles. Se achar alguma Carteira do Sindicato dos Mineradores, traz pra mim. Assim as famílias deles vão ter alguma chance de superar esse acidente terrível.", progress = "Conseguiu achar alguma Carteira do Sindicato dos Mineradores dos meus velhos companheiros?", completion = "Obrigado, &lt;name&gt;. Seu esforço pra achar essas carteiras vai ajudar a trazer um pouco de paz praqueles que pereceram nessa tragédia. Eu vou dar um jeito de avisar as famílias.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas do EscavadorLuvas de Mineração Empoeiradas\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[169] = {title = "Procura-se: Gath'Ilzogg", objective = "Mate Gath'Ilzogg e leve a cabeça dele para o Magistrado Salomão na Vila Plácida para receber a recompensa.", description = "Procura-se: Gath'Ilzogg, mandante dos ataques dos Rocha Negra à Bastilha Mirante de Pedra e dos raides contra o povoado de Vila Plácida. \n\nGath'Ilzogg é extremamente perigoso. Relatos dão conta de que ele está entocado na fortaleza recém-conquistada planejando novos crimes contra a humanidade a serem perpetrados pelo Clã Rocha Negra. A infestação dos orcs deve ser suprimida a qualquer custo. Uma vultosa recompensa será concedida à corajosa alma que trouxer a cabeça de Gath'Ilzogg ao Magistrado Salomão.", progress = "O que você tem aí, <race>?", completion = "Excelente trabalho, &lt;name&gt;! Liderando um exército de brutos, Gath'Ilzogg matou muitos humanos inocentes. Enfim ele pagou pelos crimes que cometeu. Esse foi o primeiro passo na recuperação da fortaleza pelo reino de Ventobravo!", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[170] = {title = "Uma nova ameaça", objective = "Balir Gelomarra quer que você mate 6 Troggs Pedraqueixo e 6 Troggs Pedraqueixo Parrudos.", description = "Espero que tu tenha vindo dar uma mão pra gente, <class>. Depois do último ataque dos troggs, qualquer ajuda é bem-vinda.\n\nOuvi dizer que esses topetudos têm aparecido em todo o vizindário, e o Vale Cristálgida não é exceção. Eles foram vistos de cima a baixo nas colinas a sudeste e perto do lago congelado.\n\nE não para aí. Umas noitinhas atrás, eles atacaram e causaram um estafaréu no nosso acampamento a oeste!\n\nA gente tá meio precisado aqui, jovem. Um braço forte pra rechaçar esses troggs viria a calhar.", progress = nil, completion = "Se esses problemas que tivemos são indício do que tem acontecido em outros cantos da nossa terra, pelas barbas do Magni, vem coisa feia aí! Tomara que o rei e o senado estejam fazendo alguma coisa de verdade pra mitigar a ameaça desses troggs xucros.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nXale de UrsoCinto RústicoBotas de Neve\n\nVocê também receberá:\n\n"},
[172] = {title = "A Semana das Crianças", objective = "Use o Apito de Órfão Orc para conversar com a criança de quem você vai cuidar durante a Semana da Criança.\n\nItem fornecido:\n", description = "Você está <disposto/disposta> a nos ajudar aqui no orfanato? <Abençoado/Abençoada> seja, <amigo/amiga>. Sua ajuda durante a Semana das Crianças será inestimável.\n\nPegue esse apito. Você só poderá usá-lo por um curto período de tempo – durante a Semana das Crianças apenas. Usando o apito, você poderá interagir com a criança de quem você tomará conta. Use o apito novamente para dispensar a criança.\n\nPor favor, <name>, use o apito e chame sua protegida. Tenho certeza de que ela está ansiosa para conhecer você.Completando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = nil},
[173] = {title = "Worgens na floresta", objective = "Mate 6 worgens Tece-sombras Nocturnos para Petrônio na Vila Sombria.", description = "A escuridão parece ter se abatido inexoravelmente sobre a Floresta do Crepúsculo. E a missão do mestre Carevino é expulsar o mal e a heresia. Por meio de nossos esforços, o povo da Vila Sombria se mantém em segurança.\n\nVocê se acha <digno/digna> o bastante para se juntar a nós?\n\nEu costumava pensar como você. Desiludido com a complacência da Vigília, me juntei ao mestre Carevino. Se você quer provar seu valor, não será apenas por meio de meras palavras.\n\nMostre suas habilidades contra os worgens Tece-sombras Nocturnos no Bosque Brilhante... É... brilhante, pfff... claro. Enfim, lá e no Horto Pútrido.", progress = nil, completion = "Notável, &lt;name&gt;. Parece que você é capaz de se virar. Acho que podemos encontrar um desafio mais adequado às suas habilidades.\n\nMas é curioso... Os worgens parecem surgir do nada. Mas até aí... Com todas as outras criaturas estranhas que têm cruzado as nossas fronteiras, eu acho que essa é só mais uma de tantas preocupações.", rewards = "Você receberá:\n\n"},
[174] = {title = "Ora (direis) ouvir estrelas!", objective = "Vitório Prismado, da Vila Sombria, quer que você lhe providencie um tubo de bronze.", description = "O Roda, um gnomo amigo meu, prometeu me ajudar a construir um dispositivo que me permitirá observar as estrelas. Há uma verdade, para além da magia, capaz de explicar a estranha transformação da floresta.\n\nTraga-me um tubo de bronze feito por um engenheiro habilidoso. Esse é o primeiro passo segundo o diagrama do Roda.", progress = "Já achou algum engenheiro com habilidade suficiente para fazer um tubo de bronze?", completion = "Esse é perfeito, &lt;name&gt;. O primeiro passo está completo!", rewards = "Você receberá:\n\n"},
[175] = {title = "Ora (direis) ouvir estrelas!", objective = "Encontre Maria e convença-a a fornecer uma superfície refletora para o projeto de Vitório.", description = "Segundo o diagrama do Roda, o que eu preciso arranjar agora é um jeito de refletir a luz. Eu me lembro de uma tal de Maria que ficou conhecida por circular por aí sempre carregando um espelho. Você poderia encontrá-la e ver se ela nos empresta esse espelho? Acho que ela morava no sul da Floresta do Crepúsculo...", progress = nil, completion = "Sim, eu tinha um espelho.", rewards = "Você receberá:\n\n"},
[176] = {title = "Procura-se: \"Hogger\"", objective = "Mate o gnoll Hogger e leve sua Enorme Garra de Gnoll para o Delegado Durão.", description = "Procura-se: Hogger\n\nHogger, um gnoll enorme, está espreitando os bosques na parte sudoeste de Elwynn. Até agora, ele escapou de todas as emboscadas que armamos para apanhá-lo.\n\nO Exército de Ventobravo está oferecendo uma recompensa generosa pela captura desse Gnoll. Para coletar a recompensa, os caçadores de recompensa interessados devem apresentar provas da queda de Hogger ao Delegado Durão em Vila d'Ouro.", progress = "É, o Hogger tem sido uma dor de cabeça pra mim e meus homens. Você tem algo a relatar sobre esse bicho?", completion = "Ha! Muito bem! Já estava começando a achar que ninguém despacharia aquele monstro!\n\nAqui está, &lt;name&gt;. E obrigado. Aquele gnoll estava me dando uma dor de cabeça do tamanho do Pico da Rocha Negra!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nTúnica de Soldado RasoPerneiras da Guarda de VentobravoVara de Luta Balanceada\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[177] = {title = "Ora (direis) ouvir estrelas!", objective = "Recupere o Espelho de Maria do Carniçal Insano e entregue-o a Vitório na Vila Sombria.", description = "Aquele espelho era um dos meus mais preciosos tesouros até... o incidente. Depois do acontecido, eu o joguei fora atrás do celeiro. Um carniçal maluco costumava perambular por lá. Talvez ele tenha pegado o espelho. Dizem por aí que aquela criatura do demônio mora no Cemitério Jardins da Paz.", progress = "Eu mostrei ao Roda como a nossa máquina de ver estrelas está ficando. Ele ficou muito impressionado. Você conseguiu encontrar algum objeto reflexivo?", completion = "Magnífico! Isso vai funcionar direitinho. Muito obrigado!", rewards = "Você receberá:\n\n"},
[178] = {title = "A resposta de Teócrito", objective = "Leve o Pingente Umbrapele Esmaecido para Teócrito, o Mago.", description = "<Você ouve uma voz incorpórea...>\n\nNão se alarme. Eu sou Teócrito, Alto Mago da Torre Azora, na Floresta de Elwynn. O pingente que você tem em mãos é um método de comunicação entre os gnolls Umbrapele e o mestre deles, Morganth.\n\nApós meses de pesquisa, creio que também consigo me comunicar por meio desses pingentes. Se você consegue ouvir esta mensagem, meu feitiço foi um sucesso.\n\nTraga-me o pingente e eu <o/a> recompensarei pelo serviço.", progress = "Eu lhe falei por um pingente, é? Mmm... Dê ele aqui para eu estudá-lo.", completion = "Você tem minha gratidão, &lt;name&gt;. Mandei a mensagem para os pingentes Umbrapele há semanas, e achava que havia falhado. Porém, parece que o feitiço chegou a pelo menos alguns pingentes com um atraso significativo.\n\nCom o tempo eu espero aprender a enviar um ataque através dos pingentes para imobilizar os gnolls Umbrapele que os estejam usando.\n\nEntretanto, antes que isso seja possível, receio que seja necessária muito mais pesquisa.", rewards = "Você receberá:\n\nBálsamo Restaurador"},
[179] = {title = "Provedores enânicos", objective = "Sten Braçoforte quer 8 pedaços de Carne Dura de Lobo.", description = "Mas o que é que nós temos aqui? Parece que tu precisa é de alguma coisa para esquentar a mão, não?\n\nMas eu te conto o que é que ajuda: um bom par de luva quente. E, buenacho como sou, eu te arreglo um feliz da vida. Mas tem uma condição.\n\nTu precisa ir e me conseguir umas carnes de lobo. Tu me traz carne de lobo e eu cuido de que nenhum dedo teu caia de frio. Tás contratado?", progress = "Mas os lobos te deram trabalho? Vai ser bom é evitar aquelas presas e garras e outras partes afiadas, não é?", completion = "Ah! Maravilhoso. A carne de lobo deve dar. Ah, não te preocupa, não, &lt;name&gt;. Tás pensando que eu esqueceria a minha parte do contrato? Aqui. Uma dessas deve servir para ti.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nLuvas do Tratador de CoelhosLuvas do Tratador de LobosLuvas do Tratador de Javalis\n\nVocê também receberá:\n\n"},
[180] = {title = "Procura-se: Tenente Mordente", objective = "Mate o Tenente Mordente e leve a pata dele para o Magistrado Salomão em Vila Plácida.", description = "Procura-se: Tenente Mordente.\n\nLíder conhecido do clã de gnolls Umbrapele, trabalha para Morganth, inimigo do condado e do reino. Por mandato do Magistrado Salomão, este vil traidor de Ventobravo é procurado morto. Apresente a pata dele ao Magistrado como prova de sua morte.", progress = "Que notícias você traz à Corte?", completion = "Então o perverso Tenente Mordente está morto? Aquela criatura imunda já vai tarde. Aplaudo a sua coragem, &lt;class&gt;. O condado de Vila Plácida agradece a sua ajuda.", rewards = "Você receberá:\n\n"},
[181] = {title = "Ora (direis) ouvir estrelas", objective = "Encontre Zzarc'Vul no morro ôgrico ao sul da Floresta do Crepúsculo, pegue com ele o monóculo e leve para Vitório na Vila Sombria.", description = "Agora só precisamos de mais um item para terminar o aparelho. O diagrama do Roda pede um determinado tipo de lente. A única lente que eu conheço que é grande o suficiente é muito difícil de conseguir.\n\nTem um ogro chamado Zzarc'Vul que mora no morro a sul da Floresta do Crepúsculo. Se puder, pegue o monóculo dele emprestado para usarmos como lente!", progress = "Conseguiu convencer o Zzarc'Vul a emprestar o monóculo para o nosso experimento?", completion = "Finalmente! A máquina de olhar estrelas está pronta! Obrigado, &lt;name&gt;. Agora já posso continuar minhas pesquisas.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nLuvas do ZodíacoCinturão Estelar\n\nVocê também receberá:\n\n"},
[182] = {title = "A caverna dos trolls", objective = "Grolin Barbabranca quer que você mate 14 Jovens Trolls Jubafria.", description = "Eu e meu irmão Senir fomos mandados a partes diferentes de Dun Morogh para investigar a ameaça trólica. O Senado já está ocupado demais com os troggs e não precisa de ainda mais aborrecimentos.\n\nPelo que vi, acho que os trolls não estão bem situados aqui no Vale Cristálgida — no geral, estão na caverna mais ao sul. Eu diria que um exército não será necessário, alguns combatentes fortes já dão conta do recado.\n\nQue tal ajudar na empreitada? Tenho autonomia para oferecer uma boa compensação em troca da ajuda.", progress = nil, completion = "Argh! Malditos trolls!\n\n&lt;Ele respira algumas vezes e parece se acalmar... um pouco.&gt;\n\nUm grupo de trolls veio na calada da noite e roubou o meu diário! Aprendiz imprestável, eu sabia que não devia ter conf...", rewards = "Você vai poder escolher uma dessas recompensas:\n\nMachado Pequeno de SidermarMartelo de SidermarFaca de SidermarMarreta de Sidermar\n\nVocê também receberá:\n\nErva Curativa"},
[183] = {title = "Caçando javali", objective = "Talin Olhobom quer que você mate 12 Rochetuscos Pequenos.", description = "Nada como um belo dia caçando javalis, né?\n\nPena que aqui, no Vale Cristálgida, tem tanto rochetusco que a coisa quase perde a graça. Nem precisamos fazer os bichos atacarem, eles já estão sempre furiosos e prontos para a briga. Na verdade, o número de javalis aumentou tanto nos últimos tempos que a minha caça diária está ficando cada vez mais perigosa.\n\nIndo direto ao ponto, se você me ajudar a matar uns rochetuscos, eu ficarei muito grato.", progress = nil, completion = "Maravilha! Agora posso voltar à minha caça, hã... recreativa. Obrigado, &lt;name&gt;.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCulotes Enânicos de TecidoCalças de Couro Enânicas\n\nVocê também receberá:\n\n"},
[184] = {title = "Escritura do Taturana", objective = "Leve a Escritura do Taturana para o Fazendeiro Taturana.", description = "Esta é a escritura de um trecho de terreno cultivável em Cerro Oeste. Está assinada por um tal de Teodoro Taturana e sua esposa, Vera. No verso da escritura, tem algumas palavras escritas às pressas:\n\n\"Nós apertamos o Taturana e conseguimos a escritura dele. Achamos que seria útil se você forjasse uma igual para sua casa.\n\nOs Taturana não vão causar problema. Da última vez em que os vi, eles estavam tentando sair de Cerro Oeste, mas estavam parados por conta de uma carroça quebrada.\"\n\nOs Taturana com certeza vão querer a escritura de volta...", progress = "Você está com a escritura da minha fazenda?? Mas que boa notícia! Um bando de rufiões me roubou uns dias atrás... Achei que ela já era!\n\nPor favor, devolva para mim. Estamos indo embora de Cerro Oeste e não pretendemos voltar tão cedo, mas se voltarmos, vamos precisar do documento...", completion = "Muito obrigado, &lt;name&gt;! Como eu disse antes, as pessoas honestas não têm mais o que fazer por essas bandas, mas se as coisas melhorarem, poderemos reaver nossas terras graças à escritura.\n\nNão tenho muito a oferecer, mas... fique com isto.", rewards = "Você receberá:\n\nMelancia MaduraAbóbora Pequena"},
[185] = {title = "Maestria em tigres", objective = "Merília Ceireles da Expedição do Rosarães quer que você mate 10 Tigres-do-espinhaço Jovens.", description = "Quando eu era apenas uma menina, meu pai me ensinou a caçar tigres.\n\nVocê vai perceber que os felinos mais jovens são muito mais fáceis de rastrear e matar. Assim, vamos começar devagar. É possível encontrar tigres jovens perto do acampamento da expedição.\n\nTeste a sua habilidade de caça e veja se consegue pegar algumas destas feras.", progress = nil, completion = "Muito bem, &lt;name&gt;. Você está pronto para seguir em frente.", rewards = "Você receberá:\n\n"},
[186] = {title = "Maestria em tigres", objective = "Merília Ceireles da Expedição do Rosarães quer que você mate 10 Tigres-do-Espinhaço.", description = "Você está aprendendo, <class>. Agora vamos pôr suas habilidades à prova com os grandes felinos. Desta vez, mate 10 Tigres-do-espinhaço.", progress = nil, completion = "Estou impressionada, &lt;class&gt;. Você está progredindo bem.", rewards = "Você receberá:\n\n"},
[187] = {title = "Maestria em tigres", objective = "Merília Ceireles da Expedição do Rosarães quer que você mate 10 Tigres-do-espinhaço Anciãos.", description = "Agora vou dobrar a aposta em você e testar suas habilidades de verdade. Se me provar que matou 10 tigres-do-espinhaço anciãos, você estará pront<o/a> para o desafio final que vai definir se posso declarar você um <um verdadeiro mestre/uma verdadeira mestra> na caça aos tigres na presença do Mestre Rosarães.\n\nTigres-do-espinhaço são difíceis de encontrar, e mais difíceis ainda de abater.", progress = nil, completion = "Muito impressionante. Acho que você está quase pronto.", rewards = "Você receberá:\n\n"},
[188] = {title = "Maestria em tigres", objective = "Merília Ceireles da Expedição do Rosarães quer que você mate Sin'Dall e lhe traga a pata dela.", description = "Eis aqui o desafio final para você. Estamos rastreando um tigre muito esperto há várias semanas. É uma fera chamada Sin'Dall. Vamos ver se você consegue o que nenhum <race> conseguiu: caçar e abater Sin'Dall. Traga uma pata dela como prova.\n\nE se prepare, porque rastrear essa fera não vai ser fácil.", progress = "Como vai a caça a Sin'Dall?", completion = "Muito impressionante, devo dizer! Você é mesmo um verdadeiro mestre na caça aos tigres, &lt;name&gt;!", rewards = "Você receberá:\n\nLuvas do Caçador de Tigres"},
[189] = {title = "Orelhas de Sanguescalpo", objective = "Consiga 15 Orelhas de Sanguescalpo e entregue-as a Kebok na Angra do Butim.", description = "Malditos trolls! Estão matando nossos agentes na selva! Já perdi vários dos meus melhores guardas por causa de escaramuças com eles. Isso está me saindo muito caro! Como vou explicar para o Barão Rezingada?\n\nAção decisiva... Pense, Kebok... não foi por ficar sentado que você conquistou este posto... Já sei! Você... <name>, certo? Você vai me ajudar, não vai? Tem dinheiro para ganhar!\n\nÉ o seguinte... se você me trouxer as orelhas de quinze trolls Sanguescalpo, vai receber uma bela recompensa.", progress = "Você conseguiu, imagino. Tem trolls por toda parte. Demônios nojentos.\n\nAh, isso me lembra de uma coisa! Não deixe de contar a seus amigos, ok? Qualquer ajuda é bem-vinda!", completion = "Ah, excelente, excelente. Isso vai ajudar, por enquanto...\n\n&lt;Ele se afasta, resmungando sobre custos e margens de lucro...&gt;\n\nO quê? Você ainda está aqui? Recompensa? Ah, claro... tem razão, tome... isso deve ser mais que o suficiente.", rewards = "Você receberá:\n\nVara Goblínica de Pesca"},
[190] = {title = "Maestria em panteras", objective = "Raciliano Gramos da Expedição do Rosarães quer que você mate 10 Panteras Jovens.", description = "Para fazer parte da caçada com este grupo de elite que o Rosarães reuniu, você precisa mostrar que é um bom caçador de panteras. Vamos começar com o mais fácil, não se preocupe. Quero ver você matar dez panteras, para começo de conversa.\n\nRastreá-las é só o começo...", progress = nil, completion = "Começou bem, &lt;name&gt;!", rewards = "Você receberá:\n\n"},
[191] = {title = "Maestria em panteras", objective = "Raciliano Gramos, da Expedição do Rosarães, quer que você mate 10 Panteras.", description = "Agora você está pront<o/a> para o próximo nível. Se quiser mostrar seu valor e ficar lado a lado com estes grandes caçadores, você precisa provar que consegue matar dez panteras.\n\nOs bichos são dureza. Não tem mais aquela molezinha de matar filhotinhos, não.", progress = nil, completion = "Execução perfeita, &lt;name&gt;!", rewards = "Você receberá:\n\n"},
[192] = {title = "Maestria em panteras", objective = "Raciliano Gramos, da Expedição do Rosarães, quer que você mate 10 Panteras Umbragorja.", description = "Agora vem a parte difícil. <Um verdadeiro caçador/Uma verdadeira caçadora> de panteras mostra seu valor abatendo panteras umbragorja, as mais perigosas da Selva do Espinhaço. Prove que você consegue matar dez feras dessas.\n\nSe elas já são difíceis de encontrar, imagina de matar...", progress = nil, completion = "Muito bem, &lt;name&gt;!", rewards = "Você receberá:\n\n"},
[193] = {title = "Maestria em panteras", objective = "Raciliano Gramos, da Expedição do Rosarães, quer que você traga para ele a Presa de Bhag'thera.", description = "Você está prestes a provar que é <um grande caçador/uma grande caçadora>. Uma pantera chamada Bhag'thera está à espreita na selva. Até agora, ela conseguiu enganar nosso grupo. Use todas as suas habilidades e mate a fera.\n\nSe me trouxer a presa de Bhag'thera, você terá todo o meu respeito!", progress = "Bhag'thera pode ser uma fera esquiva. Como vai a caçada?", completion = "Quer dizer que a poderosa Bhag'thera está morta! Eu te saúdo, &lt;name&gt;. Você é realmente um poderoso &lt;class&gt;!", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nPerneiras do Caçador de Panteras"},
[194] = {title = "Maestria em raptores", objective = "Rosarães Guima quer que você mate 10 Raptores-do-Espinhaço.", description = "Então você acha que as suas habilidades de caça são boas o suficiente? Bom, eu posso colocá-las à prova em um minuto. Vá para a floresta e mate dez raptores-do-espinhaço. Vamos ver se você é <um caçador/uma caçadora> de elite mesmo.\n\nE não, não vou dizer onde encontrá-los! Encontrar as feras é metade do desafio.", progress = nil, completion = "Muito bem, meu camarada!", rewards = "Você receberá:\n\n"},
[195] = {title = "Maestria em raptores", objective = "Rosarães Guima quer que você mate 10 Raptores Batecauda.", description = "Agora vamos dobrar a aposta e ver se você está à altura do desafio. Entre na selva e colete dez desses malditos raptores batecauda. Quero ver do que você é capaz!", progress = "Um verdadeiro <class> fica focado na presa. Você, meu amigo, parece mais interessado em bater papo ao redor da fogueira. Agora vá e cace 10 batecaudas antes que eu saia e acabe com todos os raptores dessa maldita selva em uma caçada só.", completion = "Assim que se faz para aquecer um velho coração! Bela matança, &lt;name&gt;!", rewards = "Você receberá:\n\n"},
[196] = {title = "Maestria em raptores", objective = "Rosarães Guima quer que você mate 10 Espreitadores da Selva.", description = "Vamos ver como você se sai neste próximo desafio. Existe uma espécie de raptores particularmente desconfiada, que chamamos de espreitadores da selva. Eles são muito mais difíceis de rastrear do que as outras espécies que você já encarou. Mate dez espreitadores da selva e eu lhe indico para a melhor caçada de todas.", progress = nil, completion = "Ora, ora! Temos aqui um verdadeiro matador de raptores! Muito bem, meu camarada!", rewards = "Você receberá:\n\n"},
[197] = {title = "Maestria em raptores", objective = "Rosarães Guima quer que você mate Tethis, um raptor perigoso e esquivo, na Selva do Espinhaço.", description = "Como você demostrou muita tenacidade na selva, vou lhe contar sobre um raptor feroz que nem eu mesmo consegui matar. A Merília aqui chama esse raptor de Tethis.\n\nSe quiser provar que é <um grande mestre/uma grande mestra>, mate Tethis e me traga as garras dele. Seria um feito que nenhum outro grande caçador realizou.", progress = "Como foi a caçada a Tethis? Você conseguiu rastreá-la até o acampamento?", completion = "Mestre &lt;name&gt;, o matador de raptores!\n\nSoa bem, não é?", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nTúnica do Caçador de Raptor"},
[198] = {title = "Suprimentos para o Recruta Turgo", objective = "Entregue os Suprimentos Goblin Sortidos ao Recruta Turgo.", description = "Hum. Sim, tem uma coisa que você pode fazer. O Tenente Doren e seus seguidores do norte precisam receber nossos suprimentos regulares.\n\nAinda não entregamos e, com os ataques dos trolls, preciso de alguém que faça isso. O acampamento de Doren fica a noroeste da estrada que fica na entrada para a Selva do Espinhaço, a partir da Floresta do Crepúsculo. Entregue os suprimentos ao Recruta Turgo. Se ele não estiver lá, estará em patrulha ao sul do acampamento. Ele cuidará de tudo.\n\nFique alerta. Se você falhar, vou querer indenização.", progress = "Sim! O que você quer? Ah, suprimentos dos goblins. Que estranho ver um <race> fazendo a entrega, eles mesmos costumam cuidar disso. Não importa... você não mexeu em nada, mexeu? Ótimo, ótimo... não queremos que nada aconteça com nossas minas, não mesmo.", completion = "Essas minas terrestres vão nos ajudar na defesa contra aquele animal do Kurzen e seu exército. Agradeço pela entrega. Se precisar de mais trabalho, consigo encontrar algo para você fazer. E você nem vai precisar lidar com esses goblins, que tal?", rewards = "Você receberá:\n\n"},
[199] = {title = "Uma ameaça sombria à espreita", objective = "Fale novamente com o Engenheiro-chefe Vedaçude e mostre a ele a pista que você descobriu.", description = "Pegue uma amostra do pó com cheiro estranho.", progress = "Conseguiu descobrir alguma pista, <name>?", completion = "É uma descoberta alarmante, para dizer o mínimo!", rewards = "Você receberá:\n\n"},
[200] = {title = "Contador Herodes", objective = "Encontre os Registros do Contador Herodes.", description = "Se Kurzen conhece o segredo de Turgo, temos um espião entre nós. Precisamos descobrir como o segredo vazou!\n\nO contato de Turgo no acampamento de Kurzen era o Contador Herodes. Ele é responsável pela logística e sabe tudo sobre a administração do Complexo do Kurzen. Se você pegar as anotações ou registros do Herodes, talvez a gente consiga saber como o Turgo foi descoberto.\n\nQuando ainda estávamos com Kurzen, o escritório do Contador ficava no andar superior da casa de dois andares do acampamento. Os registros ainda devem estar lá.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[201] = {title = "Investigue o acampamento", objective = "Encontre o acampamento de caçadores e relate a localização para Krazek na Angra do Butim.", description = "Recebi relatos de que um grupo de caçadores montou acampamento rio acima, depois do campo de mineração da Empreendimentos S.A. no Lago Nazferiti. Geralmente eu não estou nem aí pra isso, mas ouvi dizer que Rosarães Guima é um dos caçadores. Ele tem muito bem relacionado, se é que você me entende, e o seguro morreu de velho.\n\nPor que você não dá uma olhada no acampamento, <name>, e descobre o que estão pretendendo?", progress = nil, completion = "Hum, então a informação que eu tinha estava correta, como sempre. Interessante. Bem, não acho que eles sejam uma ameaça à nossa operação, mas talvez estejam precisando de alguns de nossos excelentes produtos manufaturados. Hum...", rewards = "Você receberá:\n\n"},
[202] = {title = "Coronel Kurzen", objective = "Mate 6 Elites de Kurzen, 4 Subchefes de Kurzen e traga a Cabeça do Kurzen para o Tenente Doren no Acampamento dos Rebeldes.", description = "Você já demonstrou seu valor diversas vezes, e parece ser tão engenhos<o/a> quanto o próprio Kurzen. Você pode ser a salvação do nosso grupo de rebeldes... se conseguir enfrentar Kurzen e vencer!\n\nEle comanda o exército de dentro do Arsenal. Você precisa entrar na caverna, abrir caminho entre os defensores e então encarar Kurzen e seus líderes.\n\nAdeus, <name>. Você leva minha esperança e o destino de meus homens com você.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nTrabuco de Estilhaços"},
[203] = {title = "A segunda rebelião", objective = "Mate 15 Guerrilheiros da Selva de Kurzen.\n\nFale novamente com o Sargento Yohwa no Acampamento dos Rebeldes.", description = "Estamos travando uma guerra perdida contra o Coronel Kurzen. Ele não é um estrategista brilhante, e seu exército é dez vezes maior que o nosso! Para sobreviver, precisamos usar as estratégias secretas dele contra si próprio.\n\nEntre no acampamento dele pelo leste e mate os soldados. Tantos quanto puder. E saia antes que os reforços encurralem você.", progress = nil, completion = "Muito bem. A morte desses guerrilheiros da selva vai aliviar um pouco da pressão sobre o nosso acampamento.\n\nAgora que teve uma amostra das forças que estamos enfrentando, você ainda quer se juntar a nós?", rewards = "Você receberá:\n\n"},
[204] = {title = "Remédio do mal", objective = "Traga 7 Remédios da Mata e 1 Extrato de Samambaia Peçonhenta para o Sargento Yohwa no Acampamento dos Rebeldes.", description = "Para ter alguma chance contra, precisamos neutralizar o suporte de cura do Kurzen. Ele reuniu um grande número de Curandeiros, que curam suas forças com remédios da mata e envenenam nossos soldados com extratos de samambaia peçonhenta.\n\nPrecisamos dos remédios para curar os nossos e das amostras dos extratos para analisar e criar antídotos. Vasculhe o Complexo do Kurzen em busca de ambos. Os remédios estão com os Curandeiros, e os extratos devem estar armazenados em algumas das caixas de suprimento do acampamento.", progress = "Estou esperando os remédios e o extrato, <name>.", completion = "Muito bem, &lt;name&gt;. Esses remédios vão garantir a sobrevivência e a força de luta de vários de nós. E os extratos vão nos ajudar a criar nossos próprios remédios.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nDragonas de Fronde de PalmeiraCutelo de Guerrilha\n\nVocê também receberá:\n\n"},
[205] = {title = "Bruxaria trólica", objective = "Leve 4 Fetiches Rachacrânio para o Irmão Nimetz no Acampamento dos Rebeldes.", description = "Enquanto estudo as lendas que você adquiriu, reúna implementos de magia trolesa maligna para mim. Entre no território dos trolls Rachacrânio, a segunda tribo em direção ao sul distante, e compre fetiches Rachacrânio dos mandingueiros e místicos.", progress = "Você está com os fetiches, <name>? Talvez eles guardem o segredo para a degradação de Kurzen... Preciso saber!", completion = "Agradeço. Vou estudar esses fetiches e ver se explicam por que Kurzen se rendeu ao mal.", rewards = "Você receberá:\n\n"},
[206] = {title = "Mai'Zoth", objective = "Leve o Olho da Mente para o Irmão Nimetz no Acampamento dos Rebeldes.", description = "Kurzen pode estar morto, mas... aquilo que o levou para o mal ainda vive! Com meus estudos, descobri o seguinte: um ogro-mago, Mai'Zoth, mora em uma gruta dentro das montanhas orientais da Selva do Espinhaço. Dizem que ele usa magia controladora de mentes e tenho certeza de que ele corrompeu Kurzen.\n\nSe Mai'Zoth continuar vivo, a maldade dele pode influenciar outros e talvez até levar ao surgimento de outro Kurzen.\n\nMate-o e me traga o Olho da Mente, o artefato que ele usa para corromper os outros.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nOrbe Sereno"},
[207] = {title = "O mistério de Kurzen", objective = "Encontre as 4 tabuletas e leve as lendas para o Irmão Nimetz.", description = "O Coronel Kurzen era um grande e nobre homem, preciso descobrir o que o levou à depravação! Talvez ele esteja sob o domínio de magia trólica. Para confirmar isso, preciso estudar algumas lendas.\n\nExistem quatro tabuletas escondidas nas ruínas trólicas da Selva do Espinhaço. Preciso ver o que está escrito nessas tabuletas! Transcreva as marcas e traga as transcrições para mim.\n\nA primeira tabuleta está nas Ruínas de Bal'lal, a segunda, no Arrecife Torpe, a terceira e a quarta estão nas Ruínas de Zul'Kanda.", progress = "<name>! Conseguiu as lendas dos trolls? Preciso saber os segredos deles!", completion = "Obrigado, &lt;name&gt;. Vou estudar essas lendas para ver se existe alguma ligação entre os trolls e o mal que se apossou do Coronel Kurzen.", rewards = "Você receberá:\n\n"},
[208] = {title = "A grande caçada", objective = "Rosarães Guima quer que você lhe traga a cabeça do Rei Bangalash, o grande tigre branco.", description = "<name>, você é <um caçador digno/uma caçadora digna> da nossa companhia. Dos campos de batalha de Lordaeron à mais entranhada floresta, poucas vezes testemunhei tamanha destreza no manejo de armas quanto a sua.\n\nVocê abateu a feroz Sin'Dall, a ardilosa Bhag'thera e o traiçoeiro Tethis. Mas o verdadeiro prêmio é a cabeça do Rei Bangalash. Aquele tigre branco é o motivo que me trouxe aqui... Faz tempo que eu estou de olho nele. Mate o rei Bangalash e você provará que sua habilidade como <caçador/caçadora> é incomparável.", progress = "Estou vendo que você voltou, amigo. O Rei Bangalash já me fez voltar rastejando para o acampamento várias vezes também. Aguenta firme!", completion = "Muitíssimo bem feito! Isso sim! Parabéns, &lt;name&gt;, por triunfar onde todos os outros falharam.\n\nMe sinto honrado por considerá-lo parte da nossa grande companhia de caça. Ninguém jamais duvidará da sua grande capacidade de caçada e rastreamento!\n\nEu te saúdo, &lt;name&gt;!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nArco do Mestre CaçadorRifle do Mestre Caçador\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[209] = {title = "Presas Rachacrânio", objective = "Consiga 18 Presas Rachacrânio e traga-as de volta para Kebok.", description = "É caro e difícil matar tigres suficientes para atender à demanda de exportação de presas de tigre nos Mares do Sul. Felizmente, nós desenvolvemos uma técnica que nos permite transformar objetos triviais, parecidos com chifres, em falsificações perfeitas.\n\nA melhor matéria-prima para isso, acredite se quiser, são presas de trolls Rachacrânio.\n\nEi, nem me venha com reclamações, se o comprador não descobrir nada, não tem problema. Traga uma grande quantidade pra gente preparar o envio mensal!", progress = "Aqueles patifes são dureza, né não? E então, conseguiu? Desculpe pela impaciência, mas eles são uma pedra no meu sapato há tempos! Conseguiu?", completion = "Bom trabalho, bom trabalho! Eca, mas você podia pelo menos ter limpado a... Bom, deixa pra lá, deixa pra lá! Muito obrigado, &lt;name&gt;, a produtividade e o rendimento da nossa operação vão melhorar muito com a sua ajuda! Então, mudando de assunto...", rewards = "Você receberá:\n\n"},
[210] = {title = "A cozinha de Krazek", objective = "Leve a Caçarola Meia-boca do Krazek para Krazek, o goblin.", description = "Eu faço quase toda a comida do acampamento. Isso costumava me dar muito trabalho, até que eu comprei uma panela mecânica de um mercador goblin na Angra do Butim. A panela é maravilhosa! Ela me poupou horas e horas... até que ela quebrou!\n\nVocê poderia levá-la de volta para o goblin e pedir que ele a conserte? O nome dele é Krazek.", progress = "Olá! Você trouxe alguma coisa pra mim?", completion = "Ah, eu tô vendo, é uma Caçarola Meia-boca do Krazek! É uma obra de arte da engenharia, mas, de vez em quando, solta uma pecinha ou outra. Deixa eu ver o que há de errado com essa aqui...\n\nAhá! O carregador de vapor tá enferrujado, a gaxeta tá arrebentada e o fobulador tá sem um dente. É impressionante que essa panela tenha conseguido ferver um nabo sequer nessas condições!\n\nSe eu posso consertar? É claro que eu posso consertar!\n\nMas isso tem um preço. Ah sim, tudo tem um preço...", rewards = "Você receberá:\n\n"},
[211] = {title = "Adeus, Andorhal", objective = "Leve o Fragmento de Filactério de Araj ao Comandante Ashlam Punhobom no Acampamento Ventogelante, que fica nas Terras Pestilentas Ocidentais.", description = "Eis que chegou a hora de atacar Andorhal e expulsar o lich que a controla!\n\nAs ruínas da cidade são o domínio do lich — Araj, o Evocador. Seus protetores são inúmeros Flagelos que rodeiam os destroços do centro da cidade, além de uma comitiva formidável de guardiões que estão diretamente ligados a ele. Você vai precisar de muitos aliados para sequer ter um chance de derrotá-lo.\n\nDestrua Araj, <name>, e me traga um fragmento do filactério dele como prova!", progress = "Você tinha uma tarefa a cumprir, <name>. Trouxe o fragmento do filactério do lich para provar que fez o que tinha que fazer?", completion = "Você conseguiu! Araj... o lich, enfim, derrotado!\n\nHoje você prestou um tremendo serviço à Aliança, &lt;name&gt;. Sua bravura e perseverança são características dos heróis de verdade. Por favor, aceite isto como um presente, para que todos saibam que a sua conquista fez história para a Aliança!", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nMarca da Determinação"},
[212] = {title = "A festança é um prato que se come frio", objective = "Mate um Basilisco Olho Frio, pegue uma Anca Fria de Basilisco e leve até Angelo Severo no Recôndito Azulego.\n\n<Você não pode liberar o seu espírito para conseguir realizar esta missão>", description = "Um dos meus pratos mais famosos é o basilisco gelado em vinagrete de limão. O problema é que... é impossível manter a carne fresca, e preciso servir o prato hoje à noite, no aniversário de um nobre!\n\nSe você conseguir me trazer rapidamente uma anca fria de basilisco olho frio recém-abatido, eu vou ficar lhe devendo um banquete real!\n\nOs basiliscos olho frio costumam vagar pelas dunas litorâneas do noroeste Selva do Espinhaço, então, você vai ter que fazer uma longa viagem. Seja rápid<o/a>!", progress = "<name>! Você está com a anca? Preciso começar a preparar logo, senão, adeus banquete!", completion = "Ah, que maravilha! Muito, muito obrigado!\n\nAgora, se me dá licença, preciso cortar, marinar e temperar a anca para o banquete.", rewards = "Você receberá:\n\n"},
[213] = {title = "Abordagem hostil", objective = "Recupere 8 Cristais Rolados e entregue-os a Kebok na Angra do Butim.", description = "Ele conseguiu desta vez! Não bastasse ter expulsado os barões dos negócios indicados pelos príncipes mercadores, agora Gelriz quer dividir os lucros com o pirata mais infame!\n\nRezingada não vai engolir essa, e me mandou cuidar do problema do meu jeito.\n\nSabe qual é o meu jeito? Roubo. Os geólogos da Empreendimentos S.A., próximos ao Lago Nazferiti estão extremamente interessados naqueles cristais azuis estranhos que andam encontrando nas minas. Pegue algumas amostras do cristal com os geólogos, não importa o que você vai ter que fazer pra conseguir.", progress = "Preciso dessas pedras, <name>. Não importa o que seja preciso fazer! Consiga para mim e não me conte nenhum detalhe!\n\nSó quero saber do resultado, <name>, e ponto final.", completion = "Fascinante. O fato dos cristais terem sido rolados por meio deste processo só pode indicar que estão planejando usá-los como algum tipo de combustível ou outro catalisador. Não sei muito mais do que isso. É preciso enviar amostras para o Químico-chefe Gerarriso.", rewards = "Você receberá:\n\nLuvas de Pedraria"},
[214] = {title = "Bandana de seda vermelha", objective = "A Batedora Riell, da Torre do Morro da Sentinela, quer que você lhe traga 10 Bandanas de Seda Vermelha.", description = "Não é segredo que a marca registrada da gangue Défias é a bandana vermelha. Mas descobrimos que o material da bandana simboliza a patente do membro.\n\nDito isto, quero que você elimine o maior número possível membros do alto escalão dos Défias. Os membros de maior patente podem ser encontrados, sem falta, no esconderijo secreto de VanCleef. Quando terminar, traga-me 10 bandanas de seda vermelha como prova de suas mortes, e eu lhe darei uma recompensa. Boa sorte, <name>.", progress = "Você está reivindicando nossas terras da gangue Défias? Se sim, mostre-me 10 Bandanas de Seda Vermelha como prova.", completion = "Muito bem, &lt;name&gt;! Em nome da Milícia do Povo, eu saúdo os seus esforços. Em breve, devolveremos estas terras ao povo, com a ajuda de almas corajosas como você.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nEspadim MaciçoAdaga do EntalhadorMachado Perfurante\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[215] = {title = "Segredos da selva", objective = "Fale com o Tenente Doren no Acampamento dos Rebeldes.", description = "Muito obrigado por me salvar, <name>. Eu sei o que parece, mas... permita-me explicar.\n\nO Tenente Doren ordenou que eu me realistasse no exército de Kurzen para que eu pudesse espioná-los e trazer informações estratégicas extremamente necessárias para os rebeldes. Você viu aqui o que eu pensava ser a minha chance de me infiltrar no Complexo do Kurzen. Eu me enganei. Mais uma vez, obrigado por me salvar.\n\nEsta missão era ultrassecreta, portanto, não comente com ninguém. Mas se você falar com Doren, ele vai lhe explicar.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[216] = {title = "No meio do caminho tinha uma pedra... e um pelocardo...", objective = "Mate 12 Vingadores Pelocardo e 12 Xamãs Pelocardo; a maioria se localiza a leste da Praia de Zoram, na Aldeia dos Pelocardo. Uma vez concluído, fale novamente com Karang Amakkar no Assentamento Zoram'gar, Vale Gris.", description = "Os pelursos Pelocardo são uma ameaça à nossa expansão! A Aldeia dos Pelocardo, a leste daqui, está bloqueando o caminho entre nós e o Posto Machadada. A trilha contorna a vila de Astranaar, e, se não a controlarmos, daremos de bandeja mais uma via de expansão para a Aliança.\n\n<name>, não podemos permitir... uma pedra no caminho da nossa expansão!\n\nAvance até a Aldeia dos Pelocardo e reduza o contingente deles. Você vai receber uma recompensa por cumprir a tarefa, mas o mais importante é que você vai cumprir o seu dever perante a Horda!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[217] = {title = "Em defesa das terras do Rei", objective = "Mate o líder dos troggs, Gromug, e seus dois guardas, Triscadente e Brigão, e então fale novamente com o Capitão Balbúrdia na torre de guarda sul.", description = "<name>, nossos batedores identificaram o líder dessa recente incursão de troggs. Parece que essas criaturas de Loch Modan estão sendo lideradas por um chefe conhecido como Gromug. Mas Gromug está fortemente protegido. Dois dos seus maiores guardas de elite, Triscadente e Brigão, nunca saem do lado dele.\n\nCaso você se sinta <merecedor/merecedora> de tal encargo, <name>, quero que você lidere a missão para assassinar Gromug e seus dois brutamontes. Com a liderança dos troggs morta, podemos ter uma chance de forçar aqueles monstros a voltar para a terra.", progress = nil, completion = "Excelente, &lt;name&gt;! Você trouxe esperança para Loch Modan. Com a morte de Gromug, aumentam as chances de conseguirmos eliminar os troggs destas terras.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCulotes da FronteiraDefensor EnânicoCalças da Sorte\n\nVocê também receberá:\n\n"},
[218] = {title = "O diário roubado", objective = "Grolin Barbabranca quer que você mate Grik'nir, o Frio, e recupere seu diário.", description = "Meu diário! Eles o levaram para a caverna. O sujeito que o pegou... era um brutamontes enorme com marcas estranhas na pele e no rosto. Eu não consegui ver muita coisa além disso.\n\nJá que você teve sorte com os trolls, será que poderia tentar recuperar o meu diário?", progress = "<name>! Teve sorte?", completion = "Maravilha, &lt;name&gt;! Muito obrigado por recuperar o meu caderno. Bem, pelo visto, a situação com os trolls aqui no Vale Cristálgida está sob controle e não é mais motivo de preocupação.\n\nDepois que eu der os últimos toques no meu relatório, vou precisar que alguém o entregue ao meu irmão Senir.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nEscudo Ogival EnânicoCajado Liso de Viagem\n\nVocê também receberá:\n\n"},
[219] = {title = "Desaparecidos em ação", objective = "Proteja o Cabo Keeshan em sua jornada de volta a Cristarrubra.", description = "Finalmente, amigos da Aliança!\n\nAjude-me... por favor.... \n\nA última coisa de que me lembro é que aqueles orcs cruéis sobrepujaram o nosso regimento. A maioria dos homens foi morta. Eu levei um golpe forte na cabeça e apaguei. Agora estou preso nesta caverna. Ajude-me, estou muito ferido.\n\nVou precisar de ajuda para voltar à cidade... por favor... Você é a minha última esperança...", progress = nil, completion = "Você prestou um grande serviço ao Reino, &lt;class&gt;. Obrigado por auxiliar o Cabo Keeshan em sua fuga daqueles temíveis orcs Rocha Negra. Você receberá uma recompensa por seus atos heroicos de hoje.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nVeste de SalomãoCota Encadeada do AdjuntoCouro Cravejado com Ossos\n\nVocê também receberá:\n\n"},
[220] = {title = "O clamor da Água", objective = "Leve a Ampola da Mais Pura Água para Islen Vidente da Água nos Sertões.", description = "Islen terá mais uma tarefa para você antes de recompensá-<lo/la> com seu próximo totem. A ampola de água que eu lhe dei é necessário para qualquer que seja a tarefa. \n\nAgora vá e encontre-a. Tornaremos a nos ver algum dia.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSapta da Água"},
[221] = {title = "Worgens na floresta", objective = "Mate 12 Worgens Corretrevas Nocturnos para Petrônio na Vila Sombria.", description = "Você deve ter notado alguns worgens maiores vagando por aí com os tece-sombras na floresta, não? Pelo que podemos dizer, esses Corretrevas compõem a maior parte dos números de worgens.\n\nNas minhas caminhadas, também notei que eles invadiram o Horto Pútrido a sudoeste da cidade. Esses worgens são um pouco mais durões que os últimos que você enfrentou.\n\nFique alerta.", progress = nil, completion = "Você se saiu bem contra os Corretrevas, &lt;name&gt;. Mas parece que a quantidade deles não diminui nunca. Toda vez que fazemos algum progresso, mais deles surgem para completar suas fileiras.\n\nQue tipo de poder maligno os atrai para cá? Por que eles vieram perturbar nossas malfadadas terras...?\n\nEu vou depositar a minha fé no Mestre Carevino. Sem dúvida, ele vai desvendar esse mistério.", rewards = "Você receberá:\n\n"},
[222] = {title = "Worgens na floresta", objective = "Mate 8 Presas Torpes Nocturnos e 8 Maculados Nocturnos para Petrônio na Vila Sombria.", description = "Suas façanhas anteriores me convenceram de que você está <pronto/pronta> para lidar com os mais fortes worgens a infestar o bosque.\n\nDos worgens que se estabeleceram aqui, os Presa Torpe e os Maculados são os mais perigosos. Eles ocuparam a mina e algumas cavernas ao sul.\n\nDá para ver de longe as luzes das fogueiras deles...", progress = nil, completion = "&lt;name&gt;, para ser honesto, eu não achava que você chegaria até aqui, mas você é, sem dúvida, um &lt;class&gt; com quem se pode contar. Na verdade, se você quiser se juntar oficialmente à luta do Mestre Carevino, eu escreverei uma carta de recomendação com prazer.", rewards = "Você receberá:\n\n"},
[223] = {title = "Worgens na floresta", objective = "Entregue o bilhete do Petrônio para João Carevino.", description = "Aqui está, <name>. Leve esta carta para o Mestre Carevino.\n\n<Ele rapidamente pega um pedaço de pergaminho desbotado e lhe entrega.>\n\nMais algumas pessoas como você, e em breve superaremos a Vigília Noturna em número! Quem sabe então não conseguiríamos alcançar o objetivo que os poucos que somos hoje tanto almejam?", progress = "Sim, o que foi? Seja rápido tenho assuntos mais urgentes a tratar.", completion = "Desculpe-me pela recepção fria, &lt;name&gt;, mas, como você pode ver, sou um homem muito ocupado. Pelo jeito você causou boa impressão no Petrônio – eu garanto que isso não é fácil – e ele escreveu uma carta de recomendação para você.\n\nHá muitos sujeitinhos desagradáveis por aí, &lt;name&gt;, por isso a ajuda de todos os que se mostram capazes é muito útil. Nós combatemos demônios, mortos-vivos e qualquer um que os ajude. Fique atento, tome cuidado e desconfie de todos que não apoiam nossa causa.\n\nGlória na Luz.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nManto da FéEscudo da Fé\n\nVocê também receberá:\n\nVarinha Consagrada"},
[224] = {title = "Em defesa das terras do Rei", objective = "O Montanhista Sapatorro, da torre de guarda sul, quer que você mate 10 Troggs Lascapedra e 10 Batedores Lascapedra.", description = "Temos que proteger o Loch, <name>! Com tantas tropas lutando bravamente em frentes longínquas, nós ficamos sobrecarregados com a defesa de casa. Tem troggs surgindo de cada fenda na rocha! Essa infestação é a maior ameaça a Altaforja hoje. Aqueles mutantes nojentos têm que ser destruídos.\n\nPrecisamos da sua ajuda, <bravo aventureiro/brava aventureira>, para dar fim a esse perigo. Mate 10 troggs Lascapedra e 10 batedores Lascapedra volte aqui.", progress = nil, completion = "Muito bem, &lt;name&gt;! Você demonstrou coragem fenomenal em campo. Com inciativas como a sua, venceremos a guerra contra os troggs. Você serviu ao rei Magni com excelência.\n\nSe você se acha adequado para esse tipo de aventura, sugiro que você fale com o Montanhista Cascalhaça. O Capitão o designou para patrulha difícil, e eu tenho certeza de que ele pode precisar da sua ajuda. Fale com Cascalhaça dentro da torre.", rewards = "Você receberá:\n\nQueijo Azedo de Dalaran"},
[225] = {title = "A velha lápide", objective = "Fale com Sirra Von'Indi na Vila Sombria.", description = "Na lápide antiga lê-se apenas:\n\nMORGAN LADIMORE\n\n<Ao olhar mais de perto, você repara que a terra sobre o túmulo foi remexida há pouco tempo. Na verdade, parece que uma boa porção de terra foi deslocada...>", progress = nil, completion = "Morgan Ladimore?\n\nAh, sim, claro. Homem de triste e longa história. Eu o conhecia... Bem, eu o conheci antes de ele sair para a guerra, mas essa foi a última vez que o vi. Era nobre e bondoso, mas foi vítima de um destino terrível.\n\nEspere, devo ter algo aqui que conte a história melhor do que minha memória...", rewards = "Você receberá:\n\n"},
[226] = {title = "Lobos nos nossos calcanhares", objective = "Mate 12 Lobos Hediondos Famintos e 8 Lobos Hediondos Raivosos e fale novamente com Lars no Acampamento do Noel, na fronteira oeste da Floresta do Crepúsculo.", description = "Noel e eu temos dias perigosos pela frente, com o Necromante a leste e tudo o mais. E, aqui sozinhos como estamos, temos que caçar nossa própria comida. Parece que, toda vez que estou voltando para o acampamento trazendo carne, lobos famintos ou raivosos saem da floresta querendo uma mordida. Nem preciso dizer que viver aqui é um trabalho perigoso!\n\nMas se você puder nos livrar de alguns desses lobos, vai facilitar a nossa vida. Eles costumam rondar o norte e o leste daqui, perto do rio.", progress = nil, completion = "Ah, que bom. Você deu uma reduzida na alcateia. Sem aqueles lobos mordendo os nossos calcanhares, podemos nos concentrar em cuidar do Morbídio Vil.\n\nAceite alguns desses sacos de chamas. Eles são feitos na cidade, e muitos foram doados para ajudar a causa de Noel.", rewards = "Você receberá:\n\nSaco de Chamas"},
[227] = {title = "Morgan Ladimore", objective = "Fale com a Comandante Aldora Ebanez na Vila Sombria.", description = "<Ele olha as prateleiras e encontra um livro com capa de couro.>\n\nCaso você queira saber mais, é melhor perguntar à Aldora. É ela que tem lidado com o... problema... ultimamente.", progress = nil, completion = "Morgan Ladimore? Nunca ouvi falar...\n\nAh, você quis dizer Mor'Ladim! Acho que você ainda não conhece a história.\n\nBem, vou lhe contar a versão reduzida. Um morto-vivo errante chamado Mor'Ladim vem vagando pela Floresta do Crepúsculo. Pelo que sabemos, trata-se do cadáver vivente de Morgan Ladimore, esse que você mencionou. Ele vem causando todo tipo de problemas, desde atacar patrulheiros até assassinar inocentes.\n\n&lt;Aldora olha para você de cima a baixo.&gt;\n\nVocê me parece bom o bastante. Talvez consiga fazer Mor'Ladim descansar.", rewards = "Você receberá:\n\nA História de Morgan Ladimore"},
[228] = {title = "Mor'Ladim", objective = "Mate Mor'Ladim e entregue a caveira dele para a Comandante Aldora Ebanez na Vila Sombria.", description = "Pelo que os batedores me disseram, Mor'Ladim vaga pela Floresta do Crepúsculo, seguindo um estranho e sinuoso caminho pelo cemitério. Nós o enterramos logo acima da casa do outeiro, você sabe onde fica?\n\nNão posso te ajudar muito além disso, mas lhe desejo boa sorte.", progress = "Eu entendo que você esteja tendo dificuldade com o Mor'Ladim, <name>. Perdemos alguns dos nossos mais fortes vigias para ele.", completion = "Você o matou? Isso é um grande feito, &lt;name&gt;! Em nome da Vigília Noturna e do povo de Vila Sombria, eu lhe agradeço.\n\nAh... Só tem um probleminha...", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[229] = {title = "A filha sobrevivente", objective = "Fale com a Vigia Sarah Ladimore na Vila Sombria.", description = "Eu devia ter mencionado isso antes...\n\nComo é que vou explicar...\n\nEmbora Morgan acreditasse que toda a família dele estava morta, na verdade, a filha Sarah Ladimore agora é uma Vigia. Ela sempre foi muito perturbada pelas... circunstâncias... da morte do pai. Talvez você possa dar a notícia a ela.", progress = nil, completion = "Pois não? Meu pai...\n\n&lt;O olhar de Sarah se entristece.&gt;\n\nQuem me dera... que eu pudesse ter feito algo por ele... Ah, se eu tivesse conversado com ele antes de...", rewards = "Você receberá:\n\n"},
[230] = {title = "Acampamento do Noel", objective = "Leve o Livro da Fazenda de Noel para Noel.", description = "Cavando o monte, você encontra um pequeno livro sujo de terra.\n\nO livro não tem título, mas... contém informações que o Noel gostaria de saber.", progress = "Você achou o quê?! Por favor, deixe-me ver isso...", completion = "&lt;Noel lê as páginas do livro e, então, fecha-o com um estrondo.&gt;\n\nAquela figura sombria que eu vi foi testemunha da morte da minha família! Eu queria fazer algumas perguntas àquele homem... se eu soubesse ele era!", rewards = "Você receberá:\n\n"},
[231] = {title = "Amor de filha", objective = "Leve o anel de Sarah Ladimore até o túmulo de Morgan Ladimore.", description = "Pegue isto e bote no túmulo de meu pai. Quem sabe... de alguma forma, assim ele saberá que eu estou bem e que ninguém o responsabiliza pelo que aconteceu.\n\n<Lágrimas escorrem do rosto de Sarah enquanto ela retira um anel da mão direita e o entrega a você.>\n\nE... Obrigada, <name>.", progress = "Deixe-me em paz...", completion = "&lt;Uma voz fantasmagórica ecoa no vento...&gt;\n\nIsto é o...? Sarah? Como é possível ela ainda estar viva? Tirei um peso dos ombros...\n\n&lt;name&gt;. Por favor, pegue Arqué, a minha espada. Como minha alma por fim vai descansar, não preciso mais dela. Arqué foi forjada para fazer o bem e, embora eu não tenha honrado sua natureza, talvez você possa usá-la para espalhar a Luz.\n\nLis, meu amor...", rewards = "Você receberá:\n\nArqué"},
[232] = {title = "Tarefa para a Boticária Zilda", objective = "Leve a ordem de compra para Alessandro Luca na Cidade Baixa.", description = "A Real Sociedade de Boticários precisa da sua ajuda. Ouça atentamente, e nós <dois/duas> podemos lucrar com o meu plano.\n\nO boticário Daniel na sala ao lado pode ficar... sentido... mas sua pesquisa tem potencial para dar frutos se for conduzida por um pesquisador inteligente. Felizmente, eu sou esse pesquisador. Eu quero que você leve este pedido de compra para Alessandro Luca, o proprietário da Miudezas da Lua Azul; ele vai lhe dar um kit de teste. Assim que recebê-lo, traga-o para mim, e eu vou detalhar a sua tarefa.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[233] = {title = "Correio de Vale Cristálgida", objective = "Entregue a Pilha de Cartas para Talin Olhobom.", description = "Hum, será que você poderia me fazer um favor, <rapaz/moça>? Eu recebi uma pilha de cartas hoje, mas não tenho tempo para encaminhá-las.\n\nElas são todas endereçadas a Talin Olhobom. Você pode encontrá-lo a oeste, na estrada. Ele montou acampamento ao lado do lago congelado.\n\nO que me diz?", progress = "Sim? Trouxe alguma coisa para mim?", completion = "Obrigado, eu estou esperando por essas cartas há um tempão...\n\nInfelizmente, essas elas não são todas para mim. Esta é endereçada a Grolin Barbabranca. Ele não está muito longe daqui, caso você queira entregá-la para ele.", rewards = "Você receberá:\n\n"},
[234] = {title = "Correio de Vale Cristálgida", objective = "Entregue a Carta para Grolin Barbabranca.", description = "Se me lembro corretamente, o acampamento do Grolin fica seguindo a estrada para sudeste. Sem dúvida, ele vai estar ansioso para receber a carta dele.", progress = "Posso ajudar de alguma forma?", completion = "Ah, excelente. Faz algum tempo que não recebo notícias de Altaforja.", rewards = "Você receberá:\n\n"},
[235] = {title = "A caçada do Vale Gris", objective = "Fale com Senani Coração Trovejante no Posto Machadada, que fica no Vale Gris.", description = "Atenção, jovens aventureiros! Os ermos selvagens do Vale Gris esperam por vocês!\n\nA Horda estabeleceu uma forte presença nas terras ao norte dos Sertões. Nossos dois postos avançados, o Posto Machadada e o Posto da Praia de Zoram, pelejam pela glória da Horda! Os que desejarem se pôr à prova deverão seguir para um desses postos para obter orientações. E atenção: Senani Coração Trovejante, no Posto Machadada, ao norte dos Sertões, procura aventureiros dispostos a tomar parte numa grande caçada no Vale Gris!", progress = nil, completion = "Bem-vindo à nova fronteira, &lt;name&gt;. O Vale Gris é uma terra de oportunidades, em que um jovem &lt;class&gt; como você poderá encontrar inúmeras chances de provar seu valor. Dê uma volta pelo posto e não esqueça de visitar a Praia de Zoram, pois lá existe um outro posto da Horda.\n\nDeduzo pela sua presença que deseja aprender mais sobre a caçada. Escute com atenção, e eu lhe direi o que você precisa saber.", rewards = "Você receberá:\n\n"},
[236] = {title = "Abastecimento dos demolidores", objective = "O Mestre de Cerco Cabofirme na Fortaleza Invérnia quer que você tome 10 Brasas Eternas de jogadores da Horda no Caldeirão das Chamas.", description = "<name>! Até que enfim apareceste. Embora a batalha tenha terminado, ainda temos que nos preparar para a próxima. Vai ao Caldeirão das Chamas no sudeste de Invérnia e consegue algumas brasas eternas.\n\nAs brasas são misturadas com carvão para abastecer as máquinas de cerco e demolição. \n\nAh, e se avistares membros da Horda no Caldeirão, não hesites em matá-los e tomar-lhes as brasas. Não podemos deixar que tenham a vantagem na próxima batalha.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[237] = {title = "Em defesa das terras do Rei", objective = "O Montanhista Cascalhaça, da torre de guarda sul, quer que você mate 10 Batecrânios Lascapedra e 10 Videntes Lascapedra e fale novamente com ele.", description = "O Montanhista Sapatorro foi só elogios em relação a você, <race>. Por esse motivo, eu vou lhe confiar uma missão da maior importância. Precisamos manter a pressão sobre as forças invasoras de troggs até que os nossos irmãos anões retornem do front da Aliança.\n\nVá para as colinas ao sul e mate 10 Batecrânios Lascapedra e 10 Videntes Lascapedra. Seus ataques vão nos dar algum tempo. Reporte-se quando sua missão estiver concluída.", progress = nil, completion = "Você cumpriu bem sua missão, &lt;race&gt;. O próprio rei ficaria orgulhoso.\n\nMas os reforços prometidos nunca vieram. Aparentemente, o front da Aliança é um mar rubro do sangue de nossos irmãos caídos. A notícia das baixas deixa um gosto amargo na nossa boca. Mas não devemos deixar que a morte deles seja em vão. Precisamos nos unir nesses tempos sombrios e resgatar a glória da Aliança.\n\nFale com o Montanhista Batemuro para ser redesignado. Precisamos de você mais do que nunca.", rewards = "Você receberá:\n\nPoção Inferior de Cura"},
[238] = {title = "Tarefa para a Boticária Zilda", objective = "Leve o Kit de Amostra de Campo para Boticária Zilda na Cidade Baixa.", description = "O kit está pronto, <name>. Não há dúvida de que a Boticária Zilda vai querer isso o mais rápido possível. Leve para ela sem demora, e avise a ela que vou pendurar esse kit na conta da Sociedade. Além disso, por favor, transmita a ela minhas mais calorosas saudações.\n\nBem, não fique aí <parado/parada>. Essa é a vida de um simples mensageiro, não é?Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[239] = {title = "Ribeira d'Oeste precisa de ajuda!", objective = "Vá até a Guarnição da Ribeira d'Oeste e fale com o Delegado Ranieri.", description = "A Guarnição na nossa fronteira ocidental informa que tem havido aumento da atividade de gnolls e ladrões. Eles estão requisitando mais soldados de Ventobravo... mas não temos ninguém para enviar!\n\nSe você puder ajudar, viria em boa hora. Fale com o Delegado Ranieri, na Guarnição da Ribeira d'Oeste, e veja o que ele quer.\n\nA Guarnição fica seguindo a estrada, na direção oeste. É só atravessar a ponte que passa pelo córrego. A Guarnição fica logo à direita.", progress = nil, completion = "O delegado Durão enviou você, não é? Bom, você não é do exército, mas se Durão o enviou, está bom pra mim!\n\nNossa situação é... estressante, para dizer o mínimo. Espero que você possa nos ajudar.", rewards = "Você receberá:\n\n"},
[240] = {title = "Fale novamente com Medrisco", objective = "Fale novamente com Medrisco levando Bolinhos de Caranguejo Crepuscular.", description = "Aqui está: bolinhos de caranguejo crepuscular à la Goroba!", progress = "<name>, você chegou! Cadê a minha comida?!", completion = "Ah-rá! Obrigado, &lt;name&gt;. Você acertou em cheio!\n\nAceite isto como pagamento, por favor!", rewards = "Você receberá:\n\n"},
[243] = {title = "Trabalho de campo", objective = "Fale com o Engenheiro-chefe Silvadágua em Geringontzan sobre obter uma fonte de energia para o kit de testes.\n\nItem fornecido:\n", description = "Você vai para o deserto de Tanaris testar a consistência tecidual dos basiliscos, dos escorpídeos e da hiena nativos de lá. Obtenha amostras de tecido e use-as com o kit de teste a reboque; uma parte das amostras vai falhar, conte com isso. Preciso de oito amostras aceitáveis de cada tipo de criatura.\n\nAlém disso, os goblins de Geringontzan têm uma fonte de energia que deve ser usada com esse tipo de kit modificado. Fale com o Engenheiro-chefe Silvadágua e arranje uma para o kit.\n\nSó me procure de novo quando tiver concluído tudo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[244] = {title = "Invasão de gnolls", objective = "Fale com o Subdelegado David.", description = "Salve, <class>. Estou encarregado de patrulhar este trecho da estrada. Embora a estrada esteja segura por ora, eu vi acampamentos de gnolls ao norte e a leste daqui.\n\nA Vila Plácida deve ser avisada sobre as forças dos gnolls que estão se reunindo! Reporte-se ao Subdelegado David na Vila Plácida e conte a ele sobre os gnolls. Faça isso, e tenho certeza de que Feldon lhe oferecerá o salário de um batedor.\n\nFeldon costuma inspecionar a área ao sul da ponte para a Vila Plácida.", progress = nil, completion = "Tem gnolls de Cristarrubra tão perto assim de Elwynn?! Eles podem estar prontos para avançar sobre nossa terra. Em breve, o povo da Vila Plácida pode não ser os únicos humanos sob cerco!\n\nAqui está o seu pagamento, embora você nos traga notícias terríveis. E não tinha pior hora para isso acontecer, pois estamos mal equipados para lidar com esse problema.", rewards = "Você receberá:\n\n"},
[245] = {title = "Ameaças de oito patas", objective = "Mate 15 Veneracnas Pigmeias, e depois apresente-se novamente ao Vigia Gouveia na Floresta do Crepúsculo.", description = "Salve, <class>. Talvez você tenha visto aquelas aranhas que tomaram conta da fronteira ocidental. Essas ameaças de oito patas são demais para nós, e o Comandante não tem homens suficientes para atuar tão longe de Vila Sombria.\n\nDetesto lhe pedir isso, mas você me parece capaz de lidar com elas. Não posso lhe prometer nada de grande valor, mas posso recompensá-l<o/a> se puder ajudar a erradicar esses pragas imundas.\n\nAh, um conselho: procure evitar o veneno delas, se puder.", progress = nil, completion = "Ah! Você voltou. E não muito acabada, ao que parece. Bem, &lt;name&gt;, a Vigília Noturna é grata pelo seu trabalho, e lhe prometi uma recompensa, então aqui está.", rewards = "Você receberá:\n\nManoplas da Vigília Noturna"},
[246] = {title = "Avaliando a ameaça", objective = "Mate 10 Mestiços de Cristarrubra e 6 Caçadores Ilegais de Cristarrubra, e então apresente-se novamente ao Subdelegado David em Vila Plácida.", description = "Não temos homens sobrando, mas é preciso fazer algo com relação aos gnolls que você relatou.\n\nVasculhe o sul de Cristarrubra à procura de gnolls. Persiga-os, mate aqueles que puder e volte para me informar quantos são e a ameaça que representam. Você vai encontrar esses gnolls acampados ao longo da estrada sul de Cristarrubra.\n\nFaça isso pelo Exército de Ventobravo, <name>, e você será recompensad<o/a>.", progress = nil, completion = "&lt;O Subdelegado David ouve seu relatório...&gt;\n\nHá uma força considerável de gnolls acampados por lá, e, pelo que você me diz, são bem durões. Não deve ter sido fácil obter essas informações.\n\nMaravilha, &lt;name&gt;. Agradecemos sua ajuda.\n\nFale com o Oficial Marris e o Magistrado Salomão, em Vila Plácida, se ainda não fez isso. Nossa situação está cada vez mais desesperadora — esses dois precisarão de você, tenho certeza.", rewards = "Você receberá:\n\n"},
[247] = {title = "Caçada do Vale Gris terminada", objective = "<name>, você abateu as três feras da Caçada do Vale Gris e trouxe para mim as provas de seu feito. Portanto, sua força e sua tenacidade são inquestionáveis. O sangue dos caçadores corre nas suas veias, e você merece todos os elogios!\n\nPor favor, aceite este adorno como um símbolo de reconhecimento dos seus impressionantes feitos no Vale Gris em nome da Horda. Que seus antepassados o protejam!", description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nManto do Caçador Selvagem"},
[248] = {title = "Vendo além", objective = "Suba ao topo da torre de vigia intacta ao lado da Bastilha Mirante de Pedra.\n\nEncontre um recipiente adequado para colocar o Glifo de Azora.", description = "Tenho observado a Torre de Ilgalar, e percebo que Morganth está em conflito com os orcs na Bastilha Mirante de Pedra. Não sei o motivo por trás dessa disputa, mas você pode me ajudar a desvendar o mistério.\n\nPegue outro Glifo de Azora, então vá para o topo da torre de vigia intacta ao lado da Bastilha.\n\nEncontre um objeto adequado em que colocar o Glifo. Um baú ou um jarro — algum lugar onde o glifo não seja notado — servirá ao meu propósito.", progress = "Esta jarra já conteve um líquido malcheiroso, mas agora está vazia e esquecida em cima de dois barris.", completion = "É um recipiente adequado para o Glifo de Azora, permitindo que Teócrito mergulhe nas mentes dentro da Bastilha Mirante de Pedra.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[249] = {title = "Morganth", objective = "Derrote Morgan.\n\nLeve o Pingente das Sombras dele para Teócrito na Torre de Azora, na Floresta de Elwynn.", description = "Após muito observar, descobri os planos de Morganth.\n\nEle procura pela Foice de Eluna... e pode tê-la encontrado! Se for verdade, então as Montanhas Cristarrubra e o reino de Ventobravo logo sentirão seu poder sombrio.\n\nNão estou preparado para me opor a Morganth, mas receio que seja preciso combatê-lo agora. Então você deve entrar na Torre de Ilgalar e enfrentar Morganth.\n\nDerrote-o e me traga seu Pingente das Sombras. Faça isso, e o reino terá uma grande dívida com você.", progress = "Depressa, <name>, antes que meu receio de que Morganth possa expandir seu poder se concretize!", completion = "Você se saiu bem, &lt;name&gt;. Ao derrotar o bruxo Morganth, Ventobravo foi salvo de um grave perigo.\n\nOfereço-lhe humildemente esta recompensa. E lhe desejaria paz, se os tempos fossem pacíficos.\n\nEm vez disso, desejo-lhe sorte em suas provações e vitória nos conflitos que estão por vir.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nDragonas Rosas"},
[250] = {title = "Uma ameaça sombria à espreita", objective = "Investigue a área próxima à rampa leste do Dique e dê ao Engenheiro-chefe Vedaçude uma pista que possa indicar o que os Anões Ferro Negro estão tramando.", description = "Ainda bem que tem gente que se preocupa com a situação do Dique. Primeiro destroem a Ponte Thandol, agora a pilhagem de Dun Modr! O Dique é o próximo alvo dos Ferro Negro, isso é mais certo que geada no pastiçal quando inverna. A maior parte do destacamento de segurança daqui foi mandado pro Front da Aliança, incluindo meu melhor inspetor.\n\nÉ por isso que eu preciso de ti, <name>. Eu avistei uns Sapadores Ferro Negro cheirando não sei o que perto da rampa leste do Dique. Vai lá campear e traz alguma pista.", progress = nil, completion = "O barril de aparência suspeita contém um pó com cheiro estranho.", rewards = "Você receberá:\n\n"},
[251] = {title = "Tradução da carta de Abercrombie", objective = "Leve a Carta para Malaquias até Sirra Von'Indi.", description = "Leve a mensagem que você me entregou para Sirra Von'Indi. Ele é bastante versado em idiomas e artes antigas — se alguém aqui pode traduzir essa mensagem, é ele.", progress = "Uma mensagem de Abercrombie, o eremita, hein? Não o conheço, mas se mora no cemitério, sei que o cérebro dele deve estar podre!", completion = "&lt;Sirra começa a traduzir a carta...&gt;", rewards = "Você receberá:\n\n"},
[252] = {title = "Tradução para Malaquias", objective = "Leve a Carta Traduzida para Lorde Malaquias Ebanez.", description = "Minha nossa! Se o que está escrito na carta for verdade, então Abercrombie fez algo... inconcebível.\n\nMelhor você levar a carta traduzida de volta para Malaquias.\n\nE depressa, pois temo que uma desgraça já assome em direção à nossa cidade...", progress = "Ah, a carta está traduzida? Por favor, deixe-me vê-la...", completion = "&lt;Malaquias lê a carta... e seu rosto fica pálido. Ele então olha para você...&gt;\n\nEmbora a ajuda que você deu a Abercrombie possa ter sido motivada pela gentileza, suas ações condenaram nossa cidade.\n\n&lt;Malaquias se perde em pensamentos...&gt;", rewards = "Você receberá:\n\nCarta do Embalsamador Traduzida"},
[253] = {title = "A noiva do Embalsamador", objective = "Encontre o túmulo de Elisa. Retire dela o Coração do Embalsamador, então fale novamente com Malaquias Ebanez.", description = "O Embalsamador é um nome presente em uma das histórias que contamos aos nossos filhos. Era um gentil alquimista, que enlouqueceu com a morte da esposa, Elisa. Para recuperá-la, ele usou magia negra para colocar o próprio coração no seio da esposa morta.\n\nCom isso, Elisa reviveu... mas foi amaldiçoada com uma fome por carne humana, forçando o Embalsamador a mantê-la enterrada.\n\nO poder dele reside no coração. Encontre o túmulo da esposa, recupere o coração e traga-o para mim enquanto ainda bate. Faça isso e o poder dele será destruído.", progress = "Você já encontrou o túmulo de Elisa? Está com o coração do Embalsamador?", completion = "Você conseguiu! A magia no coração dele será prova contra o mal do Embalsamador, e uma poderosa égide sobre a cidade de Vila Sombria.\n\nVocê fez algo maravilhoso por nosso povo, &lt;name&gt;. Seu nome permanecerá em nossos anais para sempre.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nDragonas da HonraEscudo de Vila Sombria"},
[254] = {title = "Escavando a terra", objective = "You dig through the semi-loose dirt over the grave...and find the remains of a coffin.\n\nWithin the coffin you can feel the vibrating scrapes of clawed fingers and the gnashing of rotted teeth...\n\nWill you open the coffin and retrieve the Embalmer's heart?", description = nil, progress = nil, completion = nil, rewards = nil},
[255] = {title = "Mercenários", objective = "O Magistrado Grã-narina de Thelsamar contratou você para matar 4 Ogros Mo'grosh, 4 Brutamontes Mo'grosh e 4 Impositores Mo'grosh.", description = "Pra tu vê, dois meses só para ouvir de Altaforja que receberemos um punhado de soldados para a nossa defesa, e mais dois meses até eles chegarem! Não bastasse nossa cidade estar desprotegida, a escavação e a represa — a represa, tchê! — também estão abertas a ataques.\n\nBah, não tenho escolha, ao que parece. Vou precisar contratar ajuda.\n\nE tu? Parece bem aprumad<o/a>, <class>. Um grupo de ogros montou acampamento a nordeste do lago. Talvez tu possa arrebanhar alguns outros para ajudar a pelear e eliminar a ameaça à nossa cidade?", progress = nil, completion = "Bah, isso é bom como faca achada! Eu e o povo de Thelsamar apreciamos muito seus esforços. Melhor ainda, tchê, se pudermos resolver a pendenga do ogro, poderemos nos concentrar naqueles troggs de Luz Queimante.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[256] = {title = "PROCURA-SE: Chok'sul", objective = "Mate Chok'sul e entregue a cabeça para o Magistrado Grã-narina, em Thelsamar.", description = "Por ordem do Magistrado de Thelsamar:\n\nChok'sul, o ogro que supostamente liderou os ataques contra Thelsamar, o Dique Lapidado e o sítio arqueológico, foi condenado à morte.\n\nSerá oferecida uma recompensa financeira a quem entregar uma prova da morte do facínora ao Magistrado.\n\nChok'sul foi visto pela última vez no acampamento dos ogros, que fica a nordeste de Loch Modan.", progress = "Pois não, posso ajudar?", completion = "Eca! Que coisa é essa? E por que você traria isso...\n\nCacilda! Será? Que a Luz queime meus fundilhos! Essa é a cabeça daquele ogro sanguinário? Que baita troféu, hein? Tome aqui sua recompensa. Você tem a minha gratidão e a do povo de Thelsamar.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nOmoplatas Encadeadas DuráveisBotas Kimbra\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\nAnel de Canalização Menor"},
[257] = {title = "Modéstia de Caçador", objective = "Mate 6 Urubus Montanheses e fale novamente com Dário, o Novato, no Pavilhão dos Andarilhos, dentro de 15 minutos.", description = "Você acredita mesmo que pode competir em inteligência com Dário, o Ousado, hein? Acho que não! É claro que tem todo o direito de tentar. Vou lhe propor um desafio que deve estar acima de suas habilidades, então não se sinta mal se não estiver à altura.\n\nUm bando de urubus aninhou-se aqui em Loch Modan. Por que não tenta se livrar de alguns desses bichos? Vamos fazer assim, se cumprir meu desafio em quinze minutos, eu lhe darei meu arco ou minha arma.\n\n<Dário <o/a> encara com desdém.>\n\nParece que você não tem muito a perder, de qualquer maneira.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nArco de Caça de DarylRifle de Caça de Daryl\n\nVocê também receberá:\n\n"},
[258] = {title = "Desafio de caçador", objective = "Mate 5 Javalis Montanheses Anciãos e fale novamente com Dário, o Novato, no Pavilhão dos Andarilhos, dentro de 12 minutos.", description = "<name>ath, é isso? Vejo que está explodindo de orgulho por ter completado o primeiro teste, não é mesmo? Como lhe disse antes, não foi nada demais.\n\nVocê deveria experimentar caçar javali. Confie em mim, isso aqui não se parece em nada com a caça ao javali do Vale Cristálgida, então é melhor tomar bastante cuidado. Vou lhe dar apenas doze minutos desta vez.\n\nNão se sinta mal se falhar. Sou capaz de lhe dar minha roupa do corpo se você conseguir!\n\nJá lhe contei a história de como arranjei minha famosa cicatriz? Não? Foi há dois anos...", progress = nil, completion = "Você parece um pouco acabado. Sem dúvida os javalis lhe deram trabalho, não é? Não se preocupe, não vou sair por aí falando nada, posso imaginar o quanto já deva ser difícil para você sem o disse-me-disse.\n\nAh! Você conseguiu matar os javalis? Eu... bem... quer dizer, não estou nem um pouco surpreso! Qualquer criança teria... aposta?\n\nQue aposta?", rewards = "Você receberá:\n\nCamisa de Tecido de QualidadeEspada Curta de Daryl"},
[261] = {title = "No caminho Escarlate", objective = "Destrua 30 Assoladores Mortos-vivos, depois fale novamente com o Irmão Anton no Posto do Nijel.", description = "Deixe-me ser franco. Estamos em guerra com o Flagelo. É um mal que corrompe o nosso povo e infecta a nossa terra. Precisamos contê-lo antes que ele nos destrua e mergulhe nosso mundo nas sombras. Nós, da Cruzada Escarlate, juramos lutar de corpo e alma contra o Flagelo.\n\nSe estiver conosco, lance mão da sua coragem e prove a sua lealdade: destrua os assoladores mortos-vivos da Desolação, no Vale dos Ossos, que fica ao sul.\n\nCumpra essa missão e Cruzada acolherá você.", progress = nil, completion = "Você comprovou a sua determinação, com os cumprimentos da Cruzada Escarlate.\n\nSe você não se esquivar do massacre honrado, talvez seu caminho deva mesmo se juntar ao nosso...", rewards = "Você receberá:\n\n"},
[262] = {title = "O vulto sombrio", objective = "Leve o livro que encontrou na fazenda de Noel e mostre-o aos cidadãos da Vila Sombria.", description = "Preciso saber quem era o vulto que estava se esgueirando nas sombras na noite em que minha família foi assassinada!\n\nPor favor, <name>, você já demonstrou sua astúcia. Encontre aquela figura sombria, por favor. Preciso descobrir seus segredos!\n\nNão tenho nenhuma pista que você já não saiba, mas tome... leve o livro que você encontrou na minha fazenda. Talvez alguém na cidade consiga ligar o livro ao dono.", progress = "Se você tiver algum pertence desse vulto sombrio que procura, então deixe-me ver. Posso tentar intuir algo sobre ele.", completion = "Hmm... este livro foi manejado recentemente, por alguém que é movido pela vingança. mas estão muito fracas as impressões deixadas nele...pelo menos, as deixadas pelo dono original do livro.\n\nContudo, posso dizer que o dono original não é nativo da Vila Sombria.", rewards = "Você receberá:\n\n"},
[263] = {title = "Em defesa das terras do Rei", objective = "Montanhista Batemuro na torre de vigia sul quer que você mate 10 Xamãs Lascapedra and 10 Quebraossos Lascapedra.", description = "Os Montanhistas me contaram que você é muito competente e <corajoso/corajosa>, <name>. <Um/Uma> <race> como você seria muito útil por aqui. Os reservistas foram convocados para o front e ficamos desguarnecidos. Mas agora que temos <um/uma> <class> <veterano/veterana> conosco, vamos ver o que podemos fazer.\n\nSaia em campo e mate 10 Xamãs Lascapedra e 10 Quebraossos Lascapedra. Vamos ver se você faz jus à sua reputação, <matador/matadora> de troggs.", progress = nil, completion = "Assim que eu o vi, &lt;name&gt;, soube que você deixaria Altaforja orgulhosa. Você prestou um grande serviço ao Rei Magni. Agora que já provou que é um aliado leal ao Reino, pode ir falar com o Capitão para ver se ele tem uma tarefa mais relevante para te dar.\n\n Eu o saúdo, &lt;name&gt;.", rewards = "Você receberá:\n\nPedra de Afiar Grosseira"},
[264] = {title = "Até que a morte nos separe", objective = "Coloque o Pingente da Clarice no Túmulo de Yuriv em Pinhaprata.", description = "Cachorro! Me deixar por causa dessa cruzada maldita. Tudo porque \"a Luz é o que temos de mais importante contra a ameaça que os mortos-vivos representam.\"\n\nE as crianças?! E EU?! Esperei sentada por ele, noite após noite, pacientemente... mas a porcaria do dever sempre veio primeiro!\n\nÉ isso aí, <name>, veja bem o que você ganha com a \"justiça\"! Agora ele está morto e aquilo contra o que ele lutou toda a vida é o meu fardo!\n\nLeve esse lixo para a tumba dele lá no Sepulcro. Não quero ter mais nada a ver com isso... nem com ele!", progress = "O túmulo está frio e, ao olhar, você percebe que ele foi violado. Há lixo espalhado, rachaduras e fendas decoram a imagem entalhada no caixão e as plantas em volta começaram a tomar conta do lugar.\n\nNinguém, e muito menos as vítimas da peste, se importa com as pessoas enterradas neste lugar.", completion = "Ao colocar o pingente no túmulo, é possível observar que a gema perde o brilho rapidamente.\n\nAntes de partir você olha para o pingente, que jaz sem vida sobre a mão entalhada no topo do caixão. Seus pensamentos são interrompidos por uma forte e gelada brisa que corta o Sepulcro. Por alguns instantes, só se ouve o silêncio.", rewards = "Você receberá:\n\n"},
[265] = {title = "A busca sombria continua", objective = "Tente encontrar outras pistas sobre o vulto sombrio entre os aldeões.", description = "Aqui está o seu livro de volta, <name>, e eu lhe desejo sorte na busca. Se você perguntar a outras pessoas na cidade, talvez alguém tenha pistas melhores para te dar.\n\nEnquanto isso, eu tenho mais uma para você: embora o autor deste livro seja um forasteiro em Vila Sombria, o livro em si foi comprado aqui.", progress = "Às vezes eu vendo volumes em branco para estudiosos e bruxos, ou para qualquer um que queira escrever um diário.\n\nEu me lembro muito bem de cada cliente dos últimos meses...", completion = "Ah, sim. Vendi este livro coisa de um mês atrás para um sujeito sombrio. Não disse o nome dele, mas estava nervoso e inquieto, sempre olhando por cima do ombro.\n\nEspero que isso ajude...", rewards = "Você receberá:\n\n"},
[266] = {title = "Perguntas na estalagem", objective = "Procure pistas sobre o vulto sombrio na estalagem", description = "Aqui está o seu livro e boa sorte com a investigação.\n\nAcho que pode ser uma boa ideia perguntar na estalagem. Se a pessoa que você procura passou algum tempo aqui na cidade, talvez alguém na estalagem possa ajudar.", progress = "O movimento no Corvo Escarlate não anda muito bom, mas ainda recebemos alguns viajantes de passagem.\n\nSe você puder descrever o sujeito que procura, talvez eu me lembre dele.", completion = "Ah, sim, eu me lembro deste livro. O dono alugou um quarto durante alguns dias e passava as noites no bar, escrevendo.\n\nDeixou a cidade às pressas, resmungando que estava sendo perseguido por... alguma coisa. Da última vez que o vi, ele estava saindo da cidade rumo ao oeste, procurando um lugar para se se esconder.\n\nO primeiro lugar a oeste daqui é o Monte Corvo...", rewards = "Você receberá:\n\n"},
[267] = {title = "A ameaça dos troggs", objective = "Leve 8 Dentes de Pedra de Trogg para o Capitão Balbúrdia na torre de vigia sul.", description = "<class>, não sei se você já soube da ameaça trogg que paira sobre as terras enânicas. Os Reservistas de Altaforja foram enviados ao front da Aliança. Com isso, nos restou apenas uma fração das tropas necessárias para manter a segurança destas terras. Meu regimento foi designado para vigiar o Portão e não podemos deixar nossos postos, por causa do perigo de invasão.\n\nMas temos que oferecer alguma resistência a estes malditos troggs que espreitam as montanhas. Ataque-os, se for capaz. E traga 8 Dentes de Pedra de Trogg como prova da façanha.", progress = "Já conseguiu os 8 Dentes de Pedra de Trogg? Se não, o trabalho ainda não acabou, <name>.", completion = "Estou deveras impressionado, &lt;name&gt;. Defendendo nossas terras, você prestou um grande favor à raça enânica. O Rei Magni Barbabronze ficaria orgulhoso!", rewards = "Você receberá:\n\n"},
[268] = {title = "Respostas a Noel", objective = "Leve o diário completo de Medrisco para Noel.", description = "O livro que você encontrou ainda não estava terminado. Tive que deixar na fazenda de Noel quando fugi...\n\nAqui, leve o meu diário completo para Noel, pois nele está descrito tudo o que aconteceu na fazenda dele.\n\nEmbora este diário tenha sido minha única companhia das últimas semanas, eu o entrego de bom grado se isso me livrar de ter que encarar a ira de Noel!", progress = "Descobriu quem era o Vulto Sombrio?", completion = "Este diário esclarece muitas coisas e me indica o caminho para enfrentar os cavalgantes negros! Já vi esses cavaleiros na Floresta do Crepúsculo e também já os vi falando com o feiticeiro Morbídio Vil, no Morro dos Esquecidos.", rewards = "Você receberá:\n\n"},
[269] = {title = "Em busca da sabedoria", objective = "Entregue o Diário Completo de Medrisco para o Bispo Palhares em Ventobravo.", description = "Vá à cidade de Ventobravo e procure o Bispo Palhares na Catedral da Luz. Entregue o diário de Medrisco a ele e diga que você quer enfrentar Morbídio Vil.\n\nEmbora Ventobravo tenha renegado a Vila Sombria, Palhares é sábio, uma alma generosa. Talvez ele colabore com a sua missão de nos salvar.\n\nSua tarefa é perigosa, <name>, mas é melhor se apressar. A cada hora que passa, as sombras da Floresta do Crepúsculo ficam mais escuras.", progress = "Descanse, irmão. Percebo que sua jornada foi longa e que você carrega um pesado fardo. Pode me dizer o que está causando esta sombra escura que paira à sua volta?", completion = "&lt;Depois de ler o diário e ouvir suas intenções...&gt;\n\nUma mácula maliciosa está ganhando cada vez mais tração na já atormentada Floresta do Crepúsculo. Graças a Luz, temos um herói como você para enfrentar o mal.\n\nMas primeiro você precisa se preparar para essa provação... pois o necromante Morbídio Vil é muito poderoso e criou encantamentos sombrios para se proteger dos ataques físicos. Para derrotá-lo, você vai precisar de uma arma que seja à prova dessas proteções.", rewards = "Você receberá:\n\n"},
[270] = {title = "A esquadra naufragada", objective = "Vá até o Porto de Menethil, no Pantanal, e fale com Glorin Testaço.", description = "Anos atrás, a Terceira Frota de Kul Tiraz afundou na costa do Pantanal, uma grande área charcos ao norte do reino enânico de Altaforja. Um navio levava uma carga de Ferro da Forja de Luz.\n\nO metal é muito precioso para nós, pois um item forjado com essa matéria-prima golpeia com verdade sagrada. Se você for mesmo enfrentar Morbídio Vil, precisará de tal arma.\n\nVá ao Porto de Menethil e fale com meu colega enânico Glorin Testaço. Se ainda for possível encontrar o metal perdido, ele saberá onde procurar.", progress = nil, completion = "O ferro de Forja de Luz é um metal nobre, mas parece que sua missão será vil. Você percorreu um longo caminho até aqui, irmão. Oxalá encontre o que procura!\n\nE se encontrar o ferro, eu sei de alguém que pode moldá-lo em uma arma contra esse necromante da Floresta do Crepúsculo que você mencionou.", rewards = "Você receberá:\n\n"},
[271] = {title = "A vingança de Vyrin", objective = "Mate o Velho Fumaça e mostre sua proeza a Dário, o Novato, no Pavilhão dos Andarilhos.", description = "Você estaria <interessado/interessada> em dar o troco em Sua Excelência o Caçador? Até que o Dário tem talento. Me dói dizer isso, mas é verdade.\n\nHá um ano, todo o Pavilhão dos Andarilhos saiu atrás de um urso enorme, mas nenhum caçador teve sorte. Então, o Dário resolveu tentar também. E tudo que trouxe de volta foi aquela cicatriz horrorosa por cima do olho.\n\nSe você matasse aquele urso, tenho certeza que até o Dário ficaria sem palavras.\n\nO Velho Fumaça, nome que demos ao urso, costuma vagar perto da toca dele, nas montanhas que acimam Thelsamar.", progress = "Ah, <name>! Já voltou? Estava caçando outra vez, presumo. Não se preocupe se você tiver alguns percalços... Com o tempo isso deixa você mais forte!\n\nBem, mais fraco é que não dá para você ficar...", completion = "Er... O que é isso? Pelo jeito é uma cabeça de urso.\n\n&lt;Inconscientemente, Dário coça a cicatriz.&gt;\n\nHum, que presa curiosa você tem aí. Não é possível que seja aquele urso de antigamente...\n\n&lt;Dário perde a voz e começa a tremer.&gt;\n\nAi, tire isso de perto de mim! Leve essa coisa horrível para longe daqui!", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[272] = {title = "Missão do leão marinho", objective = "Encontre o Meio-pingente da Agilidade Aquática e o Meio-pingente da Resistência Aquática. Fale com os residentes da Clareira da Lua para obter pistas de onde esse itens podem estar.\n\nForme o Pingente do Leão Marinho a partir das duas metades. Você precisa estar perto do Santuário de Remulos para fazer isso.\n\nLeve o pingente reunido ao Dendrite Stellardor na vila de Refúgio Noturno, que fica na Clareira da Lua.", description = "Para a segunda prova, você vai precisar das duas metades que formam o Pingente do Leão Marinho. Uma delas extrai poder do aspecto da agilidade do leão marinho, que desliza pela água. A outra extrai poder do aspecto da inacreditável resistência do leão marinho. Os druidas se valem igualmente das duas características para encarnar o aspecto de sua forma aquática.\n\nFale com os moradores da Clareira da Lua para saber onde as partes podem estar, e traga ambas para cá, onde podemos reuni-las. Quando o pingente estiver formado, leve para Dendrite Stellardor.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nMeio-pingente da Agilidade AquáticaMeio-pingente da Resistência Aquática"},
[273] = {title = "Suprimentos para o sítio", objective = "Fale com Huldar", description = "Não é tarde demais, fique à vontade para procurar o Huldar.\n\nEle não deve ter ido muito longe. Fortes como são, Miran e Saean não conseguem ir muito rápido com todo aquele peso.\n\nEles foram para o sítio pela rota de sempre, que começa no sul da cidade e segue pelas margens do Loch.\n\nNão vai ser difícil encontrá-los, a não ser que já tenham chegado lá!", progress = nil, completion = "Humpf, não é uma emboscada dos Ferro Negro que vai interromper as entregas!\n\nMas o Saean... Eu nunca poderia suspeitar que ele fosse um simpatizante deles. A gente já trabalhava junto há quase um ano... Não sei como não percebi.\n\nBem, depois eu penso nisso.", rewards = "Você receberá:\n\n"},
[274] = {title = "Uma ameaça sombria à espreita", objective = "Ashlan Risadura quer que você fale novamente com o Engenheiro-chefe Vedaçude e avise-o sobre a nova descoberta.", description = "Isso não é uma pólvora comum. Veja só os cristaizinhos prateados. Sinta o cheio característico! Está claro como o dia que isso aqui é pó de Cequatrum. O cequatrum é geralmente inofensivo, mas, quando úmido, pode explodir Altaforja inteira, com montanha e tudo.\n\nA reação química é desencadeada com a mistura de quatro componentes: peçonha de tocaieiro, cristal mo'grosh moído, lágrima de crocolisco e este coloide de desarme. Conte para Vedaçude antes que seja tarde demais!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[275] = {title = "Pústulas na terra", objective = "Mate 12 Rastejantes do Pântano, depois fale novamente com Rethiel, o Verdião no Pantanal.", description = "Não sei por que, mas um pus cresce dentro do Pantanal. Os rastejantes do pântano se ergueram. São pústulas doentes e febris na terra, e para acabar com a febre é preciso expulsá-los e permitir que se acomodem novamente nos pântanos.\n\nOs rastejantes do pântano andam trôpegos em meio aos cursos d'água do Pantanal.", progress = nil, completion = "Seus esforços aliviam esta terra, e por isso eu vou lhe dar um dom.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nVeste do Protetor do CharcoCorrente FlorestalFitolâmina\n\nVocê também receberá:\n\n"},
[276] = {title = "Patas pisoteantes", objective = "Mate 15 Gnolls Pelemusgo e 10 Mestiços Pelemusgo, depois fale novamente com Rethiel, o Verdião no Pantanal.", description = "Os gnolls Pelemusgo viveram pacificamente no Pantanal por muito anos, mas agora eles são muitos. A pisoteada deles esmaga as flores e os arbustos do pântano, e seus malditos machados curtam madeira demais para alimentar suas fogueiras.\n\nEles estão em desequilíbrio com a terra. Para que o Pantanal sobreviva, é preciso reduzir a quantidade de gnolls.\n\nMate quinze gnolls Pelemusgo e dez mestiços Pelemusgo. Cace-os no sul, perto do portão enânico de Algaz. Depois disso, fale novamente comigo.", progress = "A tarefa ainda está de pé, jovem <class>.", completion = "Ao reduzir o contingente de gnolls, você contribuiu para o equilíbrio do Pantanal. Embora a minha fé no seu povo tenha sido posta a prova no passado, &lt;race&gt;, ela ainda pode ser restaurada.", rewards = "Você receberá:\n\n"},
[277] = {title = "Tabu do fogo", objective = "Leve 9 Pederneiras Grotescas para Rethiel, o Verdião.", description = "Os Pelemusgos estão cortando as árvores do Pantanal, usando a madeira para de aquecer com o fogo. Não vou mais sofrer essa dor!\n\nTire dos Pelemusgos as pederneiras grosseiras, as ferramentas que eles usam para fazer fogo. Todo Pelemusgo tem uma, com exceção dos gnolls Pelemusgo menores e dos mestiços Pelemusgo.", progress = "Trouxe as ferramentas gnoll para o fogo?", completion = "...Obrigado. Vou destruir essas pederneiras e jogar no mar, para que nunca mais possam ser usadas para queimar madeira viva.", rewards = "Você receberá:\n\nMorel Esponjoso"},
[278] = {title = "Uma ameaça sombria à espreita", objective = "O Engenheiro-chefe Vedaçude quer que você colete Peçonha de Tocaieiro, um Cristal Mo'grosh e uma Lágrima de Crocolisco.", description = "Comece coletando os materiais de desarme imediatamente, <name>. A peçonha de tocaieiro pode ser encontrada nas aranhas nativas do Loch Modan. Lágrimas de crocolisco também são encontradas aqui no Loch. Já o cristal Mo'Grosh é muito difícil de encontrar. Os ogros do nordeste mineram esses cristais, que são uma matéria-prima muito rara.\n\nPrecisamos estar preparados para desmantelar um ataque dos Ferro Negros! Fale novamente comigo depois que coletar os itens que eu prepararei a mistura.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[279] = {title = "Garras das profundezas", objective = "Mate 12 Murlocs Guelrazul.\n\nMate o Comilão e pegue a cabeça dele.\n\nLeve a Cabeça de Comilão para Carlos Borba no Porto de Menethil.", description = "Os murlocs estão saindo das águas profundas e construindo aldeias na costa. Eles estão atormentando os pescadores e comerciantes. Temos que dar um fim nisso.\n\nUm deles, o Comilão, costuma se juntar a outros murlocs Guelrazul para saquear os comerciantes na estrada. Depois, eles sempre fogem para as cabanas por perto. Os nossos comerciantes estão em perigo, e estamos dispostos a pagar a quem puder garantir a segurança deles.\n\nO Comilão ronda a baía do rio a norte daqui. Encontre-o, mate todo o bando e traga-me a cabeça dele.", progress = "Nossos comerciantes não estarão seguros enquanto o Comilão e os outros murlocs estiverem vivos.", completion = "Ah, bom trabalho, &lt;name&gt;.\n\nAceite isto como pagamento por ter cumprido a sua parte do acordo com a Marinha Mercante.", rewards = "Você receberá:\n\n"},
[280] = {title = "Uma ameaça sombria à espreita", objective = "O Engenheiro-chefe Vedaçude quer que você nade até a base da represa, localize o barril de pólvora e espalhe a Mistura de Desarme para evitar uma explosão.", description = "Hoje, mais cedo, avistei alguns insurgentes Ferro Negro nadando em direção à barragem com um grande barril. Não havia montanhistas por perto para detê-los! Acho que eles plantaram o cequatrum na base da represa, que pode explodir a qualquer momento! Vou combinar todos esses ingredientes na mistura de desarme. Pronto.\n\nAgora preciso que você leve a mistura e espalhe pelo barril antes que seja tarde demais! Corra!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[281] = {title = "O que é nosso de direito", objective = "Vasculhe os caixotes e barris nos acampamentos dos murlocs na costa.", description = "Pouco tempo atrás, um navio mercante, o Princesa Cega, foi atacado por murlocs. O navio não sofreu avarias, mas a maior parte da carga, que continha até uma estatueta muito valiosa de Terenas Menethil, foi roubada pelas criaturas. Queremos a estatueta de volta.\n\nProcure caixotes, barris e outros indícios da carga roubada nas cabanas dos murlocs na costa. Encontre a estatueta de Menethil e traga-a de volta.", progress = nil, completion = "O caixote está com vários arranhões de garras e marcas de avarias causadas pela água. Você o arromba e se depara com uma palha encharcada e apodrecida, como se algo de valor tivesse sido guardado no caixote. Você vasculha a palha...\n\n... e encontra várias garrafas vermelhas.", rewards = "Você receberá:\n\nPoção de Cura"},
[282] = {title = "Observações de Senir", objective = "Fale com o Montanhista Thalos.", description = "Eu estava planejando mandar meu aprendiz levar o relatório para meu irmão Senir, mas eu ficaria muito mais tranquilo se o documento estivesse em mãos... mais confiáveis. Isso, é claro, se você não se importar.\n\nHum, você vai precisar pegar o túnel para chegar até Kharanos.\n\nFale com o Montanhista Thalos antes de passar pelo túnel. Está completamente infestado por troggs agora.\n\nSiga a estrada de volta para Sidermar, depois siga em direção leste para o túnel, Thalos está estacionado por perto.", progress = "Saudações! Siga com cuidado, <name>, o túnel para Dun Morogh está infestado por troggs e não é seguro para viajantes.\n\nSe você não tiver nenhum negócio urgente em Dun Morogh, peço que permaneça em Sidermar até que o túnel esteja mais seguro.", completion = "Hum, bem, se o Barbabranca está mandando você para Dun Morogh em uma missão importante, eu não posso te impedir, posso?\n\nBom, pelo menos me deixe lhe dar alguns conselhos e orientações.", rewards = "Você receberá:\n\n"},
[283] = {title = "Uma ameaça sombria à espreita", objective = "A Mistura de Desarme parece ter surtido efeito. Fale novamente com o Engenheiro-chefe Vedaçude para dar a boa notícia.", description = "O barril crepita levemente.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nCortador de Árvore EnânicoLâmina Espinhosa\n\nVocê também receberá:\n\n"},
[284] = {title = "A busca continua", objective = "Procure a Estatueta de Menethil.", description = "Você vasculha o conjunto inteiro de cabanas dos murlocs e não encontra nem sinal da Estatueta de Menethil.\n\nTalvez uma das duas cabanas ao norte e ao nordeste tenha alguma pista.", progress = nil, completion = "Esse barril parece intacto. Você o arromba...\n\n... e sai dele um cheiro forte de queijo. Está um pouco úmido, mas continua uma delícia!", rewards = "Você receberá:\n\nQueijo Enânico Suave"},
[285] = {title = "Vasculhe mais cabanas", objective = "Continue vasculhando as cabanas dos Murlocs em busca da Estatueta de Menethil.", description = "Mesmo depois de uma busca completa, a aldeia murloc não oferece pista alguma.\n\nVocê tem certeza de que a próxima pista que encontrar será proveitosa.\n\nA estatueta de Menethil deve estar em uma das cabanas ao norte!", progress = nil, completion = "O barril está parcialmente enterrado no meio do lodo e da grama...", rewards = "Você receberá:\n\n"},
[286] = {title = "Devolva a estatueta", objective = "Entregue a Estatueta de Menethil para Carlos Borba.", description = "Depois de tirar o barril do lamaceiro e abri-lo, você encontra um artigo de prata no meio da palha. É uma imagem de um jovem paladino de cabelos longos com um martelo erguido.\n\nDeve ser a estatueta de Menethil! Leve-a a Carlos Borba no Porto de Menethil.", progress = "Trouxe a estatueta?", completion = "O capitão do Princesa Cega ficará aliviado quando souber que a estatueta foi recuperada. Você fez por merecer o pagamento.\n\nÉ muito bom ter pessoas de confiança como você por perto, especialmente em momentos tão difíceis.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBastão do Pingente de GeloBotas de Marinheiro\n\nVocê também receberá:\n\n"},
[287] = {title = "A Fortaleza Jubafria", objective = "Percorra a Fortaleza Jubafria e mate 5 Caçadores de Cabeças Jubafria a pedido de Senir Barbabranca em Kharanos.", description = "Estou atrasado com meu relatório, <name>. Bem que você poderia me ajudar. Consegui descobrir onde os trolls estão entocados, mas eram tantos que fiquei com medo de entrar.\n\nEu preciso que você faça o seguinte: desça na caverna, dê uma fuçada no ambiente, mate alguns trolls e volte para cá.\n\nPegue a estrada que sai de Kharanos em direção ao norte. Ao chegar à ponte, siga a oeste ao longo do rio congelado até chegar ao Lago Nevado. A Aldeia da Cevada fica na margem oeste do lago, e a fortaleza fica a sudoeste da aldeia.", progress = nil, completion = "Ah, &lt;name&gt;! Não imaginei que você teria dificuldade para encontrar o lugar... Sou ótimo para indicar o melhor caminho!\n\nAgora só falta terminar o relatório.\n\n\n\nPronto! He he he, isso foi muito engraçado... Eu me pergunto, &lt;name&gt;, se você se importaria de fazer um último favor para mim?", rewards = "Você vai poder escolher uma dessas recompensas:\n\nVeste Grossa de InvernoBroquel de Pedra\n\nVocê também receberá:\n\n"},
[288] = {title = "A terceira frota", objective = "Compre uma Jarra de Hidromel para o Primeiro Oficial Simonez.", description = "Eu era Primeiro Oficial do Senhora das Ondas, um navio de linha da Terceira Frota de Kul Tiraz, a Esquadra Naufragada. Naufragada porque, na última viagem, foi atacada pelos Dragões Vermelhos... Dragões controlados por orcs!\n\nNão tivemos a mínima chance contra o fogo de dragão... A nossa frota inteira foi queimada e afundou perto da costa. Sou o único sobrevivente desses pobres navios...\n\nAi, minha cabeça não para de latejar! Preciso de uma bebida gelada para controlar a ressaca e continuar a contar a minha história.", progress = "Nossa, que sede! Compra uma bebida para o pobre coitado aqui.", completion = "Ah! Desceu redondo e deu uma boa aliviada. Valeu, camarada!", rewards = "Você receberá:\n\n"},
[289] = {title = "A tripulação amaldiçoada", objective = "Mate 13 Marinheiros Amaldiçoados, 5 Fuzileiros Amaldiçoados e o Primeiro Oficial Expedito. Leve a Caixa de Rapé do Expedito para o Primeiro Oficial Simonez no Porto de Menethil.", description = "<Animado, Simonez continua o conto...>\n\nSou o único sobrevivente da Terceira Frota de Kul Tiraz, que teve um fim trágico e naufragou perto da costa ao norte. Mas, apesar de ser o único que continua vivo, eu não sou o único andando à solta por aí! Em um dos navios, havia um grande e terrível tesouro escondido. O poder misterioso dele mantém as almas dos meus irmãos aprisionadas dentro dos cascos apodrecidos dos navios.\n\nSe você libertar alguns dos meus irmãos, talvez isso desfaça a maldição.", progress = "Às vezes eu ouço o lamento dos meus irmãos nos meus sonhos! Por favor, meu senhor, liberte-os dessas amarras para fazer cessar seus gritos.", completion = "Meus agradecimentos, &lt;name&gt;. E você tem a gratidão dos meus irmãos amaldiçoados também.", rewards = "Você receberá:\n\n"},
[290] = {title = "O fim da maldição", objective = "Obtenha a Chave do Cofre do Intrépido do Capitão Halyndor.\n\nUse a chave para abrir o Cofre Trancado do Intrépido.", description = "Para quebrar a maldição e libertar meus irmãos, você terá que buscar e purificar um... negócio... no meu navio! Não sei de que se trata, mas sei que fica guardado no cofre do navio, sob os cuidados do Capitão Halyndor.\n\nAinda vejo o velho capitão nos meus sonhos. Sei que ele vaga por aquele navio amaldiçoado, o Intrépido. Pegue a chave dele e use-a para abrir o cofre do Intrépido.\n\nDepois que tiver aquele negócio maldito em mãos, leve-o para o sacerdote Glorin Testaço lá na cidade.", progress = "O baú está muito bem trancado.", completion = "A chave está enferrujada, mas entra sem resistência na fechadura.", rewards = "Você receberá:\n\n"},
[291] = {title = "Os relatórios", objective = "Entregue o relatório de Senir ao Senador Barin Itarrubra em Altaforja.", description = "Excelente! Entregue meu relatório ao Senador Barin Itarrubra. Ele é um tipinho enfezado, então não deixe aquele humor de cão influenciar você. O Senador dá audiências em Altaforja, na sala de beija-mão do rei Magni.\n\nVocê não sabe como chegar em Altaforja? Pegue a estrada que sai de Kharanos em direção ao norte, cruze a ponte e, então, basta seguir a estrada a leste, montanha acima. Não tem erro, há várias bandeiras ao longo do caminho.\n\nA propósito, você se importa de não mencionar que me ajudou? Não quero que eles pensem que não estou dando duro aqui, sabe?", progress = "Hum... Aparentemente meus guardas estão ficando cada vez menos seletivos com quem entra nas audiências. Bem, o que você quer? Seja breve.", completion = "&lt;O Senador passa os olhos no relatório.&gt;\n\nAutoridade do REI?!? O Magni perdeu completamente o juízo! E aquele maldito Barbabranca... Resolvendo o assunto por conta própria, hã? O Senado não vai gostar nem um pouco disso.\n\nO quê? Você ainda está aqui? Vá embora antes que eu chame os guardas!", rewards = "Você receberá:\n\n"},
[292] = {title = "Olho de Paleth", objective = "Leve o Olho Maldito de Paleth para Glorin Testaço.", description = "Ao abrir o baú, revela-se um orbe enegrecido que emite uma luz sinistra. É o tesouro de que Simonez falou: o Olho Maldito de Paleth.\n\nLeve o Olho Maldito de Paleth para o sacerdote anão Glorin Testaço, no Porto de Menethil. Peça a ele para remover a maldição do Olho.", progress = "Aquele bêbado do Simonez mandou você vir aqui? Pois bem, diga a ele que não tenho cerveja sobrando. Ele vai ter que ir mendigar com outro!", completion = "Um Olho de Paleth? Que incrível! Hum... tem algo errado com este aqui. Você o encontrou nos navios naufragados? Isso explica muita coisa! Mas... eu não sabia que tinham um olho desses na Terceira Frota...\n\nNão espanta que o Simonez estivesse tão atarantado. Com um Olho Maldito de Paleth, não dá para ser feliz.", rewards = "Você receberá:\n\n"},
[293] = {title = "A purificação do olho", objective = "Leve o Olho Maldito de Paleth para o Arcebispo Benedictus. Benedictus se encontra na Catedral da Luz, na cidade de Ventobravo.", description = "Eu não consigo remover a maldição do Olho de Paleth. Os Olhos foram feitos por Sacerdotes Humanos, você terá que procurar ajuda em Ventobravo. Fale com o Arcebispo Benedictus. Ele é o chefe da Catedral da Luz, lá em Ventobravo. Vai poder ajudar.\n\nTomara que possa, pelo seu próprio bem.", progress = "Que orbe é este? Parece que está... sujo.", completion = "Um Olho de Paleth. Mas este já viu muita tristeza... deve ter sido testemunha de uma tragédia muito, muito terrível.\n\nDeixe-me purificá-lo, os Olhos foram criados para brilhar. O poder deles pode ser um perigo quando desvirtuado.", rewards = "Você receberá:\n\nOlho de Paleth"},
[294] = {title = "A revanche de Ormer", objective = "Ormer Trançaferro, no Sítio de Escavação Whelgar, quer que você mate 10 Guinchadores Mosqueados e 10 Raptores Mosqueados.", description = "A situação é grave, isso é certo. Quando descobrimos os ossos, eles atraíram os raptores para cá. Esses bichos imundos mataram meus irmãos e prenderam a mim, Marina e o pobre coitado do Prospector aqui.\n\nAjude a livrar o Pantanal dos Raptores, <name>. Os Raptores Mosqueados e os Guinchadores Mosqueados estão a oeste do penhasco. Mate 10 de cada, se possível.\n\nVai ser um bom começo para a vingança que tenho em mente.", progress = "Mate 10 Raptores Mosqueados e 10 Guinchadores Mosqueados, <name>. Meus colegas não mereciam a sina que sofreram. É hora de quitar as contas.", completion = "Matar é um negócio sujo, mas você fez um bom trabalho, &lt;name&gt;. Se eu não tivesse a obrigação de ficar com o Prospector e com Marina, teria ido derramar esse sangue com você.", rewards = "Você receberá:\n\n"},
[295] = {title = "A revanche de Ormer", objective = "Ormer Trançaferro quer que você mate 10 Garrafoices Mosqueados e 10 Rasgaqueixos Mosqueados, depois fale novamente com ele no Sítio de Escavação Whelgar.", description = "É hora de fazer aqueles Raptores se arrependerem de ter derramado nosso sangue. Lá embaixo tem um monte de Garrafoices Mosqueados e Rasgaqueixos Mosqueados. Faça aquelas criaturas lazarentas pagarem caro, mate 10 de cada!", progress = nil, completion = "Tu matou os Raptores feito um bichano mata uma lagartixa, &lt;name&gt;!", rewards = "Você receberá:\n\n"},
[296] = {title = "A revanche de Ormer", objective = "Ormer Trançaferro, no Sítio de Escavação Whelgar, quer que você mate o Sarilodonte e leve uma de suas garras até ele depois que tiver cumprido a tarefa.", description = "Enquanto você estava lá, eu reparei que uma das feras se destaca do resto. Ela é maior e mais ameaçadora. Aposto que foi ela que levou as outras a perturbar o sítio de escavação.\n\nSó te peço um último favor, <name>. Leve justiça ao Sarilodonte. E considerando a gravidade de seus crimes, justiça nesse caso significa morte! Traga-me uma das garras como prova de que ele morreu.", progress = "O Sarilodonte está morto? Tu redimiu a memória dos que tombaram?", completion = "Obrigado, &lt;name&gt;. Teu empenho jamais será esquecido. Muitos anões inocentes morreram aqui, enquanto faziam seu trabalho. A vingança que você realizou contra aquelas criaturas malignas vai fazer com que elas pensem duas vezes antes de atacarem os súditos do rei Magni de novo.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nVerdugo dos RaptorsArmadura Arruina-raptorBastão de Escavação\n\nVocê também receberá:\n\nReceita: Omelete Curiosamente Saboroso"},
[297] = {title = "Juntando ídolos", objective = "Leve 8 Ídolos Esculpidos em Pedra para Magmar Machadeiro.", description = "Há pouco tempo, antes dos troggs aparecerem no sítio, nós desenterramos estranhos ídolos esculpidos. Mas não tivemos nem chance de estudá-los, porque logo depois da descoberta os troggs nos expulsaram das ruínas! E esses artefatos provocam estranhos efeitos nos troggs. Eles ficam enlouquecidos!\n\nTraga 8 ídolos para mim. Quero estudá-los e mantê-los longe dos troggs! Os ídolos estão com os troggs que infestam o sítio.", progress = "Pegou os ídolos? Temos que estudá-los e apresentar os resultados em Altaforja!", completion = "Você conseguiu! Muito bem, &lt;name&gt;!\n\nE sua cara nem está tão ruim, mesmo com todos aqueles troggs enfurecidos. Você me parece promissor. Vou ficar de olho em você.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nVara-lume EnânicaFatiador TroggMachado de Thelsamar\n\nVocê também receberá:\n\n"},
[298] = {title = "Relatório de progresso da escavação", objective = "Entregue o Relatório de Progresso de Bandaferro para Jern Elmocorno em Thelsamar.", description = "Eu preciso enviar um relatório de andamento para Thelsamar – isso e uma reiteração do meu pedido de pólvora! Aqui está o relatório. Entregue-o para Jern Elmocorno. Ele é o nosso contato em Thelsamar.\n\nNão é lá uma tarefa muito desafiadora, mas tem que ser cumprida.", progress = "Ah, um relatório de progresso do sítio arqueológico? Que bom!", completion = "Bah! O Bandaferro está tendo problemas com os troggs. Será que toda essa pólvora é para fazer a escavação ou para afastar os troggs?\n\nBem, de qualquer maneira será pólvora muito bem gasta!\n\nO estranho é que eu enviei muitos barris de pólvora há semanas. Onde será que esse carregamento foi parar...", rewards = "Você receberá:\n\n"},
[299] = {title = "Desvendando o passado", objective = "O Prospector Whelgar quer que você revire o sítio de escavação em busca dos 4 fragmentos perdidos da tabuleta: Ados, Modr, Golm e Neru.", description = "Pouco antes da invasão, eu descobri uma enorme tabuleta chamada Pedra de Goaz. O texto traduzido se divide em 4 partes. Ele fala de um \"plano divino\" e uma \"profecia de danação\".\n\nReceio que o nosso tempo esteja acabando. Estou velho demais pra descer e enfrentar aquelas bestas. Mas você é forte.\n\nRevire a escavação e descubra os 4 fragmentos perdidos da tabuleta: Ados, Modr, Golm e Neru. Procure-os em artefatos antigos ou onde a terra estiver solta. Traga-os até aqui, para eu começar a desvendar esse mistério!", progress = "Tu encontrou os quatro fragmentos, <name>? Ados, Modr, Golm e Neru...", completion = "Ah, a Pedra de Goaz está completa! Ótimo trabalho, &lt;name&gt;. Ótimo mesmo. Agora eu posso continuar com a tradução delicada dessa escrita titânica. Já consigo entender mais palavras! Hum... Aqui diz \"Deuses antigos... Acorrentados debaixo da terra\". Esse é o começo de algo épico.", rewards = "Você receberá:\n\nDragonas Sedosas de Gamn"},
[301] = {title = "Apresente-se a Altaforja", objective = "Entregue o Relatório de Progresso de Bandaferro para o Prospector Lançatroz.", description = "Precisamos de aprovação para enviar mais pólvora para o Bandaferro. Você vai ter que enviar o relatório dele para o quartel-general da Liga dos Exploradores, em Altaforja.\n\nAqui está o relatório. Entregue-o para o Prospector Lançatroz, e não esqueça de se abaixar após entregar o documento. O Lançatroz é muito esquentado e não vai ficar feliz ao saber do lento progresso da escavação.\n\nO Lançatroz está na Assembleia dos Exploradores.", progress = "Nós, os Lançatroz, não gostamos de ser incomodados com questões triviais, <class>. Espero que as notícias que você traz valham a minha valiosa atenção.", completion = "Maldito Bandaferro! Eu enviei um carregamento de pólvora para ele há semanas, mas ele nem mencionou isso no relatório.\n\nEntão... Onde diabos foi parar aquela pólvora???", rewards = "Você receberá:\n\n"},
[302] = {title = "Pólvora para Bandaferro", objective = "Fale com Jern Elmocorno em Thelsamar.", description = "Se é pólvora o que o Bandaferro quer, por Magni, é isso o que ele vai ter! Já enviei um mensageiro entregar a aprovação do pedido para Jern Elmocorno, em Thelsamar. Ele vai preparar a pólvora e os outros suprimentos para enviarmos para o Bandaferro.\n\nMais uma coisa: quero que você acompanhe o carregamento de pólvora até o destino. O último não chegou, então mantenha os olhos abertos.", progress = nil, completion = "O quê? O Lançatroz quer que você acompanhe o carregamento?\n\nEu... Bem, por mim tudo bem. O problema é que eu já despachei o Huldar e os carregadores, Miran e Saean. Você não encontrou com eles por pouco, eles acabaram de partir.", rewards = "Você receberá:\n\n"},
[303] = {title = "A guerra dos Ferro Negro", objective = "Rocio Garmason, de Dun Modr, quer que você mate 15 Anões Ferro Negro, 5 Escavadores Ferro Negro, 5 Sabotadores Ferro Negro e 5 Demolidores Ferro Negro.", description = "Há guerra em nosso lar e lá fora. Enquanto os soldados anões dão suas vidas em territórios distantes em nome da Aliança, nossas próprias terras sofrem com as crueldades dos anões Ferro Negro. Eles causaram devastação à Ponte Thandol. Dun Modr caiu logo em seguida.\n\nPrecisamos da sua força para fazer a linha de frente deles recuar. Se tu matar 15 Anões Ferro Negro, 5 Escavadores, 5 Sabotadores e 5 Demolidores, a força reserva deve chegar a tempo para concluir o ataque.\n\nVá, depressa!", progress = nil, completion = "Tu cumpriu bem as suas ordens, &lt;class&gt;. Provou sua valentia e lealdade ao rei Magni, e eu te cumprimento por isso! Com a pressão que tu botou contra o assentamento Ferro Negro, agora temos uma chance de rechaçá-los de nossas terras de uma vez por todas... se pelo menos as forças-reserva aparecessem...", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[304] = {title = "Uma tarefa cruel", objective = "Mate Balgaras, o Asqueroso e leve a orelha dele para Trançalonga, o Austero, que está do lado de fora de Dun Modr.", description = "Muitos dos meus soldados morreram na batalha de Dun Modr. Meu próprio irmão tombou quando a Ponte Thandol foi destruída. Sim, os anões Ferro Negro são malignos até o osso. De acordo com o que Roggo conseguiu descobrir, o líder daquele bando de brutamontes do Pantanal é um bruxo chamado Balgaras, o Asqueroso. Contudo, ele não passa de um borra-botas. Ele manda os soldados fazer o trabalho sujo, mas fica escondido, evitando o perigo.\n\nSe tu conseguir encontrar aquele verme, mate-o e me traga a orelha dele. Eu vou te recompensar bem, <class>.", progress = "Já conseguiu localizar Balgaras, o Asqueroso, <class>? Ele anda fugindo da gente já faz algum tempo.", completion = "Balgaras, o Asqueroso, finalmente, está morto. Apesar de a morte dele não trazer de volta os anões inocentes que morreram inutilmente por suas mãos, ela vai ajudar a restaurar a paz de espírito daqueles que sobreviveram. Com Balgaras morto, os vermes Ferro Negro restantes serão mais fáceis de lidar. Agora, temos que eliminá-los antes que um novo líder seja mandado pra cá.\n\nVocê trabalhou muito bem, &lt;class&gt;.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nEscudo do Leão DouradoAnel Plácido\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[305] = {title = "Em busca da equipe de escavação", objective = "Tarrel Tecepedra quer que você viaje até o Sítio de Escavação e contate Marina Tecepedra.", description = "Minha nossa, você deve ser <o batedor/a batedora> que Trançalonga disse que mandaria! Você nem imagina como estou preocupado.\n\nMinha esposa, Marina, me mandou cartas toda semana no mês passado. Ela faz parte da equipe de escavação de Whelgar. Mas já se passaram quase duas semanas desde a última correspondência dela. Com certeza, tem alguma coisa errada!\n\nTrançalonga disse que mandaria um batedor pra checar as coisas se não tivéssemos notícias da equipe logo. Eu suponho que seja você, <name>. procure a Marina no local de escavação ao leste!", progress = nil, completion = "Ah, que bom que você chegou!", rewards = "Você receberá:\n\n"},
[306] = {title = "Em busca da equipe de escavação", objective = "Leve o bilhete de Marina para Tarrel Tecepedra.", description = "Foi horrível! Estávamos desenterrando alguns artefatos raros quando encontramos uns ossos grandes.\n\nAntes que nos déssemos conta do que estava acontecendo, aqueles raptores mosqueados tomaram o sítio de escavação. Muitas vidas foram perdidas.\n\nO Ormer aqui — tão valente — salvou a mim e ao Prospector. Ele nos defendeu enquanto corríamos em busca de abrigo. Agora estamos presos, e o nosso trabalho foi arruinado.\n\nAqui, leve este bilhete para o meu querido Tarrel. Vai explicar a situação toda para ele.", progress = "Conseguiu encontrar a Marina? Ela mandou algum recado?", completion = "Que notícia horrível! Pelo menos 3 deles sobreviveram. Mas quando essa matança vai acabar? Eu rogo para que, um dia, os súditos do rei Magni possam viver sem medo.", rewards = "Você receberá:\n\n"},
[307] = {title = "Patas nojentas", objective = "Vá até a Mina da Prata e recolha 4 cargas de Equipamento de Mineração.\n\nFale novamente com o Montanhista Lançatroz.", description = "A Mina da Prata, a leste daqui, está esgotada há muito tempo. A Liga dos Mineradores a transformou em armazém, mas agora os kobolds estão ocupando as galerias e botando aquelas patas nojentas em ferramentas enânicas de qualidade!\n\nNós vamos expulsar esses vermezinhos em breve, mas a Liga quer retirar os equipamentos antes que nós, guerreiros, invadamos o lugar e quebremos alguma coisa. Vai ser uma operação difícil, então sugiro que você leve alguns companheiros para ajudá-<lo/la>.\n\nO equipamento está armazenado em caixotes da Liga dos Mineradores ao longo de toda a mina. Boa sorte.", progress = "Conseguiu pegar o equipamento de mineração, <name>?", completion = "Tinha um monte de kobolds lá dentro? Não vejo a hora de desinfetar aquele lugar. Fico enjoado só de pensar naquelas mãozinhas encardidas mexendo na nossa mina!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCorrente de Coração FérreoVeste do GuardiãoBroquel de Placas Férreas\n\nVocê também receberá:\n\n"},
[308] = {title = "Distraindo Jarven", objective = "Humpf! Toda essa bebida dando sopa e eu com ordens expressas de não tocar em nada... Ai se eu pudesse tirar uma provinha dessa Cerveja do Trovão... Ia me deixar mais esperto, sem brincadeira!", description = nil, progress = "Humpf! Toda essa bebida dando sopa e eu com ordens expressas de não tocar em nada... Ai se eu pudesse tirar uma provinha dessa Cerveja do Trovão... Ia me deixar mais esperto, sem brincadeira!", completion = "Para mim? Você é um verdadeiro herói, &lt;name&gt;!", rewards = nil},
[309] = {title = "Em defesa do carregamento", objective = "Garanta que Miran e o carregamento cheguem ao sítio de escavação, depois informe ao Prospector Bandaferro.", description = "Tudo certo, <name>?\n\nPrimeiro precisamos entregar a pólvora para o Bandaferro. É muita coisa para carregar, esta região costuma ser perigosa, e sabe-se lá o que os Ferro Negro podem estar tramando contra mim.\n\nEu me sentiria muito melhor se você me acompanhasse.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nCinto do EncarregadoPisoteadores da Lama\n\nVocê também receberá:\n\n"},
[310] = {title = "A guerra das cervejas", objective = "Troque um barril de Cerveja do Trovão por um barril de Grogue Cervevada no porão da Destilaria Cervaforte, que fica em Kharanos.", description = "Eu tenho que admitir: esses Cervaforte fazem umas cervejas até bem razoáveis. Mas eles têm que saber que a deles não é a única cerveja boa do mundo! Quem sabe você não possa me ajudar a ensinar uma lição àqueles convencidos...\n\nPegue este barril de Grogue do Cervevada. Entre de fininho no porão da Destilaria Cervaforte, em Kharanos, e troque um dos barris de Cerveja do Trovão por este aqui. Aí vamos ver o que os fregueses deles vão achar da minha cerveja!\n\nE, caso o porão seja vigiado, você talvez tenha que distrair o guarda...", progress = "Pela data neste barril, essa cerveja vai ficar pronta em breve.", completion = "Corra para deixar o meu barril de Grogue do Cervevada entre os barris do Cervaforte!", rewards = "Você receberá:\n\n"},
[311] = {title = "Fale novamente com Marleth", objective = "Entregue o barril de Cerveja do Trovão para Marleth Cervevada na Aldeia da Cevada.", description = "Depois de deixar um barril de Grogue do Marleth na adega da Destilaria de Kharanos, pegue um barril de Cerveja do Trovão como prova da façanha...", progress = "Fez a troca?", completion = "Nada mal, &lt;name&gt;! Foi difícil passar pelos guardas?\n\nNão importa. Muito obrigado, e sempre que você tiver com vontade de tomar uma cerveja que desce macio e reanima, lembre-se dos Cervevada!", rewards = "Você receberá:\n\n"},
[312] = {title = "Por baixo da carne-seca", objective = "Pegue de volta a carne-seca do Tundra MacGrann do armário de carnes roubado, na caverna do Velho Barbafria.", description = "Eu tinha um estoque de carne-seca suficiente para um mês separado para o inverno. Tudo muito bem trancado para manter os ursos longe das minhas provisões! Mas enquanto eu estava fora caçando cervos, aquele maldito urso, o Velho Barbafria, carregou o armário de carnes inteirinho! Pelo menos ele não vai conseguir arrebentar aquelas portas de tório. Mas eu vou morrer de fome se não pegar meu estoque de volta.\n\nVocê parece ser um pouco mais <discreto/discreta> do que eu. Será que poderia pegar meu estoque de volta? Eu nunca conseguiria entrar e sair correndo daquela caverna com essa minha perna imprestável.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nBraçadeiras de Ferro MarteladoMitenes de Lã\n\nVocê também receberá:\n\n"},
[313] = {title = "O Covil Cinzento", objective = "Colete 8 Crinas de Wendigo e leve-as para Piloto Marchapedra.", description = "Não é qualquer bobalhão que gineteia uma máquina de cerco. É pulso de ferro de um lado e nervos de aço do outro... Por sorte, me sobram as duas coisas! E tu? Te metes a tentar? Quer provar que tu sabe?\n\nOra bolas... Tô querendo aprumar mina máquina, a Lavratroll, deixar mais confortável. Que tal tu me trazer uma pilha de crinas de wendigo do Covil Cinzento, lá pro oeste de Kharanos? Essas crinas dariam um baita pelego pro interior do Lavratroll!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[314] = {title = "Amarre sua cabra pois Ragash está solto", objective = "Rudra Ambarmanso quer que você mate Ragash e leve sua presa para ela no rancho dos carneiros.", description = "Ouvimos o salseiro na calada da noite. Daí, pela manhã, certo era previsto, duas reses estavam faltando dos carneiros. Uma fera tranqueira, tratada por Ragash, vem caçando em nossas coxilhas. Como quase não se vê tropas de Altaforja por aqui, não se arranja um dente seco pra correr o Ragash.\n\nTerias coragem pra ir atrás da fera e desnucar a maldita? Traz-me uma das presas de Ragash que tu serás <recompensado/recompensada>. Ele vive se espreitando logo acima do rancho. Mas não te atires de sopetão, que a fera é letal.", progress = "O Ragash continua a aterrorizar o rebanho! Por favor, nos ajude! Mate essa fera desprezível!", completion = "Muito bem! O Veron vai ficar muito satisfeito ao saber das boas notícias. Matar o Ragash não é tarefa fácil. Sei que, um dia, você vai lutar ao lado do rei Magni no front da Aliança.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCalças do RancheiroTúnica de Couro MacioMartelo de Cristálgida\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[315] = {title = "Em busca da cerveja perfeita", objective = "Entregue 6 ramos de Tremulerva para Rejold Cervevada na Aldeia da Cevada.", description = "Já me decidi. Decidi que vou fazer a stout perfeita. Não se duvida isso, afinal, fazer cerveja está no sangue! Só me falta uma receita bagual...\n\nOs trolls Jubafria criam uma planta chamada tremulerva no alto da serra ao leste, para usar naqueles rituais tribais estranhos dele. Pra anão, ela não tem muita serventia, mas o sabor... Ah, o sabor é único. Quero ver se essa erva combina com cerveja!\n\nQuero que tu me traga um pouco de tremulerva dos videntes Jubafria ou tu me passa a mão nas cestas de tremulerva dos trolls.", progress = "Conseguiu pegar a tremulerva? Estou com tudo encaminhado para fazer uma nova batelada de stout e quero tentar usar a erva na mistura.", completion = "Ótimo! Mal posso esperar para testar a tremulerva em algumas receitas. Tem uma em particular que acho que vai ficar muito boa com a planta...", rewards = "Você vai poder escolher uma dessas recompensas:\n\nManto de Pele de BodeBraçadeiras de Hera Trançada\n\nVocê também receberá:\n\n"},
[317] = {title = "Provisões para a Vaporeta", objective = "Junte 4 Nacos de Carne de Javali e 2 Pelames Grossos de Urso e entregue tudo para o Piloto Urrabolha na Garagem do Gradaço.", description = "Estou me preparando para sair em missão para a Brigada de Cerco. É uma missão longa, por isso tenho que abastecer a Vaporeta com suprimentos para um mês. Então, enquanto o Gradaço não libera o meu tanque, você poderia sair à caça por mim?\n\nVou precisar de peles para montar uma cama e de carne para comer. A pele você tira dos ursos, e a carne, dos javalis. Esses bichos costumam zanzar pelos campos nevados a sul do Covil Cinzento.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[318] = {title = "Sempre-aceso", objective = "Pegue um barril de Sempre-aceso com o Rejold Cervevada na Aldeia da Cevada.", description = "O Rejold Cervevada costuma fazer experiências com as cervejas que faz. Umas ficam boas, algumas ficam ruins e outras... Bem, outras derrubam até anão velho.\n\nO Rejold mora na Aldeia da Cevada, a oeste de Kharanos. Estou atrás de uma das bebidas que ele faz, a Sempre-aceso. Isso vai me ajudar a esquentar os ossos enquanto eu estiver naquela estrada fria.\n\nE mais... Essa bendita bagaceira queima tanto que dá até para jogar na fornalha da Vaporeta para dar um gás na potência da bichinha! Esse truque já me tirou de poucas e boas...", progress = nil, completion = "O Urrabolha tá querendo um pouquinho de Sempre-aceso? Hum... Tá, tudo bem.", rewards = "Você receberá:\n\n"},
[319] = {title = "Tudo pela Sempre-aceso", objective = "Mate 6 Ursos Garra de Gelo, 8 Rochetuscos Adultos e 8 Leopardos da Neve. Em seguida, fale novamente com Rejold Cervevada na Aldeia da Cevada.", description = "Eu tenho um barrilete de Sempre-aceso prontinho, mas... Pode me fazer um favor? A Aldeia da Cevada está precisando de uma mãozinha com os bichos selvagens que vagam pela neve. Às vezes, eles se aproximam demais.\n\nNão que a gente esteja com medo desses ursinhos e gatinhos e javalizinhos – esses bichos não metem medo em nenhum anão de respeito – mas, se você desse cabo deles, a gente ia ter muito mais tempo para cultivar a arte de fazer cerveja.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[320] = {title = "Fale novamente com Urrabolha", objective = "Entregue o barrilete de Sempre-aceso para o Piloto Urrabolha na Garagem do Gradaço.", description = "Aqui está seu barrilete de Sempre-aceso, <name>. E, mais uma vez, obrigado pela caçada de mais cedo.", progress = "Conseguiu pegar a Sempre-aceso?", completion = "Sim, isso deve dar. Acho que vou abrir uma garrafa agora para tirar uma provinha...", rewards = "Você vai poder escolher uma dessas recompensas:\n\nMachado AfiadoCajado Curto NodosoFaca Campeira\n\nVocê também receberá:\n\n"},
[321] = {title = "Ferro da Forja de Luz", objective = "Vasculhe os destroços do Águia-pesqueira no Pantanal.", description = "Esses naufrágios na costa são da malograda Terceira Frota de Kul'Tiraz, que os dragões vermelhos deram uma churrasqueada e afundaram na Grande Guerra.\n\nA frota vinha de Lordaeron, mas veio na companhia de um destróier élfico, o Águia-pesqueira. Ele vinha apinhado de ferro da Forja de Luz e se aprochegou da Terceira Frota para se proteger. A barbaridade foi que os dragões apareceram...\n\nChega de causo. O Águia-pesqueira está naufragado ao sul do Porto de Menethil. Caso encontres ferro da Forja de Luz lá, tu me traz.", progress = nil, completion = "This chest is waterlogged, and its siding broken.  Many claw marks mar both the inside and outside of the chest...as if Murlocs have searched through it and stolen its treasure.\n\nBut tucked deep within the chest is a single Lightforge Ingot.  Unfortunately, many more will be needed if enough metal to fashion a weapon is to be gained.", rewards = "Você receberá:\n\nLingote da Forja de Luz"},
[322] = {title = "Braço abençoado", objective = "Leve o Caixote de Lingotes de Forja de Luz para Grimand Elmore em Ventobravo.", description = "No Distrito dos Anões de Ventobravo, tu encontras um anão ferreiro que dá conta de trabalhar o ferro da Forja de Luz e atende pelo nome de Grimand Elmore.\n\nEmbrulhei o ferro que trouxeste e pus num caixote. Leva ele para Grimand e explana teu causo. Te jogo que ele terá prazer em te ajudar em tal missão, e vai te fabricar uma arma que deixa até Morbídio Vil de orelha em pé!", progress = "Como meus serviços podem te servir, <class>?", completion = "Mas bá, te puxaste no causo. E deste a prova com ferro da Forja de Luz! Não me meto com tal metal desde o tempo do êpa. É uma honra meter-lhe na fornalha outra vez. E tu me volta aqui depois de lutar contra o Necromante pra me contar o ocorrido.\n\nMas não nos adiantemos. Primeiro, uma arma para usares contra Morbídio Vil...", rewards = "Você receberá:\n\n"},
[323] = {title = "Prova de valor", objective = "Mate 15 Saqueadores Descarnados, 3 Curandeiros Descarnados e 3 Guardadores Descarnados, e depois fale novamente com Noel.", description = "Morbídio Vil é um necromante, aliado dos Cavalgantes Negros. Mandaria você para confrontar o poder de Morbídio Vil, aquele sórdido infame, mas não sem antes testar sua força.\n\nVocê já provou sua bravura, mas se deseja mesmo encarar esse necromante, deve mostrar o que sabe contra os lacaios dele.", progress = nil, completion = "Sua perícia em combate, &lt;name&gt;, é indubitável. Sua perseverança ainda será a ruína de Morbídio Vil!", rewards = "Você receberá:\n\n"},
[324] = {title = "Os lingotes perdidos", objective = "Colete 5 Lingotes da Forja de Luz e fale novamente com Glorin Testaço no Pantanal.", description = "Após encontrar o lingote da Forja de Luz do baú encharcado, ficou evidente que os murlocs roubaram o resto. Cace os saqueadores murlocs perto dos destroços do Águia-pesqueira para recuperar o ferro da Forja de Luz.", progress = "Tens o ferro da Forja de Luz que precisava?", completion = "Mazá, encontraste! Vamos empeçar logo a martelar esse ferro da Forja de Luz no jeito, hã?", rewards = "Você receberá:\n\n"},
[325] = {title = "Com bala na agulha", objective = "Leve a Desgraça de Morbídio até Noel, na Floresta do Crepúsculo.", description = "Aqui está a arma contra o demônio de que você falou. Você está <pronto/pronta>, <name>. Boa sorte, e mal posso esperar para ouvir as histórias da sua vitória!", progress = "Olá, <name>! Não faz muito tempo que você partiu, mas percebi uma chama nos seus olhos que eu não via antes.", completion = "Ah, esse machado com certeza é à prova de demônios como o Morbídio Vil! Muito bem!\n\nVocê está pronta. O Morbídio Vil cairá.", rewards = "Você receberá:\n\n"},
[328] = {title = "A chave oculta", objective = "Use a Pequena Chave de Latão para abrir o Cofre do Contador Herodes.", description = "Embora o Recruta Turgo não seja mencionado nesses registros, encontra-se, oculta em uma das costuras dos livros-caixa de Herodes, uma chave de latão.\n\nDepois de analisar melhor as anotações, nota-se que uma \"caixa-forte segura\" é citada algumas vezes em vários papéis. A caixa encontra-se em algum lugar no Arsenal — um conjunto de cavernas ao fundo do Complexo do Kurzen.\n\nSe a caixa-forte segura for encontrada, e a pequena chave de latão puder abri-la, talvez o mistério por trás do paradeiro de Turgo seja revelado.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nCetro do Contador"},
[329] = {title = "O espião é revelado!", objective = "Leve o Bilhete Lamacento para Tenente Doren no Acampamento dos Rebeldes.", description = "Entre os itens valiosos na caixa-forte, encontra-se um bilhete enlameado. Mesmo quase ilegível, distingue-se um itinerário do Recruta Turgo. Detalhes sobre rotas de patrulha, com quem ele fala e o que ele faz quando está com os rebeldes. Na assinatura consta apenas de uma letra: M.\n\nQuem escreveu isto deve ter seguido Turgo de perto. Essa pessoa é o traidor infiltrado no acampamento dos rebeldes.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[330] = {title = "Itinerário das patrulhas", objective = "Fale com o Cabo Malta.", description = "O bilhete que você trouxe foi assinado por \"M\"... não sei o que isso significa.\n\nPorém... o bilhete também menciona participação nas rotas de patrulha do Turgo semana passada. Então, quem fez a patrulha com Turgo semana passada deve ser o traidor!\n\nProcure o Cabo Malta. Ele cuida do itinerário das patrulhas e pode dizer quem estava com Turgo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[331] = {title = "Relatório para Doren", objective = "Fale com o Tenente Doren.", description = "O Tenente Doren não pediu que você falasse novamente com ele sobre aquela patrulha?Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nSandálias do Andarilho das SelvasBrafoneiras de Metal Gélido\n\nVocê também receberá:\n\n"},
[332] = {title = "A propaganda é a alma do negócio", objective = "Vá até a Vinícola Callina e entregue o Vale-vinho para Suzette Callina para ganhar uma garrafa de vinho.", description = "Se você conversar com qualquer um sobre vinhos, todos com certeza vão dizer que nós, os Callina, vendemos as melhores garrafas de vinho de Ventobravo. E nossa loja fica pertinho, aqui mesmo no Distrito Comercial, ao lado dos adoráveis canais da cidade.\n\nTome aqui um panfleto que dá direito a uma garrafa do nosso famoso pinot noir como cortesia. Basta entregá-lo para minha irmã Suzette, lá na loja. Você não vai se arrepender!", progress = "Saudações e seja bem-vindo, cavalheiro. Já conhece nossos vinhos finos?", completion = "Ah, então você encontrou o Renato? Bem, então tome, cavalheiro. Uma garrafa do nosso pinot noir especial. Você nunca provou nada igual em toda Azeroth!", rewards = "Você receberá:\n\nGarrafa de Noir de Dalaran"},
[333] = {title = "A lista de material do Fialho", objective = "Vá até a loja Ao Molde Moderno e entregue o Pedido de Tecido de Timóteo Fialho para Isabel Armarinho.", description = "Os negócios andam agitados nos últimos dias. Todo mundo está comprando armaduras e roupas pesadas, como se estivessem esperando uma estação muito fria e difícil pela frente...\n\nMas ainda não é hora de nos preocuparmos com isso. Meu problema de hoje é que estou ficando sem roupas de malha para vender. Preciso fazer um pedido novo para o fornecedor.\n\nEu ficaria muito agradecido se você pudesse levar este pedido para a Isabel Armarinho na loja Ao Molde Moderno.", progress = "Você trouxe um recado do Sr. Fialho?", completion = "Aqui diz que o movimento na loja do Timóteo aumentou muito. Isso é bom, mas me faz pensar em por que tanta gente está precisando de armaduras... Pelo que eu sei, não estamos em guerra... Será que os nobres estão escondendo alguma coisa de nós? Será que há algo de podre no reino de Ventobravo?\n\nBem, eu agradeço a você por trazer o pedido. Aqui está seu pagamento, e vou tomar as providências para atender o Timóteo.", rewards = "Você receberá:\n\n"},
[334] = {title = "Entrega para Antônio Armarinho", objective = "Vá até a Alfaiataria Galante, no Distrito dos Magos de Ventobravo, e entregue a caixa de costura para Antônio Armarinho.", description = "Meu filho, o Antônio, trabalha como aprendiz na Alfaiataria Galante, no Distrito dos Magos. Hoje de manhã, ele saiu atrasado e se esqueceu de levar a tesoura e as agulhas. Eu sei que <um/uma> <class> importante como você deve ter assuntos mais urgentes a resolver, mas sem esse material o Antônio vai perder o dia de trabalho!\n\nPor favor, <name>. Você poderia levar a caixa de costura para ele? Só existem duas lojas de roupas no Distrito dos Magos. A Alfaiataria Galante é a que fica mais para dentro do distrito, perto da Torre do Mago.", progress = "Pois não, posso ajudar? Precisando de calças novas?", completion = "Ah, que droga! Eu achei que, esquecendo a caixa de costura em casa, ia conseguir escapar do trabalho... Agora, não tem jeito: vou ter que ajudar os Galantes com a costura.\n\nÉ a vida. Mais tarde virá a diversão – assim eu espero.", rewards = "Você receberá:\n\n"},
[335] = {title = "Uma bebida para poucos", objective = "O Malvino da Garra Preta quer que você lhe traga uma Lágrima de Tirsa da Vila Sombria e uma Raiz Almiscarada do Pantanal.", description = "O barão Jânio de Quadranilha está criando um rebuliço na Câmara dos Nobres. Ele ouviu rumores sobre o nosso pequeno \"estabelecimento\" e agora só fala em abrir uma Comissão Real de Inquérito e em passar uma vassoura no reino. Eu já maquinei um plano para manter o Quadranilha \"ocupado\", mas para isso preciso da sua ajuda.\n\nQuero que você traga para mim uma pequena flor branca, chamada lágrima de Tirsa, que cresce no Jardim de Brumanto, nos arredores da Vila Sombria. Também preciso de uma raiz almiscarada, que é encontrada apenas na base das quedas d'água do Dique Lapidado, no Pantanal.", progress = "O Quadranilha já está quase conseguindo recolher todas as assinaturas para abrir a comissão de inquérito contra nós. Já conseguiu pegar a lágrima de Tirsa e a raiz almiscarada?", completion = "Formidável! Estes espécimes estão muito bons!", rewards = "Você receberá:\n\n"},
[336] = {title = "Uma bebida para poucos", objective = "Sirva o Chope Garra Negra para o Barão Quadranilha na Sala de Audiência.", description = "O barão Quadranilha, como todo homem, tem lá suas fraquezas. A dele é o gosto pela água que passarinho não bebe. É de uma hipocrisia espantosa. Aquele imbecil moralista passa os dias tentando nos fechar e as noites bebendo na nossa pequena \"taberna\", e ainda por cima pede fiado. Bem, vamos ver o que o Quadranilha acha dessa cerveja especial que eu preparei para ele: Chope Garra Negra. Esse pega pra valer.\n\nSirva a cerveja para o Barão e veja o que ele acha. Ele deve estar trocando as pernas como sempre na Sala de Audiência.", progress = "Não está vendo que estou ocupado aqui? Escutem minhas palavras! Eu sou muito importante para a história deste reino!\n\nHã... O que é que você traz aí? Hum, parece refrescante...", completion = "Ah, obrigado por me servir este belo chope, garçom. Tome um cobre para você. Agora, saia da minha frente e me deixa apreciar minha cerveja em paz!", rewards = "Você receberá:\n\n"},
[337] = {title = "Um velho livro de história", objective = "Devolva o livro \"A História de Ventobravo\" para a Biblioteca de Ventobravo.", description = "Apesar da capa toda marcada e enlameada, ainda se pode ler na lombada \"A História de Ventobravo\". Ao abrir o livro, o selo da Biblioteca de Ventobravo está visivelmente estampado no folha de rosto.\n\nParece que alguém retirou esse livro e nunca se deu ao trabalho de devolvê-lo...", progress = "Ah! Qual é esse livro antigo que você traz aí? Posso dar uma olhada?", completion = "Minha nossa! Esse livro não repousa nessas estantes desde a reconstrução de Ventobravo, há muitos anos. Uma restauração será necessária, obviamente, pois ele está em péssimas condições, mas seria uma honra adicioná-lo à nossa coleção de livros raros.\n\nAgradeço muito, senhor. Por favor, aceite isto como retribuição por recuperar o livro!", rewards = "Você receberá:\n\n"},
[338] = {title = "Grande Espinhaço: Veredas", objective = "Reúna as páginas que faltam do manuscrito de Grande Espinhaço: Veredas. Quando todos os quatro capítulos estiverem completos, devolva-os a Eucunha.", description = "Mestre Rosarães ficará tão aperreado se descobrir!\n\nBah, ele confiou em mim para revisar o manuscrito de seu romance, Grande Espinhaço: Veredas. Tudo estava indo bem que era uma lindeza, quando uma rajada de vento triforte varreu o acampamento e espraiou as páginas por toda parte!\n\nConsegui reunir a maioria delas, tchê, mas muitas ainda estão espalhadas pela selva. Te raspa daqui pra recolher as páginas e completar cada um dos quatro capítulos.\n\nEu te ajudo a colocá-las em ordem. Traga os capítulos para mim assim que tiver todos os quatro.", progress = "Tu já reuniste os capítulos, <name>?", completion = "Tu certamente me salvaste de uma baita pendenga, &lt;name&gt;! Nem sei como te agradecer, tchê. Não tarda para o livro do Mestre Rosarães estar nas bibliotecas do mundo inteiro!", rewards = "Você receberá:\n\nBotas da SelvaPoção Superior de CuraKit de Armadura Grossa"},
[339] = {title = "Capítulo I", objective = "Leve as páginas 1, 4, 6 e 8 de Grande Espinhaço: Veredas, de Rosarães, até Eucunha de Clides para completar o Capítulo I.", description = "Bah, o Capítulo I é fascinante por demais da conta! Mestre Rosarães define o cenário da história e fala de seus companheiros de caça, inclusive eu! \n\nBueno, isso não vem ao caso. Deixe-me ver quais páginas ainda estão faltando. Parece que as páginas 1, 4, 6 e 8 ainda estão em algum lugar na floresta!", progress = "Acho que tu campeaste, mas não conseguiste encontrar as páginas 1, 4, 6 e 8, tchê. Só precisamos dessas quatro agora para organizar o Capítulo I novamente.", completion = "Bah, até que enfim! O Capítulo I está completo!", rewards = "Você receberá:\n\nGrande Espinhaço: Veredas – Capítulo I"},
[340] = {title = "Capítulo II", objective = "Leve as páginas 10, 11, 14 e 16 de Grande Espinhaço: Veredas, de Rosarães, até Eucunha de Clides para completar o Capítulo II.", description = "O Capítulo II estava repleto de histórias triemocionantes sobre caçadas a panteras e tigres. Me caiu os butiá do bolso quando deixei as páginas se espraiarem! \n\n Parece que consegui recuperar todas as páginas, menos a 10, a 11, a 14 e a 16.", progress = "Bah, ainda precisamos das páginas 10, 11, 14 e 16 para completar o Capítulo II, <name>.", completion = "Bah, o Capítulo II finalmente está completo! Muito bem, guri!", rewards = "Você receberá:\n\nGrande Espinhaço: Veredas – Capítulo II"},
[341] = {title = "Capítulo III", objective = "Leve as páginas 18, 20, 21 e 24 de Grande Espinhaço: Veredas, de Rosarães, até Eucunha de Clides para completar o Capítulo III.", description = "O Capítulo III era trimassa. Conta sobre Dom Raciliano e a primeira morte de Lady Ceireles com um rifle de anão. E o final do Capítulo III, barbaridade, que suspense! \n\nMas me parece que ainda precisamos das páginas 18, 20, 21 e 24 para arrematar o capítulo. Te apura, gur<i/ia>, que essas páginas têm que estar por aí em algum lugar...", progress = "Tiveste alguma sorte com as páginas 18, 20, 21 ou 24? São as únicas que faltam para arrematarmos logo o Capítulo III, tchê.", completion = "Barbaridade, tchê! Bom trabalho, &lt;name&gt;. O Capítulo III está completo de novo!", rewards = "Você receberá:\n\nGrande Espinhaço: Veredas – Capítulo III"},
[342] = {title = "Capítulo IV", objective = "Leve as páginas 25, 26 e 27 de Grande Espinhaço: Veredas, de Rosarães, até Eucunha de Clides para completar o Capítulo IV.", description = "Barbaridade, Capítulo IV; o grande final! E que final! \n\nFaltam apenas três páginas para este capítulo, tchê. Precisamos das páginas 25, 26 e 27 para atrelar o Capítulo IV.", progress = "Bah, nós só precisamos de três páginas para completar o Capítulo IV, <name>. Tu já conseguiste as páginas 25, 26 e 27, guri?", completion = "Trilegal! Agora completamos o Capítulo IV!", rewards = "Você receberá:\n\nGrande Espinhaço: Veredas – Capítulo IV"},
[343] = {title = "Falando sobre fortitude", objective = "Vá à Biblioteca Real na Bastilha Ventobravo e fale com Milton Resma. Ele pode encontrar para você o livro sobre metalurgia que o Irmão Kristoff precisa para o discurso.", description = "Estou fazendo um discurso sobre a Fortitude do Espírito, e gostaria de recitar passagens de um livro específico sobre metalurgia: \"Fadigas do Ferro\". Deve haver uma cópia deste livro na Biblioteca Real, dentro da Bastilha Ventobravo. Você pode trazê-lo para mim? Se puder, então que as bênçãos da Luz estejam com você!\n\nA Biblioteca Real é muito grande, mas o bibliotecário-chefe, Milton Resma, conhece cada prateleira como se fosse um filho. Fale com ele, e sei que encontrará o livro para você.", progress = nil, completion = "Metalurgia? Ah, sim, temos muitos livros sobre o assunto e ficamos felizes em ajudar o Irmão Kristoff com seu discurso! Agora... de que livro em particular o bom irmão precisa?", rewards = "Você receberá:\n\n"},
[344] = {title = "Irmão Paxeco", objective = "Fale com o Irmão Paxeco na Abadia de Vila Norte.", description = "Fadigas do Ferro? Sim, eu conheço o livro. É muito bom, foi escrito pelo mineiro anão Margulf Blaggon algumas décadas atrás. Ele passou a maior parte da vida nas montanhas de Khaz Modan, escavando minério. Camarada inteligente!\n\nMas onde eu estava? Ah, sim, o Fadigas do Ferro! Receio que o livro tenha sido transferido para a Abadia de Vila Norte. O zelador da biblioteca da abadia, Irmão Paxeco, queria fazer uma cópia do livro para as prateleiras dele.\n\nMas isso foi há meses. Não entendo por que ele está demorando tanto...", progress = nil, completion = "Seu Resma está querendo saber onde está o livro dele? Bem, isso é um pouco embaraçoso. Estou com o livro, mas ainda não terminei de fazer minha cópia e não estou pronto para devolver o original!", rewards = "Você receberá:\n\n"},
[345] = {title = "Estoque de tinta", objective = "Fale com o Encarregado Oslow em Vila Plácida e peça-lhe Minério de Ferro Retibano.", description = "Eu estava querendo terminar minha cópia de Fadigas do Ferro, mas...\n\nBem, copio os livros com a tinta que eu mesmo faço. A receita é minha. A tinta não escorre nem mancha, e seca bem depressa! Infelizmente, seca depressa demais! Meu último lote secou antes que eu pudesse terminar a cópia, e não tive chance de fazer mais tinta!\n\nPara preparar mais, preciso de minério de ferro retibano das Montanhas Cristarrubra. O Encarregado Oslow, em Vila Plácida, costuma ser meu fornecedor.", progress = nil, completion = "Você precisa do quê??? Talvez não tenha notado, mas estamos ocupados intensificando as defesas de Vila Plácida e consertando os danos causados por recentes ataques de orcs. Se você precisa de minério de ferro retibano, terá de se virar sozinho!", rewards = "Você receberá:\n\n"},
[346] = {title = "De novo com Kristoff", objective = "Fale novamente com Irmão Kristoff na Praça da Catedral. Dê a ele o livro Fadigas do Ferro.", description = "Agora que terminei de copiar o livro, aqui está o original de Fadigas do Ferro. Obrigado por sua paciência e, quando estiver com Milton, por favor, diga que sinto muito pela demora.", progress = "Você conseguiu achar o livro?", completion = "É esse! Obrigado, &lt;name&gt;. Que a Luz ilumine intensamente seu corpo e seu espírito!", rewards = "Você receberá:\n\nBotas do Errante"},
[347] = {title = "Minério Retibano", objective = "Colete 5 porções de Minério Retibano e leve-as para o Irmão Paxeco na biblioteca de Vila Norte.", description = "O minério retibano tem esse nome em razão do primeiro homem que o encontrou, Bartolomeu Retiban. Quando fundido, não é tão forte quanto o ferro, mas o minério bruto tem uma cor escura e uniforme que alguns escribas usam em seus pigmentos. O minério pode ser encontrado nas Cavernas Retibanas, ao norte daqui.\n\nGeralmente temos um pouco em estoque, mas estamos ocupados demais nos preparando para ataques de orcs para extraí-lo. E, além disso... os gnolls de Cristarrubra agora estão morando nas Cavernas Retibanas, e seus Mourejantes estão extraindo o minério.\n\nEntão, se você está precisando, vai ter que ir atrás do minério sozinh<o/a>.", progress = "Você está com o minério retibano?", completion = "Ah! Esplêndido! Posso usar esse minério para fazer o meu pigmento, e depois posso terminar de copiar Fadigas do Ferro.\n\nObrigado, &lt;name&gt;!", rewards = "Você receberá:\n\n"},
[348] = {title = "Febre da Selva do Espinhaço", objective = "Procure o Mandingueiro Unbagwa e faça-o evocar Mokk, o Selvagem. Leve o Coração de Mokk para Fino Algazarro.", description = "Doente... muito... d-d-doente...\n\nContraí febre da Selva do Espinhaço...vírus mortaaaal...\n\n...a única cura é... é... comer o coração do m-m-m-Mokk, o Selvagem... fera branca furtiva...\n\n...Mandingueiro Unbagwa... único que sabe evoc-c-c-caaar...\n\n...procure Unbagwa na caverna escondida ao sul do cabo...\n\n...Rápido... estou... morrendo...Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nCobertor Medicinal"},
[349] = {title = "Febre da Selva do Espinhaço", objective = "texto temporário 02 - registro", description = "texto temporário 02 - descrição", progress = nil, completion = nil, rewards = nil},
[350] = {title = "Procurando um velho amigo", objective = "Fale com Elling Trias em Ventobravo.", description = "Aquele Marzon estaria entrando em contato com alguém dos Défias e teria ligação com o Lescovar...\n\nCom VanCleef e Taborda mortos, seria difícil levar Lescovar a julgamento, já que as suas provas não são nada sem o testemunho deles. E isso sem levar em conta o fato de Lescovar ser nobre e muito bem-relacionado. Isso na prática o deixa acima da lei.\n\nVocê vai precisar de mais ajuda. Eu odeio fazer isso, mas conheço alguém que nos ajudará: Trias. Ele trabalha no Distrito Comercial.", progress = nil, completion = "Você está de brincadeira? O Shaw quer a minha ajuda de novo? Mas que ousadia...\n\nA Irmandade Défias, é? Bom... Notícia boa essa sua, hein? Daqui a pouco você vai me dizer que o Asa da Morte está ali, vivinho da silva, atacando a cidade.\n\nEstá bem. Conte tudo desde o início. E não deixe de fora nada que você possa não achar importante. Eu preciso saber tudo.", rewards = "Você receberá:\n\n"},
[351] = {title = "Encontre OOX-17/TN!", objective = "Leve o transmissor de emergência até o robô teleguiado de Olhatorto.", description = "Você encontrou um estranho dispositivo feito de metal que se parece com um ovo. Ao esbarrar em um botão, a geringonça gnômica se abre, ganhando vida! Uma voz surge de dentro do ovo robótico.\n\n\"Me chamo Olhatorto e meu robô teleguiado se perdeu. Eu <o/a> recompensarei se você o trouxer de volta. Leve este transmissor até o robô. Excelente! Acabo de calcular as coordenadas do robô para você. Ele caiu perto da Fenda Hazzali em Tanaris.\"", progress = "Tudo indica que você descobriu os destroços de uma... galinha robótica? A voz do interior do ovo ganha vida novamente:\n\n\"Sim, excelente trabalho! Este é, de fato, meu robô teleguiado, embora meus sensores indiquem que ele precisa de uma partida auxiliada para poder voar de volta à Angra do Butim para reparos. Vá em frente e insira o transmissor no viajante. O transmissor cuidará do resto!\"", completion = "Você insere o ovo... transmissor de emergência... na galinha... robótica teleguiada.\n\nOuvem-se zumbidos e chiados vindos do interior do robô, que luta para ficar em pé. Quando o robô volta à vida, ouve-se mais uma vez a voz de Olhatorto, mas agora do interior do robô:\n\n\"Pelo menos OOX-17/TN está funcionando, mas precisa de sérios reparos! Ele é pesado demais para você carregar... mas acho que tenho uma ideia. Você está disposto a tomar conta dele um pouco mais?\"", rewards = "Você receberá:\n\n"},
[353] = {title = "Entrega para Lançatroz", objective = "Entregue o Pacote para Lançatroz para o Montanhista Lançatroz em Loch Modan.", description = "Os Lançatroz são um clã enânico muito respeitado, conhecidos pelo bom gosto e por serem muito exigentes. Então, não é surpresa que o Gringer Lançatroz, um montanhista de Altaforja, tenha encomendado uma arma comigo.\n\nJá acabei de fazer a arma, mas... O Gringer está de serviço em Loch Modan, que é muito longe daqui. Já que você estava pensando em ir para o norte, poderia entregar a encomenda para ele?\n\nNa última mensagem que me enviou, ele disse que estava aquartelado na torre de guarda norte, em Loch Modan.", progress = "Você veio lá de Ventobravo? Eu soube que as coisas estão ficando meio complicadas por aqui, com salteadores e orcs por todos os lados. É o lugar perfeito para um <class> como você mostrar seu valor!", completion = "Aha! O Grimand finalmente terminou meu machado! Mal posso esperar para testar esta belezura nuns troggs e kobolds!\n\nMuito obrigado, &lt;name&gt;. Sei que você andou muito para chegar até aqui. Tome aqui umas moedinhas pelo esforço.", rewards = "Você receberá:\n\n"},
[354] = {title = "Mortes na família", objective = "Obtenha os Restos Mortais de Gregor, os Restos Mortais de Nissa, os Restos Mortais de Timóteo para Eurico Palhares em Montalvo.", description = "A família Agamand era a família mais próspera nas Clareiras de Tirisfal. Eu costumava trabalhar nos moinhos deles... antes da peste.\n\nQuando o Flagelo chegou os Agamand fortificaram o terreno e convenceram os empregados a ficarem e ajudá-los com a defesa do local. Fomos tolos mas, ao menos, fomos tolos leais.\n\nOs Agamand, em seu orgulho, condenaram todos à morte-viva. Agora são lacaios do Flagelo!\n\nSirva aos Renegados derrotando os Agamand que sucumbiram à peste. Sirva a mim trazendo os restos deles.", progress = "Você tem os restos mortais dos Agamand? Aquelas feras amaldiçoadas foram finalmente destruídas?", completion = "A vingança tem um sabor doce, não acha? Quando destruiu os Agamand, você notou algum vestígio de livre arbítrio neles? Espero que sim. Espero que eles tenham conhecido o medo antes de serem esmagados e entregues ao esquecimento.\n\nÉ uma esperança tola, eu sei. Mas, ainda assim, é uma esperança que eu alimento.", rewards = "Você receberá:\n\n"},
[355] = {title = "Fale com Sevren", objective = "Fale com o Magistrado Sevren em Montalvo.", description = "Você me deu os restos mortais dos Agamand e saciou minha sede de vingança. Mas os Moinhos dos Agamand representam uma ameaça a todos os Renegados, e não só a mim.\n\nContei ao Magistrado Sevren sobre suas façanhas contra os Agamand, e ele quer falar com você.", progress = nil, completion = "Eurico o recomendou, &lt;name&gt;, e falou das suas incursões nos Moinhos dos Agamand.\n\nOs Renegados precisam de seus talentos novamente...", rewards = "Você receberá:\n\n"},
[356] = {title = "Patrulha da retaguarda", objective = "Mate 8 Horrores Sangrentos e 8 Espíritos Errantes. Depois, apresente seu relatório a Linnea no acampamento.", description = "Os defensores do Baluarte, que protege Tirisfal das Terras Pestilentas, ficam em alerta constante. Às vezes, porém, criaturas do Flagelo conseguem passar por eles.\n\nNosso sucesso no Baluarte depende de mantermos a batalha em uma só frente. Não podemos permitir um ataque pela retaguarda, nem permitir que as linhas de suprimentos dos defensores sejam cortadas.\n\nAjude o Baluarte. Patrulhe ao leste e mate quaisquer seres do Flagelo que você encontrar. Concentre mais esforços na fazenda dos Balnir. Ela se tornou um refúgio para os infiltrados do Flagelo.", progress = nil, completion = "Muito bom. Seus esforços contribuem muito para rechaçarmos o Flagelo. Você será muito bem recomendado a meu superior, o Executor Zigano.", rewards = "Você receberá:\n\n"},
[357] = {title = "A identidade do lich", objective = "Leve o Grimório do Lich para Boanerges Stalactus na Cidade Baixa.", description = "Há um lich na ilha no Lago Águas Claras, ao norte. Embora ele seja um Renegado com livre-arbítrio, ele crê que todos os outros mortos-vivos são escravos do Flagelo, e faz seus lacaios atacarem qualquer um que se aproxime.\n\nNo entanto, como ele é um Renegado, a Rainha quererá falar com ele. E, se ele for habilidoso com a necromancia, o conhecimento dele seria... bastante útil para nós.\n\nPara descobrir a identidade e os talentos dele, eu preciso ver o grimório dele. Entre incógnit<o/a> no acampamento, roube-o e venha falar comigo.", progress = "<name>, conseguiu o livro?", completion = "Muito bom, &lt;name&gt;. Estudarei o livro para determinar a identidade do lich. Você serviu bem a sua Rainha.\n\n\n\nAssombroso! O livro que você pegou pertence a ninguém menos que Tertuliano Arcanus!\n\nTertuliano foi um necromante habilidoso em vida. Eu e ele fomos amigos e companheiros de armas... antes da Peste. E, a julgar pelo livro, ele se tornou ainda mais poderoso na morte.\n\nA perícia dele seria uma dádiva para nós.", rewards = "Você receberá:\n\n"},
[358] = {title = "Roubacovas", objective = "Mate Roubacovas Putricouro e Mestiços Putricouro.\n\nLeve 8 frascos de Linfa Embalsamadora para o Magistrado Sevren, em Montalvo.", description = "As sepulturas coletivas a sudoeste do Sítio dos Garren, ao norte, foram feitas para acomodar o número... impressionante... de mortos em Tirisfal quando a Peste chegou.\n\nOs corpos nessas sepulturas escaparam à morte-viva até agora, mas o Flagelo enviou gnolls Putricouro para recolher os cadáveres e usá-los para os exércitos deles. Não podemos permitir isso!\n\nSua tarefa é dupla: mate os gnolls Putricouro na sepultura coletiva e no sítio dos Garren e extraia a Linfa Embalsamadora que lhes dá vida.", progress = "Você terminou a tarefa? Você destruiu aqueles bichos-cachorros malditos e drenou a linfa deles?", completion = "Muito bem feito, &lt;name&gt;. O Flagelo está iludido se acha que pode usar aqueles cadáveres contra nós... o fluido que você recolheu dos escravos Putricouro será estudado pelos Boticários. A linfa pode ter segredos que poderemos usar contra eles.\n\nComo eu já disse, ótimo trabalho. Mas nossa luta continua, e o conflito com certeza lhe oferecerá mais chances de provar seu valor para os Renegados.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nManoplas de Aço FrioBotas de Pele Zumbis\n\nVocê também receberá:\n\n"},
[359] = {title = "Deveres Renegados", objective = "Fale com a Necroguarda Lina.", description = "Preciso de um relatório atualizado de nossa estação de passagem de necroguardas a leste daqui.\n\nPreciso saber se mais tropas do Flagelo conseguiram se infiltrar pelo Baluarte em Tirisfal. Nossa vigília nas fronteiras das Terras Pestilentas deve ser mantida. Não podemos permitir que o Flagelo finque o pé aqui!\n\nVá para o sul seguindo a estrada e então vire para leste na encruzilhada e siga até a estação de passagem. Fale com a Necroguarda Lina. Ela tem a informação de que preciso.", progress = nil, completion = "O Magistrado Sevren quer um relatório? Muito bem: os defensores no Baluarte estão conseguindo manter a maior parte do Flagelo à distância, mas alguns conseguem se infiltrar de vez em quando.\n\nTemos visto atividade esporádica do Flagelo a leste daqui, com uma concentração de forças na Fazenda dos Balnir.", rewards = "Você receberá:\n\n"},
[360] = {title = "De volta ao Magistrado", objective = "Fale novamente com o Magistrado Sevren em Montalvo.", description = "Apresente-se ao magistrado Sevren com a informação que eu lhe dei. Ele precisa das informações o quanto antes, então apresse-se.\n\nE se você vir qualquer criatura do Flagelo no caminho de volta, acabe com ela. Não podemos deixar que eles vaguem pelas nossas terras impunemente.", progress = nil, completion = "Preocupa-me saber que o Flagelo está conseguindo passar pelo Baluarte. O Lich Rei e os lacaios dele atacam incessantemente.\n\nMas não vamos ceder. Ele não nos subjugará novamente!", rewards = "Você receberá:\n\n"},
[361] = {title = "A carta que nunca chegou", objective = "Encontre Ivete Palhares e entregue a carta de Timóteo Agamand.", description = "A carta é de Timóteo, filho mais velho de Gregor Agamand. Está endereçada a uma certa Ivete Palhares, mas parece não ter sido entregue...", progress = "Pois não?", completion = "Ah, meu pobre Timóteo! Eu implorei a ele para sair dos Moinhos dos Agamand comigo, mas a lealdade dele à família era grande demais! Além disso, eu sabia! Eu sabia que o Brandt queria o mal dele! Ah, maldita seja essa peste e maldito seja o Flagelo!\n\n&lt;Então a expressão de Ivete se acalma, tornando-se gélida.&gt;\n\nMas lamentar é para os fracos. Como Renegada, tenho novos objetivos, e o amor não é um deles. Eu agradeceria a você pela entrega da carta, por ter ficado me perguntando o que aconteceu com meu antigo amor.\n\nMas aquela vida acabou. Para sempre.", rewards = "Você receberá:\n\n"},
[362] = {title = "Os moinhos assombrados", objective = "Mate Delmiro Agamand e leve os Restos Mortais de Delmiro para Eurico Palhares em Montalvo.", description = "Delmiro Agamand era o mais novo dos dois filhos da família Agamand, e em vida os dois não poderiam ser mais diferentes. Timóteo era alto e gentil, enquanto o caçula era fraco e de língua ferina.\n\nQuando os Moinhos dos Agamand caíram perante a peste, não me surpreendeu saber que Delmiro sucumbiu rápido. Ainda se ouve o matraquear louco dele perto da estrada dos Moinhos dos Agamand.\n\nEstou coletando os restos mortais dos Agmand, e quero o pobre Delmiro. Encontre-o, destrua-o e traga-me os ossos dele.", progress = "Você encontrou o Delmiro?", completion = "Obrigado. Os ossos de Delmiro descansarão no alto da minha lareira.\n\nMeu coração gelado só se aquece um pouquinho ao saber que os Agamand foram destruídos. Eles falharam comigo e com minha família quando veio a peste. E agora eu juro que vou pisar nos restos mortais deles!", rewards = "Você receberá:\n\n"},
[363] = {title = "Despertar bruto", objective = "Fale com o Sacerdote Sombrio Sarvis.", description = "Já era hora de acordar. Estávamos prontos para jogar você no fogo com os outros, mas parece que você despertou.\n\nSou Mordo, zelador da cripta de Plangemortis. E você não é mais escrav<o/a> do Lich Rei.\n\nFale com o Sacerdote Sombrio Sarvis na capela no pé da colina. Ele lhe contará mais coisas que você precisa saber.", progress = nil, completion = "Mais um morto deambulante, hein? Deve ter sido um choque e tanto acordar na cripta, só com o frio e o Mordo como comitê de boas-vindas...\n\nDá para ver a confusão no seu rosto. Permita-me explicar... a sua... situação.\n\nFomos libertados do controle do Lich Rei por nossa nova líder, a Grande Dama Sylvana. A Dama Sombria nos guia em nossa guerra contra o odiado Flagelo e os remanescentes da humanidade que nos ameaçam a cada passo.", rewards = "Você receberá:\n\n"},
[364] = {title = "Os desmiolados", objective = "O Sacerdote Sombrio Sarvis quer que você mate 8 Zumbis Desmiolados e 8 Zumbis Atormentados.", description = "Nós Renegados estamos em guerra contra o exército do Flagelo do Lich Rei: fileiras de mortos-vivos reanimados com necromancia, feras imundas do norte e espectros atormentados.\n\nA parte norte da aldeia foi invadida pelos Desmiolados, e eles devem ser eliminados. Destrua-os, não tenha misericórdia, nossos antigos irmãos e irmãs ou não. Os caídos não passam de escravos do Lich Rei.", progress = nil, completion = "É uma pena não podermos trazer o Flagelo para o nosso lado, suas vastas quantidades seriam úteis nas próximas batalhas.\n\nMas eles não vão se juntar a nós, então não temos outra escolha senão destruí-los.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nLuvas de LinhãoManto Surrado\n\nVocê também receberá:\n\n"},
[365] = {title = "Campos de mágoa", objective = "Roube 10 abóboras da fazenda a oeste, que fica ao norte de Plangemortis, e leve-as para o Boticário Joaquino em Montalvo.", description = "O que traz você aqui? Você aparenta ser <um/uma> aprendiz, <class>. Se quiser provar o seu valor para a Dama Sombria, você precisa aprender o modo de vida dos Renegados.\n\nA oeste daqui você encontrará uma fazenda. Os humanos infestam a área como se fossem fungos num cadáver podre. Pior ainda, a Cruzada Escarlate faz patrulhas no local, partindo de uma das torres. Ensine uma lição àquela escória e roube dez das preciosas abóboras deles.\n\nQuando as tiver juntado, leve-as para o Boticário Joaquino em Montalvo.", progress = "O Necroguarda Simério avisou que você me providenciaria alguns dos reagentes de que necessito. Você já conseguiu colher as 10 abóboras, <name>?", completion = "Você desempenhou bem a sua tarefa, jovem &lt;class&gt;. Você vem se mostrando um grande reforço para o exército da Dama Sombria.", rewards = "Você receberá:\n\n"},
[366] = {title = "Devolva o livro", objective = "Devolva o Grimório de Tertuliano a ele, na ilha do Refúgio de Tertuliano.", description = "Muito tempo atrás, Tertuliano e eu, mais o necromante do Flagelo Thule Corvinalle, éramos amigos e estudantes do Kirin Tor — uma sociedade de magos de Dalaran.\n\nTertuliano e eu sucumbimos à peste, mas eu pensei que ele tivesse permanecido morto, ou havia se juntado as fileiras do Lich Rei. É bom saber que ele está livre.\n\nNós precisamos dele do nosso lado. Eu coloquei um feitiço neste livro que, quando ele abrir, mostrará que também estou livre do Lich Rei. Depois que souber disso, eu espero que ele se junte a nós.", progress = "Os seus mestres foram tolos de enviá-lo aqui, pois eu nunca mais serei um escravo do Flagelo!", completion = "Meu livro?! Então foi você quem o roubou!\n\nMas... que encantamento é esse... Boanerges? Ele está em Lordaeron?\n\nSe ele ainda existe, então talvez também tenha escapado do jugo do Lich Rei...", rewards = "Você receberá:\n\n"},
[367] = {title = "A nova peste", objective = "O Boticário Joaquino na cidade de Montalvo quer que você colete 5 Ampolas de Sangue de Cão das Trevas.", description = "A Grande Dama Sylvana convocou a Real Sociedade de Boticários. Ela crê que nosso conhecimento, aliado à magia recém-adquirida, será crucial para a derrocada de Arthas. A Dama Sombria nos desafiou a preparar uma nova peste, mais mortal que qualquer mazela de Azeroth. Essa nova doença será a ruína do exército do Flagelo de Arthas.\n\nMeus estudos mostram que o sangue de certas feras pode ser a chave para o sucesso. Traga 5 ampolas com sangue de cão das trevas para que eu possa testar minha teoria.", progress = "Já coletou as 5 ampolas de sangue de cão das trevas, <name>? O tempo está passando!", completion = "Você agiu bem, &lt;name&gt;, e agradeço os esforços.", rewards = "Você receberá:\n\nPoção Fraca de Sangue de Troll"},
[368] = {title = "A nova peste", objective = "O Boticário Joaquino na cidade de Montalvo precisa de 5 Escamas de Pinavil dos Murlocs das Clareiras de Tirisfal.", description = "Enquanto você estava coletando amostras para mim, minhas experiências me fizeram compreender que mais reagentes serão necessários para que essa nova doença se espalhe adequadamente. Envenenar uma vítima indefesa é brincadeira de criança, mas empestear um mundo inteiro é um pouco mais complicado.\n\nEu vou precisar de 5 escamas de pinavil dos murlocs das redondezas. Você encontrará as criaturas vagando pela costa ao norte ou a oeste.", progress = "<name>, você conseguiu as 5 escamas de pinavil dos murlocs?", completion = "As escamas estão perfeitas, &lt;name&gt;. Justo o que eu precisava para este composto.", rewards = "Você receberá:\n\nAreia do Sono"},
[369] = {title = "A nova peste", objective = "O Boticário Joaquino na cidade de Montalvo quer que você leve 4 amostras de peçonha da Trevateia Malévola para ele.", description = "Enquanto você estava fora, eu encontrei alguns textos em um dos meus livros que indicam que uma praga antiga matou milhares de vítimas inocentes. Mais tarde se descobriu que o agente da praga foi preservado na peçonha das aranhas trevateias.\n\nTraga-me um pouco de peçonha de trevateia malévola para que eu possa completar este experimento. Eu quero ver se o elemento contagioso da peçonha funcionará com o meu novo composto. Dizem que as aranhas podem ser encontradas ao leste das Clareiras de Tirisfal.", progress = "Já conseguiu as peçonhas de trevateia malévola, <name>? É o ingrediente final para que eu possa testar meu experimento.", completion = "Ah, essa peçonha serve perfeitamente, &lt;name&gt;. Todo o resto já foi adicionado à mistura e fervido. Finalmente estou pronto para testar este novo agente mortal!", rewards = "Você receberá:\n\nFaixa de Aprendiz"},
[370] = {title = "Guerra à Cruzada Escarlate", objective = "O Executor Zigano em Montalvo quer que você mate o Capitão Perrine, 3 Zelotes Escarlates e 3 Missionários Escarlates.", description = "Os documentos que recebemos do Executor Arren nos dão todos os detalhes necessários para acabarmos com a infestação humana nestas terras. De acordo com a informação, a Cruzada Escarlate designou o Capitão Perrine e uma brigada para ficar na torre em ruínas a sudoeste de Montalvo.\n\nMate o Capitão Perrine junto com 3 Zelotes Escarlates e 3 Missionários Escarlates, e então fale comigo novamente.", progress = nil, completion = "A morte do Capitão Perrine sem dúvida agradará à Dama Sombria. Você cumpriu bem as suas ordens, &lt;class&gt;.", rewards = "Você receberá:\n\n"},
[371] = {title = "Guerra à Cruzada Escarlate", objective = "O Executor Zigano em Montalvo ordenou que você mate o Capitão Vidálio e 5 Frades Escarlates.", description = "Ainda estamos em guerra e a Cruzada Escarlate continua a se fortalecer. O relatório que o Executor Arren me enviou indica que os ataques dos cruzados escarlates têm partido da torre em ruínas na parte sudeste de Tirisfal, perto da Fazenda dos Balnir. Eles estão sob o comando do Capitão Vidálio.\n\nMate Vidálio e 5 Frades Escarlates. Isso será um golpe devastador para a Cruzada!", progress = nil, completion = "A morte do Capitão Vidálio vai refrear os avanços da Cruzada Escarlate aqui em Tirisfal por algum tempo, mas outra ameaça paira sobre nós.", rewards = "Você receberá:\n\n"},
[372] = {title = "Guerra à Cruzada Escarlate", objective = "O Executor Zigano na cidade de Montalvo quer que você mate o Capitão Metrache e seus dois seguranças.", description = "Cruzado escarlates têm atacado da torre em ruínas que fica no norte de Tirisfal, além do Repouso de Faol. De acordo com as nossas informações, um impiedoso capitão chamado Metrache está a cargo desse grupo perverso.\n\nEu confio a você agora uma missão especial e perigosa. Mate o Capitão Metrache e seus dois seguranças em nome da Dama Sombria.", progress = nil, completion = "Você se saiu muito bem, &lt;class&gt;. Com combatentes impiedosos como você conquistando em nome dos Renegados, a nossa raça está a um passo de derrotar Arthas de uma vez por todas. Eu tenho um bom pressentimento sobre você, camarada.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nFaca CerimonialMachadinha de Ataque\n\nVocê também receberá:\n\n"},
[373] = {title = "Uma carta não enviada", objective = "Entregue a Carta para o Arquiteto da Cidade a Baros Aleixo em Ventobravo.", description = "Ao revistar o corpo de Edwin VanCleef, você encontra, entre outras coisas, uma carta não enviada. Ela está endereçada a Baros Aleixo, arquiteto da cidade de Ventobravo, Prefeitura, Praça da Catedral.\n\nParece que foi escrita e selada recentemente.", progress = "Sim? Posso ajudar de alguma forma?", completion = "Edwin VanCleef? Tem certeza de que não é uma carta da minha falecida avozinha? Quer dizer então que você matou ele? Desculpa, mas eu estou surpreso. Ele era um guerreiro sem igual quando jovem. Vejamos o que ele achou necessário escrever para mim depois de tantos anos.\n\n&lt;Baros examina a carta.&gt;\n\nEdwin... Dá para ver que você não mudou nada. Como sempre um idealista, e um romântico. Ele não se importa com quem magoa, &lt;name&gt;. A vingança o consumiu, mas não sei se posso culpá-lo por isso.", rewards = "Você receberá:\n\n"},
[374] = {title = "Prova da morte", objective = "Leve 10 Anéis com Insígnia Escarlates para o Necroguarda Belchior em Montalvo.", description = "Executor Zigano tells me you've been out killing Scarlet Crusaders. Tirisfal Glades is crawling with them.\n\nVarimathras, lieutenant to The Dark Lady, is seeing to it personally that the scum of Azeroth, the Human race, gets wiped into oblivion. Here in Tirisfal Glades we face a particularly annoying breed of humans: The Scarlet Crusade.\n\nVenture forth and wage an offensive on the Scarlet Crusade, <class>. Bring me back 10 Scarlet Insignia Rings as proof of your loyalty to Varimathras and The Dark Lady.", progress = "Você já conseguiu os 10 anéis com insígnia escarlates, <class>?", completion = "Varimatras ficaria orgulhoso. Você serve bem os seus líderes, &lt;class&gt;.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBraçadeiras de Pele ZumbisCinto AfiveladoLuvas de Rede\n\nVocê também receberá:\n\n"},
[375] = {title = "O frio da morte", objective = "Leve 5 Pelagens de Quiropúsculo e um pouco de Fio Grosso para Gretchen Dedamar em Montalvo.", description = "Está tão frio agora. A Peste da Morte-viva desliza em minhas veias como uma serpente de gelo. Logo a demência chegará. Mas nem mesmo um destino condenado me impedirá de servir a Dama Sombria. Quando veio o chamado, eu costurei sacos de corpos para os soldados caídos do poderoso exército de Sylvana.\n\nAgora minhas mãos tremem de frio. Se você me trouxer cinco pelagens de quiropúsculo e um pouco de fio grosso, vou poder costurar um cobertor pra mim. Me ajude, <name>, e assim poderei continuar servindo à causa.", progress = "Já conseguiu as cinco pelagens de quiropúsculo e um pouco de fio grosso, <name>?", completion = "Sou grata por seus esforços, &lt;name&gt;. Que Sylvana reconheça sua bravura em breve...", rewards = "Você vai poder escolher uma dessas recompensas:\n\nManto do AdeptoLuvas de Costura\n\nVocê também receberá:\n\n"},
[376] = {title = "Os malditos", objective = "A Noviça Elvira precisa de 6 Patas de Carniceiro e 6 Asas de Quiropúsculo.", description = "As minhas obrigações incluem cuidar dos guerreiros feridos, costurar armaduras e roupas, e ajudar o sacerdote sombrio Sarvis com o que mais for preciso.\n\nEstá parecendo que você vai ficar sob o comando dele... Caçando os desmiolados, se eu o conheço bem. Bem, se você quiser continuar inteir<o/a> — e não tenho dúvida disso —, talvez eu possa ajudar. Eu estou ficando sem patas e asas aqui. Se me trouxer algumas, eu lhe arranjarei uma armadura. Você vai encontrar um bom número de lobos e morcegos ao sul daqui.", progress = "Faça o melhor que puder para se manter vivo até que eu lhe arrume uma armadura.", completion = "Obrigada, &lt;name&gt;. Esta armadura deve ajudá-lo.\n\nEspero que lhe traga melhor sorte do que ao último dono...", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBraçadeiras de LinhãoCinto de Couro Velho\n\nVocê também receberá:\n\n"},
[377] = {title = "Crime e castigo", objective = "O Conselheiro Millstipe da Vila Sombria quer que você leve a mão de Flávio Lúcio para ele.", description = "Como se o abandono dos cidadãos da Floresta do Crepúsculo não fosse ruim o bastante; agora a Câmara dos Nobres cospe no olho do Concílio da Vila Sombria com a decisão de aprisionar Flávio Lúcio em Ventobravo, em vez de decapitar o criminoso de acordo com a sentença do Lorde Ebanez.\n\nLúcio foi pego vendendo corpos do cemitério para Morbídio Vil, um crime punível com a morte. Ainda assim, Ventobravo fez dele um prisioneiro.\n\nMate Lúcio no Cárcere de Ventobravo. Traga-me a mão dele, e eu lhe recompensarei.", progress = "Enquanto um profanador dos mortos como Flávio Lúcio continuar a viver, a justiça nunca será feita. Fale novamente comigo quando a sentença de morte dada a ele pelo Lorde Ebanez for executada. Nós daremos o desfecho que as famílias dos mortos merecem e, melhor ainda, enviaremos uma mensagem clara para a Câmara dos Nobres de Ventobravo.", completion = "Então, finalmente o Flávio Lúcio pagou por seus crimes contra a humanidade? Quer saber, já vai tarde. E parabéns para você, meu amigo! Você não só garantiu a paz de espírito que as famílias dos mortos merecem, como também enviou uma mensagem incisiva para aqueles burocratas corruptos da Câmara dos Nobres. Ventobravo precisa atender as necessidades dos cidadãos da Floresta do Crepúsculo ou nós nos libertaremos de sua tirania.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas do EmbaixadorPerneiras de Malha de Vila Sombria\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[378] = {title = "Fúria profunda", objective = "Rocio Garmason quer que você leve a cabeça de Kam Fundafúria para ele em Dun Modr.", description = "Você provou tua lealdade ao rei Magni, <class>. E o teu ódio pela escória dos Ferro Negro é tão grande quanto o meu.\n\nTem um trabalho que eu queria fazer, mas sou obrigado por honra a ficar ao lado de Trançalonga. Mas Roggo obteve informações que provam que Kam Fundafúria conspirou no ataque à Ponte Thandol. Foi por causa da traição de Fundafúria que Trançalonga perdeu seu irmão.\n\nFundafúria está sendo mantido no Cárcere de Ventobravo por razões políticas. Eu quero ele morto, <name>. Por Trançalonga! Traga-me a cabeça dele!Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nCinto da VindicaçãoAfunda-cucas\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[379] = {title = "Mate a sede", objective = "Leve 5 Bolsas de Água do Errante para o Engenheiro-chefe Silvadágua em Geringontzan.", description = "Você quer uma fonte de energia. Eu posso te ajudar. Mas quer saber? Eu quero um negócio de beber! Me traz cinco bolsas de água do errante, e eu te dou o 4711-KTC.\n\nAgora, adivinha onde você acha bolsas de água do errante?! Procura um nômade errante a leste daqui e pede uma para ele. Depois me conta se deu certo.", progress = "Bem, os nômades \"cederam\" essas bolsas de água para você?", completion = "Excelente, a fonte de energia é sua. Olha, ela não foi feita para ser usada em um kit de ciências, então você só vai ter um tempo até que os dados que coletou façam cabum!\n\nQuando tiver conseguido tudo que precisa para o que seja lá que esteja fazendo, traz ela para mim e eu desligo direto. Você vai ter no máximo duas horas para terminar o que precisa terminar.\n\nSe não terminar a tempo, eu posso te vender outra fonte de energia para você tentar de novo. Eu falei vender!", rewards = "Você receberá:\n\nModelo 4711-KTC Fonte de Energia"},
[380] = {title = "Vale Teia da Noite", objective = "O Executor Arren quer que você mate 10 Trevateias Jovens e 8 Trevateias.", description = "Uma das nossas maiores lutas é para obter os recursos naturais que precisamos para sobreviver. O ouro sempre foi escasso, mesmo na época de maior poder da Aliança.\n\nHá uma mina de ouro a nordeste que foi invadida por aranhas. Precisamos do ouro desta mina, mas não conseguiremos enquanto as aranhas estiverem por lá. Não tenho muitos homens para pôr nesta tarefa, então vamos fazendo aos poucos.\n\nVá até lá e veja o que pode fazer por nós, <name>.", progress = nil, completion = "Hm... Bem, já é um começo. Levará algumas semanas ou até meses para acabarmos definitivamente com a infestação. Depois disso, desceremos a mina com tochas e atearemos fogo nas teias.\n\nVocê cumpriu com o seu dever, &lt;name&gt;. Tenho certeza que posso encontrar outra coisa para você fazer.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nColete de LinhãoColete de Malha RústicoPerneiras de Pele de Zumbi\n\nVocê também receberá:\n\n"},
[381] = {title = "A Cruzada Escarlate", objective = "Leve 12 Braçadeiras Escarlates de Neófitos Escarlates e Iniciados Escarlates para o Executor Arren.", description = "Você ficará feliz de saber que estamos fazendo progresso na mina, graças em grande parte aos seus esforços. Agora podemos nos concentrar em outros problemas.\n\nOs meus batedores relataram que um destacamento da Cruzada Escarlate montou acampamento a sudeste daqui. A Cruzada Escarlate é uma organização desprezível que nos caça, e eles não descansarão até que todos os mortos-vivos — Flagelo do Lich Rei ou não — sejam destruídos. Temos que atacar primeiro!\n\nTenha cuidado, seu fervor profano faz deles adversários perigosos.", progress = "Esses idiotas... Eles cavam a própria sepultura com alegria.", completion = "Se ao menos eles ouvissem a voz da razão, não é mesmo, &lt;name&gt;? Talvez possamos fazer com que sigam o bom senso... Rá!\n\nTolos cegados pela Luz.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nAdaga dos RenegadosMalho dos RenegadosEspada Curta dos RenegadosEspada Bastarda dos Renegados\n\nVocê também receberá:\n\nÁgua Refrescante da Fonte"},
[382] = {title = "O mensageiro escarlate", objective = "Mate Meven Korgal, o mensageiro, no acampamento da Cruzada, e leve as informações que encontrar para o Executor Arren.", description = "Relatos dos meus superiores em Montalvo indicam que um agente da Cruzada Escarlate foi despachado do Monastério Escarlate e enviado aqui para Plangemortis. De acordo esses relatos, o mensageiro carrega informações importantes consigo. Eu não tenho mais detalhes, mas seja o que for, nós precisamos interceptá-lo!\n\nVocê é a pessoa mais qualificada à disposição, <name>. Encontre o mensageiro e traga qualquer informação que conseguir.", progress = "Como vai sua missão, <name>?", completion = "Hm, vejamos o que você conseguiu...\n\n&lt;Ele inspeciona os documentos que você recuperou.&gt;\n\nNotícias de alguns ataques contra nós... Inútil. Relatórios de batedores... Novas ordens: \"Continuem a montar acampamento próximo à cidade arruinada...\". Nada que já não soubéssemos... Hã, o que é isso? Um mapa com a localização de alguns dos comandantes e agentes de campo! Isso pode ser útil.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCajado do ExecutorBroquel do Necroguarda\n\nVocê também receberá:\n\n"},
[383] = {title = "Informações cruciais", objective = "Entregue os Documentos da Cruzada Escarlate ao Executor Zigano, em Montalvo.", description = "Preciso levar esses documentos ao meu superior, o Executor Zigano, em Montalvo. Com essas informações, vamos poder efetuar um ataque decisivo contra a Cruzada Escarlate. Seus serviços foram úteis aqui em Plangemortis, mas preciso entregar isso a Zigano, e acredito que ele pode ter mais trabalhos para você.\n\nPegue a estrada ao norte para sair de Plangemortis. Logo depois de entrar em Tirisfal, você vai chegar a uma encruzilhada. Pegue a estrada leste e continue indo para o leste. Você vai passar pelo Casarão Lar Glacial no caminho.", progress = "Sim, o que deseja?", completion = "Isso é muito bom. Você fez um bom trabalho obtendo estas informações, &lt;name&gt;. Hum... Os nomes dos oficiais nas Clareiras de Tirisfal...\n\nImagine como abalaríamos o moral desses zelotes malditos se matássemos os líderes em um só ataque.", rewards = "Você receberá:\n\n"},
[384] = {title = "Costelinhas de javali na cerveja", objective = "Ragnar Cervaforte, em Kharanos, quer que você lhe traga 6 Costeletas de Rochetusco e uma caneca de Malte Rapsódia.", description = "A freguesia aqui do estabelecimento adora nossas costelinhas de javali na cerveja! Só que o coureador que abastecia meu estoque se alistou no Exército do Rei para ajudar na guerra, no front da Aliança. \n\nQuem sabe você não podia me dar uma ajudinha? Se você me trouxer seis costelinhas de rochetusco e uma cerveja Malte Rapsódia da taberna, eu lhe dou a receita de família das minhas famosíssimas costelinhas de javali na cerveja. E você ainda leva uma amostra grátis de brinde! O segredo é o Malte!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nCostelinhas de Javali na CervejaReceita: Costelinhas de Javali na Cerveja"},
[385] = {title = "Crocoliscos na mira", objective = "Obtenha 5 peças de Carne de Crocolisco e 6 Peles de Crocolisco para Marek Ferrocordis no Pavilhão dos Andarilhos.", description = "Muitos caçadores vem para o Loch Modan caçar os famosos crocolisco. Tem muitos mercadores em busca de pele de crocolisco, que são boas para fazer roupas e armadoras, e tem até quem goste da carne do bicho.\n\nNós chegamos a entrar nesse comércio, mas em pequena escala, porque os crocoliscos são muito ferozes e se entrincheiraram nas ilhas do Loch. Mas não se assuste com o que eu digo, é uma experiência e tanto, lutar contra as mandíbulas dessas feras.\n\nTeve uma vez...", progress = "Já te contei da vez em que quase perdi a mão por causa de um crocolisco? Bicho sinistro, os dentes pareciam navalhas. Mas eu tive sorte... usei a faca para deixar a bocarra do monstrão fechada. Ainda guardo essa faca comigo, só não sei onde...", completion = "Ora, ora, você conseguiu espécimes excelentes, &lt;name&gt;. Vou pagar bem, pois é material de alta qualidade.", rewards = "Você receberá:\n\nReceita: Bife de CrocoliscoCapa Rústica"},
[386] = {title = "Tudo que vai...", objective = "Leve a cabeça de Targorr, o Horror para o Guarda Berto em Vila Plácida.", description = "Targorr, o Horror serviu como Carrasco supremo sob as ordens de Gath'Ilzogg. Seus métodos eram cruéis demais, mesmo para o sinistro padrão dos orcs. Homens que lutaram bravamente para defender o Reino foram torturados a seu bel-prazer. Agora ele está preso no Cárcere de Ventobravo, condenado à morte. Um dos nobres burocratas ordenou a suspensão da execução. Tenho certeza de que estão tramando alguma coisa\n\nAcabe com Targorr, o Horror, <name>. Viaje até O Cárcere e corte a cabeça dele antes que façam alguma tramoia.", progress = "Você já esteve em Ventobravo, no Cárcere? Meu medo é que a tramoia que manteve Targorr, o Horror vivo durante todo este tempo acabe libertando ele em algum momento. Ele foi condenado à morte, <name>, não pode virar um peão político nas mãos de um nobre.", completion = "Targorr, o Horror finalmente encontrou seu destino. Eu, pelo menos, estou feliz de ouvir que a fera agora sabe como se sente quem morreu e passou para o lado. Você foi muito bem, &lt;name&gt;. Às vezes, a verdadeira justiça só pode ser encontrada fora dos tribunais e além da visão encoberta da política.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nEspada LucinaCajado de Raiz Densa\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[387] = {title = "Sufoque a rebelião", objective = "O Carcereiro-chefe Thelágua de Ventobravo quer que você mate 10 Défias Prisioneiros, 8 Défias Condenados e 8 Défias Insurgentes em O Cárcere.", description = "A rebelião precisa ser sufocada! Preciso da sua ajuda. Se vazar a notícia de que a prisão se rebelou, eu perco o emprego! Quero morrer cego de um olho se isso não for alguma tramoia. Mas o que importa agora, pelo bem de Ventobravo, é dominar os prisioneiros.\n\nA punição por insurgência é a morte. Matem alguns desses marginais! Isso deve fazer as coisas voltarem ao controle.", progress = nil, completion = "Sua ação foi muito valiosa no Cárcere, &lt;name&gt;. É óbvio que o problema é maior do que nós dois, mas você se saiu muito bem e eu agradeço por isso.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[388] = {title = "A cor do sangue", objective = "Nikova Raskol, de Ventobravo, quer que você colete 10 Bandanas Vermelhas de Lã.", description = "O desgosto corre em minhas veias como as águas geladas de um rio. Marquinho, meu neto, era um menino de ouro. Apenas 14 anos. Quando o exército foi mandado para o front, o Marquinho pegou em armas para defender a cidade. Ele estava de guarda no Cárcere, onde a gangue dos Défias cumpria pena. O Marquinho repreendeu alguns prisioneiros por rasgar cobertores para cobrir o rosto, como eles costumam fazer. Por causa disso, esfaquearam o pobrezinho pelas costas.\n\nEu quero vingança. Traga 10 das Bandanas de Lã deles. Elas são vermelhas como sangue.", progress = "Vejo que você voltou, <class>. Coletou 10 bandanas vermelhas de lã dessa escória dos Défias no Cárcere?", completion = "Essas bandanas... a marca imunda da corrupção... Foi por isso que tiraram a vida do Marquinho? Que tragédia. Que coisa terrível.\n\nInfelizmente, é impossível voltar no tempo. Saiba que seus feitos trouxeram um pouco de justiça para a minha família, &lt;name&gt;.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[389] = {title = "Basílio Taborda", objective = "Fale com o Carcereiro-chefe Thelágua no Cárcere.", description = "VanCleef e eu éramos membros da Guilda dos Alvanéis. Nossa principal incumbência era reconstruir Ventobravo depois da Guerra.\n\nQuando terminamos nossa tarefa, fomos traídos. Os nobres se recusaram a pagar pelo nosso trabalho. Alguns dos Alvanéis mais experientes foram convidados para trabalhar para o governo, mas VanCleef recusou por pura lealdade aos Alvanéis. Ele liderou uma rebelião e deixou a cidade jurando vingança.\n\nBasílio Taborda, um subordinado dele, deve saber mais sobre o que ele planeja. Basílio está preso no Cárcere.", progress = nil, completion = "O que em nome da Luz você quer? Não está vendo que temos uma crise aqui?\n\nBasílio Taborda?! Por que você quer falar com esse canalha? Como eu vou saber que você não é capanga dele e veio aqui para ajudar com essa rebelião dos infernos? Se as malditas celas não estivessem todas escancaradas, eu te jogaria em uma e tacaria a chave fora!", rewards = "Você receberá:\n\n"},
[391] = {title = "Rebelião no Cárcere", objective = "Mate Basílio Taborda e leve a cabeça dele para o Carcereiro-chefe Thelágua no Cárcere.", description = "Vou deixar uma coisa clara, <name>: eu não confio em você. Mas, do jeito que a situação está, dificilmente você vai fazer diferença.\n\nÉ o seguinte: pode ir lá falar com o Taborda. Se estiver mesmo do nosso lado, você vai acabar com a raça dele e me trazer a cabeça quando tiver feito o serviço. Se, no fim das contas, você estiver do lado dele e não voltar... aí você vai morrer com o resto dos vermes quando nós te acharmos.", progress = "Ou você me traz a cabeça do Taborda, ou eu vou ficar com a sua, entendido, <name>?", completion = "Sem o Taborda encabeçando o movimento, deve ficar mais fácil controlar essa rebelião. Veremos.\n\nTenho que admitir, depois da primeira meia hora, achei que você não sairia mais com vida, mas acho que foi um julgamento apressado.\n\nEntão, no fim das contas, você não arrancou muitas informações úteis dele, não é? Eu algumas coisas que podem ser do seu interesse sobre as atividades do Taborda.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[392] = {title = "Visitante suspeito", objective = "Leve a Descrição da Visita do Taborda Lacrada para Baros Aleixo em Ventobravo.", description = "Nos primeiros anos que passou preso, o Taborda não recebia muitas visitas. Eu imaginei que ele não servisse mais para a Irmandade, por isso tinha sido abandonado.\n\nO que aconteceu foi que, uns meses atrás, isso mudou. Ele começou a receber visitas com frequência... uma, duas vezes por semana. Era um homem estranho, desses tipos quietos. Eu fiquei desconfiado, mas os documentos dele estavam certos.\n\nSegundo a papelada, o nome dele era Maelik. Esta é a descrição dele. Acho que ele não deve visitar o Taborda tão cedo.", progress = "Que foi, <name>? O que você achou?", completion = "Hmm. Maelik? Não, nem o nome nem a descrição me soam familiares. Mas ele pode ser qualquer coisa! Um ex-alvanel, membro da Irmandade Défias... Bem, se ele for bem relacionado como o Theláguas sugere, talvez alguém o reconheça. Eu preciso pensar um instante.", rewards = "Você receberá:\n\n"},
[393] = {title = "Sombra do passado", objective = "Leve a Descrição da Visita do Taborda Lacrada para o Mestre Mathias Shaw em Ventobravo.", description = "Eu não faço ideia mesmo, <name>. Mas existe uma pessoa que talvez possa te ajudar, e essa pessoa me deve um favor por... bom, melhor deixar para lá. O Mestre Mathias Shaw, da AVIN, pode ser encontrado no quartel, na Cidade Velha. O Shaw sabe tudo sobre as mancomunações mais espúrias, sobre as negociatas mais questionáveis da cidade.\n\nSe alguém vai ter informações, é ele.", progress = "Saudações. Como posso lhe ser útil, <class>?", completion = "O quê? O que é isso? Sim, acho que eu conheço esse homem, mas o nome dele não é Maelik. Assassinos usam muitos nomes, muitas máscaras, e sempre se livram delas quando não são mais úteis. Este mesmo homem era conhecido como Marzon, mas com certeza esse também não é o nome verdadeiro dele.\n\nSeja como for, ele é um assassino, e eu o conheço muito bem.\n\nNo momento, Marzon trabalha para o Lorde Gregor Lescobar, um lorde de reputação duvidosa, mesmo com toda a corrupção que se instalou entre a nobreza de Ventobravo.", rewards = "Você receberá:\n\n"},
[394] = {title = "A cabeça da fera", objective = "Fale com Mathias Shaw em Ventobravo.", description = "Fale com Shaw na Cidade Velha. Diga a ele que Lescobar não representa mais ameaça e conte sobre o seu envolvimento com os planos do Lorde contra a cidade. Tenho certeza de que ele vai ficar contente em contar com mais um herói em Ventobravo.\n\nEle provavelmente vai apresentar você ao príncipe ou algum outro nobre. Não deixe isso subir à sua cabeça, <name>. Os nobres pensam que controlam tudo que acontece na cidade, mas algumas coisas ainda dependem de nós.", progress = nil, completion = "Então ele morreu? Com você, a justiça não tarda, &lt;name&gt;. Que a Luz me proteja de ser seu inimigo!\n\n\n\nNão encare isso como uma crítica, &lt;name&gt;. Afinal, como poderíamos ser inimigos se eu sou mestre de todos os assassinos? A bem da verdade, você prestou ao povo de Ventobravo um grande serviço. Eu agradeço por isso.", rewards = "Você receberá:\n\n"},
[395] = {title = "Fim da irmandade", objective = "Fale com Baros Aleixo em Ventobravo.", description = "Ah, eu quase esqueci. O Aleixo apareceu enquanto você estava fora. Ele quer falar com você. Com certeza ele quer informar os nobres a respeito da situação da Irmandade Défias.\n\nUm conselho, <name>: se alguém perguntar, eu daria todos os detalhes, exceto que foi você quem matou Lescobar. Acredite em mim. Eu sei como a coisa funciona nesses meios.\n\nQuanto a Trias, bem, não fale dele para ninguém... nunca... se puder evitar.", progress = nil, completion = "Que bom vê-lo &lt;name&gt;. Ouvi dizer que o Lorde Gregor Lescobar foi encontrado morto não faz muito tempo.\n\nParece que você teve sucesso em sua missão. Você tem minha gratidão por salvado a mim e muitos outros da vingança do VanCleef.", rewards = "Você receberá:\n\n"},
[396] = {title = "Uma audiência com o rei", objective = "Entregue o Relatório sobre a Irmandade Défias de Baros Aleixo ao Rei Varian Wrynn na Bastilha Ventobravo.", description = "Eu preparei um relatório para o rei, e acredito que você deveria entregá-lo, junto com um relato de suas descobertas. Não se preocupe, não há duvida de que ele louvará você por cumprir seu dever para com o povo de Ventobravo.\n\nA sua audiência está marcada para logo, então é melhor se apressar, o caminho até a Bastilha Ventobravo é longo.", progress = "<name>, não? Eu fui informada por Baros Aleixo de que você viria. Devo admitir que estou interessada no que você tem a dizer.", completion = "Você será louvado pelo serviço que prestou a Ventobravo, &lt;name&gt;. Se as maquinações de VanCleef tivessem sido bem-sucedidas, ele sem dúvida teria causado um dano considerável ao reino. Algo que obviamente devemos evitar.\n\nAceite isto, &lt;class&gt;, como uma demonstração da apreciação do rei. Sinto muito que ele não possa estar presente, mas sua majestade está em uma missão diplomática e deve se ausentar por um tempo.", rewards = "Você receberá:\n\nSelo de Wrynn"},
[397] = {title = "Atendimento nota 10", objective = "Fale com Pingo, o lacaio de Malvino da Garra Preta, para receber sua recompensa.", description = "Eu soube que o Barão Quadranilha gostou da cerveja.\n\nSeu atendimento foi nota 10, <name>. Os bruxos de Ventobravo poderão praticar sua arte sem maiores preocupações, graças a você. Em agradecimento pelos seus esforços, preparei um presente especial para você. Pegue sua recompensa com o meu lacaio, Pingo.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nChapéu de Mago do Horror"},
[398] = {title = "Procura-se: Olho de Verme", objective = "Mate e leve a pata de Olho de Verme para o Executor Zigano em Montalvo para receber a recompensa.", description = "Procura-se: Olho de Verme!\n\nUma fera horrenda, mesmo para um gnoll, Olho de Verme foi sentenciado à morte por seus crimes contra os Renegados. Este bandido desprezível tem roubado cadáveres para o exército do Lich Rei. Olho de Verme foi visto pela última vez saqueando as terras próximas ao Sítio dos Garren, ao norte de Montalvo.\n\nA cidade de Montalvo oferece uma recompensa à alma caridosa que puder eliminá-lo. Mostre a pata do bandido para o Executor Zigano quando a tarefa for concluída.\n\nPiedade é para os fracos.", progress = "Pois não?", completion = "Os atos covardes do Olho de Verme foram finalmente vingados. Que a sua demonstração de coragem envie uma mensagem clara para aqueles que desejam ferir o nosso povo. Em nome da cidade de Montalvo, eu lhe agradeço, &lt;name&gt;.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCalças de Escama de LatãoColete do Lavrador\n\nVocê também receberá:\n\n"},
[399] = {title = "Origens humildes", objective = "Vá até a casa de Baros Aleixo em Cerro Oeste e procure sua bússola, então a entregue para ele na Praça da Catedral de Ventobravo.", description = "Parece que faz uma eternidade desde que eu era um garoto trabalho na fazenda em Cerro Oeste. Dizem que não há como voltar atrás, e é verdade. Ainda mais no meu caso, a casa da minha família foi incendiada e tomada por ladrões.\n\nEu conversei com o meu pai sobre o destino de algumas das minhas coisas, incluindo a minha primeira bússola. Ele não foi capaz de salvá-la. No entanto, ele diz que ela deve estar escondida na fazenda.\n\nVocê vai encontrar a Fazenda dos Aleixo a oeste do Morro da Sentinela. Você poderia ir lá e recuperar a bússola para mim?", progress = "<name>! Deu alguma sorte?", completion = "Ah, obrigado, &lt;name&gt;! Ela não tem nenhum uso prático, mas é de grande valor sentimental... Nem preciso dizer que agradeço por trazer isso para mim... ao custo do seu bem-estar. Sem mencionar o tempo que levou para ir até Cerro Oeste. Você tem minha gratidão, e fique com isso como sinal de agradecimento.", rewards = "Você receberá:\n\n"},
[400] = {title = "Ferramentas para Gradaço", objective = "Entregue as Ferramentas do Gradaço para Beldin Gradaço.", description = "O Beldin Gradaço é o dono da oficina mecânica daqui e é o maior especialista em máquinas de cerco que há! Mas ele pega pesado com as ferramentas. Juro que ele quebra mais chaves de arco voltaico do que eu consigo repor!\n\nAcabamos de separar o último pedido de ferramentas que ele enviou. Se você entregar o pedido para ele, tenho certeza de que o bom e velho Gradaço vai retribuir à altura.\n\nA loja dele se chama Garagem do Gradaço e fica a nordeste de Kharanos. Lá é um ponto de encontro de veteranos de máquina de cerco daqui, então fique <atento/atenta>, pois pode aparecer alguma oportunidade boa para você.", progress = "Hum? Você parece meio novo para um piloto de máquina de cerco. Mas deixa para lá... Está precisando de algum consertinho?\n\nBem, pegue um número e pode esperar sentado. Estou ocupado com algumas máquinas e não vou ter tempo para serviço novo até daqui a uns dias.\n\nHum, você veio aqui para outra coisa?", completion = "Você trouxe as minhas ferramentas? Bom trabalho, guri, bom trabalho! Acabei de quebrar meu último pino de catraca e preciso disso para acabar o conserto da máquina de cerco do piloto Marchapedra. Você fez bem em trazer as ferramentas até aqui, &lt;name&gt;.\n\nTome estas moedas pela ajuda.", rewards = "Você receberá:\n\n"},
[401] = {title = "Espere Sirra terminar", objective = "Espere um pouco e então fale novamente com Sirra Von'Indi.", description = "Estou quase terminando de traduzir o bilhete. Se voltar daqui um tempo, ele deve estar pronto.", progress = nil, completion = "Pronto. Está traduzido...\n\n&lt;O ar fica pesado, como se uma cortina escura e pesada tivesse sido puxada sobre a terra...&gt;", rewards = "Você receberá:\n\n"},
[402] = {title = "Sirra is Busy", objective = "Check back with Sirra later to see if he's still busy.I'm sorry, but with the town in the state it's in I'm afraid have other duties to attend to. Perhaps later, if we survive, I can finish this translation.", description = "I'm sorry, but with the town in the state it's in I'm afraid have other duties to attend to. Perhaps later, if we survive, I can finish this translation.", progress = nil, completion = nil, rewards = nil},
[403] = {title = "Cuidado com o barril", objective = "Este barril de cerveja é o xodó do Jarven Cervaforte. Enquanto Jarven estiver no porão, não perturbe os barris.", description = nil, progress = nil, completion = nil, rewards = nil},
[404] = {title = "Uma tarefa podre", objective = "Traga 7 Garras Pútridas para o Necroguarda Dinis em Montalvo.", description = "O Flagelo se infiltrou nas Clareiras de Tirisfal e infestou a área a oeste de Montalvo, perto da antiga ponte.\n\nVá e expulse os mortos podres e cadáveres assolados que encontrar. Vasculhe os ossos que sobrarem e me traga suas garras pútridas. Os necroguardas recompensarão você.", progress = "Você cumpriu a tarefa de que lhe incumbi? Você trouxe as garras pútridas?", completion = "Muito bem. Eu me arrependo de não estar lá para ver você transformar aqueles mortos-vivos em polpa podre!", rewards = "Você receberá:\n\n"},
[405] = {title = "O Lich pródigo", objective = "Apresente as Ordens de Sevren a Boanerges Stalactus na Cidade Baixa.", description = "Boanerges Stalactus, um mago de alta patente poderoso ao sul da Cidade Baixa, me incumbiu de enviar um agente verdadeiramente digno contra o Flagelo. Você, <name>, será esse agente.\n\nApresente estas ordens a Boanerges. Ele então lhe instruirá sobre a sua missão. Eu não sei os detalhes, mas envolve recrutar um lich desgarrado. E será perigoso.\n\nEntão se prepare, <class>. Você não deve falhar.\n\nVocê deve encontrar Boanerges no Distrito da Magia da Cidade Baixa.", progress = "Saudações. Ou você não sabe quem eu sou, ou o seu assunto comigo é urgente.\n\nPor que se nenhum desses fosse verdade, então você não seria tão idiota de me incomodar...", completion = "Ah, você é o agente enviado pelo Magistrado Sevren. Então sim, seu assunto comigo é crucial.\n\nEu imagino que Sevren tenha enviado alguém capaz de realizar o trabalho, mas nós veremos se é verdade.", rewards = "Você receberá:\n\n"},
[407] = {title = "Campos de mágoa", objective = "Leve a Abóbora Inoculada para o Zelote Escarlate Capturado que esta sendo mantido no porão da Taberna Nó da Forca.", description = "Abóboras inofensivas, certo? As aparências enganam. Se quisermos derrotar os avanços de Arthas ao norte e a infestação humana ao sul, precisamos começar a compreender o potencial integral de nossa dádiva de morte-viva.\n\nCom um pouco de engenhosidade, uma mera abóbora se torna um agente de nossa Dama Sombria. Esta abóbora, inoculada com minha última fórmula, é uma gostosura – e uma travessura também...\n\nOutro zelote escarlate foi capturado e está sendo mantido no porão da Taberna Nó da Forca. Leve esta abóbora para o cretino.", progress = "Afaste-se de mim, criatura profana e abjeta! Que a Luz me proteja! A Cruzada Escarlate limpará Azeroth da sua ra...\n\nHein? Que é isso? Comida para mim? Estou faminto...", completion = "Pela luz! Finalmente, comida! Que abóbora docinha...", rewards = "Você receberá:\n\n"},
[408] = {title = "A cripta da família", objective = "Mate 8 Ancestrais Ululantes e 8 Ancestrais Apodrecidos.\n\nMate o Capitão Dargol, e leve seu crânio para o Magistrado Sevren em Montalvo.", description = "A área dos Moinhos dos Agamand se tornou um refúgio para o Flagelo. O ponto mais protegido é a Cripta da Família Agamand, onde o líder militar local, um esqueleto chamado Capitão Dargol, se estabeleceu. Usando necromancia, ele ressuscitou os ancestrais dos Agamand e planeja usá-los contra nós.\n\nNão podemos permitir isso, e cabe a você detê-los. Vá para a Cripta da Família Agamand e derrote os ancestrais ressuscitados dos Agamand.\n\nE traga-me o crânio do Capitão Dargol.", progress = "Tempo é um luxo de que não dispomos, <name>. A cada hora que passa, o jugo do Flagelo sobre as Clareiras de Tirisfal fica mais forte.", completion = "Suas ações causaram grande prejuízo ao Flagelo. E sua vitória não vai passar despercebida – nem pelos Renegados, nem pelos nossos inimigos.\n\nContinue lutando conosco, &lt;name&gt;, e um dia nós expulsaremos o próprio Lich Rei de Azeroth!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCajado LenhanegraRacha-osso\n\nVocê também receberá:\n\n"},
[409] = {title = "Prova de lealdade", objective = "Obtenha uma Vela de Sinalização.\n\nEvoque e mate Lillith Nefara.\n\nFale novamente com Tertuliano em sua ilha.", description = "Você diz que não está sob o jugo do Lich Rei, e que o encantamento que Boanerges colocou no meu livro corrobora isso. Mas, antes que eu acredite nisso, preciso de uma prova de que você é <um inimigo/uma inimiga> do Flagelo.\n\nExiste um antigo altar manchado de sangue ao sul da costa desta ilha. Ele é usado por uma poderosa agente do Flagelo, Lillith Nefara.\n\nPegue uma Vela de Sinalização desse caixote. Acenda-a no altar e Lillith despertará. Destrua Lillith e eu acreditarei que você é <um adversário/uma adversária> do Lich Rei.", progress = nil, completion = "Você derrotou Lillith Nefara, provando que é um inimigo do Flagelo. Incrível.\n\nEu acreditava ser o único morto-vivo com livre arbítrio, mas você me mostrou que há outros que superaram o jugo do Lich Rei.", rewards = "Você receberá:\n\n"},
[410] = {title = "A sombra dormente", objective = "É uma antiga mesa manchada de sangue.", description = nil, progress = "É uma antiga mesa manchada de sangue.", completion = "Você coloca a Vela de Sinalização sobre a mesa e a acende...", rewards = nil},
[411] = {title = "A volta do Lich pródigo", objective = "Leve a Gema Etérea para Boanerges Stalactus na Cidade Baixa.", description = "Leve esta Gema Etérea para o meu velho amigo Boanerges. Ela permitirá que nos comuniquemos... Eu tenho muito o que tratar com ele.", progress = "Você devolveu o livro ao Tertuliano? Ele respondeu?", completion = "Você o convenceu a se juntar a nós! Você realizou um grande feito para os Renegados, &lt;name&gt;. A Dama Sombria será informada disso.", rewards = "Você receberá:\n\nBroquel de Osso"},
[412] = {title = "Operação remendão", objective = "Leve 8 Rodas de Reestabilização e 8 Equipamentos Giromecânicos para Razzle Molavivaz em Kharanos.", description = "Ao analisar a situação de Gnomeregan mais a fundo, parece que nós não só falhamos em erradicar os troggs, como também transformamos a maior parte da raça gnômica em gnomos leprosos do mal.\n\nOzzie e eu planejamos reverter esse efeito horrível com a nossa mais recente invenção: o Recombobulador. A máquina está quase completa, mas nós estamos desesperados atrás de rodas de reestabilização e equipamentos giromecânicos. Recupere alguns para nós com os gnomos leprosos na frente de Gnomeregan.", progress = "O Recombobulador vai estar a todo vapor assim que tivermos rodas de reestabilização e equipamentos giromecânicos suficientes.", completion = "Essas rodas de reestabilização e equipamentos giromecânicos estão perfeitamente de acordo com os diagramas do Recombobulador do Ozzie. Depois que eu colocar o hidrolubrificante nos pistões de combustão internos, ajustar a manivela-catraca e aumentar a viscosidade do gel eletrocolante, a raça gnômica vai voltar a ser o que era.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nLuvas para DirigirManto Manchado de Óleo\n\nVocê também receberá:\n\n"},
[413] = {title = "Cerveja Tremeluz", objective = "Entregue o Barril de Cerveja Tremeluz para o Montanhista Cervevada.", description = "Eu gostaria muito que meu irmão Wellart provasse da minha nova cerveja, a Cerveja Tremeluz. Ele é montanhista e está aquartelado na torre de guarda do sul, uma das duas torres na fronteira do Loch Modan.\n\nEle não é cervejeiro como os outros Cervevada, mas adora tomar umas e outras, e tenho certeza de que vai adorar esta aqui.\n\nVocê poderia entregar esse barril de Cerveja Tremeluz para ele?", progress = "Olá, <class>! O que traz você aqui tão longe?\n\nAlgo muito interessante, imagino. Faz dias que não entro em uma briga pra valer, e o meu barril de cerveja está quase vazio...", completion = "&lt;O montanhista Cervevada prova a Cerveja Tremeluz...&gt;\n\nAh! Uau! Essa coisa faz brilhar os olhos mesmo. Parece até que estou enxergando no escuro!", rewards = "Você receberá:\n\n"},
[414] = {title = "Cerveja para Kadrell", objective = "Entregue o Barril de Cerveja Tremeluz para o Montanhista Kadrell.", description = "Venho bebendo dessa tal cerveja Tremeluz que meu irmão fez – que coisa boa! Quero que um montanhista camarada meu também prove. O nome dele é Kadrell, e ele costuma patrulhar a estrada que corta Thelsamar, no Loch Modan.\n\nPara chegar a Thelsamar, vá para sudeste, atravesse o túnel e, então, quando chegar a uma bifurcação, pegue a estrada a nordeste que leva direto a Thelsamar.\n\nEntregue este barril de Tremeluz para o montanhista Kadrell e veja o que ele acha da cerveja!", progress = "Saudações, <class>, e bem-vindo a Thelsamar!", completion = "Bom, deixe-me tirar uma provinha dessa cerveja...\n\nPelas barbas de Magni! Esse troço faz a cabeça rodar! E também deixa as pernas mais leves.\n\nParece até que eu vou sair voando!", rewards = "Você receberá:\n\n"},
[415] = {title = "A nova cerveja do Rejold", objective = "Fale com Rejold Cervevada.", description = "O Rejold Cervevada, da Aldeia da Cevada, tem perguntado por você.\n\nEle quer conversar sobre uma nova cerveja que ele está inventando... E também disse algo sobre você ajudá-lo nessa empreitada. Talvez seja melhor voltar para a Aldeia da Cevada e ver o que ele quer com você.\n\nQuem sabe você ainda leva umas cervejas de graça nessa história...Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nCaneca de Cerveja Tremeluz"},
[416] = {title = "Caça ao rato", objective = "Leve 12 Orelhas de Ratatúnel para o Montanhista Kadrell em Thelsamar.", description = "Estamos com um baita problema de infestação no oeste. Kobolds Ratatúnel se mudaram para os contrafortes, fazendo tocas e sujando nossas terras!\n\nQueremos que eles sejam eliminados. Cace-os, traga-me as orelhas dele e ganhe sua recompensa.\n\nVocê pode encontrar kobolds Ratatúnel e suas tocas a oeste de Thelsamar, espalhados ao longo da encosta da montanha.", progress = "Olá, <name>. Como vai a caça ao rato?", completion = "Ugh! Quando pedi as orelhas daqueles ratos, eu não imaginava que elas cheirassem tão mal!\n\nAqui está a sua recompensa, &lt;name&gt;. Bom trabalho.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBraçadeiras de Pelego QueimadoBotas do Fidalgo\n\nVocê também receberá:\n\n"},
[417] = {title = "A vingança do piloto", objective = "Mate o Ronhagarra.\n\nEntregue a Pata Sarnenta do urso e o Diário de Hildelve para o Piloto Pisafundo.", description = "Hildelve escreveu no diário que foi atacado pelo Ronhagarra, um \"imenso urso Garra de Gelo\". Apesar de ferido gravemente pela fera, conseguiu espantá-la.\n\nA julgar pelo estado do corpo de Hildelve, Ronhagarra provavelmente voltou em seguida para matá-lo.\n\nAinda assim, Hildelve ainda teve forças para anotar no diário o derradeiro pedido:\n\nVingança contra Ronhagarra.", progress = "Olá, <name>. Tem notícias do meu amigo Hildelve?", completion = "São notícias muito tristes, pois Hildelve era um grande amigo. E como eu gostaria de estar lá quando você encontrou aquela fera, o Ronhagarra!\n\nObrigado, &lt;name&gt;. Sua façanha será lembrada pelos pilotos da Brigada de Cerco de Altaforja.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nAdaga do ArtíficeMartelo Compacto\n\nVocê também receberá:\n\n"},
[418] = {title = "Chouriço de Thelsamar", objective = "Leve 3 pedaços de Carne de Urso, 3 Tripas de Javali e 3 Linfas de Aranha para Vidra Fornalenha, de Thelsamar.", description = "Sempre tem uma barriga roncando aqui em Thelsamar: crianças que correm o dia inteiro, operários dos sítios arqueológicos que voltam de um dia de trabalho... Nossos chouriços são muito famosos, já provou?\n\nNão? Bem, por estas bandas, ninguém ganha um prato de comida de graça, e não pense que você vai ser exceção só por ser <um/uma> <class> <todo/toda> chique.\n\nEu vou precisar de carne de urso, tripa de javali e linfa de aranha. Arranje os ingredientes, e Vidra trata de cozinhar!", progress = "O fogo já está aceso, você trouxe os ingredientes?", completion = "Sério, é muito mais fácil do que parece...\n\nAh, eu nunca conheci alguém que tivesse tanto interesse em fazer chouriço, mas vou lhe dar a receita por precaução. Aí está. Lembre-se de comê-lo fresco!", rewards = "Você receberá:\n\nChouriçoReceita: Chouriço"},
[419] = {title = "O piloto perdido", objective = "Encontre o piloto Hildelve.", description = "Meu amigo Mori Hildelve, que também é piloto de máquina de cerco, desapareceu nas montanhas. Nós estávamos em campo procurando um minério raro para fazer pólvora de alta qualidade. Mas, durante as buscas, ele subiu um barranco com a máquina e o motor acabou explodindo!\n\nMori tinha certeza de que havia minério naquela serra, então me pediu para ficar vigiando as máquinas de cerco e seguiu a pé, procurando.\n\nMas isso já faz dias, e eu ouvi uns grunhidos alarmantes nas colinas à noite. O Mori é duro na queda, mas estou preocupado.\n\nPor favor, <name>, encontre meu amigo.", progress = nil, completion = "Você está diante do cadáver de um anão — despedaçado, congelado e devorado pelos carniceiros da montanha. A mão inerte ainda segura um caderno com anotações rabiscadas às pressas por Mori Hildelve Um colete de brigadeiro em bom estado envolve o esqueleto.\n\nEste cenário triste deve ter sido onde o piloto de máquina de cerco encontrou seu destino final.", rewards = "Você receberá:\n\nColete da Brigada de Cerco"},
[420] = {title = "Observações de Senir", objective = "Entregue o relatório de Grolin para Senir Barbabranca em Kharanos.", description = "Os troggs no túnel são extremamente hostis e não demonstraram a mínima hesitação em atacar os viajantes que tentam atravessar. Você parece <durão/durona> o suficiente, provavelmente vai ficar bem.\n\nAonde ir? Se você quiser falar com Senir, vai encontrá-lo em Kharanos.\n\nAtravesse o túnel e, depois que sair do outro lado, continue seguindo a estrada, que vai direto para Kharanos.", progress = "Como vai? Que tal beber uma comigo? Não tem muito o que fazer nesse frio.", completion = "Que é isso? Ah, o relatório do meu irmão. Hum...\n\nEu disse a ele pra não ficar usando o nome do rei, mas ele não me ouve. Não faz mal, eu acho, por mais que voem penas no Senado. Não que precisasse disso pras penas voarem por lá! Ha!\n\nContudo, acho que isso significa que eu também tenho que enviar meu relatório pra Altaforja, mas admito que estou um pouco atrasado. Maldita friagem.\n\nEu devo ter algum trabalho pra ti mais tarde, se tiveres interesse.", rewards = "Você receberá:\n\n"},
[421] = {title = "Prove seu valor", objective = "Dalar Tessalba, do Sepulcro, quer que você mate 5 Grenhalvas Lunafúria.", description = "Lady Sylvana encarregou Varimatras com a conquista das terras humanas e enânicas ao sul.\n\nMas aquele idiota, o Arugal — charlatão de Dalaran e agora besta amaldiçoada da Bastilha da Presa Negra — causou estragos com sua magia imprudente na fortaleza estratégica da Floresta de Pinhaprata.\n\nEu preciso de alguém com habilidade em combate para me ajudar a limpar a bagunça do Arugal. Prove seu valor a mim matando 5 grenhalvas lunafúria. Os monstros miseráveis podem ser encontrados fora da estrada, descendo colina abaixo.", progress = nil, completion = "Você me serviu bem, &lt;class&gt;. É óbvio para mim que você será um auxiliar digno para mim enquanto me preparo para eliminar a maldição de Arugal da Floresta de Pinhaprata.", rewards = "O seguinte feitiço será lançado em você:\n\nIntelecto Arcano\n\nVocê receberá:\n\n"},
[422] = {title = "A loucura de Arugal", objective = "Recupere o Remédio de Arugal para Dalar Tessalba, do Sepulcro.", description = "Eu não faço ideia de como Arugal ganhou aceitação dentro do Kirin Tor. Seu conhecimento de feitiços era tão transparente quanto um frasco de vidro soprado.\n\nO que importa agora é descobrirmos exatamente que magia Arugal usou para que eu possa virá-la contra ele e capturar a Floresta de Pinhaprata para a Dama Sombria.\n\nArugal ficou primeiro na fazenda de trigo ao norte da ponte. Um dos Sicários relatou ter visto alguns grimórios, mas não conseguiu pegá-los. Recupere para mim o feitiço intitulado Remédio de Arugal desses livros.", progress = "Antes de saber com o que estamos lidando aqui, <class>, preciso estudar o feitiço conhecido como Remédio de Arugal. Traga-o para mim agora ou serei forçado a procurar um servo digno.", completion = "É exatamente desse feitiço que eu estava atrás, &lt;class&gt;. Se sua dedicação à causa da Dama Sombria permanecer infalível, você descobrirá que tem um excelente futuro entre os Renegados.", rewards = "O seguinte feitiço será lançado em você:\n\nIntelecto Arcano\n\nVocê receberá:\n\n"},
[423] = {title = "A loucura de Arugal", objective = "Leve 6 Grilhões de Glutão e 3 Grilhões Almanegra para Dalar Tessalba, no Sepulcro.", description = "Depois de examinar o trabalho de Arugal, minhas piores suspeitas se confirmaram. O velho não era qualificado nem para limpar os penicos de Dalaran, muito menos para representar o Kirin Tor em sua hora mais terrível. Tolos!\n\nArugal usou itens encantados para reforçar sua magia fraca. Eu preciso examinar esses itens em primeira mão. Siga em frente e mate glutões Lunafúria e almanegras Lunafúria até que você tenha coletado o suficiente de seus grilhões encantados para a minha pesquisa. Aquelas criaturas vis foram avistadas ao norte e ao leste.", progress = "Eu vou precisar de 6 grilhões de glutão e 3 grilhões Almanegra antes de poder avaliar a situação e criar uma solução final para Arugal. agora, cumpra seu dever e mate glutões Lunafúria e almanegras Lunafúria até conseguir o que eu preciso.", completion = "Espero que eu consiga extrair energia suficiente de uma amostra tão limitada. Talvez eu devesse ter lhe pedido mais grilhões.\n\nNão obstante, você demonstrou grande habilidade em juntar estas, &lt;class&gt;.", rewards = "O seguinte feitiço será lançado em você:\n\nIntelecto Arcano\n\nVocê receberá:\n\n"},
[424] = {title = "A loucura de Arugal", objective = "Mate Severo, o Pálido, e leve sua cabeça para Dalar Tessalba no Sepulcro.", description = "De fato, levarei mais tempo do que eu pensava para descobrir os segredos obscuros por trás dos encantos que Arugal estava usando.\n\nMas, enquanto isso, preciso que você cuide de um probleminha que nossos Espreita-trevas descobriram. Parece que Arugal deixou sua magia se espalhar para a Mina Elenfunda, nas colinas a sudeste.\n\nA mina seria um bom recurso para o avanço de Varimatras.\n\nQuero que você decapite o capataz contaminado na mina, Severo, o Pálido. Com ele morto, a mina será nossa.", progress = "Você matou Severo, o Pálido? Varimatras não vai ficar contente se ignorarmos as ordens dele.", completion = "Excelente. A mina fornecerá recursos formidáveis para as nossas forças enquanto os espalhamos por toda Pinhaprata e por Azeroth.", rewards = "O seguinte feitiço será lançado em você:\n\nIntelecto Arcano\n\nVocê receberá:\n\n"},
[425] = {title = "Ivar, o Imundo", objective = "Mate Ivar, o Imundo, e leve a Cabeça de Ivar para Rane Yorick, que está na Plantação do Ivar.", description = "Meu irmão Quintino e eu fomos encarregados de reconhecer a Floresta de Pinhaprata e nos reportar ao Alto-executor Hadrec. Mas não chegamos longe. Quando estávamos passando por esta fazenda, fomos atacados pelo carniçal Ivar, o Imundo.\n\nNós escapamos de Ivar, mas ele atacou gravemente o Quintino. Ele está se curando com poção que você deu a ele, mas Ivar pode retornar antes de Quinn se recuperar.\n\nMatar Ivar para que Quintino tenha tempo de se curar. Ivar, geralmente, fica no celeiro, esperando eu baixar a minha guarda.", progress = "Você matou Ivar, o Imundo? Se você não fizer isso por mim e meu irmão, faça pelos Renegados.", completion = "Ah, obrigada. Você tem minha gratidão, &lt;name&gt;.\n\nSeu nome vai estar no relatório que vou entregar aos meus superiores.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBraçadeiras AcolchoadasCinto Surrado\n\nVocê também receberá:\n\n"},
[426] = {title = "Os moinhos invadidos", objective = "Colete 5 Costelas Carcomidas e 3 Crânios Enegrecidos, depois fale novamente com o Necroguarda Dinis em Montalvo.", description = "O Flagelo vem tentando estabelecer uma base nos Moinhos dos Agamand. Se eles conseguirem, poderão lançar mais ataques em Tirisfal.\n\nAs ordens foram dadas. O Flagelo deve ser erradicado dos Moinhos.\n\nVá até os Moinhos. Reúna costelas carcomidas dos soldados Range-ossos e dos soldados do Crânio Partido e crânios enegrecidos dos osteurgos Olhonegro.\n\nPara chegar aos Moinhos, siga a estrada a oeste. Depois de cruzar a ponte, vá para o norte na próxima bifurcação e siga nessa direção. Quando você avistar os moinhos, a batalha vai começar.", progress = "Nossas batalhas contra o Flagelo continuam, <name>. Faça sua parte e expulse os malditos mortos-vivos sem mente de volta para as Terras Pestilentas!", completion = "Seus esforços na guerra são preciosos para nós, &lt;name&gt;. Eu não vou lhe agradecer por fazer o que é necessário, mas os Necroguardas de Tirisfal se lembrarão de seu nome.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas AndacriptaCalças de Tecido Resistente\n\nVocê também receberá:\n\n"},
[427] = {title = "Guerra à Cruzada Escarlate", objective = "O Executor Zigano, de Montalvo, quer que você mate 10 Guerreiros Escarlates.", description = "Os documentos que o Executor Arren forneceu são exatamente a vantagem que precisávamos em nossa batalha contra a maldita Cruzada Escarlate. Agora sabemos suas localizações exatas nas Clareiras de Tirisfal.\n\nMas a Necroguarda tem preocupações maiores. O exército do Lich Rei cresce em número a cada noite. Precisamos de alguém com \"iniciativa\" como você para mandar a Cruzada Escarlate para a cova.\n\nProve que você é capaz de servir à Dama Sombria e vá para oeste, para a torre que fica depois da Fazenda dos Solliden, e mate 10 guerreiros escarlate.", progress = nil, completion = "Excelente, &lt;class&gt;. Sua perícia na arte do combate é inegável.", rewards = "Você receberá:\n\n"},
[428] = {title = "Sicários perdidos", objective = "Encontre os Sicários Quintino e Rane Yorick.", description = "Algumas semanas atrás, dois de nossos sicários, Rane e Quintino Yorick, foram enviados em uma missão de reconhecimento pelo norte de Pinhaprata. Não tivemos mais notícias deles.\n\nEles podem ter se tornado vítimas do Flagelo, e se isso tiver acontecido, eu quero uma confirmação. Encontre aqueles sicários.\n\nSeu primeiro objetivo era explorar as fazendas no norte de Pinhaprata. Você deve começar sua busca por lá.", progress = nil, completion = "Você nos encontrou! E bem na hora...", rewards = "Você receberá:\n\n"},
[429] = {title = "Corações selvagens", objective = "Junte 6 Corações Descoloridos de Worg e leve-os para o Boticário Renferrel no Sepulcro.", description = "Meu irmão Quintino foi ferido gravemente pelo carniçal Ivar, o Imundo, e eu não sei se ele vai conseguir se curar direito sem ajuda mágica. Embora eu não seja boticária, sei que é possível fazer poções de cura com corações descoloridos de worgs.\n\nJunte esses corações e leve-os para o Boticário Renferrel no Sepulcro, ao sul. Depois volte aqui com a poção.\n\nVocê vai encontrar muitos worgs no caminho daqui para o Horto dos Maldonado, a leste.", progress = "Meu tempo é curto, <class>, pois a Dama Sombria me encarregou de uma tarefa importantíssima.", completion = "&lt;Renferrel pega os corações.&gt;\n\nÉ preocupante ouvir que nossos Sicários falharam em sua missão.\n\nVamos torcer para que esses fracassos sejam raros.\n\nEu lhe darei a poção num instante, mas aqui está sua receita — se aqueles Sicários vierem a precisar de ajuda no futuro, talvez você mesmo possa prepará-la.", rewards = "Você receberá:\n\nReceita: Poção Descolorida de CuraPoção Descolorida de Cura"},
[430] = {title = "Reencontrando Quintino", objective = "Leve a poção para Quintino Yorick na Plantação do Ivar, ao norte do Sepulcro.", description = "Aqui está a poção para ajudar Quintino Yorick a se curar.\n\nAgora, se me dá licença, preciso voltar ao meu trabalho de suma importância...", progress = "Minha irmã diz que você veio nos ajudar. É verdade?", completion = "&lt;Quintino pega a poção e bebe.&gt;\n\nSim, já consigo sentir a poção fazendo efeito. Logo poderei viajar de novo.\n\nVocê me fez um imenso favor, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[431] = {title = "Velas de sinalização", objective = "Você pega uma das velas e guarda na mochila.", description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nVela de Sinalização"},
[432] = {title = "Malditos troggs!", objective = "Mate 6 Batecrânios Pedraqueixo para o Encarregado Pedracenho na Pedreira Gol'Bolar.", description = "Malditos troggs! Viraram meu sítio de cabeça para baixo! Vê só, lá se vai mais um barril de pólvora!\n\nAh, como eu queria ir até lá e esganar cada um daqueles pescocinhos de macaco magro! Quero acabar com todos!\n\nIsso não faz nada bem para a minha saúde... Muito stress, muita raiva... E o que eles deixaram para mim? Nada!\n\nEu daria cabo de todos eles sozinho, mas a minha mira não é mais a mesma. Então você é quem vai lá na pedreira para acabar com essa cambada de patifes! Pago bem! É pra matar mesmo, hein?", progress = nil, completion = "Bem, isso me fez sentir um pouco melhor...\n\nMas não vou sossegar enquanto eu não der cabo de todos! Todos!", rewards = "Você receberá:\n\n"},
[433] = {title = "O funcionário público", objective = "Mate 10 Quebraossos Pedraqueixo para o Senador Mehr Sacrapetra na Pedreira Gol'Bolar.", description = "Como membro do Senado e da Liga dos Exploradores, encarreguei-me de cuidar desta parte da infestação de troggs que assola nossas terras.\n\nSem o menor cabimento, eles causaram a maior desordem na Pedreira Gol'Bolar. Estávamos cavando as profundezas da terra quando, de repente, eles minaram como água lá de dentro, destruindo nossos equipamento e espantando os mineradores. Não há muito o que fazer além de exterminar todo o bando, reconstruir o que se estragou e voltar ao trabalho.\n\nSe você me ajudar com os troggs, receberá uma recompensa à altura.", progress = nil, completion = "Já é um começo. Espero que possamos aproveitar a vantagem para alocar uns Montanhistas na área e estabilizar a situação. Depois podemos começar a restaurar a nossa infraestrutura e mandar os mineradores de volta ao trabalho.\n\nObrigado pela ajuda, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[434] = {title = "O ataque!", objective = "Fiquei no jardim até que Lorde Gregor Lescobar e Marzon, a Lâmina Silente conversem. Depois, mate ambos antes de falar novamente com Elling Trias em Ventobravo.", description = "Vou pôr o disfarce no robô-espião e depois ele vai falar com Lescovar. Duvido que vá ter problemas para passar pelos guardas.\n\nEnquanto você estava buscando os materiais para mim, já cuidei de avisar a Marzon que já era hora dele e Lescovar se reencontrarem. Até deixei escapar umas informações falsas.\n\nLescovar vai dispensar os guardas e esperar Marzon aqui fora, no jardim. Depois que você entreouvir alguma coisa sobre Défias, ataque. Tem que matar os dois antes que os guardas retornem! Quando terminar, fale com Trias.", progress = nil, completion = "Está feito? Ótimo. Agradeço muito a sua discrição, &lt;name&gt;. Você provou o seu valor. Vou ficar de olho em você e no seu progresso dentro da Aliança.\n\nVeja se não se mete em confusão.", rewards = "Você receberá:\n\n"},
[435] = {title = "Uma escolta para Orlando", objective = "Escolte Orlando a salvo dos lobos até encontrar-se com Rane Yorick.", description = "Os lobos do lado de fora não vão embora, e me olham com maldade cada vez que eu ponho o pé para fora daquela porta. Eles devem estar com muita fome para me considerar uma refeição aceitável.\n\nPreciso me reportar à minha colega, a Sicária Rane Yorick. E para chegar até ela, vou precisar de sua ajuda para sobreviver aos lobos.\n\nPor favor, me escolte! E como tem muitos lobos lá fora, teremos mais chance de conseguir se você trouxer amigos para ajudar...", progress = nil, completion = "Obrigada pela ajuda, &lt;name&gt;. Estava com medo de Orlando ser derrotado pelos perigos de Pinhaprata. Com a sua ajuda, vejo que ele conseguiu se sair bem.", rewards = "Você receberá:\n\nEspada Curta do Sicário"},
[436] = {title = "Sítio de Escavação de Bandaferro", objective = "Fale com o Explorador Machadeiro.", description = "O Prospector Bandaferro lidera uma escavação de ruínas antigas a leste do lago. Nos últimos tempos, o progresso tem sido lento, ainda mais se considerarmos todos os suprimentos que mandamos para ele.\n\nBandaferro é um anão honesto e durão que se importa com os resultados, o que me deixa com receio de que haja forças trabalhando contra ele.\n\nVá à escavação de Bandaferro e fale com Magmar Machadeiro. Ele cuida dos detalhes da expedição e saberá o que está atrasando o processo.\n\nPara chegar ao sítio de escavação, contorne a ponta sul do lago e depois rume para leste.", progress = nil, completion = "Ah, o Jern quer saber o motivo do atraso, é?\n\nBandaferro está incluindo muitos detalhes no relatório de progresso mais recente, mas o que eu posso te dizer é isso:\n\nSão os Troggs!", rewards = "Você receberá:\n\n"},
[437] = {title = "Os Campos Estéreis", objective = "Mate gnolls Putricouro nos Campos Estéreis.\n\nQuando Vergasta aparecer, mate-a.\n\nLeve a Essência de Vergasta para o Alto-executor Hadrec no Sepulcro.", description = "Os relatórios iniciais dos batedores de Pinhaprata mostram que os Putricouro têm uma base na velha fazenda ao norte, os Campos Estéreis. Já mandamos pequenas investidas contra eles, mas eles sempre recebem reforços da banshee Vergasta, que usa sua magia para nos derrotar.\n\nQueremos que Vergasta seja eliminada.\n\nVá aos Campos Estéreis e ataque os Putricouro. Se não vir Vergasta, vá matando os Putricouro até que ela apareça, e então, derrote-a.\n\nA essência dela virará pó. Traga o pó como prova.", progress = "Já derrotou Vergasta, <name>? Nosso sucesso contra os Putricouro depende da destruição dela!", completion = "Ouvi falar do seu ataque bem-sucedido contra os Putricouro, &lt;name&gt;, e esta essência comprova a morte de Vergasta.\n\nHoje o Flagelo se enfraqueceu. E seu valor para os Renegados só aumentou.", rewards = "Você receberá:\n\nBotas de Reconhecimento"},
[438] = {title = "Os Campos Apodrecidos", objective = "Vá aos Campos Apodrecidos.\n\nDescubra o motivo pelo qual o Flagelo está lá.", description = "<name>, descobrimos um novo covil do Flagelo: os Campos Apodrecidos. Os campos ficam a leste do Sepulcro, e o território é fortemente patrulhado pelos Putricouro e outros mortos-vivos.\n\nDeve haver um motivo para que o Flagelo ocupe a área. Vá aos Campos Apodrecidos e descubra por que o Flagelo esta lá.", progress = nil, completion = "Neste barco há uma pilha de cadáveres cobertos. E do outro lado do Espelho de Lordaeron, a leste, vê-se uma doca na Ilha de Fenris.\n\nO destino dos cadáveres deve estar na Ilha de Fenris.", rewards = "Você receberá:\n\n"},
[439] = {title = "Pistas dos Putricouro", objective = "Leve o Anel de Deliah para o Alto-executor Hadrec no Sepulcro.", description = "Dentre os cadáveres surge uma mão, descoberta. É a mão de uma mulher, com marcas da peste na pele, mas relativamente fresca.\n\nHá um anel na mão. Removendo-o e inspecionando-o, vê-se a gravação que diz:\n\n\"Para Deliah\"", progress = "<name>. Você retorna, então posso presumir que sua investigação foi bem sucedida?", completion = "Você disse que este anel veio de uma mulher que morreu recentemente?\n\nA única fonte próxima para um cadáver seriam as covas coletivas de Tirisfal. Os gnolls Putricouro devem estar levando esses cadáveres para a Ilha de Fenris!", rewards = "Você receberá:\n\n"},
[440] = {title = "O anel gravado", objective = "Leve o Anel de Deliah para o Magistrado Sevren em Montalvo.", description = "Se você quer encontrar o marido ou o ente querido que deu o anel para Deliah... então fale com o Magistrado Sevren em Montalvo. Ele deve ter os arquivos das mortes recentes e, se você tiver sorte, ele pode conseguir descobrir quem é Deliah.\n\nMas eu não teria muitas esperanças. Quem quer que tenha dado o anel a ela provavelmente está morto. E se for morto-vivo, então provavelmente não se importa mais com Deliah.", progress = "Olá, <name>. O que traz você de volta a Montalvo?", completion = "Pouco tempo atrás havia, sim, uma Deliah em Montalvo. Ela foi enterrada na cova coletiva, mas se o que você diz é verdade, os gnolls Putricouro devem ter roubado o corpo dela e levado para Pinhaprata.\n\nOs Putricouro, como todo o restante do Flagelo, precisam ser destruídos. Confio que você esteja tomando todas as medidas para que isso aconteça.", rewards = "Você receberá:\n\n"},
[441] = {title = "Olavo e a Cidade Baixa", objective = "Leve o Anel de Deliah para Olavo.", description = "Deliah tinha um marido, Raleigh, que agora mora na Cidade Baixa. Se a Deliah que morava em Montalvo era mesmo a dona deste anel, então Olavo vai saber.\n\nProcure Raleigh no Centro Comercial da Cidade Baixa.", progress = "Eu conheço você?", completion = "Sim, este anel era mesmo da minha mulher. Ela teve o destino de tantos que não conseguiram suportar os duros tempos em que vivemos. Não sinto nada pela morte dela, nem pela história que você contou sobre o corpo ter sido roubado.\n\nTais sentimentos já congelaram há muito tempo dentro de mim. Mas o desejo ainda queima no meu coração inanimado, &lt;name&gt;.\n\nO desejo de vingança.", rewards = "Você receberá:\n\n"},
[442] = {title = "Ataque à Ilha de Fenris", objective = "Leve a Cabeça de Thule para o Alto-executor Hadrec no Sepulcro.", description = "Agora chegou a hora de atacar o bastião do Flagelo em Pinhaprata: a Ilha de Fenris.\n\nVocê deve voltar à Ilha e destruir Thule Corvinalle. Quero a cabeça dele!\n\nEsta missão é tão perigosa quanto vital, <name>. Você não deve falhar.", progress = "Não podemos permitir que o Flagelo continue reinando em Pinhaprata, <name>. Volte quando tiver uma prova da morte de Thule.", completion = "A morte de Thule Corvinalle foi um evento de peso, &lt;name&gt;. Renferrel me contou que, quando você destruiu Thule, as energias libertadas chegaram a ser sentidas até mesmo no Sepulcro.\n\nSuas ações reverberam entre as fileiras dos Renegados. Para nós, o seu valor só cresce.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nGrande Veste do ÁrbitroLançagarra\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[443] = {title = "Linfa de Putricouro", objective = "Leve 8 garrafas de Linfa de Putricouro para o Boticário Renferrel no Sepulcro.", description = "Antes do ataque à Ilha de Fenris, temos que determinar quem está por trás dos gnolls Putricouro. Nossos boticários acreditam que a origem dessa corja esteja no fluido estranho que corre pelo corpo deles.\n\nVá à Ilha de Fenris, a leste dos Campos Apodrecidos, e colete linfa de Putricouro nos gnolls que encontrar lá. Depois leve a linfa para o Boticário Renferrel, que fica do lado de fora do Sepulcro.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[444] = {title = "A origem dos Putricouro", objective = "Leve a Amostra de Linfa para Boanerges Stalactus, na Cidade Baixa.", description = "Reduzi a linfa que me trouxe e separei seus componentes básicos. Minhas descobertas ainda não são conclusivas, mas consegui determinar que houve magias de bruxo e de necromante envolvidas na criação da linfa.\n\nVou continuar estudando o material e, enquanto isso, quero que você leve uma amostra dela para Boanerges Stalactus, na Cidade Baixa. Boanerges não é boticário, mas seu conhecimento em magia pode nos ajudar a desvendar o mistério dos Putricouro.\n\nAgora vá. Procure Boanerges no Distrito da Magia, na Cidade Baixa.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[445] = {title = "Entrega na Floresta de Pinhaprata", objective = "Leve as descobertas do Boticário Joaquino ao Boticário Renferrel na Floresta de Pinhaprata.", description = "O tempo é um luxo ao qual nós, os servos de Lady Sylvana, não podemos nos dar. A esta altura, você há de saber disso muito bem.\n\nComo membro da Real Sociedade de Boticários, tenho o dever de compartilhar meu conhecimento com meus colegas, para que um dia os nossos esforços coletivos resultem na Nova Peste que a Dama Sombria tanto deseja.\n\nLeve essas descobertas para o Boticário Renferrel, que fica no Sepulcro, na Floresta de Pinhaprata. Siga a estrada na direção sul a partir de Montalvo. Ao chegar na Cidade Baixa, siga para o sudoeste.", progress = "O que você quer comigo, <class>?", completion = "Ah, que bondade do Boticário Joaquino ter mandado a pesquisa dele. Com tantos novos acontecimentos aqui em Pinhaprata, quase me esqueci das descobertas vindo de Lordaeron e das Clareiras de Tirisfal. O que me faz lembrar que preciso levar essas amostras para a Necrópole em breve.\n\nMas perdoe minhas divagações. E estenda sua estadia aqui em Pinhaprata, &lt;name&gt;. Um &lt;class&gt; vigoroso como você seria bastante útil por aqui.", rewards = "Você receberá:\n\n"},
[446] = {title = "Thule Corvinalle", objective = "Leve o Pergaminho de Boanerges para o Boticário Renferrel no Sepulcro de Pinhaprata.", description = "Este icor foi criado por um antigo colega meu: Thule Corvinalle. Ele era um mago jovem e promissor antes da grande guerra, mas quando o Lich Rei subiu ao poder, ele traiu seus irmãos e aliou-se ao Flagelo.\n\nNão sabia que ele ainda estava vivo, nem que liderava os Putricouro em suas incursões contra nós!\n\nConheço muitos dos feitiços dele e posso ajudar a preparar uma poção para combatê-los. Leve este pergaminho com os encantamentos necessários para a poção até o Boticário Renferrel.", progress = "Saudações, <name>. O que Boanerges tem a dizer?", completion = "Este pergaminho está imbuído de uma magia poderosa. Nunca usei tais forças em uma poção... será uma mistura potente e deverá lhe servir bem caso decida atacar Thule em seu baluarte na Ilha de Fenris.", rewards = "Você receberá:\n\nPoção de Boanerges"},
[447] = {title = "Receita mortal", objective = "Colete 6 Corações de Urso Cinzento e 6 amostras de Sangue de Pateante e leve-os para o Mestre-boticário Faranello na Cidade Baixa.", description = "Os números de Arthas são impressionantes. Mas, com uma nova praga, poderíamos erradicar o Exército do Flagelo e a infestação humana de uma vez por todas.\n\nMeus estudos provaram que sangue de aranha combinado a uma toxina derivada do coração de um urso cinzento resulta em um elixir mortal. Colete amostras das aranhas da Penumbra Furtiva a noroeste e dos ursos da Floresta de Pinhaprata. Entregue os reagentes ao Mestre-boticário Faranello da Real Sociedade de Boticários, na Cidade Baixa.", progress = "Salve, <class>. Que negócios você tem com a Real Sociedade de Boticários?", completion = "Que bondade do Boticário Renferrel enviar você até mim, &lt;name&gt;. Essas amostras serão mesmo úteis para a causa. Vou começar a estudar suas propriedades tóxicas e contaminantes imediatamente.", rewards = "Você receberá:\n\nRaiz de Senggin"},
[448] = {title = "Relatório para Hadrec", objective = "Apresente-se ao Alto-executor Hadrec no Sepulcro.", description = "Antes de lançar um ataque à Ilha de Fenris, apresente-se ao Alto-executor Hadrec. Ele pode ter equipamentos ou informações adicionais de que você vai precisar.", progress = nil, completion = "Se o que Boanerges diz é verdade, então Thule Corvinalle é uma força que precisa ser contida e rápido.\n\nPara ajudar nessa tarefa, você pode pegar algo do nosso arsenal aqui...", rewards = "Você vai poder escolher uma dessas recompensas:\n\nContrapeso GrosseiroPedra de Afiar Grosseira\n\nVocê também receberá:\n\nKit de Armadura Média"},
[449] = {title = "Relatório dos Sicários", objective = "Apresente o Relatório do Sicário ao Alto-executor Hadrec no Sepulcro.", description = "Embora Quintino esteja se curando dos ferimentos, quero apresentar o nosso relatório ao Alto-executor Hadrec o mais rápido possível. Temos informações que são vitais para a ocupação de Pinhaprata pelos Renegados.", progress = "Você descobriu o destino dos Sicários?", completion = "Não tinha me dado conta do nível da presença de Flagelos que vimos na Floresta de Pinhaprata, nem dos problemas que nossos Sicários tiveram em suas missões de reconhecimento.\n\nSua ajuda aos nossos Sicários foi inestimável, &lt;name&gt;. Seus esforços serão louvados.", rewards = "Você receberá:\n\n"},
[450] = {title = "Receita mortal", objective = "Recupere o Diário do Boticário Berardo em Vilarejo Lenhardente e leve-o ao Boticário Renferrel no Sepulcro.", description = "Você ainda por ser útil para a Real Sociedade de Boticários, <name>.\n\nQuando Arugal lançou sua magia vil na floresta, amaldiçoou um dos nossos membros mais promissores, o Boticário Berardo. Agora que virou um servo irracional de Arugal, Berardo é inútil para nós. Mas suas descobertas podem valer a pena. Ele estudava uma praga antiga que exterminou toda a vida aquática no Espelho de Lordaeron anos atrás.\n\nEncontre o Diário de Berardo em seu escritório na Estalagem de Vilarejo Lenhardente e leve-o ao Boticário Renferrel.", progress = "Vejo que você está de volta, <name>.", completion = "O Diário do Boticário Berardo! Ora, você é bastante valente, &lt;name&gt;. Agora vamos ver o que Berardo descobriu antes de enlouquecer...", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[451] = {title = "Receita mortal", objective = "Leve 6 amostras de Musgo de Rastejante do Lago, 6 amostras de Musgo de Sorrateiro do Lago e um Tumor Endurecido para o Mestre-boticário Faranello na Cidade Baixa.", description = "De acordo com o Diário de Berardo, o musgo dos Sorrateiros e Rastejantes do Lago pode ser útil em nossa missão de criar uma nova praga. Essas criaturas habitam as ilhas do Espelho de Lordaeron, a leste.\n\nEle também menciona um Tumor Endurecido muito raro que descobriu em um murloc Pinavil. Aparentemente, o tumor é tão raro que Berardo só conseguiu um.\n\nColete amostras do musgo e do raro Tumor Endurecido, e leve-as ao Mestre-boticário Faranello, da Cidade Baixa.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nBeberagem do Brilho da NoiteManto à Prova de Ácido\n\nVocê também receberá:\n\nElixir de Fortitude MenorPoção de Rapidez"},
[452] = {title = "Emboscada de Lenhardente", objective = "Ajude a Sicária Fabiana a eliminar o Conselho de Lenhardente.", description = "Você chegou aqui na hora certa, <name>!\n\nO Conselho de Lenhardente voltará a qualquer momento. Arugal tem outros assuntos para tratar na fortaleza. Ele faz do Conselho meros fantoches para manter os asseclas estúpidos de Lenhardente sob seu controle. Mas acredito que, sem a orientação do Conselho de Lenhardente, os filhos de Arugal se voltarão contra o mestre.\n\nNão consigo cuidar de todos eles sozinha. Preciso da sua ajuda. Juntos, podemos acabar com o Conselho para a Grande Dama Sylvana.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nEscudo de FaerleiaCalças de Couro EsticadasXale Místico\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[453] = {title = "À procura do Vulto Sombrio", objective = "Encontre o Vulto Sombrio. Suas pistas:\n\nEle não é nativo de Vila Sombria.\n\nÉ uma pessoa nervosa e agitada.\n\nEle deixou Vila Sombria e seguiu para o oeste.", description = "Boa sorte, <name>. Espero que você encontre o sujeito, e espero que ele esteja vivo.\n\nSe ele deixou a segurança de Vila Sombria, então pode ter se tornado presa das feras selvagens.", progress = "Quem é você??? Me deixe em paz!", completion = "&lt;Medrisco suspira...&gt;\n\nSim, eu sou o autor desse livro. Você finalmente me encontrou, hein?\n\nBem, se você leu o diário... então só sabe de metade da história!", rewards = "Você receberá:\n\n"},
[454] = {title = "O rescaldo da emboscada", objective = "Fale com Miran.", description = "A gente viajava para cima e para baixo entregando barris de pólvora até o... acontecido. Agora, temos que dividir o trabalho: Miran leva os barris e eu fico vigiando os restantes.\n\nMas não me sinto seguro em deixar o Miran levar o restante do carregamento sozinho. Quando ele estiver pronto para partir, você poderia escoltá-lo nessa próxima viagem?", progress = nil, completion = "Olá, &lt;class&gt;.\n\nVocê vai me acompanhar até o sítio arqueológico? Que bom ouvir isso... Depois de Saean e seus camaradas, ninguém sabe o que os Ferro Negro podem estar tramando.\n\nDe qualquer forma, só falta eu fazer uma checagem final. Fale comigo novamente quando você estiver pronto para partir.", rewards = "Você receberá:\n\n"},
[455] = {title = "Sufoco em Algaz", objective = "Atravesse Dun Algaz.\n\nMate 8 Batedores Presa do Dragão e 6 Brutos Presa do Dragão.\n\nApresente-se a Valstag Ferroqueixo no Porto de Menethil.", description = "Orcs do clã Presa do Dragão têm um refúgio no Pantanal, tchê. E agora eles se espraiaram para Dun Algaz! Não podemos deixar que se estabeleçam aqui ou Loch Modan ficará isolado do Porto de Menethil, nosso porto e baluarte no Pantanal.\n\nCruze Dun Algaz até o Pantanal além, e mate alguns orcs por via das dúvidas! Apresente-se a Valstag Ferroqueixo no Porto de Menethil.\n\nVocê chega a Menethil passando por Dun Algaz, depois seguindo ao norte ao longo da estrada, e a oeste na bifurcação.\n\nEstamos contando com você, <name>. Não falhe, gur<i/ia>.", progress = nil, completion = "Barbaridade, seu relatório está cheio de más notícias!\n\nVejo que os Presa do Dragão estão fazendo uma grande jogada em Algaz. Normalmente, os Presa do Dragão só se escondem nas colinas. O que os fez afluírem para Algaz ainda é um mistério...\n\nObrigado, &lt;name&gt;. E provavelmente vamos querer sua ajuda para pelear com esses orcs.", rewards = "Você receberá:\n\n"},
[456] = {title = "O equilíbrio da natureza", objective = "Mate 7 Sabres-da-noite Jovens e 4 Javalis-espinho Jovens e fale novamente com o Conservador Ilthalaine.", description = "Saudações, <name>. Meu nome é Conservador Ilthalaine. Meu propósito em Umbravale é garantir que o equilíbrio da natureza seja mantido.\n\nEste ano, as chuvas de primavera foram inclementes e, enquanto algumas feras se deleitavam, outras sofriam.\n\nInfelizmente, a população de sabres-da-noite e porcos-espinho cresceu de tal maneira que, se não a reduzirmos, Umbravale não terá comida suficiente para todas as criaturas. Vá, jovem <class>, reduza a população de porcos e sabres para que a harmonia da natureza seja mantida.", progress = nil, completion = "Você cumpriu bem suas tarefas, &lt;name&gt;.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nLuvas de Treinamento em Arco e FlechaBraçadeiras Folhaste\n\nVocê também receberá:\n\n"},
[457] = {title = "O equilíbrio da natureza", objective = "O Conservador Ilthalaine precisa que você mate 7 Sabres-da-noite Sarnentos e 7 Javalis-espinho.", description = "Reduzir a população mais jovem de criaturas aqui em Umbravale foi um bom começo, <name>, mas ainda há trabalho a fazer.\n\nOs recursos da floresta se esgotarão rapidamente se o problema não for resolvido. Matar feras da natureza é um mal necessário para o bem de todos os que compartilham a terra. Aventure-se pela floresta e mate sabres-da-noite sarnentos e javalis-espinho para garantir o equilíbrio.", progress = nil, completion = "Você provou sua dedicação à natureza, &lt;name&gt;. Um jovem &lt;class&gt; como você tem um futuro promissor.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nManto DrapejadoCinto de Couro Enegrecido\n\nVocê também receberá:\n\n"},
[458] = {title = "A protetora dos bosques", objective = "Procure a dríade conhecida como Tarindrella.", description = "Que bom que você está aqui, <class>. Notícias estranhas chegaram aos meus ouvidos pelos sussurros dos espíritos da floresta.\n\nA misteriosa protetora dos bosques, Tarindrella, voltou a Umbravale. A presença da dríade não é sentida nas florestas de Kalimdor há anos. Algo certamente está errado se ela voltou a esta terra.\n\nProcure Tarindrella e veja o que ela faz em nosso bosque. Uma das sentinelas relatou tê-la visto a sudoeste de Aldrassil.", progress = nil, completion = "Então você conseguiu me encontrar, jovem &lt;race&gt;. Foi sábio da parte do &lt;class&gt; Melithar enviá-lo.", rewards = "Você receberá:\n\n"},
[459] = {title = "A protetora dos bosques", objective = "Colete 8 Limos Vis e leve-os para Tarindrella.", description = "Algo maligno está se formando nas florestas de Teldrassil. Procure ao longo das colinas, onde moravam os pacíficos pelursos. Eles abandonaram suas casas e estão se reunindo sob o nome da tribo Masca-pinho.\n\nApenas a corrupção do maligno Limo Vil poderia causar tal transformação. Os capetas e capetinhas infestaram a área e estão ameaçando os moradores de Umbravale. \n\nConfronte esses capetas e tinhosos, <name>, e veja se eles foram de fato apanhados pelo encantamento do maligno Limo Vil.", progress = "Confirme minhas suspeitas, <name>. Traga-me 8 Limos Vis.", completion = "O serviço que você prestou pelas criaturas de Umbravale é digno de recompensa, &lt;name&gt;.\n\nMas você confirmou meus receios. Se os capetas foram maculados pelo Limo Vil, não consigo nem imaginar o que aconteceu com os pelursos da tribo Masca-pinho que outrora moravam aqui.\n\nSe for a Dolanaar, valente &lt;class&gt;, procure o sábio &lt;class&gt; Athridas Mantursino. Ele compartilha nossa preocupação com o bem-estar da floresta.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nPerneiras das FrondesBotas de RastrearLuvas Vinosas\n\nVocê também receberá:\n\nErva Curativa"},
[460] = {title = "Descanse em pedaços", objective = "Leve a Cabeça de Alaric ao seu túmulo.", description = "Você não me parece um Putricouro. Que bom. Já tinha perdido as esperanças...\n\nMeu nome é Alaric, e um dia já fui o guarda-costas e o criado principal de Thule. Ele usou sua magia para me \"proteger\" da Peste de Morte-viva, mas em vez disso seu feitiço me deu uma vida-morta da qual não posso escapar. E depois de criar os malditos gnolls Putricouro, Thule me decapitou... e deu minha cabeça aos gnolls como brinquedo!\n\nPor favor, leve-me ao meu corpo. Acho que os gnolls o enterraram aqui no Castelo Fenris, perto do estábulo, em um túmulo não identificado.", progress = "Este túmulo foi cavado às pressas.", completion = "Obrigado, &lt;name&gt;. Eu temia que os gnolls tivessem destruído meu corpo. É bom saber que não fizeram isso.", rewards = "Você receberá:\n\n"},
[461] = {title = "O nicho escondido", objective = "Leve Alaric para a alcova escondida.", description = "Sei de um nicho escondido dentro da bastilha que guarda algo muito precioso para Thule e seus antigos colegas. Também guarda algo que você pode achar útil. Leve-me até lá!\n\nA alcova fica na torre sudoeste da bastilha, atrás de uma prateleira empoeirada.\n\nPegue meu corpo, mas deixe minhas botas. Elas nunca me serviram mesmo...", progress = "Este é o lugar! Leve-me mais perto...", completion = "Pronto, está aberto. Me deixe aqui. Passei os melhores dias da minha vida nesta bastilha, e a ideia de descansar no interior de suas paredes me conforta.\n\nFeche bem a alcova — não quero que os Putricouro me encontrem.\n\nAh, e leve isso. Você mereceu.", rewards = "Você receberá:\n\n"},
[463] = {title = "O Verdião", objective = "Encontre o Verdião.", description = "Você já ouviu falar do Verdião? Não está procurando por ele, está? Se estiver, enlouqueceu. Mas quem sou eu para impedir u<m/ma> tol<o/a> de encontrar a morte?\n\nSe você está mesmo procurando aquela fera, pode encontrá-lo no pântano, a leste da estrada onde há a bifurcação para Dun Modr. Ele está por lá entre os crocodilos... e coisa pior!\n\nE deixe seu dinheiro aqui. Você não vai precisar dele aonde está indo... e não vai querer lascar o dente do velho Verdinho no seu dinheiro quando te cortar ao meio, não é?", progress = nil, completion = "As raízes e samambaias falam bem de você, pequena criatura. Ainda que eu tenha muitos nomes, respondo somente a poucos. Mas você pode me chamar de Rethiel.\n\nE você tem que escutar bem... pois as terras choram suas feridas infeccionadas, e encarregarei você de curá-las.", rewards = "Você receberá:\n\n"},
[464] = {title = "Estandartes de guerra", objective = "Leve 8 Estandartes de Guerra Presa do Dragão para o Capitão Punhoforte.", description = "O clã órquico dos Presa do Dragão foi praticamente destruído no final da Segunda Guerra, depois que eles perderam o controle de seus dragões vermelhos de estimação, tchê. Os dragões não estavam lá muito felizes com seus antigos mestres e caçaram com gana os Presa do Dragão. Os poucos que restaram botaram a cola no lombo de Grim Batol para as montanhas a leste da Escavação de Whelgar.\n\nAgora eles ganharam força e têm um acampamento a nordeste de Menethil.\n\nQueremos que você ataque esse acampamento, gur<i/ia>. Traga-me os estandartes de guerra deles como prova.", progress = "Bah, você atacou o acampamento dos Presa do Dragão?", completion = "Boa luta, &lt;name&gt;. Os Presa do Dragão não representam mais a mesma ameaça de quando tinham dragões, mas ainda são ferrenhos oponentes, tchê.\n\nOs Presa do Dragão são liderados por Nek'rosh, filho de Nekros, que foi líder dos Presa do Dragão há muitos anos. Nekros usou uma magia poderosa para domar os Dragões Vermelhos, mas, quando a magia o deixou na mão, não adiantou chorar as pitangas... os dragões não ficaram nada felizes.\n\nNekros foi morto, e seu filho jurou vingar-se dos dragões que mataram o pai.", rewards = "Você receberá:\n\n"},
[465] = {title = "A artimanha de Nek'rosh", objective = "Destrua as Catapultas da Presa do Dragão.", description = "Enquanto tu peleavas contra os Presa do Dragão, descobrimos mais coisas sobre os planos deles.\n\nNek'rosh construiu catapultas e pretende sitiar Menethil! E, com os combates em Dun Algaz, os reforços não têm como chegar, tchê!\n\nTu precisas destruir essas catapultas num upa. Vai encontrá-las no acampamento dos Presa do Dragão.\n\nAqui, gur<i/ia>, toma isto para incendiá-las. Abra caminho até as catapultas e taque fogo nelas.\n\nDepois que terminar, encontre e mate Nek'rosh. Precisamos liquidar essa ameaça dos Presa do Dragão... pela raiz.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[466] = {title = "À procura de incendicita", objective = "Leve 6 cargas de Minério de Incendicita ao Piloto Marchapedra em Dun Morogh.", description = "Bah, ouvi dizer que um minério raro, incendicita, foi encontrado no Pantanal, em uma caverna a noroeste do Portão de Algaz. Incendicita é um material volátil e difícil de manipular sem mandar tudo pelos ares, mas tenho certeza de que, se for fundido da maneira correta, poderá produzir balas de canhão que garantirão um golpe tripotente!\n\nTraga-me minério de incendicita e encontrarei alguém corajoso o suficiente para trabalhá-lo.\n\nAh, para extrair esse minério de um veio, tu vai precisar ter habilidade em mineração. Se não entenderes do riscado, então... leva um amigo que entenda para aquela caverna!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nLuvas Manchadas de Cerveja"},
[467] = {title = "A busca de Marchapedra", objective = "Fale com o Piloto Marchapedra.", description = "O Piloto Marchapedra adora sua máquina de cerco, como todo piloto que se preza! E está sempre ansioso que é uma barbaridade para encontrar novas maneiras de melhorar o poder de fogo de seu tanque!\n\nEle ouviu rumores de um minério raro e está à procura de alguém que o encontre para ele, tchê. Se tu já estás começando a se coçar por uma aventura, então encontre Marchapedra na Garagem do Gradaço, em Dun Morogh, e veja o que ele quer, gur<i/ia>.", progress = nil, completion = "Olá, &lt;name&gt;. Então estás interessada na tarefa que tenho para ti, hein? É bom ver uma jovem &lt;race&gt; como você, cujo sangue ferve com a perspectiva de uma aventura, tchê!", rewards = "Você receberá:\n\n"},
[468] = {title = "Apresente-se ao Montanhista Rochapeixe", objective = "Fale com o Montanhista Rochapeixe.", description = "O Montanhista Rochapeixe está à procura de voluntários para uma missão cruzando Dun Algaz em direção ao interior do Pantanal. \n\nBah, se quiseres molhar as botas naquele condado encharcado ao norte, fale com Rochapeixe em Dun Algaz. Dun Algaz fica ao norte da estrada, além da torre de vigia.", progress = nil, completion = "Olá, &lt;class&gt;! Estás aqui para a missão de reconhecimento, guri? Muito bem!\n\nEspero que seu equipamento esteja em boas condições, porque essa missão de \"reconhecimento\" se transformou em algo bem mais confuso, tchê...", rewards = "Você receberá:\n\n"},
[469] = {title = "Entrega diária", objective = "Entregue o Pacote de Peles de Crocolisco a Iago Halberque, o curtumeiro, no Porto de Menethil.", description = "Saudações, viajante!\n\nBah, tu por acaso não está indo na direção de Menethil, está? Se não estiver, sugiro que pare por lá, é um lugar tribom para comprar mercadorias e até mesmo tomar uma cerveja e relaxar.\n\nAgora, se por acaso está indo mesmo para aquelas bandas, creio que não se importaria de levar esse pacote de peles de crocolisco para Iago Halberque, o curtumeiro?\n\nEu mesmo faria isso, mas minha esposa anda mais contrariada que potro com mosca no ouvido, e gostaria de evitar enfrentá-la pelo máximo de tempo possível.", progress = "Olá, <class>. Por acaso, te interessarias por um par de botas, guri? Quem sabe algumas peles recém-curadas?", completion = "Humm, isso veio de Einar, certo? Já faz quase uma semana que ele não me entrega pessoalmente as peles. Aposto que está tendo problemas com a esposa!\n\nTalvez eu peça minha mulher para falar com a Cida qualquer hora dessas...", rewards = "Você receberá:\n\n"},
[470] = {title = "Escavando a Gosma", objective = "A bolsa de Cida está em uma das gosmas na Tumba dos Barbaférreos. Recupere a bolsa e leve de volta para ela no Porto de Menethil.", description = "Na minha visita semanal à Tumba dos Barbaférreos, fui atacada por gosmas medonhas e gotejantes! Naturalmente, entrei em pânico, tchê.\n\nAtirei minha bolsa para cima de uma delas, mas bah, não aconteceu nada! A gosma só sugou a bolsa para dentro... Felizmente, isso me deu tempo de fugir.\n\nA má notícia é que preciso muito do que está na minha bolsa!\n\nNão sei em que gosma atirei minha bolsa... todas pareciam iguais! A Tumba dos Barbaférreos fica a nordeste daqui, perto de Dun Modr. Muito obrigada, gur<i/ia>!", progress = "Bah, encontraste minha bolsa?", completion = "Obrigada, &lt;name&gt;! Receava ter perdido a bolsa para sempre!\n\n\n\nBah, acho que não vou usar mais essa bolsa para nada, mas, se você lavá-la, tenho certeza de que poderá ser útil.", rewards = "Você receberá:\n\nBolsa Coberta de Gosma"},
[471] = {title = "Deveres de aprendiz", objective = "Colete 6 Peles de Crocolisco Gigante e leve-as para Iago Halberque no Porto de Menethil.", description = "Mandei meu aprendiz ao pântano para esfolar alguns dos nossos famosos crocoliscos gigantes do Pantanal. Quando ele não voltou, saí para procurá-lo... Digamos apenas que a visão que encontrei não foi nada bonita.\n\nA parteira diz que ele deve sobreviver, mas, nesse meio tempo, ainda preciso das minhas peles!\n\n...Se você quisesse matar mais algumas das feras, eu não me oporia, depois do que elas fizeram. Você encontrará os crocoliscos ao norte da costa, além das choupanas dos murlocs.", progress = "Meu aprendiz é como um filho para mim. Vai ser difícil para ele viver com apenas uma perna.\n\nAh, <name>, como vão as coisas? Você trouxe minhas peles?", completion = "Humm... Você por acaso não estaria interessado em se tornar meu aprendiz, não é?\n\nHá! Não, estou só brincando. Para começar, você é muito velho. Além disso, pelo seu jeito, imagino que tenha outras ambições na vida. Mas essas peles que você trouxe são muito boas, e farei bom uso delas.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nPerneiras Encadeadas MaleáveisPoncho Resiliente\n\nVocê também receberá:\n\nReceita: Bobó de Crocolisco"},
[472] = {title = "Queda de Dun Modr", objective = "Procure Trançalonga perto da cidade de Dun Modr.", description = "Barbaridade, Dun Modr está nas mãos dos Anões Ferro Negro!\n\nMeus ferimentos são sérios, <class>. A maior parte do regimento foi morto! Os brutamontes Ferro Negro nos atacaram antes que pudéssemos nos reagrupar após a emboscada da Ponte Thandol, tchê.\n\nNosso líder, Trançalonga, tocou a trompa de retirada. Ao sairmos da cidade, fui atingido por um machado nas costas. Tudo escureceu.\n\nAcordei aqui em Menethil, mas temo por meus colegas soldados. A esperança ainda arde dentro de mim. Talvez Trançalonga ainda esteja vivo! Veja se consegues encontrá-lo perto de Dun Modr, <race>!", progress = nil, completion = "Que valente &lt;class&gt; nos traz mensagem de Menethil? Ah, &lt;name&gt; é como o chamam.\n\nVocê traz esperança a uma situação desesperadora. Que bom saber que o jovem Lépore sobreviveu. Enquanto fugia, ele foi ferido gravemente. Só tivemos tempo de mandá-lo embora na garupa do único corcel sobrevivente do regimento.\n\nMas agora enfrentamos uma situação delicada. Dun Modr caiu e somos apenas uma sombra do grande regimento que um dia fomos.", rewards = "Você receberá:\n\n"},
[473] = {title = "Apresente-se ao Capitão Punhoforte", objective = "Fale com o Capitão Punhoforte.", description = "<name>, não só em Algaz que o clã órquico Presa do Dragão está mostrando sua força. O Capitão Punhoforte quer falar com quem estiver disposto a oferecer ajuda contra esses orcs. Ele está dentro da bastilha, na sala de guerra.", progress = nil, completion = "&lt;name&gt;, Vastalg me informou sobre o teu sucesso contra os orcs Presa do Dragão em Dun Algaz. Tu é um &lt;race&gt; de valor.\n\nE eu tenho um trabalho para ti.", rewards = "Você receberá:\n\n"},
[474] = {title = "Derrotar Nek'rosh", objective = "Mate o Chefe Nek'rosh.\n\nLeve a Cabeça do Nek'rosh ao Capitão Punhoforte.", description = "Agora que as catapultas queimaram, encontre e mate o Chefe Nek'rosh.", progress = "Tu matou o Nek'rosh?", completion = "A la pucha, &lt;name&gt;. Tu sentou o braço no clã órquico dos Presa do Dragão. Capaz de eles nunca se recuperarem. E a destruição daquelas catapultas deixou o Porto de Menethil mais seguro.\n\nTu é um &lt;race&gt; de respeito, e o reino de Altaforja recompensa aqueles que o servem bem.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nEspada Bélica AntigaCeifadeira de Tanoeiro\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[475] = {title = "Uma leve brisa", objective = "Encontre Garyol Talvethren no Vilarejo Brisastral.", description = "Uma leve brisa sopra da floresta.\n\nGaryol Talvethren trabalha como Grande Guardião para os hibernantes Druidas do Gadanho na Gruta de Ban'ethil. O trabalho dele como protetor escolhido dos Adormecidos é garantir a segurança destes para que seus pactos com Ysera sejam cumpridos.\n\nMas há muito não recebo notícias de Garyol e isto muito me preocupa. Vá para leste do Vilarejo Brisastral e traga alguma notícia de Garyol para que minhas preocupações acabem. Preciso saber que meu sonhador confrade dorme em segurança.", progress = nil, completion = "Graças aos espíritos da floresta você está aqui! Sabia que Athridas sentiria o perigo e me mandaria ajuda.", rewards = "Você receberá:\n\n"},
[476] = {title = "Corrupção Masca-pinho", objective = "Fale com Athridas Mantursino em Dolanaar.", description = "A tribo dos Masca-pinhos foi corrompida!\n\nOs pelursos, antes pacíficos, agora se voltaram contra os protetores da floresta. Eles me espreitaram no caminho para a Gruta de Ban'ethil e foram pilhar o Vilarejo Brisastral. O líder deles, Ursal, o Espancador, está usando os malignos poderes do limo vil para enlouquecê-los.\n\nEstou muito ferido para voltar e dar esta grave notícia para Athridas. Você precisa completar esta tarefa, jovem <class>. Tudo que podemos fazer é torcer para que os loucos Masca-pinho não tenham chegado a Ban'ethil!", progress = nil, completion = "Pelas estrelas! Isso é de fato muito perturbador!", rewards = "Você receberá:\n\n"},
[477] = {title = "Cruzando fronteiras", objective = "Recupere o conteúdo de um dos caixotes dos teurgos de Dalaran. Você vai encontrar o Vilarejo Lenhardente ao sul.", description = "Como você deve saber, a cidade de Lenhâmbar resiste como fonte de resistência humana, principalmente por causa do apoio que recebem dos teurgos de Dalaran.\n\nNão sei qual é o plano, mas o interesse deles em uma vila remota como Lenhâmbar indica que o lugar deve ter uma importância maior.\n\nEstão enviando carroças com caixotes de suprimentos a partir de Eira dos Montes. Vários desses suprimentos chegam a um pequeno acampamento a norte do Vilarejo Lenhardente. Recupere o conteúdo de um caixote e traga-o para mim.", progress = nil, completion = "&lt;Quando a tampa é aberta, você encontra um sortimento de objetos cuidadosamente embalados. Na parte de cima do caixote há um pacote de mapas, amarelecidos pelo tempo, que parecem mostrar a Floresta de Pinhaprata e outras regiões da parte oeste de Lordaeron.\n\nAbaixo dos mapas está um pingente mágico de aparência misteriosa.&gt;", rewards = "Você receberá:\n\n"},
[478] = {title = "Mapas e runas", objective = "Leve o estranho pingente para o Sacerdote Sombrio Allister, no Sepulcro.", description = "<Você retira o pingente do caixote e substitui a tampa.>", progress = "Já descobriu alguma coisa útil, <name>?", completion = "Mapas e esse... pingente, hum? Interessante.\n\nÉ óbvio que os teurgos de Dalaran estão interessados em Lenhâmbar por alguma razão, mas ainda não descobri a razão.\n\nTalvez Dalar saiba algo que interesse a eles.", rewards = "Você receberá:\n\n"},
[479] = {title = "Investigações de Lenhâmbar", objective = "Obtenha 8 Pingentes de Dalaran para o Sacerdote Sombrio Allister, no Sepulcro.", description = "Dalar está tentando localizar a fonte do lançamento do feitiço do teurgo. Por ora, precisamos fazer todo o possível para retardar o avanço deles...\n\nCom certeza os conjuradores, magos e protetores estão carregando os pingentes. Recupere-os.\n\nPegue a estrada principal para o sul e a bifurcação leste para Lenhâmbar.", progress = "Precisamos nos apressar, se os planos deles se concretizarem, vamos acabar perdendo o controle da Floresta de Pinhaprata.", completion = "Dalar descobriu que a trama está se alongando, apesar do revés.\n\nUm teurgo de grande poder está guiando a energia mágica. Ele precisa ser detido rapidamente.", rewards = "Você receberá:\n\n"},
[480] = {title = "O tecelão", objective = "Mate o arquimago de Dalaran, depois leve o cajado dele para o Sacerdote Sombrio Allister, no Sepulcro.", description = "Um arquimago de grande poder está supervisionando a reativação do foco de energia meridiana. Com o progresso que fizeram até agora, nossa única esperança é matá-lo. Vai ser difícil, pois com certeza ele é protegido por muitos outros magos e guardiões.\n\nVá e acabe com ele rapidamente. Quando ele estiver morto, traga o cajado dele para mim. Vá.", progress = "Depressa, o tempo está acabando, <name>!", completion = "A magia dos teurgos se dissipa, e as energias meridianas se desvanecem. Seus esforços renderam frutos, &lt;name&gt;, e conseguimos impor uma grande derrota a nossos inimigos e aos insuportáveis teurgos de Dalaran.\n\nPrecisamos estar atentos, no entanto, sabendo que uma fonte de energia dessas está ao alcance das mãos... Precisamos fazer planos para o futuro.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBastão de CerânioTúnica Camuflada\n\nVocê também receberá:\n\n"},
[481] = {title = "Análise de Dalar", objective = "Leve o pingente inscrito em runas para Dalar Tessalba, no Sepulcro.", description = "Leve o pingente para Dalar Tessalba, talvez ele consiga descobrir quais são os planos dos teurgos.", progress = "Pois não, <class>? O que foi?", completion = "Interessante, mais maquinações do Kirin Tor, ao que parece. É um artefato poderoso. Era usado pelos poderosos teurgos do Kirin Tor para canalizar energias mágicas.\n\nO fato de estarem mandando tanta energia para Lenhâmbar indica que eles devem estar trabalhando em um projeto grande.\n\nDeixe-me ver se consigo furar as defesas deles e descobrir o que pretendem.", rewards = "Você receberá:\n\n"},
[482] = {title = "As intenções de Dalaran", objective = "Fale com o Sacerdote Sombrio Allister, no Sepulcro.", description = "Claro! Com é que eu fui me esquecer?\n\nLenhâmbar foi um dos lugares marcados pelo Kirin Tor, porque abriga um foco de energia meridiana latente. Os teurgos devem estar planejando reativar o foco e usar as energias para algum propósito maior.\n\nNão podemos permitir quer isso aconteça...\n\nVai ser necessário um grande volume de força para ativar as energias meridianas da cidade. Se roubarmos os pingentes dos teurgos, nós conseguiremos deter o progresso deles.\n\nLeve essa informação para Allister, ele vai decidir o que fazer.", progress = nil, completion = "Más notícias você me trouxe, &lt;name&gt;. Se Dalar estiver correto, e tenho todas as razões para acreditar que está, precisamos agir rapidamente.\n\nPreciso discutir com ele nosso plano de ataque. Neste meio tempo, precisamos atrasar o progresso deles.", rewards = "Você receberá:\n\n"},
[483] = {title = "As relíquias do despertar", objective = "Recupere as Relíquias do Despertar e leve para Athridas Mantursino, em Dolanaar.", description = "Invasores Masca-pinho foram vistos saqueando a Gruta de Ban'ethil, a oeste.\n\nOs druidas adormecidos ficarão presos dentro do Sonho Esmeralda por toda a eternidade, inconscientes de seus destinos, a não ser que ajudemos. O delicado ritual de hibernação não pode ser interrompido sem as Relíquias do Despertar.\n\nVá à Gruta e recupere o Talismã de Garra de Corvo, o Cálamo de Pena Negra, a Safira do Céu e a Runa da Nidificação. Os druidas as guardam em baús sagrados. Traga-as para mim para que eu prepare o ritual do despertar.", progress = "<name>, os Druidas do Gadanho que foram sequestrados ficarão aprisionados para sempre no Sonho Esmeralda caso não consigamos recuperar as Relíquias do Despertar que estão dentro da Gruta de Ban'ethil, a oeste.\n\nA cada minuto que passa, o destino deles se aproxima da perdição eterna.", completion = "Você conseguiu, jovem &lt;class&gt;! Muito bem. E bem há tempo, devo dizer.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nPerneiras de Malha de TroncoCajado Pardo\n\nVocê também receberá:\n\n"},
[484] = {title = "Crocolisco jovem é que dá pele boa", objective = "Obtenha 4 Peles de Crocolisco Jovem para Iago Halberque, no Porto de Menethil.", description = "Sou Iago Halberque, o curtumeiro. Minha peles de crocolisco curadas são muito procuradas, com carregamentos sendo enviados de navio para portos de todo o mundo.\n\nConheceu o Einar? Ele é um dos caçadores que traz peles para mim.\n\nMeu estoque de peles mais macias, de crocoliscos jovens, está meio baixo. Por conta disso, prometo lhe pagar muito bem por uma pilha dessas peles. Os crocoliscos jovens são encontrados nos Pântanos que ficam logo na saída da cidade.", progress = "Você é <name>, certo? Trouxe as minhas peles?", completion = "Você fez um excelente trabalho, &lt;name&gt;. A qualidade dessas peles é excepcional. Vou colocar no processo de cura o quanto antes. Ah, mas, antes disso, tem um outro trabalho que você pode fazer para mim. O que você acha?", rewards = "Você receberá:\n\n"},
[485] = {title = "Encontre OOX-09/HL!", objective = "Leve o transmissor de emergência até o robô teleguiado de Olhatorto na cabeceira do rio, perto da Rocha Oculta, que fica nas Terras Agrestes.", description = "Você descobriu algum tipo de dispositivo estranho em formato de ovo feito de metal. Ao mexer em um dos botões, o ovo volta à vida e se abre formando um tipo de geringonça robótica gnômica! Uma voz do interior do ovo robótico ganha vida.\n\n\"Meu nome é Olhatorto Obnótico e meu robô teleguiado sofreu uma colisão! Eu <o/a> recompensarei se você o encontrar; leve este transmissor até o robô!\"\n\n\"Acabo de calcular as coordenadas do robô para você; ele caiu na cabeceira do rio, perto da Rocha Oculta!\"Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[486] = {title = "Ursal, o Espancador", objective = "Mate Ursal, o Espancador, e fale novamente com Athridas Mantursino, em Dolanaar.", description = "É chegada a hora de salvarmos os Druidas do Gadanho. Se falharmos agora, <name>, eles estarão perdidos para todo o sempre em seus próprios sonhos.\n\nPrepararei as Relíquias do Despertar e realizarei o ritual. Para que meu trabalho surta efeito, a fera amaldiçoada responsável por esta terrível situação deve ser morta. Só assim o ritual estará completo.\n\nFoi Ursal, o Espancador, quem intrometeu-se com o chamado de nossos confrades e é ele quem deve morrer para que eles sejam libertados. Siga até o acampamento Masca-pinho, a sudoeste, e mate-o.", progress = nil, completion = "&lt;name&gt;, você mostrou ser uma &lt;class&gt; muito confiável e capaz. Uma &lt;race&gt; que segue o caminho da honra como você certamente encontrará muitas glórias neste mundo.\n\nQue os espíritos da floresta a protejam aonde quer que as suas viagens a levem.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nMachado DefensorClava Raiz-espinhosa\n\nVocê também receberá:\n\n"},
[487] = {title = "A estrada para Darnassus", objective = "Mate 6 Emboscadores Masca-pinho e fale novamente com a Sentinela Amara Noctívaga fora de Dolanaar.", description = "Devemos manter a estrada para Darnassus segura. Os viajantes que seguem de Dolanaar para Darnassus vêm relatando ataques constantes, feitos por pelursos corrompidos da tribo dos Masca-pinho.\n\nMercadores e informantes utilizam essa estrada diariamente. Não podemos permitir que um bando de ladinos pagãos aterrorizem as pessoas.\n\nEmpunhe suas armas em nome da floresta sagrada, <class>. O covil deles fica em algum lugar abaixo desse ponto de observação. Mate seis desses emboscadores Masca-pinho e fale novamente comigo.", progress = nil, completion = "Você serviu ao bom povo de Dolanaar e de Darnassus, brava &lt;class&gt;. Como integrante da força Sentinela de Teldrassil, eu saúdo seus esforços.", rewards = "Você receberá:\n\n"},
[488] = {title = "O comando de Zenn", objective = "Leve para Zenn Cascovil, fora de Dolanaar, 3 Presas de Sabre-da-noite, 3 Penas Estrígidas de Coruja e 3 amostras de Seda de Aranha Lenhateia.", description = "<Ávido/Ávida> por trabalho, estou certo? Por sorte, não existe um dia sequer que eu não anseie por ter <um novato/uma novata> <class> para cumprir minhas tarefas.\n\nEntenda, <name>, posso lhe fazer muito feliz e lhe dar presentes que você nunca sonhou. Mas para isto, preciso de alguns itens.\n\nMeu trabalho na floresta muitas vezes requer certos... reagentes. Consiga-me 3 presas de sabre-da-noite, 3 penas estrígidas de coruja e 3 amostras de seda de aranha lenhateia.\n\nVamos fazer disso o nosso segredinho, <race>.", progress = "Tem sido uma boa formiguinha, <name>? Estou esperando que me traga o que pedi.", completion = "Ha ha! Excelente!\n\nQuem poderia imaginar que Zenn Cascovil teria um &lt;race&gt; fazendo o trabalho dele? Nem mesmo eu acreditaria! Enfim... este agradável mundo em que vivemos é cheio de surpresas.\n\nTrês vivas para os ingênuos e os crédulos!", rewards = "Você receberá:\n\nGarra de Vodu Partida"},
[489] = {title = "Consiga redenção!", objective = "Colete 3 Pinhas Vis e entregue-as para Zenn Cascovil, fora de Dolanaar.", description = "O Conselho da Floresta recebeu notícias de que você ajudou Zenn Cascovil. Os sátiros são inimigos da floresta. Como $Gum:uma; <race>, deveria saber que não se deve profaná-la, matando criaturas da Natureza.\n\nVocê deve conseguir redenção perante o Conselho caso pretenda permanecer amig<o/a> de Teldrassil.\n\nEnsine uma lição à Cascovil e você estará <redimido/redimida>. Pinhas vis são sementes corrompidas que caem das árvores. Elas produzem uma fumaça verde.\n\nDê algumas para Cascovil. Ele acreditará que você trouxe um petisco para ele.", progress = "O que você tem pra mim, <name>? Um delicioso petisco, é isso?", completion = "Ah, que &lt;race&gt; bondoso! Sabia que você me seria útil!", rewards = "Você receberá:\n\n"},
[491] = {title = "Uma boa varinha para Boanerges", objective = "Leve a Varinha Entretecida para Boanerges Stalactus, no Distrito da Magia da Cidade Baixa.", description = "Certa vez, Thule me contou que, num passado remoto, ele e dois colegas criaram uma varinha mágica, entretecida das varinhas de aprendiz de cada um dos três magos. A varinha era uma símbolo da amizade e, apesar de passar a odiá-la, Thule não podia se separar dela, nem deixar que os outros ficassem com ela. Por isso, ela a roubou e a escondeu no Castelo Fenris.\n\nTome, leve a varinha até Boanerges Stalactus, um dos amigos de Thule. Ele vai querer ficar com ela.", progress = "<name>. Você derrotou Thule Corvinalle?", completion = "Então foi isso? Nossa Varinha Entretecida estava com Thule? Pensei que havia se perdido quando a amizade acabou e ele se bandeou para o lado do Lich Rei!\n\nEle nunca invocou o poder da varinha, pois eu teria sentido se fizesse. E é bom que ele não a tenha usado...\n\nQue descoberta maravilhosa. Você tem minha gratidão, &lt;name&gt;, e será recompensado.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCalças EstampadasLuvas Grosseiras de MalhaFaca Serrilhada\n\nVocê também receberá:\n\n"},
[492] = {title = "Uma nova peste", objective = "Leve a Bebida Especial do Joaquino para o Montanhista Capturado.", description = "De acordo com a necroguarda, outro daqueles montanhistas anões foi capturado. A necroguarda gosta de usar a adega da Taberna Finda Forca como prisão provisória enquanto não é possível dar tratamento \"adequado\" aos prisioneiros.\n\nPor que você não vai lá e descobre se o montanhista capturado gosta deste aperitivo que fiz especialmente para ele? Isso é só um gostinho sutil daquilo que a Dama Sombria está preparando para o resto de Azeroth.", progress = "Ah, se eu tivesse meu rifle aqui comigo... Tu estaria morto, <class>! Espere só a Brigada do Tanque a Vapor chegar pra me resgatar!", completion = "Ah, um trago, finalmente! Não deve ser nenhuma Malte Rapsódia, mas estou aceitando qualquer coisa pra molhar o bico.", rewards = "Você receberá:\n\n"},
[493] = {title = "Jornada ao Contraforte de Eira dos Montes", objective = "Entregue as descobertas do Boticário Renferrel ao Boticário Lindolfo na cidade de Serraria Tarren, que fica no Contraforte de Eira dos Montes.", description = "A Real Sociedade de Boticários está sofrendo enorme pressão da Dama Sombria para desenvolver uma nova peste. Estamos trabalhando diligentemente, mas não fizemos muitos progressos. Acreditamos que conseguiríamos obter sucesso muito mais rápido de compartilhássemos informações entre a sociedade.\n\nPor essa razão, quero que você entregue minhas últimas descobertas ao Boticário Lindolfo, na Serraria Tarren, uma cidadezinha encarapitada no Contraforte de Eira dos Montes. A jornada vai ser longa.\n\nSiga para o sul e mantenha-se nas estradas. Siga as placas atentamente!", progress = "Você tem algo para mim?", completion = "Ótimo, ótimo. O Boticário Renferrel tem todo o meu respeito. Estou ansioso para estudar o trabalho dele.", rewards = "Você receberá:\n\n"},
[494] = {title = "Hora de atacar", objective = "Viaje até Serraria Tarren para entregar a mensagem do Sicário Açoute para a Alta-executora Darthalia.", description = "Fique em silêncio e preste atenção, <class>.\n\nFui mandado para esta posição com o objetivo de pesquisar a cidade de Eira dos Montes. Minha missão é de reconhecimento. É imprescindível que você leve notícias para a Alta-executora Darthalia na Serraria Tarren imediatamente. Diga-lhe que eu, o Sicário Açoute, enviei a seguinte mensagem:\n\n\"O grito do corvo do oeste chama.\"\n\nSiga a estrada para o leste e preste muita atenção às placas. Vá, vá! Siga para Serraria Tarren, é urgente!", progress = nil, completion = "O grito do corvo do oeste chama?\n\nExcelente notícia! Eira dos Montes está pronta para o ataque. Podemos executar nosso plano de destruição agora. Varimatras vai gostar disso.", rewards = "Você receberá:\n\n"},
[495] = {title = "A Coroa da Vontade", objective = "Apresente-se a Melissara, na Serraria Tarren, no Contraforte de Eira dos Montes.", description = "Mug'Thol e seu bando de ogros foi enviado a Alterac para resgatar a Coroa da Vontade e devolvê-la à Grande Dama Sylvana. Lamentavelmente, em vez de devolvê-la, o idiota resolveu experimentá-la... Acho que ogros não conseguem resistir a objetos brilhantes.\n\nEnfim, essa ação desastrada se tornou um problema. A Coroa da Vontade fez com que ele quebrasse a possessão que o obrigava a servir a grande dama.\n\nEla deseja que o ogro seja feito de exemplo. Viaje para a Serraria Tarren, em Eira dos Montes, e apresente-se a Melissara, minha tenente.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[496] = {title = "Elixir do sofrimento", objective = "Boticário Lindolfo, de Serraria Tarren, quer 10 Línguas de Urso Cinzento e um pouco de Pus de Rastejante.", description = "Ah, mais um dia maldito na Serraria Tarren. Todo esse ar fresco me deixa de mau humor, <name>.\n\nO quanto antes nós conseguirmos disseminar a peste entre os humanos daqui, melhor. Realizei estudos extensivos sobre possíveis agentes mortais a ser usados em minhas misturas, mas não tive tempo de coletar todos.\n\nQuer ser útil? Então procure os seguintes itens para mim: dez línguas de urso cinzento e uma coisa muito rara e difícil de achar, pus de rastejante. Ursos e rastejantes podem ser encontrados logo na saída de Serraria Tarren.", progress = "Então você voltou. Espero que tenha conseguido reunir os agentes de que preciso. Mais um dia sem mortes vai me deixar muito deprimido.", completion = "Estes espécimes são perfeitos. Bem, &lt;name&gt;, você conseguiu trazer para mim um pouquinho de alegria em mais um dia de tristeza. Seus esforços foram de grande ajuda.", rewards = "Você receberá:\n\n"},
[498] = {title = "O resgate", objective = "Krusk, da Serraria Tarren, precisa que você liberte Drull e Tog'thar do Forte do Desterro.", description = "Eu e alguns outros guerreiros fomos enviados pelo Chefe Guerreiro para recuperar um objeto do Desterro e investigar essa tal de Camarilha.\n\nFomos ao Desterro dar uma olhada, um grande número de humanos nos pegou de surpresa. Nós lutamos bravamente, mas eles estavam em número suficiente para nos derrotar. Eles levaram alguns de nós como prisioneiros, mas eu consegui escapar.\n\nO Desterro fica a sudeste daqui, do outro lado do rio. Drull e Tog'thar estão sendo mantidos lá, você precisa ir resgatá-los.", progress = nil, completion = "Então os humanos queriam transformar nossos guerreiros em escravos? Nós vamos mostrar a eles!\n\nParece que eles levaram Gol'dir, nosso líder, para outro local. Temos que encontrá-lo e resgatá-lo. Mas, no momento, não temos ideia de onde ele está...\n\nQuando tivermos uma pista, você vai saber o quanto antes, &lt;name&gt;.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nColete do BrutoCorrente Bélica Órquica\n\nVocê também receberá:\n\nReceita: Filé de UrsãoChave de Ouro DesluzidoChave de Ouro Lustroso"},
[499] = {title = "Elixir do Sofrimento", objective = "Dê o Elixir do Sofrimento a Umpi.", description = "Só vou precisar de um instante para misturar estes componentes no que eu espero que resulte em um Elixir do Sofrimento.\n\nLá vamos nós! Ah, nada ilumina um dia sombrio nestas colinas miseráveis como trazer um pouco de sofrimento ao mundo.\n\nVamos ver se a nossa pequena poção produzirá o efeito desejado.\n\nDê ao meu assistente, Umpi, uma amostra do que a Dama Sombria planejou, tanto para humanos quanto para o Flagelo.", progress = "Croac!", completion = "Croc!", rewards = "Você receberá:\n\n"},
[500] = {title = "Caça de Massacrista", objective = "Reúna 9 Nós dos Dedos Sujos dos Ogros Massacrista nas Montanhas de Alterac. Leve-os para o Delegado Trilharrubra, na Costa Sul.", description = "Os ogros Massacrista cavaram uma gruta de ogro nas Montanhas de Alterac, perto da cidade arruinada de Alterac. E meus batedores me disseram que eles também tomaram conta daquelas ruínas.\n\nNão podemos deixar que eles fiquem confortáveis lá em cima. Se eles acharem que estão seguros ali, o próximo passo será, então, descer as colinas, o que os colocará bem na soleira das nossas casas!\n\nVá para o norte, para as Montanhas de Alterac e cace ogros. Traga-me os nós dos dedos sujos deles, e você vai ganhar uma boa recompensa.", progress = "<name>, você ainda está caçando ogros? Conseguiu os nós dos dedos que eu lhe pedi para juntar?", completion = "Muito bom, &lt;name&gt;. Você deve ter dilacerado uma pilha enorme de ogros para conseguir esses nós dos dedos.\n\nIsso deve mantê-los longe de nós — pelo menos por enquanto.", rewards = "Você receberá:\n\n"},
[501] = {title = "Elixir da dor", objective = "Leve 10 Ampolas de Sangue de Leão da Montanha para o Boticário Lindolfo na Serraria Tarren.", description = "Ah, <name>... as flores estão desabrochando em Eira dos Montes, e o ar está tão fresco e vivo. Não posso deixar de me regozijar com a miséria em tudo isso. Olho pela minha janela encardida e anseio pelo dia em que nossa Nova Peste trará ao mundo a morte que ele merece.\n\nPor mais que eu adore ficar sentado refletindo o dia todo, meu dever para com a Dama Sombria me chama. Você poderia me ajudar? Minha pesquisa me leva a acreditar que posso criar a mais esplêndida bebida mortal com o sangue de um leão da montanha. Traga um pouco para mim e vamos nos divertir.", progress = "Eu me lembro de você, <name>. Você ia me ajudar a preparar o meu mais recente elixir. Eu vou precisar daquele sangue para pode levar a minha própria marca especial de sofrimento para os habitantes de Eira dos Montes.", completion = "Excelente coleta, &lt;name&gt;.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nLuvas da Força MuscularBotas da PisaduraBotas Andachama\n\nVocê também receberá:\n\nReceita: Costeletas Picantes de Leão"},
[502] = {title = "Elixir da dor", objective = "Alimente Fido com o Elixir da Dor.", description = "Ah, nada como o cheiro de sangue fresco no sopé dos montes!\n\nAgora vamos misturar um pouco disso e um pouco daquilo e começar a verdadeira diversão! O que eu criei com esse sangue, gosto de chamar de Elixir da Dor. Se meus cálculos estiverem corretos, essa mistura pode ser o começo de algo muito bonito para a Grande Dama Sylvana. Mas precisamos de um teste.\n\nLeve esse elixir para a fazenda no norte dos Campos de Eira dos Montes, que ficam a sudoeste. Vamos ver o que o cachorrinho do Fazendeiro Raí, o Fido, vai achar dessa \"guloseima\".", progress = "Au-au?", completion = "Você coloca um pouco do elixir na mão e a estica diante da boca do Fido.", rewards = "Você receberá:\n\n"},
[503] = {title = "Gol'dir", objective = "Liberte Gol'dir e fale novamente com Krusk na Serraria Tarren.", description = "Pelo que descobri nos documentos que você conseguiu, estão mantendo Gol'dir preso em uma base da Camarilha ao norte, muito provavelmente nas Montanhas de Alterac. Ele já está preso há muito tempo e precisa ser libertado.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[504] = {title = "Fomentadores da Guerra Massacrista", objective = "Mate 15 Fomentadores da Guerra Massacrista, depois fale novamente com o Delegado Trilharrubra na Costa Sul.", description = "Agora que você já deu um gostinho aos ogros Massacrista, eu quero que você os derrote de uma vez...\n\nVá para as Ruínas de Alterac e procure os Fomentadores da Guerra Massacrista. Quero que você reduza o número deles drasticamente — essa é a única forma de fazer aqueles brutos aprenderem a ficar longe do território da Aliança.", progress = nil, completion = "Muito bom. Aqui está sua recompensa.\n\nE, quando você foi lá caçar aqueles Fomentadores da Guerra, conseguiu ver como está cidade de Alterac? Imagino que esteja um desastre. Os ogros não são conhecidos pela limpeza.\n\nFelizmente, seus esforços ajudarão a mantê-los longe da Costa Sul.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nLasca ArdenteBroquel Lunar\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[505] = {title = "Assassinos da Camarilha", objective = "Mate 12 Salteadores da Camarilha e 8 Ladrões da Camarilha, depois fale novamente com o Magistrado Henrique Maleb na Costa Sul.", description = "Eu sou o novo magistrado da Costa Sul, designado recentemente após o assassinato do magistrado anterior. Os assassinos nunca foram encontrados, mas por meio de nossas investigações, estamos quase certos de que foram contratados pela Camarilha — um grupo de bandidos liderados pela vil nobreza do agora caído reino de Alterac.\n\nA Camarilha tem um acampamento ao norte da Serraria Tarren, ocupada pela Horda, e nossas últimas pistas levaram àquele lugar. Mate os membros da Camarilha que você encontrar lá.", progress = nil, completion = "Obrigado, &lt;name&gt;. Embora eles ainda possam estar à espreita, esperamos que alguns dos ladrões e salteadores que você matou tenham sido responsáveis pela morte do último magistrado. Mas, mesmo que não sejam, a Camarilha é uma mácula nesta terra e na raça humana.\n\nSuas mortes são bem-vindas para a Costa Sul.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCinto do CruzadoLuvas Isolantes do Sábio\n\nVocê também receberá:\n\n"},
[506] = {title = "O legado de Pantanegro", objective = "Leve as informações reunidas por Gol'dir para Krusk na Serraria Tarren.", description = "Um ou dois dias atrás, fui visitado pelo líder da tal Camarilha. Seu nome era Aliden Perenolde, e ele se intitulava senhor dessa terra. Ele me confidenciou seus planos de levar os orcs que se opunham a ele como escravos, exatamente como seu pai e mentor, o desprezado Pantanegro, havia planejado.\n\nO mais importante foi que ele trouxe sua amante, o fogo-fátuo de uma garota chamada Elysa. Pendurado no pescoço dela, estava o pingente que o Chefe Guerreiro deseja.\n\nVá agora; eu vou seguir o meu próprio caminho depois de ter resolvido meus negócios aqui.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[507] = {title = "Lorde Aliden Perenolde", objective = "Mate Lorde Aliden Perenolde e pergunte à sua concubina, Elysa, sobre o pingente de Taretha.", description = "Está claro que Perenolde precisa morrer e sua maldade ser extirpada do mundo. A mansão dele fica ao norte daqui, nas margens do Espelho de Lordaeron. Faça o seu trabalho, <name>, recupere o pingente que o Chefe Guerreiro deseja e, então, volte aqui.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[508] = {title = "O presente de Taretha", objective = "Devolva o pingente de Taretha a Krusk na Serraria Tarren.", description = "O pingente? Claro que você pode ficar com ele... Leve! Não quero guardar nenhuma lembrança dele... só vá embora em paz, por favor...Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas do MantídeoBrafoneiras do Bandoleiro\n\nVocê também receberá:\n\n"},
[509] = {title = "Elixir da Agonia", objective = "Leve 6 Cogumelos Fuçalama para o Boticário Lindolfo na Serraria Tarren.", description = "Mais um dia, mais um elixir. Por mim, eu quero mais é que os humanos patéticos e o Flagelo briguem. Eles já parecem determinados a matar uns aos outros mesmo.\n\nInfelizmente, nossa Dama Sombria quer ajudar a acelerar um pouco o processo, então, por que não passar esses dias mórbidos espalhando um pouco de morte e pestilência pela terra?\n\nO plano é o seguinte, <name>: você vai até o Sítio de Nethander, que fica ao sul da Serraria Tarren e a leste do rio e da Costa Sul, e colhe 6 cogumelos fuçalama do campo para que eu possa fazer meu próximo elixir.", progress = "Teve sorte no Sítio de Nethander?", completion = "Ah, o cheiro pútrido dos cogumelos fuçalama. Pelo menos para isso aqueles gnolls imundos servem.", rewards = "Você receberá:\n\nPoção de RapidezPoção de Cura"},
[510] = {title = "Planos agourentos", objective = "Leve os Planos Agourentos ao Magistrado Maleb na Costa Sul.", description = "Dentre estes documentos há uma lista de suprimentos necessários que vão de itens mundanos (comida, armas, roupas) a equipamentos mais exóticos (máscaras, óleo, veneno).\n\nPorém, o mais notável é a pressa do pedido. Os suprimentos são necessários com urgência para que se possa realizar aquilo que o documento descreve como \"uma importante missão contra Costa Sul.\"", progress = "<name>, você parece ansioso. Deseja me dizer alguma coisa?", completion = "&lt;Maleb lê os documentos.&gt;\n\nIsso é muito preocupante, &lt;name&gt;. Considerando as informações nestes documentos e as ações da Camarilha no passado, podemos presumir que um bando de assassinos declarou guerra contra Costa Sul.", rewards = "Você receberá:\n\n"},
[511] = {title = "Carta cifrada", objective = "Leve a Carta Cifrada para o Historiador Dibbs em Costa Sul.", description = "Uma das cartas nesta mesa está cifrada e é inescrutável.", progress = "Pois não, senhor? Tem negócios a tratar comigo?", completion = "O que é isso? Só um instante... deixe-me dar uma olhadinha.\n\n...\n\nHum, nunca vi esse tipo de criptografia antes, mas devo admitir que meu conhecimento nessa área está longe de estar completo.", rewards = "Você receberá:\n\n"},
[512] = {title = "Mortes nobres", objective = "Leve 7 Anéis-sinete de Alterac para o Magistrado Maleb na Costa Sul.", description = "Parece que a liderança da Camarilha está determinada a arruinar a Costa Sul. Temos que pegar os líderes de jeito antes que consigam concretizar esses planos.\n\nEntre nas ruínas de Strahnbrad, nas Montanhas de Alterac, que ficam ao norte daqui. Suspeitamos que a Camarilha está usando a região como quartel-general.\n\nSabemos que essa gangue de assassinos é formadas por nobres destituídos de Alterac. Podem estar tentando se passar por agentes normais da Camarilha, mas você saberá que matou um nobre ao ver o anel-sinete na mão dele.\n\nQuero que me traga esses anéis-sinete.", progress = "Você estava caçando a Camarilha? Já conseguiu aqueles anéis?", completion = "Eliminar a nobreza da Camarilha vai prejudicar o poder de reação dela, reduzindo a organização a um mero bando de bandidos.\n\nVocê serviu bem à sua Aliança, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[513] = {title = "O elixir da agonia", objective = "Leve o Composto Fuçalama para o Mestre-boticário Faranello na Cidade Baixa.", description = "Logo hoje, que eu acreditava ser mais um dia completamente desprovido de alegria, você me trouxe esses incríveis Cogumelos Fuçalama.\n\nPrimeiro vou usá-los para criar um composto. Depois vamos precisar que o Mestre-boticário Faranello, na Cidade Baixa, acrescente seu coloide da decomposição, e aí a nossa diversão vai começar!\n\nTome aqui, <name>, leve este Composto Fuçalama para o Mestre Faranello sem demora.", progress = "Não está vendo que estou muito ocupado, <class>? A Dama Sombria insiste que tenhamos uma nova peste antes que sejamos subjugados pelo avanço do Flagelo, vindo de Nortúndria. E com a ameaça humana nos pressionando ao sul, o tempo é meu bem mais precioso. Acho bom que seja importante!", completion = "O Boticário Lindolfo mandou você? Ora, por que não disse logo?\n\nApesar de seu mau humor, o Boticário Lindolfo é um dos nossos membros mais dedicados e habilidosos.\n\nO camarada me mandou Composto Fuçalama! Já entendi o que nosso amigo da Eira dos Montes está planejando. E aprovo com entusiasmo. A Dama Sombria ficará muito impressionada com o progresso dele na descoberta da nova peste.", rewards = "Você receberá:\n\n"},
[514] = {title = "Uma carta para Lançatroz", objective = "Leve a Carta Habilmente Cifrada para o Prospector Lançatroz em Altaforja.", description = "Tenho um colega em Altaforja que pode ajudar a decifrar essa carta. Vá até a Biblioteca no Salão dos Exploradores, em Altaforja, e fale com o Prospector Lançatroz. Diga que eu é que mandei você e mostre a carta. Se tem alguém ao norte do Pico da Rocha Negra que conseguiria ler isso aí, esse alguém é o Lançatroz.", progress = "Ah, foi o Dibbs, de Costa Sul, que mandou você, hein? Como vai aquele velho rato de biblioteca?", completion = "Ah, decodificação! Uma das minhas especialidades. Dá cá, deixe-me ver...", rewards = "Você receberá:\n\n"},
[515] = {title = "O elixir da agonia", objective = "Leve a Mistura Fuçalama, uma Poção Forte de Sangue de Troll, 5 Escamas de Espinhadaga e 5 Olhos de Rancapina para o Boticário Lindolfo, na Serraria Tarren.", description = "Um composto fuçalama? Brilhante! Como foi que eu não pensei nisso antes?\n\nTerei muito prazer em dar o meu coloide da decomposição como contribuição a esta mistura diabólica.\n\nPara ativar os agentes contaminadores desta mistura fuçalama, Lindolfo precisará de uma poção forte de sangue de troll, além de algumas escamas de espinhadaga e olhos de rancapina, encontrados na costa sul de Eira dos Montes. Leve todos esses reagentes para ele, que saberá o que fazer. Rapaz, mal posso esperar para saber o resultado do experimento!Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nManto da Grã-boticáriaFaixa Meditativa\n\nVocê também receberá:\n\n"},
[516] = {title = "Cilada de Beren", objective = "Encontre a Cilada de Beren, mate 6 Mourejantes Corvinalle e 6 Guardiões Corvinalle e depois fale novamente com o Sacerdote Sombrio Allister, no Sepulcro.", description = "Recebi relatos de que um ajuntamento de mortos-vivos estaria se preparando para nos atacar. De posse dessa informação, podemos virar o jogo e atacar primeiro, cortando o mal pela raiz de uma vez por todas.\n\nInfelizmente, meus dados são, na melhor das hipóteses, vagos. Acredita-se que eles estejam escondidos em um local conhecido como Cilada de Beren. Não se sabe ao certo onde fica, mas, ao que tudo indica, é uma caverna nas colinas, perto de um grupo de teurgos de Dalaran.\n\nConfio nas suas habilidades. Encontre e destrua os mortos-vivos.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nVarinha de Apodrecimento"},
[517] = {title = "O elixir da agonia", objective = "Leve o Barril de Stout do Festeiro para o Boticário Lindolfo na Serraria Tarren.", description = "Este novo elixir finalmente está pronto para ser testado. Mas por que desperdiçar uma praga perfeitamente boa e altamente contagiosa em um simples sapo ou cachorro? Vamos nos divertir para valer em nome da Grande Dama Sylvana.\n\nSiga até Dun Garok. Você sabe, o quartel onde perambulam aqueles anõezinhos sujos. Roube-me um barril de Stout do Festeiro. Assim que eu estiver com esse barril, nós dois daremos nossa festinha, <name>.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[518] = {title = "A Coroa da Vontade", objective = "Mate 14 Espancadores Massacristas para Melissara na Serraria Tarren.", description = "Mug'Thol e seus ogros Massacristas invadiram as ruínas de Alterac. Será difícil infligir qualquer punição séria a ele sem reduzir o número de ogros.\n\nDepois de amansá-los, prosseguiremos.\n\nEntendido? Ótimo. Agora vá.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[519] = {title = "A Coroa da Vontade", objective = "Mate Targ, Revirestrume e Glommus e leve a cabeça deles para Melissara na Serraria Tarren.", description = "Targ, Revirestrume e Glommus são tenentes leais de Mug'Thol, embora não se possa dizer o que leva um ogro à lealdade.\n\nTraga-me a cabeça deles, <name>, e então só será preciso cortar a de Mug'Thol, com coroa e tudo.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[520] = {title = "A Coroa da Vontade", objective = "Mate Mug'Thol, e leve a cabeça dele com a Coroa da Vontade para Melissara na Serraria Tarren.", description = "Com os tenentes de Mug'Thol mortos, só falta liquidar Mug'Thol e recuperar a Coroa da Vontade. Não tenho dúvidas de que ele relutará em se separar dela. E muito provavelmente está à sua espera, depois da... partida... dos companheiros dele.\n\nEncontre Mug'Thol, e traga cabeça dele e a Coroa para mim. A resistência Massacrista chegará ao fim.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[521] = {title = "A Coroa da Vontade", objective = "Entregue a Coroa da Vontade à Sharlindra na Cidade Baixa.", description = "Só falta levar a Coroa da Vontade à Sharlindra na Cidade Baixa. Ela cuidará para que seja entregue à Grande Dama Sylvana. Aconselho não pensar em usá-la em benefício próprio, <name>. O destino de Mug'thol deve ter deixado isso bem claro.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nTalismã Etéreo"},
[522] = {title = "Contrato do assassino", objective = "Leve o Contrato do Assassino ao Magistrado Maleb, na Costa Sul.", description = "Um contrato pelo assassinato do Magistrado Henrique Maleb. Foi selado com cera vermelha, com a marca de um característico anel-sinete.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[523] = {title = "A morte do Barão", objective = "Leve a Cabeça do Barão Vardus ao Magistrado Maleb na Costa Sul.", description = "O contrato que você recuperou foi assinado pelo Barão Vardus, um nobre do antigo reino de Alterac, conhecido pelo coração frio e pelas práticas brutais. Não me surpreende que alguém como o Barão estivesse por trás disso.\n\nEncontre esse homem. Encontre-o e leve-o à justiça. Ele deve estar entre os assombrações da Camarilha... talvez nas ruínas de Strahnbrad, ou talvez no vale ao norte de lá.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[524] = {title = "O elixir da agonia", objective = "Coloque o Barril Estragado no tapete para os fazendeiros capturados.", description = "Aqui ao lado os necroguardas estão com alguns fazendeiros de reféns. A Alta-executora Darthalia desconfiou de que um dos estrategistas militares de Ventobravo estaria escondido entre eles. Aparentemente, o tal militar foi encontrado morto nas Terras Pestilentas noite passada. Darthalia, sempre pensando em mim, decidiu deixar os prisioneiros à minha disposição.\n\nAcho que é hora de darmos uma festinha para esses fazendeiros. Parece o momento perfeito!\n\nColoque esse barril estragado no tapete do cômodo onde eles estão. Cerveja pestilenta para todos!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[525] = {title = "Outros mistérios", objective = "Entregue a Carta Decifrada para o Magistrado Maleb, em Costa Sul.", description = "Eu decifrei a carta, mas não tenho a menor ideia do que fazer com ela. Você disse que encontrou isso com ladrões nos contrafortes de Alterac? Muito estranho... \n\nAcho que você deveria entregar essa carta para o magistrado de Costa Sul. Sei que o texto está cifrado, mas essa é a cidade mais próxima de Alterac. Se houver alguma ameaça iminente, Costa Sul precisa ser alertada.", progress = "<name>! Viajando pelos vales e montanhas outra vez? Que notícias você traz?", completion = "&lt;Maleb lê a carta.&gt;\n\nEsta é a tradução de uma carta que você encontrou no acampamento da Camarilha? Não foi escrita por um ladrão, muito menos por um nobre de Alterac.\n\nParece que a Camarilha está ligada a alguma outra fonte de poder, e tremo só de pensar nas implicações disso.", rewards = "Você receberá:\n\n"},
[526] = {title = "Lingotes da forja de luz", objective = "Colete 5 Lingotes da Forja de Luz e fale novamente com Glorin Testaço no Pantanal.", description = "É verdade que tu achou um lingote da forja de luz no baú, mas só com um não se forja nada que preste!\n\nTu devia voltar lá e procurar mais. Pode ser que os murlocs perto do navio naufragado tenham mais lingotes da forja de luz.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[527] = {title = "A batalha de Eira dos Montes", objective = "Mate 6 Lavradores de Eira dos Montes, 6 Fazendeiros de Eira dos Montes, o Fazendeiro Raí e o Fazendeiro Pegaz, depois apresente-se novamente a Darthalia na Serraria Tarren.", description = "Posição de sentido ao me dirigir a palavra, <class>.\n\nEu estou sob comando direto de Varimatras. Nós teremos que combater a infestação humana até nossos boticários desenvolverem a nova peste.\n\nInformações nos levam a crer que a cidade de Eira dos Montes está vulnerável. Sua primeira tarefa será sabotar o abastecimento de comida e a infraestrutura.\n\nViaje para o oeste. Saqueie as fazendas mais ao norte, nos Campos de Eira dos Montes. E certifique-se de executar o Fazendeiro Raí e o Fazendeiro Pegaz.", progress = nil, completion = "Descansar, &lt;class&gt;. Vejo que você já voltou da batalha. Segundo minhas fontes, você se saiu muito bem na fronte de Eira dos Montes.", rewards = "Você receberá:\n\n"},
[528] = {title = "A batalha de Eira dos Montes", objective = "Mate 15 Camponeses de Eira dos Montes e apresente-se novamente a Darthalia na Serraria Tarren.", description = "As notícias sobre o ataque nas fazendas ao norte deixaram os moradores da cidade em pânico. Formidável.\n\nSegundo nossos Sicários, os camponeses estão fugindo para os campos na parte sul de Eira dos Montes. Aqueles tolos acham que podem se esconder nos pomares e escapar da morte.\n\nMostre que estão equivocados, <name>. Volte aos Campos de Eira dos Montes e vá atrás desses camponeses covardes. Extermine essa gente em nome da Dama Sombria.\n\nDispensad<o/a>!", progress = nil, completion = "O cerco da morte se fecha ao redor de Eira dos Montes. A batalha corre bem.", rewards = "Você receberá:\n\n"},
[529] = {title = "A batalha de Eira dos Montes", objective = "Mate o Ferreiro Verrintão e 4 Ferreiros Aprendizes de Eira dos Montes.\n\nConfisque um carregamento de ferro e apresente-se novamente a Darthalia na Serraria Tarren.", description = "Os humanos de Eira dos Montes estão resistindo mais do que o previsto. O problema é que aparentemente estão sendo armados pelo ferreiro da cidade. Nós temos que interromper o abastecimento.\n\nSua missão é executar o Ferreiro Verrintão. Mate os aprendizes também, e aproveite a viagem para trazer um carregamento de ferro do estoque deles. Os suprimentos nos serão úteis depois que já tivermos conquistado Eira dos Montes e partirmos para a Costa Sul.", progress = "Você levou as ordens a cabo, <class>? Já se livrou de Verrintão e dos assistentes dele? E cadê o carregamento de ferro?", completion = "Missão cumprida, &lt;class&gt;. E desempenhada com esmero, é bom dizer.", rewards = "Você receberá:\n\n"},
[530] = {title = "A vingança do marido", objective = "Mate Valfrido Moray.\n\nLeve as mãos dele para Olavo Nascimento na Cidade Baixa.", description = "Valfrido, um bom amigo em tempos idos, foi o assassino da minha esposa. Embora incapaz de luto, eu anseio pela vingança. Se você matar Valfrido e me trouxer aquelas mãos amaldiçoadas, eu lhe darei uma recompensa.\n\nEu soube que ele estava na Muralha Greymane, na parte sul de Pinhaprata, tentando fugir para o reino de Guilnéas.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nAnel do Escárnio"},
[531] = {title = "A vingança de Vyrin", objective = "Entregue a Cabeça do Velho Fumaça para Vyrin Velozvento no Pavilhão dos Andarilhos.", description = "Pela Luz! Não me traga mais essas coisas para o Pavilhão! É insalubre, é incômodo, é in... in... insalubre!\n\nLeve isso lá para fora, tá? Ninguém quer ver esse seu trofeuzinho ensanguentado! É sério!", progress = "Bom trabalho, <name>! Ensinamos uma boa lição a ele! Agora, entregue-me esta cabeça, que darei um fim a ela.", completion = "Obrigada por me ajudar com o plano, &lt;name&gt;. Faz meses que estou me coçando para me vingar daquele arrogante convencido e acho que fizemos um excelente trabalho! Tome, aceite isto, você mereceu.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nSaco de Munição de CaçaAljava de Caça\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[532] = {title = "A batalha de Eira dos Montes", objective = "Mate o Magistrado Ardelado e 5 Conselheiros de Eira dos Montes. Destrua a Proclamação de Eira dos Montes. Roube o Livro de Registro de Eira dos Montes. Depois apresente-se de volta a Darthalia na Serraria Tarren.", description = "A cidade de Eira dos Montes está um caos e estimamos que não demora a cair. Mas precisamos continuar implacáveis até que os humanos sejam exterminados do contraforte por toda a eternidade.\n\nApesar das baixas numerosas, os humanos têm boa liderança e continuam lutando. Precisamos eliminar esse líder, o Magistrado Ardelado, assim como o conselho da cidade. Destrua também o documento político que tanto reverenciam, a Proclamação de Eira dos Montes. E já que vai estar com a mão na massa, aproveite para roubar o livro de registro da cidade.", progress = "Que novidades você me traz?", completion = "As notícias de suas glórias em Eira dos Montes viajaram ligeiro. Você se saiu bem, &lt;class&gt;.", rewards = "Você receberá:\n\n"},
[533] = {title = "Infiltração", objective = "Obtenha informações sobre o paradeiro de Gol'dir para Krusk na Serraria Tarren.", description = "Finalmente ouvimos alguns rumores. Precisamos de provas do paradeiro de Gol'dir. Os humanos têm levado ele para lá e para cá, mas nossa busca vem dando frutos e aponta para um grande acampamento da Camarilha ao norte daqui.\n\nVá até lá, <name>, e traga qualquer informação em que conseguir botar as mãos.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[535] = {title = "Vito", objective = "Leve um caneco de Stout da Costa Sul para Vito no acampamento da Camarilha.", description = "Urrgh... Ferido, à beira da morte e diante do inimigo.\n\nVocê vai me matar de qualquer jeito, mas, se me trouxer uma Stout da Costa Sul – o seu povo bebe, não bebe? – eu lhe conto o que você quer saber.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nMissiva da Camarilha"},
[536] = {title = "Desceu pro mar", objective = "Mate 10 Caçamarés Rancapinas e 10 Oráculos Rancapinas para o Tenente Fárrio Orinelle na Costa Sul.", description = "Transferido da guarda de Ventobravo para Costa Sul, uma cidadezinha tão acabada que podiam abandoná-la... Esse cheiro de porto dia e noite.\n\nSem falar no velho Trilharrubra... Ele quer que eu cuide dos murlocs. Hah! \n\nEu mal tenho ânimo para encarar aqueles peixões lá na Praia Ocidental. Se você for no meu lugar e der um jeito neles, eu fico agradecido.", progress = nil, completion = "Tomara que assim o Trilharrubra fique convencido de que eu — hah! — venho cumprindo as ordens dele e livrando a Costa Sul desses murlocs tão ruins, malvados e periculosos! Pois não existe nada mais terrível neste mundo que um cardume de peixões endiabrados!", rewards = "Você receberá:\n\n"},
[537] = {title = "Concílio sombrio", objective = "Mate 4 Magos das Sombras Argus.\n\nLeve a Cabeça de Nagaz ao Magistrado na Costa Sul.", description = "A carta que você me trouxe foi escrita por um ser que diz se chamar Nagaz, integrante do Despertar de Argus. Nem sei o que é esse Despertar de Argus, mas eles se aliaram à Camarilha, então devem ser uma ameaça.\n\nQuero que você encontre Nagaz e os tais Magos das Sombras Argus de que fala a carta. Quero que você os encontre e os mate.\n\nVasculhe as Terras Altas, onde a Camarilha tem mais poder. Fica ao norte de Strahnbrad.\n\nCom sorte, os enviados do Despertar de Argus estarão lá.", progress = "Encontrou os enviados do Despertar de Argus?", completion = "Muito bem, &lt;name&gt;. Não podíamos permitir que o Despertar de Argus assumisse o controle a Camarilha.\n\nApesar de você ter destruído Nagaz, acho que o líder do grupo era um mero serviçal de alguém. Pode ser que o Despertar de Argus acabe se infiltrando no continente de Azeroth.\n\nOu até do mundo.", rewards = "Você receberá:\n\n"},
[538] = {title = "Costa Sul", objective = "Fale com o Historiador Dibbs em Costa Sul.", description = "<name>. Se não se importar em fazer uma longa jornada, eu tenho uma tarefa para você. É para ajudar a biblioteca de Ventobravo.\n\nO velho reino de Alterac, que chegou ao fim após a grande guerra, tinha uma grande coleção de livros. A ruína da cidade já é uma tragédia por si só, mas a perda do conhecimento seria uma ainda mais trágica. Precisamos recuperar tudo o que for possível.\n\nCosta Sul é a cidade mais próxima das Ruínas de Alterac. Vá até lá e fale com o Historiador Dibbs. Ele vai lhe mostrar como chegar até as ruínas e encontrar os livros.", progress = nil, completion = "Ah, eu me lembro do Milton. Um rato de biblioteca, com certeza. Abençoado seja.\n\nE ele está certo. As Ruínas de Alterac ainda guardam vários tomos de conhecimento em risco de se perderem.\n\nAlterac agora é ocupada por ogros, que está se lixando para livros.", rewards = "Você receberá:\n\n"},
[539] = {title = "A batalha de Eira dos Montes", objective = "Mate o Encarregado Grilhões e 10 Mineradores de Eira dos Montes, depois apresente-se novamente a Darthalia em Serraria Tarren.", description = "O Cartório de Eira dos Montes indica que a Mina Veioazul está sob controle da Aliança. Além disso, a mina é uma fonte primária de minério de ferro para o arsenal deles.\n\nApesar do nosso ataque decisivo contra a cidade de Eira dos Montes, a Aliança ainda protege Veioazul com unhas e dentes.\n\nA mina fica ao sul de Eira dos Montes. Vá até lá e mate o encarregado, um humano chamado Grilhões. Mate os mineradores também. Isso vai mandar uma mensagem clara à Aliança.", progress = nil, completion = "A Dama Sombria ficará satisfeita ao saber que tem uma nova mina que fornece matéria-prima para armar seus soldados.", rewards = "Você receberá:\n\n"},
[540] = {title = "O conhecimento precisa ser preservado", objective = "Leve 5 Tomos Recuperados e o Livro de Couro Desgastado contendo o Braço de Gri'lek para o Historiador Dibbs, em Costa Sul.", description = "Estes ogros que agora moram nas Ruínas de Alterac são uns selvagens, eu tenho calafrios só de pensar no que estão fazendo com os preciosos livros que ainda estão lá. Recupere tudo o que for possível!\n\nEntre nas Ruínas de Alterac e procure pelos tomos saqueados pelos ogros. Pegue tudo o que encontrar e traga para mim, mas procure por uma obra específica: O Braço de Gri'lek. Ele contém um antigo conhecimento trólico que eu preciso aprender, e as Ruínas de Alterac foram o último local conhecido de um exemplar intacto do livro.", progress = "Eu também queria vasculhar as ruínas, mas duvido que meus ossos aguentariam subir as montanhas. Mas, como anda a sua busca por conhecimento, <name>?", completion = "Ah, você conseguiu! Esplêndido! Preciso começar a estudar o Braço de Gri'lek imediatamente.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[541] = {title = "A batalha de Eira dos Montes", objective = "Viaje até Dun Garok é mate 10 Montanhistas, 8 Carabineiros, 4 Sacerdotes e o Capitão Monteferro, depois apresente-se novamente a Darthalia na Serraria Tarren.", description = "Anões ajudando humanos? Malditos sejam estes animais sujos.\n\nVamos pôr à prova as forças da chamada Aliança. Veremos se esses pouca sombra vão continuar ajudando os humanos depois de sentirem o peso da nossa ira. Sabemos exatamente onde fica o baluarte deles.\n\nÉ um quartel chamado Dun Garok, que fica na parte sudeste de Eira dos Montes, ao longo da Muralha de Thoradin. O objetivo dessa batalha é estabelecer um cerco a Dun Garok, <class>. O Capitão Monteferro, líder dos anões, tem que morrer.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[542] = {title = "Fale novamente com Milton", objective = "Leve os Tomos de Alterac para Milton Resma, em Ventobravo.", description = "Preciso passar algum tempo estudando o Braço de Gri'lek que você me trouxe, mas tenho certeza de que o Milton está ansioso para incluir os outros livros recuperados na coleção de Ventobravo.\n\nAqui estão os livros que você coletou nas Ruínas de Alterac, embalados e catalogados. Leve de volta a Milton Resma, em Ventobravo, e ele ficará muito grato.", progress = "<name>, você voltou. Como foi a sua incursão a Alterac? Trouxe alguma coisa com você?", completion = "Minha nossa! Olhe só para isso! Mal posso acreditar que sobreviveram à destruição de Alterac. E a todos esses ogros!\n\nObrigado, &lt;name&gt;. Muito obrigado! Você ajudou a preservar o conhecimento humano e contribuiu para a grandeza da Biblioteca Real de Ventobravo.", rewards = "Você receberá:\n\n"},
[543] = {title = "A Tiara de Perenolde", objective = "Leve a Tiara de Perenolde para o Conde Capistrano de Espargosa, em Ventobravo.", description = "A Tiara de Perenolde é uma relíquia dos Perenolde, a família aristocrata que governava Alterac antes da ruína do reino. Dizem que foi forjada por mestres joalheiros de Altaforja, e que foram usadas as mais belas esmeraldas já vistas.\n\nA tiara se perdeu quando Alterac foi destruído, mas há rumores de que foi encontrada por um ogro-mago chamado Grel'borg, o Sovina, que agora vaga pelas Ruínas de Alterac à procura de mais tesouros.\n\nQueremos que você recupere a tiara para nós.", progress = "Saudações, <name>. Cuidado para não pisar na bainha das minhas calças. Este traje acabou de chegar da alfaiataria.", completion = "Ah, esplêndido. A tiara está precisando de uma limpeza, por certo, mas você realmente se superou, &lt;name&gt;. Vou mandar que alguém prepare a tiara e a guarde imediatamente entre as outras peças da coleção.\n\nVocê há de concordar que nossa gratidão é tão grande quanto nossa coleção.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[544] = {title = "Invasão da prisão", objective = "Encontre os traidores e recupere os artefatos, depois fale novamente com o Magus Olharvazio na Serraria Tarren.", description = "Vim para Serraria Tarren fazer pesquisas, mas agora preciso solucionar uma crise. Quatro Renegados fugiram da Cidade Baixa alguns meses atrás. Viraram as costas para seus irmãos e, o que é pior... roubaram da Dama Sombria.\n\nEsses infelizes arrombaram um cofre e roubaram quatro artefatos, itens que os nossos boticários precisam usar em determinados estudos. Sylvana quer os artefatos de volta.\n\nOs ladrões fugiram para Dalaran, e os teurgos colocaram eles em quarentena no Campo de Internação de Lordaeron.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[545] = {title = "Patrulhas de Dalaran", objective = "Mate 6 Evocadores de Dalaran e 12 Escravos Elementais, depois fale novamente com o Magus Olharvazio na Serraria Tarren.", description = "Os teurgos de Dalaran construíram um enorme domo mágico em torno no coração da cidade. Alguns acreditam que foi uma proteção contra a violência dos dias de hoje, enquanto outros dizem que eles precisavam de solidão para estudar e tramar, nada mais.\n\nNão me importa o motivo. O que me importa são os arredores arruinados da cidade, fora do domo, que podem esconder tesouros mágicos valiosos.\n\nSó que a região é patrulhada pelos teurgos e seus escravos elementais.\n\nPor isso, cace-os e volte aqui quando o contingente deles tiver sido consideravelmente reduzido.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[546] = {title = "Lembrancinhas da morte", objective = "O Necroguarda Sandoval da Serraria Tarren quer 30 Crânios Humanos de Eira dos Montes.", description = "Ouvi dizer que Darthalia convocou você para a guerra contra os humanos de Eira dos Montes. Que inveja!\n\nEnquanto você estava lá se divertindo, matando humanos, pilhando a cidade, aterrorizando inocentes, fiquei preso aqui na Serraria Tarren, montando guarda.\n\nMas talvez você se compadeça de um velho necroguarda como eu. O negócio é o seguinte: estou colecionando crânios humanos e sei que você vai lutar contra eles durante algum tempo. Traga 30 para mim, garanto que vai valer a pena.", progress = "Então nosso sortudo <name> voltou? Quantos humanos você eliminou hoje? Centenas, espero.\n\nTrouxe crânios para mim?", completion = "Que crânios preciosos! São lindos! Te devo uma, como prometido, &lt;name&gt;!", rewards = "Você receberá:\n\nAnel de Crânio"},
[547] = {title = "Espada do Humberto", objective = "Recupere a espada do Necroguarda Humberto em Dun Garok e devolva-a para ele na Serraria Tarren.", description = "A Alta-executora Darthalia me mandou ficar vigiando Dun Garok de longe. Montei acampamento fora do campo de visão deles. Quando amanheceu, precisei ferver água e saí para recolher lenha.\n\nQuando voltei ao acampamento, descobri que minhas coisas tinham sumido.\n\nImagino que anões patrulheiros de Dun Garok passaram por ali e levaram tudo. Preciso da minha espada! Se conseguir pegar de volta para mim, eu ficarei muito grato. Tem muitos anões por aqui, vai ser difícil encontrar o ladrãozinho imundo que a levou.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nPeitoral com SuportesPerneiras Mercenárias\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[549] = {title = "PROCURA-SE: integrantes da Camarilha", objective = "Mate 10 Ladinos da Camarilha e 10 Vigias da Camarilha. Fale novamente com a Alta-executora Darthalia na Serraria Tarren para receber sua recompensa.", description = "Pela autoridade a mim conferida por Lady Sylvana, ordeno a morte de todos os membros da organização conhecida como Camarilha. Os que forem trazidos sob custódia ou feitos prisioneiros serão sumariamente executados. Sabe-se que eles estão se reunindo nas ruínas do Forte do Desterro, a sudeste de Serraria Tarren.\n\nUma polpuda recompensa será concedida a quem trouxer provas dos planos deles à Alta-executora Darthalia.", progress = nil, completion = "Excelente, &lt;class&gt;. Se continuarmos assim, a maldita Camarilha vai ser varrida dessa terra em breve.", rewards = "Você receberá:\n\n"},
[550] = {title = "A batalha de Eira dos Montes", objective = "Leve a Comenda Lacrada de Darthalia para Varimatras, na Cidade Baixa.", description = "Em reconhecimento à sua bravura e tenacidade na batalha de Eira dos Montes, escrevi esta comenda, que exalta e dá testemunho de seu heroísmo em combate ao alto-comando.\n\nLeve essa comenda lacrada a Varimatras, na Cidade Baixa. Vá de cabeça erguida, <class>.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nCalças do Sepultamento SagradoEscudo Crânio-mortoLaminegra Rúnica\n\nVocê também receberá:\n\nElo da Cidade Baixa"},
[551] = {title = "Pergaminho encantado", objective = "Leve o Pergaminho Encantado para o Historiador Dibbs, em Costa Sul.", description = "O pergaminho foi encantado com um feitiço, o que o torna indecifrável. Mas uma palavra pode ser lida no topo da folha:\n\n\n      Nagaz\n\n", progress = "Ah, você quer que eu dê uma olhada em uma coisa? Espero que seja um extrato de conhecimento coletado no alto da montanha...", completion = "Mais anotações criptografadas?\n\nQueria ter conhecimento para decifrar todos os trechos dessa escrita estranha que chegam de Alterac, mas me falta talento.", rewards = "Você receberá:\n\n"},
[552] = {title = "Uma vingança para Helcolar", objective = "Recupere o Bastão de Helcolar do Yeti e leve-o de volta para o Noviço Trevando, em Serraria Tarren.", description = "A transformação do mestre Helcolar em lich estava quase completa quando uma turba humana descobriu o covil ritualístico dele nas montanhas e o linchou brutalmente. Para piorar as coisas, enterraram o corpo dele no cemitério de Costa Sul.\n\nUma falta de respeito dessas com o grande teurgo é inaceitável. Como aprendiz dele, vou cuidar para que a tempestade da vingança caia sobre Costa Sul.\n\nMas antes, precisamos do bastão de Helcolar. Yetis ocuparam o covil ritual dele, com certeza um deles está com o bastão.", progress = "Já conseguiu recuperar o bastão de Helcolar? Tem muito yeti nessa região, vai ser muito difícil de encontrar. Só que a gente precisa dele para poder continuar.", completion = "Você achou! Excelente, &lt;name&gt;!", rewards = "Você receberá:\n\n"},
[553] = {title = "Uma vingança para Helcolar", objective = "Carregue o Bastão de Helcolar com os poderes da Chama de Azel, da Chama de Veraz e da Chama de Uzel.\n\nColoque o bastão carregado na tumba de Helcolar, em Costa Sul.", description = "Aqui, pegue o bastão de Helcolar. Você vai precisar.\n\nHelcolar confeccionou três piras cerimoniais para seu feitiço mais poderoso. A Chama de Azel e a Chama de Veraz está dentro da Caverna do Pé da Serra. A Chama de Uzel fica em uma gruta acima da Caverna do Pé da Serra, mais ao norte nas montanhas.\n\nO bastão de Helcolar precisa ser carregado em cada uma das três chamas. Quando o ritual estiver concluído, coloque o bastão dentro da tumba de Helcolar, em Costa Sul, a cidade humana fortemente protegida que fica ao sul. Nossa vingança será maligna!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[554] = {title = "Decifração de Lançatroz", objective = "Leve o Pergaminho Encantado para o Prospector Lançatroz, em Altaforja.", description = "O Prospector Lançatroz é o mestre da criptografia e da decifração. Se você levar o pergaminho que encontrou para o Prospector, talvez ele consiga entender o que está escrito. Lançatroz está estudando na sede do Prospector em Altaforja.\n\nQuando você o encontrar, diga-lhe que conversou comigo, eu tenho mandado bastante trabalho para ele ultimamente.", progress = "Mais interrupções?\n\nAh, esse pergaminho está protegido por um feitiço muito intrincado, finamente entretecido. Eu posso desfazer a trama, é claro, mas isso levaria tempo. E o tempo é valioso...\n\nNão quer vender para mim?", completion = "Uma decisão sábia. Eu não acredito que exista outro erudito além de mim com habilidade para traduzir este pergaminho. E se o conteúdo se mostrar interessante, não se preocupe, eu mando para você.", rewards = "Você receberá:\n\n"},
[555] = {title = "Caldo reconfortante de tartaruga", objective = "Leve 10 peças de Carne de Tartaruga e alguns Temperos Reconfortantes para o Mestre-cuca Jessé, em Costa Sul.", description = "Se tem uma coisa que você vai aprender sobre a Costa Sul é que temos a melhor cozinha ao norte de Ventobravo.\n\nA minha receita de caldo de tartaruga, por exemplo. Tem gente que viaja de lugares tão distantes quanto Vila Sombria só para provar uma tigela. Por falar nisso, faz tempo que não faço um caldo. Eu costumava passar por Dalaran e ir até o Espelho de Lordaeron para caçar mordeliscas, mas isso é muito perigoso hoje em dia. Se você me trouxer carne de tartaruga das mordeliscas do norte e temperos reconfortantes, eu faço um pra você.", progress = "Conseguiu encontrar os ingredientes? Vou lhe dizer uma coisa, esse território não é mais tão pacífico quanto costumava ser. Queria que essa inquietação nunca tivesse acontecido...", completion = "Todos vão adorar: o caldo reconfortante de tartaruga do Mestre-cuca Jessé vai ser servido de novo!", rewards = "Você receberá:\n\nReceita: Caldo Reconfortante de TartarugaCaldo Reconfortante de Tartaruga"},
[556] = {title = "Fichas de pedra", objective = "Leve 10 Fichas de Pedra Gasta para o Guardião Bel'varão, na Serraria Tarren.", description = "Um talento notável dos teurgos de Dalaran é a familiaridade com os elementais: criaturas poderosas de outras dimensões. Os teurgos usam fichas mágicas como formar de controlar esses elementais. O Magus Olharvazio gostaria de estudar algumas dessas fichas.\n\nEntre nos territórios dos teurgos de Dalaran, a oeste de Eira dos Montes, e colete as fichas.\n\nVocê vai achar as fichas com qualquer cidadão de Dalaran, mas eu sugiro que você cace no acampamento de internação. É mais seguro que a própria Dalaran.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[557] = {title = "Braçadeiras da vinculação", objective = "Leve 4 Braçadeiras do Vínculo à Terra para o Guardião Bel'varão, na Serraria Tarren.", description = "O Magus estará ocupado estudando essas fichas de pedra que você juntou, mas eu sei que, uma hora, ele vai querer amostras da contraparte delas: braçadeiras da vinculação. Elas são presas em torno dos pulsos dos elementais, colocando-os a serviço do mago.\n\nCace elementais nas ruínas de Dalaran e recolha suas Braçadeiras do Vínculo à Terra. O Magus ficará satisfeito com isso.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[558] = {title = "Autógrafo da Jaina", objective = "Vá a Theramore para tentar conseguir o autógrafo da Grã-senhora Jaina Proudmore para o seu protegido.", description = "Dizem que a grã-senhora Jaina Proudmore de Theramore é uma das maiores heroínas que a Aliança já teve. Quando eu crescer, quero ser um herói da Aliança também!\n\nVocê conhece a Jaina, <name>? Posso pedir um favor? Você poderia me conseguir um autógrafo dela? Eu não saberia o que falar, e ela provavelmente não tem tempo pra alguém como eu. Mas você... Você é <um aventureiro/uma aventureira>, assim como ela! Aposto que ela lhe daria um autógrafo na mesma hora!\n\nPor favor?Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[559] = {title = "A prova de Fárrio", objective = "Colete 10 Cabeças de Murloc dos murlocs da Praia Ocidental para o Tenente Fárrio Orinelle, que está na Costa Sul.", description = "Dá para acreditar, <name>? Trilharrubra não ficou satisfeito com todos os murlocs que matamos — ah, deixe de modéstia, você teve participação nisso. Agora ele quer que eu apresente provas a ele, decepando cabeças frias e pegajosas de murlocs para submeter à aprovação dele.", progress = "Conseguiu as cabeças para mim, <name>?", completion = "Ah, pelo amor da Luz... estão pingando pelo chão todo.\n\nEu não quero pegar nessas coisas nojentas. Peixe é bacana e tudo mais, mas quando eles criam pernas e começam a andar por aí... Faz semanas que eu não como peixe, e isso, aqui em na Costa Sul, significa que não estou comendo quase nada.", rewards = "Você receberá:\n\n"},
[560] = {title = "A prova de Fárrio", objective = "Entregue o Saco com Cabeças de Murloc ao Delegado Trilharrubra na Costa Sul.", description = "Sabe, <name>, eu prefiro mesmo não levar essas cabeças... Já que você está segurando elas mesmo, será que poderia entregá-las ao Trilharrubra por mim?", progress = "Saudações, cidadã. Você veio se oferecer para garantir a segurança da Costa Sul?", completion = "Você é a garota de entregas do Fárrio, não é?\n\nSe você ajudou Fárrio a conseguir essas cabeças, eu lhe agradeço. A Costa Sul precisa de trabalhadores dedicados e muito esforço para se manter protegida.", rewards = "Você receberá:\n\n"},
[561] = {title = "A prova de Fárrio", objective = "Fale com o Tenente Fárrio Orinelle na Costa Sul.", description = "Por favor, informe ao Tenente Orinelle que eu gostaria que ele mudasse o foco dos esforços para a infestação de nagas na Praia Oriental. Diga a ele para se reportar a mim pessoalmente assim que tiver resultados.", progress = nil, completion = "As nagas, é? Trilharrubra deve ter recebido a mesma informação que eu... Rá! Ele não vai receber uma promoção às minhas custas. Deixe-me explicar.", rewards = "Você receberá:\n\n"},
[562] = {title = "Ventobravo, avante!", objective = "Mate 10 Caça-costas Espinhadaga e 10 Sirenas Espinhadaga para o Tenente Fárrio Orinelle na Costa Sul.", description = "Meu antigo mentor na guarda de Ventobravo me mandou uma mensagem dizendo que o Alto Comando da Aliança está ficando preocupado com a ameaça das nagas. Pelo que ele ouviu, existe uma boa chance de ter uma promoção — ou, pelo menos, uma realocação — para quem provar seu valor nessa tarefa.\n\nSó tem um probleminha: eu não posso com as nagas, <name>. Eu consigo separar uma briga de bar ou enxotar um pedinte agressivo tranquilamente, mas as nagas são muita areia para a minha carroça. Você tem que me ajudar, <name>!", progress = nil, completion = "Com as nagas derrotadas na Costa Sul, serei o primeiro da fila para ser realocado. Eu vou poder ver Ventobravo outra vez... sair desse porto esquecido pela Luz.", rewards = "Você receberá:\n\nRasgador de Peixe"},
[563] = {title = "Transferência", objective = "Entregue o Relatório de Fárrio para o Major Samuelson na Bastilha Ventobravo.", description = "Acho que não vai ser difícil mostrar o meu relatório. O novo capitão da guarda, Major Samuelson, foi o meu mentor quando eu estava alocado em Ventobravo. Isso já deve ser pretexto suficiente para ele me transferir de volta para a cidade. Mas preciso que você entregue o meu relatório a ele... Não quero que o velho Trilharrubra fique sabendo.", progress = "Saudações, cidadã. Em que a guarda de Ventobravo pode lhe ser útil?", completion = "O que é isso? Ah... então, meu atento pupilo se candidatou para resolver o problema das nagas na Costa Sul. Agradeço por me trazer isto, sem dúvida vou levar isso em consideração quando revisar a alocação de pessoal no mês que vem.", rewards = "Você receberá:\n\n"},
[564] = {title = "Ameaça cara", objective = "Dario Malavista, da Costa Sul, quer que você mate 8 Leões da Montanha e 10 Leões da Montanha Imponentes.", description = "Se tem uma coisa que me dêxa tiririca da vida é perdê essas belezura de cavalo. E, pó'querditá, nóis perdeu um bom bocado deles por causa daqueles morto-vivo disgramado, o diacho da Camarilha e outros coisa-ruim fiduma disgraça que vêm atazaná nessas banda.\n\nMas eu juro de pé juntim pela tumba do meu vovozim que num tem ameaça maior que aqueles leão da montanha lá de Alterac. Os bichão sempre desce o monte pra atrapaiá a minha vida!\n\nOcê é <um herói/uma heroína>? Vai dá um jeito nisso, intão, sô!", progress = nil, completion = "Ara, mas que notícia boa, sô! Eu devia tê procurado uma heroína desde o começo logo de uma vez pra dá cabo daqueles bichão. Ocê dexô o papai aqui orgulhoso, &lt;name&gt;.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCinturão do PastorLuvas do Pastor\n\nVocê também receberá:\n\nReceita: Bife Saboroso de LeãoBife Saboroso de Leão"},
[565] = {title = "Manto de Pele de Yeti do Bartolo", objective = "Leve um Rolo de Lã, um Manto do Montanhista, Fio Delicado e 10 chumaços de Pele de Yeti para Bartolo Ginsetti na Costa Sul.", description = "Eu, o grande Bartolo, farei para você o mundialmente famoso, aclamado pela Aliança, o renomado manto de pele de yeti!\n\nPode rodar o mundo inteiro atrás de um manto melhor do que esse, eu duvido que você encontre. Ah, espere, o que disse, <pequeno/pequena> <race>? \"Bartolo, não existe manto mais fino que esse!\" Sim, sim! Eu já ouvi isso antes.\n\nBartolo precisa de um rolo de lã, um Manto do Montanhista feito por um coureiro habilidoso, fio delicado e pele de yeti, é claro! Traga-me esses itens e você será <um/uma> cliente <satisfeito/satisfeita>!", progress = "Então, você retornou ao grande Bartolo. Por acaso, você terminou de colher os materiais necessários para o seu manto de pele de yeti? Ou você só gosta de admirar a minha grandiosidade?", completion = "Está na hora de o grande Bartolo fazer sua mágica! Eu não preciso de cajado de bruxo nem de bastão de mago, porque eu, o grande Bartolo, faço mágica com uma simples agulha e linha!", rewards = "Você receberá:\n\nManto de Pele de Yeti"},
[566] = {title = "PROCURA-SE: Barão Vardus", objective = "Mate o Barão Vardus e entregue a cabeça dele para a Alta-executora Darthalia na Serraria Tarren.", description = "Por ordem da Alta-executora Darthalia, o humano chamado Barão Vardus é procurado por crimes contra os Renegados. Sabe-se que Vardus é um membro do alto escalão da organização conhecida como Camarilha.\n\nEle foi visto pela última vez nas Terras Altas, nas Montanhas de Alterac, ao norte da Serraria Tarren.\n\nA ordem é para executá-lo. Traga uma prova de sua morte para a Alta-executora Darthalia para receber a recompensa.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nVeste Infernal"},
[567] = {title = "Perigo!", objective = "A Alta-executora Darthalia da Serraria Tarren está oferecendo uma recompensa pelas cabeças de Tabelião Horácio Brancorcel, Cidadão Urbano, Minerador Talhatudo e a Fazendeira Kalaba.", description = "Perigo!\n\nOs seguintes humanos de Eira dos Montes são indivíduos perigosos, e a Alta-executora Darthalia dará uma recompensa a quem os matar:\n\nTabelião Horácio Brancorcel. Procurado pelo assassinato da Necroguarda Roma.\n\nCidadão Urbano. Procurado pelo assassinato da Boticária Eli.\n\nMinerador Talhatudo. Procurado pelo assassinato do Sicário Frígio.\n\nFazendeira Kalaba. Procurada por emboscar suprimentos destinados à Cidade Baixa.\n\nTodos esses indivíduos estão foragidos e será difícil encontrá-los. A recompensa será entregue a quem os executar.", progress = nil, completion = "Você é um caçador de recompensas e tanto, &lt;class&gt;. Muito bem.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nArco da PilhagemBroquel da SentinelaVarinha Carbonizada\n\nVocê também receberá:\n\nCapa com Capucho"},
[568] = {title = "A defesa de Grom'gol", objective = "O Comandante Aggro'gosh do Acampamento Grom'gol quer que você mate 15 Raptores Batecauda.", description = "Throm'ka, <class>!\n\nVocê chegou a Grom'gol na hora certa. Como comandante do acampamento-base do Chefe Guerreiro aqui na selva, é meu dever solene garantir a segurança de todos os membros da Horda. Nossa missão de manter uma linha de abastecimento segura para o Pedregal está sendo atrapalhada por alguns habitantes locais.\n\nEu estou encarregando você de reduzir a população de raptores nos arredores de Grom'gol. Assim que tiver feito algum progresso significativo, fale comigo novamente para receber mais instruções.", progress = nil, completion = "Seu dever foi cumprido com honra, &lt;class&gt;.", rewards = "Você receberá:\n\n"},
[569] = {title = "A defesa de Grom'gol", objective = "O Comandante Aggro'gosh do Acampamento Grom'gol quer que você mate 10 Brutamontes Ogros e 5 Mandingueiros Ogros.", description = "Quando nós chegamos à floresta, não tínhamos a intenção de entrar em guerra com as tribos locais. Nossa missão de suprir o Pedregal é a nossa maior prioridade, mas aconteceram complicações. Um bando de ogros locais ocuparam as Ruínas de Nizjah ao sudeste de Grom'gol. Apesar dos avisos, eles continuam emboscando nossas caravanas de suprimentos.\n\nEstou encarregando você para dar um jeito naqueles ogros teimosos.\n\nVá até as ruínas e tente expulsá-los da área. Na pior das hipóteses, mande uma mensagem bem dura.", progress = nil, completion = "Muito bem! Você serviu ao Chefe &lt;class&gt; com honra.", rewards = "Você receberá:\n\nBroquel Grom'gol"},
[570] = {title = "O encantamento de Mok'thardin", objective = "O Clarividente Mok'thardin do Acampamento Grom'gol quer 8 Garras de Umbragorja e uma Presa Imaculada de Tigresa.", description = "A Horda precisa fazer o poder da magia da selva se manifestar para depois dominá-lo. Até agora, meus rituais tiveram sucesso. Eu consigo lançar encantamentos muito poderosos. Para você, valente <class>, farei um item muito especial.\n\nTraga-me algumas garras de pantera umbragorja juntamente com uma presa de tigresa. Não é qualquer presa: ela deve estar completamente imaculada.\n\nAs feras perambulam pela selva, mas você vai encontrar algumas por perto, cruzando o rio a partir das Ruínas de Nizjah a sudeste.", progress = "Que notícias você traz?", completion = "Seu conhecimento da selva é forte, &lt;name&gt;. Estes espécimes servirão perfeitamente. Vou agradecer aos espíritos por sacrificar feras tão formidáveis pela causa da Horda.", rewards = "Você receberá:\n\n"},
[571] = {title = "O encantamento de Mok'thardin", objective = "Leve um Tendão de Gorila Velho para o Clarividente Mok'thardin em Grom'gol.", description = "O que eu preciso de você agora será muito difícil de obter.\n\nEu preciso de uma amarra mágica para prender todas essas penas, garras e a presa imaculada direitinho, e só o tendão de um gorila velho vai servir.\n\nVocê só vai conseguir encontrar isso em um gorila ancião do Vale da Névoa. Eles são conhecidos por forragear a leste da Angra do Butim.\n\nO tendão deve estar em perfeitas condições. Talvez você tenha que matar um número razoável até conseguir exatamente o que eu preciso, mas a caçada vai valer a pena.", progress = "A Sorte lhe sorriu, <name>? Você conseguiu obter um tendão de gorila velho?", completion = "Um tendão ideal, &lt;name&gt;. Seus esforços serão compensados em breve.", rewards = "Você receberá:\n\n"},
[572] = {title = "O encantamento de Mok'thardin", objective = "Leve 10 Penas de Espreitador da Selva para o Clarividente Mok'thardin no Acampamento Grom'gol.", description = "Os itens que você recolheu dos felinos do Vale são só o primeiro requisito para um poderoso item encantado. Estes berloques da selva combinados com o meu ritual xamânico lhe darão uma ferramenta muito útil para ajudar a Horda e o Chefe Guerreiro.\n\nMas, agora, você deve reunir para mim algumas penas de espreitador da selva. Tradicionalmente, as feras usam essas penas amarradas em seus embraces. Você vai encontrar a tribo dos raptores seguindo direto para o sul de Grom'gol, perto da grande arena.", progress = "E então, sua colheita foi frutífera?", completion = "Excelente. Vou preparar agora mesmo.", rewards = "Você receberá:\n\n"},
[573] = {title = "O encantamento de Mok'thardin", objective = "O Clarividente Mok'thardin de Grom'gol precisa de Água da Fonte Abençoada. Ele também quer que você mate 10 Exploradores Nagas.", description = "Só falta um último reagente para completar o encantamento.\n\nAo sul, em direção à costa oeste, no alto dos penhascos, existe uma Fonte Sagrada. A fonte é objeto de grande conflito entre acadêmicos místicos e religiosos. Todas as raças de Azeroth afirmam que as águas da fonte são divinas para seu povo.\n\nMeu último pedido a você é: traga-me a Água da Fonte Abençoada. Mas fique alerta: uma expedição de nagas exploradoras avançou sobre a fonte. Você precisará enfrentá-las também.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nGargantilha do Sumo Xamã"},
[574] = {title = "Forças especiais", objective = "Mate 10 Tropas de Choque de Kurzen e 6 Encolhedores de Cabeças de Kurzen.\n\nFale com o Tenente Doren no Acampamento dos Rebeldes.", description = "Agora que o exército dele foi reduzido drasticamente, você terá que enfrentar forças altamente treinadas. Quando eles não estão na selva matando nossos soldados rebeldes, estão nas profundezas do Complexo do Kurzen, em uma caverna que eles chamam de Arsenal.\n\nEu conheço pessoalmente muitos soldados do Kurzen e sei que são habilidosos, leais e cruéis. Essa tarefa que lhe confio não é fácil, <name>.\n\nDepois de derrotá-los, fale com o Tenente Doren, meu comandante. Ele quer ser o primeiro a receber seu relatório.", progress = "O Sargento Yohwa me disse que você é habilidoso e valente, <class>. Tem alguma coisa a relatar?", completion = "&lt;name&gt;. A Sargento Yohwa confia nas suas habilidades, e eu confio nela. A derrota que você causou às forças de Kurzen é quase boa demais para ser verdade. Eu temia que os rebeldes estivessem sozinhos, e, sem ajuda, nós nunca conseguiríamos enfrentar a genialidade do Coronel.\n\nPelo visto, a esperança de sobrevivermos continua viva.", rewards = "Você receberá:\n\n"},
[575] = {title = "Oferta e demanda", objective = "Leve 2 Peles de Crocolisco Grande do Rio para o Drizzlik na Angra do Butim.", description = "\"O melhor coureiro dos Mares do Sul, do mundo novo e do mundo antigo\". É assim que eu sou conhecido. Eu! Drizzlik! O melhor cou... Caham. Enfim.\n\nMeus produtos de couro são conhecidos em todos os cantos como os melhores, a escolha dos especialistas!\n\nEu acabei de receber uma encomenda do Diretor Charadox do Sindicato dos Faz-tudo para fazer uma dúzia de botas da linha Excelsior, as favoritas dele. Na verdade, são as minhas campeãs de vendas.\n\nPra começar a forma básica, eu preciso de couro de crocolisco, daqueles que ficam na margem do rio na parte norte da Selva do Espinhaço Setentrional.", progress = "Meu equipamento está pronto para ser usado, <name>. Eu só preciso das peles. Nós não queremos deixar o Charadox esperando, com certeza.\n\nNão é uma boa ideia para ninguém deixar o inventor-chefe do Sindicato dos Faz-tudo insatisfeito.", completion = "Estas peles vão servir perfeitamente. A qualidade delas não é muito importante, já que serão a parte de dentro das botas, mas elas precisam ser confortáveis.\n\nSe estiverem muito rígidas, podemos usar sangue de tigre para amaciar. Truques da profissão, sabe?", rewards = "Você receberá:\n\n"},
[576] = {title = "Fique de olho", objective = "Dizzy Caolho da Angra do Butim quer que você recupere seu olho com os Bucaneiros da Vela Sangrenta.", description = "Alto! Quem vem lá?\n\nArre, sou inútil sem meu olho encantado. Pra começo de conversa, já foi uma desgraça perder as vistas em batalha! Mas, num golpe de sorte, aportamos em Menethil e um mago viajante encantou um olho de vidro que me devolveu a visão.\n\nNa noite passada, fui atacado por uns bucaneiros da Vela Sangrenta... e afanaram meu olho! Parece que esses tipos deram um jeito de entrar na Angra do Butim.\n\nVocê ajuda este velho lobo do mar a conseguir o olho de volta!?", progress = "Eia, compadre! Posso não ter vista, mas consigo te farejar desde a entrada do porto!\n\nPreciso mesmo recuperar meu olho! O capitão vai me jogar nos porões quando descobrir que minha butuca tá nas mãos daqueles malditos Bucaneiros da Vela Sangrenta.\n\nDe que serve um vigia que não enxerga? O topo da vela era minha casa... Sem minha vista, vai ser lucro se me deixarem escovar o convés.", completion = "Macacos me mordam e mordam meus macacos! Tu achou meu olho! Deixa ver se ainda cabe.\n\nHurra, &lt;name&gt;! Eu tô enxergando!", rewards = "Você receberá:\n\nCapa Ondanegra"},
[577] = {title = "Algumas partes faltando", objective = "Leve 5 Peles de Crocolisco Arapuca para Drizzlik na Angra do Butim.", description = "Bom, eu já fiz a forma básica das botas e elas estão ficando ótimas, mas as peles que você trouxe não serão suficientes para fazer toda a camada interna.\n\n\"O que faremos agora, Drizzlik?\" Certamente essa é a pergunta que você está se fazendo e, ora, não é uma sorte você estar falando com o Drizzlik? Ele te dirá exatamente o que fazer!\n\nAs camadas internas nunca são vistas e, portanto, podemos usar umas peles mais grossas e mais baratas dos crocodilos que ficam ao redor do Lago Nazferiti para complementar o forro.", progress = "Botas de couro Excelsior do Drizzlik, manufatura perfeita, cem por cento couro de crocolisco de água salgada de alta qualidade. Não existe nada melhor!\n\nNinguém consegue vencer uma descrição imponente como essa, concorda?\n\nVocê conseguiu as peles pra mim?", completion = "Minhas botas... elas são como salsichas, &lt;name&gt;. Têm uma aparência ótima, têm um gosto ótimo. Te digo que elas são recheadas com os melhores ingredientes e, se você comer, vai gostar.\n\nAí, é melhor — e mais seguro — confiar em mim e não perguntar exatamente do que elas são feitas.", rewards = "Você receberá:\n\n"},
[578] = {title = "A Pedra das Marés", objective = "Encontre a ilha assombrada para o Barão Rezingada na Angra do Butim.", description = "Se houver mesmo mago goblin e elementais da água assombrando a ilha, isso pode significar que a Pedra das Marés pode ser minha!\n\nEu quero que você localize essa ilha assombrada... descubra o que está acontecendo lá. Ah, e se, por acaso, você quiser saber mais sobre a Pedra das Marés, leve este canhoto para a Biblioteca de Ventobravo. Eles devem conseguir encontrar uma cópia do livro que me apresentou a lenda.\n\nSerá que Gazban realmente encontrou a Pedra...?", progress = nil, completion = "Então as histórias eram verdadeiras! Incrível!\n\nContudo, vamos precisar pensar numa forma de quebrar a maldição da Pedra das Marés se quisermos recuperá-la. Como a maldição é uma coisa mágica, vamos precisar da ajuda daqueles magos.", rewards = "Você receberá:\n\n"},
[579] = {title = "A Biblioteca de Ventobravo", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nA História de Morgan LadimoreLendas dos Gurubashi, Volume 3"},
[580] = {title = "O grogue perdido do Aldo Isque", objective = "Aldo Isque, na Angra do Butim, quer que você leve para ele as garrafas de Xerez de Pupellyverbos que ele perdeu.", description = "Nóis tava lubrificando as engrenagem da coragem porque tava vindo uma pancada lá dos Penhasco Panorâmico, com as Terras Agrestes no horizonte. Achei que o mar ia engolir a gente inteiro.\n\nO velho Isque aqui manja tudo de coragem líquida! O capitão tinha um caixote de Xerez de Pupellyverbos que nóis encomendou de um marujo a caminho de Ventobravo.\n\nDaí, quando aquela tempestade vindo das Terra Agreste catou a gente, eu me enfiei com tudo no grogue. Um vagalhão bateu na gente e derrubou tudo as garrafa pro mar. Agora a chibata do capitão vai estalar se você não me ajudar a trazer as garrafa de volta!", progress = "Pelas barbatana de Neptulon! Meu goró já tá só no cheirinho aqui! O capitão vai içar vela e o velho Isque vai ter que navegar com a garganta mais seca que o Deserto de Tanaris. E quando ele descobrir que o xerez dele sumiu... afe!", completion = "Macacos me mordam! Você achou o grogue do Capitão!\n\nFico te devendo uma, camarada.\n\nParece que tem uma baita tempestade se formando lá fora. Acho que vou ficar por aqui, esperando ela passar, e tomando esse goró pra esquentar o peito..", rewards = "Você receberá:\n\nRum Rumsey"},
[581] = {title = "A caçada ao Yennikan", objective = "Leve 9 Presas de Sanguescalpo para Nimboya no Acampamento Grom'gol.", description = "A ruína, isso sim. A ruína do filho mais novo do meu chefe, Yennikan. Como é de costume para a nossa tribo, ele foi entregue para os trolls Gurubashi de Zul'Gurub, mas depois que a minha tribo, os Lançanegra, partiu com o orc Thrall, o pobre Yennikan se perdeu para nós. Eu fui enviado lá de Orgrimmar de volta para a Selva do Espinhaço, para procurar o filho do meu chefe.\n\nE acredito que os Sanguescalpo estão com ele.\n\nSe quiser me ajudar, vá atrás dos Sanguescalpo. Você pode encontrá-los ao norte. Junte as presas deles e me entregue.", progress = "<class>, mostre-me que você fez dos Sanguescalpo suas presas.\n\nMostre-me os troféus.", completion = "Essas são belas presas, &lt;name&gt;. Você provou que é capaz de caçar os Sanguescalpo.\n\nO que é ótimo, porque sua caçada ainda não terminou.", rewards = "Você receberá:\n\n"},
[582] = {title = "Caçando cabeças", objective = "Leve 20 Cabeças Encolhidas para Nimboya, no Acampamento Grom'gol.", description = "Nós precisamos descobrir se Yennikan está morto.\n\nMate os Caçadores de Cabeças Sanguescalpo e pegue as cabeças encolhidas deles. Você deve juntar muitas, mas muitas cabeças mesmo! E eu vou olhá-las para ter certeza de que a cabeça do Yennikan não é uma delas.\n\nE se o Yennikan foi morto pelos Sanguescalpo, que uma maldição de 600 anos caia sobre o espírito do chefe deles, Gan'zulah!\n\nVá, <name>. Você pode encontrar Caçadores de Cabeças nas ruínas de Zuuldaia e Zul'Kunda, na costa ao norte.", progress = "<name>, temo pelo filho de nosso chefe. Você conseguiu as cabeças encolhidas para que eu possa inspecioná-las?", completion = "Obrigado, &lt;name&gt;. Vou olhá-las uma por uma para ter certeza de que o pobre Yennikan foi poupado do machado dos caçadores de cabeça.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nManilhas LançanegraTalas Lançanegra\n\nVocê também receberá:\n\n"},
[583] = {title = "Boas-vindas à selva", objective = "Fale com o Rosarães Guima.", description = "Bem-vind<o/a> à Selva do Espinhaço!\n\nTalvez você não esteja ciente disso, mas aquele anão ali é grande Rosarães Guima, renomado herói de guerra da Aliança e mestre da caça esportiva. Ele não gosta de receber estranhos no acampamento, mas você parece já ter passado por um bocado de ação, <class>.\n\nO mestre Guima é um veterano de muitas guerras e tem um fraco por heróis como ele.\n\nVá lá falar com ele. Talvez você consiga umas dicas de caça.", progress = nil, completion = "Prazer te conhecer, &lt;name&gt;. Não vejo um &lt;class&gt; por essas partes há algum tempo.\n\nEu me juntei a essa expedição por um único motivo: caçar uma grande fera furtiva. Já que você tem fama de herói, eu vou deixá-lo participar da caçada. Mas antes, você tem que provar que é capaz.\n\nEu vou ensinar você a caçar raptores. Merília treinará você para caçar tigres. E o Dom Raciliano é um mestre em rastrear panteras.\n\nMostre o que pode fazer e eu deixarei você participar da caçada de verdade.", rewards = "Você receberá:\n\n"},
[584] = {title = "Cabeças do Clã Sanguescalpo", objective = "Leve a Cabeça de Gan'zulah e a Cabeça de Nezzliok para o caldeirão borbulhante no Acampamento Grom'gol.", description = "Yennikan não estava entre as cabeças encolhidas, graças à Grande Serpente. Mas nós não estamos mais perto de encontrá-lo.\n\nNós precisamos descobrir a verdade, e eu temo que os únicos que saibam sejam o chefe sanguescalpo e seu mandingueiro. Eles são inimigos da tribo Lançanegra e não vão falar por vontade própria. Precisaremos arrancar a verdade dos lábios mortos deles!\n\nTraga-me a cabeça de Gan'zulah e de Nezzliok, o Ríspido, e coloque-as no caldeirão.\n\nEles estão nas profundezas de Zul'Kanda. Traga as cabeças pra mim... elas vão me contar tudo.", progress = "O caldo esverdeado no caldeirão borbulha.", completion = "Você põe as cabeças de Gan'zulah e Nezzliok dentro do caldeirão...", rewards = "Você receberá:\n\n"},
[585] = {title = "Falando com Nezzliok", objective = "Leve um Troféu de Balia'mah, um Troféu de Ziata'jai e um Troféu Zul'Mamwe para o Caldeirão Borbulhante no Acampamento Grom'gol.", description = "<A cabeça de Nezzliok fala.>\n\nÉ verdade. Eu sei para onde o filho dos Lançanegra foi levado. Você me derrotou e prendeu meu espírito, mas eu não direi nada até ser apaziguado!\n\nOs Sanguescalpo estão em guerra com os Rachacrânio. Se quer a minha ajuda, preciso que roube 3 troféus de crânio dos Rachacrânio e ponha-os aqui comigo, para que o meu espírito tenha escravos no Éter. Quero um crânio da pilha de troféus em Balia'mah, outro, de Ziata'jai e o último, de Zul'Mamwe.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[586] = {title = "Falando com Gan'zulah", objective = "Mate 8 Caçadores Rachacrânio, 6 Caçadores de Cabeças Rachacrânio e 4 Berserkers Rachacrânio.\n\nLeve a Armadura Quebrada de Ana'thek para o Caldeirão Borbulhante, que fica no Acampamento Grom'gol.", description = "<A cabeça de Gan'zulah fala.>\n\nEu amaldiçoo você! E também seus ancestrais!!\n\nMinha ira só será aplacada por sangue e vingança, então se é isso que você quer... eis o que deve fazer:\n\nAdentre o coração do covil dos Rachacrânio, ao leste. Mate os mais fortes deles e, se sobreviver... lute contra o chefe deles, Ana'thek, o Cruel. Rá! Se for capaz, traga para mim a armadura partida dele!\n\nQue ele arranque seus braços e pernas e te largue lá, para que os vermes devorem a sua carne pútrida.", progress = "Minha pele fervida pode estar se soltando do meu crânio, mas eu ainda consigo sentir o fedor do seu medo.\n\nRá! Você nunca vai conseguir completar a minha tarefa...", completion = "Não queria nem acreditar, mas não dá para negar o que você fez. E agora que Ana'thek foi derrotado, minha raiva começa a arrefecer.\n\nSeu poder vai trazer grandes feitos para você, &lt;name&gt;, mas um dia... um dia eu ainda verei você no Éter e vamos medir forças mais uma vez.\n\nVou estar esperando.", rewards = "Você receberá:\n\n"},
[587] = {title = "Levaram o rapé", objective = "O Dígui, na Angra do Butim, quer que você leve para ele 15 trouxinhas de Rapé.", description = "Ah, a vida no mar. Não dá pra dizer que é uma vida boa, mas é a minha vida.\n\nQuando eu aporto, eu gosto de arrumar uma boa garrafa de bebida e um rapé de boa qualidade. Péssimos hábitos, eu sei... Mas não existe pirata sem vícios, né?\n\nEntão tu deve imaginar como eu fiquei de coração partido quando descobri que o herborista daqui da cidade ficou sem estoque do negócio. Ele disse que o carregamento dele foi roubado por aqueles malditos Saqueadores da Vela Sangrenta.\n\nEu aposto como <um/uma> <class> destemid<o/a> como você consegue convencê-los a dividir um pouco da mercadoria, hein?", progress = "Teve alguma sorte com o rapé, companheiro?", completion = "Que beleza, &lt;name&gt;! E bem na hora que eu preciso embarcar! Não sei nem como te agradecer, amigo.", rewards = "Você receberá:\n\n"},
[588] = {title = "O destino de Yennikan", objective = "Fale com Kin'weelay.", description = "<Nezzliok fala.>\n\nVocê apaziguou a mim e ao meu chefe. Por isso, vou dizer o que você quer descobrir...\n\nO jovem Yennikan está perdido para o clã dele.\n\nEle foi levado pelo mandingueiro Zanzil, o Pária. Agora, Zanzil controla o corpo e a alma dele. Só a magia mais poderosa pode libertá-lo.\n\nUm tipo de magia que você não possui, <class>.\n\nTalvez seus aliados Lançanegra possam ajudá-lo a salvar Yennikan. Mas eu duvido.", progress = nil, completion = "Zanzil, o Pária??\n\nSe Yennikan está sendo controlado por aquele troll, um mandingueiro poderoso e cruel, sinto dizer que só o que nos resta é uma ponta de esperança. Bem pequena. Minúscula como um inseto.\n\nMas pelo menos é melhor do que não ter esperança alguma.", rewards = "Você receberá:\n\n"},
[589] = {title = "Os cristais cantantes", objective = "Leve 3 Estilhaços Azuis Pulsantes para Kin'weelay no Acampamento Grom'gol.", description = "Reza a lenda que, no âmago da Selva do Espinhaço, existe uma joia muito poderosa que chamamos de Olho da Mente. Se você conseguir trazer o Olho da Mente, talvez a gente consiga usá-la para resgatar Yennikan das trevas.\n\nVou consultar os espíritos da selva para encontrar o Olho da Mente. Enquanto isso, você deve entrar na Mina Veia de Cristal, a sudeste daqui, e coletar Estilhaços Azuis Pulsantes dos Basiliscos Ferroqueixo. Os estilhaços são raros, mas vamos precisar da magia deles para submeter o Olho da Mente à nossa vontade.", progress = "Conseguiu os estilhaços, <name>? Preciso deles para o meu ritual final, quando a gente estiver com o Olho da Mente.", completion = "Muito bem, &lt;name&gt;. Esses estilhaços são bem formados. Já ouço os fragmentos cantando para mim.\n\nEnquanto você estava fora, conversei com os espíritos da selva. Agora já sei onde está o Olho da Mente...\n\n...E também quem o guarda.", rewards = "Você receberá:\n\n"},
[590] = {title = "Palavra de ladino", objective = "Derrote Calvino Murão em Plangemortis.", description = "O quê?\n\nO quê?!\n\nÉ dinheiro que você quer? Eu não te devo nada.\n\nCarta? Que carta? Ah, então vai querer encarar? Está bem, camarada... vem pra mão! Imagina, eu, devendo dinheiro...", progress = nil, completion = "Incrível! Mas que habilidade!\n\nAchei que você seria presa fácil, &lt;name&gt;. Me pegou de jeito, isso sim.\n\nTá aqui o dinheiro que prometi... não é tanto quanto eu disse, mas é porque, antes, eu meio que tava mentindo.", rewards = "Você receberá:\n\n"},
[591] = {title = "O Olho da Mente", objective = "Leve o Olho da Mente para Kin'weelay, no Acampamento Grom'gol.", description = "O Olho da Mente está nas mãos de um ogro-mago, nas profundezas da Gruta de Mosh'Ogg, que fica a leste daqui. O nome dele é Mai'Zoth. Eu conheço ele desde a época em que os Lançanegra moravam na Selva do Espinhaço. Ele é violento e a magia dele é poderosa.\n\nAlém disso, se ele possui o Olho da Mente, então a magia dele está ainda mais forte.\n\nVocê deve enfrentar Mai'Zoth e pegar o Olho da Mente dele. É uma tarefa perigosa, mas é a única esperança que temos de salvar Yennikan.", progress = "Saudações, <name>. Os espíritos cantam em louvor à sua bravura, mas ainda não me disseram nada sobre a sua batalha contra Mai'Zoth.\n\nVocê conseguiu pegar o Olho?", completion = "Você conseguiu o Olho! A sua coragem é lendária, &lt;name&gt;!\n\nE, para pegar o Olho, você precisou derrotar Mai'Zoth e mandá-lo para o Éter. Por isso os espíritos não mencionaram ele antes.\n\nÉ bom que ele tenha partido do nosso mundo, mas agora que ele está no mundo dos espíritos, temo que vá começar um novo reinado.\n\nMas esta é uma preocupação para outra hora. Agora eu tenho que fazer um ritual para submeter o Olho da Mente aos nossos propósitos.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[592] = {title = "Salvando Yennikan", objective = "Leve a Joia de Alma Plena para Nimboya.", description = "Eu transformei o Olho da Mente em uma Gema Anímica. Ela tem dois propósitos. Quando você a usar, Yennikan ficará atordoado. Quando isso acontecer, coloque a pedra na cabeça do indivíduo para que ela atraia e prenda a alma dele.\n\nDepois, leve a gema para Nimboya.\n\nYennikan está com Zanzil, o Pária, nas Ruínas de Aboraz, perto da Praia Cristalina, no distante Cabo do Espinhaço.", progress = "<name>. Kin'weelay me disse que você foi capturar Yennikan.\n\nÉ verdade? Você conseguiu?", completion = "Você conseguiu! Você libertou o Yennikan do domínio de Zanzil e cumpriu uma missão que eu considerava impossível.\n\nNossa tribo recordará o seu nome em canções, &lt;name&gt;, e gravaremos sua história em pedra para que viva para sempre.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCajado Místico de NimboyaMedalha de Bravura\n\nVocê também receberá:\n\n"},
[593] = {title = "Enchendo a Gema Anímica", objective = "Yennikan está atordoado diante de você, a mente em outro lugar...", description = nil, progress = "Yennikan está atordoado diante de você, a mente em outro lugar...", completion = "Você coloca a Gema Anímica na testa dele. Ele estremece quando tem a alma sugada, deixando o corpo — uma casca sem mente — para trás.", rewards = "Você receberá:\n\nJoia de Alma Plena"},
[594] = {title = "Mensagem na garrafa", objective = "Encontre o autor da mensagem na garrafa.", description = "O bilhete diz, em boa caligrafia:\n\nA qualquer um que possa me ajudar...\n\nPor favor, preciso muito de ajuda. Um grande grenhacéu me mantém prisioneira em uma ilha nos Mares do Sul.\n\nNão identifico as estrelas à noite, obscurecidas pelas árvores da selva. Porém, nos dias mais claros, vejo a Praia Selvagem a noroeste e o que parece ser uma frota de grandes navios a sudoeste.\n\nImploro a você, meu possível salvador: por favor ajude-me.", progress = nil, completion = "Você veio me libertar?", rewards = "Você receberá:\n\n"},
[595] = {title = "Os bucaneiros da Vela Sangrenta", objective = "O Imediato Crazz quer que você cheque a enseada ao norte da Angra do Butim.", description = "Tá mesmo dispost$Go:a; a trabalhar, né, companheir$Go:a;!? Não sei bem por que <um/uma> <race> <class> amante de terra firme como você tá oferecendo ajuda aos Saqueadores Aguanegra, mas não vou recusar.\n\nNosso navio acabou de aportar aqui em Angra do Butim e andam dizendo à boca miúda que nossos rivais, aqueles malditos Bucaneiros da Vela Sangrenta, foram vistos por toda a região sul da Selva do Espinhaço.\n\nTodos os marujos tão ocupados enchendo a cara e procurando mulheres. Preciso de <um/uma> <batedor/batedora>.\n\nDê uma vasculhada na enseada ao norte da Angra do Butim, na Costa Selvagem, e veja o que consegue descobrir.", progress = nil, completion = "Em cima do barril, você encontra um mapa com algumas anotações feitas às pressas e umas moedas.", rewards = "Você receberá:\n\n"},
[596] = {title = "Colares de osso ensanguentado", objective = "Leve 25 Colares de Osso Ensanguentado para Kin'weelay no Acampamento Grom'gol.", description = "Me mandaram até Grom'gol pra ajudar Nimboya na missão dele, mas... também tenho minhas razões pra estar aqui, cara.\n\nA tribo Sanguescalpo é inimiga da nossa. Colecionar troféus dos nossos inimigos nos dá poder sobre eles. Tu pode ajudar nossa tribo caçando nossos inimigos.\n\nEles moram lá pras bandas do norte. Acabe com os trolls Sanguescalpo e tire deles os colares de osso ensanguentado. Junte todos que puder e sua recompensa será show.", progress = "Os Sanguescalpo um dia serão esmagados pelos Lançanegra! Você tem os colares do nosso inimigo condenado?", completion = "Ah, maravilha. Vou ferver estes aqui e sugá-los. Os espíritos dos donos deles vão ficar fraquinhos, fraquinhos quando eu transformar esses colares todos em mingau!\n\nToma, &lt;name&gt;. Isso aqui deve pagar o que tô te devendo.", rewards = "Você receberá:\n\nAnel Osso Sangrento"},
[597] = {title = "Os bucaneiros da Vela Sangrenta", objective = "Relate suas descobertas ao Imediato Crazz na Angra do Butim.", description = "O mapa mostra a Angra do Butim com um \"X\" vermelho no ponto exato em que você está.\n\nO texto escrito às pressas diz:\n\n\"Vocês devem montar um acampamento neste local e ficar na surdina. Aguardem o sinal do posto de comando do sul antes de atacar. Se alguém se aproximar de vocês, podem matar.\"", progress = nil, completion = "Claro, lembro de você, &lt;class&gt;. Vejo que voltou da sua missão de reconhecimento...", rewards = "Você receberá:\n\n"},
[598] = {title = "Colar de Osso Partido", objective = "Leve 25 Colares de Osso Partido para Kin'weelay no Acampamento Grom'gol.", description = "Agora que tu reuniu esses troféus dos Sanguescalpo, tenho outra tarefa pra te passar. A tribo dos trolls Rachacrânio vive a sudeste, e durante anos guerreamos com eles. Embora os Lançanegra não morem mais na Selva do Espinhaço, estamos encalacrados aí com esse conflito com os Rachacrânio.\n\nEntão quero que tu vá atrás deles. Junte os Colares de Osso Partido dos Rachacrânio e fale novamente comigo. Se der uma força em nossa guerra, tu vai poder contar pra sempre com a amizade dos Lançanegra.", progress = "Os Rachacrânio vão se arrepender do dia em que nos meteram nessa guerra! E aí, tu cumpriu tua tarefa?", completion = "Tu mostrou teu valor conseguindo esses colares, &lt;name&gt;. Os Rachacrânio tombaram como mudinhas esmagadas por um basilisco!\n\nEssas paradas que tu fez ajudaram a gente pra caramba.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nSapatos LançanegraBotas Lançanegra\n\nVocê também receberá:\n\n"},
[599] = {title = "Os bucaneiros da Vela Sangrenta", objective = "Relate ao Mestre de Frota Chifre do Mar o ataque planejado pelos Vela Sangrenta.", description = "Macacos me mordam! Então os Bucaneiros da Vela Sangrenta estão planejando algum tipo de ataque à Angra do Butim? Como esses patifes se atrevem? Angra do Butim sempre será domínio dos Saqueadores Aguanegra!\n\nIsso é maior do que eu pensava. Relate o que você descobriu ao Mestre de Frota Chifre do Mar imediatamente! O velho lobo do mar está em algum lugar da cidade....", progress = nil, completion = "Bucaneiros da Vela Sangrenta atacando a Angra do Butim? Por que eles não se atiram logo dos Penhascos Panorâmicos? De qualquer forma, ao me trazer esta informação, você prestou um grande serviço à minha frota de espadachins. Os Saqueadores Aguanegra agradecem.", rewards = "Você receberá:\n\n"},
[600] = {title = "Mineração da Empreendimentos S.A.", objective = "Leve 10 Cristais Azuis Canoros para Cranque Mirrabolha na Angra do Butim.", description = "A Empreendimentos S.A. tem uma cadeia de operações na Selva do Espinhaço que impede goblins trabalhadores como eu de ganhar um dinheirinho honesto! Por favor, você tem que me ajudar!\n\nA Empreendimentos S.A. está minerando perto da Mina Veia de Cristal, ao norte. Eles não conseguem entrar na mina em razão de todos aqueles basiliscos por lá, mas ainda podem desencavar Cristais Cantantes das colinas em volta.\n\nTome os cristais deles e mostre que não são donos da selva.\n\nE... hum... me traga esses cristais como prova!", progress = "Tu pegou os cristais??? Eu preciso deles!!!\n\nDigo... preciso saber que a terrível Empreendimentos S.A. está perdendo terreno na Selva do Espinhaço. Porque nós, pessoas honestas, precisamos do nosso ganha-pão! Sabe como é.", completion = "Ótimo! Show de bola! Vou conseguir um bom dinheiro em troca desses cristais aqui!\n\nA Empreendimentos S.A. escolheu a selva errada para minerar, não acha? Tudo ficaria bem melhor se eles saíssem daqui.\n\nObrigado, &lt;name&gt;. Tu fez uma boa ação hoje.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nDisparador GoblínicoPá de Prata\n\nVocê também receberá:\n\n"},
[601] = {title = "Elementais da água", objective = "Consiga 6 Braçadeiras de Elementais da Água para o Barão Rezingada na Angra do Butim.", description = "Os elementais da água foram, sem dúvida, evocados por Gazban, o mago goblin que dizem ter descoberto a Pedra das Marés. Como você já deve saber, a Pedra das Marés amaldiçoa quem a carrega, fazendo o cara se materializar e desmaterializar...\n\nTalvez a gente possa encontrar uma maneira de neutralizar a maldição. Podemos pedir pra um mago de Dalaran analisar o resíduo mágico de uma das braçadeiras dos elementais da água. Mas, pra isso, preciso que você volte à ilha e me traga alguns espécimes.", progress = "E então, teve sorte, <name>?", completion = "Hum, não sei avaliar essas braçadeiras para ver se alguma delas vai servir, mas tenho certeza de que pelo menos uma deve ajudar. Leva elas até Dalaran para que um mago dê uma olhada.\n\nAfinal, não posso deixar a Angra do Butim só pra papear com aqueles velhos magos enfadonhos.\n\nNão que eu esteja preocupado que alguém possa tentar abrir caminho para ser o novo barão. Ninguém tira Rezingada do sério e vive para contar a história.", rewards = "Você receberá:\n\n"},
[602] = {title = "Análise mágica", objective = "Leve as braçadeiras de elementais da água ao Arquimago Ansirem Tecerrunas em Dalaran.", description = "Pelo que soube, os magos de Dalaran estão ocupados com a reconstrução da cidade deles. Tenho certeza de que você pode encontrar um deles com um tempinho livre para dar uma olhada nas braçadeiras. Se tem uma coisa que sei sobre magos, é que eles não resistem a um enigma mágico.\n\nLeve a sacola com as braçadeiras de elementais da água e veja se eles conseguem dar uma luz sobre a maldição da Pedra. Procure o Arquimago Ansirem Tecerrunas. Já tratei com ele antes. Ele sabe muito.", progress = "Se você não se importa, estou muito ocupado agora.", completion = "A Pedra das Marés? Interessante. Talvez você fique surpresa em saber que escrevi um livro — vários anos atrás — a respeito das lendas do Império Gurubashi. Eu sei um bocado de coisa sobre a Pedra.\n\nPelo menos uma dessas braçadeiras parece ter algum resíduo mágico que eu possa analisar.\n\nVamos ver o que consigo descobrir...", rewards = "Você receberá:\n\n"},
[603] = {title = "Chave de Ansirem", objective = "Fale com Fátia na Angra do Butim.", description = "O mago com a Pedra está ampliando o poder dela através de algum ponto focal... algum tipo de altar. Você deve conseguir destruí-la sobrecarregando-a.\n\nPor acaso, sei exatamente como: uma antiga adaga, outra relíquia dos trolls. Só tem um... pequeno problema.\n\nVeja bem, encontrei a adaga enquanto fazia pesquisas na Selva do Espinhaço, mas a dei para Fátia, minha filha voluntariosa, que fugiu para a Angra do Butim para se tornar pirata.", progress = nil, completion = "Minha adaga? Com mil demônios, você é algum tipo de comediante?\n\nO quê? Você só quer que eu repita a história de como passei de melhor lutadora de facas na Angra do Butim para motivo de chacota dos piratas?\n\nPerdi uma briga de faca para um dos Bucaneiros da Vela Sangrenta — um bando de cretinos... isso é o que eles são! — e ele levou minha lâmina como recompensa.\n\nNão posso ser uma lutadora de facas só com insultos e uma língua afiada...", rewards = "Você receberá:\n\n"},
[604] = {title = "Os bucaneiros da Vela Sangrenta", objective = "O Mestre de Frota Chifre do Mar na Angra do Butim quer que você mate 10 Espadachins da Vela Sangrenta e traga de volta as Cartas de Navegação da Vela Sangrenta e as Ordens da Vela Sangrenta.", description = "Crazz fala muito bem da sua dedicação aos Saqueadores Aguanegra. Vou confiar a você uma missão de extrema importância para a frota.\n\nInfiltre-se no chamado posto de comando do sul que os Bucaneiros da Vela Sangrenta estabeleceram na Praia Selvagem. Invada o acampamento. Traga-me as cartas de navegação deles junto com as ordens escritas. Vamos ver exatamente o que essa escória está tramando.", progress = "Io-hô, <name>! Que notícias você me traz dos acampamentos da Vela Sangrenta ao sul?", completion = "Perfeito, &lt;name&gt;. Agora que sabemos o que eles planejam, é só uma questão de tempo.\n\nE pensar que os idiotas baixaram âncora bem ao largo do cabo...", rewards = "Você receberá:\n\n"},
[605] = {title = "Estilhaços azuis cantantes", objective = "Leve 10 Estilhaços de Cristal Cantante para Cranque Mirrabolha.", description = "Os Cristais Cantantes só existem na Selva do Espinhaço, e são muito valiosos em certos lugares. Posso transportar esses cristais, mas com a maldita Empreendimentos S.A. fica difícil um empreendedor honesto como eu garantir um cascalho!\n\nEntão eu queria te pagar pra pegar um trampo.\n\nOs basiliscos na Selva do Espinhaço comem o cristal. É o que garante a eles aquela pele dura e, às vezes, dá pra extrair um cristal decente dela.\n\nTu pode extrair os cristais de qualquer basilisco, mas os menos sinistros ficam ao longo das margens ao sul de Zul'Kunda, mais para cima.", progress = "Tu já conseguiu os estilhaços, meu chapa? Vou te dar uma parte generosa dos lucros!", completion = "Ah! Esses vão servir perfeitamente, &lt;name&gt;. Sim, joinha, joinha!\n\nToma aqui tua parte, e... foi ótimo fazer negócio com você.", rewards = "Você receberá:\n\n"},
[606] = {title = "Fazendo o Sacode tremer", objective = "Leve 5 Miúdos de Gorila do Vale da Névoa para \"Phillipe \"Sacode\" na Angra do Butim.", description = "Se tem uma coisa que eu não suporto é gente que não paga as dívidas de jogo! E tem muita gente que me deve. Você acha que consegue me ajudar a receber esse dinheiro?\n\nO primeiro é o Phillipe \"Sacode\", que está lá nas docas. Ele me deve um monte de dinheiro depois do jogo da última semana, e eu quero tudo! Mas pra conseguir isso, vamos ter que dar um susto nele.\n\nSaia da cidade e cace alguns gorilas anciãos do Vale da Névoa. Pegue alguns miúdos de gorila e leve-os pro Sacode. Não fale pra ele que são partes de animais. Deixe ele imaginar o pior.", progress = "Ei! Não tô gostando do jeito que você tá me olhando, cara.", completion = "Eca! De onde você tirou isso? Não, não me diga. Eu não quero saber.\n\nQuem te mandou? O Lobo do Mar?", rewards = "Você receberá:\n\n"},
[607] = {title = "Fale novamente com MacKinley", objective = "Leve o Pagamento do Sacode para MacKinley \"Lobo do Mar\"", description = "Toma, leva essa bolsa de dinheiro pro Lobo do Mar e diz a ele que eu ia pagar! Eu juro!\n\nSó enchi a cara e fiquei perdido e... Sabe como são essas coisas! Ele vai entender, você não precisa pegar pesado!", progress = "Venha aqui, <name>. Conseguiu o pagamento do Sacode?", completion = "He he! Bom trabalho! Aposto que Sacode se tremeu todo quando você mostrou os miúdos!\n\nHa ha! Ele dá tanta bandeira que não consigo deixar de ganhar quando jogo com ele!\n\nObrigado pela ajuda, &lt;name&gt;. Agora é a sua vez de receber o pagamento...", rewards = "Você receberá:\n\n"},
[608] = {title = "Os bucaneiros da Vela Sangrenta", objective = "O Mestre de Frota Chifre do Mar em Angra do Butim quer que você mate o Capitão Tranquiláguas, o Capitão Querena e o Mestre de Frota Firalião.", description = "Raios que o partam, Firalião! Ele não seria capaz de conduzir um esquife pelo Lago de Cristal, quanto mais liderar uma frota inteira contra nós.\n\nOs Bucaneiros da Vela Sangrenta pagarão pelo descuido. Agora que sabemos qual é o plano de ataque deles, podemos reagir antes que saibam o que os atingiu.\n\nMas por que mandar toda a frota fazer o trabalho de um bravo grupo de aventureiros? <name>, leve alguns de seus companheiros mais habilidosos para o ponto de atracamento de Firalião e mate os líderes da Vela Sangrenta. A frota deles naufragará logo depois....", progress = nil, completion = "Ah, que ótimo dia para os Saqueadores Aguanegra! Três vivas para você, &lt;name&gt;!", rewards = "Você receberá:\n\nTúnica de Aguanegra"},
[609] = {title = "Vodu devido", objective = "Leve o Pé-de-clava do Maury, a Luneta Dourada de Janjão e o Anel Enorme do Chucky para MacKinley \"Lobo do Mar\" na Angra do Butim.", description = "Minha lista está diminuindo, mas ainda há gente que me deve.\n\nAgora temos Maury Wilkins, o Manco, Janjão, o Corvo e Chucky Sem-Jeito.\n\nEsses cães sarnentos já estão meses atrasados, e eu acho que estão até evitando a cidade para fugir da cobrança! Depois eu ouvi falar que eles foram amaldiçoados e agora estão vagando pelas ruínas na selva, mas eu não estou nem aí! Eu só quero o que é meu!\n\nEles estão nas Ruínas de Aboraz e nas Ruínas de Jubuwal, a nordeste daqui. Encontre-os e cobre o que eles devem.", progress = "<name>, você encontrou aqueles três cachorros? Catou as coisas valiosas deles?", completion = "Ótimo! Eu vou conseguir um bom dinheiro por essas coisas, especialmente pelo que tem dentro do Pé-de-clava do Maury! Muito obrigado, &lt;name&gt;!\n\nEspero que os três tenham aprendido a lição: nunca aposte algo que você não pode perder.\n\nTalvez já seja tarde pra eles, mas... Deveriam ter mais cuidado ao apostar.", rewards = "Você receberá:\n\n"},
[610] = {title = "Duncan \"Bonitão\"", objective = "Recupere a adaga de Fátia com Duncan \"Bonitão\".", description = "Meu PAI mandou você aqui? E eu achando que o doido velho já tinha desistido de mim.\n\nNão foi culpa dele eu ter fugido. Ele só não deveria ter esperado que eu fosse me dispor a uma vida chata de livros mofados e magos grisalhos, aprumadinhos e empoados!\n\nSe precisar da minha adaga, você vai ter que recuperar com o Duncan \"Bonitão\", um dos Bucaneiros da Vela Sangrenta. Dê a ele um segundo sorriso de presente, e com certeza ele vai abrir mão dela.", progress = "Nunca tinham me derrubado antes, sabia, <name>? Já me cortaram, porque não dá pra entrar em briga de faca sem se cortar, mas ele me acertou no pé da orelha e me apagou. Quando eu acordei, minha faca tinha sumido.\n\nEmbora meu nome possa sugerir outra coisa, eu ainda preciso de uma faca para lutar, e aquela pra mim não tem substituta.", completion = "Você derrotou o Duncan? Acho que você não é tão pé rapada quanto parece.\n\nE acho que isso também quer dizer que é melhor eu não vou chamar você pra briga.", rewards = "Você receberá:\n\n"},
[611] = {title = "A maldição das marés", objective = "Destrua o Altar das Marés com a Lâmina de Fátia, mate Gazban e leve a Pedra das Marés e a Lâmina de Fátia para o Barão Rezingada.", description = "Então você está procurando algum tipo de altar? Bom... Não sou especialista em ruínas trólicas como meu pai, mas ouvi falar de uma cidade trólica submersa perto daquela ilha que você estava procurando. Talvez você deva tentar por lá.\n\nVocê trouxe a minha faca de volta. Acho que você pode pegar emprestada... mas eu quero que você devolva quando terminar.", progress = "Trouxe a Pedra das Marés para mim, <name>? Já abri um espaço e comprei um pedestal para ela.", completion = "Incrível! A Pedra das Marés! Ela terá um lugar de destaque em minha coleção de artefatos trólicos antigos. Viu como parece que há uma maré presa sob a superfície? Magnífica!", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nVeste das Águas Cristalinas"},
[613] = {title = "Abrindo o pé do Maury", objective = "Leve a Chave de Maury para MacKinley \"Lobo do Mar\" na Angra do Butim.", description = "Maldição! Aquele molenga do Maury pôs uma tranca no pé, e eu não consigo abrir! O butim dele está no pé! É isso que ele me deve!\n\nPrecisamos achar a chave para abrir a tranca. Dizem que o Maury jogava a dinheiro com os ogros Mosh'Ogg no covil deles a nordeste, e ele mesmo me contou quando estava vivo que, na última vez em que ele foi lá, os ogros deram-lhe uma coça por ele ter vencido demais. Os ogros lá podem estar com a chave dele.\n\nMas se eles estiverem... Você pode ter que matar um monte até achar o ogro certo...", progress = "Io-hô, <name>! Já achou a chave de Maury?", completion = "Você achou! Obrigado demais, &lt;name&gt;. Você me quebrou uma árvore inteira!\n\nToma aqui. Você merece!", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nBandeja de Coleta"},
[614] = {title = "O baú do capitão", objective = "Leve o Baú do Smotts para Hacklebury Smotts na Angra do Butim.", description = "Eu costumava comandar uma nau que fazia a travessia até Vila Catraca, em Kalimdor. Eu era um capitão bem-sucedido, de reputação impecável.\n\nAté que...\n\nAqueles malditos gigantes do mar me arruinaram! Eles destruíram o meu navio, mataram a minha tripulação e me abandonaram num bote salva-vidas a caminho da Angra do Butim. Um dos gigantes, Gorlash, roubou o meu baú. Disse que queria como porta-rapé. Que audácia!\n\nEu ouvi falar que ele vaga pela costa, a leste daqui. Encontre-o e recupere meu baú! Faça isso e você terá conquistado a gratidão de um capitão.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nBraçadeiras Feixessangue"},
[615] = {title = "O alfanje do capitão", objective = "Fale com Zero.Depois de perder meu primeiro navio para aqueles gigantes, eu comprei outro. Batizei como Vingança do Smotts. Enchi de suprimentos, contratei tripulação e zarpei atrás dos desgraçados. Encontrei os malditos, mas... eles venceram. Destroçaram o Vingança do Smotts, mataram minha segunda tripulação e me mandaram de volta noutro bote.\n\nDessa vez outro dos gigantes, Negolash, roubou meu alfange.\n\nEnfrente Negolash e me traga meu alfange!\n\nFale com o Zero. Ele foi meu cozinheiro no Vingança do Smotts, e sobreviveu ao último ataque. Ele pode ajudar você a achar o gigante.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", description = "Depois de perder meu primeiro navio para aqueles gigantes, eu comprei outro. Batizei como Vingança do Smotts. Enchi de suprimentos, contratei tripulação e zarpei atrás dos desgraçados. Encontrei os malditos, mas... eles venceram. Destroçaram o Vingança do Smotts, mataram minha segunda tripulação e me mandaram de volta noutro bote.\n\nDessa vez outro dos gigantes, Negolash, roubou meu alfange.\n\nEnfrente Negolash e me traga meu alfange!\n\nFale com o Zero. Ele foi meu cozinheiro no Vingança do Smotts, e sobreviveu ao último ataque. Ele pode ajudar você a achar o gigante.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[616] = {title = "A ilha assombrada", objective = "Fale com o Barão Rezingada na Angra do Butim.", description = "O Barão Rezingada me fez ficar de butuca pra rumores estranhos e deslocados na Angra do Butim e na Selva do Espinhaço. Geralmente eu não dou muito crédito a histórias de fantasma, mas ouvi uns marinheiros na taverna falando de uma ilha assombrada ao largo da costa. Disseram que tem elementais da água e um fantasma de um goblin ensandecido.\n\nSe você estiver indo falar com o Rezingada, pode contar essa história a ele?", progress = nil, completion = "Interessante. Acho que lembro de ter lido um livro um tempo atrás que sugeria que...\n\nAh, claro. Era uma lenda trólica. Se o que o Krazek relatou for verdade, talvez eu consiga uma Pedra das Marés para a minha coleção de artefatos antigos e relíquias em breve.", rewards = "Você receberá:\n\n"},
[617] = {title = "Akiris aos montes", objective = "Leve 10 Feixes de Junco de Akiris para o Corsário Bloads na Angra do Butim.", description = "Tá procurando trabalho, <name>? Pode ser que eu tenha alguma coisa se você topar um desafio.\n\nEu tenho um amigo, o Groy, que fatura uma bolada vendendo o que as nagas chamam de junco de akiris. É tipo um junco marinho bem valioso. Não sei bem pra que serve, mas ele paga bem pelo troço.\n\nTraz pra mim uns dez feixes e a gente pode fazer negócio. As nagas foram vistas pela última vez na praia ao norte daqui.", progress = "Se você não tem nada de novo pra falar comigo, então vaza, <name>.", completion = "Muito bom, &lt;name&gt;. Dez feixes nem foi lá um desafio tão grande, né? Sem ofensa às nagas, mas todo mundo tem que se virar de algum jeito na vida.", rewards = "Você receberá:\n\nCinta Causticante"},
[618] = {title = "Confrontando Negolash", objective = "Leve 10 Asas de Urubu Grelhadas e 5 garrafas de Vinho Selvavinha até o bote salva-vidas do Capitão Smotts.\n\nMate Negolash e leve o Alfanje de Smotts para o Capitão Smotts na Selva do Espinhaço.", description = "O Negolash é um gigante esfomeado.\n\nQuando os gigantes destruíram o nosso navio, o Negolash foi direto na minha cozinha. Ele não queria comer a tripulação, como os outros gigantes. Ele queria vinho... e a minha asa de urubu grelhada. É a minha especialidade, e eu tive que ir pegar a receita lá nos Ermos!\n\nColoca isso no nosso bote velho, a sudeste daqui, seguindo a costa. Quando o Negolash sentir o cheiro dessa comida toda, ele vem com certeza!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nBússola de Smotts"},
[619] = {title = "A sedução de Negolash", objective = "É um bote salva-vidas abandonado, que traz na lateral, em pintura descascada e esmaecida, a inscrição:\n\n\"Vingança de Smotts\"", description = nil, progress = "É um bote salva-vidas abandonado, que traz na lateral, em pintura descascada e esmaecida, a inscrição:\n\n\"Vingança de Smotts\"", completion = "Você enche o bote de comida e bebida...", rewards = nil},
[620] = {title = "A Faixa com Monograma", objective = "Devolva a Faixa com Monograma ao proprietário.", description = "A faixa refinada traz as letras \"HS\" bordadas na bainha.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[621] = {title = "O segredo de Zanzil", objective = "Leve 12 amostras do Preparado de Zanzil para Cranque Mirrabolha na Angra do Butim.", description = "Zanzil, o Pária, vive com os seguidores nas ruínas de Jubuwal e Aboraz, a nordeste da Angra do Butim. Ele se exilou da própria tribo. Por quê? Bem...\n\nZanzil tem métodos para torcer a mente das pessoas, e seus líderes o temem por isso. Ele usa um preparado de plantas silvestres em seus seguidores para fortalecer-lhes os corpos e suprimir-lhes as vontades.\n\nO preparado dele é muito útil para quem deseja controlar pessoas.\n\nTraga amostras desse preparado que vou achar um uso para ele.", progress = "Você está com as amostras do Preparado de Zanzil?", completion = "Ah, muito bem! Aqui está seu pagamento.\n\nE aqui tem um extra... use com sabedoria.", rewards = "Você receberá:\n\nCinto de Corrupção"},
[622] = {title = "Fale novamente com o Cabo Kaleb", objective = "Leve a Caçarola Melhorada do Krazek para o Cabo Kaleb no Acampamento dos Rebeldes.", description = "Enquanto você esteve fora, eu consertei a caçarola que você trouxe. Até coloquei uma válvula de vapor extra e, olha só, tem uma nova lâmina no fundo que corta até osso de costela!\n\nSim, sim, todos os cozinheiros vão adorar a Caçarola Meia-boca do Krazek! Tenho certeza que o dono dela vai ficar satisfeito.", progress = "Olá, <name>. Conseguir chegar à Angra do Butim? O goblin Krazek consertou a minha caçarola?", completion = "Está consertada. Maravilha, agora posso começar a fazer o jantar. Obrigado, &lt;name&gt;.\n\nEspero que tenha corrido tudo bem na Angra do Butim. É um lugar que pode ficar bem perigoso. Ouvi boatos de que os goblins arrastam marinheiros bêbados para seus navios de escravos e levam sabe-se lá pra onde, sabe-se lá pra quem...", rewards = "Você receberá:\n\nCasquete da Harmonia"},
[623] = {title = "Akiris aos montes", objective = "Leve um Feixe de Junco de Akiris para o Corsário Groy, em Theramore.", description = "<name>, já que você está aqui, recebi uma mensagem do Groy quando você estava fora. Ele disse que ficou satisfeito com o último carregamento que chegou e tem um cliente que encomendou mais para já.\n\nSó vou para Theramore daqui a uma semana, mas, se estiver dispost<o/a> a fazer uma viagem, você pode levar o produto pra mim.\n\nQue tal uma aventura? Você sabe onde fica o Pântano Vadeoso, não sabe? Mesmo assim, tome cuidado, Theramore pode ser perigosa para quem está em companhias erradas.", progress = "Sim?", completion = "O quê? Tenho um milhão de coisas mais importantes pra fazer do que falar com você, &lt;class&gt;. Desembucha logo ou dê o fora.\n\nHã? Foi o Bloads quem te mandou? O que você tem aí?\n\n\n\nNada mau, nada mau mesmo. Esse feixe vai deixar o meu cliente muito satisfeito.\n\nAposto como a sua próxima pergunta tem a ver com dinheiro, não é, &lt;class&gt;? Claro que é!\n\nPegue esta moeda e aceite um conselho: some das minhas docas.", rewards = "Você receberá:\n\n"},
[624] = {title = "Charada do Cortello", objective = "Resolva a charada.", description = "Tem uma ponte ornada e bem viajada\nA leste de Vento Morto e ao sul de Cristarrubra\nEmbaixo dela algo espera por você\nAlguns chamam de dica, outros, de pista\nLá vai jazer até passarem todas as águas\nAguardando a hora certa no Pântano das Mágoas", progress = nil, completion = "Este pergaminho ensopado contém a próxima pista...", rewards = "Você receberá:\n\n"},
[625] = {title = "Charada do Cortello", objective = "Resolva a charada.", description = "Existe uma terra perdida em Kalimdor\nOnde o perigo destruiu corações e mentes\nA leste dos Sertões o clima é rigoroso\nMelancolia e desgraça marcam o Pântano Vadeoso\nMas é lá que fica a dica para quem aceita o teste\nEscondida bem no fundo de uma caverna a sudoeste", progress = nil, completion = "Você encontra um pergaminho mofado.", rewards = "Você receberá:\n\n"},
[626] = {title = "Charada do Cortello", objective = "Resolva a charada.", description = "Por Lordaeron! Na grande terra das revoltosos\nOnde tantos soldados causaram choros copiosos\nEncontre o caminho em meio à confusão\nPara então chegar ao fim da sua longa missão\nNo litoral das Terras Agrestes há uma cachoeira dourada\nNo fundo dessas águas um tesouro o aguarda!", progress = nil, completion = "Você abre o baú e encontra...", rewards = "Você receberá:\n\nMochila do explorador"},
[627] = {title = "Um favor para Krazek", objective = "Leve 4 cargas de Minério de Pedra-sangrenta Inferior para Krazek, na Angra do Butim.", description = "Tem uma caverna na montanha da Garganta Seca, no extremo norte do Planalto Arathi. Ela guarda um minério raro: pedra-sangrenta. A pedra-sangrenta dessa caverna não é da melhor qualidade, mas mesmo assim é valiosa... para quem sabe como usar.\n\nE eu estou precisando desse minério.\n\nVou consertar sua caçarola meia-boca, mas, enquanto isso... traga algumas amostras de pedra-sangrenta para mim. Se me trouxer o suficiente, eu lhe dou um bônus.\n\nSe você não tiver a perícia pra extrair a pedra... encontre alguém que tenha!", progress = "Ah, olá! Conseguiu trazer a pedra-sangrenta, <name>?", completion = "Esplêndido! Você me fez um grande favor.\n\nAqui está o seu pagamento.", rewards = "Você receberá:\n\n"},
[628] = {title = "Excelsior", objective = "Traga uma Pele de Crocolisco Ancião para Drizzlik, na Angra do Butim.", description = "Depois que a parte de dentro fica pronta, é só fazer a parte de fora. Para isso, eu uso o couro dos melhores crocoliscos anciãos. Ele é mais durável e serve como uma tela melhor para podermos colocar os desenhos e as formas que caracterizam a linha Excelsior do Drizzlik.\n\nÉ difícil encontrar o couro perfeito, no entanto. O único que serve para o meu projeto é o de crocoliscos anciãos de água salgada. Eles só saem para defender os outros crocoliscos de água salgada, então você vai precisar matar alguns jovens para atrair os anciões.", progress = "Li em algum lugar que os crocoliscos de água salgada estão entrando em extinção... Péssimas notícias para o meu negócio, hein?\n\nMas e aí, você trouxe a pele de crocolisco ancião de água salgada que eu pedi?", completion = "Show de bola! Graças a sua ajuda, vou conseguir terminar o pedido e enviá-lo para o Diretor Charadox no próximo navio pra Inframina — e antes do prazo!\n\nPor você ter trabalhado tão bem, eu fiz um par extra pra você. Por minha conta.\n\nE lembre-se, não existe bota mais chique do que as da linha Excelsior do Drizzlik!", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nBotas Excelsior"},
[629] = {title = "O Arrecife Torpe", objective = "Leve um Estilhaço de Tabuleta para Kin'weelay, no Acampamento Grom'gol.", description = "Há muito tempo, um grande tremor de terra afundou uma antiga cidade trólica sob as águas da Costa Selvagem, a noroeste. Esse lugar é conhecido como Arrecife Torpe, porque murlocs hoje moram nas ruínas da cidade, atacando qualquer um que se aproxime.\n\nExiste uma velha tabuleta entre as ruínas que conta a antiga história de Gri'lek, um herói lendário dos trolls. A história é sagrada para a tribo dos Lançanegra e, embora a tabuleta seja grande demais para ser transportada, eu quero um estilhaço dela para consagrar nossa nova casa em Orgrimmar.", progress = "Vou aguardar o Estilhaço de Tabuleta, <name>.", completion = "Isso é ótimo. Quando voltar a Orgrimmar, vou colocar este estilhaço em um lugar sagrado. Ele vai nos lembrar da honra e da glória de Gri'lek.\n\nObrigado, &lt;name&gt;. Seus feitos marcam uma amizade cada vez mais sólida com a tribo dos Lançanegra.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[630] = {title = "Mensagem na garrafa", objective = "Mate o Rei Mukla e fale novamente com a Princesa Manda-chuva com a Chave para libertá-la.", description = "<name>, eu acredito em você e em todos que você consiga reunir para ajudar a me libertar. O grande Rei dos Grenhacéu, Mukla, é o meu captor. Ele comanda todas as feras dessa ilha, e elas me mantêm aqui contra a minha vontade, com a ajuda dessa corrente. Você precisa matá-lo e encontrar a chave que abre esse grilhão, e eu serei eternamente grata a você.\n\nFrequentemente, eu ouço os rugidos dele vindo do lado sudeste da ilha. Por favor, tenha cuidado. Ele não deve ser subestimado.", progress = "Graças aos meus ancestrais alguém veio me salvar. Você trouxe a chave para me libertar?", completion = "&lt;name&gt;, você me salvou de uma grande desonra. Se eu pudesse, ofereceria a você um lugar na minha tribo. Mas, nesse momento, só posso lhe oferecer esse pequeno presente para agradecer a coragem e nobreza que você demonstrou.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nArgola de Nariz da Princesa Manda Chuva"},
[631] = {title = "Ponte Thandol", objective = "Rhag Garmason quer que você encontre o velho Ferratranca.", description = "Pues, teve uma explosão gigantesca nas colinas. Te afianço que foi horrível!\n\nMuitos soldados de buena cepa morreram defendendo a Ponte Thandol. Trançalonga disse que vamos nos vingar dos Ferro Negro antes que seque o sangue deles.\n\nSó que a situação não está fácil e não temos reforços a caminho. Trançalonga mandou o velho Ferratranca até a ponte leste para ver se dava para protegê-la.\n\nPero já se passaram vários dias e não tem contingente suficiente para procurar por ele em uma Dun Modr sitiada.\n\nTalvez tu possa encontrar ele.", progress = nil, completion = "Você vasculha o pequeno cadáver enânico e encontra, gravado no capacete, o nome Ebenezer Ferratranca.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[632] = {title = "Ponte Thandol", objective = "Apresente-se novamente a Rhag Garmason e mostre a ele o pergaminho encontrado ao lado do cadáver de Ferratranca.", description = "Parece que o pobre do velho Ferratranca caiu em uma emboscada dos Ferro Negro. Você percebe que há um pergaminho perto do punho cerrado do cadáver.\n\nDê a notícia trágica a Rhag Garmason e mostre-lhe o que encontrou.", progress = "Algum sinal do Ferratranca, <name>?", completion = "Minha nossa, a notícia é grave....", rewards = "Você receberá:\n\n"},
[633] = {title = "Ponte Thandol", objective = "Destrua a Caixa de Explosivos.", description = "Não pode ser! Pobre Ferratranca. Que tragédia...\n\nNão podemos permitir que a morte dele seja em vão. O pergaminho que tu descobriste indica que Kam Fundafúria, a mente criminosa por trás do ataque, conseguiu esconder uma caixa de explosivos perto da Ponte Thandol, que fica no Planalto Arathi.\n\nSe esses explosivos chegarem à ponte, nossa principal linha de suprimentos para o norte será cortada. É preciso destruir a caixa, <name>. Fale novamente comigo quando sua missão estiver completa.\n\nTu és nossa única esperança.", progress = nil, completion = "A ponte remanescente está salva! Tu prestaste um enorme serviço ao Rei Magni e ao povo de Khaz Modan.\n\nPues agora precisamos resistir até que os reforços chegarem aqui...", rewards = "Você vai poder escolher uma dessas recompensas:\n\nManto de Guarda AnãoCalças das Terras Pantanosas\n\nVocê também receberá:\n\n"},
[634] = {title = "Súplica à Aliança", objective = "Procure a Capitã Nélia, em Ponta do Refúgio.", description = "Eu peço a ti mais um pequeno favor, <class>. Estamos acá esperando as reservas de Altaforja. Pero Roggo descobriu que há um acampamento da Aliança ao norte do Planalto Arathi, na Ponta do Refúgio.\n\nVá até lá e procure a Capitã Nélia. Pergunte a ela se a Aliança pode ceder alguns soldados para que retomemos Dun Modr e a Ponte Thandol.", progress = nil, completion = "Minhas condolências a Trançalonga e seus bravos soldados. A notícia da tragédia na Ponte Thandol e a queda de Dun Modr me deixam de coração apertado.\n\nO problema é que Stromgarde também caiu! É por isso que estamos aqui na Ponta do Refúgio. Nossa tarefa é recuperar nossa grande cidade.\n\nA honra e o dever exigem que não saiamos do Planalto enquanto a justa bandeira do rei não for novamente desfraldada em Stromgarde. Mandarei alguém a Trançalonga avisando que, infelizmente, ele precisa se virar sozinho...", rewards = "Você receberá:\n\n"},
[635] = {title = "Um cristal nas montanhas", objective = "Encontre o cristal da sua visão.\n\nItem fornecido:\n", description = "O estilhaço pulsa quando você o segura.\n\nVocê sente algo como uma súplica vindo dele, como se alguém estivesse tentando se comunicar, alguém que precisa desesperadamente de ajuda.\n\nUma visão aparece em sua mente, das montanhas da parte norte de Arathi e de um cristal brilhante que brota daquelas terras.\n\nO lugar atrai você.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[637] = {title = "Carta de Sully Balof", objective = "Entregue a Carta de Sully Balof a Sara Balof.", description = "O envelope encharcado e maltratado por muitas batalhas se dissolve quando você o pega. Agora, você tem em mãos as páginas de uma carta escrita com extremo cuidado.\n\nA carta está endereçada a uma Sra. Sara Balof, em Altaforja, e começa assim: \"Querida Sara...\"", progress = "Você tem a aparência cansada de quem viajou grandes distâncias e testemunhou muitas tribulações, <class>. Vivemos épocas difíceis, que testam as melhores almas. Que notícias você me traz?", completion = "Você viu meu querido Sully preso debaixo das águas gélidas? Diga que não é verdade!\n\nDeixe-me ver o que ele escreveu...", rewards = "Você receberá:\n\n"},
[638] = {title = "Matatroll", objective = "Viaje até Ruína do Martelo, no Planalto Arathi, e fale com Zengu.", description = "Com Thoras Matatroll morto e Stromgarde em ruínas, Vol'jin me orientou a fazer o possível e o impossível para tirar a lendária espada Trol'kalar das mãos humanas para que possamos usá-la em batalhas futuras contra os trolls de Zul'Gurub.\n\nZengu, um de nossos caçadores mais experientes, foi enviado para Ruína do Martelo, na parte nordeste do Planalto Arathi, para supervisionar a obtenção da arma.", progress = nil, completion = "Tudo bem, &lt;class&gt;? A tarefa que Vol'jin nos confiou vai ser difícil, e qualquer ajuda que você puder nos dar será muito bem-vinda.", rewards = "Você receberá:\n\n"},
[639] = {title = "Signo de Strom", objective = "Obtenha o Signo de Strom para Zengu, em Ruína do Martelo.", description = "Em tempos antigos, trolls lutaram contra o império arathoriano e nosso adversário mais capaz, o Lorde Ígnaeus de Strom, chamado de Matatroll. Ele entrou em batalha empunhando a Trol'kalar, ou matadora de trolls, na antiga língua deles.\n\nHoje, porém, os filhos de Matatroll estão mais fracos, pois seu baluarte foi reduzido a ruínas.\n\nA espada é protegida por diversos signos, que foram divididos entre os sobreviventes de Stromgarde, e precisamos recuperá-los.\n\nO primeiro foi levado pela Camarilha, ele deve estar com um de nossos homens em Stromgarde.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[640] = {title = "O signo partido", objective = "Recupere os 11 Fragmentos de Signo dos defensores de Stromgarde e leve para Tor'gan, em Ruína do Martelo.", description = "A tumba de Thoras Matatroll é protegida por três signos: o primeiro deles você recuperou da Camarilha. Os outros dois vão ser mais difíceis de se obter, porque estão sob a posse da milícia de Stromgarde, que está firmemente entrincheirada na cidade.\n\nO signo de Thoradin foi dividido em onze pedaços pelo filho de Thoras, Galen, e disperso entre os defensores de Stromgarde. Recupere os pedaços e leve até Tor'gan, o troll xamã. Ele será capaz de juntar todos e deixar o signo íntegro novamente.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[641] = {title = "Signo de Thoradin", objective = "Leve o Signo de Thoradin restaurado para Zengu, na Ruína do Martelo.", description = "A reconstrução funcionou perfeitamente. Agora o signo pode ser usado para remover o selo da tumba de Matatroll. Leve de volta para Zengu, com certeza ele vai lhe dar mais alguma missão.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[642] = {title = "A princesa aprisionada", objective = "Colete 12 Grânulos of Myzrael, depois leve-os até os Fragmentos Iridescentes, em Garganta Seca.", description = "Meu nome é Myzrael. Sou uma princesa da terra, mas os gigantes me prenderam nas profundezas do solo do Planalto Arathi. Estes cristais são a única forma que tenho de me comunicar com a superfície.\n\nPor favor, ajude-me. Os aliados dos gigantes, os kolbods Bigodesseco, possuem um cristal como este na Garganta Seca, a leste daqui. Para energizá-lo, use as partículas de Myzrael que os kobolds carregam.\n\nEu imploro, <name>, ajude-me!", progress = "Esta drusa de cristais se parece muito com os Estilhaços de Myzrael, mas não passa a mesma sensação de segurança.", completion = "Ah! Você ativou os cristais. Já consigo me sentir melhor!\n\nMas infelizmente ainda falta muito para me libertar...", rewards = "Você receberá:\n\n"},
[643] = {title = "Signo de Arathor", objective = "Recupere o Signo de Arathor e leve de volta a Zengu, em Ruína do Martelo.", description = "Não dá para dizer que o príncipe humano controle Stromgarde, muito menos o Planalto Arathi. Mesmo assim, ele ainda trota pelos campos com os restos da tropa de cavalaria. Não consigo entender o motivo, mas quem é que consegue entender o que passa na cabeça dos humanos?\n\nSeja qual for a razão, o importante é que ele deu um dos signos ao Tenente Clamabravo, o líder da cavalaria. Localize-o é traga o signo de volta para mim.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[644] = {title = "Signo de Matatroll", objective = "Mate o Príncipe Galen Matatroll e leve o Signo de Matatroll para Zengu, na Ruína do Martelo.", description = "O último dos signos necessários para destrancar a tumba de Thoras Matatroll está nas mãos do filho dele, que mora no coração de Stromgarde, em uma capela que fica nos distritos dominados pela milícia. O Príncipe Galen está bem protegido e não é um guerreiro fácil de enfrentar, por isso esta missão pode ser deveras difícil.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[645] = {title = "Trol'kalar", objective = "Recupere Trol'kalar de dentro da tumba de Matatroll, em Stromgarde.", description = "O que é isso? Quando reunidos, os quatro signos formam um único grande signo. Não resta dúvida de que esse signo conseguirá romper as proteções colocadas sobre a tumba de Matatroll.\n\nOs restos de Matatroll estão sepultados no Sacrário que fica atrás da capela, em Stromgarde. Recupere Trol'kalar.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[646] = {title = "Trol'kalar", objective = "Leve Trol'kalar para Zengu, na Ruína do Martelo.", description = "A luz se enfraquece até desaparecer e a espada Trol'kalar, guardada dentro de uma bainha de pedra, é liberada.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nArmadura Tingida de SangueEscudo do Lutador do Fosso\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[647] = {title = "Pinga de Mackreel", objective = "Leve a Pinga de Mackreel para o Mestre-cervejeiro Podrágua na Costa Sul.", description = "Abusei da marvada ontem à noite... Trançalonga vai querer minha cabeça se souber que eu desmaiei no meu turno de guarda.\n\nAi meu coco, dor de cabeça da moléstia... Parece um monte de máquinas de cerco me atropelando.\n\nE essa agora, olha só a hora! Prometi ao mestre-cervejeiro Podrágua, na Costa Sul, que eu ia pagar minha dívida com pinga.\n\nMas só faltam 15 minutos pra eu estar oficialmente atrasado! Por favor, leve esse lote de pinga pra ele! E se apresse!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[648] = {title = "Resgate OOX-17/TN!", objective = "Escolte o OOX-17/TN até Porto de Bondebico e apresente-se ao Olhatorto Obnótico, na Angra do Butim.", description = "A voz do gnomo irrompe novamente de dentro do robô:\n\n\"Preciso levar OOX-17/TN a um lugar aberto e seguro para que ele possa dar início a um demorado procedimento de decolagem. Ele tem ocultação embutida, mas preciso de tempo para torná-lo operacional novamente. Preciso que você escolte o robô desde a atual localização dele até, digamos, Porto de Bondebico! Este lugar parece perfeito, e extenso o bastante, para a conexão!\"\n\n\"Escolte-o em segurança até o porto e fale novamente comigo na Angra do Butim! Olhatorto Obnótico – desligo!\"", progress = nil, completion = "Ah, VOCÊ é a grande salvadora da OOX-17/TN! Nem sei por onde começar a expressar minha gratidão por sua ajuda! Minha série de robôs teleguiados ainda precisa de ajustes, mas o fato de você ter recuperado ele me poupou horas incontáveis de reconstrução, além de muitas moedas de ouro com custos de fabricação!\n\nEscolha o item que você desejar. Espero que faça bom proveito dele. Mais uma vez, obrigado pelo seu generoso auxílio, &lt;name&gt;. Meus robôs e eu estamos em débito com você!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nDefletor OptomáticoTemporizador de Ovo Termostático\n\nVocê também receberá:\n\n"},
[649] = {title = "Recuperando suco gami", objective = "Fale com Malton Droffers em Orgrimmar.", description = "Tenho um trabalho de salvatagem para você. Se você for fã de suco gami como eu, você vai adorar. Gami! Suco Gami é um drinque, $ô seu bobão:sua pateta;!\n\nExiste um posto avançado abandonado da Horda com um estoque de Suco Gami das Terras Agrestes, da época em que o lugar ainda funcionava. Já não se faz mais suco gami como aquele! Um dos sócios do Malton está por dentro. Siga pra lá e me traga uma caixa de suco gami, mais duas caixas pra minha coleção pessoal! Fale com o Malton, ele vai te passar os detalhes.", progress = nil, completion = "Ah, sim, o suco gami que o papai quer! Ele quer vender o suco para uma taverna aqui de Orgrimmar, mas podemos cobrar mais caro se preservamos as embalagens originais. Eu sei quem pode nos ajudar. Ele fica nas Terras Agrestes, onde o suco gami também está. Mas ele é um, ahm, elfo superior.\n\nUm elfo superior bem bom, não faz parte da Aliança nem nada. Quer dizer, no fim ele não é um elfo superior bem bom, tá mais pra elfo superior bebum... ele ama, AMA um birinaite.", rewards = "Você receberá:\n\n"},
[650] = {title = "Recuperando suco gami", objective = "Fale com Gilveradin Buscassol. O acampamento dele fica nas Terras Agrestes.", description = "O elfo com quem você deve falar se chama Gilveradin Buscassol. Ele conhece todos os vinhos do mundo pelo cheiro! Encontre-o, pois ele sabe como embalar o suco gami. Precisamos de seis garrafas empacotadas, duas pro papai, ou quantas ele pedir, e acredite, ele vai pedir muitas... Depois que o suco estiver empacotado e você já tiver as duas garrafas do papai, volte a falar comigo!\n\nO acampamento de Gilveradin fica próximo de um chalé de elfos superiores nas Terras Agrestes. Esses elfos superiores não são boa gente. Escreva o que eu digo.", progress = nil, completion = "Ah, então você conhece o Malton? Ele é um velho parceiro de bebedeira. Eu encontrei algo que transcende os limites de meros tratados e alianças artificiais: álcool. Se todos nos uníssemos para saciar a sede na suavidade cristalina do álcool, estaríamos em um ambiente espiritual muito mais evoluído.\n\nMas algo me diz que você não veio até aqui para aprender sobre as qualidades redentoras do álcool...", rewards = "Você receberá:\n\n"},
[651] = {title = "Pedras de união", objective = "Consiga a Chave Ardente, a Chave Troante e a Chave Trovejante nas Pedras da União Ocidental, da União Oriental e de União Exterior.\n\nLeve-as até a Pedra da União Interna.", description = "Quatro pedras de vínculo estão espalhadas por Arathi e são elas que me prendem abaixo da terra.\n\nPara destrancá-las, você precisa desativar a mais forte: a Pedra da União Interna. Isso exige que você obtenha as chaves que estão nas outras três.\n\nEssas pedras são vigiadas por guardiões, mas, se você for <habilidoso/habilidosa> e tiver um pouco de sorte, conseguirá bani-los.", progress = "Esta é a Pedra da União Interna. A mais forte das pedras de vínculo. Ela precisa ser destrancada para que as outras se abram.", completion = "&lt;Myzrael fala através da pedra.&gt;\n\nVocê conseguiu! Você destrancou as pedras de união! Já posso sentir os grilhões afrouxarem e a liberdade se aproximar.\n\nVocê é um nobre &lt;class&gt;, &lt;name&gt;. Meu salvador!", rewards = "Você receberá:\n\n"},
[652] = {title = "Quebrando a pedra-chave", objective = "Encontre e mate Fozruk. Leve o Bastão de Ordem para a Pedra-chave, no Planalto Arathi.", description = "A abertura das pedras de união minimizou o meu confinamento, mas ainda há algo que me mantém aprisionada: a pedra-chave que você vê no meio deste círculo de pedras. Para abri-la, precisamos de um artefato que é guardado por Fozruk, um dos vigias de pedra que me capturaram.\n\nPreciso que você pegue esse artefato, chamado pelos vigias de pedra de Bastão de Ordem.\n\n<name>, você já fez tanto por mim... Só mais um pouco e minha gratidão fará a própria estrutura da terra tremer!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[653] = {title = "Aliados de Myzrael", objective = "Fale com Gerrig Agarrosso na Caverna Esquecida de Altaforja.", description = "<Myzrael fala.>\n\nLivre! EU ESTOU LIVRE! Livre para ganhar força, oculta de meus captores. Se me enfrentassem agora, com certeza eles me subjugariam e prenderiam novamente.\n\nMas na hora certa eu confrontarei os gigantes, e eles se arrependerão dos grilhões que me impuseram!\n\nVocê é u<m/ma> nobre aliad<o/a>, <name>. Precisarei de sua ajuda novamente quando chegar a hora. Quando estiver pronta, precisarei que você me convoque à superfície.\n\nFale com Gerrig Agarrosso. Ele está em Altaforja, em uma loja da Caverna Esquecida. Ele sabe como posso ser evocada.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[654] = {title = "Amostras de campo de Tanaris", objective = "Obtenha amostras aceitáveis de 8 basiliscos, 8 hienas e 8 escorpiões. Leve o kit de teste de volta ao Engenheiro-chefe Silvadágua, em Geringontzan, antes que a fonte de energia se esgote.", description = "Quando a fonte de energia é ativada, o kit de teste de campo começa a brilhar e vibrar. Parece que está funcionando como esperado.\n\nDe acordo com o Engenheiro-chefe Silvadágua, você tem duas horas para coletar os dados necessários no deserto de Tanaris. Se não coletar em duas horas, todos os dados serão perdidos e você precisará comprar outra fonte de energia.\n\n. Fale novamente com ele quando concluir a tarefa.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[655] = {title = "Ruína do Martelo", objective = "Fale com Tor'gan em Ruína do Martelo.", description = "Ainda me lembro de tudo, como se fosse ontem. Tantos anos sem esperança, com madeira e aço me ligando a este lugar terrível. Agora, finalmente, finalmente! Ali estava o Chefe Guerreiro. O Martelo da Perdição em sua placa da meia-noite, o Martelo de Guerra em punho.\n\nFoi... terrível demais... ver o golpe de um covarde derrubar nosso maior herói. Ele atacou pelas costas, a cavalo, e o trespassou com a lança... Eu caí de joelhos. Que esperança restava? Que esperança resta?", progress = nil, completion = "Ah, você está falando de Gor'mul. Sim, ele diz que nunca se recuperou da morte do Martelo da Perdição.\n\nGor'mul estava cativo aqui quando Thrall e Orgrim lutaram para libertar o acampamento, mas, quando a terra tremeu e nossos irmãos escaparam, ele ficou para trás. Vagou sozinho durante anos sem saber para onde ir, sem esperança, até que voltamos e nos estabelecemos aqui, em Ruína do Martelo.", rewards = "Você receberá:\n\n"},
[656] = {title = "Evocação da Princesa", objective = "Vá até os Estilhaços de Myzrael, evoque-a e derrote-a.\n\nColete os Grilhões Sobrenaturais e vincule aos Estilhaços de Myzrael.", description = "Você precisa derrotar Myzrael antes que ela fique mais forte. Caso contrário, ela vai enfrentar seus captores assim que reunir força suficiente. Alguns acreditam que podem controlá-la, mas, se isso fosse possível, por que os gigantes a acorrentariam?\n\nÉ uma lógica meio falha, não acha?\n\nPra derrotá-la, evoque-a nos Estilhaços de Myzrael, onde vocês conversaram pela primeira vez. Depois de derrotar ela, colete e vincule os Grilhões Sobrenaturais aos Estilhaços de Myzrael.\n\nVocê precisa fazer isso para que ela seja aprisionada de novo.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nEstilhaço Cristalino Pulsante"},
[657] = {title = "Sinais de uma nova peste?", objective = "Fale com Kinelory.", description = "<Quae dá uma folheada na pasta.>\n\nMUITO interessante. Pelo visto, o Boticário Jorel vem trabalhando em experimentos letais aqui em Go'Shek. Não sei o que Eira dos Montes tem a ver com isso, mas Phin ficaria contrariado se não fizéssemos nada para impedi-los.\n\nNós tivemos um arranca-rabo com os orcs na sua ausência, e eu não ando muito em forma para ajudar Kinelory. Se estiver a fim, ela vai precisar de proteção quando for à fazenda roubar as partes da pesquisa que o Jorel ainda não tiver enviado para a Serraria Tarren.", progress = nil, completion = "Se isso for trazer paz ao Phin, eu mato cada um dos Renegados com minhas próprias mãos. Ele fez muito por nós depois que a peste chegou. Nós achamos que jamais voltaríamos a ter amigos, que dirá uma família.\n\nVenha me avisar assim que estiver preparado. Nós partiremos imediatamente.", rewards = nil},
[658] = {title = "Sinais de uma nova peste?", objective = "Encontre a Mensageira Renegada e leve a Pasta Lacrada dela para Quae nas redondezas da Fazenda Go'Shek.", description = "Phin pediu para ver como estamos? Não me espanta... ele é cismado demais.\n\nEstamos vigiando a fazenda há dias. Diz a Kin que ela viu uma mensageira saindo da casinha ao sul, não faz muito tempo.\n\nEla fala pelos cotovelos, mas pelo menos posso confiar nos instintos dela quando estamos em áreas perigosas.\n\nVocê por acaso é bom farejado<r/ra>? Estamos desconfiados de que a mensageira está levando anotações importantes para a Serraria Tarren. Se botarmos as mãos nesses documentos, pode ser que expliquem o que está acontecendo em Eira dos Montes.", progress = "Nós agradecemos por nos ajudar na questão da mensageira, <name>.\n\nEnquanto você estava fora, a Kin percebeu que um boticário saiu da mesma casa ao sul daqui. Tenho certeza de que estão tramando alguma coisa. Entretanto, eu preciso de provas para não fazer algo de que me arrependa depois.\n\nJá conseguiu descobrir alguma coisa?", completion = "Excelente! \n\nVamos ver o que temos aqui...", rewards = "Você receberá:\n\n"},
[659] = {title = "Sinais de uma nova peste?", objective = "Encontre Quae e Kinelory nas redondezas da Fazenda Go'Shek, no Planalto Arathi.", description = "Saudações, <name>, é um prazer. Eu estou à frente de um grupo de aventureiros que veio investigar acontecimentos bizarros na região.\n\nUm primo em Eira dos Montes teve que sacrificar o cachorro, Fido, porque o bicho ficou imenso por motivos sobrenaturais e começou a atacar todo mundo que chegava perto.\n\nAcho que os mortos-vivos estão envolvidos nisso, e nós estamos procurando provas.\n\nDuas companheiras minhas, Quae e Kinelory, foram para a Fazenda Go'Shek, no Planalto Arathi, e há dias não dão sinal de vida. Você iria atrás delas por mim? Estou ficando preocupado.", progress = nil, completion = "Seja o mais discreto possível, viajante.\n\nOs orcs não gostam muito de serem espionados, e aí nessa fazenda tem coisa.", rewards = "Você receberá:\n\n"},
[660] = {title = "Sinais de uma nova peste?", objective = "Proteja Kinelory até que ela volte para Quae.", description = "Muito bem, <name>. Estou pronta à sua espera.\n\nPodemos ir?", progress = nil, completion = "Você nos fez um grande favor, &lt;name&gt;. Obrigada.\n\nEspero que Kin não tenha causado muitos problemas lá embaixo. Embora seja muito amigável, ela sabe ser mortal quando tem inimigos por perto.", rewards = "Você receberá:\n\n"},
[661] = {title = "Sinais de uma nova peste?", objective = "Encontre Phin Odelic na Costa Sul.", description = "Eu já vi Kinelory bem pior depois de uma noite na taverna. Você fez um bom trabalho ao protegê-la.\n\nPelo menos os itens que ela trouxe da fazenda vão dar ao Phin uma ideia do que está havendo aqui em Arathi. Só para garantir que não haverá retaliação depois do seu ataque, vamos ficar aqui por mais um tempo. Você iria à Costa Sul avisar o Phin?", progress = nil, completion = "Quer dizer então que aquelas duas encrenqueiras estão bem? Haha, fico feliz em saber. Obrigado por me trazer notícias do paradeiro e da situação das duas... é um grande alívio saber que ainda estão vivas. São duas amigas e companheiras de viagem insubstituíveis.\n\nMas... o mais importante é agradecer você direito. Aqui, aceite isto em nome do grupo.\n\nSe tudo der certo, vamos descobrir o que os Renegados andam tramando e pôr fim nessa história.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nVestes QuedapóPerneiras Pé Ligeiro\n\nVocê também receberá:\n\n"},
[662] = {title = "Resgate no mar profundo", objective = "O Primeiro Oficial Nadaliso quer que você recupere as Cartas Náuticas e Diários de Bordo do Extravagância da Donzela e do Espírito de Pinhaprata.", description = "Nabrava levou os navios para a enseada. Estávamos certos de que iríamos achar o tesouro lá. O Emissário atracou em segurança, mas ,quando o Extravagância da Donzela e o Espírito de Pinhaprata se aproximaram, a maré começou a baixar. Foi a coisa mais estranha que eu já vi!\n\nOs dois navios encalharam em cima de uma estrutura... parecia um templo no meio do recife. Afundaram na hora, foi uma tragédia.\n\nTemos que recuperar os diários de bordo e as cartas náuticas antes de irmos embora, antes que algum enxerido bote os olhos neles!", progress = "Como vai a missão de busca e resgate, <name>? Nós precisamos muito dos diários de bordo e das cartas náuticas do Extravagância da Donzela e do Espírito de Pinhaprata.\n\nSe essas informações caírem nas mãos do inimigo, o Mestre de Frota Chifre do Mar vai nos botar para andar na prancha.\n\nIsso se sairmos desta vivos...", completion = "Saber que essas informações estão seguras vai deixar o comando mais tranquilo na Angra do Butim. Bom trabalho, &lt;name&gt;.", rewards = "Você receberá:\n\nMartelo Água Negra"},
[663] = {title = "Terra à vista!", objective = "Fale com o Capitão Nabrava na Enseada de Faldir.", description = "O Lolo viu <um/uma> <race> miudinh<o/a>! Sempre de olho aberto, o Lolo!\n\nBem-vind<o/a> à Enseada de Faldir. O Capitão Nabrava falou que só íamos ficar aqui umas horinhas. O tempo de catar o tesouro e voltar pra Angra do Butim.\n\nMas o Lolo acha que vamos demorar um pouquinho mais. Perdemos dois navios da frota. O Extravagância da Donzela e o Espírito de Pinhaprata, coitados. No fundo do mar, eles estão!\n\nO Lolo vai te sugerir falar com o Capitão Nabrava se tiver intenção de ficar por aqui.", progress = nil, completion = "Saudações, marinheiro.\n\nFoi a maior presepada. O Chifre do Mar periga tomar meu navio se ficar sabendo.\n\nUma engenhoca do Professor indicou que o tesouro perdido estava perto da enseada. Eu atraquei o Emissário e sinalizei para o Espírito de Pinhaprata e o Extravagância da Donzela fazerem o mesmo.\n\nAí o mar se alvoroçou. A maré baixou e os dois navios bateram no recife.\n\nMas não é qualquer recife... tem alguma coisa lá embaixo.\n\nAjuda minha tripulação que tu vai ganhar uma recompensa bem gorda.", rewards = "Você receberá:\n\n"},
[664] = {title = "Afogando as mágoas", objective = "O Capitão Tripaço, na Enseada de Faldir, quer que você mate 10 Saqueadores Espinhadaga e 3 Feiticeiras Espinhadaga.", description = "Argh, eu odeio esta vida. Devia ter virado comida de peixe, em vez de estar aqui respirando ar fresco.\n\nA tripulação fez o que pôde para salvar o meu lindo Extravagância da Donzela. Abençoados os corações daqueles rapazes.\n\nJuanka Nabrava conseguiu chegar à praia, mas nós não tivemos a mesma sorte. O mar nos engoliu, eu juro.\n\nDe uma hora para outra, água por toda parte... e aqueles monstros! Mataram todos os sobreviventes. Maldita escória naga. Daí tudo ficou preto e quando acordei estava aqui.\n\nQuero que todos aqueles monstros morram.", progress = nil, completion = "Belo trabalho, &lt;name&gt;.\n\nAqueles bichos tiveram o que mereceram. Mesmo sabendo que nada trará de volta meu barco e minha tripulação, a morte daquelas nagas me trouxe um pouco de consolo.", rewards = "Você receberá:\n\nLuvas Lobo-do-mar"},
[665] = {title = "Tesouro submerso", objective = "Escolte o Professor Bruzundanga até a caverna e retorne com ele.", description = "Agora que somos Saqueadores Aguanegra de verdade, cabe a nós ajudar Juanka Nabrava a encontrar o tesouro perdido dos elfos.\n\nÉ praticamente impossível encontrar gemas no fundo do mar sem o auxílio dos óculos que o doutor construiu. Precisamos energizá-los com o poder da pedra encantada na caverna logo acima da colina.\n\nO problema é que a caverna é amaldiçoada! Sempre que nos aproximamos, somos emboscados. Me proteja enquanto eu extraio energia da pedra para os óculos.", progress = nil, completion = "Os óculos estão funcionando perfeitamente! Muito bem, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[666] = {title = "Tesouro submerso", objective = "O Doutor Medeixo na Enseada de Faldir quer que você consiga 10 Gemas Élficas e devolva os Óculos para Caçar Gemas quando tiver terminado.", description = "O tesouro ficou tanto tempo no fundo do mar que uma grossa camada de pedra encobriu as gemas, mas a energia contida nesses óculos vai fazer com que você as encontre facilmente.\n\nNada que um toquezinho de gnomicidade não resolva!\n\nUse os óculos para caçar gemas, <name>, e recupere parte do tesouro para o Capitão Juanka Nabrava.\n\nEu mesmo nadaria até lá, mas... ahm... você sabe, tenho negócios a tratar aqui em terra firme... e seca... é, isso.", progress = "E aí? Os óculos funcionaram? Deu tudo certo? Posso fazer alguns ajustes, se precisar.\n\nAh, quando acabar, traga os óculos de volta! Tenho certeza de que o Capitão Nabrava vai precisar deles depois.", completion = "Totalmente excelente, &lt;name&gt;! O Capitão ficará muito satisfeito! E espere só até o Mestre de Frota Chifre do Mar ficar sabendo disso!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nFulminador GnômicoMarrão Servomecânico\n\nVocê também receberá:\n\n"},
[667] = {title = "A morte vem das profundezas", objective = "Proteja Juanka Nabrava durante o ataque.", description = "Nada bom. Parece que os Espinhadaga estão se reunindo para um ataque.\n\nAs nagas são conhecidas pela crueldade em batalha. Elas não vão parar até que eu esteja morto.\n\nNão prezo a minha vida mais do que a da minha tripulação, mas você precisa me ajudar. Meus homens não conseguirão voltar para a Angra do Butim sem minha prática marítima.\n\nAs nagas sairão das águas. Preciso que você fique aqui comigo para nos defendermos do ataque. Pegue um canhão e expulse-as.\n\nEstá <pronto/pronta>, <name>?Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nAnel Água FriaElo Fogo-do-mar\n\nVocê também receberá:\n\n"},
[668] = {title = "Tesouro submerso", objective = "Leve as Gemas Élficas para o Capitão Juanka Nabrava.", description = "Não podemos deixar o Capitão Juanka Nabrava esperando. Ele quer ver essas gemas imediatamente. Afinal de contas, é para isso que estamos aqui!\n\nAlgumas poucas semanas de convivência com esses piratas são suficientes para descobrir que esconder o tesouro deles não faz bem para sua saúde.\n\nTome, <name>, leve-as para Juanka Nabrava.", progress = "Io-ho, <class>! O que veio fazer aqui na ponte?", completion = "Quer dizer que o gnominho serviu para alguma coisa? Eu sabia que tinha um bom motivo para mantermos aquele meio quilo aqui!", rewards = "Você receberá:\n\n"},
[669] = {title = "Tesouro submerso", objective = "Leve a Amostra de Gema Élfica para o Mestre de Frota Chifre do Mar na Angra do Butim.", description = "O Mestre de Frota Chifre do Mar vai gostar de saber da sua descoberta.\n\nMas, como dá para ver, a gente não tem muito como sair daqui. Não só a maré está baixa demais, mas as criaturas que nós perturbamos estão de olho na nossa movimentação.\n\nVocê pode prestar um grande serviço aos Saqueadores Aguanegra se conseguir avisar o Mestre de Frota Chifre do Mar lá na Angra do Butim de que descobrimos o tesouro e estamos tentando resgatá-lo. Leve essa amostra de prova.", progress = "O que o traz à Angra do Butim, <class>?", completion = "Ah, o bom e velho Juanka Nabrava! Eu sabia que ele era o capitão certo para a missão!", rewards = "Você receberá:\n\n"},
[670] = {title = "Tesouro submerso", objective = "Entregue a carta de Chifre do Mar a Juanka Nabrava na costa do Planalto Arathi.", description = "Olha, você parece ser do tipo aventureiro. Minha frota está presa na Angra do Butim até podermos reabastecer.\n\nTem toró pesado armando, <name>, aqui e pelo mundo afora, e eu preciso mandar uma correspondência confidencial para o Juanka Nabrava, que está lá parado na costa de Arathi.\n\nPosso confiar em você para entregar a mensagem confidencialmente e em segurança?", progress = "E então? O Chifre do Mar ficou impressionado? Aposto que o sorriso dele foi da Angra à Catraca.", completion = "Obrigado por fazer essa viagem tão longa e perigosa. Você tem a gratidão dos Saqueadores Aguanegra.", rewards = "Você receberá:\n\n"},
[671] = {title = "Magias torpes", objective = "Obtenha 10 Amuletos de Pedra-sangrenta e entregue-os a Tor'gan em Ruína do Martelo.", description = "Senti algo estranho no ar... uma sensação que fervilha em meu sangue. Tive minhas suspeitas. Saí à caça da fonte da minha inquietação e encontrei, ao oeste, na Casa Grande do Norte, resíduos de evocações demoníacas. Conforme me aproximava, meu sangue começou a fervilhar e a fúria cresceu dentro de mim.\n\nDurante o reconhecimento eu identifiquei um amuleto de pedra sangrenta usado em evocações demoníacas. No entanto, o que eu encontrei estava descarregado. Vou precisar de outro para examinar, mas não ouso voltar à casa grande. Você pode me trazer alguns amuletos?", progress = "Nas minhas veias ainda corre sangue que já foi amaldiçoado, <name>. Se alguém... se alguma coisa está invocando demônios, precisamos impedir.", completion = "Levarei um tempo para decifrar as verdadeiras intenções da Camarilha, mas a associação óbvia com magia negra, demoníaca, me preocupa. Eu lhe direi quando souber mais.", rewards = "Você receberá:\n\n"},
[672] = {title = "Espíritos elevados", objective = "Consiga 10 Olhos de Raptores do Planalto matando os Andarilhos do Planalto ou os Espancadores do Planalto e entregue a Tor'gan em Ruína do Martelo.", description = "Tentamos acomodá-lo, animá-lo, contar histórias de nossa poderosa cidade de Orgrimmar, em Kalimdor, mas nada.\n\nHistórias do nosso jovem Chefe Guerreiro, Thrall, que usa a armadura e o martelo de Orgrim, mas... Pode ser... Pode ser! Uma demonstração de nossa recém-descoberta magia xamanística pode lhe dar um novo alento. Vale a pena tentar!\n\nAlgum objeto, coisa pequena, que possamos fazer para ele... Isso! Traga alguns olhos de raptores e eu verei o que posso fazer.", progress = "Criarei um amuleto que dará a Gor'mul um pouco da força dos raptores. Talvez isso desperte alguma chama nele...", completion = "Excelente. Os espíritos dos raptores que cederam esses olhos darão força a quem usar o amuleto.", rewards = "Você receberá:\n\n"},
[673] = {title = "Magias torpes", objective = "Obtenha a Pedra-sangrenta de Marez Capucho e entregue-a a Tor'gan na Ruína do Martelo.", description = "Não tenho dúvida de que há uma bruxa muito poderosa em Stromgarde. De vez em quando, ela usa uma relíquia arcana mágica para evocar um grande número de terríveis demônios. Temos que acabar com essa fonte de poder.\n\nEncontre a bruxa e mate-a. Traga-me todos os objetos mágicos que encontrar com ela. Tomarei providências para destruí-los e garantir que o poder do artefato não seja mais usado para macular esta terra.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nCasquete de Pele de Draco BrancoBraçadeiras de Prata Radiante\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[674] = {title = "Espíritos elevados", objective = "Entregue o Amuleto de Garra de Raptor a Gor'mul.", description = "Aqui. Entregue esse amuleto ao Gor'mul e veja se a força dos raptores consegue despertar a chama do guerreiro que já correu pelas veias dele.", progress = "Que esperança se pode ter quando a mão de um covarde pode matar o mais poderoso dos poderosos? Que esperança?", completion = "O que é isso? Osso polido? De que me serve osso polido? Um berloque lá vai restaurar a glória da Horda?", rewards = "Você receberá:\n\n"},
[675] = {title = "Espíritos elevados", objective = "Fale com Tor'gan.", description = "<Gor'mul joga o amuleto no chão.>\n\nFoi o Tor'gan que mandou você, não foi? Gah! Por que diabos ele tem piedade de mim? Eu não entendo. Qualquer misericórdia me foi negada quando não me permitiram morrer em batalha com meu Chefe Guerreiro. O destino foi cruel comigo...", progress = nil, completion = "Não funcionou, pelo jeito. A despeito disso, sinto que mostrar a ele o poder dos xamãs deve fazer Gor'mul voltar a si. O poder do amuleto não era suficiente para mostrar a ele nosso novo poder.", rewards = "Você receberá:\n\n"},
[676] = {title = "O Martelo pode ruir", objective = "Mate 8 Ogros Punho de Pedra e 10 Impositores Punho de Pedra. Depois apresente-se a Drum Fel no posto avançado Ruína do Martelo, no Planalto Arathi.", description = "Fracos! Todos eles! E você, <class>, está olhando o quê? Está se achando especial? Vá e prove, então!\n\nO posto avançado Ruína do Martelo, em Arathi, está sendo atacado. Se não mandarmos reforços logo, a queda é iminente.\n\nMas primeiro, antes de sair desembestad<o/a>, pode ser melhor você testar suas habilidades. A nordeste de Stromgarde, em Arathi, fica uma gruta dos ogros da tribo Punho de Pedra. Quando conseguir dar conta dos ogros comuns e dos impositores de lá, apresente-se a Drum Fel, na Ruína do Martelo.", progress = nil, completion = "Então você se acha forte o suficiente para ajudar Ruína do Martelo? Então tá.\n\nOu o Sebo mandou você à gruta dos ogros na esperança de você se matar ou ele acha que você é forte o bastante para nos ajudar. De qualquer forma, agora você já está aqui e eu preciso de mais soldados em nossa luta contra os ataques constantes.\n\nSe você planeja ficar em Ruína do Martelo, então descanse e fale comigo quando estiver pronto. Combate é que não vai faltar.", rewards = "Você receberá:\n\n"},
[677] = {title = "Chamado às armas", objective = "Mate 10 Arremessadores Cascasseca, 10 Caçadores de Cabeças Cascasseca e 8 Mandingueiros Cascasseca. Depois, fale novamente com Drum Fel no posto avançado Ruína do Martelo.", description = "Os trolls Cascasseca e os ogros Punho de Pedra de Arathi se uniram, na tentativa de nos aniquilar. Mas não conseguirão, nem eles, nem ninguém. Vamos trucidá-los e mostrar a todos que nós também fazemos daqui nosso lar.\n\nOs trolls nos ignoraram, e os ogros iriam se fartar de nossa carne e se vestir com nossa pele se deixássemos. E acredite, não iremos deixar nada disso acontecer. \n\nNosso primeiro ataque será contra a tribo Cascasseca ao sul daqui. Mate dezenas deles e só volte quando o acampamento deles feder a sangue.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[678] = {title = "Chamado às armas", objective = "Mate 10 Brutamontes Punho de Pedra e 4 Magi Punho de Pedra. Depois, fale novamente com Drum Fel no posto avançado Ruína do Martelo.", description = "Sua força é evidente, <name>. Agora vejamos como você se sai contra um inimigo de verdade.\n\nOs Punho de Pedra têm um covil a sudoeste daqui, na direção da vila enânica. Se não me engano, os espancadores e magos de lá devem ser um desafio maior para você, como têm sido para nós até agora.\n\nMate quantos quiser e volte em carne e osso caso consiga... ou em espírito caso falhe. Eu saudarei sua tenacidade em batalha de uma forma ou de outra.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[679] = {title = "Chamado às armas", objective = "Mate 15 Xamãs Punho de Pedra e 10 Senhores dos Punho de Pedra e fale novamente com Drum Fel no posto avançado Ruína do Martelo.", description = "A cabeça da fera está pronta para rolar... Força, <name>. Você deu todos os sinais de que é capaz de nos ajudar nesse poderoso golpe final. Os líderes desses ataques se instalaram nas ruínas de Stromgarde, símbolo gritante da fragilidade humana.\n\nEncontre e mate esses senhores dos Punho de Pedra e os lacaios xamãs que tentam nos expulsar dessa terra.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nCaçador SilenciosoRachacrânio\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[680] = {title = "A ameaça real", objective = "Mate Or'Kalar e leve a cabeça dele para Korin Fel no posto avançado Ruína do Martelo.", description = "Você serve bem a meu marido, <class>, mas ele não sabe por que Ruína do Martelo está sob ataque.\n\nOr'Kalar, joguete de Mug'thol em Stromgarde, nutre imenso ódio por meu marido e não deseja nada mais do que a morte de meu amado por tê-lo derrotado anos atrás.\n\nMeus augúrios me dizem que Or'Kalar armou uma armadilha para meu marido, então ocultei sinais da presença do ogro.\n\nMeu marido se precipitaria se soubesse o paradeiro do ogro, e eu peço sua ajuda para matar a criatura.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nKilt BrumaceiroEspada de Ruína do Martelo\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[681] = {title = "Casa Grande do Norte", objective = "Mate 10 Bandoleiros da Camarilha e 6 Mercenários da Camarilha.\n\nDepois, fale novamente com a Capitã Nélia na Ponta do Refúgio.", description = "Estamos em guerra por Stromgarde, e estamos perdendo. Nós, os ogros e a Camarilha dividimos nossa cidade, que já foi tão grandiosa, e cada facção combate as outras nesse impasse maldito.\n\nMas as nossas forças estão minguando, enquanto as dos inimigos crescem.\n\nPrecisamos da ajuda de Costa Sul, mas está difícil receber os suprimentos com a Camarilha controlando parte do território no caminho.\n\nVá até a base da Camarilha na Casa Grande do Norte e mate-os. Quem sabe assim a pressão sobre os nossos comboios de suprimentos não alivia e eles não voltam a chegar?", progress = nil, completion = "Muito bom, &lt;name&gt;. Com as mortes daqueles integrantes da Camarilha, deve ficar mais fácil para nós recebermos os suprimentos da Costa Sul.\n\nTalvez a nossa causa ainda não esteja perdida.", rewards = "Você receberá:\n\n"},
[682] = {title = "Insígnias de Stromgarde", objective = "Entregue Insígnias de Stromgarde para a Capitã Nélia na Ponta do Refúgio.", description = "A Camarilha está em constante guerra contra nossa milícia em Stromgarde. No intuito de encorajar a ralé deles a encarar nossas tropas mais treinadas, os líderes da Camarilha estão oferecendo recompensa para cada Insígnia de Stromgarde que seus soldados pegarem dos corpos dos nossos homens.\n\nQueremos essas insígnias de volta. Não podemos permitir que estes assassinos acabem com a honra dos nossos mortos!\n\nEntre em Stromgarde, mate-os e recupere nossas insígnias.", progress = "Conseguiu alguma Insígnia de Stromgarde, <name>? A Camarilha vai aprender a não tirar proveito dos nossos mortos.", completion = "Obrigada, &lt;name&gt;. Informaremos às famílias dos soldados que eles foram mortos em batalha.\n\nSão péssimas notícias para se receber, mas é melhor do que a incerteza sobre o destino de um ente querido.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nPerneiras de Cavalaria de Stromgarde"},
[683] = {title = "Apelo de Sara Caxias", objective = "Entregue o bilhete de Sara Caxias para o Rei Magni Barbabronze, em Altaforja.", description = "<name>, eu gostaria de pedir um pequeno favor a você.\n\nPegue este bilhete que escrevi para o nosso grande monarca, o rei Magni Barbabronze. Eu sei que essa é uma tarefa menor para alguém tão <destemido/destemida> como você, que já fez tanto pela causa da Aliança.\n\nProcurar o Rei triste deste jeito não ajudaria em nada a minha causa. Tenho certeza de que você entende.", progress = "Então você é <name>, o <class>? As notícias da sua contribuição à Aliança viajaram bastante, sabia?\n\nO que o traz perante mim hoje?", completion = "Vou precisar de um momento para ler atentamente esta carta...", rewards = "Você receberá:\n\n"},
[684] = {title = "Procura-se: Marez Capucho", objective = "Entregue a Cabeça de Marez para a Capitã Nélia na Ponta do Refúgio.", description = "Oferece-se recompensa pela cabeça de Marez Capucho, uma alta conselheira e detetive da Camarilha. Ela foi vista pela última vez em Stromgarde, no território controlado pela Camarilha.\n\nProcure a Capitã Nélia para receber a recompensa.\n\nAviso: Marez é uma exímia bruxa conhecida pelo envolvimento com demônios. Recomenda-se extremo cuidado ao caçá-la.", progress = "Saudações, <class>. Algo a relatar?", completion = "Você matou Marez Capucho? Meus parabéns, &lt;name&gt;. Excelente trabalho!\n\nMarez ameaçava todos os defensores de Stromgarde, e dizem que ela era uma intermediária entre a Camarilha e forças sombrias. Estamos felizes em saber que ela nunca mais nos causará mal.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nBraçadeiras Arcanas com Runas"},
[685] = {title = "Procura-se: Otto e Falcocrista", objective = "Entregue a Cabeça de Otto e a Cabeça de Falcocrista para a Capitã Nélia na Ponta do Refúgio.", description = "A Milícia de Stromgarde oferece recompensa pelas cabeças do Lorde Falcocrista e de Otto, o guarda-costas. Falcocrista lidera as ações da Camarilha no Planalto Arathi, e a morte dele afetaria grandemente os planos desta organização.\n\nSeu guarda-costas, embora não seja um alvo estratégico, é um perigoso inimigo que já matou dúzias de defensores de Stromgarde.\n\nA recompensa deverá ser retirada junto à capitã Nélia.", progress = "Olá. Algo a relatar?", completion = "Falcocrista e Otto? Que maravilha!\n\nAqui está sua recompensa, &lt;name&gt;. Fico muito feliz em pagá-la.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBastão da TristezaBraçadeiras de Mantícora de Batalha\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[686] = {title = "Homenagem real", objective = "O Rei Magni Barbabronze quer que você fale com o Grão-alvanel Orcarmeyer.", description = "Não espero que gente buena como a senhora Caxias entenda o porquê de precisarmos suportar as agruras da guerra pelo bem maior do nosso povo. A perda do marido dela, e a perda de todo anão e anã que luta em nome da Aliança, me pesa na alma.\n\nNão posso trazer o marido dela de volta da Espiral Etérea, mas posso prestar tributo.\n\nEu vou é encomendar um memorial de Floriano Caxias, a ser construído pelo Grão-alvanel Oscarmeyer.\n\nMande a mensagem para ele agora, <class>. Você será <meu/minha> segund<o/a>, nessa questão.", progress = nil, completion = "Então você foi enviado pelo próprio rei Magni Barbabronze, hein?\n\nDeve ser assunto importante...", rewards = "Você receberá:\n\n"},
[687] = {title = "Theldurin, o Perdido", objective = "Encontre Theldurin, o Perdido.", description = "Theldurin, o Perdido, já fez parte de nossa ordem, o Martelo do Crepúsculo. Mas ele enlouqueceu e zombou de nossos ensinamentos. E antes de fugir de nós ele roubou o pergaminho de Myzrael! Não conseguimos pegá-lo, mas há rumores de que ele esteja escondido nos Ermos, louco e balbuciante.\n\nPara trazer a Dama de volta para nós, você precisa achar o pergaminho. E, para isso, você precisa encontrar Theldurin.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[688] = {title = "Aliados de Myzrael", objective = "Fale com Zaruk em Ruína do Martelo.", description = "<Myzrael diz.>\n\nLivre! EU ESTOU LIVRE! Livre para ganhar força, oculta de meus captores. Se me enfrentassem agora, com certeza eles me subjugariam e prenderiam novamente.\n\nMas, na hora certa eu confrontarei os gigantes, e eles se arrependerão dos grilhões que me impuseram!\n\nVocê é u<m/ma> nobre aliad<o/a>, <name>. Precisarei de sua ajuda novamente quando chegar a hora. Quando estiver pronta, precisarei que você me convoque à superfície.\n\nFale com Zaruk em Ruína do Martelo. Ele sabe como posso ser convocada.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[689] = {title = "Homenagem real", objective = "O Grão-alvanel Oscarmeyer, de Altaforja, quer que você lhe traga 5 pedras de Granito de Alterac.", description = "Então nosso bom rei quer construir um memorial em homenagem a um soldado chamado Floriano Caxias? E o pobre diabo morreu na tragédia da Ponte Thandol?\n\nAh, será que um dia teremos paz em Azeroth?\n\nMas qualquer determinação do rei é de suma importância. E, para construir uma obra verdadeiramente imponente e eterna, quero trabalhar com uma das melhores rochas à disposição. Vá até o Monte das Flechas, na Eira dos Montes, e pegue algumas pedras de Granito de Alterac para mim.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[690] = {title = "O pedido de Malin", objective = "Procure Rodapeh na Ponta do Refúgio, no Planalto Arathi.", description = "Saudações, <name>. Você tem jeito de aventureir<o/a>, e eu tenho uma tarefa que precisa ser feita.\n\nO arquimago Trelane, meu primeiro mestre, fugiu de Stromgarde, no Planalto Arathi, não faz muito tempo, e pediu minha ajuda para reaver alguns itens deixados para trás.\n\nA cidade sucumbiu ao cerco, e ele tentou defender a Torre de Arathor... em vão. Já tenho agentes lá no Planalto, mas eles solicitaram que eu enviasse reforços. Interessad<o/a>?", progress = nil, completion = "Mas foi o Malin que te mandou, é? Podia ser o próprio Muradin desesticando o molambo e te carretando até aqui num dragão. Eu estou ocupado! Pauteia ali com o Kryten se tu precisar de alguma coisa.", rewards = "Você receberá:\n\n"},
[691] = {title = "Isto vale ouro", objective = "Obtenha 10 Presas de Cascasseca, 4 Bolsas Medicinais de Cascasseca e uma Faca de Caçador Sombrio para o Aprendiz Kraiten, que aguarda na Ponta do Refúgio.", description = "O Arquimago Malin pediu para que eu e Rodapeh coletássemos reagentes para ele. Confesso que eu não fazia ideia de como consegui-los, mas, agora que você chegou, tudo vai ficar mais fácil.\n\nPreciso de presas e bolsas medicinais dos Cascasseca, e de uma faca de caçador sombrio.\n\nPoderia me trazer esses reagentes? Os Cascasseca vivem no sudeste de Arathi.", progress = "E aí, conseguiu, <name>?\n\n\n\nSe minha pesquisa estiver correta, as presas devem ser de qualquer troll Cascasseca. As bolsas medicinais são dos mandingueiros, e os mais fortes entre os caçadores sombrios devem ter uma faca especial que eles recebem ao completar os ritos de passagem. Não há como saber quais caçadores sombrios estarão com a faca até lutar com eles.", completion = "Incrível! Dá pra ver por que Malin pediu que você se juntasse a nós. Muito bom mesmo!\n\nNesse ritmo eu e o Rodapeh estaremos de volta em Ventobravo logo, logo.\n\nCreio que meu companheiro enânico tenha voltado do reconhecimento do Forte Punho de Pedra. Fale com ele quando estiver pronto.\n\nParece que ele pode ter se complicado um pouco com os ogros, mas não mencione essa parte. Ele é muito sensível a críticas a respeito da habilidade dele como batedor.", rewards = "Você receberá:\n\n"},
[692] = {title = "Os fragmentos perdidos", objective = "Leve o Fragmento de Pergaminho Rasgado, o Fragmento de Pergaminho Amassado e o Fragmento de Pergaminho Chamuscado para Theldurin, o Perdido.", description = "O Pergaminho de Myzrael... o Pergaminho de Myzrael?! Você não quer isso, não! A magia dele pode trazer Myzrael à superfície, e, se você fizer isso, estaremos em apuros.\n\nOu... já pode ser tarde demais. Talvez ela já esteja livre. Talvez ela esteja apenas ganhando força antes de vir contra nós. Se for esse o caso, nós PRECISAMOS do pergaminho!\n\nPena que ele foi destruído. Elementais enfurecidos o roubaram, despedaçaram e agora lutam pelos fragmentos.\n\nNo entanto, se você conseguir juntar esses fragmentos, talvez possamos restaurá-lo...Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[693] = {title = "Entre a varinha e a espada", objective = "Encontre a Varinha de Evocação de Trelane e entregue-a a Rodapeh, na Ponta do Refúgio.", description = "Ei! <name>! Desculpa por ter sido curto e grosso com você mais cedo... E não, isso não é um trocadilho.\n\nEu descobri onde é que foi parar o primeiro item do Trelane: no Forte Punho de Pedra, com o Kor'gresh. E pelo jeito que ele anda lascando raios de gelo nos lacaios, deve estar aprendendo a usar todo o poder da varinha.\n\nO Forte Punho de Pedra fica a sul daqui, e a leste da Ponte Thandol.\n\nEnquanto isso, vou pensar no que fazer com Stromgarde.", progress = "Nosso objetivo é a varinha, <name>. Concentre-se nisso.", completion = "Oigalê, &lt;name&gt;! Um a menos, faltam três. E todos eles estão em Stromgarde.\n\nVê se descansa e recupera a força... A próxima parte do meu plano vai tirar o seu couro. Churrasqueia, sesteia e depois fala comigo.", rewards = "Você receberá:\n\n"},
[694] = {title = "As defesas de Trelane", objective = "Consiga uma Ágata Lazúli e entregue-a ao Aprendiz Kraiten, na Ponta do Refúgio.", description = "Sesteou?\n\nO próximo passo é o seguinte: tem ogro a pampa rodeando a Torre de Arathor, em Stromgarde. Temos que ativar as defesas da torre pra tu ter chance contra a ogreada e ainda chegar viv<o/a> nos itens. Para isso, vamos precisar de uma gema daquelas que os xamãs Punho de Pedra tanto gostam. Eles carchearam tudo da torre, mas não vão ficar muito tempo com elas.\n\nTu pega uma e traz para o Kraiten. Ele sabe um feitiço de encantar para elas servirem de chave.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[695] = {title = "O encantamento de um aprendiz", objective = "Fale com Rodapeh.\n\nItem fornecido:\n", description = "Vamos lá, <name>. A gema só precisa ser pressionada contra um dos pedestais próximos à base da torre para ativar as defesas. Use quando estiver pront<o/a> para entrar na torre e encontrar os itens restantes do Trelane. \n\nBoa sorte. Aguardarei seu retorno aqui no acampamento. Ah! E fale com o Rodapeh antes de partir. Ele lhe passará o resto das informações necessárias.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[696] = {title = "Ataque à torre", objective = "Encontre o Filactério de Trelane, o Orbe de Trelane e a Ágata-de-fogo de Trelane e entregue-os a Rodapeh na Ponta do Refúgio.", description = "Muito bem. Seus alvos são: o Filactério de Trelane, o Orbe de Trelane e a Ágata-de-fogo de Trelane.\n\nEles estão em baús espalhados pela torre. A embretada não é abrir as fechaduras, mas a ogreada de lá.\n\nAs defesas da torre não vão pelear sozinhas. Pega a tua arma, que se eles te lobrigam na roubada é rusga certa!\n\nCuidado e boa sorte.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[697] = {title = "O pedido de Malin", objective = "Leve a Carta Lacrada para o Arquimago Malin, no Distrito dos Magos de Ventobravo.", description = "Leva essa carta lá para Ventobravo e diz pro Malin que eu e Kraiten vamos voltar logo. Tenho absoluta certeza de que ele vai te recompensar bem, <name>, por ter ajudado a gente.\n\nBoa viagem.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nBroquel VigilanteBotas Áleas\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[698] = {title = "Falta de suprimentos", objective = "Leve 8 Ancas Não Tratadas de Dentesserra para Tok'Kar no Pântano das Mágoas.", description = "Estou trabalhando para juntar alguns suprimentos para o nosso posto avançado perto da praia a leste daqui. Tok'kar faz o que pode, mas, entre os turnos de guarda e cozinhar para os soldados dele, não sobra muito tempo no dia.\n\nEles gostam de cozinhar carne de dentesserra quando tem, mas não tive tempo de arrumar outro carregamento para eles. Você se incomoda de ajudar o pessoal?\n\nOs dentesserras jovens são menos saborosos que os mais velhos, então melhor passar longe dos mais novos. Dê um ou dois anos para eles crescerem.", progress = "Lok'tar, <class>. O risco de se viajar pelos pântanos não deve ser desprezado. Você deve ter muita coragem para vir testar sua força aqui.", completion = "Ah! Maravilha. Carne fresca para a fogueira de hoje. Obrigado, &lt;name&gt;.\n\nVocê ajudou a deixar as noites vindouras mais toleráveis. A fogueira está bem viva e nossos buchos ficarão bem cheios.\n\nEsse carregamento que você nos traz é uma grata surpresa. Dar pode não ser o &lt;race&gt; mais formoso que já se viu por aí, mas pelo menos a atitude dele é muito mais agradável do que o cheiro.", rewards = "Você receberá:\n\n"},
[699] = {title = "Falta de suprimentos", objective = "Leve 6 Garras de Tartalisca Dentesserra para Tok'Kar, no Pântano das Mágoas.", description = "Com a ameaça dos murlocs no litoral, ficamos destacados aqui para proteger Pedregal de qualquer ameaça que possa vir — ou avisar os outros, caso os inimigos sejam muitos. Não sobra muito tempo pra gente relaxar e, se você puder, uma ajudinha viria bem a calhar.\n\nAs garras de tartalisca dentesserra dão umas armas ótimas quando preparadas do jeito certo. Traga para mim algumas garras e, como recompensa pela ajuda, pela carne fresca e pela boa companhia, vou dar a você uma das nossas armas.", progress = "Um bom hidromel, uma fogueira quentinha, um exército de orcs e um arsenal de armas... aqui no litoral, a gente não precisa de mais nada. Não tenho do que reclamar aqui do meu posto cuidando de Pedregal, mas seria bom se um dia a gente tivesse uma toca ou, pelo menos, uma torre.", completion = "Fico feliz que você tenha voltado em segurança, &lt;name&gt;.\n\nAs garras são perfeitas. Quanto mais armas a gente tiver à disposição, melhor vamos reagir a quem nos atacar.\n\nVou começar a produzir a sua arma assim que puder. Vai ser rapidinho. Com o tempo, eu fiquei bem rápido e hábil na feitura delas.", rewards = "Você receberá:\n\n"},
[700] = {title = "Homenagem real", objective = "Apresente-se ao Rei Magni Barbabronze.", description = "Ah, Lordaeron nos dá rochas tão lindas! É uma pena que aquelas terras estejam tão devastadas.\n\nMas com o Memorial está tudo bem! Posso crer que o Rei vá ficar bastante satisfeito com o resultado.\n\nAgora, por favor, avise Sua Majestade que o Memorial está pronto, conforme solicitado.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nAnel Comemorativo de Altaforja"},
[701] = {title = "A astúcia dos raptores", objective = "Consiga 12 Corações de Raptores dos Fareja-carne do Planalto para Tor'gan.", description = "Com mais alguns objetos, posso criar um item para imbuir Gor'mul com a perspicácia do raptor! Com o poder do raptor correndo em suas veias, Gor'mul vai conhecer o nosso novo poder e redescobrir o guerreiro adormecido que há dentro dele!\n\nPara fazer o item e o feitiço, vou precisar de corações de raptor.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[702] = {title = "A astúcia dos raptores", objective = "Entregue o Orbe de Tor'gan para Gor'mul.", description = "Pegue esse orbe e use a energia para conferir a essência do raptor a Gor'mul. Tenho certeza de que ele vai ficar com o espírito revigorado, renovando a sensação de propósito na vida. Quando ele voltar ao normal, podemos explicar a situação atual da Horda e então contaremos com a ajuda de um grande guerreiro em nossas batalhas sem fim.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[703] = {title = "Churrasquinho de urubu", objective = "Leve 4 Asas de Urubu para Bertolezo.", description = "Ah, sim, o meu churrasco de asa de urubu é sempre o maior sucesso! A textura do molho, o sabor forte da carne... e um cheiro capaz de atrair as barrigas famintas em um raio de quilômetros!\n\nTenho tudo aqui para fazer uma porçãozinha pra gente, menos... as asas de urubu!\n\nSe quiser se refestelar, vai ter que me trazer umas asas de urubu. Se trouxer bastante, posso até pensar em te dar a receita!", progress = "E aí, <name>, tá com as minhas asas?", completion = "Ah, maravilha. Vou começar os trabalhos agora mesmo. Já estou até sentindo o gostinho!\n\nE se você quiser preparar essa iguaria, tá aqui a receita!", rewards = "Você receberá:\n\nAsa de Urubu GrelhadaReceita: Asa de Urubu Grelhada"},
[704] = {title = "O destino de Agmundo", objective = "Leve 4 Urnas de Pedra com Gravuras para o Prospector Bandaferro, em Loch Modan.", description = "O último relatório que recebi de Agmundo era muito perturbador, ainda mais agora, com a morte dele! Ele mencionou que os trabalhadores encontraram \"urnas antigas\" na escavação perto de Uldaman. Temo que, assim como os ídolos que encontrei aqui, as urnas que Agmundo achou no sítio de Uldaman estejam incitando os troggs.\n\nPreciso que você vá buscar essas urnas para que possamos estudá-las. Vá à escavação em Uldaman e traga as urnas para mim.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nLuvas de Prospector"},
[705] = {title = "Pescando pérolas", objective = "Leve 9 Pérolas Azuis para Bertolezo, nos Ermos.", description = "A vida nos Ermos, um lugar cheio de temíveis predadores e bichos carniceiros, não é moleza para um goblin nanico como eu. Pra sobreviver, tenho que ser malandro!\n\nSei como fazer uma bomba de clarão, e uso para espantar os animais selvagens. Mas estou quase sem estoque de um dos ingredientes: pó de pérola azul macerada. Arruma umas pérolas pra mim e eu vou te recompensar bem.\n\nAh, e espero que você tenha um bom par de botas, porque os mariscos que dão as Pérolas Azuis que preciso ficam no Arrecife Torpe.\n\nO Arrecife Torpe na Selva do Espinhaço, pode crer!Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nRifle RelâmpagoVarinha Faiscante\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\nBomba de Clarão"},
[706] = {title = "Encantamentos de Labareda Abrasadora", objective = "Consiga um Coração de Draco Preto para Sigrun Derrubaferro.", description = "Como aprendiz de Finca-pé Diaço, aprendi a técnica especial dele para imbuir armas com encantamentos de fogo. Da última vez que tive notícia, ele tava trabalhando em uma forja, acompanhando um grupo de caçadores lá em Lordaeron.\n\nNão posso te contar muito, mas posso dizer que o processo requer o sangue de um coração ainda pulsante de draco negro jovem. Tô aqui de papo pro ar, esperando umas notícias de Altaforja. Se você me trouxer o coração de um dos dragonetes da Ravina Lethlor, posso te mostrar o que eu sei fazer!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nEncantamento de Labareda Abrasadora"},
[707] = {title = "Bandaferro quer você!", objective = "Fale com o Prospector Bandaferro, no Sítio de Escavação de Bandaferro, em Loch Modan.", description = "<name>. Tivemos notícia do Prospector Bandaferro no sítio de escavação em Loch Modan, e ele está precisando de uma força em uma outra escavação nossa.\n\nNão tenho muitos detalhes para te dar, mas se quiser dar uma ajuda, vá falar com ele.", progress = nil, completion = "Ah, então o Lançatroz te mandou, foi? Ótimo.\n\nTenho um trabalhinho pra você...", rewards = "Você receberá:\n\n"},
[708] = {title = "A caixa preta", objective = "Leve a Caixa Preta Corroída para o Salão das Armas de Altaforja.", description = "A superfície desta caixa metálica está muito danificada e parece ter sido mergulhada em ácido... deve ter passado muito tempo no estômago do condor gigante.\n\nVirando a caixa, encontra-se um símbolo gravado na parte de baixo. Sob o símbolo se lê:\n\n\"Máquina nº 19. Brigada de Cerco de Altaforja\"Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[709] = {title = "A solução para o fim", objective = "Leve a Tabuleta de Ryun'eh para Theldurin, o Perdido.", description = "Barbaridade! O fim está próximo! Pode crer! O fim!\n\nNão, não posso falar disso... não posso dizer os nomes, eles podem me ouvir! Mas se a gente descobrir, sim, se a gente descobrir mais sobre eles... então talvez a gente possa arrumar um jeito de impedir eles!\n\nTalvez.\n\nVá a Uldaman. Não! Eu já falei! Você tem que encontrar... encontrar a tabuleta... de Ryun'eh! Sim, ache a tabuleta! E não me faça dizer esse nome outra vez!\n\nEla está bem nas entranhas lá daquele lugar, em um baú antigo. É lá que ela se esconde... em um baú criado antes mesmo de qualquer obra concebida por um anão.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nVeste do Agourento"},
[710] = {title = "Um estudo dos elementos: rocha", objective = "Encontre 10 Estilhaços Pequenos de Pedra e leve-os para Lotwil Viriato, nos Ermos.", description = "Aqui nos Ermos eu parei para trabalhar no meu mais novo projeto: meus grilhões de vinculação elemental.\n\nQuando eu terminar, eles permitirão que os magos invoquem e usem elementais ainda mais poderosos e capazes. Para começar, preciso de estilhaços pequenos de pedra para testar alguns dos meus feitiços ofensivos mais novos, junto com uns outros trecos que eu criei.\n\nÉ bem fácil coletar os estilhaços dos elementais inferiores da rocha que ficam a oeste daqui. Pode trazer isso para mim?", progress = "Essa área é perfeita para... quer segurar isso aqui um instantinho? Sim, assim mesmo, perto do vetor normal. Agora, o que eu estava...? Ah, é. A área é perfeita para testar o meu mais recente projeto. Os materiais brutos, o espaço aberto... um praticante da arte esclarecido e inteligente não iria precisar de mais nada.", completion = "Ah, perfeitos, &lt;name&gt;. Obrigado. Hm, tem um pouco mais de areia do que eu esperava. Vou ter que ajustar o coeficiente de fricção. Não, espera, com óleo gélido, esse fator ainda seria desprezível.", rewards = "Você receberá:\n\n"},
[711] = {title = "Um estudo dos elementos: rocha", objective = "Leve 3 Lajes Grandes para Lotwil Viriato, nos Ermos.", description = "<name>, você já mostrou que é mais capaz do que todos os meus aprendizes, juntos.\n\nPara as próximas etapas, vou precisar de um pouco mais de força. Se tiver o cuidado necessário, poderá encontrar grandes lajes nos elementais de pedra. O corpo deles é maior e mais forte, portanto é mais provável que tenham pedaços grandes o suficiente para os meus testes. Os elementais ficam bem a oeste daqui, ou ao sudeste da borda das montanhas.\n\nLeve o tempo que precisar, pois eu ainda tenho que fazer um teste com os pedacinhos menores que você me trouxe.", progress = "Estou quase satisfeito, <name>. Meus feitiços afetaram os menores pedaços, exatamente como eu imaginava. Se eu conseguir o sucesso que espero ter nesse experimento, poderei controlar elementais muito mais poderosos do que qualquer coisa que já invoquei na vida.", completion = "Impressionante, &lt;name&gt;, muito impressionante! São tão grandes que parece até que foram trazidas até aqui por uma meia dúzia de taurens.", rewards = "Você receberá:\n\n"},
[712] = {title = "Um estudo dos elementos: rocha", objective = "Leve 5 Braçadeiras de Liga de Pedra para Lotwil Viriato, nos Ermos.", description = "<name>, preciso de mais uma parte de elementais aqui dos Ermos: as braçadeiras de liga de pedra dos elementais maiores da rocha. Segundo meus cálculos, as braçadeiras devem servir para medir a força dos meus próprios grilhões.\n\nUsando a vidência, descobri um grupo de elementais maiores da rocha a sudoeste daqui, perto de uns ogros, se bem me lembro.\n\nTraga para mim 5 braçadeiras – melhor sobrar do que faltar. Afinal, Servo vive perdendo meus reagentes e minha pesquisa.", progress = "As lajes de pedra que você encontrou são perfeitas, <name>. Lucien vai levar as sobressalentes de volta para Kharanos quando terminarmos aqui. Afinal, elas podem ser úteis em outra oportunidade.\n\nViriam muito a calhar, por exemplo, como base para um abriguinho que penso em montar quando voltar meus estudos para uma abordagem multidisciplinar dos explosivos de alta potência...", completion = "&lt;name&gt;, já pensou em se tornar assistente de pesquisa? Não? Ora, sem as suas habilidades, eu não sei o que eu teria feito. Inteligência, curiosidade e força... você tem o pacote completo.", rewards = "Você receberá:\n\n"},
[713] = {title = "Cuca fresca", objective = "Encontre Óleo Gélido e leve para Lotwil Viriato, nos Ermos.", description = "Deixa eu te contar uma coisinha sobre o Óleo Gélido: não tem nada melhor para resfriar um motor ou engrenagens.\n\nSuas propriedades de resfriamento são extraordinárias, assim como sua longevidade como lubrificante. E não é só isso: também é bom para ajudar a reter encantamentos em itens mágicos.\n\nPreciso de uma ampola desse óleo para aumentar a capacidade mágica dos meus grilhões e deixar o item poderoso o suficiente para impedir que o nosso amigo invocado acabe com a raça de todos nós.\n\nKe'bradas, um goblin amigo meu, vai te dar as orientações. Ele está em Alterac.", progress = "Já conseguiu o meu óleo? Quando você trouxer, vai ser uma mão na roda.", completion = "Joinha! Esse óleo vai resfriar as minhas braçadeiras novas. Os encantamentos vão durar pelo menos um século, ou eu dou até o meu último tostão pro Servo.\n\nRapaz, quando eu terminar isso aqui, todos os magos da Assembleia vão ficar boquiabertos. Pode esperar, &lt;name&gt;, pode esperar.", rewards = "Você receberá:\n\n"},
[714] = {title = "Girosc... é o quê?", objective = "Leve um Giroscronatom para Lotwil Viriato, nos Ermos.", description = "<name>, seu trabalho superou as minhas expectativas.\n\n<Lotwil está claramente distraído com o trabalho.>\n\nSe algum dia for a Kharanos e quiser um trabalho, é só me procurar.\n\nPapagaio! Rápido, pegue o... isso, isso aí. Obrigado.\n\nAgora só preciso de mais uma coisinha. Uma coisinha de nada, mas um tanto crucial: um giroscronatom.\n\nSe você puder ir buscar para mim, consigo terminar isso aqui em dois palitos.", progress = "Sim, sim, sim. Só um momento.\n\n<Lotwil continua a trabalhar em uma engenhoca.>", completion = "RÁ!\n\n&lt;Lotwil toma o giroscronatom da sua mão.&gt;\n\nPerfeito... quase acabando. Não, de verdade, agora eu tô mesmo quase acabando. Esse projeto é a minha maior prioridade... por ora. Deixa só eu colocar isso aqui no lugar e você vai ver com os próprios olhos o que me ajudou a criar. Só vai levar mais uns minutinhos... é... talvez um pouco mais. Puxa, talvez muito mais. Veremos...", rewards = "Você receberá:\n\n"},
[715] = {title = "Pedra líquida", objective = "Leve uma Poção de Cura e uma Poção Inferior de Invisibilidade para Lucien Tacachave, nos Ermos.", description = "Estou pensando que esses itens que você trouxe para Lotwil serão a menor das nossa preocupações, <name>. Por outro lado, você provou grande habilidade para as ciências que são sagradas para Lotwil, então o que acha de fazer uma coisinha para me ajudar?\n\nPreciso de uma poção de invisibilidade e uma poção de cura; ambas podem vir a calhar muito em breve, se Lotwil tiver sucesso nesse troço que ele está tentando fazer.\n\nEm troca, eu vou te dar a receita de uma poção que eu desenvolvi enquanto aprendia com Lotwil, com base em uma das pesquisas que ele está fazendo aqui nos Ermos.", progress = "Ele está perto de terminar. E você?", completion = "ISSO!\n\nQuer dizer, obrigado, &lt;name&gt;. Elas ser muito úteis na travessia dos Ermos.", rewards = "Você receberá:\n\nReceita: Poção de Escudopétreo InferiorPoção Inferior de Escudopétreo"},
[716] = {title = "Pedra é mais resistente que tecido", objective = "Leve algumas Braçadeiras Decoradas de Bronze para Lucien Tacachave, nos Ermos.", description = "Sei que Lotwil pode ser meio pão-duro, <name>, então deixa eu te dar um pequeno extra pelo seu trabalho duro. Talvez até ajude você a se proteger da atrocidade que, com certeza, vai resultar da mais nova invenção do Lotwil.\n\nEstou trabalhando com todas aquelas pedras que você trouxe para ele e posso fazer uma braçadeira bem legal. Uma braçadeira encantada, é claro. Sei que você vai gostar.\n\nPara concluir a peça que estou fazendo para você, só preciso de algumas braçadeiras decoradas de bronze feitas por um ferreiro.", progress = "Se eu fosse você, não enrolaria tanto para arrumar essa braçadeira, <name>.\n\nQuem sabe o que pode acontecer em breve?", completion = "Toma aí, &lt;name&gt;. Experimenta.", rewards = "Você receberá:\n\nBraçadeiras de Panopétreo Encantadas"},
[717] = {title = "Tremores da Terra", objective = "Use o Signo da Terra para ativar os Pilares de Ametista, Opala e Diamante, e obter as Pedras Rúnicas.\n\nPonha as pedras rúnicas no Selo da Terra para libertar Neraçoite e Hematus.\n\nMate-os e entregue as Braçadeiras de Neraçoite, os Grilhões de Hematus e o Signo da Terra para Garek.", description = "Ficou óbvio que não dá para garantir que Neraçoite e Hematus continuarão aprisionados em Lethlor. Os dragões negros que foram morar lá e os ogros que conseguiram roubar o Signo indicam com clareza que precisamos tomar medidas mais drásticas.\n\nA prisão dos dracos é mantida por três pedras rúnicas que só podem ser removidas usando o Signo da Terra. Então o selo que aprisiona os dois pode ser rompido por essas pedras rúnicas.\n\n<name>... vá e faça com que os sonhos deles sejam eternos.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nPeitoral LufardenteCota de EscamaprismaGuarda-peito Forjado para GuerraMedalhão de Explosão Mental\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[718] = {title = "Miragens", objective = "Recupere o Caixote de Suprimentos para Sigrun Derrubaferro.", description = "Na pressa de sair do sítio de escavação de Uldaman, a gente foi forçado a abandonar várias carroças e vários caixotes de suprimentos pelo caminho. Os ogros do Acampamento Kosh rapinaram os nossos equipamentos abandonados. Pareciam até abutres em um cadáver apodrecido. O cheiro também combinava. Quer dizer, eles têm um certo futum de carniça.\n\nEnfim, eles levaram a carroça que trazia todas as nossas armas e afins, então... bem, é um pouco constrangedor... mas não conseguimos trazer nenhuma comida.\n\nEstamos meio encrencados aqui, então o que acha de nos ajudar?", progress = "O deserto é um lugar ruim para sentir fome, guria, vá por mim.\n\nOpa, o que é aquilo ali...", completion = "Valeu mesmo, &lt;name&gt;. Enquanto você tava fora, eu... hã... eu engoli um bocado de areia. Foi bem...\n\nEnfim, já chega, hoje nós vamos sair e arrumar comida. Vamos tirar a barriga da miséria!", rewards = "Você receberá:\n\n"},
[719] = {title = "Um anão e suas ferramentas", objective = "Ache a Picareta da Sorte de Ryedol e devolva ao Prospector Ryedol, ao sul do Sítio de Escavação de Pé-de-malho.", description = "Ei, <name>. Vê se toma cuidado nessas bandas, <piá/guria>. Tem anão Ferro Negro pra todo lado, parece que é o clã de Umbraforja.\n\nEles acabaram de atacar o sítio de escavação onde eu tava trabalhando... acabaram com a casca de quase todo mundo, incluindo meu chefe, Pé-de-malho. Eu mesmo só escapei por um triz.\n\nTô tentando arrumar um jeito de voltar lá no sítio, ao norte daqui, pra recuperar uns suprimentos, principalmente a minha picareta da sorte.\n\nPues tu não quer me ajudar, não? Aposto que vai ter um bagual Ferro Negro usando a minha picareta! Pode estar com qualquer um.", progress = "Não sei o que os Umbraforja tão procurando, mas que eles tinham um propósito, ah, mas tinham mesmo.", completion = "Tu é mesmo uma &lt;class&gt; buenaça! Já estava na hora de alguém ensinar uma lição praqueles Umbraforja. Obrigado pela ajuda.", rewards = "Você receberá:\n\nMartelo de Ryedol"},
[720] = {title = "Um sinal de esperança", objective = "Encontre o Prospector Ryedol e avise que Grez Pé-de-malho está vivo.", description = "<No mapa há um texto rabiscado em giz.>\n\n\"Ryedol,\nTão me levando pra Uldaman. Mande ajuda.\n–Pé-de-malho\"", progress = nil, completion = "Pé-de-malho tá vivo?! Oigalê, &lt;name&gt;!", rewards = "Você receberá:\n\n"},
[721] = {title = "Um sinal de esperança", objective = "Encontre Grez Pé-de-malho em Uldaman.", description = "<name>, tu consegue achar ele? Consegue achar o Pé-de-malho em Uldaman?\n\nSe aqueles infelizes dos Umbraforja estiverem com ele de refém para descobrir tudo o que ele sabe sobre escavações titânicas, toda a Altaforja vai estar em maus lençóis.\n\nJá faz muito tempo que os Ferro Negro procuram mágicas para fazer golens mais poderosos. Pé-de-malho tava fazendo a mesma coisa, no más, só que pra impedir os Ferro Negro.\n\nAposto que vão deixar ele vivo até descobrir o que ele sabe. Se tu correr, capaz de ainda conseguir salvar o meu compadre!", progress = nil, completion = "Oi? Ah, desculpa... &lt;tosse&gt; Eu tô fraco uma barbaridade. Pode chegar mais perto, &lt;class&gt;?\n\nRyedol te mandou, foi? Graças a Muradin. Aquele piá é mais esperto do que eu pensava.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[722] = {title = "O amuleto dos segredos", objective = "Encontre o Amuleto do Pé-de-malho e o devolva a Grez, em Uldaman.", description = "Tu só pode ser muito topetud<o/a>... <tosse>... pra vir cá em Uldaman me procurar, <class>.\n\nSe tu quer mesmo ajudar... o velho Pé-de-malho... <tosse>... vai ter que achar... o meu amuleto.\n\nEle tá com o Sombrafunda... Magregan Sombrafunda. Ele tá em algum lugar por aqui... nas ruínas... com certeza. Se tu não impedir, o amuleto vai levar eles... <tosse>... direto pros pergaminhos. Agora é contigo, <name>. Por favor... traz o amuleto de volta.\n\nOs pergaminhos vão ajudar os Ferro Negro a criar golens ainda mais poderosos. Tenho certeza, sinto nos meus ossos.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[723] = {title = "Prospector da fé", objective = "Leve o Amuleto do Pé-de-malho para o Prospector Ryedol nos Ermos.", description = "<Pé-de-malho agarra as feridas e leva um momento para se recompor antes de continuar falando.>\n\nEncontre Ryedol, <name>. Leva o meu amuleto pra ele. <cof cof> Ele vai saber... <cof> o que fazer. Ele sabe que eu tava procurando as origens do nosso povo, e ele... <cof>... sabe que eu também tinha a missão de parar o clã Umbraforja. Eles não podem achar os pergaminhos... <cof>Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[724] = {title = "Prospector da fé", objective = "Leve o Amuleto do Pé-de-malho para o Historiador Karnik, em Altaforja.", description = "Tá bom, tá bom... quem pode saber o que fazer?\n\nO Historiador Karnik! Pronto!\n\nEle tá em Altaforja e vai poder ajudar. Ele e Pé-de-malho tavam trabalhando juntos pra encontrar os artefatos perdidos em Uldaman.\n\nPues parece que Umbraforja descobriu que eles sabiam mais do que aparentavam.\n\nConta pro Karnik o que aconteceu com o Pé-de-malho e dá o amuleto pra ele... ele vai te falar o que tu tem que fazer em seguida.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[725] = {title = "A notícia de uma ameaça", objective = "Encontre o Conselheiro Belgrum e entregue o bilhete.", description = "<name>, corra e encontre o Conselheiro Belgrum o mais rápido que puder. Conte a ele tudo o que testemunhou e pergunte o que fazer. Esse bilhete vai explicar tudo melhor.\n\nNesse meio tempo, eu vou aproveitar para conversar com o espírito de Pé-de-malho. Talvez ele saiba de alguma coisa que nos ajude a dar um basta às atividades desses cretinos de Umbraforja nos Ermos. A esperança é a última que morre.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[726] = {title = "A notícia de uma ameaça", objective = "Fale com o Historiador Karnik.", description = "<Conselheiro Belgrum chega ao fim do bilhete e arregala os olhos por um instante.>\n\nMas são péssimas notícias, tchê.\n\n<name>, por favor, volte lá no Historiador Karnik e diga a ele que ele tem a permissão e a autoridade do rei para agir de forma enérgica e imediata. Ele tem que encontrar um jeito de conter as atividades de Umbraforja nos Ermos.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[727] = {title = "Em busca do Compêndio de Yagyin em Altaforja", objective = "Dê o Signo do Martelo a Gerrig Agarrosso na Caverna Esquecida de Altaforja.", description = "A Tabuleta revela muito. Bah, mas não o suficiente! Nem de perto, tchê!\n\nHá um livro, um compêndio que Yagyin escreveu em eras passadas. As... pessoas de quem eu fugi... guardam este livro em muitos lugares. E os mais instruídos dentre eles o estudam. O compêndio engloba um vasto conhecimento. Conhecimento para nos proteger!\n\nProcure Gerrig Agarrosso. Ele está em uma loja em Altaforja, na Caverna Esquecida. Mostre-lhe este signo e ele vai pensar que tu é um deles. Peça-lhe o Compêndio de Yagyin... e traga para mim!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[728] = {title = "Em busca do compêndio de Yagyin na Cidade Baixa", objective = "Leve o Signo do Martelo ao Guardião Bel'dugur na Cidade Baixa.", description = "A Tabuleta revela muito. Mas não o suficiente! Nem de perto!\n\nHá um livro, um compêndio que Yagyin escreveu em eras passadas. As... pessoas de quem eu fugi... guardam este livro em muitos lugares. E os mais instruídos dentre eles o estudam. O compêndio engloba um vasto conhecimento. Conhecimento para nos proteger!\n\nProcure o Guardião Bel'dugur na Cidade Baixa de Lordaeron. Mostre-lhe este signo e ele vai pensar que tu é um deles. Peça-lhe o Compêndio de Yagyin... e traga para mim!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[729] = {title = "O prospector distraído", objective = "Vá para o sul e veja como está o Prospector Trilheiro.", description = "Barbaridade, foi uma honra e tanto quando o Mestre Suiçagris me designou para trabalhar sob a orientação do grande Prospector Trilheiro. Na academia em Altaforja, todos já tinham ouvido falar das grandes descobertas de Trilheiro.\n\nMas o Prospector é um tanto quanto... hã... alheio ao que há em volta.\n\nHavíamos descoberto evidências de uma grande sociedade. Golens horríveis saíram do chão e infestaram o local. Trilheiro nunca pareceu notar. Voltei num upa a Auberdine em busca de ajuda.\n\nPor favor, viaje de vereda até o sul e veja se o Prospector está bem!", progress = nil, completion = "Hollee, é tu que estás aí, guria? Preciso de ajuda para esculpir esta pedra. Me passa de vereda meu pincel de pedra, acho que estou vendo algo.\n\nEi, tu não é a Hollee! Por acaso viste meu pincel de pedra?\n\nDeixa pra lá! Prometi à Liga que enviaria o fóssil misterioso que encontrei. Bah, onde Hollee se meteu?\n\nHá tanto trabalho a fazer! Agora... onde está o fóssil misterioso...? E meu pincel...? E Hollee...?\n\nE quem é tu, tchê...? Não importa, desde que estejas aqui para ajudar.\n\nMe fala quando estiveres pronto para começar a procurar.", rewards = "Você receberá:\n\n"},
[730] = {title = "Problemas na Costa Negra?", objective = "Vá até Auberdine e procure sinais da equipe de escavação enânica.", description = "Barbaridade, como é bom ver <um/uma <class> interessad$Go:a> nas grandes maravilhas arqueológicas do nosso mundo.\n\nPor vezes, nosso trabalho é considerado um mero passatempo pelos nossos amigos da Aliança. Mas o que muitos não percebem é que as recentes descobertas em Khaz Modan provaram que uma força grande e poderosa ameaça toda Azeroth, de Lordaeron a Kalimdor.\n\nEstou muito preocupado com a equipe que enviei para Costa Negra, tchê. Eles se fizeram fumaça e não mandam notícias há semanas.\n\nVá até Auberdine e procure uma pista sobre o paradeiro deles.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[731] = {title = "O prospector distraído", objective = "Proteja o Prospector Trilheiro enquanto ele procura o fóssil misterioso, depois fale novamente com a Arqueóloga Hollee em Auberdine.", description = "Então, Hollee... ah, é, tu não é a Hollee. Cadê a Hollee?\n\nBah, não interessa. Temos que achar o pincel de pedra... Digo, o fóssil misterioso! Temos que encontrar aquele fóssil. A Liga dos Exploradores em Darnassus vai querer saber o que eu ando fazendo.\n\nMe avisa quando tu estiver pront<o/a> e vamos caçar aquele pincel... quero dizer, fóssil! Precisamos encontrar algo e escrever para dar uma satisfação à Liga.\n\nEntão, já estás <pronto/pronta>, <guri/guria>?", progress = nil, completion = "Mas, bah, graças aos céus que o prospector Trilheiro está bem! Eu te disse que ele tinha cabeça de passarinho. Não acredito que ele insistiu em ficar por lá com todas aquelas criaturas horríveis rondando.", rewards = "Você receberá:\n\n"},
[732] = {title = "Tremores da Terra", objective = "Recupere o Signo da Terra para Garek.", description = "Sigrun me falou que você tem ajudado bastante. Talvez eu possa lhe pedir ajuda também. Fui enviado aqui pelo meu mestre, Krasus do Kirin Tor, para colocar um fim no problema que se forma na Ravina Lethlor.\n\nNão posso divulgar a natureza exata da minha missão aqui ou as tarefas que devo empreender, mas tenha certeza de que são de vital importância.\n\nUm bando de ogros errantes, às vezes visto perto do Acampamento Barf, roubou um objeto chamado Signo da Terra. Preciso dele. Traga-o para mim e lhe direi mais.", progress = "O bando nômade de ogros viaja pelos Ermos, de um acampamento deles ao outro. Com o calor que sobe da terra seca e os ventos revoltos, será difícil rastreá-los. Ouvi dizer que às vezes eles viajam pelo Acampamento Barf. Se você está perdido, pode tentar procurar por lá.", completion = "É bom sinal ver você de volta com o Signo, &lt;name&gt;. O Signo da Terra foi criado pelos anões Ferro Negro da Fortaleza Angor para vincular dois dos implacáveis tenentes Asa da Morte, os gêmeos Neraçoite e Hematus.", rewards = "Você receberá:\n\n"},
[733] = {title = "Fuçando", objective = "Leve 7 pedaços de Sucata para Sigrun.", description = "As coisas por aqui vão ficar mais difíceis que nadar de poncho se eu não conseguir juntar materiais para construir algumas defesas e outros itens importantes, tchê. Notei que os ogros da área parecem estar bem equipados com sucata. Talvez as histórias de máquinas de cerco nos Ermos não estivessem tão longe da realidade, afinal...\n\nDe qualquer forma, o Prospector tem se preocupado com o estado do nosso pequeno acampamento, então eu gostaria de começar a trabalhar logo, se possível.\n\nO acampamento principal dos ogros fica na Gruta Arrota-pó, a uma boa caminhada de distância seguindo a oeste daqui.", progress = "Bah, fiz o possível com o que eu tinha, mas... não é muito. Você encontrou mais suprimentos?", completion = "Bah, é um começo, &lt;name&gt;, e terá que servir até termos notícias de Altaforja ou da Liga dos Exploradores.\n\nAgora, se eu pudesse encontrar um pouco de sal em algum lugar para o meu barril extintor...", rewards = "Você vai poder escolher uma dessas recompensas:\n\nEscudo SalbacBotas Talão-de-ferro\n\nVocê também receberá:\n\n"},
[734] = {title = "Isso vai ser difícil!", objective = "Fale com Lucien Tacachave nos Ermos.", description = "Puxa, puxa, maravilha.\n\n<Lotwil olha para você por um instante antes de voltar para os grilhões dele.>\n\nVá falar com Lucien, <name>. Preciso de uma engrenagem Nº 5. Traga para mim, está bem?", progress = nil, completion = "Engrenagem Nº 5, hein? É, isso não vai ser nada bom.", rewards = nil},
[735] = {title = "A estrela, a mão e o coração", objective = "Leve os itens do Bilhete de Agarrosso para ele em Altaforja.", description = "Compêndio de Yagyin, hein? Barbaridade, por acaso tu estás começando uma nova filial nossa em algum lugar? Não importa, não cabe a mim perguntar.\n\nVou te ajudar. Tenho uma cópia extra. Devemos nos ajudar, não é? É assim que prosperamos.\n\nPreciso de três itens de poder. Traga-os para mim e te darei o compêndio.\n\nNão vou dizer quais são. Podem estar de orelha em pé nos ouvindo, tchê. Mas aqui, vou anotar tudo.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[736] = {title = "A estrela, a mão e o coração", objective = "Leve os itens do Bilhete de Bel'dugur para o Guardião Bel'dugur na Cidade Baixa.", description = "Você precisa do Compêndio de Yagyin? Seu plano, então, é reunir outro bando? Claro que é... e é claro que vou ajudar você, <irmão/irmã>.\n\nMas você também tem que me ajudar.\n\nPreciso de três itens de poder. Traga os três e o compêndio será seu.\n\nNão posso pronunciar o nome desses itens, mas vou anotá-los.\n\nTraga os itens desta lista. Se conseguir, é sinal de que merece mesmo começar seu próprio capítulo da nossa ordem.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[737] = {title = "Conhecimento proibido", objective = "Leve o Compêndio de Yagyin para Theldurin.", description = "Aqui está o compêndio, <name>. Não preciso lhe dizer para tomar muito cuidado. E mantê-lo longe de mãos hostis. \n\nEste livro contém muitas verdades...\n\n...E a verdade pode machucar.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nCrânio do Destino Iminente"},
[738] = {title = "Encontre Agmundo", objective = "Encontre Agmundo.", description = "O Prospector Agmundo estava encarregado de nossos sítios de escavação nos Ermos, a sudoeste. Digo \"estava\" porque... bah, não tenho notícias dele há semanas. Preciso que tu encontres Agmundo para que eu tenha acesso ao relatório dele.\n\nDa última vez que soube dele, Agmundo estava no sítio de escavação mais ao sul dos Ermos — uma minúscula operação além da Fortaleza Angor. Se eu fosse tu, <guri/guria>, começaria a busca por ele lá.", progress = nil, completion = "Este esqueleto deve ser o que sobrou do desventurado do Agmundo. O corpo dele está quebrado e ferido, e os ossos, expostos.", rewards = "Você receberá:\n\nCinturão Rasgado de ProspectorMinério de OuroJade"},
[739] = {title = "Murdaloc", objective = "Mate Murdaloc, o assassino de Agmundo.\n\nMate 12 Quebraossos da Abóbada de Pedra.\n\nApresente-se ao Prospector Bandaferro em Loch Modan.", description = "Os rastros de Agmundo vêm do oeste. É fácil dizer pelas manchas de sangue na rocha e na areia que o anão rastejou um bocado.\n\nE morreu aqui.\n\nObservando com atenção, é possível ver a palavra \"Murdaloc\" escrita às pressas em um trecho de arenito dos Ermos.\n\nBem ao lado da mão morta de Agmundo.", progress = nil, completion = "Que notícia desoladora, &lt;name&gt;. Mas Agmundo dava potentes golpes de martelo com seu braço forte, e tinha a cabeça dura como ninguém! Embora Agmundo tenha sido morto por aqueles malditos troggs, tenho certeza de que levou vários junto com ele, tchê.", rewards = "Você receberá:\n\nPulverizador de Rochas"},
[741] = {title = "O prospector distraído", objective = "Leve o fóssil misterioso ao Arqueólogo-chefe Suiçagris em Darnassus.", description = "Então o Prospector quer enviar o misterioso fóssil para a Liga dos Exploradores em Darnassus? Não acho certo deixar Costa Negra sem ele.\n\nAqui, toma, <name>, entregue o fóssil misterioso ao Arqueólogo-chefe Suiçagris em Darnassus.", progress = "Em que posso ajudar, <race>?", completion = "Mazá! Só o Prospector Trilheiro pra descobrir um achado como esse!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nLuvas de Punho de MarteloLuvas Toque de VentoCinto do Caçador de Relíquias\n\nVocê também receberá:\n\n"},
[742] = {title = "A caçada do Vale Gris", objective = "Fale com Senani Coração Trovejante no Posto Machadada, que fica no Vale Gris.", description = "Atenção, jovens aventureiros e aventureiras! A floresta do Vale Gris os aguarda!\n\nA Horda estabeleceu forte presença nas terras ao norte dos Sertões. Nossos dois postos: o Posto Machadada e o Posto da Praia de Zoram lutam para trazer glória à Horda! Aqueles que desejarem se colocar à prova devem se dirigir aos postos supracitados para buscar orientação. Observação: Senani Coração Trovejante, no Posto Machadada, ao norte dos Sertões, procura aventureiros e aventureiras dispostos a tomar parte na grande caçada do Vale Gris!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[743] = {title = "Perigos das Ventofúria", objective = "Leve 8 Garras de Ventofúria para Ruul Garra da Águia na Aldeia Casco Sangrento.", description = "Obrigado pela atenção de falar comigo, <name>. Meu nome é Ruul, guerreiro e instrutor.\n\nVocê atingiu uma idade em que é necessário se preparar para as provas caso deseje se aventurar muito além dos limites de Mulgore.\n\nSe quiser testar sua força, comece procurando as harpias ventofúria a sudeste. Elas fazem seus ninhos nas encostas da montanha, longe da estrada.\n\nElas são um dos nossos inimigos naturais aqui em Mulgore e serão uma boa aferição da sua habilidade.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[744] = {title = "Os preparativos da cerimônia", objective = "Reúna 6 Penas Lazúli e 6 Penas Bronze, e leve-as para Eyahn Garra de Águia no Penhasco do Trovão.", description = "<name>, em breve meu irmão estará diante do Chefe Casco Sangrento e terei a honra de confeccionar o cocar que ele usará na ocasião.\n\nEu gostaria de lhe pedir um favor, enquanto termino de curtir as tiras de couro. Eu não terei tempo para encontrar todas as penas e queria saber se você não poderia procurá-las para mim.\n\nAs penas das harpias que vivem a norte e a noroeste do Penhasco do Trovão têm o tamanho exato. Preciso apenas de seis penas lazúli e seis penas bronze para finalizar.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nArnês AtadoEscudo do Guerreiro Tribal\n\nVocê também receberá:\n\n"},
[745] = {title = "Dividindo a terra", objective = "Mate 10 Coureiros Jubalba, 8 Esfoladores Jubalba, e 5 Larápios Jubalba, então fale novamente com Baine Casco Sangrento na Aldeia Casco Sangrento.", description = "<name>, não são poucos os conflitos que castigam estas terras. Espero que você não tenha que testemunhar tantas lutas quanto eu. Contudo, há um confronto em que você precisa entrar. Assim, você terá uma ideia de como uma pequena ameaça à terra pode se tornar terrível quando ignorada.\n\nGnolls Jubalba estabeleceram-se ao sul de Casco Sangrento e em uma caverna a oeste. Eles ignoram nossas tentativas de comunicação e abatem os animais selvagens de Mulgore sem nenhum controle.\n\nPalavras já não surtem efeito.", progress = nil, completion = "&lt;name&gt;, foi bom você ter levado minha tarefa a sério. O respeito pela terra e suas criaturas é importante. Morte e vida são um ciclo... uma necessidade. Uma não existe sem a outra. Pense nisso com cuidado e faça o possível para nunca esquecer.", rewards = "Você receberá:\n\nProjétil Faiscante"},
[746] = {title = "Escavação enânica", objective = "Recolha 5 Picaretas do Prospector.\n\nEm uma forja, esmague as Picaretas do Prospector para criar Ferramentas Quebradas.\n\nLeve 5 Ferramentas Quebradas para Baine Casco Sangrento na Aldeia Casco Sangrento.", description = "Tem uns anões preparando um sítio de escavação a noroeste. Eles acham que a terra esconde segredos, e é verdade, mas abrir buracos e profaná-la não torna ninguém merecedor de seus ensinamentos.\n\nRecolha as ferramentas dos anões, esmague-as e traga-as para mim.\n\nEsmague as ferramentas em uma forja. Tem uma no Penhasco do Trovão, mas qualquer forja serve, até mesmo a do acampamento dos anões.", progress = "Olá, <name>. Conseguiu as Ferramentas Quebradas? A escavação dos anões vai enfurecer os kodos de Mulgore. É preciso por fim a essa profanação!", completion = "Obrigado, &lt;name&gt;. Isso acalmará as feras de Mulgore. E, com a graça dos espíritos, ensinará aos anões que escavar a terra não é o caminho do conhecimento.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nGuarda-braços ReforçadosPerneiras Rústicas de Kodo\n\nVocê também receberá:\n\nPicareta do Prospector"},
[747] = {title = "A caçada começa", objective = "Grull Vento do Falcão na Aldeia Narache quer que você lhe traga 7 Penas de Pinote e 7 pedaços de Carne de Pinote.", description = "Você tem um ar bastante promissor, e vai se provar para a tribo. Talvez em breve você seja bem-vind<o/a> à grande cidade do Penhasco do Trovão. Mas, para que isso possa acontecer, você precisa se provar para o meu pai, o Chefe Vento do Falcão.\n\nAqui na Chapada Nuvem Vermelha, temos orgulho das nossas habilidades de caça. Taurens caçam por necessidade e por esporte. Nosso suprimento de carne está baixo e também precisamos de penas para as roupas. Cace os pinotes mais próximos e prove seu valor reabastecendo a aldeia.", progress = "Fornecer carne e penas para a tribo é o primeiro passo para se provar como caçador diante do Chefe.", completion = "Os taurens de Narache agradecem a você, &lt;name&gt;, que se mostrou bastante promissor.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCinto NômadeLuvas Encadeadas Pintadas\n\nVocê também receberá:\n\n"},
[748] = {title = "Água venenosa", objective = "Leve 6 Patas de Lobos-da-pradaria e 4 Garras de Pinotes para Mull Chifre Troante em Casco Sangrento.", description = "Goblins e os servos deles macularam nossos poços sagrados! Não podemos permitir isso.\n\nPara purificar os poços, preciso criar um totem de purificação. Em seguida, você o levará até o poço e realizará um ritual de purificação.\n\nPrimeiro vamos criar um totem de purificação para o Poço Casco Invernal. Para isso, vamos precisar da ajuda da terra. Cace lobos-da-pradaria para pegar as patas e pinotes adultos para pegar as garras. Eles ficam a sudoeste daqui.\n\nVolte com as patas e garras, <name>.", progress = "Trouxe as patas e as garras, <name>?", completion = "Você se saiu bem, &lt;name&gt;. Vou vincular o poder delas ao totem de purificação.", rewards = "Você receberá:\n\n"},
[749] = {title = "A caravana devastada", objective = "Morin Espreita Nuvem quer que você examine o conteúdo de uma caixa de suprimentos na Caravana Devastada.", description = "Há alguns dias, encontramos uma caravana da Empreendimentos S.A. viajando na margem norte do Lago da Ferradura. Nós havíamos perdido dois dos nossos guerreiros em outra batalha, por isso corremos para atacar e destruir a caravana.\n\nDeixamos as caixas de suprimentos, certos de que nossas tochas destruiriam tudo o que fosse de valor, mas como catadores da Empreendimentos S.A. foram vistos perto das carroças queimadas, talvez estejamos errados.\n\nO que você acha de ir até a caravana e examinar o conteúdo das caixas?Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[750] = {title = "A caçada continua", objective = "Grull Vento do Falcão na Aldeia Narache quer que você lhe traga 10 Pelagens de Puma da Montanha.", description = "Um tauren habilidoso na caça sabe que a presa não é um mero troféu. As feras das planícies nos fornecem um meio de sobrevivência. Você causará uma grande impressão nos anciões se puder trazer algumas Pelagens de Puma da Montanha, bastante procuradas e valorizadas. Você pode encontrar essas feras vagando furtivamente pelas colinas ao sul.\n\nNossas crianças precisam de roupas e nossas tendas precisam ser consertadas.", progress = "Se você puder fornecer peles para a tribo, falarei com meu pai, o Chefe Vento do Falcão, de suas generosas ações.", completion = "Os taurens de Narache lhe agradecem por estas provisões, &lt;name&gt;. Com sua habilidade na caça, você certamente será reverenciado no Penhasco do Trovão algum dia.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBraçadeiras NômadesCinto Encadeado Pintado\n\nVocê também receberá:\n\n"},
[751] = {title = "A caravana devastada", objective = "Entregue os Documentos da Empreendimentos S.A. para Morin Espreita Nuvem perto da Aldeia Casco Sangrento.", description = "Quando a tampa é aberta, um sortimento bem acondicionado de minério e minerais é revelado. Mais uma prova das operações da Empreendimentos S.A. em Mulgore. Você remove uma resma de papéis firmemente atada em cima do minério e dos minerais.\n\nVocê ouve sons de trabalho vindo das colinas a leste, e vê a fumaça que sobe de uma fogueira.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[752] = {title = "Uma humilde tarefa", objective = "Chefe Vento do Falcão quer que você procure a mãe dele perto do poço a sudeste da Aldeia Narache.", description = "Todos os membros da tribo compartilham a harmonia da vida. Vivemos juntos e trabalhamos juntos.\n\nNosso compromisso mútuo envolve um alto grau de responsabilidade. Peço a você agora uma humilde tarefa.\n\nMinha mãe saiu esta manhã para buscar água do poço a sudeste de Narache. Já faz algum tempo agora. Talvez você possa ver como ela está por mim enquanto cuido de alguns assuntos da tribo aqui?", progress = nil, completion = "Você viajou tudo isso só para ajudar uma velha senhora? Minha nossa, você não é mesmo para casar?", rewards = "Você receberá:\n\n"},
[753] = {title = "Uma humilde tarefa", objective = "Pegue uma Jarra d'Água do poço.\n\nLeve a Jarra para o Chefe Vento do Falcão na Aldeia Narache, que fica a noroeste do poço.", description = "Percorri muitos caminhos ao longo da vida, e essas pernas velhas já não têm o mesmo vigor de antes. Ainda posso cumprir meus deveres na tribo. Às vezes, uma velha só precisa de um pouco mais de tempo para fazer a tarefa.\n\nMas você me parece <um/uma> <class> entusiasmad<o/a>. Vamos colocar um pouco dessa vitalidade à prova. Pegue uma jarra d'água do poço e leve para o meu filho, o Chefe, na Aldeia Narache.\n\nLembre-se de que até mesmo a mais humilde das tarefas pode conquistar o reconhecimento dos anciões.", progress = "Parece que você voltou das planícies. Tem alguma notícia da Grande Mãe Vento do Falcão?", completion = "Vejo que você trouxe este jarro pra cá a pedido da Grande Mãe.\n\nSua disposição em ajudar os outros e prover os taurens da Aldeia Narache me leva a crer que você deixará orgulhosa a tribo do Penhasco do Trovão algum dia.", rewards = "Você receberá:\n\n"},
[754] = {title = "A purificação de Casco Invernal", objective = "Use o Totem de Purificação Casco Invernal no Poço Casco Invernal, então fale novamente com Mull Chifre Troante.", description = "Entalhei o totem para purificar o Poço Casco Invernal. Agora, você precisa levá-lo ao poço e realizar o ritual de purificação. O poço fica a sudeste, guardado por terríveis goblins!\n\nNão será uma tarefa fácil, mas que precisa ser feita se quisermos voltar a usar as águas do poço um dia.\n\nBoa sorte, <name>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[755] = {title = "Ritos da Mãe Terra", objective = "Vá ao Vidente Grislíngua que mora nas colinas ao sul da Aldeia Narache.", description = "Sua disposição em realizar uma humilde tarefa para os taurens de Narache e sua vontade de aprender são traços nobres, <name>. Creio que um dia você será proclamad<o/a> <um/uma> <class> valoros<o/a> no Penhasco do Trovão.\n\nAntes disso, você precisa embarcar nos Ritos da Mãe Terra, que são três.\n\nO primeiro teste é o Rito de força. Vá até o Vidente Grislíngua e diga-lhe que o Chefe Vento do Falcão mandou você.\n\nVocê encontrará a morada do vidente bem ao sul da Aldeia Narache, escondida nas colinas.", progress = nil, completion = "O Chefe Vento do Falcão mandou você? Embarcar nos Ritos da Mãe Terra não é uma tarefa fácil...", rewards = "O seguinte feitiço será lançado em você:\n\nMarca do Indomado\n\nVocê receberá:\n\n"},
[756] = {title = "Totem de Chifre Troante", objective = "Leve 6 Garras de Espreitador e 6 Garras de Puma para Mull Chifre Troante.", description = "Para criar o Totem de Purificação Chifre Troante, você precisa reunir as garras dos predadores da planície de Mulgore. Cace espreitadores-da-pradaria para pegar as garras de espreitador, e pumas das estepes para pegar as garras de puma. Em seguida, volte aqui.\n\nOs espreitadores-da-pradaria e os pumas das estepes ficam a leste e a oeste daqui.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[757] = {title = "Rito de força", objective = "Mate alguns Costagulha na Ravina da Lâmina Espinhenta e leve 12 Cintos de Costagulha para o Chefe Vento do Falcão na Aldeia Narache.", description = "Os Ritos da Mãe Terra são os passos que um jovem tauren precisa dar para conquistar respeito no Penhasco do Trovão.\n\nPrimeiro você deve passar pelo Rito de Força. Nesse teste, você deve provar sua bravura matando os inimigos da tribo.\n\nOs Costagulha da Ravina da Lâmina Espinhenta, a leste, estão invadindo as terras da nossa tribo. Eles surpreendem nossas equipes de caça com emboscadas e roubam a aldeia à noite.\n\nMostre seu valor matando esses malfeitores e leve os cintos deles para o Chefe na Aldeia Narache como prova de seus feitos.", progress = "Você já concluiu o Rito de Força, <name>? Exijo prova de seu valor contra os inimigos da nossa tribo, os Costagulha.", completion = "Você passou no primeiro teste dos Ritos da Mãe Terra. A tribo ficará orgulhosa.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nMaça de RochaMachadinha AborígeneFaca TalhadoraBengala do AnciãoMachado do Valente\n\nVocê também receberá:\n\n"},
[758] = {title = "Purificação de Chifre Troante", objective = "Use o Totem de Purificação Chifre Troante no Poço Chifre Troante, então fale novamente com Mull.", description = "O totem está pronto, <name>. E novamente é sua tarefa executar o ritual de purificação.\n\nVá ao Poço Chifre Troante, ao norte da aldeia, e use o Totem de Purificação Chifre Troante em seu ritual. Depois que terminar, fale novamente comigo.\n\nQue os espíritos guiem você.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nManto Chifre Troante"},
[759] = {title = "Totem de Juba Agreste", objective = "Leve 8 Dentes de Lobos-da-pradaria alfas para Mull Chifre Troante na Aldeia Casco Sangrento.", description = "Ainda resta pelo menos um poço a ser purificado, que recebeu o nome do clã Juba Agreste. Para que esse poço seja purificado, a terra tem de oferecer os dentes de um predador feroz, o lobo-da-pradaria alfa. Encontre os alfas ao norte e volte aqui quando a caçada terminar.\n\nOs alfas costumam vagar no sopé de nossa imponente cidade, Penhasco do Trovão.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[760] = {title = "Purificação de Juba Agreste", objective = "Use o Totem de Purificação Juba Agreste no Poço Juba Agreste, então fale novamente com Mull Chifre Troante.", description = "Os espíritos falam do seu feito de purificar os poços Casco Invernal e Chifre Troante, jovem <name>. O totem de Juba Agreste está pronto e sua última tarefa <o/a> aguarda.\n\nO poço Juba Agreste fica a norte do Penhasco do Trovão. Realize o ritual! Cure a terra do veneno dos goblins! Que a água possa fluir pura de novo!Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nMachadinha Aborígine CerimonialCajado do Velador de Sonhos\n\nVocê também receberá:\n\n"},
[761] = {title = "Caçada ao rapineiro", objective = "Leve 8 Troféus de Cálamo de Rapineiro para Harken Totem do Vento na Aldeia Casco Sangrento.", description = "O rapineiro é um pássaro astuto e difícil de encontrar e caçar. Para um caçador, uma coleção de cálamos de rapineiro é uma insígnia de inteligência e determinação.\n\nSe estiver <disposto/disposta> a aceitar a tarefa, vá para a planície e cace os rapineiros.\n\nTraga-me os cálamos e você honrará seu clã.\n\nOs rapineiros podem ser encontrados em qualquer lugar de Mulgore, mas é preciso olhos de lince para avistá-los, e cascos ligeiros para pegá-los.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[762] = {title = "Um embaixador do mal", objective = "Mate o Embaixador Infernus na Fortaleza Angor e leve a prova de sua morte para o Conselheiro Belgrum em Altaforja.", description = "O Embaixador Infernus foi enviado à Fortaleza Angor pelo próprio Ragnaros para tomar conta da Umbraforja que busca artefatos perdidos em Uldaman.\n\nBarbaridade, com isso nosso povo já sofreu diversas baixas, mas a situação obviamente piorou. Hesito em calcular quantos artefatos eles poderiam encontrar e usar contra a Altaforja.\n\nNosso objetivo é simples, <class>. Volte aos Ermos e mate o Embaixador Infernus. Quando a ameaça for eliminada, retorne ao Conselheiro Belgrum com a prova de sua vitória.\n\nEstá em suas mãos agora.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nEspada de Capitão dos Anões"},
[763] = {title = "Ritos da Mãe Terra", objective = "Leve o Totem do Vento do Falcão para Baine Casco Sangrento, na Aldeia Casco Sangrento. Siga a estrada que sai da Aldeia Narache.", description = "Para dar continuidade aos seus Ritos da Mãe Terra, você precisa passar por mais dois testes. Está na hora de você ampliar a sua experiência, <name>.\n\nViaje para a Aldeia Casco Sangrento e procure o chefe, Baine Casco Sangrento, filho de Caerne. Lá, você poderá continuar a sua jornada e conquistar a aceitação dos anciões do Penhasco do Trovão.\n\nLeve este totem para Baine. Ele vai reconhecer os meus entalhes e ajudá-<lo/la> em seu caminho.\n\nSiga a estrada que sai da Aldeia Narache e viaje depressa. Não se desvie do caminho para não se perder.", progress = "O que o traz à minha aldeia, <class>?", completion = "Uma mensagem do meu bom amigo Chefe Vento do Falcão! Ah, vejo pelos entalhes dele que você é um indivíduo ímpar.", rewards = "Você receberá:\n\n"},
[764] = {title = "Empreendimentos S.A.", objective = "Mate 14 Trabalhadores da Empreendimentos S.A. e 6 Supervisores da Empreendimentos S.A. para Morin Espreita Nuvem na Aldeia Casco Sangrento.", description = "Isso explica o aumento do número de empregados e equipamentos da Empreendimentos S.A. que vimos em Mulgore. Esses goblins... A empresa deles está se expandindo rápido demais, e exclusivamente para o bem deles. É como se diz: mostre algo a um goblin e você logo ouve o som das moedas tilintando.\n\nCriaturinhas gananciosas, essas. Infelizmente para o empreendimento comercial deles, nós taurens não podemos permitir tais operações em nossas terras. Vá à mina deles, a nordeste da caravana devastada, e dê-lhes um recado.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[765] = {title = "Supervisor Geringonça", objective = "Mate o Supervisor Geringonça e leve sua prancheta para Morin Espreita Nuvem na Aldeia Casco Sangrento.", description = "De acordo com os documentos que você resgatou da caravana, a Empreendimentos S.A. bolou muitos planos na tentativa de nos expulsar de nossas terras, para ter a liberdade de saqueá-las.\n\nA falta de limite para as baixezas de que são capazes para atingir as metas me enoja e aterroriza. Quero o mandante por trás dos planos morto. O nome dele é Supervisor Geringonça. Encontre-o na mina da Empreendimentos S.A., a leste da caravana devastada. Traga-me a prancheta dele, e veremos se conseguimos descobrir algo de útil.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nPunhal de CombateEsmaga-goblin\n\nVocê também receberá:\n\n"},
[766] = {title = "Mazzranache", objective = "Leve um Coração de Lobo-da-pradaria, um Fêmur de Puma das Estepes, uma Escama de Pinote e uma Moela de Rapineiro para Maur Clamachuva na Aldeia Casco Sangrento.", description = "Meu avô me contou histórias sobre as batalhas dele com um moa chamado Mazzranache. Ele me falou dos olhos vermelhos demoníacos, das garras afiadas como navalhas e da mordida venenosa.\n\nEntão, viajando pelas planícies, me deparei com Mazzranache. Ele é tão terrível quanto meu avô descreveu, e me deu uma bela mordida no ombro. Eu vou precisar de partes de animais difíceis de encontrar para limpar a infecção da mordida dele: um coração de lobo, um fêmur de puma, uma escama de pinote e uma moela de rapineiro.\n\nDepressa, o tempo urge!", progress = "A febre está piorando e minha mente divaga com pesadelos delirantes... Eu já devia saber que não seria capaz de lidar com uma fera que meu avô não conseguiu derrotar...\n\nConseguiu os ingredientes do remédio? Tenho medo de não conseguir limpar a ferida e morrer de infecção.", completion = "Ah, obrigado, &lt;name&gt;. Com os itens que você me trouxe, poderei criar o remédio necessário para impedir que a infecção se espalhe e, enfim, curá-la por completo. Devo minha vida a você.\n\nNunca me esquecerei daquele guincho quando Mazzranache veio para cima de mim, do esplendor das cores quando seu bico desceu...\n\nCuidado por onde anda.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas do CorremontesMunhequeiras do Caçador das Planícies\n\nVocê também receberá:\n\n"},
[767] = {title = "Rito de Visão", objective = "Fale com Zarlman Duas Luas na Aldeia Casco Sangrento.", description = "O Rito de Visão, um dos Ritos da Mãe Terra, ajudará a guiá-<lo/la> na conquista do respeito dos anciões do Penhasco do Trovão.\n\nNosso povo aprendeu que a terra é a nossa mais sagrada provedora. Para partilhar da visão ritualística, você precisa falar com o líder espiritual da aldeia, Zarlman Duas Luas.", progress = nil, completion = "Eu pressenti que você viria a mim, &lt;class&gt;. Sempre reconheço alguém que está tentando passar nos Ritos da Mãe Terra.", rewards = "Você receberá:\n\n"},
[768] = {title = "Em busca de couro", objective = "Leve 12 peças de Couro Leve a Veren Passo Alto, no Penhasco do Trovão.", description = "Saudações, jovem. Seus olhos brilham. Vejo que está <ansioso/ansiosa> para aventurar-se nas planícies em novas caçadas. Que você leve honra ao seu clã!\n\nTrabalho com couros, confeccionando vestes e armaduras para o povo do Penhasco do Trovão.\n\nSe caçar algumas feras e me trouxer as peles delas, farei algo para você.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nCinto de Pele de AnimalLuvas de Duas Camadas\n\nVocê também receberá:\n\n"},
[769] = {title = "Bolsa de Pelego de Kodo", objective = "Leve 4 Couros Leves e 4 Fios Grossos a Veren Passo Alto no Penhasco do Trovão.", description = "Os kodos de Mulgore têm a pele grossa e resistente, qualidades muito respeitadas entre nós. Caso tenha habilidade com couraria e queira aprender a confeccionar Bolsas de Pelego de Kodo, traga-me alguns suprimentos de que preciso.\n\nSe fizer isso, vou compartilhar meus conhecimentos com você.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nMolde: Bolsa de Pelego de Kodo"},
[770] = {title = "O manto marcado por demônios", objective = "Encontre alguém que saiba do Uivo Fantasma.\n\nItem fornecido:\n", description = "O pelego roto do Uivo Fantasma ainda exibe um terrível ferimento, consequência das lutas do lobo contra a Legião Ardente.\n\nTalvez alguém na Aldeia Casco Sangrento saiba da morte de Uivo Fantasma...Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nMartelo de SkornBacamarte de Skorn\n\nVocê também receberá:\n\n"},
[771] = {title = "Rito de visão", objective = "Colha 2 Pedras de Poço e 2 Pinhâmbar e leve-as para Zarlman Duas Luas na Aldeia Casco Sangrento.", description = "Para passar pelo Rito de visão, você precisa juntar os reagentes necessários para preparar a Água dos Videntes.\n\nVocê vai precisar de pedras de poço, que podem ser encontradas em torno de poços d'água em Mulgore. Com elas, eu vou combinar o pinhâmbar que cai das imponentes árvores da nossa terra.\n\nEsses reagentes contém elementos mágicos e, quando cozidos e consumidos diante da fogueira tribal, farão com você encontre uma visão. Siga-a, e ela <o/a> levará para o próximo passo desta missão sagrada.", progress = "Assim que você tiver recolhido a quantidade necessária de pedras de poço em torno dos poços d'água e pinhâmbar aos pés das árvores, prepararei a Água dos Videntes para você.", completion = "Vejo que você aprendeu a colher da nossa terra sagrada, &lt;name&gt;. Só preciso de um instante para preparar a Água dos Videntes.", rewards = "Você receberá:\n\n"},
[772] = {title = "Rito de visão", objective = "Beba a Água dos Videntes em frente à fogueira tribal na Aldeia Casco Sangrento e siga a visão quando ela aparecer.\n\nItem fornecido:\n", description = "Agora, eu lhe apresento a Água dos Videntes.\n\nQuando estiver pront<o/a>, consuma a água perto de uma fogueira tribal. Uma vez ingerida a água benta, levará alguns instantes para que a visão se materialize diante do fogo.\n\nQuando isso acontecer, caberá a você segui-la rumo ao seu destino...", progress = nil, completion = "Não tenha medo, &lt;name&gt;. Você conseguiu passar no Rito de Visão.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas do Pé d'ÁguaManto Insolado\n\nO seguinte feitiço será lançado em você:\n\nMarca do Indomado\n\nVocê também receberá:\n\n"},
[773] = {title = "Rito de sabedoria", objective = "Siga para o Rochedo Vermelho, a leste do Penhasco do Trovão, e procure o Espírito Ancestral.", description = "Para ser <aceito/aceita> entre os anciãos do Penhasco do Trovão é preciso concluir o rito de sabedoria.\n\nAgora que você concluiu o rito de visão, os espíritos ancestrais do Rochedo Vermelho lhe concederão a bênção dos nossos ancestrais. Somente os que beberam da água dos Videntes merecem a bênção.\n\nSiga para o leste do Penhasco do Trovão, para o Rochedo Vermelho, e procure o Espírito Ancestral, <name>.", progress = nil, completion = "Você mostrou um grande zelo no afã de concluir os Ritos da Mãe Terra, &lt;name&gt;.\n\nNós, os Espíritos Ancestrais, representamos os bravos taurens que deram a vida corajosamente para fundar e proteger a bela cidade do Penhasco do Trovão. Com isto eu transfiro a você o dever de tal proteção.\n\nJovem &lt;class&gt;, você passou no Rito de sabedoria. Adentre Penhasco do Trovão com orgulho.", rewards = "Você receberá:\n\n"},
[775] = {title = "Siga para o Penhasco do Trovão", objective = "Fale com Caerne Casco Sangrento no Penhasco do Trovão.", description = "Agora você será <reverenciado/reverenciada> em nossa bela cidade. Qualquer <race> digno de concluir os Ritos da Mãe Terra merece essa honra.\n\nComo última missão, procure nosso nobre líder, Caerne Casco Sangrento, que habita o topo da chapada mais alta do Penhasco do Trovão.", progress = nil, completion = "Saudações, jovem &lt;class&gt;. Vejo que você encontrou o caminho da minha morada.", rewards = "Você receberá:\n\n"},
[776] = {title = "Ritos da Mãe Terra", objective = "Caerne Casco Sangrento, no Penhasco do Trovão, quer que você leve para ele o Chifre de Arra'chea.", description = "Você concluiu os Ritos da Mãe Terra e conquistou seu lugar no Penhasco do Trovão.\n\nMas para conservar o posto que você lutou tanto para conseguir, é necessário que você continue provando seu valor para o seu povo.\n\nSomos caçadores, <name>. Lá embaixo, em Mulgore, existe um poderoso kodo chamado Arra'chea. Para comprovar suas habilidades de rastreamento e caça, quero que me traga o Chifre de Arra'chea.", progress = "Já seguiu o rastro de Arra'chea?", completion = "Muito bem! Foi uma boa caçada.\n\nVou mandar um grupo ir buscar a carcaça para que possamos usar o couro e a carne.\n\nVocê conquistou um feito e tanto para o seu povo, &lt;name&gt;.", rewards = "Você receberá:\n\nPerneiras do Caçador de Kodos"},
[777] = {title = "Isso vai ser difícil!", objective = "Leve a Engrenagem Nº 5 para Lotwil Viriato.", description = "Certo, toma aqui.\n\nVou ficar aqui atrás... bem protegido.\n\nPode ficar aqui comigo, se quiser.", progress = "<Sem erguer o olhar dos grilhões, Lotwil estende a mão e agita os dedos, impaciente.>\n\nA engrenagem, <name>, a engrenagem!", completion = "Ah, perfeito. Agora chega mais pra trás, enquanto eu me preparo pra fazer mais história!\n\n&lt;Lotwil continua trabalhando alucinadamente.&gt;", rewards = nil},
[778] = {title = "Isso vai ser difícil!", objective = "Derrote o Guardião Fam'retor e devolva os Grilhões de Laço Elemental do Lotwil para Lotwil Viriato.", description = "Pronto! Haha! Acabei os grilhões!\n\nChegou a hora, Lucien. Chegou a hora, Servo!\n\nPront<o/a> para testemunhar uma combinação perfeita entre alquimia, engenharia e magia, <name>?\n\nAgora eu vou invocar um elemental muito poderoso. É um dia de progresso para todos nós.", progress = "Não era bem o resultado que eu esperava. Pode devolver os meus grilhões?", completion = "Obrigado, &lt;name&gt;. Que situação, hein?\n\nNão sei o que deu errado. Ainda bem que a gente tinha as minhas magias para ajudar a mandar a criatura de volta para seu plano de origem.\n\nAqui, fica com esse pequeno gesto de agradecimento por todo o seu esforço...", rewards = "Você receberá:\n\nCronômetro Bacana"},
[779] = {title = "O Selo da Terra", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[780] = {title = "Os javaliços", objective = "Grull Vento do Falcão, na Aldeia Narache, quer que você mate Javaliços e leve para ele 8 Focinhos de Javaliço e 8 Flancos de Javaliço.", description = "Os Javaliços da Ravina da Lâmina Espinhenta, ao leste, estão invadindo a nossa área tribal de caça. Os Javatuscos Costagulha, com quem estamos em guerra, estão treinando os Javaliços para que eles sejam muito cruéis\n\nVá até lá e mate essas criaturas vis. Traga de volta uns focinhos e uns flancos para nós fazermos comida para os mais jovens.", progress = "A agressão dos Javaliços só aumenta. Temos que mandar uma mensagem bem clara. Já trouxe os focinhos e flancos?", completion = "Excelente. Os ingredientes darão um ótimo guisado, e perder esses javalis vai ensinar uma lição e tanto aos vis Costagulha.", rewards = "Você receberá:\n\nColete NômadeNaco de Pão Duro"},
[781] = {title = "Aldeia Narache sob ataque", objective = "Leve o Plano de Ataque dos Costagulha para o Chefe Vento do Falcão, na Aldeia Narache.", description = "Depois de analisar o mapa com cuidado, você percebe que descobriu os planos do chefe guerreiro dos Costagulha para fazer um longo cerco à Aldeia Narache.\n\nO Chefe Vento do Falcão certamente vai querer saber dessa informação!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nBroquel de Casca Grossa"},
[782] = {title = "Alianças rompidas", objective = "Mate o Chefe Tho'grun e leve o Signo da Terra para Gorn, em Karrath.", description = "Da aliança da Horda com o dragão Asa da Morte durante a Segunda Guerra, sabemos que dois dos tenentes dele, Neraçoite e Hematus, foram aprisionados na Ravina Lethlor, que fica bem longe ao leste. Para restaurar a honra da Horda, devemos silenciar esses seres depravados que já foram nossos aliados!\n\nContratamos o mercenário Tho'grun e seu bando de ogros para nos ajudar a obter o Signo da Terra, que é a chave necessária para abrir a prisão dos dracos. Mas Tho'grun nos traiu, ficando com o Signo. Vá ao Acampamento Barf e tome-o de volta!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[783] = {title = "A ameaça interna", objective = "Fale com o Major Belmonte.", description = "É bom se preparar, jovem <class>, porque há muito trabalho para se fazer aqui na Vila Norte.\n\nE eu não estou falando de lavrar a terra.\n\nOs guardas de Ventobravo estão tendo bastante dificuldade de manter a paz aqui, já que tantos de nós estão em terras distantes enquanto tantas ameaças se assomam. Estamos aceitando a ajuda de todos os que estiverem dispostos a defender o nosso lar. E a nossa aliança.\n\nSe quiser atender ao nosso chamado, fale com o meu superior, o Major Belmonte. Ele está dentro desta abadia logo atrás de mim.", progress = nil, completion = "Ah, mais um voluntário, que bom. Esses dias, temos recebido vários.\n\nSó espero que seja o suficiente.\n\nEstas terras têm sido alvo de inúmeras ameaças vindas de fora, e muitas das nossas forças estão concentradas longe daqui. Acontece que isso dá a oportunidade para que grupos de corruptos fora da lei atuem dentro das nossas fronteiras.\n\nNossa guerra tem muitos fronts, &lt;name&gt;. Acho bom se preparar para uma longa campanha.", rewards = "Você receberá:\n\n"},
[784] = {title = "Aniquile os invasores", objective = "Mate 10 Marinheiros de Kul Tiraz, 8 Fuzileiros de Kul Tiraz e o Tenente Bento. Depois, fale novamente com Gar'Thok no Monte Navalha.", description = "Liderados pelo Almirante Proudmore, os humanos de Kul Tiraz atacaram Durotar, violando o pacto que o Chefe Guerreiro havia feito com Jaina Proudmore para derrotar Arquimonde anos antes.\n\nO assalto humano foi contido e a Bastilha Tiragarde caiu. Mas recentemente os reforços do Almirante conseguiram retomar a bastilha, sob a liderança do Tenente Bento, e voltaram a representar uma ameaça à nossa terra. Esses humanos não demonstram o menor respeito pela diplomacia.\n\nPara provar que é honrad<o/a>, viaje para o sul e elimine os invasores humanos da Bastilha Tiragarde.", progress = nil, completion = "Notícias sobre a sua bravura correm rapidamente, &lt;class&gt;. Histórias sobre a vitória na Bastilha Tiragarde serão contadas em Orgrimmar.", rewards = "Você receberá:\n\n"},
[785] = {title = "Uma aliança estratégica", objective = "Procure Larri Presador no flanco ocidental da Aldeia Sen'jin.Thrall é um líder muito sábio e nobre. A tenacidade dele em batalha só é superada pela diplomacia dele.\n\nOs Trolls Lançanegra já se provaram aliados valiosíssimos para os orcs. Com eles, fizemos um pacto de proteção, pela lealdade e pela honra.\n\nO Chefe Guerreiro deu ordens para garantir que nossos amigos Lançanegra da Aldeia Sen'jin não estejam sofrendo nenhuma ameaça direta. Larri Presador, um dos batedores mais confiáveis deles, está vigiando o flanco ocidental da aldeia.\n\nProcure o Larri e ajude no que for preciso.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", description = "Thrall é um líder muito sábio e nobre. A tenacidade dele em batalha só é superada pela diplomacia dele.\n\nOs Trolls Lançanegra já se provaram aliados valiosíssimos para os orcs. Com eles, fizemos um pacto de proteção, pela lealdade e pela honra.\n\nO Chefe Guerreiro deu ordens para garantir que nossos amigos Lançanegra da Aldeia Sen'jin não estejam sofrendo nenhuma ameaça direta. Larri Presador, um dos batedores mais confiáveis deles, está vigiando o flanco ocidental da aldeia.\n\nProcure o Larri e ajude no que for preciso.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[786] = {title = "Frustrando o ataque dos Kolkar", objective = "Larri Presador, junto à Aldeia Sen'jin, quer que você destrua 3 Planos de Ataque, que estão no Penedo de Kolkar.", description = "Fale baixo, <class>. Os centauros Kolkar ficam no Penedo de Kolkar, que é logo além deste cume, ao oeste.\n\nOntem à noite, enquanto eles estavam fora para pilhar, eu me esgueirei pelo vilarejo deles e descobri que as bestas imundas bolaram um plano em três fases para atacar os trolls e os orcs de Durotar.\n\nNão podemos deixar a invasão se concretizar. Talvez você tenha a coragem necessária para se infiltrar no Penedo de Kolkar e destruir os planos de ataque.\n\nAté onde eu sei, eles dividiram os planos entre três líderes deles.", progress = nil, completion = "Se os centauros atacassem, não há dúvidas que a Horda triunfaria. Mas, ao prevenir o ataque, você poupou nossos bravos guerreiros de um banho de sangue desnecessário.\n\nE até que esses tempos difíceis cheguem ao fim, todos nós sabemos muito bem que muito banho de sangue ainda está por vir, tão certo quanto o sol se ergue e se põe nas areias do deserto de Tanaris.\n\nVocê prestou um grande serviço ao seu povo, &lt;class&gt;.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nManto do Lutador ExperienteBraçadeiras de Corda Grossa\n\nVocê também receberá:\n\n"},
[787] = {title = "A nova Horda", objective = "Apresente-se a Gornek, no Covil.Throm'ka, <class>. Sou Eitrigg e fui encarregado por Thrall de supervisionar o treinamento dos recrutas.\n\nA Horda mudou muito, já não é mais como antes. Houve um tempo em que eu me afastei da Horda, decepcionado com o crescimento da influência dos marionetes gananciosos da Legião Ardente. Durante o meu exílio, acabei sendo prisioneiro de um grupo de humanos, mas fui resgatado pelo Chefe Guerreiro. Depois de ouvir os ideais dele para uma Horda livre da influência demoníaca e guiada pelos xamãs, resolvi voltar.\n\nGornek vai te dar as instruções completas.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", description = "Throm'ka, <class>. Sou Eitrigg e fui encarregado por Thrall de supervisionar o treinamento dos recrutas.\n\nA Horda mudou muito, já não é mais como antes. Houve um tempo em que eu me afastei da Horda, decepcionado com o crescimento da influência dos marionetes gananciosos da Legião Ardente. Durante o meu exílio, acabei sendo prisioneiro de um grupo de humanos, mas fui resgatado pelo Chefe Guerreiro. Depois de ouvir os ideais dele para uma Horda livre da influência demoníaca e guiada pelos xamãs, resolvi voltar.\n\nGornek vai te dar as instruções completas.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[788] = {title = "Dentes cortantes", objective = "Mate 10 Mosquetuscos, depois fale novamente com Gornek no Covil.", description = "A primeira coisa a fazer é dar um pouco de firmeza ao seu caráter. Eu poderia enviar você até os Sertões para caçar kodos, mas, bem, com toda franqueza, você nos é mais útil viv<o/a> do que mort<o/a>.\n\nAcho que os mosquetuscos que vivem ao norte daqui vão ser um desafio à altura para você.", progress = nil, completion = "Hum, nada mal, &lt;name&gt;. Mas não deixe isso subir à cabeça... você terá que lutar com coisas piores ao longo da carreira.\n\nDe qualquer forma, você provou sua capacidade hoje, e a provação seguinte será contra um oponente consideravelmente mais perigoso. Assim sendo, você precisará de alguma proteção extra.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas de CasimiraLuvas de Couro Desgastadas em Batalha\n\nVocê também receberá:\n\n"},
[789] = {title = "A ferroada do escorpídeo", objective = "Leve 10 Caudas de Escorpídeo Operário a Gornek, no Covil.", description = "Tanto guerreiros poderosos quanto novatos desajeitados encontraram a morte na ferroada do escorpídeo. Você encontrará um grande número desses seres venenosos a noroeste daqui. Para provar seu talento no combate, me traga dez caudas de escorpídeo.\n\nO antídoto para a ferroada deles é feito do próprio veneno extraído dos aguilhões. Temos sempre à mão uma boa quantidade de antídoto para veneno de escorpídeo, para curar jovens como você...\n\nMas tenho certeza de que você não vai precisar de nada disso, não é?", progress = "A carapaça dos escorpídeos não é grossa a ponto de deter a força de um guerreiro determinado. Ataque com força e determinação, e os escorpídeos serão presas fáceis.", completion = "Há uma lição importante que você precisa aprender ao lutar com escorpídeos. Tanto os maiores quanto os menores oponentes podem ser a sua perdição. Em um combate feroz, muitas coisas podem ser a sua ruína.\n\nNão tenho mais a ensinar, &lt;name&gt;. Você se saiu bem, eu acompanharei sua evolução com interesse.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCinto de CasimiraCapa Desgastada em Batalha\n\nVocê também receberá:\n\n"},
[790] = {title = "Sarkoth", objective = "Mate Sarkoth e leve a garra dele para Hana'zua.", description = "<class>! Pensei que eu ia morrer aqui sem que ninguém soubesse. Enquanto eu caçava escorpídeos no Vale, encontrei um que parecia especialmente perigoso. Corri pra cima dele e consegui acertar um golpe forte na garra antes dela fechar na minha perna.\n\nMas eu não estava pronto para o ferrão, e ele foi bem no meu peito, abriu um buraco e me fez sangrar muito. Por favor, você tem que matar o escorpídeo para mim! Minha honra deve ser defendida! Eu combati ele no platô ao sul.", progress = "Ah... meu pai sempre disse que eu nunca conseguiria muita coisa, e aqui, prostrado sob uma árvore enquanto minha vida se esvai, parece que ele estava certo.\n\nAo menos, quero morrer sabendo que o meu último inimigo em vida jaz morto.", completion = "O meu golpe não foi forte o bastante para matar ele, mas olhar para o dano que causei me dá um leve sabor de orgulho. Este pequeno orgulho será tudo que terei se eu morrer, e, sob essa luz, a breve lista de conquistas da minha vida me enche de raiva.", rewards = "Você receberá:\n\n"},
[791] = {title = "Carregue suas tralhas", objective = "Férrano Escarnecenho na torre de vigia do Monte Navalha quer 8 Retalhos de Lona.", description = "A idade me tornou inútil na batalha. Agora me faço útil de outras maneiras.\n\nProcuro invasores a partir desse ponto de observação. À medida que a nossa força aqui cresce, me pego soprando cada vez menos a trompa sinalizadora.\n\nPara passar o tempo, invento apetrechos para ajudar os guerreiros mais jovens e capazes a defender nossa terra natal.\n\nPosso criar uma bolsa para você carregar seus pertences. Se isso for útil para você, traga-me uma lona, material que pode ser encontrado com humanos e centauros.", progress = "Foi com muito orgulho que lutei lado a lado do Chefe Guerreiro durante a conquista destas terras. Minha pele ainda conserva as cicatrizes da batalha.\n\nQuando, por necessidade, tivemos que fazer uma aliança profana com humanos e elfos para derrotar Arquimonde, o meu machado e o meu grito de guerra contribuíram para defender a honra da Horda.\n\nE hoje minha posição como vigia e provedor me traz um novo orgulho do meu próprio valor.", completion = "Excelente, &lt;name&gt;. Qualquer &lt;class&gt; que se preze certamente encontrará uso para esta bolsa no campo de batalha.\n\nSaúdo seu vigor e sua disposição para morrer em nome da Horda!", rewards = "Você receberá:\n\nBolsa de Couro Artesanal"},
[792] = {title = "Familiares torpes", objective = "Mate 12 Familiares torpes.\n\nFale novamente com Zureta Vista-longa em frente ao Covil.", description = "Creio que o Vale das Provações ensinará muito a você, jovem <class>.\n\nFui enviada ao Vale para guiá-l<o/a>, mas descobri uma mácula crescente neste lugar...\n\nUm grupo que se autointitula Lâmina Ardente tem um conventículo aqui no Vale das Provações. Escondem-se em uma caverna a nordeste, e os Familiares torpes deles saíram por aí causando caos.\n\nA sua primeira tarefa contra a Lâmina Ardente será derrotar esses familiares. Mate muitos e, se você sobreviver, fale novamente comigo.", progress = nil, completion = "Você se saiu bem, &lt;name&gt;.\n\nEmbora os familiares torpes sejam meras marionetes dos poderes mais sombrios da Lâmina Ardente, seu sucesso contra eles prediz grandes feitos no futuro.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nClava PrimitivaNavalha PrimitivaMachadinha PrimitivaBengala Primitiva\n\nVocê também receberá:\n\n"},
[793] = {title = "Alianças rompidas", objective = "Use o Signo da Terra para ativar os Pilares de Diamante, Opala e Ametista, e pegue as Pedras Rúnicas.\n\nColoque as pedras rúnicas no Selo da Terra para libertar Neraçoite e Hematus.\n\nMate-os e entregue as Braçadeiras de Neraçoite, os Grilhões de Hematus e o Signo da Terra para Gorn.", description = "O Selo da Terra é guardado por três pedras rúnicas, contidas nos Pilares de Diamante, Opala e Ametista, na Ravina Lethlor. Usando o Signo da Terra, você poderá remover as pedras rúnicas e desbloquear o Selo da Terra.\n\nMas seja cauteloso, <name>, os dracos negros Neraçoite e Hematus não devem ser menosprezados. Mate-os e devolva as braçadeiras deles para mim.\n\nPara alcançar a ravina, vá ao extremo leste dos Ermos. Mas tenha cuidado, pois a ravina é o lar de inúmeros dracos.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nPeitoral LufardenteCota de EscamaprismaGuarda-peito Forjado para GuerraMedalhão de Explosão Mental\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[794] = {title = "Medalhão da Lâmina Ardente", objective = "Leve o Medalhão da Lâmina Ardente para Zureta Vista-longa em frente ao Covil.", description = "Através de minhas divinações, vejo que um item de grande poder se esconde nas profundezas do Covil da Lâmina Ardente, guardado por feras e magia negra.\n\nEle é chamado de Medalhão da Lâmina Ardente, e sua próxima tarefa é encontrá-lo e removê-lo do covil.\n\nMas tenha cuidado, pois um agente da Lâmina Ardente pode ter se apossado do medalhão, e, se for o caso, então ele teria mais poder do que os familiares que você já encontrou.\n\nVá, <name>. Você encontrará o covil em uma caverna, a norte e a oeste.", progress = "Você concluiu sua tarefa, <name>? Trouxe o Medalhão da Lâmina Ardente?", completion = "Você o encontrou! Muito bem!\n\nOs seus esforços dentro do Covil da Lâmina Ardente são fundamentais para a expurgação dessa seita do Vale das Provações, mas temo que eles tenham outros objetivos em vista na nossa terra.\n\nAinda não foi desta vez que nos livramos deles.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nPerneiras EmpoeiradasColete Encadeado SerrilhadoCalças Rasgadas\n\nVocê também receberá:\n\nPoção Menor de Cura"},
[795] = {title = "Selo da Terra", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[804] = {title = "Sarkoth", objective = "Leve as notícias sobre os apuros de Hana'zua a Gornek, no Covil.", description = "Ver o que você fez por mim fortalece o meu coração. Não posso cair tão facilmente! Preciso resistir!\n\nMas não consigo percorrer o caminho de volta ao Covil sem ajuda. Por favor, <name>, volte e explique minha situação para Gornek. Talvez ele possa me ajudar.", progress = nil, completion = "A julgar pela sua descrição da fera, creio que você esteja falando de Sarkoth! Não é de surpreender que Hana'zua tenha sido subjugado por ele. O socorro será enviado imediatamente, não se preocupe mais com a situação de Hana'zua.\n\nContudo, devo dizer, estou muito surpreso em saber que você matou Sarkoth. Foi um feito digno de orgulho, &lt;name&gt;. E, por você ter lutado pela honra de um estranho enquanto outras tarefas ocupavam o seu tempo, a sua honra é ainda maior.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nColete de CasimiraPerneiras Encadeadas Desgastadas em Batalha\n\nVocê também receberá:\n\n"},
[805] = {title = "Apresente-se na Aldeia Sen'jin", objective = "Fale com o Mestre Gadrin na Aldeia Sen'jin.", description = "As suas provações contra a Lâmina Ardente terminaram... aqui, no Vale. Mas quero que você reporte as suas descobertas.\n\nVá à aldeia troll de Sen'jin e procure o Mestre Gadrin. A Aldeia Sen'jin fica a leste, fora do vale, e então à direita na bifurcação.\n\nAvise ao Gadrin a respeito da Lâmina Ardente, e que eles alcançaram o Vale das Provações. Descubra se eles já chegaram a Sen'jin.\n\nVá, <name>, e seja rápid<o/a>. Temo que o mal que encontramos dentro do Covil da Lâmina Ardente tenha sido apenas o prenúncio de uma ameaça maior...", progress = nil, completion = "Hum... seu relatório chegou numa hora ruim. A Lâmina Ardente não tá aqui em Sen'jin, mas a maldade deles se espalhou pela costa, nas Ilhas do Eco.\n\nOs orcs são amigos dos trolls Lançanegra. Amigos extraordinários. A gente quer ajudar os orcs, mas... a gente precisa de ajuda também.", rewards = "Você receberá:\n\n"},
[806] = {title = "Tempestades sombrias", objective = "Leve a Garra do Bulho para Orgnil Chaganalma no Monte Navalha.", description = "Não podemos permitir que a Lâmina Ardente se estabeleça em Durotar! Precisamos destruí-los antes que esse mal se espalhe!\n\nConduzi minhas próprias investigações e descobri que um bruxo da Lâmina Ardente, o goblin Bulho Tempesnigra, acampou no Desfiladeiro do Trovão a noroeste. Lá, ele e seus servos sectários espalharam o caos.\n\nEncontre e derrote Bulho, e traga-me a garra morta dele!", progress = "Você achou o Bulho, <name>? Ele e o restante da Lâmina Ardente devem ser varridos das nossas terras!", completion = "A-há! Você o pegou!\n\nAssim você deixa o seu clã orgulhoso, &lt;name&gt;. E, por sua causa, Durotar está livre de mais um agente do mal.", rewards = "Você receberá:\n\nBotas de Pelego de Tigre"},
[808] = {title = "O crânio de Minshina", objective = "Recupere o Crânio de Minshina do círculo do poder nas Ilhas do Eco.\n\nLeve-o para o Mestre Gadrin na Aldeia Sen'jin.", description = "Ouço a voz do meu irmão, Minshina, me chamando em meus sonhos.\n\nEle foi levado por Zalazane, o bruxo das Ilhas do Eco a leste. E está morto.\n\nMas a morte não significou a liberdade do meu irmão, parceir<o/a>. O espírito de Minshina foi preso dentro do próprio crânio dele pelas magias de Zalazane. Nos meus sonhos, vejo ele junto de outros crânios, em um círculo de poder na maior Ilha do Eco. Enquanto ficar por lá, a alma do meu irmão está condenada.\n\nPor favor, <name>. Encontra o círculo e recupera o crânio de Minshina. E traz aqui pra mim.\n\nLiberta o espírito dele!", progress = "Tu trouxe o crânio do meu irmão, <name>? Ele finalmente está livre?", completion = "Obrigado, &lt;name&gt;. Tu salvou Minshina. Salvou o espírito do meu irmão da escravidão!", rewards = "Você receberá:\n\nCrânio Levemente Faiscante"},
[809] = {title = "Ak'Zeloth", objective = "Fale com Ak'Zeloth nos Sertões.", description = "Desde a última grande guerra, quando a Legião Ardente foi derrotada, tenho buscado fontes de corrupção demoníaca na sociedade órquica. O colar que você trouxe confirma o que eu temia.\n\nEle pertence à Lâmina Ardente, um culto que se une em torno de um item de poder demoníaco, chamado de Semente Demoníaca. Esse item pode ser encontrado nos Sertões, no topo do Morro de Brumedo. E deve ser destruído!\n\n. Vá ao Posto Remoto, que fica à margem dos Sertões, a oeste daqui, e fale com o meu assistente, Ak'Zeloth. Ele lhe orientará melhor.", progress = nil, completion = "Neeru quer que a Semente Demoníaca seja destruída? Estranho...\n\nPois bem. Se ele quer que a semente desapareça, então lhe direi como removê-la.", rewards = "Você receberá:\n\n"},
[812] = {title = "Busca da cura", objective = "Encontre Kor'ghan em Orgrimmar e pegue o Antídoto para Caudaçonha. Então leve o antídoto para Rhinag perto da fronteira noroeste de Durotar.", description = "<name>... seu timing é perfeito. Só espero poder elogiar sua rapidez também.\n\nFui descuidado ao lutar com algumas das caudaçonhas aqui perto, e uma delas me picou pra valer. Posso sentir o veneno dela me enfraquecendo enquanto falamos. Nesse ritmo, talvez me reste uma hora de vida. Mas vou precisar da sua ajuda se quiser sobreviver...\n\nKor'ghan, em Orgrimmar, sabe como fazer o antídoto. Encontre ele... depressa, <name>. Não vou conseguir resistir muito mais tempo. Ele deve estar no Antro das Sombras.", progress = "Estou quase feliz por não poder voltar a Sen'jin como estou agora. Minha fraqueza e estupidez com certeza seriam ridicularizadas.", completion = "&lt;name&gt;, tu salvou minha vida. Obrigado.\n\nPor favor, tome isso. Espero que pelo menos possa te ajudar em suas viagens, se não lhe render uma graninha. Pelo que tu diz, não vou usar isso tão cedo. Kor'ghan vai me fazer passar por mais daqueles rituais dele até eu conseguir me provar. Vou ter que matar mais javalis, juntar mais caudas de escorpídeos...", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBroquel de CargaArmadura Escorpídea Leve\n\nVocê também receberá:\n\n"},
[813] = {title = "Em busca do antídoto", objective = "Leve 4 Vesículas de Veneno de Caudaçonha para Kor'ghan em Orgrimmar.", description = "Os Caudaçonhas são alguns dos escorpídeos mais mortais de Durotar. Rhinag não foi nada inteligente em caçá-los sem ter o antídoto, mas, neste momento, a última coisa de que ele precisa é punição.\n\nTraz pra mim algumas vesículas de veneno de caudaçonha de qualquer caudaçonha perto da entrada de Orgrimmar, que faço um antídoto para ele.\n\nDepressa, <name>. Um dos nossos precisa da sua ajuda.", progress = "Lembre-se, independentemente da criatura que tu for caçar, é sempre bom estudá-la e entender seu comportamento. Esse conhecimento pode salvar sua vida.", completion = "Aqui está o antídoto, &lt;name&gt;. Leva pro Rhinag o mais rápido possível e tenha cuidado.", rewards = "Você receberá:\n\nAntídoto para Caudaçonha"},
[815] = {title = "Quebrar alguns ovos", objective = "Leve 3 Ovos de Açoitacauda para o Cozinheiro Torka no Monte Navalha.", description = "Bah! Quase me esqueci que preciso preparar o café da manhã.\n\n<name>, mexa-se e me traga alguns ovos de açoitacauda. Precisarei de pelo menos três para a refeição de amanhã.\n\nOs açoitacaudas Garrasangre podem ser ferozes, e quase sempre protegem seus ovos até a morte. Às vezes, eles enterram os ovos bem fundo na terra para desenterrarem em segurança, mas, se você for para as Ilhas do Eco a sudeste da Aldeia Sen'jin, poderá encontrá-los por todas aquelas ilhas lá.", progress = "Café da manhã, almoço, jantar! Quem se importa qual é a refeição? Ainda assim tem que ser preparada por alguém, ou seja, eu!", completion = "Você pode ser fraco, mas pelo menos não é desastrado, &lt;name&gt;.\n\nAqui, pegue isso e saia da minha frente. Tenho que cozinhar.", rewards = "Você receberá:\n\nNaco de Pão DuroCarne-seca Dura"},
[816] = {title = "Em memória", objective = "Leve uma prova do destino de Kron para Misha Tor'kren na fazenda a noroeste do Monte Navalha.", description = "<name>, por favor, você pode me ajudar? Meu filho Kron foi caçar dias atrás e ainda não voltou. Ele foi para o oeste em direção dos Sertões caçar crocoliscos ao longo do Rio Furiaustral. Temo pelo pior.\n\nPor mais forte que ele seja, seu orgulho teimoso sempre o colocou em apuros. Eu o avisei que os crocoliscos eram violentos e cruéis. Ele ficou bravo e saiu, furioso.\n\nSe você não conseguir encontrá-lo, pelo menos me traga uma prova do destino dele... mesmo que tenha que abrir o estômago de todos os crocoliscos ao longo das margens do Furiaustral.", progress = "Rezo para que Kron volte para mim, mas estou quase certa de que já sei o destino dele.", completion = "Ah, meu filho... meu lindo filho.\n\nVenho sofrendo desde que meu filho partiu, sem ter certeza do destino dele. Agora que sei o que aconteceu, talvez eu possa finalmente começar a pranteá-lo.\n\nObrigada, &lt;name&gt;. Por favor, pegue isso. Eu ia dar a Kron como um presente quando ele voltasse vitorioso. Agora que sei que ele está morto, não posso suportar olhar para isso.", rewards = "Você receberá:\n\nManto Costurado à Mão"},
[817] = {title = "Presa prática", objective = "Leve 4 Peles de Tigre de Durotar para Vel'rin Presa na Aldeia Sen'jin.", description = "Muitas das peles que usamos vêm dos tigres de Durotar, <name>. Cobertores, armaduras, tendas: há várias razões pra caçarmos as feras e, ao mesmo tempo, várias pra deixarmos que prosperem.\n\nChegou a hora de abater o rebanho, por assim dizer, <meu/minha> chapa. Aumentamos em número e nossas necessidades estão começando a exceder nossos estoques. Preciso de mais peles pra preparar bens adequados pro nosso povo.\n\nTraga-me 4 peles de tigre de Durotar e vou recompensá-l<o/a>. Você pode encontrar os tigres nas ilhas ao sul daqui.", progress = "Lembro da minha primeira caça ao tigre, <name>. Escolhi caçar na maior das Ilhas do Eco, e fiquei paradinho às sombras das folhagens por quase um dia inteiro... observando... esperando.\n\nMeus músculos estavam retesados, e eu estava pronto pra atacar. Foi uma das minhas maiores vitórias quando aquele tigre finalmente mordeu a isca.", completion = "Tô satisfeito, &lt;name&gt;. Com a sua ajuda, nosso povo estará protegido e não passará frio quando a estação mudar.\n\nObrigado.", rewards = "Você receberá:\n\n"},
[818] = {title = "Um espírito solvente", objective = "Leve 4 Olhos de Makrura Intactos e 8 ampolas de Muco de Rastejante para Mestre Vornal na Aldeia Sen'jin.", description = "Embora meus olhos já não sejam os mesmo, ainda posso ver claro que chegue. Frequentemente, preciso confiar em minhas habilidades alquímicas pra me ajudar com magias que um dia me foram fáceis. Mas me recuso a aceitar um aprendiz — nenhum troll ou orc jamais se mostrou digno.\n\nTu é dign<o/a>? Sim, claro que é... claro que tu pensa que é, camarada.\n\nPreciso de algumas coisas. Pode trazê-las para mim?\n\nPreciso de olhos de makrura intactos e ampolas de muco de rastejante. Tu pode pegá-los de qualquer makrura ou rastejante em Durotar. A gente se fala em breve.", progress = "O tempo é o verdadeiro teste pra sua força e vontade. Perca a paciência ou ceda à fraqueza, e seu verdadeiro eu será revelado, parceiro.", completion = "Muito bem, meu jovem... Muito bem, mesmo.", rewards = "Você receberá:\n\nCola Grudenta à Beça"},
[819] = {title = "Barril Vazio do Chen", objective = "Encontre alguém que saiba sobre o Barril Vazio do Chen.", description = "Um pequeno cartaz em uma das pontas do barril diz:\n\n\"Chen Malte do Trovão\n\nQue a bebida sempre possa animar seu espírito.\"", progress = "Sim?", completion = "Minha nossa...\n\nEsse era um dos barris de Chen Malte do Trovão. Ele viajou com Rexxar faz muito tempo. Não tenho notícias dele há séculos. Obrigado por trazer isso para mim, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[821] = {title = "Barril Vazio do Chen", objective = "Leve 5 Presas de Leão da Savana, 5 Rins de Pinote e 1 Chifre de Lagarto Trovejante para o Cervejeiro Drohn, em Vila Catraca.", description = "Quer experimentar a bebida que leva o nome do Chen? Ah, a malte do trovão é uma cerveja fantástica. Chen ensinou a receita ao meu mentor, que a repassou para mim. Preciso que você me traga algumas coisas, mas já lhe adianto: o sabor vale a pena!\n\nTraga pra mim cinco presas de qualquer leão da savana, cinco rins de qualquer raça de pinote e um chifre de qualquer espécie de lagarto trovejante. Isso vai bastar para mim.\n\nVocê acha esses ingredientes em qualquer parte dos Sertões.", progress = "Como está indo a busca?", completion = "Hahah... Nunca imaginei que conseguiria fazer outra partida dessa bebida de novo.\n\nVocê me encheu de nostalgia agora, &lt;name&gt;. Isso me lembra a última vez que fiz um lote de cerveja tróggica. Obrigado. As lembranças me aqueceram o estômago.", rewards = "Você receberá:\n\nMalte do Trovão"},
[822] = {title = "Barril Vazio do Chen", objective = "Leve 5 Glândulas Relampejantes, 1 Glândula Salivar de Falcotrom e 1 Fígado de Kodo para o Cervejeiro Drohn, em Vila Catraca.", description = "É boa essa malte do trovão, hein?\n\nTem outra receita que aprendi com o Chen. Interessa? Fica pronta rápido e pode ser útil se você pretende viver mais aventuras.\n\nTraga pra mim cinco glândulas relampejantes de qualquer tipo de borrascouro, uma glândula salivar de grande falcotrom e um fígado de qualquer tipo de kodo dos Sertões.\n\nComo eu disse, essa coisa é muito saborosa. É a cerveja trogg de que lhe falei antes.", progress = "Está tendo muito difícil encontrar aqueles ingredientes, <name>?", completion = "Haha! Eu sabia que você não ia resistir a essa cerveja. Volte sempre que quiser tomar mais uma, &lt;name&gt;. É só trazer os ingredientes necessários.", rewards = "Você receberá:\n\nCerveja Trogg"},
[823] = {title = "Apresente-se a Orgnil", objective = "Fale com Orgnil Chaganalma no Monte Navalha.", description = "Nós da Aldeia Sen'jin temos nossos próprios problemas, e agradecemos a sua ajuda. Mas as notícias que você traz sobre a Lâmina Ardente podem ser um problema pra todos.\n\nTem um orc no Monte Navalha, Orgnil Chaganalma, que policia Durotar contra males como a Lâmina Ardente! Procura Orgnil e conta pra ele nossa situação, assim como as notícias que tu traz do Vale das Provações. Ele vai querer saber essas coisas.\n\nO Monte Navalha fica ao norte.", progress = nil, completion = "Sim? Você tem alguma coisa a relatar? Bem, então diga!", rewards = "Você receberá:\n\n"},
[824] = {title = "Je'neu da Harmonia Telúrica", objective = "Leve o Globo d'Água Conspurcado para Je'neu Sancrea no Assentamento Zoram'gar, no Vale Gris.", description = "A Harmonia Telúrica é um grupo xamanista dedicado ao estudo e à preservação dos elementos. Eles sabem mais de deuses malucos, lendas e histórias velhas do que qualquer estudioso jamais saberá.\n\nPor sorte, eu conheço um membro da Harmonia Telúrica aqui no Vale Gris. Vá até o Assentamento Zoram'gar, perto da Praia de Zoram, e mostre o globo para Je'neu Sancrea. Se há alguém capaz de descobrir o significado dessa coisa, esse alguém é ele.", progress = "Ah, olá. Eu já passei tanto tempo estudando a água... todos os elementos, na verdade... que às vezes nem noto as pessoas do meu lado.\n\nPosso ajudá-lo em alguma coisa?", completion = "Este globo... É preocupante. A força de muitos dos mais poderosos elementais vem de artefatos como este. Encontrar um globo quase inteiro é uma coisa... Encontrar um tão corrompido é outra.\n\nVou tomar providências para que o globo seja estudado a fundo pela Harmonia Telúrica. Quem sabe assim consigamos dar um fim a esta corrupção dos elementos. Por favor, aceite isto como justo pagamento por ter trazido isto para a Harmonia Telúrica.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCinto HábilEscudo Lodo FlutuanteCapa Macia do Salgueiro\n\nVocê também receberá:\n\n"},
[825] = {title = "Dos destroços...", objective = "Gar'Thok, do Monte Navalha, quer que você recupere 3 Ferramentas Gnômicas dos destroços junto à costa.", description = "Um dos meus batedores mais atentos informa que os destroços da frota de Proudmore ainda permanecem próximos à costa de Durotar, a leste da Bastilha Tiragarde.\n\nNão é nenhum segredo que os humanos, em aliança com aquelas criaturinhas torpes conhecidas como gnomos, têm um conhecimento avançado de mecânica. Devemos saber todo o possível sobre nossos inimigos em potencial. E o nosso povo também se beneficiará desse novo conhecimento.\n\nPercorra os destroços, <name>, e recupere para mim as ferramentas da Aliança.", progress = "Quando aprendermos como os humanos operam a estranha maquinaria deles, teremos mais chances de derrotá-los em futuras batalhas.\n\nCom os novos conhecimentos que adquirimos, a Horda só cresce em força.", completion = "Sua missão de resgate foi um sucesso, &lt;class&gt;. Vou cuidar para que essas ferramentas sejam levadas para Orgrimmar com a próxima caravana.\n\nMuito bem.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas Sujas de BarroMunhequeiras do Corredor da AreiaCinturão Largo de Metal\n\nVocê também receberá:\n\n"},
[826] = {title = "Zalazane", objective = "Derrote Zalazane.\n\nMate 8 Trolls Vodu e 8 Trolls Bagateados.\n\nLeve a Cabeça do Zalazane para Gadrin.", description = "O mandingueiro Zalazane vive nas Ilhas do Eco, ao leste. Lá já foi o nosso lar.\n\nDe lá ele manda os trolls dele para o continente, para embagatar nosso povo e dominar mais e mais da gente.\n\nEle precisa ser detido.\n\nDerrote o Zalazane e os lacaios dele. Eles já foram trolls Lançanegra, mas hoje estão perdidos pra gente. Traz a cabeça dele pra mim, pra eu saber que o reino de terror dele acabou.", progress = "Zalazane foi derrotado, <name>?", completion = "Com o fim do Zalazane a nossa tribo pode dormir em paz de novo.\n\nObrigado, &lt;name&gt;. A tribo Lançanegra deve muito a você. Se você sofrer de algum mal, é só falar aqui atrás com o meu assistente, o Bom'bay. O vodu dele é sinistro...", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas LevesGuantes Velados\n\nVocê também receberá:\n\n"},
[827] = {title = "A Rocha da Caveira", objective = "Junte Colares Calcinantes dos sectários na Rocha da Caveira.\n\nLeve-os para Margoz no acampamento dele.", description = "<name>, a Lâmina Ardente infestou a caverna a leste, conhecida em Orgrimmar como a Rocha da Caveira. Dentro dela, eles realizam rituais torpes e queimam a própria carne com colares calcinantes.\n\nUsando esses colares, acredito que os sectários se harmonizem com o poder demoníaco. Mas, para ter certeza, preciso estudar uma coleção de colares.\n\nVá até a Rocha da Caveira e obtenha os colares calcinantes dos sectários que você encontrar por lá. Traga-os a mim e descobrirei quais segredos eles escondem.", progress = "Você já foi até a Rocha da Caveira, <name>? Conseguiu os colares calcinantes?", completion = "Excelente. O segredo por trás da Lâmina Ardente está escondido nestes colares. E eu vou descobrir que segredo é esse...", rewards = "Você vai poder escolher uma dessas recompensas:\n\nAdaga SerrilhadaEspada Bastarda FirmeMaça Pungente\n\nVocê também receberá:\n\n"},
[828] = {title = "Margoz", objective = "Fale com Margoz.", description = "Um dos nossos xamãs, Margoz, tem mais informações sobre a corrupção da Lâmina Ardente. Ele falou sobre uma caverna chamada Rocha da Caveira, nas montanhas próximas a Orgrimmar, que abriga um bando de sectários da Lâmina Ardente.\n\nAntes de você ir até a Rocha da Caveira, fale com Margoz. Ele é sábio e seus conselhos são valiosos.\n\nEle está acampado a nordeste daqui, entre a costa e o Barranco da Ravina Seca.\n\nSiga os conselhos dele, mas independente do que ele disser, <name>, ainda quero que você esmague aqueles sectários!", progress = nil, completion = "Bem-vindo, &lt;name&gt;. Fiquei sabendo da sua chegada e das suas proezas em Durotar.\n\nVocê é um &lt;class&gt; com perícia e reputação crescentes.\n\nMantenha-se no caminho certo e seu futuro será grandioso.", rewards = "Você receberá:\n\n"},
[829] = {title = "Neeru Cortafogo", objective = "Leve um Colar de Amostra para Neeru Cortafogo em Orgrimmar.", description = "Os colares calcinantes que você trouxe são implementos demoníacos poderosos. Receio que descobrir a origem destes colares vá além das minhas habilidades como xamã. Nós vamos precisar de um bruxo para conseguir estudá-los.\n\nLeve um colar calcinante para Neeru Cortafogo. Embora seja um bruxo habilidoso, ele garante que usa seus poderes contra os demônios, e que suas pesquisas em ocultismo são benignas. Verdade ou mentira, nós precisamos da ajuda dele contra a seita demoníaca em Durotar.\n\nEncontre Neeru em Orgrimmar, no Antro das Sombras.", progress = "Minhas mais humildes saudações, <class>. Como posso ajudar meu irmão <race> hoje?", completion = "Ah, e onde você achou isso? O bom Margoz do Monte Navalha lhe enviou até aqui, não foi?\n\nBem, deixe-me olhar mais de perto...", rewards = "Você receberá:\n\n"},
[830] = {title = "As ordens do almirante", objective = "Leve as Ordens do Almirante Proudmore para Gar'Thok no Monte Navalha.", description = "Você abre o envelope velho e surrado e descobre um documento de aparência oficial. Você reconhece o selo do Almirante Proudmore.\n\nParece importante. Talvez Gar'Thok, comandante do Monte Navalha, se interesse por esta informação.", progress = "Você está com um semblante preocupado, <class>. O que é que você tem?", completion = "Isso não é bom sinal. Foi sábio de sua parte me trazer essa informação.", rewards = "Você receberá:\n\n"},
[831] = {title = "As ordens do almirante", objective = "Entregue as Ordens do Almirante Proudmore a Nazgrel nos aposentos de Thrall, em Orgrimmar.", description = "Não se pode confiar nos humanos. Nós lutamos ao lado deles com um peso no coração, sabendo que um dia eles nos trairiam.\n\nA morte do Almirante Proudmore não foi suficiente para acabar com o legado da escória traidora. Os planos do humano já estavam traçados muito antes do fim.\n\nO reino dele não vai sequer morrer com Bento. Quem sabe quanto tempo levará até que a próxima vaga dos Proudmore se lance sobre a nossa costa?\n\nPrecisamos levar essas ordens a Nazgrel, em Orgrimmar, imediatamente! Ele fica nos aposentos de Thrall.", progress = "Não está vendo que estou ocupado aqui? É bom ser importante...", completion = "Eu insisti vezes sem conta para que o Chefe Guerreiro não confiasse nos humanos, mas o que está em jogo aqui não é o orgulho pessoal.\n\nVocê serviu honradamente à Horda, jovem &lt;class&gt;.\n\nAgora me desculpe. Preciso tratar desse assunto com Thrall imediatamente...", rewards = "Você receberá:\n\n"},
[832] = {title = "Sombras incandescentes", objective = "Leve esse olho para Neeru Cortafogo.", description = "<Palavras emanam do pingente para sua mente.>\n\nGazz'uz?\n\nGazz'uz... responda!\n\nHá notícias de sua descoberta na Rocha da Caveira. Você precisa se preparar para um ataque!\n\nGazz'uz, eu ordeno que você fale! Fale ou eu vou garantir que Neeru Cortafogo saiba da sua presença, e ele será veloz e brutal.\n\nNão teste sua perícia contra Neeru...\n\n...Gazz'uz... você está aí?Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[833] = {title = "Sepultamento sagrado", objective = "O Erudito Totem da Chuva quer que você mate 8 Traiçoeiros Costagulha no Rochedo Vermelho.", description = "Somente os mais valorosos taurens têm o direito de repousar no Rochedo Vermelho, nosso cemitério sagrado. Trata-se de uma honra concedida aos grandes guerreiros que ajudaram a fundar e defender o Penhasco do Trovão e àqueles que deram suas vidas pelo bem maior de suas tribos e chefes.\n\nContudo, uma horrível ameaça acaba de invadir nosso solo sagrado. Um bando de javatuscos Costagulha está saqueando o cemitério. E eu já não estou mais no meu auge, estou velho demais.\n\nÉ preciso expulsá-los à força, <name>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[834] = {title = "Ventos do deserto", objective = "Recupere 5 Sacos de Suprimentos e entregue-os a Rezlak perto de Orgrimmar.", description = "Meu nome é Rezlak. Sou um dos rapazes do Gasganete. O chefe me mandou aqui para dar uma mãozinha pros orcs de Durotar. Até que estaria tudo numa boa se não fosse pelas caravanas. Não consigo manter a segurança delas! Isso dificulta o meu trabalho, sabia?\n\nO último carregamento que, aliás, eles juraram que seria entregue, foi surrupiado pelas harpias Lufareia de... onde é que foi mesmo... foi na Garganta do Vento Cortante?\n\nEu preciso daqueles suprimentos, ou eu não vou conseguir fazer nada aqui! Siga pelo grande cânion em direção ao sul e você encontrará uma ravina encravada a leste e oeste das muralhas de pedra.", progress = "Eu sou ótimo no que faço, não me leve a mal, mas isso é ridículo! O que eles querem? Que eu faça castelos de areia?", completion = "Opa! Era disso mesmo que eu precisava! Você conseguiu pegar nossos suprimentos de volta. Que ótimo! Vou poder voltar ao trabalho assim que eu achar alguém para carregar esses sacos para mim.", rewards = "Você receberá:\n\n"},
[835] = {title = "Faça o que eu digo...", objective = "Mate 12 Selvagens Sopravento e 8 Bruxas da Tempestade Sopravento para Rezlak perto do Barranco da Ravina Seca.", description = "O Gasganete me disse uma vez assim: \"Rezlak, olha só. Tem uma coisa que você tem que saber na vida. Se você quer uma coisa bem feita, faz você mesmo.\"\n\nEu não vou aguentar perder todas as caravanas, então eu vou é resolver do meu jeito. O Barranco da Ravina Seca fica do lado leste da Garganta do Vento Cortante. Se a gente matar as harpias de lá, vai ficar tudo bem com as caravanas.\n\nMas é claro que o Gasganete me ensinou outra coisa, também: \"Pagando bem tudo se arranja\". Então... E aí? Me ajuda nessa, <name>?", progress = "Você nunca imaginaria isso de um cara como meu chefinho Gasganete, mas parece que ele sabia o resultado desde o início. \"Não tenha medo de dar aquele grande passo\", disse ele.\n\nAs harpias não vão parar de nos perturbar e as caravanas não vão deixar de ser atacadas a não ser que a gente tome uma providência a respeito, não é?", completion = "Agora já dá para respirar, porque as harpias não vão mais encher a paciência das caravanas. Nem tão cedo. Eu soube que vêm uns brinquedinhos legais no próximo carregamento!\n\nVocê é muito bom no que faz, &lt;name&gt;! Você tem que bater um papo com o meu chefinho em Vila Catraca! Aposto que ele arruma trabalho para você em dois tempos.\n\nAh, não se preocupe... Como eu poderia esquecer de pagar pelo serviço que você me prestou?", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCorta-asa-de-harpiaArco Curto de NogueiraCajado de Madeira Manchado\n\nVocê também receberá:\n\n"},
[836] = {title = "Resgate OOX-09/HL!", objective = "Escolte o OOX-09/HL até a costa além dos Penhascos Panorâmicos, então se apresente a Olhatorto Obnótico, na Angra do Butim.", description = "A voz do gnomo surge de novo do robô:\n\n\"Preciso transportar o OOX-09/HL para um local amplo e seguro para eu começar o longo procedimento de lançamento. Ele tem um manto de invisibilidade embutido, mas preciso de tempo para torná-lo funcional de novo. Escolte o robô de sua localização atual para, digamos, a costa além dos Penhascos Panorâmicos! Lá seria um lugar perfeito e extenso o suficiente para a conexão!\"\n\nLeve-o em segurança para a costa e volte para falar comigo na Angra do Butim! Olhatorto Obnótico – câmbio!\"Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas do Inventor GnômicoDispositivo Gnômico de Afundamento\n\nVocê também receberá:\n\n"},
[837] = {title = "Invasão", objective = "Mate 4 Javatuscos Crinavalha, 4 Batedores Crinavalha, 4 Levanta-poeira Crinavalha e 4 Guardas de Batalha Crinavalha para Gar'Thok no Monte Navalha.", description = "Quando chegamos, os javatuscos Crinavalha dominavam grande parte da região e provaram ser um espinho em nossas vidas. Com muito esforço, conseguimos expulsar a maior parte deles, mas ainda permanecem firmemente estabelecidos em algumas áreas.\n\nSó que isso já se prolongou demais. Para nossa própria proteção, não podemos permitir que os Crinavalha dominem nenhuma área em nossas terras. Os acampamentos deles podem ser encontrados a oeste daqui. Procure os espinheiros e você os encontrará. Hoje nós os expulsamos de Durotar, amanhã, talvez de toda a Kalimdor.", progress = nil, completion = "Transmitirei a notícia de que os javatuscos foram removidos de seus covis e que suas construções podem ser incendiadas. A remoção dos Crinavalha de Durotar é mais um passo para assegurarmos completamente as fronteiras da nossa nova pátria.", rewards = "Você receberá:\n\n"},
[838] = {title = "Scolomântia", objective = "Fale com o Boticário Trépido no Baluarte das Terras Pestilentas Ocidentais.", description = "Na ilha de Castro das Flechas fica Scolomântia, uma escola que serve de baluarte para o Flagelo. Nossos inimigos estão lá, onde podem conduzir em segurança pesquisas para se fortalecer. O caminho está bloqueado por uma porta, e, por mais que tenhamos tentado, é impossível entrar sem a chave certa.\n\nO Boticário Trépido é versado em Scolomântia e tem uma ideia sobre como obter uma chave. Você já provou que é competente, então confio essa tarefa a você. Fale com ele sobre o assunto.", progress = nil, completion = "Sim, &lt;class&gt;, o Alto-executor falou que eu poderia contar com você para tratar da chave. Dediquei grande parte dos meus estudos recentes à Scolomântia, e acredito que conheço um meio para obter uma chave que vai lhe permitir cruzar a porta principal.\n\nEssa tarefa não será fácil, mas tarefas assim raramente são. Você me parece ser do tipo que já sabe bem disso.", rewards = "Você receberá:\n\n"},
[840] = {title = "Recruta da Horda", objective = "Siga a estrada a oeste, de Monte Navalha para os Sertões, passando por uma ponte.\n\nPare no posto avançado órquico do outro lado da ponte.\n\nEntregue sua carta de recrutamento para Kargal Chaga de Guerra, no posto avançado dos Sertões.", description = "Hum, você me parece forte o bastante.\n\nEscute só: meu velho amigo Kargal está precisando de recrutas para servir nos Sertões. Eu sei que você quer fazer o melhor que puder pela Horda. Bem, a sua chance chegou.\n\nLeve esta carta de recrutamento para Kargal e veja se ele aceita o seu alistamento.", progress = "O que você quer, garoto? Se não veio se alistar, não quero nem saber.", completion = "Ah, quer dizer que Takrin enviou você? Ele é um excelente batedor. Não sei o que eu faria sem ele.\n\nVou assinar sua carta de recrutamento, mas precisamos mesmo é de alguém na Encruzilhada.", rewards = "Você receberá:\n\n"},
[841] = {title = "Outra fonte de energia?", objective = "Leve 10 Bolsas de Água do Errante para o Engenheiro-chefe Silvadágua em Geringontzan em troca de outra Fonte de Energia.", description = "Deixe-me adivinhar... tu não conseguiu concluir a tempo seja lá qual fosse sua terrível façanha capaz de acabar com o mundo. Não espera, melhor ainda... você a deixou cair na areia! Bem, seja qual for o motivo, tenho fontes de energia extras que vou vender pra você. Mas desta vão te custar dez, SIM, DEZ bolsas de água do errante!\n\nSabe como é, os tempos são difíceis, <name>, e as fontes de energia não crescem em árvores! Hum, mas e se crescessem. Talvez pudéssemos FAZER uma árvore de fonte de energia...Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nModelo 4711-KTC Fonte de Energia"},
[842] = {title = "Recrutamento na Encruzilhada", objective = "Siga a estrada a oeste a partir do Posto Remoto de Kargal.\n\nNa interseção em T, vire à esquerda e siga a estrada para o sul. \n\nEncontre Sergra Espinhonegro no cruzamento de estradas da Encruzilhada.", description = "Leve esta carta de recrutamento assinada para a Encruzilhada.\n\nSergra Espinhonegro, maldita seja, está no comando por lá. Ela é uma xamã terrível que vai deixá-l<o/a> zonz<o/a> de tanto falar antes de permitir que faça qualquer coisa útil. Thrall está convencido de que ela é especial, mas eu não tenho tanta certeza.\n\nVocê terá de chegar a uma conclusão sozinh<o/a>, filhote...", progress = "Ora, ora, ora, o que temos aqui? Kargal me considerou merecedor de outro recruta?", completion = "Está bem, &lt;name&gt;. Você quer ganhar seu sustento com a Horda? Bom, há muito o que fazer por aqui, então ouça atentamente e faça o que lhe for pedido.\n\n Posso notar esse brilho em seus olhos. Não pense que tolerarei qualquer insolência. O próprio Thrall declarou que as mulheres da Horda estão em pé de igualdade com vocês, homens. Me desrespeite minimamente e saberá o que é sentir dor de verdade.", rewards = "Você receberá:\n\n"},
[843] = {title = "Reivindicação de Gann", objective = "Gann Agulha de Pedra quer que você mate 15 Escavadores de Bael'Dun e 5 Capatazes de Bael'dun.\n\nLeve o Diário de Khazgorm para Gann Agulha de Pedra.", description = "Em meio à solidão, vagueio pelas estradas.\n\nOs anões de Bael Modan não mostram nenhum respeito pela minha terra. Explosões horrendas fazem buracos no chão enquanto máquinas barulhentas rasgam as colinas. Terras que já serviram como lar e provedora para minha tribo agora estão repletas de anões destrutivos.\n\nTentativas de alcançar uma resolução diplomática falharam. Agora chegou a hora de uma ação decisiva.\n\nRemova os anões do Sítio Arqueológico Bael Modan à força e traga-me o diário do líder deles, o Prospector Khazgorm.", progress = "Muitos taurens inocentes perderam suas vidas ou foram forçados a deixar seu lar ancestral quando os anões de Bael Modan chegaram. Minha terra precisa ser recuperada!", completion = "Khazgorm encontrou o destino que merecia. Não sinto nenhum remorso pelo tolo. Ele era um infiel nessas terras.\n\nQue a morte dele sirva como um aviso a todos aqueles que ameaçam perturbar nosso modo de vida.\n\nAgora permita-me avaliar o diário do anão por um instante...", rewards = "Você receberá:\n\n"},
[844] = {title = "A ameaça pinote", objective = "Colete 7 Bicos de Pinote e entregue-os a Sergra Espinhonegro na Encruzilhada.", description = "Sua primeira presa será fácil.\n\nOs pinotes a leste têm vindo atrás de nosso estoque de comida e se tornaram um verdadeiro incômodo.\n\nAcabe com os pinotes e me traga os bicos deles.", progress = "Trouxe os bicos de pinote?", completion = "Muito bem, &lt;name&gt;. Com o fim dos pinotes, será mais fácil manter as nossas redes de suprimentos intactas. E espero que você tenha aprendido alguns truques lá nos Sertões, pois sua próxima presa tem um pouco mais de fogo no sangue do que os pinotes...", rewards = "Você receberá:\n\n"},
[845] = {title = "As zevras", objective = "Mate Zevras Corredoras e leve 4 Cascos de Zevra para Sergra Espinhonegro na Encruzilhada.", description = "As zevras, embora não sejam as criaturas mais ferozes dos Sertões, são um pouco mais perigosas do que os pinotes. Não se preocupe... mandaremos você ir atrás de presas maiores no momento certo, mas, por enquanto, você precisa de um pouco mais de experiência.\n\nCace as zevras ao norte e ao sul, e colete seus cascos. Traga-os para mim e quem sabe em seguida não mandamos você atrás de uma presa mais difícil.", progress = "Quantas zevras você matou?", completion = "Nada mau, &lt;name&gt;. Essas zevras têm pernas bem fortes. O coice delas derruba até mesmo taurens!\n\nParece que você também é bastante forte. Pronto para um verdadeiro desafio?", rewards = "Você receberá:\n\n"},
[846] = {title = "A vingança de Gann", objective = "Gann Agulha de Pedra quer que você leve para ele 6 ampolas de Nitroglicerina, 6 feixes de Polpa de Madeira e 6 amostras de Nitrato de Sódio.", description = "Está claro que o prospector dependia de explosivos e daqueles veículos barulhentos para rasgar o solo. Podemos esperar que um novo encarregado assuma em breve.\n\nQuero dar aos anões uma prova do próprio remédio. De acordo com esses planos, se combinarmos nitroglicerina com polpa de madeira e nitrato de sódio, podemos criar uma carga explosiva. Os suprimentos podem ser encontrados com o carabineiro, os soldados e os oficiais da bastilha.\n\nArranje esses itens e traga-os para mim. Vou criar um explosivo que os anões não vão esquecer!", progress = "Vejo que você está de volta, <name>.\n\nEmbora a morte de Khazgorm possa adiar a escavação em minhas terras ancestrais, de forma alguma representará o fim da destruição.\n\nA bastilha em Bael Modan é uma ameaça militar crescente. Construída para defender o local da escavação, agora também está pronta para funcionar como força ofensiva.\n\nSe você tiver os ingredientes que pedi, posso criar um explosivo que fará os anões apressados reconsiderarem as próprias ações aqui nos Sertões.", completion = "Vejo que você é bastante habilidoso, &lt;class&gt;.\n\nSem dúvida, em breve teremos a surpresa que esses anões merecem.", rewards = "Você receberá:\n\n"},
[847] = {title = "A astúcia dos raptores", objective = "Fale com Tor'gan.", description = "Esse... esse poder... É esse o poder dos xamãs que Tor'gan tentou me mostrar? Devo dizer que é interessante. Diga a Tor'gan que ele me convenceu... por enquanto. Vou ouvir o que ele tem a dizer.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nChamado do Raptor"},
[848] = {title = "Esporos de Fungos", objective = "Leve 4 Esporos de Fungos ao Boticário Hermógenes na Encruzilhada.", description = "Os oásis dos Sertões ocultam um mistério. Emana de suas águas uma energia vital que revigora as plantas e feras que a sorvem.\n\nRevigora e altera.\n\nHá um cogumelo que cresce perto dos oásis. Seus esporos contêm propriedades que são úteis a nós, os boticários de Lordaeron.\n\nTraga-me os esporos e terá nossa gratidão.", progress = "Você trouxe os esporos, <name>? Preciso mandar um preparado para meu associado no Penhasco do Trovão que requer os esporos...", completion = "Ah, sim. Estes espécimes são ótimos. Potentes.\n\nSou Renegado, e honramos os nossos contratos. Aqui está sua recompensa, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[849] = {title = "A vingança de Gann", objective = "Destrua a máquina voadora em Bael Modan e fale novamente com Gann Agulha de Pedra.", description = "Seguindo o projeto de Khazgorm, não deve ser muito difícil criar uma carga explosiva.\n\nAh, pronto.\n\nAgora, <name>, vou lhe confiar uma tarefa especial. Pegue este bastão explosivo e use-o para explodir a máquina voadora em Bael Modan.\n\nAlém de ser parte fundamental da infraestrutura do lugar, a máquina voadora serve de arma de guerra. E, mais importante que isso, é um símbolo de poder e um insulto à memória da minha tribo.\n\nMe deixe orgulhoso cumprindo essa tarefa heroica e você será recompensad<o/a>.", progress = nil, completion = "Muito bem, &lt;name&gt;! Que você seja reverenciado no Penhasco do Trovão como defensor das terras sagradas dos taurens e campeão da tribo perdida.", rewards = "Você receberá:\n\nAnel do Clã Totêmico"},
[850] = {title = "Os líderes Kolkar", objective = "Leve a Cabeça do Barak para Regthar Portões-da-morte a oeste da Encruzilhada.", description = "Os centauros já incomodam os taurens há anos. E agora os centauros Kolkar dos Sertões se tornaram uma verdadeira ameaça. Antes eram desorganizados, mas surgiram novos líderes entre eles que os uniram.\n\nPara preservarmos nossos assentamentos, é preciso aniquilar os líderes dos centauros.\n\nTraga-me a cabeça de Barak Findekodo. Ele se encontra num acampamento perto dos Charcos Esquecidos, ao norte.\n\nRetorne depois de derrotá-lo.", progress = "Olá, <name>. Trouxe a cabeça do Barak para mim?", completion = "Muito bem, &lt;name&gt;. Os centauros são criaturas baixas e brutais, mas alguns têm inteligência e visão para liderar. E esses são os mais perigosos.\n\nBarak era esse tipo de centauro. Que bom que ele está morto.", rewards = "Você receberá:\n\n"},
[851] = {title = "Verog, o Dervixe", objective = "Leve a Cabeça do Verog para Regthar Portões-da-morte a oeste da Encruzilhada.", description = "O centauro Verog, o Dervixe, vagueia pelos Sertões – não vai ser fácil de achar. Mas ele está lotado na tenda de comando centáurea no Oásis Estagnado, a sudeste daqui. Talvez você consiga atrair ele.\n\nVá ao Oásis Estagnado, a sudeste, e ataque os centauros perto da tenda de comando. Vai ser perigoso, mas, se você conseguir matar centauros suficientes, eles vão ficar alarmados. Aí, o Verog dar as caras.\n\nTraga a cabeça dele para eu colocar junto da cabeça de Barak Findekodo.", progress = "Encontrou Verog, <name>?", completion = "Muito bom. Você deve ter deixado aqueles centauros loucos. Nossos guardas notaram uma movimentação perto do Oásis Estagnado, que provavelmente era você.\n\nPode sentir orgulho, &lt;name&gt;. Você demonstrou muita bravura.", rewards = "Você receberá:\n\n"},
[852] = {title = "Hezrul Marca de Sangue", objective = "Leve a Cabeça do Hezrul para Regthar Portões-da-morte a oeste da Encruzilhada.", description = "Hezrul Marca de Sangue é o líder dos centauros Kolkar nos Sertões. Ele é feroz, brutal e sagaz. Derrotá-lo desarticularia os Kolkar, reduzindo muito a ameaça que eles representam para nós.\n\nAssim, mate-o. Como no caso de Barak e Verog, traga-me a cabeça do Hezrul.\n\nEle comanda o povo dele a partir do Oásis das Águas Claras, ao sul.", progress = "Hezrul foi derrotado, <name>?", completion = "Louvado seja, &lt;name&gt;. Os centauros são indisciplinados e não conseguem concentrar sua fúria como os orcs, mas são seres ferozes, não posso negar. Ao derrotá-los, você provou sua bravura.", rewards = "Você receberá:\n\nAnel do Caçador de Recompensas"},
[853] = {title = "Boticária Zaqueia", objective = "Leve os Esporos Entregues para a Boticária Zaqueia no Penhasco do Trovão antes que o tempo se esgote.", description = "Transformei os esporos que você me deu em uma emulsão. Agora preciso enviá-la a minha colaboradora, a Boticária Zaqueia. Se você levasse para ela, ela poderia lhe oferecer algo de nosso estoque de produtos alquímicos.\n\nEla está nos Poços das Visões, uma caverna abaixo de onde vivem os xamãs do Penhasco do Trovão. A trilha para a caverna fica bem escondida no promontório do Platô dos Espíritos, mas você deve conseguir encontrar.\n\nApresse-se. Essa emulsão só conservará a potência por pouco tempo, e precisa ser selada e processada logo.", progress = "Trouxe alguma coisa do Boticário Hermógenes?", completion = "Ah, os esporos dos Sertões, que o Hermógenes estava coletando. Ele conseguiu que você ajudasse na entrega, é? Que bom.\n\nE a emulsão ainda está potente. Quer dizer que foi encomenda expressa! Muito bem, &lt;class&gt;.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nElixir de Fortitude MenorElixir de Agilidade MenorPoção Menor de RejuvenescimentoPoção de Rapidez\n\nVocê também receberá:\n\nMexedor de Caldeirão"},
[854] = {title = "Jornada para a Encruzilhada", objective = "Fale com Thork na Encruzilhada, nos Sertões.", description = "Previ sua chegada, jovem <class>. Embora você talvez não seja mais tão jovem assim...\n\nAcho que está na hora de você ir mais fundo nessa terra. Eu consigo sentir que você está destinad<o/a> à grandeza.\n\nÉ a Encruzilhada que você deve buscar, pois nossas vidas e as dos orcs se entrelaçaram, e eles precisam de ajuda. Continue pela estrada do leste e siga o rumo norte até a Encruzilhada. Fale com Thork, dentro dos muros. A bênção da Mãe Terra está com ele.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[855] = {title = "Braçadeiras de centauro", objective = "Leve 15 Braçadeiras de Centauro para Regthar Portões-da-morte a oeste da Encruzilhada.", description = "Os centauros Kolkar são uma ameaça ao domínio da Horda sobre os Sertões, e precisamos reduzir o contingente deles. Assim, eu lhe proponho uma tarefa a ser muito bem recompensada.\n\nEnquanto estiver em território Kolkar, mate os guerreiros deles e colete seus braceletes. Volte com um monte deles e a Horda <o/a> recompensará.", progress = "Como vai a caçada, <name>?", completion = "Muito bem. Tenho certeza de que você instilou o medo no coração dos centauros que restam, &lt;name&gt;. Se tiverem juízo, eles se aquietarão e interromperão o avanço.\n\nMas não acho que eles tenham juízo nenhum, e isso é mesmo uma pena...\n\n... para eles.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nArco de Batalha ÓrquicoMachado PontudoMartelo de Petralenha\n\nVocê também receberá:\n\n"},
[857] = {title = "A Lágrima das Luas", objective = "Feegly, o Exilado, quer que você recupere a Lágrima das Luas para ele.", description = "O poder era meu! Estava nas minhas mãos! E eu o quero de volta! Eu disse que eu quero!\n\nAqueles escavadores imundos a descobriram... e era tão linda, ela. Chamaram de Lágrima das Luas. Foi a primeira grande descoberta em Bael Modan.\n\nEla para ela ter sido minha! Eu disse minha!\n\nO General Duas-tranças teve ciúme do poder que ela me deu. Ele a trancou na Bastilha. Disse que ela estava amaldiçoada! O ignorante!\n\nOutros a queriam, e precisavam ser detidos! Aquele desgraçado do Duas-tranças me expulsou!\n\nAgora... Eu a quero de volta! Eu quero! Eu já disse!", progress = "Onde está a Lágrima das Luas? Preciso dela imediatamente! Eu disse imediatamente!", completion = "Até que enfim! O poder é meu outra vez! Eu disse meu!", rewards = "Você receberá:\n\n"},
[858] = {title = "Ignição", objective = "Consiga a Chave de Ignição para Manivela.", description = "Foi o Cobogó que te enviou? Tô numa enrascada. Pulei dentro sem nem pensar que ia precisar de uma chave para destravar a alavanca de movimentação do retalhador. Um dos pilotos me perguntou se estava tudo bem e eu entrei em pânico! Em vez de dizer que tava sem a chave, disse que tinha rolado um problema mecânico.\n\nPrecisamos vazar daqui, e rápido! Sobe lá na sala de controle na parte de cima do guindaste. Deve ter uma chave do Supervisor Rancatraca guardada lá.\n\nMe tira dessa!", progress = "Rápido. A gente tem que dar o fora antes que alguém suspeite!", completion = "Aham! acho que é essa chave aí mesmo. Deixa só eu ver se sei brincar com esta coisinha...\n\nHum... controle de rotação... Aqui eu regulo velocidade e estabilidade... o controle de movimento dos braços é legal. Olha só pra isso! Não acredito que a Empreendimentos S.A. conseguiu projetar uma máquina tão melhor que a nossa. Tenho que levar essa coisa pra Vila Catraca!", rewards = "Você receberá:\n\n"},
[860] = {title = "Sergra Espinhonegro", objective = "Fale com Sergra Espinhonegro na Encruzilhada.", description = "Se você quer seguir o caminho da caça, sua jornada leva aos Sertões. As feras de lá são ferozes e voluntariosas. Você aprenderá muito caçando por lá, e seu espírito se desenvolverá.\n\nFale com Sergra Espinhonegro. Ela será sua primeira guia nos Sertões.\n\nSergra está morando na Encruzilhada. Para chegar a ela, siga para o leste a partir da Aldeia Casco Sangrento, continue pelos Sertões e pegue a estrada do norte na bifurcação. E viaje com cuidado. Os Sertões são muito perigosos para os despreparados ou incautos.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[861] = {title = "A senda do caçador", objective = "Leve 4 Garras de Predador das Estepes para Melor Casco de Pedra no Penhasco do Trovão.", description = "Vejo que você está <ansioso/ansiosa> para explorar. Eu também já tive esse desejo urgente de vagar...\n\nVagar e caçar. Pois caçar é a maior honra de um tauren.\n\nSe você realmente quer seguir a senda do caçador, Melor Casco de Pedra pode guiá-l<o/a>. Ele está no Penhasco do Trovão, no Platô do Caçador.\n\nMostre sua perícia e determinação. Leve as garras dos predadores das estepes de Mulgore para ele. Eles são vigorosos e astutos — a presa certa para <um/uma> jovem <class> na senda do caçador.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[862] = {title = "Ensopado de ratoupeira", objective = "Leve 8 Ratoupeiras para Grude a leste da Encruzilhada.", description = "<Um/Uma> <class> como você deve ficar com muita fome aqui nos Sertões. Eu? Eu estou morrendo de faminto aqui o dia todo.\n\nQuer saber? Eu arrumo um ensopado do bom. Só preciso de umas ratoupeiras.\n\nParece que aqueles anões nojentos em Bael Modan estão com uma praga que é um banquete. Agora vá até Bael Modan, no sul, e me traga umas boas ratoupeiras.\n\nVocê não pode se intitular aventureir$o:a; experiente se não tiver passado um tempo matando rato! Há!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nReceita: Ensopado de RatoupeiraLuvas de BoticárioEnsopado de Ratoupeira"},
[863] = {title = "A fuga", objective = "Escolte o Manivela e o retalhador goblínico roubado até Cobogó, na Vila Catraca.", description = "Acho que vou ter que aprender no caminho... Não deve ser muito difícil. É só apertar uns botõezinhos, mexer na alavanca... E aí, tá <pronto/pronta> pra partir?", progress = nil, completion = "Espero que ainda dê pra aproveitar alguma peça de valor do protótipo... Meu prazo está acabando! Vou despachar pra ontem uma equipe de recuperação pra onde o Manivela está.\n\nAh, e deixa eu dar isso aqui a você pelo trabalho, inclusive.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBastão ÍgneoCalças Graxentas do Faz-tudo\n\nVocê também receberá:\n\n"},
[864] = {title = "Fale novamente com a Boticária Zilda", objective = "Leve o Kit de Teste de Campo Lacrado para a Boticária Zilda na Cidade Baixa.", description = "\"Pronto. A fonte de energia foi removida com segurança e os dados estão intactos. Bem... Boa viagem e coisa e tal. Tomara que o experimento nefasto tenha funcionado pra você e tudo mais.\"\n\nO goblin sai para cuidar dos assuntos da Companhia de Saneamento de Geringontzan. Você guarda o kit de teste selado para a viagem de volta à Cidade Baixa. A boticária Zilda está esperando você e o kit de teste de campo.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nLuvas do Manejo HábilCapa do Mestre BoticárioOmoplatas Dermossapiens\n\nVocê também receberá:\n\n"},
[865] = {title = "Chifres de raptor", objective = "Obtenha 5 Chifres Intactos de Raptor de Garrafoices Helióscamos e leve-os para Cáliper Porcatraca na Vila Catraca.", description = "Os raptores dos Sertões são mais espertos que os de outros lugares. Acho que essa esperteza deles tá nos chifres! Se eu estiver certo, dá pra moer os chifres deles e usar o pó pra fazer \"drinques espertos\". Vou fazer fortuna, <meu caro/minha cara>!\n\nE você pode quebrar o meu galho. Desenrola aí pra mim uns chifres de raptor intactos. Dos garrafoices helióscamos é que é a boa. Eles caçam no sul dos Sertões, e no norte também, perto da Floresta do Vale Gris.", progress = "Conseguiu os chifres, <name>? Esses chifres vão me render uma fortuna!", completion = "Ótimo! Você conseguiu! Agora eu só preciso moer, bater com vinho e...", rewards = "Você receberá:\n\nPonche de RaptorManto de Taberneiro"},
[866] = {title = "Amostras de raiz", objective = "Leve 8 Amostras de Raiz para Cáliper Porcatraca na Vila Catraca.", description = "As plantas dos Sertões não são como as plantas de outros lugares. As propriedades de ambas são praticamente as mesmas, mas a água do oásis altera as plantas. Elas ficam com uma leve diferença.\n\nQuero estudar as diferenças... pra ver se são lucrativas!\n\nSe tu tiver as manhas no herborismo, podia pegar umas amostras de raiz aí nos Sertões pra mim. Me traz as amostras, que eu vou comparar elas com plantas de outras regiões.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nTúnica Coberta de Esporos"},
[867] = {title = "As harpias bandoleiras", objective = "Obtenha 8 Garras de Asabruxa.\n\nLeve-as para Darsok Punhálacre na Encruzilhada.", description = "Essas harpias dos infernos já saquearam as nossas caravanas por tempo demais. Está na hora de cortar algumas gargantas e controlar essa praga.\n\nEu tenho rondado os ninhos delas e descoberto quem manda em quem. Precisamos de um ataque que atinja grande parte da cadeia de comando inferior e, com isso, poderemos galgar até o topo.\n\nVamos começar por baixo. Aniquile as harpias Asabruxa e as Asabruxas plumerrantes no noroeste e traga-me 8 garras delas. Isso já deve ser um bom começo...", progress = "Já cortou o suficiente? Continue cortando e catando. Eu quero 8 gadanhos de Asabruxa.", completion = "Ah, essas são das boas. Muito boas... Bom trabalho, &lt;name&gt;. Espero ver mais de seu trabalho no futuro.", rewards = "Você receberá:\n\n"},
[868] = {title = "Ovo tipo extra", objective = "Obtenha 12 Ovos de Silitídeo e devolva a Garra de Escavação para Korran na Encruzilhada.", description = "Ei, <name>. Me mandaram para a Encruzilhada para vigiar a região e registrar eventos importantes para meus mestres em Orgrimmar.\n\nUm dos objetos de meus estudos são as criaturas insetoides encontradas ao sul, no Campo dos Gigantes. Sabemos pouco sobre as criaturas, e eu faço questão de descobrir mais. Elas parecem ter inteligência própria, mais que qualquer animal normal.\n\nLeve essa garra de escavação e colete alguns ovos das criaturas nos montículos delas, mas cuidado com os ninhos. Se alertadas, elas atacarão.", progress = "Cuidado com os ovos que encontrar, <name>. Se quebrarem, não me servem de nada.", completion = "Você me deixa deveras feliz, &lt;name&gt;. Os ovos e a ferramenta. Muito bem.\n\nE a sua recompensa...", rewards = "Você vai poder escolher uma dessas recompensas:\n\nVestes do ArlequimArmadura de Escama Violeta\n\nVocê também receberá:\n\n"},
[869] = {title = "Raptores ladrões", objective = "Leve 12 Cabeças de Raptor para Gazrog na Encruzilhada.", description = "Não faz muito tempo, um carregamento de prata, que se destinava à folha de pagamento dos guardas da Encruzilhada, foi roubado da nossa torre de guarda. Nós queremos aquela prata de volta.\n\nO estranho é que pegamos um dos ladrões na noite do roubo e... era um raptor! Inacreditável!\n\nNão faço ideia do propósito que a prata teria para os raptores. Mas não importa... Quero eles mortos para que não voltem a roubar de nós!\n\nCace os raptores nos Sertões. Traga-me as cabeças deles!", progress = "Trouxe as cabeças, <name>?", completion = "Você se livrou dos raptores!\n\nObrigado, &lt;name&gt;. Você é um &lt;class&gt; de valor.", rewards = "Você receberá:\n\n"},
[870] = {title = "Os Charcos Esquecidos", objective = "Informe Tonga Runa Totem sobre as suas descobertas.", description = "Tempos atrás, os Sertões eram um lugar exuberante, a vida resplandecia. Mas a guerra e cataclismos devastaram a região, deixando apenas restos secos e calcinados. Quando isso aconteceu, meu coração se partiu.\n\nNos últimos anos, no entanto, novos oásis se formaram e a vida voltou aos Sertões. Nós druidas sentimos um poder afluir das entranhas da terra rumo à superfície.\n\nTemos que achar a origem dele. Vá até os Charcos Esquecidos, a noroeste da Encruzilhada. Procure a fonte desse poder nas águas e venha falar comigo de novo.", progress = nil, completion = "Você disse que viu uma fissura na terra, com gases borbulhando dela? Estranho. Talvez seja a resposta que procuramos, mas não temos como ter certeza.\n\nÉ uma pista. Obrigado pela ajuda.", rewards = "Você receberá:\n\n"},
[871] = {title = "Cortando o ataque pela raiz", objective = "Da tribo dos Crinavalha, mate 8 Rabdomantes, 8 Tecespinhos e 3 Caçadores, depois fale novamente com Thork na Encruzilhada.", description = "Todos os javatuscos são inimigos, <name>. Mas alguns se tornam uma verdadeira pedra no sapato.\n\nA tribo dos Crinavalha tem atacado nossas linhas de suprimentos vindas de Durotar, é uma dor de cabeça sem fim. Os batedores saíram à caça do líder dos raides, mas, enquanto não o encontramos, qualquer dano infligido aos porcos imundos nos ajudaria.\n\nComece pelo nordeste, na direção de Durotar. Os covis deles são muito fáceis de achar, basta procurar as vinhas espinhentas que brotam da terra. Encontre-os e mate-os.", progress = nil, completion = "Você agiu bem, &lt;name&gt;. Aqueles javatuscos insolentes aprenderão, enfim, que a força da Horda não deve ser ignorada.", rewards = "Você receberá:\n\n"},
[872] = {title = "Adeus aos ataques", objective = "Mate 8 Geomantes Crinavalha, 8 Defensores Crinavalha e Kreenig Rosnento.\n\nDepois leve a Presa de Kreenig Rosnento para Thork, na Encruzilhada.", description = "Um dos meus batedores testemunhou um ataque contra uma caravana de Durotar, <name>. Os responsáveis são da tribo de javatuscos Crinavalha. O javatusco que lidera os raides se chama Kreenig Rosnento. Ele foi visto a nordeste daqui, logo ao sul da estrada para Durotar. Só a morte de Kreenig vai dar fim a esta ameaça.\n\nPorém, para dar uma lição bem dada e acabar de vez com os ataques, é preciso ir além. Mate Kreenig e outros integrantes da tribo, depois me traga a presa dele.", progress = "Corte a cabeça do líder e o caos reinará, <name>. Aprenda esta lição. Pode lhe ser útil no futuro.\n\nOs javalis vão ficar perdidos, sem saber o que fazer, e nós conseguiremos retomar os Sertões.", completion = "Muito bem, &lt;name&gt;. Com a morte de Kreenig, os ataques a caravanas vão diminuir bastante.\n\nSeu povo deve se orgulhar de você.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCinturão do VínculoCinto Cingido\n\nVocê também receberá:\n\n"},
[873] = {title = "Isha Awak", objective = "Leve o Coração de Isha Awak para Mahren Vidente do Céu.", description = "Estas águas são o domínio do grande Isha Awak. Grandiosa é sua força, e solene, seu orgulho. Os humanos da costa o temem, pois muitos foram suas vítimas.\n\nEu, porém, não o temo. Sou grata pela sua presença. Ele é um desafio digno e uma presa ainda mais honrosa.\n\nCaso sinta-se <preparado/preparada>, mergulhe de cabeça e cace Isha Awak, a Ruína Profunda.\n\nO espírito dele reside no coração, cuja batida sela o destino de quem a ouve.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nBastão de MarcarProtetor do Vale\n\nO seguinte feitiço será lançado em você:\n\nForça de Isha Awak\n\nVocê também receberá:\n\nCinturão do Senhor das Feras"},
[874] = {title = "Mahren Vidente do Céu", objective = "Fale com Mahren Vidente do Céu.", description = "Você já caçou as feras dos Sertões. Seu espírito é forte. Mas <um/uma> caçado<r/ra> de verdade tem que estar sempre preparad<o/a>. <Um/Uma> caçado<r/ra> de verdade persegue a presa por qualquer caminho. Montanhas e pântanos não são páreo para sua determinação.\n\nNem mesmo o oceano.\n\nEncontre a minha irmã, Mahren. Ela vive no litoral, caçando as feras da água. Ela ensinará a você os segredos delas.\n\nMas cuidado quando for procurar Mahreen, pois a costa dos Sertões é território dos humanos.", progress = nil, completion = "Foi meu irmão que mandou você, não é mesmo? Então você deve ter provado seu potencial para ele. Quem tem o respeito de Jorn também merece o meu.\n\nEstá pronto para avançar no caminho do caçador?", rewards = "Você receberá:\n\n"},
[875] = {title = "Tenentes harpias", objective = "Obtenha 6 Anéis de Tenente Harpia de Asabruxas Matadoras e leve-os para Darsok Punhálacre na Encruzilhada.", description = "Hahaha, <name>. Já voltou? Ótimo, gosto desse entusiasmo.\n\nDesta vez, quero que você mate umas tenentes harpias nos Montes Áridos. Apenas Asabruxas Matadoras podem virar tenentes, e dá para identificá-las pelo anel que elas usam. É um bando cruel, mas que lidera alguns subalternos nessa área. Eliminar uma meia dúzia delas será um golpe e tanto contra esse matriarcado.\n\nFaça com que sofram bastante antes de morrer, <name>. Vamos mostrar para essas megeras o erro que cometeram quando compraram briga com a Horda.", progress = "Você já conseguiu seis anéis de tenente harpia? Elas precisam pagar pelos ataques atrozes à Horda.", completion = "Excelente trabalho, colega. Você tem futuro na Horda.", rewards = "Você receberá:\n\n"},
[876] = {title = "Serena Plumassangue", objective = "Mate Serena Plumassangue e leve a cabeça dela para Darsok Punhálacre na Encruzilhada.", description = "Você se saiu muitíssimo bem contra tenentes e subalternos, por isso, quero que faça uma última coisa por mim.\n\nSerena Plumassangue é irmã de uma harpia chamada Plumassangue, que foi morta por Rexxar algum tempo atrás. Parece que os ataques às caravanas da Horda são uma vingança pela morte da irmã.\n\nQuero que você corte a garganta me traga a cabeça de Serena. Vou mandar exibi-la na próxima caravana que sair... e fazer essas harpias pensarem duas vezes.", progress = "Enquanto eu não vir a cabeça da Serena, você não receberá nada.", completion = "Ha! Muito bem, &lt;name&gt;! Muito bem mesmo... Não tinha certeza se você estava à altura da empreitada, mas acabou se revelando um excelente carrasco. Obrigado mais uma vez por ter nos ajudado a sanar a epidemia de harpias. Tome sua recompensa, e faça bom proveito.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nEspada Curta EleganteEsfolador de HarpiaMachado de Zhovur\n\nVocê também receberá:\n\n"},
[877] = {title = "O Oásis Estagnado", objective = "Fale novamente com Tonga na Encruzilhada depois de investigar o Oásis Estagnado.", description = "Outros oásis dos Sertões podem ter fissuras como a que você viu nos Charcos Esquecidos. Se for assim, talvez as fissuras sejam a fonte da vida dos oásis.\n\nTemos que fazer o teste.\n\nAqui, tome estas sementes. Elas estão mortas e inférteis, mas vá com elas para o Oásis Estagnado, a sudeste daqui. Se encontrar uma fissura lá, coloque as sementes no interior dela e... veja o que acontece.", progress = nil, completion = "Suas descobertas são incríveis! As sementes que lhe dei estavam secas, já mortas. O que está abaixo do oásis, seja lá o que for, pode criar vida do nada!\n\nTemos que continuar investigando...", rewards = "Você receberá:\n\n"},
[878] = {title = "Tribos em guerra", objective = "Mate 6 Rabdomantes Costagulha, 12 Tecespinhos Costagulha e 12 Geomantes Costagulha e fale novamente com Denterroto no Acampamento Taurajo, nos Sertões.", description = "Quero que você ataque meus inimigos. <bufa> Para ajudar Denterroto e os Crinavalhas, você precisa enfraquecer os Costagulhas. As duas tribos são muito espiritualizadas, ligadas à terra e seu poder. <bufa> Isso nos dá força para tudo... especialmente para a guerra.\n\nSe quiser descobrir quem está causando tanta angústia a Thork <bufa> e à Horda, ajude Denterroto! Ataque os líderes espirituais dos Costagulhas ao norte. Enfraqueça eles para que os Crinavalhas possam acabar com eles e governar os Sertões.", progress = nil, completion = "Isso, isso... alegria define, porque finalmente &lt;bufa&gt; os Costagulhas conhecem a dor da minha tribo, &lt;race&gt;. Ou é como eu deveria estar me sentindo, só que  não é bem assim.\n\nVocê ajuda o seu povo e eles passam a te respeitar. Denterroto ajuda o povo dele, e o que acontece? Eles viram as costas. Eles esquecem e deixam ele apodrecendo em uma gaiola!\n\nEu ajudo eles mesmo estando atrás das grades e eles não mandam ninguém pra me ajudar!", rewards = "Você receberá:\n\n"},
[879] = {title = "Traição fatal", objective = "Mate Nak, Kâz e Lok Mataorc e leve o crânio deles para Denterroto no Acampamento Taurajo, que fica nos Sertões.", description = "Três javatuscos da minha tribo saíram do Urzal para liderar raides contra a Horda, <race>. <bufa> Impiedosos e ardilosos, mas se você acabar com eles, a Encruzilhada e boa parte dos Sertões terá paz... <bufa> pelo menos quanto à tribo Crinavalha. <bufa>\n\nVá atrás de Nak, Kâz e Lok Mataorc. Eles ficam no extremo sul dos Sertões. Um é lançador de feitiços, outro é um rastreador e o líder, o Mataorc, <bufa> um guerreiro. Acabe com eles, <race>, assim como eles acabaram comigo.", progress = "Como vai a caçada, morto-vivo? <bufa>\n\nThork pensa que sabe quem está liderando os raides contra seu povo, <bufa> mas ele vai ver como está enganado se você tiver êxito.\n\nMinha morte é inevitável, mas você causou muito sofrimento aos meus inimigos. Por isso, Denterroto não tem arrependimentos. <bufa>", completion = "\"Então está feito. Foi melhor assim\", diz Denterroto. \"&lt;bufa&gt; Eles erraram &lt;bufa&gt; em ter me deixado viver quando me traíram &lt;bufa&gt; e me largaram preso aqui, nessa cela abandonada.\"\n\n\"Só peço que você termine mais uma coisinha para mim, morto-vivo.\"", rewards = "Você receberá:\n\n"},
[880] = {title = "Seres alterados", objective = "Leve 8 Cascos Alterados de Mordelisca para Tonga Runa Totem, na Encruzilhada.", description = "Suas descobertas são incríveis, <name>. As propriedades desses oásis devem vir de uma fonte externa. Ou talvez interna.\n\nQuero saber como essas fissuras estão afetando as feras que bebem a água dos oásis.\n\nVá ao Oásis das Águas Claras e ao Oásis Estagnado para caçar mordeliscas e traga os cascos para mim. Quero examiná-los.", progress = "Como vai a sua missão? Já conseguiu os cascos?", completion = "Muito obrigado, &lt;name&gt;. Estudar as feras revela muito a respeito da região em que elas habitam. Vamos ouvir a história que esses cascos têm a nos contar.\n\nLeve consigo a minha gratidão... e talvez essas moedas lhe sejam úteis. Não preciso delas.", rewards = "Você receberá:\n\n"},
[881] = {title = "Echeyaki", objective = "Leve o Pelego de Echeyaki para Sergra Espinhonegro, na Encruzilhada.", description = "Alva Névoa, ou Echeyaki na língua taurahe, é o rei dos felinos da savana. Quando caça, é tão sorrateiro que diz-se ser apenas uma névoa fina e branca sobre o solo. Suas presas morrem tão rápido que nem têm tempo de sentir medo ou dor.\n\nDe acordo com os taurens, ele é ao mesmo tempo misericórdia e morte.\n\nE você vai descobrir por si mesm<o/a> se isso é verdade, pois quero que cace Echeyaki. O covil dele fica a nordeste da Encruzilhada, entre ossos de kodos gigantes.\n\nVá. Quando chegar ao covil, sopre este berrante e ele virá.", progress = "Ouça o chamado do grande felino, <name>.", completion = "Você o derrotou. Embora os dias de caçada de Echeyaki tenham acabado, o espírito dele caminha ao seu lado. Ele mostrará a você a força da sutileza e a honra da misericórdia.\n\nVocê ainda tem um longo caminho pela frente, &lt;class&gt;. Espero que seja benigno.", rewards = "O seguinte feitiço será lançado em você:\n\nGraça de Echeyaki\n\nVocê receberá:\n\n"},
[882] = {title = "Ishamuhale", objective = "Leve a Presa de Ishamuhale para Jorn no Acampamento Taurajo.", description = "Ishamuhale, o Dente-de-lança, é o raptor helióscamo mais feroz dos Sertões. Ele não caça por esporte, nem para comer. Ele caça porque a caça é a paixão dele. Ele mata porque essa é a natureza dele.\n\nE você aprenderá sobre a natureza dele, <name>, pois seu caminho agora segue no encalço de Ishamuhale.\n\nComece a caçada. Mate a presa favorita dele, uma zevra, e leve a carcaça até a árvore morta a noroeste da Vila Catraca. Ishamuhale farejará a carcaça, e ela o atrairá.\n\nEsteja <preparado/preparada> quando ele chegar.", progress = "<name>, você derrotou Ishamuhale?", completion = "Essa presa é só um símbolo, mas o que ele representa é algo profundo.\n\nA força de Ishamuhale está em você agora, &lt;name&gt;. Que você a use com prudência. Esse é o seu fardo.\n\nEssa é sua honra.", rewards = "O seguinte feitiço será lançado em você:\n\nRaiva de Ishamuhale\n\nVocê receberá:\n\nCarcaça Fresca de Zevra"},
[883] = {title = "Lakota'mani", objective = "Leve o Casco de Lakota'mani para Jorn no Acampamento Taurajo.", description = "O Casco de Lakota'mani é um símbolo da força do grande kodo.", progress = "<name>, quando você se aproximou, notei um novo poder em seu passo. Diga-me... seu espírito se encontrou com o grande Lakota'mani?", completion = "Lakota'mani é chamado de \"Treme-terra\" em nossa língua, e seus passos são como um grande trovão. Ele está com você agora, e a coragem dele se mistura com a sua. Juntos, não há conflito que você não possa enfrentar.\n\nCaminhe de cabeça erguida, &lt;name&gt;. De cabeça erguida, Treme-terra.", rewards = "O seguinte feitiço será lançado em você:\n\nTrovão de Lakota'mani\n\nVocê receberá:\n\n"},
[884] = {title = "Owatanka", objective = "Leve a Ponta da Cauda de Owatanka para Jorn no Acampamento Taurajo.", description = "Quando tocado, o espigão da cauda de Owatanka solta faíscas.", progress = "<name>, há um novo poder em seus olhos! Você encontrou o poderoso Owatanka, não foi?", completion = "Os taurens chamam o lagarto trovejante Owatanka de \"Raio Azul\", porque nele há uma rapidez que o tamanho esconde. Dizem que um raio caiu no Raio Azul há muito tempo, e que, alimentada pela ira do céu, a energia da grande fera não tem limite.\n\nQue a energia dele corra em você, &lt;name&gt;. Agora você segue no passo de Owatanka.", rewards = "O seguinte feitiço será lançado em você:\n\nVelocidade de Owatanka\n\nVocê receberá:\n\n"},
[885] = {title = "Anhan Guera", objective = "Leve a Pena de Anhan Guera para Jorn Vidente do Céu, no Acampamento Taurajo.", description = "Ao toque, a pena de Anhan Guera machuca.", progress = "Vejo em você uma determinação renovada, <name>. Uma determinação forjada na dor.\n\nEstá sofrendo com a morte de Anhan Guera?", completion = "O raro falcotrom é uma fera que habita os Sertões, e sua morte pode ser um presságio de grande sorte, ou de grande azar.\n\nMas a morte de Anhan Guera, que se chama \"Mordida Espiritual\" em Taurahe, pode trazer nenhuma das duas... ou ambas.\n\nA dor que você sentiu ao caçar Mordida Espiritual é a dor que todos sentem ao conseguir grandes ganhos através um alto custo. A pontada que você sentiu era a dor do arrependimento.\n\nMas vejo que você já conseguiu superar. Você aprendeu que o arrependimento vira sabedoria, e que a dor leva à força.\n\nVocê aprendeu muito, &lt;name&gt;.", rewards = "O seguinte feitiço será lançado em você:\n\nDeterminação de Anhan Guera\n\nVocê receberá:\n\n"},
[886] = {title = "Oásis nos Sertões", objective = "Fale com Tonga Runa Totem na Encruzilhada.", description = "Os druidas do Penhasco do Trovão sentiram uma força estranha se infiltrando nos Sertões, a leste de Mulgore.\n\nTonga Runa Totem foi enviado para descobrir a fonte dessa força, mas achamos que ele vai precisar de ajuda.\n\nViaje até os Sertões e fale com Tonga. Ele vai estar na Encruzilhada. Para chegar lá, pegue a estrada a leste para sair da vila de Casco Sangrento. Entre nos Sertões, depois vire para o norte quando a estrada se dividir. Continue para o norte e você chegará à Encruzilhada.\n\nAh, e não saia da estrada, <name>, os Sertões são uma terra perigosa.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[887] = {title = "Os Flibusteiros dos Mares do Sul", objective = "Mate 12 Bucaneiros dos Mares do Sul e 6 Canhoneiros dos Mares do Sul para Gasganete, que aguarda na Vila Catraca.", description = "Eu queria é que o Vice-almirante Grezzlik fizesse melhor o trabalho de segurança marítima pras nossas embarcações mercantes. Com o ouro todo que os príncipes mercadores botam nas Frotas Mercantes, me revolta ver tanto pirata por aí, atacando os meus navios como bem entendem e roubando a minha mercadoria!\n\nDisseram que os Flibusteiros dos Mares do Sul resolveram acampar mais pro sul daqui. Eles tão sendo uma pedra no meu sapato, e se o Grezzlik não vai dar jeito, bem... Você pode me ajudar a dar um sumiço neles.", progress = nil, completion = "Trabalho do bom, jovem. Mais um serviço ou outro desse e vai ser uma coisa a menos pra eu me preocupar. Com sorte eles se convencem de que é melhor transferir a operação pra outro lugar.\n\nMas enquanto isso ainda tem aquele detalhe... de pegar de volta o que eles me roubaram!", rewards = "Você receberá:\n\n"},
[888] = {title = "Butim Roubado", objective = "Recupere o Carregamento de Botas e a Lente Telescópica para Gasganete na Vila Catraca.", description = "Eu adoraria recuperar meus bens, mas com certeza não deve ser possível. Mesmo assim, tem algumas coisas de que eu não posso abrir mão!\n\nComo você pode ver, o meu observatório está pronto, mas ainda falta a lente menor. A primeira chegou na caravana de Durotar, mas a segunda vinha de navio e nunca chegou. Minha encomenda de botas com o Drizzlik na Angra do Butim também não!\n\nVai lá no acampamento dos piratas e vê se acha as minhas coisas, pode ser?", progress = "Você encontrou minhas coisas, <name>?", completion = "É um alívio recuperar tudo isso. Valeu a ajuda, &lt;name&gt;. Sabe, um &lt;race&gt; que nem você ia ajudar muito nessa operação aqui. Se um dia você for procurar emprego, lembra de como o Gasganete paga bem!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nLuvas da JornadaBotas Lamelares Acolchoadas\n\nVocê também receberá:\n\n"},
[889] = {title = "Espírito do vento", objective = "Traga 10 estilhaços de sangue para Denterroto e eu abençoarei você com uma velocidade incrível. Você correrá como uma zevra e será a inveja dos ventos.\n\nNão será para sempre, mas deve ajudar você a atravessar os Sertões.", description = nil, progress = "Traga 10 estilhaços de sangue para Denterroto e eu abençoarei você com uma velocidade incrível. Você correrá como uma zevra e será a inveja dos ventos.\n\nNão será para sempre, mas deve ajudar você a atravessar os Sertões.", completion = "Excelente. Vá em companhia do espírito de Agamaggan, morto-vivo. Fale novamente comigo caso deseje mais do poder de Agamaggan.", rewards = "Você irá aprender:\n\nEspírito do Vento"},
[890] = {title = "Carregamento perdido", objective = "Leve o Livro-caixa do Gasganete para o Mestre Portuário Caruncho.", description = "Já faz uma cara que eu tô esperando meu último carregamento de mercadorias da Angra do Butim! Tô achando que os Flibusteiros roubaram, mas, só pra garantir, você pode dar uma chegadinha lá nas docas e perguntar pro Caruncho se as minhas paradas já foram pro armazém e ninguém me avisou?\n\nToma aqui meu livro-caixa. Entrega pro Caruncho e pede pra ele conferir o inventário e comparar com o registro dele.", progress = "Mercadoria pra mandar no próximo navio, <name>?", completion = "Ah, foi o Gasganete que te mandou aqui? Dá um minutinho aí que eu vou conferir os meus registros, pra confirmar se o carregamento dele não foi mandado pra outras bandas por acidente.\n\nAposto que o que ele tá esperando são botas do Drizzlik...", rewards = "Você receberá:\n\n"},
[891] = {title = "Os canhões da Guardanorte", objective = "O Capitão Thalo'thas Solaz de Vila Catraca quer que você obtenha 10 Medalhas de Theramore e mate a Capitão Amazona, o Canhoneiro Ocastro e o Canhoneiro Smythe.", description = "Mais uma navio da frota perdido para os humanos insensatos da Fortaleza da Guardanorte!\n\nTemos que dar um jeito naqueles canalhas. A paranoia cria neles um frenesi. Essa gentalha abjeta se apraz atirando em tudo que vê.\n\nSiga para o Sul, rumo à Fortaleza, pela Costa dos Mercadores e cerceie as forças deles. Traga-me as medalhas dos soldados abatidos como prova. E, pelo amor de minha frota, acabe de uma vez por todas com a Capitão Amazona e seus canhoneiros mentecaptos!", progress = "Minha frota corre perigo, com a Capitão Amazona e seus malditos canhoneiros disparando da Fortaleza da Guardanorte. Eu quero que os homens dela paguem. Quando minhas mãos estiverem cheias de medalhas tomadas de inimigos mortos, eu saberei que meus corsários foram vingados.", completion = "Enfim as armas da Aliança foram silenciadas. Sua ajuda será recompensada, &lt;name&gt;.\n\nAgora, à tarefa lúgubre de recuperar os corpos daqueles que morreram...", rewards = "Você vai poder escolher uma dessas recompensas:\n\nMosquete do CorsárioCulotes de Lobo-do-mar\n\nVocê também receberá:\n\n"},
[892] = {title = "Carregamento perdido", objective = "Leve o Livro-caixa do Gasganete para o Gasganete na Vila Catraca.", description = "Tá tudo certo nos meus registros, <name>. Diz pro Gasganete que ele tá sem sorte. Bem capaz que piratas tenham roubado o material dele, mesmo.\n\nNão posso fazer nada. Ah... Aproveita e diz que eu tô com umas coisas da Inframina pra ele. Aí quando ele tiver um tempinho pra vir buscar...", progress = "Então, o que foi que o Caruncho disse?", completion = "Nada, né? Devem ter sido aqueles Flibusteiros mesmo... O Caruncho é firmeza, aposto que ele não ia furar meu olho. Sem falar que ele tá na folha de pagamento.\n\nBom, parece que eu vou ter que dar um jeito de recuperar a mercadoria. E aí, o que me diz, &lt;name&gt;?", rewards = "Você receberá:\n\n"},
[893] = {title = "Armas preferidas", objective = "Leve um Punhal Crinavalha, uma Varinha de Crinavalha Carbonizada e um Escudo de Guerra Crinavalha ao Trapista Forjaço no Acampamento Taurajo, que fica nos Sertões.", description = "Pelo que eu sei, os javatuscos Crinavalha do sul, pra lá do Campo dos Gigantes, não têm ferreiros de calibre, mas parece que começaram a projetar armas mais robustas. Se for possível, quero pôr as mãos em algumas amostras das armas deles para analisar.\n\nSe você deseja honrar a Horda, então me traga amostras dessas armas. Quero uma adaga dos espreitadores, uma varinha dos clarividentes e um escudo dos belicosos. Isso deve bastar!", progress = "Também tenho lá minhas dúvidas de que um javatusco saiba se virar em uma forja, mas o Chefe Guerreiro ordenou, eu cumpro. Tenho que saber tudo que for possível sobre a tribo Crinavalha.\n\nJá fiz coisas até mais triviais, então não ligo de fazer isso se for pro bem do povo orc.", completion = "Excelente, exemplares incríveis mesmo.\n\nOs javatuscos avançaram mais do que eu imaginava.\n\nTome, &lt;class&gt;, pegue isso e cai fora. Tenho que analisar essas armas com atenção. Talvez a gente tenha subestimado o inimigo, e esse tipo de erro pode sair caro.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nMartelo da DemoliçãoLanterna do Brilho Eterno\n\nVocê também receberá:\n\n"},
[894] = {title = "A rebimboca", objective = "Acesse o Painel de Controle no laboratório da Empreendimentos S.A.\n\nItem fornecido:\n", description = "A Empreendimentos S.A. montou um pequeno laboratório de pesquisa bem longe ao norte daqui, a sudoeste do Charco de Lodo. Não sei muito sobre o que estão fazendo, mas consegui descobrir que são experiências com um troço chamado \"rebimboca\".\n\nQue diabos é uma rebimboca? Não importa. Seja o que for, quero examiná-la, então preciso que alguém vá lá pegá-la.\n\nConsegui uma cópia do manual de operação do sistema de controle deles. Com ele você descobre como desencaixar a rebimboca.", progress = nil, completion = "Um mar de botões, alavancas e luzes piscantes estende-se numa organização um tanto caótica sobre o console de controle. Um pequeno indicador mostra que o aparato está com o desempenho ideal e que as válvulas de controle de um a três estão abertas. Há uma pequena fechadura no canto inferior direito do painel de controle.", rewards = "Você receberá:\n\n"},
[895] = {title = "PROCURA-SE: Barão Longacosta", objective = "Leve a cabeça do Barão Longacosta para Gasganete na Vila Catraca.", description = "PROCURA-SE!\n\nBarão Longacosta, Capitão do Imprudente\n\nO Capitão Longacosta de Guilnéas lidera a frota dos Flibusteiros dos Mares do Sul e é procurado por pirataria. Os restos mortais devem estar em condição identificável!\n\n– Gasganete", progress = "O que há? Posso ajudar? Transporte de mercadorias? Quem sabe um trabalho de engenharia, hein?", completion = "Aaaaah! Esse tipo de encomenda. Sabe, eu vi o Imprudente uma vez em mar aberto. Navio de dar medo, aquele... Ou barco. Sei lá. Marinheiro dá nome diferente pra tudo!\n\nMas enfim, o treco deslizando na água era uma visão e tanto. Eu fico feliz é de saber que o capitão dele não vai mais tretar com navio meu.", rewards = "Você receberá:\n\n"},
[896] = {title = "A fortuna do mineiro", objective = "Consiga a Esmeralda Olho-de-gato de um dos feitores ou seguranças da Empreendimentos S.A. e leve para o Mestre Portuário Caruncho, em Vila Catraca.", description = "Como Mestre de um porto movimentado como o de Vila Catraca, eu vivo de butuca pra saber tudo o que tá rolando. Fico ligado em todas as trocas de itens e dinheiro que acontecem entre a Vila e a Angra do Butim.\n\nA última que fiquei sabendo veio da mina Veio do Pedregulho, da Empreendimentos S.A., que fica a nordeste do Charco de Lodo. Um dos mineiros descobriu uma esmeralda do tamanho de uma mão fechada. Tenho contatos que estão interessados em comprar a pedra e estou disposto a dividir o valor meio a meio.", progress = "Se a gente pelo menos soubesse qual dos mineiros achou a esmeralda, seria mais fácil que tirar doce de criança...", completion = "Olha só o tamanho dessa coisa, &lt;name&gt;! A gente tá rico! Aqui, de acordo com os preços de gemas que eu já vi no mercado de Inframina dá pra saber qual é a sua parte: cinquenta por cento, não se preocupa!\n\nVamos ver... Acho que isso aqui tá bom. Foi um prazer fazer negócios com você, &lt;name&gt;.", rewards = "Você receberá:\n\nSaco de Moedas"},
[897] = {title = "O ceifador", objective = "Leve a Cabeça do Ceifador para Jorn Vidente do Céu.", description = "Essa criatura estranha, insectoide, parece não fazer parte da ordem natural, como se não pertencesse aos Sertões.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[898] = {title = "Ex-detento", objective = "Escolte Gilthares Galhareda em segurança até o Capitão Solaz, que está na Vila Catraca.", description = "Até que enfim alguém veio me salvar!\n\nNão acredito que você passou pelos guardas. Aqueles fanáticos de Theramore piraram. Eles afundaram nosso navio e só eu sobrevivi, mas acabei preso. Fui acusado de traição e considerado uma ameaça à Aliança.\n\nQuem sou eu para ameaçar essa farsa gigantesca que é a Aliança? Fui marujo num navio que transportava pinga entre a Vila Catraca e a Angra do Butim.\n\nChega de conversa. Você vai me ajudar a voltar para a Vila Catraca, não vai? É só dar o sinal e damos o fora daqui.", progress = nil, completion = "Quem diria, todo mundo já tinha dado Galhareda como morto faz tempo!\n\nVocê conquistou um lugar de honra entre os Corsários de Thalo'dan por isso, &lt;name&gt;.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas com FivelasManoplas Rebitadas\n\nVocê também receberá:\n\n"},
[899] = {title = "Consumido pelo ódio", objective = "Leve 60 Presas de Javatusco Costagulha para Mankrik na Encruzilhada.", description = "Talvez você já saiba bem como é a dor da incerteza, <class>; talvez não. Mas saiba: eu fico aqui todos os dias, do alvorecer ao crepúsculo, perscrutando o horizonte em busca de mais monstros. Matei todos os homens-porcos que cruzaram o meu caminho, mas minha sede de sangue está longe de ser saciada. Em vez de vir para o norte, para a Encruzilhada, talvez eu devesse ter rumado para Taurajo.\n\nVocê, <class>... você pode me ajudar.\n\nMate-os. Mate todos que encontrar. Traga-me suas presas e vamos celebrar juntos a morte deles.", progress = "Os javatuscos vão pagar caro por isso. Eu juro que vão.", completion = "Por ter levado a sério a empreitada, &lt;class&gt;, você tem a minha gratidão.\n\nMinha vingança ainda não está concluída, mas alegra-me saber que os javatuscos tiveram sua cota de sofrimento.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCapa do Caçador de JavalisFaixa da Campina\n\nVocê também receberá:\n\n"},
[900] = {title = "A rebimboca", objective = "Feche a Válvula de Controle de Combustível, a Válvula de Regulagem e a Válvula Principal e, em seguida, use o Painel de Controle.", description = "As três válvulas de controle estão abertas. A sequência de leituras disposta no painel leva você à conclusão de que é preciso fechar as válvulas para desligar o aparato.", progress = "As luzes piscando no painel de controle indicam que as válvulas de controle ainda não foram desligadas.", completion = "As luzes das três válvulas de controle apagam à medida que as válvulas são fechadas. As luzes verdes aos poucos vão se tornando amareladas. Com as válvulas fechadas, a alavanca que controla o aparato se destrava e o aparelho pode ser desligado.", rewards = "Você receberá:\n\n"},
[901] = {title = "A rebimboca", objective = "Pegue a Chave do Painel do Engenhoqueiro Faísca para usar no painel de controle.", description = "Agora que o aparato está desativado, o painel de controle pode ser aberto e a rebimboca removida do painel. Agora só resta obter a chave do painel para destravar o console.", progress = "As luzes do painel estão apagadas e todos os ponteiros estão na posição inicial: nada está operante.", completion = "Ao virar a chave, o painel de controle se abre. Uma luz vermelha no topo do painel se apaga e toda a energia do terminal é exaurida.", rewards = "Você receberá:\n\n"},
[902] = {title = "A rebimboca", objective = "Leve a Rebimboca para Cobogó na Vila Catraca.", description = "Quando o painel é aberto, uma densa selva de cabos, fios e estranhos objetos mecânicos se revela, a maioria dos quais parece completamente inútil. Afastando-os, para alcançar o fundo do console, você encontra a rebimboca. Com um puxão você a remove do compartimento.", progress = "Olha só quem está de volta! Trouxe a rebimboca?", completion = "Isso é... mmm... interessante... é. Vou mandar pra sede o Sindicato dos Faz-tudo na Inframina. Com certeza, depois de uma dissecação cuidadosa e alguma pesquisa, eles vão conseguir entender esse treco.\n\nMas... Talvez eu mesmo dê uma olhada primeiro...", rewards = "Você vai poder escolher uma dessas recompensas:\n\nMartelo do EngenheiroEscudo de Soldagem\n\nVocê também receberá:\n\n"},
[903] = {title = "Predadores dos Sertões", objective = "Colete 7 Garras de Predador de Predadores da Savana para Sergra Espinhonegro na Encruzilhada.", description = "Agora é hora de ir atrás de algo que morde pra valer. Os predadores dos Sertões são uma raça difícil. Difícil, engenhosa, astuta... e mortal. Você vai encontrá-los em meio à grama alta. Você encontrará muitos a sudoeste da Encruzilhada.\n\nNão se descuide, <name>, ou você pode acabar passando de predador a presa.", progress = "Como vai sua caçada, <name>? Encontrou os predadores?", completion = "Ah, vejo que você andou ocupado, movendo-se furtivamente na grama à caça de predadores. Você acha que está na hora de enfrentar um campeão entre eles?", rewards = "Você receberá:\n\n"},
[905] = {title = "Garrafoices furiosos", objective = "Mate raptores Helióscamos e colete as penas. Use as penas em 3 dos ninhos de Garrafoices. Fale novamente com Sergra Espinhonegro na Encruzilhada.", description = "Agora, <name>, a caçada se intensifica. Desta vez você terá de derrotar sua presa e depois encontrar o covil.\n\nCace os raptores helióscamo ao sul. Mate-os e retire suas penas. Deposite as penas nos ninhos de garrafoice, a sudoeste do Oásis Estagnado. Mostre a eles que você não tem medo deles!", progress = nil, completion = "Então você esteve nos ninhos e depositou sobre eles as penas de suas presas, não é? Para fazer isso é preciso bastante coragem, sem dúvida.\n\nSente-se mais forte depois disso, &lt;name&gt;? Pois você me parece mais forte agora do que da primeira vez em que nos vimos. Acho que os Sertões estão endurecendo você.", rewards = "Você receberá:\n\n"},
[906] = {title = "Traição intestina", objective = "Leve o Crânio de Lok para Thork na Encruzilhada, que fica nos Sertões.", description = "Leve a cabeça de Lok para Thork na Encruzilhada, <race>. <bufa> Ele deveria saber o que aconteceu com a minha tribo sem qualquer palavra — rá, mas ele nunca acreditaria que Denterroto ajudou <bufa> nestas coisas. Tenho certeza de que ele vai lhe recompensar por cumprir uma missão tão importante. <bufa>\n\nVou continuar a abençoar você com o poder de Agamaggan durante o tempo em que eu permanecer nesta gaiola, <race>. Até lá, adeus.", progress = "Pois não? O que foi?", completion = "Você encontrou o líder dos Crinavalhas e o matou com as suas próprias mãos, &lt;name&gt;? Impressionante.\n\nNão tenho palavras para agradecer, mas me sobra gratidão. O povo da Encruzilhada lhe deve muito respeito.\n\nPreciso confessar que pensei que o líder desses raides contra nossas linhas de suprimentos fosse outro javatusco. Obrigado mais uma vez, &lt;name&gt;.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nTúnica de Couraça LenhosaTúnica de Musgo Seco\n\nVocê também receberá:\n\n"},
[907] = {title = "Lagartos trovejantes enfurecidos", objective = "Leve 3 unidades de Sangue de Lagarto Trovejante para Jorn Vidente do Céu, no Acampamento Taurajo.", description = "Agora, <name>, você deve enfrentar os grandes lagartos trovejantes. Essas criaturas são tão grandes que o chão ressoa quando atravessam pesadamente as planícies. Tome cuidado ao lutar com essas feras, pois qualquer passo em falso e você pode acabar sob os pés delas.\n\nVocê vai encontrar os lagartos trovejantes na parte sul dos Sertões. Eles são conhecidos como Eletrossauros, Borrascouros e Cabeças-de-trovão. Fale novamente comigo depois que derrotá-los.", progress = "Os lagartos trovejantes foram derrotados, <name>?", completion = "É bom ver você voltar com a prova da vitória. E é bom saber que você saiu dessa com seus ossos intactos.", rewards = "Você receberá:\n\n"},
[908] = {title = "Nas ruínas", objective = "Leve o Núcleo das Profundezas para Je'neu Sancrea no Assentamento Zoram'gar, no Vale Gris.", description = "O Martelo do Crepúsculo se instalou nas Ruínas do Sacrário Lunar, nas Profundezas Negras. A presença deles só pode ter o fim de coagir os elementos a trabalhar contra nós. Se não forem contidos, dominarão essa região em definitivo.\n\n<name>, adentre as Profundezas Negras e encontre a pedra das profundezas da ruína. Ela deve estar próxima, na água. Nela há um núcleo das profundezas, um dispositivo que, caso seja interpretado corretamente, nos dará um registro de toda atividade elemental. Com ele, talvez eu e a Harmonia Telúrica possamos fazer algo para impedi-los!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[909] = {title = "O Barão Aquanis", objective = "Leve o Globo d'Água Estranho para Je'neu Sancrea no Assentamento Zoram'gar, no Vale Gris.", description = "O elemental da água, derrotado, deixa para trás um globo d'água estranho. Você supõe que o objeto era a fonte de energia da criatura. Dentro do globo, uma forma aquática de aparência pútrida move-se para lá e para cá. O globo em si parece invulnerável a qualquer tipo de força física.\n\nSe existe alguém que possa saber o que é esse objeto, esse alguém é Je'neu Sancrea, da Harmonia Telúrica. Ele está no Assentamento Zoram'gar, um posto avançado da Horda no Vale Gris.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nSabre do Fora da LeiDedo de Bruxa\n\nVocê também receberá:\n\n"},
[910] = {title = "Lá nas docas", objective = "Leve seu protegido às docas da Vila Catraca, que fica nos Sertões.", description = "Eu ouvi lendas muito legais sobre navios poderosos que navegavam entre a Vila Catraca e a Angra do Butim... Seria tão legal navegar pelo oceano e participar de todas essas grandes batalhas em alto mar! Talvez eu vire um grande marinheiro da Horda quando eu crescer... É, acho que vou ser isso mesmo!\n\nVocê me levaria para ver as docas da Vila Catraca? Eu prometo que não vou entrar na água, e nem causar muitos problemas... Vamos, por favor! Eu prometo me comportar! Só quero ver os grandes barcos e sentir o cheiro do mar!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[911] = {title = "O portal da fronteira", objective = "Leve seu protegido até a Paliçada Mor'shan, que fica ao norte dos Sertões, um pouco antes da entrada do Vale Gris.", description = "Eles falam que o Vale Gris é a nova fronteira, e que a Paliçada Mor'shan, nos Sertões, é o portão. Seria muito legal ser um pioneiro, abrindo caminho por entre aquela mata selvagem! Eles dizem que é lá que Horda e a Aliança vão se confrontar toda hora no futuro, e eu quero muito estar lá, <name>!\n\nVocê me levaria até a Paliçada Mor'shan, para que eu veja como que é essa fronteira? Por favor! Eu quero ver como a Horda está lidando com aqueles elfos noturnos fedorentos!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[913] = {title = "O grito do Falcotrom", objective = "Encontre e mate um falcotrom, depois leve as asas dele para Jorn Vidente do Céu, no Acampamento Taurajo.", description = "O falcotrom, <name>, é uma fera muito poderosa. Chegou a sua hora de enfrentá-lo. Descubra o paradeiro dele e traga-me as asas da fera para comprovar que sua caça foi um sucesso.\n\nFaça isso, e seu tempo comigo estará próximo do fim.", progress = "Conseguiu? Matou o falcotrom?", completion = "Você deveria se orgulhar de si mesmo, um orgulho digno de um falcotrom. Você derrotou todos os inimigos que pusemos no seu caminho, sem nunca deixar de demonstrar força, coragem e honra.\n\nMas agora, &lt;name&gt;, seu caminho continua. Inclusive, você logo descobrirá que o caminho dos verdadeiros caçadores nunca chega ao fim, mas eles sempre o percorrem com o mesmo orgulho que você demonstrou.\n\nChegou a hora de seguir em frente.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBroquel de CobaltoCajado da Mantícora\n\nVocê também receberá:\n\nLuvas da Lua"},
[914] = {title = "Líderes da Presa", objective = "Leve as Gemas de Cobrahn, Sucurina, Pítias e Serpentis para Nara Juba Agreste no Penhasco do Trovão.", description = "Aqueles druidas da Caverna Ululante, os Druidas da Presa, são umas aberrações. Eles já fizeram parte de uma ordem de druidas nobres cujo plano era curar os Sertões, mas passaram a buscar a recriação dessa terra à imagem e semelhança dos sonhos torpes deles.\n\nOs Druidas da Presa têm quatro líderes, cada um em posse de uma gema onírica. Mesmo acordada, eles conseguem me assombrar! Derrote os líderes e traga-me as gemas. Só assim os Sertões terão paz.\n\nVá, <name>. Os druidas podem ser encontrados à espreita no fundo da Caverna Ululante.", progress = "Vivo assombrada pelas lembranças dos meus pesadelos, <name>. Conseguiu derrotar os líderes da Presa e pegar as gemas?", completion = "Você conseguiu, &lt;name&gt;, matou os líderes dos Druidas da Presa. Meu sono está livre daqueles rostos vis, e você ajudou a salvar os Sertões de um futuro maldito.\n\nObrigada, &lt;name&gt;. Agradeço por mim, pelos druidas do Penhasco do Trovão e por esta terra.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCajado CrescenteLaminasa\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[915] = {title = "Olha o sorvete!", objective = "Consiga um pouco de Sorvete de Morango para o órfão. Ele parece gostar de sorvete da marca Tigule e Foror.", description = "SORVETE! Por favor, por favor, por favor, vamos tomar sorvete?! Meu favorito é o de morango, e não tem sorvete de morango melhor do que o Sorvete de Morango de Tigule e Foror! É o meu sorvete favorito em todo o mundo!\n\nA última vez que eu tomei foi lá na pista de corrida da Chapada Cintilante, mas parece que agora também vendem na cidade! Por favor! É tudo que eu quero! Com cobertura de Morango de Tugule e Foror por cima!!!!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[916] = {title = "Peçonha de lenhateia", objective = "Leve 10 Vesículas de Peçonha de Lenhateia para Gilshalan Andavento em Aldrassil.", description = "Eu vim para Umbravale no intuito de observar as aranhas lenhateia que vivem na Caverna Fionumbra. Elas são primas de uma variedade muito menor, o que me leva a crer que a Árvore do Mundo tenha um efeito profundo sobre elas. Por isso, eu gostaria de obter alguns espécimes para realizar estudos e confirmar isso.\n\nAntes, no entanto, preciso de um pouco do veneno delas. Pegue vesículas de peçonha de lenhateia das aranhas na Caverna Fionumbra, ao norte, e nas redondezas. Assim poderei examiná-las e confirmar se há similaridades entre a peçonha delas e a de suas primas menores.", progress = "Você conseguiu as vesículas de peçonha, <name>?", completion = "Obrigado, &lt;name&gt;. Quando retornar a Darnassus, analisarei o veneno nessas vesículas para comparar ao de outras aranhas. Acredito que ele terá propriedades relacionadas ao crescimento recente de nossa Árvore do Mundo.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nMalho de Pau-cardoAdaga de Pau-cardoCajado de Pau-cardoLâmina de Pau-cardo\n\nVocê também receberá:\n\n"},
[917] = {title = "Ovo de Lenhateia", objective = "Leve um Ovo de Lenhateia para Gilshalan em Aldrassil.", description = "Agora que consegui peçonha de aranhas, preciso estudar espécimes vivos. Infelizmente, é demais pedir a você, jovem <class>, que capture uma aranha gigante. E mesmo que você conseguisse, eu não daria conta de lidar com ela depois!\n\nMas se puder encontrar um ovo não eclodido, será mais fácil obter alguns espécimes, e posso dar um jeito de conter essas aranhas pré-natais também.\n\nAcredito que haja um ninho no fundo da Caverna Fionumbra. Por favor, encontre um ovo nesse ninho e traga-o para mim.", progress = "Você já adentrou a Caverna Fionumbra, <name>? Encontrou algum ovo de aranha?", completion = "Ah, excelente. Arrumarei transporte para o ovo e a peçonha até Darnassus e depois irei para lá quando concluir meus estudos aqui. Acredito que esses espécimes serão reveladores, &lt;name&gt;. Você tem sido de grande ajuda.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nEscudo do BosqueTúnica do BosqueVestes do Bosque\n\nVocê também receberá:\n\n"},
[918] = {title = "Sementes de muscoide", objective = "Leve 8 Sementes de Muscoide para Denelan no Lago Al'Ameth.", description = "Os muscoides de Teldrassil são elementais da natureza. Eles refletem alguns aspectos da ordem natural de plantas e animais em nossa grande árvore.\n\nÉ preocupante vê-los se tornando tão raivosos.\n\nCreio que seja algo relacionado ao solo. Tenho trabalhado em diferentes métodos para o cultivo de plantas e gostaria de testá-los em sementes de muscoides. Por favor, poderia me trazer algumas sementes dos muscoides perto do Lago Al'Ameth?", progress = "Conseguiu as sementes? Estou ansioso para plantá-las.", completion = "Você conseguiu. Ótimo!\n\nPlantarei estas sementes em um solo especial que preparei. Acredito que muscoides muito mais dóceis nascerão delas. Talvez você consiga ver os resultados mais tarde!", rewards = "Você receberá:\n\nCogumelo da Floresta"},
[919] = {title = "Brotos de muscoide", objective = "Leve 12 Brotos de Muscoide para Denalan no Lago Al'Ameth.", description = "Pequenos muscoides crescem ao redor das águas do Lago Al'Ameth. Temo que tais brotos não possam ser salvos. Devemos tirá-los da terra antes que cresçam ao ponto de nos causar problemas.\n\nQuando estiver andando pelo lago, por favor arranque os brotos de muscoides que encontrar. Ajude a manter este terra limpa!", progress = "Olá, <name>. Encontrou algum broto perto das águas?", completion = "Veja quantos! Temo que estejam se espalhando em uma taxa alarmante. Espero descobrir o que está causando esta mácula.\n\nObrigado pela ajuda, &lt;name&gt;. A terra é um lugar mais puro graças aos seus esforços.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nLuvas de JardinagemBraçadeiras Pedragris\n\nVocê também receberá:\n\n"},
[920] = {title = "O chamado de Tenaron", objective = "Fale com Tenaron Fortagarras no topo de Aldrassil, em Umbravale.", description = "<name>, não é? Ouvi que Tenaron tem perguntado de você. Você o encontra no topo de Aldrassil, no salão mais alto.\n\nNossas vidas são longas, é verdade, mas é melhor não deixá-lo esperando.", progress = nil, completion = "Ah, &lt;name&gt;. Eu já esperava que você respondesse prontamente ao meu chamado. Tenho uma tarefa importante e gostaria que você se incumbisse dela.", rewards = "Você receberá:\n\n"},
[921] = {title = "Coroa da Terra", objective = "Encha o Frasco de Cristal e leve-o de volta para Tenaron Fortagarras, no topo de Aldrassil.", description = "Chegou a hora de sair em busca do seu destino, <name>. Mas, antes de se considerar pront<o/a> para ganhar o mundo além das nossas florestas encantadas, você ainda precisa aprender mais sobre a nossa história recente.\n\nDesde a Batalha do Monte Hyjal, nosso povo passou por muitas mudanças. Nordrassil não passa de uma pálida sombra do que já foi, pois seu poder foi usado para derrotar Arquimonde e afugentar a Legião Ardente...\n\nVocê deve cumprir uma tarefa. Vá ao poço lunar ao norte de Aldrassil e traga-me um frasco da água de lá.", progress = "Os poços lunares contêm as águas da Nascente da Eternidade, a fonte ancestral de toda magia que outrora derramou incontáveis horrores sobre nosso mundo.\n\nOs druidas se utilizam de suas propriedades e as Sentinelas as reverenciam como templos a Eluna, mas todos são proibidos de usar feitiçaria.", completion = "Então, você já sabe a primeira parte do que sucedeu à Batalha do Monte Hyjal. Ainda há muito o que aprender e a jornada que iniciou aqui se estenderá a Teldrassil e além: a Darnassus.", rewards = "Você receberá:\n\n"},
[922] = {title = "Rellian Spiraverde", objective = "Leve uma Semente de Muscoide para Rellian Spiraverde, em Darnassus.", description = "<name>, pode pegar uma dessas sementes que você me trouxe e levá-la para o meu amigo, Rellian Spiraverde? Ele é um druida de Darnassus e, da última vez em que nos falamos, ele demonstrou interesse em trabalhar com os Muscoides. Ele tinha algumas ideias boas e vai gostar de receber uma semente para suas pesquisas.\n\nObrigado, <name>. Você me prestou um grande serviço. Espero que, um dia, você possa ver os frutos do meu trabalho.\n\nRellian costuma gostar de perambular pelas trilhas do Enclave Cenariano, em Darnassus.", progress = "Olá...\n\nEm que posso ajudá-la?", completion = "Ah, uma semente de muscoide? Eu estava à procura de uma dessas para ajudar Denalan com os estudos dele.\n\nMas descobri que a corrupção já afetou a maioria dos muscoides e que as sementes também carregam mácula dentro de si. Não existe nada que eu possa fazer para ajudar.\n\nDenalan tem um profundo conhecimento sobre seres que florescem. Talvez ele encontre uma cura para os muscoides. Acredito que ele seja a única esperança.", rewards = "Você receberá:\n\n"},
[923] = {title = "Tumores", objective = "Leve 5 Tumores Musguentos para Rellian Spiraverde, em Darnassus.", description = "Existe um mal crescente nos muscoides. Estamos tentando descobrir a origem, mas até lá... se quisermos manter Teldrassil a salvo, devemos acabar com os muscoides que não tem mais salvação. Aqueles que vagueiam perto do Lago Olho-d'água, ao norte de Teldrassil, são os mais maculados. Precisamos acabar com eles!\n\nDestrua os muscoides que encontrar lá e arranque os tumores musguentos que crescem neles. Traga-me os tumores para que sejam queimados.", progress = "Já esteve no Lago Olho-d'água, <name>? Já caçou os muscoides de lá?", completion = "Parabéns! Estes tumores são um sintoma da doença que afeta os muscoides. Neles existe um veneno que devemos extinguir.\n\nDarei um fim a estes tumores. Agradeço, &lt;name&gt;.", rewards = "Você receberá:\n\nFaca de Podar"},
[924] = {title = "A Semente Demoníaca", objective = "Pegue uma Pedra do Poder Defeituosa. Leve-a ao Altar de Fogo antes que ela expire e fale novamente com Ak'Zeloth.", description = "A Semente Demoníaca é um instrumento poderoso da Legião Ardente. Ela fica num altar de fogo demoníaco, dentro de uma caverna do Morro de Brumedo.\n\nFiz algumas pedras de poder que bloqueiam a conexão da Semente com a Legião quando colocadas perto dela.\n\nPegue uma pedra na mesa e leve até o altar de fogo no cume do Morro de Brumedo. Vá para oeste e vire para o norte ao chegar numa encruzilhada. Você vai ver o morro à sua direita. É mais seguro subir o morro pela face norte.\n\nVá depressa, a energia da pedra não dura muito.", progress = nil, completion = "Você cumpriu sua tarefa: destruiu a Semente Demoníaca como Neeru mandou, mas não é o fim...\n\nA destruição da Semente Demoníaca propagou ondas de energia no éter, tsunamis poderosos. Se eu consegui sentir, tenho certeza de que outros seres também sentiram. Seres que vão se perguntar por que destruíram o brinquedo deles...\n\nMas esses problemas são para depois. Tenho uma coisa para você. Neeru enviou como pagamento e pediu que eu lhe agradecesse.\n\nEntão, muito obrigado.", rewards = "Você receberá:\n\nArmadura da Banshee"},
[925] = {title = "Impressão do casco de Caerne", objective = "Vá ao Penhasco do Trovão para tentar conseguir a impressão do casco de Caerne Casco Sangrento para o seu protegido.", description = "Dizem que o Caerne Casco Sangrento é um dos maiores heróis que a Horda já teve. Quando eu crescer, quero ser um herói da Horda também!\n\nVocê conhece o Caerne, <name>? Posso pedir um favor? Você poderia conseguir uma impressão do casco dele pra mim? Eu não saberia o que falar, e ele provavelmente não tem tempo pra alguém como eu. Mas você... Você é <um aventureiro/uma aventureira>, assim como ele! Aposto que ele lhe faria o que eu tô pedindo na mesma hora!\n\nPor favor?Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[926] = {title = "Pedra do Poder Defeituosa", objective = "Estas pedras estão imbuídas de energia mágica, mas energia propositalmente defeituosa. Elas são muito instáveis e, uma vez retiradas da mesa, se consumirão rapidamente.", description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nPedra do Poder Defeituosa"},
[927] = {title = "O coração enroscado em musgo", objective = "Leve o Coração Enroscado em Musgo para Denalan no Lago Al'Ameth.", description = "O coração do Limonegro, o Fétido, está coberto com um limo negro oleoso. A única coisa que o identifica como um coração é a lenta batida rítmica... uma batida que continua mesmo depois de morto.\n\nTalvez Denalan queira ver este coração.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[928] = {title = "Coroa da Terra", objective = "Leve o Recipiente Parcialmente Cheio a Corithras Lunafúria em Dolanaar.", description = "Ainda tenho muito a dizer sobre os poços lunares e sobre Teldrassil, mas você precisa ir. Corithras Lunafúria <o/a> aguarda. Passei a água que me trouxe para este outro vidro, que você deve levar a ele.\n\nCorithras se encontra no poço lunar de Dolanaar. Siga a estrada ao sul de Aldrassil até sair de Umbravale e continue seguindo os paralelepípedos a oeste.\n\nAconselho que não saia da estrada, <name>. Ultimamente, feras perigosas andam rondando a floresta.", progress = "Saudações, <class>. A que devo o prazer da sua visita?", completion = "Hum... Compreendo. Tenaron o enviou. Muito bem, temos muito o que falar, muito o que fazer e pouco tempo para isso.\n\nAcho melhor começarmos.", rewards = "Você receberá:\n\n"},
[929] = {title = "Coroa da Terra", objective = "Encha o Frasco de Jade e leve-o de volta para Corithras Lunafúria em Dolanaar.", description = "Antes, deixe-me contar mais sobre a tarefa que você precisa cumprir. Os druidas em Darnassus usam a água dos poços lunares de Teldrassil, que precisam ser reabastecidos regularmente. Usando estes frascos especialmente criados, apanhe a água desses poços.\n\nLeve este recipiente para o poço lunar a leste do Vilarejo Brisastral e encha-o com um pouco da água. Volte quando tiver acabado. Quando tiver concluído sua tarefa, continuarei a história de onde Tenaron parou...", progress = "Olá mais uma vez, <name>. Cumpriu seu dever?", completion = "Após a Batalha do Monte Hyjal, ficamos sem rumo. Nordrassil ardia nas chamas e nossa imortalidade — a essência que nos define — se perdeu.\n\nFoi nesse momento difícil que o Traidor foi libertado de sua prisão e Shan'do Tempesfúria desapareceu. Um período sombrio para todos.", rewards = "Você receberá:\n\n"},
[930] = {title = "A fruta brilhante", objective = "Leve a Fruta Brilhante para Denalan no Lago Al'Ameth.", description = "Dependurada em meio à folhagem desta planta lustrosa há uma fruta grande e redonda.\n\nDenalan certamente gostará de estudá-la.", progress = "<name>, você parece ter algo para me contar. São notícias sobre os muscoides?", completion = "Você encontrou isso em Teldrassil? Interessante... esta é uma fruta exótica. Talvez as sementes tenham sido trazidas de muito longe. Talvez dos confins de Azeroth! Mas tem algo de estranho nela... parece ter reagido de forma peculiar com o solo de Teldrassil.\n\nObrigado, &lt;name&gt;. Agora, se me permite, preciso estudar mais sobre isso...", rewards = "Você receberá:\n\n"},
[931] = {title = "A fronde cintilante", objective = "Leve uma Fronde Cintilante para Denalan no Lago Al'Ameth", description = "As frondes desta planta reluzem sob a luz da floresta, emitindo uma trançada aura pulsante.\n\nVocê acredita que Denalan gostaria de um espécime disto.", progress = "Traz algo para mim?", completion = "Onde conseguiu isto? Não vejo uma planta como esta desde a minha breve estadia no Pântano das Mágoas... há décadas! É incrível que um espécime tenha chegado até Teldrassil. E tenha crescido tanto!\n\nObrigado, &lt;name&gt;. Perdoe-me a brevidade das minhas palavras, mas tem um teste que gostaria de fazer com este fronde...", rewards = "Você receberá:\n\n"},
[932] = {title = "Aversão pervertida", objective = "Mate o Senhor Málinus e leve a cabeça dele para Tallonkai Radicélere, em Dolanaar.", description = "Preciso alertá-<lo/la>, <name>, este assunto deve ficar entre nós. Os sátiros são um aborrecimento constante e um deles mora próximo demais do nosso lar.\n\nEle se chama Senhor Málinus e reside na caverna da Pedra Vil, onde reuniu um grande grupo de criaturinhas demoníacas. O coração dele é negro como a noite, e sei que ele planeja algo terrível.\n\nEncontre-o na caverna a norte daqui e traga-me a cabeça dele.", progress = "Já matou o Senhor Málinus? É de extrema importância acabarmos com ele de maneira rápida e silenciosa, <name>. A pura existência deste monstro é um empecilho para todos nós.", completion = "Com o Senhor Málinus morto, finalmente posso me preocupar com outras questões. Agradeço, &lt;name&gt;.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBraçadeiras FeraisBotas Vinosas\n\nVocê também receberá:\n\n"},
[933] = {title = "Coroa da Terra", objective = "Encha o Frasco de Turmalina e leve-o de volta para Corithras Lunafúria em Dolanaar.", description = "Há outro poço lunar a sudeste da entrada de Darnassus, no litoral das Águas de Arlithrien. As Sentinelas estão tendo dificuldade para patrulhar a área por causa dos ataques decorrentes de um temperamento cada vez mais bravio por parte dos pelursos Masca-pinho.\n\nTenha cuidado quando for procurar pelo poço – e mantenha suas armas sempre à mão.", progress = "Inicialmente, as notícias sobre pelursos ensandecidos foram ignoradas. Quem seria capaz de acreditar que os homens-ursos tão pacíficos poderiam ser vitimados por uma fúria tão cega? Isso é só mais um dos muitos problemas que assolam o nosso novo lar.", completion = "O Shan'do Tempesfúria jamais retornou, o que causou desordem entre os druidas. Até hoje não sabemos qual foi o fim dele. Com Malfurion desaparecido, o Arquidruida Fandral Guenelmo assumiu a liderança druídica, convencendo o Círculo dos Ancientes da Costa Negra que era chegada a hora de o nosso povo se reerguer, de o nosso povo reaver a imortalidade.\n\nCom a aprovação do Círculo, Guenelmo e os druidas mais poderosos cultivaram Teldrassil, a nova Árvore do Mundo.", rewards = "Você receberá:\n\n"},
[934] = {title = "Coroa da Terra", objective = "Encha o Frasco de Ametista e leve-o de volta para Corithras Lunafúria em Dolanaar.", description = "Nem tudo estava bem com Teldrassil, no entanto. Os planos cuidadosamente elaborados por Guenelmo para a nova Árvore do Mundo transcorreram como ele esperava, mas havia um pequeno problema, um problema ao qual muitos dos transtornos em Teldrassil podem ser atribuídos.\n\nNo entanto, não vou entrar nessa questão ainda. Você deve visitar o último poço lunar, a noroeste, na Clareira do Oráculo. Sob os galhos da Árvore dos Oráculos está o primeiro e mais poderoso de nossos poços. Busque um frasco da água de lá e fale novamente comigo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[935] = {title = "Coroa da Terra", objective = "Leve o Recipiente Cheio para o Arquidruida Fandral Guenelmo em Darnassus.", description = "Sem as bênçãos de Alexstrasza, a Mãe da Vida, e Nozdormu, o Atemporal, o crescimento de Teldrassil foi prejudicado. Criaturas estranhas foram avistadas saindo do próprio solo da árvore, e pelursos enlouquecidos atacaram viajantes.\n\nSó me resta esperar que a solução que o Arquidruida procura seja encontrada em breve. Eu porei todos os frascos que você trouxe neste recipiente, para que você os entregue em Darnassus.\n\nLeve-o para Fandral Guenelmo. Você o encontra no bosque dos druidas.", progress = "Não fui eu quem convocou você, por isso não posso deixar de me perguntar por que você veio até mim.\n\nSeja qual for o assunto, por favor, seja breve.", completion = "Ah, sim. A água que solicitei. Tenaron e Corithras certamente se empenharam para que ela me fosse entregue... ainda que talvez não tenham escolhido os mensageiros mais confiáveis para fazê-lo.\n\nEnfim, finalmente poderei voltar ao trabalho. O peso das agruras de Teldrassil recai sobre meus ombros. Um jugo do qual espero estar livre em breve.\n\nTome isto. Talvez lhe seja útil.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nArco de FreixoMartelo da Mata\n\nVocê também receberá:\n\n"},
[936] = {title = "Auxílio ao Arquidruida Runa Totem", objective = "Fale com o Arquidruida Hamuul Runa Totem no Penhasco do Trovão.", description = "Com licença, <class>, um momento de seu tempo, se possível.\n\nO Círculo Cenariano no Penhasco do Trovão está interessado em aventureiros experientes como você para ajudar em um projeto de pesquisa importante. Apesar de não saber os detalhes, eu posso dizer que o próprio Hamuul Runa Totem está liderando essa pesquisa.\n\nPor favor, se tiver interesse, fale com ele diretamente no Platô dos Anciãos, que fica no Penhasco do Trovão.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[937] = {title = "A Clareira Encantada", objective = "Consiga 6 Cintos Plumassangue e leve-os para a Sentinela Arynia Rasganimbus, na Clareira do Oráculo.", description = "Fui enviada para cá com um pequeno grupo de Sentinelas para proteger a Árvore dos Oráculos das harpias que fizeram seus ninhos por toda a clareira. Aos poucos, estamos tentando expulsá-las.\n\nQuando a Árvore dos Oráculos tentou enviar um mensageiro para Darnassus com um relatório, ele foi atacado e morto por um grupo de harpias.\n\nSe quiser ajudar, vá até os ninhos e mate-as. Traga-me os cintos delas como prova.", progress = "As afiadas garras e os perfurantes bicos serão um grande teste para suas habilidades, <name>, mas tenho fé de que você não falhará.", completion = "Estou impressionada com o quanto você conquistou em tão pouco tempo, &lt;name&gt;. Gostaria de pedir para que ficasse e me ajudasse com outras tarefas... mas meu coração sabe que conquistas muito mais grandiosas a aguardam.\n\nPercebi que a Árvore dos Oráculos acabou de soltar um pedaço da casca. Com certeza essa casca traz uma missão que a árvore quer ver cumprida. Fale com ela.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCinturão AcorrentadaCapa Manchada de Chuva\n\nVocê também receberá:\n\n"},
[938] = {title = "Bruma", objective = "Escolte Bruma para a Sentinela Arynia Rasganimbus no poço lunar, perto da Árvore dos Oráculos.", description = "O pelame da gata-de-sabre é de um cinza inconfundível que se mistura com a floresta ao redor. Você observa que ela está gravemente ferida e com profundas lacerações nas costas e na barriga.\n\nEla levanta a cabeça e fixa o olhar em você, com brilhantes olhos azuis que transmitem sabedoria. Ela parece querer seguir você.", progress = nil, completion = "Ah, obrigada, &lt;name&gt;! Achei que jamais veria Bruma de novo. Acreditei que viveria sem minha leal companheira, sofrendo com a incerteza de sua morte. Devo a você mais do que pode imaginar. Aceite minha eterna gratidão.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBraçadeiras de CordãoBroquel RochedoManto do Batedor\n\nVocê também receberá:\n\n"},
[939] = {title = "Flauta de Xavaric", objective = "Leve a Flauta de Xavaric e 5 amostras de Ligavil de Flamejade para Eridan Brisazul, na parte sul da Selva Maleva.", description = "Você estuda a flauta de madeira simples que você pegou após derrotar Xavaric.\n\nA madeira nodosa é sólida, mas as espirais e os padrões na superfície parecem começar a girar e se mover quando você a vira nas mãos. Subitamente, você é tomad<o/a> por uma sensação de pavor...\n\nVocê sacode a cabeça, cert<o/a> de que sua mente está lhe pregando uma peça.\n\nÉ possível que outros sátiros tenham pistas sobre o que você descobriu. Leve sua descoberta a Eridan Brisazul, o druida, que mora na parte sul da Selva Maleva.", progress = "Qual o problema, <class>? Você parece preocupado.", completion = "Sinto o sofrimento dos antigos, &lt;name&gt;...\n\nPara mim, é quase insuportável segurar estes itens...", rewards = "Você receberá:\n\n"},
[940] = {title = "Teldrassil", objective = "Entregue o relatório da Árvore dos Oráculos para o Arquidruida Fandral Guenelmo em Darnassus.", description = "A floresta me sussurra os seus feitos valorosos, <name>. Senti as harpias sendo expulsas de seus ninhos e a calma que se espalhou pela floresta em seguida, com as criaturas novamente se sentindo em segurança. Eu estava com medo de enviar outra mensagem para o arquidruida, mas agora sei que você consegue cumprir a missão em segurança.\n\nEntregue isto a ele e fique tranquil<o/a>, a floresta vai proteger você no caminho até a floresta de pedra.", progress = "Hum, o espírito da floresta é poderoso em você, <class>. O que você quer falar com o Arquidruida dos Kaldorei?", completion = "Eu estava mesmo me perguntando por que a Árvore dos Oráculos ficou tanto tempo sem se comunicar comigo... Parece que alguns problemas foram contornados, enquanto outros se tornaram ainda mais preocupantes.\n\nTemo que meu trabalho em Teldrassil jamais seja concluído, e nossa imortalidade jamais seja restaurada.\n\nSeja como for, você cumpriu sua missão e merece ser recompensada.", rewards = "Você receberá:\n\n"},
[941] = {title = "Plantando coração", objective = "Plante o Coração Maculado na Sementeira do Denalan.", description = "Removi o limo pútrido do coração, mas ele ainda está maculado...\n\nTente colocá-lo na sementeira  que enchi de terra nutritiva. Talvez ela limpe e cure este coração.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nCoração Purificado de Muscoide"},
[942] = {title = "O prospector distraído", objective = "Leve o fóssil misterioso ao Arqueólogo Pançacheia no Porto de Menethil.", description = "A estampa desse fóssil me recorda de outro que vi em Altaforja. Um arqueólogo que atende por Pançacheia trouxe na conferência anual da Liga dos Exploradores. Dava por certo que fóssil tinha um poder que dava para liberar.\n\nDa última notícia que tive, Pançacheia tinha se abancado na Taberna Aguafunda, lá no Porto de Menethil. Tá só esperando uma escolta pra picar a mula pro Sítio de Escavação de Whelgar. Alcança o vivente e mostra teu fóssil do Trilheiro. Se bobear, ele dá conta de te revelar esses mistérios do passado.\n\nO navio pra Menethil zarpa de Auberdine.", progress = "O que tu tem aí, bagual?", completion = "Caramba! Quer dizer que isso veio lá de Kalimdor?\n\nIncrível! Realmente incrível!", rewards = "Você receberá:\n\n"},
[943] = {title = "O prospector distraído", objective = "Arqueólogo Pançacheia, no Porto de Menethil, quer que você traga a Pedra de Relu e o Fóssil do Pançacheia para ele.", description = "O fóssil original está com o Prospector Whelgar, enfiado no sítio de escavação dele no Pantanal. Quando me informei da Pedra de Relu, me dei conta de que ela podia ser a chave para decifrar o mistério do fóssil.\n\nQuando troteava para me encontrar com Whelgar, raptores me deram uma volteada e se foi a relíquia. Não sei qual das criaturas mosquetuscas papou a relíquia, mas, se deres conta de recuperá-la, vou poder liberar o poder desses artefatos.\n\nVê se me trazes a Pedra de Relu, e não te esquece do fóssil original dele.", progress = "Com a Pedra de Relu, destrincharei os segredos atravancados no meu fóssil e, de quebra, no fóssil do Trilheiro da Costa Negra, <name>.", completion = "Mazá, &lt;name&gt;!\n\nAgora, ligeiro, vamos ver se me atino a usar esta pedra para decifrar os poderes dos fósseis.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nLâmina RaraLasca-crânio\n\nVocê também receberá:\n\n"},
[944] = {title = "Glaive do Mestre", objective = "Reúna informações, depois use o Frasco de Vidência para criar uma Bacia de Vidência. Use a bacia para falar com Onu.", description = "Acho estranho que as nagas estejam aqui, na Costa Negra. Mas ainda mais estranho... é o Martelo do Crepúsculo. Esse culto venera os muito, muito antigos senhores da terra. Senhores há muito derrotados.\n\nUm desses antigos senhores foi derrotado na Glaive do Mestre, que fica ao sul daqui.\n\nVá até lá e procure mais sectários do Martelo do Crepúsculo.\n\nLeve este frasco de vidência. Depois de procurar na Glaive do Mestre, use o frasco para criar uma bacia de vidência, que você vai usar para falar comigo.", progress = nil, completion = "O Martelo do Crepúsculo está na Glaive do Mestre?\n\nÉ um problema.\n\nO senhor antigo que foi empalado pela glaive já morreu há muito tempo, mas é possível que alguns traços de poder tenham ficado para trás.\n\nO Martelo do Crepúsculo deve estar buscando esse poder.", rewards = "Você receberá:\n\n"},
[945] = {title = "A fuga de Therylune", objective = "Ajude Therylune a escapar e, depois, conte a Therysil, no Altar de Aessina, que a irmã dela está a salvo.", description = "Socorro!\n\nEu estava andando perto da Glaive do Mestre e esses sectários imundos me cercaram. Que bom que eu sou boa em me esconder!\n\nVocê pode me ajudar a sair daqui? E eu preciso dar notícias a Therysil, a minha irmã, dizendo que eu estou bem. Ela está no Vale Gris, mais pro sul, no Altar de Aessina.", progress = nil, completion = "Minha irmã estava aonde??? A Therylune não se importa de se sujar, mas mesmo assim! É longe demais para uma perambulada só, e a Glaive do Mestre é um lugar macabro.\n\nBem, obrigada, &lt;name&gt;. Foi muito gentil da sua parte me contar que ela está bem.", rewards = "Você receberá:\n\n"},
[947] = {title = "Cogumelos da caverna", objective = "Leve 5 Galhos de Piolho e 1 Cogumelo-cicuta para Barithras Sombraluna, em Auberdine.", description = "Estou criando uma poção que requer cogumelos raros, cogumelos que só crescem em uma determinada caverna. A caverna fica por trás das Quedas do Fontescarpa, a leste e ligeiramente ao norte, ao longo das montanhas.\n\nEu mesmo iria até lá, mas fui aconselhado pelo Bosque dos Ancientes a ficar longe daquele lugar. Nossos veneráveis aliados sentem que a caverna é o esconderijo de um novo mal na Costa Negra.\n\nPor favor, <name>, colha os cogumelos para mim. Aproveite, então, para explorar a caverna e ver se confirma o receio dos Ancientes.", progress = "Você trouxe meus cogumelos, <name>? Já esteve na caverna?", completion = "Muito obrigado, &lt;name&gt;. Que ótimos espécimes de cogumelos você trouxe!\n\nQuando esteve nas Quedas do Fontescarpa, você encontrou alguma coisa que confirmasse o receio dos Ancientes?\n\nOs Ancientes são sábios, mas eu esperava que, dessa vez, estivessem errados.", rewards = "Você receberá:\n\nManto da Rajada FlorestalCogumelo de Bolinhas Vermelhas"},
[948] = {title = "Onu", objective = "Fale com Onu no Bosque dos Ancientes.", description = "Onu, um Anciente da Tradição no Bosque dos Ancientes, soube da sua jornada às Quedas Vertentes e deseja falar com você. O bosque fica ao sul, perto das montanhas.\n\nAncientes costumam ser pacientes e sábios, <name>, mas se Onu busca sua opinião acerca do presenciado na queda d'água, creio que seja urgente.", progress = nil, completion = "&lt;name&gt;, você chegou.\n\nBom.\n\nTemos assuntos... a discutir, você e eu.", rewards = "Você receberá:\n\n"},
[949] = {title = "O Acampamento do Crepúsculo", objective = "Encontre uma pista no acampamento do Martelo do Crepúsculo, que fica na Glaive do Mestre.", description = "Não se sabe o propósito do Martelo do Crepúsculo na Glaive do Mestre, mas deve ser sinistro.\n\nVasculhe o acampamento na Glaive... Encontre alguma coisa que nos dê uma pista sobre as intenções dele.\n\nÉ importante sabermos disso antes de traçarmos um plano.", progress = nil, completion = "Este livro foi escrito à mão num idioma antigo e foi ilustrado por um artista de grande perícia.", rewards = "Você receberá:\n\n"},
[950] = {title = "A volta a Onu", objective = "Leve para Onu os Rabiscos Malucos.", description = "Perscrutar o Tomo do Crepúsculo é enlouquecedor. Num segundo o texto começa a oscilar e dançar, e as belas iluminuras se transformam em formas grotescas.\n\nArrancar uma página de rabiscos malucos é tudo o que é possível fazer antes de desfalecer.", progress = "<name>. Você voltou.", completion = "Uma magia caótica, primitiva, envolve esse pergaminho de rabiscos. Sinto o trabalho dos antigos nele.\n\nTorçamos para que ele revele o objetivo do Martelo do Crepúsculo na Costa Negra...", rewards = "Você receberá:\n\n"},
[951] = {title = "Relíquias de Mathistra", objective = "Leve 6 Relíquias de Mathistra para Onu no Bosque dos Ancientes.", description = "O pergaminho que você achou na Glaive do Mestre é uma página de um texto antigo... escrito por elfos! Está com maldições pesadas e maculado pela corrupção do Martelo do Crepúsculo, mas parece que a seita está usando magia élfica antiga para trazer seus antigos mestres de volta ao nosso mundo.\n\nVá às Ruínas de Mathistra, a nordeste, e procure relíquias antigas no meio da grama e das pedras ruídas. Estudando-as podemos adivinhar como o Martelo do Crepúsculo pretende explorar a magia dos elfos.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nClava de Madeira DuraEspada do Mateiro\n\nVocê também receberá:\n\n"},
[952] = {title = "Bosque dos Ancientes", objective = "Entregue a mensagem de Fandral para Onu no Bosque dos Ancientes, que fica na Costa Negra, ao sul de Auberdine.", description = "Se você teve tempo para sair levando recados para a Árvore dos Oráculos, devo insistir para que você entregue uma mensagem ao Bosque dos Ancientes na Costa Negra, ao sul de Auberdine.\n\nÉ bem provável que você precise viajar de hipogrifo, mas tenho plena confiança nas suas capacidades. Leve isto aqui para Onu, o Anciente da Tradição. Ele está esperando notícias minhas, assim como eu estava esperando notícias da Clareira do Oráculo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[953] = {title = "A queda de Ameth'Aran", objective = "Estude as tabuletas que contam sobre Ameth'Aran e sua queda, depois fale novamente com a Sentinela Tysha Lamiluna na Costa Negra.", description = "A leste, você vai encontrar as ruínas de Ameth'Aran. O lugar agora é habitado pelos espíritos inquietos dos Altaneiros que um dia viveram dentro de seus muros, mas outrora foi um lugar em que os servos de Azshara praticavam livremente sua poderosa magia.\n\nFui enviada para explorar as ruínas e encontrei duas grandes tabuletas, que contam a história e a queda de Ameth'Aran. Enquanto lia as runas, fui acossada pelos espíritos e fugi.\n\nPor favor, se puder, entre nas ruínas e decifre as tabuletas por mim.", progress = nil, completion = "Temos poucos registros do período da Guerra dos Antigos, principalmente da época em que ocorreu a destruição da Nascente da Eternidade. O que não é de causar surpresa, dadas as revoltas e os eventos cataclísmicos que ocorreram então.\n\nObrigada, &lt;name&gt;. Graças à sua ajuda, meu trabalho aqui foi concluído e poderei entregar um relatório completo ao Círculo.", rewards = "Você receberá:\n\n"},
[954] = {title = "Bashal'Aran", objective = "Encontre a fonte da estranha aura azul nas ruínas de Bashal'Aran.", description = "As ruínas de Bashal'Aran a leste estão infestadas de lacaios demoníacos. Os duendes e sátiros que se instalaram no local se alimentam das energias mágicas da área, e os poderes deles aumentam com a exposição continuada.\n\nMesmo com isso, notei que eles não se aproximam de um determinado santuário. No alto de um pequeno penhasco no lado oeste das ruínas, uma estranha aura azul permeia o lugar... Deve haver uma explicação para a relutância dos demônios.\n\nEu gostaria que você investigasse isso.", progress = nil, completion = "Ahh... A que eu deveria a honra mais do que especial de ter um &lt;race&gt; como você como companhia? Realmente diz muito dos meus companheiros atuais — sem ofensa aos meus anfitriões, os nobres capetas e sátiros — que a sua presença possa ser considerada grandes coisas.\n\nMas, por favor, não deixe minha língua rude expulsá-lo daqui. Na verdade, faz muitos anos, décadas até, que não tenho uma companhia decente.", rewards = "Você receberá:\n\n"},
[955] = {title = "Bashal'Aran", objective = "Consiga 8 Brinco de Capeta para Astérion em Bashal'Aran.", description = "Se eu contasse a história de minha vida, sem dúvida ela iria além do limite da sua paciência. Digamos apenas que minha vida foi longa e sofrida, e que essa forma espectral talvez seja o pior de todos os tormentos.\n\nEstou preso aqui por meios mágicos. Embora minhas palavras pareçam ingênuas, eu lhe garanto que ficarei indescritivelmente grato se você me ajudar a descobrir os meios de meu encarceramento. Um selo me agrilhoa, e examinando os brincos dos duendes e capetas eu posso encontrar alguma pista dele.", progress = "Quando eu estiver com os brincos, lançarei o feitiço em busca do selo que me prende. Há séculos eu penso na liberdade que a destruição do selo me traria... Talvez esses séculos tenham cobrado seu preço e minha mente precise se recuperar...", completion = "Entendo... Os capetas de Bashal'Aran não têm o que eu procuro... mas eles tiveram contato com o selo recentemente. Recentemente... Sim, pelos seus padrões, não pelos meus. O que é recente para mim adentra o véu do passado até... quase uma outra Era...", rewards = "Você receberá:\n\n"},
[956] = {title = "Bashal'Aran", objective = "Obtenha o Selo Antigo de Pedra-da-lua e leve-o para Astérion em Bashal'Aran.", description = "Se os capetas tiveram contato com o selo que me aprisiona, tenho minhas suspeitas quanto ao motivo. Sem dúvida o selo está em posse do sátiro que os lidera.\n\nSinto que essa é a verdade, <name>. Um dos sátiros deve estar com ele. Se conseguisse obtê-lo, você me deixaria tão perto de transpor as barras de minha prisão que eu iria às lágrimas.", progress = "Os pilares deste santuário são como as barras de uma prisão para mim, <name>. A força que me resta não pode quebrá-los, e a magia que domino não é capaz de destruí-los...\n\nPor mais de mil anos eu os observo, imaginando... Se eu durar mais que as pedras, estarei livre? Ou serão barras invisíveis a me aprisionar, então...", completion = "É... É até difícil acreditar que tenho em mãos aquilo que me encarcerou por tanto tempo. Não percamos tempo, &lt;name&gt;. Quando o selo for destruído, poderei andar livre pelas florestas dessa terra novamente.", rewards = "Você receberá:\n\n"},
[957] = {title = "Bashal'Aran", objective = "Destrua o Selo Antigo de Pedra-da-lua na chama ancestral em Ameth'Aran, depois fale novamente com Astérion em Bashal'Aran.", description = "O selo que enforma minha prisão foi habilidosamente criado por um dos mais poderosos Altaneiros. Em Ameth'Aran, as ruínas ao sul que são exatamente iguais a estas, perdura ainda hoje uma chama ancestral, de cor azul. O selo pode ser destruído por ela.\n\nTenha cuidado quando estiver lá, <name>...", progress = nil, completion = "Eu estou livre, &lt;name&gt;! Agora posso ver com meus próprios olhos as mudanças em nosso mundo... Eu sabia apenas um pouco daqui, algo de acolá. E pensar que a última vez que caminhei em liberdade, a Nascente ainda estava de pé e os Altaneiros se reuniam na corte de Azshara, nossa amada rainha.\n\nSinto que Athrikus, meu antigo mestre e responsável pela minha prisão, ainda vive... Cedo ou tarde, a desesperança em meu interior dará lugar à sede de vingança.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nColete do ExploradorPerneiras do VagabundoVarinha Élfica\n\nVocê também receberá:\n\n"},
[958] = {title = "Ferramentas dos Altaneiros", objective = "Resgate 7 Relíquias dos Altaneiros para Trovejius Tecevento, em Auberdine.", description = "Tenho uma tarefa para você, <class>.\n\nSabe onde ficam as ruínas de Ameth'Aran? Elas estão no lado leste da estrada principal, em direção ao sul. Já foram a casa de Altaneiros poderosos, agora são apenas um testemunho da destruição causada por seus malfeitos.\n\nAs Sentinelas me disseram que os espíritos dos Altaneiros ainda persistem por lá, e estão de posse de seus antigos implementos mágicos. Precisamos nos apropriar dessas relíquias para poder destruí-las.", progress = "Se ao menos as ferramentas da ruína deles tivessem sido destruídas no cataclismo que destruiu nosso mundo... Seja como for, precisamos fazer o possível e o impossível para evitar que os horrores do passado se repitam no futuro.", completion = "Pode ser que minha imaginação esteja me pregando peças, mas quase posso sentir a mácula da imunda magia Altaneira nessas relíquias. Vou cuidar para que sejam destruídas e impedir de vez que seu poder maligno adormecido seja libertado.", rewards = "Você receberá:\n\nManilhas de Hera"},
[959] = {title = "Encrencas nas docas", objective = "O Operador de Grua Mafuá, em Vila Catraca, quer que você recupere a garrafa de Xerez de 99 Anos com Maluc Insano, que se refugiou na Caverna Ululante.", description = "Ops!\n\nDá para ver que tive um pequeno contratempo, né? O carregamento matinal da Angra do Butim era muito grande. Muito grande mesmo! Nunca vi tantas caixas no mesmo lugar. Comecei a descarregar, pegando um pouco mais de carga do que de costume, para dar um adianto no serviço, mas acabei pegando mais do que devia. Aí tudo começou a desmoronar.\n\nEu estava mais perdido que cego em tiroteio, mas vi aquele maldito do Maluc Insano fugindo com uma garrafa de xerez de 99 anos. Fui atrás dele até a Caverna Ululante, mas eu só entraria lá se tivesse pirado na batatinha.\n\nSe você tiver nervos de aço, pode ir atrás dele.", progress = "O Mestre de Doca está no meu pé desde que o Maluc Insano fugiu com o xerez de 99 anos. Acho que a garrafa era um presente do barão Rezingada para o Gasganete.\n\nSe você tiver a coragem e a paciência de procurar o Maluc Insano e trazer o xerez de volta, eu lhe arrumo uma bela recompensa.", completion = "Ah, gostei de ver! Eu já estava quase no olho da rua! Vou levar isso aqui para o Gasganete agora mesmo.\n\nValeu mesmo, &lt;name&gt;. Mandou ver, cara!", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[960] = {title = "A meditação de Onu", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nFrasco de Vidência"},
[961] = {title = "A meditação de Onu", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nFrasco de Vidência"},
[962] = {title = "Ofídeas", objective = "A Boticária Zaqueia, no Penhasco do Trovão, quer que você colete 10 Ofídeas.", description = "A Real Sociedade de Boticários, que fica na grande Cidade Baixa, em Lordaeron, me mandou aqui para fazer um serviço muito específico, <name>. Talvez você possa ajudar a mim e, consequentemente, à Dama Sombria em nossos esforços pelo progresso dos Renegados.\n\nNão faz muito tempo, comecei a estudar um raro espécime da flora chamado Ofídea. Acredito que, em grandes quantidades, essa planta pode ter grande potencial.\n\nInfelizmente a Ofídea só pode ser encontrada nos recônditos mais sombrios da Caverna Ululante, uma perigosa rede de cavernas que fica nos Sertões.", progress = "Estou ansioso para ver se você conseguirá coletar ofídeas em quantidade suficiente na Caverna Ululante. Muitos partiram nessa empreitada, mas ninguém retornou.", completion = "Ah, que espécimes esplêndidos. Bom trabalho, &lt;name&gt;.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nLuvas de Boticário"},
[963] = {title = "Amor eterno", objective = "Liberte o espírito de Anaya Correalba e leve seu pingente para Cerellean Garralva em Auberdine.", description = "Após as batalhas da Nascente da Eternidade, ouvi que Ameth'Aran fora destruída e seu povo perecera, inclusive Anaya, meu grande amor.\n\nEu jamais pensei que, milhares de anos depois, as lembranças de Anaya ainda assombrariam meus sonhos. Vagando pela Costa Negra em estado de estupor, vi-me nas ruínas de Ameth'Aran... onde me deparei com o espírito assolado de minha amada.\n\nEla tem de ser libertada, mas me falta a coragem para fazê-lo. O espírito dela tem de ser destruído.", progress = "Com grande pesar no coração, entrei em hibernação com o Shan'do Tempesfúria e, durante os mil anos que dormi, convivi com o pesar em meus sonhos.", completion = "Obrigado, &lt;name&gt;. Talvez o melhor fosse ser eu a ter a coragem de libertá-la. Mas, mesmo após milhares de anos, eu não seria capaz de erguer a mão contra minha amada.\n\nAgora deixe-me aqui, entregue ao meu sofrimento...", rewards = "Você receberá:\n\nLágrima de Luto"},
[964] = {title = "Fragmentos de Ossos", objective = "Leve 15 Fragmentos de Ossos para o Boticário Trépido no Baluarte, que fica nas Terras Pestilentas Ocidentais.", description = "A chave para Scolomântia é chamada de chave-mestra. Ela precisa ser forjada a partir dos restos de um esqueleto — de vários, na verdade — e só pode ser enrijecida pelo metal mais poderoso dentro de um molde adequado. Um sinete de poder de um ser que consegue abrir o portal para Scolomântia naturalmente vai garantir o funcionamento da chave.\n\nMas vamos por partes, <name>. Precisamos dos fragmentos de ossos para forjar a chave. Os esqueletos dentro de Andorhal devem fornecer o que precisamos, mas os que estão fora dos muros também podem servir.", progress = "Mate muitos e mate rápido, <class>. Vamos precisar de um suprimento decente de fragmentos de ossos adequados para produzir a camada externa da chave. Eles não chamam de chave-mestra à toa, né?", completion = "Isso vai servir, &lt;name&gt;. É minha vez agora, eu vou imbuir esses aqui com uma têmpera adequada que vai permitir que sobrevivam ao intenso processo de forja por que eles vão ter que passar.\n\nA próxima etapa é conseguir um molde adequado para a chave. Conheço um ferreiro goblin em Tanaris que, no passado, fez moldes para outros itens macabros. Ele trabalha para aqueles para quem os goblins costumam trabalhar: quem paga mais.", rewards = "Você receberá:\n\n"},
[965] = {title = "A Torre de Althalaxx", objective = "Encontre Balthule Umbrataque perto da Torre de Althalaxx, que fica na Costa Negra.", description = "Salve, jovem <race>. Sou Elissa Brisastral e é meu dever proteger Auberdine contra o mal.\n\nPara tanto, mandei Balthule Umbrataque a nordeste para investigar ocorrências estranhas nos arredores da Torre de Althalaxx.\n\nEle já devia ter voltado. Fico preocupada de que ele possa ter sido vítima de um perigo inesperado na floresta. Ficaria muito grata se você o encontrasse e cuidasse para ele ficar bem.", progress = nil, completion = "Elissa mandou você? É muito bom te ver, então. Tenho notícias perturbadores a relatar e não conseguia achar uma maneira de comunicá-las.", rewards = "Você receberá:\n\n"},
[966] = {title = "A Torre de Althalaxx", objective = "Colete 4 Pergaminhos Gastos para Balthule Umbrataque, perto da Torre de Althalaxx.", description = "Um grupo de bruxos se estabeleceu dentro e nos arredores da torre. Eu teria regressado antes a Auberdine para relatar o ocorrido a Elissa, mas fiquei preocupado de que acontecesse alguma coisa entanto eu estivesse fora.\n\nDinis suspeitava que um grupo como esse estivesse se reunindo na torre, mas não sabia a razão.\n\nAlguns pedaços de pergaminho carregados pelos bruxos chegaram até as minhas mãos, mas preciso de mais para completar o quebra-cabeça. Meu conselho: não entre na torre, porque os bruxos de lá são muito mais poderosos.", progress = "Conseguiu encontrar mais pergaminhos? Ao juntar mais deles, eu consigo obter um quadro mais abrangente do culto.", completion = "Meus agradecimentos, &lt;name&gt;! Isso aqui deve lançar alguma luz sobre essa reunião de bruxos...\n\nHum... Seita da Linhagem das Trevas... Nunca ouvi falar desse grupo antes. Sem conhecer a história deles, é impossível descobrir quais são os planos deles.\n\nNão podemos perder mais tempo. Dinis precisa ser alertado imediatamente.", rewards = "Você receberá:\n\n"},
[967] = {title = "A Torre de Althalaxx", objective = "Entregue a carta de Balthule a Dinis, o Purificador, na Floresta do Vale Gris.", description = "Meu mestre, Dinis, o Purificador, é um paladino que graciosamente nos ofereceu sua ajuda na defesa de nossas florestas contra as forças dos demônios e dos mortos-vivos. Ele me ensinou tudo o que sei sobre a Luz Sagrada e a arte da guerra.\n\nDinis precisa ser informado imediatamente sobre as operações da seita.\n\nVocê vai encontrá-lo bem ao sul daqui, no Entreposto de Maestra, que fica na Floresta do Vale Gris. Não demore, pois a ameaça da Linhagem das Trevas cresce a cada minuto.", progress = "O que você quer comigo, <class>?", completion = "Permita-me dizer que você me parece bem-equipado demais para um mensageiro. Acho que Balthule queria ter certeza de que esta carta chegaria às minhas mãos. Vamos ver o que ele tem a dizer...\n\nQue notícia preocupante! Quando mortos-vivos ou forças demoníacas surgem por aqui, eu ajudo as Sentinelas a destruir tais inimigos.\n\nDe início, estranhei um pouco o comportamento dos elfos noturnos, mas hoje eu os respeito demais como aliados.", rewards = "Você receberá:\n\n"},
[968] = {title = "Os Poderes de Baixo", objective = "Leve o Livro: Os Poderes de Baixo para Gerrig Agarrosso na Caverna Esquecida, em Altaforja.", description = "Embora muito gasto e arranhado, o título do livro ainda está legível na capa:\n\nOs Poderes de Baixo\n\nO livro traz uma lista de nomes impronunciáveis com títulos agourentos, métodos de adoração e sacrifícios preferenciais... muitos dos quais envolvem seres vivos e humanoides.\n\nNa contracapa está escrito: \"Runas e Ruínas de Agarrosso, Caverna Esquecida, Altaforja. Proprietário: Gerrig Agarrosso\".\n\nSe Agarrosso vende livros como esse, talvez ele se interesse em comprar de volta.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[969] = {title = "Que a sorte esteja contigo", objective = "Colete 10 Lascas de Friomalho para Mandingueira Mau'ari, em Visteterna.", description = "Só de olhar pra tu dá pra saber, tu quer fazer um patuá da sorte. Mau'ari tudo sabe!\n\nIndo para o sul tem um cânion, e gigantes fazem um gelo sólido. Os gigantes costumam carregar o gelo com eles, mas sempre dá pra tu recolher umas lascas no cânion. Essas lascas tem um reflexão poderosa.\n\nSe tu me trouxer dez lascas de friomalho, eu te dou meu patuá da sorte.", progress = "Traz as lascas que eu te conto mais.", completion = "Boa! Vou fazer o patuá pra tu. Espera aí!", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[970] = {title = "A Torre de Althalaxx", objective = "Colete uma Gema Anímica Faiscante e fale novamente com Dinis, o Purificador, no Entreposto de Maestra.", description = "A carta de Balthule é atroz. A Seita da Linhagem das Trevas é uma pedra no meu sapato e quero me livrar logo deles. Já enfrentei alguns exemplares dessa escória da Linhagem das Trevas a nordeste daqui, em Ordil'Aran. Um deles possui uma gema anímica que, acho eu, guarda os segredos do poder da seita.\n\nTraga a gema para mim que eu conseguirei decifrar os segredos que ela guarda.", progress = "Já conseguiu a gema anímica?", completion = "Hum, esta pedra da alma foi encantada por uma magia sombria. Vamos ver o que está preso dentro dela.", rewards = "Você receberá:\n\n"},
[971] = {title = "Conhecimento nas Profundezas", objective = "Leve o Manuscrito de Lorgalis para Gerrig Agarrosso na Caverna Esquecida, em Altaforja.", description = "Talvez você já saiba que eu coleciono lendas. Lendas antigas. Lendas poderosas. Lendas que abrem portas e lendas que podem acordar Aqueles Que Dormem.\n\nCorrem rumores sobre uma lenda, o Manuscrito de Lorgalis, que estaria no fundo das Profundezas Negras no Vale Gris. Lá é o antigo lar de elfos que há muito já se foram. Elfos que eram detentores de grande conhecimento antes que sua cidade virasse ruínas.\n\nProcure o manuscrito nas Profundezas Negras. Faça isso por mim e eu nunca vou me esquecer. Nem mesmo depois... do fim dos dias.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nAnel do Encorajamento"},
[972] = {title = "Sapta da Água", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nSapta da Água"},
[973] = {title = "A Torre de Althalaxx", objective = "Leve o Tomo de Ilkrud Magthrull para Dinis, o Purificador, no Entreposto de Maestra.", description = "Ilkrud Magthrull. Sim, já ouvi falar dele, é um bruxo orc poderoso que reside no Santuário Cicatriz de Fogo, um lugar profundamente maligno na parte sudoeste do Vale Gris.\n\nEle pouco tinha feito além de incomodar até agora, por isso não me ocupei dele antes, mas parece que a hora chegou.", progress = "Você parece meio abalado, <name>. Está tudo bem?", completion = "Você fez algo grandioso hoje, &lt;name&gt;. Pode se orgulhar de si mesmo por ter enfrentado um bruxo tão poderoso.\n\nAgora, vamos ver o que os escritos de Ilkrud têm a nos ensinar.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nAnel do CleroCajado do Purificador\n\nVocê também receberá:\n\n"},
[974] = {title = "Morno... morno... quente!", objective = "Gualter, na Cratera Un'Goro, quer que você encontre a área mais quente do Pico Penacho de Fogo.\n\nAo se deparar com um ponto de calor, clique com o botão direito no termômetro para medir a temperatura. Procure até encontrar o mais quente de todos.", description = "As águas termais daqui me deixam com uma pulga atrás da orelha. De onde vem o calor?\n\nOlhando como os lagos ficam pertinho do Pico Penacho de Fogo, eu fico me perguntando se o vulcão tem alguma coisa a ver com isso. Eu criei até um termômetro pra checar a temperatura do vulcão e achar o ponto mais quente. Ele mede a temperatura em graus Gualtercius!\n\nToma aqui. Vai lá no pico e mede a temperatura de todos os pontos de calor que achar!", progress = "Eu até podia ir eu mesmo, mas... os elementais de fogo de lá me dão medinho!", completion = "Arrá, olha aí. Dá pra ver que tem sim uma relação entre a temperatura do vulcão e as águas quentes daqui. Tenho que dar uma olhada nas equações pra conferir a precisão, mas parece que a distância e a taxa de transferência de calor... Hmm...", rewards = "Você receberá:\n\n"},
[975] = {title = "Provisões de Mau'ari", objective = "Fale novamente com a Mandingueira Mau'ari em alguns instantes para receber o patuá da sorte.", description = "Agora, antes de te dar o patuá, vou explicar pra tu como funciona.\n\nSe levar o patuá contigo, tu vai ver que alguns itens caem das criaturas com quem você luta. Se não estiver com ele, tu não vai encontrar nada. Sacou?\n\nEsses itens especiais se chamam E'ko. Se encontrar algum, traz pra Mau'ari aqui que ela te dá uma ajuda.\n\nEspera aqui enquanto eu termino o patuá.", progress = nil, completion = "Aqui está, &lt;name&gt;. Não se esqueça, tu precisa levar o patuá contigo. Se eu fosse você, voltava lá pra floresta pra ver o que mais dá para encontrar.", rewards = "Você receberá:\n\nProvisões de Mau'Ari"},
[976] = {title = "Suprimentos para Auberdine", objective = "Fale com Dinis, o Purificador, no Entreposto de Maestra, após escoltar Firo pela Floresta do Vale Gris.", description = "Dinis me pediu para entregar suprimentos e algumas correspondências para a sentinela Brisastral, em Auberdine, mas eu não quero viajar sozinho. Há inimigos perigosos em ação por estas matas, e eu não posso arriscar que meu carregamento caia em mãos erradas.\n\nMas, se você e mais alguns pudessem fazer a minha escolta, eu poderia sair imediatamente. Com a sua proteção, com certeza vamos conseguir levar esse carregamento para Auberdine em segurança.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nLanterna do Brilho EternoGuarda-peito de Kor\n\nVocê também receberá:\n\n"},
[977] = {title = "Eu yeti falar uma coisa...", objective = "Colete 2 Chifres Impolutos de Yeti para Umi Zombarruga em Visteterna.", description = "Já tenho tudo que é preciso para cobrir as engrenagens mecânicas, agora só preciso de mais uma coisinha. Duas, na verdade. Dois chifres.\n\nSão os patricarcas e matriarcas cardo de gelo que têm os maiores chifres; é deles que eu preciso. Esse tipo de yeti habita uma caverna a sudeste. Tem muitos dos animais lá, você vai encontrar os chifres rapidinho.\n\nE não se esqueça, eu preciso dos melhores chifres. Não quero nada rachado ou quebrado, entendeu?", progress = "Estou acabando de prender a pele, <name>. Traga logo os chifres, por favor!", completion = "Mal posso esperar pra mostrar pros meus amigos! Agora só deixa eu colocar aqui...\n\nPerfeito!", rewards = "Você receberá:\n\n"},
[978] = {title = "Coruscantes Lunamarca", objective = "Colete 10 Penas Lunamarca de Hibérnia, depois fale novamente com Erelas Ambarcéu, na Vila de Rut'theran.", description = "As penas dos coruscantes das Terras Agrestes parecem conter traços de qualidades mágicas. Mas não creio que os animais se aproveitem desses poderes, simplesmente é algo inerente à espécie, parte da essência.\n\nDurante meus estudos, ouvi falar de uma grande população que habita as terras nevadas de Hibérnia. Será que essas criaturas têm o mesmo poder? Me ajude a encontrar uma resposta, <name>. Vá até Hibérnia e colete as penas que caíram ao chão, depois fale novamente comigo.", progress = "De onde será que esses poderes se originaram? Acho que estamos no caminho certo para descobrir a resposta, <name>.", completion = "Agora posso comparar as penas... Vamos ver...", rewards = "Você receberá:\n\n"},
[979] = {title = "Encontrando Ranshalla", objective = "Encontre Ranshalla em Hibérnia oriental.", description = "Sim, sim, parece que essas penas contêm o mesmo encantamento mágico que notei antes... Embora eu não saiba ao certo o que podemos encontrar, peço que continue procurando respostas, <name>.\n\nUma colega viajou recentemente para Hibérnia para observar os coruscantes de lá em seu hábitat natural. Vá até lá se encontrar com ela e veja se ela já conseguiu descobrir alguma coisa.", progress = nil, completion = "Olá! Ah, o Eralas mandou você? Ótimo. Eu estava prestes a ir para as cavernas neste exato momento, e uma companhia cairia bem.", rewards = "Você receberá:\n\n"},
[980] = {title = "As novas fontes", objective = "Vá a Hibérnia e fale com Bruna Covaneve.", description = "Já ouvi falar sobre outras fontes termais em um lugar chamado Hibérnia, bem longe ao norte daqui. A parada estranha é que não tem vulcões perto delas. Interessante, sei disso.\n\nBem, se você quiser descobrir mais, vá a Hibérnia e fale com minha amiga. O nome dela é Bruna Covaneve e tu vai achá-la junto às fontes termais de lá.\n\nValeu novamente por me ajudar com o termômetro! Até logo!", progress = nil, completion = "Olá, &lt;name&gt;. Bah, sim, o que Gualter te disse é verdade, tchê...", rewards = "Você receberá:\n\n"},
[981] = {title = "A Torre de Althalaxx", objective = "Fale com Dinis, o Purificador, no Entreposto de Maestra.", description = "O grande trabalho que você fez merece ser bem recompensado. Fale novamente com Dinis e diga a ele que Athrikus Narassin encontrou a morte pelas suas mãos, e que a Seita da Linhagem das Trevas foi desbaratada e destruída.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nCulotes PiosGolpe de Seraph\n\nVocê também receberá:\n\n"},
[982] = {title = "Nas profundezas do mar", objective = "Recupere o Baú do Aurora de Prata e o Baú do Véu das Brumas para Gorbold Manácero em Auberdine. Os dois itens podem ser encontrados nos destroços dos navios, a norte da vila.", description = "No litoral da Costa Negra, a norte de onde estamos, há dois navios naufragados - o Aurora Prateada e o Véu das Brumas. Tempos atrás, os dois foram atacados por esses murlocs lazarentos quando cruzavam o vasto mar em direção a Auberdine. Agora estão no fundo do mar, como troféus desses djanhos.\n\nOs dois capitães afundaram junto com as embarcações, e seus diários de bordo e outros pertences ainda estão lá nos destroços, guardados em baús. Quero que tu recupere tudo para nós, vivente, pois é algo valioso por demais para os tripulantes que sobreviveram.", progress = "Os capitães desses navios eram elfos noturnos de valor e merecem um destino melhor, tchê. Pues, creo que cuidar dos pertences deles é a melhor maneira de permitir que seus espíritos descansem em paz.", completion = "Has prestado um gran serviço ao povo de Auberdine, guri. Estes pertences vão ser muy bem destinados.\n\nToma isto, é para ti. É justo regalar alguém que mostrou tanta bravura para fazer o certo, no más.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nLuvas Mina d'ÁguaManoplas da Pegada Nodosa\n\nVocê também receberá:\n\n"},
[983] = {title = "Caixazorra 827", objective = "Colete 6 Patas de Rastejante e coloque-as na Caixazorra 827.", description = "Ei! Preciso de ajuda com a minha última invenção, a caixazorra. Com ela, você pode falar com pessoas que estão muito longe.\n\nTalvez você já tenha visto, é uma caixa com alavancas. O negócio é que elas precisam de manutenção constante.\n\nCada uma tem um problema diferente, mas eu tomei uma decisão muito inteligente. Coloquei cada uma perto de criaturas que têm as peças necessárias para consertar aquela máquina específica. No momento, é a Caixazorra 827 que tá quebrada. Ela está ao sul de Auberdine, pertinho daqui. Preciso conseguir 6 patas de rastejante para consertar. Pago bem...", progress = "A Caixazorra 827 está imóvel e assustadoramente silenciosa. Uma luz piscante indica que ela precisa de 6 patas de rastejante a serem acondicionadas na caixa.", completion = "Ao colocar as patas de rastejante na máquina, você começa a ouvir as engrenagens se movimentando. A julgar pelos sons metálicos que saem da caixazorra, as patas de rastejante estão sendo colocadas no lugar certo. Logo a máquina começa a zumbir e você ouve uma vozinha chamando de lá de dentro.\n\n\"Alô! Oi, alô! Você consertou! Aqui é o Bangpou falando. Obrigado. É, você não está interessado em consertar outra pra mim?\"", rewards = "Você receberá:\n\n"},
[984] = {title = "Uma grande ameaça?", objective = "Encontre um acampamento de pelursos corrompidos na Costa Negra e fale novamente com Terenthis em Auberdine.", description = "Alguns dos meus irmãos foram salvos de um pelurso corrompido em Teldrassil e jurei dar um fim às atrocidades antes que mais algum dos nossos se ferisse... ou algo pior.\n\nJá percebi alguns indícios de corrupção na Costa Negra, mas ainda não vi sinal de epidemia. Acho que seria lógico continuar a investigação com os pelursos. Gostaria que você encontrasse um acampamento de pelursos e falasse novamente comigo se visse algum sinal de corrupção.", progress = nil, completion = "Que notícia terrível, &lt;name&gt;.\n\nCom os pelursos tão perto de Auberdine, precisamos nos preparar para o inevitável.\n\nMuito obrigado, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[985] = {title = "Uma grande ameaça", objective = "Mate 8 Desbravadores Bosquenero e 5 Vozes-do-vento, depois fale novamente com Terenthis, em Auberdine.", description = "Você já comprovou suas habilidades ao fazer o reconhecimento do nosso inimigo, <name>. Agora, será que é capaz também de lutar contra ele?\n\nNão são todos os aventureiros que preferem o combate direto em vez da furtividade e evasão.\n\nSe achar que está à altura da tarefa, no momento, a maior ameaça ao nosso povo é acampamento de pelursos ao sul de Auberdine. Lá também há alguns membros da tribo Bosquenero. Mate 8 desbravadores e 5 vozes-do-vento e depois venha falar comigo.", progress = nil, completion = "Lave o sangue de suas vestes, &lt;name&gt;, e não se martirize pelo que teve que fazer. Em vez disso, é melhor agradecer. Você combateu a ameaça ao nosso povo aqui em Auberdine, mesmo que Costa Negra ainda esteja em perigo por causa dos efeitos do limo vil.", rewards = "Você receberá:\n\n"},
[986] = {title = "Um mestre perdido", objective = "Encontre 5 Pelagens de Espreitaluna Finas e leve-as para Terenthis, em Auberdine.", description = "<name>, suas habilidades já me ajudaram em minha missão. Será que eu poderia contar com você de novo para ajudar Garratroz e o mestre dele, Volcor? Além disso, posso criar um manto mágico que lhe permitirá passar despercebido pelas criaturas da Costa Negra enquanto procura por ele.\n\nPara criar o manto, precisarei de cinco pelagens finas de um espreitaluna macho ou matriarca para ter material suficiente. Os gatos podem ser encontrados perto do Rio Crenado, ao sul, ou ainda mais ao sul, perto de Vale Gris.", progress = "Não posso em sã consciência permitir que você procure Volcor antes de eu confeccionar esse manto para você.", completion = "Essas vão servir bem, &lt;name&gt;. Vou começar a trabalhar no manto imediatamente.\n\nMe dê um tempo, depois fale novamente comigo.\n\nAh, mais uma coisa: depois de usar o encantamento no manto, você terá suas interações com os outros limitadas. Se você puder evitar, não faça nada além de falar com os outros. Muito mais do que isso poderá dissipar a ilusão.", rewards = "Você receberá:\n\n"},
[990] = {title = "Viagem ao Vale Gris", objective = "Encontre Raene Correlobos, no Vale Gris.", description = "<name>, minha senhora, Raene Correlobos, espera por você na cidade de Astranaar, que fica no Vale Gris. Com a sua ajuda, talvez a gente consiga pelo menos minimizar a corrupção que tomou conta de lá.\n\nSiga para o sul a partir daqui e fique perto da estrada — você vai chegar lá sem grandes problemas se seguir as instruções.", progress = nil, completion = "Ah, um &lt;class&gt; da Costa Negra. Selarin fez bem em mandar você para cá tão rapidamente, &lt;name&gt;. Espero que a sua viagem não tenha sido difícil. Acho que, com sua ajuda, poderemos melhorar as coisas.\n\nEu começaria a visita conversando com outros cidadãos de Astranaar. Com certeza você vai encontrar quem precise de ajuda.", rewards = "Você receberá:\n\n"},
[991] = {title = "O expurgo de Raene", objective = "Procure Teronis no Vale Gris.", description = "<name>, um amigo meu de longa data também está ajudando as Sentinelas aqui no Vale Gris, mas ele ainda não voltou.\n\nEle tinha pistas para encontrar um item que pensava poder retardar os ataques de pelurso ao nosso povo — um bastão criado por um mago maligno, agora morto.\n\nAntes de ir embora, ele disse estar à procura de uma gema para o bastão.\n\nEle comentou que a gema provavelmente estava escondida no santuário do Lago Falathim, na base da montanha a oeste. A gema estava sendo mantida lá antes do local ser invadido.\n\nEncontre meu amigo, <name>, por favor.", progress = nil, completion = "O corpo de Teronis jaz inerte no alto da ilha. Por alguma razão desconhecida, os murlocs o deixaram em paz.\n\nOs cortes profundos no cadáver obviamente vieram das armas e garras dos murlocs.", rewards = "Você receberá:\n\n"},
[992] = {title = "Levantamento das águas de Geringontzan", objective = "Use o treco de coleta vazio junto à poça d'água perto do Posto Silitriste. Depois que você coletar a amostra, leve o treco de coleta cheio para o Topógrafo Sênior Secamolha em Geringontzan.", description = "Temos uma vaga temporária de topógraf<o/a> júnior na Companhia de Saneamento de Geringontzan, se tu tiver a fim!\n\nCom os nômades se apoderando dos nossos poços, precisamos explorar mais fontes de água livres! Há água no deserto, mas só para os malandros o suficiente para examinar por lá primeiro.\n\nPegue o treco de coleta e recolha uma amostra da água junto à poça perto do Posto Silitriste. Está bem à vista dos trolls por lá. Traga o treco de coleta cheio de volta para mim quando você terminar!", progress = "Bem-vindo de volta, Topógrafo Júnior <name>! Já concluiu sua tarefa?", completion = "Como é que é? Você foi surpreendido por algum tipo de mojo ruim rastejante? Ah, isso não é um bom presságio para a Companhia de Saneamento de Geringontzan, não mesmo...\n\nAcho que deveria ter lhe contado sobre os relatórios que falam de umas criaturas estranhas que parecem estar sugando toda a água do deserto. No começo, achei que fosse besteira. Já temos problemas suficientes com os nômades. Pensei que fosse apenas mais uma das peripécias deles. Bom, pelo menos agora sabemos!", rewards = "Você receberá:\n\n"},
[993] = {title = "Um mestre perdido", objective = "Encontre Volcor na Costa Negra e entregue a ele o Manto Encantado de Espreitaluna.", description = "O manto está terminado, <name>. Chegou a hora a hora de encontrar Volcor, e só posso torcer para que ainda haja tempo.\n\nUma vez invocada, a magia do manto não vai durar muito, talvez uns cinco minutos. Você vai ter que saber o melhor momento de usá-la. Sugiro esperar até encontrar Garratroz.\n\nSe ele estava falando a verdade, você encontrará o mestre dele em uma caverna ao sul daqui. Garratroz estará perto da estrada esperando pelo seu sinal. Quando o avistar, acene para ele e ele irá indicar o caminho que levará ao mestre dele.", progress = nil, completion = "Maravilha! Terenthis encontrou alguém para me ajudar.\n\nE veja só, um manto de espreitaluna...&lt;tosse&gt; Ah... que dor... Obrigado, &lt;name&gt;.\n\nConsegui fugir dos pelursos, mas eles me feriram. Me dá só um instante e aí eu posso conversar de novo.", rewards = "Você receberá:\n\n"},
[994] = {title = "Fuga difícil", objective = "Proteja Volcor até vocês alcançarem a estrada, então fale com Terenthis em Auberdine.", description = "Muito bem, <name>, que seja pela força. Vamos sair daqui assim que você estiver pront<o/a>. Vamos tentar seguir pela estrada... Garratroz ainda deve estar lá. Quando chegarmos, podemos nos separar.\n\nTerenthis vai querer ouvir sua versão da história imediatamente, então vá para Auberdine assim que sairmos daqui.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nCilha Constante"},
[995] = {title = "Fuga furtiva", objective = "Fuja da caverna do Pelurso e encontre Terenthis em Auberdine.", description = "Muito bem... se você tem certeza, vamos dar um jeito de sair daqui. Seguimos quando você estiver pront<o/a>.\n\nNão se esqueça, depois de escapar, encontre Terenthis em Auberdine. Vou pra lá por conta própria, porque devo chegar mais rápido que você.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nCalças do Escaravelho"},
[996] = {title = "Botão Selvagem corrompido", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[997] = {title = "A terra de Denalan", objective = "Leve o pacote de Terra Rara para Denalan no Lago Al'Ameth.", description = "Está indo para o sul? Para o Lago Al'Ameth? Então tenho um favor para pedir...\n\nMeu colega Denalan tem um acampamento perto da margem leste do lago, onde estuda e realiza experimentos com a flora de Teldrassil. Ele solicitou um pacote de terras raras de Darnassus que só chegou a Dolanaar recentemente.\n\nPode levar o pacote até ele?", progress = "Você tem algo para mim?", completion = "Ah, chegou! Estou esperando por esta terra rara há bastante tempo. Espero que ainda esteja fresca...\n\nObrigado por me trazê-la, &lt;name&gt;. Você é uma &lt;race&gt; muito generosa.", rewards = "Você receberá:\n\n"},
[998] = {title = "Botão Selvagem corrompido", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[999] = {title = "E os sonhos se tornaram pesadelos", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1000] = {title = "Uma nova fronteira", objective = "Fale com o Arquidruida Hamuul Runa Totem no Platô dos Anciãos, no Penhasco do Trovão, sobre o chamado do Círculo Cenariano para explorar as fronteiras de Kalimdor.", description = "Atenção, heróis!\n\nO Círculo Cenariano está à procura de aventureiros para ajudar na exploração das vastas fronteiras de Kalimdor! Um deserto esquecido pelos deuses e repleto de perigos inimagináveis jaz a oeste de Tanaris e Geringontzan, e o Círculo precisa de representantes honrados e dispostos para defender seus interesses.\n\nInteressados, favor marcar audiência com o Círculo Cenariano. Procurem o Arquidruida Hamuul Runa Totem no Platô dos Anciãos, no Penhasco do Trovão, para mais informações!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1001] = {title = "Caixazorra 411", objective = "Recolha 3 Olhos de Mangual dos Manguais da Costa Negra no fundo do mar, perto da Caixazorra 411.", description = "Lá do fundo da máquina, sai uma vozinha esganiçada.\n\n\"Aqui fala o Xafetim! A próxima caixazorra está ao norte de Auberdine, na praia.\n\nOlha só, o número dessa caixazorra é 411 e vai precisar de três olhos de mangual pra manutenção. Esses bichos vivem no litoral! Os manguais da Costa Negra... *Hic*... Perdão...\n\nAssim que você botar o negócio na caixazorra, ela vai cuspir a recompensa pra fora, que nem da outra vez. *Hic*... O que foi? Não, eu tô ferpeito!\"", progress = "A escotilha da máquina está aberta, e parece que está esperando avidamente alguém colocar três olhos de mangual lá dentro.", completion = "A caixazorra começa a zumbir assim que você deposita os olhos de mangual. Alguns segundos de estática depois, você ouve uma vozinha bem conhecida.\n\n\"Vinho de rastejante é uma delícia...\" *hic*.\n\n\"Como é? Ah, já consertou a caixa? Nossa, você é ligeiro mesmo, hein? Inhaí, que tal consertar mais uma?\"\n\nVocê ouve um barulho que lembra uma taça se enchendo de vinho, seguido por uma bela duma golada.", rewards = "Você receberá:\n\n"},
[1002] = {title = "Caixazorra 323", objective = "Recolha 6 Presas de Espreitaluna e deposite-as na Caixazorra 323.", description = "A voz do Xafetim sai de dentro da caixa, abafada por um barulhão de estática.\n\n\"O número da próxima caixazorra é 323. Ela tá ao norte de Auberdine, só que... cadê a breja? Quê? Ah, fica no meio da estrada, pertim da ponte, não tem como deixar de ver.\"\n\nO Xafetim balbucia algumas palavras e logo depois toma um trago de alguma coisa.\n\n\"Ezze aqui picisa de presa de espreitaluna... uãs seis.\"", progress = "Apesar de ainda não ter depositado as seis presas de espreitaluna na caixa, você tem a impressão de ouvir um ruído de estática e um <race> resmungando. Poderia alguém já ter consertado a máquina?", completion = "A caixazorra de súbito ganha vida, dá para ouvir o barulho das presas sendo esfareladas lá dentro.\n\n“Pfft, olha zó, num é que vozê conseguiu? Pfft, que bela notícia... Então... Pfft... Por que você não xegue teu rumo? Ah! A menos que esteja atrásh de outro servicinho!\"", rewards = "Você receberá:\n\n"},
[1003] = {title = "Caixazorra 525", objective = "Recolha 4 Escalpos Grisalhos dos Ursocardos Grisalhos ao sul de Auberdine e deposite-os na Caixazorra 525.", description = "Por cima do borbulhar e do gargolejar da máquina, ouve-se a voz trêmula do Xafetim.\n\n\"A prózima fica na estrada, beeem ao zul. Num tem erro... fica à beira da estrada... perto duma cerca. Acho que escondi a caixa num matagalho... matagal. Hehehe... isso memo, a caixazorra 525 picisa de escalpos grisalhos, escalpo de ursocardo grisalho. Rapaz, que negoço gostoso. Grisaldo... hehehe...\"\n\nAntes de cortar a transmissão ainda dá para ouvir mais umas goladas. E eis que, de repente, a máquina volta à vida.\n\n\"Traz quatro!\"", progress = "Você ouve uma cantoria vindo de dentro da caixazorra 525.\n\n\"Vinho de rastejante, breja de mangual e cana de espreitaluna eu bebo um jarro todo. Mas é com cerva de ursocardo que eu vou chapar o globo!\"\n\nA escotilha está escancarada, só esperando quatro escalpos grisalhos, repetindo os mesmos versinhos sem parar.", completion = "\"Cê trouxe os escalpos? Que coisa boa... Quê? É, acho que chê já deve ter percebido que eu num preciso dessas coisash na máquina pá falar contigo. Mas eu num menti quando falei que elash tavam com defeito!\n\nAlém de serem ótimas ferramentash de comunicação, as caixazorras também fermentam e destilam bebidash da melhor qualidade. Desculpa eu ter enganado vozê, mas os elvos nodurnos não aprovam muito a venda de bebidas na zidade delesh. Eu botei um negoxinho nessa caixa como agradecimento pelo zeu empenho. Aproveita!\"", rewards = "Você receberá:\n\nSaco de Aniagem do LhogolhóBeberagem Especial de Lhogolhó"},
[1004] = {title = "Uma nova fronteira", objective = "Fale com o Arquidruida Hamuul Runa Totem no Platô dos Anciãos, no Penhasco do Trovão, sobre o chamado do Círculo Cenariano para explorar as fronteiras de Kalimdor.", description = "Atenção, heróis!\n\nO Círculo Cenariano está à procura de aventureiros para ajudar na exploração das vastas fronteiras de Kalimdor! Um deserto esquecido pelos deuses e repleto de perigos inimagináveis jaz a oeste de Tanaris e Geringontzan, e o Círculo precisa de representantes honrados e dispostos para defender seus interesses.\n\nInteressados, favor marcar audiência com o Círculo Cenariano. Procurem o Arquidruida Hamuul Runa Totem no Platô dos Anciãos, no Penhasco do Trovão, para mais informações!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1005] = {title = "Ao encontro dos mais fortes", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1006] = {title = "Ao encontro do mais forte", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1007] = {title = "A estatueta ancestral", objective = "Leve a Estatueta Ancestral para Talen, no acampamento perto da Praia de Zoram.", description = "Vou dar uma tarefa para você, <name>.\n\nEu estava no meu barquinho, deslizando pelas águas sobre as ruínas submersas de Zoram, quando algumas nagas me atacaram. Elas surgiram da água e partiram para cima de mim com aquelas garras imensas! Eu consegui fugir com alguns suprimentos e montei esse arremedo de acampamento.\n\nMas enquanto corria praia afora... acabei perdendo meu maior tesouro.\n\nPor favor, <name>, você iria ao local onde fui emboscado atrás de uma estatueta ancestral? Foi por ela que enfrentei os perigos todos da Praia de Zoram.", progress = "Encontrou a estatueta, <name>?", completion = "Você encontrou o que eu pedi! Obrigado, &lt;name&gt;!\n\nA antiga cidade de Zoram guarda muitos segredos, e essas estatuetas podem revelar muitos deles.", rewards = "Você receberá:\n\n"},
[1008] = {title = "Praia de Zoram", objective = "Entregue 20 Cabeças de Iracauda para Shindrell Fogofugaz em Astranaar.", description = "O mal anda à espreita no litoral a noroeste, na Praia de Zoram.\n\nLá jaz a cidade de Zoram, destruída durante a Cisão e coberta pelas águas. Os elfos noturnos a consideram desaparecida há eras. Desaparecida e quase esquecida.\n\nAgora as nagas voltaram, e ainda não sabemos o porquê. Mas o motivo pouco importa: temos que matar essas diabas e mandá-las de volta para as profundezas, de onde nunca deveriam ter saído!\n\nVolte com a missão cumprida.", progress = "Não podemos deixar que as nagas invadam nossas praias, <name>. É muito importante que você termine sua missão na Praia de Zoram.", completion = "Muito bem, &lt;name&gt;. Seus feitos na Praia de Zoram contra as nagas foram louváveis.\n\nSei que a tarefa não foi fácil, pois nós, elfos noturnos, conhecemos bem a força e a esperteza das nagas. Sabemos disso porque temos uma longa história com essas criaturas.\n\nE é uma história que não quero ver se repetir.", rewards = "Você receberá:\n\n"},
[1009] = {title = "Ruuzel", objective = "Leve o Anel de Zoram para Talen, perto da Praia de Zoram.", description = "Receio que os segredos das estatuetas ancestrais continuem escondidos para sempre, a não ser que você encontre a chave de uma antiga fechadura.\n\nA chave na verdade é um anel, o Anel de Zoram. Ele pertencia aos líderes da cidade antes das ondas da Cisão tomarem conta de tudo.\n\nO anel ficou perdido por muitos anos, mas há pouco tempo as nagas o encontraram.\n\nA líder delas, Ruuzel, habita a ilha na ponta norte de Zoram. Por favor, <name>, consiga esse anel para mim!", progress = "Dizem que o Anel de Zoram é a chave para qualquer fechadura da cidade. Você conseguiu pegá-lo, <name>?", completion = "Você encontrou o anel! Obrigado, &lt;name&gt;. Por favor, aceite isto como pagamento, e saiba que seu feito ficará para sempre em minha memória.", rewards = "Você receberá:\n\nVestes da Antiguidade"},
[1010] = {title = "Cabelo-de-bathran", objective = "Leve 5 Cabelos-de-bathran para Orendil Folharga no Vale Gris.", description = "Tinha uma planta que crescia nas ruínas do Refúgio de Bathran, ao norte. O nome da planta era cabelo-de-bathran, dizem que ela curava os males do espírito.\n\nTem uma criança doente no vilarejo e eu acredito que não seja apenas um mal do corpo. Você iria ao Refúgio de Bathran atrás de um pouco de cabelo-de-bathran? Vou precisar da planta para tratar dessa criança.\n\nO Refúgio de Bathran fica ao norte do Entreposto de Maestra, que é logo a sul da fronteira da Costa Negra.", progress = "Você conseguiu o cabelo-de-bathran, <name>? A menina fica mais fraca a cada hora que passa...", completion = "Ah, você trouxe o cabelo! Agora é só preparar a mistura e rezar para dar certo.\n\nE... tinha Renegados no Refúgio de Bathran? É uma notícia alarmante, um tanto alarmante. E tenho dito...", rewards = "Você receberá:\n\n"},
[1011] = {title = "Moléstias renegadas", objective = "Leve uma Garrafa de Moléstia para Kainet Brisacalma em Cantilenda.", description = "A ala de mortos-vivos da Horda, os Renegados, se infiltrou no Vale Gris e anda à espreita pelas nossas ruínas e cemitérios. Suspeitamos que o objetivo deles seja espalhar doenças pelas terras noctiélficas. Precisamos confirmar se é isso mesmo!\n\nOs Renegados montaram acampamento ao sul daqui, perto do Retiro de Dor'Danil, onde vêm engarrafando suas poções e moléstias. Entre no acampamento à força e roube um frasco. Volte com uma garrafa. Eu vou testá-la para descobrir para que servem.", progress = "Encontrou o acampamento, <name>? Trouxe uma garrafa de moléstia?", completion = "Bom trabalho, &lt;name&gt;. Pronto... é só testar a substância dentro da garrafa, que os objetivos dos Renegados se revelarão!", rewards = "Você receberá:\n\n"},
[1012] = {title = "Druidas insanos", objective = "Mate os druidas Taneel Lenhanegra, Uthil Chamaluna e Mavoris Rasganimbus. Depois fale novamente com Kainet Brisacalma em Cantilenda.", description = "Enquanto pelejava contra os venenos dos mortos-vivos, eu recebi uma visão. Recebi uma visão dos druidas do Retiro de Dor'Danil envenenados... assassinados pelos mortos-vivos! Eles agora são fantasmas errantes, separados de seus corpos e tomados pela loucura.\n\nPara que o espírito dos druidas encontre paz algum dia, você terá que adentrar o Retiro de Dor'Danil e destruir seus outrora tão grandiosos líderes: Taneel Lenhanegra, Uthil Chamaluna e Mavoris Rasganimbus.\n\nDepois que tiver feito isso, volte aqui.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nMarca de Emílio"},
[1013] = {title = "O Livro de Ur", objective = "Leve o Livro de Ur para o Guardião Bel'dugur no Boticarium da Cidade Baixa.", description = "<name>, tem um livro na Bastilha da Presa Negra, o Livro de Ur, que ficaria muito bem na minha coleção. Ur foi um grande mago de Dalaran, antes da chegada do Flagelo. Seus estudos sobre outros mundos são de grande valia para... certos Renegados.\n\nAdentre a Bastilha da Presa Negra e encontre o livro. Traga-o para mim e eu farei com que a Dama Sombria saiba do serviço que você nos prestou...", progress = "Encontrou o livro, <name>?", completion = "Bom trabalho. Este livro dará um belo acréscimo à minha coleção das obras de Ur. O conhecimento do autor era imenso, mas os escrúpulos o impediram de alcançar o poder. Por isso, quando o Flagelo chegou e a força dele foi posta à prova, ele fracassou.\n\nNós, Renegados, não podemos ser fracos se quisermos sobreviver...\n\nSe quisermos conquistar.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas CinzentasBraçadeiras com Fecho de Aço\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[1014] = {title = "Arugal tem que morrer", objective = "Mate Arugal e leve sua cabeça para Dalar Tessalba no Sepulcro.", description = "Arugal continua na Bastilha da Presa Negra. Não temos como transformar Pinhaprata em ponto estratégico para a Dama Sombria enquanto ele estiver vivo.\n\nEu mesmo dissiparei as magias dele, <name>. Mas conto com você para fazer com que Arugal receba a morte que tanto merece.\n\nVá para a Bastilha da Presa Negra e ponha fim nos feitiços de Arugal de uma vez por todas. Traga-me a cabeça daquele feiticeiro degenerado!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nIntelecto Arcano\n\nVocê receberá:\n\nSelo de Sylvana"},
[1015] = {title = "Uma nova fronteira", objective = "Fale com o Arquidruida Fandral Guenelmo, no Enclave Cenariano de Darnassus, sobre o chamado do Círculo Cenariano para explorar as fronteiras de Kalimdor.", description = "Atenção, heróis!\n\nO Círculo Cenariano está à procura de aventureiros para ajudar na exploração das vastas fronteiras de Kalimdor! Um deserto esquecido pelos deuses e repleto de perigos inimagináveis jaz ao oeste de Tanaris e Geringontzan, e o Círculo precisa de representantes honrados e dispostos para defender seus interesses.\n\nInteressados, favor marcar audiência com o Círculo Cenariano. Procurem o Arquidruida de Darnassus, Fandral Guenelmo, para mais informações!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1016] = {title = "Braçadeiras Elementais", objective = "Recolha 5 Braçadeiras Elementais Intactas e use o Pergaminho Divinatório nelas. Depois, leve o Pergaminho Vaticinado para a Sentinela Velene Golpestela no Refúgio Brisaprata.", description = "Tome este pergaminho divinatório, ele vai identificar quem foi o criador dos elementais. Mas você vai precisar de alguma coisa deles para usar como reagente e canalizar a magia do pergaminho: as braçadeiras devem servir.\n\nAlgumas braçadeiras intactas já vão bastar. As mais desgastadas não servirão aos nossos propósitos, mas talvez você consiga vendê-las para os comerciantes por aí. Depois que estiver com as braçadeiras intactas, é só usar o pergaminho. Volte com os resultados e nós daremos prosseguimento à missão.", progress = "Considerando que só dá para matar os elementais destruindo as braçadeiras, deve ser difícil encontrar as que contêm as informações de que o pergaminho divinatório precisa. Talvez você as consiga negociando com os outros, isso se já não tiver realizado o feitiço...", completion = "Bom trabalho, &lt;name&gt;, isso vai nos dar a informação para descobrir quem foi o culpado. Está a fim de acabar com o mago que vem enchendo nossas águas de elementais corrompidos? A corrupção que se espalha pelo Vale Gris e além é de revirar o estômago — só de pensar que um feiticeiro fez isso de propósito me dá nojo. Ele vai pagar caro por isso.", rewards = "Você receberá:\n\nBraçadeira Elemental Intacta"},
[1017] = {title = "O mago evocador", objective = "Mate Sarilus Malnato e leve a cabeça dele para a Sentinela Velene Golpestela no Refúgio Brisaprata.", description = "Só tem um mago morto-vivo em toda a região dos Sertões capaz de perverter tantos elementais d'água assim. E só ele é tolo o bastante para fazer isso. Sarilus Malnato deve estar encarapitado no alto do Morro de Brumedo, rindo da minha cara.\n\n<name>, você tem que adentrar o território inimigo e destruir esse infiel. Eu já estou farta dessas idiotices, temos que mostrar a ele o que acontece com quem vem importunar as Sentinelas.\n\nDestrua-o e traga-me sua cabeça. Mas tome cuidado, a Horda estará de olho em você.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nLuz de Eluna"},
[1018] = {title = "Uma nova fronteira", objective = "Fale com o Arquidruida Hamuul Runa Totem no Platô dos Anciãos, no Penhasco do Trovão, sobre o chamado do Círculo Cenariano para explorar as fronteiras de Kalimdor.", description = "Atenção, heróis!\n\nO Círculo Cenariano está à procura de aventureiros para ajudar na exploração das vastas fronteiras de Kalimdor! Um deserto esquecido pelos deuses e repleto de perigos inimagináveis jaz a oeste de Tanaris e Geringontzan, e o Círculo precisa de representantes honrados e dispostos para defender seus interesses.\n\nInteressados, favor marcar audiência com o Círculo Cenariano. Procurem o Arquidruida Hamuul Runa Totem no Platô dos Anciãos, no Penhasco do Trovão, para mais informações!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1019] = {title = "Uma nova fronteira", objective = "Fale com o Arquidruida Fandral Guenelmo, no Enclave Cenariano de Darnassus, sobre o chamado do Círculo Cenariano para explorar as fronteiras de Kalimdor.", description = "Atenção, heróis!\n\nO Círculo Cenariano está à procura de aventureiros para ajudar na exploração das vastas fronteiras de Kalimdor! Um deserto esquecido pelos deuses e repleto de perigos inimagináveis jaz ao oeste de Tanaris e Geringontzan, e o Círculo precisa de representantes honrados e dispostos para defender seus interesses.\n\nInteressados, favor marcar audiência com o Círculo Cenariano. Procurem o Arquidruida de Darnassus, Fandral Guenelmo, para mais informações!", progress = nil, completion = "O que lhe faz pensar que um reles &lt;class&gt; teria o direito de me incomodar assim? Perfeitamente, vou interromper os deveres que cumpro todo santo dia para ajudá-lo a encontrar uma traquitana por aí. O bem-estar de Darnassus — ou melhor, de toda Teldrassil — nem se compara à importância de você adquirir mais tralhas para a sua coleção.\n\nNão basta aqueles tolos da Clareira da Lua desperdiçarem meu tempo com pedidos de ajuda, agora tenho que tolerar isso?", rewards = "Você receberá:\n\n"},
[1020] = {title = "Cura de Orendil", objective = "Entregue a Cura de Orendil a Pelturas Lunalva em Astranaar.", description = "<name>, eu preparei um medicamento que pode curar a criança doente. Leve-o para Astranaar e entregue-o ao pai da menina, Pelturas Lunalva.\n\nPara chegar lá, siga pela estrada em direção ao sul e, em seguida, ao leste.", progress = "Desculpe-me, não tenho tempo para conversar. Minha filha, minha Relira, está à beira da morte!", completion = "Foi Orendil quem mandou isso?\n\nEle é ótimo com ervas e curas! Este preparado me encheu de esperanças, quando já não me restava nenhuma...", rewards = "Você receberá:\n\n"},
[1021] = {title = "Malditos sátiros! Dríades em perigo!", objective = "Encontre Anilia em Xavian.", description = "Escute aqui, eu não sei quem é você, mas preciso da sua ajuda. Minhas irmãs foram pegar o Ramo de Cenarius de volta dos sátiros. Elas disseram que voltariam rápido, mas já se passaram dois dias inteiros!\n\n<name>, você tem que encontrá-las! Elas estavam procurando as ruínas de um templo noctiélfico em Xavian, a nordeste daqui. Eu não sei mais o que pensar. Procure Anilia, ela era a responsável pela expedição.", progress = nil, completion = "A julgar pela ferida aberta, Anilia está à beira da morte.", rewards = "Você receberá:\n\n"},
[1022] = {title = "Vale Uivante", objective = "Vá para o Vale Uivante estudar o Tomo de Mel'Thandris, depois fale novamente com a Sentinela Melyria Algissombra no Altar de Aessina.", description = "Por mais que tenhamos empregado nossos recursos e energia para expulsar os demônios da Selva Maleva, ao norte, não fomos muito bem-sucedidos. Pelo menos, mantivemos os demônios afastados do Vale Gris.\n\nAo norte, perto da fronteira com a Selva Maleva, o santuário arruinado de Mel'Thandris foi tomado por misteriosos lobisomens. Por causa dos uivos incessantes, a região acabou ficando conhecida como Vale Uivante. Talvez o Tomo de Mel'Thandris, que jaz no santuário, ajude a explicar o surgimento dos lobisomens.", progress = nil, completion = "Esse Tomo de Mel'Thandris é muito estranho. Os eventos que registrou, os eventos que mostra ali... Ninguém entende direito a lógica.\n\nDe qualquer forma, uma coisa ficou clara: essa tal Foice de Eluna que a Sacerdotisa Stelacanto recebeu precisa ser melhor investigada.", rewards = "Você receberá:\n\n"},
[1023] = {title = "O expurgo de Raene", objective = "Encontre a Gema Faiscante e fale novamente com Raene Correlobos em Astranaar, em posse do Diário de Teronis.", description = "Ao revirar os pertences de Teronis, você encontra um diário ainda em boas condições.\n\nVocê pega o diário e deixa o corpo de Teronis como estava.\n\nAgora só falta encontrar a gema que Teronis vinha procurando. Segundo Raene, ela estava por ali. Pode ser que esteja com os murlocs...", progress = "Ah, bem-vindo de volta, <name>.", completion = "Que terrível, &lt;name&gt;! Teronis morreu?!\n\nAgora não é hora para luto, precisamos nos concentrar. Vou mandar algumas Sentinelas para recolher o corpo de Teronis assim que possível.\n\nVocê já trouxe o diário dele e a gema que ele vinha procurando. Acha que consegue terminar a tarefa de Teronis? Vou ficar mais tranquila sabendo que a morte dele não foi em vão.", rewards = "Você receberá:\n\n"},
[1024] = {title = "O expurgo de Raene", objective = "Encontre Shael'dryn no poço lunar a nordeste.", description = "Você terá que fazer a pesquisa por conta própria, mas pelas anotações de Teronis, eu diria que o próximo passo é encontrar as outras partes do bastão de Dartol. Eu começaria pelo poço lunar a leste do Lago Íris. Quanto estiver pront<o/a>, pegue a estrada e dobre para o norte ao passar por uns banquinhos à sua esquerda.\n\nLá, uma dríade chamada Shael'dryn poderá orientá-<lo/la> melhor. Vá atrás dela.\n\nE guarde bem a gema. Shael'dryn vai precisar dela para ajudá-<lo/la> a reconstruir o bastão.", progress = "Olá! Como vai?", completion = "Ah, então Raene mandou você aqui? O caso deve ser muito sério mesmo. Como posso ajudá-lo?", rewards = "Você receberá:\n\n"},
[1025] = {title = "Uma defesa agressiva", objective = "Mate 1 Vigia do Covil, 2 Ursinos, 10 Totêmicos e 12 Guerreiros da tribo Torpeflora, depois fale novamente com Raene Correlobos em Astranaar.", description = "Antes de partir para o poço lunar, eu tenho outra tarefa para lhe dar que ajudará o povo do Vale Gris, caso você queira colocar suas habilidades à prova.\n\nAo longo da estrada, rumo ao leste, antes do Rio Felfarren, pelursos vêm atacando os viajantes. Suas mentes torturadas alimentando-se do medo e seu apetite saciando-se com sangue de inocentes.\n\nAjude as Sentinelas a proteger a região, tenha tanta piedade dos monstros quanto eles têm nós.\n\nAcredito que o assentamento deles fique ao sul da estrada... tome cuidado.", progress = nil, completion = "Você se saiu bem, &lt;name&gt;. Obrigada.", rewards = "Você receberá:\n\nBroa de Milho MolhadinhaSuco de Melão"},
[1026] = {title = "O expurgo de Raene", objective = "Encontre a Chave de Madeira e uma parte do Bastão de Dartol antes de falar novamente com Shael'dryn no poço lunar.", description = "Quisera eu receber notícias melhores.\n\nRaene está certa. Eu sei da criação de Dartol e conheço os arvorosos daqui... ou pelo menos conhecia. Todos foram corrompidos, suas naturezas desvirtuadas.\n\nPara encontrar a outra parte do bastão, você vai precisar de uma chave para abrir o baú onde a peça está guardada.\n\nA chave de madeira está com um dos arvorosos, a nordeste daqui. Após pegar a chave, procure uma pequena clareira, a oeste da estrada, perto da fronteira com a Selva Maleva... O baú deve estar escondido por ali.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nChave de Madeira"},
[1027] = {title = "O expurgo de Raene", objective = "Encontre a última peça do Bastão de Dartol e fale novamente com Shael'dryn no poço lunar.", description = "A última parte do bastão foi confiada aos druidas de Dor'Danil por razões de segurança. Não faço ideia de onde a esconderam. Lamento não poder ajudar mais.\n\nSó posso lhe dizer para seguir a estrada que leva aos Sertões. Os druidas se abrigavam ao leste dessa estrada, mas eles tampouco serão de grande ajuda: foram todos mortos não faz muito tempo... não gosto nem de pensar no assunto.\n\nDepois que tiver encontrado a peça, traga-a para mim e eu <o/a> ajudarei a montar o bastão.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1028] = {title = "O expurgo de Raene", objective = "Encontre o santuário oculto e encante o Bastão de Dartol.", description = "Aqui está o bastão inteirinho, <name>. Reconstruí-lo foi fácil... o difícil vai ser encontrar o altar que tem o poder de encantar a peça.\n\nCom um pouquinho de empenho não vai ser tão difícil assim para você, que já chegou tão longe.\n\nSiga rumo ao sudeste – as estradas são poucas, então tenha cuidado – e fique sempre voltado para o norte. O altar se esconde entre as montanhas que margeiam a clareira.\n\nChegando lá, bote o bastão no altar... o resto vai se dar num passe de mágica. Hehe.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nBastão de Transformação de Dartol"},
[1029] = {title = "O expurgo de Raene", objective = "Fale novamente com Raene Correlobos em Astranaar.\n\nItem fornecido:\n", description = "Eu sugiro que você encontre Raene e diga a ela que o bastão está pronto. Ela saberá dizer qual é o próximo passo a dar.\n\nBoa sorte, <name>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1030] = {title = "O expurgo de Raene", objective = "Encontre um pelurso não corrompido e use o poder do Bastão de Transformação de Dartol para falar com ele.\n\nItem fornecido:\n", description = "Infelizmente, não sei muito bem o que ele planejou.\n\nSe o que ele disse sobre o poder do bastão for verdade, você poderá assumir outra forma. Que forma é essa, eu não sei.\n\nTalvez o objetivo dele fosse se aproximar dos pelursos corrompidos, mas mesmo os não corrompidos poderiam matá-lo caso não fosse cuidadoso. Transformado pelo poder do bastão, Dartol teria que falar com algum pelurso livre dos efeitos do limo vil.\n\nEu começaria a busca a sudeste do Lago Mistral.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1031] = {title = "O ramo de Cenarius", objective = "Mate Geltharis e leve o Ramo de Cenarius para Iliana no Altar de Aessina.", description = "Anilia põe a mão sobre os seus ombros e puxa você para perto. Ofegante, ela aponta para o arco ao norte, antes de fechar os olhos e exalar o último suspiro. \"Ele pegou o ramo... Geltharis...\" Você sente em sua alma que o último pedido de Anilia é devolver o Ramo de Cenarius às dríades.", progress = "Você as encontrou? O que Anilia disse? Onde está ela?", completion = "Anilia morreu? Não... Não pode ser! Ontem mesmo estávamos correndo e brincando na mata.\n\nEsses dias acabaram, pelo jeito...", rewards = "Você vai poder escolher uma dessas recompensas:\n\nDragonas FeéricasCinturão Escama Brilhante\n\nVocê também receberá:\n\n"},
[1032] = {title = "Temporada de caça aos sátiros", objective = "Leve 16 Chifres de Sátiro para Iliana no Altar de Aessina.", description = "Nem acredito que Anilia se foi... Nós sempre brincávamos nas florestas. Eu estou quase... triste.\n\nMas eu sei de uma coisa que vai me fazer sentir melhor. Por favor, mate os sátiros que tiraram Anilia de mim! Traga os chifres desses facínoras como prova de seu feito, e lhe serei eternamente grata.", progress = "Por favor, me ajude a vingar a morte da Anilia.", completion = "Obrigada, gentil &lt;race&gt;. O espírito da minha irmã certamente se alegrou com o que você fez.", rewards = "Você receberá:\n\n"},
[1033] = {title = "Lágrima de Eluna", objective = "Leve a Lágrima de Eluna para Pelturas em Astranaar.", description = "Eu tinha esperanças de que a cura de Orendil fosse salvar Relira, mas ela apenas reduziu o avanço do mal. Temos que encontrar um remédio mais poderoso! Mas, graças a Eluna, Orendil mencionou outros remédios, caso o primeiro não funcionasse.\n\nA lágrima de Eluna é um cristal que só existe em um lugar: na ilha do Lago Íris, a leste de Astranaar. Orendil disse que esse cristal vai livrá-la da corrupção... Por favor, <name>, traga-me a lágrima de Eluna e reze para que ela dê um fim ao mal de minha filha!", progress = "<name>, você encontrou o Lago Íris? Trouxe a Lágrima?", completion = "Ah, você achou! Vou fazer uma infusão com a lágrima e rezar para que isso cure a menina.\n\nObrigado! Muito obrigado, &lt;name&gt;!", rewards = "Você receberá:\n\n"},
[1034] = {title = "Ruínas de Poeira Estelar", objective = "Leve 5 Punhados de Poeira Estelar para Pelturas em Astranaar.", description = "A Lágrima de Eluna levou embora parte da doença. Os pesadelos dela se abrandaram e eu agradeço à deusa pela bênção. Mas... ela continua queimando de febre.\n\nOrendil falou de um pó das Ruínas de Poeira Estelar. Pode ser que ele ajude a aliviar a febre de Relira. Vá buscar um pouquinho e volte aqui.\n\nAs Ruínas de Poeira Estelar ficam ao sul, <name>. Que Eluna abençoe sua jornada.", progress = "Já conseguiu a poeira estelar, <name>?", completion = "Você trouxe o tal pó! Vou preparar um cataplasma já, já... ele deve abaixar a febre de Relira.\n\nObrigado mais uma vez, &lt;name&gt;. Sem sua ajuda, minha filha ia acabar morrendo.", rewards = "Você receberá:\n\n"},
[1035] = {title = "Lago Céu Caído", objective = "Recolha uma Pedra-da-lua Caída do Oráculo Umbrárvore, depois leve-a para Pelturas em Astranaar.", description = "A febre de Relira passou, mas ela continua muito fraca. Acho que só a passagem do tempo não vai bastar para lhe devolver as forças perdidas. Ela vai precisar de uma pedra-da-lua.\n\nNós costumávamos buscar essas pedras no Lago Céu Caído, a sudeste... Isso até chegarem os Umbrárvores.\n\nO líder deles, o Oráculo Umbrárvore, juntou todas as pedras e passou a guardá-las como um tesouro. Você vai que ter tomar uma pedra-da-lua dele.\n\nPara fazer isso, receio que só matando-o.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nArmadura Riacho EstrondosoAlgemas BestiaisSobreveste Luminescente\n\nVocê também receberá:\n\n"},
[1036] = {title = "Ioho, cão sarnento!", objective = "Fale com o Mestre de Frota Firalião a bordo do Véu Carmesim na costa da Selva do Espinhaço.", description = "<name>... já ouvi falar de tu. Soube que tu não é muito bem-<vindo/vinda> na Angra do Butim... assim como a gente, os Bucaneiros do Vela Sangrenta. O mestre de frota Firalião está procurando homens e mulheres dispostos a atender o chamado do mar para uma missão especial.\n\nSe você tá <interessado/interessada>, marujo, corre no Véu Carmesim e fala com ele abaixo do convés. O Véu tá a ferro solto na Costa Brava a leste daqui, perto do Rasgamar e do Sorte da Donzela.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1037] = {title = "Velinde Stelacanto", objective = "Fale com Thyn'tel Tecelâmina no Terraço dos Guerreiros, em Darnassus.", description = "Velinde Stelacanto foi minha predecessora na Floresta do Vale Gris. A princípio, parecia que ela tinha a Selva Maleva sob controle, mas de pouco em pouco foi vacilando. Um dia, sumiu sem mais nem menos.\n\nMandaram-me para cá para substituí-la. Receio não saber mais nada sobre a sacerdotisa. De repente Thyn'tel Tecelâmina, uma das comandantes das Sentinelas, tenha detalhes que eu desconheço sobre o sumiço.\n\nEstou certa de que ela compreenderá a importância de tais informações.", progress = nil, completion = "Velinde Stelacanto? O sumiço da &lt;class&gt;, um tempo atrás, é de conhecimento geral. Quanto aos detalhes sobre o acontecido... eu precisaria de um motivo muito bom para compartilhar essas informações.", rewards = "Você receberá:\n\n"},
[1038] = {title = "Os efeitos de Velinde", objective = "Vasculhe o baú de Velinde atrás do seu diário, depois leve-o junto com a chave para Thyn'tel Tecelâmina em Darnassus.", description = "Foi isso que você viu no Tomo de Mel'Thandris? Imagino que não vá ter nenhum problema se você mexer nas coisas dela. Esta chave abre o baú onde guardamos as coisas da sacerdotisa, no alojamento das Sentinelas. Ela registrava seus deveres em um diário. Se for para descobrir alguma coisa, vai ser nele.\n\nÉ melhor você ficar avisad<o/a>: as Sentinelas acreditam que ela tinha seus motivos para partir e que pode voltar a qualquer momento. A sacerdotisa fez muito por nós e conquistou nossa confiança.", progress = "E aí, descobriu alguma coisa?", completion = "Pelo visto, o desaparecimento de Velinde é mais preocupante do que parecia. Não consigo acreditar que ela teve um fim tão trágico. Pode acabar sendo vantajoso para nós entrar em contato com ela.\n\n&lt;name&gt;, já que tem mostrado tanto interesse no assunto, eu gostaria de pedir sua ajuda.", rewards = "Você receberá:\n\n"},
[1039] = {title = "O porto dos Sertões", objective = "Fale com o Mestre Portuário Caruncho na Vila Catraca.", description = "Vila Catraca é o único porto em toda a região dos Sertões. O provável é que Velinde tenha encontrado um navio mercante em Vila Catraca para levá-la à Angra Aguanegra. Não tivemos muito contato com os goblins que cuidam do porto, mas o mestre da doca deve ter mais informações sobre as idas e vindas dos passageiros.\n\nSiga a estrada a sudeste passando pelo Vale Gris e você chegará nos Sertões. Cuidado onde pisa, <name>, pois a área é patrulhada pela Horda. Você estará em segurança quando chegar ao porto.", progress = nil, completion = "&lt;race&gt;, né? Devo admitir que não aparece muita gente do seu tipo aqui. Está procurando alguém, né? Me dá um segundo, eu com certeza anotei em um dos meus registros.\n\nMas é muita gente que passa por aqui, então aguenta um segundo.", rewards = "Você receberá:\n\n"},
[1040] = {title = "Um barco para a Angra do Butim", objective = "Pegue um barco para Angra do Butim e fale com Caravaneiro Russote.", description = "Ah, sim, enfim achei. Por que não avisou logo que foi há muito tempo que ela passou por aqui? Vejamos.\n\nVelinde. Comprou passagem no Gavião Negro para Angra do Butim. Como não tem nada aqui dizendo o contrário, presumo que o navio chegou bem.\n\nNão sei se serve de alguma coisa, mas ela quis saber sobre transportes terrestres por aquelas bandas, e eu indiquei o Russote, um caravaneiro que fica na Angra do Butim. Talvez essa tal Velinde tenha viajado com ele.\n\nAgora circulando, que eu tenho mais o que fazer.", progress = nil, completion = "Sim? Olhando assim pra você, diria que você não tem mercadoria nenhuma a transportar, então das duas, uma: ou você está procurando emprego na minha caravana, e não temos vagas neste momento, ou está falando com o goblin errado.\n\nQual vai ser?", rewards = "Você receberá:\n\n"},
[1041] = {title = "Na estrada com a Caravana", objective = "Fale com o Tabelião Azambuja na Vila Sombria.", description = "Então eu estava errado a seu respeito, e olha que isso não acontece todo dia.\n\nPor sorte, eu lembro bem dessa tal de Velinde. Não é sempre que aparece uma sacerdotisa elfa noturna disposta a viajar com uma caravana velha e suja — porém ideal para todas as suas necessidades logísticas!\n\nNossos caminhos se separaram rumo ao norte, quando ela seguiu para Vila Sombria. O tabelião de lá é firmeza, então talvez tenha algo útil nos registros dele.\n\nCuidado, pois, até no melhor dos dias, a selva é um lugar mortífero.", progress = nil, completion = "Com tanta coisa suspeita acontecendo nos últimos anos, tento anotar tudo o que posso sobre os estranhos que passam por aqui. Às vezes a informação pode ser útil... muito embora, nesses casos, raramente seja coisa boa. Não que eu esteja sugerindo qualquer coisa sobre essa tal de Velinde.", rewards = "Você receberá:\n\n"},
[1042] = {title = "A família Carevino", objective = "Fale com João Carevino na Vila Sombria.", description = "Não, não tenho nenhum registro de que uma Velinde Stelacanto tenha ficado em Vila Sombria... mas também, se me perdoa a intromissão, não consigo imaginar uma sacerdotisa elfa noturna se hospedando na estalagem, sacou?\n\nJá esses lobisomens que você mencionou, disso aí eu já ouvi falar. No outro dia mesmo o Petrônio chegou na cidade com um cordão cheio de cabeças de lobisomem. Ele trabalha com os Carevino, caçadores de demônios, mortos-vivos e outras monstruosidades. Procure o João, ele é o chefe da família.", progress = nil, completion = "Vivemos tempos sombrios. As notícias ruins se propagam como sussurros entre as folhas das árvores e o mal está à espreita por todos os lados.\n\nE assim, no alto desses tempos, um estranho vem à minha casa e pergunta sobre worgens... não peço desculpas pela minha suspeita, mas peço respostas.\n\nE hei de ter essas respostas.", rewards = "Você receberá:\n\n"},
[1043] = {title = "A foice de Eluna", objective = "Procure sinais da Foice de Eluna, e depois fale novamente com João Carevino na Vila Sombria.", description = "Sua história parece ser verdade... não entendo totalmente os seus motivos, mas se seu objetivo aqui na Floresta do Crepúsculo envolve expurgar os worgens da floresta, aceito abrir mão da compreensão em troca do resultado.\n\nAo sul existe uma mina que foi tomada por worgens... Eles vieram do nada, mas até onde sabemos, a mina foi o primeiro lugar em que foram avistados.\n\nFaça o que tiver que fazer, mas peço que compartilhe comigo quaisquer questões preocupantes que surgirem. Aceitamos qualquer ajuda em nossa luta contra o mal.", progress = nil, completion = "&lt;name&gt;, a história que você me conta é interessante. Embora a influência da família Carevino não seja grande o suficiente para que possamos encontrar a raiz dessas tribulações e acabar com elas de uma vez por todas, faremos o que estiver ao nosso alcance para eliminar os worgens na Floresta do Crepúsculo.\n\nJá avisei a Petrônio que essa deve ser a prioridade dele.", rewards = "Você receberá:\n\n"},
[1044] = {title = "Perguntas esclarecidas", objective = "Fale novamente com Thyn'tel Tecelâmina em Darnassus.", description = "Asseguro-lhe de que nós vamos aplacar esse problema dos worgens. O mal que a sua amiga trouxe à nossa floresta será contido e, a despeito das ações dela, não desejo a ela mal algum. Eventos estranhos estão em curso, <name>, e a escuridão é incansável nestes tempos sombrios.\n\nNão quero tomar mais de seu tempo. Imagino que você deseje propagar a notícia da sua descoberta nas minas da Floresta do Crepúsculo.", progress = nil, completion = "Então este foi o destino de Velinde... a alta-sacerdotisa ficará desapontada, elas eram boas amigas antes de Velinde desaparecer. Em todo o caso, a Foice de Eluna deve ter sido um dom com o qual nossa deusa a agraciou, mas acabou tendo um fim perturbador nas mãos de sua serva.\n\nMas essa é uma outra conversa para um outro momento. Por tudo o que você fez, eu lhe devo meus agradecimentos.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nArco LunarisVarinha Raio Lunar\n\nVocê também receberá:\n\n"},
[1045] = {title = "O expurgo de Raene", objective = "Mate Ran Dente Sangrento e 4 Guardas Dente Sangrento e fale novamente com Krolg perto do Lago Mistral.", description = "Os elfos noturnos são tão antigos quanto o próprio tempo, mas são tão arrogantes que só fazem destruir o próprio mundo. Isso está saindo de controle e começando a destruir também as nossas vidas...\n\nMuito mal ocorre por culpa dos elfos, mas isso não nos isenta de responsabilidade pelas nossas próprias ações. Para além da corrupção, nós mesmos somos capazes de causar grande mal.\n\nRan Dente Sangrento é um desses males. Ganancioso, avarento... a corrupção só o deixa mais forte. Tenho ódio profundo pelos elfos, mas não desejo mal aos filhos deles, e é por isso que precisamos policiar os nossos.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nCrânio de Ran Dente Sangrento"},
[1046] = {title = "O expurgo de Raene", objective = "Leve o Crânio de Ran Dente Sangrento e o Bastão de Transformação de Dartol para Raene Correlobos em Astranaar.", description = "Volte à sua tribo e conte a todos sobre a sua vitória aqui.\n\nMostre a todos o seu troféu e se regale em sua glória...Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nPedra GlacialRasga-sarjeta\n\nVocê também receberá:\n\nAnel de Prata Pura"},
[1047] = {title = "Uma nova fronteira", objective = "Fale com o Arquidruida Fandral Guenelmo, no Enclave Cenariano de Darnassus, sobre o chamado do Círculo Cenariano para explorar as fronteiras de Kalimdor.", description = "Atenção, heróis!\n\nO Círculo Cenariano está à procura de aventureiros para ajudar na exploração das vastas fronteiras de Kalimdor! Um deserto esquecido pelos deuses e repleto de perigos inimagináveis jaz ao oeste de Tanaris e Geringontzan, e o Círculo precisa de representantes honrados e dispostos para defender seus interesses.\n\nInteressados, favor marcar audiência com o Círculo Cenariano. Procurem o Arquidruida de Darnassus, Fandral Guenelmo, para mais informações!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1048] = {title = "Adentrando o Monastério Escarlate", objective = "Mate a Alta-inquisidora Cristalba, o Comandante Escarlate Mograine, Herodes, o Campeão Escarlate e o Mestre de Matilha Lobato, depois volte a falar com Varimatras na Cidade Baixa.", description = "Enquanto a Dama Sombria cuida das urgências relacionadas ao avanço do Lich Rei no norte, eu estou me certificando de que os assuntos mais domésticos sejam resolvidos.\n\nNesse quesito, sem dúvida, nossa maior pedra no sapato são as pestes humanas que se intitulam como Cruzada Escarlate. As Clareiras de Tirisfal estão apinhadas dessas formiguinhas vermelhas.\n\nVá até o Monastério, <class>, e trucide a Alta-inquisidora Cristalba e seus oficiais mais graduados.", progress = nil, completion = "Você foi de grande valia à Dama Sombria, &lt;class&gt;. A tarefa que pedi a você não era nada fácil.\n\nQue fique registrada a sua lealdade aos Renegados, e que sua tenacidade no combate nunca seja esquecida.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nEspada do AgouroBordão ProféticoColar de Sangue de Dragão\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[1049] = {title = "O Compêndio dos Caídos", objective = "Recupere o Compêndio dos Caídos no Monastério nas Clareiras de Tirisfal e volte a falar com o Sábio Devoto da Verdade no Penhasco do Trovão.", description = "A Harmonia Telúrica tinha boas intenções em relação à nossa amaldiçoada irmandade. Contudo, quem sabe as motivações por trás dos sussurros que chegam aos ouvidos do Conselho dos Anciãos?\n\nNós nos aliamos aos Renegados, mas a história deles é repleta de traição. Nosso pacto foi precipitado. Talvez Caerne devesse ter dado mais atenção aos avisos de Orgrimmar.\n\nO âmago do Monastério Escarlate esconde o Compêndio dos Caídos, guardado por fanáticos zelotes humanos. Por mais que o método deles seja forjado na insanidade, a pesquisa deles pode nos ser útil.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nProtetor TorpeBroquel PedraforteOrbe Ômega\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[1050] = {title = "Mitologia dos Titãs", objective = "Recupere o livro Mitologia dos Titãs no Monastério e leve-o para a Bibliotecária Maé Palidopó em Altaforja.", description = "O futuro é inevitável! Nós nos lançamos em sua direção como pingos de chuva caindo na imensidão do Grande Oceano.\n\nMas estudar o passado pode nos ajudar a entender o que devemos esperar do desconhecido.\n\nTempos atrás, antes das agruras se abaterem sobre Lordaeron, havia um bastião de conhecimento escondido nas Clareiras de Tirisfal. Dizem por aí que um livro chamado Mitologia dos Titãs ainda pode estar na biblioteca do Monastério.\n\nTraga o livro para mim e talvez possamos nos unir para revelar os segredos do passado!", progress = "O Monastério já foi um seminário para aspirantes a paladino. Deixou de ser um baluarte da Luz ao cair nas mãos dos zelotes fanáticos da Cruzada Escarlate.\n\nA Cruzada acreditava que seu objetivo era nobre: expurgar a praga dos mortos-vivos. Mas a loucura maculou o empenho deles, e agora são inimigos de todos.\n\nTraga para mim a Mitologia dos Titãs, para que eu possa estudar e guardar o tomo aqui em Altaforja.\n\nOs salões corruptos do Monastério não são um lugar digno para um tesouro histórico de tamanha importância.", completion = "Você resgatou o livro sagrado!\n\nVocê fez uma longa jornada e sei que recuperar o livro não foi uma tarefa fácil. Mas os anões de Altaforja têm muito a ganhar com o seu sucesso.\n\nObrigada, &lt;name&gt;, em nome da Liga dos Exploradores.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nComenda da Liga de Exploradores"},
[1051] = {title = "A vingança de Vorrel", objective = "Leve a aliança de Vorrel Sentripaz para Mônica Sentripaz na Serraria Tarren.", description = "Embora minha vontade seja livre, não posso mais servir à Dama Sombria. Sinto que meu tempo em Azeroth chegou ao fim. Minha força vital está em frangalhos.\n\nFoi o Interrogador Vishas que fez isso comigo! Passou semanas me torturando! Roubou minha aliança de casamento e deu de presente para a esposa, aquele miserável!\n\nVingança... quero vingança!\n\nO Interrogador é tão burro que ficou se gabando da esposa, Nancy, que mora perto do Espelho de Lordaeron. Encontre a sirigaita, acabe com ela e leve minha aliança para a minha esposa, Mônica, na Serraria Tarren.\n\nPeço que cumpra meu desejo final...", progress = "O que você quer, forasteiro?", completion = "Vorrel teve uma existência muito perturbada. E foi um parceiro exemplar...\n\nMalditos humanos!\n\nVorrel se foi, e ainda tinha tanta coisa para fazer nessa morte-viva. Pelo menos você vingou o nome dele, o que me traz algum consolo. Obrigada.\n\nAgora deixe-me em sozinha com o meu luto...", rewards = "Você vai poder escolher uma dessas recompensas:\n\nDragonas do SofrimentoCapa de Aço Inexorável\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\nBotas do Vorrel"},
[1052] = {title = "No caminho Escarlate", objective = "Leve a Carta de Comenda de Anton para Raul, o Devotado, na Costa Sul.", description = "Nós, da Cruzada Escarlate, detemos baluartes desde Amparo até as Clareiras de Tirisfal. Temos muito orgulho dos nossos bastiões de purificação em toda Lordaeron.\n\nVocê já se provou contra os mortos-vivos em Desolação. Mas a verdadeira ameaça da praga está em Lordaeron.\n\nVá até a cidade de Costa Sul, nos Reinos do Leste. Procure um cruzado chamado Raul, o Devotado. Dê a ele esta carta de comenda com o meu selo e ele levará você a um lugar de honra em nosso Monastério Escarlate.", progress = "Quem vem lá? O que quer de mim?", completion = "O Irmão Anton ainda espalha a palavra da Cruzada Escarlate? Nossa...", rewards = "Você receberá:\n\n"},
[1053] = {title = "Em nome da Luz", objective = "Mate a Alta-inquisidora Cristalba, o Comandante Escarlate Mograine, Herodes, o Campeão Escarlate e o Mestre de Matilha Lobato, depois volte a falar com Raul, o Devotado na Costa Sul.", description = "Eu já fui um servo da Cruzada Escarlate, com muita honra, lealdade e orgulho. Acreditava que a causa deles era nobre: livrar Azeroth dos mortos-vivos.\n\nContudo, ao passar um tempo no Monastério nas Clareiras de Tirisfal, percebi que eles estavam perdendo a lucidez. Agora eles acham que todos os que não usam o tabardo da Cruzada são pestilentos. Homens e mulheres inocentes foram torturados por serem supostamente pestilentos.\n\nEm nome da Luz, a Cruzada Escarlate deve ser dizimada. Você, <name>, deve destruir esse regime ensandecido.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nEspada da SerenidadeMorde-ossosAmeaça NegraOrbe de Lórica\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[1054] = {title = "Contendo a ameaça", objective = "Leve o Crânio de Dal Sangarra para Raene Correlobos em Astranaar.", description = "Os pelursos Pelocardo ocupam a área logo ao norte de Astranaar. Meus batedores relataram que aquela população aumentou muito nos últimos meses. Se eles se expandirem mais, é possível que tomem coragem para fazer um ataque direto a Astranaar.\n\nMate tantos quanto puder, mas me traga o crânio do chefe deles para comprovar que eles foram contidos... por ora.\n\nEu ficarei aqui em Astranaar aguardando notícias. Que Eluna esteja com você.", progress = "Por causa da corrupção dos pelursos, o Vale Gris só possui uma fração da glória de outrora.", completion = "Muito bem, &lt;name&gt;.\n\nOs pelursos são uma ameaça constante. Espero que isso os contenha por mais algum tempo.", rewards = "Você receberá:\n\nBálsamo Restaurador"},
[1055] = {title = "O expurgo de Raene", objective = "Fale novamente com Shael'dryn no poço lunar.", description = "O bastão, agora reconstruído, está em suas mãos. Shael'dryn vai querer saber tudo sobre o seu sucesso.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1056] = {title = "A jornada para o Morro das Torres de Pedra", objective = "Vá até o Guardião Albagorm no Morro das Torres de Pedra.", description = "Os espíritos da floresta me revelam que você tem coragem e disposição para viajar.\n\nAo sul, não muito longe do Lago Mistral, há um túnel chamado Garracava. Atravessando esse túnel você chegará a uma área conhecida como Rochedo Cortavento, na Cordilheira das Torres de Pedra. Chegando lá, rume para o sudoeste, passando pelo Lago do Rochedo, e depois para o norte, subindo a montanha íngreme, até chegar ao Morro das Torres de Pedra.\n\nO Guardião Albagorm estará esperando por você lá. Atenda ao pedido dele, <race>.\n\nA jornada será perigosa.", progress = nil, completion = "Você chegou, enfim. Vejo que Faldreas deu ouvidos aos espíritos da floresta...", rewards = "Você receberá:\n\n"},
[1057] = {title = "Reivindicando o Vale Carbonizado", objective = "O Guardião Albagorm, no Morro das Torres de Pedra, quer que você mate 7 Harpias Furissangue, 7 Emboscadoras Furissangue e 7 Plumerrantes Furissangue.", description = "Sendo eu um protetor dos espíritos da floresta, você pode certamente imaginar por que estou tão alarmado.\n\nVocê viajou por estas montanhas para alcançar este pico. Sem dúvida, você já viu a farsa que aflige a terra. A cobiça nos levou à destruição destas montanhas sagradas.\n\nO Vale Carbonizado ao sul foi incendiado pelo ganancioso exército mecanizado de industrialistas. Pior ainda, as furissangues que se apossaram destas terras nos negam passagem para que comecemos a recuperação.\n\nExpulse as malditas harpias, <name>!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1058] = {title = "A magia florestal de Jin'Zil", objective = "O Mandingueiro Jin'Zil, em Malaka'jin, quer 5 porções de Seiva das Torres de Pedra, 5 Bigodes de Felino do Crepúsculo, 30 Olhos de Corcel e uma Escama de Dragão Encantado.", description = "Tão excitado estou!\n\nTrês pequenas jaulas para três pequenos elfos! Mas o que devemos fazer com nosso adoráveis amigos abraçadores de árvores? Bem, vamos dar a eles uma amostra de... como se chama isso mesmo... magia florestal!\n\nPreciso de uma boa quantidade de seiva das Torres de Pedra. Daqueles gatos horríveis, traga-me alguns bigodes de felinos do crepúsculo. Isto pede também por muitos olhos de corcel, claro. Quanto mais, melhor. Ah, e, antes que esqueçamos, uma preciosa escama de um dragão encantado.\n\nCorra para o Morro das Torres de Pedra, <name>, para que eu possa preparar um pouco de magia da floresta!", progress = "Olhe só nossos pobres amiguinhos elfos. Enjaulados e sem uma floresta para brincar!\n\nBem, você conseguiu me arranjar um pouco de seiva? E quanto aos bigodes de felinos do crepúsculo? Suponho que você também esteja com aquela escama de dragão encantado, certo?\n\nE os olhos?! Preciso de tantos olhos! Simplesmente não dá pra preparar uma poção mágica florestal forte sem uma boa quantidade de olhos!", completion = "Ah, essas ninfinhas da floresta vão ficar tão contentes! Vão se sentir em casa com esse preparado!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nDragonas VoduBraçadeiras Bagateadas\n\nVocê também receberá:\n\n"},
[1059] = {title = "Reivindicando o Vale Carbonizado", objective = "Viaje até Falfindel Trilhemas na fronteira entre Mil Agulhas e Feralas.", description = "As furissangues foram subjugadas. Mas, se não fizermos nada a respeito, é certo que se reagruparão em breve.\n\nBem ao sul dos Sertões, há uma terra conhecida como Mil Agulhas. Se você viajar para o oeste em Mil Agulhas até alcançar a exuberante floresta de Feralas, você encontrará um druida chamado Falfindel Trilhemas.\n\nEsse sábio druida é capaz de evocar grandes espíritos da floresta que podem ajudar na recuperação do Vale Carbonizado.\n\nAvise-o de que precisamos de ajuda! Vá procurá-lo imediatamente!Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nBraçadeiras TemperadasBroquel do Condestável\n\nVocê também receberá:\n\n"},
[1060] = {title = "Uma carta para Jin'Zil", objective = "Entregue a carta de Darsok a Jin'Zil em sua caverna em Malaka'jin, nas Torres de Pedra.", description = "Você me ajudou tanto, <name>. Você é tipo um pé de coelho da sorte, e eu queria te mostrar uma coisa.\n\nAs harpias não são aqui dos Sertões. Elas vêm de um lugar chamado Torres de Pedra, que ficam lá longe, a noroeste daqui. Embora o lugar talvez seja um pouco perigoso para você, preciso que essa carta seja entregue a um velho amigo que mora por lá, um mandingueiro chamado Jin'Zil.\n\nEle vai ficar feliz em saber que Serena Plumassangue está morta, e algo que diz que você vai achar Torres de Pedra um lugar interessante.", progress = "Você é uma figura meio suspeita. Trouxe algo para mim?", completion = "Ah, uma carta do Darsok. Faz muitos anos que não tenho notícia dele.\n\nEntão a última Plumassangue morreu, foi? Isso é uma boa notícia, lembro bem de quando o meu amigo Rokhan foi matar a irmã mais velha dela, junto com aquele mok'nathal maluco. Ah, como era bom ser jovem como você...", rewards = "Você receberá:\n\n"},
[1061] = {title = "Os espíritos das Torres de Pedra", objective = "Fale com Seereth Quebra-pedra na Cordilheira das Torres de Pedra.", description = "Os espíritos da Cordilheira das Torres de Pedra estão enraivecidos, pois os goblins e seus servos estão pilhando e queimando a terra. Então a terra pediu ajuda, e aqueles que sabem ouvir... escutaram.\n\nSeereth Quebra-pedra, uma promissora xamã da Horda, foi enviada às Torres de Pedra. Ela relatou que os goblins estão produzindo desmatamentos em massa, e pediu ajuda.\n\nEncontre Seereth nas Torres de Pedra. Você precisa seguir a estrada a oeste da Encruzilhada, nos Sertões. Ela está acampada perto do Vale Matagrande.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1062] = {title = "Invasores goblins", objective = "Mate 15 Madeireiros da Empreendimentos S.A. e, então, fale novamente com Seereth Quebra-pedra na fronteira entre as Torres de Pedra e os Sertões.", description = "A empreitada goblínica Empreendimentos S.A. se mudou para a Cordilheira das Torres de Pedra e já chegou derrubando árvores e fazendo queimadas extensivas. Os espíritos deste lugar estão cegos de dor e raiva. Precisamos parar a Empreendimentos S.A.!\n\nVá para o noroeste passando pelo Vale Matagrande e você chegará ao Rochedo Cortavento. É lá que os goblins e seus servos estão trabalhando. Mostre a eles que a Horda não permite a exploração das Torres de Pedra. Use a língua que eles mais entendem...\n\nA violência.", progress = nil, completion = "Você agiu com bravura, &lt;name&gt;.\n\nPode ser que, em última instância, precisemos da sabedoria dos anciões tauren para apaziguar os espíritos das Torres de Pedra, mas...\n\nUma dispensa em massa e compulsória dos empregados da Empreendimentos S.A. é um bom começo.", rewards = "Você receberá:\n\n"},
[1063] = {title = "A Bruxa Anciã", objective = "Fale com Magatha no Penhasco do Trovão.", description = "Para aplacar os espíritos das Torres de Pedra, precisamos consultar os anciãos taurens.\n\nNão conheço muitos líderes pelo nome, mas conheço uma: Magatha. Ouvi dizer que ela é muito velha, e muito sábia. Se tem alguém que pode liderar a ajuda espiritual taurena, é ela.\n\nMagatha mora na capital taurena de Penhasco do Trovão, no Platô dos Anciãos — o penhasco mais ao nordeste da cidade. Fale com Magatha e conte a ela sobre os perigos que assolam a Cordilheira das Torres de Pedra.", progress = nil, completion = "Saudações, viajante. Você está aqui para se aconselhar comigo?", rewards = "Você receberá:\n\n"},
[1064] = {title = "Ajuda Renegada", objective = "Fale com a Boticária Zaqueia nos Poços das Visões, que ficam no Penhasco do Trovão.", description = "Os taurens têm um vínculo forte com a terra, e lamento saber das agruras das Torres de Pedra. Contudo, sinto informar que, para curar a terra, primeiro precisamos erradicar a doença que a permeia.\n\nAssim sendo, não é uma sorte que os Renegados sejam nossos aliados? Eles entendem muito de doenças. Acredito que eles poderão nos ajudar, fortalecendo, assim, os laços com o nosso povo.\n\nFale com a Boticária Zaqueia, uma estudiosa que também é emissária dos Renegados. Você a encontra nos Poços das Visões, abaixo do Platô dos Espíritos.", progress = nil, completion = "Magatha avisou que você viria, &lt;name&gt;. Embora meu coração não bata mais, ainda sinto que ele se aperta pelos espíritos das Torres de Pedra.\n\nOs Renegados estão prontos para ajudar como for possível.", rewards = "Você receberá:\n\n"},
[1065] = {title = "A jornada para Serraria Tarren", objective = "Leve o Bilhete de Zaqueia para o Boticário Lindolfo na Serraria Tarren.", description = "Para livrar as Torres de Pedra da Empreendimentos S.A., precisamos tomar medidas extremas. O que não significa derramamento de sangue.\n\nUm colega meu pode ter o que precisamos. Ele se chama Lindolfo e mora na cidade de Serraria Tarren, no Contraforte de Eira dos Montes, na longínqua terra de Lordaeron.\n\nPara ir para Eira dos Montes, pegue o zepelim até a nossa capital, a Cidade Baixa. Depois, vá para o norte rumo às Clareiras de Tirisfal, depois sudoeste até Pinhaprata e siga para o sul, chegando em Eira dos Montes.\n\nEncontre Lindolfo e entregue a ele este bilhete que explica o que nós precisamos.", progress = "Ah, você é uma das minhas novas cobaias?", completion = "Um bilhete de Zaqueia? Ela deve estar precisando muito, para me mandar um recado vindo de tão longe. Vejamos o que diz...\n\nAh, esplêndido! Sei exatamente o que fazer!", rewards = "Você receberá:\n\n"},
[1066] = {title = "Sangue dos Inocentes", objective = "Leve 5 Ampolas de Sangue Inocente para o Boticário Lindolfo na Serraria Tarren.", description = "Criarei uma toxina para minar a violência da Empreendimentos S.A., deixando-os fracos e dóceis. Isso deve resolver o problema da Horda nas Torres de Pedra, sim?\n\nMas para criar a toxina, eu preciso do sangue de inocentes, um ingrediente muito difícil de se obter.\n\nPara nossa sorte, essa área tem a Camarilha, um grupo de bandoleiros humanos que trabalham com magos das sombras... e esses magos coletam sangue inocente para satisfazer seus próprios desígnios nada agradáveis.\n\nOs magos das sombras da Camarilha podem ser encontrados no Forte do Desterro, a sudeste daqui.", progress = "Encontrou os magos das sombras da Camarilha? Pegou o sangue com eles?", completion = "Muito bom! Imagino que a Camarilha tenha dado algum trabalho?\n\nComo mencionei, sangue de inocentes não se consegue assim tão fácil. Ainda bem que a parte difícil ficou para os magos das sombras da Camarilha.\n\nAguente um pouco aí enquanto eu preparo essa concocção...", rewards = "Você receberá:\n\n"},
[1067] = {title = "Volte ao Penhasco do Trovão", objective = "Leve a Toxina do Lindolfo para a Boticária Zaqueia no Penhasco do Trovão.", description = "Aqui está a toxina. Usá-la contra a Empreendimentos S.A. deve render resultados interessantes, quem sabe até vire uma febre. Devo dizer que é deveras emocionante ver minha obra sendo aplicada!\n\nMas antes de disso, leve-a à Boticária Zaqueia. Ela deve criar um método de dispersão para maximizar os efeitos.", progress = "Você voltou, <name>. O Boticário Lindolfo mandou alguma ajuda?", completion = "Uma toxina do Boticário Lindolfo? Muito bem. Se bem conheço Lindolfo, essa toxina será muito eficaz contra a Empreendimentos S.A...", rewards = "Você receberá:\n\n"},
[1068] = {title = "Retalhando as máquinas retalhadoras", objective = "Destrua os retalhadores XT:4 e XT:9 e, então, fale novamente com Seereth Quebra-pedra na fronteira entre a Cordilheira das Torres de Pedra e os Sertões.", description = "Existe uma conexão entre a magia espiritual dos xamãs e a natural dos druidas. É por isso que nós, xamãs, ouvimos a súplica dos espíritos das Torres de Pedra. Não consigo distinguir grande parte dos murmurinhos, mas uma mensagem, uma visão, é bem nítida...\n\nVi duas máquinas humanoides no Rochedo Cortavento, a noroeste do Vale Matagrande, derrubando a floresta com uma rapidez inconcebível. Temos que deter essas feras metálicas!\n\nNa visão, tinha uns símbolos estranhos nessas máquinas:\n\nXT:4 e XT:9.", progress = nil, completion = "A destruição das máquinas XT:4 e XT:9 apaziguou muitos dos espíritos das Torres de Pedra, &lt;name&gt;. Torçamos para que, após essa perda, a Empreendimentos S.A. se retire destas montanhas.\n\nPorque, caso contrário, temo que as atrocidades cometidas contra este lugar venham a parecer pouco frente ao que está por vir.", rewards = "Você receberá:\n\n"},
[1069] = {title = "Ovos de Aranha de Fundolimo", objective = "Leve 15 Ovos de Fundolimo para Cáliper Porcatraca em Vila Catraca.", description = "Omeletes de ovos de aranha são a nova sensação na Angra do Butim! Só tem um problema... nada de ovos na Angra do Butim.\n\nSinto cheiro de oportunidade!\n\nNo Rochedo Cortavento e no Desfiladeiro Sishir, lá pro noroeste da Cordilheira das Torres de Pedra, existe a aranha de fundolimo. Arranja ovos dessa aranha pra mim que te pago uma bolada!\n\nNo Rochedo Cortavento, tem um bocado de ovos nas árvores que sobraram por lá.\n\nNo Desfiladeiro Sishir..., bem, é melhor catar ovos no Rochedo Cortavento se tiver estômago fraco...", progress = "Catou os ovos, <name>? Já fechei uma encomenda com o Mestre Portuário Caruncho!", completion = "Você trouxe! Valeu mesmo!\n\nVou levar isso pro Caruncho e mandar pra Angra do Butim. Já posso sentir o gostinho dos lucros!\n\nToma aí a tua parte, &lt;name&gt;. Esse rolo não ia fechar sem você.", rewards = "Você receberá:\n\n"},
[1070] = {title = "Em guarda nas Torres de Pedra", objective = "Encontre Kaela Umbralança, que está de frente para o vale que adentra o Rochedo Cortavento, na Cordilheira das Torres de Pedra.", description = "Olá, <name>.\n\nParece que o bicho da exploração pegou você. Já esteve na Cordilheira das Torres de Pedra? É uma terra assolada pela Empreendimentos S.A. e outros inimigos dos elfos noturnos.\n\nNão faz muito, a Sentinela Correlobos despachou minha amiga Kaela Umbralança para lá numa investigação sobre a situação na região, mas ainda não tive notícias. O que mais me preocupa é a Aliança ter enviado um mago gnomo junto... receita para encrenca.\n\nPoderia procurar por ela e me trazer notícias?", progress = nil, completion = "Ah, você veio do Vale Gris a mando de Tara. Sinto muito a falta dela, da minha terra, dos meus amigos. Para poder cooptar aventureiros para tarefas assim, ela deve estar se dando bem com as Sentinelas. Fico orgulhosa.\n\nFoi um prazer conhecer você, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[1071] = {title = "Adiamento gnômico", objective = "Mate 10 Madeireiros da Empreendimentos S.A. e 10 Desmatadores da Empreendimentos S.A. e fale novamente com Gaxim Deuxabu nas Torres de Pedra.", description = "Veja bem, <name>, sou membro da Assembleia Esclarecida de Arcanologia, Alquimia e Ciências da Engenharia, e é meu objetivo... não, meu DEVER!... mostrar aos elfos noturnos que um uma misturazinha de magia, preparados e mecanismos pode ajudar a salvar suas florestas.\n\nMas é uma guerra dura, e os goblins parecem se reproduzir como coelhos. Sequer tive tempo de trabalhar nas minhas invenções!\n\nPreciso de um tempinho para planejar e organizar, mas, para isso, preciso da sua ajuda para manter os goblins sob controle no Rochedo Cortavento, a leste daqui.", progress = nil, completion = "Muito obrigado pela ajuda, &lt;name&gt;. Você realmente voltou bem na horinha.\n\nAcho que tenho um plano perfeito: explosivos. Não, isso não é o plano, mas uma partezinha do plano.\n\nVeja bem. Tenho usado altos explosivos. Mas não se preocupe. Eles não são tão perigosos quanto se pensa por aí, e servem muito bem para assustar um pouquinho os goblins. Posso fazê-los correr para onde eu quiser com uma ou outra detonação ao longo das montanhas, mas estou com pouco equipamento já faz alguns dias...", rewards = "Você receberá:\n\n"},
[1072] = {title = "Um velho colega", objective = "Fale com Lomac Motopista em Altaforja.", description = "O dispositivo que tenho em mente é minha versão mais avançada até o momento. No entanto, será necessária uma poçãozinha especial para que ele funcione. Acho que, como esta pode ser a sua última missão, podemos muito bem usar o que temos de melhor.\n\nAssim sendo, precisaremos de explosivos potentes: nitromirglicerônio.\n\nA única pessoa que sabe fazer NG-5 é um velho amigo meu: Lomac Motopista. Procure por ele no Beco da Gambiarra.\n\nVocê o convence a fazer um pouco de NG-5 para nós e eu vou preparando os suportes para os meus aparelhos.", progress = nil, completion = "Ahhh... Gaxim enviou você, né? Como posso saber que você não está mentindo? Hein? Hein...?", rewards = "Você receberá:\n\n"},
[1073] = {title = "Inaptidão + Reagentes = Diversão", objective = "Leve 4 Poções Menores de Mana e 2 Elixires de Fortitude para Lomac Motopista em Altaforja.", description = "Hahaha... você precisa de Nitromirglicerônio para impedir que os goblins destruam a floresta?!\n\nNão me importa se você conhece Gaxim ou não... Vou lhe dar a mercadoria só para ver você ou eles explodindo em pedacinhos bem pequenininhos.\n\nSó tem um problema: acabou o meu estoque.\n\nMas olha só: me traga as poções certas e eu faço um pouquinho para você em dois tempos.\n\nO mínimo que você pode fazer é se provar um alquimista competente antes receber uma amostra da minha maior criação.", progress = "Não tenho como fazer nem um nitromirglicerôniozinho sem as poções... elas são vitais para o preparo.", completion = "Isto vai servir bem.\n\nSe eu fosse menos vivido, estranho, eu diria que você sabe um pouco de alquimia. Bom, bom... o mundo não é feito só de espadas e magia, sabe?", rewards = "Você receberá:\n\n"},
[1074] = {title = "Inaptidão + Reagentes = Diversão", objective = "Leve o Nitromirglicerônio para Gaxim Deuxabu nas Torres de Pedra.", description = "Aqui está, estranh<o/a>, prontinho. Mas tenha cuidado: isso é volátil.\n\nSe você é mesmo amig<o/a> do Gaxim, então mande um oi para ele. Se você não é, então divirta-se com as suas explosões.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1075] = {title = "Um pergaminho de Mauren", objective = "Encontre Collin Mauren em Ventobravo.", description = "Bem, quanto à parte do meu plano em que acionamos os explosivos remotamente... ah, ainda não expliquei essa parte, não é?\n\nEstá bem. Resumindo, a situação é a seguinte: com um pouquinho de magia, consigo fazer com que os explosivos só explodam quando você não estiver por perto. Estou montando um dispositivo que permitirá a você explodir o NG-5 ao apertar de um botãozinho, mas só quando você estiver <pronto/pronta>.\n\nPorém, preciso de um feitiço de um amigo de Ventobravo. O nome dele é Collin Mauren. Você pode encontrá-lo no distrito dos magos. Ele é humano, então não deve ser fácil deixar de notá-lo.", progress = nil, completion = "Agora, este feitiço de que você precisa... um que permita que você detone explosivos à distância... hmmm...", rewards = "Você receberá:\n\n"},
[1076] = {title = "Redemoinhos no Cerro Oeste", objective = "Leve os Destroços do Redemoinho de Poeira para Álvaro Columba em Ventobravo.", description = "Ah, já sei... um feitiço de mensagem. Acho que é esse o efeito que ele busca.\n\nTerei de criar um pergaminho impresso com o feitiço. Para isso, precisarei de destroços de um redemoinho de poeira.\n\nEnquanto eu reúno o resto dos componentes, porque você não vai até Cerro Oeste atrás dessas criaturas ignóbeis? Elas são perigosas, mas se você está <disposto/disposta> a servir de cobaia nos experimentos de Gaxim, acho que dará conta do recado.\n\nEstarei aqui quando retornar.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1077] = {title = "Entrega especial para Gaxim", objective = "Leve o Pergaminho de Mensagem para Gaxim Deuxabu nas Torres de Pedra.", description = "Aí está, <name>. Isso deve servir.\n\nPor favor, transmita meus cumprimentos a Gaxim da próxima vez que o vir. Mal posso esperar para saber como essa aventura terminará.\n\nAté breve.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1078] = {title = "Coleta para Columba", objective = "Leve 8 Escamas Cristalizadas para Álvaro Columba em Ventobravo.", description = "Os viajantes sempre me perguntam sobre a Cordilheira das Torres de Pedra. Parece ser um lugar famoso para se aventurar, seja em busca de mantícoras, elementais ou para quem tenha pendências a tratar com a Empreendimentos S.A.\n\nNo Vale Carbonizado, no meio das Torres de Pedra, vivia uma espécie de basilisco cujas escamas, pulverizadas, davam um excelente reagente para alguns dos feitiços que criei.\n\nSe esses basiliscos ainda existirem, adoraria conseguir um pouco de escamas.\n\nQuando puder, não se apresse, mas lembre que pagarei bem.", progress = "Olá outra vez, <name>.\n\nJá voltou do Vale nas Torres de Pedra com as escamas?", completion = "Perfeito, &lt;name&gt;. Fico muito agradecido.\n\nQuem sabe um dia, no futuro, eu possa retribuir o favor.", rewards = "Você receberá:\n\nVarinha de Feitiçaria"},
[1079] = {title = "Operações secretas – Alfa", objective = "Consiga os Planos de Engenharia da Empreendimentos S.A. na serraria, no Rochedo Cortavento.", description = "É isso, <name>. Preste atenção. Os dispositivos estão feitos e prontinhos para serem usados.\n\nLeve estas páginas. Elas explicam mais sobre os detonadores e explosivos, assim como onde você deve colocá-los.\n\nLembre-se de que o sucesso da sua missão não depende de quanta destruição você causa. O sucesso do Plano Alfa depende de você recuperar os planos de engenharia da Empreendimentos S.A. na serraria ao sul do Lago do Rochedo.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nExplosivos NG-5 (Azul)Explosivos NG-5 (Vermelho)"},
[1080] = {title = "Operações secretas - Beta", objective = "Consiga as Cartas da Empreendimentos S.A. na Mina Cortavento, que fica no extremo leste do Rochedo Cortavento.", description = "O Plano Beta é igualzinho ao Plano Alfa, <name>.\n\nCriaremos uma distração para, então, roubar as preciosas cartas deles.\n\nO fundamental é pegar as cartas deles... lembre-se disso.\n\nAs páginas que dei a você já dizem direitinho onde os explosivos devem ser plantados. Tudo que você tem que fazer é voltar <vivo/viva> e com aquelas cartas...Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1081] = {title = "A recepção de Tyrande", objective = "Encontre Tyrande Murmuréolo em Darnassus.", description = "<name>, estou certa de que a própria Tyrande Murmuréolo ficaria grata a você por participar desta missão.\n\nPode não parecer, mas você ajudou a dar início a algo que pode muito bem fortalecer nossa posição na Cordilheira das Torres de Pedra.\n\nCaso se considere <um aliado/uma aliada> contra tais atrocidades, procure por Tyrande no Templo da Lua, em Darnassus.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nVeste FlorescenteTúnica GrisCota Encadeada Matagreste\n\nVocê também receberá:\n\nAnel do Donaire Élfico"},
[1082] = {title = "Deixando a Sentinela Tenysil a par de tudo", objective = "Fale novamente com a Sentinela Tenysil no Vale Gris.", description = "<name>, você realizou um grande feito ao nos ajudar aqui.\n\nAs Sentinelas do Vale Gris recompensarão você se informá-las de que Gaxim e eu estamos vivos. Conte a elas que estamos bem e a caminho de deter a Empreendimentos S.A. e curar estas terras.\n\nEncontre Tara Tenysil em Astranaar. Ela ficará satisfeita ao ver que você voltou com boas novas.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1083] = {title = "Espíritos enfurecidos", objective = "Leve 8 Brasas Fumegantes de quaisquer elementais do fogo do Vale Carbonizado a Kaela Umbralança nas Torres de Pedra.", description = "Não sei ao certo o que a Empreendimentos S.A. fez ao Vale, mas é óbvio que foram subjugados pelas criaturas daqui.\n\nEles incendiaram a floresta e usaram suas invenções para dizimar o máximo que puderam do Vale.\n\nMas, inadvertidamente, eles também libertaram algo mais: espíritos flamejantes, elementais de fogo, pedra e fúria.\n\nEstas terras não têm chance de ver Eluna sorrir enquanto essas criaturas permanecerem livres.\n\nTraga-me as brasas fumegantes dos elementais como prova de que você reduziu a população deles.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1084] = {title = "Ancientes feridos", objective = "Leve alguns Estilhaços Corroídos para Kaela Umbralança nas Torres de Pedra.", description = "Parece-me óbvio que os protetores do Vale, os arvorosos, tiveram algo a ver com o recuo da Empreendimentos S.A.. Infelizmente, mesmo esses guardiões, outrora magníficos, corromperam-se.\n\nGaxim acha que a corrupção deles tem alguma ligação com as armas que os goblins usaram enquanto fugiam.\n\nEu disse a ele que eu coletaria uma amostra de um arvoroso ferido para que ele a estudasse e descobrisse se isso é verdade.\n\nTraga-me um pouco dos estilhaços para que o gnomo possa me dizer mais...Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1085] = {title = "Em guarda nas Torres de Pedra", objective = "Fale com Gaxim.", description = "A situação aqui nas Torres de Pedra é obviamente grave para um elfo noturno e um gnomo estarem trabalhando juntos.\n\nA Empreendimentos S.A. vem devastando estas terras e gastei a maior parte do meu tempo pondo fim à destruição.\n\nNão julgue Gaxim prematuramente. Ele entende a importância de trabalharmos juntos.\n\nSe você quiser nos ajudar, comece falando com Gaxim.\n\nLamento por eu ainda não poder mandar você de volta a Tara com boas notícias. Talvez em breve.", progress = nil, completion = "Traiçoeira... traiçoeira e perigosa, é isso que esta terra é.\n\nMas estou aqui para mostrar aos elfos noturnos que as coisas estão um pouco mais seguras com a ajuda da boa e velha engenharia e de uma pitada de magia.\n\nRenegar totalmente a magia não vai ajudar os elfos-norturnos. Eles precisam dela. O mundo precisa dela. E, com engenharia e alquimia para completar, não tem como errar. As possibilidades são infinitas.", rewards = "Você receberá:\n\n"},
[1086] = {title = "Aeroporto de máquinas voadoras", objective = "Leve o Vaporizador Tóxico até o aeródromo da Empreendimentos S.A. e depois fale com a Boticária Zaqueia.", description = "A toxina está pronta. Só falta entregar a encomenda. Você, <name>, fará a entrega.\n\nA Empreendimentos S.A. deve ter uma forma de transportar mensagens e cargas leves entre o quartel general e as operações nas Torres de Pedra. Deve ser algum aeródromo pequeno demais para um zepelim, mas perfeito pra uma máquina voadora. Vasculhe a região nas mãos da Empreendimentos S.A em Torres de Pedra e ache esse aeródromo, se ele existir. Quando o encontrar, é só largar a toxina lá.\n\nLargue e dê o fora.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1087] = {title = "O legado de Cenarius", objective = "Mate 4 Filhos de Cenarius, 4 Filhas de Cenarius e 4 Botânicos Cenarianos para Braelyn Manígnea, no Retiro Rocha do Sol.", description = "Você não pode imaginar o ódio que sinto por esta terra infame. A laia de Cenarius e os druidas kaldorei no Morro das Torres de Pedra empregam seus poderes patéticos na supressão do fluxo da magia, mesmo tão longe ao sul da floresta patética que tanto amam.\n\nEu gostaria de poder voltar a Jaedenar e me regozijar no fluxo da magia, mas meu mestre não permitirá meu retorno até que meu trabalho esteja concluído.\n\nNão me resta dúvida de que você nutre tão pouco apreço pelos elfos noturnos e seus aliados quanto eu. Auxilie-me e nossos objetivos serão ambos satisfeitos.", progress = nil, completion = "Já posso sentir... sim... o delicioso poder... uma inundação avassaladora. Como são tolos os kaldorei, desperdiçando as dádivas deste mundo, desprezando o curso da magia quando ela flui melodicamente pelas veias.\n\nComo é vã uma vida sem esse abraço ardente!", rewards = "Você receberá:\n\n"},
[1088] = {title = "Ornanos", objective = "Leve a Cabeça de Ornanos a Braelyn Manígnea no Retiro Rocha do Sol.", description = "Eles acreditaram que seriam capazes de estancar o fluxo da magia. Estavam errados. Pensaram que conseguiriam ocultar Ornanos, mas terei a cabeça dele!\n\nA magia me concede a verdadeira visão. Sim... lá está ele. Uma encruzilhada... No topo de uma grande árvore na Floresta do Vale Gris. Um nome... Bosque Real. Um dos filhos mais velhos do deus da floresta. Eles retiraram Ornanos do morro antes que eu conseguisse encontrá-lo, mas o tempo dele está acabando.\n\nTraga a cabeça dele para mim e partilharei com você uma poderosa dádiva.", progress = "Quando meu mestre me ordenou que fosse ao Morro das Torres de Pedra, pensei que bastaria esmagar os elfos noturnos que se arrastavam pelas ruínas sagradas de nosso antigo templo de Azshara, maculando a santidade de tudo que tocavam... mas Ornanos escapou das minhas mãos.", completion = "Já faz muito tempo, mas cheguei a conhecer Ornanos. Ele tinha a arrogância da própria raça, considerando-se superior até mesmo aos altaneiros.\n\nEle não parece mais tão orgulhoso, não é mesmo, &lt;name&gt;?\n\nPrometi uma recompensa, e você a terá. Escute.", rewards = "Você receberá:\n\n"},
[1089] = {title = "O Retiro", objective = "Vá ao Retiro da Garra no Morro das Torres de Pedra. Usando a Chave do Guardião do Portal, obtenha os itens escondidos pelos druidas. Use esses itens para abrir o Tesouro do Retiro da Garra.", description = "Os druidas construíram o Retiro da Garra no Morro das Torres de Pedra, e nele guardam muitos itens poderosos.\n\nEles desenvolveram um esquema complexo para impedir que esses bens caiam em mãos erradas.\n\nFelizmente, eu consegui... extrair alguns detalhes de um deles.\n\nEscondida em uma pequena lanterna na entrada do retiro, você encontrará a chave do guardião do portal, com a qual você poderá abrir os baús espalhados pelo retiro. Os itens desses baús permitirão que você abra o cofre, localizado em uma das áreas centrais.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nAnel SagradoChave do Guardião do PortalArmadura da PanteraPerneiras do Implacável"},
[1090] = {title = "Ordens de Hanfritz", objective = "Pegue o minério misterioso com Piznik depois que ele terminar de minerá-lo.", description = "Sabe, eu já fui a segunda pessoa mais importante aqui. Mas tudo mudou quando o Hanfritz tomou o comando. Ele despediu o meu chefe, o Zé Fízzica, e me mandou pra cá pra baixo pra trabalhar na mina. Mas o Zé me falou pra ficar e ver o que está rolando. O novo chefe dele vai pagar bem se a gente conseguir descobrir o plano do Hanfritz.\n\nDe qualquer forma, tudo que eu sei é que o Hanfritz mandou pegar muito desse minério verde dourado aí. Eu posso te dar um pedaço pra levar pro Zé, mas você vai ter que olhar os guardas enquanto eu pego.", progress = nil, completion = "Obrigado, &lt;name&gt;. Teve uma hora em que o negócio lá ficou meio complicado e a gente provavelmente chamou mais atenção do que devia.\n\nMas o importante é que a gente está com o pedaço do minério e precisa levar logo pro Zé.", rewards = "Você receberá:\n\n"},
[1091] = {title = "Deixando Kaela a par de tudo", objective = "Leve as Cartas da Empreendimentos S.A. para Kaela Umbralança.", description = "<name>, leve estas cartinhas a Kaela. Tenho certeza de que ela vai querer saber disso. Ou pelo menos as sentinelas vão.\n\nCertifique-se de contar a ela sobre como fomos bem-sucedidos e como não teríamos conseguido sem um pouco de magia.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1092] = {title = "Ordens de Hanfritz", objective = "Entregue o Minério Não Identificado a Zé Fízzica na Cordilheira das Torres de Pedra.", description = "Eu acho melhor eu pensar em sair daqui, também. Não vai demorar muito até a notícia do que aconteceu aqui chegar nos gerentes, e eles não vão ser muito simpáticos se me encontrarem de novo.\n\nEu vou de fininho encontrar o Zé. Espero que ele consiga me arranjar um emprego novo!\n\nE eu vou tentar arrumar um pouco essa bagunça, mas é melhor você levar logo esse pedaço do minério pro Zé. Ele me disse que ia montar acampamento em algum logar no lado oeste do Rochedo Cortavento. Boa sorte.", progress = "Precisa de ajuda aí?", completion = "Ahhh, eu sabia que eu tinha feito a coisa certa quando mandei o Piznik ficar. Eu tenho certeza que os engenheiros na Inframina vão ficar muito interessados nisso...\n\nClaro que eu vou querer ficar com um pouquinho para mim, mas o que os olhos não veem o coração não sente, certo?", rewards = "Você receberá:\n\nBotas de Mourejar"},
[1093] = {title = "Super Ceifador 6000", objective = "Consiga os Diagramas do Super Ceifador 6000 e entregue-os a Zé Fízzica na Cordilheira das Torres de Pedra.", description = "Mas que coisa horrível a Empreendimentos S.A. fez aqui, hein? O que diabos eles estavam pensando? Uma empresa goblínica contratando um gnomo!\n\nPra minha sorte, o Cobogó reconhece um gênio quando dá de cara com um. Ele me contratou logo depois de terem me chutado!\n\nParece que eles construíram uma máquina gigante de cortar lenha, e o Sput quer que eu roube os diagramas pra ele. Infelizmente, o Hanfritz... maldito gnomo!... quer me ver morto. Você não quer roubar os diagramas pra mim? Eles devem estar com um dos operadores.", progress = "Você encontrou aqueles diagramas? Eu quero impressionar o chefe novo, sabe como é. Se tudo der certo, depois que eu convencer ele da minha lealdade... Bem, até onde ele puder ser convencido... Honra de goblin, sabe?... Enfim, ele vai ficar à vontade pra aproveitar o meu intelecto impressionante.", completion = "Ei, isso aí é o que eu acho que é? Ótimo!\n\nAgora, vamos ver... Aí, sério. Como é que eu vou entender um troço desse? Não dá pra sacar como os gnomos pensam. Cá entre nós, eu já fico surpreso por eles terem conseguido construir a roda. Agora imagina essas máquinas voadoras que eles adoram...", rewards = "Você receberá:\n\n"},
[1094] = {title = "Instruções adicionais", objective = "Entregue o Envelope Selado para Cobogó nos Sertões.", description = "Nesse envelope aí estão todas as informações que eu consegui pescar enquanto eles estavam me chutando porta afora. Documentos internos da Empreendimentos S.A., alguns manuais, coisas técnicas... Ah, e, claro, os diagramas do Ceifador 6000.\n\nO Cobogó está morando na Vila Catraca, lá nos Sertões. Eu não acho que vai ser difícil achar ele. Tipo... o cara é um sujeitinho verde.", progress = "Olá! Como é que eu posso ajudar?", completion = "Fízzica, né? Eu não posso dizer que eu confio nele, mas essa informação parece razoável. O que eu não daria pra tirar a Empreendimentos S.A. da minha cola...\n\nSe bem que eles até bolam umas máquinas boas... Não dá pra negar que a gente se beneficiou com algumas melhorias que eles projetaram.", rewards = "Você receberá:\n\n"},
[1095] = {title = "Instruções adicionais", objective = "Leve as novas ordens a Zé Fízzica na Cordilheira das Torres de Pedra.", description = "Esse lance do gnomo contratado pela Empreendimentos S.A. anda causando um fuzuê na Inframina. Os príncipes mercadores não aprovam o Frustrus andar de conchavo com esse tal Hanfritz.\n\nCharadox pediu pra eu dar um jeito nisso e aposto que foi um, ou vários, príncipes mercadores que deu a ordem. Claro, o Diretor não disse nada. Vamos dizer que é só um palpite.\n\nVerdade ou não, Hanfritz já era, e mandar o Fízzica cuidar dele já serve de teste pra ele.", progress = nil, completion = "Dar cabo do Hanfritz? Taí um prospecto que não me incomoda.\n\nGnomos já não são grande coisa, mas o que descobri do Hanfritz só piora a fama. Pelo visto, ele era um dos engenheiros-chefes em Gnomeregan e estava trabalhando num projeto secreto. Nunca tivemos um infiltrado em Dun Morogh que desse conta de conseguir os detalhes. Foi um fracasso. Os relatos indicam que o troço explodiu na fuça deles.", rewards = "Você receberá:\n\n"},
[1096] = {title = "Hanfritz Schaventorck", objective = "Leve o Braço Mecânico do Hanfritz a Zé Fízzica na Cordilheira das Torres de Pedra.", description = "Eu já encontrei o Hanfritz uma vez, mas, cá entre nós, o cara é feio de doer. A carne dele dá impressão de que está caindo de podre, ele inventou de pôr metal e engrenagens no próprio corpo e trocou alguns membros por outros mecânicos.\n\nMais pro nordeste daqui, em cima do Lago do Rochedo, lá perto da roda d'água. É de lá que o Hanfritz vigia tudo. Eu não tenho como abrir caminho na marra até lá, e muito menos lutar com o Hanfritz... mas você parece capaz de conseguir.\n\nSe você matar ele pra mim, o que eu faço valer o seu esforço!", progress = "O Cobogó vai querer algum tipo de prova de que o Hanfritz morreu. Acho que esse braço mecânico deve servir.", completion = "Olha esse troço... só mesmo um gnomo pra ter uma ideia dessas. Eu não consigo entender por quê que você ia cortar um braço pra botar isso no lugar quando dá pra ficar com os dois braços e botar um terceiro!\n\nGnomos, eu vou te contar...\n\nPelo menos o Hanfritz já era. Tomara que isso sirva seja prova suficiente e o Cobogó me bote pra trabalhar numa das oficinas do Sindicato dos Faz-tudo em Geringontzan ou até quem sabe na Inframina.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nManto do EngenheiroBotas do Escrevente\n\nVocê também receberá:\n\n"},
[1097] = {title = "Tarefa de Elmore", objective = "Fale com Grimand Elmore.", description = "Um dos armeiros de Ventobravo, o Grimand Elmore, mandou avisar que precisa de ajuda com uma entrega. Acho que ele quer enviar uma encomenda para a cidade natal dele, no norte.\n\nQue pernas fortes você tem, hein? Então, se estiver <interessado/interessada> em botá-las para trabalhar, fale com o Grimand. Você até nos seria útil aqui, mas sempre é bom fortalecer os laços com os anões.\n\nO Grimand Elmore fica na loja de armas do Distrito dos Anões, em Ventobravo, na parte nordeste da cidade.", progress = nil, completion = "Você veio me ajudar com a entrega? Muito bom.", rewards = "Você receberá:\n\n"},
[1098] = {title = "Sicários na Presa Negra", objective = "Encontre o Sicário Admeto e o Sicário Vicente.", description = "Nos preparativos do ataque à Presa Negra, dois sicários forma enviados para coletar informações. Já faz vários dias e nada de notícias, mas precisamos desse relatório se quisermos que o ataque dê certo!\n\nEntão, você vai fazer o seguinte, <name>. Vá para o sul, na Bastilha da Presa Negra, e procure pelos Sicários Admeto e Vicente. Encontre-os e, caso necessário, auxilie-os.\n\nEssa é uma missão crítica, mas perigosa, <name>. Não fracasse.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nDragonas Fantasmagóricas"},
[1099] = {title = "Vitória dos goblins!", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nCaixa de Prêmios de Goblin"},
[1100] = {title = "Diário do Monocelha", objective = "Leia o Diário de Henrig Monocelha.", description = "Você abre, com cuidado, o diário desgastado de Henrig Monocelha...", progress = "Pelo seu aspecto de consternação, vejo que tem alguma coisa errada...", completion = "Quer dizer que você encontrou esse diário no corpo de um anão? Dê-me um instante para folheá-lo...", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[1101] = {title = "A bruxa má do Urzal", objective = "Leve o Medalhão da Talhaflanco para Falfindel Trilhemas em Thalanaar.", description = "Coitado do Heralath! E desse anão... o Monocelha, que alma valente.\n\nDevemos honrar a bravura deles. Essa bruxa... Charlga Talhaflanco... precisa ser detida.\n\nClaro, infiltrar o Urzal será arriscado. Não há tempo para comunicar Darnassus. <name>, reúna um grupo e assassine a bruxa.\n\nTraga-me o medalhão da Talhaflanco como indício de sua morte.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nOmbreiras BeríliasCinturão PunhopétreoBroquel Marmóreo\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\nBacamarte \"Olho de Mago\""},
[1102] = {title = "Um destino vingativo", objective = "Leve o Coração da Talhaflanco para Auld Agulha de Pedra no Penhasco do Trovão.", description = "Caerne é um nobre líder por ter unificado nosso povo aqui no Penhasco do Trovão.\n\nMas, diferente de outros, não perdoo tão facilmente quem nos expulsou de nossas terras ancestrais. Meu povo habita os Sertões Meridionais há décadas. Essa terra era sagrada para nós.\n\nUm destino vingativo está reservado para a bruxa Charlga Talhaflanco, que está formando um exército vil no interior do Urzal dos Tuscos.\n\nTraga-me o coração de Talhaflanco para que eu repouse em paz, <name>.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nOmbreiras BeríliasCinturão PunhopétreoBroquel Marmóreo\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[1103] = {title = "O clamor da Água", objective = "Leve uma Sapta da Água para Tiev Borduno na Floresta de Pinhaprata se não tiver conseguido falar com a Manifestação Menor da Água.", description = "Saudações, <mano/mana>! Tu também tá aqui pra purificar as águas e mostrar o que tu manja pra Islen?\n\nAcabei de dar cabo no espírito d'água corrompido e tava pronto pra botar as braçadeiras e as gotas d'água no braseiro.\n\nHein? Aaahhh. Tiev sabe bem o que rola se o angu desandar. Bem, se tu quiser ver o espírito de água pura de novo, Tiev te dá o esquema.\n\nÉ só tu me trazer sapta da água da Islen pra mostrar que tu tá <preparado/preparada> que eu te espero pra botar meus badulaques.", progress = nil, completion = nil, rewards = nil},
[1104] = {title = "Peçonha do salar", objective = "Leve 6 Peçonhas Salgadas de Escorpídeo para Xabu Latafuso no Salar Cintilante.", description = "Bolei vários desenhos para nosso carro-foguete! Vários! Preciso testar todos eles!\n\nEstou trabalhando em um novo tipo de combustível, com uma combustão muito, muito elevada! Ainda não acertei a medida, mas acho que sei o que falta...\n\nTem uns escorpídeos no Salar Cintilante com uma peçonha cheia, mas cheia mesmo de sal. Não tem igual! E é disso que eu preciso!\n\nTraz essa peçonha e confie em mim... nosso carro vai voar!!", progress = "Hihihi! Conseguiu a peçonha?", completion = "Ótimo! Essa peçonha vai funcionar que é uma beleza! Valeu, &lt;name&gt;!\n\nAgora é só eu ferver, aplicar o capitômero, adicionar um nulicatalisador e... Parafusos espanados! Cadê minhas anotações!?", rewards = "Você receberá:\n\n"},
[1105] = {title = "Cascos endurecidos", objective = "Leve 9 Cascos Endurecidos de Tartaruga para Capu Latafuso no Salar Cintilante.", description = "Meu irmão está trabalhando em um novo combustível para o carro-foguete. Ele é brilhante, sem dúvida. Tão brilhante que... acho que o novo combustível do projeto é tão forte que vai devorar o chassi do carro!\n\nQuero reforçar os tanques de combustível para evitar isso.\n\nAs tartarugas do salar têm uns cascos superduros. Quero criar novos tanques para o carro-foguete com eles. Tanques que aguentem o novo combustível do meu irmão.\n\nVocê tem cara de saber o que faz. Pode me arranjar uns cascos?", progress = "Meu irmão está progredindo no novo projeto de combustível e estou ficando preocupado. Preciso começar logo a trabalhar num tanque de combustível de casco de tartaruga o quanto antes!\n\n<name>, conseguiu os cascos?", completion = "Você trouxe! Valeu, &lt;name&gt;!\n\nUau, esses cascos são mais resistentes do que imaginei! Heh, acho que vou acabar com um monte de ferramentas tortas trabalhando neles!", rewards = "Você receberá:\n\n"},
[1106] = {title = "Martek, o Exilado", objective = "Entregue a carta do Xabu para Martek, o Exilado, nos Ermos.", description = "Estou desenvolvendo um novo motor que vai fazer o carro andar tão rápido, mas tão rápido, que vai decolar, pode apostar! Só preciso de uns pistões que aguentem um tranco muito pesado. Todas as minhas tentativas fracassaram porque não consigo fazer pistões tão resistentes!\n\nMas acho que sei de alguém que consegue: Martek, o Exilado.\n\nEle é um gênio inigualável do trabalho com metais. Entregue essa carta para ele, sei que ele pode ajudar!\n\nO problema é que ele mora muito, muito longe. Em Azeroth, nos Ermos, num acampamento com um goblin!\n\nEncontre-o!", progress = "Ora, ora, você me encontrou. Bom pra você.\n\nVeio aqui por causa do machado que fiz pro Gath'Ilzogg? Ou para defender a honra da Duquesa Pamaia?\n\nQuer saber, não importa. Se veio aqui pra lutar, então vamos logo com isso...", completion = "Xabu te mandou? Ora, por que não disse logo? Claro que ajudo. Xabu e o irmão dele são umas das poucas pessoas que não querem a minha cabeça!\n\nEntão o Xabu quer uns pistões, hein? Pistões resistentes o bastante para aguentar o tranco do que ele está projetando. Se bem conheço o Xabu, esse carro novo teve ser um vulcão sobre rodas!", rewards = "Você receberá:\n\n"},
[1107] = {title = "Barbatanas de cauda incrustada", objective = "Leve 10 Barbatanas de Cauda Incrustada para Capu Latafuso no Salar Cintilante.", description = "Nosso carros-foguete é a coisa mais rápida da chapada! É tão rápido que pode acabar sendo desmantelado pelo atrito!\n\nPara evitar isso, preciso criar um composto para revestir as partes móveis do carro. Deve ser forte, mas não quebradiço, e precisa ser resistente a corrosão salina, senão o contato com o Salar Cintilante vai corroer tudo.\n\nConhece o Arrecife Torpe, <name>? Fica perto da costa da Selva do Espinhaço. Lá tem uns murlocs Escama Salgada e a barbatana da cauda deles é um ingrediente essencial para o meu composto.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[1108] = {title = "Indúrio", objective = "Leve 10 Lâminas de Indúrio para Martek, o Exilado, nos Ermos.", description = "Não estou aqui só para ficar longe das pessoas. Os Ermos são ricos em metais, <name>, isso para quem tiver peito para minerá-los.\n\nPara nossa sorte, dizem que o metal conhecido como indúrio tem alta resistência contra o calor e a pressão. Ele deve servir no carro do Xabu.\n\nMas é melhor ter certeza. Traga umas amostras de indúrio que eu vou testar suas propriedades.\n\nO veio de indúrio verdadeiro corre nas profundezas de Uldaman, mas os troggs da Abóbada de Pedra dos Ermos costumam carregar lâminas desse metal. Busque essas lâminas para mim.", progress = "Encontrou alguma lâmina de indúrio, <name>? Minha forja já está no ponto para fazer o teste.", completion = "Ah, elas vão servir. Agora, vamos botar um quente nelas para ver como se saem...", rewards = "Você receberá:\n\n"},
[1109] = {title = "Matéria orguânica", objective = "Leve 1 porção de Guano de Morcego para o Mestre-boticário Faranello na Cidade Baixa.", description = "Como pode ver, <name>, aqui na Real Sociedade de Boticários nos engajamos em experimentos muito... interessantes.\n\nComo mestre aqui, meu dever é supervisionar as investigações alquímicas mais ambiciosas. Tanto trabalho e tão pouco tempo!\n\nVocê para ter viajado muito para <um/uma> <class>. Talvez possa me ajudar.\n\nPreciso de uma substância rara. É um material podre derivado de uma rara espécie de morcego encontrada apenas naquele buraco nojento do Urzal dos Tuscos. Traga-me o guano dos morcegos do Urzal para darmos início aos trabalhos...", progress = "Ah, você voltou. Conseguiu recolher algum guano de morcego?", completion = "Esplêndido! Era desse impulso inicial que precisávamos, &lt;name&gt;.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[1110] = {title = "Peças de carro-foguete", objective = "Leve 30 Peças de Carro-foguete para Kravel Barbakarvão no Salar Cintilante.", description = "Hehe, com tantas raças disputando corrida aqui, capaz que um ou outro infortúnio não fosse acontecer, né? Se deres uma banda pelo Salar Cintilante, vais te deparar com os sinais de acidentes ocorridos. Montes de sucatas de carros-foguete esparramadas por toda parte!\n\nE os gnomos e goblins pagam uma ponchada de pilas nessas peças. Eles se atiram atrás de geringonças para encangalhar nos carros.\n\nTe manda logo e me traz essas peças. Tu me traz uma pilha que te pago uma dinheirama.", progress = "Então, <name>, deste uma banda pelo salar? Deste conta de arranjar as peças que te pedi?", completion = "Mas bá, mira essa baguaça! Esse pilhão de sucata tem uma coisarada buenacha. Jogo contigo que posso me apartar disso por uma dinheirama.\n\nE... aquilo ali no meio daquelas chapas de metal é uma orelha?? Ai, ai, ai! Heh, eu não queria estar no couro desse piloto!\n\nGrato, &lt;name&gt;. Toma teus pilas. E ainda te dou um conselho...\n\nSe olhares com intento, verás que sempre se pode ganhar um troco na tragédia do outro.", rewards = "Você receberá:\n\n"},
[1111] = {title = "Mestre Portuário Caruncho", objective = "Leve a Encomenda de Peças de Kravel para o Mestre Portuário Caruncho na Vila Catraca.", description = "Meu trampo é arranjar o que me pedem, e aqueles gnomos ali querem encomendar umas peças. As peças já foram bandeadas para a Vila Catraca faz uma semana, mas não consegui me atinar de fazer essa troteada.\n\nTás a fim de dar uma pernada? Te dirige até a Vila Catraca e passa este pedido de peças para o Mestre Portuário Caruncho. Tu encontras ele pelas docas.\n\nTu me faz essa mão e te arranjo mais serviços.\n\nServiços, bagual, serviços bem rentáveis...", progress = "Desembucha! Veio entregar ou fazer uma encomenda?", completion = "Ah, sim, as peças do Kravel. Que bom que você veio. Isso tava aí largado faz dias e achei que já iam enferrujar!", rewards = "Você receberá:\n\n"},
[1112] = {title = "Peças para Kravel", objective = "Leve as Peças de Kravel para Kravel Barbakarvão no Salar Cintilante.", description = "Taí a encomenda do Kravel, <name>. Quando entregar, diz pra ele que... se ele quiser mais encomendas especiais, é melhor pagar o que tá pendurado!", progress = "Conseguiste passar na Vila Catraca e pegar as peças?", completion = "Bá, conseguiste, maravilha! Agora é só levarmos para os gnomos. Só que antes...\n\nVamos deixar de fora um mixe de um item...", rewards = "Você receberá:\n\n"},
[1113] = {title = "Corações do fanatismo", objective = "O Mestre-boticário Faranello, da Cidade Baixa, quer 20 Corações do Fanatismo.", description = "Você deve estar se perguntando para quê preciso de um tipo raro de guano. Bem, quero observar nesse experimento o efeito que duas substâncias quimicamente antagônicas terão em contato, especialmente quando combinadas com um agente muito tóxico para assegurar os... resultados mais pavorosos.\n\nNos picos ao nordeste das Clareiras de Tirisfal se encontra o Monastério Escarlate. Lá congregam os discípulos insanos do Cruzada Escarlate. Mate-os e traga-me seus corações zelosos.\n\nNossa concocção será a mais vil de todas!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[1114] = {title = "Entrega para os gnomos", objective = "Leve as Peças Frágeis de Carro para Xabu Latafuso no Salar Cintilante.", description = "Agora que eu já consegui o que queria, leva o resto das peças para o Xabu Latafuso. Ele vem esperando por elas faz tempo, não vai nem reparar que eu subtraí uma pecinha...\n\nSe eu fosse você, ficava caladit<o/a> sobre o assunto. É coisa pouca, uma coisita de nada.\n\nVai por mim.", progress = "Trouxeste alguma coisa pra mim? Maravilha! Deixe-me ver!", completion = "Aí estão minhas travas hidráulicas e rebites de repetição! Estão mais que perfeitos. Obrigado!", rewards = "Você receberá:\n\n"},
[1115] = {title = "O boateiro", objective = "Fale com Krazek na Angra do Butim.", description = "Agora que prestamos esse serviço aos gnomos, é hora de fazer um favorzito aos goblins! \n\nEu ouvi dizer que Narque Fervapor, o piloto goblin, nem sempre usou esse nome. Me pergunto por que teria trocado de nome. Se é um segredo e... se ele dá muito valor ao tal segredo.\n\nSe alguém conhece a verdade por trás disso, esse alguém é o Krazek da Angra do Butim, lá na ponta sul da Selva do Espinhaço. Vai ter uma conversinha com o Krazek sobre o nosso piloto goblin.\n\nSe arrancar alguma informação dele, a gente vai lucrar em cima.", progress = nil, completion = "Você quer saber do Narque Fervapor? É um piloto de corrida do Salar Cintilante, não é isso?\n\nÉ, acho que consigo descobrir alguma coisa... Mas não vai ser fácil. Nem rápido.\n\nE nem barato.", rewards = "Você receberá:\n\n"},
[1116] = {title = "Pó onírico no pantanal", objective = "Leve 10 Partículas de Pó Onírico para Krazek na Angra do Butim. O Pó Onírico é coletado dos dragonetes no Pântano das Mágoas.", description = "Levantar informações sobre o Narque é um favor e tanto, <name>. É um favor e tanto... Você já veio lá do Salar Cintilante... topa viajar um pouquinho mais?\n\nEnquanto eu entro em contato com as minhas fontes, você bem que podia buscar uma coisinha para mim, hein?\n\nHum... mas o quê? Que tal buscar o pó onírico dos dragonetes, lá no Pântano das Mágoas. Isso mesmo, um pozinho onírico nas ventas seria ótimo!\n\nTraga o pó, <name>, e eu lhe passo a informação.", progress = "Já voltou? Trouxe meu pó onírico?", completion = "Ora, vejam só. Você encontrou o pó onírico!\n\nE que tal o pântano, &lt;name&gt;? Meio difícil achar uma refeição quente por lá, é ou não é?", rewards = "Você receberá:\n\n"},
[1117] = {title = "Rumores para Kravel", objective = "Leve os Rumores Goblínicos para Kravel Barbakarvão no Salar Cintilante.", description = "Eu tive que cobrar alguns favores, abrir mão de uma garrafa rara de vinho élfico e fazer um trabalho pesado de investigação! Mas descobri a tal informação.\n\nAqui, escrevi uma carta com dados muito interessantes sobre nosso amigo Narque Fervapor. Faça o que quiser com ela — é perigoso isso ficar comigo.\n\nSó não conte a ninguém onde foi que conseguiu esse negócio!", progress = "Descobriu alguma coisa sobre o Narque Fervapor? Algo que ele queira manter em segredo??", completion = "Ora, ora! Isso é interessante... muito interessante. He, é melhor eu me curvar na próxima vez que passar pelo piloto goblin.\n\nIsso vale uma grana, &lt;name&gt;. Eu aposto que o Narque daria uma pequena fortuna para manter esse segredinho... em segredo.", rewards = "Você receberá:\n\n"},
[1118] = {title = "De volta à Angra do Butim", objective = "Leve o Plano de Kravel para Cranque Mirrabolha na Angra do Butim.", description = "<name>, você é <um/uma> <class> de confiança, tanto para fazer o trabalho quanto para ficar de bico fechado.\n\nTem uma ideia fermentando na minha cabeça. É arriscada, mas se der certo vai dar muito lucro! E acho que você é a pessoa ideal para o serviço.\n\nPara começar, você terá que voltar à Angra do Butim. Foi mal, <name>. Eu sei que você acabou de voltar de lá. Mas desta vez você vai procurar o Cranque Mirrabolha.\n\nEntregue esta carta para ele. Se ele lhe pedir alguma coisa, faça o que puder para ajudar. Vai valer a pena.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1119] = {title = "Preparado de Zanzil e Cerveja Escura do Tonto", objective = "Leve a Cerveja Escura do Tonto para Kravel no Salar Cintilante.", description = "Misturando o preparado de Zanzil com cerveja, eu consegui uma bebida bem forte: a Cerveja Escura do Tonto. Acho que é isso que o Kravel queria, mas é melhor você testar e me contar os resultados.\n\nAh, <name>, não fique guardando esse negócio por muito tempo. O preparado de Zanzil produz um efeito estranho na levedura, intensifica a fermentação. Esse negócio estraga rápido.", progress = "Oi, <name>. O Mirrabolha tinha como ajudar?", completion = "Maravilha! O Mirrabolha tem a manha! Preciso agradecer o cara na próxima.\n\nE muito obrigado, &lt;name&gt;. Você vem me prestando um ótimo serviço, nós dois vamos fazer uma dinheirama...", rewards = "Você receberá:\n\n"},
[1120] = {title = "Deixe os gnomos bêbados", objective = "Leve a Cerveja Escura do Tonto para o Chefe dos Boxes Gnomo.", description = "Precisamos testar a Cerveja Escura do Tonto com uma das equipes. Se quiser testar com os gnomos, pode levar: é só dar a cerveja para o gnomo chefe dos boxes.\n\nMas não fique enrolando. Uma hora a cerveja estraga.", progress = "Oi! Trouxe alguma coisa pra mim?", completion = "Cerveja? Quanta consideração! Obrigado!\n\nNão devíamos beber tão perto assim da corrida, mas... um pouquinho só não fará mal aos rapazes. Além do mais, não deve ser tão forte assim, não é mesmo?", rewards = "Você receberá:\n\n"},
[1121] = {title = "Deixe os goblins bêbados", objective = "Leve a Cerveja Escura do Tonto para o Chefe dos Boxes Goblin.", description = "Precisamos testar a Cerveja Escura do Tonto com uma das equipes. Se quiser testar com os goblins, pode levar: é só dar a cerveja para o chefe dos boxes goblin.\n\nMas não fique enrolando. Uma hora a cerveja estraga.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1122] = {title = "Fale novamente com Mirrabolha", objective = "Leve o relatório sobre a Cerveja Escura do Tonto para Cranque Mirrabolha na Angra do Butim.", description = "A Cerveja Escura do Tonto foi um sucesso! Eu fiz uma aposta na próxima corrida... tomara que a equipe bêbada tenha avacalhado bastante o carro!\n\nSó falta mais uma coisa: levar esse bilhete para o Cranque Mirrabolha na Angra do Butim. Ele vai gostar de saber do resultado.\n\nE tomara que a gente consiga mais dessa cerveja pras próximas corridas!", progress = "Oi, <name>. A cerveja funcionou? A parada é forte, não é?", completion = "Diz aqui que a Cerveja Escura do Tonto foi um sucesso. Que ótima notícia!\n\nSe quiser mais, é só me falar e buscar um pouco do Preparado de Zanzil.", rewards = "Você receberá:\n\n"},
[1123] = {title = "Ravine Saturna", objective = "Fale com Ravine Saturna no Refúgio Noturno, que fica na Clareira da Lua. A Clareira da Lua fica entre a Selva Maleva e Hibérnia, uma estrada que sai do Domínio dos Presamatos leva até lá.", description = "Ravine Saturna, o senescal do Guardião Remulos, na Clareira da Lua, procura alguém disposto a explorar os vastos ermos ao oeste de Tanaris e de Un'Goro. Ele se encontra no Refúgio Noturno, o vilarejo mais expressivo da região.\n\nÉ preciso que você compreenda a importância da sua presença. As relações entre os elfos noturnos de Darnassus e o Círculo Cenariano na Clareira da Lua vêm ficando mais tensas. Deixe de lado as questões raciais e busque a sabedoria de seus ancestrais para lidar com o Círculo, <name>. Boa sorte.", progress = nil, completion = "Meu nome é Ravine Saturna. Em nome do Guardião Remulos e de todos os moradores desta clareira sagrada, eu lhe dou as boas-vindas.\n\nFico grato pelo interesse e pelo entusiasmo ao vir ajudar o Círculo Cenariano. Os taurens demonstraram possuir grande afinidade pela doutrina de Cenarius e, por intermédio deles, a Horda é bem-vinda na clareira. A preservação de Azeroth é um objetivo partilhado por todos nós.\n\nVocê está entre amigos aqui, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[1124] = {title = "Terras Devastadas", objective = "Mostre a Carta de Ravine para Layo Golpestela perto do Repouso dos Valorosos, o cemitério de Silithus.", description = "A oeste da Cratera Un'Goro, passando pela serra ao noroeste, existe uma terra agreste: Silithus. Poucos sabem de uma guerra antiga, travada entre os elfos noturnos e um poder alienígena maligno naquela região. O mal foi selado, mas suspeitamos que forças inimigas queiram despertar esse... terror inconcebível.\n\nVá atrás do Layo Golpestela, um de nossos agentes em Silithus. Procure-o no Repouso dos Valorosos, o lugar onde descansam os mortos no deserto. Entregue-lhe esta carta e você ganhará a confiança dele.", progress = "Esta terra é perigosa demais para tratar de banalidades, <class>. O que o traz aos ermos de Silithus?", completion = "Foi o Ravine quem o mandou, não foi? Bem, considere-se bem-vindo... na medida em que for possível sentir-se bem-vindo nesta terra abandonada pelos deuses.\n\nEu ainda não tinha nascido na época das grandes atribulações que afligiram a região, mas em algumas partes do deserto ainda se vislumbra esse passado esquecido. Com sua ajuda, pode ser que Círculo Cenariano consiga dar fim a um perigo que já devia ter morrido há várias gerações.", rewards = "Você receberá:\n\n"},
[1125] = {title = "Os espíritos de Vento Sul", objective = "Liberte os espíritos de 8 Druidas Torturados e 8 Sentinelas Torturadas na Vila do Vento Sul, depois fale novamente com Layo Golpestela no Repouso dos Valorosos, o cemitério de Silithus.", description = "Ao sudoeste daqui jaz um lugar trágico: as ruínas da Vila do Vento Sul. É por lá que precisamos começar, <name>.\n\nAté onde sabemos, a vila foi um dia a base de operações dos elfos noturnos em Silithus. Há muito que foi tomada pelas colmeias de silitídeos que infestam o deserto. Imagine o horror que não deve ter sido quando a vila foi atacada...\n\nO espírito torturado dos druidas e das sentinelas ainda vaga a esmo pelas ruínas. Vá explorar o lugar e trate de libertar a alma daquelas criaturas miseráveis desse suplício.", progress = nil, completion = "Eu já imaginava que aquela colmeia imensa perto das ruínas tivesse alguma coisa a ver com a destruição da vila. Pelo que tudo indica, minhas suspeitas se confirmam. Aquela colmeia apresenta um risco para tudo que quisermos fazer aqui.\n\nEu agradeço por ter tido a bondade de libertar aquelas almas torturadas. Sei que não foi uma tarefa fácil. Porém, quando você estiver disponível, eu gostaria de lhe pedir mais um favor...", rewards = "Você receberá:\n\n"},
[1126] = {title = "A colmeia da torre", objective = "Escale a torre da Vila do Vento Sul e descubra um jeito de despertar a colmeia. Leve tudo de estranho que encontrar durante a missão para Layo Golpestela no Repouso dos Valorosos, o cemitério de Silithus.", description = "Precisamos cuidar daquela colmeia na Vila do Vento Sul, e acho que sei como fazer isso. Pela minha experiência, as colmeias dos silitídeos são muito sensíveis ao que acontece em volta. Isso explica por que atacaram ao ouvir os estertores mortais dos espíritos.\n\nOs silitídeos têm mais força na torre — aquela rodeada pela nuvem de vespas. Escale a torre e faça alguma coisa para agitar a colmeia. Volte para me contar o que viu por lá, mas tome cuidado! Se descobrir alguma coisa que possa nos servir, traga para mim.", progress = "E então, deu certo? Você continua inteiro, já é uma boa notícia. Descobriu alguma coisa?", completion = "Mas que diabos... o que é isso?\n\nTem o formato de... uma caixa, será? Está encrustada para valer. Espere, tem uma parte exposta... esse negócio embaixo dessa meleca toda é um livro?\n\nAcho que você fez um achado e tanto, &lt;name&gt;... Bom, tomara que seja. Vai ver é um diário de tempos antigos, que foi preservado por essa carapaça.\n\nAcho que sei como descobrir que negócio é esse.", rewards = "Você receberá:\n\n"},
[1127] = {title = "Cerveja Escura do Tonto", objective = "Leve 12 Preparados de Zanzil para Cranque Mirrabolha na Angra do Butim.", description = "Quer um pouco de Cerveja Escura do Tonto, né? Está pensando em voltar para as pistas?\n\nCerto, traga um pouco mais do preparado de Zanzil, que eu preparo a cerveja.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nCerveja Escura do Tonto"},
[1130] = {title = "Melor mandou lembranças", objective = "Fale com Melor Casco de Pedra no Penhasco do Trovão.", description = "<name>, depois que soube dos seus feitos, Melor Casco de Pedra separou uma tarefa para você.\n\nCaso tenha interesse, vá para o Penhasco do Trovão e converse com ele. Ele se encontra no Platô dos Caçadores.", progress = nil, completion = "Então você veio, &lt;name&gt;. Está preparado para desbravar terras distantes?\n\nSe estiver, jovem &lt;class&gt;, ouça minha proposta...", rewards = nil},
[1131] = {title = "Estalaço", objective = "Leve a Costela do Estalaço para Melor Casco de Pedra no Penhasco do Trovão.", description = "Às vezes caçamos em busca de comida. Às vezes caçamos em busca de honra. Às vezes caçamos para receber os ensinamentos da Mãe Terra.\n\nMas a caçada em questão não se encaixa em nenhuma dessas categorias. A criatura que você vai abater não tem a bênção da Mãe Terra.\n\nA hiena Estalaço ronda as Mil Agulhas, ao sul dos Sertões. É um bicho tirano, que atrai outras hienas para espalhar medo e derramar o sangue dos bichos mais mansos.\n\nEncontre o Estalaço, <name>. Encontre-o e derrote-o.", progress = "Derrotou o terrível Estalaço, <name>?", completion = "Ao derrotar o Estalaço, você livrou as Mil Agulhas de uma mácula terrível.\n\nEssa, &lt;name&gt;, foi a sua lição hoje.", rewards = "Você receberá:\n\n"},
[1132] = {title = "Fiora Longorelhas", objective = "Fale com Fiora Longorelhas em Theramore.", description = "Ah, navegar pelos mares outra vez! Sentir o beijo do vento e ser embalado pelas ondas como fazia minha mãezinha, tempos atrás!\n\nAh, quisera eu ter a sua sorte, <class>, pois seu futuro é o mar!\n\nÉ meu dever contar aos espíritos aventureiros da terra de Kalimdor, a terra das oportunidades! Se quiser tentar a sorte além-mar, é só embarcar com destino ao lindo porto de Theramore. Fale com a minha parceira, a elfa Fiora Longorelhas.\n\nEla dará início à sua saga em Kalimdor!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1133] = {title = "Jornada para Astranaar", objective = "Fale com Shindrell Fogofugaz em Astranaar.", description = "Se você acabou de chegar de Menethil, a primeira coisa que precisa fazer é... ir para Astranaar. Tenho certeza de que <um herói/uma heroína> da Aliança tão <dedicado/dedicada> como você será de grande ajuda por lá. Ofereça ajuda a Shindrell Fogofugaz.\n\nMas não diga quem <o/a> enviou. Ela não sabe quem eu sou...", progress = nil, completion = nil, rewards = nil},
[1134] = {title = "Os asaltivas das Torres de Pedra", objective = "Leve 12 Vesículas de Peçonha de Asaltiva para Shindrell Fogofugaz em Astranaar.", description = "Não estamos em guerra declarada contra a Horda, mas a paz é frágil. Seus agentes estão sempre agindo contra nós, porém em segredo.\n\nUma dessas manobras nos foi revelada por fontes secretas em Theramore, segundo as quais os orcs vêm recolhendo veneno das asaltivas, perto do Lago Mirkfallon, na Cordilheira das Torres de Pedra ao sul. Depois os assassinos orcs usam o veneno em massacres perpetrados contra a Aliança!\n\nSe isso for verdade, nós temos que detê-los, <name>. Precisamos secar a fonte do veneno.", progress = "Conseguiu as vesículas de peçonha dos asaltivas, <name>? Não podemos deixar os orcs usarem o veneno daqueles bichos contra nós!", completion = "Obrigada, &lt;name&gt;. É triste ter que ordenar a matança de criaturas que não têm culpa de nada disso, porém esse é meu dever.\n\nA morte das asaltivas pode pesar no seu peito, meu amigo, mas que ele se encha de orgulho por ter livrado nossos aliados do veneno daquelas criaturas.", rewards = "Você receberá:\n\n"},
[1135] = {title = "Veneno de Alcândora", objective = "Leve 10 Vesículas de Peçonha de Alcândora para Fiora Longorelhas em Theramore.", description = "Como você já deve saber, os orcs costumam adestrar mantícoras para usar de montaria. O que nem todo mundo sabe é que o veneno da mantícora, em determinados círculos, é mais valioso do que o próprio animal. Se você não tiver medo de sair à caça de uma manticorazinhas... eu tenho um serviço para você.\n\nExiste um lugar chamado Alcândora na região das Mil Agulhas, a oeste daqui. As mantícoras de Alcândora têm um veneno potente. Potente e valioso.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nCinto Pairavento"},
[1136] = {title = "Bocarragelo", objective = "Leve a Juba de Bocarragelo para Melor Casco de Pedra no Penhasco do Trovão.", description = "<name>, é hora de viajar para terras longínquas. Você já está preparad<o/a>.\n\nVocê caçará o Bocarragelo, um yeti das Montanhas de Alterac. É um bicho esquivo, sua pelagem branca se camufla na neve das montanhas. Raramente ele se mostra.\n\nMas eu lhe ensinarei como armar um chamariz. Aqui, tome este pergaminho. Eu escrevi aí como fazer para atrair o Bocarragelo.\n\nAnde logo, <name>. A jornada será longa e muitos desafios <o/a> aguardam. Somente ao regressar com a juba de Bocarragelo sua tarefa estará concluída.", progress = "Já voltou das Montanhas de Alterac, <name>? Enfrentou aquele Bocarragelo arredio?", completion = "Só com garra e astúcia seria possível encontrar e derrotar o Bocarragelo, &lt;name&gt;. Você possui ambas as coisas.\n\nSeu caminho já o levou aos vales das Mil Agulhas e às grandiosas Montanhas de Alterac. Um dia ele ainda o levará a todos os cantos da Mãe Terra, pois tal é o caminho do caçador.", rewards = "Você receberá:\n\nCocar do Caçador EspiritualCarcaça Fresca"},
[1137] = {title = "Um bizu para o xabu", objective = "Fale com Xabu Latafuso no Salar Cintilante.", description = "O indúrio tem uma resistência incrível ao calor! Vai ser perfeito para os pistões do Xabu. Mas ele vai precisar do veio de indúrio verdadeiro, só as lascas não vão servir.\n\nPara extrair esse minério, só nas profundezas de Uldaman, na fronteira norte dos Ermos. A fonte boa mesmo de Indúrio fica lá.\n\nMande lembranças ao Xabu Latafuso e transmita as informações para ele.\n\nBoa viagem, <name>. A gente vai acabar se vendo de novo – depois que o Xabu ficar sabendo do indúrio, na certa vai mandá-<lo/la> para Uldaman atrás do minério!", progress = nil, completion = "&lt;name&gt;, já voltou? Encontrou o Martek? Falou com ele? O que ele disse? Ele vai ajudar??\n\nConte logo, criatura, vamos!!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nIsqueiro Zippy do XabuLuvas do Mecânico Gnômico\n\nVocê também receberá:\n\n"},
[1138] = {title = "Frutos do mar", objective = "Recolha 6 Pedaços Delicados de Caranguejo para Gubber Blump em Auberdine.", description = "Eu adoro caranguejo. O caranguejo é o verdadeiro fruto do mar! Dá para assar, grelhar e cozinhar. Fica bom frito, refogado e salteado. Tem também a salada de caranguejo, a sopa de caranguejo, o caranguejo de panela, o caranguejo picante, o caranguejo-limão, o caranguejo-raiz e o caranguejo-surpresa-de-Altaforja. É... acho que é isso.\n\nNo momento, eu estou pescando, então não tenho como catar caranguejo. Dá para catar nos tiscorais e nos maretiscos incrustados. Dá para fazer muita coisa. Dá para assar, grelhar e cozinhar...", progress = "...caranguejo-limão, caranguejo-raiz e caranguejo-surpresa-de-Altaforja. Prontinho.\n\nAh, oi, <name>. Você trouxe aqueles pedacinhos de caranguejo que eu lhe pedi?", completion = "Uau, esses pedações de caranguejo estão perfeitos. Obrigado, &lt;name&gt;! Eu pesquei esse negócio agorinha há pouco. De repente tem serventia para você. Para cozinhar caranguejo é que não vai servir.\n\nEu já lhe falei o quanto gosto de caranguejo, não falei?", rewards = "Você vai poder escolher uma dessas recompensas:\n\nLuvas de DebulhaBotas Crustáceas\n\nVocê também receberá:\n\n"},
[1139] = {title = "As tabuletas perdidas da vontade", objective = "Encontre a Tabuleta da Vontade e leve-a para o Conselheiro Belgrum em Altaforja.", description = "Nós atrapalhamos o clã Umbraforja ao barrar o Pico da Rocha Negra, mas ainda falta cumprir a missão do Pé-de-malho. Ele não pode ter morrido em vão. O sonho dele era encontrar a Tabuleta da Vontade: uma pedra onde os titãs escreveram uma magia que dava vida às suas criações, fazendo com que tivessem vontade própria, ou pelo menos é o que reza a lenda.\n\nNós temos que buscar essa tabuleta antes que o clã Umbraforja ponha as patas nela.\n\nVá a Uldaman, encontre a tabuleta e volte para cá.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nMedalha de Bravura"},
[1140] = {title = "A torre de Althalaxx", objective = "Liberte a alma dos Altaneiros na Trilha da Noite e em Satyrnaar, depois fale novamente com Dinis, o Purificador, no Entreposto de Maestra.", description = "A caligrafia dos orcs é pouco mais legível que os garranchos de uma criança. Muitos membros da ordem desdenharam de mim quando decidi aprender a língua deles, porém isso ajudaria a compreender melhor o inimigo.\n\nPelos rabiscos de Ilkrud, eu diria que Athrikus ainda está com duas gemas anímicas, uma na Trilha da Noite e a outra em Satyrnaar, duas regiões dominadas por sátiros, na parte nordeste do Vale Gris.\n\nTome cuidado, <name>, os templos macabros dos sátiros são perturbadores, mesmo para um <class> calejad<o/a> como você.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1141] = {title = "Vara de canoeiro, prefere pescar", objective = "Pesque 6 Garoupas da Costa Negra para Gubber Blump em Auberdine.", description = "Eu adoro pescar. A família Blump tem fama de pescadora. Meu nome é Gubber Blump, eu sou pescador.\n\nTem um peixe que eu gosto de pescar por estas bandas chamado Garoupa da Costa Negra. Eu saía de barco para pescar garoupa, mas quase não saem mais barcos desde que os murlocs pintaram na área. Aposto que estão comendo todas as garoupas.\n\nDiga lá, que tal me ajudar a pescar umas Garoupas da Costa Negra? Eu lhe dou uma Vara de Pescar da Família Blump se você me ajudar! É uma vara boa que só para pegar peixe.", progress = "Dá para preparar um monte de receitas com as Garoupas da Costa Negra. São boas para comer, mas gostoso mesmo é pescar.\n\nJá pegou alguma Garoupa da Costa Negra?", completion = "São umas baitas garoupas, &lt;name&gt;! Obrigado pela ajuda!\n\nTu pesca quase tão bem quanto minha prima Grã Blump. É uma anã barbuda e de cheiro forte, mas isso não impede ninguém de pescar. Já vi que tu está mais do que apta a manejar uma Vara de Pescar da Família Blump.", rewards = "Você receberá:\n\nVara de Pescar da Família Blump"},
[1142] = {title = "A mortalidade se esvanece", objective = "Encontre e devolva o Pingente de Treshala para Treshala Corregofulvo em Darnassus.", description = "Não tenho muito mais tempo de vida... me sinto... fraco.\n\n...mandaram o Monocelha, um anão corajoso, alertar Falfindel...\n\nMas antes que eu... morra...\n\n...minha esposa, Treshala... nosso pingente de casal... roubado... por um dos agressores...\n\nEncontre-o, por favor... e devolva-o a Treshala em Darnassus...\n\n...junto com a notícia de minha morte... solitária...Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nXale da LamentaçãoBotas Lanceiras\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[1143] = {title = "A torre de Althalaxx", objective = "Mate Athrikus Narassin e leve a cabeça dele para Balthule Umbrataque perto da Torre de Althalaxx.", description = "Entendi. Bom, se o que você diz for verdade, a hora de atacar é agora. Acho que os aposentos de Athrikus ficam no topo da torre. Mas tome cuidado, ele está cercado de bruxos poderosos, muito mais letais que os que você enfrentou antes.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1144] = {title = "Importador Willix", objective = "Escolte o Importador Willix para fora do Urzal dos Tuscos.", description = "Finalmente a ajuda chegou!\n\nVocê não faz ideia de há quanto tempo eu estou preso aqui. Não consigo nem me lembrar quando foi a última vez que vi o lado de fora.\n\nEu sou um empreendedor, sabe? Sempre atrás do cobre, como dizem por aí. Eu soube que Cáliper Porcatraca estava pagando bem pelos Tubérculos Carmins desse urzal. Imaginei que seria vapt-vupt. Como dá para ver, eu estava equivocado!\n\nDe qualquer forma, se você puder fazer a gentileza de me ajudar a sair daqui, eu lhe garanto que vai valer a pena.", progress = nil, completion = "Obrigado mais uma vez, amizade! Eu disse que ia valer a pena.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nAnel SímioAro OfídioElo Tigrino\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[1145] = {title = "O enxame se adensa", objective = "Leve o Bilhete Selado de Korran para Belgrom Pedramalho em Orgrimmar.", description = "Muitos viajantes que passaram pela Encruzilhada me ajudaram a recolher ovos no Campo dos Gigantes, e vários contaram casos de como aquelas criaturas são perigosas. Eu ouvi tantos casos que já considero aqueles insetos um perigo muito maior do que meu mestre imaginava.\n\nEu preciso de um mensageiro que vá até Belgrom Pedramalho, em Orgrimmar. Não será uma tarefa simples, preciso de alguém que vá levá-la a sério.\n\nBelgrom costuma ficar em frente a guilda dos guerreiros.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1146] = {title = "O enxame se adensa", objective = "Leve o Bilhete Selado de Belgrom para Moktar Krin em Mil Agulhas.", description = "Então Korran chegou a uma conclusão parecida sobre os insetos... parece que são maiores do que pensávamos.\n\nUns dias atrás um caçador voltou das Mil Agulhas à beira da morte. Ele estava com um grupo de busca que tinha ido resgatar uma equipe de escavação enânica no Salar Cintilante. Durante o ataque, um monte de insetos irrompeu da caverna no sítio de escavação e quase matou o grupo.\n\nEles estavam sob a liderança de Moktar Krin, e se refugiaram na beira do Salar Cintilante. Leve esse bilhete para ele.", progress = "A guerra trouxe a mim e meu grupo ao Salar Cintilante, <class>. O que o trouxe aqui?", completion = "Não ligue para o estrondo dos trovões na encosta das montanhas. Os goblins e os gnomos estão no Salar, apostando corrida em atrocidades criadas pelo que eles chamam de ciência. Há perigos maiores em Mil Agulhas, um dos quais dizimou metade do meu pessoal.\n\nMas, enquanto Belgrom não nos mandar notícias de Orgrimmar, nós permaneceremos aqui. Até lá, defenderemos nossa posição e ficaremos a postos.", rewards = "Você receberá:\n\n"},
[1147] = {title = "O enxame se adensa", objective = "Mate 5 Farejadores Silitídeos, 5 Zangões Silitídeos da Colmeia e 5 Invasores Silitídeos, depois fale novamente com Moktar Krin em Mil Agulhas.", description = "Hum... Então Belgrom acha que os monstros são um perigo. Ele quer mandá-los de volta para as profundezas de onde jamais deveriam ter saído.\n\nPelo visto, matar os anões enviados pela Liga dos Exploradores não foi o bastante... vou ter que mandar um bando de moribundos para outra batalha. Nós não vamos dar conta sozinhos, não nestas condições. O sítio de escavação fica ao sul daqui. Acho que não sobrou nenhum anão. Está a fim de fazer a sua parte, empunhar uma lâmina ou um cajado?Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1148] = {title = "Pedaços do enxame", objective = "Leve 1 Coração de Silitídeo, 5 Garras de Silitídeo e 3 Carapaças Intactas de Silitídeo para Korran na Encruzilhada.", description = "Você examina a carapaça com cuidado. Ao revirá-la nas mãos, você percebe que a forma como o revestimento de quitina se entrelaça faz com que ele seja extremamente resistente. Você nunca viu nada igual. Ao examinar o resto do corpo, você conclui que são criaturas muito poderosas e evoluídas.\n\nAo examinar a carapaça outra vez, lhe ocorre que Korran ia gostar de estudar a criatura. Aquilo o ajudaria a entender o que eram aquelas criaturas e de onde tinham vindo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1149] = {title = "Teste da Fé", objective = "Se tiver fé, pule lá de cima das tábuas, no topo de Mil Agulhas.", description = "Então você veio atrás de mim, ou vai ver foi o destino quem <o/a> trouxe aqui? Pouco importa. Cá está você para iniciar uma nova jornada.\n\nMuitas são as facetas de um ser: o espírito, o corpo, a mente... Algumas se pode medir pelas tradições de uma cultura. Outras, só nos resta ter fé de que existem no âmago do ser. Se você estiver de acordo, eu colocarei algumas das suas facetas à prova.\n\nSe passar no teste, você ficará mais forte. Se fracassar, você perceberá como é vasto o Éter.\n\nO primeiro é o teste da fé.", progress = "Fé... é uma coisa bem mais importante do que se imagina.\n\nNão importa no que você acredita. O importante é acreditar em alguma coisa.", completion = "Só depois de confrontados com adversidades inauditas, começamos a ver quão forte é nosso espírito. Você deu o salto de fé sem nenhuma garantia de que iria sobreviver. Você tem a mente aberta e seu espírito está preparado para que há de vir.\n\nSe quiser prosseguir, o próximo teste já está pronto.", rewards = "Você receberá:\n\n"},
[1150] = {title = "Teste de resistência", objective = "Leve a Garra da Grenka para Dorn Espreitaprados em Mil Agulhas.", description = "Fico feliz em ver que sua coragem continua a mesma depois de tudo que você já passou. Já que sua fé permanece intacta, é hora de testar seu corpo.\n\nPrimeiro vem o teste de resistência. Vá com alguns amigos ao Covil Plumerrante, a oeste do Aldeia Vento Livre. Desbrave as profundezas do covil e destrua os alimentos que encontrar por lá. Isso vai provocar a fúria das harpias. Se você sobreviver, uma hora Grenka Sangrunhidos vai atrás de você. Mate-a e traga-me suas garras para concluir o teste.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nFlanco de Carne"},
[1151] = {title = "Teste de força", objective = "Leve os Fragmentos de Rok'Alim para Dorn Espreitaprados em Mil Agulhas.", description = "Na época dos Deuses Antigos, os elementais habitavam boa parte do mundo. Eles adoravam os Deuses Antigos, e os Deuses, por meio de seus poderes, mantinham os elementais vinculados a este mundo. Quando os Deuses Antigos foram banidos, os elementais sumiram do nosso mundo e agora só conseguem se manifestar sob o comando de magos poderosos.\n\nMas existem algumas exceções. Alguns elementais eram poderosos o bastante para permanecer aqui por conta própria. Rok'Alim é um desses elementais.\n\nPara testar sua força, encontre Rok'Alim em Mil Agulhas e mate-o.", progress = "Rok'Alim é conhecido como o Pilador pelas tribos centaurinas de Mil Agulhas, por causa do estrondo que faz ao esmurrar quem é corajoso ou tolo o bastante para ficar no seu caminho.", completion = "Você passou no teste de força. Demonstrou força e coragem, e já está preparado para o último teste, que encerra os ritos.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[1152] = {title = "Teste de saber", objective = "Encontre Braug Espírito Turvo perto da entrada do Túnel Garracava, na Cordilheira das Torres de Pedra.", description = "Nosso tempo está chegando ao fim. Eu sou culto, mas o teste de saber não será conduzido por mim. Há lições a serem aprendidas e lugares sob o domínio de terceiros a serem visitados.\n\nSe julgar que está preparad$Go:a;, procure Braug Espírito Turvo, ele é um xamã de grande sabedoria. Ele dará prosseguimento aos testes, então ouça bem as palavras dele, <name>.\n\nQuando estiver pront<o/a>, dirija-se ao túnel que liga a Cordilheira das Torres de Pedra ao Vale Gris: Braug mora ali perto. E tome cuidado, <name>, pois os elfos noturnos podem querer impedi-<lo/la> de realizar o teste.", progress = nil, completion = "Os dias e noites ficam cada vez mais frios enquanto os goblins vão abusando da terra e os elfos noturnos vão apontando dedos para as raças mais jovens. Os Renegados e orcs não são os únicos culpados pelas atribulações de Kalimdor e Lordaeron. Muitas das raças inteligentes contribuíram para aumentar a dor do mundo. Dorn sabe disso, e ele o mandou aqui porque você demonstrou possuir os atributos necessários para compreender a questão.\n\nAgora você se encontra diante de Braug.", rewards = "Você receberá:\n\n"},
[1153] = {title = "Uma nova amostra de minério", objective = "Obtenha uma Amostra Não Refinada de Minério de um Escavador Britanapa ou de um Topógrafo e leve para Trapista Forjaço no Acampamento Taurajo, que fica nos Sertões.", description = "<class>, você já me ajudou bastante. O Chefe Guerreiro está satisfeito com tudo que fizemos e me pediu para manter a mesma determinação. O mais honrado seria dar-lhe o reconhecimento diante do Chefe Guerreiro, mas antes vou pedir sua ajuda mais uma vez.\n\nNão faz muito, passaram alguns viajantes vindo do sul, de um lugar chamado Mil Agulhas. Lá, eles mataram um kobold que possuía um minério um tanto incomum, mas não quiseram vendê-lo. Eu quero uma amostra do minério para examiná-la.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nEspada Bélica Órquica"},
[1154] = {title = "Teste de saber", objective = "Encontre o Legado dos Aspectos e leve-o para Braug Espírito Turvo perto da entrada do Túnel Garracava, na Cordilheira das Torres de Pedra.", description = "Você passou nos testes de Dorn e agora quer fazer o próximo. Muito bem. Mas, até agora, os desafios foram dignos de uma criança. Testar o corpo é fácil... a mente, já é outra história.\n\nDeixe-me simplificar as coisas: encontre o livro que vou lhe indicar, estude-o e responda à minha pergunta sobre o seu conteúdo.\n\nQue livro é esse? O Legado dos Aspectos. Ele se encontra bem no fundo de Dor'danil, na porção sul do Vale Gris, protegido por druidas mortos e pelos seus algozes.", progress = "Traga-me o livro só depois que o tiver estudado. Eu não farei a pergunta enquanto você não me trouxer o livro.", completion = "É o livro certo. Então há mesmo esperança de que você vá responder à pergunta corretamente... a menos que tenha se esquecido de estudá-lo.\n\nAvise-me quando estiver pronto e eu lhe farei a pergunta.", rewards = "Você receberá:\n\n"},
[1156] = {title = "Fale com o Alquimista Almofarrista.", objective = "xx", description = "x", progress = nil, completion = nil, rewards = nil},
[1157] = {title = "Almofarrista precisa de itens", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1158] = {title = "Fale com Ravine Saturna.", objective = "xx", description = "x", progress = nil, completion = nil, rewards = nil},
[1159] = {title = "Teste de saber", objective = "Encontre Pasqual Fintallas na Cidade Baixa.", description = "Muito bem, <name>. Você passou na primeira parte do teste de saber, mas ainda há outra pergunta a ser respondida. No entanto, não cabe a mim fazê-la.\n\nProcure Pasqual Fintallas na cidade de Lordaeron. Ele foi um grande historiador entre os humanos antes de ser acometido pela Peste. Continua atrás de conhecimento hoje, porém de outra natureza.\n\nEle lhe dará uma tarefa antes de fazer a pergunta, mas, como você se saiu tão bem até agora... na certa, vai superar o obstáculo.", progress = nil, completion = "Braug o mandou para cá? Impossível.\n\n&lt;Pasqual o olha de cima a baixo.&gt;\n\nDesbravou Dor'Danil, deu um salto de fé, confrontou e subjugou um elemental poderoso? Bah! Aposto que é um farsante, um aventureiro que andou ouviu casos de taberna. E agora acha que está à altura do meu teste. É o que veremos... &lt;class&gt;.", rewards = "Você receberá:\n\n"},
[1160] = {title = "Teste de saber", objective = "Encontre o Início da Ameaça dos Mortos-vivos e leve para Pasqual Fintallas na Cidade Baixa.", description = "Eu vou lhe pedir para buscar algo e vou até lhe fazer a tal pergunta, mas não porque você quer provar alguma coisa. O conhecimento é muito mais importante do que a força. Mesmo que os outros superem suas habilidades, seu conhecimento do mundo será mais valioso.\n\nVá para o Monastério ao norte das Clareiras de Tirisfal. Na biblioteca, há um livro chamado Início da Ameaça dos Mortos-vivos. Ele servia para ensinar os paladinos corrompidos que vivem por lá como combater o meu povo.\n\nTraga-o para mim.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[1162] = {title = "Fale com Hamuul Runa Totem.", objective = "xx", description = "x", progress = nil, completion = nil, rewards = nil},
[1163] = {title = "Fale com Tyrande Murmuréolo.", objective = "xx", description = "x", progress = nil, completion = nil, rewards = nil},
[1164] = {title = "Ladrão que rouba ladrão", objective = "Leve as cabeças de Kenata, Fardel e Marcel Dabyrie para Geneva Celara na Cidade Baixa.", description = "Aquela meretriz! Aquela vaca! Kenata está viva e a família dela prosperando enquanto eu sofro aqui. Renegada de fato!\n\nEu perdi minha família para a peste. Meu terreno foi pilhado e queimado durante a guerra. E ainda assim, ela e os filhos tiveram a audácia de roubar meus últimos bens restantes.\n\nNão me importo mais com o que me foi tirado. Quero as cabeças deles!\n\nMate-os por mim! Vá até a Fazenda dos Dabyrie em Arathi, a nordeste de Ponta do Refúgio.", progress = "Mate todos e me traga as cabeças!", completion = "Você é a personificação da minha vingança e por isso me ajoelho em agradecimento.\n\nAgora que aquela vaca está morta, e sei que as sementes da família dela não mais se espalharão por Azeroth, posso me dedicar a algo mais lucrativo. Espero que você tenha pilhado toda a fazenda antes de sair.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBrafoneiras InflexíveisManilhas Gallan\n\nVocê também receberá:\n\n"},
[1166] = {title = "As preocupações do lorde supremo", objective = "O Lorde Supremo Mok'Morokk da Aldeia Muralha Verde quer que você busque grogue, rapé e um cofre no Pântano Vadeoso.", description = "Você se curvar pra lorde supremo Mok'Morokk!\n\nRexxar fez eu líder poderoso do clã Pedramalho!\n\nEu bom líder. Eu proteger clã.\n\nDragão grande veio incendiar lar antigo. Eu ogro esperto. Achar casa nova pro clã aqui.\n\nEu sair da casa antiga correndo, esquecer coisas. <Pequeno/Pequena> <race>, você ir pegar minhas coisas. Você ir pro sul, encontrar bebida gostosa, rapé e cofre de Mok'Morokk. Eu gostar você depois que fazer o que Mok'Morokk mandar. Procurar no Antro das Chamas e nas Ruínas Pedramalho... eles estar lá, eu ter certeza!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nBotas Ôgricas Gigantescas"},
[1167] = {title = "A torre de Althalaxx", objective = "Fale com Balthule Umbrataque perto da Torre de Althalaxx.", description = "Antes de confrontar Athrikus, seria melhor você consultar Balthule. Sem dúvida, ele andou vigiando a torre com bastante atenção e deve ter informações sobre os últimos desdobramentos.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1168] = {title = "O exército dos dragões negros", objective = "Tharg, na Aldeia Muralha Verde, quer que você mate 15 Batedores Flamigrenha, 10 Caudas Cinza Flamigrenha e 5 Guardas Flamigrenha.", description = "Rexxar nomeou Mok'Morokk como lorde supremo. Ele deseja poder mais que nosso bem.\n\nDragão negro matar muitos ogros. Matar mulher de Tharg. Mok'Morokk mandar ogros fugir. Trazer ogros aqui.\n\nOgros têm que lutar. Ogro não corre. Mok'Morokk só olha barriga de Mok'Morokk.\n\nTharg chamar as Ruínas Pedramalho de casa. Tharg gostar do sul. Aqui não ser casa.\n\nAjuda ogro lutar contra dragão negro. Tharg quer vingança. Tharg quer ir para casa.", progress = nil, completion = "Você matar muito dragão negro. Você &lt;race&gt; corajoso.\n\nTharg contente.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCadarço de ThargDisco de Tharg\n\nVocê também receberá:\n\n"},
[1169] = {title = "Identificando a ninhada", objective = "Draz'Zilb, na Aldeia Muralha Verde, quer que você traga 15 Línguas Calcinantes e 15 Corações Calcinantes.", description = "Enquanto aquele covarde do Mok'Morokk refestela-se com seu poder e Tharg trava um embate com os demônios do sofrimento e da vingança, almejando a liderança do clã, eu pareço ser o único preocupado em identificar a origem da agressão ao nosso antigo lar.\n\nPor que a cara de espanto, <name>? Esperava que eu falasse como um rufião inculto pelo mero fato de que sou um ogro?\n\nDe volta aos negócios: traga-me os corações e as línguas dos dragonetes e dos filhotes. Eu desvelarei a origem desse mal...", progress = "Eles são a prole do exército dos dragões negros. Com suas línguas e corações queimados, decifrarei qual fera ignóbil introduziu suas crias em nosso lar. Um maior conhecimento sobre os nossos agressores pode levá-los à destruição absoluta e ao reerguimento da Vila Pedramalho a partir das ruínas.\n\nTodavia, convencer Mok'Morokk a mover um centímetro de sua massa gorda e letárgica para fora de seu buraco imundo é uma missão épica por si só...", completion = "Obter esses corações e línguas foi, sem dúvida, uma aventura repleta de perigos. Com sua licença, eu me aprofundarei nos recônditos arcanos para buscar a origem da ameaça negra que se abateu sobre nosso antigo lar.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCapa CalcinadaLuvas do Larápio\n\nVocê também receberá:\n\n"},
[1170] = {title = "A linhagem de Onyxia", objective = "Fale com o Lorde Supremo Mok'Morokk na Aldeia Muralha Verde.", description = "A Vila Pedramalho foi invadida pela linhagem de Onyxia. Mas por que a filha do lorde-dragão negro, o Asa da Morte, escolheria as nossas terras?\n\nIsso é o mais intrigante. Decerto Onyxia se dirigiu para cá com um propósito maior que espalhar o terror entre um pequeno bando de ogros.\n\nFale com Mok'Morokk imediatamente! Alguma ação tem de ser tomada.", progress = nil, completion = "Por que você falar de dragão? Eu não gostar dragão. Eu gostar aqui. Aqui sem dragão.", rewards = "Você receberá:\n\n"},
[1171] = {title = "A linhagem de Onyxia", objective = "Fale com Draz'Zilb na Aldeia Muralha Verde.", description = "Deixar Mok'Morokk em paz. Dizer pra Draz'Zilb que nós ficar.", progress = nil, completion = "Covarde preguiçoso!\n\nMok'Morokk era forte quando Rexxar era nosso líder. Mas, logo após a partida de Rexxar, Mok'Morokk sucumbiu às tentações do poder. Em vez de nos dar liderança, nos fez exigências. Em vez de servir a seu povo, espera que seu povo lhe sirva.\n\nAlgo precisa ser feito.", rewards = "Você receberá:\n\n"},
[1172] = {title = "A linhagem de Onyxia", objective = "Draz'Zilb, da Aldeia Muralha Verde, quer que você destrua 5 Ovos de Onyxia.", description = "A linhagem de Onyxia está espalhada por Draconumbra. É mister impedir que doravante qualquer dragonete nasça.\n\nCorra como o vento para o Pântano da Serpe, ao sul do Pântano Vadeoso. Ela fez de lá seu covil. Encontre os ovos dos dragões malignos e destrua-os.\n\nNunca reaveremos a Vila Pedramalho se as áreas circundantes permanecerem servindo de campos de reprodução.\n\nQuanto a Mok'Morokk... Tenho outros planos para o imbecil.", progress = nil, completion = "Ótimo! Onyxia não colocará mais ovos tão cedo.\n\nEm nome de meu povo, agradeço-lhe imensamente, &lt;name&gt;.\n\nNão quero aborrecê-lo novamente, mas talvez você possa prestar um último serviço ao clã Pedramalho.\n\nMok'Morokk não está apto a nos liderar. Tharg seria um excelente líder, mas suas feridas e sua idade são um problema. Expulse Mok'Morokk deste lugar à força. E então poderemos escolher um novo líder para nos levar de volta ao nosso lar no sul.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas EncarnadasBotas de Zua'tec\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[1173] = {title = "Desafie o lorde supremo Mok'Morokk", objective = "Derrote Mok'Morokk e leve a notícia para Draz'Zilb na Aldeia Muralha Verde.", description = "Você ousar desafiar o lorde supremo Mok'Morokk?\n\nHar!\n\nEu esmagar <race> <pequeno/pequena>!\n\nVocê falar com eu quando querer apanhar.", progress = nil, completion = "Aquela massa de gordura pérfida do Mok'Morokk fugiu como uma criancinha assustada. Você nos prestou um grande serviço, &lt;name&gt;.\n\nAgora meu povo pode nomear um novo líder, um que nos ajudará a reivindicar nosso antigo lar de uma vez por todas.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nLâmina BestialEstilhaçador Garrafria\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[1174] = {title = "Vitória dos gnomos!", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nCaixa de Prêmios de Gnomo"},
[1175] = {title = "Tinha um calombo no meio do caminho", objective = "Mate 10 Basiliscos Sal-gema, 10 Cristaldermes Sal-gema e 6 Observadores Sal-gema para o Mestre de Pistas Zheran no Salar Cintilante.", description = "Depois do acidente envolvendo o piloto goblin do Trovão Carmim e um basilisco, Kronrider me contratou para tomar conta da pista. Não sei o que os bichos do salar andam comendo para ficarem tão grandes, mas temos que reduzir a população deles para que não entrem na pista no meio da corrida.\n\nQuer saber de uma coisa? Se estiver a fim de fazer um capilé, é só fazer a limpa nos basiliscos da área.", progress = nil, completion = "Eu espero que você não tenha matado os basiliscos em cima da pista! Leva uma eternidade para a carcaça se desfazer sob o sol, e eu não estou a fim de catar uma pá para limpar a pista.", rewards = "Você receberá:\n\n"},
[1176] = {title = "Peso pena", objective = "Obtenha 10 Ossos Ocos de Abutre para Relé no Salar Cintilante.", description = "Quando o assunto é corrida, todo detalhe faz diferença! Seja um leve ajuste na potência do motor, um afrouxamento nas bobinas de ignição ou um pequeno alívio no peso do chassi, tudo tem que ser levado em conta.\n\nNós usamos osso de abutre para um monte de pequenos ajustes na estrutura dos veículos, porque eles oferecem o equilíbrio perfeito entre força e peso. O melhor de tudo: é fácil de se obter, já que a região está apinhada de abutres.\n\nTraga-me uns ossos e eu vou lhe dar um dinheirinho.", progress = "Trouxe os ossos para mim? Eu estava prestes a começar a suavizar o volante, mas não quero mexer no negócio sem ter todas as peças.", completion = "Maravilha! Vou botar a equipe para trabalhar nisso agora mesmo. Valeu, &lt;name&gt;!", rewards = "Você receberá:\n\n"},
[1177] = {title = "Fome!", objective = "Esmagabarro Pé-sujo, na parte norte do Pântano Vadeoso, quer 12 Cabeças de Lamapina.", description = "Barro ogrão.\n\nEu com fome.\n\nVocê ir arrebentar peixinho. Trazer comida pro Barro.\n\nVocê alimentar Barro ou Barro comer você.", progress = "Você me dar comida ou eu comer você.", completion = "Eu gostar de cabeça de peixinho!\n\nEu não comer você agora.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nEsmagadores de LamaPisoteadores Pé-sujo\n\nVocê também receberá:\n\n"},
[1178] = {title = "Patrocínio goblínico", objective = "Fale com Gasganete na Vila Catraca.", description = "Nossa equipe é o orgulho dos goblins. Você ficaria surpres<o/a> se soubesse quem contribui com a nossa equipe.\n\nSabe o Gasganete? Ele vai atrás de peças no mundo todo para nós. Já chegou até a trabalhar pessoalmente em um dos veículos!\n\nAlgumas semanas atrás, ele disse que mandaria um novo protótipo de regulador de combustível, mas a peça ainda não chegou. Ele costuma ser bem ligeiro... Você se importa de ir até a Vila Catraca averiguar a situação? Não quero ser chato, mas os gnomos estão ganhando da gente!", progress = nil, completion = "Foi o Relé quem mandou você, né? Sabe, o Relé era um dos engenhoqueiros mais respeitados da Inframina antes de abandonar o ramo para trabalhar com corrida.\n\nA primeira coisa que fez quando entrou na equipe foi desenvolver o modelo de dois motores. O Sindicato dos Faz-tudo saiu no prejuízo sem a presença dele, mas e daí? Nada se compara às nossas corridas!", rewards = "Você receberá:\n\n"},
[1179] = {title = "Os irmãos Latafuso", objective = "Leve a caixa de capacetes de impacto para Capu Latafuso no Salar Cintilante.", description = "Você já ouviu falar no Salar Cintilante? É um deserto de sal que fica em Kalimdor, logo ao sul do cânion das Mil Agulhas. É decerto o lugar ideal para uma corrida porque, bom, porque é plano e deserto!\n\nTem dois irmãos gnomos lá, amigos meus, testando um carro-foguete novo, e pelo que ouvi dizer ele é bem rápido! He, tão rápido que todo piloto acaba batendo o carro. É um festival de hematomas.\n\nAí eles me pediram uns capacetes de máquina de cerco, mas eu tenho obrigações a cumprir. Não posso viajar até lá!\n\nVocê poderia?", progress = "Cuidado onde pisa! Cuidado, cuidado, cuidado!! Isso aqui é tudo coisa delicada, se a gente quiser vencer tem que estar tudo tinindo trincando!", completion = "Ah! O Barbalonga mandou um monte de capacetes! Hurra!\n\nAgora talvez a gente consiga convencer alguém a pilotar nosso carro...", rewards = "Você receberá:\n\n"},
[1180] = {title = "Patrocínio goblínico", objective = "Fale com o Mestre Portuário Lozgil na Angra do Butim.", description = "Voltando ao nosso assunto... Eu estou ciente de que prometi a Relé que mandaria um regulador de combustível novo para ele, mas ainda não recebi o negócio. O Rezingada disse que enviaria lá da Angra do Butim, mas pode ser que a encomenda tenha sido extraviada.\n\nSe for isso mesmo, não há muito o que fazer. Mas podemos pelo menos descobrir se ela foi despachada na Angra do Butim.\n\nVá conferir com o Mestre Portuário Lozgil, ele deve ter acesso ao histórico de encomendas.", progress = nil, completion = "Encomendas para a Vila Catraca? Deixe eu conferir o livro de registro...\n\nNão, a última encomenda para a Vila Catraca foram as botas do Drizzlik. Não tem muito mais coisa, além de uns materiais de construção e ferramentas. Nada de peça de carro de corrida.", rewards = "Você receberá:\n\n"},
[1181] = {title = "Patrocínio goblínico", objective = "Fale com o Barão Rezingada na Angra do Butim.", description = "Eu receio não poder ajudar mais, <name>. Se você faz tanta questão, sugiro ir atrás do Barão em pessoa. Se tem alguém que está sempre a par dos negócios do Rezingada, esse alguém é o próprio Rezingada.\n\nEle fica na taverna, do outro lado do porto, no último andar.", progress = nil, completion = "Ah, lógico que eu sei do que você tá falando. Só que não tenho como ajudar, não. Era pro meu pessoal ter localizado os diagramas do regulador de combustível, mas voltaram de mãos abanando.", rewards = "Você receberá:\n\n"},
[1182] = {title = "Patrocínio goblínico", objective = "Leve os Esquemas do Regulador de Combustível para o Barão Rezingada na Angra do Butim.", description = "Se liga, os retalhadores da Empreendimentos S.A. na região do Lago Nazferiti, lá na Selva do Espinhaço Setentrional, foram equipados com um novo modelo de regulador de combustível. Tenho certeza que o Relé faria a festa se conseguisse mais detalhes. Infelizmente parece que o Cosella, o encarregado do lugar, é um pouco mais esperto do que eu imaginava.\n\nAcho que você talvez tenha mais chance de conseguir os diagramas. Eu sei que ele guarda tudo na casa dele, mas com certeza guardou a chave e cadeado.", progress = "É sofrido ter que admitir isso, mas a equipe gnômica é boa pra caramba. Nosso pessoal vai precisar de um gás.", completion = "Deixa só eu tirar uma cópia dos diagramas pra fins de estudo pessoal, aí você pode levar tudo pro Relé. Senão daqui a pouco ele vai começar a pensar que foi abandonado pelo patrocinador.", rewards = "Você receberá:\n\nChave do Cosella"},
[1183] = {title = "Patrocínio goblínico", objective = "Leve os Esquemas do Regulador de Combustível para Relé no Salar Cintilante.", description = "Pronto, já está tudo no jeito. Mande lembranças pro Relé e diga a ele que, precisando de qualquer coisa, é só pedir. Eu ficarei aqui aguardando a notícia de que ele quebrou o recorde de velocidade!", progress = "<name>! Conseguiu localizar o regulador de combustível?", completion = "Eu achei que a peça já ia estar montada, mas isso me dá uma oportunidade excelente de aprimorar o projeto do zero. Valeu, &lt;name&gt;. Eu vou bolar um diagrama melhorado, construir esse negócio e tratar de instalar a peça sem demora.", rewards = "Você receberá:\n\n"},
[1184] = {title = "Pedaços do enxame", objective = "Fale novamente com Belgrom Pedramalho em Orgrimmar.", description = "Volte para Orgrimmar e diga a Belgrom que Moktar vem cumprindo seus deveres aqui em Mil Agulhas. Diga também que meus estudos vem progredindo, e que vou aprender bastante coisa. Ele vai ficar mais do que satisfeito.", progress = nil, completion = "Eu fico grato pelo empenho, &lt;class&gt;. O Chefe Guerreiro vai gostar de saber o quanto você leva a sério o cumprimento dos seus deveres.\n\nAh, é claro que mencionarei seu nome. Falo com ele sempre, e ele tem minha opinião em alta conta.\n\nPode ir, eu tenho muito o que fazer. Se voltar a falar com Korran e ele tiver mais notícias sobre essas criaturas, venha me informar – elas são um perigo e devem ser levadas a sério.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCalças FolhassecaCrescente Sangrento\n\nVocê também receberá:\n\n"},
[1185] = {title = "Sob a carapaça de quitina...", objective = "Ei, você voltou! É, é com você... você aí, <name>! Isso, você mesmo!\n\nDeixe-me recomeçar do zero!\n\nAquele objeto que você encontrou em Silithus... no fim das contas era um livro. Um diário escrito em um dialeto muito antigo da língua darnassiana. Vai dar um trabalhinho, mas eu vou conseguir palavra por palavra. Eu queria que você soubesse que encontrou uma coisa extremamente valiosa para o Círculo.\n\nA matriarca do covil ficou animadíssima com o achado!", description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1186] = {title = "O décimo-oitavo piloto", objective = "Fale com Razzeric no Salar Cintilante.", description = "Eu vou estar ocupado, integrando o novo regulador de combustível nos nossos carros, mas se quiser ajudar a equipe mais um pouco, fale com Razzeric. Ele é nosso piloto... quer dizer, o décimo-oitavo piloto, para ser mais preciso, e ele gosta de fazer algumas modificações no carango.\n\nEle tem uns parafusos soltos, então é melhor não contrariar o cara.", progress = nil, completion = "Você é o &lt;race&gt; que vem dando uma mão pro Relé, não é? Eu já fui que nem você, vivia correndo para lá e para cá, fazendo tudo que os mecânicos me mandassem fazer.\n\nVeja só onde vim parar! Sou a estrela da equipe de corrida dos goblins!", rewards = "Você receberá:\n\n"},
[1187] = {title = "Do jeitinho do Razzeric", objective = "Recupere o Foguete de Cequatrum para Razzeric no Salar Cintilante.", description = "O Relé é um mecânico de mão cheia, isso é fato, mas ele tem medo carregar a mão e meter a potência máxima. Já eu, não tenho.\n\nDepois que o Relé termina de mexer no carro, eu faço umas modificaçõezinhas, sabe? Afrouxo o controle, tiro umas partezinhas do chassi e dou uma turbinada no negócio!\n\nEu encomendei um foguete de cequatrum com o Xavekko, lá da Pinacotreca de Geringontzan, mas parece que o zepelim que fazia o transporte caiu no Pântano Vadeoso! Eu preciso dessa peça, <name>!", progress = "Como é que vou dar as caras na pista sem uma coisinha a mais para dar aquele gás? Sem condições, assim não dá jeito.", completion = "Olha só essa belezura! Sete quilos de pura explosão de cequatrum! Depois que prender isso do lado do meu carro, eu vou andar tão rápido que vou abrir umas duas voltas de vantagem sobre os gnomos!", rewards = "Você receberá:\n\n"},
[1188] = {title = "Segurança em primeiro lugar", objective = "Leve o Foguete de Cequatrum para Xavekko em Geringontzan.", description = "Espera, o que é isso? O Xavekko deixou a trava ativada. Isso vai limitar toda a potência do foguete. Eu falei para ele desativar! Isso não é nada bom.\n\nVocê precisa levar o foguete até a Pinacotreca de Geringontzan no Deserto de Tanaris e pedir para o Xavekko remover a trava.", progress = "Você já deve saber que a Pinacotreca faz todo tipo de conserto! Como eu posso te ajudar?", completion = "Só o Razzeric para olhar isso e dizer que eu instalei uma trava de segurança. Esse foguete é quase todo feito de explosivos de cequatrum. Eu já modifiquei ele para que entregasse mais carga ao ser acionado, mas o Razzeric não fica satisfeito se não chutar o pau da barraca!", rewards = "Você receberá:\n\n"},
[1189] = {title = "Segurança em primeiro lugar", objective = "Entregue o Foguete de Cequatrum Modificado a Razzeric no Salar Cintilante.", description = "Pronto, eu fiz o que o Razzeric pediu. Só fala para ele ter cuidado ao acionar essa coisa. Se vacilar só um pouquinho, ele não vai sair acelerando. Ele vai virar uma mancha na pista.\n\nIa ser uma pena se isso acontecesse, sabe. Quem sabe se o décimo-nono piloto vai ser tão bom assim?", progress = "Você acredita que o Relé colocou um balde de areia no carro? Como é que eu vou chegar na velocidade máxima com um balde de areia debaixo do banco?", completion = "Agora sim! Eu queria ver a cara daqueles gnomos quando eu acionar essa belezinha.\n\nQue pena que eu vou estar ocupado quebrando um novo recorde!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCinto de Segurança Personalizado do RazzericLuvas de Corrida do Razzeric\n\nVocê também receberá:\n\n"},
[1190] = {title = "Tirando o atraso", objective = "Faça com que Zamek crie uma distração, então roube os planos do Rebu para o Relé no Salar Cintilante.", description = "Os irmãos Latafuso descobriram um novo material de construção, essa é a única explicação que faz sentido. Os carros deles estão rodando mais rápido do que antes, mas os desenhos e todos os outros dados que a gente obteve das corridas não mudaram.\n\nO que a gente sabe é que o Rebu Latafuso está cuidando dos planos dessa nova descoberta.\n\nO Zamek pode criar uma distração para que você possa roubar os planos deles.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1191] = {title = "A distração de Zamek", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1192] = {title = "Minério de Indúrio", objective = "Quero integrar o indúrio a várias peças dos nossos carros, mas preciso de grandes quantidades de indúrio bruto para criar essas peças.\n\nVocê vai encontrar minério de indúrio na escavação de Uldaman nos Ermos, do outro lado do mar, em Khaz Modan.\n\nOs gnomos têm seu próprio suprimento, então é importante que a gente tenha o nosso para não ficarmos para trás.", description = nil, progress = "Quero integrar o indúrio a várias peças dos nossos carros, mas preciso de grandes quantidades de indúrio bruto para criar essas peças.\n\nVocê vai encontrar minério de indúrio na escavação de Uldaman nos Ermos, do outro lado do mar, em Khaz Modan.\n\nOs gnomos têm seu próprio suprimento, então é importante que a gente tenha o nosso para não ficarmos para trás.", completion = "Excelente! Vou mandar a equipe criar as peças que vamos precisar para a próxima corrida agora mesmo.", rewards = "Você receberá:\n\n"},
[1193] = {title = "Armadilha quebrada", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1194] = {title = "Diagramas de Rebu", objective = "Leve a Amostra de Minério de Indúrio para Relé no Salar Cintilante.\n\nItem fornecido:\n", description = "Os diagramas do Rebu descrevem o processo de refino do minério de indúrio. Segundo seus rabiscos, fica claro que o minério de indúrio está sendo obtido do sítio de escavação enânico de Uldaman, que fica nos Ermos.\n\nA maioria das outras anotações trata das propriedades de resistência ao calor.\n\nUm pedaço do que parece ser minério de indúrio bruto foi usado como peso de papel para os planos.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1195] = {title = "A Chama Sagrada", objective = "Leve o Frasco Cinzelado Cheio para Zangan Casco de Pedra no Penhasco do Trovão.", description = "Os centauros Galath em Mil Agulhas protegem um artefato da época de sua criação.\n\nNós gostaríamos de obtê-lo, mas precisamos de um frasco de água de um dos poços lunares dos elfos noturnos.\n\nPara coletar a água, você precisará primeiro obter um dos frascos das dríades próximas ao Refúgio Bosque Real, no coração da Floresta do Vale Gris. Há um poço lunar perto da margem oeste do Rio Felfarren, ao sul da estrada principal, onde você poderá encher o frasco.", progress = "Uma vez que tivermos o frasco preenchido, poderemos enviá-lo para o meu associado, Rau, em Mil Agulhas. Foi ele quem solicitou o frasco de água, então ele saberá como poderemos usá-lo.", completion = "Muito bom. Isso deve atender o pedido do Rau. Você poderia entregar o frasco a ele? Talvez você goste de passar um tempo nas Agulhas.", rewards = "Você receberá:\n\nFrasco Cinzelado"},
[1196] = {title = "A Chama Sagrada", objective = "Entregue o Frasco Cinzelado Cheio a Rau Corta Montes na Aldeia Vento Livre.", description = "Leve o frasco preenchido para Rau Corta Montes na Aldeia Vento Livre, ao sudeste de Mil Agulhas. Você pode chegar a Mil Agulhas pegando o Grande Elevador na extremidade sul dos Sertões.\n\nEu não ficaria surpreso se você puder nos ajudar mais um pouco.", progress = "Imagino que o Zangan tenha enviado você, não?", completion = "Hm, sim, esse frasco de água servirá aos nossos propósitos. Por causa da ancestralidade compartilhada das dríades e centauros, a água dos poços lunares nos permitirá ignorar a proteção da chama eterna que envolve a relíquia deles.", rewards = "Você receberá:\n\n"},
[1197] = {title = "A Chama Sagrada", objective = "Recupere o Casco Fendido para Rau Corta Montes na Aldeia Vento Livre.", description = "Usando a água que você trouxe, será possível apagar temporariamente a chama que envolve a relíquia dos centauros. O efeito não será permanente, no entanto, então você terá que agir rápido.\n\nVocê encontrará o Rochedo do Casco Fendido, a fortaleza dos centauros, ao norte daqui. A chama é mantida no Castelo do Casco Fendido, uma parte muito bem protegida do rochedo.\n\nTenha cuidado, os centauros Galath estarão em grande número e relutarão em entregar algo que lhes é tão valioso.", progress = "Não vejo a hora de colocar as mãos no Casco Fendido. Você já conseguiu pegá-lo?", completion = "Sinto uma energia estranha emanando do casco... Não tenho dúvidas de que descobriremos informações surpreendentes sobre os centauros com este estudo.\n\nObrigado, &lt;name&gt;. Agora eu preciso começar a desvendar os mistérios que você trouxe para mim.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nMira do Corredor do DespenhadeiroFaixa Lazúli\n\nVocê também receberá:\n\n"},
[1198] = {title = "Procurando Thaelrid", objective = "Procure o Guarda Argênteo Thaelrid nas Profundezas Negras.", description = "Há muito rumores circulando hoje em dia sobre a união do Martelo do Crepúsculo e das nagas das Profundezas Negras na costa do Vale Gris.\n\nMinha função na Aurora Argêntea é coletar o máximo de informações possível, para que saibamos quando e onde a ajuda é necessária.\n\nFaz semanas que eu enviei um batedor para verificar a atividade nas Profundezas Negras. O Guarda Argênteo Thaelrid ainda não reportou de volta.\n\nOs meus outros batedores estão ocupados com assuntos mais urgentes.\n\nEncontre e ajude Thaelrid por mim, <name>.", progress = nil, completion = "O Vigilalva Saedlass agiu bem em enviá-lo. De fato, há alguma trama sórdida se desenrolando neste lugar corrompido.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[1199] = {title = "A Hora do Crepúsculo", objective = "Leve 10 Pingentes do Crepúsculo para o Guarda Argênteo Manados em Darnassus.", description = "Encorajados pelos velhos rituais dos caóticos Deuses Antigos, os membros do Martelo do Crepúsculo se esforçam para trazer os sombrios patronos de volta e espalhar morte e terror pelo mundo.\n\nNós acreditamos que o Martelo do Crepúsculo se aliou às nagas e está se reunindo nas Profundezas Negras, que fica na costa do Vale Gris.\n\nQualquer plano maligno que esteja sendo preparado nas profundezas do antigo templo deve ser impedido.\n\nAtaque o Martelo e traga provas dos seus feitos. Os seus esforços serão muito bem recompensados.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nBotas NímbusCinturão de Alburno"},
[1200] = {title = "Vilania das Profundezas", objective = "Leve a cabeça do Senhor do Crepúsculo Kelris para o Vigilalva Selgorm em Darnassus.", description = "Eu estou sem forças. Vou precisar da sua ajuda!\n\nMuito tempo atrás, este local abrigava o grande templo de Eluna. Mas a desgraça levou à ruína quando a corrupção do deus antigo se infiltrou das profundezas da terra e contaminou o poço lunar sagrado.\n\nAku'mai, servo do deus antigo, se ergueu das águas.\n\nOs sectários do Martelo do Crepúsculo se aliaram às nagas para ocupar este local. Os cultistas, liderados por Kelris, sacrificam inocentes ao poder de Aku'mai.\n\nMate Kelris e leve sua cabeça para Selgorm em Darnassus, <name>, por favor.", progress = "O tempo é um bem precioso, <class>.\n\nO meu papel aqui em Darnassus é assegurar que a Aurora Argêntea floresça e que as forças do mal que invadem Kalimdor sejam detidas.\n\nDiga o que quer de uma vez ou vá embora.", completion = "Kelris nos escapou por muito tempo.\n\nParece que, sempre que o mal mostrou sua presença nestas partes, Kelris estava envolvido. Por algum tempo, nós pensamos que ele tinha morrido ou desaparecido.\n\nMas isso faz muito sentido. Ao encerrar seu reinado, você poupou a vida de muitas pessoas inocentes.\n\nPela Luz! Sacrificar alguém a um servo de um deus antigo em troca de ganhos pessoais é inaceitável!\n\nVocê fez um grande bem, &lt;name&gt;. Eu o saúdo em nome da Aurora Argêntea.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCetro da LápideBroquel Ártico\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[1201] = {title = "Os espiões de Theramore", objective = "Mate 9 Infiltradores de Theramore e, em seguida, fale com Nazir Piquessangue, na Aldeia Muralha Verde.", description = "A trégua da Aliança e da Horda é deveras instável. Ambos os lados criam disputas marginais e ações veladas contra o lado oposto, e o nosso trabalho é garantir que a Horda se sobressaia sempre!\n\nTemos uma oportunidade perfeita.\n\nInfiltrados da Aliança se posicionaram a leste da Aldeia Muralha Verde. Espionam a aldeia e reportam nossos movimentos a Theramore, e isso não podemos permitir.\n\nEncontre-os e mate-os. Mande uma mensagem clara aos seus superiores de que não toleraremos a presença de espiões!", progress = nil, completion = "Bom trabalho. Os infiltradores deles são habilidosos, mas não o suficiente para escapar do olho vigilante da Horda!", rewards = "Você receberá:\n\n"},
[1202] = {title = "As docas de Theramore", objective = "Leve os Documentos do Capitão a Nazir Piquessangue na Aldeia Muralha Verde.", description = "<name>, dois espiões nossos foram enviados para roubar documentos de um navio, nas docas de Theramore. Eles encontraram os papéis, mas foram pegos e forçados a jogar os documentos no mar.\n\nUm dos espiões conseguiu escapar, depois, mas não conseguiu recuperar o trunfo. Ele me informou de seu fracasso e, agora, estou encarregando você de concluir a tarefa.\n\nOs documentos estão numa maleta submersa na região das docas de Theramore. Encontre os documentos, <name>, e traga-os para mim.", progress = "Encontrou os documentos, <name>? Eles contêm informações vitais para a estabilidade da Aliança e, portanto, vitais para a segurança da Horda.", completion = "Louvado seja, &lt;name&gt;. A missão não era fácil. E a obtenção desses documentos foi bem apreciada pelos nossos superiores.", rewards = "Você receberá:\n\n"},
[1203] = {title = "Zé precisa de uma lâmina", objective = "Leve uma Espada Larga de Lunaço para o Zé no Pântano Vadeoso.", description = "Preciso cortar umas coisas... Não posso dizer o que é, mas... Tô precisado de uma lâmina. É uma certa lâmina...\n\nBom, há quem a chame de espada larga de lunaço. Eu chamo de lâmina de cortar.\n\nVocê vai me ajudar? Quem sabe você não acha na cidade, quem sabe você não fala com um ferreiro e ele faz para você.\n\nMas não vai demorar, hein?! Tenho que cortar essa coisa e não tô por sua conta...Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nAnel de Nó de BambuCalças do Artífice"},
[1204] = {title = "Sopa de pedralama com peixe", objective = "Leve 8 Línguas Bifurcadas de Pedralama a Morgado Severo em Theramore.", description = "Estou trabalhando num prato que batizei de \"Sopa de pedralama com peixe.\" Ainda preciso dos ingredientes principais e... bem, para ser sincero, eu não duraria muito no Pântano Vadeoso!\n\nPor isso, preciso que alguém cace para mim. <name>, você faria isso? Primeiro, preciso de línguas de tartaruga pedralama. É possível encontrá-las ao longo da costa, principalmente nas redondezas da baía.\n\nMas não quero qualquer língua! Ouvi dizer que as línguas de algumas são bifurcadas...\n\nDizem que as línguas bifurcadas têm um sabor especial, e este é o sabor que quero dar ao meu prato...", progress = "Conseguiu as línguas bifurcadas?", completion = "Estão ótimas! Muito obrigado, &lt;name&gt;!\n\nVou experimentar diferentes técnicas de culinária para extrair o sabor das línguas.\n\nE, mais tempo, menos tempo, terei um prato digno do Recôndito Azulego em Ventobravo!", rewards = "Você receberá:\n\n"},
[1205] = {title = "Mortocharco", objective = "Leve o Dente de Mortocharco a Melor no Penhasco do Trovão.", description = "O Mortocharco é um crocolisco ancião que vive no Pântano Vadeoso. É chegado o momento de sua partida.\n\nSeus ossos, já velhos, estalam à medida que a criatura desloca seu corpo gigantesco através do pântano, e, embora ainda se mova com a força e a agilidade da juventude, seu corpo ancestral tortura o espírito grandioso que reside em seu interior, um espírito cuja chama não minguará.\n\nEle vive numa dor constante e enlouquecedora. Você precisa dar um fim à vida dessa nobre criatura, <name>. Você precisa entregá-la à paz.", progress = "Trouxe o dente do Mortocharco, <name>? Acabou com o sofrimento da criatura?", completion = "Muito bem.\n\nEstou certo de que a caçada não foi fácil, mas é bom saber que a dor da fera enfim se foi.", rewards = "Você receberá:\n\n"},
[1206] = {title = "Zé \"olho em falta\"", objective = "Leve 40 Olhos Névoa Negra Inteiros para Zé \"Olho de Lama\" no Casarão do Pantanal Iluminado.", description = "Suco de olho é ótimo pra beber. Pena que a maioria dos bichos só tenha dois olhos.\n\nSó que as aranhas têm um bocado. Um bom bocado. E o suco de Névoa Negra é gostosíssimo. Suave e salgadinho! As bolinhas dos olhos redondinhas...\n\nDá pra imaginar que o nome das aranhas é esse por causa da caverna... ou é o contrário?", progress = "Cadê os olhos?", completion = "Você trouxe os olhos! Você trouxe os olhos! Hum, delicinhas de olhos de aranha!\n\nTem uns que, quando você olha para eles... eles olham de volta. Coma esses por último. Eles são boa companhia... aqui no pântano.\n\nAlô, olhinhos. Opa. Vocês têm gosto de frango. Frango gosmento e salgado!\n\nHmmm.", rewards = "Você receberá:\n\n"},
[1218] = {title = "Temperos Reconfortantes", objective = "Leve 3 Temperos Reconfortantes para o Zé \"Olho de Lama\" no Pântano Vadeoso.", description = "Eu gosto de cozinhar. A boa e velha cozinha do pantanal. Perna de sapo verde frita, esse tipo de coisa.\n\nE para preparar, eu preciso de pimenta. Uma pimentinha deliciosa. Vou fazer um ensopado, cozinhar e secar um pouco, daí te dou um cadinho também.\n\nVocê gosta de ensopado?", progress = "Pimenta. É ótimo dar uma apimentada na vida. Você não acha?", completion = "Ah, e essa é a pimenta! Isso, sim, é o bom ensopado do pantanal.", rewards = "Você receberá:\n\nEnsopado de Perna de Sapo"},
[1219] = {title = "O relatório orc", objective = "Leve o Relatório do Espião a um Tenente de Theramore.", description = "Remexendo a terra, você encontra a mão decepada de um orc. A mão segura um tubo feito de osso. Dentro do tubo há um pergaminho sujo de terra.\n\nO pergaminho está escrito em órquico... e parece importante.", progress = "Algo a relatar?", completion = "Hm... Não leio órquico muito bem, mas isso parece um relatório... escrito por um espião orc!", rewards = "Você receberá:\n\n"},
[1220] = {title = "O Capitão Valente", objective = "Leve o Relatório do Espião ao Capitão Garrido Valente.", description = "O Capitão Valente tem de ver este relatório dos orcs que você encontrou, <name>. Ele é um dos maiores conhecedores da língua órquica. Ele precisa saber que há espiões da Horda nas proximidades de Theramore!\n\nO capitão está no quartel, no lado nordeste da Ilha Theramore.", progress = "Você tem algo para mim?", completion = "Um relatório de um espião orc! Como você encontrou isso? Não importa... vamos ver o que está escrito aqui...", rewards = "Você receberá:\n\n"},
[1221] = {title = "Tubérculos Folhazul", objective = "Pegue um Caixote com Furos.\nPegue um Bastão de Adestramento do Fungafuça.\nPegue e leia o Manual de Proprietário do Fungafuça.\n\nNo Urzal dos Tuscos, use o Caixote com Furos para evocar uma Marmota Fungafuça, e use o Bastão de Adestramento do Fungafuça para fazer ela farejar os tubérculos.\n\nLeve 6 Tubérculos Folhazul, o Bastão de Adestramento do Fungafuça e o Caixote com Furos para Cáliper Porcatraca na Vila Catraca.", description = "Os tubérculos folhazul são uma iguaria no mundo todo! Mas eles são raros, muito raros. Só dá para encontrar aqui nos Sertões, bem fundo na terra, no Urzal dos Tuscos.\n\nE, mesmo assim, eles são difíceis de achar, a não ser que você saiba onde procurar! É por isso que eu treinei essas marmotas fungafuça para farejar eles. Elas têm focinhos incríveis e podem farejar um tubérculo a uns cinquenta passos de distância.\n\nNão vai ser fácil, mas se conseguir uns tubérculos para mim, eu vou ser muito generoso com você.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nPequena Caixa de Gemas"},
[1222] = {title = "A fuga do Cascão", objective = "Escolte Inácio \"Cascão\" e, em seguida, fale com Morgado Severo em Theramore.", description = "Até que enfim encontro alguém que não quer me devorar!\n\nPreciso de alguma ajuda com os bichos daqui e você é <perfeito/perfeita> para a tarefa.\n\nO cozinheiro Morgado Severo está criando uma nova receita e me mandou buscar trevo aquático por aqui. Ele deve achar isso gostoso...\n\nEnfim, eu estava procurando essa planta quando me vi cercado! Você pode me ajudar a sair daqui e me proteger enquanto eu procuro esses pés de trevo aquático?", progress = nil, completion = "O Sr. Inácio acabou de passar por aqui com as folhas de trevo aquático e ele me contou que você o ajudou a se livrar de um problemão.\n\nObrigado, &lt;name&gt;. Pedirei ao meu tio que o receba com um banquete da próxima vez que você visitar o Recôndito Azulego em Ventobravo.", rewards = "Você receberá:\n\nElixir de Fortitude"},
[1238] = {title = "O relatório perdido", objective = "Leve o Relatório do Espião para Nazir Piquessangue na Aldeia Muralha Verde.", description = "Remexendo a terra, você encontra a mão decepada de um orc. A mão segura um tubo feito de osso. No tubo há um pergaminho sujo de terra.\n\nO pergaminho é um relatório feito por um orc que passou algumas semanas espionando a cidade de Theramore.\n\nApós uma passada de olhos, fica claro que isso deve ser encaminhado ao mestre espião orc da Aldeia Muralha Verde.", progress = "Você tem algo para mim?", completion = "Bom trabalho, &lt;name&gt;. Nosso agente, Marg Olholunar, estava posicionado nas adjacências de Theramore. Sua designação era espionar a cidade.\n\nEste é o último relatório dele, provavelmente o último de todos... pelo que você disse, temo que o pântano tenha sido o fim do Marg...", rewards = "Você receberá:\n\n"},
[1239] = {title = "A cabeça decepada", objective = "Leve a Cabeça de Orc Desafiante para Nazir Piquessangue na Aldeia Muralha Verde.", description = "Uma escavação vigorosa revela a cabeça de um orc, decepada grosseiramente na altura do pescoço. Os lábios estão congelados em um rosnado, e os olhos negros encaram desafiadoramente.", progress = "Você tem mais alguma coisa para reportar, <name>?", completion = "Essa é a cabeça de Marg Olholunar! Como eu temia, ele morreu.\n\nOlhe o ferimento no pescoço! Será que uma arma poderia fazer esse rasgo, ou será que uma fera matou o nosso agente?\n\nNós temos que resolver esse mistério, &lt;name&gt;. Temos que descobrir se o Marg morreu nas mãos da Aliança!", rewards = "Você receberá:\n\n"},
[1240] = {title = "O troll mandingueiro", objective = "Leve a Cabeça de Orc Desafiante para Kin'weelay no Acampamento Grom'gol da Selva do Espinhaço.", description = "Temos que entender a morte do Marg! E o único jeito de fazer isso, <name>, é com a mandinga dos trolls.\n\nLeve a cabeça do Marg para o Kin'weelay no Acampamento Grom'gol da Selva do Espinhaço.\n\nO Kin'weelay é o mandingueiro mais poderoso dos trolls Lançanegra. Se tem alguém que pode falar com o espírito do Marg Olholunar, esse é o Kin'weelay.", progress = "Você trouxe um espírito furioso com você, mermão. O que quer com o Kin'weelay?", completion = "Ah, já entendi. O espírito desse &lt;race&gt; está preso no nosso mundo, consumido pela raiva por um assunto não resolvido.\n\nBem, vamos conversar com esse &lt;race&gt; enfurecido...", rewards = "Você receberá:\n\n"},
[1241] = {title = "O diplomata desaparecido", objective = "Encontre Jorge em Ventobravo.", description = "Recentemente, um diplomata foi enviado a Theramore para ter com Jaina Proudmore. O diplomata nunca chegou.\n\nEu acredito que os Défias estão envolvidos nisso, mas não sei bem como. O desaparecimento do diplomata ainda não chegou a público, mas não vai continuar assim por muito tempo.\n\nOs agentes do rei já estão vasculhando a cidade atrás de pistas, mas eu gostaria que os meus contatos participassem.\n\nEu tenho um velho amigo chamado Jorge que anda pelo Vale dos Heróis. Encontre, entregue esse bilhete e siga as instruções dele.", progress = "Hunf, é melhor que tenha um bom motivo para interromper um homem e sua pescaria. Eu não suporto vendedores, muito menos pedintes.", completion = "Hm... &lt;Jorge encara você com olhos desconfiados.&gt;\n\nEstá bem. Eu vou ajudar, mas não porque quero, mas porque sou obrigado.\n\nE vamos deixar uma coisa bem clara: você sabe mais do que deveria saber, para começo de conversa. Vamos manter isso em segredo, e nem ouse abrir a matraca pelas tabernas sobre o que você descobriu. Entendeu?", rewards = "Você receberá:\n\n"},
[1242] = {title = "O diplomata desaparecido", objective = "Entregue o Bilhete Lacrado para Elling Trias em Ventobravo.", description = "Leve este bilhete para Elling Trias. Não diga mais nada para ele. Haja com educação e aguarde ele responder. E não se preocupe, ele vai responder.\n\nEu sou vou falar mais uma vez, <name>: trate esse assunto com seriedade, e fique de bico fechado.\n\nVocê vai encontrar o Trias em sua queijaria, a Queijo de Trias, no Distrito Comercial. Boa sorte.", progress = "Olá, <name>. Há algo que eu possa fazer por você? Talvez você queira experimentar o nosso cheddar especial do Trias? Ou um pedaço de Roquefort Darnassiano?", completion = "Desculpe, uma carta para mim? Bem, eu... raramente chega uma entrega que não seja um caixote ou barril quando você só faz trabalhar. Vamos ver do que se trata.\n\n&lt;Elling olha para você enquanto abre a carta.&gt;\n\nVocê parece um pouco tenso, está tudo bem, &lt;name&gt;?", rewards = "Você receberá:\n\n"},
[1243] = {title = "O diplomata desaparecido", objective = "Entregue o Bilhete Lacrado para o Vigia Backor na Floresta do Crepúsculo.", description = "Jorge, seu velho maldito... Então é assim que vai ser, hã?\n\nMuito bem, eu dou conta. Eu espero que ele encontre a Espiral Etérea antes disso!\n\nPreste atenção, <name>. Eu vou passar a usar o meus contatos daqui, mas é você quem vai andar para cima e para baixo para me economizar umas moedas.\n\nVá para a Vila Sombria na Floresta do Crepúsculo e encontre o Vigia Backor. Ele geralmente patrulha a estrada ao norte, logo na saída da cidade. Diga que você está investigando as atividades dos Défias para mim e ele lhe dará qualquer informação que tiver.", progress = "Não é muito seguro viajar pela floresta, <name>. Tenha cuidado, e se vir algo suspeito ou perigoso, procure os Vigilantes imediatamente. Não tente cuidar das coisas sozinho. Nós não nos responsabilizamos por qualquer desgraça que recair sobre você.\n\nMas nós sabemos que você vai me ignorar e lutar com o que quer que saltar das sombras, não é mesmo? Haha, sim, vocês aventureiros... são todos iguais.", completion = "Ah, Trias mandou você, é? Que curioso.\n\nFique à vontade para enfrentar qualquer monstruosidade que saltar das sombras. Tenho certeza que você não terá problemas com isso, se é do tipo que costuma andar com gente como ele.\n\nEntão, o que o meu amigo precisa de mim em um dia tão sombrio como esse?", rewards = "Você receberá:\n\n"},
[1244] = {title = "O diplomata desaparecido", objective = "Encontre a Súmula Défias e entregue-a ao Vigia Backor na Floresta do Crepúsculo.", description = "Atividade dos Défias? Bem, tem sempre alguma atividade; mesmo que fiquem na deles, nós os consideramos uma ameaça.\n\nMas pensando bem, houve um relato recente que me pareceu estranho. Deve ter sido umas semanas atrás, alguns dos agentes deles se reuniram perto da Quinta dos Aguiar. Segundo o relato, eu acho que algo grande aconteceu por lá.\n\nPor que você não olha lá primeiro e me fala se encontrar alguma coisa?\n\nA fazenda fica ao sul da estrada de Cerro Oeste, assim que você entra na Floresta do Crepúsculo.", progress = "Tenha cuidado por aí, <name>. Eu sei que foi o Trias que mandou você, mas isso não quer dizer que não haja coisas espreitando pela floresta que podem lhe dar trabalho... especialmente se estiver investigando os Défias.", completion = "Você voltou em segurança. Não dá para pedir mais do que isso, não é?\n\nBem, vamos ver o que temos aqui. Isso parece ser bastante formal, em comparação aos documentos dos Défias que eu pude ver. Mas pode ser uma pista falsa para desviar sua atenção do que quer que esteja procurando.", rewards = "Você receberá:\n\n"},
[1245] = {title = "O diplomata desaparecido", objective = "Leve a Súmula Défias para Elling Trias em Ventobravo.", description = "Nossa, isso parece muito complexo para ser uma pista falsa. Eu nem devia estar olhando isso. Leve-a para o Trias. Ele vai conseguir decifrar essas coisas. E NÃO deixe ninguém atrapalhar você. Isso é maior do que eu. E talvez seja até maior do que ele, mas vamos esperar para ver.\n\nE me faça um favor, <name>, não fale para ninguém que eu lhe ajudei. Se os meus superiores descobrirem que eu sabia e não comuniquei nada disso, eles provavelmente vão me enforcar.", progress = "Você está de volta, e inteiro. Nós dois sabemos que você não veio aqui provar os queijos, então vamos dispensar as gentilezas. O Backor foi útil ou você nem foi para a Floresta do Crepúsculo ainda?", completion = "É... Uma leitura interessante. Jorge e DeLavey foram inteligentes em falar comigo.\n\nAparentemente os Défias estão se esforçando sobremaneira para realizar o plano deles. Hmm... como é? \"Punho\"? Esse nome é familiar.", rewards = "Você receberá:\n\n"},
[1246] = {title = "O diplomata desaparecido", objective = "Fale com Dashel Punhopétreo em Ventobravo.", description = "É, pensando bem... Dashel... Dashel alguma coisa... Como era o nome dele mesmo? PUNHO! Dashel Punhopétreo. Ele é conhecido como Punho em alguns círculos, provavelmente por que gosta de se meter em brigas.\n\nÉ capaz de você encontrar ele na Cidade Velha, bem no centro dela. Vá falar com ele. Se for preciso, use uma espada ou alguma coisa pesada e afiada para convencer ele a abrir o bico. E não hesite. Se ele estiver tão envolvido quanto esses papéis sugerem, então ele não vai dar nenhuma informação de boa vontade.", progress = nil, completion = "Epa!\n\nTu vem se aprochegando e metendo o bedelho nos meus assuntos? Assuntos que claramente não te dizem respeito.", rewards = "Você receberá:\n\n"},
[1247] = {title = "O diplomata desaparecido", objective = "Fale com Elling Trias em Ventobravo.", description = "Bem, sim... Hm... Eu talvez saiba alguma coisa sobre essa reunião na Quinta dos Aguiar. Aconteceu faz umas semanas. Eu não tive nada a ver com isso. Eu só me envolvi quando o plano A falhou... Malditos aventureiros.\n\nO bagual que trouxemos para o plano B veio de Menethil. Um tal de Fino. Isso é tudo que eu sei sobre o plano reserva deles.", progress = nil, completion = "É bom ver você com vida, &lt;name&gt;. Então, deu tudo certo com o Dashel?\n\nEu andei lendo mais sobre o plano deles, e apesar de não mencionar o nome do diplomata que estavam atrás, ele fala desse plano B que usaram depois que a primeira tentativa de captura falhou.\n\nEsse tal de Fino que o Dashel mencionou não tem outro nome escrito aqui, mas se você diz que ele está em Menethil, então é um começo.", rewards = "Você receberá:\n\n"},
[1248] = {title = "O diplomata desaparecido", objective = "Fale com Mikhail no Pantanal.", description = "Um contato meu se aposentou no Pantanal, na cidade portuária de Mikhail. Ele deve aceitar ajudar você se disser que está trabalhando para mim. O nome dele é Mikhail. Ele disse que queria trabalhar como garçom antes de deixar Ventobravo alguns anos trás.\n\nFale com ele, encontre esse Fino e descubra se tem mais alguma coisa sobre o diplomata sequestrado. Temos que descobrir para onde levaram ele, ou pelo menos quem ele é. A súmula não menciona nada sobre o plano deles depois da captura.", progress = nil, completion = "Ora, ora, ora, seja bem-vindo, &lt;name&gt;! Que bom que você veio. Que tal uma cerveja? Ou um assado de carneiro?\n\nÉ só dizer o que quer que eu providencio para você. Eu me chamo Mikhail, e estou aqui para servir você.\n\nHm, como é mesmo? Trias? Fale baixo, meu amigo. Isso não é o tipo de coisa que a taberna toda precisa ficar sabendo.", rewards = "Você receberá:\n\n"},
[1249] = {title = "O diplomata desaparecido", objective = "Impeça Tapoke Jahn de fugir e fale novamente com Mikhail em Menethil.", description = "Foi o Elling que mandou você? Muito bem, você é muito mais bem-<vindo/vinda> agora. O que precisar, é só pedir. Eu vou fazer de tudo para ajudar.\n\nHm, o Fino, é? O nome não me parece familiar, mas eu vou ficar de olhos abertos.\n\nNão olhe agora, <name>, mas o cavalheiro perto da porta está olhando para cá. Ele está prestando muita atenção na nossa conversa, se e que você me entende... Se fosse você, eu faria de tudo para impedir ele de fugir.", progress = nil, completion = "Você pegou ele? Que bom. Não imaginava que aquele fosse o Fino.\n\nPara dizer a verdade, não achei que fosse sentir falta da emoção das atividades escusas. Acho que velhos hábitos demoram a morrer. Não mencione isso para o Elling. Ele provavelmente vai tentar me convencer a voltar para Ventobravo, se souber que eu sinto falta da ação.\n\nQue bom que eu pude ajudar um pouco enquanto você estava aqui em Menethil. Se precisar de mais alguma coisa, é só pedir.", rewards = "Você receberá:\n\n"},
[1250] = {title = "O diplomata desaparecido", objective = "Fale com Mikhail no Pantanal.", description = "Os Défias não me pagaram o suficiente para morrer pela causa deles. Eles levaram o rei. Sim, o rei de Ventobravo, não um diplomata qualquer. Ele estava a caminho de Theramore para se encontrar com Jaina Proudmore. Parece que era um assunto importante, e um grande segredo. Mas não são apenas os Défias que estão envolvidos. Eles estão trabalhando com alguém. Acho que eles sabiam que isso era maior do que esperavam.\n\nA minha parte foi embarcar o homem deles enquanto o navio do rei estava no porto. O cara se chama Remos. Isso é tudo que sei, eu juro!", progress = nil, completion = "O REI?! Quer dizer... o rei? O rei de verdade? É sério? Você não disse que estava atrás dos sequestradores do rei. Eu nem fiquei sabendo que ele desapareceu... Isso explicaria a coroação do filho.\n\nEu vou avisar o Elling imediatamente. Ele vai querer saber que você está no rastro.\n\nNunca imaginei que o Jahn iria entregar Ventobravo por tão pouco. Mesmo um ladino precisa ter honra quando se trata de algumas coisas.\n\nO importante agora é encontrar esse tal de Remos. E eu acho que posso ajudar nessa.", rewards = "Você receberá:\n\n"},
[1251] = {title = "O escudo negro", objective = "Leve o Escudo de Ferro Enegrecido a Krog, na Aldeia Muralha Verde.", description = "Um escudo de ferro, pretejado pelo fogo que devastou a estalagem, está afixado aos destroços da chaminé – uma das poucas partes restantes da estrutura que um dia foi a Estalagem Pouso do Umbral.\n\nÉ possível remover o escudo dos tijolos da chaminé.", progress = "O que você traz aí, <name>? Uma pista encontrada nos escombros da Estalagem Pouso do Umbral?", completion = "Admito que humanos têm um gosto questionável pra decoração, mas será que usariam mesmo um escudo de ferro para adornar uma lareira? Talvez o escudo tenha sido colocado lá por quem pôs fogo na estalagem.\n\nUm aviso, talvez? Uma ameaça?", rewards = "Você receberá:\n\n"},
[1252] = {title = "Tenente Paval Reethe", objective = "Leve o Distintivo de Reethe ao Capitão Garrido Valente em Theramore.", description = "Uma luz fugidia no chão sob os escombros chama sua atenção. Ao retirar os destroços e as cinzas, é revelada uma insígnia de ouro em formato de âncora com uma inscrição branca: o símbolo de Theramore.\n\nAbaixo da âncora lê-se: \"Tenente Paval Reethe\".", progress = "Encontrou algo importante na Estalagem Pouso do Umbral?", completion = "As notícias que seguem a entrega de um distintivo nunca são boas.\n\nAlgumas vezes trata-se de alguém que caiu em batalha e eu sou o responsável por levar a uma família já sofrida a notícia da perda. Em outras, nunca descobrimos o que lhes sucedeu. E há outras ainda que são bem piores...", rewards = "Você receberá:\n\n"},
[1253] = {title = "O escudo negro", objective = "Leve o Escudo de Ferro Enegrecido para o Capitão Garrido Valente em Theramore.", description = "Um escudo de ferro, pretejado pelo fogo que devastou a estalagem, está afixado aos destroços da chaminé – uma das poucas partes restantes da estrutura que um dia foi a Estalagem Pouso do Umbral.\n\nÉ possível remover o escudo dos tijolos da chaminé.", progress = "Você encontrou alguma evidência na Estalagem Pouso do Umbral?", completion = "Hm, eu não sou um especialista em armas ou armaduras. Como usá-las, sim, mas não sua criação.\n\nEste escudo parece tão comum quanto qualquer outro, exceto pelo dano deixado pelo fogo que consumiu a estalagem.", rewards = "Você receberá:\n\n"},
[1258] = {title = "Caranguejo não é peixe...", objective = "Leve 12 Patas Impecáveis de Rastejante a Morgado Severo em Theramore.", description = "O outro componente da minha nova receita é a pata de caranguejo gigante. E os caranguejos conchalodo não servem! Infelizmente os caranguejos ideais vivem muito longe, no Pântano das Mágoas.\n\nA carapaça da pata é o elemento chave de apresentação do prato, de forma que preciso de patas impecavelmente perfeitas. Um caranguejo adulto tem no máximo uma ou duas patas bonitas assim e eu preciso de várias para estudar o meu prato.\n\nEntão, <name>, você está <disposto/disposto> a ir lá?", progress = "Meu estudo com as línguas de pedralama está alcançando ótimos resultados. E como vai a sua caçada de caranguejos?", completion = "São espécimes maravilhosos, &lt;name&gt;! As carapaças estão perfeitas, a carne é rosada e macia. Você tem sangue de chef nas veias, garota!", rewards = "Você receberá:\n\nAvental Barroco"},
[1259] = {title = "Tenente Paval Reethe", objective = "Descubra mais sobre Reethe consultando o Auxiliar Tesoran em Theramore.", description = "Não reconheço esse nome. É melhor você conferir com o Auxiliar Tesoran. Ele toma conta de todos os registros da guarda de Theramore, tanto de pessoal quanto de equipamento.\n\nCom uma pesquisa rápida ele deve descobrir quem é Reethe.", progress = nil, completion = "Olá, &lt;name&gt;. Você quer informações sobre um membro da guarda de Theramore? Reethe... é isso, não é?\n\nSó um segundo que eu encontro o livro.", rewards = "Você receberá:\n\n"},
[1260] = {title = "Morgado Severo", objective = "Fale com Morgado Severo em Theramore.", description = "Estou desenvolvendo um novo cardápio para o restaurante. Vou encher de pratos com temas pantaneiros. Acho que os clientes do Recôndito Azulego vão amar!\n\nEu mandei o meu sobrinho para Theramore, uma cidade na fronteira do Pântano Vadeoso. Ele está pegando receitas com os locais, mas eu preciso saber o que ele já conseguiu.\n\nA viagem até Theramore leva muito tempo. Tempo demais para mim. Aposto que <um/uma> <class> jovem como você adora viajar, não é mesmo?", progress = nil, completion = "Ah, o meu tio mandou você?\n\nBem, eu estou procurando receitas e tenho algumas ideias, mas preciso de ajuda.\n\nAjuda de alguém disposto a molhar os pés.", rewards = "Você receberá:\n\n"},
[1261] = {title = "Assim falou Marg", objective = "Leve o Pingente Adornado para Nazir na Aldeia Muralha Verde.", description = "Maldito conchalodo! Você não vai matar o Marg! Ah, mas estou morto. Ele deve ter me matado...\n\nMas a minha missão! Minha missão! <name>, você precisa completá-la.\n\nEnquanto estava perto de Theramore, eu avistei um grupo de soldados da Aliança acampados na praia. Um deles usava um pingente... um pingente adornado com mais gemas que um soldado poderia comprar!\n\nNaquela noite, eu invadi o acampamento e roubei o pingente, mas quando voltei para minha base, um homem-caranguejo me matou!\n\n<name>, você precisa encontrar esse conchalodo e recuperar o pingente!", progress = "<name>, você descobriu o que aconteceu com o Marg?", completion = "Esse pingente era de um soldado de Ventobravo?\n\nObrigado, &lt;name&gt;. Você descobriu o segredo sobre a morte do Marg. Mas este pingente é um novo mistério a ser resolvido, um que pode ter consequências terríveis para a Aliança e a Horda.", rewards = "Você receberá:\n\n"},
[1262] = {title = "Apresente-se a Zor", objective = "Leve o Pingente Adornado para Zor Solárbol em Orgrimmar.", description = "É estranho que o pingente roubado pelo Marg estivesse nas mãos de soldados comuns. Talvez o dono estivesse disfarçado, ou talvez os soldados o tivessem roubado!\n\nDe todo modo, este é o pingente de um nobre e nós temos que descobrir quem é o verdadeiro dono, se vamos desvendar este mistério.\n\nLeve o pingente para Zor Solárbol em Orgrimmar. Ele entende as políticas da Aliança, e saberá se este pingente tem uma história entre os nobres de Ventobravo.", progress = "Você veio da Aldeia Muralha Verde, que fica no Pântano Vadeoso? Como vai Nazir e seus esforços por lá?", completion = "Este pingente foi feito com grande perícia e perfeição, &lt;name&gt;. Nenhum orc cobiçaria uma joia dessas...\n\nMas muitos humanos, sim.\n\nEu vou estudar o pingente. Vamos torcer para que os mistérios sejam revelados.", rewards = "Você receberá:\n\n"},
[1263] = {title = "A estalagem incendiada", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1264] = {title = "O diplomata desaparecido", objective = "Encontre o Comandante Samaul no Pântano Vadeoso.", description = "No começo de todo mês, os fuzileiros de Kul Tiraz recebem o salário e geralmente deixam Theramore. Muitos deles veem para cá sapecar, bebericar e se divertir um pouco. Um desses grupos é cliente frequente da taberna, e eu acredito que o Remos costuma andar com eles.\n\nO Comandante Samaul de Theramore deve ser capaz de ajudar você a encontrar o Remos, se ele ainda estiver servindo por lá. Ele é completamente leal à Proudmore, mas se mencionar o nome do Elling, é capaz dele ajudar você... de forma discreta.", progress = nil, completion = "É um prazer conhecê-la, &lt;name&gt;. Eu sou o Comandante Samaul e estou no comando das forças de Proudmore aqui em Theramore.\n\nApesar de estar muito ocupado, se tiver alguma pergunta, fique à vontade para me perguntar. Eu talvez seja breve, mas não há porque ser rude com alguém que precisa de ajuda.\n\nSe planeja sair da cidade, eu sugiro que tenha cuidado. Além dos ogros, orcs e murlocs, a parte mais ao sul do pântano está cheia de dragões negros.", rewards = "Você receberá:\n\n"},
[1265] = {title = "O diplomata desaparecido", objective = "Procure pistas sobre o Recruta Remos no Pontal de Vigília.", description = "Remos? Sim, eu sei quem é. Mas por que você está atrás de um dos meus homens?\n\nO Elling falou para você encontrá-lo? Hm, eu não estou gostando nada disso. O Elling não teria mandado você até mim primeiro se o assunto não fosse importante. Entendo que não possa me contar nada, mas fique sabendo que eu tenho o dever de proteger essas terras. Se for causar algum tumulto, eu sugiro que me informe o que puder.\n\nAcredito que Remos foi designado para a primeira torre fora de Theramore. Tenha cuidado.", progress = nil, completion = "O homem que você procura, o tal de Remos, não está aqui. Outras fontes comunicaram à Grã-senhora Proudmore informações que acredito que você já tem. Ela me pediu que a encontrasse e lhe ajudasse em sua missão. Se permitir, eu lhe direi onde encontrar o Recruta Remos, para que possamos levá-lo à justiça.", rewards = "Você receberá:\n\n"},
[1266] = {title = "O diplomata desaparecido", objective = "Encontre o Recruta Remos no Pântano Vadeoso.", description = "O Recruta Remos está a oeste daqui. Ele atualmente está protegendo alguns dos infiltradores de Theramore enquanto eles descansam. Eles têm vigiado a base da Horda, Muralha Verde, e buscando outras coisas que possam ameaçar as nossas fronteiras.\n\nVocê não deve ter dificuldade de encontrá-lo. É só ficar <atento/atenta> às tendas ao longo da estrada.\n\nEu tenho outro assunto para resolver, mas devo alcançá-l<o/a> em breve. Você pode me encontrar na torre da Grã-senhora Proudmore, se tiver algum problema.", progress = nil, completion = "Olá, cidadã. É perigoso ficar andando por aí. Se você valoriza a sua vida, então é melhor não se demorar.\n\nNão se alongue com as perguntas, e eu tentarei lhe ajudar. Só fique de olho naqueles ogros imundos. Dizem que há uma base da Horda em algum lugar do pântano. Os infiltradores estão fazendo de tudo para encontrá-la, mas o processo tem sido devagar e nós tivemos pouco sucesso.", rewards = "Você receberá:\n\n"},
[1267] = {title = "O diplomata desaparecido", objective = "Fale com Jaina Proudmore no Pântano Vadeoso.", description = nil, progress = nil, completion = "Permita-me ser a primeira a lhe agradecer pelo que fez, &lt;name&gt;. Você tem sido uma bênção extraordinária para o meu povo e o povo de Ventobravo. É uma pena não nos termos encontrado em circunstâncias mais favoráveis.\n\nEntenda também que não posso agradecer abertamente por seus esforços, eu lhe asseguro novamente de que são muito bem-vindos.\n\nQuando visitar Theramore no futuro, fale comigo e eu tentarei deixá-la a par da situação.\n\nObrigada de novo, &lt;name&gt;.", rewards = "Você receberá:\n\nAnel-sinete de Jaina"},
[1268] = {title = "Pegadas em forma de casco", objective = "Informe Krog, na Aldeia Muralha Verde, sobre as pegadas suspeitas na Estalagem Pouso do Umbral.", description = "Você nota um rastro de pegadas em forma de casco saindo do esqueleto ainda fumegante da Estalagem Pouso do Umbral. Há algumas outras marcas e outros rastros quase imperceptíveis na terra macia do pântano.", progress = nil, completion = "Pegadas de cascos... Podem ter sido os javatuscos. Ou os centauros. A presença deles nos Sertões é bem significante, e não acho implausível que tenham cometido um crime assim.\n\nEu cheguei a lhe contar? Eles não incendiaram apenas a estalagem... A mulher e o filhote do dono também. Situação terrível, essa.\n\nEnfim, vou enviar Nagoro para descobrir onde os rastros nos levam.", rewards = "Você receberá:\n\n"},
[1269] = {title = "Tenente Paval Reethe", objective = "Leve o Distintivo de Reethe para Krog na Aldeia Muralha Verde.", description = "Um reluzir no chão chama sua atenção. Ao retirar os destroços e as cinzas, é revelada uma insígnia de ouro em formato de âncora com uma inscrição branca: o símbolo de Theramore.\n\nAbaixo da âncora, lê-se: \"Tenente Paval Reethe\".", progress = "Encontrou alguma pista sobre o que aconteceu na Estalagem Pouso do Umbral?", completion = "Conheço este humano, Paval Reethe. É um ser patético, mesmo dentre os humanos. Desertou seus colegas e está se escondendo na floresta.\n\nEnviei Ogron para encontrá-lo. Ele é suspeito de roubar nossos suprimentos. Acho que Ogron foi em direção sudeste.", rewards = "Você receberá:\n\n"},
[1270] = {title = "A fuga do Cascão", objective = "Escolte Inácio \"Cascão\" e, em seguida, fale com Cáliper Porcatraca na Vila Catraca.", description = "Enfim alguma coisa que não quer me devorar!\n\nPreciso de ajuda para lidar com os bichos daqui e você é <perfeito/perfeita> para a tarefa, cara.\n\nUm goblin da Vila Catraca precisa de umas folhas de trevo aquático para uma poção ou algo que o valha... Para ser sincero, não me interessa... o dinheiro era bom, eu conheço o pântano e tô aí.\n\nO negócio é que eu fui emboscado antes de encontrar os trevos! Você me ajudaria a fugir e a me proteger enquanto procuro a planta para Mebok?", progress = nil, completion = "O Cascão passou aqui com as ervas que pedi e me contou que você ajudou ele a se livrar de uma situação das brabas. Os camaradas do Cascão são meus camaradas!\n\nEle me pediu pra te entregar isso, se você aparecesse por aqui, e depois voltou pro pântano.", rewards = "Você receberá:\n\nElixir de Fortitude"},
[1271] = {title = "O banquete do Recôndito Azulego", objective = "Meu sobrinho disse que você o ajudou em sua pesquisa em Vadeoso, e que você livrou o sócio dele de uma enrascada. Bem, nós da família Stern nos mantemos unidos e não nos esquecemos de nossos amigos!\n\nLave as mãos, <name>, pois será dada uma festa em honra de seu nome!", description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nToalhinha Úmida"},
[1272] = {title = "Procurando Reethe", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1273] = {title = "O interrogatório de Reethe", objective = "Vá com Ogron falar com Reethe, e então fale novamente com Krog na Aldeia Muralha Verde.", description = "Demorou, mas Ogron encontrar Reethe. Ele se esconder bem para um humano.\n\nOgron preocupado com ele ter ficado louco depois de tanto tempo no pântano. Você vir com Ogron fazer perguntas a Reethe.", progress = nil, completion = "Humpf. Por um lado é bom que Reethe não esteja mais rastejando pelo pântano, mas por outro é uma pena que esses humanos tenham uma consistência tão fraca que sequer são capazes de se recuperar de uma ferida de flecha.\n\nEssa pista não nos levou a lugar algum; precisamos começar outra busca.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nFura-olhosArcabuz Explosivo\n\nVocê também receberá:\n\n"},
[1274] = {title = "O diplomata desaparecido", objective = "Encontre o Bispo DeLavey na Bastilha Ventobravo.", description = "Licença, <moço/moça>. O Bispo DeLavey pediu para eu encontrar aventureiros para ajudar ele com um assunto difícil.\n\nSe puder ir discretamente para a Bastilha Ventobravo e falar com ele assim que possível, tenho certeza que ele vai agradecer a sua ajuda. Mas seja <discreto/discreta>. Esse é um assunto muito importante.", progress = nil, completion = "Olá, &lt;name&gt;. Eu sou o Bispo DeLavey.\n\nSe você está me questionando sobre meus assuntos aqui na bastilha, então o jovem Tomás conseguiu persuadi-lo a ajudar o nosso reino. Ele é muito competente em seguir minhas ordens.\n\nTemo não ter muito tempo para explicar, então me permita ser direto e, por favor, tente evitar que isso venha a público.", rewards = "Você receberá:\n\n"},
[1275] = {title = "Pesquisar a corrupção", objective = "Gershala Umbrurmúrio em Auberdine quer 8 Troncos Encefálicos Corrompidos.", description = "Esses elfos sangrentos ignorantes brincam com magia demoníaca. Eles não viram o que aconteceu com as nagas e os sátiros de Kalimdor?\n\nEu temo que os elfos sangrentos adquiram uma deformação parecida. Azeroth não se pode dar ao luxo de criar outra raça de monstros depravados.\n\nEu quero estudar os troncos encefálicos dos sátiros e nagas para tentar entender para onde os elfos estão se dirigindo. Nas Profundezas Negras, que fica na costa do Vale Gris, você encontrará tanto sátiros quanto nagas.\n\nEsta será uma viagem muito longa, <name>.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nBroches de BesouroCapa da Prelação\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[1276] = {title = "O escudo negro", objective = "Mostre o Escudo de Ferro Enegrecido para Mosarn no Penhasco do Trovão.", description = "Ah, me lembrei! Mosarn é um ferreiro respeitado no Penhasco do Trovão. Sabia que já havia escutado esse nome antes.\n\nEm agradecimento, a Harmonia Telúrica me recompensou com um conjunto de armadura forjado por Mosarn. Muito bem feita, aliás.\n\nTalvez ele possa dar mais detalhes sobre o escudo que forjou.\n\nNão é uma pista muito boa, mas já que não conseguimos nada melhor de Reethe, isso é tudo o que temos.", progress = "Olá, <class>. Há algo que eu possa fazer por você?", completion = "É, você está certo. Eu fiz esse escudo.\n\nMas sinto não saber muito mais sobre ele, &lt;name&gt;. Forjo tantos escudos e armaduras diferentes que é impossível me lembrar de todos.", rewards = "Você receberá:\n\n"},
[1277] = {title = "Pegadas de centauros", objective = "Derrotado.Nagoro revelou que as pegadas de centauro pertencem a alguns sujeitos em Maraudon.", description = "Nagoro revelou que as pegadas de centauro pertencem a alguns sujeitos em Maraudon.", progress = nil, completion = nil, rewards = nil},
[1278] = {title = "Clã Temível Totem", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1279] = {title = "Pegadas de centauros", objective = "Derrote um dos três.", description = "Derrote um dos três.", progress = nil, completion = nil, rewards = nil},
[1280] = {title = "Pegadas de centauros", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1281] = {title = "A canção do Jim", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1282] = {title = "Chamam-no Jim Sorridente", objective = "Fale com o Capitão Garrido Valente em Theramore.", description = "Não consigo deixar de sentir pena do velho Jim. Ele já foi diferente. Teve uma estalagem no pântano por algum tempo, um lugar maravilhoso.\n\nOlha, eu não estou muito a fim de falar disso. Se quiser saber a história, vá falar com o capitão Valente.", progress = nil, completion = "O nome verdadeiro dele é James Hyal. Ele abriu uma estalagem bem à beira dos Sertões. Quis abrir um lugar decente para os viajantes fazerem uma parada...\n\nTalvez ele estivesse pensando grande demais, mas tudo correu bem na estalagem por um tempo. Só que nós perdemos o controle das estradas do pântano, que foram tomadas pelos ogros.\n\nJames era teimoso demais para desistir de seus sonhos e gozar da segurança entre os nossos muros, e foi assim que a Estalagem Pouso do Umbral foi queimada. Estamos investigando, mas não tivemos muita sorte. Se quiser ajudar, veja o que consegue encontrar nos destroços.", rewards = "Você receberá:\n\n"},
[1283] = {title = "Incêndio no Pouso do Umbral", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1284] = {title = "Pegadas em forma de casco", objective = "Informe o Capitão Garrido Valente, em Theramore, sobre as pegadas suspeitas na Estalagem Pouso do Umbral.", description = "Você nota um rastro de pegadas em forma de casco saindo do esqueleto ainda fumegante da Estalagem Pouso do Umbral. Há algumas outras marcas e outros rastros quase imperceptíveis na terra macia do pântano.", progress = nil, completion = "Pegadas de cascos? É, é verdade que há muita atividade dos centauros nas redondezas do Pântano Vadeoso. Em nossas breves incursões, encontramos acampamentos e propriedades deles espalhados pela região.\n\nVou enviar Falgran Hastil, um de meus melhores rastreadores, para descobrir aonde levam as pegadas.", rewards = "Você receberá:\n\n"},
[1285] = {title = "Os homens de Daelin", objective = "Leve as informações sobre Reethe ao Capitão Garrido Valente em Theramore.", description = "Redão... Redolfo... Achei! Tenente Paval Reethe.\n\nAlistado na guarda de Theramore junto com a maior parte dos recrutas que atravessaram o mar com a grã-senhora Proudmore. Alistado na marinha de Kul Tiraz com dezesseis anos. Serviços primorosos, promoções...\n\nSituação atual: desaparecido.", progress = nil, completion = "Desaparecido? É assim que Tesoran chama os traidores.\n\nCertamente é um daqueles tolos tão cegamente fieis ao almirante Daelin que acabam virando traidores. Não é uma ironia? Os desertores nos acusam de trair a Aliança.", rewards = "Você receberá:\n\n"},
[1286] = {title = "Os desertores", objective = "Obtenha informações sobre Reethe de Balos Xavier e dos desertores no Pântano Vadeoso.", description = "Tem sido difícil marcar presença no pântano, mas, com muito esforço, conseguimos manter várias torres de observação defensivas espalhadas pela região.\n\nAs últimas informações e relatórios de batedores indicam que um grupo de desertores invadiu uma de nossas torres abandonadas no Posto Perdido, a sudeste da estalagem.\n\nParece que o líder dos desertores se chama Balos. Tente obter alguma informação dele.\n\nTenha cuidado, <name>. Pode ser difícil arrancar alguma coisa do homem.", progress = nil, completion = "Foi o Valente que mandou você? Ele não quis sujar as mãos, não foi?", rewards = "Você receberá:\n\n"},
[1287] = {title = "Os desertores", objective = "Fale sobre Reethe com o Capitão Garrido Valente em Theramore.", description = "Reethe? Aquele covarde?\n\nExpulsamos ele do acampamento porque ele insistiu em continuar saqueando os suprimentos dos ogros da Aldeia Muralha Verde. Nossos números não são grandes o suficiente para enfrentarmos aqueles brutamontes.\n\nEle passou dos limites várias vezes, daí nós o deixamos por conta dele.\n\nAgora que ajudei você... Bem que poderia nos ajudar e fingir que nunca nos encontrou, não é mesmo?", progress = nil, completion = "Vou enviar o Tenente Caldwell para lidar com os desertores do Posto Perdido. E também o instruirei a encontrar Reethe. O pântano não é grande o bastante para uma pessoa desaparecer.", rewards = "Você receberá:\n\n"},
[1293] = {title = "Pegadas de centauros", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1294] = {title = "Solidariedade de centauro", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1295] = {title = "Plano de ação", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1296] = {title = "Plano de ação", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1297] = {title = "Plano de ação", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1299] = {title = "O trabalho sujo de Thrall", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1300] = {title = "Lorne Totem Severo", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1301] = {title = "James Hyal", objective = "Fale com Vincent Hyal no Porto de Menethil.", description = "Antes de trabalhar no Recôndito Azulego, treinei com um dos melhores chefs e estalajadeiros que já conheci: James Hyal. Tudo que consegui e onde cheguei na vida foi graças a ele.\n\nComo será que ele está? Ele e o irmão, Vincent, foram trabalhar numa taberna no Porto de Menethil, mas faz tempo que não tenho notícias.\n\nSe passar por aquelas bandas, pode dar um alô a ele por mim?", progress = nil, completion = "James? Ah, ele partiu já faz um tempo.\n\nTeve uma recaída de patriotismo, eu acho. Não é difícil ver o porquê. Com toda essa loucura em Lordaeron, muitas pessoas se juntaram a Jaina Proudmore quando ela cruzou o oceano.", rewards = "Você receberá:\n\n"},
[1302] = {title = "James Hyal", objective = "Fale com o Tabelião Lendry em Theramore.", description = "Meu irmão James cruzou o oceano com a Grã-senhora Proudmore. A última notícia que tive foi que o conflito tinha acalmado por lá e que James estava montando uma estalagem no continente. Do jeito que cozinha, aposto que ela deve vai de vento em popa onde ele estiver.\n\nNão tenho notícias dele faz um tempo — ocupado com a estalagem, imagino — mas o tabelião de Theramore deve saber onde encontrá-lo.", progress = nil, completion = "James Hyal, James Hyal... de onde conheço esse nome?\n\nMas é claro! O incidente da Estalagem Pouso do Umbral. Caso tenha informações sobre o ocorrido, insisto que você informe o Capitão Valente. Ele está encarregado das investigações.", rewards = "Você receberá:\n\n"},
[1318] = {title = "Negócios pendentes", objective = "Encontre as Manoplas do Poder de Gordok e devolva-as para o Capitão Kebrapaw no Gládio Cruel.\n\nDe acordo com Kebrapaw, a \"história de ancião diz que Tortheldrin — um elfo \"medonho\" que se diz príncipe — roubou as manoplas de um dos reis Gordok.", description = "Agora que tu é o rei, quem sabe tu pega de volta daquele elfo medonho, que se diz príncipe, um artefato muito valioso! Desde que o elfo medonho, Tortheldrin, roubou ele do primeiro rei Gordok!\n\nO artefato é chamado de Manoplas do Poder de Gordok. A lenda conta que só o verdadeiro rei de Gordok vai dar conta de pegar de volta. A lenda também diz que o elfo medonho ainda anda por aí. Então você vai lá, pega elas e fica com manoplas poderosas para sempre! Arr, eu te mostro!Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nProtetores de Mão de GordokManoplas de GordokLuvas de GordokMitenes de Gordok\n\nVocê também receberá:\n\n"},
[1319] = {title = "O escudo negro", objective = "Mostre o Escudo de Ferro Enegrecido para Caz Doismancal em Theramore.", description = "Sinto muito por não poder ajudar, <name>, mas posso indicar alguém que <o/a> ajudará. Caz Doismancal, um de nossos melhores ferreiros, trabalha na forja da ferraria.\n\nLeve o escudo para ele e escute o que tem a dizer.", progress = "Algo que precisa ser examinado?", completion = "Hum... é uma peça de ferro interessante esta que possui. É certeza que não foi forjada por nenhum dos ferreiros que conheço do lado de cá do mar, e também não é trabalho meu.", rewards = "Você receberá:\n\n"},
[1320] = {title = "O escudo negro", objective = "Fale com o Capitão Garrido Valente em Theramore.", description = "Ah, isso quase me passou despercebido... A faixa de couro na parte traseira deve ter queimado, mas parece que foi feita para uma pessoa bem grandona. Aliás, eu até diria que para um tauren.\n\nÉ tudo que posso dizer. Peça desculpas ao capitão Valente por eu não ter podido ajudar mais.", progress = nil, completion = "É uma informação muito vaga, mas não podemos nos esquecer dela. Com o tempo, outras peças do quebra-cabeça se encaixarão para formar uma imagem mais elucidativa do que aconteceu.", rewards = "Você receberá:\n\n"},
[1321] = {title = "O escudo negro", objective = "Mostre o Escudo de Ferro Enegrecido para Do'gol na Aldeia Muralha Verde.", description = "Hmm... Não vejo nada de extraordinário neste escudo. Precisamos levá-lo a Do'gol, o ferreiro, para que ele o examine. Talvez ele possa nos dizer mais, mas eu não alimentaria muitas esperanças...", progress = "Que você me traz, <class>?", completion = "Escudo bom. Muito forte, pesado, bom. Mas Do'gol não quer ele. Do'gol fazer escudos, não comprar, &lt;name&gt;.\n\nVocê saber.", rewards = "Você receberá:\n\n"},
[1322] = {title = "O escudo negro", objective = "Obtenha 6 Vesículas de Peçonha Ácida para Do'gol na Aldeia Muralha Verde.", description = "Huuum, lateral do escudo tem coisa escrita, mas difícil ler queimado.\n\nDo'gol precisar de ácido pra limpar. Peçonha de aranhas presanegra do pântano ser bom. Traz vesícula e eu pode limpar escudo.", progress = "Você conseguir vesículas de aranha?", completion = "Do'gol não gostar de aranha muito grande. Do'gol só gostar de aranha que Do'gol sentar em cima e esmagar.\n\nObrigado, &lt;name&gt;. Agora Do'gol abrir vesícula e derramar em cima de escudo...", rewards = "Você receberá:\n\n"},
[1323] = {title = "O escudo negro", objective = "Fale com Krog na Aldeia Muralha Verde.", description = "Opa, acho que eu derramar ácido demais no escudo. Deixa Do'gol esfregar um pouco...\n\nHum, difícil ler, mas parece ser o nome de Mosarn. Nunca ouvir falar de Mosarn. Quem sabe Krog conhecer? Krog conhecer muita gente.", progress = nil, completion = "Mosarn? Não me é estranho... Talvez me lembre mais tarde.\n\nNão deve ser nada importante. O escudo pode ter vindo de qualquer parte e esse detalhe pode não ter importância...", rewards = "Você receberá:\n\n"},
[1324] = {title = "O diplomata desaparecido", objective = "Derrota o Recruta Remos in Pântano Vadeoso.", description = "Quê?! Você vem aqui me acusar de aliança com os Défias e acha que vou ficar aqui escutando essas calúnias sem fazer nada?! Alguma coisa me diz que faz tempo que você devia levar uma lição.", progress = nil, completion = "Eu lhe asseguro que todos estão consternados com a situação atual, mas permita que o levemos de volta a Theramore para interrogá-lo. Descobriremos um jeito de achar o rei.\n\nEstou certo de que a Grã-senhora Proudmore gostaria de agradecer-lhe pela ajuda. Fale com ela quando for oportuno.", rewards = "Você receberá:\n\n"},
[1338] = {title = "Ordens dos Lançatroz", objective = "Entregue o Pedido de Lançatroz para Furen Barbalonga em Ventobravo.", description = "Eu conheço um ferreiro de armaduras cujo trabalho admiro muito. O nome dele é Furen Barbalonga, e habilidade como a dele não há. Estou precisando de um escudo novo, quero um dos dele!\n\nMas tem um problema... O Furen mora longe, lá para o sul, no distrito enânico de Ventobravo. Em terras humanas! Nem imagino o que o fez morar tão longe de Altaforja. Os humanos devem pagar muito bem para segurar um anão de qualidade como ele por lá!\n\nEntão, se você quer viajar e ainda ganhar umas moedinhas, poderia entregar o meu pedido para o Furen?", progress = "Uma carta das terras enânicas? Quem será que me enviaria um pedido de tão longe, lá do norte?\n\nDeixe-me ver esse pedido.", completion = "Ah, um pedido de um dos Lançatroz. É um clã nobre, com muitos anões de qualidade. E ricos também.\n\nObrigado, &lt;name&gt;. Vou começar a trabalhar no escudo do Montanhista Lançatroz até o final do dia.", rewards = "Você receberá:\n\n"},
[1339] = {title = "Uma tarefa do Montanhista Lançatroz", objective = "Fale com o Montanhista Lançatroz.", description = "Ouvi dizer que o Montanhista Lançatroz anda atrás de um corredor. Alguém para fazer umas viagens para ele. Interessa? Será você $Uo:a; <race> que procuro?\n\nSe for, encontre Lançatroz no topo da torre de vigia norte.", progress = nil, completion = "Tás com razão. Tenho comigo uma tarefa que não posso executar acampado de guarda. Pra dizer-te a verdade, terás que correr longe de Loch Modan.\n\nConseguiste uma chance boa de te aventurar!", rewards = "Você receberá:\n\n"},
[1358] = {title = "Uma amostra para Hermógenes", objective = "Leve a Amostra de Coração de Lobo para Boticário Hermógenes nos Sertões.", description = "Um colega de Kalimdor, o boticário Hermógenes, está pesquisando os efeitos das toxinas de Lordaeron nas feras de Kalimdor. Ele teria muito interesse em uma amostra dos corações de lobo que Renferrel me enviou.\n\nHermógenes está nos Sertões, numa vila chamada Encruzilhada. Para chegar até ele, viaje por zepelim até a capital orc de Orgrimmar, depois siga a pé para o sul, rumo a Durotar. Adentre os Sertões indo a oeste e logo você estará na Encruzilhada.", progress = "Você tem um pacote para mim?", completion = "Excelente. Se os relatórios iniciais estiverem corretos, a toxicidade desses corações podem promover um avanço na ciência dos venenos.\n\nAgradeço pela entrega, &lt;name&gt;. Você mereceu esta recompensa.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nLuvas do CervejeiroCapa Drapejada Longa\n\nVocê também receberá:\n\n"},
[1359] = {title = "Entrega para Zilda", objective = "Leve a Amostras de Coração de Lobo para Boticária Zilda na Cidade Baixa.", description = "Os corações de lobo que você arranjou exibem características inusitadas, jamais vistas em espécimes similares. A toxicidade deles deveria ser letal até para os lobos cujo sangue eles bombeiam!\n\nIsso é surpreendente e requer mais estudos. Separei umas amostras dos corações obtidos. Leve-as para minha colega, a boticária Zilda. Você a encontra em nossa sede na Cidade Baixa.", progress = "Você tem algo para mim?", completion = "Ah, claro, as amostras de coração que Renferrel mencionou. Não vejo a hora de fazer experimento com elas... A Real Sociedade de Boticários sempre tem interesse quando novas toxinas são descobertas.", rewards = "Você receberá:\n\n"},
[1360] = {title = "Tesouros recuperados", objective = "Pegue a posse querida de Krom Braçoforte em seu baú no Saguão Norte de Uldaman e leve-a para ele na Altaforja.", description = "Já me peonei de escavador no sítio arqueológico de Uldaman, nos Ermos... até que aqueles trastes dos troggs apareceram. Refuguei-me no mundo só com as calças na mão e nada mais. Na revira, larguei pra trás minha posse mais querida. Os troggs não podem botar a mão nela no baú, mas ele tá no grosso dessa tropa.\n\nFicaria muy grato, em honra e contos, se tu a recuperasse! Esse baú com meu nome está na ponta do Saguão Norte. É lá que precisas te enfiar.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[1361] = {title = "Regthar Portões-da-morte", objective = "Fale com Regthar Portões-da-morte.", description = "Guerreiro da Horda, Regthar Portões-da-morte está recrutando agentes pra uma missão na terra da Desolação. Fale com ele se quiser se oferecer. Ele está nos Sertões, numa série de casamatas a oeste da Encruzilhada.\n\nAfie suas armas. A guerra assola a Desolação.", progress = nil, completion = "Sim, estou recrutando. Esta missão é na Desolação, contra os centauros de lá. Vou ser breve pra você não perder tempo.", rewards = "Você receberá:\n\n"},
[1362] = {title = "Os Kolkar de Desolação", objective = "Fale com o Felgur Doiscortes no Entreposto do Espírito que Anda, em Desolação.", description = "Os centauros Kolkar são originários de Desolação, mas se espalharam pelos Sertões. Isso está muito claro, e já enviamos agentes da Horda para lidar com o problema de lá.\n\nMas enquanto os centauros mantiverem essa forte presença em Desolação, serão sempre uma ameaça. Já é hora de nos livrar desse problema.\n\nFale com o Felgur Doiscortes no Entreposto do Espírito que Anda, em Desolação. Ele foi enviado para descobrir mais informações sobre os centauros e saberá qual é a melhor forma de lidar com os Kolkar de Desolação.", progress = nil, completion = "Você veio a mando de Reghtar? Então veio para lidar com os centauros. Muito bom.\n\nEspero que você esteja descansado. Não sei quanto tempo nossa campanha vai durar, só sei que será longa.\n\nSente-se e escute.", rewards = "Você receberá:\n\n"},
[1363] = {title = "A mando de Mazen", objective = "Fale com o Acólito Dellis em Ventobravo.", description = "Como responsável pelos trabalhos na biblioteca da Academia de Ciências e Artes Arcanas, é meu dever assegurar manter o registro incessante de todos os tomos e pergaminhos mágicos. Quem sabe as confusões que tais textos encantados poderiam causar em mãos erradas?\n\nIsso me faz lembrar... meu assistente, o acólito Dellis, cometeu um erro gravíssimo na semana passada. Ele é capaz nos assuntos bibliotecários, mas inepto fora dos portões de Ventobravo. Seja valente e dê uma ajuda ao velho Dellis, por favor?", progress = nil, completion = "A culpa não foi minha. Sério, não foi.", rewards = "Você receberá:\n\n"},
[1364] = {title = "A mando de Mazen", objective = "Recupere os Ensaios de Hadggar sobre Convergência Dimensional e entregue-os para Vigia Mahar Ba, na Bastilha de Etergarde.", description = "As instruções do mestre Mazen foram claras: Entregar um ensaio importantíssimo ao Vigia Mahar Ba na Bastilha de Etergarde.\n\nMas eu nunca havia pisado na Barreira do Inferno! A jornada foi assustadora!\n\nQuando cheguei ao Pântano das Mágoas, fiz uma curva errada e horríveis criaturas vegetais do pântano me cercaram. Elas pareciam querer me atacar, então corri, mas no afã acabei derrubando o ensaio. Uma das feras o pegou, tenho certeza.\n\nPoderia fazer a gentileza de tentar recuperá-lo e entregá-lo para Mahar Ba?", progress = "O que traz você à Bastilha de Etergarde, <race>?", completion = "Esplêndido!\n\nTemi, por um tempo, que minha solicitação dos Ensaios de Hadggar sobre Convergência Dimensional tivesse sido ignorada pelo Mestre Mazen da Academia de Ciências e Artes Arcanas.\n\nEle deve estar atolado com tantas solicitações devido às muitas ocorrências estranhas que têm surgido.\n\nAgora, se me der licença, preciso dar início à pesquisa imediatamente. Muito me anima ter, enfim, um vislumbre da mente genial de Hadggar.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nFaixa do ProfessorBotas de Flanar\n\nVocê também receberá:\n\n"},
[1365] = {title = "Khan Dez'hepah", objective = "Leve a Cabeça de Khan Dez'hepah para Felgur Doiscortes na Desolação.", description = "Os Kolkar não são os únicos centauros na Desolação. Muitos clãs vagam por essas planícies áridas, lutando e morrendo nos conflitos. Ainda bem que lutam entre si, porque são fortes e brutais. Se os centauros se unissem contra nós, seriam inimigos ferozes.\n\nPrecisamos cuidar deles antes que eles se unam.\n\nSua primeira tarefa é desmantelar o clã Kolkar. Faça isso matando o líder, Khan Dez'hepah.\n\nA Aldeia Kolkar fica a nordeste daqui. Vá, e que a sorte acompanhe você.", progress = "<name>, derrotou Khan Dez'hepah?", completion = "Muito bem. Essa ação rápida contra os Kolkar é uma boa demonstração de força, e força é a linguagem dos centauros.\n\nA morte do líder Kolkar não apenas desmantela o clã, mas também serve de alerta aos outros clãs.", rewards = "Você receberá:\n\n"},
[1366] = {title = "Suvenir de centauro", objective = "Entregue 15 Orelhas de Centauro para Felgur Doiscortes em Desolação.", description = "Agora que você já deu um susto nos clãs dos centauros, é hora de fazer a sangria neles. Quando passar por Desolação, cace centauros. Não me importa de que clãs. Mas quero as orelhas. Um monte de orelhas.\n\nAo reduzi-los em número, você também vai reduzir a determinação deles, e qualquer ideia de expansão será sufocada.\n\nBoa caçada, <name>.", progress = "Como vai a coleção de orelhas, <name>? Grande?", completion = "A essa altura, os centauros já devem saber qual é o seu nome. Tenho certeza que já sabem, e respeitam, e temem. E, da mesma forma que sabem quem você é, eles já sabem qual é a sua bandeira.\n\nA bandeira da Horda.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nFaixa LilásProtetores de Mão Reforçados\n\nVocê também receberá:\n\n"},
[1367] = {title = "Aliança com os Magram", objective = "Ganhe reputação de Respeitado pelos Magram e depois fale com Warug.", description = "Para derrotar os centauros, precisamos tirar proveito do ódio mútuo que os clãs têm. Vamos formar uma aliança com um dos clãs e, assim, vamos encontrar um jeito de derrotar todos eles.\n\nSe quiser se aliar com os Magram, mate seus inimigos, os Gelkis. Isso fará os Magram respeitarem você. Quando tiver reputação suficiente, fale com o porta-voz Magram Warug, nos arredores da Aldeia Magram, sudeste da Desolação.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1368] = {title = "Aliança com os Gelkis", objective = "Ganhe a reputação de Respeitado pelos Gelkis e depois fale com Uthek, a Sábia.", description = "Para derrotar os centauros, precisamos tirar proveito do ódio mútuo que os clãs têm. Vamos formar uma aliança com um dos clãs e, assim, vamos encontrar um jeito de derrotar todos eles.\n\nSe quiser se aliar com os Gelkis, mate seus rivais mais odiados, os Magram. Depois que os Gelkis respeitarem você, fale com a xamã Gelkis Uthek, a Sábia, nos arredores da Aldeia Gelkis, sudoeste da Desolação.", progress = nil, completion = "A terra fala comigo. E ela diz que você matou muitos Magram. Bom.\n\nSe os Gelkis podem mesmo confiar em você, ouça com atenção. Tenho mais tarefas para você.", rewards = "Você receberá:\n\n"},
[1369] = {title = "Lágrimas partidas", objective = "Mantenha sua reputação alta junto aos Magram e entregue 3 Lágrimas Partidas para Warug na Aldeia Magram.", description = "Os Gelkis acham que a nossa matriarca, Theradras, nos protege. Eles são uns estúpidos. Ninguém protege os centauros. Os Magram não precisam de proteção! Somos mais fortes que todos!\n\nMostre aos Gelkis como eles são estúpidos. Vá até a caverna da vila deles, onde guardam alguns cristais, chamados de Lágrimas de Theradras. Destrua as lágrimas e traga para mim o que restar delas!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1370] = {title = "Assalto à dispensa", objective = "Entregue 6 sacos de Carne Mal Desidratada para Uthek, a Sábia, na Aldeia Gelkis.", description = "Quando não estamos em guerra, a gente caça para comer. A carne nos deixa fortes, e é melhor lutar de barriga cheia.\n\nOs Magram pensam assim também. Eles estocam carne para poder lutar por mais tempo e então saem para caçar de novo. Se você saquear a vila deles, a leste daqui, e levar os estoques de carne, eles vão ficar enfraquecidos.", progress = "Só volte aqui quando provar seu valor perante os Gelkis!", completion = "Muito bom. Dá para escutar as barrigas dos Magram roncando daqui de longe!\n\nVou distribuir a carne entre os Gelkis, e então vamos lutar dias sem descanso!\n\nVocê fez um favor para os Gelkis, &lt;name&gt;.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nEstrela de PrataFlecha com PenasTiro Detonador\n\nVocê também receberá:\n\n"},
[1371] = {title = "Engenhoca para Warug", objective = "Mantenha sua reputação alta junto aos Magram e entregue 3 Bonecos-alvo Avançado para Warug na Aldeia Magram, em Desolação.", description = "Há muito tempo, um pequeno gnomo chegou a Desolação. Ele tinha cavalos e uma carroça que carregava coisas estranhas. Coisas engraçadas. Coisas que queimavam em mil cores. Coisas que se mexiam mas não estavam vivas. Eu matei o pequeno gnomo, mas antes disso, ele me fez rir.\n\nEu guardei a coisa de que mais gostei da carroça dele. Era um boneco de um humano, feito de madeira, que ficava acenando para mim. Até que eu bati nele e quebrei ele. Traga um boneco novo para mim!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1372] = {title = "A mais pura verdade", objective = "Fale com Boticário Faustino no Refúgio dos Mendigos.", description = "Nossa força expedicionária foi enviada para mapear a baixa Azeroth à mando de Varimatras.\n\nEnquanto patrulhava, notei um infiltrador humano espionando no Refúgio dos Mendigos. Antes que conseguisse a custódia dele, alguns dos nossos... camaradas... orcs o capturaram e o levaram para o Pedregal, um baluarte orc no Pântano das Mágoas.\n\nQuero... oferecer auxílio... a nossos aliados órquicos. Eles não notaram nosso acampamento aqui, mas ainda assim quero prestar ajuda.\n\nAjude o Faustino aqui a desenvolver um... soro da verdade para nosso amigo no Pedregal.", progress = nil, completion = "Zaerdus me contou sobre o infiltrador enxerido pouco antes de você chegar.\n\nIsso é mesmo um problema e tanto. A última coisa que queremos é um bisbilhoteiro xeretando no Refúgio dos Mendigos.\n\nTenho certeza de que os orcs do Pedregal devem estar ansioso para ouvir o que nosso amigo tem a dizer. Como são nossos aliados, a-hem, é nossa obrigação ajudá-los a obter toda informação que puderem do humano.\n\nMe ajude, &lt;name&gt;, e mandaremos um soro da verdade para Pedregal.\n\nPela Horda!\n\n... É assim que diz?", rewards = "Você receberá:\n\n"},
[1373] = {title = "Ongekan", objective = "Mantenha alta sua reputação junto aos Gelkis e entregue um Estilhaço de Draenetista para Uthek, a Sábia, na Aldeia Gelkis, em Desolação.", description = "Você ainda não provou seu valor o bastante, e a sua próxima tarefa vai levar você para longe. Por isso, os Gelkis vão ter que lutar contra os Magram sozinhos.\n\nA terra sussurra segredos para os que dão ouvidos. Um dos segredos que a terra me contou trata de um cristal vermelho. Uma gema chamada draenetista, que vem de um lugar onde as montanhas voam e os demônios governam.\n\nTraga-me um fragmento de draenetista para minha mágica ficar mais forte! O estilhaço do cristal está em um lugar chamado Pântano das Mágoas, sob a guarda de um Perdido de nome Ongekan.", progress = "Prove que você é amigo dos Gelkis, <name>.", completion = "Sim, este é o estilhaço de que a terra falou. Dá para sentir a forte magia dentro dele! Vou usar essa força contra os inimigos dos Gelkis.\n\nVocê prestou mais um serviço para nós, &lt;name&gt;. Quem sabe, em breve, vamos chamá-lo de amigo.", rewards = "Você receberá:\n\n"},
[1374] = {title = "Khan Jehn", objective = "Entregue a Cabeça de Khan Jehn para Uthek, a Sábia, na Aldeia Gelkis, em Desolação.", description = "A terra falou comigo e me contou que os Magram estão mais fracos. Chegou a hora de matar o Khan deles!\n\nTraga para mim a cabeça do Jehn, o Khan Magram!", progress = "Prove que você é amigo dos Gelkis, <name>.", completion = "O Khan Jehn era forte, mas também estúpido. Ele acreditou demais na própria força. Não viu que a magia da terra é mais forte. Agora, sem um líder, os Magram cairão, e os Gelkis por fim dominarão!", rewards = "Você receberá:\n\n"},
[1375] = {title = "Khan Shaka", objective = "Mantenha alta sua reputação junto aos Magram e entregue a Cabeça de Khan Shaka para Warug na Aldeia Magram, em Desolação.", description = "Você ajuda os Magram e eu gosto de você. Agora mate o Khan dos Gelkis! O nome dele é Khan Shaka, e eu quero a cabeça de Shaka! Eu quero olhar para as fuças dele e rir!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1380] = {title = "Khan Hratha", objective = "Mantenha alta sua reputação junto aos Gelkis e entregue o Fragmento de Chave dos Maraudinos para Uthek, a Sábia, na Aldeia Gelkis.", description = "<name>, você nos ajudou contra os Magram. Agora, nos ajude contra os Maraudinos! Mate o líder deles, o Khan Hratha!\n\nPara lutar com Hratha, toque a Corneta de Guerra Mauradina, que está no alto de um penhasco do Vale das Lanças.\n\nMas, para tocar a corneta, você vai precisar de uma boquilha, objetos raros que apenas Maraudinos de confiança carregam.\n\nCom o toque da corneta, os centauros aparecerão. Mate um a um até que Hratha apareça.\n\nKhan Hratha carrega um pedaço de... chave. Traga-o para mim...", progress = "O Khan Hratha está morto? Você conseguiu pegar o pedaço de chave?", completion = "Morreu! Khan Hratha morreu! E os Maraudinos cairão! Quando os Gelkis dominarem estas terras, &lt;name&gt;, você será lembrado. Os espíritos da terra cantarão seu nome para sempre.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCorrente do Espoliador GelkisDedo de Uthek\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\nBoquilha da Corneta de Guerra"},
[1381] = {title = "Khan Hratha", objective = "Mantenha alta sua reputação junto aos Magram e entregue o Fragmento de Chave dos Maraudinos para Warug na Aldeia Magram, em Desolação.", description = "O Khan dos Gelkis morreu, e os Maraudinos vão ser derrotados! Mate o khan dos Maraudinos, o Hratha!\n\nPara lutar com ele, toque a corneta no alto do penhasco no Vale das Lanças. Mas, para tocar a corneta, você precisa de uma boquilha. E para arranjar uma boquilha, mate um centauro Maraudino. Vai matando um a um até achar uma boquilha. E então toque a corneta.\n\nFaça isso para que os centauros Maraudinos venham para a torre. Vai matando eles até o Khan Hratha aparecer. O Hratha tem um pedaço de chave. Traga isso para mim...Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nCinto do Caçador MagramManta Cerimonial do Centauro\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\nBoquilha da Corneta de Guerra"},
[1382] = {title = "Aliança inusitada", objective = "Ganhe a reputação de Respeitado pelos Gelkis e depois fale com Uthek, a Sábia.", description = "Para nossa sorte, os clãs de centauros vivem em conflito, porque se eles se unissem numa frente única, seriam implacáveis. Sim, temos sorte e devemos tirar proveito disso.\n\nUm dos clãs dos centauros, o clã Gelkis, está travando uma guerra cruel contra o clã Magram. Se atacarmos os Magram, talvez possamos forjar uma aliança com os Gelkis.\n\nFale com Uthek, a Sábia, nos arredores dos acampamentos Gelkis, na ponta sudoeste da Desolação. Através dela, talvez possamos forjar uma aliança com os Gelkis.", progress = nil, completion = "Os Gelkis sussurram seu nome. Suas mãos estão sujas de sangue Magram. Excelente.\n\nTalvez devêssemos conversar. Discutir o que você pode fazer pelos Gelkis e o que podemos oferecer em troca.", rewards = "Você receberá:\n\n"},
[1383] = {title = "A mais pura verdade", objective = "Boticário Faustino no Refúgio dos Mendigos precisa de 5 Corações de Pantera das Sombras, Fungo do Senhor do Brejo e um Tumor Passofundo.", description = "Sei exatamente qual soro usar. Com ele, a verdade será revelada como deve ser revelada.\n\nPara tal preparado, precisarei de vários corações de panteras das sombras do Pântano. Também preciso do fungo encantado do Senhor do Brejo que habita a região. Sei que alguém capaz como você, <name>, saberá persuadi-lo a ceder alguns fungos.\n\nAgora, o difícil será localizar um tumor Passofundo nos confins da Desolação. Dificilmente os gigantes por lá adoecem e desenvolver tumores.\n\nAgora, pode ir!Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nManto EmpesteadoMalho de Madeira Densa\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[1384] = {title = "Onde os fracos não têm vez", objective = "Mantenha alta sua reputação junto aos Gelkis e entregue 10 Patuás Grosseiros para Uthek, a Sábia, na Aldeia Gelkis, em Desolação.", description = "Os centauros Kolkar acham que a magia deles é forte. Bah, eles não sabem nada de magia! Nem se engolissem uma varinha mágica eles iam saber!\n\nVocê vai mostrar para eles como aquela magia é fraca. Vá até a aldeia dos Kolkar e mate todo mundo. Pegue os patuás deles e traga para mim. Vou queimar tudo na fogueira e rir!", progress = "Você pegou os patuás? Mostrou para os Kolkar que eles são uns fracos?", completion = "Você fez um grande favor para os Gelkis. Os Kolkar são fracos e vão morrer todos. Aquela magia patética não vai salvar ninguém. Você deixou isso muito claro, e o medo vai deixar os Kolkar ainda mais fracos!\n\nE, por estas bandas, os fracos não têm vez.", rewards = "Você receberá:\n\n"},
[1385] = {title = "Política feroz", objective = "Ganhe reputação de Respeitado pelos Magram e depois fale com Warug.", description = "A Desolação é uma região conturbada. Os centauros travam guerras entre si e acreditamos que os vencedores irão se espalhar e ameaçar nossos baluartes. Não podemos permitir isso.\n\nTemos que descobrir um jeito de derrotar os centauros e, para isso, precisaremos nos aliar a um dos clãs. Os centauros Magram guerreiam contra os Gelkis. Se você matar Gelkis, os Magram podem cogitar uma aliança.\n\nConquiste o respeito dos Magram e depois procure Warug na Aldeia Magram, no sudeste da Desolação.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1386] = {title = "Ataque aos Kolkar!", objective = "Mantenha alta sua reputação junto aos Magram. Mate 12 Centauros Kolkar, 12 Batedores Kolkar e 6 Espancadores Kolkar. Em seguida, fale novamente com Warug, na Vila Magram, em Desolação.", description = "Os Kolkar acham que são fortes, mas a gente ri deles! A gente mata eles rindo! Você também mata eles rindo. Prove para mim que consegue matar os Kolkar como a gente. Vá para norte e acabe com todos!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1387] = {title = "Suvenir de centauro", objective = "Entregue 15 Orelhas de Centauro para Cabo Melquias no Posto do Nijel em Desolação.", description = "A raça dos centauros é composta por muitos clãs. Cada qual com líder e objetivos próprios, mas todos com uma coisa em comum: crueldade.\n\nSe é paz que buscamos, os centauros precisam ser eliminados. Então, encarrego você de resolver isso.\n\nAo atravessar a Desolação, cace centauros e remova suas orelhas. Traga muitas, muitas orelhas para mim.\n\nFaça isso em prol da Aliança, <name>, e daremos mais um passo rumo ao fim da guerra.", progress = "Algo a relatar, <name>?", completion = "Ah, as orelhas. Muito bem.\n\nVocê prestou um serviço inquestionável para a Aliança, &lt;name&gt;. O sangue derramado na Desolação salvará, sem dúvida, incontáveis vidas.", rewards = "Você receberá:\n\nAnel da Calma"},
[1388] = {title = "A mais pura verdade", objective = "Entregue o Soro da Verdade do Faustino para Sicário Zaqueu no Refúgio dos Mendigos.", description = "O plano está progredindo como planejado, <name>. Quero que você entregue este soro da verdade ao Sicário Zaqueu para que ele demonstre como aplicá-lo... adequadamente.\n\nHaha!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1389] = {title = "Cristais de draenetista", objective = "Leve 6 Cristais de Draenetista para Magtoor no Pântano das Mágoas.", description = "Os perdidos que vivem no Retiro Alqueivado se parecem conosco, mas são monstros... Eles enlouqueceram pelos rigores de nossa jornada a Azeroth. Eram nossos irmãos, mas agora residem a leste, aprisionando ou matando todos que se aventuram por lá.\n\nE eles roubaram algo sagrado para nós – cristais de draenetista.\n\nNão somos fortes o suficiente para invadirmos o Retiro Alqueivado e tomar de volta a draenetista roubada das suas cabanas e fogueiras, mas, sem esses cristais... nós também poderemos nos perder de nosso caminho.", progress = "Trouxe os cristais?", completion = "Obrigado, &lt;name&gt;. Nós estamos em dívida com você.", rewards = "Você receberá:\n\n"},
[1391] = {title = "A mais pura verdade", objective = "Leve a Cerveja de Zaerdus para Infiltrador Marksen no Pedregal.", description = "Precisamos exercer um módico de discrição. Afinal, não é como se você pudesse entrar lá sem mais nem menos e convencer o tolo a ingerir o preparado do Faustino, não é?\n\nMas, se tem uma coisa que os humanos não negam é a birita. Vou misturar o soro do Faustino nessa cerveja aqui. Aposto que o pobre coitado não vai recusar uma bebida por conta da casa.\n\nVá depressa para Pedregal, no Pântano das Mágoas, <name>. E não comente com ninguém sobre essa nossa missãozinha...Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1392] = {title = "Noboru, a Borduna", objective = "Leve a Clava de Noboru ao líder do Refúgio no Pântano das Mágoas.", description = "Noboru vem importunando os draeneis do Refúgio há meses, e sua clava é um símbolo da brutalidade que sofreram nas mãos dos perdidos.", progress = "Saudações. E seja bem-vindo ao Refúgio.", completion = "Você obteve a arma do detestável Noboru? Então, ele está morto?\n\nEste é um dia alegre para o Refúgio. Noboru e sua turba eram um terror para nós, espreitando os pântanos e matando qualquer draenei que cruzasse seu caminho. Ele era uma ameaça direta para nossa sobrevivência, e não tínhamos força para combatê-lo.\n\nSomos afortunados por ter você como amigo, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[1393] = {title = "A fuga de Galen", objective = "Escolte Galen até a segurança para, então, encontrar seu acampamento no Pântano das Mágoas e abrir seu cofre.", description = "Por favor, ajude-me! Essas criaturas vão me comer!\n\nAjude-me aqui e darei a você um grande tesouro! Está no meu acampamento no pântano ao norte do Pedregal, mas está trancado em um cofre que pode ser aberto por um truque que apenas eu conheço.\n\nMas, se você me ajudar a escapar, eu o ensinarei a você.", progress = nil, completion = "O buraco da fechadura deste cofre foi preenchido com resina preta. É impossível abrir.\n\nMas Galen falou de um trinco escondido no fundo da caixa...\n\n...Após uma breve busca, o trinco é encontrado. O trinco gira e o cofre se abre...", rewards = "Você receberá:\n\nBroquel do Visionário"},
[1394] = {title = "Passagem final", objective = "Fale com Dorn Espreitaprados em Mil Agulhas.", description = "Você se saiu bem, <name>. Passou no meu teste e nos testes anteriores ao meu. Fale novamente com Dorn Espreitaprados em Mil Agulhas. Ele perceberá a mudança em você, verá que sua mente, corpo e espírito têm a força necessária e que você merece uma recompensa pelos seus esforços.\n\nNão se afaste do equilíbrio demonstrado nessas provações, <name>. Dorn vai testar você novamente caso isso aconteça.", progress = nil, completion = "Ah, &lt;name&gt;. Já faz tempo que você não visita minha casa. Parece que muitas luas se passaram desde o seu primeiro passo por este caminho.\n\nE agora você voltou mais forte do que nunca... com mais sabedoria. Isso me alegra. E você merece uma recompensa à altura por ter passado nos testes.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nMartelo VentempestaChama Dançante\n\nVocê também receberá:\n\n"},
[1395] = {title = "Suprimentos para Etergarde", objective = "Leve o Caixote de Pedras de Poder para Intendente Lungertz antes que expirem.", description = "Esse caixote de pedras de poder precisa seguir para a Bastilha de Etergarde, na Barreira do Inferno. A energia não vai durar muito tempo, então não podemos esperar a próxima caravana.\n\nVocê precisa levá-las e rápido.\n\nEntregue-as ao Intendente Lungertz na Bastilha de Etergarde. Para chegar lá, siga para o leste pela Trilha do Vento Morto, depois continue para o leste pelo Pântano das Mágoas, daí sul pela Barreira do Inferno.\n\nTenha cautela, <name>. São regiões perigosas, mas você não pode fracassar.", progress = "Olá! Não ligue para esse tumulto, é de praxe na Bastilha de Etergarde.\n\nTrouxe alguma coisa pra mim?", completion = "Ah, as pedras de poder! Os magos ficarão contentes de ver isso!\n\nMeus agradecimentos, &lt;name&gt;. Sei que sua a jornada desde a Vila Sombria não foi fácil.", rewards = "Você receberá:\n\n"},
[1396] = {title = "Invasão da vida selvagem", objective = "Mate 8 Crocoliscos Dentesserras Jovens, 10 Tece-mágoas e 10 Jaguares do Pântano, então fale novamente com Vigia Biggs no Pântano das Mágoas.", description = "Minha caravana se dirigia para a costa oriental, mas não chegamos ao destino. Os Perdidos nos atacaram, matando guardas e furtando suprimentos.\n\nSó eu sobrevivi ao ataque. Jurei proteger os suprimentos restantes, mas criaturas selvagem espreitam por perto. Por favor, me ajude a proteger meu acampamento dessas feras!", progress = nil, completion = "Os sons dos animais soam cada vez mais distantes. Obrigado, &lt;name&gt;. Vamos torcer para que não retornem!", rewards = "Você receberá:\n\n"},
[1398] = {title = "Madeira Flutuante", objective = "Leve 8 peças de Madeira Flutuante Seca ao Sol para Vigia Biggs no Pântano das Mágoas.", description = "Devo muito a você, <name>. Um coração honroso pulsa no seu peito. Porém, tenho outra tarefa para a qual devo humildemente pedir sua ajuda.\n\nSe espero um dia chegar a Etergarde, preciso consertar essa carroça. E para isso... preciso de madeira. Madeira robusta e seca, nada daquelas madeiras retorcidas e esponjosas das árvores pantanosas.\n\n<name>, siga para leste até a costa e procure por madeira flutuante que secou ao sol. Traga-as para mim.", progress = "Encontrou alguma madeira flutuante, <name>?", completion = "Essa madeira é leve, mas forte. Obrigado, &lt;name&gt;. Isso vai servir.", rewards = "Você receberá:\n\n"},
[1418] = {title = "Nika Cicatriz-de-sangue", objective = "Fale com Nika Cicatriz-de-sangue.", description = "Os Ermos são cruéis e estão situados perto do território da Aliança, Loch Modan, portanto é normal que a comunicação entre Pedregal e o posto avançado de Karrath nos Ermos seja interrompida.\n\nMostre seu valor, <name>. Dirija-se aos Ermos e fale com Nika Cicatriz-de-sangue, batedora líder do posto avançado de Karrath. Fale com ela e retorne com seu relatório.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1419] = {title = "Coiotes ladrões", objective = "Leve 30 Mandíbulas de Coiote para Nika Cicatriz-de-sangue em Karrath.", description = "Você passou vários dias nos Ermos, <class>? Sentiu o calorão? Enfrentou as feras que vagam naquelas planícies poeirentas? Já estava na hora.\n\nOs coiotes do rochedo são uma raça teimosa. Resistentes e ousados o bastante para invadir Karrath e roubar suprimentos de comida. Ajude a livrar os postos avançados desses ladrões!\n\nCace coiotes do rochedo, <name>. Cace-os e traga as mandíbulas como prova. Traga muitas e talvez eu acredite quando você disser que conhece os Ermos.", progress = "Trouxe as mandíbulas?", completion = "Bela coleção de mandíbulas, &lt;name&gt;. Agora esses coiotes não vão mais atacar nossos suprimentos atrás de restos.\n\nMuito bem. Os coiotes dos Ermos são presas difíceis. Seu feito mostra que você pode muito bem sobreviver por aqui.", rewards = "Você receberá:\n\n"},
[1420] = {title = "Relatório para Helgrum", objective = "Leve o Relatório de Nika para Helgrum, o Ligeiro em Pedregal.", description = "Escrevi um relatório novo para ser entregue a Helgrum no Pedregal. Leve-o para ele.\n\nE se você se considera mesmo <um corredor/uma corredora>, não se perca, nem seja <capturado/capturada>.", progress = "Você voltou. Algo a relatar?", completion = "Obrigado, &lt;name&gt;. Pelo relatório da Nika, vejo que a Aliança também tem seus problemas. Inclusive parece que compartilhamos alguns inimigos.", rewards = "Você receberá:\n\n"},
[1421] = {title = "A caravana perdida", objective = "Encontre o Baú da Caravana no Retiro Alqueivado, obtenha os Reagentes do Teurgo e leve-os para Vigia Biggs.", description = "Fiquei aturdido durante o ataque dos Perdidos, mas, antes de perder os sentidos, notei uma das carroças sendo levada para o Retiro Alqueivado, a aldeia dos perdidos ao norte.\n\nHavia um baú da caravana cheio de reagentes valiosos na carroça, reagentes imprescindíveis nos estudos dos teurgos da Bastilha de Etergarde. Encontre o baú e recupere os reagentes, <name>. Pegue-os e traga-os para mim.", progress = "Trouxe os reagentes do teurgo, <name>?", completion = "Ah, você os encontrou! Com isso, os teurgos de Etergarde podem continuar seus trabalhos. Não sei o que eles fazem, mas sei que tem a ver com o Portal Negro na Barreira do Inferno, e que é crucial para a segurança da Aliança.", rewards = "Você receberá:\n\n"},
[1422] = {title = "Ameaça do mar", objective = "Fale com Katar no Pântano das Mágoas.", description = "Enquanto começo a trabalhar na sua arma, por que não fala com Katar? Ele mencionou alguma coisa sobre precisar de ajuda nas patrulhas da praia. Prefiro que você se ocupe e mantenha suas armas em riste do que acabe enchendo a cara com esse bando inúteis aqui do acampamento.", progress = nil, completion = "Faça silêncio por um instante, &lt;class&gt;. Mesmo com o rugido surdo do oceano, ainda se pode ouvir aqueles gorgolejos nojentos do povo-peixe.", rewards = nil},
[1423] = {title = "Os suprimentos perdidos", objective = "Leve os Suprimentos Perdidos para Intendente Lungertz.", description = "Esse barril é pequeno, mas resistente. O vão entre os aros foi selado com uma cera amarelada e pungente. No fundo do barril foram gravadas as palavras a seguir:\n\nINTENDENTE LUNGERTZ\n\nBASTILHA DE ETERGARDE.", progress = "Você tem algo para mim?", completion = "Ah, muito obrigado, &lt;name&gt;! Tantos suprimentos desaparecem no caminho entre a Vila Sombria e aqui. É bom ver que alguns podem ser recuperados.", rewards = "Você receberá:\n\nCaixa de Suprimentos"},
[1424] = {title = "Poço de Lágrimas", objective = "Vil'zerul, no Pedregal, quer que você colete 10 Artefatos Atal'ai.", description = "O Chefe Guerreiro mandou a gente investigar os acontecimentos estranhos que ocorreram no Templo de Atal'Hakkar.\n\nOs trolls Atal'ai são uma tribo de lançadores tenebrosos, notórios pelo uso de magia proibida. Dá pra imaginar como ficamos espantados quando chegamos aqui e o templo tinha afundado.\n\nAgora, dragões verdes protegem o local, e se dizem protetores de Azeroth.\n\nPrecisamos descobrir o que aconteceu. Explore em volta do Poço de Lágrimas e colete artefatos Atal'ai. Com eles, posso começar a decifrar esse quebra-cabeça.", progress = "Ninguém sabe que práticas malignas ocorriam no Templo de Atal'Hakkar, mas sabemos que forças mágicas poderosas e incríveis atuavam ali.\n\nPrecisamos aprender o que pudermos sobre essa magia. Talvez os feitiços dos Atal'ai sejam úteis para a Horda.\n\nQuando você tiver coletado artefatos Atal'ai suficientes no Poço de Lágrimas, vou poder entender o que os trolls tentaram fazer e o que deu tão errado.", completion = "Sua diligência na coleta de artefatos Atal'ai é digna de louvor, &lt;class&gt;.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[1425] = {title = "Entregue a remessa", objective = "Leve a Remessa para Etergarde ao Intendente Lungertz na Bastilha de Etergarde.", description = "Enquanto conserto a carroça, <name>, tenho um última tarefa para você.\n\nEmpacotei os suprimentos mais essenciais que salvei dos ataques dos ogros. Leve-os para a Bastilha de Etergarde. A bastilha está aninhada nas colinas setentrionais dos Ermos, vigiando o Portal Negro.\n\nPara chegar a Etergarde, siga a estrada para o leste. Na primeira bifurcação, vire para o sul, rumo aos Ermos, depois vire à esquerda na bifurcação seguinte. Siga a estrada, <name>, e tome cuidado.", progress = "Tem negócios a tratar comigo?", completion = "O quê? Um pacote da caravana do Biggs? Pensamos que ela, e ele, estivessem perdidos.\n\nObrigado, &lt;name&gt;. Com sorte, esses suprimentos contêm um reagente crucial, cristais de Azoran. Nosso estoque está baixo...", rewards = "Você receberá:\n\n"},
[1426] = {title = "Ameaça do mar", objective = "Mate 10 Murlocs do Pântano, 10 Jogatintas do Pântano e 10 Comecarnes do Pântano. Depois, fale novamente com Katar, no Pântano das Mágoas.", description = "Não morro de amores pelo mar, nem por criaturas que vêm das suas profundezas. Ele é poderoso e cego, impiedoso e brutal. Seria um prazer enorme aniquilar centenas de homens-peixe se eu tivesse tal poder, mas me contento com suas mortes pelas mãos de qualquer aliado da Horda.\n\nE isso inclui você, <class>. Quantos você conseguiria matar? Você arriscaria enfrentar o mais poderosos deles?", progress = nil, completion = "O mar parece vasto e impiedoso, mas você o manchou com o sangue de suas crias. Os homens-peixe conheceram o sofrimento. A estupidez deles fará com que recuem e reforcem suas fileiras por causa da devastação que você causou, ótimo.\n\nQue os gorgolejos sofridos deles abafem o rugido do mar ao longo da noite.\n\nFale novamente comigo se quiser matar mais homens-peixe. Recompensarei você novamente com todo o prazer.", rewards = "Você receberá:\n\n"},
[1427] = {title = "Ameaça do mar", objective = "Fale com Tok'Kar no Pântano das Mágoas.", description = "Antes de parar para descansar, fale com Tok'Kar de novo. Acho que enquanto você esteve fora ele terminou algumas armas. Pela ajuda que você deu aqui, ele pode lhe dar uma delas. Seria uma boa você testar a força dela contra os homens-peixe quando estiver pront<o/a>... mas só para me agradar mais.", progress = nil, completion = "Ah, &lt;name&gt;, você voltou! Que bom. Terminei de fazer mais armas para a sua luta contra os murlocs. Por nos trazer a carne fresca de dentesserra e fazer Katar sorrir um pouco, eu lhe ofereço uma delas.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nPerfurador de Murloc de Tok'karEsmagador de Murloc de Tok'karCortador de Murloc do Tok'kar\n\nVocê também receberá:\n\n"},
[1428] = {title = "Ameaça contínua", objective = "Mate 10 Jogatintas do Pântano, 10 Comecarnes do Pântano e 10 Oráculos do Pântano. Depois, fale novamente com Katar, no Pântano das Mágoas.", description = "É um prazer ver que seu ódio pelos homens-peixe cresce junto com o meu.\n\nVá e encontre mais deles ao longo da praia e, quando tiver se cansado, volte e fale novamente comigo. Poderemos combinar uma recompensa pela sua diligência.", progress = nil, completion = "Bom trabalho, &lt;class&gt;. Eu quero o sangue deles. Morte a todos os homens-peixe.", rewards = "Você receberá:\n\n"},
[1429] = {title = "O exilado Atal'ai", objective = "Leve o Saco de Artefatos Atal'ai para o Exilado Atal'ai nas Terras Agrestes.", description = "Enquanto você enfrentava os perigos do Poço de Lágrimas, um dos meus batedores voltou com um pergaminho escrito com sangue em carne seca. Parece uma sentença judicial contra um sacerdote da tribo Atal'ai.\n\nEle foi condenado a um destino considerado pior que a morte entre os Atal'ai.\n\nEle foi exilado, indo viver longe, ao norte, nas Terras Agrestes com os Trolls Cascasseca.\n\nVou empacotar esses artefatos. Você vai em busca do exilado e descobre a utilidade real eles. Volte quando completar sua jornada.", progress = "Você tem coragem para vir aqui. Coragem ou estupidez...", completion = "Você me traz os artefatos de evocação dos Atal'ai?\n\nVejo que Jammal'an, o Profeta, está levando discípulos da Câmara de Sangue ao destino que eles escolheram. Me pergunto se Hakkar voltou para o templo. Será que eles seguiram esse destino?", rewards = "Você receberá:\n\n"},
[1430] = {title = "Carne fresca", objective = "Leve 10 Pernas de Rastejante Monstruoso para Dar no Pântano das Mágoas.", description = "A demanda pelo sabor suculento da carne de rastejante anda alta entre os meus clientes, em especial a carne dos rastejantes monstruosos.\n\nSe você estiver pensando em ir para o litoral, para mim é fácil fazer valer a pena para você me trazer umas pernas para eu mandar levar para Orgrimmar.\n\nO litoral fica a leste daqui, e você não deve ter problemas para achar as criaturas perambulando pela praia e pelo fundo, no mar.", progress = "Tenho um barrilete de vinho esperando pelo seu sucesso, <name>. É apenas correto eu provar o produto antes de enviar para os meus clientes.", completion = "Ah, parece perfeito. Mal posso esperar para provar isso aí assado no espeto. Você devia levar uma porção para a viagem. É delicioso, de verdade.", rewards = "Você receberá:\n\nPatas Grelhadas de Caranguejo-reiCinto de Couro do Chef"},
[1431] = {title = "Relações da Aliança", objective = "Fale com Keldran em Orgrimmar.", description = "Fique <quieto/quieta> um pouco, <class>, e me permita compartilhar com você algo importante para nosso povo.\n\nSomos uma raça orgulhosa, e a guerra contra os humanos testou até mesmo a grande paciência de Thrall. Nosso Chefe Guerreiro é sábio e prudente, e ele continua a colocar os interesses do povo em primeiro lugar. No final, ele é um diplomata, e o tempo que ele passou ao lado dos humanos lhe ensinou muito a respeito dessa raça.\n\nSe essa história interessou você, procure Keldran perto da saída ocidental da cidade. É lá que ele mora.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1432] = {title = "Relações da Aliança", objective = "Fale com Takta Laminaço em Desolação.", description = "À medida que a Aliança progride cada vez mais para dentro de Kalimdor, conclusões errôneas podem ser inferidas quanto à Horda ter feito pactos com a Legião Ardente. O Chefe Guerreiro não pode permitir que isso aconteça. Com a bênção dele, estou tomando o controle da operação de remoção dos intrusos.\n\nTenho agentes perto de Desolação, no Entreposto do Espírito que Anda, que fica ao norte do grande Cemitério dos Kodos. Siga na direção sul a partir do Vale Carbonizado, na Cordilheira das Torres de Pedra, e você chegará lá. Ao chegar, procure Takta Laminaço.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1433] = {title = "Relações da Aliança", objective = "Fale com Maurin Rachaosso em Desolação.", description = "A Lâmina Ardente tem muitos planos, mas saiba que eles são maus até a raiz da alma, seus espíritos são corruptos e sua destruição é a única maneira de impedir a realização de seus objetivos.\n\nKeldran enviou Maurin até mim por causa dos seus conhecimentos a respeito da Lâmina e de seus bruxos.\n\nEle está supervisionando nossos planos contra a Lâmina enquanto eu tomo conta das outras ameaças demoníacas aqui em Desolação.\n\nFale com ele, e ele lhe dará mais instruções.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1434] = {title = "Mácula dos sátiros", objective = "Mate 7 Errantes Furiódios, 7 Devotos Vis Furiódios, 7 Traidores Furiódios e 7 Arautos Infernais Furiódios. Depois fale com Takta Laminaço em Desolação.", description = "Uma infestação demoníaca não ocorre sem outras influências. Sempre há alguma coisa que permite que o mal se estabeleça.\n\nPode ser um espírito enfraquecido, um desejo por poder... talvez seja sede de vingança, ou vontade de obter justiça. Quando somos afligidos pela dor, o mal se aproveita de nós. Um mal como o dos sátiros.\n\nEles criaram laços com a Lâmina Ardente e Thrall quer vê-los mortos. Você irá encontrá-los a nordeste daqui. Só volte a falar comigo quando os tiver expulsado.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1435] = {title = "A queima dos espíritos", objective = "Devolva a Gema Ardente e leve 15 Gemas Ardentes Infusas para Maurin Rachaosso na Desolação.", description = "A morte é deveras desejável para as criaturas desprezíveis que dão as costas ao próprio senhor por motivos tão egoístas. Mas a morte não é suficiente para os que se entregaram às trevas.\n\nLeve esta gema. Quando alguém da Lâmina Ardente estiver às portas da morte, ative a magia. O feitiço puxará o espírito corrompido para fora da vítima, deixando para trás uma gema infusa.\n\nTraga as gemas infusas para que eu possa descartá-las adequadamente.\n\nVá, e que você possa enganar a morte.", progress = "Tentei manter as mágicas o mais simples possível. Extrair o espírito da vítima da forma física ferida deve ser brincadeira de criança. Não conte com o poder da gema para matar completamente o oponente. Você precisa ativar a gema quando ele estiver perto da morte, ou ela será inútil.", completion = "Ah, você voltou vitorioso. Isso é bom. Entregue as gemas para eu colocá-las em um lugar seguro...\n\nMinhas tarefas para você terminaram, &lt;class&gt;. Fale com Takta depois de descansar. Ele pode ter outras tarefas para você, ou talvez precise que você volte a Orgrimmar para relatar a Keldran seu progresso aqui na Desolação.", rewards = "Você receberá:\n\nFlayed Demon Skin (old2)"},
[1436] = {title = "Relações da Aliança", objective = "Fale com Keldran em Orgrimmar.", description = "A luta vai bem, <name>. Talvez o Chefe Guerreiro nos permita retornar para nossos lares antes da próxima estação se continuarmos a fazer progressos em nossa batalha contra os demônios e seus servos.\n\nFale novamente com Keldran e informe-o do que fizemos aqui. Tenho certeza de que ele <o/a> recompensará pelos seus esforços. A causa que ele defende lhe é muito cara, e ele terá orgulho em saber que o Chefe Guerreiro respeita suas vontades.", progress = nil, completion = "Você foi mais que bem-sucedido em Desolação, &lt;name&gt;. Obrigado.\n\nObrigado pelos seus esforços, seu tempo e sua compreensão. Thrall compreende e também deseja a paz entre a Horda e a Aliança. Ao tomar medidas para garantir que nossa aparência seja aceitável, não provocaremos mais ameaças de violência desnecessária.\n\nEstou endividado com você.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nLuvas de KapelanCapa do Correleve\n\nVocê também receberá:\n\n"},
[1437] = {title = "A busca de Vahlarriel", objective = "Procure a carroça de Tyranis e Dalinda Malem na Desolação.", description = "Eu sou Vahlarriel, <name>. Vim aqui descobrir o paradeiro de dois nobres desaparecidos de uma família darnassiana de mercadores, a Casa Malem. O patriarca da casa receia que tenha acontecido o pior, e eu preciso fazer tudo que puder para encontrar os filhos dele. Ainda não comecei minha busca por Tyranis ou Dalinda, mas espero não passar muito tempo aqui.\n\nAs últimas notícias foram de que eles passaram pela Cordilheira das Torres de Pedra semanas atrás.\n\nSe acontecer de você encontrar a carroça deles, fale comigo imediatamente.", progress = nil, completion = "Conforme você vasculha as carroças, logo fica claro que elas foram atacadas com armas e feitiços. A batalha parece ter sido rápida e nem um pouco equilibrada. Mas não há sangue, nem sinal de qualquer cadáver.\n\nQuem quer que tenha atacado, foi rápido e eficiente.", rewards = "Você receberá:\n\n"},
[1438] = {title = "A busca de Vahlarriel", objective = "Encontre Dalinda Malem na Desolação.", description = "Encontre Dalinda e Tyranis se puder, e me conte o que descobrir. Comece a procurar nessa fortaleza. Vou entrar em contato com meus superiores em Darnassus imediatamente e informá-los da situação.\n\n<name>, há mais uma coisa que eu quero que você saiba. Meus superiores demonstraram certa preocupação com Tyranis e os motivos dele para vir aqui. O mais importante para eles é localizar Dalinda e levá-la de volta em segurança. Tyranis deve ser... avaliado antes de voltar para casa.", progress = nil, completion = "Graças a Eluna alguém veio ajudar. Por favor, &lt;class&gt;, eu preciso da sua ajuda. Eu e o meu irmão Tyranis fomos capturados e trazidos para cá pela Lâmina Ardente. Acho que eles querem me sacrificar aos mestres demoníacos deles!\n\nEu sabia que vir à Desolação era um erro, mas ultimamente Tyranis ficou obcecado por conhecimentos arcanos de todo tipo, mesmo contrariando a vontade de nosso povo. Ele resolveu viajar quando soube das histórias sobre a infestação demoníaca nessas terras.\n\nTenho medo que tenha acontecido o pior.", rewards = "Você receberá:\n\n"},
[1439] = {title = "A busca por Tyranis", objective = "Encontre Tyranis Malem na Desolação.", description = "Fomos trazidos para cá depois que os orcs mataram todos os nossos guardas e servos. O que eles fizeram foi horrível! Tive certeza de que eles nos matariam lá mesmo, mas logo ficou óbvio que eles tinham outros planos.\n\nTyranis tentou falar com eles. Acho que ele tentou impressioná-los com o próprio conhecimento arcano... Não sei bem por quê.\n\nPor favor. Não posso ir antes de saber o que aconteceu com meu irmão. Encontre-o e venha ter comigo quando souber o destino dele.", progress = "Tenho medo de que Tyranis tente aprender os segredos da Lâmina Ardente e se juntar a eles, se lhe for permitido. Ele sempre adorou o poder que a magia concede aos seduzidos por ela.\n\nNão sei se consigo aguentar voltar para casa com a notícia de que o espírito de Tyranis está flertando com tais perigos. Sair em busca desse tipo de coisa já é crime suficiente para o meu povo, mas se tornar aquilo de que tentamos nos proteger, bem, é compreensível.", completion = "Isso é terrível! Mas, pelo visto, era necessário. Se não fosse pelas suas mãos, certamente teria sido pelas do meu povo. Acho que eu lhe devo agradecimentos. Peço perdão se não soa sincero. Eu não tenho intenção nenhuma de ofender.\n\nSabendo do destino do meu irmão, parece que é hora de fugir. Se você ainda estiver disposto, eu aceito sua ajuda para fugir deste lugar horrível.\n\nQuando você quiser, podemos ir para um lugar seguro.", rewards = "Você receberá:\n\n"},
[1440] = {title = "O retorno a Vahlarriel", objective = "Escolte Dalinda Malem a partir da Fortaleza Machado do Trovão e fale novamente com Vahlarriel Matademônios na Desolação.", description = "Então chegou a hora? Obrigada pelo seu esforço, e se a situação não permitir, deixe-me agradecer por tudo que você fez por mim e pela minha família. Se você estiver pront<o/a>, aqui não há mais nada para mim. Podemos partir quando você achar oportuno.", progress = nil, completion = "Obrigado, &lt;name&gt;. Dalinda voltou em segurança aqui para o Posto do Nijel, e em condição muito melhor do que eu poderia pedir. O fato de ela ter chegado por conta própria foi um bom sinal. Espero que você esteja bem.\n\nDói saber que um dos dois não voltou, mas cabe a ela aguentar a vergonha das decisões de Tyranis. É algo com que ela terá de lidar quando voltar a Darnassus.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCinto do LaçadorLuvas da PerspicáciaManto da GuarniçãoSobreveste Luz do Luar\n\nVocê também receberá:\n\n"},
[1442] = {title = "Em busca da Gema Kor", objective = "Leve a Gema Kor para Trovejius Tecevento, na Costa Negra.", description = "Saudações, <name>. Como posso ajudar alguém que trilha o caminho sagrado?\n\nAh, sim, Calmafonte, o ferreiro... quanto tempo. Eu me lembro bem dele, mas faz muito tempo que não escuto esse nome.\n\nEle nunca me pareceu um homem afeito a brincadeiras, então creio que o pedido dele de uma gema Kor seja justo.\n\nSe você realmente quer encontrar uma das gemas, vá para a região noroeste do Vale Gris. Lá, por entre as ruínas ancestrais, há um lugar chamado Profundezas Negras, lar de nagas que ainda usam as gemas. Não vá <sozinho/sozinha>, <class>.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nGema de Kor Purificada"},
[1444] = {title = "Retorno a Vil'zerul", objective = "Fale novamente com Vil'Zerul em Pedregal.", description = "Jammal'an, o Profeta, me sentenciou ao exílio do Templo de Atal'Hakkar. Houve um tempo em que eu achava o sumo-sacerdote um líder digno.\n\nA fé inabalável dele na própria profecia trará a destruição dos Atal'ai, se é que já não trouxe.\n\nQuando eu expressei minha preocupação, fui taxado de infiel e expulso do templo.\n\nOs artefatos que você me trouxe são inofensivos. Mas se Jammal'an tiver encontrado o encantamento de fetiche adequado para o ritual de evocação, o mundo estará em grande perigo...", progress = nil, completion = "Já se vai um bom tempo desde a última vez em que nos falamos, &lt;class&gt;. Espero que sua jornada tenha sido bem-sucedida\n\nO que você conseguiu descobrir com o Atal'ai exilado nas Terras Agrestes?", rewards = "Você receberá:\n\n"},
[1445] = {title = "O templo de Atal'Hakkar", objective = "Obtenha 20 Fetiches de Hakkar e leve-os para Vil'Zerul em Pedregal.", description = "Então ele falou da profecia de Jammal'an?\n\nO líder espiritual dos Atal'ai acredita que, quando Hakkar voltar do Éter para Azeroth, ele concederá a imortalidade à tribo Atal'ai.\n\nUm truque estúpido para antecipar o fim do mundo, se me permite dizer.\n\nMas você disse que o exilado falou de fetiches encantados. Isso me preocupa muito. Se eles forem mesmo a chave para o ritual atal'ai que provocou esse caos, precisamos compreender os poderes intrínsecos deles.\n\nEntre no templo e confisque aqueles fetiches!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nTalismã do Guardião"},
[1446] = {title = "Jammal'an, o Profeta", objective = "O Exilado Atal'ai nas Terras Agrestes quer a Cabeça de Jammal'an.", description = "Meus companheiros Cascasseca não são fãs de gente estranha perto da casa deles.\n\nEu? Eu não tenho mais um lar. Fui exilado pela minha tribo, os grandes Atal'ai Pântano das Mágoas.\n\nNosso líder espiritual teve o que chamou de profecia. Ele acredita que a evocação do deus Hakkar dará a imortalidade aos Atal'ai.\n\nMas eu insisti para que tivéssemos cautela. E se a profecia não passasse de uma manipulação?\n\nQuero vingança pelo meu exílio. Traga-me a cabeça de Jammal'an, e então quem sabe meu povo se liberte.", progress = "Quando Jammal'an não estiver mais no poder, terei a possibilidade de voltar para o meu povo.\n\nCom Jammal'an como líder espiritual, a destruição dos Atal'ai é certa.", completion = "A confiança temerária de Jammal'an em visões falsas levou meu povo à danação eterna.\n\nEu lhe agradeço, &lt;class&gt;, por vingar meu exílio. Estou de luto por meu povo. Não tenho mais um lar para o qual voltar.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nPerneiras do Andarilho das ChuvasElmo do Exílio\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[1447] = {title = "O diplomata desaparecido", objective = "Derrote Dashel Punhopétreo em Ventobravo.", description = "É, você obviamente não é <o/a> <class> mais espert<o/a> a entrar em Ventobravo, mas vai ser u<m/a> d<os/as> mais estúpid<o/a>s a jamais sair.\n\nJá conheceu os meus amigos?", progress = nil, completion = "Então. Você veio aqui por um motivo. Do que você precisa?", rewards = "Você receberá:\n\n"},
[1448] = {title = "Em busca do templo", objective = "Brohann Barrilga, em Ventobravo, quer que você saia à procura do Templo de Atal'Hakkar.", description = "Houve um tempo em que eu era jovem e cheio de vigor, como você, <class>.\n\nMas agora eu estou velho, incapaz de explorar o mundo como antes. Ah, quanta coisa eu vi!\n\nMas um mistério me escapou. Como minha última missão, a Liga dos Exploradores me enviou em busca do Templo de Atal'Hakkar. Os rumores diziam que ele ficava no Pântano das Mágoas.\n\nPor causa do meu frágil estado, viajei pelo céu, fazendo minha busca num grifo, mas nunca o encontrei.\n\nVocê pode ajudar esse anão velho? Você pode ter mais sorte a pé.", progress = nil, completion = "O templo de Atal'Hakkar estava submerso no Poço de Lágrimas? E cercado pela revoada dragônica verde?\n\nÉ uma descoberta incrível, &lt;class&gt;. Mas... É óbvio que alguma coisa por lá deu terrivelmente errado.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[1449] = {title = "Rumo às Terras Agrestes", objective = "Fale com o Alto Thane Falstad Martelo Feroz no Ninho da Águia nas Terras Agrestes.", description = "Como eu disse antes, tentei localizar o templo pelo ar sozinho. Bom... Não exatamente sozinho.\n\nEu fui até o renomado grifiário do Ninho da Águia, nas Terras Agrestes. Lá eu consegui a ajuda de um dos melhores pilotos deles, mas o coitado caiu da montaria e sumiu no pântano...\n\nNessa minha idade avançada o nome do pobre sujeito me escapa, mas o mestre do grifiário lembraria, sem dúvida.\n\nSiga viagem até lá e procure Falstad Martelo Feroz.\n\nPrecisamos descobrir o que aconteceu com aquele piloto.", progress = nil, completion = "Mas quê que tu quer, ô vivente? Não vê que eu estou embretado aqui? Quem é que tem tempo pra pautear com uma &lt;race&gt; que fala mais que goblin em liquidação?\n\nTem troll saindo do mato! Fala e risca!", rewards = "Você receberá:\n\n"},
[1450] = {title = "Mestre de Grifos Garra-de-acha", objective = "Fale com o Mestre de Grifos Garra-de-acha no Aviário do Ninho da Águia.", description = "Bah! Mas tu é u<m/ma> <piazinho/chinoquinha> de recado dum mancarrão qualquer da Liga dos Exploradores, é?\n\nEntão escuta aqui, <class>. Eu não sei com quem o teu camarada voou por aí por Azeroth. Eu lá tenho cara de Mestre de grifos Garra-de-acha? Tenho?\n\nSe tu está tão interessad<o/a> nos voos, por que não vai lá perguntar ao mestre dos grifos? Se eu te pego vagueando mais por aqui, eu te arreglo trabalho de verdade.\n\nAgora se apura e risca.", progress = nil, completion = "Ah, sim. Claro que eu lembro de mandar um dos meus grifoneiros dar uma mãozinha ao Brohann Barrilga.\n\nSempre tive um fraco pela Liga dos Exploradores. Na verdade meu pai ajudou a equipe de escavação que descobriu Uldaman.\n\nNa ocasião mandei quem eu considerava meu melhor grifoneiro com o Brohann. Era um quera chamado Rapsódico Festanjeiro.\n\nEra um baita ginete, ele! Bem... até...", rewards = "Você receberá:\n\n"},
[1451] = {title = "Rapsódico Festanjeiro", objective = "Procure Rapsódico Festanjeiro nas Terras Agrestes.", description = "O Rapsódico começou a traguear. Não me entenda mal. Todo anão aqui no aviário adora uma boa breja na hora certa.\n\nMas o velho Festanjeiro ficava alto que nem grifo no céu. E foi uma baita pena. Ninguém voava como o Rapsódico.\n\nO primeiro sinal de problema foi quando ele voltou cambaleando daquela missão com o teu camarada Brohann. E daí pra frente a vaca foi pro brejo. Eu tive que dispensar.\n\nA última coisa que eu soube foi que ele tinha montado uma cervejaria a leste daqui...", progress = nil, completion = "Hic!\n\nEu te olho reto, mas tu fica coleando pra cima e pra baixo. Tás me azonzando. Ôôôôô!\n\nHic!\n\nCapaz! Tás ouvindo?! É o chão tremendo, vivente. Eu preciso de um trago.\n\nHic!", rewards = "Você receberá:\n\n"},
[1452] = {title = "O Coquetel de Kalimdor do Rapsódico", objective = "Rapsódico Festanjeiro, nas Terras Agrestes, quer que você consiga 3 Moelas de Roca, 3 Fígados de Groddoc e 3 Fígados de Veloférreo.", description = "Hic!\n\nCapaz que o maluco do Brohann te mandou! Foi é uma baita embretada a nossa... hic!\n\nSe tu quer que eu conte o que aconteceu lá no pântano, eu vou precisar de um trago do forte. Não tava nenhum céu de mestre grifoneiro lá, entende? Hic!\n\nQuer saber? Traz o que eu preciso pra um Coquetel Kalimdor que eu te conto da arriscada de cabo a rabo.\n\nPrecisa de umas moelas de roca de Tanaris e... deixa eu ver... fígado de groddoc e veloférreo de Feralas...\n\nUm Coquetel de Kalimdor do bom, firme... Com ele eu falo!", progress = "Já tomaste uma Malte Rapsódia? Das minhas melhores, se me permite dizer. Hic!\n\nMas se é para eu reviver o horror daquele voo fatídico... hic... o pântano... Bom, eu vou é precisar de coisa mais enquartada. Hic!\n\nTu não achou aqueles fígados e moelas para eu fazer o meu Coquetel de Kalimdor dar aquele coice, não, né?\n\nHic!", completion = "Mas tu não teve problema nenhum pra achar o que eu preciso! Deve ter dado um baita trabalho, isso, lá longe em Kalimdor.\n\nÉ um minutinho só para extrair o que eu preciso dessas moelas e desses fígados. Hic!\n\nDepois eu posso destilar uma bebida da grossa pra contar finalmente o que tu quer saber.\n\nHic!\n\nMas arrepia só de pensar naquele brejão velho.\n\nHic!\n\nDeixa eu preparar uma dose aqui, rapidinho.", rewards = "Você receberá:\n\n"},
[1453] = {title = "Trago qualquer coisa em três dias", objective = "Fale com Kreldig Ungor, em Desolação.", description = "Há muito a fazer em Desolação, <name>. É um lugar atroz, infestado de centauros e outras criaturas detestáveis. Mas, se você estiver <interessado/interessada> em uma boa semana de trabalho honesto, vá até lá, e diga a Kreldig Ungor que eu mandei você procurá-lo.\n\nKreldig costuma ficar enfiado no Posto do Nijel – um aconchegante acampamento escondido entre as montanhas do lado norte de Desolação. Para quem parte da Cordilheira das Torres de Pedra, basta seguir para norte. É muito fácil se perder no caminho, então fique alerta.", progress = nil, completion = "Fico feliz em saber que o que não falta para a Recuperação Ilimitada (Ltda) é trabalho e aventureiros dispostos a ajudar.\n\nMeu nome é Kreldig, &lt;name&gt;. Ajudar a Recuperação Ilimitada (Ltda) foi uma atitude inteligente. Hoje em dia, todo mundo está à procura de alguma coisa: uma herança de família, um artefato mágico, alguém que se ama... Nunca se sabe. Mas, com a Recuperação Ilimitada (Ltda) por perto, não há preocupação: pelo preço certo, todo mundo pode achar o que procura.", rewards = "Você receberá:\n\n"},
[1454] = {title = "O naufrágio de Karnitol", objective = "Encontre o Baú de Karnitol em Desolação.", description = "O trabalho do dia é pra um cara meio da nobreza de Ventobravo, o mago Karnitol. Parece que o barco dele foi atacado por gigantes do mar enquanto passava pela costa noroeste de Desolação. Ele e os empregados tentaram resgatar um dos baús mais valiosos, mas, quando os gigantes vieram atacando em levas, ele achou melhor abandonar o baú e se teleportar junto com os empregados pra um lugar seguro.\n\nAgora ele quer que a gente ache o tal baú com tudo que tinha dentro e leve pra ele em Ventobravo.", progress = nil, completion = "Você percebe que o baú não parece ter sido danificado pela água.\n\nLogo depois, repara que a tampa foi arrombada e que todas as armadilhas mágicas preparadas para proteger o baú foram disparadas – o que é óbvio pelas marcas de queimadura ao redor da abertura.", rewards = "Você receberá:\n\n"},
[1455] = {title = "O naufrágio de Karnitol", objective = "Fale novamente com Kreldig Ungor em Desolação.", description = "Você leva alguns minutos para descobrir o que poderia ter aberto esse baú. Até que algumas marcas de arranhão longas e profundas, nas laterais e na tampa, chamam a sua atenção: Aparentemente, algo além de humanos e gigantes se interessou por ele. As marcas não parecem ter sido causadas por espadas ou facas.\n\nAo abrir o baú, você descobre que ele está completamente vazio.\n\nSem ter mais nada a fazer, você fecha o baú e se prepara para partir. O Kreldig ficará desapontado.", progress = nil, completion = "Bem-vindo de volta ao Posto do Nijel, &lt;name&gt;. Então, o que foi que você achou?\n\nQuê!? Nada!? Droga, um baú vazio não vai ser suficente para aquele mago nos pagar.\n\nConte mais mais sobre essas marcas que você viu... esses arranhões.", rewards = "Você receberá:\n\n"},
[1456] = {title = "O naufrágio de Karnitol", objective = "Encontre a Algibeira de Karnitol e leve-a de volta para Kreldig Ungor em Desolação.", description = "Interessante.\n\nBom, pode ser que ainda haja esperança. Ao longo da costa porém mais ao norte, perto da Ilha Ranazjar, há uma ruína élfica afundada. Uma tribo de nagas, as Sibilâmina, moram lá. Eu acho que é a nossa melhor aposta.\n\nSe um grupo delas foi tão longe assim pro sul, deve ter sido poderoso o bastante pra se garantir. Se você quiser achar as coisas do Karnitol, é melhor começar pelas nagas mais poderosas: as caçamarés e as bruxas do mar.", progress = "Sem querer ofender o tal do Nijel, esse acampamento não é exatamente um lugar confortável pra passar a noite, se é que você me entende, <name>.", completion = "Isso é ótimo, &lt;name&gt;! Você achou as coisas do Karnitol!\n\nMalditas nagas, sempre causando confusão. Acho que eu preferia passar a vida toda lutando contra a Horda do que ter que lutar contra elas. Você prestou um ótimo serviço à Recuperação Ilimitada.", rewards = "Você receberá:\n\n"},
[1457] = {title = "O naufrágio de Karnitol", objective = "Leve a Algibeira de Karnitol para Putren Rochamalho em Altaforja.", description = "Leve as coisas de Karnitol para Putren em Altaforja, e ele pagará pelo trabalho bem-feito.\n\nSe você não tomar cuidado, ele vai ficar tão feliz com você que vai acabar pedindo para você se juntar a nós em tempo integral.\n\nNovamente, excelente trabalho, <name>.\n\nCuidado na volta, e não demore demais. Karnitol não é dos mais pacientes, mas é bastante generoso mesmo assim.", progress = "O negócio vai de vento em popa, <name>, e eu não tenho muito tempo para responder perguntas. Se o seu assunto com a Recuperação Ilimitada for profissional, eu sou todo ouvidos. Se não for, terei que pedir que fale com um dos meus subordinados. Eles sabem cuidar bem das coisas por aqui.", completion = "Ah, aquela história do Karnitol na Desolação. Excelente!\n\nEntão o baú foi destruído, mas os itens estão todos aqui, não é? Bem, eu vou deixar que ele avalie isso. Uma parte boa desse ramo é que se eles não ficarem satisfeitos eu posso mandar praquele lugar. Eles contam conosco para encontrar as coisas deles, e não para garantir que esteja tudo lindo e perfeito quando elas são recuperadas.\n\nBom trabalho, &lt;name&gt;. Vamos cuidar do seu pagamento.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas do DesordeiroBrafoneiras Sanguíneas\n\nVocê também receberá:\n\n"},
[1458] = {title = "Reagentes para Recuperação Ilimitada (Ltda)", objective = "Leve 10 Garras Furiódio e 10 Chifres Furiódio para Kreldig Ungor na Desolação.", description = "Uma das minhas tarefas por aqui inclui arranjar reagentes para Sinismago, um mago gnomo em Kharanos. O Putren parece que acha que é dinheiro fácil porque a gente já está aqui mesmo e tal. Ele não se importa com a nossa falta de pessoal.\n\nOs primeiros itens devem ser fáceis de obter, e valeriam umas boas moedas para quem quer que me ajudasse.\n\nEu preciso de uns chifres e garras dos sátiros Furiódio ao leste daqui. Traga-os e passaremos ao resto da lista.", progress = "Criaturas desprezíveis, os sátiros. Tem uma história de que eles são ligados aos elfos noturnos de alguma forma, mas eu não acredito muito nisso, não.\n\nEu acho que a história vem daquele elfo que envolveu eles na guerra há alguns anos. E o mundo nunca mais foi o mesmo. Ele está muito mais perigoso, e muito mais assustador.", completion = "Bom, muito bom, &lt;name&gt;. Não sei dizer a qualidade, mas se Sinismago queria um tipo específico, ele deveria ter especificado antes de mandar os Recuperadores até aqui.\n\nO que você me diz de passar para o próximo item da lista?", rewards = "Você receberá:\n\n"},
[1459] = {title = "Reagentes para Recuperação Ilimitada (Ltda)", objective = "Leve 7 ampolas de Peçonha de Scorpachi e 3 Pelegos Envelhecidos de Kodo para Kreldig Ungor na Desolação.", description = "A próxima coisa na lista do Sinismago é... deixa eu ver... mmm. Ah, achei.\n\nÉ bem simples, mas com instruções bem específicas. Ele precisa e peçonha de scorpashi e couro de kodo. Na letra miúda diz que são de criaturas em particular: a peçonha tem que ser de qualquer scorpashi, e o couro tem que ser de kodo idoso, não de kodo moribundo ou antigo.\n\nMagos... Vou te contar, hein. Algumas coisas que eles querem são... são estranhas.", progress = "Desculpe por não poder apontar onde achar os kodos e scorpashis. Eu não conheço a área muito bem e não tive tempo para um reconhecimento.", completion = "Sua ajuda aqui é uma bênção, &lt;name&gt;. Você está cortando pelo menos metade do tempo que eu vou passar aqui. Com a sua ajuda, já já vou estar de volta em Altaforja esquentando os pés.\n\nQuando cuidarmos dessa lista e eu estiver de volta em casa, me procure. Eu te pago uma cerveja, com certeza.\n\nMas, até lá, vamos arrumar o resto dos reagentes.", rewards = "Você receberá:\n\n"},
[1462] = {title = "Sapta da Terra", objective = "Fale com a Vidente Pena do Corvo para obter outra Sapta da Terra.", description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nSapta da Terra"},
[1463] = {title = "Sapta da Terra", objective = "Fale com Ranago Arauto da Terra para obter outra Sapta da Terra.", description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nSapta da Terra"},
[1464] = {title = "Sapta do Fogo", objective = "Fale com Telf Joolam para obter outra Sapta do Fogo.", description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nSapta do Fogo"},
[1465] = {title = "A busca de Vahlarriel", objective = "Entregue o Pingente de Malem a Vahlarriel Matademônios na Desolação.", description = "Conforme prossegue em sua revista da carroça, você acha não apenas um pingente, como também pegadas que seguem para o sul. À distância você divisa fumaça se elevando de um grande forte. É fumaça de fogueira, e não de incêndio, e seus instintos dizem que os Malem, se ainda estiverem vivos, foram levados naquela direção.\n\nVocê acha que Vahlarriel, no Posto do Nijel, deveria não só ver o pingente, como ser informado das pistas que apontam para o sul.", progress = nil, completion = "Que ódio! Desse fiasco todo!\n\nOs dois foram avisados de como vir aqui podia ser perigoso. E agora os dois sumiram... ou pior.\n\nVocê disse que viu um rastro seguindo para um forte próximo? Pode ser a melhor aposta.\n\nPelo que dizem os locais, a fortaleza está infestada de integrantes da Lâmina Ardente, orcs que se dedicam a causas demoníacas. Eles podem não representar as crenças da maior parte da Horda, mas são maléficos e matarão você sem hesitar se a flagrarem espionando por lá.", rewards = "Você receberá:\n\n"},
[1466] = {title = "Reagentes para Recuperação Ilimitada (Ltda)", objective = "Leve 10 Cérebros de Canisvil, 10 Asas Etéreas e 10 ampolas de Sangue do Guardião da Ruína para Kreldig Ungor na Desolação.", description = "Esse é o último dos itens, e eu não vou mentir pra você. Não e bonito, não. Também espero que esse leve um pouco mais de tempo que os demais.\n\nEntão, esteja avisad<o/a> e vamos lá. Sinismago diz que precisa de: dez cérebros de manávoro, caça-mago ou caçador-do-meridiano; dez asas etéreas de uma donzela, irmã ou feiticeira do éter; e... dez ampolas de sangue de guardião da ruína, de um capitão ou lorde.\n\nTranquilíssimo, hein? ... Olha... Se você quiser passar essa eu vou entender.", progress = "Eu sequer sabia que esses demônios estavam na Desolação. Eu tinha lido que os centauros eram praticamente os donos da terra depois que os elfos noturnos emigraram anos atrás.\n\nEu fico me perguntando como e por que eles vieram para cá.", completion = "Sinceramente? Eu não teria pensado nisso. Você é espetacular, &lt;name&gt;. Encarar demônios por conta dos experimentos de um mago e ainda assim voltar inteira... Ótimo trabalho!\n\nVou falar pessoalmente de você para o Putren quando voltar para Altaforja.", rewards = "Você receberá:\n\n"},
[1467] = {title = "Reagentes para Recuperação Ilimitada (Ltda)", objective = "Leve a Encomenda de Sinismago para Putren Rochamalho em Altaforja.", description = "Para receber, você vai precisar voltar a Altaforja e falar com o próprio Putren. Ele vai adorar se livrar da encomenda do Sinismago. É só entregar isso aqui; deve bastar para receber a sua recompensa.\n\nE não se preocupe. Eu ainda falarei bem de você quando voltar lá. Não devo ficar mais muito tempo aqui na Desolação.", progress = "Oigalê, <name>! Os negócios estão em disparada, o som das bigornas batidas ecoa por Altaforja, os foles sopram alto o fogo e a patroa está fazendo torta para a sobremesa de hoje a noite. Podia ser melhor, essa vida?", completion = "Ah, a encomenda do Sinismago, da Desolação. Excelente. Baita trabalho, esse seu, e bem na hora, &lt;class&gt;. Foi bom ter você a bordo nesse serviço.\n\nSeu pagamento está aqui mesmo. Saibam todos que a Recuperação Ilimitada (Ltda) sempre paga bem por uma semana de trabalho duro.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBraçadeiras ÁuricasManoplas do Fogo da Tempestade\n\nVocê também receberá:\n\n"},
[1468] = {title = "A Semana das Crianças", objective = "Use o Apito de Órfão Humano para conversar com a criança de quem você vai cuidar durante a Semana da Criança.\n\nItem fornecido:\n", description = "Você está <disposto/disposta> a nos ajudar aqui no orfanato? <Abençoado/Abençoada> seja, <amigo/amiga>. Sua ajuda durante a Semana das Crianças será inestimável.\n\nPegue esse apito. Você só poderá usá-lo por um curto período de tempo – durante a Semana das Crianças apenas. Usando o apito, você poderá interagir com a criança de quem você tomará conta. Use o apito novamente para dispensar a criança.\n\nPor favor, <name>, use o apito e chame sua protegida. Tenho certeza de que ela está ansiosa para conhecer você.Completando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = nil},
[1469] = {title = "O caso do Rapsódico", objective = "Leve o Fragmento de Tabuleta Atal'ai para Brohann Barrilga em Ventobravo.", description = "Antes do voo eu bebi um barrilete inteiro de stout. Hic!\n\nE foi e um baita voo, riscando por aí com o Brohann encordoado atrás. Mas não vi templo nenhum. Hic!\n\nBom, não vi templo nenhum até ficar azonzado demais e bater a alcatra na terra ingrata. Quer dizer, na água. Eu caí direto e afundei, puxado pra baixo, e quando eu vi estava cercado por um bando de troll esquisito. Eu saí foi nadando mais rápido que estouro de carneiro! Trouxe até uma lembrancinha trólica. Aqui. Leva pro Brohann e conta que o templo dele está lá embaixo mesmo. Hic!", progress = "Finalmente você voltou, <name>. Faz um bom tempo desde que nos falamos pela última vez. Achei que você poderia ter esquecido da minha missão.\n\nAchou aquele grifoneiro? Ele tinha alguma coisa para contar?", completion = "Rapsódico Festanjeiro! Isso mesmo!... Como é que eu fui esquecer um nome desse?\n\nEntão ele sabia o tempo todo que o templo estava submerso? Bem, isso com certeza teria te poupado umas boas léguas de viagem.\n\nMas esse artefato Atal'ai! É um baita achado!", rewards = "Você receberá:\n\n"},
[1470] = {title = "Perfurando o véu", objective = "Leve 3 Crânios de Range-ossos para Vênia Martingil.", description = "Saudações, criança. Posso ver que você ainda engatinha no caminho dos bruxos, que o poder ainda lhe escapa. Consigo sentir seu desejo de aprender.\n\nE você vai aprender.\n\nNão existe força na ignorância. O conhecimento é o nosso maior poder. Por meio de sua aplicação podemos controlar a magia e os seres do caos, curvando-os à nossa vontade.\n\nEu lhe mostrarei como vincular um servo a sua vontade, mas antes você precisa me trazer três crânios dos esqueletos range-ossos lá de perto da ferraria abandonada.", progress = "Seu poder agora é pequeno, mas suspeito que você já sinta as possibilidades.\n\nAté nós mortos podemos nos sentir vivos com esse poder fluindo por nós.", completion = "Embora você ainda não tenha sido testado e esteja engatinhando no treinamento, você provou que tem habilidade suficiente para dominar a evocação e a vinculação de um diabrete. Não se deixe enganar pelo tamanho de um diabrete. Embora lhe falte força física, ele tem astúcia e poderes que lhe servirão bem.\n\nObserve bem, &lt;name&gt;, porque eu não repetirei esta lição, e deixarei o domínio dela por sua conta.", rewards = "Você irá aprender:\n\nEvocar Diabrete\n\nO seguinte feitiço será lançado em você:\n\nEnsinar Evocar Diabrete\n\nVocê também receberá:\n\n"},
[1471] = {title = "A vinculação", objective = "Usando as Runas de Evocação, evoque e subjugue um Emissário do Caos. Depois, devolva os Glifos de Evocação para Silvério Hidalgo no Distrito da Magia na Cidade Baixa.", description = "Você viu o círculo de evocação aqui abaixo? É o único lugar em que você conseguirá invocar um andarilho do caos livre.\n\nUsando essas runas você conseguirá invocá-lo, e em seguida precisará dominá-lo com força e magia.\n\nQuando for subjugado, ele se tornará seu servo. Então, venha a mim para que eu elucide o feitiço de evocação. Assim ele <o/a> servirá em todos os seus objetivos.", progress = "Espero que as notícias sejam boas, <name>. Porém, caso você tenha falhado, não satisfazer minhas expectativas será a menor de suas preocupações.", completion = "Você se saiu bem, &lt;name&gt;. Agora eu passarei a você o feitiço usado para evocar o andarilho do caos.\n\nO poder que ele usou contra você agora está sob o seu comando. Use-o sabiamente.", rewards = "Você irá aprender:\n\nEvocar Emissário do Caos\n\nO seguinte feitiço será lançado em você:\n\nEnsinar Evocar Emissário do Caos\n\nVocê também receberá:\n\n"},
[1472] = {title = "Devoradora de almas", objective = "Fale com Godofredo Fasano no Templo dos Malditos.", description = "Os sayaades são devoradores de almas, dominadores da vontade, tentadores de corações. Criaturas de profunda maldade e mente singular.\n\nO folclore, tanto dos justos quanto dos perversos, usa seus nomes comuns: súcubos para as fêmeas, íncubo para os machos.\n\nO demônio precisa ser seduzido com um estímulo para vir ao nosso mundo: os corações de mortais puros e amorosos. Os sayaades não conseguirão resistir. Então você pode dominá-los e tomá-los para si.\n\nFale com o meu assistente, o Godofredo Fasano, no templo. Ele ajudará você a achar sua isca.", progress = nil, completion = "O Silvério mandou você? Você é o &lt;name&gt;, então.\n\nNão me subestime pela cegueira. Agora eu vejo o que há por trás, com uma clareza que eu não tinha antes. Eu já achei que era uma maldição, mas agora é uma bênção.", rewards = "Você receberá:\n\n"},
[1473] = {title = "Criatura do caos", objective = "Recupere o Grimório de Egalin e leve-o para Silvério Hidalgo no Templo dos Malditos.", description = "Parece que o consenso é de que você está pront<o/a> para evocar um andarilho do caos.\n\nUm ser criado a partir do caos do além... Se você evocar um, mas não conseguir controlá-lo, você será destroçad<o/a>.\n\nO primeiro andarilho do caos foi evocado por Egalin, e o processo está explicado no grimório dele.\n\nInfelizmente porém, ele foi roubado de nós por um agente da Cruzada Escarlate, que o escondeu na própria base, uma torre pro oeste daqui. Traga o grimório e eu passarei esse conhecimento a você.", progress = "Egalin foi meu professor. Com um mero olhar de esguelha ele comandava o emissário do caos.\n\nQuando chegou a minha vez, eu fiquei confiante demais. Não brinque com os poderes que você usa, <name>. Eles podem consumir até mesmo quem permanece vigilante.", completion = "Ah, isso é excelente. Provavelmente, Perrine ignorava o poder do livro empoeirado que ele nos roubou.\n\nDoravante ele permanecerá em nossas mãos. Observe, &lt;name&gt;, a estrutura do feitiço, a forma como seu poder precisa ser canalizado.", rewards = "Você receberá:\n\n"},
[1474] = {title = "A vinculação", objective = "Usando os Corações Puros, evoque e subjugue uma súcubo. Depois, devolva os Corações Puros para Silvério Hidalgo no Distrito da Magia na Cidade Baixa.", description = "Usando esses corações, lance um feitiço no círculo de evocação para que a súcubo venha. Seus poderes estão mais fortes agora, e você vai precisar deles para derrotá-la.\n\nCuidado com os encantos da criatura, <name>. Muitas mentes fortes já foram cativadas pela vontade dela. Quando derrotada, a súcubo voltará para o caos e eu lhe ensinarei o feitiço necessário para evocá-la sob o seu comando.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nEvocar Súcubo\n\nO seguinte feitiço será lançado em você:\n\nEnsinar Evocar Súcubo\n\nVocê também receberá:\n\nBolsinho de Almas"},
[1475] = {title = "No templo de Atal'Hakkar", objective = "Leve 10 Tabuletas Atal'ai para Brohann Barrilga em Ventobravo.", description = "Diz a lenda que o Templo de Atal'Hakkar é o santuário dedicado ao antigo deus Hakkar, o Esfolador de Almas. Ele é mantido por uma tribo trólica feroz chamada Atal'ai, e dizem que é impenetrável.\n\nComo ele agora está submerso no Poço de Lágrimas, retirar artefatos do templo ficou ainda mais difícil. Mas é isso que eu preciso que você faça, <name>.\n\nAjude encontrando as Tabuletas Atal'ai intactas. Traga-as para mim e eu voltarei como herói para a Guilda dos Exploradores. E nós compartilharemos a glória.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nTalismã do Guardião"},
[1476] = {title = "Corações dos Puros", objective = "Leve os corações de Dalin Forjífice e Comar Villard para Silvério Hidalgo no Templo dos Malditos.", description = "Homens de honra e bom coração? Será que eles existem?\n\nPrecisei pesquisar muito, mas, seguindo fragmentos de relatos, consegui encontrar dois homens que devem servir.\n\nO primeiro é Dalin Forjífice, que se dedicou ao cuidado de refugiados de Lordaeron rejeitados pelos parentes na Muralha Greymane.\n\nE o outro é Comar Villard, um homem que permanece fiel à esposa perdida e vaga pelo Pantanal em busca de um sinal dela.\n\nEntregue os corações deles ao Silvério. Mas primeiro deixe-me mostrar seus alvos.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1477] = {title = "Suprimentos Vitais", objective = "Fale com Mestre-Vigia Sorigal.", description = "Nossos magos estão conduzindo pesquisas na Bastilha de Etergarde, na Barreira do Inferno, pesquisas vitais para a nossa compreensão dos Portais Negros. Eles precisam de suprimentos quase constantes, mas a nossa linha de abastecimento para Etergarde é tênue.\n\nPrecisamos da sua ajuda para garantir que os suprimentos cheguem a Etergarde. Fale com o Mestre-Vigia Sorigal na Vila Sombria e faça o que ele pede. Você andará por terras hostis por conta desta tarefa, <name>. Prepare-se bem.", progress = nil, completion = "Andromath mandou você? Muito bom! Tenho uma importante tarefa para você...", rewards = "Você receberá:\n\n"},
[1478] = {title = "Convocação de Hidalgo", objective = "Fale com Silvério Hidalgo na Cidade Baixa.", description = "Ah, <name>! Acabei de receber uma mensagem de Silvério Hidalgo, dizendo que ele gostaria de falar com você para aprofundar o seu treinamento.\n\nVocê devia se orgulhar, <name>. Não é todo dia que o Mestre Halgar em pessoa julga conveniente treinar um jovem bruxo.\n\nVocê vai encontrá-lo no Distrito da Magia da Cidade Baixa, perto do Templo dos Malditos.", progress = nil, completion = "Bom, vejo que Ageron conseguiu encontrar você, &lt;name&gt;. Vamos pular as apresentações e gentilezas e focar no seu treinamento. Você ainda tem muito o que aprender.", rewards = "Você receberá:\n\n"},
[1479] = {title = "O Ramo dos Eternos", objective = "Leve o órfão ao banco de Darnassus. O banco foi esculpido dentro de uma árvore, conhecida como o Ramo dos Eternos.", description = "Uma vez, ouvi que os elfos noturnos tem um banco dentro de uma árvore... Uma árvore que parece um urso! Isso é tão maneiro! Elfos até que são legais... Eles protegem a natureza e tudo mais... mas ursos são incríveis!\n\nVocê me levaria até Darnassus para ver a árvore-urso, por favor? Tem um barco que deixa a gente lá perto... Não deve ser muito longe. Por favor, diz que sim! Vai, diz que sim... Não seja assim... vai, siiiiiiim?Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1480] = {title = "O Corruptor", objective = "Leve a Pele de Demônio Esfolada a Maurin Rachaosso na Desolação.", description = "A pele rasgada tem cheiro podre e é notavelmente maleável para uma coisa que parece seca e frágil.\n\nConforme você a desenrola, é possível ver claramente as marcas vermelhas em algum tipo de língua em um dos lados. Na parte de baixo da pele, há um símbolo claro, que você imagina ser algum tipo de assinatura.\n\nTalvez o Maurin possa lhe dizer alguma coisa sobre isso.", progress = "A Lâmina Ardente começa a perceber o quão fracos eles são de verdade. Suas magias são patéticas, sua força, débil.\n\nSeu único poder vem daqueles a quem eles servem, e não de qualquer compreensão de verdadeiros poder e conhecimento.", completion = "Hmm, essas runas são, de fato, uma língua, &lt;name&gt;, uma língua antiga. Elas se parecem remotamente com a língua dos Eredun, mas as diferenças são drásticas. Se não me engano, isto é algum tipo de contrato... Aqui, sim, este símbolo é a assinatura do demônio: Lorde Azrethoc.", rewards = "Você receberá:\n\n"},
[1481] = {title = "O Corruptor", objective = "Leve um Escalpo de Assombrante para Maurin Rachaosso na Desolação.", description = "Acredito que eu consigo encontrar esse lorde-demônio com os reagentes certos.\n\nSe ele for mesmo tão poderoso como eu suspeito, voou precisar de itens colhidos de criaturas mortais. Mas você age em nome do Chefe Guerreiro, então, tenho certeza de que vai aceitar a minha tarefa.\n\nPrimeiro, preciso quebrar qualquer proteção que ele tenha contra espionagem. Para isso, vou precisar do escalpo de um sátiro — mas não qualquer sátiro. O escalpo deve vir de um Assombrante Furiódio.\n\nProcure-os entre as ruínas a nordeste da Desolação.", progress = "Não me traga um escalpo errado, <class>. Usar os reagentes errados pode causar a nossa morte. O lorde-demônio pode ter feitiços de contingência aplicado em si para matar aqueles que tentarem espioná-lo.\n\nO destino já me causou dor demais.", completion = "Os assombrantes não conseguiram matar você, &lt;class&gt;. Impressionante... Muitíssimo impressionante.\n\nEste escalpo servirá perfeitamente para o meu feitiço. Enquanto você estava fora, eu conseguiu juntar os outros reagentes necessários para o feitiço.\n\nJá está quase pronto. Agora só preciso de um foco para o feitiço — alguma coisa para projetar a imagem do lorde-demônio e seu paradeiro.", rewards = "Você receberá:\n\n"},
[1482] = {title = "O Corruptor", objective = "Leve um Cristal de Oráculo para Maurin Rachaosso em Desolação.", description = "Um cristal de oráculo seria perfeito. Eles são criados pelas nagas para o uso de seus oráculos.\n\nA nordeste daqui, ao longo da costa, há um vasto recife onde as nagas se instalaram. Você encontrará os oráculos delas lá. Os Sibilâminas são mortais e não vão aceitar a sua intromissão pacificamente, por isso, tome cuidado e aja rápido.\n\nQuando você voltar, trataremos de localizar o lorde-demônio Azrethoc.", progress = "Eu estou pronto para o oráculo de cristal, <class>. Achar e destruir o tal Lorde Azrethoc nos deixaria em uma situação muito boa com o Chefe Guerreiro. Nós podemos trabalhar juntos. Se formos rápidos, alcançamos nossos objetivos e ganhamos a simpatia do Chefe Guerreiro.", completion = "Um cristal de oráculo precioso... perfeito... lindo. De fato.\n\nVamos começar imediatamente. Encontrar esse lorde-demônio devia ser a nossa prioridade, e sua morte demanda a nossa atenção mais urgente.\n\nAfaste-se um pouco, &lt;class&gt;, e vamos ver o que é esse demônio e onde ele pode ser encontrado.", rewards = "Você receberá:\n\n"},
[1483] = {title = "Zé Fízzica", objective = "Fale com Zé Fízzica em Rochedo Cortavento.", description = "A Empreendimentos S.A. roubou muitos dos melhores cérebros goblínicos, mas, de tempos em tempos, temos a oportunidade de roubar alguns de volta.\n\nUm tempo atrás, a Empreendimentos S.A das Cordilheira das Torres de Pedra contratou Hanfritz Schaventorck — um gnomo! O outro engenheiro deles, Zé Fízzica, ficou tão passado com isso que me pediu pra arranjar outro emprego pra ele no Sindicato dos Faz-tudo. Eu passei algumas tarefas pra ele, mas acho que o cara vai precisar de ajuda.\n\nSerá que você pode dar um confere nele pra mim? Ele está na beirada oeste do Rochedo Cortavento.", progress = nil, completion = "Eu achei que os bam-bam-bãs da Empreendimentos S.A fossem feitores de escravos, mas daí eu conheci a Cobogó, a mais cruel de todos eles!\n\nRá! Se bem que é melhor isso do que ficar desempregado, acho... Ou pior: trabalhar com aquele Hanfritz. Se você veio aqui pra ajudar, eu tenho uma coisa pra você fazer.", rewards = "Você receberá:\n\n"},
[1484] = {title = "O Corruptor", objective = "Fale com Takta Laminaço em Desolação.", description = "Esta criatura é muito mais perigosa do que eu pensava. Sem dúvida, o Takta questiona a minha capacidade de encarar uma criatura dessas e seria um insulto à honra dele se não o informássemos sobre o que acabou de acontecer.\n\nFale com o espadachim, diga a ele que você quer matar o demônio, se ele permitir, mas não demonstre muita empolgação. É melhor ele pensar que foi ideia dele.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1485] = {title = "Familiares Vis", objective = "Leve 6 Cabeças de Demônios Familiares Vis para Ruzan.", description = "Esses bruxos que vieram antes de nós ganharam a desconfiança da Horda e, apesar de sermos tolerados, não somos queridos.\n\nMas isso não uniu os bruxos, já que muitos escolhem levar uma vida reclusa, fazendo seus experimentos mágicos sozinhos. Eu não vou pretendo guiar você por esse ou aquele caminho, só quero ajudar no seu amadurecimento.\n\nAntes de eu lhe ensinar a arte da invocação, traga seis cabeças de demônios familiares vis da caverna a nordeste.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nEvocar Diabrete\n\nO seguinte feitiço será lançado em você:\n\nEnsinar Evocar Diabrete\n\nVocê também receberá:\n\n"},
[1486] = {title = "Pelegos anormais", objective = "Nalpak na Caverna Ululante quer 20 Pelegos Anormais.", description = "Enquanto Naralex descia mais e mais fundo no pesadelo, uma raça estranha de feras surgiu nos Sertões e fez da Caverna Ululante o seu lar.\n\nEssas criaturas anormais têm características estranhas, que parecem de outro mundo. Apesar de sua natureza maligna, na minha opinião, podemos tirar algo de bom da existência deles aqui em Kalimdor. Acredito que o pelego deles será particularmente útil no estudo da couraria.\n\nSe você se sente à altura do desafio, mate quantas feras puder e me traga alguns pelegos anormais.", progress = "Eu estou muito interessado em examinar o pelego de criaturas anormais que infestaram essas cavernas. Você conseguiu coletar alguns, <class>?", completion = "Seu esforço na coleta desses pelegos não passará despercebido, &lt;name&gt;.\n\nObrigado por sua dedicação.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nPerneiras Anormais LustrosasMochila de Pelego Anormal\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[1487] = {title = "Erradicação de Anormais", objective = "Ebru, na Caverna Ululante, quer que você mate 7 Assoladores Anormais, 7 Víboras Anormais, 7 Trôpegos Anormais e 7 Presáteras Anormais.", description = "Naralex tinha um objetivo nobre.\n\nNosso grande líder almejava entrar no Sonho Esmeralda e ajudar estas terras áridas a serem novamente a floresta pujante que outrora havia aqui. Entretanto, aconteceu algo muito errado.\n\nO sonho de Naralex virou um pesadelo, e criaturas corrompidas começaram a habitar as cavernas.\n\nEnquanto alguns Discípulos de Naralex tentam acordar o nosso mestre, minha preocupação está em livrar as cavernas dos monstros malignos.\n\nExplore as cavernas, <name>, e erradique essa cria perversa.", progress = nil, completion = "Como Discípulo de Naralex, eu saúdo a sua bravura, &lt;name&gt;;\n\nSua ajuda na limpeza das cavernas é o primeiro passo para cumprir o nosso voto de ver os Sertões recuperados.\n\nObrigado, e que você tenha prosperidade.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nMolde: Cinto de Escama AnormalEspeto de ChurrascoManoplas Lamacentas\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[1488] = {title = "O corruptor", objective = "Mate Lorde Azrethoc e Jugkar Grim'rod e fale novamente com Takta Laminaço em Desolação.", description = "Apesar de não conhecer esse Lorde Azrethoc, eu concordo com Maurin e acho que ele precisa ser detido, especialmente se estiver tentando ganhar influência sobre a Lâmina Ardente para levar seus objetivos adiante.\n\nVocê já mostrou que deseja ajudar o Chefe Guerreiro, e eu lhe confio a tarefa de reunir um grupo e matar o lorde-demônio e seu servo bruxo.\n\nQuando estiver <pronto/pronta>, siga direto para o sul e venha falar comigo quando a ameaça for eliminada.", progress = nil, completion = "Uma grande vitória para todos nós, &lt;name&gt;. Vamos comemorar o seu sucesso. Agora tenho confiança de que o nosso futuro aqui não é tão sombrio quanto eu pensava.\n\nOs demônios serão mortos, a Lâmina Ardente fracassará em seus esforços, e nós podemos, um dia, ter um lugar para chamar de lar outra vez.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBroquel de BasaltoBrafoneiras do Impositor\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[1489] = {title = "Hamuul Runa Totem", objective = "Fale com Hamuul Runa Totem", description = "As carapaças que você me trouxe têm uma mácula de um tipo que eu nunca vi antes. Tenho certeza de que as águas do Oásis são a causa, e eu sinto a confusão que elas causam a esta terra. É como se a terra sonhasse e não pudesse acordar.\n\nA Caverna Ululante deve ser a fonte dessa corrupção. Mas antes de ir, fale com Hamuul Runa Totem, o druida ancião. Eu mandei um aviso a ele do que está acontecendo nos Sertões. Ele é sábio e pode lhe aconselhar.\n\nVocê pode encontrá-lo no Penhasco do Trovão, no Platô dos Anciãos.", progress = nil, completion = "Minhas saudações, &lt;name&gt;. Desejo-lhe boas-vindas e sugiro que você reúna todas as suas forças, pois esta tarefa que lhe outorgamos... é tenebrosa.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[1490] = {title = "Nara Juba Agreste", objective = "Fale com Nara Juba Agreste.", description = "Eu entrei na terra dos sonhos e sonhei com uma seita de druidas que habitavam a Caverna Ululante. Eu vi que seus objetivos já foram nobres, mas eles se desviaram do caminho. Agora são servos da mácula que toma os Sertões.\n\nEu sou velho, e já não tenho mais forças. Eu não consegui ficar mais tempo naquele lugar onírico para saber mais sobre os druidas corrompidos. Mas Nara é jovem, e seu espírito é bravio. Ela ficou no sonho quando eu fugi.\n\nFale com Nara. Ela está na tenda, logo ali.", progress = nil, completion = "Sim, é verdade. Eu sei dos druidas na Caverna Ululante. Eu vi o rosto distorcido e escamado deles. Sei de seus crimes e conheço seus planos.\n\nVocê precisa detê-los, &lt;name&gt;.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[1491] = {title = "Bebidas inteligentes", objective = "Leve 6 porções de Essência Ululante para Cáliper Porcatraca na Vila Catraca.", description = "Tá, então os chifres de raptor não deram certo. Mas não tem problema! Ninguém fica rico logo de cara! Além disso, eu acho que encontramos uma coisa que vai funcionar ainda melhor nas minhas bebidas inteligentes!\n\nExiste uma substância chamada essência ululante, e eu acho que ela é a causa dos animais e plantas estranhos nos Sertões. Me traga um pouco para eu testar na minha bebida!\n\nOuvi dizer que essa essência se acumula em criaturas ectoplasmáticas na Caverna Ululante, a oeste. Cace essas criaturas e colha a essência.", progress = "Cadê a minha essência ululante?", completion = "Maravilha, você conseguiu! Mal posso esperar para testar. Eu vou nadar na grana com isso, tenho certeza!\n\nValeu, &lt;name&gt;. Fique com isso — eu mando mais quando ficar rico, prometo!", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[1492] = {title = "Mestre Portuário Caruncho", objective = "Leve o Caixote Fechado para o Mestre Portuário Caruncho na Vila Catraca.", description = "Eu preciso mandar caixas de poções, reagentes e outros artigos para os meus sócios no Velho Mundo. Mas não tenho tempo para levar tudo até a doca na Vila Catraca, e a última caravana já partiu.\n\nSe você puder, leve isto para o Mestre Portuário Caruncho. Ele vai garantir que coloquem a carga no próximo barco para Angra Aguanegra.", progress = "Você quer embarcar no próximo navio ou tem algum item que quer enviar a bordo?", completion = "Essa caixa é relativamente pequena. Sim, dá pra encaixar em algum lugar a bordo desse navio. Ele vai zarpar na próxima maré. Imagino que seja cedo o bastante. Excelente. Eu só vou anotar aqui no meu registro.\n\nTudo resolvido! Tenha um bom dia, &lt;class&gt;.", rewards = "Você receberá:\n\n"},
[1498] = {title = "Caminho da defesa", objective = "Leve 5 Escamas Chamuscadas para Uzzek no Posto Remoto, nos Sertões.", description = "Como guerreiros, uma das nossas tarefas mais importantes é... proteger. Nós somos o escudo da Horda e mantemos os irmãos mais fracos em segurança. Se você quiser se juntar às nossas fileiras, precisa me mostrar que tem fibra.\n\nVá até o Desfiladeiro do Trovão, em Durotar, onde vivem os pelegos de relâmpago e os lagartos trovejantes. Cace aqueles animais! Mostre-me que você é mais <durão/durona> que eles! Enfrente os ataques deles e traga-me uma pilha de escamas chamuscadas como prova.\n\nFaça isso, e então eu <o/a> treinarei.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nCaminho da Defesa\n\nVocê receberá:\n\n"},
[1499] = {title = "Familiares torpes", objective = "Fale com Zureta Vista-longa.", description = "Zureta tem mandado novatos e novatas como você para ajudar a diminuir o número de criaturas na caverna perto de onde você lutou com os familiares torpes.\n\nAcho que lhe faria bem ganhar experiência ajudando-a. Não se esqueça de dizer a ela que foi você quem a ajudou com o problema dos diabretes, provavelmente ela tem uma recompensa para você.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nNavalha PrimitivaBengala Primitiva\n\nVocê também receberá:\n\n"},
[1500] = {title = "Despertando Naralex", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1501] = {title = "Criatura do caos", objective = "Recupere a Tabuleta de Verga para Gan'rul Olho de Sangue em Orgrimmar.", description = "Então, restaram alguns bruxos... Nós arriscamos muita coisa, mas os riscos fazem sentido. Você precisa amadurecer suas habilidades se quiser ser útil à Horda e ao Chefe Guerreiro.\n\nEstá na hora de você aprender um feitiço de evocação novo.\n\nPrimeiro, você precisa recuperar a Tabuleta de Verga, que foi roubada pelos bruxos da Lâmina Ardente e é mantida agora no covil deles, na Rocha da Caveira, a leste da cidade. Com a tabuleta, eu vou lhe mostrar as runas necessárias para evocar um Emissário do Caos.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1502] = {title = "Thun'grim Olhafogo", objective = "Fale com Thun'grim Olhafogo nos Sertões.", description = "É essencial que a arma de um guerreiro seja de qualidade. E se existe um orc que sabe fabricar armas de qualidade, esse é Thun'grim Olhafogo. Ele é um eremita que mora no alto das colinas ao leste da Encruzilhada.\n\nSe você procura uma arma, <guerreiro/guerreira>, então procure Thun'grim. Para encontrá-lo, primeiro viaje até a Encruzilhada a oeste, depois, caminhe no sentido leste pelas colinas. O acampamento dele fica em cima de um cume.", progress = nil, completion = "Faz muito tempo que meus olhos secaram, de tanto olhar para a chama da forja, mas tem coisas que eu ainda consigo enxergar. Posso ver que você deseja algo.\n\nE, se você conseguiu chegar até aqui, posso imaginar o que você deseja.", rewards = "Você receberá:\n\n"},
[1503] = {title = "Aço forjado", objective = "Leve as Barras de Aço Forjado para Thun'grim Olhafogo nos Sertões.", description = "Você terá sua arma, <name>, mas, primeiro, você precisa me ajudar. Os Crinavalhas a noroeste vieram ao meu acampamento algumas noites atrás. Eles não tentaram me enfrentar em combate, mas sabe que meus olhos são ruins — alguns deles me distraíram bufando enquanto os outros me roubavam! Covardes!\n\nEles levaram um baú de ferro que guardava algumas barras de aço forjado. Se você recuperá-las, eu lhe darei a arma que você procura.\n\nProcure o baú de ferro nos acampamentos dos javatuscos e volte com as barras de aço forjado.", progress = "Trouxe as minhas barras de aço?", completion = "Você as encontrou! E todas as barras estão aqui, o que não me surpreende. Não achei que os Crinavalhas saberiam o que fazer com um aço de qualidade como este.\n\n. Muito obrigado, &lt;name&gt;. Aqui está a sua recompensa. Que ela lhe sirva bem.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nMachado do Thun'grimMaça do Thun'grimAdaga do Thun'grimEspada do Thun'grim\n\nVocê também receberá:\n\n"},
[1504] = {title = "A vinculação", objective = "Usando os Glifos de Evocação, evoque e subjugue um Emissário do Caos; depois, devolva os Glifos de Evocação para Gan'rul Olho de Sangue em Orgrimmar.", description = "Agora, observe a Tabuleta de Verga, porque ela contém os glifos de que você vai precisar para a evocação. Leve-os ao círculo na tenda de Neeru Cortafogo, no outro lado da caverna. Lá, você poderá evocar o Emissário do Caos.\n\nVocê tem que derrotá-lo usando quaisquer meios físicos ou mágicos que tiver à mão. Quando terminar, volte aqui com os glifos de evocação, e eu vou demonstrar como você pode vincular criatura à sua vontade.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nEvocar Emissário do Caos\n\nO seguinte feitiço será lançado em você:\n\nEnsinar Evocar Emissário do Caos\n\nVocê também receberá:\n\n"},
[1505] = {title = "Veterano Uzzek", objective = "Fale com Uzzek no Posto Remoto.", description = "A Horda honra seus guerreiros porque sua força é inquestionável. Mas os verdadeiros guerreiros também são grandes estudantes.\n\nProcure Uzzek. Ele é um veterano de muitas batalhas, e agora ensina a jovens guerreiros a arte do combate. Ele fica na parte sul do Posto Remoto, a guarnição órquica na fronteira dos Sertões e Durotar, ao leste.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1506] = {title = "Convocação de Gan'rul", objective = "Fale com Gan'rul Olho de Sangue em Orgrimmar.", description = "Você aí. <name>, não é?\n\nEspero que a sua agenda não esteja cheia, porque Gan'rul quer falar com você imediatamente. Ele está junto com os bruxos veteranos no Antro das Sombras.\n\nBaseado nas minhas experiências com o Gan'rul, essa convocação é uma notícia ótima ou horrível.", progress = nil, completion = "Ótimo. Fiquei com medo de que Ophek não conseguisse falar com você.\n\nEstá na hora de aprofundarmos o seu treinamento de &lt;class&gt;. Qualquer outra coisa que você tenha que resolver terá que esperar agora.\n\nImagino que não tenha problema, tem?", rewards = "Você receberá:\n\n"},
[1507] = {title = "Devoradora de almas", objective = "Fale com Cazul no Antro das Sombras.", description = "Chegou a hora de você aprender mais sobre magias de evocação. Você aprenderá a evocar um sayaad, uma criatura de poderes mentais formidáveis. Ela pode submeter seres de mente fraca à servidão — mas não precisa ter medo! Sei que você estará à altura do desafio.\n\nUm sayaad só pode ser evocado quando atraído com um item que representa grande amor e devoção. Vou deixar em suas mãos encontrar esse objeto.\n\nUma dica? Fale com Cazul... Parece que não existem muitas coisas que o velho bruxo não saiba.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1508] = {title = "Cazul cego", objective = "Fale com Zankaja em Orgrimmar.", description = "Humm... Mas, naturalmente, não é por isso que você veio falar comigo.\n\nSe Gan'rul mandou você me procurar, deve ser porque você quer evocar uma Súcubo e precisa de um objeto para atraí-la.\n\nHum... Se você confia na orientação de um homem cego, <name>, então escute. Nos penhascos de Orgrimmar, a oeste da torre das mantícoras, vive Zankaja. Ela está esperando o retorno de seu companheiro.\n\nFale com ela.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1509] = {title = "Notícias de Dogran", objective = "Fale com Gazrog na Encruzilhada.", description = "Meu companheiro, Dogran, foi destacado recentemente para a Encruzilhada. Foi uma promoção, e ele estava muito orgulhoso.\n\nEu fiquei triste, é claro, mas eu não deixei que ele soubesse disso. Fiquei preocupada também. Os Sertões são um lugar mais perigoso que Orgrimmar, e eu temia pelo pior.\n\nAgora já faz uma semana que ele deveria ter voltado, e eu não recebi nenhuma notícia dele...\n\nSerá que você pode ir até a Encruzilhada e ver se descobre alguma coisa? Ele trabalhava com Gazrog, que ainda deve estar destacado lá.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1510] = {title = "Notícias de Dogran", objective = "Fale com Ken'zigla em Malaka'jin.", description = "Ele estava destacado para o Acampamento Taurajo, que estamos tentando livrar dos javatuscos. Ele liderou uma tropa de brutos até um dos acampamentos deles e acabou ferido. Os outros voltaram e tentaram resgatá-lo. A situação dele é ruim, as feridas infeccionaram, por isso estão com receio de carregá-lo sem tratar as feridas dele antes.\n\nEles mandaram uma mensagem para cá, pedindo um trago curativo dos trolls de Malaka'jin, no sopé da Cordilheira das Torres de Pedra. Nós mandamos alguém lá para buscar, mas ele não voltou mais.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1511] = {title = "Trago do Ken'zigla", objective = "Leve o Trago do Ken'zigla para o Bruto Logmar no Acampamento Taurajo.", description = "Imaginei que outra pessoa viria atrás do trago, por isso já deixei ele preparado.\n\nProntinho, <name>. Mas lembre-se: você só pode dar metade pra ele! Isso já vai dar força suficiente pra ele se mexer sem sangrar litros. Espere uma noite, depois dê a outra metade.\n\nEnfim, você precisa levar isso para Logmar, no Acampamento Taurajo. Pelo que o primeiro rapaz me disse, ele deve ter alguma ideia de onde encontrar o Dogran.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1512] = {title = "Presente do amor", objective = "Leve o Pingente Coberto de Terra para Gan'rul Olho de Sangue em Orgrimmar.", description = "Zankaja é jovem e bonita. Ficar assim, presa a um inválido... Eu não vou permitir que ela sofra essa injustiça.\n\nPode ir, <name>. E obrigado. Eu quero terminar a minha vida em uma batalha gloriosa, e não como uma carcaça sem vida.\n\nE, por favor, não diga nada à minha companheira. Não quero que ela se sinta culpada.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1513] = {title = "A vinculação", objective = "Com o Pingente de Dogran, evoque e subjugue uma Súcubo; depois, devolva o Pingente de Dogran a Gan'rul Olho de Sangue, em Orgrimmar.", description = "Leve isto para o círculo de evocação na tenda de Neeru, <name>. Evoque uma Súcubo perversa e mostre a ela sua maestria. Eu acredito na força da sua magia. Mas, como sempre, aconselho cautela, pois o orgulho e a autoconfiança exacerbada já derrotaram muitos bruxos promissores.\n\nQuando você a tiver derrotado, traga-me o pingente de volta para que eu possa levar as notícias para Zankaja.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nEvocar Súcubo\n\nO seguinte feitiço será lançado em você:\n\nEnsinar Evocar Súcubo\n\nVocê também receberá:\n\nBolsinho de Almas"},
[1514] = {title = "Botão Selvagem corrompido", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1515] = {title = "O cativeiro de Dogran", objective = "Leve o Trago do Ken'zigla para Dogran nos Sertões.", description = "Como você já passou por tudo isso, se quiser ver como essa história acaba, eu não vou impedi-<lo/la>. Mas eu não posso lhe disponibilizar mais homens, você está por conta própria.\n\nDogran está preso em um acampamento de javatuscos ao norte daqui. Procure um par de cabanas. Ele estará fortemente protegido — digo, se é que não o mataram ainda ou se ele não tiver sucumbido aos ferimentos.\n\nBoa sorte.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1516] = {title = "Clamor da Terra", objective = "Leve 2 Cascos de Espreitador Vil para Ranago Arauto da Terra no Vale das Provações.", description = "Chegou a hora, jovem <class>. Você cresceu e seu espírito ficou forte como a terra. Seguir o caminho dos xamãs é a prova de que você é <sábio/sábia> antes mesmo de provar seu valor.\n\nO elemento da terra guiará seus passos, tornando-se parte do seu ser se você estiver <pronto/pronta>. Mas você precisa se pôr à frente da terra. Se estiver na hora, você verá coisas que só um xamã pode enxergar.\n\nProcure os Espreitadores Vis ao norte e traga-me dois cascos deles. Aí, sim, falaremos do seu futuro.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1517] = {title = "Clamor da Terra", objective = "Encontre a Rocha dos Espíritos e beba o Sapta da Terra.\n\nItem fornecido:\n", description = "Um Sapta é uma bebida criada para vincular o nosso espírito aos elementos. Infundida com magia, a poção permitirá aos de vontade férrea enxergar os elementos como ninguém mais pode. Seu espírito se conectará ao elemento para o qual o sapta foi criado, e somente um seleto grupo de xamãs conhece a receita.\n\nO sapta sempre é bebido em um local sagrado, e nunca em qualquer outro lugar — lembre-se disso.\n\nProcure a Rocha dos Espíritos a sudoeste daqui — ela fica no final do Caminho Oculto. Beba o seu sapta lá e... bem, você verá.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1518] = {title = "Clamor da Terra", objective = "Leve o Quartzo Grosseiro para Ranago Arauto da Terra no Vale das Provações.", description = "Pague este quartzo grosseiro e leve até Ranago Arauto da Terra como prova do nosso encontro. Ele vai lhe fazer um totem que terá este quartzo como coração. Por menor que ele possa parecer, você vai acabar descobrindo que tamanho não é documento, e que mesmo as menores coisas podem durar mais que muitas outras.\n\nTenha paciência, <name>. Tenha força. E busque a sabedoria. É tudo o que a terra lhe pede. No fim, você e a terra serão uma coisa só, respeite-a como ela respeita você.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nTotem Litopele\n\nO seguinte feitiço será lançado em você:\n\nTotem Litopele\n\nVocê também receberá:\n\nTotem da Terra"},
[1519] = {title = "Clamor da Terra", objective = "Leve 2 aplicações de Unguento de Ritual à Vidente Pena do Corvo na Aldeia Narache.", description = "Chegou o momento, jovem <class>. Seu corpo amadurece e se fortalece, e seu espírito resiste como a terra. Vejo isso dentro de você — já há sabedoria aí.\n\nA terra guia o nosso povo, ela fala conosco, assim como falará com você em seu devido tempo. Mas você precisa passar pelo testo. Se estiver <pronto/pronta>, então você verá coisas que só o nosso povo conhece.\n\nProcure os xamãs Costagulha na ravina ao leste e tome deles um unguento que eles usam em rituais. Só volte aqui quando tiver suficiente para a sua própria poção.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1520] = {title = "Clamor da Terra", objective = "Encontre a Rocha dos Kodos e beba o Sapta da Terra.\n\nItem fornecido:\n", description = "O Sapta é uma bebida criada para vincular o nosso espírito aos elementos. Infundida com magia, a poção permitirá aos de mente aberta enxergar os elementos como ninguém jamais enxergou. Seu espírito se ligará para sempre ao elemento para o qual o sapta foi criado, e somente um seleto grupo da nossa raça conhece a receita.\n\nO sapta sempre é ingerido em um local sagrado, e nunca em qualquer outro lugar.\n\nProcure o santuário do xamã a leste daqui e beba o sapta.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1521] = {title = "Clamor da Terra", objective = "Leve o Quartzo Grosseiro à Vidente Pena do Corvo na Aldeia Narache.", description = "Pegue este quartzo grosseiro e leve até a Vidente Pena do Corvo como prova do nosso encontro. Ela vai lhe fazer um totem, que terá este quartzo como coração. Por menor que possa parecer, você vai acabar descobrindo que tamanho não é documento, e que mesmo as menores coisas podem durar mais que montanhas.\n\nTenha paciência, <name>. Tenha força. E busque a sabedoria. É tudo o que a terra lhe pede. No fim, você e a terra serão uma coisa só. Respeite-a como ela respeita você.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nTotem Litopele\n\nO seguinte feitiço será lançado em você:\n\nTotem Litopele\n\nVocê também receberá:\n\nTotem da Terra"},
[1522] = {title = "Clamor do Fogo", objective = "Encontre Kranal Fiss nos Sertões.", description = "Que o fogo esteja contigo, fera. Tô ligado que você já tem a força da terra e está pront<o/a> pra falar com o Kranal.\n\nEle mora ao norte da Encruzilhada dos Sertões. Ele tem a força dos elementos e pode ensinar você a dominar o fogo... se você acha que consegue.\n\nVai ser fácil achar o cara, é só não ir longe demais... se você for longe demais, vai acabar parando nas terras dos elfos, sacou?Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1523] = {title = "Clamor do Fogo", objective = "Encontre Kranal Fiss nos Sertões.", description = "Já faz tempo que você foi testad<o/a>, <name>. Você demonstrou muita paciência em seus estudos até agora, mas chegou a hora de aprender ainda mais. Chegou a hora de aprender sobre destruição... e caos. O elemento do fogo está ao alcance da sua compreensão, desde que você esteja dispost<o/a> a pagar.\n\nProcure por Kranal Fiss nos Sertões. Ele vive em uma casinha ao norte da Encruzilhada, mas, atenção, se você passar direto e chegar à terra dos elfos noturnos, terá ido longe demais.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1524] = {title = "Clamor do Fogo", objective = "Leve a Tocha da Chama Latente para Telf Joolam em Durotar.", description = "Você tem poder suficiente para empunhar o totem do fogo, mas isso não significa que esteja pront<o/a>.\n\nNas margens do Rio Furiaustral, em Durotar, antes do Grande Oceano, fica uma trilha oculta que leva a um dos picos mais altos da região. Faça o caminho até o cume e encontre o altar sagrado vigiado por Telf Joolam, o Altar da Chama Latente.\n\nEntregue e ele esta tocha. Ela mostra que você está tentando domar o fogo. Acenda no braseiro do altar, traga de volta para cá e cuidarei para que você receba o totem do fogo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1525] = {title = "Clamor do Fogo", objective = "Traga 1 Piche Ígneo e 1 Bornal de Reagentes para Telf Joolam em Durotar.", description = "Mas você não veio aqui estudar história, não é? Veio em busca da Chama Eterna e do totem do fogo.\n\nO primeiro passo é criar um sapta e se vincular à chama.\n\nConsigo criar o sapta do fogo se você me trouxer dois itens. Um é o piche ígneo, algo que os feiticeiros Crinavalha dos Sertões Orientais costumam carregar sempre com eles.\n\nO outro é mais perigoso, o bornal de reagentes de um dos sectários da Lâmina Ardente. Eles costumam ficar em uma caverna no alto das ravinas a nordeste do Monte Navalha.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSapta do Fogo"},
[1526] = {title = "Clamor do fogo", objective = "Derrote a Manifestação Menor do Fogo e coloque a Brasa Faiscante no braseiro do Altar da Chama Eterna.", description = "O sapta do fogo está pronto. Chegou a hora de você encarar a chama. Use o sapta no altar e siga até o cume da montanha para encontrar a manifestação lá em cima. Ele não vai atacar enquanto você não estiver pront<o/a>, mas vai perceber que você consegue vê-lo.\n\nAcabe com ele rapidamente, <name>, porque vai ficar mais forte a cada minuto que passa. Depois de derrotá-lo, coloque as brasas faiscantes no braseiro e acenda a tocha.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nTocha da Chama Eterna"},
[1527] = {title = "Clamor do Fogo", objective = "Leve a Tocha da Chama Eterna a Kranal Fiss nos Sertões.", description = "Segure a tocha com firmeza, afaste-se do braseiro e olhe para fora na direção de Durotar. Você vai sentir a empolgação enlaçando seu espírito enquanto o vento entra e obriga a chama a dançar violentamente.\n\nFaça o caminho de volta até os Sertões em busca de Kranal Fiss.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nTotem Calcinante\n\nO seguinte feitiço será lançado em você:\n\nTotem Calcinante\n\nVocê também receberá:\n\nTotem do Fogo"},
[1528] = {title = "O clamor da água", objective = "Encontre Islen Vidente da Água nos Sertões.", description = "A água está no seu futuro, fera. Tô ligado que você já tem a força da fogo e está pront<o/a> para falar com a Islen.\n\nEla mora no litoral dos Sertões. Se tu seguir na direção sul do Rio Furiaustral, vai acabar encontrando ela: é mó tubarão grandão, não tem como errar! Ela tem a força dos elementos e pode te ensinar a dominar a água... se é que tu acha que consegue.\n\nSó se liga para não ir demais para o sul, ela mora logo depois da Vila Catraca.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1529] = {title = "O clamor da água", objective = "Encontre Islen Vidente da Água nos Sertões.", description = "Já faz tempo que você foi testad<o/a>, <name>. Você demonstrou muita paciência em seus estudos até agora, mas chegou a hora de aprender ainda mais. Chegou a hora de aprender sobre a pureza da água.\n\nProcure por Islen Vidente da Água nos Sertões. Ela vive no litoral leste, ao sul do Rio Furiaustral e da Vila Catraca. Ela vai estar em uma cabana de pesca. Boa sorte, <name>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1530] = {title = "O clamor da água", objective = "Encontre Salma nos Sertões Meridionais.", description = "Se tem uma lição que a água pode lhe ensinar, é a seguinte: água significa renascimento.\n\nSeu poder flui, erode e limpa todos os tipos de coisas. Você vai descobrir uma habilidade de cura e reabastecimento incomparável, mas só quando a água é pura.\n\nQuando entender que a água poluída pode destruir tudo em que toca, você vai perceber que ela é um recurso vital.\n\nSe quiser um sapta da água, encontre Salma no coração dos Sertões Meridionais. A casa dela é molestada ininterruptamente pelos javatuscos.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1531] = {title = "Clamor do ar", objective = "Encontre Pradosa Fita Nuvens em Mil Agulhas.", description = "Tu fez um grande progresso, fera. Já tá prontinh<o/a> para mostrar se é dign<o/a> do totem do ar, mas, pra isso, tu precisa encontrar a Pradosa Fita Nuvens em Mil Agulhas. Se tu mostrar respeito, ela pode te ensinar como conseguir um totem do ar.\n\nSegue para o sul em direção aos Sertões e, depois que chegar a Mil Agulhas, segue para o leste. Ela vai tá escondida num buraco com o namorado — haha, não conta pra ela que eu falei isso, ela vai ficar pistola.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nVento Veloz\n\nVocê receberá:\n\nTotem do Ar"},
[1532] = {title = "Clamor do ar", objective = "Encontre Pradosa Fita Nuvens em Mil Agulhas.", description = "Os ventos de Kalimdor se apressam para conhecer você, que com certeza está entre nossos irmãos e irmãs mais poderosos. Muito me orgulha você ter chegado tão longe e ainda ter um futuro tão promissor.\n\nÉ difícil controlar o elemento do ar, mas eu tenho fé que suas habilidades vão superar todos os obstáculos que aparecerem à sua frente.\n\nVá para o extremo sul dos Sertões e entre em Mil Agulhas. É na parte leste da região que você vai encontrar a Pradosa Fita Nuvens. Ela vai ensinar o que você precisa.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nVento Veloz\n\nVocê receberá:\n\nTotem do Ar"},
[1534] = {title = "O clamor da água", objective = "Encha o Odre Azul Vazio nas Ruínas de Poeira Estelar, que fica no Vale Gris, e devolva-o para Salma nos Sertões.", description = "Pegue este odre e embrenhe-se pela densa floresta que os elfos noturnos chamam de Vale Gris. Fica no extremo norte dos Sertões.\n\nDepois que entrar, siga em direção oeste. Margeando as montanhas que separam Torres de Pedra e Vale Gris, depois do Lago Mistral, você vai encontrar um lugar sagrado: as Ruínas de Poeira Estelar. No meio do lago tem uma pequena fonte.\n\nTraga uma amostra da água da fonte para mim. É o último componente que você vai precisar antes que eu mande você de volta a Islen.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1535] = {title = "O clamor da água", objective = "Encha o Odre Marrom Vazio no laguinho abaixo da cabana de Salma e leve para ela nos Sertões.", description = "Para encontrar o conhecimento e ganhar o sapta, você precisa procurar as formas mais puras de água. Ao encontrar as amostras para mim, faça anotações sobre os arredores. Fique atent<o/a> às criaturas que precisam da água, que a protegem. Veja como ela afeta florestas e desertos. Veja como a substância que sustenta a vida obriga criaturas e dar cabo de outras vidas.\n\nVamos começar com uma tarefa simples: abaixo da minha cabana tem um laguinho do líquido precioso. Encha este odre e traga para mim.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1536] = {title = "O clamor da água", objective = "Encha o Odre Vermelho Vazio no poço da Serraria Tarren e entregue-o para Salma nos Sertões.", description = "Muito tempo atrás eu fui com alguns companheiros até o Contraforte de Eira dos Montes. Nós nos defendemos de humanos tenazes que viviam ali e acabamos indo parar em uma cidadezinha que os Renegados haviam tomado: Serraria Tarren.\n\nFoi lá que encontrei algo bastante peculiar: no centro da cidade havia um pequeno poço com água fresca da fonte. Nem sei se aqueles cidadãos pestilentos tinham noção do valor daquilo: um ínfimo vestígio sagrado em um mar de profanidades.\n\nTraga-me uma amostra da água neste odre.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1558] = {title = "O Dique Lapidado", objective = "Leve o órfão ao Dique Lapidado em Loch Modan. Vá com ele até o meio do dique para que ele possa ver por cima da cachoeira.", description = "Sabe Altaforja? Onde todos os anões e gnomos moram? Dizem que lá perto tem uma cidade chamada Loch Modan, onde os anões construíram um dique ENORME. Dizem que é uma das maiores coisas que alguém já construiu. Dá pra imaginar?!\n\nMe leva até Loch Modan pra ver o dique, por favor? Acho que se chama Dique Levi... ahm.. Levitado? Lapidado? Bom, o que eu sei é que é bem grande. Queria tanto poder subir nele e olhar lá de cima da cachoeira. Eu prometo não me inclinar muito pra fora!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1559] = {title = "Receita de bomba de clarão", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nDiagrama: Bomba de Clarão"},
[1560] = {title = "A missão de Tuga", objective = "Leve Tuga até a mulher dele, Tarta.", description = "Estou completamente perdido!\n\nMinha mulher, a Tarta, me mandou buscar o jantar, e eu devo ter errado o caminho.\n\nPode me ajudar a voltar até a Tarta? Ela está me esperando ao sul do Porto de Bondebico.", progress = nil, completion = "Ele não conseguiu voltar sozinho? Típico.\n\nBom, acho que você merece uma recompensa pelo esforço...", rewards = "Você vai poder escolher uma dessas recompensas:\n\nManilhas QuelonianasElo da Grande Tartaruga\n\nVocê também receberá:\n\n"},
[1578] = {title = "Suprimentos para o front", objective = "Entregue 6 Machados de Cobre e 6 Cintos Encadeados de Cobre para Thorvald, na torre de vigia sul de Loch Modan", description = "Os montanhistas de Loch Modan estão combatendo os troggs e andam muito ocupados! Eles vão precisar de um reabastecimento constante de armas e armaduras, se quiserem ganhar a peleja com os troggs.\n\nÉ por isso que o meu sócio, Thorvald, está lá, tentando dar conta da reposição do armamento dos montanhistas. Então, se você pudesse forjar alguns machados e cintos encadeados de cobre e levá-los para o Thorvald, ele ficaria muito agradecido. Talvez ele até divida parte do que sabe com você.\n\nO Thorvald está na torre de guarda sul de Loch Modan.", progress = "Não tenho tempo para conversa fiada. O que você quer comigo?", completion = "Ah, me desculpe! Eu não sabia que você estava aqui para ajudar a reabastecer as tropas. As coisas que você fez serão de grande ajuda!\n\nAqui estão os modelos de alguns armamentos. Espero que sejam úteis para você.", rewards = "Você receberá:\n\nInstruções: Colete Encadeado de Cobre"},
[1579] = {title = "Deschanfradores", objective = "Entregue 8 Deschanfradores para Xafetim Manigiro em Auberdine.", description = "Eu vim para Auberdine porque é um lugar silencioso. Quero inventar minhas invenções onde faz silêncio, porque depois de uma carraspana eu não gosto da barulheira de Altaforja!\n\nEntão peguei o barco para Auberdine e bebi um tiquinho a mais no caminho e acabei deixando a minha caixa de deschanfradores cair no mar. Preciso dos meus deschanfradores!\n\nNão sei onde caíram, mas se você tentar pescar, quem sabe consegue encontrar...Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1580] = {title = "Eletromolas", objective = "Entregue 12 Eletromolas para o Xafetim Manigiro em Auberdine.", description = "Você tem sorte... Mas tem sorte de verdade mesmo, <name>?\n\nCerta noite eu estava andando pelas ruas da Costa Negra, procurando um rio ou lago para testar o meu engabela-patos explosivo com autopropulsão. Só que eu devia estar meio ébrio... porque desmaiei e acordei horas depois em Auberdine. Meu cabelo estava chamuscado e minha bolsa de eletromolas tinha desaparecido! Devo ter deixado cair na água!\n\nPreciso das minhas eletromolas! Será que você consegue pescar eles em algum riacho ou lago da Costa Negra.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nPeixescópio Aquadinâmico"},
[1581] = {title = "Elixires para os Folhâminas", objective = "Traga 6 Elixires de Força de Leão e 2 Elixires de Defesa Menor para Syral Folhâmina, em Dolanaar.", description = "Nossas lojas de plantas e alquimia estão lucrando incrivelmente, mas meu marido gasta tanto tempo preparando poções que nunca temos tempo para nós.\n\nPoderia nos ajudar?\n\nSe puder me trazer um suprimento de elixires já preparados, ficarei grata em realizar uma troca. Tenho umas plantas raras que talvez lhe sejam úteis.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nRaiz-telúricaMagi-real"},
[1582] = {title = "O colete brilho da lua", objective = "Leve 1 par de Luvas de Couro com Relevo, 5 pares de Botas de Couro com Relevo e 5 Mantos de Couro com Relevo para Lotherias em Darnassus.", description = "Nós de Darnassus, especialmente os artesãos, estamos sempre ocupados. Os jovens aventureiros desejam se colocar à prova no mundo lá fora e estão sempre precisando de equipamento.\n\nSe puder me ajudar a cuidar de todos os pedidos de artigos de couro, talvez eu lhe ensine uma técnica de couraria avançada.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nMolde: Colete Brilho da Lua"},
[1598] = {title = "Ladrão que rouba ladrão...", objective = "Tome Poderes do Caos para Drusilla La Salle.", description = "Você aí! <name>, não é?\n\nEspero que você queira mesmo se tornar <um/uma> brux<o/a>, porque se não conseguir segurar a barra, você já era.\n\nEu vou treinar você, mas antes preciso ter certeza de que você tem força suficiente.\n\nUm noviço que estudava na Abadia fugiu para entrar para a Irmandade Défias. Eu não daria a mínima para isso, mas eles roubou livros importantes.\n\nPor coincidência, um deles é um livro que eu queria: Poderes do Caos. Vá lá roubar para mim. Ele está escondido na tenda deles, nos Vinhedos.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nEvocar Diabrete\n\nO seguinte feitiço será lançado em você:\n\nEnsinar Evocar Diabrete\n\nVocê também receberá:\n\n"},
[1599] = {title = "Primeiros passos", objective = "Leve 3 Patuás de Penas para Alamar Carranca em Sidermar.", description = "Você deve ser <o/a> nov<o/a> brux<o/a> de quem todos estão falando à boca miúda. Alguém deve gostar mesmo de você, para virem me pedir para fazer uma pausa na minha pesquisa e começar o seu treinamento.\n\nNão precisa se preocupar, eu sei que a culpa não é sua.\n\nO feitiço mais simples de evocação é o do diabrete. No entanto, antes de dividir isso com você, você tem que provar que tem a habilidade física e o poder mágico necessários.\n\nTraga aqui três amuletos de penas dos noviços jubafria que estão na caverna a sudoeste.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nEvocar Diabrete\n\nO seguinte feitiço será lançado em você:\n\nEnsinar Evocar Diabrete\n\nVocê também receberá:\n\n"},
[1618] = {title = "Armamentos para Cristarrubra", objective = "Tormus Baixaforja quer que você dê 4 Cintos Rúnicos de Cobre e 4 Malhos Pesados de Cobre para Vervo Obom, em Cristarrubra.", description = "Os anões não são os únicos que estão em guerra. Nossos aliados humanos também estão na peleja!\n\nCristarrubra está sob ataques constantes dos orcs Rocha Negra, e o estoque de equipamentos deles está sempre baixo. Se ajudá-los, tenho certeza que o ferreiro deles, Vervo Obom, ensinará uma coisinha ou outra para você.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInstruções: Peitoral de Altaforja"},
[1638] = {title = "Treinamento de guerreiro", objective = "Fale com Ari Barbotina.", description = "Você provou ser u<m/ma> guerreir<o/a> capaz, <name>. Mas você ainda é jovem e tem muito a aprender.\n\nFale com Ari Barbotina. Ele geralmente fica na taberna Javatusco Saltitante, na Cidade Velha de Ventobravo. Além de veterano, o Ari é um bom professor. Ele pode te ensinar umas técnicas que vão te ajudar a manter o fôlego por mais tempo.", progress = nil, completion = "Olá! Venha beber comigo, jovem. Você tem essa cara de durão aí, mas vamos ver quantos copos do especial do Javatusco Saltitante você aguenta mandar para dentro!\n\nOu... você veio aprender?", rewards = "Você receberá:\n\n"},
[1639] = {title = "Bartolino, o bebum", objective = "Fale com Bartolino.", description = "Sim, eu posso te ensinar, sim. Mas antes quero ver o que você já sabe.\n\nEstá vendo aquele bebum lá no canto? É o Bartolino. Eu quero que você vá lá tomar a caneca dele.\n\nSe você conhece o Bartolino, sabe que ele não vai deixar isso barato.", progress = nil, completion = "Olá, gracinha! O que uma coisa fofa que nem você veio fazer no Javatusco Saltitante? Cuidado, senão vai acabar quebrando a unha...", rewards = "Você receberá:\n\n"},
[1640] = {title = "Que surra, Bartolino!", objective = "Vença o Bartolino e converse com ele.", description = "Você quer o quê? Endoidou, foi?\n\nSe quiser essa caneca, vai ter que me matar antes...", progress = nil, completion = "Você é mais duro na queda do que parece!", rewards = "Você receberá:\n\n"},
[1641] = {title = "Tomo de Divindade", objective = "Aqui em Ventobravo, eu sou responsável por botar os paladinos na trilha da virtude maior enquanto eles se dedicam à Luz e à defesa de Azeroth do Flagelo e qualquer ameaça.\n\nChegou a hora de você dar o primeiro passo rumo à verdadeira virtude. Se aceitar, eu darei a você o Livro de Divindade. Por meio do estudo dele, você aprenderá sobre a Luz e sobre o que se espera de você. Se for capaz de entender o que ele ensina e tiver a força necessária, você poderá obter habilidades ainda mais poderosas.", description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nTomo de Divindade"},
[1642] = {title = "Tomo de Divindade", objective = "Fale com Benedito Brião em Ventobravo.", description = "Acima de tudo, um paladino deve refletir a Luz que alimenta nossa força. A nossa determinação para alcançar a divindade não se manifesta na luta para ser um deus, mas pela bondade em toda e qualquer ação.\n\nOs tempos são duros e clamam por nós para punir o mal, mas você precisa se lembrar sempre de que é o socorro prestado aos outros que verdadeiramente separa um paladino dos habitantes de Azeroth. Compaixão, paciência, bravura... são valores que significam tanto para nós quanto a força na batalha.\n\nSaiba-o em seu coração, e nunca se esqueça.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1643] = {title = "Tomo de Divindade", objective = "Procure Stephanie Turner no Distrito Comercial de Ventobravo.", description = "Portanto, <name>, sua tarefa é achar alguém aqui em Ventobravo que precise de ajuda. Alguém que tenha coração puro e determinação. Talvez alguém que esteja ajudando o próximo. Ou alguém que precise de um guia. Seja como for, é preciso que seja alguém com intenções nobres e que dependa da sua gentileza.\n\nFaça isso e venha falar comigo quando terminar. Aí, conversaremos mais sobre o caminho à sua espera e o que significa ser paladino da Luz.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1644] = {title = "Tomo de Divindade", objective = "Leve 10 pedaços de Linho para Stephanie Turner em Ventobravo.", description = "Pobrezinhos, a maioria nem família tem mais, depois dos ataques do Flagelo nos últimos dez anos.\n\nMeu marido João coleta a matéria-prima e eu faço calças e camisas para as crianças, mas ainda não tive tempo. Tenho me concentrado em pôr comida no prato delas e garantir um teto para as coitadinhas.\n\nComo é que é? Você quer me ajudar? Ai, graças à Luz. Muito obrigada, <name>.\n\nSe você pudesse trazer um pouco de linho para eu costurar roupas para as crianças, seria de grande ajuda.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1645] = {title = "Tomo da Divindade", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nTomo de Divindade"},
[1646] = {title = "Tomo da Divindade", objective = "Fale com Tiza Beloforja em Altaforja.", description = "Acima de tudo, um paladino deve refletir a Luz que alimenta nossa força. A nossa determinação para alcançar a divindade não se manifesta na luta para ser um deus, mas pela bondade em toda e qualquer ação.\n\nOs tempos são duros e clamam por nós para punir o mal, mas você precisa se lembrar sempre que é o socorro prestado aos outros que verdadeiramente separa um paladino dos habitantes de Azeroth. Compaixão, paciência, bravura... são valores significam tanto para nós quanto a força na batalha.\n\nSaiba-o em seu coração, e nunca se esqueça.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1647] = {title = "Tomo da Divindade", objective = "Vasculhe o anel externo de Altaforja para João Turner.", description = "Portanto, <name>, sua tarefa é achar alguém aqui em Altaforja que precise de ajuda. Alguém que tenha pureza de coração e determinação. Talvez alguém que esteja ajudando uma outra pessoa. Ou alguém que precise de um guia. Seja como for, é preciso que seja alguém com uma intenção nobre e que dependa da sua gentileza.\n\nFaça isso e venha falar comigo quando terminar. Aí, conversaremos mais sobre o caminho à sua espera e o que significa ser paladino da Luz.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1648] = {title = "Tomo da Divindade", objective = "Leve 10 peças de Linho para João Turner em Altaforja.", description = "Eu e minha esposa cuidamos de um orfanato em Ventobravo, para atender principalmente crianças que perderam os pais nos ataques do Flagelo dos últimos dez anos. Eu venho a Altaforja mais ou menos uma vez por mês para coletar suprimentos dos habitantes daqui, e converso com diversos grupos, para ver se eles podem contribuir de alguma forma.\n\nEsta viagem tem sido agonizantemente lenta, sem falar em pouco produtiva.\n\nSe eu conseguisse pegar mais linho para minha esposa fazer umas roupas, eu poderia finalmente mandar um carregamento para casa.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1649] = {title = "Tomo da Bravura", objective = "Fale com Benedito Brião em Ventobravo.", description = "A bravura, e todas as virtudes que a acompanham, são como joias raras: é preciso reconhecer o valor que elas têm e cuidar bem delas, mas também é preciso coletá-las com cuidado, e lapidá-las. Quando polidas da forma correta, força nenhuma é capaz de quebrá-las.\n\nSua força, sua fé, sua bravura, devem ser postas à prova. Você tem que aprender a nunca perder a fé e perceber que, com o poder da Luz, é capaz de superar coisas que a maioria consegue.", progress = nil, completion = "Ah, você veio solicitar um teste de bravura. Esplêndido.\n\nHá muitas coisas a serem feitas na cidade e nas redondezas que representam grandes desafios, e alguém com a sua habilidade poderia ser de grande ajuda.\n\nNão negligencie esta provação, &lt;name&gt;. Assim como muitos outros em nosso caminho, ela pode custar nossas vidas. A Igreja sempre tem interesse em ampliar suas fileiras, mas compreende que sacrifícios são necessários para que os paladinos que a seguem sejam dignos.\n\nVocê está pronta?", rewards = "Você receberá:\n\n"},
[1650] = {title = "Tomo da Bravura", objective = "Encontre Dafne Calmafonte no Cerro Oeste.", description = "Ótimo! Sua falta de hesitação demonstra que você está pront<o/a> e determinad<o/a> para provar sua força. Que assim seja.\n\nNo fim da Praia Grande do Cerro Oeste, há uma pequena casa de frente para o mar. Um casal vive lá, os Calmafonte. Jardel, um artesão incrível, foi para Altaforja passar algum tempo com os anões. Com isso, sua esposa, Dafne, ficou sozinha e sem proteção.\n\nRelatórios sugerem que os Défias praticamente tomaram o controle da área. Dafne talvez precise de ajuda para se proteger dos bandidos. Vá até lá.", progress = nil, completion = "Olá, &lt;name&gt;. Com toda essa armadura e esse armamento, suponho que você esteja aqui para tentar se apossar da minha terra. Ou isso, ou a Igreja finalmente mandou alguém para me proteger enquanto o Jardel não está.\n\nBem, eu sou Dafne Calmafonte. É um prazer.\n\nNão, você não está tão atrasada assim. Eu só matei meia dúzia dessa corja desde a última alvorada. Eles estão tendo um pouco de dificuldade para chegar aqui, mas como provavelmente viram você chegando, devem estar aqui a qualquer instante.", rewards = "Você receberá:\n\n"},
[1651] = {title = "Tomo da Bravura", objective = "Proteja Dafne Calmafonte do ataque dos Défias.\n\nPara obter êxito, seus espíritos não podem ser despidos do lodo mortal.\n\nQuando você conseguir, fale novamente com Dafne Calmafonte.", description = "Eu aposto que logo, logo eles vão estar aqui de novo, então é bom se preparar.\n\nEu tenho uma arma na casa. Vou pegar para tentar te ajudar, mas, na maior parte do tempo, você vai estar por conta própria.\n\nNão faço ideia de quantos eles vão mandar, mas tenho certeza de que vão ser mais do que mandaram mais cedo.\n\nRogue à Luz que lhe conceda todas as bênçãos que puder, porque... Ouviu isso? Acho que eles vêm aí.\n\nVamos ficar na casa e lutar!", progress = nil, completion = "Nós conseguimos, &lt;name&gt;! Eu agradeço a sua ajuda. Você se saiu muito bem. Nunca achei que uma &lt;class&gt; pudesse enfrentar tantos inimigos de uma vez.\n\nVocê conseguiu mostrar toda sua bravura mesmo numa situação desfavorável.", rewards = "Você receberá:\n\n"},
[1652] = {title = "Tomo da Bravura", objective = "Fale com Benedito Brião em Ventobravo.", description = "Por favor, fale novamente com Duthorian e avise que eu estou em segurança. Graças a você e à Igreja.\n\nEle deve mandar uma mensagem para Altaforja, para avisar o Jardel de que não há nada a temer.\n\nOh, que saudade do meu marido. Mal posso esperar a volta dele.", progress = nil, completion = "Nos encontramos de novo, &lt;name&gt;, e vejo que você está num estado de ânimo excelente.\n\nA casa dos Calmafonte terá mais um dia de paz, e pelo seu relato, não faltaram perigos e ameaças. Muito bem.\n\nDafne e Jardel ajudaram muito a Igreja, e pedir a você que protegesse a casa, e também as vidas deles, era o mínimo que podíamos fazer.\n\nVocê receberá uma recompensa pela bravura demonstrada.", rewards = "Você irá aprender:\n\nDetectar Morto-vivo\n\nO seguinte feitiço será lançado em você:\n\nDetectar Morto-vivo\n\nVocê também receberá:\n\nBastião de Ventobravo"},
[1653] = {title = "A prova da retidão", objective = "Fale com Jardel Calmafonte em Altaforja.", description = "A bem da verdade, não duvido que o próprio Jardel queira agradecer pessoalmente, e como eu preciso mesmo de um mensageiro para avisar que a esposa dele está segura, acho que você seria a escolha ideal.\n\nJardel se preparava para se tornar um paladino da Luz quando a dedicação ao ofício do pai o levou por outros caminhos. Ele está muito bem familiarizado com o nosso modo de vida e é considerado um dos mais fervorosos entre nós.\n\nEle ainda deve estar em Altaforja, e gosta de trabalhar fora dos portões durante o dia. Segundo ele, tem a ver com a brisa fresca da montanha.", progress = nil, completion = "Bom dia, &lt;name&gt;. A Luz caminha com você, não é?\n\nO que você disse? O Benedito mandou você?! E minha esposa, como ela está? Eu sabia que haveria problemas se eu saísse. Por favor, diga que ela... Oh, graças à Luz. Fico muito mais tranquilo sabendo que ela está bem. Os Défias já eram uma ameaça muito antes desses ataques, mas agora os conselheiros do Rei não terão como negar.\n\nO que mais o Benedito disse?\n\nFoi você quem protegeu a minha esposa? Então obrigado mais uma vez, &lt;name&gt;. Eu não conseguiria pensar em prova de bravura melhor que essa.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[1654] = {title = "A prova da retidão", objective = "Usando as Anotações do Jardel sobre Armas, encontre Madeira de Carvalho de Pedralva, o Carregamento de Minério Refinado do Bailor, o Martelo de Ferreiro de Jardel e uma Gema Kor. Depois, entregue tudo a Jardel Calmafonte em Altaforja.", description = "Eu gostaria de lhe oferecer uma recompensa, se você aceitar. Não me entenda mal, minha generosidade depende de quanta ajuda você pode me dar, mas eu acho que nós dois vamos nos beneficiar muito de uma aliança assim.\n\nEu vou criar para você uma arma digna do que você alcançou, um símbolo do paladinismo e da Luz, mas você terá que conseguir os itens que eu preciso para criá-lo. Eu já comecei a fazer uma lista dos itens, agora só preciso de alguém com coragem e habilidade para obtê-los. O que me diz, <class>?O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[1655] = {title = "Carregamento de minério do Bailor", objective = "Leve o Carregamento de Minério do Jardel até Bailor Manopedra, em Loch Modan.", description = "Bá, tu me espavoriu, bagual! Não viu que eu estava pensando na morte da novilha?\n\nO que é isso? Jardel mandou um carregamento... para Altaforja. Ah, sim, sei do que tu está falando. Ogros Mo'grosh... Foram esses esquentados que levaram o carregamento de minério do meu velho camarada. A caravana dele estava indo daqui para o Pantanal e caiu numa esparrela danada. O único galego que sobreviveu morreu na semana passada. O curador não conseguiu socorrer a caravana a tempo.\n\nBueno, se tu quiser pegar o carregamento, vá para nordeste, para lá do lago. Deve estar ali em algum lugar.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nCarregamento de Minério Refinado do Jardel"},
[1656] = {title = "Serviço pela metade", objective = "Leve a Trouxa de Peles para o Estalajadeiro Kauth na Aldeia Casco Sangrento.", description = "Por favor, <name>, eu imploro. Você pode me ajudar? Não terminei meus afazeres na Chapada – estou no meio do meu Rito de Força – e meu pai pediu para entregar essas peles na estalagem de Casco Sangrento.\n\nEstou no meio do rito, da tarefa do meu pai e não descansei nada. Você pode ir no meu lugar?\n\nA estalagem é agradável, um lugar perfeito para descansar durante suas longas viagens. Tenho certeza de que você vai concordar quando puser os olhos nela.", progress = "Sim, jovem? Como Kauth pode te ajudar?\n\nA jornada da Chapada até aqui pode ser uma provação tremenda para alguns, mas uma visão agradável para outros. E você, o que achou?", completion = "Ah, as peles que eu estava esperando. Obrigado.\n\nChegaram mais rápido do que eu esperava. Elas vão dar belos cobertores para qualquer um que queira ficar aqui.\n\nFique à vontade para permanecer o quanto quiser. Minha estalagem está sempre aberta e qualquer um do nosso povo é bem-vindo. E não subestime uma noite de descanso. Pode fazer toda a diferença quando você está viajando.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nNaco de Pão DuroÁgua Refrescante da Fonte\n\nVocê também receberá:\n\n"},
[1657] = {title = "Fedor na Costa Sul", objective = "Atire três Bombas Fedidas dos Renegados no coração da Costa Sul, em Eira dos Montes, e fale novamente com a Invoca-trevas Yanka no Festival do Homem de Palha, nas Clareiras de Tirisfal.", description = "Celebre a Noturnália com estilo... trazendo dor e tristeza para os nossos inimigos na Costa Sul!\n\nLeve umas dessas bombas fedidas especiais. Ela exala uma fedentina, uma catinga tão terrível que nem humano, nem bicho aguenta o futum. Mas para cumprir sua função, ela tem que ser atirada no coração da Costa Sul, então é bom se preparar para uma briga com a Aliança!\n\nQuando você concluir essa tarefa... esse tributo à nossa libertação, venha falar comigo novamente!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nGostosura de Abóbora da Noturnália"},
[1658] = {title = "Penetra no Festival do Homem de Palha", objective = "Localize o Festival do Homem de Palha nas Clareiras de Tirisfal. Fale novamente com o Sargento Hartman quando tiver feito isso.", description = "Eu preciso de indivíduos corajosos para ir até as Clareiras de Tirisfal a fim de dar uma olhada no Festival do Homem de Palha. Eu não vou mentir, vai ser perigoso. Os guardas de lá são especialmente cruéis. Ainda assim, temos que descobrir qual é o tamanho do festival este ano, e quanto problema os Renegados vão causar na Noturnália.\n\nSe você aceitar a tarefa, vá para Tirisfal e faça o reconhecimento do festival. Venha falar comigo quando tiver informações. Você vai receber uma boa recompensa.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nGostosura de Abóbora da Noturnália"},
[1661] = {title = "O Tomo de Nobreza", objective = "Fale com Benedito Brião em Ventobravo.", description = "Eu <o/a> saúdo mais uma vez, <name>. Mais do que nunca tenho orgulho de <chamá-lo/chamá-la> de <irmão/irmã>, pois sua dedicação lisonjeia todos.\n\nÉ chegada a hora de conceder a você uma grande dádiva. Saiba que, desta vez, nada lhe será pedido em troca, mas, quando chegar a hora, este será um desafio muito maior para todos os paladinos que escolherem assumir a minha missão.\n\nFale comigo novamente e eu concederei a você uma grande bênção.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nEvocar Cavalo de Guerra\n\nO seguinte feitiço será lançado em você:\n\nEvocar Cavalo de Guerra"},
[1665] = {title = "A caneca do Bartolino", objective = "Leve a Caneca do Bartolino para o Barbotina", description = "Tá, tá, toma aqui a minha caneca. Peraí! Deixa só eu terminar... pronto.", progress = "Eu vi sua briga com o Bartolino. Você mandou ver! Pegou a caneca dele?", completion = "Aí está! Ele com certeza não queria abrir mão do utensílio, mas você foi persuasivo o suficiente. Muito bem.\n\nAgora, hora da lição...", rewards = "O seguinte feitiço será lançado em você:\n\nCaminho da Defesa\n\nVocê receberá:\n\n"},
[1666] = {title = "Delegado Fadigas", objective = "Fale com o Delegado Fadigas.", description = "Você aprendeu algumas técnicas, mas uma arma decente viria a calhar. Aposto que se você falar com o Delegado Fadigas ele vai te ajudar.\n\nO Delegado Fadigas mora numa mansão na Floresta de Elwynn, na Madeireira Vale do Leste. Ele está aposentado, mas acumulou muitos equipamentos ao longo da carreira.\n\nDiga que eu te mandei. Talvez ele tenha alguma arma sobrando para você.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1667] = {title = "Diogo Dente-podre", objective = "Recupere o Distintivo do Fadigas que está no acampamento de Diogo Dente-podre e fale novamente com o Delegado Fadigas.", description = "Então foi o Barbotina que mandou você, hein? Sim, eu tenho umas armas sobrando aqui, mas antes tenho uma tarefa para você...\n\nAlgum tempo atrás, o bandido Défias Diogo Dente-podre me ludibriou. Estava escuro, ele estava usando um tabardo de Ventobravo e eu acabei achando que era o Delegado Durão. Ele disse que precisava do meu velho distintivo de Delegado de Ventobravo, e eu acabei entregando... só para assistir enquanto ele gargalhava e fugia!\n\nRecupere o meu distintivo! O Diogo Dente-podre provavelmente o escondeu no acampamento ao sul daqui.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nMachado de FadigasMartelo do FadigasAdaga de FadigasEspada de Fadigas\n\nVocê também receberá:\n\nChave do Dente-podre"},
[1678] = {title = "Vejrek", objective = "Leve a Cabeça de Vejrek para Muren Lançatroz em Altaforja.", description = "Se quiseres ganhar moral entre os guerreiros, é bom ser dur<o/a> na queda. Eu posso te ensinar umas coisinhas, mas antes, tu tem que provar que aguenta trocar umas bifas com um inimigo de verdade.\n\nO Vejrek é o troll mais casca-dura dessas bandas, e tu vai ter que dar uma sova nele. Ele mora numa cabana no contraforte logo ao sul da Fortaleza Jubafria.\n\nMe traz a cabeça do Vejrek, aí eu vou saber que tu aguenta o tranco.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nCaminho da Defesa\n\nVocê receberá:\n\n"},
[1679] = {title = "Muren Lançatroz", objective = "Fale com Muren Lançatroz.", description = "Olá, guerreir$Go:a;! Você parece um tanto promisso$Gr:ra;, mas ainda tem muito a aprender, e Muren Lançatroz é o anão certo pra te ensinar.\n\nO Muren fica no Salão das Armas, na Ala Militar de Altaforja. Fala com ele quando tiver pront$Go:a; pra aprender o que é ser guerreir<o/a> de verdade.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1680] = {title = "Tormus Baixaforja", objective = "Fale com Tormus Baixaforja.", description = "Agora que você aprendeu algumas técnicas, hora de arranjar uma arma decente. Fale com Tormus Baixaforja. Ele vai fazer uma arma que eu sei que você vai gostar.\n\nO Tormus vive aqui em Altaforja, na Grande Forja.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1681] = {title = "Complexo do Bandaferro", objective = "Leve um pouco de Minério Umbrático para Tormus Baixaforja em Altaforja.", description = "Meu mentor, Robusto Bandaferro, tinha um complexo ao sul do Lago Helm, no leste de Dun Morogh. Ele dava aulas de ferraria lá. Ninguém chegava aos pés dele.\n\nUm dia, um dos alunos levou pra ele um bocado dum minério estranho. O mestre Bandaferro chamou de minério umbrático e ficou doido pra trabalhar com ele, mas não demorou nada e... um grupo de anões Ferro Negro atacou a escola dele. Não deixaram um pra contar a história!\n\nVai lá no complexo do Bandaferro e vinga o meu mestre, <name>. Aproveita e traz um pouco do tal minério umbrático que causou isso.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1682] = {title = "Armas de ferro cinza", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nMachado ÚmbreoMaça ÚmbreaAdaga ÚmbreaEspada Úmbrea\n\nVocê também receberá:\n\n"},
[1683] = {title = "Vorlus Cascruel", objective = "Leve a Trompa de Vorlus para Elanaria em Darnassus.", description = "Para eu poder lhe ensinar, você terá que provar sua determinação.\n\nO sátiro Vorlus Cascruel achou um dos nossos poços lunares mais distantes, a sudeste da Gruta de Ban'ethil. Agora ele se banha lá, conspurcando as águas imaculadas com sua imundície.\n\nEncontre-o no poço lunar e acabe com Vorlus. Traga a trompa dele e nós daremos início ao seu treinamento avançado de guerreir<o/a>.\n\nA trilha que leva ao poço lunar é bem escondida, <name>, mas basta se concentrar e se manter alerta para encontrá-la.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nCaminho da Defesa\n\nVocê receberá:\n\n"},
[1684] = {title = "Elanaria", objective = "Fale com Elanaria.", description = "Saudações, guerreir<o/a>. Sua habilidade aumenta, mas há muito mais envolvido no ofício do que você imagina. Para avançar mais, logo será preciso encontrar um mestre.\n\nA guerreira Elanaria vive em Darnassus, no Terraço dos Guerreiros. Ela poderá instruir você.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1685] = {title = "Convocação de Gakin", objective = "Apresente-se a Gakin, o Neromante, no Distrito dos Magos de Ventobravo.", description = "Não é sempre assim? Procurei você o dia todo e não encontrei e, agora, quando menos espero, você aparece na minha frente.\n\nJá perdi muito tempo tentando te achar, por isso não vou fazer você perder mais tempo. O mestre Gakin convocou você para um treinamento adicional. Me parece que é melhor você ir até ele quanto antes.\n\nEle está no porão da taverna Cordeiro Imolado. Inclusive, é uma boa ideia evitar alarde sobre aquele lugar. Não quero que todo mundo fique sabendo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1686] = {title = "O vulto de Elura", objective = "Leve 8 cargas de Minério de Elunita e o Medalhão de Elura para Elanaria em Darnassus.", description = "Você aprendeu muito, <name>. Agora, criaremos uma arma para você. Uma arma de guerreiro.\n\nPara isso, você tem que ir até a Costa Negra.\n\nTempos atrás, um navio se chocou contra as rochas perto do farol de Auberdine, espalhando várias caixas de minério de elunita pelo leito oceânico.\n\nPrimeiro, você terá que derrotar a guardiã da elunita, o Vulto de Elura. Ela era a capitã do navio, então se amaldiçoou a vagar pelas profundezas a fim de proteger o carregamento até o fim dos tempos. Derrote-a, tome o medalhão dela e colete as caixas perdidas de elunita.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1687] = {title = "Farol assombrado", objective = "Leve o órfão ao Farol, na costa de Cerro Oeste.", description = "Têm algumas noites em que as mães-dos-órfãos contam histórias de terror pra gente no orfanato. São as MELHORES noites! Uma das histórias fala de um fantasma que assombra o farol de Cerro Oeste. Dizem que ele era um capitão pirata que perdeu o navio pra outros piratas. Sinistro, né?!\n\nA gente pode ir até o farol de Cerro Oeste? Quero ver se esse fantasma pirata existe mesmo! Eu prometo não me afastar de você... Nem sair correndo ou algo assim!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1688] = {title = "Surena Caledon", objective = "Recupere a Gargantilha de Surena e entregue-a a Gakin, o Neromante, em Ventobravo.", description = "Eu tive uma aluna chamada Surena Caledon. Ela estava na posição que você ocupa agora, sedenta por aprender a magia dos bruxos, e demonstrava muito talento. Além disso, ela era jovem e um verdadeiro colírio. Eu bem que devia ter ficado de olhos abertos naquela pilantra!\n\nA rampeira farsante fugiu com Erlan Mourejardo, um Défias. Ela mesma não tem nenhuma importância, mas eu a presenteei com uma gargantilha de pedra-sangrenta que preciso ter de volta.\n\nA vida dela não significa nada para mim. Ela agora está na Plantação de Abóboras dos Braga. Vá buscar o que é meu.", progress = "Mesmo os mais velhos e sábios podem ficar à mercê dos ardis da juventude e da beleza, <name>. Isso é algo para você se lembrar por toda a sua vida.", completion = "Surena era talentosa, mas não o suficiente para dominar a magia dos bruxos por conta própria. É uma pena ver tanto talento desperdiçado, mas o que há de se fazer?\n\nEspero que investir em você me traga resultados diferentes.", rewards = "Você receberá:\n\n"},
[1689] = {title = "A vinculação", objective = "Usando a Gargantilha de Pedra-sangrenta, evoque e sobrepuje um emissário do caos. Depois, devolva a Gargantilha de Pedra-sangrenta a Gakin, o Neromante, em Ventobravo.", description = "Recuperando minha gargantilha e sobrepujando Surena você provou ter ao menos tanto potencial quanto ela.\n\nNo entanto, é visível que você precisa de treinamento. Sim, você é capaz de controlar fluxos mágicos, mas ser u<m/ma> brux<o/a> é muito mais que isso.\n\nEu mostrarei a você como dominar um emissário do caos, mas antes você terá que trazer um num círculo de evocação e o subjugar usando quaisquer armas – físicas e mágicas – à sua disposição. Use a magia da gargantilha no círculo de evocação logo abaixo.", progress = "Só posso lhe mostrar como controlar um emissário do caos quando você subjugar um.", completion = "Você se saiu melhor do que eu esperava, &lt;name&gt;, mas tão bem quanto eu gostaria. Agora você já pode aprender a controlar o emissário do caos que subjugou, um aliado que será muito útil nas provações com as quais você certamente vai se deparar.\n\nNão tenho mais nada para lhe ensinar no momento, mas é certo que voltaremos a nos falar, &lt;name&gt;.", rewards = "Você irá aprender:\n\nEvocar Emissário do Caos\n\nO seguinte feitiço será lançado em você:\n\nEnsinar Evocar Emissário do Caos\n\nVocê também receberá:\n\n"},
[1690] = {title = "O erro dos Errantes", objective = "Elimine 10 Bandidos e Ladrões Errantes a leste de Geringontzan, depois fale novamente com o Engenheiro-chefe Silvadágua.", description = "Ô, psiu! É, você! Chegou a sua vez de contribuir com a Companhia de Saneamento de Geringontzan.\n\nOs nômades Errantes no deserto de Tanaris tomaram quase todas as nascentes! Sem poder chegar nelas, a gente vai ficar sem água rapidinho. Se quiser fazer uma graça pra gente, dá um jeito naqueles nômades duma figa!\n\nÉ só ir pro leste e acabar com dez ladrões e bandidos Errantes, aí você passa aqui na volta. Esses caras vão aprender a não se meter com a gent... digo, com você!", progress = nil, completion = "Isso é que é eficiência! Agora que você deu uma diminuída na quantidade de nômades, a gente pode começar a pensar num jeito de tomar as nascentes de volta.\n\nA Companhia de Saneamento de Geringontzan agradece a ajuda, mas a gente ainda tá longe de acabar de uma vez com aqueles nômades imundos. Chega mais, vou te falar qual é o nosso próximo passo...", rewards = "Você receberá:\n\n"},
[1691] = {title = "O enterro dos Errantes", objective = "Elimine 10 Magos das Sombras Errantes, 8 Ladinos Errantes e 6 Assassinos Errantes para o Engenheiro-chefe Silvadágua em Geringontzan.", description = "Agora a gente precisa que você vá pra cima dos nômades Errantes mais parrudos. Você tem feito um bom trabalho, mas pra tomar as nascentes de volta, a quantidade deles tem que ser a mínima possível!\n\nDesta vez, você vai ter que apagar os ladinos, assassinos e magos das sombras deles. Eles ficam a leste e a sudeste daqui.\n\nNão vou mentir não, eles são barra-pesada. Mas vai lá, ajuda a gente a recuperar o que é nosso por direito!", progress = nil, completion = "Supimpa, &lt;name&gt;... Supimpa! Agora que você diminuiu o número de nômades, a gente pode ir com tudo pra cima deles!\n\nCaso você não tenha falado com o Operador de Torneira Rastamala dá um oi lá nele. O cara paga uma recompensa pra cada bolsas de água dos Errantes que você encontrar na hora de fazer aqueles nômades sentirem o gostinho da justiça!", rewards = "Você receberá:\n\n"},
[1692] = {title = "Mathiel, o ferreiro", objective = "Leve o Caixote de Elunita para Mathiel, o Ferreiro.", description = "Eu acondicionei a elunita que você trouxe. Leve-a para o ferreiro Mathiel e ele criará uma arma de elunita para você.\n\nMathiel está ao sul, do outro lado desta estrutura.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1693] = {title = "Armas de elunita", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nMachado de ElunitaMartelo de ElunitaAdaga de ElunitaEspada de Elunita\n\nVocê também receberá:\n\n"},
[1698] = {title = "Yorus Cervevada", objective = "Fale com Yorus Cervevada.", description = "Yorus Cervevada lançou um desafio a todos os guerreiros aptos da Aliança. Ele está na Estalagem da Vila Plácida, em Cristarrubra, e oferece uma considerável recompensa a quem passar pela provação que ele propõe.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1699] = {title = "Desafio da Retibana", objective = "Entre nas Cavernas Retibanas, chegue à primeira bifurcação e volte até Yorus Cervevada no tempo estipulado.\n\nMorrer e libertar o espírito não é permitido.", description = "Eu batizei esse desafio como Desafio da Retibana. Pra passar por ele, tu só tem que entrar nas Cavernas Retibanas, ir até a primeira bifurcação e voltar aqui. Mais fácil que tirar doce de guri! Só tem uns detalhezinhos...\n\nTu tem uma hora pra voltar.\n\nTem uma pá de gnolls cristarrubra no caminho.\n\nAntes de começar, tu tem que virar um copo do drinque especial da minha família: o grogue Cervevada!\n\nE aí, o que me diz? As Cavernas Retibanas ficam no contraforte ao norte da Vila Plácida. Tu tem peito pra encarar a Provação Retibana ou o quê?Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1700] = {title = "Grimand Elmore", objective = "Leve as Notas de Furen para Grimand Elmore.", description = "Eu fiz uma cópia das anotações com as técnicas que aprendi enquanto fazia tua armadura, agora preciso mandar pro Grimand Elmore. Tu acha ele no Distrito dos Anões, na loja de armas.\n\nÉ bom ficar espert<o/a> na hora que entregar as anotações pra ele: orgulhoso como é, aposto que ele não vai gostar nada de aprender alguma coisa comigo...Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1701] = {title = "Malha endurecida a fogo", objective = "Colete as matérias-primas pedidas por Furen Barbalonga e leve-as para ele em Ventobravo.", description = "Te arrumei um belo escudo, mas a gente não tem que parar nele. Eu sei como criar uma armadura de malha buenaça, de primeira. Não é fácil, e requer umas matérias-primas raríssimas, mas se tu tiver a disposição pra ir atrás delas, eu te ajeito.\n\nO que me diz, <name>? Se tu topar o desafio, eu te dou uma lista do que vou precisar e onde tu pode arrumar.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[1702] = {title = "O fabricante de escudos", objective = "Leve o Barril de Grogue para Furen Barbalonga.", description = "Você mostrou mesmo que tem fibra ao enfrentar o desafio da Retibana, <name>. E eu sou anão de palavra. Vou te dar a recompensa...\n\nMas ela está em Ventobravo.\n\nFala lá com o meu amigo, Furen Barbalonga, no Distrito dos Anões em Ventobravo. Ele é o melhor fabricante de escudos que tu vai ver na vida, e ele me deve um favor.\n\nAqui, leva esse barrilete de Grogue Cervevada e fala com ele. Ele vai te dar um baita de um escudo.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nBenevolência de Furen"},
[1703] = {title = "Mathiel", objective = "Leve as Notas de Furen para Mathiel.", description = "Fiz uma cópia das minhas anotações sobre as técnicas que eu aprendi enquanto fazia a tua armadura, e eu preciso que elas sejam entregues ao ferreiro Mathiel. Ele mora em Darnassus. É um tiro longo daqui, mas pode apostar que ele vai dançar o fandango quando vir o que eu aprendi aqui.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1704] = {title = "Cronomorfus Espanafuso", objective = "Leve as Notas de Furen para Cronomorfus Espanafuso.", description = "Fiz uma cópia das minhas anotações sobre as técnicas que eu aprendi enquanto fazia a tua armadura, e eu preciso que elas sejam entregues ao ferreiro Cronomorfus Espanafuso. Ele é um ferreiro gnomo, e mora em Altaforja. Quando ele vir o que eu descobri... aposto que ele vai arrancar a própria barba de empolgação.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1705] = {title = "Sangue Ardente", objective = "Leve 20 ampolas de Sangue Ardente e 1 Pedra Ardente para Grimand Elmore em Ventobravo.", description = "Eu odeio admitir que o Furen possa ter realmente descoberto alguma coisa. Mas, só para garantir, eu preciso experimentar as técnicas dele em uma nova armadura. Se você me ajudar com o material, <name>, pode ficar com a armadura.\n\nPara fazer essa armadura eu preciso de amostras de sangue ardente e uma pedra ardente.\n\nO único lugar que eu conheço para obter isso é a Floresta do Crepúsculo. Os worgens Nocturnos da mina do Descanso de Rolando têm sangue ardente, e o líder deles, o Espirratripa, deve ter a pedra.\n\nQue a sorte <o/a> apresse, <name>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1706] = {title = "Armadura de Grimand", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nCoifa Endurecida pelo Fogo"},
[1707] = {title = "Bolsas de água pra refrescar", objective = "Leve 5 Bolsas de Água do Errante para o Operador de Torneira Rastamala em Geringontzan.", description = "Saudações, <name>. Meu nome é Rastamala, e a gente tem um trabalho importante se você estiver a fim! Oba!\n\nNão tem nada que agrade mais um operador de torneira experiente como eu do que aceitar tudo que é bolsa d'água de Errante que você encontrar pelo deserto. A água é nossa, de qualquer forma; os nômades é que malocaram tudo! A Companhia de Saneamento de Geringontzan vai te dar um kit de sobrevivência oficial pra cada cinco bolsas. Mas o mais importante é que você vai ajudar Geringontzan a ajudar a si mesma!", progress = "Conseguiu as cinco bolsas de água dos Errantes? Se conseguiu, o Pacote de Suprimentos da Companhia de Saneamento de Geringontzan, modelo 103-XB é seu!", completion = "Excelente! Quanto mais bolsa a gente pegar de volta, mais longe a gente fica do desastre! Pode ir trazendo mais que eu te dou um kit pra cada cinco bolsas.", rewards = "Você receberá:\n\nPacote de Suprimentos da Cia. Águas de Geringontzan"},
[1708] = {title = "Coral de Ferro", objective = "Leve 20 peças de Coral Causticante para Cronomorfus Espanafuso em Altaforja.", description = "Eu quero experimentar as técnicas novas do Furen Barbalonga na minha própria armadura. E, como você ajudou o Furen e o Furen me ajudou, pode ficar com a armadura quando eu terminar!\n\nPara terminar a peça eu preciso de um coral especial, causticante. Ele só cresce em um lugar, na costa ao sul do Porto de Menethil, no Pantanal.\n\nSe você me trouxer coral causticante suficiente, eu começo a trabalhar imediatamente na armadura. E anda, <name>. Eu estou louquinho pra começar!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1709] = {title = "Criação do Cronomorfus", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nManoplas Endurecidas pelo Fogo"},
[1710] = {title = "Cascas queimadas de sol", objective = "Leve 20 Cascas Queimadas de Sol para Mathiel em Darnassus.", description = "Meu colega, Furen Barbalonga, fez uma descoberta que eu estou ansioso para testar. Quero experimentar com uma armadura nova que eu estou pesquisando. Acho que a técnica do Furen dará um resultado superior.\n\nVocê pode me ajudar? Se você conseguir as coisas de que eu preciso para fazer a armadura, eu lhe farei uma peça extra.\n\nPreciso das cascas de ovo queimadas de sol das mantícoras de Alcândora, em Mil Agulhas. Procure as cascas abaixo dos ninhos e volte quando tiver uma quantidade suficiente.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1711] = {title = "A armadura de Mathiel", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nPerneiras Temperadas pelo Fogo"},
[1712] = {title = "Ciclonium", objective = "Leve os itens listados no Pergaminho de Bath'rah para Bath'rah, o Vigia dos Ventos, em Alterac.", description = "Passei eras vigiando esse lugar para poder um dia testemunhar a queda de Ciclonium. Todavia, ainda não encontrei campeão capaz de derrotar o senhor dos ventos.\n\nTalvez você seja <esse campeão;essa campeã;.\n\nProve. Obtenha os itens de que eu preciso para evocar Ciclonium, e eu saberei que você é u$gm/ma> grande guerreir<o/a>. Talvez então você possa derrotá-lo e obter o coração rodopiante.\n\nNesse caso, eu infundirei o poder do coração em uma arma e a darei a você.\n\nEis um pergaminho com a lista dos itens de que necessito.", progress = "Conseguiu os itens, <name>? Coletá-os é a prova de que preciso de que você tem a força para enfrentar Ciclonium.", completion = "Você conseguiu os itens para meu feitiço e demonstrou sua força e sagacidade. Mas o próximo estágio dessa missão é ainda mais perigoso.\n\nEsperemos que sua força não lhe falte.", rewards = "Você receberá:\n\n"},
[1713] = {title = "A evocação", objective = "Leve o Coração Rodopiante para Bath'rah, o Vigia dos Ventos.", description = "Estou pronto para realizar o ritual de evocação de Ciclonium. Quando ele for evocado, você precisará derrotá-lo e tomar o coração rodopiante dele. Eu o usarei, então, para infundir uma arma com o poder de Ciclonium.\n\n<name>, está pront<o/a> para enfrentar o senhor dos ventos?", progress = "<name>, eu preciso do coração rodopiante!", completion = "Você conseguiu! Derrotou Ciclonium e tomou o Coração Rodopiante!", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[1714] = {title = "Essência dos exilados", objective = "O caldeirão borbulha. Os vapores chamam...", description = nil, progress = "O caldeirão borbulha. Os vapores chamam...", completion = "Os patuás mergulham lentamente rumo às profundezas do caldeirão...\n\nUma grande bolha ascende à superfície e estoura...\n\n... E um aglomerado revolto de pedra derretida se revela.", rewards = "Você receberá:\n\nEssência dos Exilados"},
[1715] = {title = "O Cordeiro Imolado", objective = "Apresente-se a Gakin, o Neromante, no Distrito dos Magos de Ventobravo.", description = "Hunf. No passado todos os bruxos de Khaz Modan eram treinados sob os auspícios dos mestres de Gnomeregan, mas agora que fomos todos forçados a nos mudar para esse buraco em Altaforja e o Magni resolveu ficar de olho em nós, somos forçados a mandar nossos noviços para treinar em Ventobravo.\n\nFelizmente o Gakin não é nenhum molenga, e você ainda vai aprender o necessário, mas isso me ferve o sangue!\n\nDe qualquer forma, você deve conseguir achar o Gakin no porão do Cordeiro Imolado, lá em Ventobravo.", progress = nil, completion = "A mando do Chavenegra, é? Esses diabos desses bruxos de Altaforja têm é que resolver próprios problemas. O espaço e os professores aqui em Ventobravo têm limites. Com todo esse tráfego a mais, cedo ou tarde com certeza chamaremos a atenção.", rewards = "Você receberá:\n\n"},
[1716] = {title = "Devoradora de almas", objective = "Fale com Takar, o Vidente, nos Sertões.", description = "Uma súcubo é uma devoradora de almas, destruidora de corações, dominadora de mentes. Ela não conhece nada além do mal.\n\nEssa será a criatura que você aprenderá a evocar e controlar, mas ela não pode ser trazida ao nosso mundo sem estímulo.\n\nCom um símbolo de amor como isca ela com certeza responderá o seu chamado. Então, você poderá derrotá-la e transformá-la em uma ferramenta sua.\n\nEntretanto, será difícil encontrar algo dessa natureza. Você devia falar com Takar, o Vidente. Você deve achá-lo nos Sertões de Kalimdor, perto do Acampamento Taurajo.", progress = nil, completion = "Sua travessia foi difícil, &lt;name&gt;? Você viajou bastante para falar comigo, mas não tema. Eu sei por que o Gakin mandou você, e ele fez bem. Eu posso ajudar em sua busca.", rewards = "Você receberá:\n\n"},
[1717] = {title = "Convocação de Gakin", objective = "Apresente-se a Gakin, o Neromante, no Distrito dos Magos de Ventobravo.", description = "<name>! Não esperava encontrar você por essas bandas, mas me orientaram a ficar alerta para o caso de você aparecer.\n\nJá passou da hora de você treinar mais. Você devia ir ver o Gakin quando for a Ventobravo. Ele estará à sua espera.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1718] = {title = "O ilhéu", objective = "Fale com Klannoc Macleod.", description = "Seu renome como guerreir<o/a> esta crescendo, <name>. Chegou a hora de encarar seus pares e ver como se sai.\n\nHá uma ilha perto da costa dos Sertões, ao sul da Vila Catraca. Ela se chama Ilha da Peleja, um lugar onde guerreiros se encontram.\n\nSe encontram e se enfrentam.\n\nFale com o guerreiro chefe da Ilha da Peleja, Klannoc Macleod. Se achar você dign<o/a>, Klannoc <o/a> levará adiante no caminho dos guerreiros.\n\nPara chegar à Ilha da Peleja, procure as partes rasas e os bancos de areia da Costa dos Mercadores.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1719] = {title = "A contenda", objective = "Mate o Grande Gui. Em seguida, fale com Klannoc Macleod na Ilha da Peleja.", description = "Se quiser aprender comigo, primeiro tem que passar pela Contenda. É um desafio que nós da Ilha da Peleja gostamos de apresentar a guerreiros em ascensão que se acham durões. Se você passar no teste, eu saberei que você é dur<ão/ona> mesmo.\n\nPara começar, vá até aquele pessoal ali e pise na grade. Isso vai sinalizar para o Kabe Sashata que você está pront<o/a>, e ele vai mandar os desafiantes contra você.\n\nDerrote todos os desafiantes e o Kabe chamará o Grande Gui. Mate o Grande Gui, e então fale novamente comigo logo depois.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nCaminho do Berserker\n\nVocê receberá:\n\n"},
[1738] = {title = "Palocórdio", objective = "Obtenha o Palocórdio no Vale Gris e leve-o a Gakin, o Neromante, no Distrito dos Magos de Ventobravo.", description = "Preparando-me para sua vinda, usei minha visão para procurar pelo mundo e através das brumas do tempo. Encontrei o que você procura.\n\nNa Guerra dos Antigos, dois amantes caíram juntos em batalha. Onde deram juntos seu último suspiro, cresceu uma árvore conhecida como palocórdio, um símbolo de seu amor imortal.\n\nDeve ser tentação suficiente para uma súcubo. Ela fica nas ruínas de Ordil'Aran, no noroeste da floresta do Vale Gris.", progress = "Sua busca teve sucesso, <name>?", completion = "Sim, posso lhe dizer que funcionará bem. Só me deixe trabalhar nisso um tempinho...\n\nTakar é um camarada bem safo, não é? Se eu conseguisse convencê-lo a voltar para a civilização...", rewards = "Você receberá:\n\n"},
[1739] = {title = "A vinculação", objective = "Usando o Cerne de Palocórdio, evoque e sobrepuje uma súcubo. Em seguida, devolva o Cerne de Palocórdio a Gakin, o Neromante, no Cordeiro Imolado.", description = "Usando o cerne do palocórdio, você conseguirá lançar um feitiço no círculo de evocação na cripta aqui embaixo para abrir um portal para a súcubo.\n\nCuidado, <name>. Súcubos podem sobrepujar até mesmo as mentes mais fortes.\n\nUma vez que você a tenha derrotado e banido de volta para o caos, fale novamente comigo. Mostrarei como convocá-la e controlá-la.", progress = "Às vezes eu me pergunto se a Surena não era uma súcubo disfarçada, enviada para distorcer e confundir minha cabeça... Nah. Estou dando crédito demais a ela...", completion = "Muito bem, muito bem, &lt;name&gt;. Vejo que fiz bem ao resolver treiná-lo. Seu desempenho não decepciona.\n\nNão tenho qualquer objeção a demonstrar a forma de controlar a súcubo recém-vinculada a você. Observe atentamente.", rewards = "Você irá aprender:\n\nEvocar Súcubo\n\nO seguinte feitiço será lançado em você:\n\nEnsinar Evocar Súcubo\n\nVocê também receberá:\n\nBolsinho de Almas"},
[1740] = {title = "O orbe de Soran'ruk", objective = "Encontre 3 Fragmentos de Soran'ruk e 1 Fragmento Grande de Soran'ruk e entregue-os a Doan Karhan nos Sertões.", description = "Quando os demônios da Legião perdem prestígio, os espíritos deles são aprisionados em orbes e distribuídos para os magos e bruxos deles.\n\nMuitos desses foram usados pelos servos da Legião em Azeroth. Mesmo fragmentados, eles ainda têm os poderes dos demônios aprisionados em seu interior.\n\nO orbe de Soran'ruk foi partido em dois pedaços, um dos quais foi ainda mais fragmentado para ser usado pelos acólitos do Martelo do Crepúsculo nas Profundezas Negras. A outra metade permaneceu em posse de um mago da Bastilha da Presa Negra. Encontre todos os fragmentos e eu conseguirei restaurar o orbe.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nOrbe de Soran'rukCajado de Soran'ruk\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[1758] = {title = "Tomo do Conluio", objective = "Fale com Krom Braçoforte em Altaforja.", description = "Vai levar mais que um ciclo simples de evocação para trazer um caçador vil para um desafio.\n\nMeus três acólitos e eu ajudaremos a criar os círculos mágicos. Você vai precisar de uma cópia do Tomo do Conluio, que se perdeu faz certo tempo. Ele foi escrito pelo primeiro grupo de bruxos a evocar um caçador vil sem auxílio.\n\nTalvez Krom Braçoforte possa lhe ajudar. Hoje em dia ele é um bruxo itinerante, e vem passando um tempo colecionando textos antigos e revirando a biblioteca de Altaforja.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1778] = {title = "Tomo da Divindade", objective = "Fale com Tiza Beloforja em Altaforja.", description = "Vou tratar de planejar minha volta para casa em breve. Com sorte a Stephanie está bem lá em Ventobravo. Mal posso esperar para vê-la de novo. Até a próxima, <class>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1779] = {title = "Tomo da Divindade", objective = "Pegue o Símbolo da Vida e fale com Muiredon Beloforja em Altaforja.\n\nItem fornecido:\n", description = "Quero que você leve isso, <name>. Se chama Símbolo da Vida.\n\nVocê se provou caridos<o/a> e paciente, e agora eu quero que prove quão competente se tornou no combate e no uso do poder.\n\nMuiredon, meu marido, voltou recentemente de um confronto com alguns anões Ferro Negro, e mal escapou com vida. Ele está ali, tratando as feridas com um de nossos sacerdotes.\n\nPegue o Símbolo e fale com ele. Se você conseguir ajudá-lo, poderemos voltar a conversar logo. Que a Luz <o/a> ilumine.", progress = nil, completion = nil, rewards = nil},
[1780] = {title = "Tomo da Divindade", objective = "Fale com Benedito Brião em Ventobravo.", description = "Vou levar para as crianças todos os dias. Por favor, cuide-se, <class>. É raro vir à cidade gente de caráter como o seu, mas você provou que os paladinos aqui de Ventobravo jamais darão as costas aos necessitados.\n\nAdeus.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1781] = {title = "Tomo da Divindade", objective = "Leve o Símbolo da Vida para Gazin Tenorm em Ventobravo.\n\nItem fornecido:\n", description = "Quero que você leve isso, <name>. Chama-se Símbolo da Vida.\n\nVocê provou ser caridos<o/a> e paciente, e agora chegou a hora de ver se você é poderos<o/a> o suficiente para ser um foco de uma das maiores dádivas da Luz.\n\nGazin voltou recentemente de um confronto com alguns Défias em Elwynn, perto da Vigia dos Heróis, e mal escapou com vida. Ele está ali, perto do altar, tratando as feridas com um de nossos sacerdotes.\n\nPegue o Símbolo e fale com ele. Se você conseguir ajudá-lo, poderemos voltar a conversar logo. Vá na glória da Luz.", progress = nil, completion = nil, rewards = nil},
[1782] = {title = "A armadura do Furen", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nCota Endurecida pelo Fogo"},
[1783] = {title = "Tomo da Divindade", objective = "Leve o Símbolo da Vida e ressuscite Narm Faulk em Dun Morogh.", description = "Estava eu e ele espiando uns anões Ferro Negro que se abancaram ao sul do Lago Helm. Não era boa coisa, aquilo, então eu e o Narm achamos bom fazer o reponte de perto... e nós conseguimos ver o líder dando ordem pros peões lá dele.\n\nNarm e eu aproveitamos a chance de matar uns, mas era uma pandilha mais chucra que crocolisco buzina, e os outros pularam na peleia também. O Narm disse pra eu riscar de lá e chamar reforço, e aqui estou eu.\n\nO Narm precisa de ajuda, e eu é que não vou alçar até mandar alguma.\n\nE tu? Pode ajudar, <name>?Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1784] = {title = "Tomo da Divindade", objective = "Obtenha uma Carta de Ferro Negro com os anões Ferro Negro perto do Lago Helm e fale novamente com Muiredon Beloforja em Altaforja.", description = "Antes de eu tombar em batalha, Muiredon e eu havíamos quase derrotado um dos espiões Ferro Negro perto daquela casinhola. Flagramos o capitão entregando as ordens para eles, e tentamos matar o espião para pegá-las.\n\nParte do conselho de Altaforja ainda não acredita que há anões Ferro Negro em Dun Morogh. Obtendo uma dessas cartas nós não só teremos provas dessas movimentações, mas também quem sabe uma ideia do que eles estão planejando.\n\nConsiga uma daquelas cartas e leve para o Muiredon, <name>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1785] = {title = "Tomo da Divindade", objective = "Fale com Tiza Beloforja em Altaforja.", description = "Dá pra ver que a esposa já está é ali, ó, mais orgulhosa que dona de carneiro premiado. Risca pra lá já, que eu acho que ela quer parar e pautear contigo.\n\nQue a Luz vá contigo, poderos<o/a> <class>. Te espero no campo de batalha contra o Flagelo e os Ferro Negro quando chegar a hora.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nRedenção\n\nO seguinte feitiço será lançado em você:\n\nRedenção\n\nVocê também receberá:\n\n"},
[1786] = {title = "Tomo da Divindade", objective = "Leve o Símbolo da Vida e ressuscite Henze Faulk em Elwynn.", description = "Henze e eu havíamos chegado à Vigia dos Heróis, uma ilha no meio do Lago do Monumento, no nordeste de Elwynn. Tínhamos ouvido que os Défias haviam se infiltrado no nosso território, e precisávamos de provas para levar aos conselheiros do rei.\n\nAvistamos um grupo de vermes lendo uns documentos e atacamos. Infelizmente fomos subjugados por magos que apareceram como reforço.\n\nHenze os segurou enquanto eu fugia... o idiota, resolvendo ser corajoso.\n\nE ele está lá ainda, o Henze. Você pode nos ajudar, <name>?Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1787] = {title = "Tomo da Divindade", objective = "Obtenha uma Carta de Défias dos Teurgos Proscritos em Elwynn e fale novamente com Gazin Tenorm em Ventobravo.", description = "Depois teremos tempo para falar mais sobre o Símbolo. Agora o importante é obter aqueles papéis que vimos com os Défias. Se conseguirmos pôr as mãos naquilo, poderemos provar que os Défias se infiltraram em Elwynn e, quem sabe, ter um vislumbre dos planos deles.\n\nNão estou em condições de lutar agora, <name>. Você acha que é poderos<o/a> o suficiente para tomar os papéis dos Défias aqui na ilha?\n\nSe você conseguir, leve tudo para o Gazin e diga a ele que eu estou bem.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1788] = {title = "Tomo da Divindade", objective = "Fale com Benedito Brião em Ventobravo.", description = "Levarei as cartas aos conselheiros do rei depois que o Benedito tiver dado uma olhada nelas. Por enquanto, é melhor você falar com ele. Com certeza ele quererá parabenizá-l<o/a>.\n\nObrigado novamente, <name>. Espero que nos encontremos de novo. Você é <um aliado/uma aliada>, e eu me orgulho de tê-l<o/a> como amig<o/a>.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nRedenção\n\nO seguinte feitiço será lançado em você:\n\nRedenção\n\nVocê também receberá:\n\n"},
[1789] = {title = "O símbolo da vida", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nSímbolo da Vida"},
[1790] = {title = "O símbolo da vida", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nSímbolo da Vida"},
[1791] = {title = "O vigia dos ventos", objective = "Fale com Bath'rah, o Vigia dos Ventos.", description = "Agora que aprendeu a postura berserker, você vai precisar de uma boa arma com a qual usá-la.\n\nFale com Bath'rah, o Vigia dos Ventos, um troll eremita que mora entre as Montanhas de Alterac e as Terras Pestilentas Ocidentais. Ele é um armeiro habilidoso, e um xamã poderoso.\n\nPara encontrar o Bath'rah, chegue ao rio que passa ao leste da Serraria Tarren e siga por ele para o norte. O Bath'rah acampa perto de umas ruínas na margem oriental.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1792] = {title = "Arma do redemoinho", objective = "Tenho o coração rodopiante e estou pronto para colocá-lo no receptáculo à sua escolha.\n\nEscolha sua arma, <name>, e saiba que seu poder é o poder de Ciclonium.", description = nil, progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nMachado RedemoinhoEspada RedemoinhoMartelo de Guerra Redemoinho\n\nVocê também receberá:\n\n"},
[1793] = {title = "Tomo da Bravura", objective = "Já está comprovado que a Luz é convosco, <name>. Contudo, conforme nosso tempo se torna mais e mais perigoso, torna-se importante conhecer a força que jaz sob a carne. Quando demônios, dragões ou qualquer outro mal puser os pés em nossas terras, seu estofo será testado. Mas será que você terá a força necessária?\n\nA bravura independentemente da situação é parte do que significa ser uma <class>, tanto quanto a Luz, sua armadura ou sua arma. Proteger os fracos e não perder a fé são duas lições que você precisa aprender ao estudar aqui na Catedral da Luz.", description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nTomo de Valor"},
[1794] = {title = "Tomo da Bravura", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nTomo de Valor"},
[1795] = {title = "A vinculação", objective = "Usando the Tomo do Conluio, evoque e subjugue um caçador vil, depois devolva o Tomo do Conluio a Strahad Farsan na Vila Catraca.", description = "Se você estiver suficientemente preparad<o/a>, devemos começar. Permita-me explicar o processo.\n\nComeçarei criando o círculo de evocação básico. Quando ele estiver pronto, meus acólitos usarão os bastões de canalização para criar o círculo maior.\n\nUma vez que ele esteja completo, leia o encanto do Tomo do Conluio, e o caçador vil será forçado a se mostrar. Cuidado, <name>. Um caçador vil é um adversário poderoso, e eu não gostaria de ver você perdendo a cabeça...Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nEvocar Caçador Vil\n\nO seguinte feitiço será lançado em você:\n\nEnsinar Evocar Caçador Vil\n\nVocê também receberá:\n\nCaixa de Almas"},
[1796] = {title = "Componentes para um Robessangre Dourado Encantado", objective = "Leve Vestes de Arcana para Menara Nihila nos Sertões.", description = "O primeiro item que lhe ajudarei a criar será uma veste. Você providenciará os itens para mim e eu os prepararei. Depois, presumindo que seja bem-sucedid<o/a>, você terá o primeiro de muitos itens que <o/a> tornarão mais poderos<o/a> e a ajudarão em seus desafios.\n\nPara começar, você precisa ter umas Vestes de Arcana. Um alfaiate pode fazê-lo para você, ou talvez você possa comprá-los usados. Eu ficaria mais impressionada se você conseguisse confeccioná-las por conta própria... Mas não importa, Traga as vestes e prosseguiremos daí.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1798] = {title = "Procurando Strahad", objective = "Fale com Strahad Farsan nos Sertões.", description = "Está na hora de você aprender a evocação do caçador vil, <name>. Não tenho aqui os reagentes necessários para ensinar o feitiço. Você terá de procurar outra pessoa, Strahad Farsan, outro de nosso círculo.\n\nEle reside no porto goblínico de Vila Catraca. Eu o avisarei de que você está a caminho.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1799] = {title = "Fragmentos do Orbe de Orahil", objective = "Fale com os acólitos de Menara dentro da torre acima da Vila Catraca e escolha um caminho a seguir.\n\nDepois, leve um Orbe Infernal para Tabetha no Pântano Vadeoso.", description = "Ótimo, então vamos começar.\n\nA primeira coisa a fazer é falar com os meus acólitos lá dentro. Siga um dos caminhos que eles oferecerem — eles explicarão melhor.\n\nDepois, você tem que viajar até Desolação e conseguir um Orbe Infernal com um dos bruxos das Ruínas de Mannoroc. Você purificará esse orbe com a ajuda de uma maga chamada Tabetha, nas profundezas da parte sul do Pântano Vadeoso. Ela vai ajudar você porque eu e ela temos história.\n\nRepito, não volte até encontrar as duas coisas que pedi.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1800] = {title = "Sala do Trono de Lordaeron", objective = "Leve o seu protegido para a antiga Sala do Trono de Lordaeron, que fica logo antes da entrada subterrânea para a Cidade Baixa.", description = "Quando a gente se comporta direito, a matriarca conta histórias super legais sobre batalhas e lendas antes de dormir. Uma delas foi sobre a queda de Lordaeron e sobre como o Arthas tirou a vida do próprio pai para poder reinar.\n\nEle era um desonrado, <name>! Quando eu crescer e me tornar um poderoso guerreiro da Horda, a minha espada será guiada pela minha honra, e nunca pelo mal!\n\nVocê poderia me levar para ver essa tal sala do trono, <name>? Por favor! De acordo com a história da matriarca, ela fica logo antes da entrada subterrânea para a Cidade Baixa.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1801] = {title = "Tomo do Conluio", objective = "Fale com Jonas Anísio na Cidade Baixa.", description = "Um simples círculo de evocação não é suficiente para que você invoque um caçador vil a ser desafiado.\n\nEu e meus três acólitos vamos ajudar a criar os círculos mágicos, mas você tem que nos trazer uma cópia do Tomo do Conluio, que já se perdeu há algum tempo. Foi escrito pelo primeiro grupo de bruxos que evocou um caçador vil sem ajuda.\n\nTalvez Jonas Anísio possa ajudar você com isso. Hoje ele é cartógrafo na Cidade Baixa, mas não sei de ninguém que entenda mais de textos antigos do que ele.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1802] = {title = "Tomo do Conluio", objective = "Recupere o Tomo Mofado e o Manuscrito Rasgado para Krom Braçoforte em Altaforja.", description = "Estava perdido, mas temos uma ideia de onde pode ser encontrado.\n\nUma única cópia foi feita e, em algum momento, foi vandalizada. Uma grande porção do miolo foi arrancada da lombada. Há rumores de que ele, de alguma forma, passou para as mãos do centauro de Mil Agulhas.\n\nO resto se perdeu no litoral a oeste de Costa Sul, de onde zarparia para o Porto de Menethil. Nunca chegou.\n\nEm troca deste conhecimento, só o que peço é, se achar, que traga o volume para que eu possa dar uma lida.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1803] = {title = "Tomo do Conluio", objective = "Recupere o Tomo Mofado e o Manuscrito Rasgado para Jonas Anísio na Cidade Baixa.", description = "Ah! Vejo que eu e você temos gostos parecidos. De fato, adquirir o Tomo do Conluio é uma ambição digna. Houve um tempo em que os muros de Lordaeron o guardavam, sabia? Contudo, o volume foi vandalizado e metade das páginas foi removida. Quem seria capaz de cometer tamanha atrocidade?\n\nEssa metade não se perdeu, embora corram rumores de que ela caiu nas mãos do centauro das Mil Agulhas.\n\nO restante do texto seria transferido para Altaforja, mas acabou se perdendo no caminho, no litoral a oeste de Costa Sul.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1804] = {title = "Tomo do Conluio", objective = "Leve o Tomo Reformado e 3 Bastões de Canalização para Strahad Farsan na Vila Catraca.", description = "Bem, ah, é difícil me separar dele. Mas foi você quem o encontrou e sei que terá coisas mais importantes a fazer com o tomo, é claro. Só o que peço é que diga a Strahad que eu quero uma cópia.\n\nEnquanto você estava fora, ele deixou um recado para você. Os acólitos dele vão ajudar na evocação, mas você terá que levar três bastões de canalização.\n\nEle pode não saber onde encontrá-los, mas eu sei! Da última vez em que foram usados, estavam em poder dos orcs do clã Presa do Dragão, no Pantanal. Eu iria investigar os lançadores de feitiço deles se fosse você!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1805] = {title = "Tomo do Conluio", objective = "Leve o Tomo Reformado e 3 Bastões de Canalização para Strahad Farsan na Vila Catraca.", description = "Este é um dos textos mais interessantes que li nos últimos anos. A seleção aqui é bem esparsa, muitos livros de geografia, e eu já li todos eles. Espero que Strahad me mande uma cópia deste tomo assim que possível.\n\nFalando nele, Strahad entrou em contato enquanto você estava fora. Para a sua tarefa, você também vai precisar obter três bastões de canalização.\n\nPor permitir que eu desse uma espiada neste texto, contarei um segredo: você pode encontrar os bastões com os orcs do Clã Presa do Dragão no Pantanal.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1806] = {title = "A prova da retidão", objective = "Espere que Jardel Calmafonte termine de forjar uma arma para você.", description = "Que tal irmos para a forja começar logo com isso? Já estou me preparando há um tempo para a sua chegada, então não vai demorar.\n\nQuando terminarmos, você estará muito mais preparad<o/a> para defender a Luz, isso eu garanto.\n\nSerei honesto, <name>, ver o resultado de suas aventuras me deixa com uma certa inveja. Mas existe certa realização em saber que, à minha própria maneira, eu posso contribuir de forma significativa com as suas conquistas.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nPunho de Verigan"},
[1818] = {title = "Uma conversa com Dinis", objective = "Fale com o Necroguarda Dinis em Montalvo.", description = "Ouvi dizer que Necroguarda Dinis tem um trabalho para alguém como você. Se quiser provar o seu valor para s forças da Dama Sombria, procure Dinis agora mesmo.\n\nO posto do Necroguarda Dinis fica na ponta oeste de Montalvo, perto do cemitério.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1819] = {title = "Ulag, o Cutelo", objective = "Mate Ulag, o Cutelo, depois fale com Necroguarda Dinis.", description = "Quando o Flagelo varreu as Clareiras de Tirisfal, capturamos um deles para estudar. Ele era lúcido e se intitulava Ulag, o Cutelo. Mas embora a vontade dele não estivesse aprisionada, ele era escravo do Lich Rei.\n\nAinda tentamos romper o controle que o rei exercia sobre Ulag, mas falhamos. Agora finalmente chegou a hora de libertá-lo.\n\nLiberte Ulag e mate-o.\n\nAtive o gatilho de caveira de pedra perto do mausoléu mais ao norte. Isso vai romper o lacre mágico que o prende.\n\nE depois... esteja <preparado/preparada>, <name>.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nCaminho da Defesa\n\nVocê receberá:\n\n"},
[1820] = {title = "Encontrando Eurico", objective = "Fale com Eurico Palhares.", description = "Você provou o seu valor para a Dama Sombria, agora que tal se provar para um dos súditos dela?\n\nEurico Palhares está à espera de alguém que queira ser o instrumento da vingança dele contra os inimigos dos Renegados.\n\nEle está na Taberna Nó da Forca, em Montalvo leste.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1821] = {title = "A herança dos Agamand", objective = "Leve para Eurico Palhares o Machado da Família Agamand, a Espada da Família Agamand, a Maça da Família Agamand e a adaga da Família Agamand.", description = "Quando vivo, eu trabalhava para a família Agamand e foi por causa deles que eu morri! Acho bem feito que eles tenham virado lacaios sem cérebro do Lich Rei.\n\nÉ justo que eu fique com os últimos vestígios de honra deles. Vá à cripta da família e roube as heranças deles: uma espada, um machado, uma adaga e uma maça. Traga tudo para mim!\n\nA cripta fica na extremidade norte dos moinhos dos Agamand, protegida por flagelos sorrateiros e pelos Agamand mortos.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1822] = {title = "Meu ódio será sua herança", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nMachado da HerançaMartelo de HerançaAdaga de HerançaEspada de Herança\n\nVocê também receberá:\n\n"},
[1823] = {title = "Fale com Ruga", objective = "Fale com Ruga Totem da Fúria.", description = "Você é <um/uma> guerreir<o/a> de grande habilidade e fama crescente. Será que está pront<o/a> para a provação de Ruga Totem da Fúria? Se quiser arriscar, encontre Ruga no Acampamento Taurajo, nos Sertões.\n\nEle está sempre à espera de guerreiros que atendam chamado dele.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1824] = {title = "Provação no Campo dos Gigantes", objective = "Leve 5 antenas tremelicantes para Ruga Totem da Fúria no Acampamento Taurajo", description = "Para passar, você tem que viajar para o Campo dos Gigantes, ao sul. Lá, encontrará uma área apinhada de insetos. Chegaram há pouco tempo nos Sertões e a presença deles é um mau agouro.\n\nTraga para mim as antenas deles, ainda tremelicando, e vê se não perde tempo, <name>, porque antenas arrancadas não tremelicam durante muito tempo. Traga as antenas dentro do tempo estipulado e você terá passado na provação.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nBaluarte do Ruga"},
[1825] = {title = "Fale com Thun'grim", objective = "Fale com Thun'grim Olhafogo nos Sertões.", description = "Thun'grim Olhafogo, um orc ferreiro de renome, ficou sabendo dos seus muitos feitos valorosos a serviço da Horda. Ele quer criar uma armadura para você e pede que vá falar com ele imediatamente\n\nThun'grim está acampado nos Sertões, a leste da Encruzilhada.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1838] = {title = "Armadura brutal", objective = "Leve para Thun'grim Olhafogo 15 Lingotes Fumegantes de Ferro, 10 Azuritas Pulverizadas, 10 Barras de Ferro e a Ampola de Flogisto.", description = "Já ouvi muitas lendas sobre a sua força e coragem e quero te dar de presente uma armadura, uma armadura que vai ajudar você a conquistar ainda mais feitos lendários.\n\nMas a própria construção da armadura já é um feito, <name>. Preciso da sua ajuda e da ajuda de outros ferreiros da Horda.\n\nTome aqui esse pergaminho. Foram muitas as noites que passei lutando contra os meus olhos quase cegos para anotar o que preciso para fazer a armadura. Traga os itens deste pergaminho e eu farei a primeira peça da armadura.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1839] = {title = "Ula'elek e as Manoplas Brutais", objective = "Fale com Ula'elek em Durotar.", description = "O ferreiro troll Ula'elek fará para você um par de manoplas brutais. Ele vive em Durotar, na Aldeia Sen'jin, com seus irmãos Lançanegra, e já está esperando você.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1840] = {title = "Orm Casco de Pedra e o Elmo Brutal", objective = "Fale com Orm Casco de Pedra no Penhasco do Trovão.", description = "Orm Casco de Pedra é muito respeitado por sua perícia, e ele vai fazer o elmo brutal para você.\n\nEncontre-o no Penhasco do Trovão, perto do poço no grande totem central da cidade.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1841] = {title = "Velora Noitile e as Guarda-pernas Brutais", objective = "Fale com Velora Noitile na Cidade Baixa.", description = "Dizem que, quando viva, Velora Noitile era uma das melhores ferreiras de armaduras de Lordaeron. E, ao que tudo indica, a morte não prejudicou em nada os talentos dela. Vá falar com Velora, ela fará para você guarda-pernas brutais.\n\nVelora fica na Cidade Baixa de Lordaeron, no Centro Comercial.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1842] = {title = "Cascos de Sátiro", objective = "Leve 7 Cascos Não-fendidos de Sátiro para Ula'elek na Aldeia Sen'jin em Durotar", description = "Posso fazer para você um par de manoplas brutais, artefatos de grande magia. Mas primeiro você mesm<o/a> terá que colher a mágica e trazê-la para mim.\n\nOs sátiros vivem na região nordeste do Vale Gris por causa do poder da floresta. Eles se alimentam da pureza, sugando e corrompendo a natureza. Dá para reconhecer os sátiros que sugaram mais magia de Vale Gris pelos cascos inchados e não fendidos.\n\nEsses cascos contém muito poder. Vou precisar deles para as manoplas.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1843] = {title = "Manoplas Brutais", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nManoplas Brutais"},
[1844] = {title = "Chifre quimérico", objective = "Leve um Chifre Galvanizado para Orm Casco de Pedra no Penhasco do Trovão.", description = "Farei o elmo para você, mas primeiro você deve provar que está à altura.\n\nNos recônditos da região noroeste da Cordilheira das Torres de Pedra fica o Vale Carbonizado. Outrora um lugar verdejante, hoje o vale está conspurcado e carbonizado. As criaturas que lá vivem enlouqueceram de dor e desespero.\n\nTal é o lugar para onde envio você, <name>.\n\nVá ao Vale Carbonizado e encontre a quimera matriarca. Mate-a, libertando a pobre de seu sofrimento. Como prova, traga para mim o chifre galvanizado dela e eu farei o seu elmo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1845] = {title = "Elmo brutal", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nElmo Brutal"},
[1846] = {title = "Tíbias de Presa do Dragão", objective = "Leve 8 Tíbias Firmes de Presa do Dragão para Velora Noitile na Cidade Baixa.", description = "Para fazer as guarda-pernas, vou precisar de tíbias bem firmes. Os orcs Presa do Dragão têm umas ótimas tíbias, bem grossinhas. Servem bem para mim.\n\nMas elas têm que ser bem fortes. Teste as tíbias logo ao coletar. Se conseguir partir com as suas próprias mãos, não são resistentes o suficiente.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1847] = {title = "Guarda-pernas Brutais", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nGuarda-pernas Brutais"},
[1848] = {title = "Cota brutal", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nCota Brutal"},
[1858] = {title = "A Mão Despedaçada", objective = "Roube a chave de Tazan e use-a para abrir a Algibeira dele, levando o conteúdo para Therzok no Antro das Sombras, em Orgrimmar.", description = "Seu trabalho será abrir a algibeira de Tazan e trazer o conteúdo para mim. A tranca é um mecanismo muito complexo, então o mais fácil é arrumar a chave.\n\nComo não estava no cadáver de Tazan, só posso presumir que ele a deixou com outra... ah, sim, isso mesmo. Ele tinha um assistente chamado Gamon. É um beberrão de marca maior, então deve estar na taberna. Talvez você consiga tomar a chave dele na surdina.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nLâmina da AstúciaChave de Tazan"},
[1859] = {title = "Therzok", objective = "Fale com Therzok no Antro das Sombras em Orgrimmar.", description = "Fale baixo, <name>. Sim, eu sei quem você é. Me disseram para ficar de olho vivo para <um/uma> orc com as suas características.\n\nPelo que dizem, você consegue se virar muito bem no combate e não se opõe a um pouco de gatunagem de tempos em tempos.\n\nNão é mesmo?\n\nEnfim, procure o Therzok em Orgrimmar. Ele tem uma oferta para você.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1860] = {title = "Fale com Jana", objective = "Fale com Jana Catão em Ventobravo.", description = "Nós, magos, temos uma tarefa a cumprir. Uma tarefa vital para a segurança de Ventobravo e a floresta de Elwynn.\n\nJana Catão terá os detalhes. Ela estuda no sacrário dos teurgos, no distrito dos Magos de Ventobravo. Convém que você a procure o quanto antes.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1861] = {title = "Lago Espelhado", objective = "Traga uma amostra do Lago Espelhado para Jana Catão em Ventobravo.", description = "Este mundo já foi vítima de imensas lutas místicas, que destruíram correntes mágicas de canais que eram estáveis. Fazemos um esforço para determinar os efeitos causados pelas mudanças, e muitos de nós temem pelo pior!\n\nFarei mais revelações sobre o assunto depois. Agora, preciso que você colete informações.\n\nPegue este frasco. Vá até a base da cachoeira do Lago Espelhado, a sudoeste dos portões de Ventobravo, e colete uma amostra da água de lá. Traga para mim, é para um teste com máculas mágicas.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nOrbe MeridianoCajado Meridiano\n\nVocê também receberá:\n\n"},
[1878] = {title = "Bolsas de água pra refrescar", objective = "Voltou, <name>? Se você tiver mais cinco bolsas de água do errante pra mim, eu tenho um kit de sobrevivência pra você!", description = nil, progress = "Voltou, <name>? Se você tiver mais cinco bolsas de água do errante pra mim, eu tenho um kit de sobrevivência pra você!", completion = "Excelente! Quanto mais bolsas de água nós coletarmos, mais longe do desastre estaremos! Traga mais. Eu lhe darei uma recompensa para cada cinco bolsas de água que você me trouxer.", rewards = "Você receberá:\n\nPacote de Suprimentos da Cia. Águas de Geringontzan"},
[1879] = {title = "Fale com Bink", objective = "Fale com Bink em Altaforja.", description = "<name>! Bink, minha colega gnomida de Altaforja, mandou uma mensagem. Ela tem uma tarefa urgente é só confia em <um mago/uma maga> para cumpri-la! Você é <mago/maga>, <name>?\n\nSe for, fale com a Bink. Ela costuma ficar no Salão dos Mistérios da Ala Mística de Altaforja.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1880] = {title = "Treconitor magostático", objective = "Devolva o Treconitor Magostático a Bink.", description = "Você acha que ser mago é sacudir o nariz e fazer fumacinha? Nós controlamos a magia, o fio que entremeia nosso mundo! É preciso ter muito cuidado e vontade de ferro. E muito... mas muito conhecimento!\n\nUm dos meus dispositivos, o treconitor magostático, coleta e armazena vastas quantidades de conhecimento! Só que ele sumiu! Quando fugimos de Gnomeregan, deixei ele dentro de um baú em uma de nossas cabanas fora da cidade, mas agora o lugar está tomado por gnomos leprosos!\n\nPor favor, <name>, resgate meu treconitor!Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nOrbe ArcanoCajado Arcano\n\nVocê também receberá:\n\n"},
[1881] = {title = "Fale com Anastácia Cordato", objective = "Fale com Anastácia Cordato.", description = "<name>, Anastácia Cordato tem uma tarefa perfeita para alguém com seus talentos. Fale com ela no Distrito da Magia da Cidade Baixa e se prepare para cumprir a primeira tarefa para os magos dos Renegados.", progress = nil, completion = "Saudações, &lt;name&gt;. Sabiamente, você respondeu ao meu chamado. E vai ser mais sábio se ouvir o que tenho a dizer.", rewards = "Você receberá:\n\n"},
[1882] = {title = "A Fazenda dos Balnir", objective = "Traga Bocas-de-Leão de Balnir para Anastácia Cordato no Distrito dos Magos da Cidade Baixa.", description = "As Terras Pestilentas a leste são uma aberração. Criadas pelo Lich Rei, espalharam sua magia pelo solo, corrompendo e matando tudo que vive. Embora as forças destrutivas da peste tenham algum encanto, nós, os magos dos Renegados, acreditamos que ela é incontrolável e precisa ser restringida.\n\nAntes, porém, é preciso descobrir se ela já se espalhou muito por Tirisfal. Vá até a Fazenda dos Balnir, no leste em Tirisfal, recolha as bocas-de-leão do jardim e traga-as pra cá.", progress = "Você está com as bocas-de-leão? É uma flor altamente receptiva a energias mágicas. Se estudarmos as bocas-de-leão que ficam perto das Terras Pestilentas, podemos descobrir se a peste entrou nas Clareiras de Tirisfal.", completion = "Que belo espécime. Vou pedir que os nossos boticários o estudem para avaliar a extensão da infestação das Terras Pestilentas.\n\nMuito bem, &lt;name&gt;. Em agradecimento por seus serviços, você pode escolher um orbe ou um cajado. Qualquer um deles vai ser muito útil.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nOrbe ArcanoCajado Arcano\n\nVocê também receberá:\n\n"},
[1883] = {title = "Fale com Un'Thuwa", objective = "Fale com Un'Thuwa.", description = "Un'Thuwa, mago tribal da Aldeia Sen'jin, detectou um distúrbio nas correntes mágicas de Durotar e pediu ajuda. Fale com Un'Thuwa e faça o que for preciso para ajudar.\n\nFaça isso e outros segredos lhe serão revelados no momento certo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1884] = {title = "Pilhas de ju-ju", objective = "Destrua 4 Pilhas de Ju-Ju, depois fale novamente com Un'Thuwa na Aldeia Sen'jin.", description = "O bruxo Zalazane das Ilhas do Eco abalou o fluxo normal de magia de Durotar. Para fazer isso, ele espalhou fetiches maculados, as pilhas de ju-ju, por entre as cabanas da grande Ilha do Eco.\n\nEssas pilhas corrompem a vida selvagem e conferem poder aos bruxos e às criaturas infernais que ficam à espreita nos cantos sombrios da região.\n\nVocê precisa destruir as pilhas de ju-ju, <name>, antes que o fluxo de magia seja alterado para sempre.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nOrbe MeridianoCajado Meridiano\n\nVocê também receberá:\n\n"},
[1885] = {title = "Júnio Aquino", objective = "Fale com Júnio Aquino no Distrito dos Ladinos da Cidade Baixa.", description = "<name>? Não era bem o que eu estava esperando, em vista do interesse que o Aquino demonstrou. Mas, quem vê cara não vê coração, né?\n\nSeja como for, você precisa falar com ele o quanto antes. Se eu fosse você, nem iria querer saber o que aconteceu com quem não atendeu a convocação.", progress = nil, completion = "Hum, aposto que você correu para cá assim que soube da minha convocação, não é? É exatamente isso que eu espero de um futuro Sicário.", rewards = "Você receberá:\n\n"},
[1886] = {title = "Os Sicários", objective = "Obtenha a Carta de Apresentação do Astolfo e leve até Júnio Aquino no Distrito dos Ladinos.", description = "Para fazer parte dos Sicários, a guarda de elite do Lorde Varimatras, você precisa mostrar seu valor.\n\nNão vou mentir, <name>, esta missão pode ser fatal.\n\nTem algumas coisas que preciso resolver. Traga-me os dados pessoais de Astolfo Hadren, um mensageiro que circula entre Pinhaprata e a Cidade Baixa. Como você vai fazer isso é problema seu.\n\nMas é melhor que você não saiba mais nada sobre o motivo dessa missão. Se você for bem-sucedi:<o/a>, eu conto mais.", progress = "Contou o que tinha pra contar?", completion = "Muito bem, &lt;name&gt;. Você passou na primeira parte do teste. Tomara que o Andron não tenha ouvido falar das suas atividades, assim a gente vai conseguir convencê-lo de que você é o mensageiro que ele está esperando.", rewards = "Você receberá:\n\n"},
[1898] = {title = "Os Sicários", objective = "Fale com Andron Gante na Cidade Baixa.", description = "A carta de apresentação deve bastar para convencer Andron de que você é o mensageiro que ele estava esperando. Já o encontrei antes, ele não tem capacidade mental para ser tão desconfiado.\n\nLorde Varimatras suspeita que Andron esteja ajudando algum outro grupo. Ele quer saber que organização é essa e precisa de provas da ajuda.\n\nQuero que você converse com Andron e obtenha essa informação. Ele deve estar em uma loja próxima ao Boticarium.", progress = "Olá, tem alguma coisa que eu possa fazer por você?", completion = "Ah, sim, eu estava esperando você. Nossos contatos em comum estão satisfeitos com as informações que forneci até agora?\n\nNão sei por que estou perguntando isso a você, duvido que confiariam esse tipo de conhecimento a um simples mensageiro. Estou certo?", rewards = "Você receberá:\n\n"},
[1899] = {title = "Os Sicários", objective = "Entregue o Livro-caixa de Astor a Júnio Aquino no Distrito dos Ladinos da Cidade Baixa.", description = "Você vai encontrar o livro-caixa com as informações que seus empregadores buscam na prateleira atrás de mim. Entregue-o com os meus cumprimentos e diga a eles que eu prefiro que mandem você, se acharem que o conteúdo vale a pena e quiserem prolongar nosso acordo.", progress = "Conseguiu, <name>?", completion = "Bem, eu não esperava obter tantas informações de Andron. Preciso que você me dê uns minutos para folhear o livro-caixa antes de lhe passar sua próxima missão.", rewards = "Você receberá:\n\n"},
[1918] = {title = "O elemento conspurcado", objective = "Entregue o Globo d'Água Conspurcado a Mastok Wrilehiss no Posto Machadada, no Vale Gris.", description = "O elemental morto libera um globo de água intensamente brilhante. O conteúdo do globo é, no mínimo, repulsivo. É certo supor que, seja lá o que for, o que corrompeu a água do Lago Mistral também esteja presente na água do globo.\n\nNão é muito fácil descobrir alguém que se interesse em um achado dessa natureza. Talvez a pessoa que mandou você para o lago, Mastok Wrilehiss, do Posto Machadada, tenha alguma ideia.", progress = "O que você quer?", completion = "Então você encontra uma bola de água nojenta e traz para mim? Quem você acha que eu sou, a Harmonia Telúrica???\n\nBem... na verdade, isso me deu uma ideia... Talvez você precise mesmo da Harmonia Telúrica. Quer dizer, se isso ajudar a resolver os problemas do lago de uma vez por todas, eu tô dentro.\n\nVocê conhece a Harmonia Telúrica, não é?", rewards = "Você receberá:\n\n"},
[1919] = {title = "Jana tem uma missão", objective = "Fale com Jana Catão em Ventobravo.", description = "Você domina cada vez mais essa arte, jovem <name>. Está pront<o/a> para começar a lição de verdade? Está pront<o/a> para tomar seu lugar na nossa guerra secreta?\n\nSe estiver, fale com Jana Catão que está no Sacrário dos Teurgos, no Distrito dos Magos de Ventobravo. Ela tem uma tarefa que pode lhe abrir a primeira janela para o mundo invisível.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1920] = {title = "Investigue o Recôndito Azulego", objective = "Obtenha um Cantochão da Manifestação e um Baú de Cofres de Contenção atrás de Jana Catão. Leve 3 Cofres de Contenção Cheios para Jana no Sacrário dos Teurgos.", description = "Ouvimos relatos de um distúrbio na taberna Recôndito Azulego do Distrito dos Magos. É estranho, criaturas invisíveis estão perambulando pela taberna, e acho que uma fenda de mana se formou lá.\n\nQuero que você investigue a taberna e capture as criaturas. Use o cantochão da manifestação para tornar a criaturas visíveis, depois é só dominar e capturar em um cofre de contenção. Quando tiver bastante, volte aqui com os cofres cheios, os vazios e o cantochão.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1921] = {title = "Em busca de materiais", objective = "Leve 10 Linhos e 6 Gemas Energizadas da Fenda para Manoela Galante em Ventobravo.", description = "Você serviu bem a seus companheiros magos, agora é hora de envergar sua veste de teurgo. Manoela Galante pode fazer a sua veste, mas você precisa trazer os materiais necessários. \n\nA veste é feita de linho, que é fácil de encontrar, mas você também precisa obter gemas energizadas de fenda, e o único local conhecido das gemas é a Mina da Prata de Loch Modan, deixadas em caixotes pela liga dos mineiros anões.\n\nObtenha o linho e as gemas e entregue tudo para Manoela Galante na Alfaiataria Galante do Distrito dos Magos.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1938] = {title = "Tratado de Ur sobre Umbromancia", objective = "Entregue o Tratado de Ur sobre Umbromancia ao Grão-feiticeiro Andromath em Ventobravo.", description = "Há tempos, três magos estudaram nessa torre, mas um deles caiu nas artes sombrias. Tentaram convencê-lo a se afastar da magia sombria, mas ele não resistiu. E foi banido.\n\nOs novos domínios do mago caído ficam na torre de Ilgalar em Cristarrubra, e ele levou consigo um livro poderoso: o Tratado de Ur sobre Umbromancia.\n\nNós, os magos de Ventobravo, queremos o livro de volta. Entre na torre, encontre o tomo na biblioteca e traga-o de volta para mim.", progress = "Você tem o livro, <name>? Os segredos que ele traz não são para amadores, mergulhar nas suas profundezas pode trazer desgraça e ruína.", completion = "Eu temia que Morganth tivesse escondido o livro em outro lugar ou tivesse dado um jeito de abrir e ler o que está escrito.\n\nÉ um alívio saber que ele não conseguiu, e que os selos que colocamos no Tratado de Ur sobre Umbromancia funcionaram.", rewards = "Você receberá:\n\n"},
[1939] = {title = "Grão-feiticeiro Andromath", objective = "Fale com o Grão-Feiticeiro Andromath.", description = "O Grão-feiticeiro Andromath quer falar com você, <name>. Ele está no topo do Sacrário dos Teurgos, no Distrito dos Magos de Ventobravo.\n\nVocê faria bem em vê-lo, o Andromath é um dos magos mais poderosos que não está enfurnado em Dalaran. Falar com ele é como falar com o Alto Conselho.", progress = nil, completion = "Saudações, &lt;name&gt;. É uma alegria lhe ver, espero que você tenha tempo para ouvir as palavras de um ancião...", rewards = "Você receberá:\n\n"},
[1940] = {title = "Seda pura de aranha", objective = "Leve 8 Sedas Puras de Aranha para Manoela Galante em Ventobravo.", description = "Seu serviço tem sido impecável, <name>. Eu acho, talvez, que seja hora de usar um traje mais fino, mais condizente com a sua condição de <mago/maga> proficiente. Se você reunir os materiais necessários, vamos cuidar para que sua veste seja feita.\n\nEntre na Floresta do Crepúsculo e cace aranhas reclusas carniceiras. Extraia a seda delas, mas saiba que só a seda mais fina e imaculada vai servir. Quando tiver o bastante, leve para Manoela Galante, da Alfaiataria Galante, aqui no Distrito dos Magos. Ela vai fazer a veste.", progress = "Ah, sim. Os magos do Sacrário dos Teurgos disseram que você viria ter comigo. Conseguiu a seda?", completion = "Belos espécimes, esses. Vou pôr mãos à obra agora!", rewards = "Você receberá:\n\n"},
[1941] = {title = "Veste de fios de mana", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nVeste de Fios de Mana"},
[1942] = {title = "Traje do nó astral", objective = "Seu traje está pronto! E seda que você trouxe era de ótima qualidade... Fiz um pouquinho mais curto porque guardei um pouco da seda pra mim, tudo bem?\n\nBrincadeirinha... Vai ficar perfeito em você, é ideal para aventuras e também jantares formais. Sucesso com o traje!", description = nil, progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nVeste do Nó AstralBata do Nó Astral\n\nVocê também receberá:\n\n"},
[1943] = {title = "Fale com Denya", objective = "Fale com Denya.", description = "A maga troll Denya precisa da sua ajuda, <name>. Ela mora em Orgrimmar, no Salão Agulhas Negras.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1944] = {title = "Águas de Xavian", objective = "Leve uma Amostra de Água Xaviana para Denya em Orgrimmar.", description = "A lendária cidade de Xavian fica bem na parte oriental do Vale Gris, e lendas de seu conhecimento ancestral são conhecidas entres os estudiosos. Dizem que as águas de Xavian têm imensos poderes mágicos.\n\nGostaria de ter uma amostra dessas águas, colhida na fonte.\n\nPegue este frasco e vá até Xavian. Colete água do fundo da mais alta cachoeira e traga pra mim. Tenha cuidado ao se locomover pela cidade antiga, as ruínas são controladas por sátiros, que odeiam quando magos se metem em seus negócios.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1945] = {title = "Irmãs Risonhas", objective = "Leve 12 Cabelos das Irmãs Risonhas para Kil'hala na Encruzilhada.", description = "Organizei a criação de novas vestes em agradecimento por seus serviços, mas preciso que você traga os materiais necessários...\n\nPara estes, preciso que você volte ao Vale Gris e pegue mechas do cabelo das irmãs risonhas, dríades que se reúnem perto do Refúgio Bosque Real.\n\nQuanto pegar, leve até Kil'hala, um troll alfaiate que fica na Encruzilhada dos Sertões. Ele vai estar lhe esperando para fazer os seus trajes.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1946] = {title = "Traje do laço etéreo", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nVeste de Renda EtéreaTúnica do Laço Etéreo\n\nVocê também receberá:\n\n"},
[1947] = {title = "Jornada ao pântano", objective = "Fale com Tabetha.", description = "Agora é hora de você conquistar sua varinha de mago. Para começar esta missão, fale com a humana eremita Tabetha no Pântano Vadeoso.\n\nAproveite o vasto conhecimento que ela pode compartilhar. Você vai encontrar a choupana dela bem no meio do pântano, a oeste de Theramore e ao norte das Ruínas Pedramalho.", progress = nil, completion = "Silêncio! Estou em um momento decisivo, não posso ser perturbada!\n\nUm instante...", rewards = "Você receberá:\n\n"},
[1948] = {title = "Itens do poder", objective = "Traga 1 Jade e a Morácea Carregada por Raio para Tabetha no Pântano Vadeoso.", description = "Não é fácil construir a varinha de um mago. Substâncias raras são necessárias, é preciso construir um vasilhame que possa armazenar grandes quantidades de energia mágica. Você precisa coletar esses itens e trazer para mim.\n\nEscrevi neste pergaminho o que você precisa encontrar e como fazer para conseguir. Traga exatamente o que está na lista e siga minhas instruções ao pé da letra, pois, se não seguir, quando fizermos a varinha as coisas podem dar... muito errado.", progress = "Olá, <name>. Você está com os itens necessários?", completion = "Arrá! Você conseguiu o jade e fez a morácea! Muito bem!\n\nEstá saindo fumaça das suas orelhas? Você provavelmente estava perto demais quando o raio atingiu as varas do totem, mas não se preocupe. Ser atingida por um raio é sinal de sorte!", rewards = "Você receberá:\n\n"},
[1949] = {title = "Segredos ocultos", objective = "Fale com Tirth, o Mago, no Salar Cintilante.", description = "Para fazer sua varinha, preciso ler os principais trechos de um livro chamado Rituais do Poder, escrito por Tirth, o Mago.\n\nNem eu nem as maiores bibliotecas do mundo têm exemplares desse livro. Você precisa falar com Tirth para conseguir um.\n\nBoa sorte. Tirth era uma grande mago e um grande acadêmico, mas agora passa o tempo bebendo e apostando nas corridas gnômicas e goblínicas no Salar Cintilante.", progress = nil, completion = "O quê? Fale mais alto! Esses velhos ouvidos não são tão aguçados quanto antes.\n\nBem, espero que você tenha trazido uma boa caneca de cerveja ou alguma dica sobre a próxima corrida! Se não, pode ir... porque tenho uma varinha de polimorfia prontinha pra você!", rewards = "Você receberá:\n\n"},
[1950] = {title = "Tente a sorte", objective = "Encontre a frase que abre o cofre de Tirth e fale novamente com ele.", description = "Posso lhe ajudar com o livro, mas você precisa me ajudar antes!\n\nO tomo que você procura está no meu cofre trancado por magia. Para abrir, é preciso dizer uma frase especial, mas eu não consigo lembrar qual é! Meu assistente costuma me ajudar a lembrar, mas ele sumiu.\n\nSe quiser meu livro, encontre meu aprendiz! Pergunte nas vizinhanças da pista de corridas, se você tiver sorte, alguém deve ter visto o sujeito. Arraste-o de volta para cá ou pelo menos consiga a frase mágica para abrir o cofre!", progress = nil, completion = "Ah, agora eu me lembro...\n\nTinha me esquecido da maldiçãozinha que lancei sobre o Johnson. Eu não sei por que lancei, mas ele com certeza sabe por que levou, aquele ingrato!", rewards = "Você receberá:\n\n"},
[1951] = {title = "Rituais do Poder", objective = "Leve o livro Rituais do Poder para Tabetha no Pântano Vadeoso.", description = "Hum, acabei de me lembrar que não tenho mais o livro! É que eu tinha umas dividazinhas de jogo e, pra conseguir pagar, precisei vender o último exemplar do Rituais do Poder para um clérigo...\n\nUm membro da Irmandade Escarlate. Ele deve ter levado para a biblioteca do Monastério Escarlate nas Clareiras de Tirisfal!\n\nSe quiser mesmo o livro, você vai ter que ir até lá. Desculpa qualquer coisa. Eu ando meio avoado ultimamente...", progress = "Saudações, <name>. Encontrou o livro Rituais do Poder?", completion = "É isso! Exatamente o que eu precisava para preparar o ritual de criação da sua varinha. Espero que você tenha tido muito trabalho...", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[1952] = {title = "Varinha do Mago", objective = "Fale com Tabetha depois do ritual.", description = "Vou fazer as varinhas agora.", progress = nil, completion = "Aqui estão as varinhas que eu faço. Pode escolher uma!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nVarinha da Fúria GlacialVarinha da Força EtéreaVarinha Iraflama\n\nVocê também receberá:\n\n"},
[1953] = {title = "Retorno ao pântano", objective = "Fale com Tabetha.", description = "Chegou a hora, <name>, de falar com Tabetha... a eremita do Pântano Vadeoso. Ela vai orientar você na criação de um implemento mágico poderoso.\n\nA choupana de Tabetha fica no meio do Pântano Vadeoso, a oeste de Theramore.", progress = nil, completion = "Ah, sim. Você veio aqui fazer um orbe ou cajado, não é, &lt;name&gt;? Vou ajudar na construção.\n\nMas, esteja avisada, o processo é perigoso e pode até te matar.\n\nOu me matar. Definitivamente, alguém pode morrer... \n\nTomara que não sejamos nós!", rewards = "Você receberá:\n\n"},
[1954] = {title = "O Orbe Infernal", objective = "Leve um Orbe Infernal para Tabetha no Pântano Vadeoso.", description = "O primeiro passo é adquirir um orbe com poder. Os bruxos da Lâmina Ardente têm orbes de ótima qualidade... pena que vinculem demônios a eles! Mas a gente pode corrigir isso, pode sim.\n\nVá até Desolação e roube o orbe infernal de um evocador da Lâmina Ardente. Os evocadores ficam nas Ruínas de Mannoroc.\n\nQuando conseguir o orbe, fale novamente comigo.", progress = "Você tem um orbe infernal, <name>?", completion = "Ora, que belo exemplar! Não, não quis dizer \"belo\" no sentido de belo como o sol de verão.\n\nNa verdade, de belo ele não tem nada. Não tô falando do sol, tô falando do orbe!\n\nConcentração, &lt;name&gt;! Precisamos que você se concentre para conseguir terminar a tarefa!", rewards = "Você receberá:\n\n"},
[1955] = {title = "O Exorcismo", objective = "Mate o Demônio do Orbe, depois fale com Tabetha.", description = "Agora que temos um orbe de evocador, precisamos exorcizar o demônio dentro dele. Deixe comigo, essa é a parte fácil.\n\nA parte difícil é com você. Matar o demônio assim que ele sair.\n\nE rápido. Se demorar muito, ele vai entrar de novo no orbe e daí vamos ter que começar tudo de novo!\n\nPor isso, esteja atent<o/a>. Prepare seus feitiços mais poderosos, mantenha suas poções à mão e chame um amigo se tiver algum. Porque a coisa vai ficar bem feia por aqui.", progress = nil, completion = "Muito bem, &lt;name&gt;! O demônio levou uma surra que vai levar tempo pra esquecer.\n\nEle vai pensar duas vezes antes de botar aquela cabeça horrorosa para espiar este mundo de novo, à procura do mortal estúpido que o derrotou. E provavelmente não virá sozinho. Vai trazer uns amiguinhos escamosos para lhe transformar em purê de &lt;race&gt;.\n\nOps, assustei você, &lt;name&gt;?", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[1956] = {title = "Poder em Uldaman", objective = "Recupere uma Fonte de Energia de Obsidiana e leve-a para Tabetha no Pântano Vadeoso.", description = "Agora que o demônio foi removido, precisamos encontrar uma fonte de energia. Infelizmente, as melhores costumam ser as mais antigas e as mais difíceis de encontrar. Engraçado como às vezes o destino prega peças na gente, né?\n\nVocê precisa ir até Uldaman e derrotar a sentinela de obsidiana, que é grande, forte e tem uma fonte de energia que é um coice!\n\nEle lhe desejo muita sorte, <name>. Você vai precisar. Se a sorte lhe faltar, bem... foi um prazer ter lhe conhecido.", progress = "Você já foi a Uldaman, <name>? Já derrotou a sentinela de obsidiana?", completion = "Você conseguiu? Muito bem!\n\nEstava conversando com alguns colegas e quando o seu nome apareceu eles disseram: \"Você sabe que &lt;name&gt; lida bem com o estresse!\"\n\nMas quando contei que mandei você a Uldaman para enfrentar a sentinela de obsidiana, me chamaram de louco! Disseram: \"Tabetha, você é maluca! &lt;name&gt; nunca vai conseguir derrotar a sentinela!\"\n\nRespondi: \"Vamos ver\". Retrucaram: \"É, vamos ver...\"\n\nNós mostramos a eles, não foi?", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[1957] = {title = "Surtos de mana", objective = "Mate 12 Surtos de Mana no tempo limite permitido.", description = "Estamos quase acabando, mas este último passo é o mais perigoso, <name>.\n\nEstou pronta para energizar o orbe de evocador vazio. Para isso, precisamos extrair a energia de uma fenda de mana. Vou lançar um feitiço que abre uma fenda temporária, da qual criaturas chamadas surtos de mana vão sair. Você precisa derrotar os surtos para liberar a energia deles, que eu vou coletar.\n\nSe você conseguir matar surtos de mana suficientes antes da fenda se fechar, eu coloco toda a energia coletada no seu orbe.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1958] = {title = "Poder celestial", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nOrbe CelestialBáculo Celestial\n\nVocê também receberá:\n\n"},
[1959] = {title = "Apresente-se a Anastácia", objective = "Fale com Anastácia Cordato.", description = "Os magos da Cidade Baixa perceberam um distúrbio mágico na cidade e pediram aos magos da Horda que investigassem.\n\nAnastácia Cordato tem mais informações sobre isso. Fale com ela no Distrito da Magia da Cidade Baixa.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1960] = {title = "Investigue a Loja de Alquimia", objective = "Obtenha um Cantochão da Manifestação e um Baú de Cofres de Contenção atrás de Anastácia Cordato. Traga 3 Cofres de Contenção Cheios, o Baú de Cofres de Contenção e o Cantochão da Manifestação para Anastácia na Cidade Baixa.", description = "A loja do alquimista de Algernon no Boticarium é foco de um estranho distúrbio. Criaturas invisíveis, feitas somente de magia, vagam pela loja. Deve haver alguma razão para isso, e precisamos descobrir qual!\n\nInvestigue o distúrbio e capture essas criaturas. Pegue um baú de cofres e um pergaminho atrás de mim. Use o pergaminho na loja para tornar as criaturas visíveis, domine-as com seus feitiços e depois lance um cofre para capturá-las. Retorne com os cofres cheios, os cofres vazios e o cantochão.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1961] = {title = "Em busca de materiais", objective = "Leve 10 Linhos e 6 Gemas de Mana de Dalaran para Josefo Gregório.", description = "Está na hora de envergar suas primeiras vestes de mag<o/a>, <name>. Elas são um símbolo da sua posição e do seu valor.\n\nPara ganhar as vestes, você precisa reunir as gemas de mana dos magos de Dalaran em Pinhaprata. Eles têm crenças parecidas com as nossas, por isso, matá-los é algo desagradável, mas, se não têm força para resistir a nós, então não merecem as próprias vidas.\n\nLeve as gemas e um suprimento de linho para Josefo Gregório da loja de alfaiataria do Distrito da Magia.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1962] = {title = "Vestes de fogo místico", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nVestes de Fogo Místico Inferior"},
[1963] = {title = "A Mão Despedaçada", objective = "Mate Tazan e leve a Algibeira dele para Therzok no Antro das Sombras, que fica em Orgrimmar.", description = "O Chefe Guerreiro é sutil demais em seus métodos. É cauteloso na hora de libertar a fúria da Horda. Já fomos mais fortes, todos grandes conquistadores. Orgulho precisamos reconquistar, isso que a Mão Despedaçada vai fazer.\n\nTiramos força das sombras e atacamos os expostos, matando os inimigos secretamente. Uma tarefa você precisa concluir para provar seu valor.\n\nO troll Tazan comercia com piratas perto da Vila Catraca. Ele tem informações de que preciso. Mate-o e me traga a algibeira dele.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1978] = {title = "Os Sicários", objective = "Entregue o Livro-caixa de Andron a Varimatras no Salão da Dama Sombria, que fica na Cidade Baixa.", description = "A Camarilha? Eu jamais suspeitaria. Parece que os lordes de Alterac entraram em decadência nos anos que se seguiram à traição.\n\nLorde Varimatras vai ficar feliz ao saber disso. Por ter sido essencial para essa descoberta, nada mais justo que você faça a entrega para ele.\n\nAh, quase que eu me esqueço!\n\n<Bem-vindo/Bem-vinda> aos Sicários.", progress = "Sim?", completion = "Hum, isso é interessante. Os Sicários cumpriram muito bem suas obrigações ao descobrir isso.\n\nNo meu entendimento, você também merece uma comenda por sua atuação. Espero que isso vire rotina para você, &lt;name&gt;, tem sempre lugar para um homem competente entre os meus Sicários.", rewards = "Você receberá:\n\nLâmina da Astúcia"},
[1998] = {title = "Fernão Thatros", objective = "Mate Fernão Thatros e leve a cabeça dele para Júnio Aquino no Distrito dos Ladinos da Cidade Baixa.", description = "Lorde Varimatras acabou de me mandar uma carta sobre uma ameaça aos Renegados que precisa ser eliminada imediatamente. Seu nome é Fernão Thatros, que Lorde Varimatras acredita ser um líder dos mortos-vivos que fincou raízes na Floresta de Pinhaprata.\n\nPensei imediatamente em você, <name>. Confio demais nas suas habilidades e sei que você vai cumprir essa missão sem erros.\n\nOs últimos relatórios dos Sicários de Pinhaprata indicam que Thatros pode ser encontrado na doca decrépita de Lordaeron.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[1999] = {title = "Ferramentas do ofício", objective = "Encontre o Relatório Situacional de Dalaran e devolva-o a Júnio Aquino no Distrito dos Ladinos da Cidade Baixa.", description = "Os teurgos de Kirin Tor em Lenhâmbar arquivaram documentos importantes sobre o estado atual de Dalaran. Isso é o que descobrimos por meio da confissão de um de nossos prisioneiros. Lorde Varimatras incumbiu aos Sicários a missão de recuperá-los.\n\nE agora a tarefa é sua, <name>. Você vai precisar de equipamentos, no entanto, e aprender técnicas de arrombamento. Fale com Estela Galina, nossa provedora, e peça um conjunto de ferramentas antes de partir.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2018] = {title = "Rokar's Test", objective = "Kill Overseer Glibby and return his Whistle to Rokar Bladeshadow in Durotar.", description = "Ah, so now Therzok sends me his budding students, hm? Well, I suppose I can teach you a thing or two. Not that I care much of your business, but I'd not want it on my conscious that I sent you to your death.\n\nBut you must first prove yourself worthy.\n\nI will tell you, <name>. I do not much like this Venture Company. This land reminds me of Draenor, and I do not like the thought of those goblins destroying it. Perhaps if their overseer at the Sludge Fen is killed, their operation will be hamstrung.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2019] = {title = "Tools of the Trade", objective = "Return to Therzok in the Cleft of Shadow in Orgrimmar.\n\nItem fornecido:\n", description = "Here you go, <name>. These should serve you well. Crafted by my own hand, I swear you'll find none better.\n\nDo not bother to tell me your business, however. I have no interest in Therzok's schemes, I wish only to live out my retirement in peace.\n\nIf you should ever need another set of tools, come back to me, and I will furnish you with a new set. But have a care with them!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2038] = {title = "Os suprimentos desaparecidos do Gomez", objective = "Encontre e devolva os suprimentos do Gomez:\n\nChave de Boca do Gomez, Chave de Fenda do Gomez, Martelo do Gomez e Detona-cuca do Gomez.", description = "Como se cair no Loch já não fosse ruim que chegasse, os troggs da ilha aqui perto me atacaram. Os cretinos roubaram as minhas ferramentas e meus explosivos detona-cuca ultraplosivos especiais. Sem girocóptero e sem explosivos, o movimento tá lascado!\n\nTraz minhas ferramentas de volta, e vê se acha meus detona-cucas, <name>. Por Gnomeregan!", progress = "<name>! Onde estão as minhas coisas?", completion = "Você prestou um grande serviço à causa, &lt;name&gt;. Os troggs e os gnomos leprosos que infestam Gnomeregan vão sentir em breve a força de um ataque aéreo gnômico!", rewards = "Você receberá:\n\nLuvas de Aviação do Gomez"},
[2039] = {title = "Cadê o Bingles?", objective = "Encontre Gomez Plosivo em Loch Modan.", description = "Perdemos contato com o Bingles! Ele foi enviado para uma missão de reconhecimento em Gnomeregan e não manda notícias há mais de uma semana. Ele só pode ter se explodido.\n\nDa última vez que ele foi visto, estava sobrevoando o Loch. Você poderia investigar isso, <name>?", progress = nil, completion = "Então o Gnoarn não confia nas minhas habilidades de reconhecimento, hein? Por que será...\n\nMas deixa isso para lá! Preciso da sua ajuda! Não! O movimento precisa da sua ajuda!", rewards = "Você receberá:\n\n"},
[2040] = {title = "Ataque subterrâneo", objective = "Recupere o Engrenotreco Gnomo das Minas Mortas e devolva a Shoni, a Shilenchiosa, em Ventobravo.", description = "Gnomeregan caiu e está sob o controle desses malditos troggs! A situação é grave, mas talvez você consiga ajudar, <name>.\n\nNas profundezas das Minas Mortas tem um retalhador goblínico. Encontre esse retalhador e traga a fonte de energia intacta. Com ela, podemos fornecer a energia necessária para que nossos escavacionadores girobrocamáticos penetrem nas fronteiras rochosas submersas de Gnomeregan, abrindo caminho para um ataque gnômico!", progress = "Conseguiu encontrar o engrenotreco gnomo, <name>?", completion = "Muito bem, &lt;name&gt;. Graças a você, Gnomeregan está mais próxima do dia da libertação!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nManoplas PolaresVarinha de Zibelina\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[2041] = {title = "Fale com Shoni", objective = "Fale com Shoni, a Shilenchiosa, em Ventobravo.", description = "Talvez você possa nos ajudar na batalha por Gnomeregan.\n\nEm Ventobravo você encontrará a comandante da nossa equipe de ataque subterrâneo, Shoni, a Shilenchiosa. Shoni precisa de ajuda com os escavacionadores girobrocamáticos dela.\n\nA comandante provavelmente está em meio aos anões de Ventobravo.\n\nBoa sorte, <name>.", progress = nil, completion = "Trouxe alguma notícia do comando central?", rewards = "Você receberá:\n\n"},
[2078] = {title = "A revanche de Giramastro", objective = "Encontre o Imediato de Gelkak e o Mangual-eliminator Pro Giramastro 4100, e leve-o de volta a Gelkak.", description = "Ô, marujo de água doce, vai procurar meu imediato.\n\nPega essa chave, enfia nele e traz de volta pra servir o capitão.\n\nQuero ver a alegria desses murlocs e do chefe do banco quando o meu imediato der conta deles!", progress = "Cadê o meu imediato, <name>? Tu vai virar comida de tubarão se não for logo!", completion = "Arr... Muito bem, cabeção, tu tá me saindo um lobo do mar bem vibrão.\n\nSe continuar assim, vou até te promover a grumete do convés de popa...", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nElixir de Respiração Aquática"},
[2098] = {title = "Uma mão para Giramastro", objective = "Encontre e devolva as três partes da Chave de Gelkak para Gelkak Giramastro.", description = "Vida de pirata não é mar de rosas, não, malhado. Não é só pilhagem e bebedeira! Meu navio afundou perto da praia por causa desses manguadontes dos infernos.\n\nPreciso do meu imediato aqui, pronto pro trabalho, imediatamente!\n\nQuando dei na praia, a chave para o meu imediato quebrou nas pedras e os bichos da Costa Negra afanaram. De rabo de olho vi murlocs, tiscorais furiosos e florestruzes furiosos remexendo nos destroços, aí quem ficou furioso fui eu...", progress = "Encontrou minha chave quebrada, marujo de água doce?", completion = "Arrr!! Muito bem, minhoca da terra! Tu conseguiu! Quando meu imediato tiver pronto, esses bichos vão tudo pro fundo do mar!\n\nHum, você viu meu imediato?", rewards = "Você receberá:\n\n"},
[2118] = {title = "Terras pestilentas", objective = "Capture um ursocardo raivoso e traga de volta para Tharnariun.\n\nSe não conseguir capturar um ursocardo raivoso e perder a armadilha, fale novamente com Tharnariun Tratárvore e solicite outra.", description = "Uma doença se espalha pela Costa Negra, dominando as mentes e os corpos de todos aqueles que toca.\n\nOs ursocardos foram as piores vítimas dessa peste. As outrora nobres feras são agora um instrumento de destruição: raivosos e enlouquecidos. Talvez eu tenha a cura para esse mal.\n\nLeve esta armadilha para a floresta e coloque no chão. Qualquer ursocardo raivoso que cruzar a luz vai ficar dócil por algum tempo. Quando isso acontecer, ele vai seguir você. Traga até aqui, <name>.", progress = nil, completion = "Nosso trabalho só está começando, &lt;name&gt;. Está pronto para a próxima tarefa?", rewards = "Você receberá:\n\n"},
[2138] = {title = "Purificação dos infectados", objective = "Purifique a floresta matando 20 Ursocardos Raivosos e fale novamente com Tharnariun Tratárvore na Costa Negra.", description = "Como temi, a cura não funciona.\n\nÉ com grande tristeza que ordeno o abate de qualquer fera da natureza, mas os ursocardos doentes e maculados precisam ser mortos.\n\nVolte à floresta e execute vinte ursocardos raivosos, <name>. Com certeza eu não vou conseguir acabar com a peste, mas pelo menos posso reduzir os danos que os animais estão causando à floresta e a seus habitantes no curto prazo.", progress = nil, completion = "Você prestou um grande serviço a Auberdine, &lt;name&gt;, mas sua tarefa ainda não está completa.\n\nFale novamente comigo depois de ter descansado.\n\nE &lt;name&gt;, prepare a mente e a alma para mais banhos de sangue.", rewards = "Você receberá:\n\n"},
[2139] = {title = "Esperança de Tharnariun", objective = "Encontre e mate a Matriarca do Covil.", description = "A sobrevivência do nosso povo e de nossas terras vai ter um custo alto, <name>.\n\nVocê derramou o sangue de servos da natureza, muitos animais doentes foram abatidos. O povo de Auberdine tem uma dívida com você, pois a dor da floresta foi amenizada, mesmo que só por um momento.\n\nTenho uma tarefa final para você, será preciso matar de novo. Espero que seja a última vez que lhe peço para fazer isso, <name>.\n\nEm uma caverna a nordeste, perto de Bashal'Aran, mora a Matriarca do Covil. Acabe com ela.", progress = nil, completion = "Você lutou por Auberdine com bravura e honra, &lt;name&gt;. Morte e desespero são comuns em nossas terras castigadas pela guerra. Muitos não enfrentarão o desafio de vida em nosso novo lar, escolhendo, em vez disso, se esconder em suas casas ou escapar para terras mais protegidas da Aliança.\n\nObrigado, &lt;name&gt;. Quem sabe, em um futuro próximo, lutemos lado a lado, como camaradas, contra outro inimigo.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nLuvas PerenesCapa das Terras Florestais\n\nVocê também receberá:\n\n"},
[2158] = {title = "Repouso e relaxamento", objective = "Fale com o Estalajadeiro Fábio na Estalagem do Leão Orgulhoso.", description = "Todo aventureiro deve descansar quando bate o cansaço. E não há lugar melhor para repousar e relaxar do que a Estalagem do Leão Orgulhoso!\n\nMeu melhor amigo, o estalajadeiro Fábio, é o dono da estalagem. Se você disser que foi indicação minha, ele pode te dar descontos especiais na comida e na bebida.\n\nA Estalagem do Leão Orgulhoso fica pro sul, seguindo a estrada. Não dá pra errar!", progress = nil, completion = "Repouso e relaxamento para os cansados e famintos, esse é nosso lema! Por favor, sente-se ao pé do fogo e descanse os ossos.\n\nVocê gostaria de alguma coisa para comer e beber?", rewards = "Você vai poder escolher uma dessas recompensas:\n\nAbóbora PequenaÁgua Refrescante da Fonte\n\nVocê também receberá:\n\n"},
[2159] = {title = "Entrega para Dolanaar", objective = "Leve a Entrega para Dolanaar para o Estalajadeiro Keldamyr.", description = "Saudações, jovem <class>. Poderia me ajudar? Tenho um pacote de plantas para ser entregue na cidade de Dolanaar. Infelizmente, ainda tenho assuntos a tratar com os druidas de Umbravale e não posso ir até lá.\n\nPoderia levá-lo para mim? Deve ser entregue ao estalajadeiro Keldamyr na estalagem de Dolanaar. Pegue a estrada que segue para o sul.", progress = "Em que posso ajudá-lo, jovem? Veio até aqui para descansar? Necessita de uma pedra de regresso?", completion = "Ah sim... as plantas de Umbravale. É uma pena que Porthannius não pôde trazê-las. Eu e ele temos muito o que conversar, mas fico feliz de que você tenha vindo.\n\nEnquanto estiver aqui, por favor, descanse. Todo herói deve manter a força e a coragem sempre em alta e descansar quando for necessário. Negar o descanso do corpo e da alma é um caminho certo para a derrota.\n\nSendo assim... descanse.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nQueijo Roquefort DarnassianoÁgua Refrescante da Fonte\n\nVocê também receberá:\n\n"},
[2160] = {title = "Suprimentos para Tannok", objective = "Entregue um Caixote com Provisões para Estalagem a Tannok Marrãogélido, em Kharanos.", description = "Ei! Você parece ser <um aventureiro/uma aventureira> forte. Se estiver planejando enfrentar a passagem, será que você poderia levar um pacote até a estalagem em Kharanos?\n\nJá estava nos seus planos parar na estalagem, não estava? Se você conseguir atravessar a passagem, com certeza, vai querer se recompor lá.\n\nDe qualquer forma, leve isto para Tannok Marrãogélido, o assistente da estalagem. Eu não consigo atravessar a passagem sozinho, e vai levar dias para uma escolta de montanheiros passar por aqui!", progress = "Olá! Entre e sente-se perto do fogo para se aquecer com um caneco de cerveja.", completion = "Ah, finalmente, os suprimentos! Já estava começando a me preocupar, não recebemos muitas notícias de Sidermar desde que os troggs dominaram a passagem.\n\nObrigado por me trazer isto, &lt;name&gt;. E, por favor, fique à vontade. Você deve estar precisando de um descanso depois dessa viagem.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCarne-seca DuraÁgua Refrescante da Fonte\n\nVocê também receberá:\n\n"},
[2161] = {title = "O fardo de um peão", objective = "Leve o Fardo de Ulkor para o Estalajadeiro Grosk no Monte Navalha.", description = "Com licença! Você pode me ajudar?\n\nEu tenho um carregamento de comida aqui. Eu caminhei pela estrada e enfrentei escorpídeos, aranhas e coisas piores! Eu trouxe comida ao Vale das Provações porque imaginei que precisassem dela lá e resolvi ajudar. Mas eles não precisam de comida! Agora eu tenho que levar isto de volta para o Monte Navalha e estou com medo de todos aqueles monstros no caminho...\n\nVocê pode fazer isso por mim? Eu sou só um peão, mas você é $gum herói:uma heroína. Você nada teme!\n\nLeve isto para o Estalajadeiro Grosk, na estalagem do Monte Navalha. Obrigado!", progress = "Bem-vindo ao Monte Navalha!", completion = "Ah, essa é a comida que Ukor levou ao Vale das Provações. Não precisavam dela lá? Bem, acho que eles gostam de treinar os heróis com o estômago roncando. Fortalece o espírito, dizem eles!\n\nObrigado por trazer a comida de volta. Eu vou colocá-la de volta na nossa despensa... Mas permita-me lhe oferecer uma refeição antes!\n\nE não se esqueça de descansar aqui na estalagem. Você pode ser um &lt;class&gt; valente, preparado para enfrentar o mundo, mas com sua energia esgotada, não tem como você ajudar nem a si mesmo, nem à Horda.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nNaco de Pão DuroÁgua Refrescante da Fonte\n\nVocê também receberá:\n\n"},
[2178] = {title = "Moa a quem moer", objective = "Leve 5 porções de Carne de Moa para Alanndária Noturcanto, em Auberdine.", description = "Está sentindo esse aroma delicioso? É a minha receita secreta, <name>: ensopado de moa fresca. Delícia!\n\nVocê gostaria de aprender a receita?\n\nAntes de lhe ensinar a receita, quero que você prove que consegue enfrentar aquelas feras.\n\nAgora, eu não espero que você vá até lá, dome uma e volte cavalgando ela até mim, mas se você conseguir matar algumas e trazer um pouco da carne suculenta delas, posso me sentir persuadido a lhe mostrar como preparar o ensopado de moa.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nReceita: Ensopado de MoaEnsopado de Moa"},
[2198] = {title = "O colar estilhaçado", objective = "Procure o criador original do colar estilhaçado para saber sobre seu valor potencial.", description = "Você achou um colar manchado, embora a mancha oculte um artesanato mágico e intricado. O colar foi estilhaçado, pois seu conjunto aparenta ter perdido três gemas. Na parte de baixo do colar, tem uma impressão fraca do selo da cidade de Altaforja. Abaixo do selo, três letras pequenas gravadas: \"DdM\".\n\nSe quisermos entender esse colar, você vai precisar descobrir sobre suas origens. Talvez o selo e as iniciais sejam um bom ponto de partida...", progress = "Você pegou o gnomo errado, xará! Eu nunca estive lá! Você não pode provar nada!\n\nEspere um segundo... quem é você? O que... onde você achou esse colar?", completion = "Sim... Eu sou o joalheiro que fez este colar. E não estou no ramo de repassar informações, apesar de não terem me custado nada. Informação nunca é grátis, camarada. Se quiser a minha ajuda, você precisa me ajudar antes.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2199] = {title = "Aí tem truta!", objective = "Leve cinco barras de prata para Didiê Du Mocó em Altaforja.", description = "Eu sou um joalheiro muito famoso aqui em Altaforja! Bem, ex-muito famoso, na verdade... Eu caí em desgraça por causa das trapaças de um concorrente! Audácia da pilombeta! E, nesse ramo, má reputação é sinônimo de fiasco financeiro. Então eu fiquei sem nem um cobre furado para salvar meu bom-mas-atualmente-difamado nome.\n\nEu gostaria que você trouxesse para mim cinco barras de prata para terminar um pedido – o primeiro que recebo há uma eternidade.\n\nVocê me arranja as cinco barras de prata que eu respondo suas perguntas sobre o colar.", progress = "Ô psit! Conseguiu pegar as barras de prata que pedi? Então agora vou lhe contar minha triste história com esse colar que você tem aí... Mas só depois que eu botar as mãos nessas barras de prata! Por favor, dá uma mãozinha pro gnomo, dá?", completion = "Você é muito legal – eu não ligo para o que andam falando por aí... Brincadeira, é brincadeira! Agora, a sério: obrigado, minha querida.\n\nEsse seu colar é uma das minhas mais finas criações, mas estão faltando as três gemas que conferem poderes mágicos à joia. Eu fiz esse colar para um paladino já faz algum tempo. Ele estava em uma cruzada pessoal para desmacular um sítio de escavação chamado Uldaman, nos Ermos, e foi aí que começou a minha desgraça.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2200] = {title = "De volta a Uldaman", objective = "Procure pistas sobre disposição atual do colar de Didiê dentro de Uldaman. O paladino morto que ele mencionou foi a última pessoa a portá-lo.\n\nItem fornecido:\n", description = "Um concorrente meu espalhou um boato de que o meu colar amaldiçoou o paladino. Nem preciso dizer que ninguém quer comprar artigos amaldiçoados de um joalheiro.\n\n<name>, eu lhe faço uma proposta: encontre as gemas, e eu montarei o colar, contanto que você espalhe por aí que eu NÃO faço itens amaldiçoados!\n\nTente procurar pistas em Uldaman; talvez procurar o corpo do paladino lá, já que sabemos que ele foi o último dono! Quando encontrar as gemas, você deve usar isto para entrar em contato comigo aqui em Altaforja.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2201] = {title = "Encontre as gemas", objective = "Encontre o rubi, a safira e o topázio que foram espalhados por toda Uldaman. Uma vez adquiridos, entre em contato com Didiê Du Mocó remotamente, usando o Frasco de Vidência que ele lhe deu.\n\nDe acordo com o diário...\n* O rubi está escondido em uma área barricada de Umbraforja.\n* O topázio está escondido numa urna em uma das áreas dos troggs, perto dos anões da Aliança.\n\n* A safira foi reivindicada por Grimlok, o líder dos troggs.", description = "O diário do paladino forneceu, talvez, as únicas pistas que Uldaman pode oferecer sobre o local das gemas do colar. Se os registros do paladino estiverem corretos, então, os anões de Umbraforja e os troggs que lutam pelo controle do local de escavação serão um empecilho.\n\nArmados com esse conhecimento, começamos a sério a busca pelas gemas. Quando todas as gemas forem adquiridas, Didiê, em Altaforja, deve ser avisado por meio do Frasco de Vidência, segundo as instruções dele.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2202] = {title = "Busca pelos reagentes de Uldaman", objective = "Leve 12 Campânulas de Fungo Magenta para Jarkal Limuno em Karrath.", description = "Ouvi falar de uma planta recém-descoberta chamada de campânula de fungo magenta. Elas crescem em aglomerados, no fundo do sítio de escavação de Uldaman — pode haver delas em outro lugar, mas é lá que eu tenho certeza de que você vai encontrá-las. Eu quero estudar seu uso potencial em alquimia, e é aí que você entra.\n\nEncontre os aglomerados de campânula de fungo magenta e me traga uma dúzia. Mas esteja ciente de que os aglomerados podem espalhar esporos venenosos se forem chacoalhados.\n\nFaça isso para mim, e vou preparar uma leva de um dos meus famosos elixires restauradores para você!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nPoção de Restauração"},
[2203] = {title = "Busca pelos reagentes dos Ermos Parte II", objective = "Use os recipientes de taumaturgia vazios em dragões guardiões calcinados encontrados nos Ermos. Quando enchê-los, leve-os para Jarkal Limuno em Karrath.", description = "Você tem sido muito útil, <class>. Eu te recompensaria com a própria receita da bebida que te dei mais cedo, mas preciso te dar uma tarefa muito perigosa. escute o meu pedido.\n\nDentro deste cofre têm três recipientes de taumaturgia vazios; eles estão imbuídos de uma aura de harmonização que vai drenar o sangue de um dragão guardião flâmeo. Usar um na fera vai deixá-lo extremamente furioso, fique ciente disso. Assim que encher todos os três, traga-os para mim.\n\nFaça isso e a receita será sua.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nPoção de Restauração\n\nVocê receberá:\n\n"},
[2204] = {title = "Restaurando o colar", objective = "Obtenha uma fonte de energia do constructo mais poderoso que você encontrar em Uldaman e entregue-a a Didiê Du Mocó em Altaforja.", description = "Você vai precisar encontrar um \"empurrão\" para o colar, e Uldaman, provavelmente, é o melhor lugar para isso. Eu acho que há muitos constructos por lá, não? Bem, derrote o maior e pior constructo daquele lugar e tome sua fonte de energia! Tenho certeza de que posso fundir essa fonte ao colar.\n\nSem a fonte de energia, consertar o colar será impossível. Volte aqui, para Altaforja, quando a tiver, e vamos ajeitar tudo para você!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2205] = {title = "Em busca da AVIN", objective = "Entregue o pacote de Anabela Cabreira para o mestre Mathias Shaw nos Quartel de Ventobravo.", description = "O distintivo? Ah, nem esquenta... Você não ia dar conta mesmo. Olhando assim, o Mathias vai acabar achando que você é <o entregador/a entregadora> de queijos, e não <um ladino/uma ladina> de verdade. Enfim, eles me deram uma tarefa, e eu prometi cumprir.\n\nSem delongas: entregue esse pacote ao Mathias, que ele deve arrumar uns trabalhinhos para você.\n\nAnda... circulando.", progress = "Como posso ajudar, <class>?", completion = "Ora, ora... Você me achou. E pela sua cara, eu diria que a longa viagem e essa busca tiveram um grande impacto no seu estado físico e mental.\n\nQuer que Renzik leve você até uma estalagem? Ou acha que aguenta mais alguns trabalhos?", rewards = "Você receberá:\n\n"},
[2206] = {title = "O punguista", objective = "Encontre o Mestre de Doca dos Défias, pegue o Horário de Embarque e entregue-o para o Mestre Mathias Shaw.", description = "Nossos agentes em campo relataram forte atividade dos Défias ao sul de Vila d'Ouro. Os relatórios indicam que o Ancoradouro de Jerod foi tomado pelos Défias, que estão usando-o para transportar contrabando entre Cristarrubra e Cerro Oeste.\n\nQuero que você se infiltre na sede da doca e roube a tabela com os horários de embarque, que provavelmente está guardada no fundo dos bolsos do mestre de doca.\n\nAh, <name>, caso você seja <descoberto/descoberta> a AVIN vai negar conhecimento da sua existência.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nLâmina da Astúcia"},
[2218] = {title = "Estrada para a salvação", objective = "Fale com Hulfdan Barbanegra em Altaforja.", description = "Outra alma em busca de orientação? Aposto que está procurando um propósito... o seu propósito. Já vi essa história um milhões de vezes; mais um herói querendo trabalho.\n\nSeus problemas acabaram, <guri/guria>! O Hogral aqui tem a solução.\n\nPois bem, tu vais subir essa estrada até Altaforja e entrar na Caverna Esquecida. Lá dentro, quero que encontre a Guilda dos Ladrões e fale com Hulfdan Barbanegra. Ele vai te mostrar a estrada da salvação.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2238] = {title = "Simples subterfúgio", objective = "Encontre Onin MacHammar em Gnomeregan.", description = "Tu vês, o rei tá garimpando a pedreira atrás de ouro e pedras preciosas. Mais riquezas que qualquer anão veria em dez encarnações.\n\nO Círculo Oculto tem valores, <name>. Nós valorizamos valores. 'Tás entendendo?\n\nAgora, a gente acha que o rei deveria dividir um pouco desse butim, mas não queremos que o velho Barbodum perceba que ele tá dividindo — se é que tu me entendes.\n\nPrecisamos que tu fales com Onin MacHammar lá em Gnomeregan e traga um relatório de volta. O anão esperto tá se escondeu entre os leprosos. Tome cuidado.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2239] = {title = "Relatório de Onin", objective = "Leve o relatório de Onin para Hulfdan em Altaforja.", description = "A Pedreira? Bah, eu vejo aquela carretama de tanques blindados passar por esse posto quase todo dia.\n\nMeus contatos do leste dizem que os tanques saem de Altaforja, fazem uma parada na Pedreira Gol'Bolar, reabastecem em Kharanos e, por algum motivo soturno, pegam o caminho mais longo que passa por Gnomeregan até descarregarem em Altaforja novamente.\n\nTá tudo no relatório, <race>. Leva logo pro Hulfdan pra ele traçar o próximo passo.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nLâmina da Astúcia"},
[2240] = {title = "A câmara oculta", objective = "Leia o Diário de Baelog, explore a câmara oculta, depois fale com o Prospector Lançatroz.", description = "Vamos cuidar da tarefa em mãos.\n\nExiste uma câmara secreta — a Câmara de Khaz'mul — que precisa ser explorada. O problema é que ela fica atrás de uma enorme porta trancada! A boa notícia é que eu sei como abri-la.\n\nEu descrevi tudo no meu diário, aqui na mesa. Meu bando e eu estamos presos aqui por ora... Por isso, você, <name>, deve encontrar a câmara de Khaz'mul para nós!\n\nLeia o meu diário, explore a câmara e depois fale com o Prospector Lançatroz na Liga dos Exploradores, em Altaforja.", progress = nil, completion = "Você viu a Câmara de Khaz'mul Incrível! Baelog tinha certeza de que ela existia, mas admito que eu tinha lá minhas dúvidas.\n\nObrigado, &lt;name&gt;. Com sorte, seu achado incitará os nossos líderes a renovar seus esforços para retomar Uldaman. E se um dia ela tornar a ser controlada por Altaforja, pelo menos os segredos da Câmara de Khaz'mul serão nossos!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCarga EnânicaEstrela Guia da Liga dos Exploradores\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[2241] = {title = "Amor só dura em liberdade", objective = "Leve a flor de Jannok para Syurna em Darnassus.", description = "Dizem que estou \"apaixonado\", seja lá o que isso signifique.\n\nClaro, passo cada segundo pensando em Syurna. Claro, minha casa está cheia de quadros e desenhos de Syurna. Claro, muitas vezes passo dias sem comer, dormir ou beber, enquanto lamento sobre o amor que poderíamos ter. Qual é o problema nisso?\n\nAgora ela não quer me ver! A MIM! Eu tentei mandar mensagens para ela, mas ela não quer falar com ninguém, a menos que seja um ladino.\n\nVocê poderia entregar esta flor para ela? Não se esqueça de dizer a ela que é de Jannok.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2242] = {title = "O destino chama", objective = "Encontre Sethir, o Antigo, e leve qualquer pista a respeito de Syurna.", description = "Acreditamos que haja um ser que possa nos ajudar a compreender o que está acontecendo nas florestas de Teldrassil. Infelizmente, foi desastroso quando tentamos convencê-lo a nos ajudar.\n\nEle se esconde em um dos galhos na beirada da Clareira do Oráculo. Todos que tentaram se aproximar do sátiro, tanto por força quanto por diplomacia, tiveram um triste fim. Talvez furtividade seja a solução, <name>.\n\nDescubra o que Sethir, o Antigo, carrega nos bolsos e depois volte aqui.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nLâmina da Astúcia"},
[2258] = {title = "Busca pelos reagentes dos Ermos", objective = "Leve 5 Moelas de Urubu, 10 Presas de Coiote do Rochedo e 5 Estilhaços de Elemental da Rocha para Jarkal Limuno em Karrath, Ermos.", description = "<name>, eu poderia contar com sua ajuda, se você topar. Sou um alquimista de certo renome, e preciso da sua ajuda pra conseguir alguns reagentes encontrados nos Ermos.\n\nPreciso de algumas coisas pra reabastecer meus estoques: cinco moelas de urubu, dez presas de coiote do rochedo e cinco estilhaços de elementais da rocha. Obviamente, você pode tirar essas coisas dos próprios bichos; mas se você não for muito de lutar, então procure amigos que sejam.\n\nEu vou lhe dar uma grana se você conseguir essas coisas pra mim. E aí, o que me diz?Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2259] = {title = "Erion Umbrurmúrio", objective = "Fale com Erion Umbrurmúrio em Darnassus.", description = "Não se preocupe, <name>, eu não vou mandar você falar com a Syurna. Erion requer a sua presença no Enclave Cenariano. Vá depressa!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2260] = {title = "A mando de Erion", objective = "Vá para Ventobravo e procure o conselho de Renzik, \"O Bicudo\".", description = "Notícias dos seus feitos se espalharam por todos os lados, <name>. Tantos lados, de fato, que a Inteligência de Ventobravo convocou você especificamente.\n\nA jornada é exaustiva e nada inofensiva, mas uma oportunidade de treinar com a AVIN não pode ser ignorada.\n\nUm novo mundo lhe aguarda, <name>. Vá até a Cidade Velha em Ventobravo e fale com Renzik, \"O Bicudo\" no quartel da AVIN.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2278] = {title = "Os discos de platina", objective = "Fale com o vigia de pedra e descubra quais são os segredos da história antiga que ele guarda. Após aprender os segredos históricos, ative os Discos de Norgannon.", description = "No centro dessa câmara do tesouro há quatro enormes discos de platina, cada um decorado com numerosos selos de origem desconhecida. Ao tocar os discos, sua mão começa a formigar e, logo em seguida, o semblante de um enorme vigia de pedra aparece.\n\n\"Saudações. Sou o guardião do conhecimento. A síntese dos Terrenos dos Criadores está registrada e armazenada aqui nos Discos de Norgannon\".\n\nVocê acabou de descobrir uma nova e até então desconhecida parte da história do mundo!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2279] = {title = "Os discos de platina", objective = "Leve a versão em miniatura dos Discos de Norgannon para a Liga dos Exploradores em Altaforja.", description = "Ao tocar os Discos de Norgannon, sua mão começa a formigar novamente. Dessa vez, você escuta um rangido estranho vindo de dentro dos discos.\n\nSua mochila fica mais pesada de uma hora para outra. Olhando dentro dela, você encontra uma versão em miniatura dos discos!\n\nSem dúvida, esse objeto é de valor inestimável para qualquer pessoa interessada na história antiga de Azeroth. A Liga dos Exploradores, em Altaforja, conhecido por toda a Aliança, é um grupo desse tipo de pessoas — talvez eles façam bom uso disso.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2280] = {title = "Os discos de platina", objective = "Leve a versão em miniatura dos Discos de Norgannon para algum dos sábios no Penhasco do Trovão.", description = "Ao tocar os Discos de Norgannon, sua mão começa a formigar novamente. Dessa vez, você escuta um rangido estranho vindo de dentro dos discos.\n\nSua mochila fica mais pesada de uma hora para outra. Olhando dentro dela, você encontra uma versão em miniatura dos discos!\n\nSem dúvida, esse objeto é de valor inestimável para qualquer pessoa interessada na história antiga de Azeroth. Os sábios do Penhasco do Trovão são historiadores conhecidos por toda a Horda — talvez eles façam bom uso disso.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2281] = {title = "Encontro em Cristarrubra", objective = "Vá até a Vila Plácida, nas Montanhas Cristarrubra, e fale com Lucius.", description = "Mandar você para campo agora seria suicídio. Renzik não precisa de mais <um ladino morto/uma ladina morta> nas mãos. Mas não se preocupe, <name> — Renzik vai te deixar a par de tudo.\n\nVá até as Montanhas Cristarrubra e fale com um associado da AVIN chamado Lucius. Você vai encontrá-lo nas docas da Vila Plácida com os outros imprestáveis.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2282] = {title = "Serraria Alther", objective = "Abra o Baú do Lucius, recupere o Símbolo de Ladroagem e devolva-o ao Lucius na Vila Plácida.", description = "Nós treinamos ladrões aqui há anos! Afinal, nem todo larápio nasce com a graça e finesse do velho Lucius.\n\nSubindo por fora da estrada aqui, você vai encontrar a Serraria Alther. Eu deixei um baú lá com uma coisa importante dentro. Vá lá buscá-lo para mim! Traga-me aquele símbolo e eu lhe darei o Certificado de Ladroagem!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nCertificado de Ladroagem"},
[2283] = {title = "O resgate do colar", objective = "Procure um colar valioso na escavação de Uldaman e leve-o para Dran Droffers em Orgrimmar. O colar pode estar danificado.", description = "Temos trabalho para você, isto é, se você tiver coragem para isso!\n\nNos recônditos dos Ermos, fica a escavação de Uldaman. Nós ouvimos uma história sobre um paladino idiota que morreu recentemente. O que importa para nós de verdade é que ele carregava um colar muito valioso. Supostamente, ele tem três gemas grandes: um rubi, uma safira e um topázio.\n\nO negócio é o seguinte: nós o queremos e vamos pagar MUITO DINHEIRO para consegui-lo. Ele pode estar danificado, mas isso não importa; vamos consertar os arranhões nele.", progress = "E então, você está com o colar? Você não vai ver um tostão furado nosso se não trouxer aquele colar!", completion = "Hmmm, resgate! Sinto o cheiro do colar daqui. Agora, vamos dar uma olhada nessa belezinha...\n\nEi, o que você pensa que está fazendo? Estão faltando as gemas neste colar! Achou que não iríamos notar? Até o Malton conseguiria ver que as gemas sumiram.\n\nBem, talvez você não as tenha roubado... mas o colar não vale um tostão furado sem as gemas! Seu trabalho aqui ainda não acabou — não está nem perto de acabar!", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2284] = {title = "O resgate do colar 2 – A missão", objective = "Encontre uma pista do paradeiro das gemas nas profundezas de Uldaman.", description = "A mesma certeza que tenho da burrice do Malton, também tenho de que essas gemas ainda estão em Uldaman, em algum lugar. Você vai ter que voltar lá e procurar por elas.\n\nComo eu disse antes, se as informações que temos estiverem corretas, então há um rubi, uma safira e um topázio que compõem o colar. Encontre-as e coloque-as no colar.\n\nQuanto à localização das gemas, quem sabe? Talvez você tenha sorte e encontre o cadáver do Paladino. Se ele ainda não foi completamente saqueado, talvez ainda esteja com as gemas.", progress = nil, completion = "Aparentemente, você encontrou os restos do paladino que o Dran mencionou. Vasculhando o corpo, você encontra o que parece ser o diário dele. O livro está em condições ruins, com sangue seco manchando a maior parte do texto. O que dá para ler, ainda por cima, é só incompreensível — está escrito na língua nativa dos humanos, até onde você sabe.\n\nVocê precisa que alguém traduza o diário se quiser entender alguma coisa dele.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2298] = {title = "Aperto régio", objective = "Viaje para Ventobravo e procure Renzik, \"O Bicudo\".", description = "Daí a gente tava lá, caraminholando a fase três, quando alguns brutamontes do Magni chegaram se intrometendo, falando de algum assalto e prendendo um pessoal do Círculo Oculto.\n\nOra, ninguém algema o velho Barbanegra! O bate-boca esquentou e deixaram escapar o teu nome!\n\nQuer um conselho? Pega o primeiro grifo pra fora de Altaforja e te pica pra Ventobravo. Vai pra Cidade Velha e, chegando lá, fala com um velho amigo meu, Renzik, \"O Bicudo\".Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2299] = {title = "Ao Hulfdan!", objective = "Vá até Altaforja e fale com Hulfdan Barbanegra.", description = "Por onde tu andou, <name>? O Hulfdan tava te procurando em tudo que é canto. Achamos que o rei tivesse te tacado na prisão.\n\nVá até Altaforja o mais rápido possível e fale com o Hulfdan!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2300] = {title = "AVIN", objective = "Viaje para Ventobravo e procure Renzik, \"O Bicudo\".", description = "Renzik estava perguntando por você.\n\nSe eu fosse você, não deixaria o braço direito do Mathias esperando.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2318] = {title = "Traduzindo o diário", objective = "Encontre alguém que consiga traduzir o diário do paladino. O local mais próximo que pode ter alguém que o faça é Karrath, nos Ermos.\n\nItem fornecido:\n", description = "Se houver algum impasse, você vai precisar do livro traduzido para um idioma que você consiga ler. Sei que há muitos indivíduos que podem nos ajudar com isso... Talvez o lugar mais perto seja lá no posto avançado de Karrath, nos Ermos. Acho que é uma boa ideia começar por lá.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2338] = {title = "Traduzindo o diário", objective = "Empreste o colar a Jarkal. Em troca, ele vai traduzir o diário para você.", description = "O pessoal do Ferro-velho Droffers e Filho não é esperto o suficiente para restaurar a magia do colar, mas eu sou. Eu só preciso estudar o colar para aprender a fazê-lo sozinho. Para isso, vou precisar do seu colar. Deixe-me pega-lo emprestado e, em troca do tempo que vou poder estudá-lo, vou traduzir o diário para você. Enquanto você estiver procurando as gemas, devo ter tempo suficiente para aprender o que preciso.\n\nÉ um acordo simples, e ninguém vai ficar sabendo de nada. O que você me diz?O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2339] = {title = "Encontre as Gemas e a Fonte de Energia", objective = "Recupere todas as três gemas e uma fonte de energia para o colar de Uldaman e leve-as para Jarkal Limuno, em Karrath. Jarkal acredita que uma fonte de energia pode ser encontrada no constructo mais forte presente em Uldaman.\n\nDe acordo com o diário:\n* O rubi está escondido em uma área de Umbraforja protegida por barreiras.\n* O topázio está escondido numa urna em uma das áreas dos troggs, perto de alguns anões da Aliança.\n* A safira foi reclamada por Grimlok, o líder dos troggs.", description = "Aqui está o diário traduzido para você ler <sozinho/sozinha>. O ponto crucial é que os anões escondiam o rubi, os troggs escondiam o topázio e um trogg chamado Grimlok estava com a safira.\n\nVeja, você vai precisar de uma fonte de energia para fazer o colar funcionar novamente. Dran Droffers não sabe disso. Eis o que eu faria para encontrar uma: eu avançaria o máximo possível em Uldaman e mataria qualquer constructo que fosse o mais forte de lá. Seja qual for, com certeza, ele a tem.\n\nQuando você tiver recolhido tudo, volte aqui!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2340] = {title = "Entregue as gemas", objective = "Entregue o Colar e Gema Recuperados para Dran Droffers em Orgrimmar.", description = "Qualquer tipo de colar com gemas funciona, desde que sejam do mesmo material. Esta fonte de energia, no entanto, é o que trará a magia de volta ao colar... espero.\n\nVou fornecer as gemas necessárias para a sua versão do colar, contanto que a fonte de energia funcione.\n\nVá até Orgrimmar e cumpra a sua parte do trato com Droffers e o filho. Só não diga nada sobre o nosso acordo e, quando você voltar, eu posso ter uma surpresa maravilhosa para você.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2341] = {title = "O resgate do colar 3 – O Retorno", objective = "Visite Jarkal Limuno em Karrath para ver se ele obteve sucesso.", description = "Com Dran e Malton Droffers brigando por seu novo item recuperado, metade do acordo já está completo. Agora você tem uma viagem para os Ermos pela frente para terminar as coisas, já que Jarkal Limuno pode ter uma recompensa especial esperando por você em Karrath.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nColar de Aprimoramento do Jarkal"},
[2342] = {title = "Tesouros recuperados", objective = "Pegue o tesouro de Patrício Garreta no baú de sua família, que está no Saguão Sul de Uldaman, e entregue a ele na Cidade Baixa.", description = "Varrer o viveiro dos morcegos com esta vassoura não é digno de um Garreta! Eu ganhei um dinheirinho com esse trabalho maldito e estou disposto a gastá-lo com você para melhorar minha situação.\n\nA arma preferida da família Garreta está trancada em nosso baú no Saguão Sul de Uldaman, da época em que nós, Garreta, trabalhávamos lá como escavadores. Sem ela, ficarei preso aqui para sempre — literalmente — varrendo a sujeira desses morcegos infernais.\n\nPegue o tesouro da minha família e traga-o para mim! Ajude-me a recuperar o que é meu por direito!", progress = "A faxina nunca acaba... esses morcegos são prolíficos em zombar de mim e da minha vassoura. Por favor, diga que você recuperou o tesouro da minha família! Eu lhe ofereci meu dinheiro — isso não lhe basta?!", completion = "Finalmente, uma vassoura digna de um Garreta. Você vale cada moeda que gastei com você!\n\nEstão ouvindo, morcegos imundos? Agora eu empunho a arma da família Garreta! Tremei, morcegos, tremei!", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2359] = {title = "Torre do Filipe", objective = "Roube a chave, entre na Torre Défias e descubra o que tem dentro do Baú da Floresta do Crepúsculo. Leve todas as informações que coletar para Mathias Shaw em Ventobravo. Consulte o Diário de Marta se quiser mais detalhes sobre a torre.", description = "Eu tenho vigiado esta torre há semanas, <name>. Recentemente, uma grande remessa chegou da Floresta do Crepúsculo e, junto com ela, vieram aqueles guardas.\n\nO que são eles? Eu sei tanto quanto você. Mas posso lhe assegurar uma coisa: eles não são humanos.\n\nDe qualquer forma, o carregamento tinha um baú — que é o que eu presumo que essas abominações estão guardando. O zumbi com espinhos está coma a chave de que precisamos.\n\nPegue aquela chave, entre na torre e descubra o que tem naquele baú. Leve suas descobertas ao Mathias.", progress = "Alguma notícia da Agente Marta Hari?", completion = "Isso explicaria os zumbis dos Défias que você e Marta viram do lado de fora da torre, &lt;name&gt;. Também explicaria o envolvimento da Empreendimentos S.A. com os Défias e as estranhas remessas da Floresta do Crepúsculo.\n\nDe acordo com este livro, os trolls Zanzil na Selva do Espinhaço descobriram uma maneira de ressuscitar os mortos por meio de poções... ou, para ser mais específico: venenos.\n\nVocê não terá nenhuma utilidade para a AVIN sem um conhecimento sólido de venenos, &lt;name&gt; — especialmente com a receita para criar morte-viva ao nosso alcance.", rewards = "O seguinte feitiço será lançado em você:\n\nVenenos\n\nVocê receberá:\n\nReceita: Chá de Cardo"},
[2360] = {title = "Mathias e os Défias", objective = "Viaje para Cerro Oeste e encontre a Agente Marta Hari para receber instruções.", description = "Até que enfim, <name>. O reconhecimento da AVIN relata atividade particularmente pesada dos Défias em Cerro Oeste.\n\nAparentemente, os Défias ocuparam uma torre de vigia abandonada da Aliança a sudeste do Morro da Sentinela, e a usam como base das operações de recepção de contrabando da rota de suprimentos da Cristarrubra.\n\nVá até Cerro Oeste e fale com a Agente Marta Hari para receber mais instruções. Ela está escondida perto da torre.\n\nE, <name>, caso descubram sua presença, a AVIN negará ter conhecimento da sua existência.", progress = nil, completion = "Finalmente, o Mathias mandou outro agente para me dar apoio? Bem, espero que ele esteja pronto para más notícias.", rewards = "Você receberá:\n\n"},
[2361] = {title = "Restaurando o colar", objective = nil, description = nil, progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nColar de Aprimoramento do Didiê"},
[2378] = {title = "Encontre a Mão Despedaçada", objective = "Vá até Orgrimmar e fale com Shenthul no Antro das Sombras.", description = "Você tem sido de muita serventia aos Sicários, <name>, mas talvez esteja na hora de seguir em frente. Embora seja <talentoso/talentosa> nos quesitos furtividade e enganação, você ainda precisa de muito treino.\n\nOs Sicários têm aliados na Mão Despedaçada. Vá atrás daqueles dispostos a nos ajudar. Fale com Shenthul em Orgrimmar.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2379] = {title = "Zando'zan", objective = "Fale com Zando'zan no Antro das Sombras.", description = "A sua determinação não é o bastante para superar os obstáculos à nossa frente.\n\nFale com Zando'zan para começar o treinamento como agente da Mão Despedaçada.\n\nSó depois de dominar as artes da furtividade e da ladroagem, você poderá dar os próximos passos rumo ao seu destino.", progress = nil, completion = nil, rewards = nil},
[2380] = {title = "Para Orgrimmar!", objective = "Fale com Shenthul no Antro das Sombras, em Orgrimmar.", description = "Você não domina as artes da furtividade e da enganação, mas tudo isso pode e será mudado. Sem dúvida, as suas habilidades mequetrefes impressionaram o alto escalão, já que o grande Shenthul indicou você a dedo.\n\nPegue a estrada ao norte que leva a Orgrimmar e fale com Shenthul no Antro das Sombras.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2381] = {title = "Saqueando os saqueadores", objective = "Leve o Tesouro dos Mares do Sul para Wrenix, o Ignóbil, na Vila Catraca. Não se esqueça de pegar um B.E.C.A. e as Ferramentas de Ladrões no Aparato Mercadotrônico do Wrenix. Você precisará desses dois itens para cumprir a missão.\n\nCaso seja atacado por papagaios nada amigáveis, use o B.E.C.A!", description = "Sem querer te assustar, <name>, os últimos cinco ladinos que tentaram roubar o tesouro voltaram em pedacinhos.\n\nAtracado no sul da costa está o Lâmina das Marés, o navio dos flibusteiros, que guarda o tesouro dos Mares do Sul. No piso inferior fica o covil dos piratas, onde eles treinam todos os ladrões.\n\nSuba a bordo do navio, roube o tesouro e traga-o para mim. Use o covil dos piratas se precisar treinar suas técnicas de ladroagem, mas tome cuidado!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nChá de Cardo"},
[2382] = {title = "Wrenix da Vila Catraca", objective = "Viaje para a Vila Catraca e fale com Wrenix, o Ignóbil.", description = "Zando'zan viu, sim! O dia vai chegar! A morte de Thrall... A adaga nas costas, o veneno correndo nas veias dele... Zando'zan já falou demais.\n\n<name>! Zando'zan tem tarefa para <name>! <name> tem que ir à Vila Catraca, a leste da Encruzilhada, e falar com Wrenix, o Ignóbil. Anda! Vai logo!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2383] = {title = "Pergaminho simples", objective = "Leia o Pergaminho Simples e fale com Frang no Vale das Provações.", description = "Ah, enquanto você estava fora, chegou um pergaminho para você, <name>.\n\nLeia quando tiver tempo. Se não me engano, é do instrutor de guerreiros Frang. Ele quer falar com você assim que você estiver <pronto/pronta>. Ele fica na entrada do Covil, protegido na sombra.", progress = "O que é isso que você tem aí? Um pergaminho? Você não é um erudito, é?", completion = "Ah, esse é o meu pergaminho. Ouvi dizer que você estava vindo para o Vale, &lt;name&gt;. Que bom que você chegou — vamos torcer para que você sobreviva às provações adiante. Como eu disse, venha falar comigo conforme ganha poder — farei o que estiver ao meu alcance para treinar você na arte do &lt;class&gt;.\n\nBoa sorte, &lt;name&gt;, volte aqui sempre que sentir que é a hora.", rewards = "Você receberá:\n\n"},
[2398] = {title = "Os anões perdidos", objective = "Encontre Baelog em Uldaman.", description = "A escavação de Uldaman nem sempre foi tomada desse jeito. Nós, anões de Altaforja, controlávamos a área antes, mas... isso foi antes de os troggs chegarem. Eles vieram tão rápido e em números tão grandes que nem mesmo o poder dos anões conseguiu detê-los!\n\nNa verdade, o ataque deles foi tão repentino que receio que algumas pessoas possam ter ficado presas atrás das linhas inimigas.\n\nMeu amigo Baelog liderava um pequeno grupo de anões no interior do local pouco antes de os troggs aparecerem. Se ele e seu grupo estiverem vivos, quero que tu os encontre.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2399] = {title = "As frondes desenvolvidas", objective = "As frondes que Denalan plantou no jardim dele brotaram e cresceram. Elas tremulam, ansiosas para serem colhidas....", description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nFronde Desenvolvida"},
[2418] = {title = "Pedras de energia", objective = "Leve 8 Pedras de Energia de Dêntrio e 8 Pedras de Energia de An'Alleum para Bertolezo nos Ermos.", description = "Um tempo atrás, tive um incidente com os anões de Umbraforja. Eles me levaram para a Fortaleza Angor para estudar dois tipos de pedras estranhas de Uldaman. Eu descobri que as pedras eram fontes de energia estáveis e sustentáveis se tratadas corretamente. É incrível o tipo de coisa que se pode encontrar naquela antiga escavação!\n\nEnfim, depois que eu contei a eles o segredo das pedras, eles me expulsaram da Fortaleza Angor e começaram a cavar atrás de mais pedras em Uldaman.\n\nEu quero aquelas pedras para mim! Traga-me os dois tipos e eu vou lhe pagar uma boa grana!Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nCírculo de Pedra EnergizadoBraçadeiras de DuracinBotas Sempiternas\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[2438] = {title = "O apanhador de sonhos de esmeralda", objective = "Leve o Apanhador de Sonhos de Esmeralda para Tallonkai Radicélere em Dolanaar.", description = "Certa vez, Garyol Talvethren, guardião dos druidas na Gruta de Ban'ethil, me presenteou com um apanhador de sonhos de esmeralda. Este poderoso amuleto é capaz de canalizar a energia do Sonho Esmeralda, concedendo sorte a quem o possuir.\n\nInfelizmente, não consegui recuperá-lo de meu armário no Vilarejo Brisastral... Embora Brisastral já tenha sido um lugar tranquilo, hoje sucumbe à corrupção dos pelursos que lá vivem.\n\nPoderia me ajudar a recuperar meu apanhador de sonhos, <class>?", progress = "Por favor, apresse-se. Tudo que me resta é torcer para que meu apanhador de sonhos não tenha sido destruído pelos pelursos.\n\nVocê o conseguiu de volta, <name>?", completion = "O apanhador de sonhos de esmeralda é de grande importância para mim. É um presente concedido a poucos. Obrigado por recuperá-lo, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[2439] = {title = "Os discos de platina", objective = "Leve o vale-recompensa para Dinita Itamanto no Cofre de Altaforja.", description = "Este objeto é um achado incrível. Os antigos Titãs, ou os Criadores, como esses discos se referem a eles, são o elo para nossa criação encantada, se a lenda armazenada aqui estiver correta! Precisamos investir o máximo de tempo de pesquisa possível.\n\nE tu, valente <class>, leva este vale pra Dinita Itamanto, lá no cofre. Ele te dá o que eu acho que é apropriado por uma descoberta tremenda!\n\nVem direto falar comigo quando estiver <pronto/pronta>; a Liga dos Exploradores tem muito trabalho pra ti ainda!Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nPoção Superior de CuraPoção Maior de Mana\n\nVocê também receberá:\n\nSaco de Degelocouro"},
[2440] = {title = "Os discos de platina", objective = "Leve o vale-recompensa à Bena Casco Invernal no Penhasco do Trovão.", description = "Vou concentrar minhas energias nesses discos; seu verdadeiro funcionamento se revelará a mim no devido tempo.\n\nQuanto a você, <name>, vou recompensá-l<o/a> por trazer isso prontamente à minha atenção. Leve este vale-brinde para Bena Casco Invernal, a instrutora de alquimia do Penhasco do Trovão. Ela vai lhe dar um item especial que acredito que achará interessante, e lhe fornecerá algumas poções do meu estoque pessoal.\n\nFale novamente comigo, pois sinto que temos muito mais a fazer com essa descoberta.", progress = "Saudações, <class>! Você veio aqui à procura de treinamento em alquimia, ou será alguma outra coisa?", completion = "Bem, &lt;name&gt;, você deve ter agradado bastante o Sábio Devoto da Verdade para ele ter lhe dado este item. E receberá ainda um presente de seu estoque de poções. Você pode escolher entre 5 poções superiores de cura ou 5 poções maiores de mana como recompensa.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nPoção Superior de CuraPoção Maior de Mana\n\nVocê também receberá:\n\nSaco de Degelocouro"},
[2458] = {title = "Missão secreta", objective = "Vá à Torre da Empreendimentos S.A., ao norte do Charco de Lodo, e entre em contato com o agente secreto da Mão Despedaçada, o Capataz Arruela.\n\nQuando a torre estiver à vista, use o Sinalizador para anunciar sua chegada ao Capataz Arruela. Dispare DUAS vezes, depois aproxime-se do Capataz e faça a /continencia dos Mão Despedaçada.\n\nEstas ações devem ser executadas na ordem correta, já que Arruela vai atacar e eliminar qualquer pessoa que não reconheça.\n\nItem fornecido:\n", description = "A Empreendimentos S.A.... Já ouviu falar deles?\n\nPor um ano inteiro nós fizemos de tudo para infiltrar um agente lá dentro. Depois de muito esforço e planejamento, nós conseguimos.\n\nRecentemente, ficamos sabendo que a Empreendimentos S.A. está tramando alguma coisa em grande escala. Vá para o norte do Charco de Lodo, até a torre deles, e entre em contato com o nosso agente infiltrado, o Capataz Arruela.\n\nQuando encontrar a torre, dispare este sinalizador, aborde o Arruela e bata a continência da Mão Despedaçada.", progress = nil, completion = "É bom ver outro agente da Mão Despedaçada por essas bandas, mas o sinalizador pode ter sido um pouco demais!\n\nDa próxima vez que precisar falar comigo, sopre este apito duas vezes e saberei que é um aliado.", rewards = "Você receberá:\n\nApito do Arruela"},
[2459] = {title = "Ferócitas, o Comedor de Sonhos", objective = "Tallonkai Radicélere, em Dolanaar, quer que você mate 7 Místicos Masca-pinho e encontre a Joia Roubada.", description = "A esmeralda... desapareceu! Meu apanhador de sonhos foi danificado!\n\nExiste um bando de místicos Masca-pinho ao norte de Brisastral. Ouvi relatos de que o líder deles, Ferócitas, o Comedor de Sonhos, está usando um colar que emite um brilho verde à noite. E agora, vendo o que aconteceu com o meu apanhador de sonhos, tenho certeza de que ele roubou minha esmeralda... Ferócitas nunca entenderá que o poder da pedra é inútil para ele.\n\nEncontre a joia roubada, <name>. E, quando estiver lá, aproveite para acabar com alguns místicos corrompidos.", progress = "Ferócitas e os místicos Masca-pinho têm que devolver o que me pertence. Por favor, recupere a esmeralda para que eu possa consertar meu apanhador de sonhos.", completion = "Obrigado, &lt;name&gt;! Agora posso consertar meu apanhador de sonhos.", rewards = "Você receberá:\n\nColar de Masca-pinho"},
[2460] = {title = "A saudação dos Mão Despedaçada", objective = "Faça a Continência dos Mãos Despedaçada para Shenthul.", description = "Como um ladino da Mão Despedaçada, é imperativo que você aprenda a Continência dos Mãos Despedaçada. É a única maneira de realmente identificar outro membro da Mão.\n\nObserve, aprenda, repita.", progress = "Pratique!", completion = "Muito bem, &lt;name&gt;. Agora você pode continuar.", rewards = "Você receberá:\n\n"},
[2478] = {title = "Missão: possível, mas não provável", objective = "Roube a Chave da Torre do Capataz Biela e, em seguida, mate 2 Peões Mutantes da Empreendimentos S.A., 2 Patrulheiros da Empreendimentos S.A. e 2 Vigias da Empreendimentos S.A.\n\nArranque a cabeça do Capataz-chefe Gallywix, arrombe o cofre dele e roube o Esconderijo do Preparado Adulterado do Zanzil. Leve-o junto com a cabeça para Shenthul em Orgrimmar para receber mais instruções.", description = "Você precisará roubar a chave da torre do Capataz Biela, <name>. Quando consegui-la, guarde bem... posso precisar dela mais tarde.\n\nLembre-se, o veneno enfraqueceu tudo nesta torre. Use suas habilidades para trazer o caos e a destruição aos moradores. Mate todos eles — não tenha piedade!\n\nAssim que terminar o derramamento de sangue no interior da torre, pegue o cofre de Gallywix e roube o estoque do Preparado de Zanzil guardada lá dentro. Leve a cabeça de Gallywix e a mistura para Shenthul, em Orgrimmar.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nReceita: Chá de Cardo"},
[2479] = {title = "A ajuda de Hinoto", objective = "Vá até a Serraria Tarren, que fica no Contraforte de Eira dos Montes, e entregue a Amostra do Preparado de Zanzil a Serguei Hinoto.\n\nPara chegar à Serraria Tarren, pegue o Zepelim até a Cidade Baixa e siga a estrada em direção ao sul além de Pinhaprata, rumo à Eira dos Montes. É só seguir as placas!", description = "Leva uma amostra dessa mistura pro Serguei Hinoto, na Serraria Tarren.\n\nVou falar, cara, se alguém pode curar você, é o Serguei. E se tu viver o suficiente, o Serguei pode até te mostrar as manhas de como criar e usar os venenos.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2480] = {title = "A ajuda de Hinoto", objective = "Espere Serguei Hinoto concluir a cura.", description = "Nunca vi um veneno trazer resultados tão formidáveis! Zanzil, não é mesmo? Nunca ouvi falar dele, no entanto, devo louvá-lo pela criação desta mistura.\n\nEntão, devo curá-l<o/a> disso e depois ensiná-l<o/a> a usar o veneno?\n\nDê-me alguns instantes para examinar a toxina, <name>.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nVenenos\n\nVocê receberá:\n\nÓleo do Hinoto"},
[2498] = {title = "Fale com Denalan", objective = "Rellian Spiraverde quer que você fale com Denalan, no Lago Al'Ameth.", description = "Acabo de receber a notícia de que Denalan, no Lago Al'Ameth, descobriu o que pode ser a causa dos tumores que afligem os muscoides. Por favor, fale com ele e diga-lhe que <o/a> enviei.\n\nAnde logo, <name>: ele precisa da nossa ajuda.", progress = nil, completion = "Ah, então Rellian enviou você para me ajudar. Estou feliz que tenha chegado rápido.\n\nDescobri algo bastante perturbador!", rewards = "Você receberá:\n\n"},
[2499] = {title = "Carrancarvalho", objective = "Denalan, no Lago Al'Ameth, quer que você remova o Tumor Gigantesco de Carrancarvalho.", description = "Em uma caverna à margem sul do lago, um muscoide chamado Carrancarvalho está espalhando a corrupção para todas as criaturas que fazem do Lago Al'Ameth seu lar.\n\nNão ousei me aproximar demais, mas, mesmo à distância, é óbvio: Carrancarvalho está sendo envenenado pelo maior tumor que já vi... gigantesco, até.\n\n<name>, você já fez muito pela minha causa, mas lhe peço mais um favor. Vá atrás de Carrancarvalho e remova o tumor. Elimine essa fonte de corrupção do meu lar.", progress = "Conseguiu localizar o Carrancarvalho, <race>?", completion = "Eu sabia que você seria capaz de livrar o lago daquela horrível criatura.\n\nEsta é uma situação lastimosa. Carrancarvalho já foi um grande líder entre seu povo... mas a corrupção não difere nobres de plebeus.\n\nO tamanho deste tumor é perturbador, mas preciso estudá-lo para melhor entender a doença que afeta a população muscoide.\n\nObrigado, &lt;name&gt;.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCinto Bosque de TerraManoplas Cobertas de Musgo\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[2500] = {title = "Busca pelos reagentes dos Ermos", objective = "Consiga os reagentes dos Ermos de que Ghak Toquecura precisa, então fale novamente com ele em Thelsamar.", description = "<class>, eu poderia contar com tua ajuda. Sou um alquimista de certo renome, e preciso da tua ajuda para conseguir alguns reagentes encontrados lá pras bandas dos Ermos.\n\nPreciso de uma barbaridade de coisas para reabastecer meus estoques: cinco moelas de urubu, dez presas de coiote do rochedo e cinco estilhaços de elementais da rocha. Obviamente, tu pode tirar essas coisas dos próprios bichos. Se tu não é do tipo que peleja, então trate de encontrar amigos que sejam.\n\nConsegue essas coisas pra mim, <guri/guria>, que tu lucra umas pilas.\n\nEntão, o que tu acha?", progress = "Já conseguiste as coisas de que preciso? Tu não vais receber um tostão furado enquanto não me trouxer esses reagentes.", completion = "Ah, trilegal, &lt;name&gt;. Isso aqui deve durar algum tempo pelo menos. Toma aqui tuas pilas, como prometido.\n\nSe tiver interesse, tem mais alguns reagentes aí que estou precisando. Mas esses tu só vai achar nas profundezas da escavação de Uldaman. Não vai ser fácil, tchê, mas tiver interesse em me ajudar de novo e aguentar o tirão, vou fazer valer a pena.\n\nBah, talvez tu ganhe mais do que algumas moedas brilhantes dessa vez, o que acha?", rewards = "Você receberá:\n\n"},
[2501] = {title = "Busca pelos reagentes dos Ermos, parte II", objective = "Use os Recipientes de Taumaturgia Vazios em Dragões Guardiões Calcinados encontrados nos Ermos. Quando enchê-los, leve-os para Ghak Toquecura em Thelsamar.", description = "Tu tem sido útil que é uma barbaridade, <name>. Gostaria de te recompensar com a receita da bebida que te dei mais cedo, mas preciso te pedir um favor triperigoso.\n\nDentro deste cofre tem três recipientes de taumaturgia vazios. Eles estão imbuídos de uma aura de harmonização que drenará o sangue de um dragão guardião calcinado. Usar um na fera vai deixá-la mais nervosa que elfo com dor de barriga, então tenha cuidado. Assim que encher todos os três, traga-os para mim.\n\nFaça isso e a receita será tua.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nPoção de Restauração\n\nVocê receberá:\n\n"},
[2518] = {title = "Lágrima da Lua", objective = "A Sacerdotisa A'mura no Templo da Lua, em Darnassus, quer que você lhe traga as Fiandeiras Prateadas de Lady Sathrah.", description = "Lady Sathrah já foi amada por Eluna. Graciosa e pura, a aranha tecia seus fios prateados sob a luz da lua, capturando a bruma da noite. O orvalho prateado possuía fortes poderes curativos e costumava ser guardado aqui no templo.\n\nMas, nos últimos tempos, Sathrah sucumbiu à loucura, ameaçando também suas gerações futuras.\n\nEncontre Lady Sathrah, <name>, e acabe com o sofrimento dela. Ela vive na fronteira norte de Teldrassil, perto do Lago Olho-d'Água. Pegue as fiandeiras prateadas dela e traga-as para mim.", progress = "Me arrependo de lhe pedir para cumprir esta tarefa, mas não existe salvação para a Dama Sathrah.\n\nNossa única esperança é oferecer as fiandeiras dela como sacrifício para Eluna. Esperamos que Eluna abençoe Sathrah para que ela renasça em paz.", completion = "A floresta lamenta por Lady Sathrah, mas era algo que precisava ser feito.\n\nObrigada, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[2519] = {title = "O Templo da Lua", objective = "Irmã Aquinne quer que você fale com a Sacerdotisa A'mura no Templo da Lua.", description = "Uma criatura outrora grandiosa da floresta precisa de ajuda, <race>.\n\nUma difícil tarefa precisa ser cumprida.\n\nProcure a Sacerdotisa A'mura, ela lhe explicará tudo. Encontre-a a sudeste daqui, dentro do Templo da Lua.", progress = nil, completion = "Então, a Irmã Aquinne mandou você?", rewards = "Você receberá:\n\n"},
[2520] = {title = "Sacrifício de Sathrah", objective = "A Sacerdotisa A'mura quer que você coloque as fiandeiras prateadas de Lady Sathrah na fonte dentro do templo, e depois fale novamente com ela.", description = "Agora você deve concluir a tarefa para a qual foi designad<o/a>. Leve as fiandeiras e ofereça o sacrifício de Lady Sathrah à Eluna. Faça com que a grande aranha encontre seu descanso final.\n\nEncontre a fonte central dentro deste templo. É onde você deve deixar o sacrifício. As águas sagradas vão purificar e limpar a corrupção que enlouqueceu Sathrah.\n\nQuando terminar, fale novamente comigo.", progress = nil, completion = "A perda de Lady Sathrah é desoladora, mas só assim ela poderá renascer com o espírito renovado.\n\nQue Eluna aceite de bom grado o sacrifício que você lhe ofereceu.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCalças GalonadasBotas Almofadadas\n\nVocê também receberá:\n\n"},
[2521] = {title = "A serviço de Kum'isha", objective = "Em suas jornadas pela Barreira do Inferno, caso encontre uma esfera de draenetista impecável, leve-a de volta para Kum'isha, o Colecionador.\n\nAtenção, essa pedra é um dos cristais mais raros de toda Azeroth. Qualquer criatura nestas terras poderia ter em seu poder uma esfera de draenetista impecável.\n\nVocê será recompensad<o/a> por cada esfera de draenetista impecável que conseguir.", description = "<race>, seu cérebro de ervilha é capaz de contemplar a raridade de uma esfera perfeita de draenetista? Os draeneis passaram vidas inteiras em busca de apenas um desses cristais, só para sofrer sinas horríveis, sem cumprir seu propósito.\n\nEntão você aparece diante de Kumi'sha, sabendo o que Kumi'sha requisitou, e ainda assim você quer ajudar? Pois bem, <race>. Durante suas jornadas, sempre que você encontrar uma esfera perfeita de draenetista, traga-a para mim, e eu lhe recompensarei com riquezas sem fim.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nArca Incrustada de Esmeraldas"},
[2522] = {title = "Empreitadas do Kum'isha", objective = "Encontre e entregue uma Esfera de Draenetista Impecável para Kum'isha, o Colecionador.", description = "Mesmo que a conjuração do portal tenha sido um fracasso, acredito que meus próximos esforços serão bem-sucedidos.\n\nA esperança dos draeneis é tão fervorosa quanto sua cobiça por bugigangas, <race>. Volte para aquelas terras amaldiçoadas e recupere outra esfera de draenetista impecável .\n\nClaro que a decisão de aceitar essa tarefa cabe somente a você *sorriso*.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nArca Incrustada de Esmeraldas"},
[2523] = {title = "Flor da Canção corrompida", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[2541] = {title = "O druida adormecido", objective = "Traga o Patuá Vodu de Xamã para Oben Patafúria na Gruta de Ban'ethil.", description = "Os xamãs Masca-pinho que habitam este lugar descobriram um jeito de separar o espírito de um druida adormecido do corpo físico dele. Os pelursos reanimaram meu corpo e estão usando-o para atacar qualquer um que tente explorar a Gruta de Ban'ethil. Agora estou preso no Sonho Esmeralda, incapaz de impedir isso.\n\nVocê precisa me ajudar. Os xamãs Masca-pinho carregam um estranho patuá que usam para realizar este ritual. Gostaria de examiná-lo. Por favor, <name>, traga um para mim.", progress = "Se eu conseguir examinar o patuá, talvez consiga descobrir uma maneira de quebrar o encantamento. Encontrou algum?", completion = "Obrigado, &lt;name&gt;.\n\nQue estranho adorno... Posso sentir a aura maligna que emana dele. Sem dúvida, um encantamento muito poderoso.", rewards = "Você receberá:\n\n"},
[2561] = {title = "Druida da Garra", objective = "Oben Patafúria quer que você mate seu corpo sem alma e, em seguida, use o Patuá Vodu.", description = "Após examinar este patuá, <name>, descobri o que precisa ser feito. Por favor, pegue-o e faça o que eu disser.\n\nVocê deve explorar as áreas mais profundas da Gruta de Ban'ethil. Lá dentro, você encontrará meu corpo sem alma... Lamento muito pelo que vou lhe pedir, mas não vejo outra maneira de me libertar do controle dos Masca-pinho.\n\nPara que eu possa escapar deles, você terá de matar minha forma física e usar o Patuá Vodu em meu corpo inerte. Após completar esta tarefa, fale novamente comigo.", progress = nil, completion = "Finalmente estou livre do controle dos Masca-pinho! Obrigado, &lt;name&gt;.\n\nAgora, meu espírito pode descansar em paz para sempre no Sonho Esmeralda.\n\nTalvez um dia nos encontremos novamente, jovem &lt;class&gt;. Mas, por ora, aceite esta recompensa como símbolo de minha gratidão, por favor.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nVestes de DormirLâmina Matagal\n\nVocê também receberá:\n\n"},
[2581] = {title = "Mandíbulas de rasgarrindo", objective = "Traga três Mandíbulas de Rasgarrindo, dois Pulmões de Javali Destroçados e uma Tenaz de Escórpode para o Mago Sangrento Drazial.", description = "É um fato notório que as hienas possuem uma força tremenda em suas mandíbulas, mas força para romper sete centímetros de aço tório? Impossível, improvável e, no entanto, pude contemplar isso com meus próprios olhos.\n\nAvance pela Barreira do Inferno e traga-me três mandíbulas de rasgarrindo. Enquanto procura pela rasgarrindo, fique atento a javalis e escórpodes. A amostra inicial também requer dois pulmões de javali destroçados e uma tenaz de escórpode.", progress = "Você está fedendo, <name>. Andou perambulando por campos de enxofre? E onde estão meus componentes?!", completion = "Como se sente, &lt;name&gt;? Vejo que irradia a aura do rasgarrindo — sua força, inalcançável por mortais!\n\nInfelizmente, com os componentes que me trouxe, só consegui amostra suficiente para um tablete. Vou precisar de mais componentes, caso você precise de outro.", rewards = "O seguinte feitiço será lançado em você:\n\nFúria das Eras\n\nVocê receberá:\n\nB.O.M.B.A."},
[2582] = {title = "Fúria das Eras", objective = "Leve três Mandíbulas de Rasgarrindo, dois Pulmões de Javali Destroçados e uma Tenaz de Escórpode para o Mago Sangrento Drazial.", description = "Ah, <name>, bem-vind<o/a> de volta. Parece que você gostou da aplicação robusta e operacional derivada do rasgarrindo. Um aviso, embora o efeito imediato seja uma explosão titânica de força, a longo prazo pode ter consequências perigosas para sua inteligência e seu espírito.\n\nDito isso, você tem certeza de que quer mais? Se for o caso, traga-me mais três mandíbulas de rasgarrindo, dois pulmões de javali destroçados e uma tenaz de escórpode.", progress = "Você trouxe os órgãos das feras?", completion = "Prontinho! Trate de usar com parcimônia. Se precisar de outra, fale comigo quando acabar com essa aí.", rewards = "Você receberá:\n\nB.O.M.B.A."},
[2583] = {title = "A vitalidade dos javalis", objective = "Leve 3 Pulmões de Javali Destroçado, 2 Tenazes de Escórpode e 1 Cérebro de Basilisco para o Mago Sangrento Drazial.", description = "Há, nestas terras dizimadas, duas espécies de javali: os javalis jubacinza e os javiltuscos.\n\nAo que tudo indica, o grande vigor destas criaturas praticamente lhes garante imunidade, já que os demais animais as consideram difíceis demais de se matar e preferem gastar energia com outras presas.\n\nPrecisamos conduzir uma análise mais aprofundada, <name>. Traga-me três pulmões de javali destroçados, duas tenazes de escórpode e um cérebro de basilisco para que eu possa criar um elixir divino com o resíduo da amostra. Você será a minha primeira cobaia!", progress = "Onde estão os órgãos, <name>!?", completion = "O espírito do javali ilumina o seu ser, &lt;name&gt;! Diga-me... Você se acha tão invulnerável quanto essas feras?", rewards = "O seguinte feitiço será lançado em você:\n\nEspírito de Javali\n\nVocê receberá:\n\nCoquetel de Sumo de Pulmão"},
[2584] = {title = "Espírito do javali", objective = "Leve 3 Pulmões de Javali Destroçado, 2 Tenazes de Escórpode e 1 Cérebro de Basilisco para o Mago Sangrento Drazial.", description = "Da primeira vez em que administrei o elixir, <name>, logo vi que uma ou duas doses não aplacariam o seu apetite. Agora mesmo, vejo a fome em seus olhos. A beberagem tem propriedades adictícias que, ao que parece, suplantam até mesmo a vontade mais forte.\n\nEntão que assim seja! Caso queira consumir um pouco mais do coquetel de sumo de pulmão, volte às terras devastadas e me traga três pulmões de javali destroçados, duas tenazes de escórpode e um cérebro de basilisco.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nCoquetel de Sumo de Pulmão"},
[2585] = {title = "Ataque decisivo", objective = "Leve 3 Tenazes de Escórpode, 2 Moelas de Abutre e 1 Pulmão de Javali Destroçado para o Mago Sangrento Drazial.", description = "Os escórpodes, <name>. Já os viu em batalha? Já testemunhou a graça e agilidade em seus movimentos? A precisão de seus ataques?\n\nSe ao menos pudéssemos encontrar um modo de isolar e encapsular essa agilidade, ficaríamos podres de ri... digo... prestaríamos um valoroso serviço à comunidade científica.\n\nAjude-me, <name>, e eu hei de lhe conceder uma amostra do preparado.\n\nPara criar um composto estável, precisarei de três tenazes de escórpode, duas moelas de abutre e um pulmão de javali destroçado.", progress = "Onde estão os órgãos, <name>!?", completion = "Fascinante. De todas as amostras, esta foi a mais difícil de analisar.\n\nPegue uma pitada deste sal residual, aplicando-o sob a língua. Você terá uma leve euforia, seguida de um estado de agilidade e velocidade nunca dantes vistos.", rewards = "O seguinte feitiço será lançado em você:\n\nGolpe do Escórpode\n\nVocê receberá:\n\nAmostra de Escórpode Macerado"},
[2586] = {title = "Sal do escórpode", objective = "Leve três Tenazes de Escórpode, duas Moelas de Abutre e um Pulmão de Javali Destroçado para o Mago Sangrento Drazial.", description = "Você está viv<o/a>! Boas novas, de fato. Presumo que tenha voltado para buscar mais da amostra do escórpode, sim?\n\nPois bem, <name>, volte sem demora às terras devastadas e só me apareça aqui quando tiver três tenazes de escórpode, duas moelas de abutre e um pulmão de javali destroçado.", progress = "Onde estão os órgãos, <name>!?", completion = "Excelente trabalho, &lt;name&gt;. Tome aqui uma amostra do material para você. O resto eu vou usar na minha pesquisa.\n\nCaso você precise de mais escórpode triturado, é só vir falar comigo que nós nos acertamos.", rewards = "Você receberá:\n\nAmostra de Escórpode Macerado"},
[2601] = {title = "A mordida do basilisco", objective = "Leve 10 Cérebros de Basilisco e 2 Moelas de Abutre para a Maga Sangrenta Lynnore.", description = "Estas terras são infestadas de basiliscos itarrubra, <name>. Esses basiliscos se adaptaram e se estabeleceram melhor do que qualquer outro animal da região.\n\nJá passei muito tempo observando de longe o comportamento cotidiano destas feras, e posso afirmar que o processo de raciocínio delas é mais complexo do que o de muitos humanoides.\n\nPreciso estudar o cérebro dessas criaturas!\n\nTraga-me dez cérebros, <name>. Contudo, a solução não estará completa sem duas moelas de abutre para servir de base.", progress = "Trouxe os órgãos que pedi?", completion = "Esplêndido, &lt;name&gt;! Ofereço-lhe uma prova do funcionamento interno do basilisco itarrubra.\n\nCaso o composto seja de seu agrado, eu sempre posso fazer mais... por um preço, é claro.", rewards = "O seguinte feitiço será lançado em você:\n\nMente Infalível\n\nVocê receberá:\n\nComposto de Córtex Cerebral"},
[2602] = {title = "Mente infalível", objective = "Leve dez Cérebros de Basilisco e duas Moelas de Abutre para Maga Sangrenta Lynnore.", description = "A sua concentração aprimorada já se esvaiu? Preciso aprofundar minhas pesquisas!\n\nAh, claro, o preço continua o mesmo, <name>. Vá até a Barreira do Inferno e traga dez cérebros dos itarrubra e duas moelas de abutre.", progress = "Trouxe os órgãos que pedi?", completion = "Que beleza! Só um instantinho enquanto eu preparo mais desse composto para você, &lt;name&gt;.\n\nPronto! Aah, &lt;name&gt;... Não beba essas misturas experimentais uma atrás da outra. Isso pode resultar em algo terrível.", rewards = "Você receberá:\n\nComposto de Córtex Cerebral"},
[2603] = {title = "O vigor do abutre", objective = "Leve 10 Moelas de Abutre e 2 Mandíbulas de Rasgarrindo para a Maga Sangrenta Lynnore.", description = "Nestas terras dizimadas, <race>, a morte, de fato, é uma entidade alada. Já presenciei os matadores negros e os limpa-ossos se refestelando nos cadáveres de inúmeros exploradores mortos – às vezes até mesmo nas carcaças mais putrefatas e empesteadas. Contudo, é um milagre que os sistemas internos dessas criaturas consigam superar a pestilência que domina essas terras.\n\nAjude-me em minha pesquisa, <name>! Preciso de moelas! Duas mandíbulas de rasgarrindo também se fazem necessárias para constituir a base do preparado.\n\nVocê será muito bem recompensad<o/a> pelos seus esforços.", progress = "Pare de desperdiçar meu tempo, <name>. Não tenho nada a lhe dizer até que me traga os itens que pedi.", completion = "Excelente! Vou começar a trabalhar imediatamente. Talvez possa até encontrar a cura para muitas das moléstias do mundo!\n\nAh, sim, sua recompensa – se depois desejar mais chiclete, volte aqui e eu lhe darei mais uma tarefa.", rewards = "O seguinte feitiço será lançado em você:\n\nDominação Espiritual\n\nVocê receberá:\n\nChiclete de Moela"},
[2604] = {title = "Dominação espiritual", objective = "Leve 10 Moelas de Abutre e 2 Mandíbulas de Rasgarrindo para a Maga Sangrenta Lynnore.", description = "Vejo que o fogo interior se apagou. Você diria que a euforia espiritual durou uma hora inteira?\n\nSe mais chiclete é a sua vontade, de mais moela tenho necessidade. Pode voltar para as terras ermas, <name>. Retorne apenas quando conseguir dez moelas e duas mandíbulas.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nChiclete de Moela"},
[2605] = {title = "O goblin sedento", objective = "Colete uma Glândula de Orvalho Cheia e leve para Marin Nublacuca em Geringontzan.", description = "Sabe, eu tenho uma ideia. Ouvi falar que tem uns bichos chamados coletores de orvalho... eles vivem a sudoeste daqui, e dizem por aí que eles são capazes de armazenar água em glândulas especiais.\n\nAcho que, se eu pusesse as mãos em uma glândula dessas, eu conseguiria produzir uma bebida capaz de aplacar minha terrível sede! Acha que pode me ajudar, <name>?\n\nProcure o jardim de cactos, <name>, pois lá você encontrará os coletores de orvalho.", progress = "Por favor, diga que achou os coletores de orvalho...", completion = "Ah, que maravilha! Passa logo para cá... quero beber!", rewards = "Você receberá:\n\nGlândula de Orvalho Perfurada"},
[2606] = {title = "Gosto não se discute", objective = "Marin Nublacuca quer que você fale com Sguisha em Geringontzan.", description = "Eca! O gosto dessa coisa é horrível! Chega dessa história...\n\nComo é que é? Tá, tá, você acha que eu não devia desistir desse negócio. Beleza.\n\nBom, eu tive uma ideia! Uma ideia brilhante! Leva esse orvalho pra minha amiga Sguisha — ela é uma chef de mão cheia. Diz pra ela o que a gente tá fazendo e que a gente quer que esse troço desça mais fácil.\n\nSe vai ter um gosto ruim, tem que pelo menos ter um tchan... E, no momento, não tem nenhum...", progress = "Ah, então o velho Marin tá aprontando de novo? Do que é feito esse preparado?", completion = "Veja bem, Marin é um cara legal, mas ele é um pouco... excêntrico, eu diria.\n\nE é claro que, mais uma vez, ele está precisando da minha ajuda.", rewards = "Você receberá:\n\n"},
[2607] = {title = "O toque de Zanzil", objective = "Fale com o Doutor Mixilpixil no porão do Quartel.", description = "Parece que você trouxe muito mais informações do que eu esperava. De fato, é uma reviravolta peculiar, <name>.\n\nUma cura? Hm... vá até o porão e fale com o Doutor Mixilpixil, talvez ele possa ajudar.", progress = nil, completion = "Já não era sem tempo! Deixe o queijo na mesa e pode se pirulitar. Osborne vai te pagar na saída.", rewards = nil},
[2608] = {title = "O toque de Zanzil", objective = "Você precisa /deitar para ser <examinado/examinada> pelo Doutor Mixilpixil. Como é que ele vai descobrir a cura se não puder identificar a causa do problema?", description = "Carambola, você está a cara da morte. Correção: eu já vi a morte, você está ainda pior. Você, mocinh$Go:a;, seria algo que a morte escarraria no derradeiro exame médico.\n\nRá! Ouviu essa, Quilo? Pois trate de anotar essa!\n\n*Tosse* Hm, sim, posso te ajudar, mas primeiro preciso coletar seu sangue. Não se preocupe, só dói nas primeiras... cinquenta vezes... <name>, quando estiver pront<o/a> para o tratamento, é só deitar.", progress = nil, completion = "Só um momento, &lt;name&gt;, preciso consultar os meus colegas. Dr. Monteiro, sr. Quilo, vamos botar a cuca para trabalhar!", rewards = "Você receberá:\n\n"},
[2609] = {title = "O toque de Zanzil", objective = "Leve para o Doutor Mixilpixil um pacote de Flores Silvestres Simples, uma Ampola Chumbada, um Tubo de Bronze e uma Bobina de Seda Chartreuse Leve. \"Ite\" nenhuma se cura sozinha, <meu/minha> jovem.", description = "É unânime, <name>. Você está com \"ite\".\n\nPor sorte, eu conheço a cura para \"ite\".\n\nEu preciso de umas coisinhas: um ramalhete de flores silvestres simples, uma ampola chumbada, um tubo de bronze e por último, mas não menos importante, uma bobina de seda chartreuse leve. A Tia Bia deixou um rolo para mim no Ao Fio d'Ouro. A loja fica na margem dos canais.\n\nEntão vá logo, bem depressinha, <name>, e tente não encostar em ninguém até se curar. Ah, e o dr. Monteiro disse que você está precisando de um banho... ele que disse, estou só passando o recado!", progress = "Se quer curar essa \"ite\", vai ter que se esforçar mais, <name>. Cadê os reagentes para a cura?", completion = "O que eu estou fazendo? O que parece que eu estou fazendo? Estou desenvolvendo a cura para a sua \"ite\"! Não vá me esculhambar agora, &lt;name&gt;!\n\nSó mais um momentinho. Pronto! É só borrifar este remédio de odor, hã, fantástico e você ficará novinho em folha.", rewards = "Você receberá:\n\nColônia de Mixilpixil"},
[2621] = {title = "O desonrado", objective = "Fale com o Comandante de Expedição Ruag em Pedregal.", description = "Arrependimento. É tudo que me resta. Uma decisão infeliz que teve várias consequências terríveis. Se eu, ao menos, tivesse esperado as ordens. Talvez elas detalhassem os horrores que nos aguardavam naquelas terras amaldiçoadas! Talvez elas nos redirecionassem a outra missão. No final das contas, a culpa foi minha. Foi tudo culpa minha.\n\nAqui estou, esperando ordens. Eu preciso delas. Preciso dessas ordens.\n\nO Comandante de Expedição Ruag fica lá em Pedregal. Encontre-o e descubra o que houve com as ordens.", progress = nil, completion = "Garra de Thrall... a história ainda está vívida na minha mente.\n\n&lt;Comandante Ruag balança a cabeça&gt;\n\nTerrível o que aconteceu com aquele regimento. O batalhão foi destruído e a Horda perdeu alguns de seus melhores soldados.", rewards = "Você receberá:\n\n"},
[2622] = {title = "As ordens desaparecidas", objective = "Fale com Bengor.", description = "As ordens? O que raios você quer com as ordens?\n\nO que você está insinuando, <name>? É claro que entreguei! Os soldados a quem eu confiei as ordens foram emboscados e agredidos brutalmente por murlocs. Só um sobreviveu, se é que se pode chamar o estado dele de sobrevivência. Ele mal fala, não anda mais e tem que ser alimentado por um tubo. Se quer saber o que aconteceu com as ordens, fale com Bengor. Ele deve estar por aqui em algum lugar... e, <name>, cuidado onde você mete o nariz... pode acabar ficando sem ele.", progress = nil, completion = "Falastrão do Pan... Pan... tanal...", rewards = "Você receberá:\n\n"},
[2623] = {title = "O falastrão do pantanal", objective = "Recupere as Ordens do Chefe Guerreiro e devolva-as ao Herói Caído da Horda.", description = "As ordens vieram do comandante de expedição. Nós recebemos... ordens. Na estrada da Barreira do Inferno... emboscados.\n\nMurlocs... muitos murlocs... Um se chamava Falastrão do Pantanal. Tomou as ordens da minha mão e fincou a lança na minha cabeça. Caverna... leste...\n\n<Bengor desfalece.>", progress = "Aguardando ordens.", completion = "É pior do que eu imaginava. Se eu não tivesse agido de forma tão irracional, meus dezenove homens teriam sido poupados da terrível agonia que sofreram.\n\nVocê precisa ajudá-los, &lt;name&gt;! Eu nada posso fazer.", rewards = "Você receberá:\n\n"},
[2641] = {title = "O Ingrediente Secreto de Sguisha", objective = "Sguisha, em Geringontzan, quer que você colete um Tragão Violeta e fale novamente com ela.", description = "Quando eu era pequena, quem cozinhava era a minha irmã. Era terrível! Fazia umas gororobas intragáveis... até que eu descobri aquilo que eu chamo de ingrediente \"secreto\". É só acrescentar a qualquer coisa e... UAU! Fica uma delícia!\n\nAh, você quer saber o que é? Um cogumelo chamado Tragão Violeta. Se quer que eu ajude você a deixar essa beberagem horrorosa menos intragável, vá às Terras Agrestes e procure um pequeno lago que tem lá.\n\nMas não se esqueça: o Tragão Violeta só nasce dentro d'água.", progress = "O Tragão Violeta só cresce sob as águas desse lago. Espero que você seja bom em prender a respiração!", completion = "Isso aí, &lt;name&gt;!\n\nEstá com uma cara tão boa que eu fico com vontade de... haha, brincadeirinha!", rewards = "Você receberá:\n\n"},
[2661] = {title = "Uma entrega para Marin", objective = "Sguisha quer que você leve o Pó Violeta para Marin Nublacuca em Geringontzan.", description = "Aqui está o Pó Violeta, <name>. Leve para Marin e diga que ele me deve um favor!", progress = "Ah, <name>, você voltou afinal. O que você trouxe?", completion = "Pó Violeta, é? Vamos experimentar... pior que isso não fica, com certeza!", rewards = "Você receberá:\n\n"},
[2662] = {title = "O Elixir Nublacuca", objective = "Marin Nublacuca quer que você volte a falar com ele depois que ele criar o elixir.", description = "Agora é só misturar o pó no orvalho.\n\nVolte daqui a um minuto... depois que eu aperfeiçoar o fabuloso Elixir Nublacuca!", progress = nil, completion = "Beba isto! É muito maravilhoso!!!\n\nEu sabia que ia conseguir... ah, mais uma vez, &lt;name&gt;, obrigado pela ajuda, mas agora você chega pro ladinho, faz favor. Está atrapalhando os meus negócios! Vou ganhar milhões com o fabuloso Elixir Nublacuca, milhões!\n\nO quê? Sguisha disse que eu devo um favor a ela? Ah, sempre tão brincalhona, essa Sguisha!", rewards = "Você receberá:\n\nElixir Nublacuca"},
[2681] = {title = "As pedras que nos unem", objective = "Liberte nove Serviçais de Razelikh, três Serviçais de Sevine, três Serviçais de Allistarj e três Serviçais de Grol, depois fale novamente com o Herói Caído. Você precisa estar sempre próxim<O/a> das pedras ou o processo fracassará.", description = "Você <sozinho/sozinha> não será suficiente para derrotar as forças de Razelikh e os subordinados dele.\n\nReúna um grupo de aventureiros e liberte meus homens da tortura eterna! Vocês precisam destruir dezoito pedras.\n\nLute com coragem e morra com honra, <name>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2701] = {title = "Heróis de outrora", objective = "Abra o baú e receba a sua recompensa.", description = "Por favor, tire de mim esta antiga relíquia. Ela não nos serve de mais nada.\n\nQue ela ajude você na sua luta contra a legião.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nEstilhaço dos Cascos Fendidos"},
[2702] = {title = "Heróis de outrora", objective = "Fale com o Cabo Thund Casco Fendido.", description = "Você me honrou com sua habilidade e heroísmo, <name>.\n\nTambém honrou a todos eles...Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2721] = {title = "Kirith", objective = "Descubra que fim levou o Tenente Kirith.", description = "Com a conclusão de um capitulo da sua aventura, outro se inicia, <name>.\n\nAinda não temos notícias de um de meus homens, perdido em algum lugar da Barreira do Inferno. Falo do Tenente Kirith, é claro.\n\nComo falei antes, nós dois fomos aprisionados e torturados por Allistarj, mas o pobre do Kirith não teve forças para resistir. Eu conseguia ouvir os experimentos da minha cela. Os gritos desesperados ecoavam pelso salões da caverna. Foi a última vez que vi ou ouvi o Kirith.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2741] = {title = "O super ovomático", objective = "O super ovomático é a mais recente invenção de Crista Dapinote. É uma máquina realmente impressionante.\n\nO super ovomático tremelica loucamente quando alguém se aproxima do painel de controle.", description = nil, progress = "O super ovomático é a mais recente invenção de Crista Dapinote. É uma máquina realmente impressionante.\n\nO super ovomático tremelica loucamente quando alguém se aproxima do painel de controle.", completion = "Coloque o ovo de hipogrifo na máquina. Uma série de apitos começa a soar junto com estranhos sons mecânicos.\n\nParece que o ovômetro pesou, mediu e embalou o seu ovo. Isso é que é serviço rápido!", rewards = "Você receberá:\n\nCaixa de Ovos"},
[2742] = {title = "Rin'ji está preso!", objective = "Escolte Rin'ji para fora do Pavilhão Quel'Danil, depois encontre o segredo que ele esconde nos Penhascos Panorâmicos a leste.", description = "Ei, <race>!\n\nRin'ji perdeu machado e foi capturado! Por favor, ajude Rin'ji escapar. Sei que <race> não gostam da raça do Rin'ji, mas Rin'ji implora! Preciso sair daqui!\n\nDou coisa boa para <race> se <ele/ela> se ajudar Rin'ji sair daqui...\n\nRin'ji tem negócio secreto escondido nos Penhascos Panorâmicos.", progress = nil, completion = "Esta tabuleta trólica deve ser o \"segredo\" de que Rin'ji falava.\n\nEstá coberta com marcas estranhas, e você não consegue decifrar o que está escrito.", rewards = "Você receberá:\n\n"},
[2743] = {title = "O abrigo das trevas", objective = "Fale novamente com o Herói Caído da Horda e conte suas descobertas.", description = "Não vou fazer rodeios, <race>. Meu tempo é curto.\n\nVocê precisa destruir a tríade de poder que protege o lorde-demônio Razelikh. Não vai ser uma tarefa fácil como libertar uma alma penada.\n\n<Kirith sorri.>\n\nSabe, cada um dos servos de Razelikh usa no pescoço um terço de um amuleto que Razelikh criou. E você vai precisar desse mesmo amuleto para conseguir evocar o arquidemônio.\n\nFale novamente com Terbor e diga a ele o que é necessário para evocar Razelikh. Ele vai te dar mais orientações.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2744] = {title = "O caçador de demônios", objective = "Fale com Loramus Talípedes em Azshara.", description = "A tríade é protegida por Razelikh.\n\nEm troca de seus serviços, Razelikh lhes concedeu poder praticamente ilimitado dentro da Barreira do Inferno. Cada uma das partes do amuleto estão harmonizadas com a força de vida do seu dono, tornando-o imortal. Enquanto permanecerem na Barreira do Inferno, eles não podem ser abatidos, a vida deles vai durar até o fim dos tempos.\n\nTem alguém que pode ajudar você, <name>. Vá até Azshara e encontre Loramus Talípedes, o caçador de demônios. Vasculhe as ilhas.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2745] = {title = "Infiltrando-se no castelo", objective = "Fale com Tyrion no Castelo Ventobravo.", description = "Hum, uma história interessante, que deve ser levada a sério. Sim, eu posso ajudar, mas o que você precisa fazer é aplicar uma forma de justiça que seja... ilegal, se quiser chegar ao fim de tudo isso.\n\nLescovar e Marzon precisam ser derrotados rapidamente. Você acha que consegue?\n\nA decisão é sua. Se acha que sim, corra para o castelo e encontre um velho amigo meu, um gnomo chamado Tyrion. Ele está vigiando Lescovar há algumas semanas e deve conseguir ajudar você a dar um fim nisso.", progress = nil, completion = "O quê! Nunca mais me entre de fininho assim! E fale baixo! Em nome das Sombras, o que você está bisbilhotando aqui? Não vê que eu estou trabalhando, tolo insolente?\n\nCom assim, o que eu estou fazendo? Estou esperando a abertura do próximo Portal Negro, o que mais eu estaria fazendo? Diga logo o que quer e dê o fora... antes que os guardas percebam nós dois escondidos nos arbustos. Eu tenho um nome a zelar.", rewards = "Você receberá:\n\n"},
[2746] = {title = "Itens de alguma consequência", objective = "Leve 3 Sedas e 2 Maçãs Frescas da Clara para Tyrion em Ventobravo.", description = "Trias está apressando as coisas, hein? Certo, nós podemos fazer isso, mas ainda não estou pronto. Fique sabendo de uma coisa, estou encarregado da parte mais difícil, mas é você que vai ter que sujar as mãos, entendido? Muito bem.\n\nPreciso que você me traga algumas coisas antes de dar início ao meu plano. Consegui o disfarce perfeito que vai fazer o robô-espião passar pelos guardas.\n\nTraga seda e maçãs para mim. Você se vira para conseguir a seda, mas a fazendinha da Clara, que fica logo após a saída da cidade, tem maçãs do tamanho perfeito.", progress = "Já faz algumas semanas que estou espionando Lescovar. Trias nunca confiou nele, mas não imaginávamos que ele estivesse envolvido com a Irmandade Défias. Seguimos pistas que o ligaram ao Martelo do Crepúsculo. Seja como for, ele é perigoso demais para esta cidade, e é aí que entramos.\n\nConsiga estes itens para mim, para que possamos pôr meu plano em marcha.", completion = "Ah, está ótimo, &lt;name&gt;. Muito bem.\n\nTenho o resto dos equipamentos de que preciso na minha bolsa... graça à Luz eu tenho um amigo alfaiate que me fez uma bolsa de seda. Já estava ficando sem espaço para carregar tantos equipamentos.\n\nAntes que o robô-espião entre, vamos tratar os detalhes, para que você saiba o que fazer.", rewards = "Você receberá:\n\n"},
[2747] = {title = "Um ovo extraordinário", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nCaixa de Gostosuras"},
[2748] = {title = "Um ovo fecundo", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nCaixa de Feitiços"},
[2749] = {title = "Um ovo comum", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nCaixa de Rações"},
[2750] = {title = "Um ovo gorado", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2751] = {title = "Muralhas bárbaras", objective = "Leve duas Braçadeiras Decoradas de Bronze, duas Montantes de Bronze e duas Garras Afiadas para Orokk Omosh em Orgrimmar.", description = "Vinte gerações da família Omosh já empunharam este martelo, <name>. Cem mil toneladas de metal moldadas por nós... tudo isso acaba em mim, Orokk, a falha dos Omosh, pois não tenho nem posso ter filhos.\n\nNão posso deixar os segredos dos Omosh morrerem comigo! Alguém deve perpetuar nosso legado, quer seja <um/uma> Omosh ou não!\n\nProve a Orokk que você é capaz, <name>. Traga para Orokk os seguintes itens: duas braçadeiras decoradas de bronze, duas montantes de bronze e duas garras afiadas. Traga tudo para Orokk e poderemos prosseguir.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInstruções: Peitoral de Ferro Barbaresco"},
[2752] = {title = "Em brafoneiras de ferro", objective = "Leve quatro Machados de Batalha de Bronze e quatro Martelos de Guerra de Bronze para Orokk Omosh em Orgrimmar.", description = "Orokk vê a ansiedade em seus olhos, <name>. Como uma esponja, você absorve a sabedoria dos Omosh.\n\nTrês lições é o que tenho a ensinar!\n\nVolte à bigorna, absorva o calor da forja, crie para Orokk quatro machados de batalha de bronze e quatro martelos de guerra de bronze. Só então você aprenderá sobre a brafoneira de ferro.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInstruções: Omoplatas de Ferro Barbarescas"},
[2753] = {title = "Esmagado sob os calcanhares", objective = "Leve quatro Elmos Verdes de Ferro, quatro Braçadeiras Verdes de Ferro e duas Perneiras Verdes de Ferro para Orokk Omosh em Orgrimmar.", description = "Orokk só tem mais duas lições pra ensinar, <name>. Os músculos doem, eu sei, mas persista, pelos Omosh!\n\nTraga pra Orokk quatro elmos verdes de ferro, quatro braçadeiras verdes de ferro e duas perneiras verdes de ferro - você aprende, seus inimigos sofrem!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInstruções: Botas de Ferro Barbarescas"},
[2754] = {title = "Trompas de frenesi", objective = "Leve dois Malhos de Ferro Maciço, duas Botas de Bronze Prateado e duas Manoplas de Bronze Prateado para Orokk Omosh em Orgrimmar.", description = "Orokk ensina a lição final agora, <name>. Retorne à bigorna e à forja sagradas! Traga para Orokk dois malhos de ferro maciço, duas botas de bronze prateado e duas manoplas de bronze prateado.\n\nVeja o medo nos olhos dos inimigos crescer à medida que você se aproxima!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInstruções: Elmo de Ferro Barbaresco"},
[2755] = {title = "Alegria dos Omosh", objective = "Observe e aprenda a Dancinha da Alegria dos Omosh.", description = "Orokk não está chorando, <name>. É cisco no olho.\n\n<Orokk enxuga uma lágrima.>\n\nExiste um último segredo da família Omosh para você aprender. Orokk vai morrer feliz, graças a <name>.\n\nAprende a dancinha feliz do Omosh e daí você aprende o punho de ferro!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInstruções: Luvas de Ferro Barbarescas"},
[2756] = {title = "Como antigamente", objective = "Leve 4 Peitorais de Aço e 4 Elmos de Placa de Aço para Aturk, o Bigorna, em Orgrimmar.", description = "No tempo das lendas, Aturk esmagava os crânios dos inimigos e triturava os ossos até virarem um pó fino, que era usado no filamento. Mas isso foi eras atrás, <race>. O Chefe Guerreiro clama por paz entre os clãs. Velhos inimigos são apenas observados com olhos cansados!\n\nInacreditável, eu sei, mas essa é a vontade de Thrall.\n\nChega de lamentos! Você está aqui para aprender, e é isso que vai fazer! Traga quatro peitorais de aço e quatro elmos de aço e sinta o gostinho da guerra!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEnsinar Perneiras de Guerra Órquicas\n\nVocê receberá:\n\n"},
[2757] = {title = "Angra do Butim ou Mina de Mithril?", objective = "Fale com McGavan na Angra do Butim.", description = "Escuta uma coisa, jovem, você não vai aprender nada ficando aqui.\n\nConheço um sujeito que conhece outro sujeito que fica na ferraria da Angra do Butim. Se você lhe mostrar esta insígnia, ele vai lhe contar sobre o mithril.\n\nO que me diz? Deixe os assuntos de criança para quem ainda não saiu das fraldas, <name>. Hora de dar mais um passo.\n\nSe estiver interessad<o/a>, o nome dele é McGavan. Mostre esse berloque que ele vai reconhecer seu compromisso.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2758] = {title = "As origens da ferraria", objective = "Leve 6 Braçadeiras de Escama Dourada para Marcelo, o Martelo em Ventobravo.", description = "Quando eu tinha mais ou menos a sua idade, um anão chamado Galvan me pegou pelo braço e me ensinou tudo sobre ferraria de armaduras. Antes de sair da Angra do Butim, ele me fez prometer que treinaria todos que mostrassem vontade e determinação para dominar a arte da ferraria. Então, aqui estou eu, muitos anos depois.\n\nAgora é hora de você aprender uma coisinha. Faça seis braçadeiras de escama dourada e aprenda de onde vem a ferraria, <name>.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInstruções: Manoplas de Escama Dourada"},
[2759] = {title = "Em busca de Galvan", objective = "Fale com McGavan na Angra do Butim.", description = "Embora Galvan tenha deixado a Angra do Butim muitos anos atrás, alguns membros da ordem continuam na cidade, esperando um dia encontrar alguém digno da tutela de Galvan.\n\nPegue esta insígnia e apresente a McGavan na Angra do Butim.\n\nSe você conseguir chegar até lá, ele vai reconhecer o seu valor. Confio em você, <name>!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2760] = {title = "A Ordem do Mithril", objective = "Fale com Galvan, o Antigo, na Selva do Espinhaço.", description = "A maioria dos nossos recrutas nunca chega à Angra do Butim, <name>. O brilho da insígnia é suficiente para atiçar a ganância até mesmo nos mais impávidos ferreiros. Burros são eles, já que a insígnia é só uma gota no mar de riquezas escondidas!\n\nEncontre Galvan, o Antigo, e mostre a ele este broche. Ele mora lá pro norte, entre Zul'Gurub e a Gruta de Mosh'Ogg.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2761] = {title = "Fundido e desfundido", objective = "Leve 40 Barras de Mithril e 40 Barras de Ferro para Galvan, o Antigo na Selva do Espinhaço.", description = "Muitos ferreiros acreditam que mineração e ferraria são trabalhos distintos e independentes, mas existe ferraria sem mineração? Rá, é uma impossibilidade! Um bom ferreiro também precisa ser um bom mineiro.\n\nMostre ao Galvan que você é capaz, <name>. Quero que me traga quarenta barras de ferro e quarenta barras de mithril. Com isso, Galvan vai lhe ensinar como fazer calças ornadas de mithril.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInstruções: Calças Ornadas de Mithril"},
[2762] = {title = "O grande embuste prateado", objective = "Leve 40 Barras de Mithril e 5 Barras de Veraprata para Galvan, o Antigo na Selva do Espinhaço.", description = "Dizem que a veraprata é mais forte e mais durável que o mithril. Galvan não discorda dessa avaliação dos minerais, mas, ao contrário do que o nome indica, a veraprata não é verdadeira! O mithril é um mineral muito mais nobre! RÁ! Enfim, para esta lição você precisa encontrar o grande enganador prateado, <name>.\n\nQuero que você me traga quarenta barras de mithril e cinco barras de veraprata. Em troca, você vai aprender o segrego das luvas ornadas de mithril e, ainda mais importante, descobrir que a veraprata é um embuste!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInstruções: Luvas Ornadas de Mithril"},
[2763] = {title = "A arte de imbuir", objective = "Leve 40 Barras de Mithril e 4 Citrinos para Galvan, o Antigo na Selva do Espinhaço.", description = "A extração de gemas preciosas é de importância primordial para o nosso trabalho, <name>. Poucas gemas são mais preciosas que o citrino. As propriedades mágicas que imbuímos em nossos produtos são grandemente amplificadas pela presença do citrino.\n\nNesta lição, vamos fortificar quarenta barras de mithril com quatro citrinos. O resultado vai ser um melhor entendimento da gemologia mágica e o conhecimento para criar ombreiras ornadas de mithril.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInstruções: Ombreiras Ornadas de Mithril"},
[2764] = {title = "O melhor pupilo de Galvan", objective = "Fale com Trenton Lumartelo em Geringontzan.", description = "Se estiver pront<o/a> para concluir o seu treinamento com Galvan, viagem até Geringontzan.\n\nLá, você vai encontrar o meu melhor pupilo: Trenton Lumartelo. Ele tem muito a ensinar sobre a arte do ornado de mithril. Caso contrário, continue aqui e aprenda!\n\nAh, Geringontzan fica em Tanaris, é claro!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2765] = {title = "Ferreiro perito!", objective = "Pegue sua recompensa com Galvan, o Antigo!", description = "Quando você apareceu, eu tive certeza de que estava diante de um dos meus melhores pupilos! Você nem hesitou em entregar um berloque com algum poder a McGavan. Você fez isso só para poder me conhecer. Essa fé cega e essa devoção à arte merecem ser recompensadas.\n\nVocê dominou a ferraria, <name>. Pode colher os louros!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSinete da Aptidão"},
[2766] = {title = "Encontre o OOX-22/FE!", objective = "Pegue o transmissor de emergência para o robô teleguiado de Olhatorto na caverna próxima ao Vale dos Abomináveis.", description = "Você descobriu um tipo de dispositivo estranho, oval, feito de metal. Ao mexer em um dos botões, o ovo treme e se abre formando um tipo de geringonça robótica gnômica! Uma voz do interior do ovo robótico volta à vida.\n\n\"Meu nome é Olhatorto Obnótico, e meu robô teleguiado se acidentou! Vou gratificá-<lo/la> pela recuperação dele. Por favor, leve este transmissor ao robô.\"\n\nCalculei as coordenadas do robô para você. Ele está no Vale dos Abomináveis!\"", progress = "Tudo indica que você descobriu os destroços de uma... galinha robótica? A voz do interior do ovo ganha vida novamente:\n\n\"Sim, excelente trabalho! Este é, de fato, meu robô teleguiado, embora meus sensores indiquem que ele precisa de uma partida auxiliada para poder voar de volta à Angra do Butim para reparos. Vá em frente e insira o transmissor no viajante. O transmissor cuidará do resto!\"", completion = "Você insere o ovo... transmissor de emergência... na galinha... robótica teleguiada.\n\nOuvem-se zumbidos e chiados vindos do interior do robô, que luta para ficar em pé. Quando o robô volta à vida, ouve-se mais uma vez a voz de Olhatorto, mas agora do interior do robô:\n\n\"Pelo menos OOX-22/FE está funcionando, mas precisa de sérios reparos! Ele é pesado demais para você carregar... mas acho que tenho uma ideia. Você está disposto a tomar conta dele um pouco mais?\"", rewards = "Você receberá:\n\n"},
[2767] = {title = "Resgate o OOX-22/FE!", objective = "Escolte o OOX-22/FE até a doca ao longo da Costa Esquecida, depois apresente-se a Olhatorto Obnótico na Angra do Butim.", description = "A voz do gnomo surge de novo do robô:\n\n\"Preciso transportar o OOX-22/FE para um local amplo e seguro para eu começar o longo procedimento de lançamento. Ele tem um disfarce embutido, mas preciso de tempo para torná-lo funcional de novo. Escolte o robô de sua localização atual para, digamos, a doca ao longo da Costa Esquecida! Lá seria um lugar perfeito e extenso o suficiente para acertar as coisas!\"\n\nLeve-o em segurança para a doca e volte para falar comigo em Angra do Butim! Olhatorto Obnótico - câmbio!\"", progress = nil, completion = "Ah, VOCÊ é o grande salvador de OOX-22/FE! Nem tenho como expressar minha gratidão pela sua cooperação! Minha linha de robôs teleguiados precisa de mais desenvolvimento, mas seu auxílio me poupou várias horas de trabalho de reconstrução, assim como muitas peças de ouro de custo!\n\nPor favor, escolha quaisquer desses itens. Obrigadinho de novo pela generosa ajuda, &lt;name&gt;. Eu e meus robôs temos uma dívida com você!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nExperimento Voador FracassadoToalha de Cota de Malha\n\nVocê também receberá:\n\n"},
[2768] = {title = "Bastão divinomático", objective = "Leve o Bastão Divinomático ao Engenheiro-chefe Silvadágua em Geringontzan.", description = "O Sargento Bly roubou de mim! Ele disse que só iria pegar emprestado, mas roubou meu adorado bastão divinomático! Sem o bastão, como vou saber onde cavar novos poços?\n\nEncontre o Bly e traga meu bastão de volta! Ouvir dizer que ele deixou o grupo de aventureiros de má-reputação de que fazia parte para sair à caça de tesouros em Zul'Farrak, a oeste. Aposto que você vai encontrar ele lá.\n\nEspero que os trolls deem cabo do infeliz, porque se você precisar lutar com ele, a briga vai ser dureza.", progress = "Encontrou o Sargento Bly? Pegou meu bastão divinomático?", completion = "Você encontrou! Muito bem, &lt;name&gt;! Precisou lutar com o Bly? Espero que você tenha dado um belo de um couro nele e no grupo dele!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nAnel da Confraria MaçomBacinete da Guilda do Engenheiro\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[2769] = {title = "Os irmãos Latafuso", objective = "Fale com Capu Latafuso no Salar Cintilante.", description = "Você já ouviu falar dos irmãos Latafuso? Eles ficam no Salar Cintilante, que fica em Kalimdor, correndo de carro-foguete contra os goblins. E geralmente vencem, o que não é surpresa alguma, o carro dos goblins quebra tanto que quase nunca cruza a linha de chegada!\n\nE mesmo que cruzasse, os irmãos Latafuso estão sempre trabalhando para deixar o carro deles ainda mais rápido, e o Capu me disse que precisa de um componente muito raro para o próximo experimento.\n\nEle deve estar precisando de ajuda para conseguir essa peça. Fale com ele e descubra.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2770] = {title = "Gahz'rilla", objective = "Leve a Escama Eletrificada de Gahz'rilla para Capu Latafuso no Salar Cintilante.", description = "Nos recônditos de Zul'Farrak, a cidade de areia trólica que fica em Tanaris, existem uma lagoa sagrada. Nessa lagoa os trolls evocam uma fera gigantesca! Gahz'rilla! Ele é tão feroz que até suas escamas soltam faíscas elétricas. É essa energia que quero extrair para o meu carro!\n\nTraga uma escama eletrificada de Gahz'rilla para mim!\n\nA evocação de Gahz'rilla é um segredo muito bem guardado pelos trolls. Para encontrar a fera, você precisa roubar o segredo deles.", progress = "Trouxe a escama? Mal posso esperar para experimentar vários jeitos de extrair a energia dela!", completion = "Uau, você conseguiu a escama! Obrigado, &lt;name&gt;. Mal posso esperar para trabalhar nessa coisa!\n\nVocê viu o Gahz'rilla? Era tão grande quanto dizem?", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nO Burro e a Cenoura"},
[2771] = {title = "A ótima cabeça sobre os ombros", objective = "Leve 2 Coifas de Mithril e 1 Ombreira Ornada de Mithril para Trenton Lumartelo.", description = "Parece que o mestre planejou tudo isso desde o início. Não aprendi a criar os itens que ele te ensinou a criar e você não aprendeu os itens que eu aprendi a criar.\n\nO mestre é sábio, <name>. Precisamos ensinar um ao outro!\n\nMostre como fazer uma coifa de mithril e ombreiras ornadas de mithril e eu te mostrarei como criar um elmo ornado de mithril.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEnsinar Elmo Ornado de Mithril\n\nVocê receberá:\n\n"},
[2772] = {title = "O mundo a seus pés", objective = "Leve 2 Botas Pesadas de Mithril e 1 Calça Ornada de Mithril para Trenton Lumartelo.", description = "No Campeonato de Ferraria Geral do Vale do ano passado, todos na plateia viram quando um cachorro covarde da Empreendimentos S.A. me passou uma rasteira. O que eles não sabiam, no entanto, era a razão por que consegui continuar e conquistar a vitória no torneio: exatamente, botas ornadas de mithril!\n\nPara este ano, preciso aprender a criar calças ornadas de mithril, pois um retalhador da Empreendimentos S.A vai competir! Preciso que me ensine a fazer as calças, que eu te ensino como fazer as botas!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEnsinar Botas Ornadas de Mithril\n\nVocê receberá:\n\n"},
[2773] = {title = "Kid Mithril", objective = "Leve 2 Peitorais Pesados de Mithril e 1 Luva Ornada de Mithril para Trenton Lumartelo.", description = "Eu sou conhecido por aqui como Kid Mithril. Forneço ornados de mithril para aventureiros de todas as partes do mundo.\n\n<Trenton sussurra.>\n\nEle nem imaginam que eu não conheço a técnica para fazer luvas ornadas de mithril. Shhhh! Este vai ser o nosso segredinho.\n\nVocê me ensina a técnica para criar luvas ornadas de mithril, <name>, e, em troca, eu te mostro o resultado do ápice do meu treinamento com Galvan: o peitoral ornado de mithril.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEnsinar Peitoral Ornado de Mithril\n\nVocê receberá:\n\n"},
[2781] = {title = "PROCURA-SE: Califa Escorpicada", objective = "Leve a cabeça de Califa Escorpicada ao Engenheiro-chefe Silvadágua em Geringontzan.", description = "PROCURA-SE: Califa Escorpicada!\n\nA Companhia de Saneamento de Geringontzan, Tanaris vai pagar uma régia recompensa pela cabeça de Califa Escorpicada, líder dos bandidos Errantes. A lista de crimes inclui:\n\n1. Assassinato de funcionários da Cia. de Saneamento de Geringontzan!\n2. Roubo de recursos da Cia. de Saneamento de Geringontzan!\n3. Invasão de propriedades da Cia. de Saneamento de Geringontzan!\n\nApresente provas da queda de Califa Escorpicada ao Engenheiro-chefe Silvadágua e recebe uma recompensa imediatamente!", progress = "Sim, <class>, sou um representante da Companhia de Saneamento de Geringontzan. Como posso lhe ajudar?", completion = "Finalmente, a justiça foi feita! Você deu um golpe duro naqueles nômades vis! Com a queda de Escorpicada, acho que finalmente vamos conseguir expulsar esses arruaceiros dos poços d'água de Geringontzan!\n\nPela autoridade da Companhia de Saneamento de Geringontzan, eu lhe concedo esta recompensa. Você nos prestou um grande serviço.", rewards = "Você receberá:\n\n"},
[2782] = {title = "O segredo de Rin'ji", objective = "Urraca Convulserpe, uma acadêmica bastante reconhecida em assuntos relacionados às trolesas Cascasseca, pode ser capaz de decifrar a tabuleta. Ela mora no Distrito da Magia da Cidade Baixa.", description = "A tabuleta está coberta com marcas de trolesa Cascasseca. O segredo de Rin'ji é um mistério... \n\nÉ possível que você tenha algo bastante valioso nas mãos. Por outro lado, o exato oposto também é possível. Um troll Cascasseca provavelmente faria qualquer coisa para se livrar de problemas...", progress = "Bem, e agora, o que é isso?", completion = "Essa tabuleta... é bem incomum...? Onde você a encontrou, &lt;name&gt;?", rewards = "Você receberá:\n\n"},
[2783] = {title = "Brigas mesquinhas", objective = "Fale com o Herói Caído da Horda na fronteira entre Barreira do Inferno e o pântano.", description = "Lutamos contra o inimigo errado, <name>.\n\nNestas terras, o verdadeiro poder continua a se fortalecer e ganhar adeptos enquanto brigamos com os orcs por terra calcinada.\n\nSerá que é boa ideia continuar a guerra nessas terras contra a Horda, só para sermos rechaçados um dia pelos exércitos da Legião Ardente? É inútil lutar.\n\nDeixe suas diferenças de lado, <name>, e fale com o herói caído da Horda. Ele conhece a verdade por trás da Barreira do Inferno.", progress = nil, completion = "A Aliança quer ajudar?\n\nJamais imaginei que isso aconteceria de novo. Um dia em que a Aliança luta lado a lado com a Horda contra um inimigo comum.\n\nQue seja... vamos nos unir.", rewards = "Você receberá:\n\n"},
[2784] = {title = "Caindo em desgraça", objective = "Ouça o Herói Caído da Horda contando a história dele.", description = "Ouça a minhas história, <race>.", progress = nil, completion = "Enquanto espírito, não tenho poder algum, &lt;name&gt;. Preciso de ajuda. Permita que eu reconquiste a minha honra.", rewards = "Você receberá:\n\n"},
[2801] = {title = "Uma história de tristeza", objective = "Ouça o Herói Caído da Horda contando a história dele.", description = "Antes de continuarmos, você precisa ouvir minha história.", progress = "Escute o que eu tenho a dizer, <name>.", completion = "A hora de agir é agora, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[2821] = {title = "Marca de qualidade", objective = "Leve 10 peças de Pelego Grosso de Yeti para Pradal McGrubben no Domínio de Plumaluna.", description = "Minha filosofia sobre couraria é que o trabalho deve ser da melhor qualidade possível – sem comprometimentos. Qualquer um que diga que qualidade não importa estará tentando lhe vender uma propriedade com vista para o mar em Dun Morogh!\n\nEm minha última busca por matérias-primas de alta qualidade, encontrei os pelegos grossos de yeti. Os yetis são nativos de Feralas e muitos deles se encontram no Vale dos Abomináveis. Quero alguns deles e vou lhe dar a chance de escolher um de meus artefatos de couro em troca de um fardo de dez pelegos. Se lhe parece justo, temos um acordo.", progress = "Um fardo de dez pelegos grossos de yeti lhe dará a escolha de um de meus artefatos. Por causa da minha preocupação com qualidade, posso lhe garantir que terá um bom produto!\n\nSe você não conseguir encontrar yetis, eu sei onde estão alguns. Tente procurar na região próxima à praia na Costa Esquecida, perto do Vale dos Abomináveis.", completion = "Você parou para examinar a textura destes pelegos? Tratados apropriadamente, eles podem ficar bem mais fortes do que ferro. Não há muitas pessoas que conhecem esses pelegos. Com meu vasto conhecimento sobre soluções em tratamento de matéria-prima, conseguirei produzir itens de excelente qualidade... graças a você, claro!\n\nAgora escolha o que desejar pelo seu serviço!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas Artesanais de PradalLuvas Artesanais de Pradal\n\nVocê também receberá:\n\n"},
[2822] = {title = "Marca de qualidade", objective = "Leve 10 peças de Pelego Grosso de Yeti para Jangdor Passo Veloz na Aldeia Mojache.", description = "Me orgulho muito de trabalhar com couro, e creio que dá pra notar. Nunca deixo a qualidade cair. Qualquer um que diga que qualidade não importa também deve achar que a terra não guarda segredos.\n\nNa minha última busca por matéria-prima de qualidade, descobri pelegos grossos de yeti. Yetis são nativos de Feralas e geralmente são encontrados a oeste daqui, na Vale dos Abomináveis. Você poderá escolher um de meus produtos de couro em troca de uma pilha de dez pelegos. Se estiver de acordo, então estamos combinados!", progress = "Conseguiu pegar uma pilha de dez pelegos grossos de yeti? Meus produtos de couro não perdem para os de ninguém, e lhe darei uma amostra se tiver trazido os pelegos.\n\nSe você não conseguir encontrar yetis, eu sei onde estão alguns. Tente procurar a oeste daqui, na região próxima à praia na Costa Esquecida, perto do Vale dos Abomináveis.", completion = "Ah, excelente! Estes pelegos, quando passam por um tratamento especial, ficam tão resistentes quanto ferro, eu garanto! Vou fazer algumas tentativas para encontrar a mistura certa para o tingimento, mas tenho certeza de que eles terão um padrão de qualidade ainda melhor.\n\nQuanto a você, &lt;name&gt;... seu auxílio se provou valioso para mim. Escolha um destes itens que criei.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas Artesanais de JangdorLuvas Artesanais de Jangdor\n\nVocê também receberá:\n\n"},
[2841] = {title = "Guerra dos robôs", objective = "Pegue a Combinação do Cofre do Termaplugue e o Projeto de Mecatraje e leve-os para Nogg em Orgrimmar.", description = "Tecnologia gnômica. Dois anos atrás só era usada em piadas infames. Os tempos mudaram... Agora eles construíram um robô melhor que todos os nossos retalhadores juntos. Eu preciso do projeto, <name>!\n\nO problema é que ele está trancado num cofre perto do Mecangenheiro Termaplugue. Pra piorar, só ele sabe a combinação!\n\nFaça o que for preciso para descobrir a combinação, <name>. Depois abra o cofre e me traga aquele projeto!Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nVestes de CivínadaCulotes CorretrechoPerneiras Duplas Reforçadas\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[2842] = {title = "Engenheiro-chefe Scooty", objective = "Fale com Scooty na Angra do Butim.", description = "Assim que Kernobill nos alertou sobre a nova perfuratriz gnômica, Scooty e eu começamos a trabalhar no Transpoliportador 6000, a maneira mais rápida e confiável de chegar a Gnomeregan.\n\nFinalmente ele está pronto para o transporte de passageiros, <name>. Se quiser experimentar, vá até Angra do Butim e fale com Scooty no ninho de mantícoras. Para chegar a Angra do Butim, pegue o barco que sai de Vila Catraca.\n\nSe preferir, você pode tentar correr até Gnomeregan passando pelas montanhas Dun Morogh, que estão infestadas de anões.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2843] = {title = "E lá vamos nós!", objective = "Esperar até Scooty calibrar o Transponder Goblínico.", description = "Enquanto você estiver com o transponder, basta subir na plataforma para ser <teleportado/teleportada> até Gnomeregan.\n\nVocê corre o risco de sentir alguns efeitos colaterais, mas nada muito permanente... na teoria.\n\nEu já ia me esquecendo de algo muito importante: a volta. Quando quiser voltar, é só subir no transpoliportador lá em Gnomeregan!\n\nAgora, vamos calibrar o transponder com seu peso e altura.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nTransponder Goblínico"},
[2844] = {title = "O guardião gigante", objective = "Encontre a elfa noturna desaparecida.", description = "Eu não sou bom, <name>. Era pra eu tá vigiando minha amiguinha Shay, mas ela fugiu de eu. Num sei direito o que aconteceu, mas, na última vez que olhei, ela tava passando pelas ruínas ao norte daqui.\n\nTava indo tudo bem, mas, quando virei, cadê a danada?\n\nNum sei o que fazer agora. Você tem alguma ideia, <name>? Tô preocupado demais.\n\nEi, que tal você procurar a Shay? As usas pernas andam muito mais rápido que as minhas.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2845] = {title = "Shay distraída", objective = "Pegue o Sino de Shay do baú.\n\nEscolte Shay Velifrondes até o acampamento de Trinca-pedra.", description = "Você acha que pode me ajudar a achar o caminho de volta até o meu amigo Trinca-pedra? Ele devia estar me vigiando, mas, de vez em quando, eu saio andando a esmo sem nem perceber. Tem tanta coisa pra ver, isso me deixa distraída.\n\nMas tem uma coisa que sempre funciona para manter a minha atenção.\n\nDentro desse baú tem um sino que é muito importante pra mim. Eu adoro o som dele. Juro, se eu me perder, é só tocar essa sineta que eu volto correndo.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nManoplas de GranitoCilha Sebevideira\n\nVocê também receberá:\n\n"},
[2846] = {title = "Tiara das Profundezas", objective = "Leve a Tiara das Profundezas para Tabetha no Pântano Vadeoso.", description = "Há muito tempo, eu tinha uma belíssima joia, a Tiara das Profundezas. Era linda e, mais do que isso, guardava grande poder para os que soubessem com usar.\n\nEntão, quando ficou sabendo disso, a Hidromante Velratha decidiu que precisava da tiara. Ela enviou agentes à minha casa para roubar a joia enquanto eu estava fora. Ladrões!\n\nQuero minha tiara de volta! Vá até Zul'Farrak, a cidade trólica em Tanaris, encontre Velratha e arranque a tiara dela. Traga de volta para mim e eu ficarei em dívida com você.", progress = "Está com a tiara, <name>? Velratha descobriu o preço de cruzar o meu caminho?", completion = "Maravilha! Você a encontrou! E, ainda melhor, Veraltha não a tem mais! Obrigada, &lt;name&gt;. Tenho uma dívida eterna com você!\n\nE antes se eu pareci um pouco... cruel, releve. Você vai ver que sou uma pessoa muito mais legal com aqueles que não roubaram nada de mim.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nBastão Converte-feitiçoBrafoneiras de Xisto-gema"},
[2847] = {title = "Armadura de couro silvestre", objective = "Leve 10 peças de Couro Grosso para Pradal McGrubben no Domínio de Plumaluna.", description = "<name>, você está se tornando muito <habilidoso/habilidosa> como <coureiro/coureira>! Há uns moldes de armaduras de couro silvestre que talvez possam interessar você! O couro silvestre combina muito com as potentes e caóticas propriedades das vinhas selvagens, encontradas em áreas de vegetação viçosa e em algumas criaturas. A armadura feita disso é digna de pessoas como você, eu garanto.\n\nVou lhe ensinar isto, mas você terá que trabalhar para mim por um tempo. O primeiro custo será de dez peças de couro grosso. Depois disso, conversaremos melhor.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2848] = {title = "Omoplatas de couro silvestre", objective = "Leve 6 Kits de Armadura Grossa e uma Vinha Selvagem para Pradal McGrubben, que fica no Domínio de Plumaluna.", description = "Omoplatas de couro silvestre sustentam quem as veste, oferecendo alta proteção aos braços e ombros. Como em toda armadura de couro silvestre, um benefício poderoso, embora aleatório, é aplicado ao item quando a vinha selvagem é costurada nele.\n\nPara esse molde, precisarei de seis kits de armadura e uma vinha selvagem. Não deve ser uma tarefa difícil para você...Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nMolde: Omoplatas de Couro Silvestre"},
[2849] = {title = "Colete de couro silvestre", objective = "Leve 2 Peitorais de Escama de Tartaruga, 2 Luvas de Escama de Tartaruga e uma Vinha Selvagem para Pradal McGrubben no Domínio de Plumaluna.", description = "Coletes são o carro-chefe de qualquer armadura de couro silvestre. O caimento do colete em combinação com a vinha selvagem resulta em um produto de alto grau de proteção do tronco, enquanto permite a poderosa ação da magia na vestimenta.\n\nPara este molde, vou precisar de dois peitorais de escama de tartaruga, dois pares de luvas de escama de tartaruga e uma vinha selvagem. É uma tarefa árdua, mas alguém como você não terá dificuldade em fazê-la para mim.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nMolde: Colete de Couro Silvestre"},
[2850] = {title = "Capacete de couro silvestre", objective = "Leve 2 Túnicas da Escapada Noturna, 2 Bandanas da Escapada Noturna e uma Vinha Selvagem para Pradal McGrubben no Domínio de Plumaluna.", description = "Um capacete de couro silvestre oferece a quem o veste alta proteção às áreas vitais da cabeça sem causar desconforto. Como em toda armadura de couro silvestre, a integração com a vinha selvagem proporciona uma poderosa, embora aleatória, melhora ao capacete. Os usuários de couro desejarão este item!\n\nPara o molde, preciso de duas túnicas da Escapada Noturna, duas bandanas da Escapada Noturna e uma vinha selvagem. Complete a tarefa para aprender a fazer o molde!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nMolde: Capacete de Couro Silvestre"},
[2851] = {title = "Botas de couro silvestre", objective = "Leve 2 Calças da Escapada Noturna, 2 Botas da Escapada Noturna e 2 Vinhas Selvagens para Pradal McGrubben no Domínio de Plumaluna.", description = "Você já aprendeu bastante, <name>, mas ainda há mais pela frente. Agora é a vez das botas de couro silvestre, o alicerce de qualquer armadura.\n\nEssas botas são feitas para deixar os pés confortáveis enquanto seguem caminhos de perigos e aventuras. Suas solas macias são uma bênção para aqueles em busca de passos tranquilos.\n\nPara este molde, preciso de duas calças da Escapada Noturna e duas botas da Escapada Noturna. Também preciso de duas – e não só uma – vinhas selvagens. Volte quando tiver tudo isso.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nMolde: Botas de Couro Silvestre"},
[2852] = {title = "Perneiras de couro silvestre", objective = "Leve 2 Elmos de Escama de Tartaruga, 2 Braçadeiras de Escama de Tartaruga e 2 Vinhas Selvagens para Pradal McGrubben no Domínio de Plumaluna.", description = "Como você já domina o básico, está na hora de você aprender coisas mais complexas, <name>. Perneiras de couro silvestre são um dos equipamentos mais robustos e poderosos. Elas oferecem extraordinária proteção à cintura, coxas, panturrilhas e órgãos vitais. Acompanhadas pelas melhorias proporcionadas pela vinha selvagem, os aventureiros que as usarem serão sempre gratos a você.\n\nPara esse pedido, preciso de dois elmos de escama de tartaruga, duas braçadeiras de escama de tartaruga e duas vinhas selvagens.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nMolde: Perneiras de Couro Silvestre"},
[2853] = {title = "Mestre do couro silvestre", objective = "Entregue a Carta de Pradal para Telonis, o mestre coureiro de Darnassus.", description = "Muito bem, <name>! Seu sobrenome devia ser \"Dedicação\". Ainda há um molde para você aprender e se aprimorar em armaduras de couro silvestre. Não pedirei que você trabalhe em vão para obter este molde. Você o merece... mas eu não posso dá-lo a você. Meu professor, aquele que me ensinou o ofício, é quem deve <recompensá-lo/recompensá-la>.\n\nLeve estar carta para Telonis – ele ensina couraria em Darnassus. Mostre-lhe a carta e ele <o/a> reconhecerá como <um/uma> semelhante, assim como eu.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nMolde: Manto de Couro Silvestre"},
[2854] = {title = "Armadura de couro silvestre", objective = "Leve 10 peças de Couro Grosso para Jangdor Passo Veloz na Aldeia Mojache.", description = "<name>, suas habilidades como <coureiro/coureira> lhe precedem. Tenho um conhecimento em couraria que lhe seria de grande valia... armadura de couro silvestre! Couro silvestre combina com as propriedades poderosas e caóticas da vinha selvagem encontrada em áreas de vegetação viçosa e em algumas criaturas. A armadura feita disso é digna de pessoas com suas habilidades.\n\nVou lhe ensinar isso, mas você terá que trabalhar para mim por um tempo. O primeiro custo será de dez peças de couro grosso. Depois disso, conversaremos melhor.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2855] = {title = "Omoplatas de couro silvestre", objective = "Leve 6 Kits de Armadura Grossa e uma Vinha Selvagem para Jangdor Passo Veloz na Aldeia Mojache.", description = "Omoplatas de couro silvestre são os sustento perfeito para quem as veste, oferecendo alta proteção aos braços e ombros. Como em toda armadura de couro silvestre, um benefício aleatório, mas poderoso, é aplicado ao item quando a vinha selvagem é costurada nele.\n\nPara esse molde, precisarei de seis kits de armadura e uma vinha selvagem. Não deve ser uma tarefa difícil para você.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nMolde: Omoplatas de Couro Silvestre"},
[2856] = {title = "Colete de couro silvestre", objective = "Leve 2 Peitorais de Escama de Tartaruga, 2 Luvas de Escama de Tartaruga e uma Vinha Selvagem para Jangdor Passo Veloz na Aldeia Mojache.", description = "Coletes são o carro-chefe de qualquer armadura de couro silvestre. O caimento do colete em combinação com a vinha selvagem resulta em um produto de alto grau de proteção do tronco enquanto permite a poderosa ação da magia na vestimenta.\n\nPara este molde, vou precisar de dois peitorais de escama de tartaruga, dois pares de luvas de escama de tartaruga e uma vinha selvagem. É uma tarefa árdua, mas alguém como você não terá dificuldade em fazê-la para mim.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nMolde: Colete de Couro Silvestre"},
[2857] = {title = "Capacete de couro silvestre", objective = "Leve 2 Túnicas da Escapada Noturna, 2 Bandanas da Escapada Noturna e uma Vinha Selvagem para Jangdor Passo Veloz na Aldeia Mojache.", description = "Um capacete de couro silvestre oferece a quem o veste alta proteção às áreas vitais da cabeça sem causar desconforto. Como em toda armadura de couro silvestre, a integração com vinha selvagem proporciona um poderoso, embora aleatório, incremento ao capacete. Os usuários de couro desejarão esse item!\n\nPara o molde, preciso de duas túnicas da Escapada Noturna, duas bandanas da Escapada Noturna e uma vinha selvagem. Complete a tarefa e o molde será seu!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nMolde: Capacete de Couro Silvestre"},
[2858] = {title = "Botas de couro silvestre", objective = "Leve 2 Calças da Escapada Noturna, 2 Botas da Escapada Noturna e 2 Vinhas Selvagens para Jangdor Passo Veloz na Aldeia Mojache.", description = "Você já aprendeu bastante sobre armaduras de couro silvestre, <name>, mas ainda há mais pela frente.\n\nBotas de couro silvestre são o alicerce de qualquer armadura. Essas botas são feitas para deixar os pés confortáveis enquanto seguem caminhos de perigos e aventuras. Suas solas macias são uma bênção para aqueles em busca de passos tranquilos.\n\nPara o molde, preciso de duas calças da Escapada Noturna e duas botas da Escapada Noturna. Também preciso de duas – e não só uma – vinhas selvagens. Volte quando tiver tudo isso.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nMolde: Botas de Couro Silvestre"},
[2859] = {title = "Perneiras de couro silvestre", objective = "Leve 2 Elmos de Escama de Tartaruga, 2 Braçadeiras de Escama de Tartaruga e 2 Vinhas Selvagens para Jangdor Passo Veloz na Aldeia Mojache.", description = "Você já está <preparado/preparada> para aprender lições mais complexas, <name>. Perneiras de couro silvestre são um dos equipamentos mais robustos e poderosos. Elas dão enorme proteção à cintura, coxas, panturrilhas e órgãos vitais. Acompanhadas pelas melhorias proporcionadas pela vinha selvagem, os aventureiros que as usarem serão sempre gratos a você.\n\nPara adquirir este molde, preciso de dois elmos de escama de tartaruga, duas braçadeiras de escama de tartaruga e duas vinhas selvagens.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nMolde: Perneiras de Couro Silvestre"},
[2860] = {title = "Mestre do couro silvestre", objective = "Entregue a Carta de Jangdor para Una, a mestre coureira do Penhasco do Trovão.", description = "<name>, você aprendeu tudo o que sou capaz de lhe ensinar. Entretanto, ainda há um molde para você se aprimorar em armaduras de couro silvestre. Não pedirei que você trabalhe em vão para obter este molde – você já fez por merecer. Mas não posso dá-lo a você de mãos beijadas.\n\nMinha professora, aquela que me ensinou o ofício, é quem deve <recompensá-lo/recompensá-la>.\n\nLeve esta carta para Una – ela ensina couraria no Penhasco do Trovão. Mostre-lhe a carta e ela <o/a> reconhecerá como <um/uma> semelhante, assim como eu.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nMolde: Manto de Couro Silvestre"},
[2861] = {title = "A tarefa de Tabetha", objective = "Fale com Tabetha no Pântano Vadeoso.", description = "Corre um boato de que Tabetha, uma maga que vive nos recônditos do Pântano Vadeoso, tem um assunto a resolver com uma trolesa de Zul'Farrak. Fale com a Tabetha que ela lhe dará mais detalhes.\n\nMelhor levar mais gente com você, <name>. Se a Tabetha mandar você a Zul'Farrak, é melhor não ir sozinh<o/a>.\n\nA cabana de Tabetha fica a oeste de Theramore, logo ao norte das Ruínas Pedramalho.", progress = nil, completion = "É verdade. Tenho um assunto a resolver com ela. Assunto de vida ou morte... Mais de morte do que vida.\n\nQuer me ajudar?", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2862] = {title = "Patábua mole em guerra dura", objective = "Leve 10 Jubas de Gnoll Patábua para Hadoken Passo Veloz na Aldeia Mojache.", description = "Os gnolls... são mais do que um incômodo. Eles são uma ameaça constante à existência deste acampamento! Não importa se eles são nativos de Feralas ou não, se não nos fortalecermos contra eles, acabaremos sendo expulsos da região.\n\nOs ataques dos gnolls contra o acampamento têm se tornado cada vez mais vorazes ultimamente. Precisamos de sua ajuda para eliminá-los. Traga-me dez jubas deles e lhe recompensarei dignamente por seu esforço.\n\nVá!", progress = "Nós os rechaçamos, e eles ainda atacam! Alguém ou algo deve estar levando-os a um delírio... Ah, você voltou, <name>? Você tem as jubas que lhe pedi em troca de uma recompensa?", completion = "Este foi um bom começo, &lt;name&gt;. Suas habilidades como soldado serão louvadas.\n\nVocê não foi o único para quem pedi esta tarefa. Muitos antes de você lutaram contra os gnolls e, em tempos passados, nossos esforços os mantiveram afastados. No entanto, recentemente as tentativas deles de avançar para o norte, entrando na nossa área de influência, estão se intensificando drasticamente e eles também andam mais vorazes, como se estivessem desesperados.\n\nTenho um plano que pode reverter esse quadro de uma vez por todas, &lt;name&gt;...", rewards = "Você receberá:\n\n"},
[2863] = {title = "Golpe Alfa", objective = "Mate 5 Machos Alfa Patábua e fale novamente com Hadoken Passo Veloz em uma hora.", description = "O plano é matar vários líderes dos gnolls, os Alfas, como eles chamam a si mesmos, de forma rápida e eficiente. Se conseguirmos executar esse plano de devastação com presteza e precisão, vamos semear o caos entre eles. Talvez isso também deixe muito claro para aqueles cabecinhas de vento que nós podemos e vamos destruir todos!\n\nQuero que você seja <o mensageiro/a mensageira> da minha vontade. Para que funcione, você precisa eliminar cinco Alfas em uma hora, <name>. Caso contrário, o impasse vai continuar!", progress = nil, completion = "Você fez o que lhe pedi e por isso lhe saúdo. Tome esta pequena recompensa por seus esforços, &lt;name&gt;.\n\nCom o contingente diminuído e os líderes abatidos, os gnolls pensarão duas vezes antes de perturbar o acampamento. Mas isso não resolve o assunto de vez...\n\nDeve haver algum motivo para os gnolls terem entrado em tamanho frenesi. Antes que eles tenham a chance de se reagruparem, quero descobrir o que está provocando isso.", rewards = "Você receberá:\n\n"},
[2864] = {title = "Tran'rek", objective = "Fale com Tran'rek em Geringontzan.", description = "Meu primo Tran'rek está encrencado. Ele me prometeu um carregamento de couraças de escaravelho semanas atrás e ainda não entregou. Ele é parente, né, por isso que ainda não mandei ninguém a Geringontzan para quebrar um osso ou dois do sujeito.\n\nPode falar com Tran'rek por mim? Avise que ele precisa fazer a entrega antes que eu perca a paciência.\n\nGeringontzan fica no norte de Tanaris, em Kalimdor.", progress = nil, completion = "Olá!\n\nAh, você falou com o meu primo Krazek? Como ele está? Está curtindo Angra do Butim...\n\nVocê disse couraças de escaravelho? Bom, deixa eu contar uma história...", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2865] = {title = "Couraças de escaravelho", objective = "Leve 5 Couraças de Escaravelho Fechadas para Tran'rek em Geringontzan.", description = "Os escaravelhos de Tanaris têm couraças muito duras! Tão duras que podem ser usadas como material de construção para muitas coisas. Muitas, mesmo!\n\nAs couraças são tão úteis... que os escaravelhos foram caçados sem trégua e estão à beira de extinção!\n\nSei onde tem mais deles. Se você prometer que trazer as couraças pra mim, eu digo onde é. Promete?\n\nOk, tem uma colônia de escaravelhos em Zul'Farrak. Acho que os trolls não se interessam pelas couraças.\n\nMas você, sim! Vá até Zul'Farrak e me traga couraças intactas, fechadas.", progress = "Trouxe as couraças? Meu primo de Angra do Butim está esperando um carregamento, e já está perdendo a paciência!", completion = "Excelente! Você conseguiu!\n\nObrigado, &lt;name&gt;. Você salvou a minha vida!", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2866] = {title = "As Ruínas de Solarsal", objective = "Explore as Ruínas de Solarsal e investigue a presença das nagas.", description = "Ao sul de onde estamos ficam as Ruínas de Solarsal. É uma área muito anterior aos tempos modernos. Exploramos as ruínas e como, de início, não encontramos nada digno de nota, saímos em paz.\n\nRecentemente, um contingente assustadoramente grande de nagas Cristafúria ocupou as ruínas. No começo, achamos que era o prenúncio de um ataque. Embora a ameaça ainda seja real, acreditamos que tem algo mais acontecendo.\n\nVá até lá, explore as ruínas e procure por qualquer coisa que se destaque. Boa sorte, <name>.", progress = nil, completion = "Um exame do gazebo revelou aspectos interessantes e incomuns quando comparados às ruínas em torno.\n\nPrimeiro, o gazebo é uma construção claramente mais recente que as outras. Segundo, o gazebo tem uma pequena série de glifos gravados nas laterais. O padrão dos glifos se repete, mas sua origem e significado são desconhecidos. Terceiro, a pedra do gazebo é quente.\n\nIsso certamente pode ser qualificado como \"destacar-se\" em meio às outras ruínas!", rewards = "Você receberá:\n\n"},
[2867] = {title = "Regresso ao Domínio de Plumaluna", objective = "Fale novamente com Shandris Plumaluna no Domínio de Plumaluna e relate suas descobertas.", description = "Com a descoberta do estranho gazebo no coração das Ruínas de Solarsal, chegou a hora de regressar ao Domínio de Plumaluna e relatar à General Shandris Plumaluna o que foi descoberto ali.", progress = nil, completion = "Bem-vinda, &lt;name&gt;. Seu relatório sobre o gazebo é muito... perturbador. Ao que parece, mesmo que as nagas não estivessem planejando um ataque, a mera presença delas é um mau augúrio para nossa invasão de Feralas. Seja como for, aqui está a paga pela sua investigação.\n\nParece que o Domínio de Plumaluna precisa de ajuda contra as nagas Cristafúria. Gostaria de lhe oferecer essa tarefa, se você quiser. Há muito a fazer.", rewards = "Você receberá:\n\n"},
[2868] = {title = "O coração murcho", objective = nil, description = "Transforme essa belezinha em uma tumba de troll no Templo Submerso e consiga uma coisa legal!", progress = nil, completion = nil, rewards = nil},
[2869] = {title = "Cruzada contra as Cristafúrias", objective = "Leve 10 Escamas de Naga Cristafúria para Latrônicus Lunalança no Domínio de Plumaluna.", description = "A  essa altura, a melhor estratégia é considerar que, não importa o que estejam tramando, as nagas vão acabar se movimentando para garantir a ilha só para elas. Precisamos tomar medidas agressivas para conter essa ameaça antes que seja tarde demais.\n\nTraga para mim dez escamas de Cristafúria em boas condições, aposto que você já sabe como fazer para conseguir. Você enfrenta as nagas primeiro, assim conseguiremos enfraquecer a presença delas na Ilha de Sardor. São duas iniciativas que jogam a nosso favor.", progress = "<name>, se você está aqui para receber uma tarefa, excelente, estou procurando dez escamas de Cristafúria. Se está aqui pra jogar conversa fora, pode dar meia volta.\n\nSaiba de uma coisa, nós, elfos noturnos, não somos sanguinários. Nossa raça se dedica à manutenção da paz e da harmonia. Isso não significa, porém, que não vamos proteger nossos interesses de forma proativa. Esse é o meu dever para com a General Plumaluna e o domínio.", completion = "Sim, sim, essas escamas são mais que satisfatórias. Na verdade, acho que vou mandar para o Pradal dar uma olhada. Talvez seja possível construir algo útil com elas.\n\nQuanto a você, uma nova missão lhe espera. Mas, antes, aqui está o butim pelas escamas. Os serviços que você vem prestando à General e ao Domínio de Plumaluna como um todo são uma benção diante das adversidades.", rewards = "Você receberá:\n\n"},
[2870] = {title = "Contra o Lorde Shalzaru", objective = "Elimine o Lorde Shalzaru, e depois leve a Relíquia Misteriosa que ele desencavou a Latrônicus Lunalança no Domínio de Plumaluna.", description = "<name>, novos relatos de batedores nos deram uma boa noção da cadeia de comando das nagas. Há realeza presente em meio às baixas patentes, e são elas que estão no comando.\n\nEspecificamente, identificamos um dos membros da realeza mais cruéis, o Lorde Shalzaru. Batedores relataram que ele desencavou uma relíquia estranha com as próprias mãos durante uma recente escavação naga em uma caverna ao sul das ruínas principais, na Ilha do Medo.\n\nAcabe com ele e dê um ponto final nisso tudo, depois traga a relíquia até aqui para que possamos estudá-la.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2871] = {title = "A entrega da relíquia", objective = "Entregue a Relíquia Misteriosa a Véstia Lançaluna no Domínio de Plumaluna.", description = "Se você me fizesse o favor de entregar a relíquia a Véstia, minha mulher, eu ficaria muito grato. Ela é não só uma ótima sacerdotisa para nossa comunidade, mas também está entre os acadêmicos mais versados do mundo. Nem que me pagassem eu conseguiria explicar alguma coisa sobre esse objeto. Porém, se tem alguém capaz de descobrir para que isso serve, é ela... e nós precisamos saber!\n\nEla deve estar logo depois da curva, estudando, com certeza. Obrigado, <name>.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nPeito d'Armas do Cavaleiro da AuroraProtetor da Sentinela\n\nVocê também receberá:\n\n"},
[2872] = {title = "A dívida de Esmirno", objective = "Fale com Esmirno no Porto de Bondebico.", description = "Meu velho companheiro Esmirno me deve uma garrafa de rum muito especial. Ele cuida do bar do Porto de Bondebico de Tanaris, em Kalimdor, e me jurou que esse rum é muito mais forte do que qualquer bebida que se possa encontrar na Angra do Butim. Ele disse que, se eu experimentasse e não concordasse, dobraria o valor da dívida que tem comigo!\n\nEu estou achando que ele ficou com medo de ter apostado, e agora não quer me dar a garrafa. Só que aposta é aposta, e eu quero que você vá lá falar com ele por mim...", progress = nil, completion = "Então você andou conversando com o MacKinley, não é?\n\nNão é o que ele está pensando! Não estou com medo de dar o Rum do Porão do Bondebico a ele...\n\nO problema é que o rum acabou!", rewards = "Você receberá:\n\n"},
[2873] = {title = "Carregamento do Esmirno", objective = "Leve o Carregamento do Esmirno para Esmirno no Porto de Bondebico.", description = "Estou devendo uma dose de rum ao MacKinley \"Lobo do Mar\" da Angra do Butim para decidir uma aposta, mas meu rum acabou. O Capitão Cuergo roubou meu último carregamento e levou para aquela cidade pirata murada ao sul.\n\nSe você encontrar o carregamento e trouxer de volta para mim, vou provar ao MacKinley que o goró de Kalimdor é muito melhor que o da Angra do Butim!", progress = "Encontrou meu carregamento de rum, <name>?", completion = "Ótimo, obrigado pelo carregamento! Agora MacKinley e eu vamos finalmente decidir o vencedor da aposta.", rewards = "Você receberá:\n\n"},
[2874] = {title = "Entrega para MacKinley", objective = "Leve uma Garrafa do Esmirno para MacKinley \"Lobo do Mar\" na Angra do Butim.", description = "Mais uma vez, obrigado pela ajuda, <name>. Aqui está uma garrafa do rum do porão do Bondebico. Leve para o MacKinley, assim ele vai entender o que é bebida de verdade.", progress = "Esmirno liberou minha bebida?", completion = "Hum, vamos experimentar...\n\nNão dá para acreditar! Que coisa horrorosa! Adorei!\n\nBom, acho que o Esmirno pode ficar com o dinheiro que ele me deve. O rum de porão do Bondebico é tudo isso que ele falou, mesmo. É rascante como saibro e queima como lava!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nFaixa do ValentãoBotas Chuta-canela\n\nVocê também receberá:\n\n"},
[2875] = {title = "PROCURA-SE: André Barbarruiva", objective = "Leve a Cabeça do André ao Chefe de Segurança Silvadágua no Porto de Bondebico.", description = "O vil pirata André Barbarruiva é procurado por crimes contra Bondebico e Geringontzan.\n\nPor decreto do conselho governante, qualquer um que leia isto está autorizado a usar força letal durante a apreensão de André e seus apoiadores.\n\nPor decreto complementar, caso André esteja morto ou de outro modo indisponível durante o período desta audiência, sua cabeça pode agir na capacidade de procuradora, e a entrega da dita cabeça ao Chefe de Segurança Silvadágua constitui um contrato ofertável e vinculante.", progress = "Você parece meio zureta. Andou lendo nossa documentação legal?", completion = "Ah, a cabeça do André. Isso quer dizer que ele finalmente vai a julgamento!\n\nAgradecido, &lt;name&gt;. Agora, se puder assinar aqui, eu vou cuidar da liberação do pagamento pelos serviços prestados.\n\nNão se preocupe com as letrinhas miúdas. É uma cláusula inofensiva e... eu duvido que exista uma lupa potente o suficiente para ampliá-las.", rewards = "Você receberá:\n\n"},
[2876] = {title = "Cronograma de Navios", objective = "Apresente o Cronograma de Navios a uma autoridade do Porto de Bondebico.", description = "Este pergaminho contém o cronograma dos carregamentos entre o Porto de Bondebico e a Angra do Butim. Traz carregamentos antigos e futuros e os nomes dos navios, com grandes \"X\" ao lado da rota de alguns navios, e anotações como \"GRANDE SAQUE\" ou \"LUTA DURA\" ao lado de outras.\n\nTem alguém no Porto de Bondebico que adoraria ver esse documento.", progress = "Você parece sem fôlego. Tem algo a me dizer?", completion = "Nossa, isso parece uma lista de partidas e chegadas de navios, com datas e horários precisos! Se isso cai nas mãos do capitão de um navio pirata, ele faria uma festa!\n\nVocê disse que conseguiu isso com um pirata? Bem, essa é uma notícia preocupante...\n\nObrigado, &lt;name&gt;. Ainda bem que você conseguiu pegar a lista, e melhor ainda que o pirata já está comendo capim pela raiz.", rewards = "Você receberá:\n\n"},
[2877] = {title = "Limpeza na Rocha Oculta", objective = "Mate 10 Lodos Verdes e 10 Gosmas Jade, depois apresente-se novamente a Explodar Mantrovejante no Ninho da Águia.", description = "Nós, os Martelos Ferozes, nos lembramos mais do passado do que nossos irmãos de Altaforja. Se quiser fazer amigos aqui, vai precisar botar a mão na massa.\n\nA Horda abandonou as posições nas Terras Agrestes em torno da Rocha Oculta. Agora a área está tomada por lodos e gosmas nojentos. Temos planos para lá, mas precisamos que uma equipe de limpeza faça um buraco para nós.\n\nÉ aqui que você entre, chuchu! Vá até lá e arranque dez lodos verdes e deste gosmas jade, depois fale novamente comigo. Vá!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2878] = {title = "Flor da Canção corrompida", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[2879] = {title = "O Báculo de Equinex", objective = "Energize o Báculo de Troya e encontre o Monolito de Equinex.", description = "Este báculo pode ser o Báculo de Equinex! O Báculo de Equinex é na verdade uma chave, usada para destrancar o Monolito de Equinex nas Ruínas dos Ventos Corvejantes, no continente a oeste do Ramo de Morfeu.\n\nEncontre as quatro chamas que ainda ardem nas ruínas: Samha, Imbel, Byltan e Lahassa. Extraia a essência de cada uma e, diante do Monolito de Equinex, use-as para energizar o báculo. Se for o verdadeiro Báculo de Equinex, você conseguirá destrancar o monolito e recolher o artefato sagrado que ele guarda.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nUma Pedra Rutilante"},
[2880] = {title = "Recompensa dos colares trólicos", objective = "Leve 5 Colares Tribais Troll para Explodar Mantrovejante no Ninho da Águia.", description = "Entenda uma coisa, <race>, nós NÃO fazemos parte da Aliança! Os Martelos Ferozes são independentes e acreditam que o valor de alguém está no caráter e nas ações, e não no blá-blá-blá que muita gente usa para explicar as besteiras que fazem!\n\nPara fazer amigos aqui, você precisa mostrar o seu valor! Os quatro trolls que dominam as colinas das Terras Agrestes costumam usar colares tribais. Traga cinco desses colares para mim e vamos considerar que você deu um passo na direção certa.", progress = "Quero cinco desses colares tribais que os trolls costumam usar. Se precisar de mais detalhes, mate um troll que você com certeza vai conseguir um colar!\n\nProve o seu valor para os Martelos Ferozes!", completion = "Agora eu gostei! Está vendo, esses colares são a prova de que você está a fim de sujar as mãos. Nós, os Martelos Ferozes, valorizamos isso demais em nossos parceiros.\n\nSe continuar assim, não vai demorar para você cair nas nossas graças.", rewards = "Você receberá:\n\n"},
[2881] = {title = "Recompensa dos colares trólicos", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[2882] = {title = "Cuergo Ouro", objective = "Encontre o ouro de Cuergo!", description = "Este mapa mostra uma faixa de litoral voltada para o sul. Abaixo do mapa veem-se algumas palavras em caligrafia elegante...\n\n\nAo sul do Bico\nAo sul do lar\nOssos, mastro e bandeira ao ar\nVai cavar a alma aventureira\nE levar o ouro do Cuergo na algibeira!\nQuem procura, a chave acha\nMas paga em sangue a sua taxa.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nTesouro Escondido do Cuergo"},
[2902] = {title = "Investigação Patábua", objective = "Encontre os Planos de Batalha Gnoll no acampamento dos Gnolls ao sul da Aldeia Mojache.", description = "Os gnolls não são as criaturas mais espertas do mundo, mas, sem dúvida, eles têm algum tipo de estratégia documentada sobre suas atividades recentes. Se achássemos esses planos, poderíamos ter uma ideia do que os está deixando tão perturbados.\n\nQuero que você vá até os Patábua e encontre algo que possa ter servido de plano de batalha. Eles devem ter algo. Seus ataques foram muito coordenados para não terem sido planejados. Ache os planos e traga-os quando os tiver em mãos.", progress = nil, completion = "O exame do mapa revela que ele contém os planos de batalha que Hadoken Passo Veloz quer. Algumas setas, provavelmente representações dos movimentos das tropas, estão desenhadas no mapa – muitas delas dirigem-se ao sul ao invés do norte, onde fica a Aldeia Mojache.\n\nOutra parte do mapa tem um desenho de uma espécie de inseto gigante, com marcas embaixo dele. Pela aparência, talvez seja uma representação das perdas dos gnolls para os insetos desconhecidos...", rewards = "Você receberá:\n\n"},
[2903] = {title = "Os planos de batalha", objective = "Leve os Planos de Batalha de Patábua para Hadoken Passo Veloz na Aldeia Mojache.", description = "Com os planos de batalha dos gnolls descobertos, agora só falta entregá-los para Hadoken Passo Veloz na Aldeia Mojache. Talvez ele ou alguém de lá possa ter alguma ideia do que os gnolls pretendem fazer.", progress = "Seja bem-vindo de volta <name>. Você conseguiu encontrar alguma evidência dos planos dos gnolls?", completion = "Ah, isso é exatamente o que eu precisava! Por mais que eu não consiga decifrar tudo que esta rabiscado nesse mapa, já vi planos de batalha o suficiente nesta vida para entender o que está acontecendo com os gnolls. Parece que eles estão lutando em duas frentes de batalha. Uma delas é conosco... mas parece que seus esforços principais são contra um inimigo ao sul.\n\nHá mais coisas por trás dessa história. Talvez seja a hora de descobrirmos quem é esse adversário deles.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBroquel FechaterraBotas Serrapressa\n\nVocê também receberá:\n\n"},
[2904] = {title = "Uma bela bagunça", objective = "Escolte Kernobill até a saída do Corredor dos Mecanismos, depois apresente-se a Scooty na Angra do Butim.", description = "<Kernobill solta um grunhido.> Quê... O que aconteceu?\n\nA última coisa de que me lembro foi ser capturado por um grupo de anões ferro negro, apanhar até ficar à beira da morte e depois ser jogado no meio desses troggs imundos.\n\nTire-me daqui, <name>!\n\nO plano é o seguinte: é só me levar até a saída. Se escaparmos vivos, vá para a Angra do Butim e avise o Scooty que eu fui para a Vila Catraca, alertar o Guariba de que os Ferro Negro estão envolvidos no surgimento da superbroca.\n\nVamos logo!", progress = nil, completion = "Acho que você merece uma recompensa por ter trazido Kernobill de volta inteiro, &lt;name&gt;. Se por ventura você for a Gnomeregan, isto aqui lhe dará uma vantagenzinha na hora de enfrentar aquele traidor imprestável do Mecangenheiro Termaplugue.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBraçadeiras SoldadasDragonas Asa-de-fada\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[2922] = {title = "Salve o cérebro do Tecnobô!", objective = "Leve o Núcleo de Memória do Tecnobô para o Mestre-faz-tudo Superchispa em Altaforja.", description = "Quando nossa amada cidade de Gnomeregan sucumbiu, minha pobre criação, o Tecnobô, deve ter sofrido um curto do mal. Ele ajudava a mim e aos meus parceiros com artifícios incontáveis e um banco de dados sem limites, porém seu cérebro positrônico polarizou. Ficou negatrônico... e o comportamento do Tecnobô se inverteu. Ele vaga por Gnomeregan em frenesi!\n\nMas talvez seja possível reciclá-lo. Se você trouxer o núcleo de memória dele, eu posso descobrir qual foi a causa da polarização e consertar meu pobre Tecnobô.\n\n", progress = "Encontrou o Tecnobô? Conseguiu o núcleo de memória??", completion = "Ah, que maravilha! Obrigado, &lt;name&gt;! Eu vou botar a mão na massa agora mesmo!!\n\nCoitadinho do Tecnobô... Não fique assim. Nós vamos consertá-lo.\n\nOnde foi que eu enfiei o cabo da chave hidráulica de torque...", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2923] = {title = "Mestre-faz-tudo Superchispa", objective = "Fale com o Mestre-faz-tudo Superchispa em Altaforja.", description = "O Mestre-faz-tudo Superchispa ficou muito chateado com o fim que teve sua criação mecânica, o Tecnobô. Eu me lembro de que era muito útil para os gnomos, mas o Mestre-faz-tudo me disse que ele estragou.\n\nQue coisa triste. Mas talvez seja possível consertá-lo!\n\nFale com o Mestre-faz-tudo Superchispa no Beco da Gambiarra, lá em Altaforja. Ele ficará agradecido pela ajuda.", progress = nil, completion = "É verdade. O Tecnobô enlouqueceu! Você teria como me ajudar?", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2924] = {title = "Artifícios Essenciais", objective = "Leve 12 Artifícios Essenciais para Cronomorfus Espanafuso em Altaforja.", description = "Um componente mecânico praticamente universal, o artifício essencial, foi de grande valia para os faz-tudo e ferreiros de Gnomeregan. Mas na pressa do êxodo, ninguém se lembrou de trazer nenhuma dessas peças! E eu vou precisar de um estoque para o projeto que vou iniciar logo, logo.\n\nPor favor, vá buscar um punhado de artifícios essenciais para mim em Gnomeregan. Eles ficam nas partes mais profundas da cidade, guardados em extrapoladores artificiais.\n\nMuito obrigado, <name>, só peço que não demore!", progress = "Você trouxe os artifícios essenciais?", completion = "Você conseguiu! Agora já posso começar meus experimentos!\n\nNão tenho como lhe agradecer, &lt;name&gt;! Graças à sua coragem, a ciência gnômica caminha a passos largos!", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2925] = {title = "Os artifícios do Cronomorfus", objective = "Fale com Cronomorfus Espanafuso em Altaforja.", description = "Um amigo gnômico, Cronomorfus Espanafuso, mandou notícias de Altaforja. Ele me disse que precisa de ajuda em uma missão perigosa porém essencial em Gnomeregan, a antiga capital gnômica que hoje está contaminada, infestada de gnomos enlouquecidos e suas invenções.\n\nEu tenho que ficar aqui, mas meu coração quer ajudar. Se você puder ir no meu lugar, eu ficarei lhe devendo uma.\n\nO Cronomorfus vive na Vila da Gambiarra, lá em Altaforja.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2926] = {title = "Gnogaína", objective = "Use o Coletor de Chumbo Vazio nos Invasores Irradiados ou nos Pilhadores Irradiados para recolher resíduos radioativos. Depois que ele ficar cheio, leve-o de volta para Ozzie Mudavolt em Kharanos.", description = "Desde o acidente, Gnoarn só fala em livrar Gnomeregan da infestação trogg e em descobrir a cura para a doença que aflige a cidade, <name>.\n\nPor sorte, eu e o Razzle estamos perto de descobrir a cura... Se não é a cura, é uma receita forte para caramba de rum. Só vamos saber depois de testar! Mas para testar nossa teoria, vamos precisar de algumas coisinhas naturalmente. A saber: resíduos radioativos dos troggs em Gnomeregan. Traga-me esse coletor cheio de brilho verde!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2927] = {title = "O dia seguinte", objective = "Fale com Ozzie Mudavolt em Kharanos.", description = "Você não achou que eu tivesse nascido careca, não é? Não, senho<r/ra>! Foi o vazamento de radiação em Gnomeregan que provocou essa tragédia capilar. Antes daquilo, eu tinha o único gnomafro de toda Azeroth.\n\n<Gnoarn ergue o punho cerrado no ar.>\n\nMalditos sejam aqueles troggs imundos... malditos sejam!\n\nTalvez ainda não seja tarde demais, <name>. Talvez ainda seja possível reverter a situação... da cidade, digo! Fale com Ozzie Mudavolt em Kharanos. Ele e seu parceiro Razzle estão bolando uma solução para o problema.", progress = nil, completion = "Eu pensei que a perda daquela juba espetacular seria o fim dele, mas o sujeito sobreviveu!\n\nEu estava logo ao lado no momento em que ele foi atingido pela radiação. Vi aqueles cachos castanhos perfeitos desabarem no chão com tudo! Foi uma avalanche! A coisa ficou pior ainda depois que Gnoarn perdeu a cabeça! Não é exagero, perdeu a cabeça mesmo! Gnoarn levou a mão à careca totalmente descrente, seus olhos exigiam vingança, &lt;name&gt;! Foi então que ele voltou sua fúria para os troggs!", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2928] = {title = "Escavacionadores girobrocamáticos", objective = "Leve 24 Tripas Mecanobóticas para Shoni em Ventobravo.", description = "Que tragédia, <name>! Não vamos ter peças suficientes para levarmos nossa frota de escavacionadores girobrocamáticos até Dun Morogh.\n\nPrecisamos de peças de reposição, <name>. E sabe onde tem um monte delas? Lá em Gnomeregan! Fiquei sabendo que mecas vagueiam por lá como se fossem gado no pasto.\n\nEu preciso que você vá até Gnomeregan, desmanche esses mecas e me traga suas entranhas robóticas. Duas dúzias devem bastar.", progress = "Se fosse uma corrida, você já teria perdido.", completion = "Está mais que perfeito, &lt;name&gt;. Um escavacionador girobrocamático consertado; só faltam mais 398.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nFerramenta de Desarme da ShoniLuvas Bechil\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[2929] = {title = "A grande traição", objective = "Vá para Gnomeregan e mate o Mecangenheiro Termaplugue. Fale novamente com o Grão-faz-tudo Mekkatorque depois que tiver terminado a tarefa.", description = "Eu confiava no Termaplugue, <race>. Nunca imaginei que ele fosse trair a mim e a todo o povo gnômico. E a troco de quê? Poder? Riqueza? Coisas que ele conquistaria no devido tempo. Agora fomos retirados da nossa casa e aquele lunático ficou no comando. O rei de coisa nenhuma...\n\nNós retomaremos Gnomeregan, <name>. Não vamos dar trégua enquanto não tivermos a cidade sob controle. Se quiser se juntar à luta, eu tenho uma tarefa bem simples para você: matar o traidor. Destruir o Mecangenheiro Termaplugue.", progress = nil, completion = "Gosto de imaginar que a última coisa que passou pela cabeça dele antes de cair no chão foi o seu pé, &lt;race&gt;. Com Termaplugue morto, nossos planos de retomar Gnomeregan estão mais perto de se tornar realidade.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nVestes de CivínadaCulotes CorretrechoPerneiras Duplas Reforçadas\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[2930] = {title = "O resgate dos dados", objective = "Leve o Cartão Perfurado Prismático para o Mestre Mecânico Fundecano em Altaforja.", description = "Quando fugimos de Gnomeregan, deixamos muitos dados para trás! Dados de importância vital!\n\nEu preciso de dados salvos em um cartão perfurado prismático, no terminal de segurança máxima da Matriz Perfuradora 3005-D, na Gnomeregan profunda. Você terá que acessar o terminal, mas vai precisar de permissão... e para obter permissão, vai precisar acessar os terminais de segurança mínima A, B e C. Mas PARA ISSO, vai precisar de uns cartões perfurados, a começar pelo branco... E nós deixamos todos os cartões perfurados na cidade.\n\nÉ uma catástrofe! Você pode ajudar?O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nCapa do MecânicoMartelo do Mecânico"},
[2931] = {title = "A tarefa do Fundecano", objective = "Fale com o Mestre Mecânico Fundecano em Altaforja.", description = "O Mestre Mecânico Fundecano, lá em Altaforja, está atrás de aventureiros! Ele vem organizando grupos de busca com a finalidade de resgatar dados em Gnomeregan, em uma missão de importância e segurança máxima.\n\nO Fundecano pode até não ser membro da Assembleia Esclarecida de Arcanologia, Alquimia e Ciências de Engenharia que nem eu, mas não deixa de ser um gnomo inteligente, então se ele avalia que é importante, pelos meus cálculos há uma chance de 84% de que seja mesmo importante!\n\nSão ótimos números!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2932] = {title = "Recado sinistro", objective = "Recolha Crânios de Cascasseca e leve o Pique de Nimboya. Finque o Pique de Nimboya Repleto em uma das Aldeias Cascasseca nas Terras Agrestes, depois fale novamente com Nimboya na Selva do Espinhaço.", description = "Os trolls Cascasseca já foram parceiros da Horda, mas traíram o Thrall... e isso tem que ter punição!\n\nAqui, pega meu pique e vai pras Terras Agrestes, rumo ao norte toda vida. Tu vai encontrar os Renegados a nordeste dum vilarejo aliado: a Serraria Tarren.\n\nAssim que tu pisar nas Terras Agrestes, pode sair à caça dos trolls Cascasseca. Recolhe os crânios e finca meu pique numa das aldeias menores, em Hiri'watha ou Zun'watha.\n\nMostra pra eles que todo ato tem suas consequências!", progress = nil, completion = "Fiquei sabendo do que tu fez. Um feito sinistro, mas honrado. Tomara que os Cascasseca tenham entendido o recado.", rewards = "Você receberá:\n\nCrânio de Cascasseca"},
[2933] = {title = "Garrafas de peçonha", objective = "Leve uma Garrafa de Peçonha para um boticário na Serraria Tarren.", description = "Nas garrafas têm um veneno horrível de cor esverdeada.\n\nConsiderando a quantidade de garrafas na aldeia, deve ser uma coisa muito importante para os Cascasseca. Tem um boticário aqui perto, na Serraria Tarren, que ia gostar de receber uma amostra.", progress = "Olá, gentil <class>. O dia foi longo e nem um experimentozinho sequer deu certo... Tomara que você tenha trazido boas novas.", completion = "Ah, o que seria isso? Parece veneno... De uma criatura aracnídea, ao que tudo indica. E onde foi que você conseguiu isso?", rewards = "Você receberá:\n\n"},
[2934] = {title = "Vesícula de peçonha intacta", objective = "Leve a Vesícula de Peçonha Intacta para o Boticário Lindolfo na Serraria Tarren.", description = "Eu preciso de uma amostra fresquinha do veneno que você me trouxe. Essas aranhas devem viver nas Terras Agrestes. Fique caçando até encontrar uma vesícula de peçonha intacta e traga-a para mim. Tem muitas aranhas perto da cidade trólica de Shadra'Alor.\n\nNão se demore, <name>, a peçonha tem que estar fresca no momento da entrega.", progress = "Trouxe a vesícula de peçonha intacta, <name>? Eu estou doido para testar o veneno ainda fresco, já mandei trazerem um bando de bichinhos ridiculamente fofos para servirem de cobaia!", completion = "Ah, esplêndido! O veneno ainda está fresco, na certa mataria um. Obrigado!", rewards = "Você receberá:\n\n"},
[2935] = {title = "Consulte o Mestre Gadrin", objective = "Fale com o Mestre Gadrin na Aldeia Sen'jin.", description = "O veneno das Guarda-prole não era tão potente quanto o que você trouxe primeiro. Deve ser de uma aranha diferente.\n\nEu quero uma amostra fresca do outro veneno. Para descobrir de onde ele veio, você terá que consultar um troll que conhece bem os Cascasseca: o Mestre Gadrin, na Aldeia Sen'jin. Vá para Durotar e converse com o Gadrin. Pode ser que ele saiba de onde os Cascasseca tiram esse veneno.", progress = nil, completion = "Ah, então tu quer o veneno das aranhas dos Cascasseca? Senta aí, jovem... tu não vai gostar nem um pouco do que vai ouvir.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2936] = {title = "A deusa aranha", objective = "Leia a Tabuleta de Theka para descobrir o nome da deusa aranha dos Cascasseca, depois fale novamente com o Mestre Gadrin.", description = "Os Cascasseca veneram a deusa aranha e é dela que recolhem o veneno mais cabuloso. Se tu quiser o veneno, vai ter que conjurar a tal deusa!\n\nPra fazer isso, tu precisa descobrir o nome dela, que tá escrito numa tabuleta enterrada em Tanaris, lá na cidade trólica de Zul'Farrak, numa tumba protegida por Theka, o Mártir.\n\nTu vai que deitar o Theka e ler a tabuleta pra descobrir o nome da deusa aranha. Depois que tu souber o nome, volta aqui.", progress = nil, completion = "Ah, Elortha no Shadra... só diga esse nome em sussurros, pois a menção dele tem muito peso no reino dos espíritos.\n\nEu vou botar num pergaminho e elaborar um feitiço de evocação pra você...", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2937] = {title = "Evocando Shadra", objective = "Derrote Shadra no altar de Shadra'Alor, depois leve o veneno para o Boticário Lindolfo na Serraria Tarren.", description = "Tu tem que ler o pergaminho em voz alta no altar de Shadra'Alor, lá na meiuca das Terras Agrestes, no território dos Cascasseca. É assim que tu vai conjurar Shadra, a deusa aranha.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2938] = {title = "Veneno para a Cidade Baixa", objective = "Leve o Embrulho do Faranello para o Mestre-boticário Faranello na Cidade Baixa.", description = "O veneno que você extraiu da deusa aranha dos Cascasseca foi um achado e tanto! Já matei uma duas dúzias de coelhos só com uma gota daquele negócio. Tudo bem que parte dos coelhos eram muito filhotinhos e tão pequenos que cabiam na palma da mão, então é certo que tinham uma resistência a toxinas muito baixo, mas não se pode negar que o veneno é poderoso!\n\nEu gostaria de enviar uma amostra ao meu superior, o Boticário Faranello, do Boticarium da Cidade Baixa. Tenho certeza de que ele vai querer testar a substância pessoalmente.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nVestimenta da Altivez RealPeito d'Armas da Guarda HonoráriaÉgide de Batalha\n\nVocê também receberá:\n\n"},
[2939] = {title = "Em busca de conhecimento", objective = "Fale com Daryn Aelumnis na Vila de Rut'theran.", description = "Por mais que as ruínas de Feralas sejam perigosas, elas têm muito a dizer sobre o que aconteceu aqui.\n\nVasculhando os destroços ao sul alguns dias atrás, eu encontrei um cajado que parecia um cajado qualquer. No entanto, nada me tira da cabeça que tem mais coisa ali. Eu e Ângela nos debruçamos sobre os livros, mas não descobrimos nada de nada.\n\nEu tenho uma colega em Darnassus que talvez possa nos dizer que cajado é esse, <name>. Por que você não vai falar com ele e ver se ela não pode ajudar?", progress = nil, completion = "Ah, o Troyas Brisaluna mandou você aqui?\n\n&lt;Ela resmunga&gt;...sempre enchendo a paciência com essa maluquice de que encontrou alguma coisa importante...\n\nOlhe só, &lt;name&gt;, vou ser curta e grossa: o Troyas sempre acha que encontrou algo importante naquelas ruínas. A verdade é que tudo que havia de valor já deve ter sido carregado há tempos pelos ogros e harpias que se espalharam pela região.\n\nFique à vontade para procurar, mas não sei se você vai encontrar nenhum livro que possa ter serventia.", rewards = "Você receberá:\n\n"},
[2940] = {title = "Feralas: uma história", objective = "Peça o livro emprestado a Daryn Aelumnis.", description = "O livro tem cara de que não é aberto por ninguém há um bom tempo. A capa está desbotada e as páginas, amareladas, mas após um breve exame, você acha que talvez seja o que Troyas está procurando.\n\nVocê apanha o livro e só então se dá conta de que talvez fosse melhor pedi-lo emprestado antes.", progress = nil, completion = "Então você quer pegar \"Feralas: uma história\" emprestado?\n\nDeixe-me pensar...\n\nEu lhe emprestarei o livro, &lt;name&gt;, mas só se você me fizer um favor antes.", rewards = "Você receberá:\n\n"},
[2941] = {title = "O empréstimo", objective = "Leve a carta para Crista Dapinote em Tanaris.", description = "Eu já estudei muitos temas nessa vida, meu último motivo de fascínio foram as mordeliscas que ocupam o litoral das Terras Agrestes. Tem uma em particular que eu gostaria de ver, uma mordelisca gigante chamada Gammerita. Gostaria de vê-la pessoalmente, mas minha pesquisa me prende aqui. Acho que um retrato seria o mais próximo disso.\n\nLeve esta carta para a Crista Dapinote, em Tanaris. Ela inventou um artefato que captura imagens. Chamou o negócio de \"foto\", eu acho...Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2942] = {title = "A pedra matinal", objective = "Traga a Pedra Rutilante e o Báculo de Equinex para Troyas Brisaluna no Domínio de Plumaluna.", description = "No momento em que o artefato é removido do Monolito, você sente a energia do Báculo de Equinex começar a sumir.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nLasca de Pedra do MemorialAnel Época de Semear\n\nVocê também receberá:\n\n"},
[2943] = {title = "O retorno a Troyas", objective = "Leve o livro para Troyas Brisaluna no Domínio de Plumaluna.", description = "Aqui, <name>. Cuide bem do meu livro.\n\nVá, ande logo. Troyas deve estar ansioso pelo seu regresso.", progress = "Oi, <name>. Você já esteve em Darnassus?", completion = "Você trouxe um livro. Excelente!", rewards = "Você receberá:\n\n"},
[2944] = {title = "A Superinstantânea FX", objective = "Use a Superinstantânea FX para tirar uma foto da Gammerita, depois fale novamente com Daryn Aelumnis na Vila de Rut'theran.", description = "Dê uma olhada na minha nova invenção, <name>. É só mirar em qualquer coisa e apertar o botão para produzir uma imagem dela.\n\nQual era o nome da criatura das Terras Agrestes de que você falou? Gammerita? Bom, boa sorte na sua busca. Tenho certeza de que Daryn ficará mais que satisfeita com a foto do bicho.\n\nEnfim, aqui está a Superinstantânea! Divirta-se!", progress = "Até que enfim você voltou, <name>. E ainda trouxe a engenhoca que Crista arrumou para mim... perfeito.", completion = "Vejam só! A invenção da Crista atendeu às expectativas.\n\nNão vá ficar se achando, mas saiba que estou satisfeita com seu trabalho, &lt;name&gt;.\n\nAgora, onde foi que eu deixei aquele livro...", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2945] = {title = "Anel Encardido", objective = "Descubra uma maneira de remover a crosta de sujeira do Anel Encardido.", description = "No cadáver de um dos anões Ferro Negro, você encontrou o que parece ser um anel. Mas é difícil dizer que tipo de anel, pois ele está completamente incrustado de sujeira e não é possível nem mesmo usá-lo. Examinando de perto, você percebe o brilho tênue de um metal valioso e o que parece ser uma pedra preciosa em meio à crosta de sujeira.\n\nSe você deseja restaurar e usar o anel, primeiro terá que achar uma maneira de limpá-lo.", progress = "No aparelho gnômico à sua frente há uma placa com a inscrição \"Brastematic 5200\". Lendo as instruções, você descobre que a máquina serve para limpar todo tipo de objeto. Também há uma abertura para inserir moedas na máquina. Após inserir um objeto, são necessárias três moedas de prata para iniciar o processo de limpeza.\n\nEssa máquina deve ser perfeita para limpar o anel encardido que você encontrou!", completion = "Você coloca o anel dentro da Brastematic 5200 e insere as três moedas de prata.\n\nA máquina começa a balançar loucamente e a fazer um barulho estranhíssimo. Você não consegue nem imaginar o que está acontecendo com o anel lá dentro...\n\nDe repente, a máquina para, anunciando o término do processo com um simples \"Ding!\". O compartimento se abre, revelando uma caixinha bem embrulhada e com um laço vermelho. Parece que a Brastematic 5200 também embrulha pra presente!", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência"},
[2946] = {title = "Ver o que acontece", objective = "Leve os discos de platina em miniatura até os portões de Uldum, em Tanaris, e procure descobrir qual é a conexão entre essas duas coisas.", description = "Nós queremos que tu leve os discos em miniatura que encontrou até os portões selados de Uldum. Nós... não sabemos o que acontecerá quando tu chegar lá.\n\nParece arriscado, é, nós estamos cientes. Mas pode ser que não dê em nada. Talvez tu tenha que encaixar os discos em algum lugar. Talvez os portões se destranquem na presença dos discos. Esteja abert<o/a> para tentar de tudo e pront<o/a> para qualquer coisa.\n\nDescobrindo alguma coisa, volte para Altaforja. Nós vamos ter muito o que pensar depois disso!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2947] = {title = "Ficar ou não ficar... eis a questão!", objective = "Você pode ficar com o anel ou achar a pessoa que faz a gravação no interior dele.", description = "Ao abrir a caixa que saiu da Brastematic 5200, você encontra o que esperava: um anel limpo e brilhante! Colocando o anel no dedo, você sente um tremendo poder mágico correr pelo seu corpo.\n\nGravado na parte interior do anel, é possível ver o selo de Altaforja. Próximo ao selo, três iniciais estão gravadas: \"DdM\". Elas podem ser tanto as iniciais do antigo proprietário do anel quanto do próprio criador dele. Ficar com o anel ou entregá-lo ao dono... eis a questão! Decisões, decisões...O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2948] = {title = "Aperfeiçoamento gnômico", objective = "Entregue o Anel de Ouro Brilhante, uma Barra de Prata, uma Ágata Musgosa e 30 moedas de prata para Didiê Du Mocó, em Altaforja.", description = "Veja bem, <name>, o anel é seu, é claro... Mas eu posso fazer um melhor ainda para você! Esse modelo de anel, como eu disse, é muito simples. Eu agora estou mais capacitado ainda, então, pela módica quantia de trinta moedas de ouro, posso dar uma guaribada no seu anel! Bem, por um pouco mais que trinta moedas, na verdade...\n\nEu estou meio apertado agora – meu estoque de matéria-prima está vazio. Se você me trouxer uma barra de prata e uma ágata musgosa, eu consigo fazer um arabesco que vai deixar o seu anel nos trinques!\n\nAguarde e confie!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nAnel de Ouro do Didiê"},
[2949] = {title = "Ficar ou não ficar... eis a questão!", objective = "Você pode ficar com o anel ou achar a pessoa que faz a gravação no interior dele.", description = "Ao abrir a caixa que saiu da Brastematic 5200, você encontra o que esperava: um anel limpo e brilhante! Colocando o anel no dedo, você sente um tremendo poder mágico correr pelo seu corpo.\n\nGravado no interior do anel, é possível ver o selo de Orgrimmar. Próximo ao selo, há algumas letras gravadas, porém somente as três primeiras são compreensíveis depois da limpeza: \"NOG...\". Elas podem ser tanto as iniciais do antigo proprietário do anel quanto do próprio criador dele. Ficar com o anel ou entregá-lo ao seu dono, o \"NOG\"... eis a questão! Decisões, decisões...O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2950] = {title = "Recauchutagem do anel do Nogg", objective = "Leve o Anel de Ouro Brilhante, uma Barra de Prata, uma Ágata Musgosa e 30 moedas de Prata para Nogg em Orgrimmar.", description = "Não, não, não. Não estou dizendo duma hora pra outra que o anel é meu. Normalmente eu não faço isso, mas a informação que você trouxe a respeito dos Ferro Negro foi bem útil. Eu melhorarei o anel pra você, melhor do que quem fez essa bijuteria jamais poderia imaginar. Deve ter sido algum gnomo...\n\nMe traga um lingote de prata, uma ágata musgosa e trinta moedas de prata. Eu adicionarei uma treliça de ágata e prata ao anel que fortalecerá a magia do item. Te interessa?Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nAnel de Ouro do Nogg"},
[2951] = {title = "A Brastematic 5200!", objective = "Coloque um Objeto Encardido e três moedas de prata na Brastematic 5200 para iniciar o processo de limpeza.", description = "Observando o aparelho, você nota que ele se destaca em meio aos gnomos que resistem à confusão do lado de fora da sala. Em uma pequena placa é possível ler \"Brastematic 5200\", e logo em seguida: \"Crostas de sujeira incrustam seus pertences? A Brastematic 5200 resolve todos os seus problemas de limpeza! Coloque o objeto na máquina, três moedas de prata no compartimento e, tcharam! O fim da sujeira e um brilho jamais visto!\".", progress = nil, completion = nil, rewards = nil},
[2952] = {title = "A Brastematic 5200!", objective = nil, description = nil, progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nCaixa da Brastematic"},
[2953] = {title = "Brastematic FTW!", objective = "A Brastematic 5200 está vazia, esperando por um objeto encardido para limpar! Além das três moedas de prata, claro!", description = nil, progress = "A Brastematic 5200 está vazia, esperando por um objeto encardido para limpar! Além das três moedas de prata, claro!", completion = "A Brastematic 5200 toma vida, balançando para todos os lados. Dentro da máquina, a pobre sujeira que incrustava o objeto é pulverizada pelo sabão em pó GnOMO de altíssima qualidade.\n\nDe repente, A Brastematic 5200 para, emitindo um simples \"Ding!\". O compartimento onde você colocou o objeto se abre, revelando uma caixinha com um laço.", rewards = "Você receberá:\n\nCaixa da Brastematic"},
[2954] = {title = "O vigia pétreo", objective = "Descubra qual é o propósito do Vigia Pétreo de Norgannon, depois interaja com o Pedestal de Uldum no deserto de Tanaris.", description = "\"Saudações. Eu sou o guardião da entrada. A não ser que você já tenha as Placas de Uldum integradas ao seu conjunto de discos, eu não permitirei que você entre no complexo de Uldum.\"\n\nProvavelmente, o surgimento do Vigia Pétreo de Norgannon era o tipo de coisa que esperavam ao mandá-<lo/la> para o deserto de Tanaris com os discos de platina. Conforme lhe foi instruído, você terá que descobrir a história por trás do Vigia Pétreo antes de prosseguir.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2962] = {title = "A única cura é mais brilho verde", objective = "Vá até Gnomeregan e traga o Resíduo Radioativo de Alta Potência. Tome cuidado, pois a radiação é instável e pode se esvair rapidamente.\n\nOzzie também vai precisar do seu Coletor de Chumbo Pesado quando tiver terminado.", description = "Não só a amostra foi insuficiente em quantidade, como estava impura.\n\nNós precisamos de mais brilho verde. Os troggs não sofreram radiação suficiente para atender aos nossos propósitos. Você terá que adentrar Gnomeregan e recolher o resíduo radioativo na fonte. Estou falando dos visgos, tocaieiros e horrores irradiados. Esteja avisad<o/a> de que armazenar esse tipo de resíduo não é fácil. Você vai ter que fazer várias tentativas com o frasco até conseguir uma amostra decente.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2963] = {title = "Os portentos de Uldum", objective = "Fale com o Historiador Karnik no Salão dos Exploradores em Altaforja.", description = "Saudações mais uma vez, <name>! Sua descoberta em Uldaman deixou a Liga dos Exploradores em polvorosa! Estamos tentando decifrar os segredos dos discos de platina miniatura que você encontrou. Que bom que você passou aqui pelo Salão, pois assim posso lhe contar que estamos no rumo certo!\n\nO historiador Karnik descobriu evidências importantes sobre a verdadeira origem do Vale dos Vigilantes, no deserto de Tanaris, e quer falar com você assim que possível!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2964] = {title = "Uma tarefa futura", objective = "Fale com o Alto-explorador Magellas em Altaforja.", description = "Bom, <name>... Pelo visto, fomos dar num beco sem saída. Sabemos do que precisamos, das Placas de Uldum, mas não fazemos ideia de onde possam estar. Vamos ter que estudar um pouco a questão para elaborar um plano de ataque. Eu tenho alguma noção de por onde começar. Se tudo der certo, daqui a pouco já vamos poder te pedir para ir buscar as placas.\n\nTemos que deixar o Alto-explorador a par de tudo, <name>. Sem dúvida, ele vai querer te agradecer pessoalmente por mais uma vez ter ajudado a Liga.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2965] = {title = "Os portentos de Uldum", objective = "Fale com Nara Juba Agreste no Platô dos Anciãos, que fica no Penhasco do Trovão.", description = "Oh, <name>, sua volta é uma feliz coincidência. Suas descobertas na escavação Uldaman foram assunto de muitas discussões entre nossos sábios. E foi no meio de uma dessas discussões com a druidesa Nara Juba Agreste que me apercebi de que as aventuras passadas dela podem ter grande importância em relação ao próximo passo que será dado.\n\nGostaria que falasse com ela a respeito das viagens que ela fez ao Vale dos Vigilantes, que fica no deserto Tanaris. Ela <o/a> aguarda no Platô dos Anciãos.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2966] = {title = "Ver o que acontece", objective = "Leve os discos de platina em miniatura até os portões de Uldum, em Tanaris, e procure descobrir qual é a conexão entre essas duas coisas.", description = "Eu e o Sábio Devoto da Verdade queremos que você leve os discos em miniatura que encontrou até os portões selados de Uldum. Nós... não sabemos o que acontecerá quando você chegar lá.\n\nParece arriscado, é, nós estamos cientes. Mas pode ser que não dê em nada. Talvez você tenha que encaixar os discos em algum lugar. Talvez os portões se destranquem na presença dos discos. Esteja abert<o/a> para tentar de tudo e pront<o/a> para qualquer coisa.\n\nSe você descobrir alguma coisa, venha me dizer. Nós teremos muito o que discutir.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2967] = {title = "Volte ao Penhasco do Trovão", objective = "Fale com Nara Juba Agreste no Penhasco do Trovão.", description = "Você acessa o pedestal novamente. Os discos de platina aparecem mais uma vez, de repente, na sua mochila.\n\nCom o conhecimento do guardião pétreo adquirido e os discos em sua posse outra vez, chegou a hora de voltar para o Penhasco do Trovão e informar Nara Juba Agreste da sua descoberta.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2968] = {title = "Uma tarefa futura", objective = "Fale com o Sábio Devoto da Verdade no Penhasco do Trovão.", description = "Bom, <name>... Pelo visto, nós enfrentamos um impasse. Sabemos do que precisamos, das Placas de Uldum, mas não fazemos ideia de onde possam estar. Vamos estudar a questão e elaborar um plano de ataque. Eu tenho alguma noção do ponto de partida. Se tudo der certo, daqui a pouco já vamos poder lhe pedir para ir buscar as placas.\n\nTemos que deixar o Sábio Devoto da Verdade a par de tudo, <name>. Sem dúvida, ele vai querer agradecê-<lo/la> pessoalmente por mais uma vez ter atendido ao nosso chamado.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2969] = {title = "Liberdade para todas as criaturas", objective = "Com a Chave da Gaiola de Bambu, liberte os Dardejantes do cercado no acampamento dos Temível Totem. Pelo menos 6 Dardejantes e Kindal Teceluna devem sobreviver.\n\nVocê tem 6 minutos antes que Kindal resolva recuar e você fracasse na missão.", description = "Aquele imundo clã Temível Totem está capturando as dragoletas, <name>. Eu e Jer'kai não fazemos ideia do que planejam fazer com as bichinhas, mas com certeza não é coisa que preste.\n\nNão temos força bastante para adentrar o acampamento e libertar as dragoletas, mas, com sua ajuda, talvez...\n\nSe estiver dispost<o/a>, e tiver uns amigos bem fortes, solte as criaturas da jaula com esta chave e certifique-se de que escaparão com vida. Eu estarei do seu lado o tempo todo. Jer'kai ficará aqui.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2970] = {title = "Distribuindo a justiça", objective = "Mate 12 Naturalistas Temível Totem, 10 Saqueadores dos Temível Totem e 6 Xamãs Temível Totem antes reencontrar Jer'kai Teceluna em Feralas.", description = "Como ela já lhe disse, Kindal e eu levaremos uma ofensiva aos Temível Totem. Juntas, ainda podemos enfrentar alguns taurens sem corrermos um risco muito alto, mas sua ajuda seria muito bem-vinda, se você estiver <disposto/disposta>.\n\nVocê viu o acampamento lá embaixo, mas tem mais ao leste. Use suas próprias táticas para causar o máximo de dano aos taurens. Podemos nos encontrar aqui depois que eles tiverem sentido a ferroada das flechas da Kindal e as suas armas.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2972] = {title = "Distribuindo a justiça", objective = "Leve o Anel-sinete de Jer'kai para a Alta-sacerdotisa Tyrande Murmuréolo em Darnassus.", description = "Aqui, na região selvagem, Kindal e eu temos poucos meios de recompensá-<lo/la> por sua ajuda, mas não queremos parecer mal-agradecidas. Fique com o meu anel-sinete. Se um dia você se encontrar nos galhos de Teldrassil, vá até o Templo da Lua, que fica no coração de Darnassus. Lá, você encontrará minha Alta-sacerdotisa Tyrande Murmuréolo. Ela saberá pelo anel que estamos bem e acreditará na sua história.\n\nVocê tem a nossa gratidão, <name>.", progress = "Olá, criança. É uma satisfação para nós ter aventureiros mais poderosos neste templo. Se não estiver com pressa, você poderia contar uma ou outra história para os aventureiros mais jovens? Eu sei que muitos deles se beneficiariam com a sua sabedoria.\n\nPerdoe-me, que grosseria a minha. Você veio ao Templo em busca da nossa ajuda, e eu aqui lhe pedindo favores. Por favor, perdoe-me. Diga, o que os servos da Luz podem fazer por você?", completion = "Que história incrível, &lt;name&gt;. É uma satisfação para nós saber que você não só ajudou os dragões, como também uma de nossas sacerdotisas. Jer'kai acertou em mandar você falar comigo. Vou garantir que ela receba o anel de volta — tenho batedores saindo para Feralas em breve. Será mais uma coisa para eles investigarem durante a missão.\n\nMais uma vez, obrigada. Sinta-se em casa enquanto estiver aqui.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nMunhequeiras do SalgueiroCinturão Escama Noturna\n\nVocê também receberá:\n\n"},
[2973] = {title = "Dando um trato na capa", objective = "Traga 10 Asas Iridescentes de Dardejantes para Krueg Rachacrânio na Aldeia Mojache.", description = "Muito bem, <class>. Viajei com meus companheiros para Feralas numa missão para nossos mestres de Orgrimmar.\n\nMinha cara-metade Korrin gosta muito de renovar o guarda-roupa, então pensei em fazer para ela uma nova capa enfeitada com asas iridescentes de dragoletas. Você pode me ajudar? Os dardejantes de lindas asas que tanto quero estão a oeste da Aldeia Mojache.", progress = "Os dardejantes podem ser perigosos quando são provocados. Tome cuidado quando mexer com eles, <class>. E tome mais cuidado ainda com os elfos noturnos da área. Os malditos cismam em ficar na floresta também.\n\nMe avise quando tiver asas suficientes. Vou te pagar muito bem!", completion = "Maravilhoso, &lt;class&gt;! Terão boa serventia. Tem o bastante aqui para fazer uma capa para Korrin, até demais! Obrigado!\n\nEspero que a viagem não tenha sido muito perigosa. Os arredores de Feralas não são conhecidos por serem fáceis, mas você, obviamente, fez um bom trabalho.\n\nObrigado novamente, &lt;class&gt;.", rewards = "Você receberá:\n\n"},
[2974] = {title = "Uma temível descoberta", objective = "Traga 20 Chifres de Temível Totem para Krueg Rachacrânio na Aldeia Mojache.", description = "O quê?! Você encontrou taurens na área? É sério? Como eles se pareciam?!\n\nO clã Temível Totem! Você encontrou alguns deles? Belgrom, um de nossos mestres, tem interesse naquela raça — está até oferecendo uma recompensa por eles.\n\nVocê tem que voltar, <class>. Volte e traga os chifres deles... pilhas de chifres! Os Temível Totem não são de brincadeira e nem devem ser subestimados. Volte e mate-os. Que os rios de Feralas fiquem rubros com o sangue deles!", progress = "Leve minhas palavras a sério, <class>, o clã Temível Totem é maligno. Se você descobriu o paradeiro deles, então você terá que dar o primeiro golpe... antes que eles venham para nossa área, arrasem nossas vilas e surrupiem nossas terras.", completion = "Esses chifres são bons, &lt;class&gt;. Muito bem. Eu vou ficar com eles e garantir que Belgrom seja informado assim que voltarmos a Orgrimmar. Você me ajudou duas vezes e, por isso, tem a minha marca. Talvez ela possa lhe ser útil no futuro! Boa sorte.", rewards = "Você receberá:\n\n"},
[2975] = {title = "Os ogros de Feralas", objective = "Rok Orhan, da Aldeia Mojache, quer que você mate 10 Ogros Gordunni, 10 Ogros-magos e 5 Brutamontes Gordunni.", description = "Os efeitos da tribo dos Ogros Gordunni não passam despercebidos nesta área. Olhe ao seu redor, <name>; a presença deles deixa marcas nesta terra. Temos que rechaçá-los, antes que avancem ainda mais rumo a Feralas.\n\nSiga para o norte beirando o rio, e você encontrará o posto avançado deles. Mostre que eles não são bem-vindos aqui reduzindo-lhes os números, <name>.", progress = nil, completion = "Excelente, &lt;name&gt;. Com o tempo, a terra há de se curar. Não podemos desistir.", rewards = "Você receberá:\n\n"},
[2976] = {title = "Uma temível descoberta", objective = "Leve a Caixa de Chifres Temível Totem para Belgrom Pedramalho em Orgrimmar.", description = "O quê?!\n\nAh, então eu mencionei uma recompensa, não é mesmo? Bem, isso vai me ensinar a ficar de boca fechada.\n\nVocê fez o seu trabalho, por isso é direito seu receber uma boa recompensa. Fique com os chifres, eu vou pedir que os coloquem em uma caixa para você. Procure Belgrom Pedramalho no Vale da Honra. Ele vai querer saber do que você viu e vai lhe dar uma recompensa pelo seu trabalho.\n\nCuide-se, <class>, e obrigado de novo por tudo.", progress = "Não demore, <class>, eu tenho muito a fazer hoje.", completion = "Os Temível Totem?! Que interessante. Quer dizer que você se deparou com eles enquanto caçava em Feralas? Sério mesmo?\n\nMeus pares vão querer saber dessa notícia, &lt;class&gt;. Continue falando, enquanto eu conto o seu dinheiro.\n\nE, antes de ir, saiba que eu fico agradecido pela informação que você me trouxe hoje.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCapa da Batalha ÁrduaOrbe Lunajade\n\nVocê também receberá:\n\n"},
[2977] = {title = "Retorno a Altaforja", objective = "Fale com o Historiador Karnik em Altaforja.", description = "Você acessa o pedestal novamente. Os discos de platina aparecem mais uma vez, de repente, na sua mochila.\n\nCom o conhecimento do observador de pedra adquirido e os discos em sua posse outra vez, chegou a hora de voltar para Altaforja e informar à Liga dos Exploradores sobre a sua descoberta.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2978] = {title = "O Pergaminho Gordunni", objective = "Leve o Pergaminho Gordunni para Rok Orhan na Aldeia Mojache.", description = "Ao pegar o pergaminho seboso, você percebe que ele retrata algum tipo de encontro ou cerimônia. Rok Orhan ficaria interessada neste pergaminho e no que ele poderia significar para os Gordunni.", progress = "O que é isso?", completion = "Esse pergaminho, você o encontrou no Assentamento Gordunni?", rewards = "Você receberá:\n\n"},
[2979] = {title = "Cerimônia sombria", objective = "Rok Orhan, da Aldeia Mojache, quer que você encontre um Orbe Gordunni.", description = "Parecem ser planos para algum tipo de cerimônia sombria, <name>. Pelo que pude entender, esse ritual é conduzido pelos lordes-magos Gordunni.\n\nEncontre um desses lordes-magos e tome um orbe dele.\n\nO orbe de um mago contém grande poder, e vai nos ajudar a descobrir mais sobre essa cerimônia e a magia que os Gordunni usam.", progress = "Temos que descobrir mais sobre o porquê de os Gordunni estarem aqui, <name>.", completion = "Este orbe nos dará mais informações sobre os Gordunni... que usaremos para planejar a derrocada deles, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[2980] = {title = "Os ogros de Feralas", objective = "Rok Orhan, da Aldeia Mojache, quer que você mate 10 Xamãs Gordunni, 10 Bruxos Gordunni e 5 Espancadores Gordunni.", description = "Agora que você provou ser <um verdadeiro guerreiro/uma verdadeira guerreira>, eu acredito que você tem a coragem necessária para levar esta tarefa a cabo.\n\nVá para Selva Alta, que fica a sudoeste daqui. Lá, você vai encontrar estruturas abandonadas e destruídas, em uma terra que outrora pertenceu aos elfos noturnos. Procure as ruínas e, quando vir as árvores murchas e o terreno profanado, você encontrará os Gordunni. Faça que eles recuem.\n\nEsses ogros são muito mais fortes do que os de antes, <name>. Tome cuidado.", progress = nil, completion = "Você provou ser um aliado de confiança, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[2981] = {title = "Uma ameaça em Feralas", objective = "Belgrom Pedramalho quer que você fale com Rok Orhan, da Aldeia Mojache.", description = "Feralas é um local ideal para <um/uma> <class> de talento provar seu valor. Uma tribo de ogros chamada Gordunni é a força militar dominante por lá, e o número deles só aumenta. Eles precisam ser detidos antes que ameacem a nossa presença em Feralas.\n\nEu conheço uma guerreira da Aldeia Mojache, em Feralas; o nome dela é Rok Orhan. Ela vai lhe explicar melhor a situação quando você chegar lá.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2982] = {title = "A Selva Alta", objective = "Ângela Brisaluna quer que você mate 8 Bruxos Gordunni, 8 Xamãs Gordunni e 8 Brutamontes Gordunni.", description = "Embora seja perigoso, Troyas e eu andamos investigando as ruínas que cobrem as florestas de Feralas.\n\nMas, recentemente, Troyas tem ido sozinho à Selva Alta, o que me preocupa muito. Os ogros Gordunni dominaram a área e eles não são nada compreensivos com quem invade o território deles.\n\nEu queria saber se você estaria <disposto/disposta> a ir até as ruínas a sudeste daqui e eliminar alguns ogros e assim deixar o caminho mais seguro para o meu Troyas. Ficarei extremamente grata.", progress = nil, completion = "Muitíssimo obrigada por fazer isso por mim.\n\nMas, por favor, não conte nada ao Troyas, está bem?", rewards = "Você receberá:\n\n"},
[2983] = {title = "Clamor do Fogo", objective = "Encontre Kranal Fiss nos Sertões.", description = "Já faz algum tempo desde que você foi testad<o/a>, <name>. Você demonstrou muita paciência em seus estudos até agora, mas chegou a hora de aprender sobre poderes maiores — o verdadeiro poder. O elemento do fogo será seu, desde que você esteja dispost<o/a> a pagar o preço.\n\nProcure por Kranal Fiss nos Sertões. Ele vive em uma casinha ao norte da Encruzilhada, mas preste atenção para não passar direto e chegar até a terra dos elfos noturnos, o que significa que você terá ido longe demais.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2984] = {title = "Clamor do Fogo", objective = "Encontre Kranal Fiss nos Sertões.", description = "Já faz algum tempo desde que você foi testad<o/a>, <name>. Você demonstrou muita paciência em seus estudos até agora, mas chegou a hora de aprender ainda mais. Chegou a hora de aprender sobre destruição... e caos. O elemento do fogo está ao alcance da sua compreensão, desde que você esteja dispost<o/a> a pagar.\n\nProcure por Kranal Fiss nos Sertões. Ele vive em uma casinha ao norte da Encruzilhada, mas, atenção, se você passar direto e chegar à terra dos elfos noturnos, terá ido longe demais.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2985] = {title = "Clamor da Água", objective = "Encontre Islen Vidente da Água nos Sertões.", description = "Você me parece <pronto/pronta> para o seu próximo teste, <name>. Você demonstrou muita paciência em seus estudos até agora, mas chegou a hora de aprender ainda mais. Chegou a hora de aprender sobre a pureza da água.\n\nProcure por Islen Vidente da Água nos Sertões. Ela vive no litoral leste, ao sul do Rio Furiaustral e da Vila Catraca. Ela vai estar em uma cabana de pesca. Boa sorte, <name>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2986] = {title = "Clamor da Água", objective = "Encontre Islen Vidente da Água nos Sertões.", description = "Já faz muito tempo desde que você foi testad<o/a>, <name>. Você demonstrou muita paciência em seus estudos até agora, mas chegou a hora de aprender ainda mais. Chegou a hora de aprender sobre a pureza da água.\n\nProcure por Islen Vidente da Água nos Sertões. Ela vive no litoral leste, ao sul do Rio Furiaustral e da Vila Catraca. Ela vai estar em uma cabana de pesca. Boa sorte, <name>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2987] = {title = "Cobalto Gordunni", objective = "Orwin Sheiduztruc, na Aldeia Mojache, quer 12 amostras de Cobalto Gordunni.", description = "Eu acabei de chegar aqui em Feralas e fiquei sabendo que os ogros Gordunni que habitam os campos têm uns minerais interessantes. Ninguém sabe o que eles estão aprontando, só sabem que eles protegem bem aquela parada! Eles não deixam tudo jogado pelo chão, não, eles enterram direitinho pra impedir gente como eu de botar as mãos naquelas pedrinhas.\n\nSe você quiser, eu te dou uma pá. Vá até o Assentamento Gordunni, que fica ao norte daqui, e cave um pouco e cobalto Gordunni. Procure um brilho azul e comece a cavar!", progress = "Levante-se e comece a cavar!\n\nEu tenho mais o que fazer da minha vida! Tempo é dinheiro!", completion = "Perfeito! Eu vou carregar isso aqui nas minhas viagens, &lt;name&gt;. Tenho certeza de que alguém vai me oferecer uma grana gorda por esse cobalto.\n\nFique com isto pelo seu esforço.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas do MaharishiManto do Contemplador das Estrelas\n\nVocê também receberá:\n\n"},
[2988] = {title = "Jaulas Cascasseca", objective = "Verifique as jaulas nas duas aldeias Cascasseca, depois fale novamente com o Mestre de Grifos Garra-de-acha.", description = "O filho do nosso grifo mais poderoso sumiu! Bicoafiado, o herdeiro do Asinha, se perdeu enquanto caçava com os pais esses dias, e a gente acha que foram os trolls que levaram ele.\n\nTu precisa ajudar a gente! Vasculha as jaulas nas duas aldeias dos trolls Cascasseca pra ver se o Bicoafiado tá lá.", progress = nil, completion = "O Bicoafiado não tava nas aldeias? Hum... isso é uma notícia terrível. Aquele grifo está destinado a liderar a próxima geração de grifos. Se perdermos ele, a força e o espírito da nossa esquadra vai sofrer um baque pesado.", rewards = "Você receberá:\n\n"},
[2989] = {title = "Altar de Zul", objective = "Verifique o topo do Altar de Zul, depois fale novamente com o Mestre de Grifos Garra-de-acha.", description = "Bicoafiado não estava com os trolls Cascasseca. Talvez tenha sido levado pelos trolls Ramatorpe. Vá até o Altar de Zul no extremo oeste do território Ramatorpe e procure pelo filhote de grifo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2990] = {title = "Tádius Sombraustera", objective = "Leve a Pena de Bicoafiado para Tádius Sombraustera na Barreira do Inferno.", description = "Leve esta pena para Tádius Sombraustera, o divinador. Ele foi embora do Ninho da Águia anos atrás para buscar conhecimentos sombrios, mas, no coração, ele ainda é um Martelo Feroz!\n\nE talvez seus talentos possam nos ajudar a encontrar o Bicoafiado.\n\nEsta pena foi colhida no ninho onde o bebê-grifo dorme. Se tu entregar ela pro Tádius, talvez ele possa usar a magia de divinação dele pra encontrar o Bicoafiado.\n\nBoa sorte, <name>. Tu encontra o Tádius na Barreira do Inferno, dentro da torre dos magos da Bastilha de Etergarde.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2991] = {title = "Medalhão de Nekrum", objective = "Leve o Medalhão de Nekrum para Tádius Sombraustera na Barreira do Inferno.", description = "Eu vou descobrir onde o tá o seu grifo, mas, primeiro, tu precisa fazer uma coisinha pra mim. Tem um troll em Zul'Farrak, o Nekrum Cometripas, que possui um medalhão que eu quero muito estudar. Me traga aquele medalhão que eu vou usar os meus poderes pra achar o Bicoafiado.\n\nZul'Farrak fica do outro lado do oceano a oeste, no deserto de Tanaris.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2992] = {title = "A divinação", objective = "Espere até que Sombraustera termine o ritual de divinação.", description = "Me dê espaço enquanto eu realizo um ritual de divinação. Eu vou encontrar a conexão entre a pena que tu me trouxe e seu dono; daí, eu vou seguir essa conexão até chegar ao Bicoafiado.\n\nQuer dizer... se tudo sair de acordo com o planejado. Se não der certo... é melhor fechar os olhos.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2993] = {title = "Retorno às Terras Agrestes", objective = "Fale com o Mestre de Grifos Garra-de-acha.", description = "Bicoafiado está numa jaula, no alto de um penhasco, no topo de Jintha'Alor, que fica nas Terras Agrestes. A chave da jaula está nas mãos da alta-sacerdotisa dos trolls Ramatorpe, a Sacerdotisa Torpe Bagata.\n\nPronto, eu fiz a minha parte. Desejo-te sorte em sua empreitada, <name>.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[2994] = {title = "Salvando Bicoafiado", objective = "Lute até o topo de Jintha'Alor, mate a Sacerdotisa Torpe Bagata para pegar a chave da jaula de Bicoafiado e salvá-lo.", description = "Temos que salvar o Bicoafiado!\n\nLute para chegar ao topo de Jintha'Alor, no coração do território dos Ramatorpe, tome a chave da jaula do Bicoafiado das mãos da Sacerdotisa Torpe Bagata, e depois vá procurar o Bicoafiado para salvá-lo!Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nMartelo da Tempestade do Cavalga-grifoPerneiras do Cavalga-grifo\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\nChave de Bagata"},
[2995] = {title = "Linhas de comunicação", objective = "Urraca Convulserpe, da Cidade Baixa, quer que você viaje até as Terras Agrestes e queime os Arquivos do Altovale, os Relatórios do Altovale e as Cartas do Altovale.", description = "O Pavilhão Quel'Danil é um centro de comunicação para os elfos superiores das Terras Agrestes. Seus embaixadores estão trabalhando para estabelecer relações com outras raças, com quem eles compartilham seus conhecimentos sobre os trolls Cascasseca que habitam a área ao sul das Terras Agrestes. Todos os documentos relativos às comunicações são mantidos no pavilhão.\n\nNão precisamos de Altovale forjando alianças com outras pessoas, <name>.\n\nDestrua os documentos de Altovale. Precisamos queimar suas linhas de comunicação... literalmente.", progress = nil, completion = "Perfeito, &lt;name&gt;. Enquanto o Altovale trabalha para recuperar as informações perdidas, nós podemos selar de vez as linhas...", rewards = "Você receberá:\n\n"},
[2996] = {title = "Procurando Strahad", objective = "Fale com Strahad Farsan na Vila Catraca.", description = "Eu lhe ensinei as suas habilidades no passado, mas chegou a hora de outro fazer assumir essa tarefa. Strahad é esse homem, e você o encontra na Vila Catraca. Ele é humano, mas não se deixe enganar pelas aparências, pois ele não é nada fraco. Ele é poderoso, embora seja pequeno. Ele pode lhe ensinar evocações mais poderosas, e eu sinto que você está <pronto/pronta>. Procure-o agora. Ele está na Vila Catraca... acima da cidade portuária ao lado de sua torre.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2997] = {title = "Tomo de Divindade", objective = "Fale com Tiza Beloforja em Altaforja.", description = "Olá, <name>. Eu sei que tu tá <ocupado/ocupada> ajudando o povo de Dun Morogh, mas, quando tiver um tempinho, tu precisa ir até Altaforja pra falar com um de nossos mais respeitados membros: Tiza Beloforja. É ela que os paladinos mais jovens procuram quando chega a hora de aprender mais sobre a Luz e o que se espera de ti.\n\nVocê pode encontrá-la no andar de cima, no Salão dos Mistérios. Se tu nunca esteve lá, ele fica na Ala Mística. Boa sorte pra ti, <name>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2998] = {title = "Tomo de Divindade", objective = "Fale com Benedito Brião em Ventobravo.", description = "Saudações, <irmão/irmã>. Benedito Brião, um de nossos mais influentes paladinos, estava falando sobre você recentemente. Escutei ele dizendo que já estava na hora de testar você... além de um rol de elogios sobre o seu desempenho até o momento.\n\nSe eu fosse você, iria para Ventobravo e falaria com ele assim que tivesse tempo. Com certeza, ele está na Catedral da Luz — é lá que ele fica a maior parte do tempo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[2999] = {title = "Tomo de Divindade", objective = "Fale com Tiza Beloforja em Altaforja.", description = "Olá, <name>. Eu sei que tu tá <ocupado/ocupada> ajudando o povo de Dun Morogh, mas, quando tiver um tempinho, tu precisa dar um pulo no andar de cima pra falar com um de nossos mais respeitados membros: Tiza Beloforja. É ela que os paladinos mais jovens procuram quando chega a hora de aprender mais sobre a Luz e o que se espera de ti.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3000] = {title = "Tomo de Divindade", objective = "Fale com Tiza Beloforja em Altaforja.", description = "Olá, <name>. Eu sei que tu tá <ocupado/ocupada> ajudando o povo de Azeroth, mas, quando tiver um tempinho, tu precisa voltar pra Altaforja pra falar com uma respeitadíssima paladina do seu lar: Tiza Beloforja. É ela que os anões paladinos mais jovens procuram quando chega a hora de aprender mais sobre a Luz e o que se espera de todos nós.\n\nVocê pode encontrá-la no andar de cima, no Salão dos Mistérios, que fica na Ala Mística.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3001] = {title = "Procurando Strahad", objective = "Procure Strahad Farsan na Vila Catraca.", description = "Eu lhe ensinei as suas habilidades no passado, mas chegou a hora de outro fazer assumir essa tarefa. Strahad é esse homem, e você o encontra na Vila Catraca. Ele é humano, mas não se deixe enganar pelas aparências, pois ele não é nada fraco, tampouco é seu inimigo — por ora, pelo menos. Ele é poderoso e deve ser respeitado. Ele pode lhe ensinar evocações mais poderosas, e eu sinto que você está <pronto/pronta> para isso. Procure-o agora.\n\nEle está na Vila Catraca... acima da cidade portuária ao lado da torre.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3002] = {title = "O orbe Gordunni", objective = "Leve o Orbe Gordunni para Uthel'nay no Salão Agulhas Negras, em Orgrimmar.", description = "Ainda que eu conheça bem meu inimigo, sou uma guerreira, <name>. Não faço a menor ideia do que o orbe é capaz.\n\nConheço um mago em Orgrimmar que poderá nos contar mais sobre o orbe. Leve-o até ele.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3022] = {title = "Manuseie com cuidado", objective = "Leve o Caixote Cuidadosamente Embalado para Erelas Ambarcéu, na Vila de Rut'theran.", description = "<name>, vejo que você quer muito se dedicar à minha causa digna. Sim, é claro, tem uma coisa que você pode fazer por mim!\n\nLeve este caixote de ovos para Erelas Ambarcéu. Ele é um druida que cuida desses ovos, ajudando-os a chocar e treinando os hipogrifos. Você vai encontrá-lo em Teldrassil, em uma pequena vila ao sul chamada Rut'theran.", progress = nil, completion = "Obrigado pela ajuda, &lt;name&gt;. A população de hipogrifos não perseveraria se não fosse por nós. Ah, mas tenho certeza de que você entende o que estou falando, já que viajou até Feralas e testemunhou a devastação dos Gordunni por lá.\n\nBem, como você pode ver, estou bastante atarefado aqui, por isso, acho melhor você ir andando.\n\nObrigado novamente, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[3042] = {title = "Têmpera de troll", objective = "Leve 20 Frascos de Têmpera de Troll para Trenton Lumartelo em Geringontzan.", description = "Os trolls Zangareia de Zul'Farrak fazem um agente de têmpera com arenito, e eu posso usar para temperar os produtos que eu fabrico — posso, sim! Ele é muito estimado pelos especialistas, por isso, se você me trouxer um bom suprimento desse temperante, eu lhe darei uma boa recompensa.\n\nZul'Farrak fica a noroeste de Geringontzan. Boa sorte.", progress = "Trouxe o temperante, <name>?", completion = "Ah, é uma ótima têmpera, de fato! E tem bastante! Isso vai dar para muitos dias de trabalho!\n\nObrigado, &lt;name&gt;. Aceite isto como pagamento.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[3062] = {title = "Coração sombrio", objective = "Talo Casco de Espinhos, da Aldeia Mojache, quer o Coração Sombrio de Edana Iragarra.", description = "Edana Iragarra é a rainha das harpias da Nascente Norte, e reside nas ruínas a oeste daqui. Ela é pura maldade. Ouvi dizer que o coração dela é feito de cristal. É incapaz de qualquer emoção a não ser o ódio que sente pelos outros. Você deve enfrentar Edana e me trazer seu coração de trevas.\n\nOuvi falar de uma corneta que as harpias carregam. Ao soprá-la nas pedras de Iragarra, na parte setentrional das ruínas, elas convocam sua rainha.\n\nImagine a surpresa de Edana ao ver que não o chamado não foi de seu povo... mas da morte.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nLuvas Brasão AsaCinturão Chifreforte\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\nChifre de Iragarra"},
[3063] = {title = "Vingança em Nascente Norte", objective = "Talo Casco de Espinhos quer que você mate 4 Harpias da Nascente Norte, 4 Plumerrantes da Nascente Norte, 4 Arautos do Vento da Nascente Norte e 4 Matadoras da Nascente Norte.", description = "Na minha época, eu era um caçador experiente. Não havia criatura alguma que eu temesse. Até que um dia...\n\nEu estava na floresta, afiando minha lâmina depois de uma batalha, quando fui emboscado. Harpias me cercaram, e mal consegui fugir.\n\nAté hoje, ainda posso escutar seus gritos ecoando...\n\nNunca mais fui o mesmo depois daquilo, mas o ódio ferve dentro de mim. Preciso acertar as contas, mesmo depois de todos esses anos...\n\nAs harpias da Nascente Norte se encontram a oeste daqui. <name>, vingue-se por mim!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3064] = {title = "Chapéus de pirata", objective = "Leve 20 Chapéus de Pirata Intactos para Yorba Parafuseta.Preciso de chapéus de pirata para a minha loja. Traga-me um monte!", description = "Preciso de chapéus de pirata para a minha loja. Traga-me um monte!", progress = nil, completion = nil, rewards = nil},
[3065] = {title = "Tabuleta Simples", objective = "Leia a Tabuleta Simples e fale com Frang no Vale das Provações.", description = "Ah, enquanto você estava fora, chegou uma tabuleta para você, <name>.\n\nLeia quando tiver tempo. Se não me engano, é do instrutor de guerreiros Frang. Ele quer falar com você assim que você estiver <pronto/pronta>. Ele fica na entrada do Covil, protegido na sombra.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3082] = {title = "Tabuleta cinzelada", objective = "Encontre Jen'shan no Vale das Provações.", description = "Ah, enquanto você estava fora, chegou uma tabuleta para você, <name>.\n\nLeia quando tiver tempo. Se não me engano, é da instrutora de caçadores Jen'shan. Ela quer falar com você assim que você estiver <pronto/pronta>.", progress = "Jen'shan vê diante dela um poderoso <class>, e os espíritos aprovam.\n\nO caminho do <class> é um dos mais antigos que existem. A Horda recorreu a nós quando estavam lutando para sobreviver em Azeroth e nos pediu que os ensinássemos, que mostrássemos os segredos de muitas coisas. Eles já eram fortes antes — fortes como xamãs, fortes como guerreiros. Mas o caminho do <class> não era o caminho deles... até então.", completion = "Agora a gente te ensina essas coisas, e elas ensinam outras pra gente. Nós nos tornamos uma única raça... quase. Somos aliados há muito tempo agora, então lembre-se de ajudá-los, e Jen'shan vai se lembrar de te ajudar.\n\nJen'shan vai te ensinar o caminho do &lt;class&gt; — e vai ensinar bem. Não se esqueça nunca: o &lt;class&gt; é muito respeitado pela tribo. Tu vai desempenhar um papel muito importante no futuro... nunca se esqueça disso.\n\nQuando sentir necessidade, venha falar com Jen'shan. Ela te ensina o quanto pode quando ela sente que tu tá pronto.", rewards = "Você receberá:\n\n"},
[3083] = {title = "Tabuleta cifrada", objective = "Leia a Tabuleta Cifrada e fale com Rwag no Vale das Provações.", description = "Ah, enquanto você estava fora, chegou uma tabuleta para você, <name>.\n\nLeia quando tiver tempo. Se não me engano, é do instrutor de ladinos Rwag. Ele quer falar com você assim que você estiver <pronto/pronta>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3084] = {title = "Tabuleta inscrita em runas", objective = "Fale com Shikrik no Vale das Provações.", description = "Ah, enquanto você estava fora, chegou uma tabuleta para você, <name>.\n\nLeia quando tiver tempo. Se não me engano, é da instrutora de xamãs Shikrik. Ela quer falar com você assim que você estiver <pronto/pronta>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3085] = {title = "Tabuleta consagrada", objective = "Leia a Tabuleta Consagrada e fale com Ken'jai, do lado de fora do Covil, no Vale das Provações.", description = "Ah, enquanto você estava fora, chegou uma tabuleta para você, <name>.\n\nLeia quando tiver tempo. Se não me engano, é do instrutor de sacerdotes Ken'jai. Ele quer falar com você assim que você estiver <pronto/pronta>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3086] = {title = "Tabuleta glífica", objective = "Leia a Tabuleta Glífica e fale com Mai'ah perto do Covil, no Vale das Provações.", description = "Ah, enquanto você estava fora, chegou uma tabuleta para você, <name>.\n\nLeia quando tiver tempo. Se não me engano, é da instrutora de magos Mai'ah. Ela quer falar com você assim que você estiver <pronto/pronta>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3087] = {title = "Pergaminho cinzelado", objective = "Leia o Pergaminho Cinzelado e fale com Jen'shan no Vale das Provações.", description = "Ah, enquanto você estava fora, chegou um pergaminho para você, <name>.\n\nLeia quando tiver tempo. Se não me engano, é da instrutora de caçadores Jen'shan. Ela quer falar com você assim que você estiver <pronto/pronta>.", progress = "Ah, finalmente, tu chegou. Bom. Jen'shan tava te esperando.\n\nMuitos acham que esse é um caminho fácil, mas Jen'shan te garante que não é verdade. Dominar as feras de Durotar e além é tão difícil quanto dominar as artes arcanas ou falar com os nossos ancestrais.\n\nDisparar um arco ou um rifle pode não ser tão perigoso pra ti quanto lançar um feitiço, mas pode ter certeza que é perigoso pro teu inimigo.", completion = "Quando terminarmos a nossa conversa, fale com o resto dos cidadãos do Vale. Eles vão te pedir alguns favores... Coisas que testarão sua habilidade e te ensinarão os perigos desta terra.\n\nConforme sua experiência e sabedoria aumentam, tu vai precisar que Jen'shan tem mostre as melhores técnicas e habilidades. Venha visitar Jen'shan frequentemente. Eu vou te dizer o que posso te ensinar.", rewards = "Você receberá:\n\n"},
[3088] = {title = "Pergaminho cifrado", objective = "Leia o Pergaminho Cifrado e fale com Rwag no Vale das Provações.", description = "Ah, enquanto você estava fora, chegou um pergaminho para você, <name>.\n\nLeia quando tiver tempo. Se não me engano, é do instrutor de ladinos Rwag. Ele quer falar com você assim que você estiver <pronto/pronta>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3089] = {title = "Pergaminho inscrito em runas", objective = "Leia o Pergaminho Inscrito em Runas e fale com Shikrik no Vale das Provações.", description = "Ah, enquanto você estava fora, chegou um pergaminho para você, <name>.\n\nLeia quando tiver tempo. Se não me engano, é da instrutora de xamãs Shikrik. Ela quer falar com você assim que você estiver <pronto/pronta>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3090] = {title = "Pergaminho maculado", objective = "Leia o Pergaminho Conspurcado e fale com Nartok dentro do Covil, no Vale das Provações.", description = "Ah, chegou um pergaminho para você enquanto você estava fora, <name>.\n\nGeralmente eu evito lidar com bruxos, mas melhor você ler o pergaminho assim que tiver tempo. Se não me engano, é do instrutor recém-designado, Nartok. Ele quer falar com você assim que você estiver <pronto/pronta>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3091] = {title = "Bilhete", objective = "Leia o Bilhete e fale com Harutt Chifre Troante, na Aldeia Narache.", description = "Não faz muito tempo, passou uma mensageira à sua procura, <name>. Creio que foi enviada pelo instrutor de guerreiros Harutt. Se o bilhete foi mesmo enviado por Harutt, eu não demoraria a ler, se fosse você.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3092] = {title = "Bilhete cinzelado", objective = "Leia o Bilhete Cinzelado e fale com Lanka Tiro Distante na Chapada Nuvem Vermelha.", description = "Não faz muito tempo, passou uma mensageira à sua procura, <name>. Creio que foi enviada pelo instrutor de caçadores Lanka. Se o bilhete foi mesmo enviado por Lanka, eu não demoraria a ler, se fosse você.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3093] = {title = "Bilhete inscrito em runas", objective = "Leia o Bilhete Inscrito em Runas e fale com Meela Anda com a Aurora na Aldeia Narache.", description = "Não faz muito tempo, passou uma mensageira à sua procura, <name>. Creio que foi enviada pela instrutora de xamãs Meela. Se o bilhete foi mesmo enviado por Meela, eu não demoraria a ler, se fosse você.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3094] = {title = "Bilhete verdejante", objective = "Leia o Bilhete Verdejante e fale com Gart Corre com a Névoa, na Aldeia Narache.", description = "Não faz muito tempo, passou uma mensageira à sua procura, <name>. Creio que foi enviada pelo instrutor de druidas Gart. Se o bilhete foi mesmo enviado por Gart, eu o leria o mais rápido possível, se fosse você.", progress = "Enfim, você veio. Fico contente. Sinto em Mulgore e além de nossos horizontes a agitação dos espíritos ancestrais, mas não sei dizer o que os perturba. Até o fim dos seus dias, o seu papel será aprender a ouvir os espíritos da natureza e agir como avatar deles, <name>. Mantenha isso sempre em mente.", completion = "Embora compartilhemos este laço com os elfos noturnos, não espere ser bem recebido entre eles. O orgulho lhes turva a visão, mas não devemos nos importar com isso. Só devemos nos importar com os ensinamentos da Mãe Terra e eu lhe ensinarei tudo o que sei à medida que sua sabedoria se expandir.\n\nVenha ter comigo de tempos em tempos, &lt;name&gt;. Verá que a Mãe Terra dá habilidades incríveis àqueles que se mostram dedicados.", rewards = "Você receberá:\n\n"},
[3095] = {title = "Pergaminho simples", objective = "Leia o Pergaminho Simples e fale com Dannal Stern em Plangemortis.", description = "Ah, enquanto esteve fora lidando com os Flagelos desmiolados, este pergaminho chegou para você. Deve ser algo de certa importância, já que parece ter o selo do instrutor de guerreiros Danna. Melhor ler isso antes de sair novamente.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3096] = {title = "Pergaminho cifrado", objective = "Leia o Pergaminho Cifrado e fale com o Davi Trias em Plangemortis.", description = "Ah, enquanto esteve fora lidando com os Flagelos desmiolados, este pergaminho chegou para você. Deve ser algo de certa importância, já que parece ter o selo do instrutor de ladinos Davi Trias. Melhor ler isso antes de sair novamente.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3097] = {title = "Pergaminho consagrado", objective = "Leia o Pergaminho Consagrado e fale com o Clérigo das Trevas Duesten, na igreja em Plangemortis.", description = "Ah, enquanto esteve fora lidando com os Flagelos desmiolados, este pergaminho chegou para você. Deve ser algo de certa importância, já que parece ter o selo do instrutor de sacerdotes Duesten. Melhor ler isso antes de sair novamente.", progress = "Ah, quer dizer que entre fogo, peste, revoltas e coisas bem piores, mais um \"fiel\" bate à minha porta. Bom, seja bem-vindo, <class>. Permita-me explicar por um momento uma coisa ou outra antes de você decidir se é hora de seguir em frente: a Luz Sagrada não mais está ao seu lado, os espíritos dos seus antepassados são contos antigos e as criaturas do Éter já não te querem mais.\n\nConseguiu me entender até agora?", completion = "Há apenas uma coisa que você precisa saber: nós sobrevivemos a tudo isso sozinhos. É a nossa fé que nos faz diferentes dos outros, e com o poder que temos, vamos mudar Azeroth. Os fracos virão até você. Os leprosos o chamarão de Meu Senhor. E os ignorantes irão até você buscar orientação. É meu dever me certificar de que você tem o que é necessário para lidar com o que está por vir. Assim que adquirir experiência, volte a mim. Ensinarei novas habilidades assim que você estiver preparado.", rewards = "Você receberá:\n\n"},
[3098] = {title = "Pergaminho glífico", objective = "Leia o Pergaminho Glífico e fale com Isabella na igreja em Plangemortis.", description = "Ah, enquanto esteve fora lidando com os Flagelos desmiolados, este pergaminho chegou para você. Deve ser algo de certa importância, já que parece ter o selo da instrutora de magos Isabella. Melhor ler isso antes de sair novamente.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3099] = {title = "Pergaminho conspurcado", objective = "Leia o Pergaminho Conspurcado e fale com Maximillion em Plangemortis.", description = "Ah, enquanto esteve fora lidando com os Flagelos desmiolados, este pergaminho chegou para você. Acredito que se trate de algo importante, já que traz o selo do instrutor de bruxos Maximillion. Sugiro que você leia isso antes de sair novamente.", progress = "Eu lhe dou as boas-vindas, irmão. Eu sabia que você viria. Era só uma questão de tempo.\n\nO que eu disse lhe interessou, não foi? Mexi com você.. Alguma coisa aí dentro sabia que minhas palavras eram verdadeiras. Ótimo.\n\nMas saiba de uma coisa: eu nunca trairia Sylvana. Muito pelo contrário: ela apreciaria minhas afirmações, considerando que foram suas próprias crenças que colocaram os Renegados na posição atual.", completion = "Eu mencionei um escravo que continuava sendo um escravo, não importava o mestre. Você se lembra? Nós dois sabemos que é esse tipo de controle — esse tipo de poder — que agora nos impulsiona. Nós fazemos criaturas nos servir. Sabemos que somos mais poderosos e que merecemos mais respeito do que os outros nos dão... e agora queremos tê-lo.\n\nSerei seu aliado nessa luta — a luta por nossa própria liberdade. Liberdade para buscar nossas próprias alianças. Quando você se sentir pronto, volte aqui, e eu vou ensinar tudo o que sei.", rewards = "Você receberá:\n\n"},
[3100] = {title = "Carta simples", objective = "Leia a Carta Simples e fale com Hipólito Valentim na Abadia de Vila Norte.", description = "Pediram-me para lhe entregar isto assim que você voltasse dos assentamentos de kobolds, <name>. Parece ser uma carta selada com a insígnia de Hipólito, nosso instrutor de guerreiros local. Eu não hesitaria em lê-la antes de tratar de qualquer outro assunto aqui na Abadia.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3101] = {title = "Carta consagrada", objective = "Leia a Carta Consagrada e fale com o Irmão Samuel na Abadia de Vila Norte.", description = "Pediram-me para lhe entregar isto assim que você voltasse dos assentamentos de kobolds, <name>. Parece ser uma carta selada com a insígnia do Irmão Samuel, nosso instrutor de paladinos local. Eu não hesitaria em lê-la antes de tratar de qualquer outro assunto aqui na Abadia.", progress = "Finalmente! Nos encontramos frente a frente, de irmão para irmão. Bem-vindo à Vila Norte. Aqui será sua casa por algum tempo, durante o seu aprendizado inicial. Mas saiba que Ventobravo não está longe e que, cedo ou tarde, seu caminho irá te levar à capital. Então, até lá, seja paciente... Cavaleiro do Punho de Prata.", completion = "Enquanto isso, há uma ou duas coisinhas que você precisa saber. Você é um símbolo para muitos destas terras. Aja de acordo. A Luz Sagrada brilha em você, e isso vai transparecer facilmente tanto para os amigos quanto para os inimigos.\n\nE mais: conforme acumula mais sabedoria e poder, você vai precisar aprender novas habilidades. É aí que eu entro na história. Quando você sentir que já ganhou mais experiência aqui em Vila Norte, venha me procurar e lhe ensinarei o que eu achar mais adequado para o seu momento. Boa sorte... &lt;class&gt;!", rewards = "Você receberá:\n\n"},
[3102] = {title = "Carta cifrada", objective = "Leia a Carta Cifrada e fale com Rufino Raposo no estábulo atrás da Abadia de Vila Norte.", description = "Pediram-me para lhe entregar isto assim que você voltasse dos assentamentos de kobolds, <name>. Parece ser uma carta selada com a insígnia de Rufino, um dos nossos instrutores locais. Eu não hesitaria em lê-la antes de tratar de qualquer outro assunto aqui na Abadia.", progress = "Você conseguiu chegar até aqui inteiro, e parece que poucas pessoas o viram entrar. Por mim, tudo bem. Não gosto mesmo de despertar muita atenção para mim aqui atrás... Quero tudo bem quietinho. Sei que você me entende.\n\nJá se meteu em alguma confusão? Fico feliz em saber que não. Isso vai mudar logo, logo...", completion = "Você vai encontrar uma série de organizações que cobiçam nossas habilidades, &lt;name&gt;. Aventureiros, AVIN... até mesmo a Irmandade Défias adoraria ter um espião ou dois dentro de Ventobravo. Mas, lembre-se disso, você tem total independência. Não deixe ninguém forçá-lo a fazer algo que você não queira! Além disso, nós temos todos os trunfos na mão... pelo menos, antes do fim do jogo. Entende o que quero dizer?\n\nEnfim, eu só queria me apresentar e dizer que estou aqui se você precisar de instrução. Apareça quando quiser.", rewards = "Você receberá:\n\n"},
[3103] = {title = "Carta santificada", objective = "Lei a Carta Santificada e fale com a Sacerdotisa Anita na Abadia de Vila Norte.", description = "Pediram-me para lhe entregar isto assim que você voltasse dos assentamentos de kobolds, <name>. Parece ser uma carta selada com a insígnia da Sacerdotisa Anita, nossa instrutora de sacerdotes local. Eu não hesitaria em lê-la antes de tratar de qualquer outro assunto aqui na Abadia.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3104] = {title = "Carta glífica", objective = "Leia a Carta Glífica e fale com Gaspar Melchior dentro da Abadia de Vila Norte.", description = "Pediram-me para lhe entregar isto assim que você voltasse dos assentamentos de kobolds, <name>. Parece ser uma carta selada com a insígnia de Gaspar, nossa instrutor de magos local. Eu não hesitaria em lê-la antes de tratar de qualquer outro assunto aqui na Abadia.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3105] = {title = "Carta maculada", objective = "Leia a Carta Maculada e fale com Drusilla La Salle perto da Abadia de Vila Norte.", description = "Pediram-me para lhe entregar isto assim que você voltasse dos assentamentos de kobolds, <name>. Parece ser uma carta selada com a insígnia de Drusilla, uma de nossos instrutores locais. Eu não hesitaria em lê-la antes de tratar de qualquer outro assunto aqui na Abadia.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3106] = {title = "Runa simples", objective = "Leia a Runa Simples e fale com Thran Khorman no Vale Cristálgida.", description = "Enquanto tu me ajudava, recebi esta runa para ti. Te acomode uma hora para ler o que ela diz. Penso eu que foi o instrutor de guerreiros Thran quem a enviou. Dá uma olhada e depois fale com ele em Sidermar quando tiver a oportunidade.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3107] = {title = "Runa consagrada", objective = "Leia a Runa Consagrada e fale com Bromos Grummner no Vale Cristálgida.", description = "Enquanto tu me ajudava, recebi esta runa para ti. Te acomode uma hora para ler o que ela diz. Penso eu que foi o instrutor de paladinos Bromos quem a enviou. Dá uma olhada e depois fale com ele em Sidermar quando tiver a oportunidade.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3108] = {title = "Runa cinzelada", objective = "Leia a Runa Cinzelada e fale com Thorgas Grimson no Vale Cristálgida.", description = "Enquanto tu me ajudava, recebi esta runa para ti. Te acomode uma hora para ler o que ela diz. Penso eu que foi o instrutor de caçadores Thorgas quem a enviou. Dá uma olhada e depois fale com ele em Sidermar quando tiver a oportunidade.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3109] = {title = "Runa cifrada", objective = "Leia a Runa Cifrada e fale com Solm Hargrin no Vale Cristálgida.", description = "Enquanto tu me ajudava, recebi esta runa para ti. Te acomode uma hora para ler o que ela diz. Penso eu que foi o instrutor de ladinos Solm quem a enviou. Dá uma olhada e depois fale com ele em Sidermar quando tiver a oportunidade.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3110] = {title = "Runa santificada", objective = "Leia a Runa Santificada e fale com Branstock Khalder em Sidermar.", description = "Enquanto tu me ajudava, recebi esta runa para ti. Te acomode uma hora para ler o que ela diz. Penso eu que foi o instrutor de sacerdotes Branstock quem a enviou. Dá uma olhada e depois fale com ele em Sidermar quando tiver a oportunidade.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3112] = {title = "Memorando simples", objective = "Leia o Memorando Simples e fale com Thran Khorman no Vale Cristálgida.", description = "Enquanto tu me ajudava, recebi este memorando para ti. Te acomode uma hora para ler o que ele diz. Penso eu que foi o instrutor de guerreiros Thran quem o enviou. Dá uma olhada e depois fale com ele em Sidermar quando tiver a oportunidade.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3113] = {title = "Memorando cifrado", objective = "Leia o Memorando Cifrado e fale com Solm Hargrin no Vale Cristálgida.", description = "Enquanto tu me ajudava, recebi este memorando para ti. Te acomode uma hora para ler o que ele diz. Penso eu que foi o instrutor de ladinos Solm quem o enviou. Dá uma olhada e depois fale com ele em Sidermar quando tiver a oportunidade.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3114] = {title = "Memorando Glífico", objective = "Leia o Memorando Glífico e fale com Marryk Nurribit em Sidermar, no alto do Vale Cristálgida.", description = "Enquanto tu me ajudava, recebi este memorando para ti. Te acomode uma hora para ler o que ele diz. Penso eu que foi o instrutor de magos Marryk quem o enviou. Dá uma olhada e depois fale com ele em Sidermar quando tiver a oportunidade.", progress = "Sim, o que foi, <race>? Fale alto, se não se importa. Eu não escuto muito bem depois de uns canecos.", completion = "Ah, sim! Mas é claro, Artes Arcanas.\n\nAqui está o que você precisa saber, &lt;name&gt;: Não procure encrenca. Poupe moedas para me pagar uma cerveja... ou comprar uns feiticinhos. E passe longe de Gnomeregan. Tirando isso, tenha um bom dia! Estarei aqui se precisar de mim, ou quiser treinar... sei lá.", rewards = "Você receberá:\n\n"},
[3115] = {title = "Memorando Maculado", objective = "Leia o Memorando Maculado e fale com Alamar Carranca em Sidermar, no alto do Vale Cristálgida.", description = "Enquanto tu me ajudava, recebi este memorando para ti. Te acomode uma hora para ler o que ele diz. Penso eu que foi o instrutor de bruxos Alamar quem o enviou. Dá uma olhada e depois fale com ele em Sidermar quando tiver a oportunidade.\n\nE te cuida, <name>, por aqui o povo olha torto para gente como tu.", progress = "Maravilha! Evidentemente, você recebeu meu memorando.\n\nNão ligue pras tolices dos outros, <name>. Luz Sagrada?! Espada e escudo?! Gente com a mente aberta como a nossa não trilha esses caminhos. Olha só o que a magia \"normal\" fez com nosso lar. Junta isso com aqueles engenhoqueiros malditos e a raça dos gnomos quase deu tchau, tchau. Agora temos que depender dos anões? Eles preferem ficar de aliancinha com os humanos do que ajudar a reconstruir nosso lar. Somos cidadãos só no papel. Notou como olham pra gente?", completion = "O que realmente importa é que você encontrou sua própria Luz Sagrada e agora sabe de onde o verdadeiro poder vem. Entende quando digo que termos nossos próprios aliados é uma decisão muito mais... prudente? Aliados especiais... Aliados que, não importa qual seja a missão, irão obedecer até o último suspiro.\n\nEssa é a minha tarefa, &lt;name&gt;. Posso iniciá-lo no aprendizado de tais poderes especiais. Procure-me de tempos em tempos e lhe ensinarei mais feitiços.", rewards = "Você receberá:\n\n"},
[3116] = {title = "O selo simples", objective = "Leia o Selo Simples e fale com Alyissia em Umbravale.", description = "Este selo me foi entregue por um mensageiro enviado por nossa instrutora de guerreiros, Alyissia. Parece que ela deseja conversar com você quando tiver algum tempo livre. Leia o recado e, depois, leve-o a ela.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3117] = {title = "O selo cinzelado", objective = "Leia o Selo Cinzelado e fale com Ayanna Perenanda no topo de Aldrassil, em Umbravale.", description = "Este selo foi-me entregue por um mensageiro enviado por nossa instrutora de caçadores, Ayanna. Parece que ela deseja conversar com você quando tiver algum tempo livre. Leia o recado e, depois, leve-o a ela.", progress = "Chegou na hora certa, <name>. Bem-vindo a Umbravale.\n\nEspero que meu recado o tenha encontrado em boas condições de saúde e espírito.\n\nComo disse anteriormente, estou aqui para treinar caçadores como você; ensiná-los a domar feras para que os auxiliem em batalha; a manejar o arco com precisão infalível; a respeitar as terras que chamamos de lar e as que ficam além do horizonte.", completion = "À medida que for aprendendo as lições que Umbravale tem a ensinar, venha me ver. Eu lhe ensinarei mais sobre os caminhos da caça e o que somos capazes de fazer.\n\nSão tempos perigosos, &lt;name&gt;, não confie apenas na habilidade de seu companheiro animal, pois há no mundo coisas mais perigosas do que você imagina. Vá com Eluna e volte quando quiser.", rewards = "Você receberá:\n\n"},
[3118] = {title = "O selo cifrado", objective = "Leia o Selo Cifrado e fale com Frahun Umbrurmúrio em Umbravale.", description = "Este selo me foi entregue por um mensageiro enviado por nosso instrutor de ladinos, Frahun. Parece que ele deseja conversar com você quando tiver algum tempo livre. Leia o recado e, depois, leve-o a ele.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3119] = {title = "O selo sagrado", objective = "Leia o Selo Sagrado e fale com Shanda em Aldrassil.", description = "Este selo me foi entregue por um mensageiro enviado por nossa instrutora de sacerdotes, Shanda. Parece que ela deseja conversar com você quando tiver algum tempo livre. Leia o recado e, depois, leve-o a ela.", progress = "Que bom que você não demorou a chegar, <name>. Desejo-lhe as boas-vindas.\n\nComo já deve saber, toda Teldrassil está fervilhando com as idas e vindas de viajantes. Até integrantes da Aliança receberam permissão para acessar os galhos de Teldrassil para se reunirem com outros kaldorei que se preparam para as aventuras que nos aguardam.", completion = "Não se preocupe com o que há no mundo abaixo de Teldrassil, por enquanto. Terá bastante tempo para isso depois. Vá falar com as pessoas de Umbravale, elas podem dizer-lhe o que se passa por aqui. Ajude-as da forma que puder e sempre que julgar que precisa de mais treino, venha me ver e eu lhe ensinarei tudo o que estiver ao meu alcance.", rewards = "Você receberá:\n\n"},
[3120] = {title = "O selo verdejante", objective = "Leia o Selo Verdejante e fale com Mardant Carvalhaço na ávore Aldrassil, em Umbravale.", description = "Este selo me foi entregue por um mensageiro enviado por nosso instrutor de druidas, Mardant. Parece que Mardant deseja conversar com você quando tiver algum tempo livre. Leia o recado e, depois, leve-o a ele.", progress = "Ah, jovem <class>. Vejo que está ansioso para continuar seus estudos. Que bom.\n\nDeixe-me perguntar, já passou tempo suficiente no Sonho Esmeralda? Talvez ainda não esteja preparado para isso... mas tudo a seu tempo.\n\nAté lá, temos outros assuntos a discutir.", completion = "Serei seu mentor e guia por ora. É meu dever não apenas ensinar-lhe como interpretar o que dizem os espíritos, mas também a complexidade e os segredos do Sonho Esmeralda.\n\nÀ medida que aprende e que se fortalece, aprenderá mais sobre nosso transe onírico. Até lá, volte sempre que quiser e eu ensinarei o que estiver ao meu alcance dentro das artes druídicas.", rewards = "Você receberá:\n\n"},
[3121] = {title = "Um pedido estranho", objective = "Leve a Cabeça Encolhida para Neeru Cortafogo em Orgrimmar.", description = "Neeru Cortafogo tem lá em Orgrimmar o que eu preciso para começar por aqui! Ele criou um preparado que eu útil para o meu... bem, tu vai ver.\n\nAgora, pé na estrada pra Orgrimmar. Leva isso contigo. É uma cabeça encolhida que eu devia para o Neeru pelas plantas que ele me deu. É a cuca de um anão mané com quem troquei uma ideia na floresta. Pode acreditar que Neeru vai curtir.\n\nEle fica no Antro das Sombras.", progress = "Sim?", completion = "Eu tava esperando o Uzer'i me mandar isso...\n\nDeixa eu te dar a mistura de plantas que fiz pros... bem.... experimentos dele...", rewards = "Você receberá:\n\n"},
[3122] = {title = "Fale novamente com Mandingueiro Uzer'i.", objective = "Entregue a Bolsa de Herborismo de Neeru para Mandingueiro Uzer'i em Feralas.", description = "Aqui as plantas, <name>. Leve pro Mandingueiro Uzer'i.\n\nPor favor, diz pra ele que gostei muito da cabeça encolhida.", progress = "Você falou com Neeru?", completion = "É bem isso que tava faltando pra fazer o recipiente de muisec, &lt;name&gt;. Logo, vamos ver se meus encantamentos novos são esse auê todo que estou esperando.\n\nPor favor, não sai daí. Vou fazer o ritual e aplicar as plantas.", rewards = "Você receberá:\n\n"},
[3123] = {title = "O teste do recipiente", objective = "Viaje até as Terras Agrestes e localize os Coruscantes. Mate 10 e use o Recipiente de Muisec para encolher e capturar os Coruscantes abatidos.\n\nLeve 10 Muisecs de Coruscante e o Recipiente de Muisec para Mandingueiro Uzer'i em Feralas.", description = "Agora vai rolar um teste: do meu ritual e das tuas habilidades. Pega esse recipiente, <name>. Com ele, tu pode encolher e capturar uma criatura.\n\nVai nas Terras Agrestes e procura uns coruscantes — umas criaturas que já foram bichinhos de estimação da deusa noctiélfica Eluna. Tu mira nas ferujas ferozes, primitivas ou selvagens, <name>. Mata 10 e usa o recipiente de muisec pra encolher e prender eles antes que os espíritos deem no pé.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3124] = {title = "Muisec de hipogrifo", objective = "Mate 10 Hipogrifos Esfiapluma de qualquer tipo e use o Recipiente de Muisec para encolhê-los e capturá-los.", description = "Como tu mostrou que é dign<o/a> de confiança e capaz de usar o recipiente de muisec, eu quero que capture outra criatura.\n\nFaz muitos anos que os hipogrifos são companheiros leais dos elfos noturnos; os espíritos deles são íntegros e imperturbáveis.\n\nSua tarefa é matar 10 hipogrifos esfiapluma depois encolher e botar eles nesse recipiente quanto antes, pra preservar o muisec deles. Tem hipogrifos na Selva Alta, ao sul daqui.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3125] = {title = "Muisec de Dragoleta", objective = "Mate 8 Dardejantes ou Dragões-íris. Use o Recipiente de Muisec para encolher e capturar as Dragoletas abatidas.", description = "<name>, agora tu vai pegar uma criatura que tu não dá nada pra ela, mas, saca só, o espírito dela é poderoso e pode ser útil. Tô falando é das dragoletas, aliadas dos elfos noturnos. A gente tem que prender umas pra Horda tirar proveito do poder espiritual delas.\n\nToma aí o muisec, <name>. Mate 8 dardejantes ou dragões-íris e capture seus muisecs. É só tu pegar pra oeste daqui que tu encontra elas.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3126] = {title = "Muisec de Arvoroso", objective = "Mate 3 Andarilhos Errantes da Floresta. Use o Recipiente de Muisec para encolher e capturar os Arvorosos abatidos.", description = "Agora o lance é pegar os andarilhos errantes da floresta, <name>. Os elfos noturnos recrutaram arvorosos como aliados na guerra contra a Horda. Eles se arrastam devagar como lesmas, mas ninguém duvida que são bons protetores.\n\nLogo, a gente vai poder tirar proveito desse poder.\n\nAche 3 andarilhos errantes da floresta, daí tu mata eles e usa o recipiente de muisec pra encolher e prender eles.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3127] = {title = "Muisec de Gigante da Montanha", objective = "Mate 7 Terrâmbulos ou Gigantes do Penhasco. Use o Recipiente de Muisec para encolher e capturar os Gigantes da Montanha abatidos.", description = "Só tem mais uma coisa pra tu fazer: ir pro noroeste e achar o último inimigo.\n\nOs gigantes da montanha deixaram as montanhas para ajudar os elfos noturnos quando a Legião Ardente deu as caras. Tu vai reconhecer eles pelo tamanho, claro, e pela terra tremendo quando tu chega perto do terreno rochoso deles.\n\nMal posso esperar para acessar um poder desse tamanho. Mata 7 gigantes da montanha e prende esse muisecs poderosos com o recipiente de muisec.", progress = "Tu prendeu os gigantes da montanha?", completion = "Abra sua mente, &lt;name&gt;! Imagine as possibilidades que temos nas nossas mãos. Com o material que tu coletou, vou poder prender os muisecs dessas criaturas para sempre, no que eu quiser.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[3128] = {title = "Caçadores de matérias-primas perdidas", objective = "Leve 2 Pedaços de Lenha, 6 Minerais Incrustados, 20 pedaços de Tendão Resiliente e 40 Fragmentos Metálicos para Mandingueiro Uzer'i na Aldeia Mojache.", description = "Preciso de algumas matérias-primas para completar meu projeto... Tu vai atrás das outras tarefas que eu te der e vai catando essas coisas junto.\n\nTô atrás de um pedaço de lenha dos arvorosos de Feralas. Uns minerais que tu acha nos gigantes da montanha e nas dragoletas pra oeste. E também, rola me arranjar uns tendões resilientes de ferujas das Terras Agrestes ou dos hipogrifos de Feralas. E, pra terminar, preciso de um punhado de fragmentos metálicos dessas criaturas que te disse.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3129] = {title = "Armas de espírito", objective = "O Mandingueiro Uzer'i deseja que você aguarde por um instante enquanto ele prepara as armas.", description = "Imbuir armas com muisec não é moleza, não. Agora que tô com todas as matérias-primas que preciso e os muisecs das criaturas que tu prendeu, já posso começar. Não sai daí, que eu já volto.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nForça do HipogrifoEspírito de DragoletaForça do ArvorosoVontade do Gigante da Montanha\n\nVocê também receberá:\n\n"},
[3130] = {title = "Cruzada contra as Cristafúrias", objective = "Apresente-se para Latrônicus Lunalança no Domínio de Plumaluna.", description = "Por conseguinte, dirijo você ao meu segundo em comando: Latrônicus. Ele observa a movimentação das nagas há tempos. Analisarei a situação geral das nagas daqui. Por mais que deseje despachar todas as tropas para avançar contra elas, deixar o baluarte exposto a ataques diretos da Horda, ou coisa pior, parece-me uma imprudência.\n\nDe qualquer modo, Latrônicus aguarda por você. Excelente trabalho até aqui, <name>. Continue assim.\n\n<Dispensado/Dispensada>.", progress = nil, completion = "Excelente, &lt;name&gt;. Valore uma atitude \"proativa\" nos indivíduos com quem trabalho.\n\nO General Plumaluna me encarregou de lidar com as nagas de forma direta. Meu plano tem duas etapas: primeiro, enfraquecemos a liderança e o número de adversários; segundo, descobrimos o que elas andam tramando no topo das ruínas. Com a sua ajuda, combateremos a presença das nagas sem pôr em risco a segurança do baluarte.", rewards = "Você receberá:\n\n"},
[3141] = {title = "Loramus", objective = "Ouça Loramus Talípedes contar sua história.", description = "Então você quer combater demônios? Faria bem em ouvir minha história.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3161] = {title = "Gahz'ridian", objective = "Marlon Catarrebite, em Tanaris, quer que você colete 30 Ornamentos de Gahz'ridian.", description = "<name>, sou cientista, mas minha paixão é caçar tesouros!\n\nHá muito tempo, quando os trolls ocupavam a região, criaram um monte de ornamentos baseados em Gahz'rilla, uma hidra que eles idolatravam como deidade. Os ornamentos são feitos de um material que eu chamo de gahz'ridian, por motivos óbvios.\n\nJá encontrei um pouco disso, mas queria ter mais para estudar — quem sabe você não encontra?\n\nEu até inventei um detector que encontra gahz'ridian facinho! É só ligar pra busca começar!", progress = "Tá passando aperto?\n\nTem gahz'ridian em tudo que é canto de Tanaris, <name>. É só procurar mais!", completion = "Perfeito. Esses ornamentos vão servir direitinho nos meus estudos. Valeu mesmo pela ajuda, &lt;name&gt;.\n\nAh, dá pra devolver meu capacete?", rewards = "Você vai poder escolher uma dessas recompensas:\n\nTúnica do TopógrafoCajado da Tradição\n\nVocê também receberá:\n\n"},
[3181] = {title = "O chifre da fera", objective = "Leve o Chifre Gigante de Margol para a Montanhista Pedrabrita.", description = "O gigantesco chifre apresenta inúmeros arranhões e rachaduras. Um metal incomum encontra-se alojado na superfície áspera.\n\nApós um exame mais cuidadoso, fica claro que se trata de uma cabeça de machado quebrada. No metal está entalhado:\n\nPropriedade da Montanhista Pedrabrita.\n\nAo tentar desalojar a cabeça de machado, ela se desfaz em milhões de pedaços.\n\nOps!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[3182] = {title = "Certificado de realização", objective = "Leve o Chifre Gigante de Margol para o Curador Tórius em Altaforja.", description = "Você tá achando que eu vou engolir uma história dessas? O chifre verdadeiro teria a lâmina partida do meu machado ainda cravada nele.\n\nEscuta, leva essa falsificação óbvia para o Curador Tórius em Altaforja. Se ele validar sua \"descoberta\", você receberá um certificado de autenticidade. Traga-me esse certificado e eu lhe entregarei a chave da Garganta Abrasadora.\n\nSebo nas canelas!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[3201] = {title = "Finalmente!", objective = "Leve o Certificado de Realização para a Montanhista Pedrabrita.", description = "Mas me admira muito que a Pedrabrita tenha dito que o chifre é falso. Cá entre nós, é só dor de cotovelo. Ela queria era ter matado a fera, não fosse por conta do \"incidente\" e tal.\n\nEntão tu precisas de certificado de realização? Depois da tua contribuição ao museu, não tem problema.\n\n<Curador Tórius começa a preencher um longo documento.>\n\nTá na mão! Já podes levar para a Pedrabrita.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nChave da Garganta Abrasadora"},
[3221] = {title = "Fale com Renferrel", objective = "Fale com o Boticário Renferrel no Sepulcro.", description = "Enquanto você auxiliava os sicários, o Boticário Renferrel mandou convocar-lhe. Ele não forneceu detalhes, mas queria discutir com você sobre os corações de lobo que você lhe deu.", progress = nil, completion = "Ah, muito bem. Um assunto relativo aos corações de lobo que você me trouxe requer nossa atenção.", rewards = "Você receberá:\n\n"},
[3261] = {title = "Jorn Vidente do Céu", objective = "Fale com Jorn Vidente do Céu no Acampamento Taurajo.", description = "Seu tempo comigo chegou ao fim, <name>. Para aprender mais, vá ao Acampamento Taurajo ao sul e fale com Jorn Vidente do Céu. Ele vai continuar a sua orientação.", progress = nil, completion = "Saudações, jovem. Veio aqui para trilhar o caminho do caçador?\n\nÓtimo. Vamos começar.", rewards = "Você receberá:\n\n"},
[3281] = {title = "Prata roubada", objective = "Leve a Prata Roubada para Gazrog na Encruzilhada.", description = "Você matou os raptores, mas alguém ainda tem que recuperar a prata que eles roubaram! Recebi relatos de que eles fizeram um punhado de ninhos ao sul da Vila Catraca, na Terra dos Raptores. Eles devem ter levado nossa prata roubada lá.\n\nProcure esse raptor e encontre a prata roubada. Depois, traga a prata para mim.", progress = "Você trouxe a prata?", completion = "Arrá! Quer dizer que os raptores escondem mesmo a prata no covil. Por que essas feras ligam para prata? Quem sabe essas histórias sobre serem inteligentes não estejam tão erradas assim!\n\nObrigado, &lt;name&gt;. Aceite esta recompensa pelos seus serviços e saiba que os guardas da Encruzilhada só vão receber soldo no mês que vem graças a você.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas do ErrantePerneiras do Colonizador\n\nVocê também receberá:\n\n"},
[3301] = {title = "Mura Runa Totem", objective = "Fale com Mura Runa Totem no Sepulcro.", description = "Minha colega de clã, Mura Runa Totem, foi até Pinhaprata para auxiliar nossos aliados mortos-vivos. Pinhaprata é uma floresta sôfrega, moribunda, e Mura tem esperança de recuperá-la. Talvez essa forma de vida estranha dos Sertões possa ajudá-la em Pinhaprata.\n\nPegue uma amostra das conchas que você coletou e leve-a para Mura em Pinhaprata. Ela fica no Sepulcro. Com sorte, estudar essa concha pode ser a inspiração que falta para ela curar esse lugar decadente.", progress = "Sinto o cheiro da poeira dos Sertões em você, <class>. Você viajou muito.", completion = "Ah, o que vejo aqui? Essa concha tem um brilho interior, como se uma vida pulsasse lá dentro. Incrível! Preciso estudá-la com mais calma.\n\nObrigada, &lt;name&gt;. Com sorte, esta concha me ajudará a desvendar o segredo para ajudar a pobre Pinhaprata.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCinto da SemeaduraManto do Semeador\n\nVocê também receberá:\n\n"},
[3321] = {title = "Perdeu alguma coisa?", objective = "Espere até que Trenton termine o trabalho.", description = "Você fez muito pela Ordem, <name>. A dedicação incontestável para se chegar até aqui desanimaria uma pessoa comum. Sem sombra de dúvida — nem para mim, nem para Galvan —, você é <um/uma> de nós.\n\nE é por isso que você merece uma pequena recompensa. Não é nada demais, só um berloque. Você deve conhecer suas propriedades, pois creio que foi isso que trouxe você até nós.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInsígnia de Mithril Rutilante"},
[3341] = {title = "Arauto do fim", objective = "André Rosado quer que você mate Amnennar, o Frigífero, e volte com a cabeça dele.", description = "Nos Sertões, há um amarrado horrendo de trepadeiras chamado Urzal dos Mortos. Nossas impressões iniciais sugeriam que ele não oferecia ameaça, mas descobertas recentes questionam isso...\n\nOs javatuscos do Urzal dos Mortos uniram forças com o Flagelo. Um lich chamado Amnennar, o Frigífero, passou a governá-los, usando seu amplo poder cognitivo para controlar cada movimento deles.\n\nPrecisamos cortar o vínculo telepático que Amnennar forjou com Ner'zhul, <name>. É hora de pôr um fim aos planos do Frigífero.", progress = "Este é um assunto da maior importância, <name>. Temos de impedir que o Flagelo consiga ampliar suas fileiras.", completion = "A lealdade para com um lich é cega, &lt;name&gt;. Que todos saibam que essa foi a causa de sua ruína.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nEspada do VencedorTalismã de Brilhâmbar"},
[3361] = {title = "Um dia na vida de um refugiado", objective = "Leve a Caixa do Félix, o Baú do Félix e o Balde de Parafusos do Félix para Félix Reclamofuso em Sidermar.", description = "Expulsamos os troggs de Gnomeregan, mas depois tudo foi por água abaixo! A radiação tomou conta do nosso lar e nós, gnomos, nos espalhamos por toda Dun Morogh.\n\nNa pressa de escapar da radiação, perdi meus pertences e ferramentas. Foram os trolls que pegaram. Eles roubaram meu baú, minha caixa e meu balde de parafusos! Levaram tudo para o acampamento deles, a sudoeste de Sidermar.\n\nNão sou de aventuras. Será que você podia procurar minhas coisas e recuperá-las para mim, por favor?", progress = "Minha nossa, <name>, aqui não é lugar para tipos como eu. Tem tanta criatura nojenta aqui quanto havia em Gnomeregan antes da tragédia!\n\nAchou meus pertences? Se ainda não, nem quero imaginar o que os trolls já fizeram com eles...", completion = "Viva! Você achou! Salvou minha vida, meu amigo. Aqui, não é muita coisa, mas você merece uma coisinha pelo que fez por mim! Obrigado!", rewards = "Você receberá:\n\n"},
[3362] = {title = "Vale Moitagulhas", objective = "Tran'rek, em Geringontzan, quer que você mate 8 Moitagulhas Rotos e 8 Fazraízes Moitagulhas.", description = "Tu já foi no Vale Moitagulhas? Baita lugar barra pesada, <name>.\n\nAqueles bichos bizarros lá, tem que ficar esperto! Se não tô enganado, a galera chama eles de rotos e fazraízes...\n\nSei lá, o que eu quero é entrar no vale, mas tá cheio desses moitagulhas. Nem pensar, eu é que não volto lá. Aí é que tu entra... se tu der uma limpada na área, quem sabe eu piro de voltar. E aí? O que tu acha, <name>? Quebra essa pra mim?", progress = nil, completion = "É desse tamanhão? Acho que vou pôr esse plano de molho...\n\nBem, tu cumpriu a tua parte, então toma aí o que prometi.", rewards = "Você receberá:\n\n"},
[3363] = {title = "Flor da Canção corrompida", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[3364] = {title = "Rabo-de-galo em domicílio", objective = "Leve um Rabo-de-Galo Escaldante para Durnan Cortapelo em Sidermar em cinco minutos, antes que esfrie!", description = "Cuê-pucha! Nesta manhã, eu devia ter levado este rabo-de-galo escaldante para Durnan Cortapelo já faz um tempo, mas eu tava em cima do laço na entrega do Grolin aqui. Nunca que chego em Sidermar sem que isto esfrie!\n\nTu pareces ágil como um leopardo da neve. Talvez tu consiga. É cinco minutos pra isto aqui ficar que é um gelo de frio, e Durnan não pediu um rabo-de-galo \"refrescante\", então te manda. Sidermar fica pro nordeste, num assentamento cavado na montanha.\n\nTe fico muito grato, <name>, e não te bobeie: quero minha caneca de volta!", progress = "Isso, me chamo Durnan Cortapelo. O que me trazes?", completion = "Mazá, este caldo é mesmo de lei. Permita-me dar uma pausa enquanto desfruto deste rabo-de-galo escaldante!", rewards = "Você receberá:\n\n"},
[3365] = {title = "Devolva a caneca", objective = "Devolva a Caneca de Nóri para Nóri Fluiorgulho.", description = "Isso é que é caldo de lei! Nada como um rabo-de-galo fervente, não, ESCALDANTE pra tirar a friagem do bucho nessa invernada que trinca até dentadura.\n\nTome isto, <name>. Agora não te mixa e me faz uma mão: leva de volta esta caneca pro Nori.", progress = "Pelo visto, entregaste o rabo-de-galo escaldante do Durnan a tempo! Lembraste de me trazer a caneca de volta?", completion = "Barbaridade, &lt;name&gt;! Nem imaginas quantos entregadores têm por aí que não se atentam e fazem a bobagem de esquecer da caneca. Já viste pé de caneca? Eu não!\n\nMereces uma prenda pelo serviço. Mais uma vez, tens o meu obrigado.", rewards = "Você receberá:\n\n"},
[3366] = {title = "A lasca faiscante", objective = "Viaje até a Vila Catraca para descobrir o que há por trás dessa Lasca de Pesadelo.", description = "O verde da lasca brilha e, apesar de sólida, algo meio túrbido parece se contorcer logo abaixo da superfície. Você não consegue desviar o olhar do abismo no cristal e, ao mesmo tempo, sabe que há algo muito perturbador nisso.\n\nTalvez você devesse procurar se alguém na Vila Catraca saiba o que é essa lasca estranha.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3367] = {title = "Pedras de Suntara", objective = "Ajude Dório a voltar a Altaforja.", description = "<Dório solta um grunhido.>\n\nEstou muito ferido, <race>. Minha... cabeça... tá girando... hemorragia... interna.\n\nTenho que voltar a Altaforja. Socorro, <name>.", progress = nil, completion = "Dório deixou cair essa carta quando foi atingido.", rewards = "Você receberá:\n\n"},
[3368] = {title = "Pedras de Suntara", objective = "Leve a Carta Chamuscada para Tórius em Altaforja. Ele fica no Salão dos Exploradores.", description = "Irmão,\n\nSe estiver lendo esta carta, quer dizer que eu já parti. Como ela chegou até você, eu não sei, mas agradeça e recompense o responsável.\n\nSobre as pedras: elas existem e estão sob posse dos Ferro Negro. O líder deles, Latórico, o Negro, usou o poder delas para escravizar minha equipe de escavação e os levar para a caldeira. A mente deles foi corrompida. Eles trabalham dia e noite para terminar algo chamado Obsidião.\n\nAjude-os, irmão. Este é o meu último desejo.\n\n- Dório", progress = "<Tórius soluça.>", completion = "&lt;Tórius seca o rosto.&gt;\n\nEm nome da Luz, juro que honrarei o desejo de meu irmão.", rewards = "Você receberá:\n\n"},
[3369] = {title = "Em pesadelos", objective = "Leve a Lasca de Pesadelo para Hamuul Runa Totem no Platô dos Anciãos.", description = "Essa lasca guarda muitos segredos, é a pura essência do Sonho Esmeralda. Todavia, o que vejo aqui não é um sonho, alguns até diriam se tratar de uma visão pesadelar.\n\nNão tenho intenção de alarmar você, <name>, mas acho que deveria levar essa lasca à atenção de um sábio druida o quanto antes...\n\nHamuul Runa Totem, no Platô dos Anciãos, é quem você deve procurar. Dirija-se ao Penhasco do Trovão. É só o que posso dizer.", progress = "Posso ajudar em alguma coisa?", completion = "Falla mandou você aqui? Ora, mostre essa lasca que você alega tê-la deixando receosa para explicar...\n\n&lt;Hamuul fita a lasca intensamente.&gt;\n\nOh... Nada bom.\n\n&lt;name&gt;, agradeço por trazer isso à minha atenção. Contudo, realmente não é prudente falar desses assuntos. Tais questões não são preocupação sua.\n\nDigamos apenas que as coisas no Sonho Esmeralda não estão como esperávamos...\n\nTalvez, no futuro, nossos caminhos se cruzem outra vez.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nDragonas BarraltasGalochas do Atoleiro\n\nVocê também receberá:\n\n"},
[3370] = {title = "Em pesadelos", objective = "Leve a Lasca de Pesadelo para Mathrengyl Ursivagus em Darnassus.", description = "Essa lasca guarda muitos segredos, é a pura essência do Sonho Esmeralda. Todavia, o que vejo aqui não é um sonho, alguns até diriam se tratar de uma visão pesadelar.\n\nNão tenho intenção de alarmar você, <name>, mas acho que deveria levar essa lasca à atenção de um sábio druida o quanto antes...\n\nMathrengyl Ursivagus, do Círculo Cenariano, é quem você deve procurar. Dirija-se a Darnassus. É só o que posso dizer.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nDragonas BarraltasGalochas do Atoleiro\n\nVocê também receberá:\n\n"},
[3371] = {title = "Justiça enânica", objective = "Volte para a Garganta Abrasadora e encontre a unidade de arqueologia de Dório.", description = "Do barrilete envelhecido que bombeava neste peito, restou apenas espuma, <name>. Não há como frear esta tristura pelo falecimento de Dório. Devo honrar seu último desejo: fazer de pelego aqueles que cometeram esta barbaridade.\n\nTu me volta para a Garganta Abrasadora e atende-lhe o pedido derradeiro: liberta os homens dele.", progress = nil, completion = "Te larga de mim e vá ajudar os outros.", rewards = "Você receberá:\n\n"},
[3372] = {title = "Liberta os outros", objective = "Encontre o Artefato Misterioso que está protegido pelos Idólatras do Martelo do Crepúsculo no quadrante noroeste da Garganta Abrasadora.", description = "<race>, tens de libertar os outros. O corpo deles pode ter se largado, mas o poder de Suntara os compele a continuar na lida.\n\nA chave é o altar de Suntara. Te larga para noroeste, lá na caverna do alto das colinas... o Martelo do Crepúsculo protege uma relíquia. Tu a recupera e trazes para colocar no altar de Suntara.\n\n<O arqueólogo moribundo sucumbe.>", progress = "O altar está vazio.", completion = "Você coloca o artefato misterioso no altar de Suntara.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[3373] = {title = "A Essência de Erânicos", objective = "Coloque a Essência de Erânicos na Fonte de Essência encontrada neste covil do Templo Submerso.", description = "Ao tocar na gema, uma voz grave ressoa no fundo da sua mente.\n\n\"Mortal, ouça esta súplica. Sou Erânicos, da Revoada Dragônica Verde, guardião deste tempo amaldiçoado. No momento, fracassei em minha incumbência de impedir que os trolls jamais restaurem a abominação que idolatram. Como imbuí esta gema com parte de minha essência, sinto que meu controle sobre o Sonho enfraquecer, tornar-se caos. Por favor, peço que coloque esta gema na fonte de essência em meu covil e permita que a magia dela expurgue essa mácula de minha psiquê para eu volte a descansar.\"", progress = "Ao se aproximar da fonte de essência que Erânicos descreveu, a voz dele ressoa novamente em sua mente.\n\n\"Sim, mortal, essa fonte de essência redimirá qualquer elemento não maculado de minha psiquê que esteja imbuído nessa gema. Coloque a gema na fonte e permita que a magia da Revoada Dragônica Verde expurgue a corrupção e a mácula do meu ser. Só assim, serei verdadeiramente livre, e só assim, a revoada dragônica reafirmará sua liderança em Atal'ai.\"", completion = "Você coloca a gema na fonte de essência.\n\n\"Obrigado, mortal, você não imagin... não, não pode ser! Não! A corrupção deste deus profano macula até a santidade da fonte de essência da Revoada Dragônica Verde! Não, por favor, sinto minha essência sendo arrancada de mim! COMO DÓI! Sinto as garras de um pesadelo eterno me agarrando...\"\n\n\"Mortal, imploro a você! Encontre alguém da Revoada Dragônica Verde para impedir isso... socorra-me... estou preso nas trevas... em eterna agonia... por favor, não torture...\"", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nEssência Acorrentada de Erânicos"},
[3374] = {title = "A Essência de Erânicos", objective = "Leve a Pedra do Juramento da Revoada de Ysera e a Essência Acorrentada de Erânicos para Itharius no Pântano das Mágoas. Lá, você decidirá se deseja ajudar a Revoada Dragônica de Ysera ou não.", description = "Você interage com a Pedra do Juramento e Itharius usa a pedra para falar telepaticamente com você.\n\n\"Ao aceitar a incumbência desta Pedra de Juramento, você se compromete a promover minha causa, e a causa da revoada dragônica de Ysera, de redimir a alma corrompida de Erânicos. O primeiro sacrifício — dos muitos que ainda faremos — será entregar-me os resquícios da psiquê livre de Erânicos que estão aprisionados na gema.\"\n\n\"Faça isto, <name>, e trabalharemos lado a lado para redimir uma alma digna de redenção.\"Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3375] = {title = "Frasco reserva", objective = "Leve Carvão, Néctar Doce e uma Poção de Mana para Didiê Du Mocó, em Altaforja, para receber um Frasco de Vidência do Didiê.", description = "Cuma? Você precisa de outro frasco de vidência? Tá brincando comigo, não é? O que você fez com o primeiro? Temos muita coisa pra fazer aqui, <race>. Ou você acha que essas ampolas crescem em árvores? Na verdade, eu ia adorar isso, porque aí bastava chegar na árvore e pegar uma!\n\nPara fazer a sua ampola, eu preciso de uma poção de mana, um pedaço de carvão e um pouco de néctar doce. Não me pergunte por quê, só traga o que pedi e eu faço uma nova ampola para você.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nFrasco de Vidência de Didiê"},
[3376] = {title = "Quebra-presadura!", objective = "Leve a cabeça do Chefe Presadura Mantospinho para a Valente Pena ao Vento na Chapada Nuvem Vermelha.", description = "<name>... Ouvi falar de você, <neófito/neófita>. Talvez possa nos ajudar onde tantos outros falharam.\n\nNós, os taurens, fizemos desta terra o nosso lar, mas pagamos um preço alto. Os javatuscos Costagulha da Ravina da Lâmina Espinhenta, liderados pelo Chefe Presadura Mantospinho, tornaram nossa vida difícil por meio de guerras contínuas contra a nossa tribo. Comando que me traga a cabeça do chefe!\n\nEncontre-o na ravina a leste, na vila rústica dos javatuscos.", progress = "Não nos importunarão mais os javatuscos, depois que me trouxer a prova de que matou seu líder. Quero a cabeça dele, <name>.\n\nNós, taurens, vivemos para a caça, e as melhores presas são aquelas astutas o suficiente para nos caçarem elas mesmas. Talvez entenda o que eu disse, meu jovem, se voltar vitorioso.", completion = "A justiça foi feita por suas mãos ágeis, &lt;name&gt;. Que este acontecimento sirva de aviso a todos que ousarem ameaçar nossas terras.\n\nE pelos seus atos, confiro-lhe uma recompensa para ajudá-lo em sua jornada, jovem &lt;class&gt;.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nPerneiras Encadeadas PintadasLuvas Nômades\n\nVocê também receberá:\n\n"},
[3377] = {title = "Oração a Eluna", objective = "Ouça a história de Zamael.", description = "Você vai me ajudar, mortal? Ouça minha história.", progress = nil, completion = "Aceitei o meu destino aqui nesta jaula, mas a oração deve ser encontrada.", rewards = "Você receberá:\n\n"},
[3378] = {title = "Oração a Eluna", objective = "Encontre a Oração a Eluna e leve-a para Astarii Mirestela em Darnassus.", description = "Você pode me ajudar, mortal? Pode tentar encontrar a oração a Eluna?\n\nComo disse, a oração foi gravada em uma tabuleta de ouro. Um desses canalhas idólatras deve estar com ela.\n\nSe você recuperar minha oração, leve-a para Astarii Mirestela em Darnassus. Pouco importa que minha fé tenha sido reavivada após minhas inúmeras ações malévolas contra este mundo e seus habitantes, minha oração deve ser ouvida. Absolvição é a última coisa que espero.", progress = "O que a traz ao Templo da Lua, <name>?", completion = "Eluna sorri para você esta noite, &lt;name&gt;. Você devolveu uma alma perdida para ela. Por favor, aceite isso como sinal de nossa gratidão.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nOmoplatas KaylariAvambraços de Runaço\n\nVocê também receberá:\n\n"},
[3379] = {title = "Tecessombras", objective = "Mate os Larápios de Seda Sombria que rondam a Garganta Abrasadora. Nilith acredita que cinco deles precisam ser exterminados. Fale com Nilith novamente na Garganta Abrasadora quando tiver cumprido essa tarefa.", description = "As aranhas do Garganta Abrasadora são uma fonte inestimável de seda sombria. Como você sabe, seda sombria é um artigo muito procurado pelos artesãos do nosso ofício.\n\nRecentemente, um grupo de caçadores ilegais invadiu essas terras, matando aranhas descuidadamente sem pensar no efeito a longo prazo de suas ações. A primeira tarefa que lhe peço, então, é simples: encontre-os... e elimine-os.\n\nInicie sua busca ao norte.\n\nSe precisar de ajuda, fale com Arrasa.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nBraceleiras Sombrias"},
[3380] = {title = "O Templo Submerso", objective = "Encontre Marlon Catarrebite em Tanaris.", description = "Tem uns espíritos meio turbulentos nesse mundo aí, <name>. Alguns espíritos que foram maculados pelo isolamento, presos a um mal antigo, essas paradas...\n\nOuvi falar de um lugar bem abaixo da superfície da Terra... banido por poderes maiores do que jamais vi nessa vida, mano.\n\nSe quiser saber mais sobre este lugar, o Templo Submerso, procure um goblin chamado Marlon Catarrebite. O cara já estudou muitas áreas antigas do mundo, e pelo que fiquei sabendo deixou seu cafofo em Vila Catraca para estudar as ruínas dos trolls em Tanaris.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[3381] = {title = "Para encontrar um mestre", objective = "Leve 2 Penas Intactas de Hipogrifo para Sanath Lim-yo em Azshara, se quiser falar com o mestre dele.", description = "Ah, <um/uma> jovem <race> veio falar com Sanath. Que dia maravilhoso! É uma honra ser agraciado com a sua presença. Obrigado!\n\nPor favor... não seja arrogante a ponto de acreditar que meu sarcasmo foi um elogio. Vamos esclarecer uma coisa: não gosto de você. Mas esta conversa não tem nada a ver conosco... tem a ver com o meu mestre.\n\nTraga-me algumas penas de hipogrifo para minhas flechas e permitirei que o veja. Ele pediu que eu só permitisse aventureiros que se provassem dignos.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3382] = {title = "Sob fogo cerrado", objective = "Defenda a Capitão Vanessa Beltis, sua tripulação e os passageiros do Batedor do Horizonte do ataque das nagas. A Capitão Vanessa Beltis deve sobreviver.", description = "Estávamos nos aproximando da costa, procurando um ancoradouro seguro para fundear. O sol havia se posto não fazia uma hora quando elas atacaram... as malditas nagas!\n\nNão tenho certeza do que elas queriam, mas o que quer que seja, elas ainda não encontraram, porque já voltaram aqui outra vez. Dois batedores estão de guarda na entrada do vale para nos avisar do próximo ataque.\n\nSinto muito que você tenha aparecido justo agora, <name>, mas não posso fazer nada.\n\nEstá <pronto/pronta> pra luta da sua vida?Completando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = nil},
[3385] = {title = "O Submercado", objective = "Mate o Mestre Mercador Kovic e seu lacaio Clanque. Pegue a Algibeira do Comerciante do corpo dele e leve-a para Nilith.", description = "Eles se chamam de Submercado: comerciantes agressivos que vendem, negociam e fazem praticamente qualquer coisa para quem pagar o preço.\n\nE fica pior, <name>. Eles montaram uma base de operações aqui na Garganta Abrasadora. O Mestre Mercador Kovic lidera a operação e mantém estreitos laços com os anões Ferro Negro da região.\n\nDevemos atacar de forma contundente e mandar uma mensagem para a liderança deles. Acabe com o Mestre Mercador Kovic e seu lacaio Clanque. Traga de volta a algibeira de comerciante dele quando o trabalho estiver concluído.\n\nPara o Caldeirão!", progress = "O Mestre Mercador ainda vive?", completion = "Você deu um golpe decisivo contra o Submercado. Seus líderes pensarão duas vezes antes de enviar mais capangas para a garganta.\n\nAh, e também não encontrei nada de útil na algibeira do comerciante. Você pode ficar com o que encontrar lá dentro.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nCinto SombrioBolsa de Comércio de Kovic"},
[3402] = {title = "O submercado", objective = "Fale com Linhagulha em Geringontzan.", description = "Quero que você leve pessoalmente a notícia da derrota de Kovic para Linhagulha em Geringontzan. Ele ficará tão feliz em saber que Kovic está morto que tenho certeza de que lhe dará uma grande recompensa.", progress = nil, completion = "Kovic morreu?! Mas que dia glorioso, &lt;name&gt;! Por anos, aquele pilantra atuou no mercado negro, boicotando trabalhadores honestos de todo o mundo.\n\nVocê merece uma bela duma recompensa por esse feito! E é isso mesmo que você vai ganhar!", rewards = "Você receberá:\n\nMolde: Máscara de Umbratrama"},
[3421] = {title = "A viagem de volta", objective = "Vou levá-lo de volta ao acampamento de Sanath agora.\n\nNão repare nos modos ruins dele. Ele é rude com todo mundo exceto o mestre. Talvez ele aprenda a lição algum dia.", description = nil, progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nTeleportar da Torre de Azshara"},
[3441] = {title = "Castigo divino", objective = "Ouça Velarok Lamicélere contar a história dele.", description = "Ouça minha história, aventureir<o/a>.", progress = nil, completion = "Já ouviu o bastante? Tudo pronto para agir?", rewards = "Você receberá:\n\n"},
[3442] = {title = "A chama perfeita", objective = "Colete 4 Corações das Chamas e 4 globos de Óleo de Golem, e leve-os para Kalaran Finalâmina.", description = "Você tomou a decisão certa, <name>.\n\nDevemos atacar onde causaremos o maior dano possível: as quatro torres de vigília.\n\nCom as quatro torres em chamas, poderemos iniciar uma ofensiva antes mesmo que tenham tempo de reagir.\n\nVocê precisará da ferramenta adequada para incendiar as torres. E produzir essa ferramenta exige alguns materiais específicos.\n\nTraga-me quatro corações das chamas e quatro óleos de golem, que eu preparo a tocha da chama perfeita.\n\nOs golens e elementais dessas terras deveriam ser seu primeiro alvo.", progress = "Você coletou os materiais?", completion = "Muito bem, &lt;name&gt;. Estamos quase prontos.", rewards = "Você receberá:\n\n"},
[3443] = {title = "Forjando o bastão", objective = "Leve 8 Punhais de Placas de Tório para Kalaran Finalâmina.", description = "Para conter a chama perfeita, precisaremos de uma haste e um recipiente. Primeiro você terá que encontrar um cabo adequado.\n\nOs feitores dos Ferro Negro e escravagistas do caldeirão usam armas de tório encantado, uma liga extremamente resistente. Se você me trouxer oito adagas folheadas a tório, poderei derreter a liga e reforjar uma haste.", progress = "Oito, <name>. Preciso de oito!", completion = "Você trabalha muito bem, &lt;name&gt;. Já pensou numa mudança de profissão? Talvez detetive? Coletor de lixo? Ah, já sei, um desses gnomos bobos que vagam pelas praias de Azeroth com suas engenhocas detectoras de metal, à procura de tesouros perdidos. Sim, isso seria perfeito para você.", rewards = "Você receberá:\n\n"},
[3444] = {title = "O círculo de pedra", objective = "Busque o Círculo de Pedra na oficina de Marlon Catarrebite em Vila Catraca.", description = "Uma vez entrei nas câmaras sinistras do Templo Submerso. O mal toma conta de todos os que entram naquelas quebradas. Consegui escapar de lá por pouco, atravessando a gosma que agora cobre o chão. Só me lembro de passar com dificuldade por uma grande sala circular, cheia de estátuas de serpentes...\n\nFaz pouco tempo, descobri uma pedra redonda que parecia ter o mesmo símbolo de serpente impresso nela. Mandei para minha oficina em Vila Catraca. Pega ela lá pra mim e de repente eu te conto mais umas paradas.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3445] = {title = "O Templo Submerso", objective = "Encontre Marlon Catarrebite em Tanaris.", description = "Ouvi falar de um lugar bem abaixo da superfície da Terra... banido por poderes maiores do que jamais vi nessa vida.\n\nSe quiser saber mais sobre este lugar, o Templo Submerso, procure um goblin chamado Marlon Catarrebite. Assim como Troyas e eu, ele já estudou diversas áreas antigas do mundo. E, pelo que sei, deixou a casa dele em Vila Catraca para estudar as ruínas dos trolls em Tanaris.\n\nBoa sorte para encontrá-lo. Não se tem notícias dele já há algum tempo.", progress = nil, completion = "Sim, o Templo Submerso, posso dizer que vi esse lugar com meus próprios olhos. Tá a fim de ouvir mais?\n\nTu tem a maior cara de aventureiro... e sei de umas paradas que podem deixar você de boca aberta...", rewards = "Você receberá:\n\n"},
[3446] = {title = "Nas profundezas", objective = "Encontre o Altar de Hakkar no Templo Submerso, que fica no Pântano das Mágoas.", description = "Dentro do templo, encontrei uma sala circular com seis varandas, cada uma com uma estátua de serpente na borda. Curioso, empurrei uma delas para ver o que aconteceria, e era uma armadilha! Quase caí para a morte com a explosão. Deitado ali na beirada, olhei para baixo e notei que havia outra estátua na base da sala, um tipo de altar... Hakkar, acho que era esse o nome.\n\nAcho que este altar é o segredo — você deve trazer este símbolo para o altar no Templo Submerso.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[3447] = {title = "Segredo do círculo", objective = "Entre no Templo Submerso e descubra o segredo escondido no círculo de estátuas.", description = "Minhas lembranças são meio nebulosas, mas vou te contar o que puder, meu chapa. Embora perigoso, acho que o círculo de estátuas no Templo Submerso guarda o segredo para um tesouro ainda maior.\n\nDo altar principal, consegui ativar uma série de luzes. Acho que se tu descobrir o que essas luzes significam, vai conseguir revelar seja lá quais forem os segredos que estejam malocados nesta sala.\n\nMas camarão que dorme a onda leva, <name>. Há um grande mal nesse templo. E nenhum bem muito valioso ia ficar dando bobeira, sem estar muito bem guardado...O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nUrna Hakkari"},
[3448] = {title = "Passando o fardo", objective = "Fale com Timor em Altaforja.", description = "Recentemente, recebi a responsabilidade de investigar Azshara — uma área outrora tribonita que os elfos chamavam de lar. Isso na época em que eles praticavam magia. Hoje em dia o lugar está mais para perigoso do que bonito, tchê, mas isso não nos impede de revirar cada pedra atrás de respostas sobre nosso passado.\n\nUm colega meu, chamado Timor, se ofereceu para me ajudar a investigar a área, já que tem um interesse particular nisso.\n\nEle é dono de uma pequena propriedade à direita do Salão dos Mistérios, aqui na cidade.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3449] = {title = "Runas arcanas", objective = "Usando o Kit de Desenho, faça decalques da Runa de Beth'Amara, da Runa de Jin'yael, da Runa de Markri e da Runa de Sael'hai antes de ir para a pequena ilha ao largo da península meridional de Azshara e sinalizar para o Piloto Xisto Acendefase buscá-los.", description = "A magia um dia já foi reverenciada como a própria Eluna por alguns elfos, tchê. Alguns de seus magos mais poderosos criaram runas e tabuletas citando regras do arcano e informações sobre suas descobertas. Mas eles acabaram deixando isso de lado, passando a ponderar em lugares públicos.\n\nEu adoraria ter decalques dessas runas das Ruínas de Eldarath. Tenho um kit para tu fazer os decalques, mas para isso terá de encontrá-las. Mas bah, tenha cuidado, gur<i/ia>, a Liga dos Exploradores não é a única interessada em Eldarath.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3450] = {title = "Uma coleta tranquila", objective = "Fale com Xisto Acendefase em Altaforja.", description = "Eu quero os decalques entregues na minha torre, mas tu não precisas te atucanar de ir até lá. Vou enviá-los de Azshara via aérea.\n\nXisto Acendefase me deve um favor ou dois e em breve estará por aqui, tchê — ele patrulha a costa. Fale com ele antes de ir embora. Ele pode te dar um sinalizador para chamá-lo. Quando tiveres os decalques, gur<i/ia>, vá até o local combinado com Xisto e os entregue para ele levá-los até a minha torre em segurança. Xisto deve estar no hangar na Ala Militar.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3451] = {title = "Me chama que eu vou!", objective = "Fale com Xisto Acendefase e consiga uma Pistola Sinalizadora Modelo Padrão com ele em Altaforja.", description = "Ah, então foi Timor quem te mandou, gur<i/ia>? Sim, devo a ele um ou outro favor. O que foi? Bah, aquele tinhoso. Sim, posso ajudá-lo com isso. Então tu vai precisar de um sinalizador e um local... ah, mas isso é mais tranquilo que água de poço.\n\nHá uma pequena ilha ao largo da península meridional de Azshara, onde fizemos uma plataforma de pouso. Leve este sinalizador, e use lá. Verei seu sinal e vou até o local pegar... seja lá o que for que ele queira, tchê.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nPistola Sinalizadora Modelo Padrão"},
[3452] = {title = "O invólucro da chama", objective = "Encontre e leve um Símbolo de Ragnaros para Kalaran Finalâmina.", description = "Precisamos de uma peça final para formar a tocha.\n\nNas montanhas a noroeste, você encontrará um grande acampamento de Martelo do Crepúsculo. Há rumores de que eles carregam símbolos e ídolos que prestam homenagem ao seu deus. Esses símbolos e ídolos poderiam ter encantamento suficiente para formar um invólucro mágico. Traga-me um único símbolo de Ragnaros, e tentarei extrair a magia do item para criar o invólucro.", progress = "Sem uma fonte adequada de energia arcana, a Tocha da Retribuição não pode ser concluída.", completion = "O símbolo irradia magia sombria... perfeitamente adequada para a nossa tarefa.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[3453] = {title = "A Tocha da Retribuição", objective = "Espere enquanto Kalaran Finalâmina termina de preparar a Tocha da Retribuição.", description = "Espere um pouco enquanto eu junto as peças e aplico os encantamentos necessários.", progress = nil, completion = "Você precisa cumprir uma última tarefa.", rewards = "Você receberá:\n\n"},
[3454] = {title = "A Tocha da Retribuição", objective = "Pegue a Tocha da Retribuição.", description = "A tocha agora deve estar vinculada ao dono. Pegue a tocha, <name>.", progress = nil, completion = "Você pega a tocha com as duas mãos. Ondas de raiva e ódio pulsam através do seu ser.", rewards = "Você receberá:\n\nTocha da Retribuição"},
[3461] = {title = "Fale novamente com Timor", objective = "Devolva o Kit de Desenho de Timor a ele em Altaforja.", description = "Timor não me deu nada para te pagar, então sugiro que tu traga o assunto à baila com ele quando voltar a Altaforja. Tenho certeza de que ele ainda estará onde mora, na Ala Mística. Vá até lá e não o deixe esconder o leite: garanta que ele te pague o que é teu de direito, gur<i/ia>. Este lugar não é mole, não... isso é certo.\n\nMas obrigado novamente por ajudá-lo, tchê, e boa sorte.\n\nAh, e não preciso do kit de desenho. Por que você não o leva de volta para Timor quando for vê-lo?Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nGrevas de Ferreiro de AçoOrbe do Feitiço Craniano\n\nVocê também receberá:\n\n"},
[3462] = {title = "Escudeiro Maltraque", objective = "Fale com o Escudeiro Maltraque.", description = "Fale com meu enviado, o Escudeiro Maltraque. Ele irá instruí-l<o/a> quanto ao uso da Tocha da Retribuição.", progress = nil, completion = "Então, vai fazer o ataque?", rewards = "Você receberá:\n\n"},
[3463] = {title = "Taca fogo neles!", objective = "Incendeie as Torres de Sentinela Norte, Sul, Leste e Oeste usando a Tocha da Retribuição dentro de cada prédio.", description = "Ouça bem, <name>. Embora as quatro torres nunca possam ser destruídas, você pode usar a Tocha da Retribuição para incendiá-las por um longo período de tempo: um incêndio que nenhum elemento ou força seja capaz de controlar.\n\nEnquanto estiverem em chamas, nenhum guarda ou unidade de sentinela poderá ocupar os postos de vigia, então a primeira linha de defesa deles estará temporariamente anulada.\n\nVocê deve entrar em cada torre e usar a Tocha da Retribuição dentro das fortificações deles. Cuidado com as unidades de sentinela de elite dos Ferro Negro, <name>.", progress = nil, completion = "Que glória! Não é uma glória, mestre?", rewards = "Você vai poder escolher uma dessas recompensas:\n\nPerneiras da RevoadaCimeira da Chama DracônicaMachado do Draco Ebâneo\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[3481] = {title = "Berloques...", objective = "Abra o baú.", description = "O baú parece trancado.", progress = nil, completion = "O baú se abre ao seu toque. Quanto queijo será que cabe ali? Só há um jeito de saber.", rewards = "Você receberá:\n\nTesouro da Revoada Negra"},
[3482] = {title = "A Caixa Preta Esburacada", objective = "Leve essa caixa para Kravel.", description = "Esta é a versão da Horda da missão da caixa preta.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3483] = {title = "Me chama que eu vou!", objective = "Ah, outro sinalizador, é? Isso eu sei fazer.\n\nE não se esqueça: na ponta sul de Azshara. É uma ilhotinha que foi transformada em plataforma de pouso. Não dá para não ver se você estiver no lugar certo.", description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nPistola Sinalizadora Modelo Padrão"},
[3501] = {title = "Cada pedacinho conta", objective = "Leve Fragmentos Imperfeitos de Draenetista para Kum'isha e ganhe uma recompensa por cada fragmento entregue.", description = "Embora você possa jamais encontrar uma esfera perfeita de draenetista, sem dúvida encontrará vários fragmentos imperfeitos de draenetista em suas explorações.\n\nConquanto haja dúvidas quanto à capacidade de Kum'isha de usar os fragmentos imperfeitos para voltar para casa, esses pedaços podem servir a outros propósitos úteis para os draeneis.\n\nTraga-me qualquer fragmento imperfeito de draenetista e eu <o/a> recompensarei.", progress = "E então? Teve sorte na exploração?", completion = "Você me enoja, &lt;race&gt;. Um homem crescido desses babando por uma pilha de lixo? Lastimável! Pegue a sua recompensa e saia da minha frente.", rewards = "Você receberá:\n\nSucata do Kum'isha"},
[3502] = {title = "O lixo de um draenei...", objective = "Leve Fragmentos Imperfeitos de Draenetista para Kum'isha. Qualquer monstro da Barreira do Inferno pode estar carregando um fragmento desses.", description = "Ora, depois dessa demonstração de ganância, eu me sinto até enjoado.\n\nEnfim, não vou me ater às suas taras materiais.\n\nPara cada draenetista imperfeita que você me trouxer, eu te darei mais tranqueira em troca. A minha pilha de lixo só diminui, enquanto a sua só aumenta. Como dizemos em Draenor: \"todo mundo sai ganhando\".", progress = "E aí? Draenetista! Está lembrado?\n\n<Kum'isha cerra o punho e dá uns cascudos em você.>\n\nAlô-ô! Tem alguém em casa?", completion = "Você me enoja, &lt;race&gt;. Um homem crescido desses babando por uma pilha de lixo? Lastimável! Pegue a sua recompensa e saia da minha frente.\n\nSoa familiar? Deveria, porque digo isso mil vezes por dia.\n\nArgh, como eu odeio esse mundo.", rewards = "Você receberá:\n\nSucata do Kum'isha"},
[3503] = {title = "Chega de nhenhenhém!", objective = "Já houve um tempo em que eu podia exigir qualquer coisa daqueles que desejavam uma audiência com meu mestre. Mas agora basta qualquer um me pedir, e eu despacho vocês até ele.\n\nIsso me irrita, mas eu não questiono as ordens. Minha única diversão é fazer pouco das roupas ridículas que você usa, ou da expressão patética no seu rosto. Mas mesmo isso me entedia...\n\nE esse nhenhenhém também! Quer encontrar meu mestre ou não?", description = nil, progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nTeleportar para Torre de Azshara"},
[3504] = {title = "Traição", objective = "Entregue a Carta Lacrada para Ag'tor a Ag'tor Punho de Sangue, em Azshara.", description = "Traíra! É assim que eu a chamo, e ela jamais passará disso!\n\nPosso ter cometido um grave erro, <name>. Confiei em uma elfa sangrenta!\n\nEla disse que em Azshara haveria grandes riquezas... itens poderosos. Era só mandar alguns guerreiros com ela para protegê-la, e eu seria recompensado. E bom, olha quem foi feito de trouxa... Ela matou todos eles.\n\nEu quero vingança, <name>, mas não tenho como mandar mais ninguém. Fale com Ag'tor, em Valormok, ao norte do Assentamento Haldarr. Ele lhe contará o resto.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3505] = {title = "Traição", objective = "Mate 10 Recuperadores Sanguinélficos e 10 Topógrafos Sanguinélficos antes de procurar o acampamento da Maga Rimtori. Quando localizá-lo, procure algo que possa ser importante para ela e possa ser usado para forçá-la a lutar.", description = "Como eu pensava.\n\nComeçaremos nossa cruzada contra os empregados dela nos penhascos no sul da península setentrional de Azshara. Mate-os de forma furiosa e sangrenta. Deixe que eles saibam que traíram os aliados errados.\n\nNão se preocupe com ela ainda. Ela passa a maior parte do tempo com os próprios guarda-costas, prospectando Azshara. Seria prudente, depois de matar os empregados, encontrar o acampamento dela. Fazer o reconhecimento de onde podemos atacá-la seria muito bom. Talvez haja algo que possamos usar para provocá-la.", progress = nil, completion = "Você começa a folhear um pouco e chega a uma página marcada com uma fita fina de seda preta.\n\nEnquanto tenta decifrar a escrita, você vê um diagrama no pé de uma das páginas. Num relance você percebe que os cristais, o círculo e mesmo alguns outros componentes no chão estão dispostos de forma semelhante ao diagrama.", rewards = "Você receberá:\n\n"},
[3506] = {title = "Traído", objective = "Destrua o cristal de foco arcano preso à Maga Rimtori e mate-a. Depois, leve a Cabeça da Maga Rimtori para Ag'tor Punho de Sangue em Azshara.", description = "Mais alguns instantes pensando e novas ideias surgem. Uma delas traz um sorriso ao seu rosto: talvez a destruição dos cristais perturbe o elfo sangrento responsável pelo massacre de tantos orcs e pela traição de Belgrom.\n\nVocê continua a decifrar a escrita e as imagens e compreende então que um dos cristais está preso ao criador do círculo. Os outros estão presos a algo muito mais sinistro.\n\nVocê fecha o livro e começa a decidir qual cristal destruir.", progress = "Como está indo o massacre dos traidores sanguinélficos, <name>? Fez algum progresso?\n\nEspero que você seja forte o suficiente pra pôr aquela desgraçada de joelhos! Ela merece uma morte lenta e dolorosa por tudo o que fez!", completion = "AHÁ! Incrível!\n\nVocê conseguiu! Belgrom ficará satisfeito! Finalmente os espíritos de nossos guerreiros poderão descansar. Obrigado, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[3507] = {title = "Traição", objective = "Fale com Belgrom Pedramalho em Orgrimmar.", description = "Fale novamente com o Belgrom em Orgrimmar. Ele vai querer ver pessoalmente a cabeça dela. Voltaremos para lá em pouco tempo.\n\nAh, que dia bom! Muito obrigado pela sua ajuda, <name>.", progress = "Eu lembro de você, <class>. Pedi que você fosse a Azshara destruir aquela elfa que me traiu! Parece que foi luas e luas atrás. Não me diga que Ag'tor e os outros também estão mortos.", completion = "HAHA! Olha pra você agora, elfa!\n\nObrigado, &lt;name&gt;. Do fundo do coração. Hoje é mesmo um belo dia!\n\nEu daria um reino a você se pudesse! Mas talvez você se contente com isso aqui.\n\n\n\nHaha! Elfa sangrenta estúpida. Olha você agora... num saco de juta! Sem corpo! Você jamais deveria ter traído Belgrom!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nOrbe de LumepedraMartelo de Belgrom\n\nVocê também receberá:\n\n"},
[3508] = {title = "Quebrando a proteção", objective = "Espere Loramus para completar o feitiço de iluminação.", description = "Você é temerári<o/a>, <name>. É uma qualidade admirável do seu povo, entrar em batalha às cegas, com pouca ou nenhuma chance de sucesso. Eu <o/a> ajudarei, mesmo que seja só para ver a queda de um dos lacaios de Razelikh.\n\nQuebrar a proteção dos subordinados de Razelikh levará tempo e exigirá a aquisição de artefatos raros.\n\nNão se mova. Primeiro eu preciso realizar o ritual da iluminação. A informação que eu obtiver me ajudará a forjar armas de grande poder.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3509] = {title = "O nome da fera", objective = "Fale com o Lorde Arkkoroc em Azshara. comece sua busca na beira da água para encontrar o Templo de Arkkoran.", description = "Há só um jeito de ferir o Lorde-demônio e seus lacaios. Uma arma poderosa precisa ser forjada usando o verdadeiro nome dele.\n\nQuando uma arma assim é criada, o demônio e todos os subordinados dele ficam vulneráveis.\n\nUma divindade menor reside em Azshara: Lorde Arkkoroc. Você terá que buscá-lo, mas fique sabendo, os servos dele tentarão destruir você a qualquer custo, se souberem da sua presença.\n\nSem dúvida, você terá que pagar um preço por essa informação.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3510] = {title = "O nome da fera", objective = "Mate Hetaera e leve a Cabeça Ensanguentada de Hetaera, a Cabeça Arrebentada de Hetaera e a Cabeça Ferida de Hetaera para o Lorde Arkkoroc.", description = "Ela mata os meus filhos, mortal. Aquela que se autoproclamou soberana dos oceanos. Uma falsa deusa, nada mais. Acabe com ela e me traga a cabeça de todas elas.\n\nCom isso, você receberá o nome que tanto procura e outra coisa de mesma importância.\n\nEla fica perto do ninho... e se chama Hetaera.\n\n<Lorde Arkkoroc aponta para o sudoeste.>\n\nVá logo!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[3511] = {title = "O nome da fera", objective = "Leve a têmpera para Loramus Talípedes.", description = "Como prometido, revelarei o nome do lorde-demônio, Razelikh, o Profanador. Primeiro, porém, preciso preparar algo de suma importância para sua jornada.\n\nA arma que você deseja criar precisa ser temperada. Um momento, para eu drenar o sangue dessas cabeças.\n\n<O Lorde Arkkoroc espreme as cabeças entre os punhos gigantescos, drenando o sangue.>\n\nLeve a têmpera ao Loramus, junto com o nome da fera: Rakh'likh.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3512] = {title = "Nas palavras do próprio Erânicos", objective = "Viaje até o leste de Hibérnia e fale com Umbranse, o Médium.", description = "<name>, nosso primeiro passo será desenvolver um método para falar com Erânicos por meio da gema. Ele jaz em um estado precário entre o sono e a morte, envolto em uma trama de pesadelos. Embora a essência que você me deu esteja severamente maculada, há uma chance de conseguirmos contactá-lo.\n\nVá às terras gélidas de Hibérnia e procure um de nossos aliados mortais, Umbranse, o Médium. Ele é um humano que fala pelos mortos. Embora Erânicos não esteja morto de verdade, o humano pode conseguir nos ajudar.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3513] = {title = "O pergaminho rúnico", objective = "Leve o Pergaminho Rúnico até a torre de guarda norte nos Sertões.", description = "O pergaminho de aparência oficial está bem amarrado com uma fita prateada. Você tem certeza de que alguém se interessaria em ver isso, mas quem?\n\nHá um posto avançado perto da fronteira norte do Vale Gris e, se a informação nesse pergaminho tiver a ver com os elfos noturnos, lá é o melhor lugar para o qual levá-lo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3514] = {title = "Presença da Horda", objective = "Mate a líder do Retiro do Gadanho, Rynthariel, a Guardiã da Chave.", description = "Esse pergaminho pertence à ardilosa dríade Rynthariel, a Guardiã da Chave do Retiro do Gadanho, no Morro das Torres de Pedra.\n\nEla não vai mais ouvir do mensageiro de quem você saqueou isso. Ha! Mas precisamos manter nossas posições aqui. A Horda não tolerará as vanguardeiras dela por aqui.\n\n<name>, vá ao Retiro do Gadanho. Mate Rynthariel e deixe um sinal à vista de todos. Leve essa bandeira da Horda, <name>. Hasteie-a no Retiro do Gadanho para que eles não se descuidem e mandem outro mensageiro cruzar nosso território de novo.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas AbretrilhasLuvas de Trançajuta\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[3517] = {title = "Roubando conhecimento", objective = "Encontre a Tabuleta de Beth'Amara, a Tabuleta de Jin'yael, a Tabuleta de Markri e a Tabuleta de Sael'hai. Depois volte a falar com Jediga, em Azshara.", description = "Eu admito, <name>, aproveitei bem minha estadia em Azshara. A punição de Belgrom me beneficiou também.\n\nAntes de partir, fiz algumas pesquisas, e me surpreendi com a quantidade de itens valiosos espalhados por aqui.\n\nEu lhe contarei mais sobre meus negócios e lhe darei uma parte dos lucros se você me ajudar a coletar alguns desses itens.\n\nPreciso das tabuletas que estão espalhadas pelas Ruínas de Eldarath, a leste daqui. As quatro tabuletas levam os nomes dos teurgos Altaneiros. Traga-as a mim e poderemos conversar mais.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3518] = {title = "Entrega para Magatha", objective = "Leve a Tabuleta de Beth'Amara para Magatha no Penhasco do Trovão.", description = "A primeira tabuleta vai para uma taurena chamada Magatha. Ela é uma das anciãs do Penhasco do Trovão. E é uma das proponentes da aliança entre os taurens e os Renegados. Ela, mais que todos entre os anciãos, sente que os taurens podem ajudar os Renegados a tornar-se humanos novamente.\n\nLeve a Tabuleta de Beth'Amara para ela. A tabuleta pode ser o que ela precisa para encontrar um modo de ajudar os Renegados... ou assim ela diz.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3519] = {title = "Um amigo em apuros", objective = "Fale com Dirânia Pratafulge em Umbravale.", description = "Uuuugh... Fui picado em cheio por uma aranha chamada Githyiss, a Torpe, quando estava explorando a caverna das aranhas aqui perto. Com certeza fui seriamente envenenado, e você precisa me socorrer.\n\nPor favor, conte à Dirânia Pratafulge. Ela conseguirá me ajudar.\n\nRápido... Já estou... ficando... tonto...", progress = nil, completion = "Oh, não! Estava me perguntando por que não tinha visto o Iverron hoje. Eu sempre falei para ele tomar cuidado com as aranhas...", rewards = "Você receberá:\n\n"},
[3520] = {title = "Espíritos guinchadores", objective = "Capture os espíritos de 3 guinchadores em Feralas, e depois fale novamente com Yeh'kinya no Porto de Bondebico.", description = "Busco os espíritos dos guinchadores do vale de Feralas. Eles são primos de um deus antigo, muito antigo, e seus espíritos estão ligados a ele.\n\nPegue essa varinha de espinheiro e vá a Feralas. Encontre e derrote guinchadores do vale no sul e no centro de Feralas, e depois balance a varinha sobre os corpos. Isso deve coagir os espíritos a se revelarem. Toque o espírito e colete a essência.\n\nVolte com meu espinheiro e a essência coletada, e eu lhe contarei mais sobre o deus antigo do qual falei.", progress = "Você coletou a essência dos espíritos guinchadores, <name>?", completion = "Você se saiu bem, &lt;name&gt;. Agora pode ser a hora de revelar mais...\n\nHá uma profecia antiga dos trolls na qual Hakkar, o Esfolador de Almas, um deus morto, um dia voltará a esse mundo. Alguns dizem que a chegada de Hakkar trará alegria e glória à trollidade, enquanto outros acreditam que a ressurreição dele marcará o fim de todas as coisas.\n\nEu não acredito nesse mito, mas sei que a essência de Hakkar contém grande poder e que esse poder deve ser negado aos que abusariam dele.", rewards = "Você receberá:\n\n"},
[3521] = {title = "O antídoto de Iverron", objective = "Colete 7 Cogumelos Jacinto, 4 Lírios Lunapétala e 1 Linfa de Lenhateia para Dirânia Pratafulge em Umbravale.", description = "Pode ser possível ajudar Iverron. Eu conheço um antídoto que deve ajudar com o veneno. Porém... ele exige alguns ingredientes.\n\nVou precisar de cogumelos jacinto. Você pode colhê-los embaixo das árvores, ou encontrá-los com os capetas ao sul daqui. Eles parecem apreciá-los também. E eu também precisarei de pétalas de lunapétala, que só crescem às margens de pequenos lagos.\n\nO último ingrediente pode se provar ser o mais difícil. Das próprias aranhas que envenenaram Iverron, extraia a linfa das lenhateia.", progress = "Por favor, colete os ingredientes, <name>. Iverron precisa da sua ajuda.", completion = "Graças a Eluna você trouxe esses ingredientes tão rápido!\n\nVou preparar o antídoto num minuto.", rewards = "Você receberá:\n\n"},
[3522] = {title = "O antídoto de Iverron", objective = "Leve o Antídoto de Iverron para Iverron antes que o tempo se esgote. Iverron está na caverna ao norte.", description = "O antídoto está pronto, <name>. Por favor, cuide para que o Iverron beba.\n\nTem uma coisa que você precisa saber: o antídoto... só dura 5 minutos. Você precisa chegar até ele a tempo.\n\nVá célere, <name>.", progress = "Ah... <name>, estou tão feliz por você ter voltado!", completion = "O que é isso, &lt;name&gt;?\n\nAh, eu sabia que a Dirânia conseguiria me ajudar!\n\n\n\nEstou sempre me encrencando, e a Dirânia... bom... ela sempre me ajuda a sair dessas encrencas.\n\nEstou me sentindo muito melhor, mas acho que vou me sentar por aqui mais um pouco, até me sentir bem de verdade. E... eu meio que estou esperando você abrir caminho para mim por entre essas lenhateias, sabe...", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCulotes Semente de JunçaColete de Malha de Tronco\n\nVocê também receberá:\n\n"},
[3523] = {title = "O Flagelo do Urzal", objective = "Se você concordar em ajudar Belnistrasz, fale novamente com ele e devolva a pedra de juramento que ele lhe deu.", description = "Excepcional! Meu nome é Belnistrasz, e eu lhe agradeço por me resgatar. Já me dera por morto... ou pior, jantar dessas pestes do Flagelo. Sim. Eu disse Flagelo. Eles fizeram algum acordo com os javatuscos, e o resultado é esse pesadelo aqui.\n\nEscute... Em vez de simplesmente fugir, eu quero fazer diferença aqui, por todos nós. Você estaria dispost<o/a> a me ajudar? Deve ser perigoso, mas você parece ser do tipo que não se preocupa muito com isso.", progress = "Não sou muito de política. Não ligo se você é Horda ou Aliança. Estou mais interessado em esmagar ameaças graves e diretas a todas as raças... e temos uma bem grande aqui.\n\nSe você estiver disposto a ser um herói de verdade para todos os povos, entregue-me a pedra do juramento que lhe dei em sinal de comprometimento. Eu não... sou daqui. Pedras de juramento são importantes para meu povo para o selamento de acordos.", completion = "Fantástico! Bem que achei que você fazia o tipo heroico. Temos muito trabalho pela frente, &lt;name&gt;, não vou mentir. Se nos concentrarmos em nossa tarefa, devemos conseguir.\n\nAgora permita-me pôr você a par do que vem acontecendo.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[3524] = {title = "Deixa a água me levar", objective = "Obtenha Ossos de Criaturas Marinhas da criatura marinha encalhada logo ao sul Auberdine e leve para Gwennyth Bly'Leggonde em Auberdine.", description = "Sabe-se que criaturas marinhas majestosas se lançam de encontro à Costa Negra, onde terminam encalhadas até a morte. Ultimamente essas feras têm aparecido em número cada vez maior. O Templo da Lua me enviou para investigar, mas a presença de murlocs na água dificultou minha pesquisa.\n\nHá uma criatura gigante que encalhou logo ao sul de Auberdine, mas ela está cercada por aqueles murlocs Brumagris imundos. Você poderia ir até lá e extrair ossos da criatura para nosso estudo?", progress = "Só recentemente essas criaturas começaram a aparecer na Costa Negra em número tão alarmante. Não consigo deixar de pensar que seja um mau agouro. Uma amostra dos ossos daquela criatura ao sul ajudaria Darnassus a avaliar a situação.", completion = "Espero que o murloc não tenha dado muito trabalho em coletar isto para nós! Vou colocar isso aqui no próximo hipogrifo para Darnassus. O Templo da Lua me deu alguns fundos a serem gastos com ajuda. Por favor, aceite uma parte com o nosso agradecimento.\n\nSeu sucesso aqui me encoraja a lhe oferecer uma chance de ajudar mais o Templo da Lua, se você tiver interesse.", rewards = "Você receberá:\n\n"},
[3525] = {title = "O ídolo nefando", objective = "Escolte Belnistrasz até o ídolo dos javatuscos no Urzal dos Mortos.\n\nProteja Belnistrasz enquanto ele realiza o ritual para desativar o ídolo.", description = "Próximo à entrada fica um ídolo que funciona como um forno macabro para os javatuscos... eles literalmente comem as pessoas raptadas nos Sertões. Eu consigo desativar o ídolo e selá-lo para que eles não possam mais usá-lo, mas para isso preciso de sua ajuda. Meu plano é: seja minha escolta. Quando chegarmos, começarei o ritual e mal conseguirei me comunicar com você, e muito menos me defender. Proteja-me enquanto eu selo o ídolo. Se eles me abaterem será o fim para nós.\n\nVocê está comigo?", progress = nil, completion = "Em sua súbita partida, Belnistrasz deixou para trás um braseiro em chamas. Olhando para as chamas, a imagem de um dragão vermelho com asas enormes preenche sua mente. Uma versão mais gutural da voz de Belnistrasz fala diretamente com você.\n\n\"Deveras, mortais, vocês são os heróis que eu esperava que fossem. Graças a vocês a carnificina dessas feras é passado. Minha fé em mortais como vocês foi renovada. Aceitem essa recompensa digna de heróis.\"", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nAnel Dragogarra"},
[3526] = {title = "Engenharia goblínica", objective = "Se quiser aprender mais sobre Engenharia Goblínica, leve o Manual das Disciplinas de Engenharia a Nixx Molapinhão em Geringontzan.", description = "A engenharia em nível especializado divide-se em duas disciplinas: a gnômica e a goblínica. Obviamente, todos os grandes engenheiros optam pela goblínica. Aprenda a dominar o mundo usando explosivos poderosos de forma responsável e maquinários fantásticos!\n\nAqui, leia este manual. Se quiser aprender engenharia goblínica, leve-o a Nixx Molapinhão, em Geringontzan.  Lembre-se de que a escolha de uma disciplina é algo definitivo, impossível de mudar. Então é bom ter certeza absoluta de que é isso que você quer antes de concluir a tarefa de que ele lhe incumbirá.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3527] = {title = "A profecia de Mosh'aru", objective = "Leve a Primeira e a Segunda Tabuleta Mosh'Aru para Yeh'kinya, em Tanaris.", description = "A antiga profecia de Mosh'aru fala de uma forma de conter a essência do deus Hakkar. Ela foi escrita em duas tabuletas e levada para a cidade trólica de Zul'Farrak, a oeste de Geringontzan.\n\nTraga-me as tabuletas de Mosh'aru!\n\nA primeira tabuleta está em poder de Theka, o Mártir, um troll há muito falecido. Dizem que os que o perseguiram foram amaldiçoados, se tornaram escaravelhos e agora infestam seu santuário.\n\nA segunda tabuleta está com a hidromante Velratha, que vive perto do lago sagrado de Gahz'rilla.\n\nQuando estiver com as tabuletas, traga-as para mim.", progress = "Você conseguiu as tabuletas da profecia de Mosh'aru, <name>?\n\nQuando estiver com as tabuletas, eu as estudarei e aprenderei a conter a essência de Hakkar!", completion = "Ah, isso é bom. Posso fundir essas tabuletas para estudar seus segredos.\n\nMinha dívida para com você só cresce, &lt;name&gt;. E crescerá ainda mais.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[3528] = {title = "O deus Hakkar", objective = "Leve o Ovo de Hakkar Cheio para Yeh'kinya, em Tanaris.", description = "Agora é a hora de capturar a essência do avatar de Hakkar.\n\nLeve o Ovo de Hakkar ao Sacrário do Deus Caído, no Templo Submerso em Azeroth. Invoque o poder do ovo para provocar o deus morto.\n\nEle enviará os próprios lacaios contra você. Derrote os bebedores de sangue, tire seu sangue hakkari e depois use o sangue para extinguir as chamas eternas que contêm o espírito de Hakkar. Quando todas as chamas se extinguirem, o avatar de Hakkar entrará em nosso mundo.\n\nDerrote-o e coloque a essência dele dentro do Ovo de Hakkar.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nElmo de VanguardaPunhal de Força VitalDiadema Explodegema\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\nEssência de Hakkar"},
[3541] = {title = "Entrega para Sona'rima", objective = "Leve a Tabuleta de Jin'yael para Sona'rima em Orgrimmar.", description = "Eu tenho um receptador em Orgrimmar. Ele mora no Bazar. Ele é difícil de achar se você não souber onde procurar. Mas se você for olhar num dos edifícios mais altos de lá, não tem erro.\n\nSó me faça um favor: não saia por Kalimdor chamando o sujeito, divulgando onde ele está, nem o que ele faz. Seria ruim pra nós dois.\n\nEle nos pagará um preço mais que justo pela Tabuleta de Jin'yael. Não sei porque exatamente. Mas você devia ter visto como ele ficou empolgado quando soube onde estava a tabuleta.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3542] = {title = "Entrega para Andron Gante", objective = "Leve a Tabuleta de Markri para Andron Gante na Cidade Baixa.", description = "Meu próximo contato é Andron Gante. Ele geralmente fica entocado na Cidade Baixa... perto do Boticarium.\n\nEle foi um pouco mais língua-frouxa a respeito do que ele queria com a tabuleta. Parece que certos magos da Cidade Baixa estão tentando estudar alguns dos feitiços arcanos que os Altaneiros usavam. Eles acham que essa tabuleta vai ajudar. Markri costumava evocar criaturas, especialmente elementais.\n\nLeve a Tabuleta de Markri para Andron e volte aqui para pegar sua parte da recompensa.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3561] = {title = "Entrega para o Arquimago Tauriel", objective = "Leve a Tabuleta de Sael'hai para o Arquimago Tauriel em Azshara.", description = "A última tabuleta vai pra alguém muito perigoso, <name>. Escute o que digo: de todo mundo com quem mandei você se encontrar, ele é em quem menos confio.\n\nO Arquimago Tauriel mora numa torre nas colinas de Azshara. Ele é um humano, mas não age como nenhum humano que eu já tenha encontrado... ou matado. Ele emana poder, e eu não ouso perguntar o que ele planeja fazer com a tabuleta.\n\nEle veio me procurar, mas me disse que seu guarda-costas, Sanath, me ajudaria a chegar à torre quando eu estivesse pronta para encontrá-lo. Vá para o norte para encontrar Sanath.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3562] = {title = "O pagamento de Magatha para Jediga", objective = "Entregue o Bilhete de Magatha para Jediga em Azshara.", description = "Leve esse bilhete para Jediga. Diga que o pagamento dela estará esperando quando ela voltar. E diga que estou satisfeita com o trabalho dela.\n\nVá logo, <name>. Diga a sua mestra que eu a esperarei aqui.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3563] = {title = "O pagamento de Sona'rima para Jediga", objective = "Entregue o Bilhete de Sona'rima para Jediga, em Azshara.", description = "Entrega essa carta pra Jediga. Diz pra ela que curti fazer negócio, e que ela trabalha bem.\n\nDiz pra ela voltar pra Orgrimmar logo, que o Sona'rima cuida dela direitinho...\n\nDiz isso, que em breve eu posso ter mais serviço pra você.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3564] = {title = "O pagamento de Andron para Jediga", objective = "Entregue o Bilhete de Andron para Jediga, em Azshara.", description = "Ó aqui... leva... <hic> levisshaqui pra Jediga.... <hic> Boa shorte!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3565] = {title = "O pagamento de Tauriel para Jediga", objective = "Entregue o Bilhete de Tauriel para Jediga, em Azshara.", description = "O que é? Ah, sim, seu pagamento. Claro.\n\nTome isto. E pode ir embora. Tenho certeza de que Jediga está esperando impacientemente pelo seu retorno.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3566] = {title = "Erga-se, Obsidião!", objective = "Mate Obsidião e Latórico, o Negro, e então fale novamente com Tórius em Altaforja com o Coração de Obsidião e a Cabeça de Latórico, o Negro.", description = "Era... era... era uma armadilha.\n\nMe... perdoe.\n\nMate o desgraçado... Mate por nós... pelo Tórius...Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nCoxotes do CenturiãoCapacete de LordrecAnel da Fortitude\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[3567] = {title = "Até o topo", objective = nil, description = nil, progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nTeleporte para os Colossos Gêmeos"},
[3568] = {title = "Corrupção líquida", objective = "Encha as 4 Ampolas Vazias nas poças ao longo da costa das Ruínas de Eldarath em Azshara. Em seguida, volte a falar com o Químico Arnaldo.", description = "Está vendo essa taurena atrás de mim? A que parece que vai vomitar a qualquer instante? Ela esteve em Azshara não faz muito tempo. Ela diz que foi ferida por um grupo de nagas enquanto atravessava as Ruínas de Eldarath.\n\nDepois de tê-las derrotado, ela parou para descansar e bebeu a água de uma poça próxima. Agora ela está sofrendo com náuseas... dentre outros sintomas.\n\nQuero saber o que foi que ela bebeu. Tome esta caixa. Dentro dela há ampolas etiquetadas. Encha cada uma em uma poça diferente e volte aqui.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3569] = {title = "Corrupção líquida", objective = "Dê o Elixir do Arnaldo para Thersa Cantéolo, na Cidade Baixa.", description = "Dê isto para Thersa. Talvez isto a cure... talvez não. Só vamos saber depois que ela tiver bebido o elixir.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3570] = {title = "Corrupção líquida", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3601] = {title = "Kam'arawn é a mãe!", objective = "Vasculhe o Acampamento Talassiano e encontre os seguintes itens de Kam'arawn: A Bússola, o Telescópio, o Frango Recheado e a Chinforímpola. Depois, devolva os itens para Kam'arawn em Azsahra.", description = "Kam'arawn é a mãe! Você sabe o que \"Kam'arawn\" significa na língua sanguinélfica, <name>? \"Ratinho\"... ou alguma coisa assim. Aqueles valentões!\n\nMe contrataram para ajudar a vasculhar a área em busca de um tesouro perdido. Quando eles falaram em tesouro e me disseram quanto iam me pagar eu aceitei na hora. Mas daí É CLARO que eles me enganaram e tentaram me matar! EU!\n\nPra você ver que não dá pra confiar em elfos sangrentos!\n\nO problema é que eles ainda estão com meu equipamento, e eu não consigo pegar de volta. Me ajuda?", progress = "Elfos sangrentos fedidos. Tô te dizendo, se eu fosse assim grande e forte que nem você, eu ia lá eu mesmo e dava uma lição neles! Mas vou ter que me contentar em pagar você pra ir lá fazer isso.", completion = "Haha! Você mostrou pra eles, não mostrou?\n\nÉ isso aí! Isso vai ensinar os elfos sangrentos a não trair parceiros comerciais... especialmente alguém tão leal feito um mercador goblin! Nah, elfos sangrentos...!\n\nBom, o mínimo que posso fazer é pagar você... como eu prometi.\n\nAqui está. Pode vir a ser útil.", rewards = "Você receberá:\n\nGranada de Fragmentação M73"},
[3602] = {title = "Azsharita", objective = "Obtenha 20 Azsharitas Cristalizadas na região sul de Azshara. Use o Canisvil para farejar onde estão os depósitos que foram cuidadosamente escondidos.\n\nFale novamente com Loramus quando tiver cumprido essa tarefa.", description = "Os antigos gigantes dos penhascos do sul de Azshara se alimentam de rochas e edifícios. O excremento resultante é um cristal altamente maleável e extremamente forte chamado azsharita.\n\nAs formações estão espalhadas pelo arco sul, mas poucas são exploradas graças à natureza violenta dos gigantes.\n\nVocê tem que seguir para o sul e extrair azsharita suficiente para a criação do corpo da arma. Meus canisvis ajudarão na extração do cristal.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nBilhete Cristalizado"},
[3621] = {title = "A destruição vil", objective = "Leve o Carregamento para Galvan, o Antigo, na Selva do Espinhaço.", description = "As armas estão prontas para serem forjadas. Você precisa levar a azsharita temperada para alguém com a habilidade necessária para controlar a energia vil.\n\nNa Selva do Espinhaço vive um ferreiro assim. Galvan, o Antigo, líder da Ordem do Mithril, fará armas com as suas especificações. O acampamento dele fica entre Zul'Gurub e a gruta de Mosh'Ogg.\n\nQue sua fé e determinação <o/a> guiem nesse ordálio. Se você falhar, saiba que seu deus <o/a> abençoa, mortal.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3625] = {title = "Armamento vil de aszharita encantada", objective = "Aguarde enquanto Galvan termina de forjar o armamento.", description = "<name>, vai levar tempo para criar cada arma.\n\nAlém do mais, você só poderá usar o poder da Maldição Vil uma vez a cada três minutos. As propriedades debilitantes contra Razelikh, o Profanador, e seus lacaios podem ter durações variáveis. É por isso que você precisa usar a Maldição Vil na hora certa!\n\nMuito bem... Deixe-me preparar a sua arma, <name>.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nAdaga Destruição Vil de Azsharita EncantadaCajado Destruição Vil de Azsharita EncantadaEspada Destruição Vil de Azsharita Encantada\n\nVocê também receberá:\n\n"},
[3626] = {title = "Retorno à Barreira do Inferno", objective = "Fale novamente com o Herói Caído da Horda.", description = "A hora da redenção se aproxima, <name>. Você precisa voltar à Barreira do Inferno com seu novo arsenal e falar com o Herói Caído da Horda para receber sua última missão.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3627] = {title = "Restaurando o amuleto estilhaçado", objective = "Mate Grol, o Destruidor, Lady Sevine e o Arquimago Allistarj. De seus corpos, pegue o Amuleto de Grol, o Amuleto de Sevine e o Amuleto de Allistarj.\n\nLembre-se de usar o Armamento Vil de Azsharita Encantada para enfraquecer a tríade, pois só assim eles podem ser derrotados... mas use-o com sabedoria.", description = "Para chegar ao Profanador, você terá que destruir a tríade de poder e recuperar as partes do amuleto. Quando estiver com as três partes, volte aqui para que eu lhe dê novas instruções.\n\nVá, <name>! Para a Barreira do Inferno! Sevine, Allistarj e Grol devem ser eliminados!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[3628] = {title = "Você é Rakh'likh, demônio", objective = "Destrua Razelikh, o Profanador, e leve o Chifre Decepado de Profanador para o Herói Caído da Horda. Você também terá que devolver a Proteção do Profanador para que o Herói Caído a destrua, impedindo-a de cair em mãos erradas.", description = "O covil de Razelikh fica no topo da montanha mais alta da Barreira do Inferno. O único jeito de chegar até lá é pelas runas de teleporte perto da montanha. Enquanto estiver com o amuleto, isso não será problema para você. Quando chegar ao covil, você terá que conjurar a fera. O amuleto é a chave, <name>. Use-o no Altar do Profanador.\n\nSe conseguir derrotá-lo, traga seu chifre para mim. Ele será um símbolo de esperança para todos que o virem... a prova de que heróis existem neste mundo assolado por guerras.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nColar do SantuárioBolsa de Pelego de DemônioSangue de Demônio"},
[3629] = {title = "Engenharia goblínica", objective = "Se quiser aprender mais sobre a Engenharia Goblínica, leve o Manual das Disciplinas de Engenharia até Nixx Molapinhão, em Geringontzan.", description = "O engenheiro perito pode escolher entre duas especializações: a engenharia gnômica e a goblínica. Só me dou ao trabalho de mencionar a engenharia goblínica porque sou um instrutor justo. Os goblins gostam de fazer coisas que explodem e quase nunca se importam com o que está no raio de impacto – mesmo que eles mesmos.\n\n<name>, leia este manual. Caso você se interesse em aprender como explodir a si <mesmo/mesma>, entregue o manual para Nixx Molapinhão, em Geringontzan. E não se esqueça: depois que você entra para uma especialização, é impossível mudar de escolha. Por isso, é bom ter certeza absoluta da sua opção antes de concluir a tarefa que ele lhe designar.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3630] = {title = "Engenharia gnômica", objective = "Se quiser saber mais sobre a Engenharia Gnômica, leve o Manual das Disciplinas de Engenharia até o Mestre-faz-tudo Superchispa em Altaforja.", description = "O engenheiro perito pode escolher entre duas especializações: a engenharia gnômica e a goblínica. E a engenharia gnômica é, de longe, a mais importante das duas, pois trata da construção de dispositivos e aparelhos que deixam a vida melhor!\n\n<name>, leia este manual. Se quiser saber mais sobre a engenharia gnômica, entregue o manual para o mestre-faz-tudo Superchispa em Altaforja. E não se esqueça: depois que você entra para uma especialização, é impossível mudar de escolha. Por isso, é bom ter certeza absoluta da sua opção antes de concluir a tarefa que o mestre-faz-tudo lhe designar.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3631] = {title = "Evocar Corcel Vil", objective = "Fale com Strahad Farsan nos Sertões.", description = "Hmpf. Eu acho que você não está <pronto/pronta>, mas a decisão não é minha. Strahad pensa diferente, e a decisão é dele... dessa vez.\n\nEle quer falar com você imediatamente. Você o encontrará no alto da Vila Catraca, nos Sertões, em uma pequena torre com os acólitos dele.\n\nNão se demore aqui.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3632] = {title = "Engenharia gnômica", objective = "Se quiser saber mais sobre a Engenharia Gnômica, leve o Manual das Disciplinas de Engenharia até o Mestre-faz-tudo Superchispa em Altaforja.", description = "O engenheiro perito pode escolher entre duas especializações: a engenharia gnômica e a goblínica. E a engenharia gnômica é, de longe, a mais importante das duas, pois trata da construção de dispositivos e aparelhos que deixam a vida melhor!\n\n<name>, leia este manual. Se quiser saber mais sobre a engenharia gnômica, entregue o manual ao mestre-faz-tudo Superchispa em Altaforja. E não se esqueça: depois que você entra para uma especialização, é impossível mudar de escolha. Por isso, é bom ter certeza absoluta da sua opção antes de concluir a tarefa que o mestre-faz-tudo lhe designar.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3633] = {title = "Engenharia goblínica", objective = "Se quiser aprender mais sobre a Engenharia Goblínica, leve o Manual das Disciplinas de Engenharia até Nixx Molapinhão, em Geringontzan.", description = "O engenheiro perito pode escolher entre duas especializações: a engenharia gnômica e a goblínica. É óbvio que os engenheiros brilhantes escolhem a engenharia goblínica. Aprenda a dominar o mundo por meio do uso calculado de explosivos de alta potência e de equipamentos fantásticos!\n\n<name>, leia este manual. Se quiser aprender sobre a engenharia goblínica, leve-o a Nixx Molapinhão, em Geringontzan. E não se esqueça: depois de escolher uma especialização, é impossível mudar de escolha. Por isso, é bom ter certeza absoluta da sua opção antes de concluir a tarefa que o Nixx lhe designar.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3634] = {title = "Engenharia gnômica", objective = "Se quiser saber mais sobre a Engenharia Gnômica, leve o Manual das Disciplinas de Engenharia até o Mestre-faz-tudo Superchispa, em Altaforja.", description = "O engenheiro perito pode escolher entre duas especializações: a engenharia gnômica e a goblínica. Por mais que eu ache uma enorme perda de tempo, é bom que você conheça a engenharia gnômica. Eles fazem itens e dispositivos que supostamente \"mudam o mundo\", mas quase nunca funcionam.\n\n<name>, leia este manual. Se você quiser aprender a fazer pesos de papel metálicos, entregue-o para o mestre-faz-tudo Superchispa em Altaforja. Não se esqueça de que a afiliação é permanente e impede que você se junte à outra disciplina. Pense bem na sua escolha!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3635] = {title = "Engenharia gnômica", objective = "Se quiser aprender mais sobre Engenharia Gnômica, leve o Manual das Disciplinas de Engenharia para Olhatorto Obnótico na Angra do Butim.", description = "Há dois tipos de engenharia em nível especializado: a gnômica e a goblínica. Particularmente acho uma perda de tempo, mas ainda assim você precisa estar ciente da especialização gnômica. Eles fabricam itens e engenhocas que – assim eles alegam – podem mudar o mundo, mas na verdade elas raramente funcionam.\n\nAqui, leia este manual. Se sua aspiração máxima é fabricar pesos de papel de metal, leve o manual para o gnomo Olhatorto Obnótico na Angra do Butim.  Lembre-se de que a escolha de uma disciplina é algo definitivo, impossível de mudar. Então é bom ter certeza absoluta de que é isso que você quer antes de concluir a tarefa de que ele lhe incumbirá.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3636] = {title = "Trazer a luz", objective = "O Arcebispo Benedito quer que você mate Amnennar, o Frigífero, no Urzal dos Mortos.", description = "O Flagelo é uma ameaça permanente à Aliança, coisa que você certamente sabe muito bem. Recentemente fizemos descobertas deveras peculiares, <name>.\n\nNos Sertões há um emaranhado horrendo de sarças gigantes chamado Urzal dos Mortos. Lá já foi terra de simples javatuscos, mas agora está claro que foi feito um pacto com o Flagelo, que gerou criaturas muito piores... Agora quem os governa é um lich, Amnennar, o Frigífero.\n\nEsteja do lado da Luz, <name>. Acabe com essa união maligna.", progress = nil, completion = "Obrigado, &lt;name&gt;. O Flagelo agora pensará duas vezes antes de tentar engrossar as fileiras de novo.\n\nAbençoado seja pela luz, &lt;name&gt;.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nEspada do VencedorTalismã de Brilhâmbar"},
[3637] = {title = "Engenharia gnômica", objective = "Se quiser aprender mais sobre Engenharia Gnômica, leve o Manual das Disciplinas de Engenharia para Olhatorto Obnótico na Angra do Butim.", description = "O engenheiro perito pode escolher entre duas especializações: a engenharia gnômica e a goblínica. Por mais que eu ache uma enorme perda de tempo, é bom que você conheça a engenharia gnômica. Eles fazem itens e dispositivos que supostamente \"mudam o mundo\", mas quase nunca funcionam.\n\n<name>, leia este manual. Se você quiser aprender a fazer pesos de papel metálicos, leve-o ao Olhatorto Obnótico na Angra do Butim. Não se esqueça de que a afiliação é permanente e impede que você se junte à outra disciplina. Pense bem!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3638] = {title = "O pacto de sigilo", objective = "Se você concordar em se tornar <um engenheiro goblínico/uma engenheira goblínica>, clique com o botão direito no Pacto de Sigilo. Em seguida, fale novamente com Nixx Molapinhão em Geringontzan.", description = "A engenharia goblínica é focada em usos práticos na busca de grandes lucros e alto poder explosivo! Nossos diagramas nos possibilitam a fabricação de bombas potentes e armamento poderoso. O manual que você leu trata disso.\n\nEle também trata do nosso voto de sigilo sobre nossos diagramas. Uma vez que você se torne partidári<o/a> da engenharia goblínica, não tem mais volta. Não se pode ter acesso nenhum à engenharia gnômica.\n\nSe você ainda assim desejar praticar a engenharia goblínica, assine este documento no qual você se compromete a manter sigilo até o fim de seus dias. Depois que acabar, fale comigo de novo. Pense bem, <name>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3639] = {title = "Mostrando trabalho", objective = "Traga 20 Bombas Grandes de Ferro, 20 Dinamites Maciças e 5 Ovelhas Explosivas para Nixx Molapinhão em Geringontzan.", description = "Como seu mentor, eu quero amostras do seu trabalho. Você é <um engenheiro habilidoso/uma engenheira habilidosa>, disso não há dúvida, <name>. O que eu espero fazer, no entanto, é analisá-las para encontrar maneiras de enfatizar as diretrizes goblínicas nos seus esforços futuros. Eu preciso do seguinte: vinte bombas de ferro grandes, vinte bananas de dinamite sólida e cinco ovelhas explosivas. Não esqueça de ativar as travas de segurança!\n\nEntregue os itens para mim e eu emitirei o seu cartão de engenheiro goblínico associado. Ele é necessário para acessar os treinadores de engenharia goblínica!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEngenharia Goblínica\n\nVocê receberá:\n\nCartão de Engenheiro Goblínico Associado"},
[3640] = {title = "O pacto de sigilo", objective = "Se concordar em se tornar <um engenheiro gnômico/uma engenheira gnômica>, clique com o botão direito no pacto de sigilo. Em seguida, fale novamente com o Mestre-faz-tudo Superchispa em Altaforja.", description = "A engenharia gnômica molda a teoria da engenharia para servir às nossas necessidades! Nossos diagramas nos possibilitam inventar dispositivos que manipulam o mundo a nossa volta. O manual que você leu trata disso.\n\nEle também trata do nosso voto de sigilo sobre nossos diagramas. Uma vez que você escolher a engenharia gnômica, não tem mais volta. Não se pode ter acesso nenhum à engenharia goblínica.\n\nSe você ainda assim desejar praticar a engenharia gnômica, assine este documento no qual você se compromete a manter sigilo até o fim de seus dias. Depois que acabar, fale comigo de novo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3641] = {title = "Mostre serviço", objective = "Entregue 6 Tubos de Mithril, uma Mira de Precisão e 2 Bonecos-alvo Avançados para o Mestre-faz-tudo Superchispa, em Altaforja.", description = "Como seu mentor, eu quero amostras do seu trabalho. Você é um <um engenheiro habilidoso/uma engenheira habilidosa>, disso não há dúvida, <name>. O que eu espero fazer, no entanto, é analisá-las para encontrar maneiras de enfatizar as diretrizes gnômicas nos seus esforços futuros. Eu preciso do seguinte: seis tubos de mithril, uma mira de precisão e dois bonecos-alvo avançados.\n\nEntregue os itens para mim e eu emitirei o seu cartão de engenheiro gnômico associado. Ele é necessário para acessar os treinadores de engenharia gnômica!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEngenharia Gnômica\n\nVocê receberá:\n\nCartão de Engenheiro Gnômico Associado"},
[3642] = {title = "O pacto de sigilo", objective = "Se concordar em se tornar <um Engenheiro Gnômico/uma Engenheira Gnômica>, clique com o botão direito no Pacto de Sigilo. Em seguida, fale novamente com Olhatorto Obnótico na Angra do Butim.", description = "A engenharia gnômica é uma teoria no campo da engenharia designada para servir às nossas necessidades! Nossos diagramas nos possibilitam inventar dispositivos que manipulam o mundo a nossa volta. O manual que você leu trata disso.\n\nEle também trata do nosso voto de sigilo sobre nossos diagramas. Uma vez que você se torne partidário da engenharia gnômica, não tem mais volta. Não se pode ter acesso nenhum à engenharia goblínica.\n\nSe você ainda assim desejar praticar a engenharia gnômica, assine este documento no qual você se compromete a manter sigilo até o fim de seus dias. Depois que acabar, fale comigo de novo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3643] = {title = "Mostre serviço", objective = "Leve 6 Tubos de Mithril, uma Mira de Precisão e 2 Bonecos-alvo Avançados a Olhatorto Obnótico, na Angra do Butim.", description = "Como seu mentor, eu quero amostras do seu trabalho. Você é um <um engenheiro habilidoso/uma engenheira habilidosa>, disso não há dúvida, <name>. O que eu espero fazer, no entanto, é analisá-las para encontrar maneiras de enfatizar as diretrizes gnômicas nos seus esforços futuros. Eu preciso do seguinte: seis tubos de mithril, uma mira de precisão e dois bonecos-alvo avançados.\n\nEntregue os itens para mim e eu emitirei o seu cartão de engenheiro gnômico associado. Ele é necessário para acessar os treinadores de engenharia gnômica!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEngenharia Gnômica\n\nVocê receberá:\n\nCartão de Engenheiro Gnômico Associado"},
