[3661] = {title = "Protegidos de Eluna?", objective = "Pegue 15 Penas de Coruscante nas Terras Agrestes para Erelas Ambarcéu na Vila de Rut'theran.", description = "Os coruscantes são criaturas muito imprevisíveis, <name>. Capazes de delicadeza sem igual, essa criaturas demonstram uma ferocidade extrema se algo que acreditam ser importante for ameaçado.\n\nRezam as lendas que essas feras foram criadas por Eluna, e eu pretendo descobrir se é verdade.\n\nRecentemente, eu soube de um bando de coruscantes vivendo nas Terras Agrestes... Ferujas selvagens traiçoeiros e primitivos. Eles podem ser encontrados em meio à fauna dessa região. Você poderia trazer algumas penas de coruscante para mim?", progress = "Tente evitá-los se puder, <name>. Os coruscantes podem interpretar seu interesse como uma intenção de agredi-los...", completion = "Obrigado por me trazê-las, &lt;name&gt;.\n\nO que você aprendeu com os coruscantes?\n\nTalvez estas penas me contem mais. Monstros ferozes ou apenas criaturas incompreendidas? Um dia, eu gostaria de saber a verdade...\n\nEu vou continuar a pesquisa. Fale comigo de novo depois de algum tempo.", rewards = "Você receberá:\n\n"},
[3681] = {title = "Tomo de Divindade", objective = "Fale com Benedito Brião em Ventobravo.", description = "Peço desculpa por interromper tua jornada, <race>, mas pensei que quisesse saber que Ventobravo recentemente solicitou que os paladinos se dirigissem até lá para treinar.\n\nSe não estou engando, eu diria que tu alcançou um momento especial, e esse momento é o inicio do estudo dos tomos.\n\nFale com Benedito Brião em Ventobravo. Ele geralmente treina os paladinos na Catedral da Luz.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3701] = {title = "As fumegantes Ruínas de Thaurissan", objective = "Vá até as Ruínas de Thaurissan, nas Estepes Ardentes, e descubra informações sobre as Relíquias de Thaurissan. Fale novamente com a Historiadora Real Arquefilis quando terminar.", description = "Vá até as fumegantes Ruínas de Thaurissan, nas Estepes Ardentes, e descubra o máximo de informações que puder.\n\nDizem que há relíquias misteriosas espalhadas pelas ruínas. Talvez seja melhor você começar suas buscas por lá, <name>. Cuidado com os guardiões de Thaurissan, assassinos que protegem as ruínas, zelando para que quaisquer que sejam os segredos enterrados lá continuem intocados.", progress = nil, completion = "Céus! Isso explica muita coisa sobre Thaurissan.\n\nAinda há muito a descobrir!", rewards = "Você receberá:\n\nAnel do Aristocrata"},
[3702] = {title = "As fumegantes Ruínas de Thaurissan", objective = "Escute a Historiadora Real Arquefilis recontar a história de Thaurissan.", description = "Gostaria de escutar uma breve história sobre os anões Ferro Negro?Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3721] = {title = "Um OOX para chamar de meu", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nFrango Mecânico"},
[3741] = {title = "Colar da Hilária", objective = "Encontre e devolva o Colar da Hilária na Vila Plácida.", description = "Minha amiga Hilária perdeu o colar, e não conseguimos encontrá-lo. Nós temos pescado aqui o dia todo, e já procuramos por toda a área. O único lugar que falta olhar... é dentro do lago. Nossos pais não querem que a gente entre no lago... A mamãe diz que tem bicho feio na água, mas você não parece ter medo de bicho feio...\n\nVocê pode achar o colar da Hilária? Por favor?", progress = "Oi. Sinto muita falta do meu colar. Foi o papai quem me deu. O papai diz que tem bicho no lago. Você lutou com algum bicho?", completion = "Obrigada por encontrar meu colar, moço... Você é muito legal! Meu gatinho também tá dizendo obrigado. Né, Vinteum?", rewards = "Você receberá:\n\n"},
[3761] = {title = "Solo de Un'Goro", objective = "Leve 20 amostras de Solo de Un'Goro para Ghede no Platô dos Anciãos, que fica no Penhasco do Trovão.", description = "É dito que o solo da Cratera Un'Goro é enriquecido com qualidades mágicas poderosas. O Círculo Cenariano deseja estudar o solo em grandes quantidades. Vá até Un'Goro e obtenha o suficiente para continuarmos a pesquisa.\n\nO solo rico às vezes forma montes e algumas feras podem carregar amostras aproveitáveis. Leve todas para Ghede. É só procurar as pilhas de terra e um tauren esbravejando ordens. Ele tem boas intenções, só não pise nos cascos dele!", progress = "À vontade, recruta. Se veio entregar amostras do solo de Un'Goro, então muito bem! Do contrário, desapareça.", completion = "Ah, não se preocupe, recruta &lt;name&gt;, não precisa me ajudar a descarregar essa terra nas pilhas! Se fosse da vontade dos grandes espíritos, eles teriam feita ela aparecer ali milagrosamente, não é mesmo?!\n\nO Arquidruida Runa Totem quer falar com você de novo, mas saiba que a próxima tarefa vai exigir um aventureiro que entenda de herborismo. Se você não tiver esse conhecimento, ele ainda pode achar uma utilidade para você.", rewards = "Você receberá:\n\n"},
[3762] = {title = "Auxílio ao Arquidruida Runa Totem", objective = "Fale com o Arquidruida Hamuul Runa Totem no Penhasco do Trovão.", description = "Com licença, <class>, um momento de seu tempo, se possível.\n\nO Círculo Cenariano no Penhasco do Trovão está interessado em aventureiros experientes como você para ajudar em um projeto de pesquisa importante. Apesar de não saber os detalhes, eu posso dizer que o próprio Hamuul Runa Totem está liderando essa pesquisa.\n\nPor favor, se tiver interesse, fale com ele diretamente no Platô dos Anciãos, que fica no Penhasco do Trovão.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3763] = {title = "Auxílio ao Arquidruida Guenelmo", objective = "Fale com o Arquidruida Fandral Guenelmo em Darnassus.", description = "Com licença, <class>, um momento de seu tempo, se possível.\n\nO Círculo Cenariano em Darnassus está interessado em aventureiros experientes como você para ajudar em um projeto de pesquisa importante. Apesar de não saber os detalhes, eu posso dizer que o próprio Arquidruida Fandral Guenelmo está liderando essa pesquisa.\n\nPor favor, se tiver interesse, fale com ele diretamente no Enclave Cenariano, que fica em Darnassus.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3764] = {title = "Solo de Un'Goro", objective = "Leve 20 amostras do Solo de Un'Goro para Jenal no Enclave Cenariano, que fica em Darnassus.", description = "É dito que o solo da Cratera Un'Goro é enriquecido com qualidade mágicas poderosas. Eu quero separar o joio do trigo. Vá até Un'Goro e obtenha o suficiente para continuarmos a pesquisa.\n\nVocê pode pensar que tarefas de coleta não estão ao seu nível. Esse tipo de atitude não é bem aceita por mim ou pelo Círculo Cenariano. Leve as amostras para Jenal, que com certeza está perdendo tempo atrás desta árvore. É só procurar as pilhas de terra. Ele cuidará da logística, não eu.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3765] = {title = "A corrupção estrangeira", objective = "Fale com Gershala Umbrurmúrio em Auberdine.", description = "Ventobravo... um centro de atividade tão movimentado.\n\nEu vim de longe para obter auxílio. Muitas vezes se ouve dizer que aventureiros corajosos caminham por essas ruas.\n\nO meu irmão, Gershala, precisa de ajuda na Costa Negra. Ele vive na cidade costeira de Auberdine.\n\nA viagem até lá é longa e épica. É por essa razão que peço a ajuda de <um/uma> <class> experiente como você.", progress = nil, completion = "Ah, o meu irmão, Argos, enviou você? Imagino que esteja exausto da viagem, mas nós temos muito o que tratar.", rewards = "Você receberá:\n\n"},
[3781] = {title = "Pesquisa de grão-matinal", objective = "Leve o Vale-sementes do arquidruida para Mathrengyl Ursivagus no Enclave Cenariano, que fica em Darnassus.", description = "Com o que chamamos de bornal de sempre-viva, nós cultivamos sementes de Tharlendris no solo da Cratera Un'Goro. Essas sementes produzem ervas poderosas aleatoriamente. Uma resultou em grão-matinal, uma erva misteriosa sobre a qual pouco sabemos. Mas eu pretendo desvendar esse mistério.\n\nLeve isto para Mathrengyl aqui embaixo, e ele lhe dará algumas sementes para que você possa começar. Se ficar sem, você terá que comprar mais com ele.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nPacote de Sementes de Tharlendris"},
[3782] = {title = "Pesquisa de grão-matinal", objective = "Leve o Vale-sementes para Bashana Runa Totem no Penhasco do Trovão.", description = "Com o que chamamos de bornal de sempre-viva, nós cultivamos sementes de Tharlendris no solo da Cratera Un'Goro. Essas sementes produziram ervas poderosas aleatoriamente. Uma resultou em grão-matinal, uma erva misteriosa sobre a qual pouco sabemos. Mas eu espero desvendar esse mistério para o Círculo.\n\nLeve este vale para minha filha, Bashana, e ela lhe dará algumas sementes para que você possa nos ajudar. Se ficar sem, você terá que comprar mais com ela.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nPacote de Sementes de Tharlendris"},
[3783] = {title = "Eu yeti falar uma coisa...", objective = "Colete 10 Peles Grossas de Yeti para Umi Zombarruga em Visteterna.", description = "Os meus amigos sempre têm um monte de ideias boas. Eu achava que nunca ia ter uma ideia como as deles... até hoje! Acabei de completar os planos para algo genial! Se você me prometer que não vai contar nada pra ninguém, eu te falo um segredo...\n\nVou fazer um yeti mecânico! Meus amigos vão levar um baita susto quando virem, e depois, quando melhorarem do susto, vão ficar impressionados!\n\nTô precisando de pele grossa de yeti cardo de gelo. Tem um monte deles a sudeste.", progress = "Eu já tenho bastante pra começar. Traz mais peles assim que puder!", completion = "Essas peles são perfeitas, &lt;name&gt;. Agora é que vai começar de verdade!", rewards = "Você receberá:\n\n"},
[3784] = {title = "Auxílio ao Arquidruida Runa Totem", objective = "Fale com o Arquidruida Hamuul Runa Totem no Penhasco do Trovão.", description = "Com licença, <class>, um momento de seu tempo, se possível.\n\nO Círculo Cenariano no Penhasco do Trovão está interessado em aventureiros experientes como você para ajudar em um projeto de pesquisa importante. Apesar de não saber os detalhes, eu posso dizer que o próprio Hamuul Runa Totem está liderando essa pesquisa.\n\nPor favor, se tiver interesse, fale com ele diretamente no Platô dos Anciãos, que fica no Penhasco do Trovão.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3785] = {title = "Pesquisa de grão-matinal", objective = "Use o Bornal de Sempre-viva com um pacote de Sementes de Tharlendis e duas amostras de Solo de Un'Goro para tentar cultivar mudas de grão-matinal.\n\nLeve 10 Grãos-matinais para Mathrengyl Ursivagus em Darnassus.", description = "Aqui está o bornal de sempre-viva que você precisará usar com as sementes de Tharlendis. Você também precisará de amostras de solo de Un'Goro para usar como fertilizante para as sementes. Use o bornal quando tiver tudo. Ele cuidará do resto.\n\nQuando tiver grãos-matinais suficientes, traga-os para mim aqui no Enclave. Por favor, não incomode o arquidruida. Ele é um elfo muito ocupado, e não precisa ser importunado toda vez que alguém retorna com um lote de grão-matinal!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nBaú do Círculo Cenariano"},
[3786] = {title = "Pesquisa de grão-matinal", objective = "Use o Bornal de Sempre-viva com um pacote de Sementes de Tharlendis e duas amostras de Solo de Un'Goro para tentar cultivar mudas de grão-matinal.\n\nLeve 10 Grãos-matinais para Bashana Runa Totem no Penhasco do Trovão.", description = "Aqui está o bornal de sempre-viva que você precisará usar com as sementes de Tharlendis. Você também precisará de amostras de solo de Un'Goro para usar como fertilizante para as sementes. Use o bornal quando tiver tudo. Ele cuidará do resto.\n\nQuando tiver grãos-matinais suficientes, traga-os para mim aqui no Platô dos Anciãos. Por favor, não incomode o arquidruida. Ele está muito ocupado com a pesquisa, bem como com todas as outras preocupações!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nBaú do Círculo Cenariano"},
[3787] = {title = "O pedido de Jonespyre", objective = "Fale com Quintis Jonespyre no Domínio de Plumaluna.", description = "Com licença, <amigo/amiga>...\n\nUm colega meu gostaria de falar sobre a pesquisa que você está realizando junto ao Círculo Cenariano. Ele é um druida que atende pelo nome de Quintis Jonespyre e reside no Domínio de Plumaluna, em Feralas. Ele gostaria de discutir sobre o grão-matinal... e que a conversa de vocês passe longe dos ouvidos do arquidruida.\n\nSe tiver interesse, fale com ele. Com certeza, tudo será esclarecido quando o fizer.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3788] = {title = "O pedido de Jonespyre", objective = "Fale com Quintis Jonespyre no Domínio de Plumaluna.", description = "Com licença, <amigo/amiga>...\n\nUm colega meu gostaria de falar sobre a pesquisa que você está realizando junto ao Círculo Cenariano. Ele é um druida que atende pelo nome de Quintis Jonespyre e reside no Domínio de Plumaluna. Ele gostaria de discutir sobre o grão-matinal... e que a conversa de vocês passe longe dos ouvidos do arquidruida.\n\nSe tiver interesse, fale com ele. Com certeza, tudo será esclarecido quando o fizer.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3789] = {title = "Auxílio ao Arquidruida Guenelmo", objective = "Fale com o Arquidruida Fandral Guenelmo em Darnassus.", description = "Com licença, <class>, um momento de seu tempo, se possível.\n\nO Círculo Cenariano em Darnassus está interessado em aventureiros experientes como você para ajudar em um projeto de pesquisa importante. Apesar de não saber os detalhes, eu posso dizer que o próprio Arquidruida Fandral Guenelmo está liderando essa pesquisa.\n\nPor favor, se tiver interesse, fale com ele diretamente no Enclave Cenariano, que fica em Darnassus.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3790] = {title = "Auxílio ao Arquidruida Guenelmo", objective = "Fale com o Arquidruida Fandral Guenelmo em Darnassus.", description = "Com licença, <class>, um momento de seu tempo, se possível.\n\nO Círculo Cenariano em Darnassus está interessado em aventureiros experientes como você para ajudar em um projeto de pesquisa importante. Apesar de não saber os detalhes, eu posso dizer que o próprio Arquidruida Fandral Guenelmo está liderando essa pesquisa.\n\nPor favor, se tiver interesse, fale com ele diretamente no Enclave Cenariano, que fica em Darnassus.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3791] = {title = "O mistério do grão-matinal", objective = "Leve 10 Grãos-matinais para Quintis Jonespyre no Domínio de Plumaluna.", description = "Deixe-me ser franco com você, <name>. Eu estou interessado... não, estou cismado por que o arquidruida de Darnassus está usando o nome do Círculo Cenariano, bem como os recursos do Círculo para produzir vastas quantidades de grão-matinal. Eu sou um pesquisador, maldições são minha especialidade. Eu ouvi dizer que o grão-matinal, sob as condições certas, apresenta qualidades iguais a outras ervas usadas em maldições primitivas.\n\nEu quero saber mais, e por isso peço sua ajuda. Traga-me dez grãos-matinais, e eu não deixarei de recompensá-l<o/a>.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nLuvas de Pesquisa do QuintisBrafoneiras de Cascaferro\n\nVocê também receberá:\n\n"},
[3792] = {title = "Grãos-matinais para o Domínio de Plumaluna", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nPacote de Sementes de Tharlendris"},
[3801] = {title = "Legado dos Ferro Negro", objective = "Fale com Franclorn Forjífice se tiver interesse em obter uma chave para a parte principal da cidade.Saludo, <name>. Eu sou Franclorn Forjífice. Sim, o Franclorn Forjífice responsável pelo método de construção de rocha lapidada. Obviamente, tu passou ou parou sobre uma das minhas criações em tuas aventuras por este nosso mundo castigado pela guerra.\n\nTu deve estar se perguntando por que estou aqui e o que quero de ti, duas dúvidas pertinentes.\n\nEu preciso do teu ajutório, é claro. Em troca, eu te regalarei a chave para os níveis mais profundos de minha cidade amaldiçoada.", description = "Saludo, <name>. Eu sou Franclorn Forjífice. Sim, o Franclorn Forjífice responsável pelo método de construção de rocha lapidada. Obviamente, tu passou ou parou sobre uma das minhas criações em tuas aventuras por este nosso mundo castigado pela guerra.\n\nTu deve estar se perguntando por que estou aqui e o que quero de ti, duas dúvidas pertinentes.\n\nEu preciso do teu ajutório, é claro. Em troca, eu te regalarei a chave para os níveis mais profundos de minha cidade amaldiçoada.", progress = nil, completion = "Quer você tenha vindo por causa de ganância ou num ato de devoção generosa, aquilo que você está prestes a fazer é pelo bem de todos nós.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[3802] = {title = "Legado dos Ferro Negro", objective = "Mate Fineous Forçanegra e recupere o grande martelo Ferrovil. Leve-o para o Santuário de Thaurissan e coloque-o na estátua de Franclorn Forjífice.", description = "Tu vai encontrar Fineous Forçanegra além do Círculo da Lei, no Salão da Criação. Mate o patife miserável e recupere Ferrovil.\n\nLeve Ferrovil para o Santuário de Thaurissan e coloque o martelo no lugar legítimo dele: nas mãos da estátua erguida em meu nome.\n\nDepois que tiver feito isso, o compartimento onde guardei a chave-mestra vai abrir. Porém, o mais importante é que Ferrovil vai ficar para sempre nas minhas mãos. Se eles tentarem retirar o martelo, tanto a estátua quanto o martelo vão se estilhaçar, e serão perdidos para sempre.", progress = "A estátua se eleva ameaçadoramente sobre você.", completion = "O martelo se encaixa firmemente na mão do enorme monumento dos Ferro Negro. Um pequeno compartimento se abre na base da estátua. Dentro você encontra...", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nChave de Umbraforja"},
[3803] = {title = "Grãos-matinais para Darnassus", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nBaú do Círculo Cenariano"},
[3804] = {title = "Grãos-matinais para o Penhasco do Trovão", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nBaú do Círculo Cenariano"},
[3821] = {title = "Rochedo Malhorrendo", objective = "Explore as Estepes Ardentes e descubra o que aconteceu com Sha'ni Altapresa.", description = "A Sha'ni foi mandada para investigar relatos de atividades ritualísticas sinistras dos ogros Pirobucho nas Estepes Ardentes faz umas três semanas.\n\nO Trincador falou que não teve notícias de ninguém desse pelotão e que é melhor eu seguir em frente e me concentrar no meu trabalho. Dá para acreditar? É da minha esposa que a gente está falando!\n\nEu não posso deixar o meu posto, <name>, mas preciso saber o que aconteceu com a Sha'ni. Vá para as Estepes Ardentes e encontre ela.", progress = nil, completion = "Meu pobre Thal'trak.\n\nA gente estava pensando em ir morar com os meus pais nas Terras Agrestes depois que déssemos baixa, mas esses planos já eram!", rewards = "Você receberá:\n\n"},
[3822] = {title = "Krom'Grul", objective = "Recupere a Argola Nasal de Sha'ni de Krom'Grul e leve-a para Thal'trak em Karrath. Procure nas várias cavernas e minas do Rochedo Malhorrendo.", description = "Capturaram meu pelotão inteiro, <name>. Todo mundo morreu, menos eu.\n\nKrom'Grul, o líder deles, queria me usar em algum tipo de ritual.\n\nMe acorrentaram ao altar pras feras me destroçarem. Depois de uma hora de tortura, minha forma física parou de funcionar. Aí, a fera arrancou minha argola de casamento do meu nariz e botou no dedo. Eu nunca tinha visto tanta selvageria!\n\nAcha o Krom'Grul e acaba com ele! Depois, leva o meu anel de volta pro Thal'trak.\n\nE <name>, diz pra ele que a gente vai voltar a se encontrar... em outra vida.", progress = "Você encontrou a Sha'ni? Ela está bem?", completion = "&lt;Thal'trak desaba.&gt;\n\nA gente ia mudar para as Terras Agrestes. Ela te falou? Essa era a última missão dela.\n\n&lt;Thal'trak soluça.&gt;\n\nEu não quero, &lt;name&gt;. Pode ficar para você, joga fora, eu não ligo! Esse anel não significa mais nada para mim. Não sem ela...", rewards = "Você receberá:\n\nAnel de Sha'ni"},
[3823] = {title = "Extinção dos Pirobuchos", objective = "Mate 15 Ogros-magos Pirobucho, 7 Ogros Pirobucho e 7 Brutamontes Ogros Pirobucho e fale novamente com Oralius quando terminar.", description = "Precisamos atacar nossos inimigos antes que nos ataquem!\n\nVou ser breve, soldado.\n\nOs ogros Pirobucho das Estepes Ardentes são aliados e trabalham junto com os orcs Rocha Negra, fazendo a guarda do Rochedo Malhorrendo, que é rico em minérios, e mantendo as linhas de suprimentos abertas em tempo integral.\n\nDeixe a sua marca, <name>. Enfraqueça as forças deles e fale novamente comigo quando concluir o serviço.\n\n<Oralius agarra o ar e faz um movimento de enforcamento.>\n\nEU VOU MATAR VOCÊ, PISCADELA!", progress = nil, completion = "Excelente, soldado! Os orcs e ogros vão demorar vários minutos, talvez até horas, para se recuperar desse ataque!", rewards = "Você receberá:\n\n"},
[3824] = {title = "Gor'tesh, o lorde dos brutamontes", objective = "Encontre Gor'tesh em algum lugar a oeste do Rochedo Malhorrendo. Mate Gor'tesh e depois leve a Cabeça Decepada do Gor'tesh de volta a Oralius nas Estepes Ardentes.", description = "Eles ainda estão atordoados, eu garanto! Nosso próximo ataque precisa ser decisivo.\n\nGor'tesh, o lorde dos brutamontes, tem que morrer. Mais do que isso, quero a cabeça dele para preparar a terceira fase do nosso ataque contra os ogros Pirobucho.\n\nFique alerta, soldado, Gor'tesh não governa do Rochedo Malhorrendo, mas de uma estrutura dos orcs Rocha Negra, que fica a oeste do rochedo.\n\nAs ordens são simples. Encontre Gor'tesh e corte-lhe a cabeça! Fale novamente comigo quando o trabalho estiver concluído.\n\nCALE A BOCA, PISCADELA!", progress = "Ah, que dia glorioso! Voltou com a cabeça?", completion = "Olhe só para isso, soldado. Nojento! Quando os ogros Pirobucho que restaram virem essa cabeçorra enfiada numa estaca no alto da montanha deles, vai ser um pega pra capar!\n\n&lt;Oralius ri descontroladamente.&gt;", rewards = "Você receberá:\n\n"},
[3825] = {title = "A festa da cabeça cortada", objective = "Pegue a Cabeça Decepada do Gor'Tesh e coloque no topo do Rochedo Malhorrendo. Procure um montinho de terra macia para espetar a estaca.", description = "Só preciso de mais uma coisa, soldado.\n\nTomei a liberdade de enfiar a cabeça de Gor'tesh em uma estaca. Agora você só precisa é levar a cabeça ao topo do Rochedo Malhorrendo e espetar a estaca no chão. Depois disso, assista de camarote os ogros covardes correndo para se salvarem! Depois da diversão, venha falar comigo eque eu lhe recompensarei por toda a sua dedicação.\n\n<Oralius parece estar perdendo o contato com a realidade.>", progress = nil, completion = "Ótimo trabalho, soldado! Totalmente excelente! Ah, eu vou ganhar uma medalha de honra por isso, hã... digo, você será recompensado pelo seu feito!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nManoplas da InsanidadeElo da Sufocação\n\nVocê também receberá:\n\n"},
[3841] = {title = "Um órfão em busca de um lar", objective = "Fale com Quentin em Mil Agulhas.", description = "Ah, olá, <name>. Que bom ver você de novo. Como foi a viagem até Darnassus?\n\nBom, você vai ficar feliz em saber que fizemos bastante progresso aqui enquanto você não estava. Conseguimos até salvar um ninho cheio de ovos. Infelizmente, a mãe morreu antes que eu conseguisse chegar até ela.\n\nO que me diz, gostaria de ter um deles como ajudante? Vão chocar em breve, por isso precisam de <um bom pai/uma boa mãe>. O que você me diz?\n\nSe tiver interesse, leve um dos ovos até Quentin, ele está na pista de corrida do Salar Cintilante. Ele vai lhe dar mais informações.", progress = "Olá, <class>.\n\nNão deixe o lugar enganar você, não vim para cá por causa das corridas... por mais interessantes que sejam.\n\nEstou aqui em Mil Agulhas investigando a fundo se essa terra é recuperável ou não. Tomei pra mim a missão de restaurar e revitalizar a região com solo novo e fértil. Quem sabe eu consiga encontrar um manancial de água nas redondezas.\n\nVocê já notou como é estranha a transição entre Feralas e Mil Agulhas? Uma magia estranha estava em curso quando isso aconteceu.", completion = "Ah, um ovo de dardejante, e próximo da maturação, se não me engano. Incrível você ter encontrado um, ainda mais nessas condições. São raros de se achar em Feralas hoje em dia. Sei que havia vários caçadores ilegais na região tentando capturar e matar esses bichos por várias razões. Uma pena... são criaturas tão belas quanto chegam à maturidade.\n\nComo é que você conseguiu encontrar esse ovo?", rewards = "Você receberá:\n\n"},
[3842] = {title = "Uma incubação curta", objective = "Entregue 2 Elixires de Fortitude para Quentin em Mil Agulhas.", description = "Tecelunas, é? Intrigante. Tomara que os dardejantes saibam valorizar a importância de terem esses dois para tomar conta deles. Ambos são muito capazes.\n\nEntão eles lhe deram o ovo de presente. Bem, neste caso você fica responsável por eles enquanto achar que deve. Parecem quase prontos para nascer, mas temos que zelar pela saúde deles e cuidar para que não sofram nenhum dano.\n\nSó preciso de dois elixires de fortitude para fazer um feitiço que proteja os ovos. O resto dos componentes eu já tenho aqui.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3843] = {title = "O mais novo integrante da família", objective = "Leve o Ovo Frágil de Dardejante às Terras Agrestes e fale com Agnar Domaferas, mas corra, você só tem uma hora para encontrá-lo antes que o filhote dentro do ovo morra.", description = "Um espécime magnífico, <name>. Se sobreviver à gestação, essa criatura vai ser um ajudante muito saudável!\n\nSugiro que você faça uma última coisa se quiser levar o plano até o fim, vá até o Ninho da Águia, que fica nas Terras Agrestes. Fale com um anão chamado Agnar Domaferas, ele é um tratador de animais de mão cheia e com certeza vai poder lhe ajudar nas etapas finais da incubação.\n\nMas corra, <name>, o ovo é frágil e não vai durar muito sem os devidos cuidados.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[3844] = {title = "É segredo para todo mundo", objective = "Olhe em volta da lagoa para descobrir uma pista sobre quem pode ser o dono do bote destruído.", description = "Um bote destruído jaz perto da água. Considerando que é apenas uma pequena lagoa, não fica claro como ele foi parar ali, embora as árvores pareçam indicar que o nível da água pode não ser constante.\n\nTalvez uma rápida exploração da região possa trazer mais pistas...", progress = nil, completion = "Uma bolsa encharcada jaz no fundo da lagoa.", rewards = "Você receberá:\n\n"},
[3845] = {title = "É segredo para todo mundo", objective = "Examine os itens dentro da bolsa e leve até o dono.", description = "Você agarra a bolsa e puxa para fora d'água. Talvez, se examinar os itens que estão ali dentro, você consiga descobrir mais informações sobre o dono ou a dona.\n\nTem alguns viajantes perdidos no Refúgio do Marshal. Talvez você consiga descobrir mais alguma coisa lá.", progress = "Olá. A gente já se conhece de algum lugar... Não é?", completion = "Ah... Bem, essas coisas me parecem familiares... mais ou menos...", rewards = "Você receberá:\n\n"},
[3861] = {title = "COCORICÓ!", objective = "Procure a Ração Especial de Galinha e volte até sua nova amiga galinácea. Em seguida, use a expressão /incentivar diante dela e lhe dê a Ração Especial de Galinha.", description = "\"Có... có... có... Cocó!\n\nCó... có... có...\"\n\nA galinha olha para você e começa a ciscar o chão. Você tem a impressão de que ela está escrevendo alguma coisa, mas não tem certeza. Será que é... S-A-L-D-A-N-H-A? Você nem tem tempo de perguntar e ela já volta a cuidar da vida.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEvocar Ovo de Galinha"},
[3881] = {title = "A salvação da expedição", objective = "Leve um Caixote de Comestíveis e o Equipamento de Pesquisa para Williden Marshal in Cratera Un'Goro.", description = "Não sei como você chegou aqui, mas sua vinda é um alento para quem passou pelo que eu passei! Meu nome é Williden Marshal, eu sou líder de um grupo expedicionário não alinhado. Nosso último e maior empreendimento se deparou com incontáveis desastres, sendo que o mais grave foi o modo como subestimamos a ferocidade da Cratera Un'Goro!\n\nNa fuga, abandonamos nossos acampamentos nas regiões sudoeste e nordeste da cratera e deixamos suprimentos vitais para trás. Você parece capaz de nos ajudar. Será que pode ir atrás deles?", progress = "Para sair dessa com vida, vamos precisar do caixote de comida. Recuperar pelo menos parte do nosso equipamento seria uma bênção, em meio a tanto sofrimento.", completion = "Você recuperou os nossos suprimentos e o nosso equipamento de pesquisa! Você não salvou só a expedição... Você salvou a nossa vida! Eu e minha equipe não sabemos como agradecer. Talvez a gente consiga mesmo se manter aqui graças a você.\n\nNão temos muito o que oferecer, mas não vamos deixar que você saia de mãos abanando depois dessa ajuda inestimável. Obrigado, &lt;name&gt;. Você ganhou um amigo nas Expedições Marshal!", rewards = "Você receberá:\n\n"},
[3882] = {title = "Osso duro de roer", objective = "Leve 8 Ossos de Dinossauro para Chispa Minanilo na Cratera Un'Goro.", description = "Não é porque sou arqueólogo-chefe das Expedições Marshal que eu gosto de cavar. Vou te contar, se tem coisa pior no mundo, eu desconheço, viu...\n\nMandaram a gente pra escavar o Terroral – EXCELENTE nome prum monte de estegodons e dimetrodontes mortos, inclusive. O pior é que tem um monte deles vivos também! O Will e a Hol ainda tão a fim de estudar esse chiqueiro mortal, então a gente precisa dos ossos desses dinossauros. Cá pra nós, pode trazer os de uns vivos que dá no mesmo!", progress = "Pode falar o que quiser, mas duvido que eles notem alguma diferença entre os ossos dos dinossauros que morreram há anos e os dos que quase nos mataram quando estávamos montando acampamento. Eu só quero é sair daqui vivo!", completion = "Boa! Esses devem dar. Agora, dá uma licencinha...\n\nMUITO OBRIGADO POR ESCAVAR TODOS ESSES OSSOS SEM MACHUCAR NENHUM ANIMAL NATIVO, HEIN! VALEU MESMO!\n\nA verdade verdadeira é que eu não tô nem aí se você machucou ou não, mas a gente fica assim, beleza? Toma. Acho que isso aqui já dá um belo cala-boca. Como te disse, eu só quero sair daqui vivo!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas de Escavação do ArqueólogoCinto de Utilidades do Escavador\n\nVocê também receberá:\n\n"},
[3883] = {title = "Ecologia forânea", objective = "Use a Ampola de Amostra para coletar uma Amostra de Parede de Colmeia de uma das incubadoras Gorishi na Cratera Un'Goro. Procure as câmaras com larvas.\n\nLeve a Amostra de Parede de Colmeia para Hol'anyee Marshal na Cratera Un'Goro.", description = "Um contratempo não significa que a pesquisa vai parar! Nós estávamos investigando os insetos Gorishi ao sul da cratera antes de virmos para cá. Eles não pertencem à ecologia de Un'Goro, por isso nós queremos descobrir como vieram parar aqui.\n\nOs túneis das colmeias deles devem contar com algum tipo de incubadora. Não vai ser difícil achar, basta procurar as larvas. Use esta ampola para pegar uma amostrar da parede da colmeia de qualquer incubadora e traga para eu estudar. Tenha cuidado!", progress = "Trouxe a amostra da colmeia Gorishi para a análise, <name>?", completion = "Sim, isso vai servir. Sua ajuda foi fundamental. Eu bem que gostaria de ter algo mais para lhe oferecer, mas aqui, praticamente perdida no meio desta terra selvagem, eu estou meio sem recursos. Mas você pode ter certeza, a pesquisa que estamos fazendo aqui não seria possível sem a sua ajuda!", rewards = "Você receberá:\n\n"},
[3884] = {title = "Diário de Williden", objective = "Devolva o diário a Williden Marshal na Cratera Un'Goro.", description = "Você encontrou um diário em frangalhos. Em meio às partes ilegíveis, há um trecho em que um tal Williden Marshal afirma ser o dono dele. Se as informações contidas nele estiverem corretas, Williden está na parte norte da Cratera. De acordo com o diário, ele e seus companheiros partiram em busca de um terreno mais elevado depois de as feras da Cratera assolarem seus acampamentos.", progress = "Olá! Eu e meus companheiros estamos muito contentes em encontrar algo ou alguém que não seja uma dessas feras selvagens superdesenvolvidas! O que o traz à Cratera Un'Goro?", completion = "Olha só isso – é o meu diário! Eu achei que ele estivesse perdido para sempre, como quase tudo que nós trouxemos para Un'Goro. Muito obrigado por vir até aqui me devolver!", rewards = "Você receberá:\n\n"},
[3901] = {title = "Desossando alguns Range-ossos", objective = "Mate 12 Esqueletos Range-ossos e fale novamente com o Sacerdote Sombrio Sarvis em Plangemortis quando terminar.", description = "Em circunstâncias normais, você já provou aos Renegados que tem potencial, <name> – agora vamos ver como se sai sob pressão.\n\nOs esqueletos Range-ossos, os lacaios mais descerebrados do Lich Rei, são um desafio maior que os zumbis que você enfrentou até agora. Vá lá mais uma vez, reduza os números deles e mostre aos Renegados do que você é capaz. Não perca tempo. Quando terminar, venha falar de novo comigo.", progress = nil, completion = "Nós já terminamos aqui, &lt;name&gt; – você provou estar à altura da liberdade que lhe foi concedida. Muitos vão se opor a você pelo que se tornou, mas saiba que, não importa o que façam contra nós, nós somos livres e não seremos agrilhoados novamente.\n\nTome isso e siga seu caminho. Muitos feitos esperam por você, &lt;class&gt;.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas de LinhãoTúnica do CatadorPerneiras do Errante\n\nVocê também receberá:\n\n"},
[3902] = {title = "Vasculhando Plangemortis", objective = "Procure 6 peças de Material Reaproveitado nas redondezas de Plangemortis e fale novamente com o Necroguarda Saulo.", description = "Você aí! Se quiser ser útil, venha aqui um instante!\n\nPrecisamos de recrutas recém-desenterrados para ir até Plangemortis atrás de equipamentos úteis de toda sorte. Eles provavelmente estarão encaixotados e empilhados. Esperamos mais recrutas erguidos em breve e, como não queremos ninguém perambulando sem nada para cobrir os ossos, vamos ter que trabalhar!\n\nEntão, hora de pôr a mão na massa, saco miserável de ossos! Eu não vou recompensar ninguém que não dê duro.", progress = "Você conseguiu recuperar algo de útil para nós? Não há vergonha nenhuma em reutilizar o que foi jogado fora. Ninguém vai nos estender a mão – os Renegados só podem contar consigo mesmos!", completion = "Ótimo trabalho, &lt;name&gt;, eu sabia que você não era um inútil. Aqui, separei um dos melhores itens entre os que encontramos para você.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCinto de LinhãoBraçadeiras de Metal EnferrujadoCapa Curta do Quiropúsculo\n\nVocê também receberá:\n\n"},
[3903] = {title = "Madel Quintana", objective = "Fale com Madel Quintana.", description = "Você mostrou ser u<m/ma> <class> dign<o/a> de confiança, <name>. Além disso, você não tem medo de sujar as mãos, não é?\n\nEu tenho uma amiga que se chama Madel Quintana que está tendo alguns problemas. Ela está com a carroça dela do outro lado da abadia, perto do estábulo. Tenho certeza de que você seria de grande ajuda para ela.", progress = nil, completion = "Ah, o Cabo Vilém pediu para você vir falar comigo? Além de ser um homem corajoso, ele está sempre disposto a ajudar, mas o dever exige a presença dele na Abadia de Vila Norte, e infelizmente o meu problema está fora de sua alçada.\n\nE quanto a você? Será que pode me ajudar?", rewards = "Você receberá:\n\n"},
[3904] = {title = "Colheita da Madel", objective = "Leve 8 caixotes da Colheita da Madel para Madel Quintana na Abadia de Vila Norte.", description = "Os Défias, uma gangue de bandoleiros, se instalou nos Vinhedos de Vila Norte enquanto eu fazia a colheita! Eu avisei aos guardas de Vila Norte e eles me asseguraram que cuidariam de tudo, mas... eu estou preocupada com as minhas uvas! Se os Défias não roubarem todas, eu tenho medo que os guardas as estraguem na hora de rechaçar aqueles bandidos.\n\nPor favor, você tem que me ajudar! Eu pus a maior partes das uvas em baldes, mas eles ficaram nos vinhedos a sudeste.\n\nTraga aquelas caixas para mim! Salve a minha colheita!", progress = "Você trouxe minha colheita, <name>?", completion = "Oh, muito obrigada, &lt;name&gt;! Você salvou a minha colheita! E espero que tenha mostrado àqueles Défias de uma figa que aqui não é o lugar deles.\n\nPodemos ter um problema com o contingente de guardas, mas é muita sorte termos heróis e heroínas como você para nos proteger!", rewards = "Você receberá:\n\n"},
[3905] = {title = "Manifesto da Uva", objective = "Leve o Manifesto da Uva para o Irmão Neals na Abadia de Vila Norte.", description = "Agora que a minha produção está salva, leve este Manifesto da Uva para o Irmão Neals. Ele cuida da loja de comida e bebida de Vila Norte, e eu tenho certeza de que ele vai adorar saber sobre as uvas fresquinhas.\n\nO Irmão Neals está na abadia, na torre do sino... onde ele gosta de degustar seus vinhos.", progress = "Você parece animado! Venha! Puxe uma cadeira e venha beber algo comigo!", completion = "Vamos ver...\n\nMinha nossa! As uvas de Madel foram salvas! Quando ela me disse que bandoleiros tinham tomado conta dos vinhedos dela, eu quase me desesperei, mas minha fé na Luz não esmoreceu!\n\nQue a Luz te abençoe e te proteja, &lt;name&gt;!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nManto Manchado de VinhoCinto com Aldrava\n\nVocê também receberá:\n\n"},
[3906] = {title = "Desarmonia das chamas", objective = "Vá até a pedreira da Montanha Rocha Negra e mate o Mestre Supremo Piro. Fale novamente com Coração Trovejante quando tiver cumprido essa tarefa.", description = "Os anciões sentiram um perturbação entre os elementos. Os anões Ferro Negro evocaram para este mundo um espírito maligno que ameaça amaldiçoar nossas terras com uma estiagem eterna. A desarmonia das chamas tem que ser investigada.\n\nVá até a Montanha Rocha Negra, passe pela Fenda Flamejante e chegue à pedreira. É lá que você vai encontrar o Mestre Supremo Piro, o senhor das chamas.\n\nDê fim à perturbação e venha falar de novo comigo.", progress = nil, completion = "Não pode ser... Não pode ser. A morte de Piro derrubou um véu que desconhecíamos. Existe uma fonte de corrupção muito mais pérfida nas profundezas da Rocha Negra.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[3907] = {title = "Desarmonia do fogo", objective = "Entre no Abismo Rocha Negra e encontre o Lorde Incendius. Mate-o e leve todas as informações que puder para o Coração Trovejante.", description = "Posso sentir a podridão no ar ao seu redor, <name>. Existe outro ser, escondido nas profundezas da Rocha Negra, por trás disso.\n\nOs ventos e a terra gritam o nome dele: Lorde Incendius... mas alguém... algo... está no comando. Ele não passa de um emissário.\n\nEncontre-o e descubra onde o mestre dele se esconde. Fale novamente comigo quando tiver feito tudo isso.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nCapa HelionataLuvas do OcasoBraçadeiras do Demônio da CriptaAmarra Impávida\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[3908] = {title = "É segredo para todo mundo", objective = "Leve a Espada de Linken para Bruna Covaneve em Hibérnia.", description = "Sabe, eu não faço ideia de como vim parar aqui. Ninguém sabe – é um mistério pra todo mundo! Eu simplesmente não lembro de nada...\n\nMas essas coisas que você me trouxe... Acho que eu estou começando a lembrar de alguma coisa.\n\nMinha espada. Eu tenho que dar um jeito de deixar ela mais forte, mas não sei como. Eu bem que gostaria de poder dizer mais, mas não lembro de nada!\n\nBom, eu sei onde ela pode ser levada: pra Bruna Covaneve, em Hibérnia. Como está ali por perto das fontes termais, ela pode usar as águas quentes pra temperar a espada.", progress = "Ora, ora... Finalmente Linken decidiu mandar a espada pra mim.", completion = "Ora, claro que eu posso temperar a espada dele. Enquanto faço isso, você pode me explicar porque levou tanto tempo pro Linken me procurar. Ele geralmente não esquece as coisas...", rewards = "Você receberá:\n\n"},
[3909] = {title = "O elixir Videre", objective = "Procure Gregan Cospechope ao norte de Feralas. Descubra com ele como obter o elixir Videre, depois fale novamente com Bruna Covaneve em Hibérnia.", description = "Como é que é? O Linken... ESQUECEU?\n\nAh, tá... Perdeu a memória...\n\nBom, aqui está a espada dele. Só que ela ainda não está completa. Eu sei de alguém que pode arrematar ela, mas... vai ser meio difícil falar com ele.\n\nÉ muita coisa pra explicar agora, mas você vai ter que dar um jeito de conseguir um Elixir Videre. A única pessoa que eu sei que pode te ajudar com isso é o Gregan Cospechope. Não é fácil achar ele, mas a última notícia que tive foi de que ele estava acampando na selva de Feralas.", progress = "Já voltou?", completion = "Mas bah, vou precisar de uma ampola para ter certeza de que este é mesmo o elixir... Sim... É isso mesmo, tchê.", rewards = "Você receberá:\n\nRaiz do Mal"},
[3911] = {title = "The Last Element", objective = "Travel to Blackrock Depths and recover 10 Essence of the Elements. Your first inclination is to search the golems and golem makers. You remember Vivian Lagrave also muttering something about elementals.", description = "There is work to be had for those venturing into the depths, <name>.\n\nThe Dark Irons have mastered creation of extremely powerful golems.\n\nInitial reports from our spies indicate that the dwarves use a unique power source to give their creations incomparable killing power.\n\nJust imagine what we could do with our abominations if we could get our hands on this essence of the elements! Turn that city upside down if you must, but do not return until you have found the essence! Payment will be worth the risk.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nSelo de Lagrave"},
[3912] = {title = "Encontro na sepultura", objective = "Vá até o cemitério perto de Geringontzan e use o Elixir Videre. Siga as outras instruções de Bruna para achar Gaeriyan.\n\nItem fornecido:\n", description = "O elixir vai te permitir falar com alguém que tu normalmente nem veria...\n\nLeva o elixir Videre, com a espada do Linken, pro cemitério ao lado de Geringontzan, em Tanaris. Quando chegar lá, tu bebe a poção. Os resultados podem te surpreender, mas não precisa se assustar.\n\nNa \"outra\" forma, você vai ter que ir pro norte até chegar nas montanhas. A pessoa com quem tu tem que falar tá em uma das aberturas na pedra. O nome dele é Gaeriyan.\n\nNão esquece, ele só vai falar contigo nessa forma...", progress = nil, completion = "Já faz muito tempo que ninguém vem falar comigo...", rewards = "Você receberá:\n\n"},
[3913] = {title = "A sepultura", objective = "Ressuscite, depois procure a lápida que Gaeriyan disse para você encontrar.", description = "Se voltar ao cemitério, você vai encontrar uma lápide lá, <name>. Não é uma lápide qualquer — ela se destaca das outras. Segredos jazem em seu interior.\n\nEmpurre a pedra para descobrir quais são esses segredos...", progress = "A lápida que Gaeriyan mencionou deve ser esta...", completion = "Você empurra vigorosamente a pedra, que desliza e revela um pequeno sulco abaixo dela.\n\nAo pôr a Espada de Linken no sulco, você percebe que ela cabe perfeitamente...", rewards = "Você receberá:\n\n"},
[3914] = {title = "Espada de Linken", objective = "Leve a Espada Superior de Linken para Linken na Cratera Un'Goro.", description = "Depois de um clarão, você consegue pegar a espada novamente. Ela foi transformada!", progress = "Ah! Eu lembro de você! Você é a... Calma, não diz ainda!", completion = "Minha espada... Ela tá pronta!\n\nAgora eu queria lembrar pra que precisava dela...\n\nAqui, como pagamento pelo seu serviço.", rewards = "Você receberá:\n\n"},
[3921] = {title = "Juntatudy Jogafora", objective = "Leve a Rebimboca Quebrada para Juntatudy Jogafora.", description = "Hm, acho que eu quebrei essa rebimboca. Vou ter que dar um jeito nisso, porque já contei dela pra uns colegas na Inframina e eles ficaram muito intrigados. A gente vai ter que dar um jeito de consertar!\n\nLeva a rebimboca quebrada pra Juntatudy Jogafora, logo ao sul da Paliçada Mor'shan, o posto avançado da Horda entre os Sertões e o Vale Gris. Ela é crânio, se interessa pelas coisas. Se você mostrar a rebimboca pra ela, aposto que ela vai querer tentar consertar.", progress = "Tô ocupadérrima, <class>. Se não tiver nada sério aí pra mim, é melhor ir dando linha...", completion = "Que isso aí, hein? O nome é rebimboca? Tá, mas o que é uma rebimboca?\n\nGente, que movimento é esse? E o som que vem de dentro, são engrenagens bem complexas. Poxa! Agora eu fiquei doida pra consertar e ver o que essa coisa faz! Você não?", rewards = "Você receberá:\n\n"},
[3922] = {title = "Chumbalheiras", objective = "Leve 15 Chumbalheiras para Juntatudy Jogafora nos Sertões.", description = "Eu quero consertar essa rebimboca, mas pra fazer isso vou precisar de umas coisinhas. Tô sem chumbalheiras 7.5 e vou precisar de várias delas.\n\nO lugar mais perto em que dá pra encontrar essas peças é no Charco de Lodo, a leste daqui. A Empreendimentos S.A. sempre deixa elas nos baldes de ferramentas. Vai lá no Charco de Lodo e procura nos baldes de ferramentas. Quando tiver juntado bastante chumbalheiras, vem trazer pra mim.", progress = "Conseguiu as chumbalheiras, <name>? Tô de olho nessa rebimboca faz tempo. Mal posso esperar para revirar todas as parafusetas!", completion = "Aí, garoto, sabia que ia conseguir! Ao trabalho...", rewards = "Você receberá:\n\n"},
[3923] = {title = "Rilli Passomal", objective = "Leve a Rebimboca Quebrada e Surrada para o Rilli Passomal em Orgrimmar.", description = "Bom, não funcionou. Eu pus mais uns medidores pra checar a integridade estrutural da rebimboca, e eles mostraram que a integridade tá mais baixa que nunca. Dá pra adicionar mais uns sintonizadores, talvez uma ou duas alavancas pra tentar dar um jeito, mas... acho que isso pode até piorar a situação.\n\nÉ melhor levar essa rebimboca pro Rilli Passomal. Ele trabalhava pra Empreendimentos S.A. — se alguém vai saber arrumar, é o Rilli.\n\nEle vai estar na Oficina do Nogg. Fica em Orgrimmar, no Vale da Honra.", progress = "Cuidado com a cabeça! Eu não quero que ela atrapalhe o meu trabalho!", completion = "Que isso aí? Ah, é uma rebimboca! Onde você arrumou esse troço? Andou xeretando nas coisas da Empreendimentos S.A. nos Sertões, né?\n\nTem que ter muita coragem, viu, ou ser muito idiota.", rewards = "Você receberá:\n\n"},
[3924] = {title = "Manual da rebimboca", objective = "Leve o Manual da Rebimboca para Rilli Passomal em Orgrimmar.", description = "Acho que eu consigo consertar essa rebimboca, mas vou precisar do manual de instruções. Quando trabalhava na Empreendimentos S.A., tinha um cara, o Chefe Cobreplugue, que controlava o que todo mundo sabia sobre a rebimboca. Ele só repassava informação pros amiguinhos dele, e como não ia muito com a minha cara...\n\nO Chefe Cobreplugue ficava na Mina Veio do Pedregulho, no nordeste dos Sertões. Pega o manual e traz pra mim. Talvez ele tenha passado algumas páginas pra alguém, aí você vai ter que ir atrás delas.", progress = "Trouxe o manual?", completion = "Você trouxe o manual! Tô doidinho pra ler e descobrir como se conserta essa rebimboca! Eu troquei uma ideia com o Cobogó, na Vila Catraca, e ele pediu pra te agradecer, e além de mandar uma recompensazinha. Sabe como é, pela trabalheira.\n\nEu também quero te agradecer. Se conseguir consertar essa parada, eu te dou um toque.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nChave de TorqueChave de Fenda da Rebimboca\n\nVocê também receberá:\n\nCapa do Manual da RebimbocaPágina do Manual da Rebimboca"},
[3941] = {title = "Ajuda gnômica", objective = "Encontre J.D. Neri na caverna do Refúgio do Marshal.", description = "Pensando... Pensando... Eu sei que vim aqui por alguma coisa importante...\n\nTive uma ideia, <name>. Na caverna logo ali tem uma gnomida chamada J. D. que foi muito gentil comigo quando eu cheguei aqui. Talvez, por estar desorientado, eu tenha comentado alguma coisa com ela.\n\nPor que você não pergunta se ela sabe alguma coisa? Mas cuidado, hein — ela fica o tempo todo mexendo naqueles cristais brilhantes malucos que eu vi na cratera. Não sei o que ela tá pensando em fazer!", progress = nil, completion = "Fica bem aí onde você tá um instantinho... Eu tô testando uma nova combinação de cristais...", rewards = "Você receberá:\n\n"},
[3942] = {title = "Memória de Linken", objective = "Encontre Eridan Brisazul no sul da Selva Maleva.", description = "O Linken? É, ele é bem engraçado. Quando chegou aqui, ele não parava de balbuciar, com uns papos pra lá de estranhos. Imagino que o coitado bateu a cabeça com força.\n\nMas uma coisa que ele disse ficou na minha cabeça: ele disse que tava fazendo um totem, que tinha que falar com uma tal de Eridan Brisazul... num lugar chamado Selva Maleva, eu acho.\n\nA essa altura, duvido que ele consiga explicar alguma coisa. Se quiser saber mais algo, por que você não fala com essa Eridan?", progress = nil, completion = "Olá, &lt;class&gt;. Posso ajudá-la com algo?", rewards = "Você receberá:\n\n"},
[3961] = {title = "A aventura de Linken", objective = "Leve o Totem de Prata de Aquementas para Linken no Refúgio do Marshal.", description = "E pensar que o motivo para o Linken vir aqui todas aquelas vezes foi para nos salvar do Agni... Ele parece um cara tão normal, mas eu notei que ele não tinha interesse por nenhum dos meus experimentos ou engenhocas, como a maioria dos gnomos teria. No fundo, ele deve ser mesmo um guerreiro.\n\nLeve o totem para ele. Veja se isso o ajuda a lembrar por que veio para cá.", progress = "Oi de novo, <name>!", completion = "A J.D. ajudou você?", rewards = "Você receberá:\n\n"},
[3962] = {title = "É perigoso ir sozinho", objective = "Vá para o Pico Penacho de Fogo, ao sul do Refúgio do Marshal.\n\nEncontre Agni e use o totem para remover sua aura de proteção. Uma vez que for derrotado, pegue a Chama Dourada na caverna.", description = "<name>, eu finalmente me lembrei... Eu vim aqui para derrotar o Agni. Eu estava em uma jornada, e essa era para ser a última parada.\n\nMas eu não sou mais o herói... Você é. Tudo que você fez... Você que precisa terminar, não eu.\n\nO Agni protege um artefato conhecido como a Chama Dourada. Ela concede um poder maior do que qualquer outro que eu conheça, e é perigosa nas mãos de alguém com o coração maligno.\n\nÉ melhor levar outros com você... É perigoso ir sozinho. Aceite o meu conselho.", progress = "O Agni é quase invencível por trás da aura que criou usando a Chama Dourada. Só você tem o poder para removê-la com o Totem de Prata de Aquementas.", completion = "Obrigado, &lt;name&gt;.\n\nA Chama Dourada vai ser devolvida à minha terra natal, onde pode ser protegida, e nunca mais usada para o mal.\n\nSem você, eu ainda não saberia quem sou! Já que fez tanto por mim, eu gostaria de lhe dar algo especial...\n\nPegue um desses!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nEspada da Maestria de LinkenEspírito de Aquementas\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\nBumerangue de Linken"},
[3981] = {title = "Comandante Gor'shak", objective = "Encontre o Comandante Gor'shak no Abismo Rocha Negra.\n\nVocê se lembra que o tosco desenho incluía barras rabiscadas sobre o orc. Talvez você devesse procurar algum tipo de prisão.", description = "Coitado do Gargal... nem suspeitava de nada e CRÉU! A flecha cravou bem no meio da testa dele. O sujeito já não era nenhum gênio, depois disso então...\n\n<Galamav faz uma careta.>\n\nQuando extraímos a flecha, encontramos um bilhete amarrado na haste. Era um desenho tosco de um orc por trás das grades, assinado pelo Comandante Gor'shak.\n\nPode ser que ainda esteja vivo nas Profundezas, você tem que ir atrás dele.\n\nArmadilha? Não brinca! Você acha que quem vai é você por quê?O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[3982] = {title = "O que está acontecendo?", objective = "Defenda Gor'shak.", description = "Eu fui mandado aqui sob os auspícios de Thrall. Você acha mesmo que esses palhaços conseguiram me prender? Eu permiti que me capturassem para poder reunir informações.\n\n<Gor'shak sussurra.>\n\nA Princesa de Altaforja foi sequestrada pelo Imperador Thaurissan. A Princesa Barbabronze está aqui na cidade! Eu aposto que o coração do velho Magni quase parou quando ele recebeu a notícia.\n\n<Gor'shak ri.>\n\nShhh! Você ouviu isso? Os guardas estão vindo! EM GUARDA!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[4001] = {title = "O que está acontecendo?", objective = "Fale com Kharan Feromalho para obter informações sobre o sequestro da Princesa Moira Barbabronze. Leve as informações para Thrall em Orgrimmar.\n\nGor'shak mencionou que Kharan estava preso em uma cela próxima.", description = "Até agora, minha comunicação com Kharan, o anão na cela defronte, foi limitada. Apesar disso, consegui convencê-lo de que eu, ou melhor, nós não estamos contra ele.\n\nVá ter com ele e descubra tudo o que for possível sobre a Princesa e seus captores. Transmita as informações ao Chefe Guerreiro.\n\nThrall não quer que a Princesa sofra, pelo contrário, ele tem um grande plano que envolve devolvê-la a Altaforja sã e salva. Kharan já está a par de tudo... Vá logo!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[4002] = {title = "Os Reinos do Leste", objective = "Fale com Thrall se estiver <preparado/preparada> para assumir a missão que ele planejou.", description = "Você traz excelentes informações, <name>.\n\nComo Chefe Guerreiro, é meu dever fazer o melhor pela Horda. Garantir que as gerações que vierem depois de nós não sofram com uma existência sem esperança.\n\nA oportunidade para formar as nossas futuras alianças caiu no nosso colo. Uma tentativa de ajudar os anões pode fortalecer as nossas relações nos Reinos do Leste. É por isso então que você não deve falhar!\n\nEstá <preparado/preparada>?O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[4003] = {title = "O resgate real", objective = "Mate o Imperador Dagran Thaurissan e liberte a Princesa Moira Barbabronze do feitiço maligno.", description = "O ataque contra os anões Ferro Negro, se for bem-sucedido, causará um choque em todo o reino.\n\nA Princesa Barbabronze está sendo controlada pelo Imperador Thaurissan. Para libertá-la, você precisa matar o imperador. Esteja <avisado/avisada>, a sua tarefa é duplamente perigosa, já que Barbabronze atacará você sem hesitação enquanto estiver sob o controle do imperador.\n\nNÃO CAUSE MAL A ELA!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[4004] = {title = "A princesa foi resgatada?", objective = "Fale novamente com Thrall!", description = "Tu mataste meu marido, <race>! Minha cria virá ao mundo sem pai.\n\n<A Princesa Moira aponta para você.>\n\nEu te garanto que essa criança ainda se sentará no trono de Altaforja. Tu e toda tua raça serão caçados até o derradeiro dos dias por esta barbaridade.\n\n<Você tem a impressão de que a Princesa Moira ainda está sob efeito do feitiço de Thaurissan.>\n\nMe deixa em paz, <race>. O teu Chefe Guerreiro deve estar se mordendo para saber no que deu a tua patifaria.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nDeterminação de ThrallOlho de Orgrimmar\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[4005] = {title = "Aquementas", objective = "Leve o Livro de Aquor, as Garras Prateadas e o Coração de Ferrárbol até Tanaris.\n\nUse o Livro de Aquor para evocar Aquementas.\n\nApós ter completado o totem, leve-o para J.D. Neri no Refúgio do Marshal.", description = "Leve esse saco de suprimentos com você até Tanaris. Vá para a Enseada do Armador Perdido e procure um círculo de pedras.\n\nA península mais a leste é a chave...\n\nNesse círculo, leia o Livro de Aquor e evoque a única criatura que pode transformar os itens que você coletou em um totem para derrotar o Agni.\n\nAquementas não vai ficar feliz em ser acordado, mas se lembre de que você deve permanecer no círculo para que ele transforme os itens.", progress = "Então você voltou, né? Hm... Deixa eu dar uma olhada nisso, <name>.", completion = "Agni, você diz...\n\nSim, eu sei que um elemental poderoso reside no centro chamuscado da cratera. Nós simplesmente mantemos distância. Ninguém foi capaz de penetrar a aura intensa que envolve ele. Muitos foram lançados das trilhas rochosas para o terreno mais baixo ao tentar enfrentar ele.", rewards = "Você receberá:\n\nGarras Prateadas"},
[4021] = {title = "Contra-ataque!", objective = "Leve um Pedaço do Estandarte de Krom'zar para Regthar Portões-da-morte, a oeste da Encruzilhada.", description = "A Horda comemora os êxitos que obteve contra os Kolkar, mas os Kolkar, por sua vez, estão enfurecidos. Há relatos de que um contingente dos Kolkar viajou do lar deles, na Desolação, até os Sertões, em busca de vingança. O último relatório indica que, neste exato momento, eles investem contra as trincheiras, vindos do oeste!\n\nEnfrente os invasores Kolkar e dê fim à ameaça que eles representam aos Sertões. Mate-os até que seu líder, o Senhor da Guerra Krom'zar, apareça. Derrote-o e traga-me um pedaço de seu estandarte como prova.\n\nContamos com você, <name>.", progress = "Trouxe o estandarte?", completion = "Maravilha! Você derrotou os Kolkar!\n\nVou cuidar para que seus atos cheguem aos ouvidos de Thrall, &lt;name&gt;.\n\nOrgulhe-se. Pois a Horda se orgulha de você.", rewards = "Você receberá:\n\n"},
[4022] = {title = "O gosto das chamas", objective = "Mostre a Ciro Terepento a Muda de Pele da Revoada Negra que você recebeu de Kalaran Finalâmina.", description = "A revoada está ciente do seu trabalho na Garganta Abrasadora.\n\nMostre para mim, <race>. A exúvia, idiota.", progress = "<Os olhos de Ciro formam duas esferas incandescentes.>", completion = "&lt;Ciro examina a muda de pele.&gt;\n\nVocê mostrou que não é de todo imprestável, mortal.", rewards = "Você receberá:\n\n"},
[4023] = {title = "O gosto das chamas", objective = "Mostre a Ciro Terepento uma prova de seu valor.\n\nVocê tem a impressão de que Ciro já sabe que você não é <digno/digna>.", description = "Você busca a revoada? Apresente-me uma prova de seu valor, mortal.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[4024] = {title = "O gosto das chamas", objective = "Vá para o Abismo Rocha Negra e mate Bael'gar.\n\nA única coisa que você sabe é que o gigante mora no Abismo Rocha Negra. Lembre-se de usar a Muda de Pele Alterada da Revoada Negra nos restos mortais de Bael'gar para capturar a Essência Abrasadora.\n\nLeve a Essência Abrasadora Aprisionada para Ciro Terepento.", description = "Eu desejo provar das chamas causticantes da montanha de fogo. Deixar o magma da Rocha Negra banhar o meu ser. Essa essência, o calor abrasador, é o que desejo.\n\nBael'gar reside nas profundezas da Rocha Negra: uma criatura feita de chamas, nascido das labaredas de Ragnaros. Mate Bael'gar e, de seus restos fumegantes, traga-me um pedaço de seu fogo interior.\n\nUse a exúvia em seus restos para extrair o fogo interior com segurança.\n\nTenha sucesso e seja <recompensado/recompensada>.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nCapa de XistocouroEspaldares de Couro de SerpeFaixa Valconiana\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[4041] = {title = "O elixir Videre", objective = "Traga-me uma raiz do mal e, é claro, eu faço um Elixir Videre para ti.\n\nEu sei todo tipo de coisa sobre plantas e alquimia... Ora, eu vou te contar a história da vez que criei ouro a partir do queijo que ia comer no almoço... Ei, aonde tu vai?", description = nil, progress = "Traga-me uma raiz do mal e, é claro, eu faço um Elixir Videre para ti.\n\nEu sei todo tipo de coisa sobre plantas e alquimia... Ora, eu vou te contar a história da vez que criei ouro a partir do queijo que ia comer no almoço... Ei, aonde tu vai?", completion = "Tu passou a perna no velho Miblon, não foi?", rewards = "Você receberá:\n\nElixir Videre"},
[4061] = {title = "O levante das máquinas", objective = "Vá para as Estepes Ardentes e recupere 10 Estilhaços Elementais Fraturados para a Hierofante Theodora Malvadania.\n\nVocê se lembra que Theodora mencionou que os golens e elementais dessa região são uma fonte desses estilhaços.", description = "Soubemos das máquinas durante nossas incursões iniciais pela Garganta Abrasadora. Ao investigarmos melhor, descobrimos que estavam sendo importadas das Estepes Ardentes, provavelmente do Abismo Rocha Negra. É por isso que precisamos de você, <name>.\n\nSua primeira missão é se aventurar até as Estepes Ardentes e recuperar estilhaços elementais dos autômatos da rocha e criações inferiores da rocha da região. Quando você tiver recolhido uma quantidade suficiente, fale novamente comigo para que eu possa determinar a origem delas.", progress = "Não pense que esta tarefa será fácil, <name>!", completion = "&lt;Theodora dispõe os fragmentos diante de si.&gt; \n\n&lt;Theodora examina cuidadosamente os fragmentos com um enorme monóculo.&gt;\n\nInteressante. Na base estrutural de cada um desses fragmentos, posso ver... iniciais gravadas.", rewards = "Você receberá:\n\n"},
[4062] = {title = "O levante das máquinas", objective = "Leve a Amostra de Estilhaço Elemental para Lotwil Viriato.\n\nSegundo Theodora, Lotwil estava escalado em um acampamento ao leste.", description = "L.G.A.\n\nEssas são as iniciais gravadas nos estilhaços elementais. Abaixo das iniciais há um algum tipo de ranque. As máquinas que você destruiu nas Estepes Ardentes eram do \"Ranque IV\". Imagino que esse seja um ranque inferior.\n\nLeve esta amostra para Lotwil Viriato no acampamento a leste. Ele é um estudioso de algum renome no campo das ciências elementais. Ele talvez seja capaz de nos ajudar com nossa investigação.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[4063] = {title = "O levante das máquinas", objective = "Encontre e mate Lorde Golem Argelmach. Leve a cabeça dele para Lotwil. Você também precisará coletar 10 Núcleos de Elemental Intactos dos Golens Rasgafúria e Constructos Armipotentes que protegem Argelmach. Você sabe disso porque é paranormal.", description = "Não pode ser o Argelmach! Ele foi morto dez anos atrás. Como eu tenho certeza? Porque fui eu quem o matou.\n\nEle era um anão desprezível, determinado a deturpar o trabalho da minha vida para atender seus planos diabólicos. Parece que os maus sempre encontram uma maneira de escapar da justiça.\n\nSe isso é obra do Argelmach, então precisamos destruí-lo. Eu vou precisar de amostras das últimas criações dele. Com as amostras adequadas, eu talvez consiga impedir esse levante. As criações estão protegendo Argelmach no Abismo Rocha Negra.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nSobreveste da Lua LazúliClâmide LançachuvaArmadura de Escama BasálticaManoplas de Placamagma\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[4081] = {title = "SE VIR, MATE: Anões Ferro Negro", objective = "Desbrave o Abismo Rocha Negra e destrua aqueles invasores degenerados!\n\nO Senhor da Guerra Trincador quer que você mate 15 Guardas Furiadastra, 10 Carcereiros Furiadastra e 5 Soldados Rasos Furiadastra. Fale novamente com ele quando o trabalho estiver terminado.", description = "Por ordem do Senhor da Guerra Trincador, comandante da Força Expedicionária de Karrath:\n\nToda e qualquer força do General Forjaversa no Abismo Rocha Negra deve ser aniquilada.\n\nOs homens de Forjaversa são assassinos impiedosos, responsáveis pelo massacre da 109ª divisão da Força Expedicionária de Karrath. Os heróis são aconselhados a ter extremo cuidado ao confrontar esses bárbaros.\n\nQualquer um que destruir a primeira linha das forças do General Forjaversa no Abismo Rocha Negra receberá um tributo.", progress = nil, completion = "Esse primeiro ataque deve chamar a atenção do Forjaversa. Agora, pode ir, soldado. Eu tenho assuntos para resolver, batalhas para planejar e inimigos para esmagar!\n\nVerifique os cartazes de procura-se para conseguir mais trabalho.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[4082] = {title = "SE VIR, MATE: Oficiais Ferro Negro de Alta Patente", objective = "Vá até o Abismo Rocha Negra e destrua aqueles invasores degenerados!\n\nO Senhor da Guerra Trincador quer que você mate 10 Socorristas Furiadastra, 10 Soldados Furiadastra e 10 Oficiais Furiadastra. Fale novamente com ele quando o trabalho for concluído.", description = "Por ordem do Senhor da Guerra Trincador, comandante da Força Expedicionária de Karrath:\n\nO Grão-mestre Lexlort, chefe da divisão de reconhecimento da F.E.K., regressou com notícias do paradeiro de oficiais Ferro Negro do alto escalão. Por detrás de portões trancados a chave, no coração da cidade, eles treinam forças a fim de apagar nossos guerreiros. Vá para o Abismo Rocha Negra e destrua-os!\n\nO cumprimento desta missão será bem recompensado.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[4083] = {title = "Cálice Espectral", objective = "O cálice espectral flutua no ar, subindo e descendo lentamente... como se seguisse o bater de um coração agonizante.", description = nil, progress = "O cálice espectral flutua no ar, subindo e descendo lentamente... como se seguisse o bater de um coração agonizante.", completion = "As gemas não emitem som algum ao cair no fundo do cálice...", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[4084] = {title = "Coração de prata", objective = "Colete 11 Garras Prateadas e 1 Coração de Ferrárbol para Eridan Brisazul na Selva Maleva.", description = "Eu não conheço esse de quem você fala, <name>.\n\nMas não se preocupe. Se o seu amigo estava em uma jornada para me encontrar, eu sei o que ele almejava.\n\nNo coração da Cratera Un'Goro, habita o mais puro mal. Esse mal assume a forma de um elemental chamado Agni... Que é protegido por uma aura que ninguém consegue dispersar...\n\nNinguém... exceto aqueles com poder suficiente. Eu posso lhe ensinar esse poder, <name>.\n\nPrimeiro, faça o que lhe digo. Colete 11 Garras Prateadas e 1 Coração de Ferrárbol das criaturas na Selva Maleva, e então fale comigo novamente.", progress = "Eu posso lhe contar mais sobre o poder necessário para derrotar Agni, mas primeiro você precisa reunir os itens de que necessito.", completion = "Muito bem, &lt;name&gt;. Posso ver que está convicto de ajudar seu amigo.", rewards = "Você receberá:\n\n"},
[4101] = {title = "Purificar Selva Maleva", objective = "Leve 15 Âmbares de Sangue para Arathandris Argencélio na Selva Maleva.", description = "As plantas dão vida à Selva Maleva, ainda assim elas sofrem com a mácula da corrupção. Eu conheço maneiras de purificá-las, mas não posso passar tal conhecimento para aqueles que não conheço ou confio. Prove que você de fato ajudará a causa do Círculo Cenariano!\n\nAo norte, você encontrará as Cavernas Ferrárbol. Os elementais Lenhatorta que vagam por lá não têm chance de serem purificados. Traga-me a seiva deles — chamada de Âmbar de Sangue — para que eu possa estudá-la e encontrar uma maneira de salvar outras formas de vida.", progress = "Eu sei que Selva Maleva sofre, <name>. Eu sofro junto com ela! Eu devo permanecer firme na minha convicção. Se o conhecimento que possuo caísse em mãos erradas, seria um desastre para toda a Selva Maleva.\n\nTraga para mim o âmbar de sangue retirado dos elementais de Lenhatorta, e eu terei confiança o bastante para deixar que me ajude a encontrar um meio de deter a corrupção.", completion = "Este âmbar de sangue será usado para encontrar uma maneira de deter a corrupção presente na Selva Maleva. Você provou que está disposto a fazer de tudo pelo Círculo Cenariano. Eu confio em você agora, meu amigo.\n\nPode parecer estranho que matar os elementais tenha conquistado minha confiança, mas a salvação de incontáveis seres sencientes justifica as ações que tomarmos. Você e eu agora podemos nos concentrar na preservação da vida por meio dos bálsamos que sei preparar.\n\n&lt;name&gt;, chegou a hora de purificar Selva Maleva.", rewards = "Você receberá:\n\n"},
[4102] = {title = "Purificar Selva Maleva", objective = "Leve 15 Âmbares de Sangue para Maibess Brisa do Rio em Selva Maleva.", description = "As plantas dão vida à Selva Maleva, ainda assim elas sofrem com a mácula da corrupção. Eu conheço maneiras de purificá-las, mas não posso passar tal conhecimento para aqueles que não conheço ou confio. Prove que você de fato ajudará a causa do Círculo Cenariano!\n\nAo norte, você encontrará as Cavernas Ferrárbol. Os elementais Lenhatorta que vagam por lá não têm chance de serem purificados. Traga-me a seiva deles — chamada de Âmbar de Sangue — para que eu possa estudá-la e encontrar uma maneira de salvar outras formas de vida.", progress = "Eu sei que Selva Maleva sofre, <name>. Eu sofro junto com ela! Eu devo permanecer firme na minha convicção. Se o conhecimento que possuo caísse em mãos erradas, seria um desastre para toda a Selva Maleva.\n\nTraga para mim o âmbar de sangue retirado dos elementais de Lenhatorta, e eu terei confiança o bastante para deixar que me ajude a encontrar um meio de deter a corrupção.", completion = "Este âmbar de sangue será usado para encontrar uma maneira de deter a corrupção presente na Selva Maleva. Você provou que está disposto a fazer de tudo pelo Círculo Cenariano. Eu confio em você agora, meu amigo.\n\nPode parecer estranho que matar os elementais tenha conquistado minha confiança, mas a salvação de incontáveis seres sencientes justifica as ações que tomarmos. Você e eu agora podemos nos concentrar na preservação da vida por meio dos bálsamos que sei preparar.\n\n&lt;name&gt;, chegou a hora de purificar Selva Maleva.", rewards = "Você receberá:\n\n"},
[4103] = {title = "Bálsamo via caça", objective = "Os lampiões cenarianos permitem que o portador enxergue estilhaços de alma corrompida nas feras que são abatidas por um bem maior. Esses estilhaços são triturados para fazer um reagente com o qual preparo o bálsamo vegetal cenariano. Usaremos esse bálsamo para devolver a saúde a plantas corrompidas.\n\nEm troca desses estilhaços, darei a você um pouco do bálsamo vegetal cenariano que já preparei.", description = nil, progress = "Os lampiões cenarianos permitem que o portador enxergue estilhaços de alma corrompida nas feras que são abatidas por um bem maior. Esses estilhaços são triturados para fazer um reagente com o qual preparo o bálsamo vegetal cenariano. Usaremos esse bálsamo para devolver a saúde a plantas corrompidas.\n\nEm troca desses estilhaços, darei a você um pouco do bálsamo vegetal cenariano que já preparei.", completion = "Pois bem, aqui estão os bálsamos vegetais cenarianos. Explore a Selva Maleva em busca de plantas corrompidas, &lt;name&gt;! Onde quer que estejam, use o bálsamo que dei a você para purificá-las.", rewards = "Você receberá:\n\nBálsamo Vegetal Cenariano"},
