[4116] = {title = "Flor da Canção corrompida", objective = "Você encontrou uma versão doentia e corrompida do que parece ser uma flor da canção. Um pungente odor insalubre emana da planta. Ela precisa desesperadamente de cuidados para voltar ao normal.", description = nil, progress = "Você encontrou uma versão doentia e corrompida do que parece ser uma flor da canção. Um pungente odor insalubre emana da planta. Ela precisa desesperadamente de cuidados para voltar ao normal.", completion = "Você aplica um pouco de bálsamo vegetal cenariano na flor da canção. Gradualmente, ela deixar cair a forma corrompida e rebrota como uma planta vibrante e saudável.", rewards = nil},
