[5892] = {title = "Suprimentos dos Ferrofundo", objective = "Leve 10 Suprimentos de Ferrofundo para o Intendente da Aliança em Dun Baldar.", description = "Antes que a luta começasse, nós estocamos suprimentos para Dun Baldar na mina Ferrofundo. Os troggs que apareceram por lá não davam muito trabalho quando tínhamos tropas sobrando para guardar a mina. Mas agora, lutando com a Horda, está ficando difícil manter o controle de Ferrofundo.\n\nPrecisamos dos suprimentos dessa mina, <name>. Entre lá e me traga suprimentos!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[5893] = {title = "Suprimentos de Dentefrio", objective = "Leve 10 Suprimentos de Dentefrio para o Intendente da Horda na Bastilha Lobo do Gelo.", description = "A mina dos Dentefrio, ao norte daqui, já foi um lugar ideal para estocar suprimentos, mas agora, com as lutas contra os Lançatroz ficando tão renhidas, já não temos guerreiros suficientes para protegê-la. Assim, o controle da mina costuma ficar com a Aliança ou com os habitantes dos subterrâneos...\n\nMas ainda precisamos dos suprimentos, <name>! Vá até a mina Dentefrio e me traga quanto puder carregar. E cuidado com os inimigos quando for pra lá. Os rumos da batalha mudam constantemente naquele lugar...Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[5901] = {title = "Praga de cupins!", objective = "Use a Jarra de Cupins Vazia nos Cupinzeiros que há nas Terras Pestilentas Orientais. Após conseguir 100 Cupins das Terras Pestilentas, fale novamente com Guel Levine no Baluarte, nas Clareiras de Tirisfal.", description = "Para causar dor e discórdia na Cruzada Escarlate, quero inutilizar a preciosa serraria deles. Sei que a perda de um recuso tão precioso vai causar o maior prejuízo quando eles tiverem que defender e reconstruir a cidade. Você vai me ajudar, <name>?\n\nPrimeiro, leve esse jarro até o Bosque Pestilento, que fica nas Terras Pestilentas Orientais. Encha o pote com cupins, tem vários cupinzeiros por lá. Volte quando tiver uma boa quantidade.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[5902] = {title = "Praga de cupins!", objective = "Encontre um lugar apropriado para pôr o Barril de Cupins das Terras Pestilentas no centro da Serraria de Beiranorte, que fica nas Terras Pestilentas Ocidentais.\n\nItem fornecido:\n", description = "Agora que temos cupins de sobra, leve este barril de aço reforçado para a Serraria de Beiranorte, ao sul de Amparo. Não é difícil achar, fica a oeste da estrada que leva à cidade.\n\nLá, deixe o barril no centro da serraria, em algum lugar bem alto, tipo em cima de um caixote.\n\nAí é só abrir o barril e o resto vai se desenrolar por conta própria. Depois volte aqui e eu vou recompensar você pela sua ajuda.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[5903] = {title = "Praga de cupins!", objective = "Use a Jarra de Cupins Vazia nos Cupinzeiros que há nas Terras Pestilentas Orientais. Após conseguir 100 Cupins das Terras Pestilentas, fale novamente com Nataniel Dumah no Acampamento Ventogelante, em Terras Pestilentas Ocidentais.", description = "Já tentei combater a Cruzada Escarlate usando só a força bruta, mas foi em vão. Então, decidi mudar de tática e tornar a vida deles mais difícil.\n\nSe quiser ajudar, o que eu quero fazer é infestar aquela serraria com o pior tipo de cupins que já existiu.\n\nNo Bosque Pestilento, perto da outrora grande cidade de Stratholme, existe uma espécie que sobreviveu à peste e ao Flagelo! Encontre os cupinzeiros e encha esse jarro de cupins. Depois, vamos soltar os bichos contra a Cruzada.", progress = "Encontrou os cupinzeiros, <name>? Ouvi falar que todo o Bosque Pestilento está cheio deles.", completion = "Perfeito, &lt;name&gt;. Obrigado. Olha só pra eles... são tantos! Dá quase para sentir pena da Cruzada, mas por outro lado, eles não tinham nada que ter tomado a serraria que não pertencia a eles, né?", rewards = "Você receberá:\n\n"},
[5904] = {title = "Praga de cupins!", objective = "Encontre um lugar apropriado para pôr o Barril de Cupins das Terras Pestilentas no centro da Serraria de Beiranorte, que fica nas Terras Pestilentas Ocidentais.\n\nItem fornecido:\n", description = "Agora que temos cupins de sobra, leve este barril de aço reforçado para a Serraria de Beiranorte, ao sul de Amparo. Não é difícil achar, fica a oeste da estrada que leva à cidade.\n\nLá, deixe o barril no centro da serraria, em algum lugar apropriado, tipo em cima de um caixote, e abra. Aí os cupins vão se encarregar do resto do trabalho e logo a serraria vai estar inutilizável para a Cruzada.", progress = nil, completion = "Você põe o barril em cima do caixote, e fica bem claro que os cupins estão inquietos.", rewards = "Você receberá:\n\n"},
[5921] = {title = "Clareira da Lua", objective = "Use o feitiço \"Teleporte: Clareira da Lua\" para viajar para a Clareira da Lua. Ao chegar, fale com Dendrite Stellardor, no vilarejo de Refúgio Noturno.", description = "Todos os druidas são bem-vindos nas terras sagradas da Clareira da Lua, a despeito das tensões no Círculo Cenariano entre o Arquidruida Guenelmo e Remulos, o guardião da Clareira da Lua. Agora que você está aprendendo o caminho da Garra, vou ensinar o feitiço para que você se teletransporte para lá. Lance o feitiço sempre que precisar da sabedoria ou do apoio da Clareira da Lua\n\nPara a sua primeira missão, viaje para a Clareira da Lua e fale com Dendrite Stellardor, no Refúgio Noturno, sobre o caminho que você está trilhando agora. Boa sorte, jovem druid<a/esa>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[5922] = {title = "Clareira da Lua", objective = "Use o feitiço \"Teleporte: Clareira da Lua\" para viajar para a Clareira da Lua. Ao chegar, fale com Dendrite Stellardor, no vilarejo de Refúgio Noturno.", description = "Todos os druidas são bem-vindos nas terras sagradas da Clareira da Lua, tanto taurens quanto elfos noturnos. Remulos, o Guardião da Clareira da Lua, cuida de todos os druidas que lá hibernam. Agora que você está aprendendo o caminho da Garra, vou ensinar o feitiço para que você se teletransporte para lá. Lance o feitiço sempre que precisar da sabedoria ou do apoio da Clareira da Lua\n\nPara a sua primeira missão, viaje para a Clareira da Lua e fale com Dendrite Stellardor, no Refúgio Noturno, sobre o caminho que você está trilhando agora. Boa sorte, jovem druid<a/esa>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[5923] = {title = "Atendendo o chamado", objective = "Fale com Mathrengyl Ursivagus no Enclave Cenariano, em Darnassus.", description = "Com licença, amig<o/a>...\n\nChegou ao meu conhecimento que Mathrengyl Ursivagus deseja falar com você. Parece que sua formação como <druida/druidesa> tomará um novo rumo, pois ele treinará você pessoalmente. Mathrengyl é o assistente imediato do próprio Arquidruida e é um dos druidas mais sábios e veneráveis de Teldrassil.\n\nVocê pode encontrá-lo no Enclave Cenariano de Darnassus, no segundo nível da árvore de Fandral Guenelmo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[5924] = {title = "Atendendo o chamado", objective = "Fale com Mathrengyl Ursivagus no Enclave Cenariano, em Darnassus.", description = "Com licença, amig<o/a>...\n\nChegou ao meu conhecimento que Mathrengyl Ursivagus deseja falar com você. Parece que sua formação como <druida/druidesa> tomará um novo rumo, pois ele treinará você pessoalmente. Mathrengyl é o assistente imediato do próprio Arquidruida e é um dos druidas mais sábios e veneráveis de Teldrassil.\n\nVocê pode encontrá-lo no Enclave Cenariano de Darnassus, no segundo nível da árvore de Fandral Guenelmo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[5925] = {title = "Atendendo o chamado", objective = "Fale com Mathrengyl Ursivagus no Enclave Cenariano, em Darnassus.", description = "Com licença, amig<o/a>...\n\nChegou ao meu conhecimento que Mathrengyl Ursivagus deseja falar com você. Parece que sua formação como <druida/druidesa> tomará um novo rumo, pois ele treinará você pessoalmente. Mathrengyl é o assistente imediato do próprio Arquidruida e é um dos druidas mais sábios e veneráveis de Teldrassil.\n\nVocê pode encontrá-lo no Enclave Cenariano de Darnassus, no segundo nível da árvore de Fandral Guenelmo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[5926] = {title = "Atendendo o chamado", objective = "Fale com Turak Runa Totem no Platô dos Anciãos, que fica no Penhasco do Trovão.", description = "Com licença, amig<o/a>...\n\nChegou ao meu conhecimento que Turak Runa Totem deseja falar com você. Parece que sua formação como <druida/druidesa> tomará um novo rumo, pois ele treinará você pessoalmente. Turak é um dos assistentes do Arquidruida. É reconhecido por sua sabedoria e paciência, e deve lhe ensinar bem.\n\nVocê pode encontrá-lo no Penhasco do Trovão, dentro da tenda principal do Círculo Cenariano, no Platô dos Anciãos.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[5927] = {title = "Atendendo o chamado", objective = "Fale com Turak Runa Totem no Platô dos Anciãos, que fica no Penhasco do Trovão.", description = "Com licença, amig<o/a>...\n\nChegou ao meu conhecimento que Turak Runa Totem deseja falar com você. Parece que sua formação como <druida/druidesa> tomará um novo rumo, pois ele treinará você pessoalmente. Turak é um dos assistentes do Arquidruida. É reconhecido por sua sabedoria e paciência, e deve lhe ensinar bem.\n\nVocê pode encontrá-lo no Penhasco do Trovão, dentro da tenda principal do Círculo Cenariano, no Platô dos Anciãos.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[5928] = {title = "Atendendo o chamado", objective = "Fale com Turak Runa Totem no Platô dos Anciãos, que fica no Penhasco do Trovão.", description = "Com licença, amig<o/a>...\n\nChegou ao meu conhecimento que Turak Runa Totem deseja falar com você. Parece que sua formação como <druida/druidesa> tomará um novo rumo, pois ele treinará você pessoalmente. Turak é um dos assistentes do Arquidruida. É reconhecido por sua sabedoria e paciência, e deve lhe ensinar bem.\n\nVocê pode encontrá-lo no Penhasco do Trovão, dentro da tenda principal do Círculo Cenariano, no Platô dos Anciãos.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[5929] = {title = "Espírito do Grande Urso", objective = "Procure o Espírito do Grande Urso na região noroeste da Clareira da Lua e aprenda o que ele tem a compartilhar com você sobre a natureza do urso. Quando terminar, fale novamente com Dendrite Stellardor no Refúgio Noturno, que fica na Clareira da Lua.", description = "Na região noroeste da Clareira da Lua encontra-se a casa de um ser sábio e nobre que chamamos de Espírito do Grande Urso. Ele tem servido como tutor e mentor para todos os druidas em busca da primeira verdadeira compreensão natural do que é o urso. Todos os druidas em determinado momento buscam a sabedoria do espírito, e você não será exceção.\n\nVá ao Espírito do Grande Urso agora e aprenda o que ele tem a lhe ensinar. Quando ele tiver terminado, fale novamente comigo aqui no Refúgio Noturno. E então veremos se você compreendeu verdadeiramente as lições que ele ensina.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[5930] = {title = "Espírito do Grande Urso", objective = "Procure o Espírito do Grande Urso na região noroeste da Clareira da Lua e aprenda o que ele tem a compartilhar com você sobre a natureza do urso. Quando terminar, fale novamente com Dendrite Stellardor no Refúgio Noturno, que fica na Clareira da Lua.", description = "Na região noroeste da Clareira da Lua encontra-se a casa de um ser sábio e nobre que chamamos de Espírito do Grande Urso. Ele tem servido como tutor e mentor para todos os druidas em busca da primeira verdadeira compreensão natural do que é o urso. Todos os druidas em determinado momento buscam a sabedoria do espírito, e você não será exceção.\n\nVá ao Espírito do Grande Urso agora e aprenda o que ele tem a lhe ensinar. Quando ele tiver terminado, fale novamente comigo aqui no Refúgio Noturno. E então veremos então se você compreendeu verdadeiramente as lições que ele ensina.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[5931] = {title = "De volta a Darnassus", objective = "Volte a Darnassus e fale com Mathrengyl Ursivagus no Enclave Cenariano.", description = "Chegou a hora de nos separarmos, jovem, já que a prática das lições leva você de volta àquele que <o/a> enviou a mim. Mathrengyl Ursivagus caminha em verdadeiro equilíbrio com a natureza, ainda que more perigosamente perto de onde o equilíbrio se desbalanceou. Ouça as instruções dele, e em breve você completará suas primeiras lições no papel de proteto<r/ra> do equilíbrio.\n\nVá agora, jovem. Nós voltaremos a nos encontrar.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[5932] = {title = "De volta ao Penhasco do Trovão", objective = "Volte ao Penhasco do Trovão e fale com Turak Runa Totem no Platô dos Anciãos.", description = "Chegou a hora de nos separarmos, jovem, já que a prática das lições leva você de volta àquele que <o/a> enviou a mim. Embora os taurens sejam considerados jovens nos caminhos de Cenarius, Turak Runa Totem caminha com muito equilíbrio na presença da natureza. Ouça as instruções dele, e em breve você completará suas primeiras lições no papel de proteto<r/ra> do equilíbrio.\n\nVá agora, jovem. Nós voltaremos a nos encontrar.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[5941] = {title = "De novo com Crona", objective = "Leve a Coleção de Relíquias para Crona em Andorhal.", description = "Você reuniu muitas relíquias da batalha de Vila das Flechas. Toda vez que você voltava com mais, meu coração ansiava pela minha aldeia... ansiava em protegê-la de seu destino.\n\nSei que não pode ser, mas Crona, aquela estranha gnomida de Andorhal, me diz que é possível. Ela diz que você pode salvar Vila das Flechas!\n\nEla me pediu para reunir as relíquias para que você as leve até ela. Disse que espera você na estalagem em ruínas de Andorhal.", progress = "Estive com seu amigo Carlin, que anda sofrendo muito com a ruína da aldeia dele. Mas ele te elogiou muito e sabe que você pode consertar as coisas.\n\nAh, puxa, estou fugindo do assunto... <name>, você trouxe as relíquias que pedi?", completion = "Ah, sim, aí estão elas. Juntas, elas formam a trama de um poderoso feitiço, que elevará os espíritos de Vila das Flechas.\n\nCom esse feitiço, e um pouco de sorte, podemos salvar Josefo Trilharrubra!", rewards = "Você receberá:\n\n"},
[5942] = {title = "Tesouros escondidos", objective = "Leve a Chave de Josefo ao Baú de Josefo.", description = "O papai me disse para te dar esta chave. Ele disse que ela abre um baú que fica atrás da casa.\n\nEle também pediu que eu te agradecesse. Você fez algum favor para ele? Você contou para ele que encontrou a minha boneca?\n\nBem, aqui está a chave. E obrigada, <name>. Obrigada! Você é uma pessoa muito boa!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nAnel de ProteçãoRebento ArruinamagoTalismã Curvaluz"},
[5943] = {title = "Caravana Gizelton", objective = "Escolte a Caravana Gizelton pelas Ruínas de Mannoroc. Fale com o Smid no Acampamento do Rabiscafuso para receber a recompensa.", description = "Você parece ser <um/uma> <race> de qualidade. Quer ganhar um dinheirinho? Eu e o Rolho começamos essa caravana para fazer muita grana, mas não sabíamos que descolar uma bufunfa poderia ser tão perigoso!\n\nAí na frente estão as Ruínas de Mannoroc. Os demônios costumam ignorar a nossa passagem, mas alguma coisa assustou os kodos da última vez. Eu pago bem para você escoltar a caravana pelas Ruínas de Mannoroc. Depois que a gente passar em segurança, você pode pedir a recompensa ao Smid, nosso sócio, no Acampamento do Rabiscafuso.", progress = nil, completion = "Não sei se é lucrativo pagar tanto dinheiro para proteger a Caravana Gizelton. O Rolho e o Armador não ganham mais que uma moeda de ouro por semana, mas enquanto eles continuarem pagando o aluguel dos meus kodos eu não estou nem aí para como eles administram o negócio.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nArcabuz do Artilheiro de FlancoFerrete de KodosEscudo Anelar Rebitado\n\nVocê também receberá:\n\n"},
[5944] = {title = "No mundo dos sonhos", objective = "Escolte Taelan Fordring para fora de Amparo.", description = "Por tanto tempo, tenho sido um fantoche do Grande Cruzado. Que razão havia para lutar contra o que a Cruzada Escarlate se tornara? Já faz décadas, mas as lembranças do meu pai, essas lembranças preciosas, são o que me mantiveram vivo.\n\nEu tenho sonhos, forasteir<o/a>. Nesses sonhos, meu pai está comigo. Ele está orgulhosamente ao meu lado enquanto sou admitido na Ordem. E nós combatemos a legião do Flagelo, lado a lado. Trazemos honra para a Aliança, para Lordaeron.\n\nNão quero mais sonhar.\n\nMe leve até ele.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nMarca de FordringPeitoral Ornado de AdamântioMartelo de Guerra de Platina CintilanteMortalha dos ExiladosSelo de Fordring\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[5961] = {title = "O campeão da Rainha Banshees", objective = "Fale com Nathanos, o Arauto da Praga nas Terras Pestilentas Orientais. Dizem que ele fixou residência no Sítio dos Marris.", description = "Deleita-me ver guerreiros de tantas batalhas juntando-se à nossa causa. Crescemos em número e força a cada dia que se passa. Talvez seja o momento para colocar seu ânimo à prova, <class>.\n\nNathanos, que já foi um de meus mais valorosos campeões, é hoje conhecido como o Arauto da Praga. Não há muitos em nossas fileiras cuja dedicação e prontidão superem as de Nathanos.\n\nProcure por ele nas Terras Pestilentas Orientais. Ele reside no Sítio dos Marris. Costuma vagar pelos ermos, dizimando toda e qualquer escória, do Flagelo ou Aliança, que ouse cruzar seu caminho.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[5981] = {title = "Gigantes descontrolados", objective = "Ripárion Glaciares quer que você mate 4 Gigantes Friomalho e 4 Preservadores Friomalho.", description = "Aproximadamente 4 mil anos atrás, Tyrande Murmuréolo salvou a vida da rainha dos sabres-de-gelo das mãos dos gigantes descontrolados. Desde então, os sabres-de-gelo se tornaram aliados dos elfos noturnos.\n\nEsses mesmos gigantes que já foram uma ameaça para os sabres-de-gelo ainda se encontram aqui por perto. Eles são conhecidos como friomalho e tomaram o vale grande que há ao sul.\n\nProve a sua lealdade aos sabres-de-gelo matando os gigantes em nome da rainha sabre-de-gelo, Shy-Rotam!Completando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = nil},
[6001] = {title = "Corpo e coração", objective = "Aplique Poeira de Lua Cenariana na Pedra Luniscante de Auberdine para trazer Lunagarra. Então, enfrente Lunagarra e ganhe a força de corpo e coração que ele possui.\n\nFale com Mathrengyl Ursivagus em Darnassus quando você terminar.", description = "À leste de Auberdine, há muitas cavernas luniscantes. Dentro da que fica mais perto de Auberdine, há uma Pedra Luniscante. Lunagarra, o defensor da pedra, só enfrentará você quando a Poeira de Lua Cenariana for aplicada à pedra. Lunagarra possui dentro de si uma força que você deve usar como <um dos/uma das> Garras.\n\nVocê ganhou o direito de usar esta Poeira de Lua Cenariana, <name>... agora me mostre que você conquistou o direito de possuir a força do corpo e do coração necessárias como <druida/druidesa>!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nForma de Urso\n\nVocê receberá:\n\n"},
[6002] = {title = "Corpo e coração", objective = "Aplique Poeira Lunar Cenariana na Pedra Luniscante entre Mulgore e os Sertões para evocar Lunagarra. Então, enfrente Lunagarra e ganhe a força de corpo e coração que ele possui.\n\nFale com Turak Runa Totem no Penhasco do Trovão quando terminar.", description = "Nas terras a oeste de Taurajo, nos Sertões — pouco antes de darem lugar a Mulgore —, há uma Pedra Luniscante. Os luniscantes são criaturas especiais, não nativas dessas bandas. Lunagarra, o defensor da pedra, enfrentará você quando Poeira Lunar Cenariana for aplicada à pedra. Lunagarra possui dentro de si uma força que você deve usar como <um dos/uma das> que trilham o caminho da Garra.\n\nVocê ganhou o direito de usar esta Poeira de Lua Cenariana, <name>... agora mostre-me que você conquistou o direito de possuir a força de corpo e coração necessária como <druida/druidesa>!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nForma de Urso\n\nVocê receberá:\n\n"},
[6004] = {title = "Negócios inacabados", objective = "Mate 2 Socorristas Escarlate, 2 Caçadores Escarlate, 2 Magos Escarlate e 2 Cavaleiros Escarlate antes de voltar a Kirsta Sombrafunda, nas Terras Pestilentas Ocidentais.", description = "Fui enviada como batedora para fazer um pouco de estrago e avaliar a força das defesas de Amparo. Algumas pessoas na Aliança sentem que a cidadezinha tem mais do que importância histórica. Não é meu trabalho questionar essa importância, mas, independentemente disso, não estou em condições de continuar até descansar um pouco.\n\nSe você estiver dispost<o/a>, eu pagaria bem por uma mãozinha. Comece pelo acampamento deles perto do pequeno baluarte que montaram, ao sul daqui. Se você provar ser forte o suficiente, talvez eu possa contar com sua ajuda em meu objetivo principal.", progress = nil, completion = "Muito bem, &lt;name&gt;. Estou surpresa que você tenha cuidado tão rapidamente deles. Obrigada.\n\nAinda não conheci ninguém que aprove a Cruzada Escarlate e seus métodos. Acredito na Luz tanto quanto qualquer outro em Ventobravo, mas eles demonstraram que têm como objetivo matar quem não for leal ao movimento Escarlate... até mesmo inocentes.", rewards = "Você receberá:\n\n"},
[6021] = {title = "Zaeldarr, o Proscrito", objective = "Leve a Cabeça de Zaeldarr ao Zelador Alen na Capela Esperança da Luz.", description = "Dá para acreditar que, mesmo depois de todo nosso trabalho nas Terras Pestilentas... ainda temos que nos preocupar com ladrões? E nem são ladrões comuns, são ladrões trolls.\n\nAliás, também não são ladrões trolls comuns, e sim ladrões trolls mortos!\n\nEles são liderados por um troll morto grandalhão chamado Zaeldarr, o Proscrito, e vêm aqui à noite para roubar corpos da cova lá embaixo e levá-los de volta para o esconderijo maligno de trolls mortos deles — a Chácara Secreta, a oeste daqui.\n\nVá lá e acabe com Zaeldarr, <name>. Estou farto das maldades dele!", progress = "Você encontrou o esconderijo, <name>? Cuidou do Zaeldarr?", completion = "Ótimo, você pegou ele! E espero que você também tenha ensinado uma lição à gangue dele!", rewards = "Você receberá:\n\n"},
[6022] = {title = "Matar com propósito", objective = "Vá ao Cruzamento de Corin, a leste, e mate membros do Flagelo que habitam a cidade. Colete 7 pedaços de Putrefação Viva em menos de 10 minutos. Use o Almofariz e Pilão para moer a Putrefação Viva, de maneira a transformá-la em Putrefação Coagulada.", description = "Na minha terra, só se mata com propósito.\n\nSua primeira tarefa é alimentar meus cães de caça.\n\nPegue este almofariz e este pilão e vá ao Cruzamento de Corin. Ao chegar lá, você encontrará vários membros robustos do Flagelo para eliminar. Preciso da putrefação viva das carcaças frescas deles. Pegue a putrefação viva e moa no almofariz, então espere coagular. Traga a putrefação coagulada de volta aqui.\n\nE imbecil, perceba que a putrefação viva dos mortos-vivos não permanece \"viva\" por muito tempo. Você precisa matá-los rapidamente!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nPutrefação Viva"},
[6023] = {title = "Negócios inacabados", objective = "Mate o Guarda-caça Eiras e o Fidalgo Durgen antes de voltar à Kirsta Sombrafunda, nas Terras Pestilentas Ocidentais.", description = "Minha verdadeira missão era enfraquecer as forças remotas da Cruzada Escarlate e depois me infiltrar em Amparo para avaliar seus números. Ainda não estou em condições de fazer isso sozinha, então quem sabe você possa me ajudar de novo, <name>? Pelas informações que consegui reunir antes de ser descoberta, a Cruzada tem dois membros importantes que são a espinha dorsal das forças deles fora da cidade: o Guarda-caça Eiras e o Fidalgo Durgen. Mate-os e faremos progresso. Eles devem estar ao longo da Passagem de Amparo, perto da cidade propriamente dita.", progress = nil, completion = "Obrigada, &lt;name&gt;. Você me ajudou mais do que eu poderia esperar. O ar daqui parece sugar minhas energias. Me sinto cansada, e sei que não estou melhorando nada com o tempo.\n\nNão sei bem como vou me infiltrar em Amparo e informar sobre a força da presença da Cruzada Escarlate.\n\nMas essa já é outra questão. Antes de você ir embora, aqui, tome isto... como pagamento por sua ajuda. E mais uma vez obrigada.", rewards = "Você receberá:\n\n"},
[6024] = {title = "A súplica de Hameya", objective = "Mate o Infiltrador Hameya. Use a chave dele no Monturo de Terra atrás da Chácara Secreta.", description = "Mate-me. Antes que seja tarde demais.\n\nMeu nome é Hameya. Enquanto viajávamos para Zul'Mashar, meus irmãos e eu comemos uma carne ruim... e mudamos. Estamos infectados.\n\nPosso sentir as palavras do lich agora, me incitando a chegar a Zul'Mashar e infectar outros do meu povo. Não posso resistir, então escrevi este pergaminho antes de perder o controle sobre minha vontade.\n\nEnterrei um baú perto de uma carroça atrás dessa cripta, mas apenas minha chave o abrirá. Estou com a chave. Se você quiser meu tesouro, então me encontre.\n\nMate-me. Antes que seja tarde demais.", progress = "O baú enterrado aqui está muito bem trancado.", completion = "O baú de Hameya está aberto!", rewards = "Você receberá:\n\nEspada de HameyaManto de Hameya"},
[6025] = {title = "Negócios inacabados", objective = "Entre em Amparo e encontre um lugar alto de onde possa ver a cidade e avaliar as forças da Cruzada Escarlate. Então fale novamente com Kirsta Sombrafunda nas Terras Pestilentas Ocidentais.", description = "O quê? Mesmo? Você quer saber mais? Mas você já fez tanta coisa por mim, <name>. Eu não poderia lhe pedir para entrar em Amparo em meu lugar.\n\nTem certeza, <name>? Amparo é muito mais perigoso que a Passagem... não posso lhe dar nenhuma proteção nem mesmo informações sobre o que você pode ter que enfrentar.\n\nBem, se você estiver realmente dispost<o/a> a ajudar, entre na cidade e encontre um lugar alto para ter uma visão geral. De lá, você pode calcular quantos eles são e voltar para me contar. Mas tenha cuidado....", progress = nil, completion = "Você conseguiu, &lt;name&gt;! Obrigada!\n\nVoltarei para Elling... quero dizer, Ventobravo, assim que puder. Aqui, pegue esta moeda, é o mínimo que posso fazer. Você é uma pessoa de grande valor, &lt;name&gt;. Obrigada e boa sorte.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[6026] = {title = "Isso é pedir muito", objective = "Leandro Leonardo quer que você pegue 2 Barras de Tório, 1 Bastão Dourado, 8 Bombas de Alto-explosivo e 8 Gatilhos Instáveis.\n\nVocê terá que encontrar um ferreiro e um engenheiro para esses itens.", description = "Esse pessoal da Aurora Argêntea contratou o velho Leandro aqui pra fazer um servicinho de demolição para eles, tchê. Bah, mamãe do Leandro não criou nenhum tonto. Se de alguns pilas vierem me falar, os serviços do Leandro podem contratar. Esse é o meu lema!\n\nMas eu até poderia dividir a comissão se tu estiver dispost<o/a> a bater perna. Tu só não pode negar o estribo. O negócio é o seguinte: vou de vereda ao Bosque Pestilento marcar os prédios que precisamos destruir. Tu agrupa o material para as bombas. Me encontra aqui quando tiver tudo. Combinado?", progress = "Bah, tu podes confiar em mim, <race>. A recompensa vai valer a pena, tchê. Esse pessoal da Aurora Argêntea não brinca quando se trata de pagamento.", completion = "Posso te pagar metade adiantado e metade quando o trabalho estiver concluído, guri.", rewards = "Você receberá:\n\n"},
[6027] = {title = "O Livro dos Anciãos", objective = "Pegue o Livro dos Anciãos e entregue-o para Azore Albamorte na torre de Ethel Rethor.", description = "<name>, eu confio a você a Gema da Serpente. Coloque-a na mão da Estátua da Serpente, na Ilha Ranazjar, a noroeste daqui. Quando em posição, a gema invocará o protetor do Livro dos Anciãos, que guarda os segredos das nagas, escondidos há séculos. Traga o livro para mim, e então saberemos mais sobre nossas rivais escamosas!", progress = "Ah, <name>! É um prazer revê-lo. Conseguiu pegar o Livro dos Anciãos?", completion = "O Livro dos Anciãos, há tanto desaparecido! Vou tomar providências para que seja levado ao alto conselho. Você agiu bem hoje, e por isso devemos lhe agradecer... como a um herói!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nManilhas da Torrente SedosaOmoplatas de Arquimetal\n\nVocê também receberá:\n\n"},
[6028] = {title = "O relatório de Visteterna", objective = "Entregue o Relatório de Visteterna à Oficial Argêntea Cândida no Acampamento Ventogelante, que fica nas Terras Pestilentas Ocidentais.", description = "Estou procurando alguém responsável para me ajudar com uma questão pequena, mas ainda assim importante. Estou aqui como representante dos interesses da Aurora Argêntea para o governo local de Visteterna. Preciso levar um relatório de progresso à Oficial Argêntea Cândida no Acampamento Ventogelante, que fica meio longe daqui, ao longo do limite sul das Terras Pestilentas Ocidentais.\n\nSe me permite o atrevimento... essa seria a maneira perfeita de apresentar você à minha superior e talvez conseguir que ela lhe passe algum trabalho!", progress = "Saudações, <class>. Em nome da Aurora Argêntea, ofereço-lhe segurança e abrigo enquanto estiver aqui no Acampamento Ventogelante. Talvez eu também tenha algum trabalho para você...", completion = "Gregor é mesmo muito engenhoso. Ele vem lidando com o Cartel Bondebico há algum tempo e se tornou um especialista na \"arte\" das negociações com os goblins. Embora seja verdade que os goblins são mais leais a quem tiver as maiores bolsas de moeda, eles também reconhecem e respeitam o valor da diplomacia. No mínimo, isso lhes garante clientes fiéis...\n\nDe qualquer forma, a Aurora Argêntea reconhece o esforço que você fez. Por favor, aceite isso como agradecimento.", rewards = "Você receberá:\n\n"},
[6029] = {title = "O relatório de Visteterna", objective = "Entregue o Relatório de Visteterna ao Oficial Argênteo Garush no Baluarte, nas Terras Pestilentas Ocidentais.", description = "Estou procurando alguém responsável para me ajudar com uma questão pequena, mas ainda assim importante. Estou aqui como representante dos interesses da Aurora Argêntea para o governo local de Visteterna. Preciso levar um relatório de progresso ao Oficial Argênteo Garush no Baluarte, que fica meio longe daqui, ao longo do limite oeste das Terras Pestilentas Ocidentais, a leste das Clareiras de Tirisfal.\n\nSe me permite o atrevimento... essa seria a maneira perfeita de apresentar você ao meu superior e talvez conseguir que ele lhe passe algum trabalho!", progress = "Saudações, <class>. Em nome da Aurora Argêntea, ofereço-lhe segurança e abrigo enquanto estiver aqui no Baluarte. Também posso lhe oferecer a chance de lutar por uma causa pela qual vale a pena!", completion = "Gregor é bastante engenhoso para um humano. Ele vem lidando com o Cartel Bondebico há algum tempo e se tornou um especialista na \"arte\" das negociações com os goblins. Embora seja verdade que os goblins são mais leais a quem tiver as maiores bolsas de moeda, eles também reconhecem e respeitam o valor da diplomacia. No mínimo, isso lhes garante clientes fiéis...\n\nDe qualquer forma, a Aurora Argêntea reconhece o esforço que você fez. Por favor, aceite isso como agradecimento.", rewards = "Você receberá:\n\n"},
[6030] = {title = "Duque Nicolau Zverenhoff", objective = "Entregue o livro \"Estudos sobre Comunicação com Espíritos\" ao Duque Nicolau Zverenhoff na Capela Esperança da Luz, que fica nas Terras Pestilentas Orientais.", description = "Um médium recluso encontrado por essas bandas e que atende pelo nome de Umbranse produziu uma obra-prima literária para a Aurora Argêntea... uma \"obra-prima\" que equivale, provavelmente, à maior coleção de bobagens que já vi escrita.\n\nUm dos líderes mais respeitados da Aurora, o Duque Nicolau Zverenhoff, é um linguista genial. Você poderia levar isso para ele por mim? Em troca de uma recompensa, é claro. Vá para a Capela Esperança da Luz, no extremo leste das Terras Pestilentas Orientais. É lá onde ele está no momento.", progress = "Que a Luz o guie nestes tempos difíceis, <class>. O que traz você aqui a este pendão de esperança no meio da escuridão e do desespero?", completion = "Este... este livro que você me trouxe é muito especial. Eu gostaria de lhe dizer que \"especial\" neste caso significa \"único e possivelmente esclarecedor\", mas isso ainda temos que ver.\n\n\"Aaaaah, é?\" Quem em sã consciência pensa assim?\n\nBem, já ouvi rumores sobre este Umbranse de quem Gregor falou, e pelo visto não foi à toa. Farei o melhor para decifrá-lo. Quanto a você... por favor, aceite isso... cortesia da Aurora Argêntea. E, enquanto você estiver por aqui, talvez haja outras coisas com que possa nos ajudar.", rewards = "Você receberá:\n\n"},
[6031] = {title = "Runatrama", objective = "Leve 30 cortes de Runatrama para Meilosh no Domínio dos Presamatos.", description = "Aqui em Presamatos a gente é feliz. Não gostamos de estranhos. Isso é bom pra gente, mas não é bom pro comércio.\n\nNão faz muito tempo, uma elfa noturna, uma de nossas raras amigas, visitou Presamatos. Ela usava um manto de runatrama. O manto era tão belo que meu povo desejou ter roupas feitas de runatrama também. Mas runatrama aqui é um artigo raro.\n\nVocê me traria um pouco desse tecido? Se você trouxer bastante runatrama, eu lhe darei algo em troca.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nEspaldares de Couro SombrioManopla Tijolaço\n\nVocê também receberá:\n\n"},
[6032] = {title = "Tecido sagrado", objective = "Leve 2 cortes de Lunatrama para Meilosh no Domínio dos Presamatos.", description = "Embora os Presamatos adorem a maciez da runatrama, esse não é um tecido sagrado para nós. Mas lunatrama é.\n\nTraga um pouco desse tecido sagrado e eu lhe ensinarei uma técnica secreta de costura.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nBotas de Lunatrama\n\nO seguinte feitiço será lançado em você:\n\nBotas de Lunatrama\n\nVocê também receberá:\n\n"},
[6041] = {title = "Quando Leandro canta, eu fico violento", objective = "Viaje ao Bosque Pestilento, a noroeste da Esperança da Luz. Destrua 8 Estruturas do Flagelo usando o Composto Químico Especial do Leandro na Marca de Detonação plantada dentro de cada construção. Os Zigurates e Abatedouros foram marcados por instrução de Leandro.\n\nAssim que você plantar a bomba, deixe a estrutura e contemple seu trabalho.\n\nA propósito, o Composto Químico Especial do Leandro é bastante... instável.", description = "Bah, enquanto tu reunia os suprimentos, pedi a alguns dos meus para marcar os prédios do Flagelo que precisamos demolir.\n\nO plano é o seguinte: vou lhe dar dez bastões do meu composto especial. Tu vai levá-los ao Bosque Pestilento e plantá-los dentro das estruturas do Flagelo que eu marquei para detonar.\n\n<Leandro estala os dedos.>\n\nPronto! Simples assim, tchê.", progress = nil, completion = "Bah, estou te achando bem mais escuro do que me lembro. Tu tá com aquele jeito que diz: \"Fui carbonizado por uma bomba altamente explosiva\".\n\n&lt;Leandro ri.&gt;\n\nEnfim! Toma aqui a tua parte. Acrescentei mais umas pilas para ajudar a cuidar das queimaduras.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nLança-bombas do FumaçaLança-fogo do Fumaça\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\nToalhinha ÚmidaClâmide do Fumaça"},
[6042] = {title = "Pequenos transtornos da Morte-em-Vida", objective = "Mate 20 Quirópragos Nóxios e 10 Quirópragos Monstruosos. Fale novamente com Nathanos Arauto da Praga quando você tiver terminado.", description = "Ninguém dá dois passos fora desta fazenda sem se deparar com um desses morcegos detestáveis. Quero poder sair com os cães e massacrar o Flagelo sem esbarrar em nenhum maldito morcego. Obviamente, tenho coisas melhores a fazer com o meu tempo do que matar morcegos entra dia, sai noite. Você, por outro lado, não tem.\n\nO quê? Seu treinamento? Este é o seu treinamento! Agora saia da minha frente e não volte até ter matado um bom número desses demônios alados. Não me desaponte.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6061] = {title = "Domando a fera", objective = "Use o Bastão de Adestramento para domar um Pinote Adulto. Pratique sua habilidade e depois devolva o Bastão de Adestramento a Yaw Crina Cortante na Aldeia Casco Sangrento.", description = "Como um tauren, é importante lutar contra criaturas poderosas para seguir as tradições de nossos ancestrais. Ao vencer essas criaturas, podemos incorporar seus espíritos, permitindo uma aproximação maior com a Mãe Terra.\n\nDa mesma forma, domar uma fera para lutar ao nosso lado ajuda a nos integrarmos à natureza ao nosso redor. Uma hora, você aprenderá a domar a fera que escolher. Mas todas as coisas exigem prática e você tem muito a aprender. Tome este bastão de adestramento. Para começar, você precisa domar um pinote adulto.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6062] = {title = "Domando a fera", objective = "Use o Bastão de Adestramento para domar uma Mosquetusco Hediondo. Pratique sua habilidade e depois devolva o Bastão de Adestramento a Thotar no Monte Navalha.", description = "É uma habilidade admirável impor sua autoridade sobre a natureza. Como caçado<r/ra>, você deve aprender a usar a força e o poder inerentes a qualquer criatura que escolha.\n\nControlar um animal não é tarefa fácil. É preciso encará-la com muita determinação.\n\nComo primeira tarefa, pegue este bastão de adestramento e use-o para domar um mosquetusco hediondo. Você pode encontrá-los bem perto do Monte Navalha. Pratique suas habilidades em batalha com o moquetusco ao seu lado, depois fale novamente comigo.", progress = "Boa sorte, jovem <class>.", completion = "Você se saiu bem, &lt;name&gt;. Acredito que você esteja pronto para sua próxima tarefa.", rewards = "Você receberá:\n\n"},
[6063] = {title = "Domando a fera", objective = "Use o Bastão de Adestramento para domar uma Tocaieira Lenhateia. Pratique sua habilidade e depois devolva o Bastão de Adestramento a Dazalar em Dolanaar.", description = "Como <caçador/caçadora>, é importante lembrar a responsabilidade que você tem em mãos. A natureza não é um poder que possa ser forçado a atender nossos desejos — deve ser respeitado e reconhecido como algo mais forte do que nossa própria existência.\n\n<name>, você deve provar que compreende isso antes de ganhar a habilidade de domar um animal para ser seu ajudante.\n\nA primeira coisa a fazer é pegar este bastão de adestramento. Com ele, você será capaz de domar uma tocaieira lenhateia. Dome-a e pratique suas habilidades como <caçador/caçadora>.", progress = "Você vai aprender, <name>, e um dia será uma grande <class>.", completion = "Excelente, &lt;name&gt;! Você aprende rápido.", rewards = "Você receberá:\n\n"},
[6064] = {title = "Domando a fera", objective = "Use o Bastão de Adestramento para domar uma Rochetusco Grande. Pratique sua habilidade e depois devolva o Bastão de Adestramento a Grif Selvacuore em Kharanos.", description = "Olá, <guri/guria>! Então tu estás se perguntando como pode ganhar seu próprio ajudante, tchê?\n\nBem, vamos começar. Não há nada mais importante do que o equipamento quando se trata de caça, mas podemos conversar sobre o meu novo Trabuco de Estilhaços outra hora. No momento, vamos nos concentrar em algo que tenho certeza de que tu estás bastante interessad<o/a>: um animal ajudante!\n\nA melhor maneira de escolher é experimentar várias criaturas como mascote e ver de qual tu mais gosta. Então encontre um rochetusco grande e use o bastão de adestramento para domá-lo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6065] = {title = "O caminho do caçador", objective = "Fale com Yaw Crina Cortante na Aldeia Casco Sangrento.", description = "Olá, jovem <caçador/caçadora>. Você me olha como quem tem habilidade suficiente para lidar com uma fera e treiná-la como sua mascote.\n\nProcure Yaw Crina Cortante na Aldeia Casco Sangrento. Ele pode colocar você no caminho certo para ganhar um ajudante.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6066] = {title = "O caminho do caçador", objective = "Fale com Yaw Crina Cortante na Aldeia Casco Sangrento.", description = "Olá, jovem <caçador/caçadora>. Você parece ter habilidade suficiente para lidar com uma fera e treiná-la como sua mascote.\n\nProcure Yaw Crina Cortante na Aldeia Casco Sangrento. Ele pode colocar você no caminho certo para ganhar um ajudante.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6067] = {title = "O caminho do caçador", objective = "Fale com Yaw Crina Cortante na Aldeia Casco Sangrento.", description = "Olá, jovem <caçador/caçadora>. Você me encara como quem tem habilidade para lidar com uma fera e treiná-la como sua mascote.\n\nProcure Yaw Crina Cortante na Aldeia Casco Sangrento. Ele pode colocar você no caminho certo para ganhar um ajudante.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
