[6076] = {title = "O caminho do caçador", objective = "Fale com Grif Selvacuore em Kharanos.", description = "Olá, jovem <caçador/caçadora>. Tu me olha como quem entende do riscado para lidar com uma fera e treiná-la como sua mascote.\n\nProcure Grif Selvacuore em Kharanos, tchê. Ele pode colocar você no caminho certo para ganhar um ajudante.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6081] = {title = "Treinando a fera", objective = "Fale com Ormak Tiroatroz em Orgrimmar.", description = "Agora você tem o poder de domar um ajudante, mas também precisa aprender a treiná-lo.\n\nVá a Orgrimmar. Lá, procure Ormak Tiroatroz, um dos nossos caçadores mais respeitados. Ele lhe ensinará a treinar seu novo ajudante. Você vai encontrá-lo perto do Salão dos Valentes.\n\nBoa sorte, <name>.", progress = nil, completion = "Ah, um jovem &lt;class&gt;! Sim, posso ensinar o que você precisa para treinar e orientar seu ajudante. Você não só vai ser capaz de ensinar novas habilidades ao seu ajudante, como também de alimentá-lo e revivê-lo se ele tombar em batalha.\n\nPode ir, &lt;class&gt;. Outro dia, nós conversamos.", rewards = "Você irá aprender:\n\nLição de Treinamento\n\nO seguinte feitiço será lançado em você:\n\nTreinamento de Feras\n\nVocê também receberá:\n\n"},
[6082] = {title = "Domando a fera", objective = "Use o Bastão de Adestramento para domar um Escorpídeo Encouraçado. Pratique sua habilidade e depois fale novamente com Thotar no Monte Navalha.", description = "Você deveria se espelhar na última criatura que terá que domar: o escorpídeo encouraçado. Ele ataca com velocidade e confiança. Dominar tal criatura é o seu destino de caçado<r/ra>.\n\nQuando você voltar, eu transmitirei a você o conhecimento de que você precisará para domar o animal que você escolher. A criatura ficará sob seu comando. Seu mascote ganhará experiência ao seu lado. Também mostrarei como chamá-lo e dispensá-lo quando quiser.", progress = "Você conseguiu domar o escorpídeo?", completion = "Você aprendeu tudo que eu tinha para ensinar, &lt;name&gt;. É hora de usar o que aprendeu e escolher uma mascote para acompanhá-lo.\n\nCá estamos, &lt;name&gt;. Agora eu lhe darei o poder de domar as feras, assim como de chamar e dispensar as feras à sua conveniência. Use suas novas habilidades com orgulho. Você fez por merecer.", rewards = "Você irá aprender:\n\nLição de Adestramento\n\nO seguinte feitiço será lançado em você:\n\nDomar Fera\n\nVocê também receberá:\n\n"},
[6083] = {title = "Domando a fera", objective = "Use o Bastão de Adestramento para domar uma Surfatisco. Pratique sua habilidade e depois devolva o Bastão de Adestramento a Thotar no Monte Navalha.", description = "A vantagem que um animal ajudante lhe oferece contra seus rivais é bem clara, <name>. Quando você ganha controle sobre outra criatura, aumenta a própria força. Seu ajudante deve se tornar uma extensão de você mesmo.\n\nA tarefa seguinte exigirá que você se afaste da terra, pois vai encontrar a próxima criatura nas águas da costa. Do outro lado da Aldeia Sen'jin, perto das Ilhas do Eco. Procure um caranguejo espirituoso chamado surfatisco. Use o bastão de adestramento para domá-lo e depois pratique suas habilidades.", progress = "Você precisa praticar para obter as habilidades necessárias para controlar de fato seu ajudante. Você já domou um surfatisco?", completion = "Você aprende rápido, &lt;name&gt;. Saiu-se muito bem.", rewards = "Você receberá:\n\n"},
[6084] = {title = "Domando a fera", objective = "Use o Bastão de Adestramento para domar uma Leopardo da Neve. Pratique sua habilidade e depois fale novamente com Grif Selvacuore em Kharanos.", description = "Bah, esses rochetuscos são bastante corpulentos. Talvez tu prefira um animal com um pouco mais de finesse? Está bem, então, acho que tenho a fera certa para ti. A sul e leste daqui, tu vai encontrar grandes felinos brancos chamados leopardos da neve. Assim como um rifle elegantemente esculpido, por trás de seu belo exterior há um poderoso guerreiro capaz de causar danos extremos. Tu descobrirá que eles são muito mais ágeis e rápidos do que o rochetusco que domaste, tchê.\n\nPegue este bastão de adestramento e veja se você gosta do leopardo das neve.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6085] = {title = "Domando a fera", objective = "Use o Bastão de Adestramento para domar um Urso Garra de Gelo. Pratique sua habilidade e depois devolva o Bastão de Adestramento a Grif Selvacuore em Kharanos.", description = "Vai ver tu tá atrás de uma criatura que possua algo além de força bruta. O urso garra de gelo é um excelente ajudante pra <um caçador novato/uma caçadora novata>, é um bicho que vive a leste de Kharanos. Aqui o bastão de adestramento. Experimenta esse mascote pra ver se te agrada!\n\nQuando tu voltar, eu te transmitirei o conhecimento de que tu vai precisar pra domar o animal que tu escolher. A criatura enfrentará os mesmos desafios que tu, e ganhará experiência ao seu lado. Também vou mostrar como chamar e dispensar ela quando te der na telha.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nLição de Adestramento\n\nO seguinte feitiço será lançado em você:\n\nDomar Fera\n\nVocê também receberá:\n\n"},
[6086] = {title = "Treinando a fera", objective = "Fale com Bélia Granitrondo em Altaforja.", description = "<name>, agora tu já tens capacidade pra domar um mascote, mas ainda precisa de habilidade pra treinar o bicho.\n\nVai para Altaforja e procura o Salão das Armas. Lá, é só trocar uma palavra com Bélia Granitrondo, uma das nossas domadoras de feras mais respeitadas. Ela vai te ensinar a treinar teu mascote. Anda, te apressa gur<i/ia>!\n\nBélia é camarada que só, na certa que vai gostar de ti. Boa sorte, <name>.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nLição de Treinamento\n\nO seguinte feitiço será lançado em você:\n\nTreinamento de Feras\n\nVocê também receberá:\n\n"},
[6087] = {title = "Domando a fera", objective = "Use o Bastão de Adestramento para domar um Espreitador-da-pradaria. Pratique sua habilidade e depois devolva o Bastão a Yaw Crina Cortante na Aldeia Casco Sangrento.", description = "Você tem um espírito forte, <caçador/caçadora>, preparado para o seu próximo desafio.\n\nA próxima tarefa é conquistar a companhia de um lobo conhecido como espreitador-da-pradaria. Essa criatura é veloz e destemida. Para fazer dessa fera sua ajudante, você também deve assumir essas qualidades.\n\nDurante uma batalha, você aprenderá como formar uma equipe com seu ajudante. Por fim, vocês deverão trabalhar como se fossem um, músculo com músculo.\n\nAqui está o bastão de adestramento, <name>. Domine um espreitador-da-pradaria e pratique suas habilidades.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6088] = {title = "Domando a fera", objective = "Use o Bastão de Adestramento para domar uma Rapineiro. Pratique sua habilidade e depois devolva o Bastão a Yaw Crina Cortante na Aldeia Casco Sangrento.", description = "A última tarefa que tenho para você é domar uma criatura do céu. O rapineiro é um adversário difícil, mas quando for seu aliado, você herdará dele os ataques ferozes e a presença ameaçadora.\n\nSe a Mãe Terra assim quiser, quando você voltar, eu transmitirei a você o conhecimento de que você precisará para domar o animal que você escolher para ser seu ajudante. A criatura enfrentará os mesmos desafios que você, e ganhará experiência ao seu lado. Também mostrarei como chamá-lo e dispensá-lo quando quiser.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nLição de Adestramento\n\nO seguinte feitiço será lançado em você:\n\nDomar Fera\n\nVocê também receberá:\n\n"},
[6089] = {title = "Treinando a fera", objective = "Fale com Holto Chifre Troante no Platô dos Caçadores, que fica no Penhasco do Trovão.", description = "Agora você tem o poder de domar um ajudante, mas também precisa aprender a treiná-lo.\n\nVá até o Penhasco do Trovão. Fale com Holto Chifre Troante, um dos nossos caçadores mais respeitados. Se achar que você está à altura, ele lhe ensinará a treinar seu novo ajudante.\n\nHolto sobrepujou a mais poderosas e temidas feras de Azeroth, e agora tem sob seu controle muitas criaturas de grande força. Os laços que ele mantém com a Mãe Terra são profundos, portanto há muito o que aprender com ele. Boa sorte, <name>.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nLição de Treinamento\n\nO seguinte feitiço será lançado em você:\n\nTreinamento de Feras\n\nVocê também receberá:\n\n"},
[6101] = {title = "Domando a fera", objective = "Use o Bastão de Adestramento para domar um Espreitador Sabre-da-noite. Treine sua perícia, e depois devolva o Bastão de Adestramento a Dazalar em Dolanaar.", description = "A variação na natureza é algo com que se pode contar, <name>. Ela é previsível no sentido de jamais permanecer igual, de estar sempre em mudança.\n\nAs tarefas que eu lhe dou <o/a> ajudarão a ver e apreciar as vastas diferenças das criaturas você pode escolher como ajudantes.\n\nLeve este bastão de adestramento e use para domar um espreitador sabre-da-noite, ao sul daqui. Treine sua perícia de caçador<o/a>. Então você começará a entender as similaridades e diferenças da natureza.", progress = "Entender o equilíbrio entre a natureza e a própria pessoa é um modo de vida para todo elfo noturno.", completion = "Eu vejo sua dedicação, e vejo que você entendeu o que eu lhe ensinei, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[6102] = {title = "Domando a fera", objective = "Use o Bastão de Adestramento para domar uma Guinchadora Estrígida. Treine sua perícia, e depois devolva o Bastão de Adestramento a Dazalar em Dolanaar.", description = "Você domou duas criaturas da terra, e agora precisa domar uma que voe pelos céus. Use o bastão de adestramento para domar uma guinchadora estrígida, das que vivem a oeste daqui. Treine sua perícia com essa coruja poderosa a seu lado.\n\nQuando você voltar, eu transmitirei a você o conhecimento de que você precisará para domar o animal que você escolher para ser seu ajudante. A criatura enfrentará os mesmos desafios que você, e ganhará experiência ao seu lado. Também mostrarei como chamá-lo e dispensá-lo quando quiser.", progress = "Estou orgulhoso de suas conquistas, <name>. Terminou a última tarefa que eu lhe dei?", completion = "Você aprendeu tudo que eu posso lhe ensinar, &lt;name&gt;. Permita-me lhe dar mais um conselho: escolha sabiamente seu primeiro ajudante. Você escolherá uma criatura da terra, da água ou do ar? Qualquer que seja, sem dúvida seu ajudante permanecerá um bom tempo com você.\n\nCá estamos, &lt;name&gt;. Agora eu lhe darei o poder de domar as feras, assim como de chamar e dispensar as feras à sua conveniência. Use suas novas habilidades com orgulho. Você fez por merecer.", rewards = "Você irá aprender:\n\nLição de Adestramento\n\nO seguinte feitiço será lançado em você:\n\nDomar Fera\n\nVocê também receberá:\n\n"},
[6103] = {title = "Treinando a fera", objective = "Fale com Jocasta no Enclave Cenariano, que fica em Darnassus.", description = "Agora você tem o poder de domar um ajudante, mas também precisa obter a habilidade de treiná-lo.\n\nVá a Darnassus. Lá você terá que falar com Jocasta, uma das nossas caçadoras mais respeitadas. Ela lhe ensinará a habilidade de treinar seu novo ajudante, então se apresse.\n\nEmbora Jocasta possa parecer severa, ela fica sempre feliz ao ver outr<o/a> caçado<r/ra> subindo na carreira. Trate-a com respeito e ela retribuirá. Boa sorte, <name>.", progress = nil, completion = "Ah, uma &lt;class&gt;! Posso lhe transmitir a habilidade de treinar e orientar seu ajudante. Você não vai ser só capaz de ensinar novas perícias, mas alimentar e reviver seu ajudante se ele tombar em batalha.\n\nLembre-se, &lt;name&gt;, respeite sempre o equilíbrio e viva em reverência à natureza. Agora vá. Conversaremos de novo outro dia.", rewards = "Você irá aprender:\n\nLição de Treinamento\n\nO seguinte feitiço será lançado em você:\n\nTreinamento de Feras\n\nVocê também receberá:\n\n"},
[6121] = {title = "Novas lições", objective = "Vá à Clareira da Lua e fale com Dendrite Stellardor no vilarejo de Refúgio Noturno.", description = "Agora você está pront<o/a> para aprender sobre venenos e o poder que nós druidas temos sobre eles, <name>. Cenarius nos abençoou com a habilidade de purgar os mais torpes venenos com um simples encanto. Esse dom não é nada trivial. E, para garantir que você aprenda essa lição, você cuidará dos efeitos de envenenamentos como o fariam os leigos.\n\nVá à Clareira da Lua e fale com Dendrite Stellardor. Ele está à sua espera para dar sua aula.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6122] = {title = "A fonte principal", objective = "Use o Coletor Vazio das Quedas de Fontescarpa para coletar uma amostra de água da entrada da caverna da cachoeira.\n\nEntregue o Coletor Cheio das Quedas de Fontescarpa para Alanndária Noturcanto em Auberdine, na Costa Negra.", description = "As Quedas do Fontescarpa, na Costa Negra, são a principal fonte de água potável da região, e agora eu suspeito que seja a principal fonte do veneno que está prejudicando a fauna local, especificamente os cervos. Use esse coletor para trazer um pouco de água da entrada da caverna, mas tome cuidado. Agora há nagas perigosas vivendo na caverna.\n\nQuando terminar, entregue a Alanndária Noturcanto, em Auberdine. Foi ela que pediu nossa ajuda, e é com ela que você trabalhará para tratar esse mal.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6123] = {title = "Colhendo a cura", objective = "Leve 5 Raízes-telúricas e 12 Fungos Lunares para Alanndária Noturcanto em Auberdine para que ela possa preparar o bálsamo curativo.", description = "A água servirá como base do bálsamo curativo para os cervos. Pelas propriedades curativas, preciso de um pouco de raiz telúrica. Os herboristas sabem como encontrar. Se formos azarad<o/a>s e você não for herborista, você precisará achar alguém que nos ajude.\n\nE por fim, para dar fortalecer, preciso de fungo lunar. É fácil achar floresceres fúngicos; as cavernas a leste daqui são repletas deles. No entanto, moram luniscantes lá, e eles andam irritadiços ultimamente. Tome cuidado.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6124] = {title = "Curando os doentes", objective = "Use o Bálsamo Curativo para Animais em 10 Cervos Adoentados espalhados pela Costa Negra. Isso deve curá-los. Cervos Adoentados foram avistados da região ao sul do Rio Fontescarpa, seguindo até a área ao norte de Auberdine, até a Costa Negra meridional, na fronteira do Vale Gris.", description = "A quantidade de bálsamo feita com os reagentes que você me trouxe dá para dez doses, <name>. Enquanto eu trabalho aqui com a vila para tentar fazer mais bálsamo, eu gostaria que você adiantasse parte do trabalho. Há cervos doentes por toda a Costa Negra. Dê o bálsamo a eles e cure esse mal.\n\nTenho certeza de que Dendrite Stellardor deve querer conversar com você lá na Clareira da Lua quando você terminar. Transmita também ao Círculo Cenariano os agradecimentos de Auberdine, e... Permita-me agradecer a você diretamente pela ajuda que você nos prestou!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nBornal Medicinal de Povelo"},
[6125] = {title = "O poder sobre os venenos", objective = "Fale novamente com Mathrengyl Ursivagus no Enclave Cenariano, em Darnassus.", description = "Você aprendeu o que é curar venenos sem se beneficiar de ajuda espiritual. Lembre-se do que os outros precisam suportar ao enfrentar a aflição dos venenos enquanto abraça seu novo poder sobre eles.\n\nAgora você deve falar novamente com Mathrengyl Ursivagus em Darnassus, <name>. Sem dúvida ele terá um feitiço importante a lhe ensinar agora que <o/a> estudante entende as lições que recebe.\n\nAdeus, jovem druid<a/esa>. Nós nos encontraremos de novo.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nCurar Veneno\n\nVocê receberá:\n\n"},
[6126] = {title = "Novas lições", objective = "Vá à Clareira da Lua e fale com Dendrite Stellardor no vilarejo de Refúgio Noturno.", description = "Agora você está pront<o/a> para aprender sobre venenos e o poder que nós druidas temos sobre eles, <name>. Cenarius nos abençoou com a habilidade de purgar os mais torpes venenos com um simples encanto. Esse dom não é nada trivial. E, para garantir que você aprenda essa lição, você cuidará dos efeitos de envenenamentos como o fariam os leigos.\n\nVá à Clareira da Lua e fale com Dendrite Stellardor. Ele está à sua espera para dar sua aula.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6127] = {title = "A fonte principal", objective = "Use o Coletor Vazio do Morro de Brumedo para obter uma amostra de água de um lago no topo do morro.\n\nEntregue o Coletor Cheio do Morro de Brumedo a Tonga Runa Totem na Encruzilhada, nos Sertões.", description = "O Morro de Brumedo, a noroeste da Encruzilhada, é o melhor candidato a fonte de espalhamento do veneno. O pico ventoso tem lagos que influenciam o clima. Suspeito que a água agora esteja maculada pelos adoradores de demônios da Lâmina Ardente.\n\nUse esse coletor para pegar água do lago no topo do morro. Quando terminar, entregue tudo a Tonga Runa Totem, na Encruzilhada. Foi ele que pediu nossa ajuda, e você trabalhará com ele para remediar esse mal.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6128] = {title = "Colhendo a cura", objective = "Leve 5 Raízes-telúricas e 5 Chifres de Kodo para Tonga Runa Totem na Encruzilhada para que ele possa preparar um bálsamo curativo.", description = "A água servirá como base do bálsamo curativo das gazelas. Pelas propriedades curativas, preciso de um pouco de raiz telúrica. Os herboristas sabem como encontrar, mas, se você não tiver familiaridade com a perícia, vai precisar achar alguém que nos ajude.\n\nE por fim, para dar fortalecer, preciso de chifre de kodo, e meros fragmentos não vão bastar. Há kodos para os quais você será páreo, que vagam pelas áreas setentrionais dos Sertões. Abata alguns pelo bem maior do equilíbrio, <name>, e traga os chifres para mim.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6129] = {title = "Curando os doentes", objective = "Use o Bálsamo Curativo para Animais em 10 Gazelas Doentes espalhadas pelo norte dos Sertões. Isso deve curá-las. Gazelas Doentes foram avistadas por toda parte a norte da estrada leste-oeste, que atravessa a Encruzilhada.", description = "Consegui fazer bálsamo suficiente para dez doses, <name>. Enquanto eu trabalho aqui com as autoridades locais para tentar fazer mais bálsamo, eu gostaria que você adiantasse parte do serviço. Há cervos doentes por todo lado nos Sertões setentrionais. Dê o bálsamo a eles e cure esse mal.\n\nTenho certeza de que Dendrite Stellardor deve querer conversar com você lá na Clareira da Lua quando você terminar. Transmita também ao Círculo Cenariano os nossos agradecimentos, e... Permita-me agradecer a você diretamente pela ajuda que você nos prestou!Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nBornal Medicinal de Povelo"},
[6130] = {title = "O poder sobre os venenos", objective = "Fale novamente com Turak Runa Totem no Platô dos Anciãos, no Penhasco do Trovão.", description = "Você aprendeu o que é curar venenos sem se beneficiar de ajuda espiritual. Lembre-se do que os outros precisam suportar ao enfrentar a aflição dos venenos enquanto abraça seu novo poder sobre eles.\n\nAgora você deve falar novamente com Turak Runa Totem no Penhasco do Trovão, <name>. Sem dúvida ele terá um feitiço importante a lhe ensinar agora que <o/a> estudante entende as lições que recebe.\n\nAdeus, jovem druid<a/esa>. Nos encontraremos de novo.", progress = nil, completion = "Que bom que você voltou. Estive acompanhando seu progresso nessa lição desde que você partiu, e devo admitir que fiquei surpreso com a situação da Encruzilhada. Isso exige mesmo mais investigação, já que a introdução de tais venenos no ambiente é deveras perturbadora para o equilíbrio.\n\nVocê se portou muito bem sob pressão. Por isso, não consigo pensar em recompensa melhor que o poder sobre os venenos. Use-o bem, &lt;name&gt;... use-o bem.", rewards = "O seguinte feitiço será lançado em você:\n\nCurar Veneno\n\nVocê receberá:\n\n"},
[6131] = {title = "Aliando-se aos Presamatos", objective = "Grazle quer que você prove seu valor matando 5 Guerreiros de Lenha Morta, 5 Desbravadores de Lenha Morta e 5 Jardineiros de Lenha Morta.", description = "Os Presamatos são a única tribo de pelursos que escapou da corrupção. No entanto, muitas outras raças agora matam os pelursos às cegas, sem se preocupar em saber se são inimigos ou amigos. Por isso os Presamatos confiam em muito pouca gente.\n\nInteressad<o/a> em provar o seu valor? Rechace a tribo corrompida dos Lenha Morta, da Selva Maleva, e podemos um dia considerá-l<o/a> u<m/ma> aliad<o/a>. A oeste daqui você encontrará os primeiros Lenha Morta: guerreiros, desbravadores e jardineiros.Completando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = nil},
[6132] = {title = "Me tira daqui!", objective = "Escolte Melizza Zumbidorla e depois fale com Guido Zumbidorla no Cemitério dos Kodos, na Desolação.", description = "Me tira daqui!\n\nEu vim até o Vale das Lanças para conversar com os Maraudinos. Achei que topariam fazer negócio... Mas eles não quiseram nem conversar!\n\nSe você me tirar daqui, meu irmão Guido vai te dar uma recompensa polpuda! Ele está lá no nosso acampamento, perto do cemitério dos kodos, a leste daqui.\n\nDepressa, <name>. Não faz nem dois minutos que escutei uns centauros falando em pegar lenha para o caldeirão...", progress = nil, completion = "A Melizza disse que você apareceria. Muito obrigado por tirá-la daquela encrenca. Infelizmente ela já deve ter se metido em outra...", rewards = "Você receberá:\n\n"},
[6133] = {title = "A mando do lorde patrulheiro", objective = "Vá à fronteira norte das Terras Pestilentas Orientais e recupere os Arquivos de Quel'Thalas em algum lugar do Abrigo Quel'Lithien.\n\nVocê também precisa matar 8 Patrulheiros, 8 Trilheiros e 8 Mateiros.", description = "Os elfos superiores do Abrigo Quel'Lithien estão de posse de algo que me pertence, <class>: um documento que detalha minha vida como mortal.\n\nAntes que pergunte, não, você não está autorizad<o/a> a ter acesso a essas informações. Só faça o que eu mandei, verme. Recupere o registro.\n\nE... Imbecil, deixe um rastro de tumulto e destruição por onde passar. Deixe que eles sofram...Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6134] = {title = "Vá catar fantasmoplasma!", objective = "Leve 8 Fantasmoplasmas e o Caixote de Ímãs Fantasmais para Guido Zumbidorla na Desolação.", description = "Existe um vale a sudeste, chamado Vale dos Ossos, que é assombrado por fantasmas dos Magram. Assustador, não é? Imagina para os Magram! Se a gente conseguisse pegar um pouco da energia espectral daquele lugar, aposto que os centauros iriam pagar bem por isso.\n\nPegue este caixote de ímãs fantasmais. Se você colocar isso entre os dois esqueletões que estão lá no vale, os chamados Golias Mortos, os fantasmas vão se aproximar. Então, se afaste, mate os fantasmas e cate todo o fantasmoplasma – e vamos vender tudinho para os Magram!", progress = "Cadê meu fantasmoplasma, <name>?", completion = "Aí está! E parece que tem um outro tanto espalhado sobre você... Meio pegajoso, não é?\n\nBem, muito obrigado pelo esforço, &lt;name&gt;. Isso aqui vale uma fortuna... Se eu conseguir convencer os centauros disso!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBraceletes do CondorBroquel Ancorote\n\nVocê também receberá:\n\n"},
[6135] = {title = "Ah, Quiroscuro, como vos odeio...", objective = "Encontre Quiroscuro e mate-o. Do cadáver, extraia um Tufo de Pele de Quiroscuro e leve para Nathanos Arauto da Praga.\n\nDizem que Quiroscuro vaga pelo campo ao norte do Sítio dos Marris.", description = "Até agora, você tem cumprido as missões que incumbi a você, ainda que não da forma mais pontual.\n\nSuponho que você se considere pront<o/a> para um desafio. Sim... Sim, imbecil, talvez você esteja pront<o/a> para missões mais complexas.\n\nQuiroscuro, o morcego demoníaco albino perambula pelo campo bem ao norte daqui. Encontre e mate Quiroscuro. Traga um tufo do pelo branco dele para mim para receber uma recompensa.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nLuvas de QuiroscuroDragonas de Quiroscuro\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[6136] = {title = "O corpulento", objective = "Encontre Brocassangre nos ermos do norte das Terras Pestilentas e destrua-o. Fale novamente com Nathanos Arauto da Praga quando tiver cumprido a missão.", description = "Depois do acampamento da Aurora Argêntea, Esperança da Luz, você encontrará o corpulento verme feral, Brocassangre.\n\nPerdi regimentos inteiros para aquele monstro. O tamanho dele não conhece limites! Quanto mais ele come, maior ele fica...\n\nSe, por um lado, eu não serei emocionalmente afetado se a fera devorar você, por outro, torço para que você saia <vitorioso/vitoriosa>. Recomendo que você recrute outros para ajudá-l<o/a> na missão. Brocassangre é notório devorador de pequenos batalhões tolos o suficiente para atacá-lo.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nCospeçonhaMartelo RochacrânioGuia de Sarah\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[6141] = {title = "Irmão Anton", objective = "Fale com o Irmão Anton na Desolação.", description = "Meu irmão de armas, Irmão Anton, ouviu rumores de uma infestação de mortos-vivos na distante Kalimdor, em uma terra conhecida como Desolação. Anton imediatamente comprou passagem para lá, decidido a pôr fim à ameaça dos mortos-vivos!\n\nEle está à procura daqueles com bravura e determinação para ajudá-lo. Se seu coração é sincero, fale com Anton. Ele está em Desolação, na cidade da Aliança de Posto do Nijel.", progress = nil, completion = "Ouvi falar que viria ajuda do leste. Era de você que estavam falando? Você veio ajudar o Irmão Escarlate nessa cruzada?\n\nSe veio, escute bem...", rewards = "Você receberá:\n\n"},
[6142] = {title = "Boa isca", objective = "Encontre 10 nacos de Carne de Marisco de Casca Mole e entregue-os para Mai'Lahii na Aldeia Pescassombra.", description = "Você tem cara de <pescador/pescadora>, <class>! Eu adoro pescar. Faço isso desde criancinha. A melhor isca que já usei é carne de marisco de casca mole. Os peixes não conseguem resistir, sabe como é?\n\nTem um monte de mariscos de casca mole perto do navio naufragado ao largo da ponta norte da praia. Se você me trouxer dez nacos de carne de marisco, eu lhe dou uma recompensa.", progress = "Estou louca para pescar com isca de carne de marisco de casca mole... Você conseguiu trazer os dez nacos que pedi?", completion = "Perfeito, cara. Isso é justamente o que o mandingueiro me mandou fazer. Ele disse: \"Mai'Lahii, vai pescar. Vai te fazer sentir melhor\".\n\nBem, já estou me sentindo melhor, valeu mesmo, cara!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nGuantes ImoraisCinturão do MarsupialCinto de Aço Reforçado\n\nVocê também receberá:\n\nMarisco de Casca Mole"},
[6143] = {title = "Camarão que dorme a onda leva", objective = "Mate 7 Mirmidões Sibilâmina, 7 Nagas Sibilâmina e 5 Feiticeiras Sibilâmina. Em seguida, fale com Drulzeger, na Aldeia Pescassombra, em Desolação.", description = "Talvez você já tenha ouvido essa história, talvez não... Eu e minha tripulação saímos para pescar perto da Ilha Ranazjar, a norte do navio naufragado na ponta norte da costa. A gente sempre ia pescar lá. Agora, tudo que faço é pensar em vingança para os meus companheiros assassinados.\n\nCerta manhã, as nagas nos atacaram e mataram todos, menos eu.\n\nMinha consciência não me deixa viver com essa culpa, <class>. Eu preciso que você mate as nagas por mim. A recompensa é boa. E então? Dá para me ajudar nessa?", progress = nil, completion = "Por mais que me soe como música a notícia da derrota das nagas, meu coração ainda está vazio. Vou dedicar o resto da vida a matar essas criaturas.\n\nToma aqui, cara. É a sua recompensa, conforme prometi. E é pequena diante da satisfação em saber que as nagas pagaram pelo odioso ataque contra minha tripulação.", rewards = "Você receberá:\n\n"},
[6144] = {title = "Chamado ao comando", objective = "Vá à Cidade Baixa e fale com Varimatras.", description = "Ordens do comando, <class>. O próprio Varimatras pediu que eu mandasse meus agentes mais 'capacitados' à Cidade Baixa para uma operação tática altamente delicada.\n\nInfelizmente meus agentes mais capacitados morreram há mais de três anos. Em vez deles o que eu tenho agora é uma cambada de mentecapto.\n\n<Nathanos olha para você com frieza.>\n\nVá à Cidade Baixa imediatamente e apresente-se a Varimatras. Não me envergonhe, <class>!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6145] = {title = "O mensageiro carmesim", objective = "Volte às Terras Pestilentas Orientais e rastreie o Mensageiro Carmesim. Mate o mensageiro e obtenha as Ordens do Grão-cruzado.\n\nLeve-as para Nathanos Arauto da Praga para obter suas próximas instruções.", description = "Assim como o corpo não vive sem cabeça, a cabeça também não vive sem corpo. As defesas do comando da Cruzada Escarlate em Stratholme são quase impenetráveis. Em vez disso, nós precisamos é decepar o corpo.\n\nMeus Sicários vêm coletando informações sobre as atividades da cruzada fora de Stratholme. A cada dia um relatório é enviado do alto comando deles para a Manopla de Tyr. Esse relatório é a chave, <race>! Encontre o Mensageiro Carmesim, obtenha o relatório e leve-o para Nathanos.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[6146] = {title = "O ardil de Nathanos", objective = "Vá à Manopla de Tyr, a sudeste do Sítio dos Marris. Quando chegar lá, coma um pedaço da Maçã Podre. Com o disfarce da Cruzada Escarlate, entregue as Ordens do Grão-cruzado ao Lorde Cruzado Valdelmar.", description = "Fiz alguns ajustes nessas ordens. Se nosso pequeno truque funcionar, eles deixarão o Oráculo Escarlate exposto e nós atacaremos!\n\nAgora preste atenção, <class>. Você precisa entregar essa ordem ao Lorde Cruzado Valdelmar.\n\nComo? Você estenderá o braço e ele pegará, imbecil.\n\nLeve a ordem e essa maçã podre para a Manopla de Tyr. Quando entrar na cidade, coma a maçã. Você se transformará em algo mais... palatável para os humanos. Então pegue a ordem e entregue pessoalmente ao Lorde Cruzado Valdelmar.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nMaçã Podre"},
[6147] = {title = "Retorno a Nathanos", objective = "Fale novamente com Nathanos Arauto da Praga para repassar a ele as novas informações.", description = "Solicitação incomum, essa do grão-cruzado. Mmm. Não importa. Será feito o que ele pede. Volte ao Bastião Escarlate e notifique-o de que o Oráculo seguirá em breve.\n\nTomaremos todas as precauções para garantir que ela chegue ilesa a Stratholme.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[6148] = {title = "Demétria, o Oráculo Escarlate", objective = "A Cruzada Escarlate está em marcha. Em algum ponto da estrada da Manopla de Tyr para Stratholme estará o Oráculo, Demétria, e a comitiva dela. Encontre-a e mate-a. Fale novamente com Nathanos Arauto da Praga se tiver sucesso.", description = "Demétria é a conselheira-mor do Grande Cruzado. Ela recebeu a alcunha de Oráculo devido à sua presciência, tida como sobrenatural. Ela foi essencial para muitas vitórias da Cruzada contra o Flagelo.\n\nAinda que este estratagema a tenha exposto, se ela realmente tiver poderes psíquicos, é certo que ela estará esperando problema. Você vai se incumbir de dar a ela o que ela espera.\n\nEncontre a Oráculo e acabe com ela. Eles devem estar deixando a Manopla de Tyr neste momento. Me deixe orgulhoso, verme.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nArco de RubraplantaTempestofúrioProtetor Sagrado\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[6161] = {title = "O tesouro de Rodovalho!", objective = "Encontre a Chave Prateada de Rodovalho e a Chave Dourada de Rodovalho. Encontre e abra o Baú de Rodovalho.", description = "O diário de Rodovalho conta que o navio dele seguia para o Domínio de Plumaluna quando foi atacado por criaturas marinhas. Para evitar que o tesouro caísse em mãos inimigas, ele escondeu o próprio baú na Ilha Ranazjar.\n\nPara abrir o baú são necessárias duas chaves, uma prateada e outra dourada. As chaves se perderam, mas se elas e o baú forem encontrados, haverá um tesouro à espera!", progress = "O baú está muito bem trancado.", completion = "O baú se abre e revela o tesouro de Rodovalho!", rewards = "Você receberá:\n\nLeme do Capitão RodovalhoCana do Leme do Capitão Rodovalho"},
[6162] = {title = "A última batalha de um marido", objective = "Mate o Suserano Ror e leve a garra dele para Dreka'Sur no Posto Peçonha, na Selva Maleva.", description = "Meu marido viajou pela Selva Maleva pela honra de nosso clã, enfrentando o Concílio das Sombras e outras atrocidades da corrupção!\n\nDepois de escapar da morte diversas vezes, ele me mandou uma carta dizendo que um druida pediu ajuda a ele com um grande pelurso maculado chamado Suserano Ror.\n\nQuero a mão desse Ror ainda com as garras, <name>. Quero que ele pague ela minha viuvez! Creio que a criatura esteja ao sul, perto da fronteira com o Vale Gris.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6163] = {title = "Ramstein", objective = "Vá a Stratholme e mate Ramstein, o Devorador. Leve a cabeça dele como souvenir para Nathanos.", description = "Eu estava em desvantagem, e fui sobrepujado. Aqui mesmo, neste sítio, eu fiquei... eu mantive minha posição na defesa de Lordaeron. A fazenda, com tudo que havia nela, foi varrida por uma vaga imensa do Flagelo.\n\nA decomposição. A imundície. Foi então que aquilo parou em cima de mim, as entranhas pendiam da cavidade arregaçada do torso.\n\nQuando acordei, eu era parte do Flagelo. Passei tanto tempo impotente... Minha mente havia coalescido com a do Lich Rei.\n\nEla veio me buscar... de alguma forma eu sabia que ela viria... me libertar.\n\nE agora... eu preciso da minha vingança, minha vingança daquilo que tirou minha vida de mim. Destrua a fera.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nSelo Real de AlexisAnel Elemental\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[6164] = {title = "O livro-caixa do Augustus", objective = "Leve o Livro-caixa do Augustus para Augustus, o Pirado, na Várzea do Medo.", description = "Estou pronto para os negócios! O que você deseja?\n\nEi, espere... Não posso abrir a loja sem o meu livro-caixa. Como vou saber o que estou fazendo sem o meu livro-caixa?\n\nDevo ter deixado ele na cidade, em algum lugar. Eu tinha um quarto na estalagem... o livro deve estar lá.\n\nTraga-me o meu livro. Por favor, traga ele para mim! Traga-me o meu livro e você vai ver! Vou abrir a loja e você verá que os meu preços são LOUCOS!", progress = "Você está com o meu livro-caixa? Mal posso esperar para voltar aos negócios!", completion = "Aí está! E que boa surpresa! Eu havia esquecido de como ele estava cheio!\n\nEste é um dia glorioso, &lt;name&gt;! Estupendo! Loucamente bom, estou dizendo!", rewards = "Você receberá:\n\n"},
[6181] = {title = "Mensagem rápida", objective = "Mostre o Bilhete do Ludovico a Thor, o mestre de grifos.", description = "Nós não obtemos muito suporte diretamente de Ventobravo, mas tenho um contato lá que nos ajuda com os suprimentos de armaduras. O nome dele é Larso Norde. A loja dele, \"Imunidade Limitada\", fica na Cidade Velha, em Ventobravo.\n\nNosso estoque de armaduras está no fim, e preciso contatar Osric e encomendar mais. Você pode levar esse bilhete pra ele?\n\nO jeito mais rápido de chegar em Ventobravo é falando com nosso mestre de grifos, o Thor. Ele fica ali embaixo, descendo a colina; leve meu bilhete para ele e pegue um grifo para Ventobravo.", progress = "Você parece estar com pressa. Bom, você veio ao lugar certo!", completion = "Você precisa levar esse bilhete a Ventobravo? Sem problema, é só montar num dos meus grifos!", rewards = "Você receberá:\n\n"},
[6182] = {title = "O primeiro e último", objective = "Fale com Mathias Shaw na Cidade Velha de Ventobravo. Ele mora no Quartel.", description = "Nathanos Marris foi o primeiro e último Lorde Patrulheiro humano, discípulo de Sylvana Correventos, hoje a Rainha Banshee dos Renegados.\n\nAchávamos que Nathanos tivesse sido morto em ação na defesa de Lordaeron há cinco anos. Embora o cadáver dele jamais tenha sido encontrado, supúnhamos que ele não tivesse conseguido escapar das Terras Pestilentas Orientais.\n\nMathias Shaw esteve investigando o desaparecimento dele e pode ter novas informações. Apresente-se a ele imediatamente. Ele mora na Cidade Velha, no Quartel.", progress = nil, completion = "Depois de alguns relatos de atividades incomuns dos Renegados nas Terras Pestilentas Orientais, agentes da AVIN foram enviados para investigar, e os resultados dessa investigação são perturbadores.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[6183] = {title = "Em honra aos mortos", objective = "Fale com Mathias Shaw novamente se desejar aceitar esta tarefa.", description = "Cinco dos meus melhores agentes em campo foram destacados para o caso Marris. Um deles voltou, só para morrer três dias depois, enquanto dormia.\n\nA pouca informação que conseguimos tirar dele veio em balbucios incoerentes. Distinguimos duas coisas: \"Nathanos\" e \"Arauto da Praga\".\n\nNão tenho o efetivo necessário para continuar a investigação, e Corvoforte não nos ajudará. Precisamos ir a fundo nisso, mesmo que seja só para que as família dos falecidos saibam o que aconteceu. Você nos ajudará?", progress = nil, completion = "&lt;Matias assente.&gt;\n\nEu esperava que Bolvar me enviasse homens e mulheres de valor, os soldados mais dedicados da Aliança.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[6184] = {title = "Flint Umbramais", objective = "Vá até o Acampamento Ventogelante, nas Terras Pestilentas Ocidentais, e encontre seu contato, Flint Umbramais.", description = "A única informação que temos até agora é que Nathanos Marris pode ter sido morto pelo Arauto da Praga. Acreditamos que o Arauto da Praga é o mesmo mostro que deu cabo dos meus agentes.\n\nFlint Umbramais, outro agente da AVIN, está lotado no acampamento da Aliança no Morro do Ventogelante, que fica nas Terras Pestilentas Ocidentais. Procure por ele. Ele vai detalhar a você a situação atual nas Terras Pestilentas e lhe designar para uma missão.\n\nBoa sorte, <name>.", progress = nil, completion = "A primeira missão vai ser direta, &lt;name&gt;. Você entra, você sai. Reconhecimento...", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[6185] = {title = "As Pestes do Leste", objective = "Vasculhe as Terras Pestilentas Orientais em busca de pistas sobre o \"Arauto da Praga\" e os agentes da AVIN desaparecidos. Ser encontrar alguma Insígnia da AVIN, leve de volta para Flint Umbramais, no Acampamento Ventogelante.", description = "Como eu disse, nossa primeira missão é de reconhecimento. Nada muito empolgante, <name>. Você precisa viajar até as Terras Pestilentas Orientais e procurar por esse tal de Arauto da Praga.\n\nEsteja atent<o/a> a informações sobre nossos agentes desaparecidos. Já podemos considerá-los mortos e, se estão mortos, os corpos estão em algum lugar.\n\n<Flint engole em seco.>\n\nTodos os agentes da AVIN carregam esta insígnia.\n\n<Flint mostra sua insígnia da AVIN.>\n\nTragas todas as insígnias que encontrar para mim.", progress = "Então, descobriu alguma coisa?", completion = "Rutger. Fredo. Turyen. Todos mortos...\n\n&lt;Flint segura as insígnias com força.&gt;\n\nOnde estavam? Descobriu alguma informação sobre o maldito Arauto da Praga?", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[6186] = {title = "A ascensão do Arauto da Praga", objective = "Retorne a Ventobravo e informe o Grão-lorde Bolvar Fordragon sobre o destino de Nathanos Marris.", description = "<O sangue parece ter se esvaído completamente do rosto de Flint.>\n\nNa... Nathanos fez isso? Nathanos é o Arauto da Praga?\n\nPor quê? Ele era... tão nobre. Um lorde patrulheiro respeitados por todos. O único humano que obteve autorização para treinar com os elfos superiores se tornou um agente cruel dos Renegados?\n\nDesculpe lhe pedir isso, <name>, mas o Grão-lorde Fordragon precisa ser informado imediatamente sobre o acontecido. Regresse a Ventobravo e dê a notícia.", progress = nil, completion = "Não. Não pode ser! Como? Um agente dos Renegados? O CAMPEÃO da Rainha Banshee?\n\n&lt;O Grão-lorde Bolvar Fordragon é tomado pela decepção.&gt;", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[6187] = {title = "A restauração da ordem", objective = "Reúna um exército e siga até as Terras Pestilentas Orientais. Lance um ataque maciço contra Nathanos Arauto da Praga e qualquer lixo da Horda que tente protegê-lo.\n\nMantenha-se sempre alerta, <name>. Os membros da Horda vão defender o lorde patrulheiro com as próprias vidas.", description = "Você sabe quantos lordes patrulheiros existem no mundo? Quantos lordes patrulheiros humanos já existiram?\n\nOs feitos de Nathanos são inigualáveis. Ele era um gênio tático, responsável por vitórias da Aliança ao longo de uma década de conflitos.\n\nE agora é o campeão dos Renegados.\n\nIsso não pode estar acontecendo. A ordem precisa ser restaurada.\n\nReúna um exército, <name>. Retorne às Terras Pestilentas e destrua o Arauto da Praga.\n\nBoa sorte, <name>. Você vai precisa dela nesta batalha.", progress = nil, completion = "Isso é uma tragédia. Eu acho... acredito que nosso povo esteja amaldiçoado, &lt;name&gt;. Estamos amaldiçoados a perder nossos maiores guerreiros, nossos heróis mais nobres, nossos eruditos mais talentosos.\n\nEstamos lhe devendo uma, &lt;name&gt;, e posso lhe assegurar que, onde quer que Nathanos Marris esteja agora, ele sorri para você.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nArco de RubraplantaTempestofúrioProtetor Sagrado\n\nVocê também receberá:\n\n"},
[6221] = {title = "Deadwood of the North", objective = "Nafien would like you to kill 5 Deadwood Den Watchers, 5 Deadwood Avengers, and 5 Deadwood Shamans.", description = "There is a camp of Deadwood furbolgs directly to our southwest. Corruption permeates the camp, and I wish to put an end to it before they become a problem.\n\nThe Deadwood tribe is evil, but it is the effects of the fel that pollutes their minds. Their hostility is not truly an innate quality, yet they cannot be helped. For this reason, I am deeply saddened by having to fight my own brethren.\n\nPlease, <name>, do what must be done, and do not mention the details; they upset me far too much.Completando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = nil},
[6261] = {title = "Dungar Tragolongo", objective = "Leve o Caixote de Larso para Dungar Tragolongo, o mestre de grifos.", description = "<name>, eu pus aqui nesse caixote tudo o que Ludovico pediu. Você pode entregar para ele?\n\nSe você já conversou com Thor em Cerro Oeste, então você pode pegar um grifo e voltar pra lá. Dungar Tragolongo é o mestre de grifos lá no distrito comercial.\n\nFale com Dungar e leve esse caixote para Ludovico o mais rápido possível. Não queremos que nossos homens e mulheres lutando em Cerro Oeste fiquem sem equipamento.", progress = "Isso tudo é suor, rapaz? Você anda correndo demais. Pegue um grifo da próxima vez!", completion = "Um caixote para Cerro Oeste, é? Você já foi a Cerro Oeste? Se já, então está tudo certo, meu amigo. Tenho vários grifos treinados nessa rota!", rewards = "Você receberá:\n\n"},
[6281] = {title = "Siga para Ventobravo", objective = "Compre uma passagem de grifo do mestre de grifos Thor, e leve o Bilhete do Ludovico para Larso Norde na loja Imunidade Limitada, que fica na Cidade Velha, em Ventobravo.", description = "Por uma taxa módica, você pode ir de grifo para Ventobravo e entregar o bilhete de Ludovico para Larso. É a maneira mais rápida de chegar lá.\n\nSe você quiser fazer negócio, fale novamente comigo quando estiver <pronto/pronta> pra partir. Vai custar um pouco, mas acredite, vai valer a pena!", progress = "Você andou viajando, hein? Esteve em algum lugar interessante?", completion = "Ah, um bilhete do Intendente Ludovico? Não me surpreende que ele precise de mais equipamento. O Morro da Sentinela fica longe, em uma terra que Ventobravo quase esqueceu.\n\nBom, obrigado, &lt;name&gt;. Aqui tem dinheiro para cobrir suas despesas de viagem.", rewards = "Você receberá:\n\n"},
[6282] = {title = "Harpias ameaçadoras", objective = "Maggran Ata Terra, no Retiro Rocha do Sol, quer que você mate 7 Hapias Furissague, 7 Emboscadoras Furissangue, 7 Matadoras Furissangue e 7 Plumerrantes Furissangue.", description = "Os taurens têm orgulho de serem os protetores destas terras. Sem dúvida, você consegue imaginar por que estou tão alarmado com o número de harpias que vivem aqui nas Torres de Pedra.\n\nAs furissangues são a fonte do crescente número de harpias em Kalimdor. Elas avançam sobre tudo que vive, impedindo que os inocentes deixem seu domínio.\n\nPreciso que você abata as harpias, <name>!", progress = nil, completion = "Essas são notícias excelentes! Conte-me de novo como você fez para incutir o medo nos corações frios que as maléficas harpias têm. As furissangues sofreram um golpe significativo!", rewards = "Você receberá:\n\n"},
[6283] = {title = "O sangue das Furissangue", objective = "Maggram, no Retiro Rocha do Sol, quer que você mate a Estripadora Furissangue e traga os restos mortais dela como prova de seu feito.", description = "Limitar o número de harpias furissangue não será o bastante. A única cura real para a doença que elas são seria matar a líder delas, a Estripadora Furissangue!\n\nEla é tudo, exceto uma rainha para aquelas criaturas imundas. Mate-a e é certo que as fileiras delas diminuirão.\n\nVolte ao Vale Carbonizado e você a encontrará nas colinas a oeste. Não será fácil eliminá-la, mas a recompensa será vultuosa.", progress = "A Estripadora Furissangue é uma criatura sórdida! Você conseguirá encontrá-la nas colinas do lado oeste do Vale Carbonizado.\n\nSem ela, as furissangues ficarão indefesas... Com certeza poderemos eliminá-las para sempre!", completion = "Já estou sentindo a raiva me abandonar. O que vou fazer com meu tempo livre? Saber que o mal está à espreita em minha terra mantém meus pensamentos em movimento, embora eles estejam silencioso agora. Talvez isso seja bom... talvez agora eu possa me concentrar no que há de bom, como todo Tauren deve fazer.\n\nPor favor, aceite isso como recompensa pelo seu esforço.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nManto das FadasCinto do Guinchador\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[6284] = {title = "Aracnofobia", objective = "Mate Besseleth e leve a Presa de Besseleth a Maggran no Retiro Rocha do Sol.", description = "Alerta: Adentre o Desfiladeiro Sishir, a oeste, por sua própria conta e risco.\n\nBesseleth e sua prole octópode proclamaram esta cordilheira seu lar.\n\nMaggran Ata Terra, no Retiro Rocha do Sol, está oferecendo uma recompensa por Besseleth. Mate-a e leve uma prova de que você a eliminou a Maggran. Assim você receberá sua recompensa por exterminar essa ameaça.", progress = "Besseleth é uma predadora impiedosa... Temo por aqueles que percorrem a Passagem Tramateia à noite. É então que ela e sua prole caçam viajantes inocentes. Eu próprio fui vítima de suas presas enormes e mortais, mas fui afortunado o suficiente para conseguir afastá-la e chegar à segurança. Eu daria qualquer coisa para ver aquele monstro destruído.", completion = "Bela caçada, &lt;name&gt;! Besseleth era uma velha predadora por essas bandas... Não sentirei a menor falta dela. Aceite esta recompensa por ter se tornado o campeão da floresta.\n\n\n\nPode ser que, agora, muitos mais venham ao Retiro Rocha do Sol sem temer o que espreita na escuridão.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nEspada Curta de ArgilitoBastão de Cristal Claro\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[6285] = {title = "Entrega para Ludovico", objective = "Compre uma passagem para o Morro da Sentinela do mestre de grifos Dungar Tragolongo. Em seguida, leve o Caixote de Larso para Ludovico, no Morro da Sentinela.", description = "O mestre de grifos de Cerro Oeste é o Thor. Se você já o conhece, pode ir de grifo até ele.\n\nNão se esqueça disto: os grifos são treinados para chegar à capital, mas eles só <o/a> levarão para lugares mais remotos se você já tiver estado lá antes e falado com o tratador local.\n\nVocê já conhece o Thor, então é só falar comigo de novo quando quiser pegar um grifo para Cerro Oeste. Chegando lá, entregue o Caixote de Larso ao Intendente Ludovico.", progress = "Já voltou de Ventobravo? Larso mandou as armaduras?", completion = "Você trouxe as armaduras? Ótimo! Vamos dividi-las entre as pessoas que necessitam delas imediatamente.\n\nObrigado, &lt;name&gt;. Seu esforço tem sido de grande ajuda. E agora que você já voou num grifo, espero que venha sempre nos dar uma ajuda aqui no Morro da Sentinela!", rewards = "Você receberá:\n\n"},
[6301] = {title = "Ciclo de renascimento", objective = "Tammra Campo de Vento, no Retiro Rocha do Sol, quer que você colete 10 Sementes de Gaia.", description = "A ganância industrial e a guerra contínua arrasaram as terras das Torres de Pedra. Apenas as bênçãos da Mãe Terra podem curar tamanha ferida.\n\n<name>, preciso que você procure uma semente especial, a semente de Gaia. Elas só existem aqui nas Torres de Pedra, ao norte do Retiro Rocha do Sol, próximo às margens do Lago Mirkfallon.\n\nUma vez que você me tenha trazido as sementes, eu as imbuirei com poder xamanístico para acelerar seu crescimento.", progress = "A destruição contínua causada pela guerra e por aqueles que lucram com a madeira me causa profundo sofrimento. Para auxiliar o ciclo de renascimento e restaurar essas terras, preciso de sementes de Gaia. Você as tem, <class>?", completion = "Ah, &lt;name&gt;, você está com as sementes de Gaia. Observe e veja como a bênção da Mãe Terra pode fazer até mesmo estes pequenos grãos de vida desabrocharem e florescerem.", rewards = "Você receberá:\n\n"},
[6321] = {title = "Suprimentos para o Sepulcro", objective = "Leve as Ordens do Rodrigo para Carlos Razok.", description = "O Executor Hadrec me pediu para inventariar nosso equipamento aqui no Sepulcro. Com o Flagelo e outras coisas ainda piores lá na floresta, ele não quer que os necroguardas fiquem desguarnecidos.\n\nNo geral, nós estamos bem supridos, mas é bom pedirmos mais armas para substituir as que perdemos em campo.\n\nAqui está uma lista de pedidos com tudo de que precisamos, que deve ser entregue ao mercador de armas Antônio Nunes, na Cidade Baixa. Mostre a lista para nosso tratador de morcegos, Carlos Razok, e fale com ele sobre como chegar na Cidade Baixa.", progress = "Você parece estar em missão oficial...", completion = "Ah, uma lista de suprimentos. Você precisa levar isso para a Cidade Baixa, sem dúvida. E rápido, pois o Sepulcro não pode ficar desguarnecido.", rewards = "Você receberá:\n\n"},
[6322] = {title = "Miguel Garreta", objective = "Leve o Caixote do Antônio até Miguel Garreta.", description = "Eu coloquei os suprimentos para o Sepulcro nesse caixote. Se você já visitou o Sepulcro e falou com o tratador de morcegos local, então pode pegar um morcego de volta até ele.\n\nFale com o tratador de morcegos da Cidade Baixa, Miguel Garreta. Ele poderá lhe arranjar um morcego para o Sepulcro.", progress = "Se você precisa viajar para longe, morcegos são a melhor opção.", completion = "Esse caixote tem que chegar ao Sepulcro em Pinhaprata? Isso é fácil: tenho morcegos saindo pra lá diariamente.", rewards = "Você receberá:\n\n"},
[6323] = {title = "Carona para a Cidade Baixa", objective = "Adquira uma viagem de morcego até a Cidade Baixa com o tratador de morcegos Carlos Razok. Em seguida, leve as Ordens do Rodrigo para Antônio Nunes, na Cidade Baixa.", description = "Um dos meus morcegos pode levar você para a Cidade Baixa por uma tarifa módica. Fale comigo assim que estiver <pronto/pronta> e eu cuidarei do seu transporte.\n\nAntônio Nunes fica no Centro Comercial da Cidade Baixa. É lá que meus morcegos pousam.", progress = "Eu mantenho minhas armas nas melhores condições. Elas estão limpas e prontas para uso.", completion = "Uma lista de equipamentos para o Sepulcro? Pois bem. É uma honra servir aos servos da Dama Sombria.", rewards = "Você receberá:\n\n"},
[6324] = {title = "Fale novamente com Rodrigo", objective = "Adquira um voo de morcego para o Sepulcro do tratador de morcegos Miguel Garreta, depois entregue o Caixote do Antônio para o Necroguarda Rodrigo, no Sepulcro.", description = "O tratador de morcegos no Sepulcro se chama Carlos Razok. Se você já a conheceu, então poderei arranjar um voo de morcego até ele.\n\nNossos morcegos sempre voam até a Cidade Baixa, mas para ir a locais mais distantes você deve primeiro visitar a área e falar com o tratador de morcegos local.\n\nVocê conheceu Carlos no Sepulcro, então pode voar para lá. Avise-me quando estiver <pronto/pronta> para partir.", progress = "<name>, você voltou. Trouxe os suprimentos da Cidade Baixa?", completion = "Bom trabalho, &lt;name&gt;. Essas armas vão garantir que nossos necroguardas não sejam pegos despreparados.\n\nVocê prestou um serviço valioso para nossa Dama Sombria.", rewards = "Você receberá:\n\n"},
[6341] = {title = "O butim de Teldrassil", objective = "Leve a Coleção de Nessa para Vesprystus.", description = "Os pescadores de Rut'theran vão muito bem, obrigado. Os peixes daqui são grandes e abundantes. Eu gostaria de comparar o butim daqui com o do continente.\n\nTenho um a colação de ossos e escamas de peixe que gostaria de levar para um colega meu da Costa Negra. Ele se chama Laird e é peixeiro na cidade de Auberdine.\n\nLeve a minha coleção para o nosso mestre de hipogrifos, Vesprystus, e fale com ele sobre a viagem a Auberdine.", progress = "Como posso ajudá-$Glo:la? Está precisando de transporte?", completion = "Ah, você quer levar isso até Auberdine? Muito bem.", rewards = "Você receberá:\n\n"},
[6342] = {title = "Voo para Auberdine", objective = "Viaje de hipogrifo para Auberdine com o mestre de hipogrifos Vesprystus, depois leve a Coleção de Nessa para Laird.", description = "Tem duas formas de viajar de Rut'theran para Auberdine: de balsa ou de hipogrifo. As duas são rápidas e confiáveis, mas, se você ainda não voou para Auberdine de hipogrifo, sugiro que o faça.\n\nMeu com conselho é: fale com o mestre de hipogrifos em todas as cidades em que houver um. Depois de falar com o mestre, você pode voar de uma cidade a outra.\n\nAqui está a coleção de Nessa. Fale comigo novamente quando estiver pront<o/a> para voar para Auberdine e entregar a coleção de Nessa a Laird.", progress = "Você está vindo de Teldrassil? Diga-me uma coisa, como é a pesca lá?", completion = "Um pacote da Nessa? Obrigado, &lt;name&gt;. Ela me disse que você me traria amostras de peixes pescados perto da vila de Rut'theran. Ela acredita que podem ser bem diferentes dos pescados aqui.\n\nNossa! Essa mandíbula tem quase o dobro do tamanho da de um peixe da mesma espécie pescado aqui. E as escamas são grandes como um punho cerrado. Incrível!", rewards = "Você receberá:\n\n"},
[6343] = {title = "Resposta para Nessa", objective = "Leve a Resposta de Laird para Nessa Cantonegro.", description = "Preciso mandar uma resposta para Nessa. Ele vai ficar muito interessada na grande diferença entre os peixes daqui e os encontrados no litoral de Teldrassil. Só posso especular, mas imagino que a Árvore do Mundo esteja afetando a fauna em torno dele.\n\nLeve essa resposta para Nessa. Se quiser pegar um hipogrifo de volta para Rut'theran, fale com o mestre de hipogrifos Caylais Plumaluna. Se quiser pegar a balsa, tem uma que sai em horários regulares das docas a noroeste.", progress = "<name>, você voltou de Auberdine? Conseguiu falar com Laird?", completion = "Isso é muito interessante. Os peixes daqui são grandes, mas não imaginei que a diferença entre os butins daqui e de lá fosse tão grande. Deve haver uma razão para isso.\n\nObrigada, &lt;name&gt;. Vou discutir com os moradores daqui a notícia que você me trouxe. Talvez, um dia, conseguiremos encontrar a raiz dessa diferença. Até lá, o importante é nos aproveitarmos dos benefícios.", rewards = "Você receberá:\n\n"},
[6344] = {title = "Nessa Cantonegro", objective = "Fale com Nessa Cantonegro.", description = "Uma amiga minha, Nessa Cantonegro, é mercadora de peixes na Vila de Rut'theran. Ela precisa entregar uma encomenda na Costa Negra e está procurando alguém para ajudá-la.\n\nSe for do seu interesse, para falar com Nessa você precisa cruzar o portal de Darnassus para a Vila de Rut'theran. O portal fica a oeste dos Jardins do Templo.", progress = nil, completion = "Sim, estou precisando de um mensageiro que leve um pacote até a Costa Negra. Você pode me ajudar?", rewards = "Você receberá:\n\n"},
[6361] = {title = "Um pacote de peles", objective = "Leve o Pacote de Peles para o mestre de mantícoras Devrak, na Encruzilhada.", description = "Tenho um pacote de peles de animais dos Sertões que precisa chegar ao Penhasco do Trovão. Um colega de lá, Ahanu, vai usar as peles para criar artigos em couro.\n\nVocê pode levar as peles para ele?\n\nO modo mais rápido de chegar ao Penhasco do Trovão é nas costas de uma mantícora. Leve o pacote de peles para Devrak, o mestre de mantícoras da Encruzilhada, e fale com ele sobre o transporte para o Penhasco do Trovão.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6362] = {title = "Voo para o Penhasco do Trovão", objective = "Pegue uma mantícora com o mestre de mantícoras Devrak e leve o Pacote de Peles para Ahanu, no Penhasco do Trovão.", description = "Por um preço módico, eu lhe forneço uma mantícora para o Penhasco do Trovão. É o modo mais rápido de chegar à cidade!\n\nSe o valor for aceitável, fale comigo novamente quando estiver <pronto/pronta> para partir.\n\nSe as peles forem para Ahanu, creio que ele está no Arsenal de Hewa, que fica na base da torre das mantícoras do Penhasco do Trovão. E é lá mesmo que você vai pousar!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6363] = {title = "Tal, o Mestre de Mantícoras", objective = "Leve os Artigos em Couro do Ahanu para Tal no Penhasco do Trovão.", description = "Confeccionei artigos em couro para Jahan vender na Encruzilhada. Você os leva para ele?\n\nSe já tiver ido à Encruzilhada e falado com o mestre de mantícoras, você pode voar de volta em uma das mantícoras do Penhasco do Trovão.\n\nLeve os artigos em couro para Tal, o mestre de mantícoras do Penhasco do Trovão, e peça transporte para a Encruzilhada.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6364] = {title = "Fale novamente com Jahan", objective = "Pegue uma mantícora para a Encruzilhada com o mestre de mantícoras Tal e leve os Artigos em Couro do Ahanu para Jahan Asa de Falcão.", description = "Para poder voar até uma cidade, é preciso ter estado lá e falado com o mestre de mantícoras daquela cidade. Você já esteve na Encruzilhada e falou com o mestre de mantícoras Devrak. Então, por um preço módico, você pode levar uma mantícora daqui até ele.\n\nFale comigo novamente quando estiver <pronto/pronta>. Quando chegar à Encruzilhada, você pode entregar os artigos em couro para Jahan Asa de Falcão.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6365] = {title = "Encomenda para Gryshka", objective = "Leve as Carnes de Zargh para Devrak, na Encruzilhada.", description = "Quero mandar alguns cortes de carne especiais para uma amiga. A Gryshka, que é a estalajadeira de Orgrimmar. Você poderia entregar essa encomenda para mim?\n\nO jeito mais rápido de chegar lá é voando de mantícora. Leve os cortes de carne até Devrak, o mestre de mantícoras da Encruzilhada, e fale com ele sobre como chegar a Orgrimmar.", progress = "Você precisa chegar rápido em algum lugar? Então está falando com o orc certo!", completion = "Você tem que levar essa encomenda para Orgrimmar? Sem problema. Minha mantícora leva você até lá por um preço bem pequeno.", rewards = "Você receberá:\n\n"},
[6381] = {title = "Vida nova", objective = "Plante 10 Sementes de Gaia em Montículos de Terra de Gaia e, então, devolva as Sementes Encantadas de Gaia restantes a Tammra, no Retiro Rocha do Sol.", description = "Como você bem viu, <class>, apesar do sofrimento da terra, o ciclo da vida é algo poderoso. Agora, devemos fazer a nossa parte. Você deve plantar estas sementes no centro da destruição, a oeste, no Vale Carbonizado. Tenha cautela, pois harpias e elementais do fogo enfurecidos infestam aquela terra maculada.\n\n<name>, pegue estas sementes encantadas de Gaia e plante-as em montículos de solo fértil. O ciclo da vida não pode parar!", progress = "Estas sementes que você planta são fortes, resistindo à corrupção e estabelecendo uma base para a vida ainda por vir. Elas crescerão mais e mais; primeiro, impedindo a corrupção e curando a terra maculada, e, um dia, fazendo com que as Torres de Pedra voltem a ser a floresta exuberante de outrora.\n\nVocê plantou todas as sementes de Gaia?", completion = "Esta é uma grande vitória para a terra. Com o tempo, outros males também passarão: a Empreendimentos S.A. guardará as vibrasserras e os machados, os raivosos espíritos de fogo serão apaziguados e as harpias serão expulsas pela terra.\n\nSó então, &lt;name&gt;, as Torres de Pedra terão paz verdadeira.\n\nVocê agiu bem, &lt;class&gt;. Pegue um destes itens como agradecimento pela nova vida que você criou hoje.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBotas do Perseguidor dos VentosLuvas das Agulhas Arenosas\n\nVocê também receberá:\n\n"},
[6382] = {title = "A caçada do Vale Gris", objective = "Fale com Senani Coração Trovejante no Posto Machadada, que fica no Vale Gris.", description = "Sua força me impressiona, <class>. Sua vontade de abraçar a caça me dá confiança de que você pode enfrentar tarefas mais difíceis... e vencê-las.\n\nAs florestas do Vale Gris representam uma região selvagem indomada, sobre a qual a Horda pretende impor sua vontade, política e espiritualmente. Se quiser provar seu valor em uma terra de provações, busque os conselhos de Senani Coração Trovejante, no Posto Machadada. O posto avançado fica ao norte do caminho que tem ligação com os Sertões.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6383] = {title = "A Caçada do Vale Gris", objective = "A Caçada do Vale Gris tem como alvo três criaturas lendárias. Vá atrás delas e teste suas habilidades contra o poder e a astúcia das feras. No caminho, talvez você aprenda alguma coisa sobre si mesmo. As criaturas são o urso Ursangous, o sabre-da-noite Shadumbra e o hipogrifo Garraguda.\n\nAs presas da Caçada do Vale Gris são muito fortes, e talvez seja preciso levar reforços. Caso você derrote as feras, traga-me provas de seu feito.", description = nil, progress = nil, completion = nil, rewards = nil},
[6384] = {title = "Carona para Orgrimmar", objective = "Pegue uma mantícora para a Orgrimmar com o mestre de mantícoras Devrak, depois leve as Carnes de Zargh para Orgrimmar.", description = "Por apenas algumas moedas, posso botar você numa mantícora para Orgrimmar. Ao chegar lá, entregue as carnes do Zargh para a Gryshka. A estalagem dela fica no Vale da Força, que fica perto da Torre Celeste, onde a minha mantícora vai deixar você.\n\nMe procure de novo quando estiver <pronto/pronta> para partir.", progress = "Hum, que cheiro bom! Você está carregando carne crua aí, não é?", completion = "Ah, que maravilha! Estes cortes estão excelentes! Foi o Zargh que mandou, não foi? Aquele orc sabe mesmo como mexer com o coração de uma dama...\n\nAh, mal posso esperar para cozinhar isso tudo. Mas não muito! Toda carne fica muito melhor mal passada, não é?", rewards = "Você receberá:\n\n"},
[6385] = {title = "Doraso, o mestre de mantícoras", objective = "Entregue a Carta de Gryshka para Doraso em Orgrimmar.", description = "Escrevi uma carta de agradecimento para o Zargh. Você poderia entregá-la para mim? Se você já tiver ido a Encruzilhada e falado com o mestre de mantícoras local, então já pode pegar uma mantícora de volta para lá.\n\nLeve minha carta até Doraso, o mestre de mantícoras de Orgrimmar, e pergunte como chegar à Encruzilhada.", progress = "Precisa de uma mantícora?", completion = "Você tem que levar isso até a Encruzilhada, nos Sertões? Sim, claro que posso levar você até lá...", rewards = "Você receberá:\n\n"},
[6386] = {title = "Retorno à Encruzilhada.", objective = "Pegue uma mantícora para a Encruzilhada com o mestre de mantícoras Doraso e leve a Carta de Gryshka para Zargh, na Encruzilhada.", description = "Minhas mantícoras são treinadas para voar a vários lugares, desde que o passageiro já tenha estado no destino e falado com o mestre de mantícoras local.\n\nComo você já esteve na Encruzilhada e já falou com o mestre de mantícoras Devrak, pode voar direto daqui para lá. E, ao chegar, entregue a carta de Gryshka para Zargh.\n\nFale comigo quando você estiver <pronto/pronta> para ir.", progress = "Já voltou de Orgrimmar? A Gryshka gostou dos cortes de carne que enviei?", completion = "Hah! Aposto que ela gostou! Nada melhor que um grande e suculento bife para agradar uma dama!\n\nObrigado, &lt;name&gt;. Você me fez um grande favor. Tome aqui um dinheirinho pelo inconveniente, e não se espante se receber um convite de casamento!", rewards = "Você receberá:\n\n"},
[6387] = {title = "Alunos brilhantes", objective = "Leve a Lista de Brock para Thorgrum Borrelson, em Thelsamar.", description = "Tem um monte de jovens anões batendo à minha porta, todos famintos para aprender o ofício da mineração. E se há uma coisa de que um mineiro precisa, é uma picareta!\n\nTenho aqui uma relação de estudantes de mineração que foram bem nas aulas e por isso serão homenageados com uma picareta de honra. Entregue essa lista para Golnir Topadão em Altaforja. Ele é o fornecedor das picaretas.\n\nO jeito mais rápido de ir até Altaforja é voando de grifo. Então, leve a lista para Thorgrum Borrelson, nosso mestre de grifos, e converse com ele sobre como chegar à capital.", progress = "Já se cansou de Thelsamar? Tão cedo! Está pronto para partir para uma outra cidade?", completion = "Você tem que levar isso até Altaforja, hein? Por uma módica quantia, eu te boto nas costas de um grifo e te levo para lá. O que você acha?", rewards = "Você receberá:\n\n"},
[6388] = {title = "Grif Trovino", objective = "Entregue as Picaretas Honorárias para Grif Trovino em Altaforja.", description = "Aqui estão as picaretas de honra para os alunos do Brock. Se você já esteve em Thelsamar, então pode pegar um grifo de volta para lá!\n\nLeve as picaretas para nosso mestre de grifos, Grif Trovino, e peça a ele uma carona de volta para Kharanos.", progress = "Você está com cara de quem precisa chegar a algum lugar. Precisa de um grifo?", completion = "Você tem que entregar isso em Thelsamar, hein? Isso não vai ser problema. Se você já esteve em Thelsamar e falou com Thorgrum Borrelson, então pode pegar um dos meus grifos de volta até ele.", rewards = "Você receberá:\n\n"},
[6389] = {title = "Praga de cupins!", objective = "Solte os Cupins das Terras Pestilentas na Serraria de Beiranorte, depois retorne com o Barril de Cupins das Terras Pestilentas até Nataniel Dumah, no Acampamento Ventogelante, que fica nas Terras Pestilentas Ocidentais.", description = "Você vasculha a serraria em busca de algo que possa ser usado para abrir o barril. Encontra um pé-de-cabra que parece que vai dar conta do recado.\n\nVocê se prepara e reza para conseguir sair da serraria antes que os cupins lhe peguem.", progress = "Você conseguiu, <name>? Soltou os cupins? Que a Luz nos ajude se a Cruzada Escarlate conseguir conquistar a serraria e Amparo. Eles não devem conquistar nenhum dos dois. Estão apenas se aproveitando das vítimas da última guerra e devem ser abatidos como animais!", completion = "Hahah! Obrigado, &lt;name&gt;! Você conseguiu! Agora o espírito do meu pai pode descansar e eu posso viver em paz.\n\nMuito obrigado, mesmo. Se houver algo que eu possa fazer por você, diga.", rewards = "Você receberá:\n\n"},
[6390] = {title = "Praga de cupins!", objective = "Solte os Cupins das Terras Pestilentas na Serraria de Beiranorte, depois retorne com o Barril de Cupins das Terras Pestilentas até Guel Levine, no Baluarte, que fica nas Clareiras de Tirisfal.", description = "Você vasculha a serraria em busca de algo que possa ser usado para abrir o barril. Encontra um pé-de-cabra que parece que vai dar conta do recado.\n\nVocê se prepara e reza para conseguir sair da serraria antes que os cupins lhe peguem.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6391] = {title = "Carona para Altaforja", objective = "Compre um voo de grifo para Altaforja do mestre de grifos Thorgrum Borrelson. Chegando lá, entregue a Lista de Brock para Golnir Topadão.", description = "Por algumas moedas, um dos meus grifos pode levar você até Altaforja. Quando chegar lá, entregue a lista de Brock para Golnir Topadão. Você o encontrará na Guilda de Mineração Montanhas Profundas, que fica no Distrito da Grande Forja, bem perto de onde o grifo vai te deixar!\n\nMe procure de novo quando estiver <pronto/pronta> para a viagem.", progress = "O que posso fazer por você, cavalheiro?", completion = "Ah, a lista mais recente dos melhores pupilos do Brock. Eu tenho uma pilha de picaretas de honra prontas. Só preciso gravar os nomes dos alunos.", rewards = "Você receberá:\n\n"},
[6392] = {title = "Fale novamente com Brock", objective = "Compre um voo de grifo para Thelsamar do mestre de grifos Grif Trovino. Chegando lá, entregue as Picaretas Honorárias para Brock Buscapedra.", description = "Por uma módica quantia você pode pegar um grifo de volta para Thelsamar... Desde que já tenha estado lá. Os grifos só levam você a lugares aonde você já foi. Então, lembre-se de falar com todos os mestres de grifos que encontrar para depois poder voar até eles.\n\nComo você já falou com Thorgrum, o mestre de grifos de Thelsamar, pode voar até ele. Ao chegar lá, entregue a encomenda de Brock Buscapedra.\n\nMe avise quando estiver <pronto/pronta> para partir.", progress = "Ah, <name>. Já voltou de Altaforja?", completion = "Você trouxe as picaretas. Ótimo! Vou entregar para os meus alunos. Eles estão doidos para começar a usar nas minas de Loch Modan!\n\nObrigado pela ajuda, &lt;name&gt;. Estou em dívida com você e espero que este dinheiro pelo menos cubra as despesas de viagem.", rewards = "Você receberá:\n\n"},
[6393] = {title = "Guerra elemental", objective = "Leve 10 Incenderitas a Tsunomem no Retiro Rocha do Sol.", description = "O Tribunal das Marés me enviou para travar uma guerra contra os elementais do fogo nas Torres de Pedra. Neptulon, o Caçador das Marés, senhor de todos elementais da água, está especialmente interessado em ver nossos primos flamejantes apagados.\n\n<name>, você pode me ajudar a ganhar a confiança do Tribunal destruindo os elementais do fogo no Vale Carbonizado. Quando destruídos, os elementais do fogo deixam incenderitas. Traga-as a mim para receber uma recompensa.", progress = "O Tribunal das Marés está ficando impaciente. Eles querem acabar com esta guerra que assola todas as dimensões há eras. Meus irmãos anseiam por notícias da gloriosa vitória sobre os odiados elementais do fogo. Você está com as incenderitas de que preciso?", completion = "Os espíritos da água em meu interior borbulham de exultação. &lt;name&gt;, você me deu uma vitória gloriosa para reportar ao Tribunal das Marés.", rewards = "Você receberá:\n\n"},
[6394] = {title = "A picareta de Thazz'ril", objective = "Leve a Picareta de Thazz'ril para o Encarregado Thazz'ril.", description = "<name>, você é <um/uma> <race> confiável. Posso contar com você para mais uma tarefa?\n\nAlgum tempo atrás, eu estava inspecionando a caverna ao norte em busca de minerais e deixei a minha picareta para trás. Depois, quando voltei para buscá-la, descobri que a caverna estava cheia de feras cruéis! Você pode ir lá no Covil da Lâmina Ardente para buscar a minha picareta?\n\nEu a deixei em uma câmara com quedas d'água. A minha picareta tem um feitiço que a faz brilhar no escuro, então encontrá-la não será problema... o problema é o que está guardando ela!", progress = "Você entrou no Covil da Lâmina Ardente, <name>? Encontrou a minha picareta?", completion = "Ótimo, você conseguiu! Muito obrigado, &lt;name&gt;. Esta é a minha picareta favorita! Agora, se os peões algum dia terminarem de derrubar essas árvores, talvez consigamos achar uma boa caverna para minerar!", rewards = "Você receberá:\n\n"},
[6395] = {title = "O último desejo de Marla", objective = "Leve os Restos de Saulo Mitos para o Túmulo de Marla, depois fale novamente com a Noviça Elvira.", description = "Minha amiga Marla Mitos era casada com Saulo Mitos. Quando a peste veio, Saulo sucumbiu e se juntou ao Flagelo.\n\nMarla escapou da morte-viva, mas acabou assassinada pelas mãos do marido transformado em ser irracional. Só que o amor dos dois era tão forte que o último desejo dela foi ser enterrada ao lado de seu amado Saulo.\n\nSaulo Mitos vaga por um acampamento arruinado à beira da estrada nordeste de Plangemortis. Derrote-o e realize o último desejo de Marla: enterre-o na cova dela, na primeira fileira do nosso cemitério.", progress = nil, completion = "Você fez uma grande coisa hoje, &lt;name&gt;. Embora nossa luta contra o Flagelo continue, temos fé que Marla e Saulo vão encontrar a paz juntos em sua última morada.", rewards = "Você receberá:\n\nRestos Mortais de Samuel"},
[6401] = {title = "Kaya está viva", objective = "Conte a boa notícia a Tammra Campo de Vento no Retiro Rocha do Sol.", description = "Conte a boa notícia a Tammra Campo de Vento, tia de Kaya, que está no Retiro Rocha do Sol! Kaya está viva! Nós achamos que ela tinha sucumbido ao ataque brutal contra nossa aldeia. Siga a trilha a oeste para chegar ao Retiro Rocha do Sol.", progress = nil, completion = "Minha nossa, minha sobrinha Kaya está viva! Uma boa notícia mesmo. Obrigada, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[6402] = {title = "Encontro marcado em Ventobravo", objective = "Viaje a Ventobravo e vá até os portões da cidade. Fale com o Escudeiro Rui para que ele avise ao Marechal Vilasboas que você chegou.", description = "Encontre-se com Vilasboas nos portões de Ventobravo.\n\nO que ele tem a revelar abalará as fundações do reino!\n\nE não vá sozinh<o/a>, <name>. Você vai precisar de um exército! Reúna seus compatriotas. Às armas!", progress = nil, completion = "Antes de mais nada, preciso que você reúna um exército. Enfrentar em dragão não é pouca coisa.", rewards = "Você receberá:\n\n"},
[6403] = {title = "O grande baile de máscaras", objective = "Siga Reginaldo Vilasboas através de Ventobravo. Proteja-o de qualquer ataque!", description = "Foi uma aventura e tanto, <meu amigo/minha amiga>. Eu tenho uma dívida de gratidão com você. Quem sabe, no fim das contas...\n\nComo você já viu por si mesm<o/a>, Lady Prestor é Onyxia: filha de Asa da Morte, irmã gêmea do tirano do Pináculo, Nefarian.\n\nDurante muito tempo o olhar cativante dela manteve nosso reino sob controle. Até mesmo o Grão-lorde se tornou um joguete nas mãos dela. Hoje vamos dar um ponto final a esse feitiço.\n\nNão se preocupe, juntos seremos vitoriosos.\n\nEstá pront<o/a>?", progress = nil, completion = "&lt;Bolvar segura o medalhão estilhaçado.&gt;\n\nA magia que há dentro dele precisa ser reativada. O olho do dragão precisa ser harmonizado.\n\nO sacrifício de Vilasboas libertou nosso reino, mas a ameaça de Onyxia e seus pares continua à espreita.\n\nEste medalhão foi o que manteve minha mente e meu corpo cativos, mas também foi o que me deu acesso à mente e aos segredos de Onyxia. As visões estão confusas agora, mas eu ainda me lembro de algumas coisas.", rewards = "Você receberá:\n\n"},
[6421] = {title = "Ravina da Avalanche", objective = "Explore as profundezas da caverna na Ravina da Avalanche e colete 10 Cristais de Ressonita para que Mor'rogal, no Retiro Rocha do Sol, os analise.", description = "Algo está errado aqui nas Torres de Pedra. Você sente a tensão no ar, <name>?\n\nA sudeste daqui, na Ravina da Avalanche, fica uma profunda caverna. Os kobolds de lá estão minerando freneticamente um cristal raro chamado ressonita. Preciso que você colete algumas amostras do minério para que eu possa entender o que está acontecendo na caverna. Também preciso que você investigue as áreas mais profundas de lá.\n\nVá, jovem <class>. É imperativo que eu saiba que mal espreita sob estas montanhas.", progress = "Que notícias você traz da Ravina da Avalanche? Lembro-me de uma lenda que falava sobre a ressonita... mas não estou conseguindo ligar os pontos. Talvez saber o que jaz no fundo da caverna esclareça as perversas intensões dos kobolds.", completion = "Ah... Sim! É isso! Os cristais de ressonita têm traços de magia terrana. Os kobolds devem ter cavado fundo para desenterrar um terrano.\n\nDiz a lenda que os terranos são criaturas feitas pelos titãs. Eles foram usados na criação da terra sobre a qual andamos. Isto é, definitivamente, uma ameça que não podemos ignorar.\n\n&lt;Mor'rogal balança a cabeça.&gt;\n\nTalvez eu consiga encantar estas amostras de minério para que possamos usá-las para frustrar o plano dos kobolds.", rewards = "Você receberá:\n\n"},
[6441] = {title = "Chifres de Sátiro", objective = "Consiga 16 Chifres de Sátiro para Pixel no Posto Machadada.", description = "Fala aí! Eu estava passando pelo Vale Gris, indo pra Vila Catraca, e resolvi dar uma paradinha aqui no Machadada. Eu estou sempre de olho nas oportunidades, e parece que a demanda por chifre de sátiro na Vila Catraca anda bem alta.\n\nFiz umas perguntinhas aqui e ali, e descobri que os chifres dos sátiros da Trilha da Noite, de Satyrnaar e de Xavian são exatamente o que eu tô procurando. Só que eu não dou conta de sátiros grandões assim... Mas você parece ser do tipo que consegue dar conta de uns bichos desses!\n\nO negócio é o seguinte: você arruma os chifres e eu pago à altura do perrengue.", progress = "Vê se pega os chifres logo, <name>. Eu vou voltar pra Vila Catraca daqui a pouco!", completion = "Estes chifres estão perfeitos, &lt;name&gt;! Valeu por me ajudar!\n\nAh, e isso é pelo inconveniente...", rewards = "Você receberá:\n\n"},
[6442] = {title = "Nagas na Praia de Zoram", objective = "Entregue 20 Cabeças de Iracauda a Marukai, na Praia de Zoram.", description = "O mal está à espreita na costa, <race>.\n\nLá jazem as ruínas da cidade de Zoram, destruída e submersa pelas águas há muito tempo, desaparecida e quase esquecida.\n\nAgora as nagas voltaram, e o porquê ainda não sabemos. Mas pouco importa: temos que defender as terras que lutamos tanto para conquistar.\n\nVolte aqui trazendo vinte cabeças de naga! Mande as nagas de volta para as profundezas, de onde jamais deveriam ter saído!", progress = "As nagas continuam a se multiplicar, <name>. Por favor, termine a tarefa por mim.", completion = "Guerreiros como você nos seriam bastante úteis por aqui, &lt;name&gt;. Obrigada pela ajuda.", rewards = "Você receberá:\n\n"},
[6461] = {title = "Fome sangrenta", objective = "Xen'Zilla, em Malaka'Jin, precisa que você mate 10 Rastejadoras de Fundolimo e 7 Cospeçonhas de Fundolimo.", description = "A gente aqui, os trolls de Malaka'jin, tira a nossa prosperidade da terra, cara. A Cordilheira das Torres de Pedra tem uma caça ótima, e a gente come bem e tal.\n\nMas pô, de uns tempos pra cá vêm aparecendo uns convidados barra pesada pro jantar... As aranhas tão atacando os nossos acampamentos de noite pra roubar a nossa caça.\n\nSe tu ajudar a matar esses bichos sinistros, o pessoal aqui em Malaka'Jin vai ficar te devendo uma. As aranhas tão por toda parte nas Torres de Pedra. É só seguir pro noroeste pra ver do quê que eu tô falando.", progress = nil, completion = "Beleza, cara! Boa notícia, essa. A gente deve ter menos convidado barra pesada pra jantar essa noite.\n\nMuito obrigado, &lt;name&gt;. Tamo junto!", rewards = "Você receberá:\n\n"},
[6462] = {title = "Patuá troll", objective = "Entregue 8 Patuás Troll para Mitsuwa, no Assentamento Zoram'gar.", description = "Minha família viajou para Zoram'gar levando muitos patuás sagrados, mas os pelursos Pelocardo mataram todos antes de chegarem ao destino! Malditos pelursos!\n\nEstou com saudade dos meus irmãos e irmãs que morreram, mas sei que nós vamos nos ver de novo no outro mundo. O problema é o valor dos patuás neste mundo aqui. É um absurdo que eles estejam nas mãos de pelursos!\n\n<name>, você tem que pegar os patuás de volta! Os pelursos guardaram todos dentro de baús no Covil dos Pelocardo, uma caverna no final da Aldeia dos Pelocardo, que fica a norte de Astranaar.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6481] = {title = "O terrano se ergue", objective = "Abra o esquife de ressonita com o Cristal de Ressonita Encantado e, então, mate Goggeroc. Fale novamente com Mor'rogal levando as notícias e o Cristal de Ressonita Encantado.", description = "Você descobriu uma ameaça sombria para esta terra, <class>. O esquife de ressonita que você encontrou naquela caverna abriga um terrano adormecido chamado Goggeroc.\n\nAlterei a composição dos cristais de ressonita que você coletou com meus poderes xamanísticos. Leve o cristal de ressonita encantado e quebre o esquife de ressonita.\n\nGoggeroc estará enfraquecido ao despertar... Mate-o, <name>!", progress = nil, completion = "Você traz boas notícias, &lt;class&gt;! Temo o que um terrano poderia significar para Kalimdor.\n\nMatar Goggeroc foi uma tarefa para ninguém menos que um herói. Você agiu bem. As Torres de Pedra e Kalimdor estão em dívida com você, nobre &lt;class&gt;.\n\nAceite isto como sua recompensa, honorável &lt;name&gt;.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBraçadeiras da Barba da CorujaMunhequeiras Lupogrenha\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[6482] = {title = "Liberdade para Ruul!", objective = "Escolte Ruul e, em seguida, fale com Yama Casco de Neve, no Posto Machadada.", description = "Hein? Quem é você?\n\n<name>? Você não é pelurso. Eles são uns malditos! Acharam que eu fosse um urso e me pegaram, e agora estão me matando de fome! Estou ficando muito fraco...\n\nMe ajude a fugir daqui, e depois fale com minha irmã, Yama, no Posto Machadada, a leste daqui.\n\n<name>! Tenho que sair daqui!", progress = nil, completion = "Você encontrou meu irmão! Os Pelocardo o capturaram? Que notícia terrível! Muitos pelursos são criaturas nobres, mas os Pelocardo são malévolos.\n\nObrigada por resgatar meu irmão, &lt;name&gt;. Nem quero pensar no que eles fizeram com meu pobre Ruul. Acho que ele vai dormir por muito tempo depois de toda essa provação...", rewards = "Você receberá:\n\n"},
[6501] = {title = "O Olho do Dragão", objective = "Você deve vasculhar o mundo em busca de uma criatura capaz de restaurar o poder do Fragmento do Olho do dragão. A única informação que você tem sobre essa criatura é que ela existe.", description = "Um fragmento do medalhão despedaçado é tudo que posso oferecer.\n\nVocê terá que encontrar alguém mais com sangue de dragão para lhe ajudar a forjar uma nova chave. Quando a tiver, vá para o Pântano da Serpe, que fica no Pântano Vadeoso. Lá, <name>, você encontrará o covil dela.\n\nOnde encontrar outras revoadas dispostas a ajudar? Infelizmente, eu não sei... Dizem que elas existem. Algumas até vivem e operam entre nós, sob o disfarce das raças humanoides. Eu lhe desejo sorte.", progress = "Sinto uma aura maligna emanando de você, <race>. O que você pode ter aí capaz de emitir uma aura assim?", completion = "Eu já devia saber. Um pedaço de Onyxia, a mãe da ninhada dragônica negra. Repulsivo...", rewards = "Você receberá:\n\n"},
[6502] = {title = "Amuleto da Chama Dracônica", objective = "Confronte o General Drakkisath e consiga o Sangue do Campeão dos Dragões Negros. Drakkisath está na sala do trono atrás dos Salões da Ascensão, no Pico da Rocha Negra.", description = "É um pedaço do dragão: uma escama, encantada e transformada. Quando está íntegra, pode dar acesso ao covil da fera. Estilhaçada, não presta para nada.\n\nÉ possível consertar esse item, mas, para fazer isso, você precisa do sangue do campeão dos dragões negros. Ele é conhecido como General Drakkisath, líder dos exércitos de dragomorfos de Nefarian. Já ouviu falar dele?\n\nVolte aqui com o sangue dele, <race>, que eu encanto o fragmento e o transformo em algo que lhe conceda acesso ao covil de Onyxia.", progress = "O fluxo do tempo não para nem se retarda, <race>. A minha vida é eterna, a sua, não. Eu não perderia o tempo que você tem...", completion = "O amuleto concede o acesso de que você precisa. O covil de Onyxia fica no Pântano Vadeoso. Enquanto você estiver com o amuleto, as proteções da dragonesa não poderão impedir que você entre.\n\nA Revoada Azul lhe deseja sorte, &lt;name&gt;.", rewards = "Você receberá:\n\nAmuleto da Chama Dracônica"},
[6503] = {title = "Vanguardeiros do Vale Gris", objective = "Mate 9 Vanguardeiros do Vale Gris, depois fale novamente com Koray'bin, no Posto Machadada.", description = "Fomos informados que os vanguardeiros do Vale Gris deixaram o Posto Avançado Asa de Prata e agora andam bisbilhotando perto nossos territórios na floresta. Não queremos que coletem informações sobre nossas defesas!\n\nVasculhe as florestas ao sul do nosso posto daqui e a leste do posto avançado da Aliança em Asa de Prata. Encontre e mate os vanguardeiros, depois fale novamente comigo.", progress = nil, completion = "Muito bem, &lt;name&gt;. Suas habilidades de caça são impressionantes...\n\nA morte dos vanguardeiros deixa a Aliança no escuro, e eles não gostam de atacar alvos quando não tem informações sólidas.", rewards = "Você receberá:\n\n"},
[6504] = {title = "As páginas perdidas", objective = "Encontre as 12 páginas que voaram do Manual de Operações do Retalhador e organize-as nos capítulos 1, 2 e 3. Devolva o manual a Gurda Marcafúria, que está perto do Posto Machadada.", description = "Eu acabei de pegar esse retalhador na Vila Catraca e segui direto para o Acampamento de Lenhadores Brado Guerreiro, onde eu deveria estar de serviço. Mas, no caminho para lá, uma ventania espalhou as páginas do manual de operações do retalhador. Só sobrou a capa.\n\nAs páginas podem ter ido parar em qualquer lugar... E eu não sei operar o retalhador sem o manual! Você poderia encontrar as páginas que voaram?", progress = "Espero que você encontre as páginas logo, <name>! Tenho que voltar ao trabalho!", completion = "Leitura não é o meu forte... Mas agora vou poder voltar ao trabalho! Obrigada, &lt;name&gt;!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nLuvas para Operar RetalhadoraMitenes de Trapo Oleoso\n\nVocê também receberá:\n\n"},
[6521] = {title = "Uma aliança profana", objective = "Leve a cabeça do Embaixador Malcin para Varimatras, na Cidade Baixa.", description = "O embaixador Malcin diz afirma estar fora do Urzal dos Mortos. Ele precisa ser morto, a missão dele é um fracasso aos olhos do Lich Rei.\n\nA presença dele aqui explica muito do que descobrimos sobre a região: alguns dos javatuscos ficaram assustadoramente poderosos, e mostraram sinais da peste de maneira peculiar. Isso deve ser o sinal de algum tipo de acordo que fizeram.\n\nEncontre Malcin e acabe com ele, <name>. Fale novamente comigo depois de cumprir a missão.", progress = "Encontrou o Embaixador, <name>? Ele está morto?\n\nNão podemos permitir que o Flagelo se estabeleça em Kalimdor. Eles já são uma grande ameaça às nossas fronteiras. Lutar contra eles em duas frentes seria arriscado demais.\n\nDe acordo com os eventos que você me relatou, a única razão para otimismo é o fato de que a bruxa má não parece ter sido corrompida. Ela sabe, bem no fundo da alma, que o Flagelo pode trazer grande poder, mas que os Sertões jamais serão totalmente dela se fizer um acordo com deles.", completion = "Muito bem. Ótimo trabalho, &lt;class&gt;.\n\nAgora que Macin está morto, pelo menos sabemos que os avanços do Flagelo vão cessar. A Dama Sombria agradece os seus esforços e deseja lhe recompensar com um presente.\n\nNão sabemos a extensão da influência do Flagelo sobre os Mortos, mas tenho certeza de que vamos descobrir seus planos em breve.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nQuebra-crânioCospe-unhaVeste do Zelote\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[6522] = {title = "Uma aliança profana", objective = "Leve o Pergaminho Pequeno para Varimatras, na Cidade Baixa.", description = "Grande Dama,\n\nA senhora está ciente do que oferecemos. É uma grande oportunidade para a senhora tomar os Sertões e as terras além. Em troca, só queremos a sua ajuda. Quando dizemos ajuda, isso significa simplesmente nos conceder a liberdade para agir nessa área.\n\nPor favor, permita-nos discutir o assunto consigo. Nosso poderio é grande e estamos dispostos a compartilhá-lo com aqueles sábios o bastante para unirem-se a nós. Permanecerei fora de Urzal dos Mortos por ora.\n\nCordialmente,\nEmbaixador Malcin", progress = "Que assunto você quer tratar comigo, <name>? Tenha certeza de que é realmente importante antes de me incomodar.", completion = "Reconheço a insígnia neste pergaminho, &lt;name&gt;. Você o encontrou com aquela bruxa má que governa Urzal dos Tuscos? Interessante.\n\nAo que parece, o Flagelo não está satisfeito com um continente só. O Embaixador Malcin, autor da mensagem, é um dos servos pestilentos do Flagelo. Ele era um diplomata humano do reino de Lordaeron antes da... mudança.\n\nSe o Flagelo está pretendendo tomar Kalimdor, cabe a nós impedir.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[6523] = {title = "Proteja Kaya", objective = "Escolte Kaya Casco Chato até a Aldeia Apareje e, então, fale novamente com Makaba Casco Chato perto da extremidade sudeste das Torres de Pedra.", description = "Obrigada por me resgatar! Devemos ir embora rápido antes que eles descubram que estou livre. Por favor, escolte-me até a Aldeia Apareje. A partir de lá, conheço meu caminho.\n\nMeu pai, Makaba Casco Chato, estará desesperado para saber que se estou em segurança.", progress = nil, completion = "Kaya está viva! &lt;name&gt;, tenho que agradecer por você tê-la regatado.", rewards = "Você receberá:\n\n"},
[6541] = {title = "Apresente-se a Kadrak", objective = "Apresente-se a Kadrak na torre de vigia dos Sertões Setentrionais.", description = "Você já foi ao Vale Gris, ao norte? Há pouco tempo, a Horda montou um assentamento lá, na Praia de Zoram, e estamos precisando de recrutas para defender os novos fronts.\n\nCaso você se considere à altura do trabalho, procure Kadrak, nos Sertões Setentrionais, na torre de vigia. Ele está liderando a campanha do Vale Gris e poderá lhe dar mais orientações.", progress = nil, completion = "O Vale Gris é uma terra dividida, mas nossas últimas investidas ali foram bem-sucedidas. Já montamos um posto avançado na Praia de Zoram e outro a norte daqui, o Posto Machadada.", rewards = "Você receberá:\n\n"},
[6542] = {title = "Apresente-se a Kadrak", objective = "Apresente-se a Kadrak na torre de vigia dos Sertões Setentrionais.", description = "Você já foi ao Vale Gris, ao norte? Há pouco tempo, a Horda montou um assentamento lá, na Praia de Zoram, e estamos precisando de recrutas para defender os novos fronts.\n\nCaso você se considere à altura do trabalho, procure Kadrak, nos Sertões Setentrionais, na torre de vigia. Ele está liderando a campanha do Vale Gris e poderá lhe dar mais orientações.", progress = nil, completion = "O Vale Gris é uma terra dividida, mas nossas últimas investidas ali foram bem-sucedidas. Já montamos um posto avançado na Praia de Zoram e outro a norte daqui, o Posto Machadada.", rewards = "Você receberá:\n\n"},
[6543] = {title = "Os relatórios dos Brado Guerreiro", objective = "Abra o Monte de Relatórios e entregue os Relatórios dos Brado Guerreiro ao Batedor do Brado Guerreiro, o Mensageiro do Brado Guerreiro e o Pioneiro do Brado Guerreiro. Em seguida, entregue o informe sobre a situação atual que você receberá de cada um deles para Kadrak, na torre de vigia do norte dos Sertões.", description = "Há pouco tempo, eu destaquei alguns batedores para o Vale Gris, e preciso de um mensageiro para entregar ordens para eles e trazer de volta um informe sobre a situação.\n\nA primeira parada será no Assentamento Zoram'gar, na Praia de Zoram, para encontrar o primeiro batedor. Chega-se lá seguindo pela costa em direção ao oeste. Você também deverá passar no Posto Machadada, a norte, e pela estrada em direção ao leste, perto de Azshara.\n\nEntregue um relatório para cada batedor e receba deles um informe sobre a situação atual.", progress = "Rápido, <name>. Eles precisam receber os relatórios logo. É urgente.", completion = "Dá para ver que você é confiável. Os dados atualizados são cruciais para os planos de ampliar nossa presença no Vale Gris. Agora, podemos preparar nosso próximo passo.", rewards = "Você receberá:\n\n"},
[6544] = {title = "Junte-se aos bons", objective = "Acompanhe Torek no ataque ao Posto Avançado Asa de Prata e sua comandante, Duriel Flameluna. Em seguida, apresente-se a Ertog Raivatusco no Posto Machadada.", description = "Ei! <class>! Vem cá!\n\nEu e meus saqueadores estamos nos preparando para atacar o Posto Avançado Asa de Prata, a oeste daqui. Nosso alvo é a líder deles, Duriel Flameluna. Os batedores relataram que o posto está fortemente guardado por sentinelas e guerreiros. Vai ser uma batalha gloriosa!\n\nVocê gostaria de dividir essa glória conosco, <name>, nos ajudando? Caso sim, quando tudo acabar, comunique o sucesso da operação ao meu superior, Ertog Raivatusco, no Posto Machadada.\n\nE então, o que você me diz? Vem conosco?", progress = nil, completion = "Então o ataque de Torek foi um sucesso! Muito bem, &lt;name&gt;. Os elfos noturnos são adversários decentes. Um pouco magrelos, é verdade, mas fortes e violentos! Tenho certeza de que a batalha deve ter sido memorável...\n\nE espero que você tenha saído com mais umas cicatrizes!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nBengala PolidaAlfanje de Ardósia\n\nVocê também receberá:\n\n"},
[6545] = {title = "O informe do mensageiro Brado Guerreiro", objective = "Então você veio trazer novas ordens para mim? É sempre bom ver um novo recruta de natureza entusiasmada e vontade férrea.\n\nE é melhor que você também aprenda rápido, se quiser se sobreviver aqui. A ameaça das nagas não para de aumentar, <name>. Eu presenciei e combati vários ataques desde que cheguei.\n\nMas, se você quiser contribuir com o esforço de guerra, fale com um dos companheiros aqui do assentamento.", description = nil, progress = "Então você veio trazer novas ordens para mim? É sempre bom ver um novo recruta de natureza entusiasmada e vontade férrea.\n\nE é melhor que você também aprenda rápido, se quiser se sobreviver aqui. A ameaça das nagas não para de aumentar, <name>. Eu presenciei e combati vários ataques desde que cheguei.\n\nMas, se você quiser contribuir com o esforço de guerra, fale com um dos companheiros aqui do assentamento.", completion = "Bem, meu informe explica toda a situação para o Kadrak. Você precisa entregar isso para ele o mais rápido possível.", rewards = "Você receberá:\n\nInforme do Mensageiro do Brado Guerreiro"},
[6546] = {title = "O informe do pioneiro do Brado Guerreiro", objective = "Há muitos perigos neste caminho, <name>. É bom você sempre se lembrar disso quando estiver viajando por estas terras. A Aliança está próxima.\n\nHá inimigos em todos os fronts. O sátiros são uma força tremenda, com forte presença na região leste do Vale Gris. E eu descobri que as nagas tomaram uma grande parte das terras de Azshara.\n\nÉ bom ver que nosso contingente continua a ser reforçado. Esteja pronto para o que vier pela frente, <name>.", description = nil, progress = "Há muitos perigos neste caminho, <name>. É bom você sempre se lembrar disso quando estiver viajando por estas terras. A Aliança está próxima.\n\nHá inimigos em todos os fronts. O sátiros são uma força tremenda, com forte presença na região leste do Vale Gris. E eu descobri que as nagas tomaram uma grande parte das terras de Azshara.\n\nÉ bom ver que nosso contingente continua a ser reforçado. Esteja pronto para o que vier pela frente, <name>.", completion = "Entregue meu informe a Kadrak. Rápido, &lt;name&gt;.", rewards = "Você receberá:\n\nInforme do Pioneiro do Brado Guerreiro"},
[6547] = {title = "O informe do batedor Brado Guerreiro", objective = "Mantenha os olhos abertos, morto-vivo. Os elfos costumam se esconder nas sombras...\n\nEles devem estar espreitando. Temos que ficar atentos o tempo todo. Nós demos duro para conquistar essas terras, e os elfos estão tentando tomá-las de nós.\n\nVocê trouxe as novas ordens de Kadrak?", description = nil, progress = "Mantenha os olhos abertos, morto-vivo. Os elfos costumam se esconder nas sombras...\n\nEles devem estar espreitando. Temos que ficar atentos o tempo todo. Nós demos duro para conquistar essas terras, e os elfos estão tentando tomá-las de nós.\n\nVocê trouxe as novas ordens de Kadrak?", completion = "Avise a Kadrak que conseguimos deter os elfos noturnos aqui. Os planos estão correndo bem.", rewards = "Você receberá:\n\nInforme do Batedor do Brado Guerreiro"},
[6548] = {title = "Vingue minha vila", objective = "Mate 8 Rufiões Temível Totem e 6 Mercenários Temível Totem para, então, voltar a falar com Makaba Casco Chato perto da extremidade sudoeste das Torres de Pedra.", description = "O clã Temível Totem invadiu minha vila e matou quase todos. Matei o máximo que pude, mas mal escapei vivo.\n\n<name>, a única vontade que me resta é que mais deles morram. Você os encontrará a oeste daqui.", progress = nil, completion = "&lt;name&gt;, eu agradeço a você... mas jamais esquecerei o que os Temível Totem fizeram com a minha vila.", rewards = "Você receberá:\n\n"},
[6561] = {title = "Vilania das Profundezas", objective = "Leve a cabeça do Senhor do Crepúsculo Kelris para Bashana Runa Totem, no Penhasco do Trovão.", description = "Eu estou sem forças. Vou precisar da sua ajuda!\n\nMuito tempo atrás, este local abrigava o grande templo de Eluna. Mas a desgraça levou à ruína quando a corrupção do deus antigo se infiltrou das profundezas da terra e contaminou o poço lunar sagrado.\n\nAku'mai, servo do deus antigo, se ergueu das águas.\n\nOs sectários do Martelo do Crepúsculo se aliaram às nagas para ocupar este local. Os cultistas, liderados por Kelris, sacrificam inocentes ao poder de Aku'mai.\n\nMate Kelris e leve a cabeça para Bashana, no Penhasco do Trovão, <name>, por favor.", progress = "Que a Mãe Terra esteja com você, <name>.\n\nA paz entre a terra e todos os povos deve ser o seu objetivo. Ela tem um plano maior para a natureza, e nós todos temos um papel a cumprir nesse plano.", completion = "Kelris escapou da Aurora Argêntea por algum tempo.\n\nMuitos acharam que ele estava morto ou desaparecido.\n\nAo encerrar o reinado dele, você poupou a vida de muitas pessoas inocentes, &lt;name&gt;.\n\nLouvada seja a Mãe Terra! Sacrificar alguém ao servo de um deus antigo em troca de ganhos pessoais é mais que repugnante!\n\nVocê fez algo muito importante hoje. Kelris era uma criatura maligna e queria levar o mundo de volta a um tempo de caos ao adorar as criaturas dos deuses antigos. Obrigada, &lt;name&gt;.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nCetro da LápideBroquel Ártico\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[6562] = {title = "Problemas nas Profundezas", objective = "Fale com Je'neu Sancrea no Vale Gris.", description = "Je'neu Sancrea precisa de ajuda. Ele está no Assentamento Zoram'gar, no Vale Gris. A floresta dos elfos noturnos fica ao norte das montanhas Torres de Pedra. Para chegar lá, passe pelo Rochedo Cortavento ou siga para o norte através dos Sertões. A Praia de Zoram fica no extremo oeste da floresta. Tome cuidado, <name>. Existem forças que não vão aceitar sua presença pacificamente.", progress = nil, completion = "Tsunomem o mandou aqui? Muito bom! Então temos algumas coisas a conversar.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[6563] = {title = "A essência de Aku'mai", objective = "Entregue 20 Safiras de Aku'Mai a Je'neu Sancrea no Vale Gris.", description = "Dentro dos primeiros túneis das Profundezas Negras há muitas drusas de safira. Há pouco tempo, notou-se que as nagas estão extraindo os cristais. Até descobrir por quê, não podemos permitir que elas continuem. Parece que os cristais têm um imenso poder elemental e, se forem usados do jeito errado, podem se tornar um material arcano poderoso.\n\nVocê pode me ajudar, <name>? As Profundezas Negras ficam no mar, perto da Praia de Zoram, ao norte de nosso assentamento.", progress = "Como vai o garimpo, <name>? Você viu as nagas extraindo as safiras? Rezo a meus ancestrais para que descubramos o que elas pretendem fazer com os cristais.", completion = "Muito bem, &lt;name&gt;. Esses são os cristais que eu mencionei. Intrigante...\n\nConsigo sentir o poder. Os espíritos da água são atraídos pelas safiras. Agora imagine para o quê as nagas poderiam usá-los... Bem, podemos nos dar por satisfeitos só por eles não estarem mais das mãos delas, certo?", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[6564] = {title = "Lealdade aos Deuses Antigos", objective = "Leve o Bilhete Úmido para Je'neu Sancrea, no Vale Gris.", description = "O bilhete está todo amassado e a caligrafia da escrita é muito ruim:\n\n\"Sim, diga a todas as sacerdotisas para trazer os cristais para mim. Os espíritos elementais da água são fortes dentro deles e vão ajudar muito os nossos aquamantes e fazerem suas evocações.\n\nQuando controlarmos o poder dentro dos cristais, vamos apaziguar Aku'mai. Um dia teremos poder suficiente para trazer Aku'mai de volta e o mundo vai voltar à sua grande era. Uma era governada pelos deuses antigos!\"", progress = "Pois não, <name>. Do que você precisa? Ainda tem muita coisa que eu preciso aprender sobre os cristais que as nagas estão coletando.", completion = "É interessante, de fato. Ainda bem que você trouxe isso à nossa atenção.\n\nAgora nossos problemas já tem nome... Lorgus Jett.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[6565] = {title = "Lealdade aos Deuses Antigos", objective = "Mate Lorgus Jett nas Profundezas Negras e depois fale novamente com Je'neu Sancrea no Vale Gris.", description = "Esse seguidor do Martelo do Crepúsculo não pode levar o plano dele a cabo. O Martelo do Crepúsculo não saca os espíritos da natureza. Eles acreditam nos Deuses Antigos, criaturas do caos e da treta que foram derrotadas faz uma cara. Esse tal Lorgus não pode triunfar. Você tem que dar um trato nele, <name>. Eu não tenho como... sou fraco demais.\n\nVai pras Profundezas Negras, encontra o cara e passa no fio da navalha. Depois é só voltar pra Zoram'gar.", progress = nil, completion = "Aí sim!! Valeu, &lt;name&gt;. A gente não acabou com o Martelo do Crepúsculo de vez, mas pelo menos você avacalhou mais um plano deles de devolver os Deuses Antigos ao poder.\n\nVai saber quais outros planos eles não vêm bolando, mas por enquanto a gente pode ficar sossegado.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nElo do PunhoDragonas Castanhas\n\nO seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê também receberá:\n\n"},
[6566] = {title = "O que o vento carrega", objective = "Ouça Thrall.", description = "Que bom te ver de novo, <name>. O vento traz notícias dos Reinos do Leste.\n\nFique para ouvir.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6567] = {title = "O campeão da Horda", objective = "Procure Rexxar. O Chefe Guerreiro <o/a> instruiu quanto ao paradeiro dele. Procure nos caminhos de Desolação, entre a Cordilheira das Torres de Pedra e Feralas.", description = "Eu recebi notícias de um dos meus campeões de que talvez haja uma forma de adentrar o covil do dragão. Quero que vá até ele.\n\nRexxar vaga pelo deserto da Desolação, viajando entre as Torres de Pedra e Feralas. Ele aguarda a sua chegada.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6568] = {title = "O testamento de Rexxar", objective = "Leve o Testamento de Rexxar para Myranda, a Bruxa Velha, nas Terras Pestilentas Ocidentais.", description = "Você entende de ilusões, <name>? Pois é de uma ilusão que vai precisar para contornar as defesas da Revoada Negra.\n\nEu conheço alguém que pode lhe ajudar nessa missão de engodos. Ela já nos ajudou no passado, quando julgou que nossa causa era justa.\n\nNas Terras Pestilentas Ocidentais, você encontrará Myranda, a Bruxa Velha, mestre das ilusões – exilada da Aliança de Lordaeron. Vá para lá e leve esta mensagem consigo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6569] = {title = "Ilusões ópticas", objective = "Vá para o Pico da Rocha Negra e colete 20 Olhos de Dragomorfo Preto. Fale novamente com Myranda, a Bruxa Velha, depois que tiver terminado a tarefa.", description = "Então <o/a> <race> quer entrar para a Revoada Negra, é?\n\nCriar a ilusão que você quer é fácil, o difícil é obter os ingredientes para conjurar essa magia divinatória. Como foram Rexxar e o Chefe Guerreiro que lhe confiaram a missão, a Myranda não vai duvidar das suas capacidades.\n\nVá para a cidadela superior do Pico da Rocha Negra caçar dragomorfos pretos até encher uma bolsa de olhos de dragão.\n\nVenha falar novamente com a Myranda quando tiver juntado todos os reagentes.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6570] = {title = "Ardeluta", objective = "Vá até o Pântano da Serpe, no Pântano Vadeoso, e procure o Covil de Ardeluta. Quando estiver lá, use o Amuleto de Subversão Dracônica e fale com Ardeluta.\n\nItem fornecido:\n", description = "Eu harmonizei este medalhão com a magia necessária para criar e manter uma ilusão dentro dos limites do covil de Ardeluta.\n\nArdeluta é um draco ancestral, um velho guardião de Nefarian, encarregado de pôr à prova as crias dragônicas em vias de ascender às fileiras da guarda de elite de Onyxia.\n\nVá até o Pântano da Serpe, no Pântano Vadeoso, e entre no covil de Ardeluta. Uma vez lá, ponha o amuleto e siga para onde seu destino te levar!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[6571] = {title = "Suprimentos do Brado Guerreiro", objective = "Colete os seguintes Suprimentos do Brado Guerreiro: Lâminas Serrilhadas dos Brado Guerreiro, Corda para Transportar Toras, Óleo dos Brado Guerreiro e Carregamento de Machados dos Brado Guerreiro para Locke Okarr no Posto Machadada.", description = "Estão faltando algumas coisinhas no acampamento dos Brado Guerreiro!\n\nA Pixel, lá no Posto Machadada, está com o carregamento de lâminas serrilhadas. Alguém vai ter que viajar até a Angra do Butim para buscar a caixa de machados com o Mestre Portuário Lozgil.\n\nOs carregamentos de óleo e de cordas vão ser mais complicados. As cordas foram surrupiadas pelos pelursos Pelocardos e Torpefloras; já a entrega de óleo foi interceptada pelos sátiros e está sendo usada em rituais detestáveis lá na Trilha da Noite, em Satyrnaar e em Xavian!\n\nVocê pode me ajudar?", progress = "Tomara que você arranje os suprimentos! Eu vou estar encrencado se não fizer a entrega logo!", completion = "Excelente, &lt;name&gt;. Você me poupou a maior trabalheira!\n\nSaiba que fico muito agradecido!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nFaixa do Brado GuerreiroBotas do Brado GuerreiroManoplas do Brado Guerreiro\n\nVocê também receberá:\n\n"},
[6581] = {title = "Lâminas Serrilhadas dos Brado Guerreiro", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nLâminas Serrilhadas dos Brado Guerreiro"},
[6582] = {title = "O teste dos crânios: Áugure", objective = "Encontre e mate o Áugure, o draco da Revoada Dragônica Azul. Arranque a cabeça do draco morto e leve-a para Ardeluta.\n\nVocê sabe que o Áugure se encontra em Hibérnia.", description = "Nós somos filhos do Asa da Morte, filhote. Temos que impor nosso domínio sobre os Aspectos inferiores.\n\nSe você for mesmo dign<o/a> da ascensão, as revoadas inferiores vão murchar na sua presença.\n\nAcabe com os campeões das outras revoadas em nome do nosso pai!\n\nVá para Hibérnia e descubra o paradeiro do Áugure, o poderoso draco azul. Esmague o draco e todos da Revoada Azul que ficarem no seu caminho. Arranque seu crânio e traga-o para mim.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6583] = {title = "O teste dos crânios: Somnus", objective = "Destrua Somnus, o draco campeão da Revoada Verde. Pegue seu crânio e leve-o para Ardeluta.", description = "Assim como nosso pai atormenta os Aspectos, nós temos que levar caos e destruição aos seus filhos.\n\nVá pôr suas habilidades em batalha à prova contra aqueles que sonham despertos: os filhos da Revoada Verde.\n\nViaje para o Pântano das Mágoas e encontre Somnus, o draco campeão da Revoada Verde. Deixe seu ódio fluir e domine o inseto... Traga-me o crânio dele...Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6584] = {title = "O teste dos crânios: Chronalis", objective = "Guardando as Cavernas do Tempo no Deserto de Tanaris, encontra-se Chronalis, filho de Nozdormu. Destrua-o e leve seu crânio para Ardeluta.", description = "Os vigias do tempo, filhos de Nozdormu, Senhor dos Séculosss...\n\nTrês guardam as Cavernas do Tempo, mas a Revoada Negra só tem interesse em um: Chronalis, o Dobrador do Tempo, draco favorito de Nozdormu.\n\nMate o campeão e até as estoicas areias do tempo prantearão sua perda. Volte com o crânio dele em mãos.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6585] = {title = "O teste dos crânios: Axtroz", objective = "Vá para Grim Batol e encontre Axtroz, o draco campeão da Revoada Vermelha. Destrua-o e arranque sua cabeça. Leve o crânio para Ardeluta.", description = "Foi sobre as montanhas de Grim Batol que nosso pai confrontou as forças combinadas dos Aspectos. O gesto covarde de Alexstrasza, a traidora, ganhou tempo para que os outros Aspectos escapassem à ira do nosso pai.\n\nQue gesssto mais vergonhoso...\n\nHoje a Revoada Vermelha protege Grim Batol. Axtroz, seu tenente, tem que ser destruído. Traga o crânio de Axtroz para mim. Em nome do pai, filhote!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6601] = {title = "Ascensão...", objective = "Pelo jeito, a farsa chegou ao fim. Você já sabe que o Amuleto de Subversão Dracônica criado por Myranda, a Bruxa Velha, não vai funcionar no Pico da Rocha Negra. Você devia ir atrás de Rexxar e explicar a situação. Mostre a ele o Amuleto da Chama Dracônica Fraco. Pode ser que ele saiba o que fazer.", description = "A partir do crânio dos nossos inimigosss, nós confeccionamos um medalhão. Você conhece o medalhão, não? Sem dúvida, já o viu em torno do pescoço dos anciões.\n\nAqui, filhote. Volte ao Pináculo e entregue-o ao General Drakkisath. O General vai conjurar o último encantamento, que deixará o berloque e seu espírito em harmonia.\n\nVocê portará o medalhão como uma condecoração, um símbolo de que ascendeu a um dos postos mais almejados: guardi<ão/ã> da grande mãe.\n\nAnde logo!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6602] = {title = "Sangue do Campeão dos Dragões Negros", objective = "Viaje para o Pico da Rocha Negra e mate o General Drakkisath. Recolha seu sangue e leve-o para Rexxar.", description = "Você fará uma visita ao General, sim, mas não na forma de Dragão Negro.\n\nEntenda, cerimônia é só outro jeito de dizer que derramarão sangue em nome da Revoada Negra.\n\nO amuleto latente precisa do sangue do General para despertar. É um mecanismo rudimentar de proteção.\n\nVolte ao Pico da Rocha Negra e destrua Drakkisath. Traga-me o sangue dele e eu ativarei a chave do covil de Onyxia.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nAmuleto da Chama Dracônica"},
[6603] = {title = "Problema em Hibérnia!", objective = "Encontre Bruna Covaneve perto das fontes termais de Hibérnia.", description = "Apareceram uns problemas em Hibérnia!\n\nEu passo muito tempo na selva, claro que eu sei dessas coisas... Eu digo isso com muito orgulho. As horas que passo nas montanhas valem mais que ouro pra mim. Elas me ajudam a manter os reflexos rápidos, a cabeça fria e o olho afiado...\n\nÉ preciso ter juízo, cérebro e maturidade pra apreciar a selva como eu aprecio.\n\nDo que eu estava falando mesmo?\n\nAh é, problemas. Fale com Bruna Covaneve nas fontes termais, ela vai poder te dar mais informações.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6604] = {title = "Coruscante enfurecido", objective = "Fale com Jaron Moldapedra na Aldeia Chuva Estelar.", description = "Você reparou que tem um acampamento abandonado ao sul? Parece que os coruscantes endoidaram e destruíram tudo que encontraram na reta!\n\nEu ouvi rumores de que um dos sobreviventes do ataque repousa na Aldeia Chuva Estelar, ao oeste. Estou morrendo de curiosidade para saber o que aconteceu... você não?", progress = nil, completion = "Ei, como posso ajudar?", rewards = "Você receberá:\n\n"},
[6605] = {title = "Sujeitinha esquisita", objective = "Fale com Winna Perícon na Selva Maleva.", description = "Eu acabei de voltar da Selva Maleva, <name>. No caminho, parei no Posto Peçonha e conheci uma sujeitinha para lá de esquisita... Ela tinha uma ideia fixa, eu poderia até lhe contar que ideia era essa, mas acho que ela ia preferir se explicar pessoalmente.\n\nSe tiver interesse, rume para o Posto Peçonha, na porção oeste da Selva Maleva, e procure Winna Perícon.\n\nSei lá, talvez ela precise da sua ajuda...Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6606] = {title = "Um pouquinho de sorte", objective = "Fale com a Mandingueira Mau'ari em Visteterna.Um pouquinho de sorte nunca é demais, não é? Uma vantagenzinha já basta para virar o jogo a nosso favor...\n\nEu conheci uma mandingueira um tanto quanto excêntrica um tempo atrás. Ela me fez um amuleto da sorte... por um preço. Vá por mim, valeu a pena!\n\nVocê não teria interesse em um amuleto igual? Encontre a Mandingueira Mau'ari em Visteterna. Do meu, eu não abro mão!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", description = "Um pouquinho de sorte nunca é demais, não é? Uma vantagenzinha já basta para virar o jogo a nosso favor...\n\nEu conheci uma mandingueira um tanto quanto excêntrica um tempo atrás. Ela me fez um amuleto da sorte... por um preço. Vá por mim, valeu a pena!\n\nVocê não teria interesse em um amuleto igual? Encontre a Mandingueira Mau'ari em Visteterna. Do meu, eu não abro mão!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6607] = {title = "Nat Pagle, o pescador supremo", objective = "Nat Pagle quer que você pesque os seguintes peixes:\n\nO Graçapé-brumalga, na Praia Brumalga, que fica no Pântano das Mágoas.\n\nA Piranha-de-sar'theris, na Praia de Sar'theris, que fica na Desolação.\n\nO Atum-amarelo-de-feralas, no Rio Verdantis, em Feralas.\n\nA Molinésia-azul-da-costa-selvagem, na Costa Selvagem, na Selva do Espinhaço.\n\nFale novamente com Nat Pagle quando já tiver fisgado todos!", description = "Se dissesse que adoro pescar, eu estaria mentindo. Sabe, já faço isso há uns vinte, trinta anos.\n\nReconheço que as pessoas são levadas a fazer o que fazem por diferentes razões.\n\nAgora, eu não tô dizendo que cê precisa pescar por trinta anos para pegar tudo que é peixe do mar e virar um mestre pescador. Reconheço que precisa determinação – determinação pra ficar sentado por horas sem fazer porcaria nenhuma.\n\nTaí, me traz uns peixes raros que eu vou te ensinar uns macetes.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nPesca\n\nO seguinte feitiço será lançado em você:\n\nPescador Artífice\n\nVocê também receberá:\n\n"},
[6608] = {title = "Você bom demais", objective = "Você acha que Lumak está tentando dizer que ele já não pode mais ajudar a melhorar suas habilidades de pesca. Você pode tentar encontrar o amigo dele, Nat Pagle, um humano pescador, que fica em uma das ilhas ao sul de Theramore (pelo menos é isso o que você acha que Lumak disse).", description = "Lumak tem mais nada pra ensinar <name>. Você <bom/boa> demais! Procurar amigo de Lumak, Nat Pagle. É humano, não é de comer! Amigo de Lumak! Nat ensina você pescar mais bom.\n\nNat fica no Pântano Vadeoso, sul de cidade humana, Theramá-sei-lá-como-chama. Nat deve tá pescando. Vai vai!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6609] = {title = "Eu não sei nada!", objective = "Vá até a cidade costeira de Theramore, em Kalimdor. A partir de lá, procure uma ilha a sul da capital. Nat Pagle <o/a> aguarda!", description = "Eu sou um anão, tem dó, tchê! Não fui feito para pescar! Olha só para as minhas fuças! Sou mais pesado que mudança de ferreiro.\n\nNão sei mais nada – eu me rendo. Já ensinei tudo que sei.\n\nEntendeste? Bueno. Tenho um amigo em Kalimdor que é mais esperto que cachorro de padaria e vai poder te ajudar. O nome dele é Nat Pagle. Vai atrás dele nas ilhas a sul de Theramore. Pica a mula!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6610] = {title = "Escondidinho Marisquete", objective = "Funesto Marrão quer que você consiga os seguintes itens:\n\n12 Ovos Gigantescos. Você os encontra nas rocas que vagueiam por Tanaris ou em qualquer pássaro grande.\n\n10 fatias de Carne de Marisco Condimentada. Você as encontra dentro dos mariscos, óbvio!\n\n20 pedaços de Queijo Furado de Alterac. Procure alguém que venda esse queijo!\n\nConsiga todos os itens e fale novamente com Funesto.", description = "Você já ouviu falar de escondidinho marisquete? Claro que não ouviu! É a minha mais nova obra-prima culinária! Ah, <name>... só de pensar, fico com água na boca.\n\nInfelizmente, estou sem nenhum ingrediente necessário! Vamos fazer um acordo? Você me traz tudo o que preciso e eu vou te ensinar os grandes segredos da culinária exótica. E, de quebra, leva uns marisquetes pra beliscar! Fechado?\n\nEu preciso de:", progress = "Meu desejo por escondidinho marisquete vai me consumir vivo! Anda, <name>! Antes que eu coma o que sobrou do almoço de ontem.", completion = "Você está prestes a aprender como fazer o escondidinho dos deuses, &lt;name&gt;. Observe e aprenda!", rewards = "Você irá aprender:\n\nCulinária\n\nO seguinte feitiço será lançado em você:\n\nCozinheiro Artífice\n\nVocê também receberá:\n\nEscondidinho Marisquete"},
[6611] = {title = "Para Geringontzan!", objective = "Vá para Geringontzan, no Deserto de Tanaris, e fale com Funesto Marrão para progredir na carreira de <cozinheiro/cozinheira>.", description = "Você qué que Zamja ensine mais? Cê já tem todo o mojo de Zamja e ainda qué mais!\n\nZamja não pode ajudá. Vai pra Geringontzan, <race> <gordinho/gordinha>. Lá tem goblinho chamado Funesto, ele ajuda você, <gordinho/gordinha>.\n\nQuando cê virá <o/a> bambambã da cozinha, cê volta e ensina Zamja, tá? Vai vai!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6612] = {title = "Eu sei de um cara...", objective = "Vá para Geringontzan, no deserto de Tanaris, e fale com Funesto Marrão sobre como se desenvolver na carreira de <cozinheiro/cozinheira>.", description = "Dá para ver pela colher de pau que tu não é meia-boca. Sinto muito mas eu não tenho nada para ensinar para $Gum índio velho:uma índia velha; feito tu.\n\nMas olha a tua sorte: eu sei de um excelente cozinheiro! Ele é goblin e deve ser o melhor de Kalimdor, no más.\n\nBah, a má notícia é que tu vai ter que picar a mula para Kalimdor, <guri/guria>. Vai para Geringontzan e procura um tal de nome Funesto Marrão que ele vai te botar na linha.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6621] = {title = "O rei dos Torpeflora", objective = "Finque o Estandarte de Karang no Montículo Totêmico dos Torpeflora. Não deixe que os pelursos destruam o estandarte. Derrote o Chefe Murgut e leve o Totem de Murgut para Karang Amakkar em Zoram'gar.", description = "<name>, os Torpefloras, outra tribo de pelursos, vivem ao sudeste de Astranaar. Não são tantos quanto os Pelocardos, mas são ferozes e perigosos. Uma simples demonstração de força e vamos eliminar esse perigo.\n\nSiga para a aldeia dos Torpefloras com esse estandarte e finque-o no montículo do totem. Eles tentarão destruir o estandarte, mas você vai impedi-los até que o chefe Murgut apareça para defender a honra da tribo. Traga-me o totem dele.\n\nJunte seus aliados, <name>. Isso não é tarefa para $Gum:uma; <class> solitári<o/a>.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nTúnica do Guarda GrosseiroGuarda-pernas de Cobalto\n\nVocê também receberá:\n\n"},
[6622] = {title = "Triagem", objective = "Use Bandagens de Triagem para salvar pacientes. Você precisa salvar 15 deles antes que 6 morram devido aos ferimentos.\n\nLembre-se da ordem da triagem! Os Soldados Mortalmente Feridos são os que morrem mais rápido, seguidos dos Soldados Gravemente Feridos. Os Soldados Feridos demoram mais a morrer e devem ser tratados após os outros terem passado pela triagem.", description = "Bom dia, $Gdoutor:doutora;. Hoje, testaremos suas habilidades na triagem de pacientes. Caso passe neste teste, você entrará para o respeitado grupo do Pronto-socorro da Horda.\n\nAgora preste atenção! Você deve priorizar os mortalmente feridos, seguidos dos gravemente feridos. Os que estiverem apenas feridos devem ser tratados por último. Você precisa salvar 15 pacientes para passar nesse exame. Caso 6 deles morram, você será <reprovado/reprovada>.\n\nEu lhe fornecerei bandagens de triagem.\n\nBoa sorte, <doutor/doutora>.", progress = nil, completion = "Doutor &lt;name&gt;, bem-vindo ao Pronto-socorro da Horda.", rewards = "Você irá aprender:\n\nPrimeiros Socorros\n\nO seguinte feitiço será lançado em você:\n\nSocorrista Artífice\n\nVocê também receberá:\n\n"},
[6623] = {title = "Pronto-socorro da Horda", objective = "Vá para a Ruína do Martelo no Planalto Arathi e fale com o Doutor Victor Zerbini para entrar para a equipe do Pronto-socorro da Horda.", description = "<Doutor/Doutora> <name>, eu presumo. É um prazer vê-<lo/la>.\n\nTenho ótimas notícias para você! O Doutor Victor Zerbini, médico-chefe do Pronto-socorro da Horda, faz questão que você se junte à equipe dele na Ruína do Martelo. É uma oportunidade única na vida! Eu daria tudo para estudar com o grande Doutor Victor Zerbini! Não desperdice esta chance!", progress = nil, completion = "Ótimo. Vista o jaleco para começarmos logo!", rewards = "Você receberá:\n\n"},
[6624] = {title = "Triagem", objective = "Use Bandagens de Triagem para salvar pacientes. Você precisa salvar 15 deles antes que 6 morram devido aos ferimentos.\n\nLembre-se da ordem da triagem! Os Soldados Mortalmente Feridos são os que morrem mais rápido, seguidos dos Soldados Gravemente Feridos. Os Soldados Feridos demoram mais a morrer e devem ser tratados após os outros terem passado pela triagem.", description = "Bom dia, $Gdoutor:doutora;. Hoje, testaremos suas habilidades na triagem de pacientes. Caso passe neste teste, você entrará para o respeitado grupo do Pronto-socorro da Aliança.\n\nAgora preste atenção! Você deve priorizar os mortalmente feridos, seguidos dos gravemente feridos. Os que estiverem apenas feridos devem ser tratados por último. Você precisa salvar 15 pacientes para passar nesse exame. Caso 6 deles morram, você será <reprovado/reprovada>.\n\nBoa sorte, <doutor/doutora>.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nPrimeiros Socorros\n\nO seguinte feitiço será lançado em você:\n\nSocorrista Artífice\n\nVocê também receberá:\n\n"},
[6625] = {title = "Pronto-socorro da Aliança", objective = "Vá para a cidade de Theramore, no Pântano Vadeoso, e fale com o Doutor Gregório Casagrande sobre entrar para a equipe do Pronto-socorro da Aliança.", description = "<Doutor/Doutora> <name>, eu presumo. É um prazer vê-<lo/la>.\n\nTenho ótimas notícias para <o senhor/a senhora>! O doutor Gregório Casagrande, médico-chefe do Pronto-socorro da Aliança, solicitou a sua presença junto à equipe dele em Theramore. É uma oportunidade única na vida! Eu faria de tudo para estudar com o grande doutor Casagrande! Não desperdice esta chance, <doutor/doutora>.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6626] = {title = "A hoste do mal", objective = "Mate 8 Guerreiros do Urzal, 8 Tecespinhos do Urzal e 8 Sectários da Cabeça da Morte, depois fale novamente com Mírian Lunacanto perto da entrada do Urzal dos Mortos.", description = "A doença dos Renegados não é... contagiosa... até onde eu vi. Mas tem um diplomata passando a morte-viva adiante para quem tiver interesse. Começo a pensar que seja um membro do Flagelo.\n\nPor enquanto, minha preocupação não é com ele, mas com o exército que se forma ao redor do sujeito. Eles irrompem da escuridão dia após dia, implorando por mais poder.\n\nEu não tenho força para enfrentá-los, <name>. O que me diz? Está a fim de uma caçada?", progress = nil, completion = "Eu agradeço, &lt;name&gt;! O trabalho que você fez será de grande ajuda.\n\nEu vou continuar estudando o diplomata e o resto dos javatuscos na região. Se estiver atrás de aventura, sugiro juntar uns amigos e adentrar aquela caverna parecida com um focinho de javali. Uma vez lá dentro, certamente vocês enfrentarão desafios bem maiores.", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
[6627] = {title = "Teste de saber", objective = "Responda à pergunta de Braug Espírito Turvo corretamente, depois fale com ele novamente. Quando você estiver pront<o/a>, ele estará na Cordilheira das Torres de Pedra.", description = "Você me trouxe o livro, agora é hora da pergunta. Você está preparad<o/a>?\n\nNão há pressa, <class>, então não se sinta pressionad<o/a>. Pense o tempo que quiser antes de responder.", progress = nil, completion = "Parabéns, &lt;class&gt;. Você estudou o texto que eu pedi e passou em mais um teste.", rewards = "Você receberá:\n\n"},
[6628] = {title = "Teste de saber", objective = "Responda à pergunta de Pasqual Fintallas corretamente, depois fale com ele novamente. Quando você estiver pront<o/a>, ele estará na Cidade Baixa.", description = "Mais uma vez é chegada a hora. Como estudante de história, você abraçou a tarefa com grande afinco.\n\nE agora eu lhe apresentarei a pergunta. Quando estiver pront<o/a>, fale comigo novamente. Pense o tempo que quiser, <name>... não se sinta pressionad<o/a> a responder depressa.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6629] = {title = "Mate Grundig Nuvem Negra", objective = "Mate Grundig Nuvem Negra e 6 Brutos Temível Totem, e fale novamente com Makaba Casco Chato perto da extremidade sudeste das Torres de Pedra.", description = "<name>, você fez um ótimo trabalho atacando os Temível Totem. Se você se atrever, Grundig Nuvem Negra e seu bando pessoal de brutamontes são, sem dúvida, os piores do grupo. Foi ele que liderou o ataque brutal contra minha vila.\n\nVocê o encontrará no Aldeia do Temível Totem, um pouco mais adiante no caminho a oeste. Mate-o e lhe serei eternamente grato.", progress = nil, completion = "Grundig Nuvem Negra está morto! &lt;name&gt;, serei sempre grato pelo que você fez aqui hoje.", rewards = "Você receberá:\n\n"},
[6641] = {title = "Vorsha, a Açoitadora", objective = "Escolte Muglash em segurança até o braseiro. Ajude-o a apagar o braseiro, e proteja-o na luta contra as nagas.\n\nSe cumprir a missão, volte ao Assentamento Zoram'gar e informe o Mensageiro do Brado Guerreiro da morte de Vorsha, a Açoitadora.", description = "Vorsha, a Açoitadora... A diabólica hidra que vaga pelos mares...\n\nEla vem atacando o posto avançado, sempre sem motivo, acabando com tudo que atravesse seu caminho.\n\nMas eu estou pronto, <name>. Estou preparado para vencer a hidra e dar um fim aos ataques a Zoram'gar.\n\nAs nagas prestam homenagem à natureza malévola daquela fera mantendo um braseiro sempre aceso numa ilha perto da costa. Acho que, se apagarmos a chama, a Vorsha vai aparecer, furiosa.\n\nVamos precisar de ajuda. Portanto, chame seus amigos mais fortes.", progress = nil, completion = "Eu presenciei muitos ataques das nagas desde que cheguei aqui. Fico muito mais tranquilo por saber que Vorsha não mais vagará livremente por aí.\n\nSomos eternamente gratos a você, &lt;name&gt;.", rewards = "Você receberá:\n\nAnel de Chifre"},
[6642] = {title = "Um favor para a irmandade: Minérios de Ferro Negro", objective = "Minério de Ferro Negro ainda é uma mercadoria bastante valiosa, mesmo existindo em abundância aqui no Abismo. Os sagrados armamentos de Ragnaros são feitos com ele.\n\nPara cada 10 Minérios de Ferro Negro que me trouxer, sua reputação entre a Irmandade aumentará.", description = nil, progress = "Minério de Ferro Negro ainda é uma mercadoria bastante valiosa, mesmo existindo em abundância aqui no Abismo. Os sagrados armamentos de Ragnaros são feitos com ele.\n\nPara cada 10 Minérios de Ferro Negro que me trouxer, sua reputação entre a Irmandade aumentará.", completion = "Aceitamos sua oferta e sua reputação conosco só aumenta. Continue assim e logo terá acesso ao nosso antigo comércio.", rewards = nil},
[6643] = {title = "Um favor para a irmandade: Núcleos de Fogo", objective = "Ai dos que desafiarem a sorte no Núcleo Derretido. A morte chega rápido, um castigo impiedoso.\n\nMuitos membros da Irmandade se perderam na procura por Núcleos de Fogo.\n\nTraga-nos 1 Núcleo de Fogo e sua reputação entre a Irmandade aumentará.", description = nil, progress = "Ai dos que desafiarem a sorte no Núcleo Derretido. A morte chega rápido, um castigo impiedoso.\n\nMuitos membros da Irmandade se perderam na procura por Núcleos de Fogo.\n\nTraga-nos 1 Núcleo de Fogo e sua reputação entre a Irmandade aumentará.", completion = "Aceitamos sua oferta e sua reputação conosco só aumenta. Continue assim e logo terá acesso ao nosso antigo comércio.", rewards = nil},
[6644] = {title = "Um favor para a irmandade: Núcleo de Lava", objective = "A Irmandade do Tório aprova sua dedicação, <race>. É por isso que Lokthos tem permissão para fazer negócios.\n\nTraga-nos 1 Núcleo de Lava e sua reputação entre a Irmandade aumentará.", description = nil, progress = "A Irmandade do Tório aprova sua dedicação, <race>. É por isso que Lokthos tem permissão para fazer negócios.\n\nTraga-nos 1 Núcleo de Lava e sua reputação entre a Irmandade aumentará.", completion = "Aceitamos sua oferta e sua reputação conosco só aumenta. Continue assim e logo terá acesso ao nosso antigo comércio.", rewards = nil},
[6645] = {title = "Um favor para a irmandade: Couro do Núcleo", objective = "Muitas receitas sagradas utilizam a pele dos animais escolhidos por Ragnaros.\n\nTraga-nos 2 Couros do Núcleo retirados dos cadáveres de Cão-magma do Núcleo Derretido e sua reputação entre a Irmandade aumentará.", description = nil, progress = "Muitas receitas sagradas utilizam a pele dos animais escolhidos por Ragnaros.\n\nTraga-nos 2 Couros do Núcleo retirados dos cadáveres de Cão-magma do Núcleo Derretido e sua reputação entre a Irmandade aumentará.", completion = "Aceitamos sua oferta e sua reputação conosco só aumenta. Continue assim e logo terá acesso ao nosso antigo comércio.", rewards = nil},
[6646] = {title = "Um favor para a irmandade: Sangue-da-montanha", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[6661] = {title = "Ratos de porão", objective = "Capture 5 Ratos Correfundo com a Flauta do Pegador de Ratos. Conduza os ratos até o Monty. Não se esqueça de entregar a flauta quando terminar.", description = "Alto lá, <pulguento/pulguenta>! Arrr... Tá procurando faina? O tombadilho tá pegando com a rataria e não tem marujada que dê conta.\n\nQue qui é? Tu só tem que pegar a flauta do pegador de rato e soprar meia dúzia de notas perto desses vermes. Aí eles vão te seguir até o quinto dos infernos!\n\nTu pega cinco sarnentos e traz aqui de volta pro velho Monty. Mas tudo vivo, hein?\n\nArrr....", progress = "Arrr... Ô marujo, tá escamando, é?", completion = "Esses aqui servem direitinho, camarada. O mano precisa deles com sangue nas veias, senão fica tudo podre... Campanha nenhum vai querer rancho de espetinhos de rato azedo, arrr!", rewards = "Você receberá:\n\n"},
[6662] = {title = "Espetinhos de... rato", objective = "Entregue a Caixa de Carne Duvidosa para Nipsy, na estação do Metrô Correfundo de Ventobravo.", description = "Meu mano Nipsy tem um negócio de espetinhos de rato do outro lado do metrô. <O/A> camarada é <safo/safa> e vai entregar o caixote de rato pro Nipsy, arrr? Antes que fique tudo podre, arrr? Anda logo!\n\nJá andou de metrô antes, arrr? Mole, mole! Tu só tem que entrar lá e largar vela. Só não vai dar uma de afobado e ficar de bobeira com braço e perna pra fora. É assim que a marujada arruma perna de pau pra vida inteira...", progress = "<Nipsy enfia o mindinho em uma das aberturas para arejamento do caixote.>\n\nVivinhos da silva... E bem a tempo!", completion = "Pobre Monty. Desde o incidente em Gnomeregan, ele nunca mais foi o mesmo. Aquele rapaz nunca viu o oceano na vida e fica dando uma de pirata...\n\n&lt;Nipsy suspira.&gt;\n\nPelo menos ele mantém meu estoque de criaturinhas guinchantes.", rewards = "Você receberá:\n\nKebab de Rato Correfundo"},
[6681] = {title = "Corvoforte, a mansão", objective = "Leve o Selo de Corvoforte para Fahrad na Mansão de Corvoforte, que fica oculta nas colinas de Eira dos Montes.\n\nSua jornada testará sua sagacidade. Prepare-se.", description = "<name>,\n\nCorvoforte demonstrou interesse em você, jovem ladr<ão/a>. Temos observado você. Seu estilo é um tanto desajeitado, mas não é nada que não possamos corrigir.\n\nSe estiver pront<o/a> para seguir carreira como discípul<o/a> do Mestre Corvoforte e integrante da Liga dos Assassinos, basta levar o Selo de Corvoforte, anexo a esta carta, à Mansão de Corvoforte.\n\nSua primeira tarefa é encontrar a mansão, oculta nas colinas de Eira dos Montes, e demonstre sua sagacidade.\n\nAtenciosamente,\n\nFahradVocê receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6701] = {title = "Emblemas da Camarilha", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[6721] = {title = "O caminho do caçador", objective = "Fale com Dazalar em Dolanaar.", description = "Olá, jovem <caçador/caçadora>. Você me olha como quem tem habilidade suficiente para lidar com uma fera e treiná-la como sua mascote.\n\nProcure Dazalar em Dolanaar. Ele pode colocar você no caminho certo para ganhar um ajudante.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6722] = {title = "O caminho do caçador", objective = "Fale com Dazalar em Dolanaar.", description = "Olá, jovem <caçador/caçadora>. Você me olha como quem entende do riscado para lidar com uma fera e treiná-la como sua mascote.\n\nProcure Dazalar em Dolanaar. Ele pode colocar você no caminho certo para ganhar um animal de estimação.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6741] = {title = "Mais espólios!", objective = "Preciso de mais restos de armadura, <name>! Tem mais pra mim?", description = nil, progress = "Preciso de mais restos de armadura, <name>! Tem mais pra mim?", completion = "Obrigado mais uma vez mais, &lt;name&gt;. Você só dá orgulho à Horda...", rewards = "O seguinte feitiço será lançado em você:\n\nArmadura Polida"},
[6761] = {title = "Uma nova fronteira", objective = "Volte a Darnassus e fale com Mathrengyl Ursivagus no Enclave Cenariano, em Darnassus.", description = "Destaquei meu segundo em comando, Mathrengyl Ursivagus, para atender ao pedido de ajuda da Clareira da Lua. obre isso, fale com ele, e NÃO comigo.\n\nPermita-se dar um... conselho... de amigo. Fui eu quem nos tirou das trevas quando tudo estava perdido para nossa raça, e NÃO o Círculo Cenariano, lá na Clareira da Lua. Teldrassil dá nova vida a todos nós! Eles gostariam que você pensasse diferente, e acreditar no que eles dizem seria tolice.\n\nDeixarei você resolver se quer ser tol<o/a> ou não.", progress = nil, completion = "Sim, &lt;class&gt;, obrigado por vir. Peço desculpas pelo mal-entendido... Há muita suspeição entre o arquidruida aqui e o Guardião Remulos, na Clareira da Lua. Um suspeita do outro, e o ato de que a estranha corrupção de Teldrassil persiste ainda é um problema que não ajuda em nada para amenizar a desconfiança.\n\nA despeito disso, nosso dever para com o Círculo Cenariano continua, mesmo com os líderes em desacordo.", rewards = "Você receberá:\n\n"},
[6762] = {title = "Ravine Saturna", objective = "Fale com Ravine Saturna no Refúgio Noturno, que fica na Clareira da Lua. A Clareira da Lua fica entre a Selva Maleva e Hibérnia. Uma estrada que sai do Domínio dos Presamatos leva até lá.", description = "Para esta tarefa, o senescal do Guardião Remulos na Clareira da Lua, Ravine Saturna, quer auxílio na exploração dos vastos ermos a oeste de Tanaris, além de Un'Goro. Você deve encontrá-lo no Refúgio Noturno, a vila principal da Clareira da Lua.\n\nEmbora seu trabalho sem dúvida vá beneficiar a todos nós, eu <o/a> aconselho a agir com cautela. As tensões entre Darnassus e a Clareira da Lua podem vir a se agravar com o tempo.", progress = nil, completion = "Sou Ravine Saturna, e em nome do Guardião Remulos e todos os habitantes desta sagrada clareira eu lhe dou nossas boas-vindas.\n\nEu aprecio seu interesse e sua disposição para ajudar o Círculo Cenariano. Sei também que o arquidruida de Darnassus expressou desagrado por você nos prestar ajuda. Fique tranquilo. É por um bem maior, e você estará resguardado de quaisquer repercussões negativas de que possa vir a ser alvo.\n\nVocê está entre amigos aqui, &lt;name&gt;.", rewards = "Você receberá:\n\n"},
[6781] = {title = "Mais sobras de armadura", objective = nil, description = nil, progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nArmadura Polida"},
[6801] = {title = "Lokholar, o Senhor do Gelo", objective = "Eu venho do Penhasco do Trovão, morto-vivo. Caerne me mandou pra cá para ajudar o Clã Lobo do Gelo nesses tempos difíceis.\n\nMas não vamos perder mais tempo. Ataque nossos inimigos e me traga o sangue deles. Quando bastante sangue tiver sido coletado, o ritual de evocação pode começar.\n\nObteremos a vitória quando o senhor elemental for atiçado contra o exército Lançatroz.", description = nil, progress = "Eu venho do Penhasco do Trovão, morto-vivo. Caerne me mandou pra cá para ajudar o Clã Lobo do Gelo nesses tempos difíceis.\n\nMas não vamos perder mais tempo. Ataque nossos inimigos e me traga o sangue deles. Quando bastante sangue tiver sido coletado, o ritual de evocação pode começar.\n\nObteremos a vitória quando o senhor elemental for atiçado contra o exército Lançatroz.", completion = "O sangue de nossos inimigos foi derramado. Vou misturar esse sangue com o resto que já foi coletado. Quando a oferenda estiver completa, o Senhor do Gelo irá mostrar aos cães Lançatroz toda a ira do Clã Lobo do Gelo!", rewards = nil},
[6804] = {title = "Água contaminada", objective = "Use o Aspecto de Neptulon nos elementais contaminados das Terras Pestilentas Orientais. Leve 12 Braçadeiras Dissonantes e o Aspecto de Neptulon ao Duque Hidráxis em Azshara.", description = "Enviamos uma tropa de batedores elementais para as terras pestilentas orientais de Lordaeron. Infelizmente, eles foram infectados pela moléstia que grassa naquele lugar maldito. Nós, os Senhores das Águas, não podemos permitir tamanha afronta à nossa pureza!\n\nEste frasco contém um aspecto de Neptulon. Use-o nos elementais pestilentos para combater o veneno e curá-los! Tenha cuidado, <class>. O conflito interior deles incitará um surto de loucura.\n\nDerrote os surtos dissonantes e me traga as braçadeiras de vinculação deles.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6805] = {title = "Tempestades e estrondores", objective = "Mate 15 Tempestades de Poeira e 15 Estrondores do Deserto e fale novamente com o Duque Hidráxis em Azshara.", description = "Nós, os elementais da água, estamos travando uma árdua guerra contra os seres dos elementos inferiores. Mostre-me o seu comprometimento e prove que é <um aliado/uma aliada> de valor!\n\nBem longe ao sul, os servos do ar e da terra se refugiam no extremo das terras desérticas de Silithus, em um local chamado Vale de Cristal. Eles evitam que os seres menores e os mortais se aproximem dos itens de poder. Não sei por que o fazem, mas não posso permitir que os planos deles prossigam.\n\nVá para o Vale de Cristal, em Silithus, derrote nossos inimigos e venha falar comigo novamente.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6821] = {title = "O Olho do Mirabrasa", objective = "Leve o Olho do Mirabrasa ao Duque Hidráxis em Azshara.", description = "Você provou que é capaz de derrotar nossos inimigos e de cumprir missões pela causa dos Senhores das Águas, mas só enfrentou os adversários mais fracos até agora... Como você se sairá contra um inimigo mais poderoso?\n\nÉ sua próxima tarefa.\n\nOs orcs aprisionaram um servo do fogo, o Piroguarda Mirabrasa, no Pico da Rocha Negra. Descubra onde ele está preso, mate os captores e depois assassine o mirabrasa. Traga-me o olho dele e reconhecerei o seu valor.", progress = "Conseguiu trazer o Olho, <class>? Será que você é forte o bastante para entrar no Pico da Rocha Negra?", completion = "Ah, então a sua força é verdadeira. E também vejo que é astuto, pois o Pico da Rocha Negra é mais do que um simples desafio de força bruta.\n\n&lt;name&gt;, ainda há algo que precisa fazer para se consolidar como um dos nossos aliados de valor...", rewards = "Você receberá:\n\n"},
[6822] = {title = "O Núcleo Derretido", objective = "Mate 1 Senhor do Fogo, 1 Gigante Derretido, 1 Cão-magma Antigo e 1 Irrompedor de Lava e depois fale novamente com o Duque Hidráxis em Azshara.", description = "<name>, acho que agora é o momento de lhe apresentar um teste definitivo. Está <preparado/preparada>?\n\nHá uma passagem para o Núcleo Derretido nas profundezas do Abismo Rocha Negra. É uma fortaleza do inimigo, protegida pelos servos do fogo mais poderosos de todo o mundo.\n\nVá ao Núcleo Derretido, <name>. Prove que tem o poder e a força de vontade para derrotar o que encontrar lá.\n\nConvoque aliados que possam ajudar. Só um exército tem chance de invadir o Núcleo Derretido e sobreviver.", progress = nil, completion = "Você conseguiu o que seria impossível para a maioria dos seus: enfrentar e vencer os seres do Núcleo Derretido!\n\nVocê é muito mais talentosa do que poderíamos esperar de uma criatura tão pequena.\n\nUm dia, quem sabe, os Senhores das Águas e o seu povo selem um pacto.", rewards = "Você receberá:\n\n"},
[6823] = {title = "Agente de Hidráxis", objective = "Alcance a reputação de <Honrado/Honrada> pelos Senhores das Águas Hidraxianos e depois fale com o Duque Hidráxis em Azshara.", description = "Participe da guerra contra nossos adversários, os servos do ar, da terra e do fogo, para provar que realmente deseja ser nosso aliado. Cada inimigo que derrotar os enfraquecerá e provará ainda mais o seu comprometimento com a nossa causa.\n\nA campanha será longa, mas você obterá grande honra e glória se for perseverante.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6824] = {title = "Mãos do inimigo", objective = "Leve as Mãos de Lúcifron, Sulfuron, Geena e Shazzrah ao Duque Hidráxis em Azshara.", description = "<name>, os seus serviços são de grande valia para nós. Relutamos em enviá-<lo/la> nessa última missão pois não queremos perder <um/uma> agente tão útil.\n\nNo entanto, um bom resultado agora será de grande ajuda para os nossos planos e concluímos que vale a pena arriscar. Queremos que destrua os seguintes capitães do Núcleo Derretido: Lúcifron, Sulfuron, Geena e Shazzrah. Mate-os e traga-me as mãos deles!\n\nVocê precisará dar tudo de si para cumprir a missão, mas será um duro golpe para o Senhores do Fogo se conseguir.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[6825] = {title = "Clamor do Ar – Esquadrilha de Guse", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[6826] = {title = "Clamor do Ar – Esquadrilha de Jeztor", objective = "Você trabalhou duro, <name>, mas estamos apenas começando!\n\nMinhas Mantícoras de Batalha devem provar a carne dos alvos. Isso garantirá um ataque preciso aos nossos inimigos!\n\nMinha esquadrilha é a segunda mais poderosa do comando aéreo. Assim, ela atacará os nossos adversários mais poderosos. Para isso, meus bichinhos precisam provar a carne dos Tenentes Lançatroz.\n\nApresse-se, soldado!", description = nil, progress = "Você trabalhou duro, <name>, mas estamos apenas começando!\n\nMinhas Mantícoras de Batalha devem provar a carne dos alvos. Isso garantirá um ataque preciso aos nossos inimigos!\n\nMinha esquadrilha é a segunda mais poderosa do comando aéreo. Assim, ela atacará os nossos adversários mais poderosos. Para isso, meus bichinhos precisam provar a carne dos Tenentes Lançatroz.\n\nApresse-se, soldado!", completion = "Mais! Mais! Traga-me mais, pois minha esquadrilha está quase pronta!", rewards = nil},
[6827] = {title = "Clamor do Ar – Esquadrilha de Mulverick", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[6843] = {title = "Da Foo", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[6844] = {title = "Umbero, o arquivista", objective = "Leve o Objeto Silitídeo Incrustado para Umbero, arquivista do Círculo Cenariano, na vila de Refúgio Noturno, na Clareira da Lua.", description = "Ainda há muito a fazer aqui, mas acho que levar isso ao Círculo para análise é fundamental. Pode confiar. O trabalho ainda continuará por aqui quando isso tudo acabar. Se for um diário, isso nessa quitina grudenta, o Círculo finalmente terá uma janela para o passado!\n\nLeve isso ao arquivista do Círculo, no Refúgio Noturno. O nome dele é Umbero, e o homem é brilhante. Desequilibrado, claro, mas brilhante assim mesmo! Ele já restaurou tomos em estado muito pior que o desse. Se for possível restaurar, ele pode fazê-lo.", progress = "Ora, olá! Mmm...\n\nDeixe-me ser sincero, amigo... você está com um cheiro meio passado. Catinguento, digamos. Não estou fazendo nenhuma avaliação particular de sua pessoa. Só estou dizendo que... no futuro pode ser melhor você se lavar um pouco antes de entrar quando vem do campo. Isso vai tornar as suas primeiras impressões muito mais positivas!\n\nDe qualquer forma... O que eu posso fazer por você?", completion = "Macacos sapos me mordam! O que é isso aí? Tem uma forma meio livresca, eu acho... Vou tentar abrir esse invólucro aqui.\n\nO Layo mandou você lá de Silithus? Só ouvi murmúrios agitados... que não acho que sejam tão discretos assim, parando para pensar... sobre os horrores que andam acontecendo por lá. Some-se isso à minha falta de apreciação por qualquer coisa entomológica e voilà: eis a receita da minha necessidade de ficar por aqui mesmo, com os livros.", rewards = "Você receberá:\n\n"},
[6845] = {title = "Desvendando segredos do passado", objective = "Fale com Ravine Saturna no vilarejo de Refúgio Noturno, na Clareira da Lua.", description = "Enfim, é bom você voltar a mim em breve... quer dizer, se você quiser saber o que tem aqui dentro. Assim que eu descobrir o que é, e se for mesmo um livro, ficarei feliz em compartilhar a informação com você.\n\nEnquanto isso, se eu fosse você, eu conferiria com a matriarca desse covil aqui, Ravine. Sem dúvida ele vai querer saber das maluquices que você andou fazendo. Nunca deixe a matriarca esperando, <name>... quer dizer, a menos que você queira dar um mergulho no mel.\n\nJá aconteceu, eu juro.", progress = nil, completion = "Ah, &lt;name&gt;. Eu esperava que você passasse por aqui. Fico feliz de ver que você está bem.\n\nSoube que você viria do campo e quis expressar pessoalmente a gratidão do Círculo Cenariano pela assistência que você nos deu até agora. Sem dúvida há mais segredos a revelar em Silithus. O que você entregou ao Umbero deve se provar muito interessante quando for compreendido. Por favor, aceite isso como símbolo de nossa contínua apreciação pela sua ajuda.\n\nMuito obrigado, generoso &lt;class&gt;.", rewards = "Você vai poder escolher uma dessas recompensas:\n\nAnel da Pedra VivaAnel de Cristal Faiscante\n\nVocê também receberá:\n\n"},
[6846] = {title = "Que comece o ataque!", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[6847] = {title = "Olho-que-tudo-vê do Mestre Rúben", objective = "Recupere o Olho-que-tudo-vê do Mestre Rúben nas cavernas dos Invernacha e leve-o para o Mestre Rúben.\n\nEsteja <avisado/avisada>, há rumores de que o Olho-que-tudo-vê do Mestre Rúben carrega uma terrível maldição!", description = "Sua guerra no coração de Alterac nada significa para a Camarilha, <class>. Nós faremos o que sempre fizemos: esperar que um vitorioso surja das cinzas e destroços de toda essa loucura. Só então atacaremos.\n\nNo entanto, não é pra isso que estamos aqui agora. O Olho-que-tudo-vê foi perdido – levado pelos trolls Invernacha da região.\n\nRecupere o Olho e nossos serviços estarão à sua disposição pelo tempo que o seu lado ainda tiver.\n\nFale novamente comigo quando recuperar o Olho, para que eu possa examinar você.\n\n<O Mestre Rúben sorri.>Completando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = nil},
[6848] = {title = "Olho-que-tudo-vê do Mestre Rúben", objective = "Recupere o Olho-que-tudo-vê do Mestre Rúben nas cavernas dos Invernacha e leve-o para o Mestre Rúben.\n\nEsteja <avisado/avisada>, há rumores de que o Olho-que-tudo-vê do Mestre Rúben carrega uma terrível maldição!", description = "Sua guerra no coração de Alterac nada significa para a Camarilha, <class>. Nós faremos o que sempre fizemos: esperar que um vitorioso surja das cinzas e destroços de toda essa loucura. Só então atacaremos.\n\nNo entanto, não é pra isso que estamos aqui agora. O Olho-que-tudo-vê foi perdido – levado pelos trolls Invernacha da região.\n\nRecupere o Olho e nossos serviços estarão à sua disposição pelo tempo que o seu lado ainda tiver.\n\nFale novamente comigo quando recuperar o Olho, para que eu possa examinar você.\n\n<O Mestre Rúben sorri.>Completando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = nil},
[6861] = {title = "Unidade retalhadora portátil do Borogodó", objective = "O Mestre Engenheiro Borogodó pediu o seguinte material:\n\n*30 Barras de Tório.\n\n*50 Barras de Mithril.\n\n*75 Barras de Ferro.\n\n*1 Serra a Vapor.", description = "Estou tão ansioso quanto você para testar minha unidade retalhadora portátil, <race>, mas para que eu possa preparar o kit, vou precisar de matéria-prima – muita matéria-prima!\n\nPara ser mais exato, vou precisar de uma MONTANHA de tório, mithril e ferro. Também quero uma serra a vapor para cada unidade. Essas serras você acha lá no acampamento madeireiro dos Lobo do Gelo.\n\nUma vez, quando eu estava bisbilhotando por ali (antes de ser capturado), notei que havia um montão de serras a vapor lá naquela serraria.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nUnidade Retalhadora Portátil do Borogodó"},
[6862] = {title = "Unidade retalhadora portátil do Borogodó", objective = "O Mestre Engenheiro Borogodó pediu o seguinte material:\n\n*30 Barras de Tório.\n\n*50 Barras de Mithril.\n\n*75 Barras de Ferro.\n\n*1 Serra a Vapor.", description = "Estou tão ansioso quanto você para testar minha unidade retalhadora portátil, <race>, mas para que eu possa preparar o kit, vou precisar de matéria-prima – muita matéria-prima!\n\nPara ser mais exato, vou precisar de uma MONTANHA de tório, mithril e ferro. Também quero uma serra a vapor para cada unidade. Essas serras você acha lá no acampamento madeireiro dos Lobo do Gelo.\n\nUma vez, quando eu estava bisbilhotando por ali (antes de ser capturado), notei que havia um montão de serras a vapor lá naquela serraria.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nUnidade Retalhadora Portátil do Borogodó"},
[6881] = {title = "Ivus, o Senhor da Floresta", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
