[7506] = {title = "O Sonho Esmeralda...", objective = "Devolva o livro aos legítimos donos.", description = "O livro está marcado com o selo do Ateneu.O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\nSelo Real de Eldre'Thalas"},
[7507] = {title = "O compêndio de Foror", objective = "Leve o Compêndio de Foror sobre Matar Dragões para o Ateneu.", description = "Você descobriu algo de grande importância. Este livro é diferente de tudo o que você viu.\n\nParece que alguém ou alguma coisa conseguiu romper o selo do Ateneu. O texto é confuso e pouco legível.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7508] = {title = "Forjando a Quel'Serrar", objective = "Dê a Lâmina Élfica Lisa e Cega ao Erudito Lydros.", description = "Se você quiser aceitar essa tarefa, entregue a lâmina cega a Lydros.", progress = nil, completion = nil, rewards = nil},
[7509] = {title = "Forjando a Quel'Serrar", objective = "Faça Onyxia soprar fogo na Lâmina Ancestral Virgem. Quando conseguir, pegue a agora Lâmina Ancestral Incandescente. Atenção, uma Lâmina Ancestral Incandescente não permanecerá assim para sempre — o tempo urge.\n\nO último passo antes de voltar para mim é matar a fera e enfiar a Lâmina Ancestral Incandescente no cadáver dela.\n\nFaça isso e Quel'Serrar será sua.", description = "Leve a lâmina ancestral virgem ao covil de Onyxia e lute com a dragonesa.\n\nUtilize alguns truques para aquecer a lâmina. Enquanto estiver em combate, Onyxia certamente tentará incinerar você soprando fogo. Você deve fincar a lâmina ancestral virgem por perto e esperar que o bafo fumegante aqueça a lâmina. Se você sobreviver, pegue a lâmina ancestral agora aquecida e acabe com a dragonesa.\n\nEnfie a lâmina ancestral incandescente no cadáver fumegante da oponente para moldar a lâmina ancestral temperada.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nQuel'SerrarLâmina Ancestral Incandescente"},
[7521] = {title = "Thunderaan the Windseeker", objective = "To free Thunderaan the Windseeker from his prison, you must present the right and left halves of the Bindings of the Wind Seeker, 10 bars of Elementium, and the Essence of the Firelord to Highlord Demitrian.", description = "I have told you all that I know, <name>. It is you who must find elementium. It is you who must destroy the Firelord's corporeal form.\n\nDo this and gain the blessing of Thunderaan.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7522] = {title = "Examine the Vessel", objective = "Examine the Vessel of Rebirth and return it to Highlord Demitrian.", description = "Examine the vessel of his rebirth, mortal. It is from this that the Windseeker shall be reborn!", progress = nil, completion = nil, rewards = nil},
[7541] = {title = "Serviço para a Horda", objective = "Levará tempo para nossos estudiosos estudarem o pingente que você nos trouxe. Mas gostaria de lhe oferecer algo como um pequeno sinal de agradecimento por seu serviço à Horda.\n\nObrigado, <name>. Seus atos trazem honra a você e a nós.", description = nil, progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nElo da LealdadeCírculo do Solárbol\n\nVocê também receberá:\n\n"},
[7561] = {title = "Rise, Thunderfury!", objective = "Present the Dormant Wind Kissed Blade to Highlord Demitrian.", description = "You have defeated the Wind Seeker. Take the dormant blade and force the lying wretch, Demitrian, to use the materials you provided for his betrayal to wake Thunderfury.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nThunderfury, Blessed Blade of the Windseeker DEPRECATED"},
[7562] = {title = "Mor'zul Trazangre", objective = "Fale com Mor'zul Trazangre nas Estepes Ardentes.", description = "<name>, suas incursões pelas artes das trevas são profundas. Seu domínio sobre habitantes sobrenaturais aumentou muito... talvez você esteja pronto para vincular um dos terríveis corcéis medonhos.\n\nO ritual para tirar a fera de casa exige grande poder e recursos, mas se você deseja dominar essa montaria épica, então fale com Mor'zul Trazangre. Ele está acampado perto do Altar das Tempestades nas Estepes Ardentes.\n\nVá, <name>. Essa missão será perigosa, mas <um/uma> Brux<o/a> como você não evita o perigo!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7563] = {title = "Raiva de Sangue", objective = "Leve 30 garrafas de Sangue de Fera em Fúria para Mor'zul Trazangre nas Estepes Ardentes.", description = "Os corcéis medonhos habitam outro mundo, as ardentes planícies de Zoroth, e para vincular uma dessas famosas criaturas, você deve evocar poderosos glifos. Posso fazer os glifos em um pergaminho para você, mas primeiro precisarei de uma tinta especial.\n\nNa distante Hibérnia existe uma raça de ferujas. Outrora criaturas puras, elas foram enlouquecidas pelas emanações demoníacas da Garganta do Sussurro Sombrio e agora vagam em um frenesi pelas neves.\n\nPreciso do sangue delas para pintar seu pergaminho. Traga para mim.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7564] = {title = "Olholouco", objective = "Leve o Caixote de Sangue para Gorzeeki Olholouco nas Estepes Ardentes.", description = "Meu servo, Gorzeeki, é um alquimista com um certo talento. Ele pode preparar a tinta de que precisarei para seu pergaminho. Juntei o sangue que você trouxe. Leve-o para Gorzeeki para ele começar a trabalhar.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7581] = {title = "Montando a prisão", objective = "Vá até o Gládio Cruel, em Feralas, e obtenha 15 amostras de Sangue de Sátiro dos Sátiros Criasselvagem que habitam o Distrito Lenhatorta. Fale novamente com Daio no Rasgo Infecto quando tiver feito isso.", description = "Você deve conseguir com os demônios inferiores o que precisa para unir as partes da prisão do Demonarca.\n\nO sangue do Sátiro Criasselvagem que ocupa o Gládio Cruel deve servir. O sangue coagulado vai unir os fragmentos dos Hederine e energizar a prisão de cristal.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7582] = {title = "O revestimento da prisão", objective = "Vá à Garganta do Sussurro Sombrio em Hibérnia e recupere 5 Lágrimas dos Hederine dos demônios Hederine que ocupam o lugar. Fale novamente com Daio no Rasgo Infecto quando você tiver completado esta tarefa.", description = "Os demônios Hederine que ocupam o contraforte do Monte Hyjal secretam poderosos venenos, que, se deixados para amadurecer, endurecem, transformando-se em um denso cristal antimagia. Esses cristais ficaram conhecidos como Lágrimas dos Hederine.\n\nNem mesmo o imenso poder de um Demonarca poderia quebrar as paredes de uma prisão construída com esse material.\n\nVá até a Garganta do Sussurro Sombrio em Hibérnia e recupere as Lágrimas.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7583] = {title = "Supressão", objective = "Aventure-se no Rasgo Infecto e encontre um Comandante Demonarca.\n\nUse a Prisão de Cristal Faiscante no Comandante Demonarca. Prepare-se para uma sequência violenta de ataques, enquanto o demônio tenta escapar de ser preso.\n\nCaso você tenha sucesso, leve o Demonarca Aprisionado para Daio no Rasgo Infecto.", description = "A prisão está pronta. Agora, a parte mais difícil desta tarefa.\n\nVocê deve encontrar e capturar um Comandante Demonarca no Rasgo Infecto, usando esta prisão de cristal.\n\nCuidado! Um Demonarca é um adversário astuto e letal e tentará destruí-lo quando você iniciar o ritual de encarceramento. Ele se debaterá descontroladamente para tentar escapar de ser preso.\n\nEle se concentrará totalmente em você, <name>. Por isso, recomendo que leve companheiros que possam ajudar você a se manter <vivo/viva>!Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nRitual de Fatalidade\n\nO seguinte feitiço será lançado em você:\n\nRitual de Fatalidade\n\nVocê também receberá:\n\nTomo do Sacrifício"},
[7601] = {title = "O que Niby ordena", objective = "Fale com Diabretim na Selva Maleva.", description = "Se quiser que eu lhe ensine o segredo da evocação de um serviçal infernal, você terá de destruir Kroshius e retirar o núcleo infernal de seus restos incandescentes. Não será tão fácil quanto parece, pois Kroshius já se exauriu quase por completo. Você terá de reacender o corpo dele, reerguendo-o dos escombros. Para isso, no entanto, será preciso reunir alguns componentes de outras entidades demoníacas.\n\nMeu Diabretim lhe indicará o caminho.", progress = nil, completion = nil, rewards = nil},
[7602] = {title = "Essência Vil Impecável", objective = "Leve três Essências Vis Impecáveis, originárias de três locais distintos, para Diabretim na Selva Maleva.\n\nOs Sátiros Legashi de Azshara possuem a Essência Vil Impecável da região deles, assim como os Legionários de Jaedenar e as Sentinelas Guardas Vis da Barreira do Inferno.\n\nBusque as Essências Vis Impecáveis e leve-as para Diabretim, na Selva Maleva.", description = "Se você pretende reanimar Kroshius, vai precisar de fogo Vil. Posso fazer fogo Vil, mas vou precisar de alguns componentes.\n\nMe traga essências vis impecáveis de três regiões distintas do mundo.\n\nOs sátiros Legashi, de Azshara, têm o primeiro tipo de essência, os legionários de Jaedenar, o segundo, e as sentinelas guardas vis da Barreira do Inferno, o terceiro.\n\nTraga-me uma essência de cada tipo e criarei o fogo Vil. Então você poderá reanimar Kroshius.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7603] = {title = "Núcleo Infernal de Kroshius", objective = "Procure os restos mortais de Kroshius no Vale das Escaras Pungentes, que fica na Selva Maleva. Ao encontrá-los, use o Fogo Vil perto deles e aguarde o retorno de Kroshius. Quando o infernal acordar, mate-o e retire do corpo dele o Núcleo Infernal de Kroshius.\n\nLeve o Núcleo para Niby, o Todo-poderoso na Selva Maleva.", description = "Kroshius jaz a nordeste, no Vale das Escaras Pungentes. Pegue este fogo Vil e use-o nos restos mortais dele. O fogo deve reanimar o infernal caído.\n\nA esta altura, creio que ele vá matar você e qualquer amigo que possa levar junto... e acredite em mim, você vai querer levar alguns amigos.\n\nSe por algum milagre você conseguir matar Kroshius, pegue o núcleo infernal dele e leve para Niby.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nInferno\n\nO seguinte feitiço será lançado em você:\n\nInferno\n\nVocê também receberá:\n\nEstilhaço da Chama Verde"},
[7604] = {title = "Contrato vinculante", objective = "Entregue o Contrato da Irmandade do Tório para Lokhtos Umbrarganha, caso tenha interesse em receber as instruções para Sulfuron.", description = "O signatário declara por meio deste, que o lingote de Sulfuron aqui entregue para a Irmandade do Tório (IT Ltda) é genuíno e não um fac-símile do mesmo. O signatário também declara estar ciente de que todo e qualquer dano que venha porventura a sofrer devido à tentativa de produzir, forjar, encontrar, criar, vender ou morder Sulfuras não é de responsabilidade nem culpa da IT Ltda.\n\nAssim, por estarem justas e acordadas na forma acima, ambas as partes firmam este contrato.\n\n_________________\n\nAssinaturaVocê receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nInstruções: Martelo de Sulfuron"},
[7621] = {title = "Um aviso", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[7622] = {title = "Equilíbrio entre luz e sombra", objective = "Salve 50 Camponeses antes que 15 sejam mortos. Fale com Eris Abrigoardente se conseguir completar a tarefa.\n\nVocê pode conferir o Poste da Morte para saber quantos Camponeses foram mortos.", description = "Você deve fazer o que eu não consegui: salvar os plebeus que foram mortos enquanto fugiam de Stratholme.\n\nEles irão em direção à luz e você precisa garantir que sobrevivam. Se muitos deles tombarem, nosso suplício continuará e você terá fracassado.\n\nTodas as habilidades, orações e feitiços que você aprendeu serão colocados à prova. Que a Luz esteja consigo, <name>.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nLasca de Nordrassil"},
[7623] = {title = "Lorde Nihilus", objective = "Compre Poções Sombrias de Gorzeeki nas Estepes Ardentes.\n\nUse as poções para viajar através de Jaedenar e fale com Lorde Nihilus.", description = "Mor'zul disse que você quer abrir um portal para Zoroth. Para tanto, você precisa de poeira estelar zorothiana, e isso não é fácil de encontrar em Azeroth!\n\nExiste um ser que talvez tenha um pouco: Lorde Nihilus, de Jaedenar, a sede do Concílio das Sombras! Fale com ele para convencê-lo a lhe dar a poeira estelar.\n\nMas se resolver ir a Jaedenar, você vai precisar se disfarçar de acordo com a imagem maligna do Concílio das Sombras, o que vai demandar as minhas poções sombrias — por um preço...Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7624] = {title = "Ulathek, o traidor", objective = "Confronte Ulathek, depois leve o Coração do Traidor para o Lorde Nihilus em Jaedenar.", description = "Você busca a minha graça? Então mate por mim.\n\nMeu servo, Ulathek... é patético como ele se humilha diante de mim enquanto trama com o Lorde Hel'nurath de Zoroth. Hel'nurath planeja usurpar o meu poder aqui, e Ulathek é espião dele...\n\nProve que você serve a mim, e não aos meus rivais. Confronte Ulathek em seus aposentos na Fortaleza do Concílio das Sombras, a oeste. Diga a ele que eu sei de seu segredo e observe o medo tomar conta dele. Traga-me o coração do traidor depois disso!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7625] = {title = "Poeira Estelar Zorothiana", objective = "Compre Poeira Estelar Zorothiana de Ur'dan. Leve-a para Gorzeeki Olholouco nas Estepes Ardentes.", description = "Então, você veio me procurar em busca da poeira estelar zorothiana? Você quer abrir um portal para Zoroth, o reino do meu rival Hel'nurath? Por acaso, você planeja roubar um de seus preciosos corcéis medonhos?\n\nMuito bem. Meu servo Ur'dan tem um estoque de poeira estelar zorothiana. Pode pegar com ele... se puder pagar.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7626] = {title = "Sino de Defimura", objective = "Leve 10 Elixires de Poder Sombrio para Gorzeeki Olholouco nas Estepes Ardentes.", description = "Um Sino de Defimura, que recebeu esse nome em homenagem à falecida bruxa Defimura Vistreva, é uma das três ferramentas necessárias para criar um círculo de invocação maior. Quando devidamente sintonizado com a roda e a vela, esses itens conduzem vastas reservas de energia arcana.\n\nMeu servo Gorzeeki pode construir esse sino, mas, para isso, ele vai precisar de um grande suprimento de elixires de poder sombrio. Procure os elixires com um alquimista e leve-os para Gorzeeki.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7627] = {title = "Roda da Marcha Negra", objective = "Leve 6 Estilhaços Brilhantes Grandes e 25 Minérios de Ferro Negro para Gorzeeki nas Estepes Ardentes.", description = "A Roda da Marcha Negra contém e canaliza as energias violentas necessárias para o círculo de invocação maior. Para construir uma roda dessas, Gorzeeki vai precisar de estilhaços brilhantes grandes de um encantador e minério de ferro negro de um mestre minerador.\n\nObtenha tudo isso e leve para o meu servo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7628] = {title = "Vela do Juízo Final", objective = "Leve 35 Escamas de Dragão Negro para Gorzeeki Olholouco nas Estepes Ardentes.", description = "Uma Vela do Juízo Final armazena fogo e o direciona para aqueles que tentem interferir no seu ritual. Ao criar o círculo de invocação maior, se a sua vela estiver acesa, qualquer demônio impertinente será queimado por sua chama!\n\nConter essa chama não é fácil e exige a essência encontrada nas escamas de um dragão negro. Consiga essas escamas com um esfolador e leve-as para Gorzeeki Olholouco.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7629] = {title = "Entrega de diabrete", objective = "Leve o Diabrete numa Jarra para o laboratório de alquimia em Scolomântia. Depois que o pergaminho for criado, devolva a jarra para Gorzeeki Olholouco.", description = "Há mais um procedimento necessário para o pergaminho de Mor'zul: eu preciso de papel infundido com poeira estelar zorothiana. E... infelizmente, não posso fazer isso aqui, pois não tenho o equipamento necessário.\n\nMas há um laboratório de alquimia que tem tudo o que eu preciso. Ele fica em Scolomântia, na ilha de Castro das Flechas, nas Terras Pestilentas.\n\nEu não posso ir até lá, mas tome esta jarra — tem um diabrete dentro dela que é muito esperto. Solte-o no laboratório de alquimia para ele infundir o pergaminho.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7630] = {title = "Arcanita", objective = "Leve 3 Barras de Arcanita para Gorzeeki nas Estepes Ardentes.", description = "Agora que o sino, a roda e a vela estão prontos, tenho que fazer uma magnetita preta. Durante o seu ritual, você vai precisar dessa magnetita para não perder os outros objetos ritualísticos se eles falharem.\n\nPara fazer a magnetita, eu preciso de arcanita. Você tem algum pedaço aí? Se não tiver... então você terá que conseguir arcanita com um alquimista.\n\nFale novamente comigo quando tiver a arcanita e eu lhe farei a magnetita preta.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7631] = {title = "Corcel Medonho de Zoroth", objective = "Leia as Instruções de Mor'zul. Evoque um Corcel Medonho Zorothiano, derrote-o e então vincule o espírito dele a você.\n\nItem fornecido:\n", description = "Todos os preparativos foram feitos. Agora, você precisa realizar o ritual para criar um círculo de invocação maior. Depois, use o pergaminho infundido em poeira estelar zorothiana... para abrir um portal para Zoroth.\n\nEu escrevi as instruções para o ritual em um pergaminho. Estude-o com cuidado, pois você precisa executar cada passo com precisão para dar certo.\n\nBoa sorte, <name>. E que as almas de seus inimigos dirijam-se rapidamente para a sua bolsa de estilhaços.Você irá aprender:\n\n", progress = nil, completion = nil, rewards = "Você irá aprender:\n\nEvocar Corcel Medonho\n\nO seguinte feitiço será lançado em você:\n\nEnsinar Evocar Corcel Medonho\n\nVocê também receberá:\n\n"},
[7632] = {title = "A folha antiga", objective = "Encontre o dono da Folha Antiga Petrificada. Boa sorte, <name>, pois o mundo é grande.", description = "A epiderme da folha emana um brilho verde. A superfície é extremamente rugosa, expondo várias veias e rugas.\n\nSe você tivesse que adivinhar, diria que esta folha veio de algo antigo e poderoso.\n\nTalvez você devesse procurar o dono original da folha.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7633] = {title = "Uma apresentação", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[7634] = {title = "Lâmina Embrulhada de Tendão Antigo", objective = "Hastat, o Anciente, lhe pediu um Tendão de Dragão Azul Maduro. Se você encontrar esse tendão, vá falar com Hastat na Selva Maleva.", description = "Saudações, <class>. Eu sou Hastat, Anciente da Tradição. Eu carrego o conhecimento de várias eras.\n\nCom a vinculação adequada, uma de minhas folhas pode servir como aljava, do tipo que não se vê há mais de dez mil anos. Uma aljava que, quando acompanhada de um arco dos Antigos, pode conceder poderes inenarráveis.\n\nVolte aqui com o tendão de um dragão azul adulto, e esta aljava será sua.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nLâmina Embrulhada de Tendão Antigo"},
[7635] = {title = "A melhor corda", objective = "Stoma, o Anciente, lhe pediu um Tendão de Dragão Negro Maduro. Se você encontrar esse tendão, vá falar com Stoma na Selva Maleva.", description = "Vartrus pode lhe oferecer o báculo para criar um arco dos Antigos, mas somente eu posso encantar a corda necessária para completar o arco.\n\nÉ uma tarefa simples, jovem: mate um dragão negro maduro e remova o tendão dos ossos dele. Traga-me esse tendão e encantarei o material para transformá-lo em uma corda de arco quase indestrutível.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nTendão de Dragão Negro Encantado"},
[7636] = {title = "Báculo dos Antigos", objective = "Você precisa encontrar e destruir aqueles quatro corruptores demoníacos:\n\nSimone, a Sedutora.\n\nKlinfran, o Enlouquecido.\n\nSolenor, o Matador.\n\nArtórius, o Destruidor.\n\nDestrua aquelas criaturas e fale novamente com Vartrus, o Anciente, na Selva Maleva, trazendo as cabeças deles.\n\nConsulte a Casca Petrificada no seu inventário para ter pistas da localização deles.\n\nVocê TEM QUE completar a tarefa sozinh<o/a>.", description = "Agentes da Legião Ardente ainda estão ativos em nosso mundo, <class>. Corrompem tudo o que tocam, e a natureza definha e chora na presença deles.\n\nVocê precisa rastrear aqueles corruptores demoníacos e destruí-los, <name>. Um aviso: você precisa iniciar o ataque sozinh<o/a>, ou eles fugirão. Enquanto você estiver sozinh<o/a>, eles vão tentar destruí-<lo/la>. Você deve atacá-los, transformando sua arrogância em desgraça.\n\nEstou lhe dando um pedaço da minha casca. As inscrições devem levá-<lo/la> ao seus alvos. Traga as cabeças deles.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nBáculo Antigo Cinzelado com Runas"},
[7637] = {title = "Ênfase em sacrifício", objective = "Viaje para Altaforja e obtenha o Incensório de Exorcismo do Sumo Sacerdote Rohan. Você precisará fazer uma doação de 150 moedas de ouro para obtê-lo.", description = "Para se preparar para esta tarefa, você precisa demonstrar o devido sacrifício e julgamento... com ênfase em sacrifício.\n\nVocê deve obter o incensório de exorcismo do Sumo Sacerdote Rohan para sua tarefa. Mesmo que a intenção seja usá-lo para fins bons e justos, deve haver uma compensação por ele.\n\nVá para Altaforja e fale com o Sumo Sacerdote. Você demonstrará estar dispost<o/a> a fazer sacrifícios se levar uma boa soma de dinheiro para compensar a perda do incensório.\n\nAgora vá, <name>, o tempo urge.Completando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = nil},
[7638] = {title = "Lorde Grayson Quebrassombra", objective = "Fale com o Lorde Grayson Quebrassombra no Distrito da Catedral de Ventobravo.", description = "Chegou a hora, <name>, de lhe ensinar o que será necessário para que você possa adquirir uma coisa que, sem dúvida, você espera há algum tempo: seu corcel.\n\nFale com Lorde Grayson Quebrassombra, aqui no Distrito da Catedral de Ventobravo. É ele quem <o/a> instruirá não só sobre a destreza da montaria mais leal de um paladino, mas também sobre as tarefas que você deverá cumprir para obter o seu próprio corcel.\n\nDesejo-lhe sorte nesta provação. Pela Luz, nobre <irmão/irmã>!", progress = nil, completion = "Que satisfação ver você aqui, &lt;name&gt;. Eu sei que você estava aguardando instruções sobre como obter seu corcel, e essa hora chegou.\n\nVocê passará por diversas provas, mas a mais importante delas será para testar sua força de vontade para superar diversos obstáculos à sua frente. Essas provações não são meros exercícios de aprendizagem — sua estatura está além de testes didáticos. Seu futuro companheiro precisa ser salvo das garras do mal que você passou a vida inteira combatendo.\n\nAtente-se, pois sua hora chegou!", rewards = "Você receberá:\n\n"},
[7639] = {title = "O devido julgamento", objective = "Leve o Incensório de Exorcismo para Lorde Grayson Quebrassombra no Distrito da Catedral de Ventobravo.", description = "Leve este incensório com a minha bênção. Eu mesmo já o usei em diversos exorcismos, e ele deverá lhe servir bem nas tarefas árduas que você tem pela frente.\n\nProcure novamente Lorde Grayson Quebrassombra em Ventobravo, <name>. Sua busca continua com ele.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7640] = {title = "Exorcizando a Várzea do Medo", objective = "Use o Incensório de Exorcismo para expulsar os espíritos que atormentam a Várzea do Medo. Quando você tiver eliminado 25 Espíritos da Várzea do Medo, fale novamente com Lorde Grayson Quebrassombra no Distrito da Catedral de Ventobravo.", description = "Eu adicionei uma pitada de terra do solo maculado da Várzea do Medo, nas Terras Pestilentas Orientais, ao incensório. Agora você pode usá-lo para encontrar áreas que exibam sinais de agitação e perturbação espiritual por lá. Procure as auras verdes que permeiam o solo, onde o mundo físico cede sob o tormento do mundo espiritual, e use o incensório para expulsar os espíritos malignos.\n\nQuando tiver julgado espíritos o suficiente, você estará pront<o/a> para prosseguir.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7641] = {title = "O trabalho de Grimand Elmore", objective = "Fale com Grimand Elmore em Ventobravo no Distrito dos Anões de Ventobravo.", description = "O corcel de um paladino não passa de uma mera besta de carga sem a barda correta. A barda usada pelo seu corcel não só precisa ser feita com os melhores materiais possíveis, mas, por causa das circunstâncias envolvidas, também deve estar suficientemente harmonizada com o mundo espiritual.\n\nMas vamos começar pelo começo, <name>: vamos produzir a sua barda. No Distrito dos Anões de Ventobravo, há um dos melhores artífices vivos, Grimand Elmore. Seu trabalho não custará barato, mas qualidade é o que buscamos aqui. Fale com ele.", progress = nil, completion = "Buenas e me espalho, guri — o Lorde Quebrassombra me mandou uma mensagem antecipando a tua vinda pr'essas paragens pra me ver. Eu quero fazer a barda que tu precisas, mas não vai ser barato nem fácil; vamos deixar isso claro logo de cara.\n\nVou te dizer, e o Grayson não me deixa mentir: tu não vai encontrar em toda Azeroth barda melhor do que a que eu faço aqui.", rewards = "Você receberá:\n\n"},
[7642] = {title = "Coleta de produtos", objective = "Leve 40 Runatramas, 6 Barras de Arcanita, 10 Lágrimas-de-arthas, 5 ampolas de Água Benta de Stratholme e 150 moedas de ouro para Grimand Elmore, no Distrito dos Anões de Ventobravo.", description = "Tu vais precisar de quatro coisas pra barda: runatrama pro forro, barras de arcanita pras placas, lágrimas-de-arthas pra harmonização espiritual e água benta de Stratholme para imbuir tudo. Na verdade, são cinco coisas... tem também o ouro pra pagar a minha taxa. Essa taxa inclui toda a mão de obra, além da sela e dos estribos artesanais.\n\nAssim que conseguir tudo isso, vou poder terminar a tua barda. Até lá, tem algumas coisas que eu posso ir adiantando pra ti enquanto tu reúne os materiais todos.Completando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = nil},
[7643] = {title = "O Espírito Equino Antigo", objective = "Obtenha uma ração de cavalo especial para alimentar o cavalo espiritual. Aparentemente, Judite Castanheira, que vive na Costa Sul, é a fornecedora da tal ração.\n\nEntre na masmorra de Gládio Cruel, em Feralas, e mate Gavíneo Lenhatorta. Isso libertará o Espírito Equino Antigo. Alimente-o com a ração de cavalo especial, o que acalmará o espírito. Finalmente, dê-lhe a Barda de Arcanita para que ele a abençoe.", description = "Sua barda precisa de uma forma especial de bênção, a de um espírito apropriado. Existe um espírito assim em Gládio Cruel: o Espírito Equino Antigo. No momento, um arvoroso corrompido, Gavíneo Lenhatorta, o está mantendo preso. Julgue esse monstro maligno, <name>.\n\nLembre-se de uma coisa: não basta libertar o espírito, você precisa acalmá-lo para receber a bênção. Para isso, você vai precisar de uma ração de cavalo especial. Vá para a Costa Sul primeiro e fale com Judite Castanheira sobre o assunto... e pode se preparar que lá vem mais sacrifício.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7644] = {title = "Barda de Arcanita Abençoada", objective = "Leve a Barda de Arcanita Abençoada para Lorde Grayson Quebrassombra no Distrito da Catedral de Ventobravo.", description = "Ao repousar sobre o lombo do Espírito Equino Antigo, a barda brilha suavemente. O ar se adensa com o poder por um breve instante, e então a barda desaparece das costas do cavalo e torna a aparecer dentro da sua mochila.\n\nAparentemente, a barda foi abençoada. Agora, segundo as instruções do Lorde Quebrassombra, você deve voltar a Ventobravo e mostrar a barda a ele.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7645] = {title = "Ração de Cavalo Enriquecida com Mana", objective = "Obtenha 20 Biscoitos de Maná Enriquecido — o ingrediente chave para fazer Ração de Cavalo Enriquecida com Mana — para Judite Castanheira da Costa Sul, no Contraforte de Eira dos Montes. A Aurora Argêntea é conhecida como a única fornecedora dos biscoitos.\n\nVocê também precisa dar a ela 50 moedas de ouro para a apaziguar os ânimos dela.", description = "O Punho de Prata achou que eu tinha ficado LOUCA quando eu disse a eles que poderia fazer uma ração de cavalo especial que poderia alimentar cavalos espirituais. TODO MUNDO achou que eu estava louca! Bom, chegou a hora de dar o troco!\n\nFarei o que você precisa, PALADIN<O/A>... $Gsuposto CAMPEÃO:suposta CAMPEÃ;... mas terá um custo. Preciso de biscoitos especiais — biscoitos de maná enriquecidos, que só a Aurora Argêntea vende — para esta mistura. Eu também, exijo — sim, EXIJO! — ouro como compensação pelo sofrimento que passei!\n\nMostre-me o sacrifício de que tanto você se gaba, PALADIN<O/A>.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nRação de Cavalo Enriquecida com Maná"},
[7646] = {title = "O Cristal Divinatório", objective = "Leve um Diamante Azerotiano e um Diamante Preto Límpido para Lorde Grayson Quebrassombra no Distrito da Catedral de Ventobravo.", description = "Agora devemos montar um Cristal Divinatório — isso vai dar início ao teste que lhe permitirá redimir o corcel caído. Seu cristal, na verdade, está quase pronto. O incensório de exorcismo usado na Várzea do Medo é o catalisador para ele, e eu vou fazer um compartimento apropriado para o dispositivo.\n\nO último componente necessário, no entanto, é um par de diamantes. Eles devem ser equivalentes tanto em fulgor quanto em propósito — um azerotiano e um preto límpido. Os dois serão o farol com o qual o cristal trespassará as sombras!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7647] = {title = "Julgamento e redenção", objective = "Use o Cristal Divinatório no coração do porão do Grande Ossário, em Scolomântia. Isso invocará espíritos que você precisará julgar. A derrota dos espíritos evocará o Cavaleiro da Morte Noctilatrus. Derrote-o e reclame a alma perdida do corcel caído.\n\nEntregue a Alma Redimida do Corcel e a Barda Encantada Abençoada ao Corcel Caído de Noctilatrus.", description = "Pegue esta algibeira. Dentro dela, há os itens necessários para a sua tarefa.\n\nUse o cristal divinatório no coração do porão do Grande Ossário para atrair espíritos amaldiçoados, que devem ser julgados. Faça bom uso dos seus julgamentos nessa provação: eles se provarão mais potentes do que você imagina. Depois dos espíritos, o cavaleiro da morte aparecerá. Derrote-o e reclame a alma perdida do corcel. Somente então, <name>, você poderá redimi-lo e colocar sua barda sobre a nova montaria!\n\nBoa sorte!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nEvocar Corcel\n\nVocê receberá:\n\nAlma Perdida do CorcelCristal Divinatório"},
[7648] = {title = "O melhor trabalho de Grimand", objective = "Leve a Barda de Arcanita para Lorde Grayson Quebrassombra no Distrito da Catedral de Ventobravo para que ele a inspecione.", description = "<Grimand enxuga o suor da testa, sorrindo de orgulho.>\n\nAí está você, <meu amigo/minha amiga>. Este é um dos meus melhores trabalhos — tão bom quanto a barda que eu fiz para o Lorde Quebrassombra quando ele foi buscar um corcel. Eu lhe agradeço por confiar na qualidade do meu trabalho.\n\nSem dúvida, Grayson também vai querer ver essa obra com os próprios olhos. Leve sua barda para ele e diga-lhe que eu agradeci pelo honorável negócio.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
