[7655] = {title = "Braçadeira de Placa Imperial", objective = "Vou precisar de 20 barras de tório pro projeto da braçadeira.\n\nTá tudo bem contigo, piá? Tu tá com a cara vermelha.", description = nil, progress = "Vou precisar de 20 barras de tório pro projeto da braçadeira.\n\nTá tudo bem contigo, piá? Tu tá com a cara vermelha.", completion = "Foi um prazer fazer negócio contigo.\n\nE, tira essa olhar de nojo da cara, &lt;name&gt;.", rewards = "Você receberá:\n\nInstruções: Braçadeiras de Placa Imperial"},
