[7805] = {title = "Doação de runatrama", objective = "<name>, você tem sido um grande doador em nossa campanha de arrecadação de tecidos. Mas, mesmo trabalhando duro para reabastecer os estoques, ainda há um tecido em falta: runatrama. Precisamos de runatrama com urgência e esperamos que você possa nos ajudar, como já ajudou no passado!\n\nCaso queira ajudar, traga-me todos os cortes de runatrama que puder doar. Nós pedimos uma doação inicial de sessenta cortes. A partir dessa quantidade, aceitamos outras doações.", description = nil, progress = "<name>, você tem sido um grande doador em nossa campanha de arrecadação de tecidos. Mas, mesmo trabalhando duro para reabastecer os estoques, ainda há um tecido em falta: runatrama. Precisamos de runatrama com urgência e esperamos que você possa nos ajudar, como já ajudou no passado!\n\nCaso queira ajudar, traga-me todos os cortes de runatrama que puder doar. Nós pedimos uma doação inicial de sessenta cortes. A partir dessa quantidade, aceitamos outras doações.", completion = "Que ótimo! Esse tecido vai ser usado imediatamente, eu garanto a você. Muito obrigada mais uma vez pela ajuda! O que seria de nós sem você?\n\nJá que contribuiu tanto para a nossa campanha, não hesitarei em aceitar todas as doações de runatrama que você fizer no futuro. Só não se esqueça de falar comigo para que eu possa me certificar pessoalmente de que as suas ações recebam o devido reconhecimento!", rewards = "Você receberá:\n\n"},
[7806] = {title = "Runatrama nunca é demais", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[7807] = {title = "Doação de lã", objective = "Estamos aceitando doações de tecidos de lã. Os Exilados de Gnomeregan lhe serão muito gratos se você generosamente doar sessenta cortes de lã. Já conseguimos arrecadar alguma coisa, por isso só precisamos que você doe essa quantidade. O que faltar nós conseguiremos com outros doadores por todo o reino que também apoiam nossa campanha.\n\nCaso você tenha sessenta cortes de lã e queira doá-los, eu posso recebê-los agora.", description = nil, progress = "Estamos aceitando doações de tecidos de lã. Os Exilados de Gnomeregan lhe serão muito gratos se você generosamente doar sessenta cortes de lã. Já conseguimos arrecadar alguma coisa, por isso só precisamos que você doe essa quantidade. O que faltar nós conseguiremos com outros doadores por todo o reino que também apoiam nossa campanha.\n\nCaso você tenha sessenta cortes de lã e queira doá-los, eu posso recebê-los agora.", completion = "Que maravilha! Agradecemos a generosa doação, &lt;name&gt;!", rewards = "Você receberá:\n\n"},
[7808] = {title = "Doação de seda", objective = "Nosso estoque de seda está em baixa, aliás, como o de quase todos os tecidos. Com a quantidade que temos hoje, precisaríamos receber apenas sessenta peças de você. Com a sua doação e a de outras pessoas, conseguiremos alcançar nossa meta.\n\nLembre-se que um presente generoso como esse certamente aumentará o seu prestígio com a comunidade! Caso você tenha sessenta cortes de seda e queira doá-los, eu posso recebê-los agora.", description = nil, progress = "Nosso estoque de seda está em baixa, aliás, como o de quase todos os tecidos. Com a quantidade que temos hoje, precisaríamos receber apenas sessenta peças de você. Com a sua doação e a de outras pessoas, conseguiremos alcançar nossa meta.\n\nLembre-se que um presente generoso como esse certamente aumentará o seu prestígio com a comunidade! Caso você tenha sessenta cortes de seda e queira doá-los, eu posso recebê-los agora.", completion = "Que maravilha! Agradecemos a generosa doação, &lt;name&gt;!", rewards = "Você receberá:\n\n"},
[7809] = {title = "Doação de magitrama", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7810] = {title = "Mestre da Arena", objective = "Fale com João Tampinha Mithril na Arena da Selva do Espinhaço.\n\nItem fornecido:\n", description = "Um mestre de arena deve precisar fazer mais do que isso... Talvez o pirata que coordena os eventos em Gurubashi saiba!Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[7811] = {title = "Doação de runatrama", objective = "<name>, você tem sido um grande doador em nossa campanha de arrecadação de tecidos. Mas, mesmo trabalhando duro para reabastecer os estoques, ainda há um tecido em falta: runatrama. Precisamos de runatrama com urgência e esperamos que você possa nos ajudar, como já ajudou no passado!\n\nCaso queira ajudar, traga-me todos os cortes de runatrama que puder doar. Nós pedimos uma doação inicial de sessenta cortes. A partir dessa quantidade, aceitamos outras doações.", description = nil, progress = "<name>, você tem sido um grande doador em nossa campanha de arrecadação de tecidos. Mas, mesmo trabalhando duro para reabastecer os estoques, ainda há um tecido em falta: runatrama. Precisamos de runatrama com urgência e esperamos que você possa nos ajudar, como já ajudou no passado!\n\nCaso queira ajudar, traga-me todos os cortes de runatrama que puder doar. Nós pedimos uma doação inicial de sessenta cortes. A partir dessa quantidade, aceitamos outras doações.", completion = "Que ótimo! Esse tecido vai ser usado imediatamente, eu garanto a você. Muito obrigada mais uma vez pela ajuda! O que seria de nós sem você?\n\nJá que contribuiu tanto para a nossa campanha, não hesitarei em aceitar todas as doações de runatrama que você fizer no futuro. Só não se esqueça de falar comigo para que eu possa me certificar pessoalmente de que as suas ações recebam o devido reconhecimento!", rewards = "Você receberá:\n\n"},
[7812] = {title = "Runatrama nunca é demais", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[7813] = {title = "Uma doação de lã", objective = "Aceitamos doações de lã. A doação de sessenta peças de lã lhe renderá o reconhecimento da Cidade Baixa por seus atos generosos. Nossos estoques de lã estão quase completos; só precisamos de 60 peças suas. Com as doações dos outros moradores do reino conseguiremos suprir nossas necessidades.\n\nCaso você já tenha as sessenta peças e deseje doá-las agora, pode entregá-las a mim quando quiser.", description = nil, progress = "Aceitamos doações de lã. A doação de sessenta peças de lã lhe renderá o reconhecimento da Cidade Baixa por seus atos generosos. Nossos estoques de lã estão quase completos; só precisamos de 60 peças suas. Com as doações dos outros moradores do reino conseguiremos suprir nossas necessidades.\n\nCaso você já tenha as sessenta peças e deseje doá-las agora, pode entregá-las a mim quando quiser.", completion = "Esplêndido! Agradecemos sua generosa doação, &lt;name&gt;!", rewards = "Você receberá:\n\n"},
[7814] = {title = "Uma doação de seda", objective = "Nossos estoques de seda – assim como outros tecidos – estão sempre em baixa. Nossos estoques estão quase completos. Só precisamos de 60 peças de seda suas e, com as outras doações que recebemos, conseguiremos atingir nossa meta.\n\nLembrando que um presente como a seda certamente aumentará o seu prestígio na comunidade! Caso você já tenha as sessenta peças e deseje doá-las agora, pode entregá-las a mim quando quiser.", description = nil, progress = "Nossos estoques de seda – assim como outros tecidos – estão sempre em baixa. Nossos estoques estão quase completos. Só precisamos de 60 peças de seda suas e, com as outras doações que recebemos, conseguiremos atingir nossa meta.\n\nLembrando que um presente como a seda certamente aumentará o seu prestígio na comunidade! Caso você já tenha as sessenta peças e deseje doá-las agora, pode entregá-las a mim quando quiser.", completion = "Esplêndido! Agradecemos sua generosa doação, &lt;name&gt;!", rewards = "Você receberá:\n\n"},
[7815] = {title = "Mordeliscas, cara!", objective = "Katum, o Pesqueiro, quer que você mate 15 tartarugas Mordeliscas de Água Salgada. Fale novamente com ele na Aldeia Revatusco, que fica nas Terras Agrestes, quando concluir a tarefa.", description = "Não deixa os olhões delas te enganarem, cara! Essas mordeliscas de água salgada são um perigo. Não dá pra pescar nem cinco minutos sem aqueles devoradores de peixe tamanho família arrebentarem a linha pra pegar a isca.\n\nFaz um favor pra mim e pro povo da Aldeia Revatusco: mata elas, cara.\n\nMata todas elas!", progress = nil, completion = "Ah, cara! Não sei como agradecer! Espero que esta vara de pescar seja recompensa suficiente pela tarefa.", rewards = "Você receberá:\n\nVara de pescar suprema FC-5000 do Nat Pagle"},
[7816] = {title = "Gammerita, cara!", objective = "Katum, o Pesqueiro, na Aldeia Revatusco, que fica nas Terras Agrestes, quer que você mate Gammerita e apanhe a Melhor Isca de Katum da carcaça.\n\nKatum mencionou que Gammerita circula pela costa com as outras tartarugas.", description = "Falaí! Tem uma tartaruga lá fora que dá de dez nas outras. É a Gammerita, chefona das mordeliscas de água salgada – uma baita duma sangue-frio gatuna e canalha à vera.\n\nCara, ela roubou as minhas iscas mais maneiras. Arrancou diretinho da linha! E desde que rolou isso os peixes não estão nem beliscando.\n\nEncontra essa cascuda pra mim e arranca a minha isca da barrigada dela!", progress = "Conseguiu pegar a isca?", completion = "Caraca! Isso é muito maneiro! Com essa isca irada e com menos mordeliscas de bobeira por aí, vai chover peixe na minha horta!", rewards = "O seguinte feitiço será lançado em você:\n\nEstouro de Experiência\n\nVocê receberá:\n\n"},
