[7962] = {title = "Pelagens de toelho", objective = "Leve 3 Pelagens de Coelho LeCrafty para Jon LeCraft.", description = "Quero pelagens de coelho LeCrafty. Traga-me algumas dos coelhos LeCrafty.\n\nÉ importante!O seguinte feitiço será lançado em você:\n\n", progress = nil, completion = nil, rewards = "O seguinte feitiço será lançado em você:\n\nMarca do Indomado\n\nVocê receberá:\n\nLagosta de 11 Quilos"},
