[8105] = {title = "A Batalha pela Bacia Arathi!", objective = "Ataque a mina, a serraria, a ferraria, a fazenda e então fale com o Marechal-de-campo Oslight na Ponta do Refúgio.", description = "<name>, a batalha pela Bacia Arathi é uma guerra por recursos e território. Locais estratégicos na área devem ser controlados para que suprimentos e matérias-primas sejam fornecidos para nossas forças. Preciso que você ataque os locais mais distantes da nossa base na Bacia Arathi.\n\nAtaque a fazenda, a mina, a serraria e a ferraria quando estiverem sob controle do inimigo. Encontre o estandarte adversário e arranque-o do mastro!\n\nQuando tiver feito isto em todos os quatro locais, fale comigo.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8106] = {title = "A mácula de Kezan", objective = "Aceite o talismã, <class>. Aceite-o e use o poder dele sobre os nossos inimigos em comum.\n\nNão é um talismã qualquer no seu pescoço, <name>: é um pedaço da história. Kezan já foi um grande território do Império Zandalariano até os príncipes mercadores goblins expulsarem meus irmãos e irmãs de lá.\n\nKezan agora é o lar da capital goblin de Inframina. Apesar da mácula, o talismã continua grandioso. Até os dias de hoje, Kezan é considerada solo sagrado pelo meu povo.", description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nMácula de Kezan"},
