[8114] = {title = "Controlar quatro bases", objective = "Entre na Bacia Arathi, capture e controle quatro bases ao mesmo tempo e, então, fale novamente com o Marechal-de-campo Oslight na Ponta do Refúgio.", description = "<name>, você lutou bravamente na batalha pela Bacia Arathi e conquistou grande honra neste tempo. Mostrou a todos seu valor como <class> e ensinou aos nossos inimigos que não nos renderemos!\n\nPor ter provado sua coragem, tenho uma difícil missão para você. Quero que tome e controle pelo menos quatro bases na Bacia Arathi. Com isto, conseguiremos importantes recursos e mostraremos à Horda quem está no poder!\n\nVá, <name>, e boa sorte.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8115] = {title = "Controlar cinco bases", objective = "Controle 5 bases na Bacia Arathi ao mesmo tempo e fale novamente com o Marechal-de-campo Oslight na Ponta do Refúgio.", description = "<name>, lutar pelos recursos da Bacia Arathi é uma missão sangrenta. Seus serviços para a Liga de Arathor são tão necessários quanto apreciados.\n\nE temos mais uma tarefa para você.\n\nA Liga deseja empreender um grande esforço contra a ocupação da Bacia Arathi pelos Corruptores. Queremos que eles sejam expulsos de todos os pontos estratégicos no campo de batalha!\n\nNão será fácil, mas, se alguém pode fazer isso, é você.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nTabardo de Batalha de Arathor"},
[8116] = {title = "A visão de Vodúria", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nVisão de Vodúria"},
[8117] = {title = "A visão de Vodúria", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nVisão de Vodúria"},
[8118] = {title = "A visão de Vodúria", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nVisão de Vodúria"},
[8119] = {title = "A visão imaculada de Vodúria", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nVisão Imaculada de Vodúria"},
[8120] = {title = "A batalha pela Bacia Arathi!", objective = "Vá até a Bacia Arathi e ataque a mina, a serraria, a ferraria e os estábulos. Então, fale novamente com a Senhora da Morte Dwire na Ruína do Martelo.", description = "Como sabe, a Liga de Arathor está enviando um grande número de tropas para a Bacia Arathi. Os tolos da Aliança querem controlar os recursos de lá, na tentativa de conseguir comida e suprimentos. Vamos ensiná-los que Arathi nunca mais servirá de lar para humanos!\n\nVá até a Bacia Arathi e ataque a mina, a serraria, a ferraria e os estábulos. Arranque os estandartes inimigos, afirmando a hegemonia da Horda.\n\nVá, <name>, e só volte quando tiver cumprido esta missão.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8121] = {title = "Tome quatro bases", objective = "Controle quatro bases ao mesmo tempo na Bacia Arathi. Depois, fale novamente com a Senhora da Morte Dwire, na Ruína do Martelo.", description = "<name>, ouvi relatos sobre suas proezas na batalha pela Bacia Arathi. Todos confiamos em você, por isso tenho certeza de que será bem-<sucedido/sucedida> na próxima missão.\n\nÉ chegada a hora de enviarmos uma mensagem para a Liga de Arathor. Vamos mostrar àqueles fracos que podemos tomar rapidamente tudo o que eles têm, controlando quatro pontos estratégicos ao mesmo tempo.\n\nConsiga isso, <name>, e ganhe o respeito dos Profanadores.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8122] = {title = "Tome cinco bases", objective = "Controle cinco bases na Bacia Arathi ao mesmo tempo. Depois, fale novamente com a Senhora da Morte Dwire na Ruína do Martelo.", description = "Estamos tão compenetrados nesta batalha que fica difícil perceber que a Liga de Arathor está enfraquecendo aos poucos, mas nossos espiões nos garantem que sim. Esta é a hora de lançarmos um ataque brutal contra nossos inimigos! Volte à Bacia Arathi, controle todas as cinco bases e deixe um rastro de sangue por onde passar! Faça-os correr desesperadamente, chorando e sangrando, com a eterna lembrança da sua crueldade!\n\nFaça isso e consiga mais uma vitória para os Profanadores.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nTabardo de Batalha dos Profanadores"},
[8123] = {title = "Quebra da rota de suprimentos de Arathor", objective = "Vença a batalha pela Bacia Arathi, consiga uma Caixa de Recursos Arathi e fale novamente com o Sicário Mortis na Ruína do Martelo.", description = "A Liga de Arathor não está poupando recursos na retomada de Arathi. Eles, junto com todas as raças da Aliança, estão se movendo para esta distante região na tentativa de expandir seus territórios a partir da Ponta do Refúgio. A melhor maneira de impedi-los é roubando os suprimentos deles.\n\nVocê pode nos ajudar, <name>. Entre na Bacia Arathi, conhecida por suas ricas minas, solos férteis e hábeis artesãos. Capture e controle o maior número de bases, vença a batalha e traga-me os recursos que conseguir.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
