[8149] = {title = "Homenagem a um herói", objective = "Vá até a Tumba de Uther nas Terras Pestilentas Ocidentais e use o Tributo de Uther aos pés da estátua de Uther. Em seguida, fale novamente com Wagner Golparrão, que está do lado de fora de Altaforja, antes que o Festival da Colheita acabe.\n\nItem fornecido:\n", description = "Durante o Festival da Colheita de Azeroth, nós agradecemos aos nossos heróis pelos sacrifícios – em alguns casos o sacrifício supremo – que fizeram por nós. A Aliança reconhece muitos heróis, mas nenhum viveu história tão trágica como Uther, o Arauto da Luz.\n\nAjude-nos a homenagear nosso herói levando esta oferenda para o túmulo dele nas Terras Pestilentas Ocidentais. Coloque isto aos pés da estátua dele, e me procure novamente. Eu só ficarei aqui até o fim do festival, então não perca tempo.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nPela Luz!"},
[8150] = {title = "Homenagem a um herói", objective = "Vá até o Monumento de Grom no Cânion do Demônio Caído, que fica no Vale Gris. Use o Tributo de Grom na base do monumento. Fale novamente com Javnir Nashak, fora de Orgrimmar, antes que o Festival da Colheita termine.\n\nItem fornecido:\n", description = "Durante o Festival da Colheita de Azeroth, nós agradecemos aos nossos heróis pelos sacrifícios – em alguns casos o sacrifício supremo – que fizeram por nós. A Horda reconhece muitos heróis, mas nenhum exemplifica as noções de força e sacrifício mais que Grom Grito Infernal.\n\nAjude-nos a homenagear Grom levando esta oferenda para o túmulo dele no Cânion do Demônio Caído, que fica no Vale Gris. Coloque isto aos pés da estátua dele e volte aqui. Eu só ficarei aqui até o fim do festival, então não perca tempo e vá logo homenagear Grom.Você receberá:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\nO Grito Infernal da Horda"},
[8151] = {title = "O amuleto do caçador", objective = "Encontre Ogtinc em Azshara.", description = "Ogtinc de Azshara convocou caçadores experientes como você, <guri/guria>. Ele é um caçador pelurso que deixou a tribo para ajudar o Círculo Cenariano. Tudo o que ele ensina tem por objetivo enriquecer a sabedoria de outros que compartilham o ofício dele e orientá-los a manter o equilíbrio. Seria bom tu falar com ele, tchê.\n\nOgtinc mora no alto dos penhascos de Azshara, a nordeste das Ruínas de Eldarath.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8152] = {title = "REUSE", objective = "Speak with Ogtinc in Azshara.<name>, when you feel you are ready, there is a wisened furbolg hunter by the name of Ogtinc who resides in Azshara. He may be able to enhance your teaching, and provide you with guidance.\n\nOgtinc is atop the cliffs northeast the Ruins of Eldarath in Azshara.Completando essa missão você ganhará:\n\n", description = "<name>, when you feel you are ready, there is a wisened furbolg hunter by the name of Ogtinc who resides in Azshara.  He may be able to enhance your teaching, and provide you with guidance.\n\nOgtinc is atop the cliffs northeast the Ruins of Eldarath in Azshara.Completando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = nil},
[8153] = {title = "Galhada de ginete", objective = "Leve um par de Galhadas Perfeitas de Ginete para Ogtinc em Azshara. Ogtinc vive no alto dos penhascos a nordeste das Ruínas de Eldarath.", description = "O ginete usa a galhada mais poderosa das terras conhecidas. Essa galhada é usada para defender o ginete, ferir um agressor e, muitas vezes, prejudicar os pontos. Uma galhada natural e perfeita é sinal de um espírito animal extremamente confiante.\n\nTer tal galhada lhe dará a mesma confiança.\n\nCace o ginete cascomusgo, encontre a galhada perfeita e traga-a para mim.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8154] = {title = "Recursos da Bacia Arathi!", objective = "Vença a batalha pela Bacia Arathi, consiga uma Caixa de Recursos Arathi e fale novamente com a Sargento Martina na Ponta do Refúgio.", description = "Todos sabem que uma guerra se vence com soldados. Também é sabido que uma batalha que se luta com equipamentos medíocres ou de barriga vazia já está perdida antes mesmo de começar!\n\nEsta é a importância da Bacia Arathi. Existem locais estratégicos com importantes recursos como metais, armas, comida e lenha.\n\nPois bem, <name>. Preciso que você vá até a Bacia Arathi e controle mais bases do que o inimigo para, assim, vencer a batalha. E traga-me uma caixa de recursos.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
