[8169] = {title = "A batalha pela Bacia Arathi!", objective = "Vá até a Bacia Arathi e ataque a mina, a serraria, a ferraria e os estábulos. Então, fale novamente com a Senhora da Morte Dwire na Ruína do Martelo.", description = "Como sabe, a Liga de Arathor está enviando um grande número de tropas para a Bacia Arathi. Os tolos da Aliança querem controlar os recursos de lá, na tentativa de conseguir comida e suprimentos. Vamos ensiná-los que Arathi nunca mais servirá de lar para humanos!\n\nVá até a Bacia Arathi e ataque a mina, a serraria, a ferraria e os estábulos. Arranque os estandartes inimigos, afirmando a hegemonia da Horda.\n\nVá, <name>, e só volte quando tiver cumprido esta missão.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8170] = {title = "A batalha pela Bacia Arathi!", objective = "Vá até a Bacia Arathi e ataque a mina, a serraria, a ferraria e os estábulos. Então, fale novamente com a Senhora da Morte Dwire na Ruína do Martelo.", description = "Como sabe, a Liga de Arathor está enviando um grande número de tropas para a Bacia Arathi. Os tolos da Aliança querem controlar os recursos de lá, na tentativa de conseguir comida e suprimentos. Vamos ensiná-los que Arathi nunca mais servirá de lar para humanos!\n\nVá até a Bacia Arathi e ataque a mina, a serraria, a ferraria e os estábulos. Arranque os estandartes inimigos, afirmando a hegemonia da Horda.\n\nVá, <name>, e só volte quando tiver cumprido esta missão.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8171] = {title = "A batalha pela Bacia Arathi!", objective = "Vá até a Bacia Arathi e ataque a mina, a serraria, a ferraria e os estábulos. Então, fale novamente com a Senhora da Morte Dwire na Ruína do Martelo.", description = "Como sabe, a Liga de Arathor está enviando um grande número de tropas para a Bacia Arathi. Os tolos da Aliança querem controlar os recursos de lá, na tentativa de conseguir comida e suprimentos. Vamos ensiná-los que Arathi nunca mais servirá de lar para humanos!\n\nVá até a Bacia Arathi e ataque a mina, a serraria, a ferraria e os estábulos. Arranque os estandartes inimigos, afirmando a hegemonia da Horda.\n\nVá, <name>, e só volte quando tiver cumprido esta missão.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8181] = {title = "Confronte Yeh'kinya", objective = "Fale com Yeh'kinya.", description = "Bah, minhas pesquisas com as tabuletas de Mosh'aru trazem uma triste notícia, <name>. A verdadeira forma de Hakkar, o deus em si, pode renascer através do ovo em que você o capturou. Yeh'kinya te disse que o ovo era para conter o mal, mas na verdade era para recriá-lo! O troll te enganou!\n\nVá até Yeh'kinya e exija o ovo dele. E reze para que não seja tarde demais para impedir o retorno de Hakkar!Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nManto Hakkari DesbotadoCapa Hakkari Esfarrapada\n\nVocê também receberá:\n\n"},
[8182] = {title = "A Mão de Rastakhan", objective = "Fale com Molthor na Selva do Espinhaço.", description = "<name>, nossos piores medos se tornaram realidade. A lenda fala do Esfolador de Almas governando o antigo mundo dos trolls através do mal e do terror, e o poder dele era absoluto. Se ele não puder ser detido agora, então seu novo reinado pode se estender a toda Azeroth!\n\nHá um contingente de trolls zandalarianos que pode nos ajudar a conter essa maré sombria. Eles acamparam na Ilha Yojamba, próxima à costa da Selva do Espinhaço, a oeste das ruínas de Zul'Kunda. Fale com o líder deles, Molthor, e reze para que ele saiba como derrotar Hakkar.Você receberá:\n\nCompletando essa missão você ganhará:\n\n", progress = nil, completion = nil, rewards = "Você receberá:\n\n"},
[8183] = {title = "O coração de Hakkar", objective = "Leve o Coração de Hakkar a Molthor na Ilha Yojamba.", description = "Chamas se lançam de dentro do Coração de Hakkar. Embora o Esfolador de Almas tenha sido derrotado, a raiz do seu poder reside ainda no órgão amaldiçoado.", progress = "Você foi amaldiçoado pelo Esfolador de Almas, <name>? Porque você exala os vapores de uma alma perversa.", completion = "Ah, o Coração de Hakkar. Quer dizer que o Esfolador de Almas foi derrubado!\n\nCom um feito tão grande como esse não há história que consiga exagerar. Aqui, neste reino, tu é heroína!", rewards = "Você vai poder escolher uma dessas recompensas:\n\nInsígnia Heroica ZandalarianaPatuá Heroico ZandalarianoMedalhão Heroico Zandalariano\n\nVocê também receberá:\n\n"},
