[8194] = {title = "Aprendiz de pescador", objective = "Nós já temos um vencedor do concurso, mas ainda assim vou continuar comprando Papas-finas de você por um preço razoável.", description = nil, progress = "Nós já temos um vencedor do concurso, mas ainda assim vou continuar comprando Papas-finas de você por um preço razoável.", completion = "Tá bom pra mim se estiver bom pra você!", rewards = "Você receberá:\n\n"},
[8195] = {title = "As moedas Hakkari, Zulian e Razzashi", objective = "Procuro as Epítomes de Poder conhecidos como Moedas das Tribos. São usadas por todos os moradores de Zul'Gurub, e todas elas são imbuídas de um mojo que, embora sutil, é bem forte.\n\nHá nove tipos diferentes de moeda. Algumas são procuradas pelos meus compatriotas por várias armaduras que oferecem aos heróis de Zandalar. Se tiver algumas sobrando, posso trocas três por uma das nossas Insígnias de Honra. Você pode levar as insígnias para Rin'wosho, o Comerciante. Ele oferece aos nossos heróis itens especiais em troca delas.", description = nil, progress = "Procuro as Epítomes de Poder conhecidos como Moedas das Tribos. São usadas por todos os moradores de Zul'Gurub, e todas elas são imbuídas de um mojo que, embora sutil, é bem forte.\n\nHá nove tipos diferentes de moeda. Algumas são procuradas pelos meus compatriotas por várias armaduras que oferecem aos heróis de Zandalar. Se tiver algumas sobrando, posso trocas três por uma das nossas Insígnias de Honra. Você pode levar as insígnias para Rin'wosho, o Comerciante. Ele oferece aos nossos heróis itens especiais em troca delas.", completion = "Excelente! Essas moedas aqui vão ser um ótimo acréscimo para a coleção... e vamos usá-las para defender a tribo do reerguimento do Deus Sanguinário. Você merece um Emblema Honorífico e um agradecimento de toda a tribo, &lt;name&gt;!", rewards = "Você receberá:\n\nEmblema Honorífico de Zandalar"},
[8196] = {title = "As mangaromas", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Você receberá:\n\nMangaroma"},
[8201] = {title = "Uma coleção de cabeças", objective = "Amarre 5 Cabeças de Médium no cordão. Em seguida, volte com Coleção de Cabeças de Troll a Exzhal, na Ilha Yojamba.", description = "É verdade. Hakkar reside em Zul'Gurub e está imerso no poder dos nossos deuses. Neste exato momento, os altos sacerdotes enfeitiçados canalizam as energias das nossas mais sagradas divindades para o Esfolador de Almas, que, em breve, terá uma força superior à de qualquer ser em Azeroth. Precisamos detê-lo!\n\nLeve este cordão sagrado com você e amarre nele as cabeças dos altos sacerdotes responsáveis por canalizar as energias dos nossos deuses.\n\nQuando tiver feito isso, volte aqui.Você vai poder escolher uma dessas recompensas:\n\n", progress = nil, completion = nil, rewards = "Você vai poder escolher uma dessas recompensas:\n\nCinto de Cabeças EncolhidasCinto de Cabeças EnrugadasCinto de Cabeças PreservadasCinto de Minicabeças\n\nVocê também receberá:\n\nCabeça do Canalizador"},
